(window.webpackJsonp=window.webpackJsonp||[]).push([[643],{666:function(p){p.exports={chapterTitle:null,title:"\u7b2c490\u7ae0 \u5408\u540c\u7684\u6f0f\u6d1e",content:"<article><h2>\u7b2c490\u7ae0 \u5408\u540c\u7684\u6f0f\u6d1e</h2><p>\u3000\u3000\u7ed9\u674e\u4e49\u6c5f\u63a5\u98ce\u79bb\u5e2d\u65e9\uff0c\u9001\u5510\u5a67\u56de\u5bb6\u518d\u8d76\u5230\u4e39\u4e95\u5df7\uff0c\u5f20\u606a\u770b\u4e86\u770b\u624b\u8868\uff0c\u624d\u516b\u70b9\u534a\u949f\uff0c\u5012\u4e0d\u6653\u5f97\u4e01\u69d0\u8ddf\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u804a\u5f97\u5982\u4f55\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fdb\u516b\u9526\u73cd\uff0c\u9648\u5983\u84c9\u4f4e\u5934\u521a\u8981\u649e\u8fdb\u4ed6\u6000\u91cc\u624d\u582a\u582a\u6536\u4f4f\u811a\u6b65\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u624b\u6342\u7740\u80f8\u53e3\u51dd\u7738\u770b\u662f\u5f20\u606a\uff0c\u90a3\u6e05\u6f88\u6676\u4eae\u7684\u7738\u5b50\u91cc\u7acb\u65f6\u8868\u60c5\u4e30\u5bcc\u8d77\u6765\uff0c\u6807\u51c6\u74dc\u5b50\u7f8e\u4eba\u8138\u524d\u4e00\u523b\u8fd8\u662f\u51b0\u96ea\u51dd\u8102\u822c\u7684\u5ae9\u767d\uff0c\u77ac\u65f6\u8986\u4e0a\u660e\u4e3d\u52a8\u4eba\u7684\u6843\u7ea2\u8272\uff0c\u6342\u7740\u8138\u626d\u5934\u5c31\u8d70\u4e86\uff0c\u5dee\u70b9\u5c31\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\uff0c\u8d70\u5f00\u7684\u65f6\u5019\uff0c\u80a9\u8180\u8fd8\u5728\u5fae\u5fae\u62bd\u6410\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5085\u4fca\u83ab\u660e\u5176\u5999\u7684\uff0c\u9648\u5983\u84c9\u7684\u53cd\u5e94\u4f3c\u4e4e\u592a\u5927\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u5fc5\u5b9a\u662f\u82cf\u4e00\u5a77\u5c06\u81ea\u5df1\u57281978\u7ed9\u5b59\u9759\u6aac\u6349\u5f04\u51fa\u4e11\u7684\u4e8b\u60c5\u6beb\u65e0\u4fdd\u7559\u7684\u8bf4\u7ed9\u9648\u5983\u84c9\u542c\u4e86\uff0c\u5fc3\u91cc\u5927\u5927\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5f62\u8c61\u5927\u6bc1\uff0c\u89c1\u5085\u4fca\u751a\u662f\u8bb6\u5f02\uff0c\u6487\u5634\u8bf4\u9053\uff1a\u201c\u6ca1\u89c9\u5f97\u81ea\u5df1\u8fd9\u6bb5\u65f6\u95f4\u9b45\u529b\u5927\u589e\u554a\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u7b11\u4e86\u7b11\uff0c\u8fd9\u91cc\u5927\u5bb6\u90fd\u8f7b\u8f66\u719f\u8def\uff0c\u4e01\u69d0\u4ed6\u4eec\u5728\u504f\u53a2\u623f\u7684\u96c5\u5ba4\u91cc\uff0c\u4ed6\u8ddf\u7740\u5f20\u606a\u5c31\u5f80\u91cc\u8d70\uff0c\u9648\u5947\u8fce\u51fa\u6765\uff1a\u201c\u606a\u5c11\u597d\u4e45\u6ca1\u5230\u8fd9\u91cc\u6765\u5403\u996d\u4e86\uff0c\u8fd9\u4e2a\u70b9\u662f\u5403\u591c\u5bb5\u8fd8\u662f\u665a\u9910\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u771f\u96be\u5206\u8fa8\uff01\u201d\u5f20\u606a\u62ac\u8d77\u624b\u770b\u4e86\u4e00\u773c\u7897\u8868\uff0c\u770b\u89c1\u9648\u5983\u84c9\u5750\u5728\u4e2d\u5ead\u7684\u4e00\u89d2\u8138\u618b\u7740\u7b11\uff0c\u5e73\u65e5\u89c1\u60ef\u5979\u51b0\u971c\u660e\u8273\u7684\u6a21\u6837\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u5979\u8fd9\u822c\u6a21\u6837\uff0c\u5374\u66f4\u52a0\u7684\u5a07\u8273\uff0c\u6709\u7740\u5c11\u5973\u5a07\u7f9e\u7684\u65e0\u7aef\u98ce\u60c5\uff0c\u8ba9\u5750\u5728\u4e2d\u5ead\u5927\u5385\u91cc\u7684\u98df\u5ba2\u770b\u5f97\u76ee\u77aa\u53e3\u820c\uff0c\u5750\u5728\u9648\u5983\u84c9\u8fb9\u7684\u4e00\u5bf9\u5e74\u8f7b\u7684\u60c5\u4fa3\uff0c\u7537\u7684\u5939\u7740\u83dc\u5f80\u5973\u5b69\u5634\u91cc\u9001\uff0c\u4e00\u5931\u795e\u7b77\u5b50\u5934\u5dee\u70b9\u6233\u5973\u53cb\u8138\u4e0a\uff0c\u7ed9\u5973\u53cb\u674f\u76ee\u4e00\u77aa\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\u2026\u2026</p><p>\u3000\u3000\u5085\u4fca\u8ddf\u9648\u5947\u8bf4\u5b9e\u8bdd\uff1a\u201c\u6211\u4eec\u5403\u8fc7\u4e86\uff0c\u8ddf\u4eba\u7ea6\u5728\u8fd9\u91cc\u89c1\u9762\u2026\u2026\u54e6\uff0c\u90a3\u8fb9\u2026\u2026\u201d\u96c5\u5ba4\u671d\u7740\u4e2d\u5ead\u7684\u96d5\u82b1\u6728\u95e8\u6ca1\u5173\u4e0a\uff0c\u5085\u4fca\u770b\u89c1\u4e01\u69d0\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u9648\u5947\u966a\u7740\u5f20\u606a\u3001\u5085\u4fca\u8d70\u5230\u90a3\u8fb9\u96c5\u5ba4\u95e8\u53e3\uff0c\u62db\u624b\u8ba9\u5973\u513f\u9648\u5983\u84c9\u8fc7\u6765\u6dfb\u7897\u789f\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u770b\u7740\u5f20\u606a\u8fdb\u6765\uff0c\u966a\u540c\u4ed6\u4eec\u7684\u4e01\u69d0\uff0c\u8fd8\u6709\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4e24\u540d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u4e0d\u6653\u5f97\u4ec0\u4e48\u4eba\u7269\uff0c\u51fa\u4e8e\u793c\u8282\uff0c\u4e5f\u8ddf\u7740\u7ad9\u8d77\u6765\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\uff0c\u5f20\u606a\u89c1\u8fc7\uff0c\u4e0d\u8fc7\u662f\u524d\u4e16\u96f6\u4e94\u5e74\u7684\u4e1c\u4eac\u7535\u5b50\u5c55\u4e0a\uff0c\u90a3\u65f6ESS\u5728\u6570\u5b57\u50a8\u5b58\u89e3\u7801\u7cfb\u7edf\u4e0a\u6709\u4e0d\u9519\u7684\u6210\u5c31\uff0c\u4e5f\u662f\u56fd\u5185\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u89e3\u7801\u677f\u4e3b\u8981\u4f9b\u5e94\u5546\u4e4b\u4e00\uff0c\u867d\u7136\u90a3\u662f\u516b\u5e74\u4e4b\u540e\uff0c\u4e0d\u8fc7\u90a3\u65f6\u7684\u8096\u664b\u6210\u6bd4\u73b0\u5728\u770b\u8d77\u6765\u8981\u7cbe\u795e\u3001\u611f\u89c9\u4e0a\u8981\u5e74\u8f7b\u4e00\u4e9b\uff0c\u8fd9\u4e3b\u8981\u662f\u56e0\u4e3a\u8096\u664b\u6210\u4e0e\u6210\u6c49\u748b\u521d\u542c\u5669\u8017\u53c8\u957f\u65f6\u95f4\u4e58\u98de\u673a\u98de\u8fc7\u5927\u534a\u4e2a\u5730\u7403\uff0c\u4e0d\u8981\u8bf4\u5012\u65f6\u5dee\u4e86\uff0c\u90fd\u6ca1\u6709\u597d\u597d\u7684\u4f11\u606f\u8fc7\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\uff0c\u5f20\u606a\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u662f\u5728\u4e13\u4e1a\u7684\u7535\u5b50\u7c7b\u901a\u8baf\u6742\u5fd7\u770b\u5230\u8fc7\u4ed6\u7684\u7167\u7247\uff0c\u610f\u6c14\u98ce\u53d1\u7684\u6837\u5b50\u3002\u4e0d\u8fc7\u8fd9\u65f6\u5019\uff0c\u4ed6\u4eec\u8fd8\u662f\u6f66\u5012\u7684\uff0cESS\u4e4b\u524d\u5c0f\u6709\u6210\u5c31\uff0c\u90fd\u6295\u5165\u5230\u89e3\u7801\u82af\u7247\u7684\u5f00\u53d1\u4e0a\uff0c\u8fd9\u65f6\u5019\u53c8\u906d\u5230\u8fd9\u4e48\u4e00\u8bb0\u91cd\u521b\uff0c\u8c01\u90fd\u4e0d\u4f1a\u6709\u7279\u522b\u597d\u7684\u6c14\u8272\u3002\u5f53\u7136\u4e86\uff0c\u8fd9\u79cd\u4eba\uff0c\u5373\u4f7f\u906d\u53d7\u4e00\u4e24\u6b21\u6253\u51fb\uff0c\u4e5f\u4f1a\u91cd\u65b0\u7ad9\u7acb\u5e76\u5d1b\u8d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5148\u5750\u4e0b\u6765\uff0c\u6210\u5148\u751f\u3001\u8096\u5148\u751f\u90fd\u662f\u53f0\u6e7e\u4eba\uff0c\u53f0\u6e7e\u6ca1\u6709\u4e00\u5b9a\u8981\u7ad9\u7740\u4ecb\u7ecd\u7684\u89c4\u77e9\u5427\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u8d70\u8fdb\u6765\uff0c\u6c14\u7406\u987a\u4e86\uff0c\u4e0d\u8fc7\u8138\u4e0a\u7684\u7eef\u7ea2\u672a\u6d88\uff0c\u4e0d\u6562\u62ff\u773c\u770b\u5f20\u606a\uff0c\u5c31\u6015\u5fcd\u4e0d\u4f4f\u4f1a\u7b11\u51fa\u58f0\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u9648\u5983\u84c9\u8bf4\uff1a\u201c\u5e2e\u6211\u4eec\u5e2e\u4e24\u676f\u6c34\u5c31\u53ef\u4ee5\u4e86\uff0c\u201d\u53c8\u95ee\u9648\u5983\u84c9\uff0c\u201c\u4f60\u600e\u4e48\u4e0d\u7a7f\u82b1\u5e03\u65d7\u888d\u554a\uff1f\u201d\u516b\u9526\u73cd\u7684\u670d\u52a1\u5458\u4e00\u6c34\u7a7f\u7740\u84dd\u5370\u82b1\u5e03\u88c1\u526a\u7684\u65d7\u888d\uff0c\u53ef\u60dc\u5f00\u53c9\u53ea\u5230\u819d\u76d6\uff0c\u5f20\u606a\u5e0c\u671b\u5f00\u53c9\u80fd\u5230\u5927\u817f\u6839\u3002</p><p>\u3000\u3000\u201c\u6211\u53ea\u662f\u4e34\u65f6\u6765\u5e2e\u5fd9\u7684\uff0c\u4eca\u5929\u624d\u5230\u5bb6\uff0c\u201d\u9760\u5899\u58c1\u5916\u89d2\u6709\u4e00\u53f0\u653e\u7897\u789f\u53c8\u517c\u4f5c\u4f20\u83dc\u53f0\u7684\u67dc\u5b50\uff0c\u9648\u5983\u84c9\u4f4e\u7740\u5934\u62ff\u5370\u7740\u9752\u82b1\u7684\u7cbe\u81f4\u74f7\u8336\u676f\uff0c\u7ec6\u58f0\u7ec6\u6c14\u7684\u56de\u7b54\uff0c\u5374\u4e0d\u6562\u56de\u5934\u770b\u5f20\u606a\uff0c\u201c\u4e0e\u82cf\u4e00\u5a77\u5979\u4eec\u4e00\u8d77\u5750\u8239\u56de\u6d77\u5dde\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9648\u5983\u84c9\u5fcd\u5f97\u8f9b\u82e6\uff0c\u6345\u4e86\u6345\u5085\u4fca\uff0c\u8ba9\u4ed6\u53bb\u5012\u8336\uff0c\u4e0d\u8981\u8ba9\u9648\u5983\u84c9\u5c06\u6ce8\u6ee1\u6c34\u7684\u8336\u676f\u6cfc\u81ea\u5df1\u8eab\u4e0a\uff0c\u8fd8\u4e0d\u80fd\u6028\u522b\u4eba\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u4fcf\u751f\u751f\u7684\u7ad9\u5728\u95e8\u53e3\uff0c\u5f85\u5085\u4fca\u5c06\u8336\u6c34\u7aef\u56de\u53bb\uff0c\u624d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u4e01\u69d0\u5e2e\u6210\u6c49\u748b\u3001\u8096\u91d1\u6210\u4ecb\u7ecd\u5f20\u606a\u3001\u5085\u4fca\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u91d1\u6210\u90fd\u662f\u4ece\u53f0\u6e7e\u8bfb\u5b8c\u5de5\u79d1\u4e4b\u540e\u5230\u65af\u5766\u798f\u5927\u5b66\u7559\u5b66\uff0c\u7559\u5b66\u671f\u95f4\u6709\u6253\u5de5\uff0c\u6bd5\u4e1a\u4e4b\u540e\u5c31\u76f4\u63a5\u5f00\u516c\u53f8\uff0c\u4eca\u5e74\u90fd\u6ca1\u6ee1\u4e09\u5341\u5c81\u3002\u7845\u8c37\u5927\u6982\u662f\u805a\u96c6\u5e74\u8f7b\u5bcc\u7fc1\u6700\u591a\u7684\u5730\u65b9\uff0c\u4ece\u5168\u4e16\u754c\u6c47\u96c6\u5230\u7845\u8c37\u7684\u7cbe\u82f1\u4eba\u58eb\u4e00\u591c\u4e4b\u95f4\u6210\u4e3a\u767e\u4e07\u5bcc\u7fc1\u3001\u5343\u4e07\u5bcc\u7fc1\u90fd\u662f\u5e73\u5e38\u7684\u4e8b\uff0c\u5373\u4f7f\u4ebf\u4e07\u5bcc\u7fc1\u4e5f\u4e0d\u662f\u4e0d\u53ef\u80fd\uff0c\u800c\u4e14\u8fd9\u4e9b\u4eba\u5f88\u591a\u90fd\u662f\u4e8c\u5341\u5c81\u51fa\u5934\u3001\u8fd8\u6ca1\u6709\u4ece\u5b66\u6821\u51fa\u53bb\u7684\u5b66\u751f\u3002\u6bd4\u5982\u8bf4\u5427\uff0c\u6bd4\u5c14\u76d6\u8328\u7684\u4e2a\u4eba\u4f20\u8bb0\u51e0\u4e4e\u5728\u7845\u8c37\u751f\u5b58\u7684\u79d1\u5b66\u5bb6\u3001\u5de5\u7a0b\u5e08\u6240\u5fc5\u8bfb\u7684\u5723\u7ecf\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u542c\u4e01\u69d0\u4ecb\u7ecd\u5f20\u606a\u624d\u662f\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4ee5\u53ca\u7231\u8fbe\u7535\u5b50\u7684\u5e55\u540e\u8001\u677f\uff0c\u867d\u7136\u6709\u4e9b\u8bb6\u5f02\uff0c\u4f46\u662f\u53cd\u5e94\u5c31\u6bd4\u5185\u5730\u7684\u90a3\u4e9b\u4eba\u542c\u5230\u8fd9\u4e2a\u4e8b\u5b9e\u8981\u5e73\u9759\u8bb8\u591a\u3002</p><p>\u3000\u3000\u201c\u804a\u4ec0\u4e48\uff0c\u8fdb\u6765\u65f6\u770b\u4f60\u4eec\u8c08\u5f97\u5f88\u5165\u795e\u554a\uff1f\u201d\u5f20\u606a\u5c06\u5916\u5957\u8131\u4e0b\u6401\u8fb9\u4e0a\u6905\u5b50\u4e0a\uff0c\u201c\u4f60\u4eec\u8c08\u5230\u54ea\u4e00\u6b65\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u8c08\u5230\u6211\u4eec\u9526\u6e56\u9700\u8981\u4e00\u4e2a\u5e73\u53f0\uff0c\u8fd9\u4e2a\u5e73\u53f0\u9664\u4e86\u80fd\u7ed9\u9526\u6e56\u63d0\u4f9b\u4e00\u5b9a\u7684\u6280\u672f\u652f\u6301\u4e4b\u5916\uff0c\u8fd8\u8981\u80fd\u591f\u5e2e\u52a9\u9526\u6e56\u4e0e\u7845\u8c37\u6700\u65b0\u7684\u7535\u5b50\u6280\u672f\u53d1\u5c55\u52a8\u5411\u4fdd\u6301\u63a5\u89e6\u3001\u4ea4\u6d41\uff0c\u540c\u65f6\u4e5f\u662f\u4e00\u4e2a\u4f9b\u9526\u6e56\u7684\u6280\u672f\u4eba\u5458\u4e0d\u65ad\u5b66\u4e60\u3001\u63d0\u9ad8\u6280\u672f\u6c34\u5e73\u7684\u4e00\u4e2a\u6e20\u9053\u2026\u2026\u201d\u4e01\u69d0\u8bf4\u9053\uff0c\u201c\u6211\u5c06\u9526\u6e56\u5728\u7845\u8c37\u6210\u7acb\u79d1\u7814\u5b9e\u9a8c\u57fa\u5730\u7684\u7528\u610f\uff0c\u90fd\u8be6\u7ec6\u8ddf\u4e24\u4f4d\u8bf4\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u770b\u7740\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5df2\u7ecf\u8c08\u5230\u8fd9\u91cc\uff0c\u90a3\u6211\u5c31\u5f00\u95e8\u89c1\u5c71\u8bf4\u51fa\u6211\u9080\u8bf7\u4e8c\u4f4d\u8fc7\u6765\u7684\u7528\u610f\uff0c\u6211\u613f\u610f\u51fa\u8d44\u4e70\u4e0bESS\u516c\u53f8\uff0c\u5e76\u5e0c\u671b\u4e8c\u4f4d\u80fd\u7ee7\u7eed\u7559\u5728ESS\u5de5\u4f5c\u2026\u2026\u201d\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u521a\u624d\u4e0e\u4e01\u69d0\u8c08\u8bdd\u4e2d\uff0c\u80fd\u610f\u8bc6\u5230\u8fd9\u70b9\uff0c\u53ea\u662f\u6ca1\u60f3\u5230\u4f1a\u8fd9\u4e48\u5feb\u63d0\u51fa\u6765\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u601d\u8651\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e0c\u671b\u80fd\u4e86\u89e3\u5230\u5e94\u5f53\u88ab\u544a\u4e4b\u7684\u4e8b\u5b9e\uff0c\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u4f1a\u5c65\u884c\u4fdd\u5bc6\u4e49\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\uff1a\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5927\u6982\u662f\u60f3\u77e5\u9053\u4ed6\u4eec\u7684\u5e0c\u671b\u5982\u4f55\u7ed9TI\u3001\u65af\u9ad8\u67cf\u8054\u5408\u6390\u706d\u7684\u4e8b\u5b9e\u5427\uff1f\u4ed6\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\u554a\u2026\u2026\u201d\u8bed\u6c14\u987f\u4e86\u987f\uff0c\u63a2\u5934\u95ee\u4e01\u69d0\uff0c\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u62df\u597d\u7684\u4fdd\u5bc6\u534f\u8bae\u968f\u8eab\u5e26\u7740\uff0c\u8bf7\u6210\u5148\u751f\u3001\u8096\u5148\u751f\u7b7e\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u65e2\u7136\u662f\u8c08\u5224\uff0c\u4e00\u5207\u90fd\u6309\u6b63\u89c4\u7684\u7a0b\u5e8f\u8d70\uff0c\u8bb8\u591a\u6d89\u53ca\u5230\u7684\u5546\u4e1a\u673a\u5bc6\u82e5\u662f\u4ece\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5634\u91cc\u6cc4\u9732\u51fa\u53bb\uff0c\u4f1a\u51cf\u5c11\u9526\u6e56\u3001\u7231\u8fbe\u7535\u5b50\u65e5\u540e\u7684\u6536\u76ca\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5012\u6ca1\u6709\u4e1d\u6beb\u7684\u4e0d\u60a6\uff0c\u4e5d\u5341\u5e74\u4ee3\uff0c\u53f0\u6e7e\u7684\u7ecf\u6d4e\u73af\u5883\u6bd4\u56fd\u5185\u8981\u597d\u8bb8\u591a\uff0c\u53c8\u4e00\u76f4\u5728\u7f8e\u56fd\u7559\u5b66\u3001\u521b\u4e1a\uff0c\u76f8\u53cd\u4f1a\u6bd4\u8f83\u6b23\u8d4f\u5951\u7ea6\u7cbe\u795e\uff0c\u89c1\u968f\u4e01\u69d0\u8fc7\u6765\u7684\u4e00\u540d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u62ff\u51fa\u4fdd\u5bc6\u534f\u8bae\u6587\u672c\uff0c\u7ffb\u770b\u4e86\u4e00\u4e0b\uff0c\u5c31\u7b7e\u8fc7\u5b57\u9012\u8fd8\u7ed9\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u65e9\u5728\u534a\u5e74\u4e4b\u524d\u5c31\u5728\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u5b9e\u73b0\u8d85\u7ea7VCD\u7684\u7edd\u5927\u90e8\u5206\u529f\u80fd\uff0c\u53ea\u662f\u7f3a\u4e4f\u5fc5\u8981\u7684\u6280\u672f\u624b\u6bb5\u5c06\u76f8\u5173\u6280\u672f\u96c6\u6210\u5230\u89e3\u7801\u82af\u7247\u4e0a\uff0c\u624d\u4e0eTI\u3001\u65af\u9ad8\u67cf\u8054\u5408\u8d77\u6765\u5f00\u53d1\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\uff0c\u6839\u636e\u9526\u6e56\u4e0eTI\u3001\u65af\u9ad8\u67cf\u7684\u534f\u8bae\uff0c\u9526\u6e56\u62e5\u6709\u76f8\u5173\u7684\u4e13\u5229\u6280\u672f\uff0c\u6388\u6743\u8bb8\u53efTI\u3001\u65af\u9ad8\u67cf\u4f7f\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u5012\u4e0d\u662f\u8f7b\u89c6\u5185\u5730\u7684\u7814\u53d1\u6c34\u5e73\uff0c\u5185\u5730\u5728\u7535\u5b50\u6280\u672f\u7684\u6c34\u51c6\u7684\u786e\u4e0d\u5982\u4eba\u610f\uff0c\u9526\u6e56\u80fd\u5728\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u5b9e\u73b0\u65b0\u789f\u673a\u7684\u6240\u6709\u529f\u80fd\uff0c\u786e\u5b9e\u8868\u73b0\u51fa\u8ba9\u4eba\u60ca\u8bb6\u7684\u7814\u53d1\u6c34\u51c6\u3002\u8981\u4e0d\u662f\u8eab\u5904\u6d77\u5dde\uff0c\u5bf9\u65b9\u53c8\u662f\u7ecf\u516c\u5f00\u6e20\u9053\u8bc1\u660e\u4e0eTI\u3001\u65af\u9ad8\u67cf\u3001\u98de\u5229\u6d66\u8054\u5408\u5f00\u53d1\u51fa\u65b0\u789f\u673a\u7684\u7231\u8fbe\u7535\u5b50\u7684\u5e55\u540e\u8001\u677f\uff0c\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5fc3\u91cc\u7684\u7591\u60d1\u4f1a\u66f4\u6df1\u4e00\u4e9b\u3002\u5f53\u7136\uff0c\u4ece\u516c\u5f00\u7684\u8d44\u6599\u6765\u770b\uff0c\u7231\u8fbe\u7535\u5b50\u662f\u4e00\u5bb6\u975e\u5e38\u6ce8\u91cd\u6280\u672f\u7814\u53d1\u7684\u516c\u53f8\uff0c\u65e2\u7136\u505a\u5230\u8fd9\u4e00\u6b65\u4f1a\u8ba9\u4eba\u6709\u4e9b\u8be7\u5f02\uff0c\u5374\u4e5f\u6ca1\u5230\u5938\u5f20\u7684\u7a0b\u5ea6\uff0c\u6210\u3001\u8096\u4e24\u4eba\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u542c\u5f20\u606a\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u5176\u5b9e\uff0c\u6211\u4eec\u4e5f\u62e5\u6709\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u5b8c\u6574\u7684\u6280\u672f\u65b9\u6848\uff0c\u5f53\u521dTI\u5c31\u662f\u5728\u6211\u4eec\u63d0\u4f9b\u7684\u6280\u672f\u65b9\u6848\u4e0a\uff0c\u6210\u529f\u7814\u53d1\u51fa\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\uff0c\u4e0e\u65af\u9ad8\u67cf\u5206\u4eab\u8fd9\u4e00\u5757\u7684\u5e02\u573a\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5728\u6b64\u4e4b\u524d\uff0c\u6211\u4eec\u4e5f\u8be6\u5c3d\u4e86\u89e3\u5230ESS\u5728\u5f00\u53d1\u89e3\u7801\u82af\u7247\u4e0a\u7684\u52aa\u529b\u4e0e\u6210\u679c\uff0c\u6211\u60f3ESS\u7531\u9526\u6e56\u6536\u8d2d\u4e4b\u540e\uff0c\u7ee7\u7eed\u4e4b\u524d\u7684\u5de5\u4f5c\uff0c\u80fd\u5f88\u5feb\u7814\u53d1\u62e5\u6709\u5b8c\u5168\u72ec\u7acb\u4e13\u5229\u6743\u7684\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6210\u89c1\u748b\u6781\u529b\u6d88\u5316\u5f20\u606a\u8bdd\u91cc\u7684\u4fe1\u606f\uff0c\u4e8b\u5b9e\u771f\u8ba9\u4eba\u96be\u4ee5\u63a5\u6536\uff0c\u4e0d\u8fc7\u89c9\u5f97\u9526\u6e56\u4e0eTI\u3001\u65af\u9ad8\u67cf\u7684\u5408\u4f5c\u4e4b\u4e2d\u8fd8\u662f\u6709\u51e0\u70b9\u8ba9\u4eba\u96be\u4ee5\u7406\u89e3\uff1a\u201c\u96be\u9053TI\u3001\u65af\u9ad8\u67cf\u8054\u5408\u5f00\u53d1\u7684\u7b2c\u4e09\u4ee3\u89c6\u9891\u89e3\u7801\u82af\u7247\u4e2d\u5c31\u6ca1\u6709\u9526\u6e56\u7684\u5229\u76ca\u5b58\u5728\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3TI\u4e0e\u65af\u9ad8\u67cf\u5bf9\u9526\u6e56\u5c31\u6ca1\u6709\u9650\u5236\u6761\u4ef6\u5417\uff0c\u7279\u522b\u660e\u77e5\u9053\u9526\u6e56\u4e00\u65e6\u6536\u8d2d\u6211\u4eecESS\u5c31\u5b8c\u5168\u80fd\u5f00\u53d1\u62e5\u6709\u72ec\u7acb\u77e5\u8bc6\u4ea7\u6743\u7684\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\uff0c\u5c31\u6ca1\u6709\u9650\u5236\u9526\u6e56\u6536\u8d2dESS\u6216\u8005\u9650\u5236\u9526\u6e56\u5411\u7b2c\u4e09\u65b9\u8f6c\u8ba9\u76f8\u5173\u4e13\u5229\u6280\u672f\uff1f\u65e2\u7136\u4f60\u4eec\u5728\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u5b58\u5728\u5171\u540c\u5229\u76ca\uff0c\u5e94\u8be5\u4e0d\u4f1a\u5bb9\u5fcd\u5176\u4e2d\u4e00\u65b9\u5728\u5171\u540c\u5229\u76ca\u4e4b\u5916\u518d\u8c0b\u53d6\u4ec0\u4e48\u5229\u76ca\u5427\uff1f\u9664\u975e\u5408\u540c\u6709\u6f0f\u6d1e\u2026\u2026\u201d\u65af\u9ad8\u67cf\u3001TI\u8fd9\u4e9b\u5927\u516c\u53f8\u7b7e\u5b9a\u7684\u5408\u540c\u4f1a\u5b58\u5728\u6f0f\u6d1e\u5417\uff1f</p></article>"}}}]);
