(window.webpackJsonp=window.webpackJsonp||[]).push([[644],{667:function(p){p.exports={chapterTitle:null,title:"\u7b2c643\u7ae0 \u70b9\u91d1\u5947\u672f",content:"<article><h2>\u7b2c643\u7ae0 \u70b9\u91d1\u5947\u672f</h2><p>\u3000\u3000\u542c\u5468\u5174\u4e1c\u7684\u53e3\u6c14\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u8ba4\u5b9a\u6b64\u65f6\u7684\u9526\u6e56\u8fd8\u65e0\u6cd5\u6d88\u5316\u4ece\u5fb7\u4eea\u624b\u91cc\u63a5\u8fc7\u6765\u7684\u624b\u673a\u6280\u672f\u624d\u4e00\u518d\u7684\u63a8\u5ef6\u624b\u673a\u4e0a\u5e02\u7684\u65f6\u95f4\uff0c\u5468\u5174\u4e1c\u8bdd\u91cc\u7684\u610f\u601d\u5927\u6982\u662f\u8bf4\u9526\u6e56\u4e00\u53e3\u60f3\u541e\u4e0b\u6574\u53ea\u5929\u9e45\uff0c\u5374\u7ed9\u5929\u9e45\u9ec4\u8089\u7ed9\u564e\u7740\u4e86\uff1f</p><p>\u3000\u3000\u8fd9\u5bb6\u4f19\u4e5f\u53ea\u6562\u6709\u8096\u745e\u6c11\u5728\u7684\u65f6\u5019\u51b7\u5632\u70ed\u8bbd\u7684\u53eb\u5524\u4e24\u58f0\uff0c\u5f20\u606a\u8fd8\u771f\u770b\u4e0d\u8d77\u4ed6\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e0d\u8d77\u5468\u5174\u4e1c\u53ef\u4ee5\uff0c\u5374\u4e0d\u4f1a\u77a7\u4e0d\u8d77\u5468\u5174\u4e1c\u80cc\u540e\u7684\u8054\u4fe1\u96c6\u56e2\uff0c\u8096\u745e\u6c11\u8fd8\u662f\u6709\u51e0\u5206\u80fd\u8010\u7684\uff0c\u4ed6\u6ca1\u6709\u7406\u4f1a\u5468\u5174\u4e1c\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u770b\u7740\u8096\u745e\u6c11\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7231\u8fbe\u662f\u5bb6\u7b28\u62d9\u7684\u516c\u53f8\uff0c\u5b66\u4e0d\u4f1a\u8054\u4fe1\u7684\u70b9\u91d1\u5947\u672f\uff0c\u53ea\u80fd\u6309\u7167\u81ea\u5df1\u7684\u6b65\u4f10\u4e00\u6b65\u4e00\u4e2a\u811a\u5370\u7684\u5f80\u524d\u8d76\uff0c\u8096\u603b\u65e2\u7136\u5982\u6b64\u671f\u5f85\u7231\u8fbe\u7684\u624b\u673a\u2026\u2026\u201d\u5f20\u606a\u56de\u5934\u95ee\u5085\u4fca\uff0c\u201c\u4f60\u8eab\u4e0a\u6709\u6ca1\u6709\u591a\u4f59\u7684\u6837\u673a\uff0c\u9001\u4e00\u53ea\u7ed9\u8096\u603b\u770b\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u4ece\u968f\u8eab\u5305\u91cc\u53d6\u51fa\u4e00\u53ea\u5907\u7528\u624b\u673a\u9012\u7ed9\u5f20\u606a\uff1b\u5f20\u606a\u4f38\u624b\u63a5\u65f6\uff0c\u624b\u6545\u610f\u6ede\u4e86\u4e00\u4e0b\uff0c\u8ba9\u624b\u673a\u64e6\u7740\u624b\u6307\u4ece\u9f50\u80f8\u9ad8\u7684\u9ad8\u5904\u843d\u5230\u5927\u7406\u77f3\u5730\u9762\u4e0a\uff0c\u201c\u94db\u201d\uff0c\u629b\u5149\u5851\u6599\u5916\u58f3\u7684\u673a\u8eab\u53cd\u5f39\u5230\u6709\u819d\u76d6\u9ad8\uff0c\u53c8\u843d\u5411\u5730\u76d8\u7ffb\u6eda\u5230\u9648\u5983\u84c9\u7684\u811a\u4e0b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f2f\u7740\u8170\u53bb\u6361\u624b\u673a\uff0c\u5012\u89c9\u5f97\u9648\u5983\u84c9\u957f\u88d9\u4e0b\u9732\u51fa\u6765\u7684\u4e00\u622a\u5149\u6e9c\u6e9c\u96ea\u767d\u4fee\u957f\u7684\u5c0f\u817f\u771f\u662f\u5f88\u7f8e\uff0c\u5634\u91cc\u5567\u5567\u7684\u53eb\u7740\uff0c\u8f6c\u8fc7\u5934\u8d23\u602a\u5085\u4fca\uff1a\u201c\u6211\u90fd\u6ca1\u6709\u63a5\u4f4f\uff0c\u4f60\u600e\u4e48\u5c31\u677e\u624b\u4e86\uff1f\u201d\u5c06\u624b\u673a\u9012\u7ed9\u8096\u745e\u6c11\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8096\u603b\u4e0d\u4f1a\u4ecb\u610f\u8fd9\u624b\u673a\u5728\u5730\u4e0a\u7838\u8fc7\u4e00\u56de\u5427\uff1f\u6211\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u7528\u8054\u4fe1\u7684\u624b\u673a\uff0c\u8981\u4e0d\u8096\u603b\u5c06\u8054\u4fe1\u7684\u624b\u673a\u5728\u5730\u4e0a\u7838\u4e00\u4e0b\u518d\u9001\u7ed9\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8096\u745e\u6c11\u6123\u5728\u90a3\u91cc\uff0c\u4ed6\u5f53\u7136\u6ca1\u6709\u52c7\u6c14\u5c06\u8054\u4fe1\u7684\u624b\u673a\u50cf\u5f20\u606a\u8fd9\u6837\u5728\u5927\u7406\u77f3\u5730\u9762\u4e0a\u7838\u4e00\u4e0b\u518d\u9001\u51fa\u53bb\uff0c\u8be7\u5f02\u7684\u8868\u60c5\u5728\u8138\u4e0a\u6ede\u4e86\u4e09\u4e94\u79d2\u949f\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u771f\u662f\u62b1\u6b49\uff0c\u624b\u91cc\u6ca1\u6709\u591a\u4f59\u7684\u624b\u673a\uff0c\u8981\u4e0d\u6539\u5929\u8ba9\u4eba\u5c06\u624b\u673a\u7ed9\u4f60\u9001\u53bb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u9e66\u9e49\u5b66\u820c\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u76f4\u4ee5\u6765\u90fd\u5f88\u671f\u5f85\u8054\u4fe1\u7684\u624b\u673a\u2026\u2026\u201d\u8bf4\u7f62\u5c31\u544a\u8f9e\u79bb\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8096\u745e\u6c11\u5c06\u89c6\u7ebf\u6536\u56de\u843d\u5728\u8fd9\u6b3e\u6d45\u7070\u8272\u76f4\u677f\u624b\u673a\u4e0a\u3002\u786c\u5851\u5916\u58f3\u7ecf\u8fc7\u629b\u5149\u5904\u7406\uff0c\u6cdb\u7740\u91d1\u5c5e\u7684\u5149\u6cfd\uff1b\u6309\u952e\u8bbe\u8ba1\u4e5f\u8fe5\u7136\u533a\u522b\u4e8e\u56fd\u5185\u5e38\u5185\u7684\u624b\u673a\u6309\u5065\uff0c\u540c\u6837\u629b\u5149\u5904\u7406\u7684\u786c\u5851\u6309\u952e\u6709\u6e21\u4eae\u91d1\u8272\u6d82\u5c42\u7684\u5d4c\u6761\u51f8\u51fa\uff0c\u624b\u6469\u6332\u7740\u6309\u952e\uff0c\u624b\u611f\u6781\u4e3a\u8212\u9002\uff1b\u6676\u5c4f\u6bd4\u56fd\u5185\u5e38\u89c1\u7684\u624b\u673a\u6676\u5c4f\u52a0\u5bbd\u7ea6\u6709\u56db\u5206\u4e4b\u4e00\u3002\u6574\u673a\u7ed9\u4eba\u7684\u89c2\u611f\u5c31\u8981\u6bd4\u8054\u4fe1\u4ece\u98de\u5229\u6d66\u5f15\u8fdb\u7684\u4e09\u6b3e\u673a\u578b\u9ad8\u8d35\u3001\u65f6\u5c1a\u592a\u591a\u2014\u2014\u9ad8\u8d35\u611f\u53ca\u65f6\u5c1a\u611f\u8981\u5f3a\u51fa\u592a\u591a\uff0c\u8ba9\u4eba\u6ca1\u6709\u5c06\u8054\u4fe1\u624b\u673a\u62ff\u51fa\u6765\u6bd4\u8f83\u7684\u52c7\u6c14\u2014\u2014\u4e22\u5730\u7838\u4e86\u4e00\u56de\uff0c\u673a\u8eab\u5374\u6ca1\u6709\u7559\u4e0b\u660e\u663e\u7684\u635f\u4f24\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u5c31\u7ad9\u5728\u4eba\u6d41\u6e4d\u52a8\u7684\u63a5\u673a\u5927\u5385\u7ec6\u7ec6\u6469\u6332\u7740\u624b\u91cc\u8fd9\u6b3e\u7231\u8fbe\u4e00\u518d\u63a8\u5ef6\u4e0a\u5e02\u65f6\u95f4\u7684\u624b\u673a\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u770b\u7740\u5f20\u606a\u4e00\u884c\u4eba\u79bb\u5f00\u63a5\u673a\u5927\u5385\u7684\u51fa\u53e3\uff0c\u624d\u5c06\u624b\u673a\u9012\u7ed9\u5468\u5174\u4e1c\uff1a\u201c\u5f00\u673a\u8bd5\u8bd5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u522b\u662f\u8fd9\u5c0f\u5b50\u552c\u4eba\uff1f\u201d\u5468\u5174\u4e1c\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u7231\u8fbe\u7684\u8fd9\u6b3e\u6837\u673a\u5916\u578b\u8981\u6f02\u4eae\u8bb8\u591a\uff0c\u90fd\u6000\u7591\u7231\u8fbe\u4ece\u5fb7\u4eea\u624b\u91cc\u63a5\u8fc7\u6765\u7684\u4e8c\u6d41\u6280\u672f\u600e\u4e48\u53ef\u80fd\u9020\u51fa\u8fd9\u4e48\u6f02\u4eae\u7684\u624b\u673a\uff1f\u63a5\u5230\u624b\u91cc\uff0c\u4e5f\u80fd\u53d1\u73b0\u8fd9\u6b3e\u624b\u673a\u7684\u7f3a\u70b9\uff0c\u6bd4\u5f53\u524d\u7684\u8f7b\u8584\u624b\u673a\u7565\u91cd\u4e00\u70b9\uff0c\u4f46\u8fd9\u4e0d\u662f\u4ec0\u4e48\u81f4\u547d\u7684\u7f3a\u9677\uff0c\u56fd\u5185\u5e02\u573a\u4e0a\u8fd8\u770b\u4e0d\u5230\u8f7b\u8584\u578b\u624b\u673a\uff0c\u4ed6\u5374\u4e0d\u5927\u4fe1\u7231\u8fbe\u7684\u624b\u673a\u8fd9\u4e48\u7838\u4e00\u4e0b\u8fd8\u4e1d\u6beb\u65e0\u635f\uff0c\u62ff\u51fa\u624b\u673a\u5361\u63d2\u5165\u63d2\u69fd\uff0c\u4ee4\u4ed6\u6781\u4e3a\u5931\u671b\u7684\uff0c\u624b\u673a\u6210\u529f\u5f00\u542f\uff0c\u770b\u6765\u521a\u624d\u90a3\u4e00\u6454\u5bf9\u624b\u673a\u6ca1\u6709\u9020\u6210\u4e1d\u6beb\u7684\u5f71\u54cd\uff0c\u8fde\u968f\u884c\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\uff0c\u5f00\u673a\u97f3\u4e50\u662f\u8fd1\u65e5\u5e38\u542c\u7684\u6c5f\u9edb\u513f\u4e00\u9996\u6b4c\u7684\u524d\u594f\u2014\u2014\u6c5f\u9edb\u513f\u662f\u7231\u8fbe\u7684\u5f62\u8c61\u4ee3\u8a00\u4eba\uff0c\u96be\u9053\u7231\u8fbe\u8981\u9009\u7528\u8fd9\u9996\u6b4c\u4f5c\u4e3a\u4ed6\u4eec\u624b\u673a\u4ea7\u54c1\u7684\u5e7f\u544a\u6b4c\u66f2\uff1f\u5373\u4f7f\u5728\u55a7\u56a3\u5608\u6742\u7684\u63a5\u673a\u5927\u5385\uff0c\u4f3c\u4e4e\u80fd\u542c\u51fa\u5f00\u673a\u97f3\u4e50\u7684\u97f3\u8272\u6bd4\u56fd\u5185\u5e02\u573a\u7684\u624b\u673a\u97f3\u4e50\u8981\u9971\u6ee1\u6e05\u4eae\uff0c\u201c\u84dd\u8272\u5c4f\uff1f\u4f60\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u4ece\u5468\u5174\u4e1c\u624b\u91cc\u63a5\u8fc7\u624b\u673a\uff0c\u4e0d\u662f\u56fd\u5185\u5e02\u573a\u5e38\u89c1\u7684\u9ed1\u767d\u5c4f\uff0c\u6676\u5c4f\u6cdb\u7740\u84dd\u8367\u8367\u7684\u5149\uff0c\u84dd\u8272\u7684\u6676\u5c4f\u663e\u793a\u611f\u89c2\u8981\u6bd4\u9ed1\u767d\u5c4f\u7ec6\u817b\u8bb8\u591a\u3002</p><p>\u3000\u3000\u201c\u82b1\u4fcf\u662f\u591f\u82b1\u4fcf\u2026\u2026\u201d\u5468\u5174\u4e1c\u5fc3\u91cc\u4ecd\u7136\u4e0d\u80af\u627f\u8ba4\u7231\u8fbe\u624b\u673a\u5c31\u5f3a\u51fa\u592a\u591a\uff0c\u201c\u6307\u4e0d\u5b9a\u662f\u8349\u5305\u809a\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u201d\u8981\u4e0d\u5468\u5174\u4e1c\u63d2\u4e0d\u51fa\u90a3\u756a\u8bdd\uff0c\u8fd8\u4e0d\u5b9a\u8981\u53d7\u5230\u5f20\u606a\u7684\u8fd9\u756a\u7f9e\u8fb1\uff0c\u8096\u745e\u6c11\u6ca1\u6709\u5fc3\u60c5\u8ddf\u5468\u5174\u4e1c\u6cbb\u6c14\uff0c\u5012\u4e5f\u4e0d\u60f3\u542c\u4ed6\u80e1\u4e71\u8bf4\u4ec0\u4e48\uff0c\u5373\u4f7f\u7231\u8fbe\u624b\u91cc\u53ea\u638c\u63e1\u4e8c\u6d41\u7684\u6280\u672f\uff0c\u4f46\u662f\u8fd9\u6b3e\u624b\u673a\u6446\u5728\u9762\u524d\u5374\u4e0d\u5f97\u4e0d\u627f\u8ba4\u6709\u7740\u4e00\u6d41\u6c34\u51c6\u7684\u611f\u89c9\uff0c\u8fd9\u624d\u662f\u505a\u4ea7\u54c1\u7684\u5b9e\u529b\u4f53\u73b0\uff0c\u5e76\u4e0d\u662f\u800d\u5634\u76ae\u5b50\u529f\u592b\u5c31\u80fd\u5f25\u8865\u7684\uff0c\u201c\u5e2e\u6211\u53bb\u4e70\u56de\u5317\u4eac\u7684\u673a\u7968\uff0c\u6211\u9a6c\u4e0a\u5c31\u56de\u5317\u4eac\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5468\u5174\u4e1c\u4e0e\u968f\u884c\u5de5\u4f5c\u4eba\u5458\u8bb6\u5f02\u4e0d\u89e3\u7684\u770b\u7740\u8096\u745e\u6c11\uff0c\u201c\u5609\u4fe1\u7535\u5b50\u8fd9\u8fb9\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u672c\u6765\u5c31\u6ca1\u6709\u901a\u77e5\u5609\u4fe1\u7535\u5b50\u8fd9\u8fb9\u6211\u4f1a\u4eb2\u81ea\u8fc7\u6765\uff0c\u4e8b\u60c5\u4f60\u53bb\u5904\u7406\uff0c\u5c31\u5f53\u6211\u6ca1\u6709\u8fc7\u6765\u2026\u2026\u201d\u8096\u745e\u6c11\u9501\u7740\u7709\u5934\uff0c\u8fd8\u65e0\u6cd5\u60f3\u8c61\u7231\u8fbe\u8fd9\u6b3e\u624b\u673a\u4e0a\u5e02\u4e4b\u540e\u5bf9\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u76ee\u524d\u4e09\u5bb6\u6240\u5206\u5272\u7684\u56fd\u4ea7\u624b\u673a\u5e02\u573a\u4f1a\u9020\u6210\u4ec0\u4e48\u6837\u7684\u51b2\u51fb\uff0c\u4f46\u662f\u8096\u745e\u6c11\u8fd9\u4e00\u523b\u77e5\u9053\u81ea\u5df1\u5e76\u6ca1\u6709\u62ff\u8054\u4fe1\u624b\u673a\u76f4\u63a5\u4e0e\u4e4b\u7ade\u4e89\u7684\u52c7\u6c14\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u523b\uff0c\u8096\u745e\u6c11\u518d\u6ca1\u6709\u5fc3\u60c5\u53bb\u6e38\u8bf4\u9999\u6e2f\u7684\u5408\u4f5c\u8005\u3001\u8bf4\u4e9b\u6f02\u4eae\u7684\u8a00\u8f9e\u6ca1\u8fc7\u51e0\u5929\u5374\u7ed9\u7231\u8fbe\u4e0a\u5e02\u7684\u4ea7\u54c1\u65e0\u60c5\u7684\u51fb\u788e\uff0c\u4ed6\u51b3\u5b9a\u5c06\u9999\u6e2f\u7684\u4e8b\u60c5\u8fd8\u662f\u4ea4\u7ed9\u5468\u5174\u4e1c\u53bb\u5904\u7406\uff0c\u8054\u4fe1\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u5b9e\u529b\u4e0e\u8db3\u591f\u7684\u7cbe\u529b\u53bb\u7ecf\u8425\u6d77\u5916\u5e02\u573a\uff0c\u8fd8\u662f\u6512\u8db3\u5341\u5206\u7684\u529b\u6c14\u5728\u56fd\u5185\u5e02\u573a\u8fce\u63a5\u7231\u8fbe\u7684\u6311\u6218\u5427\u3002\u8096\u745e\u6c11\u8fd9\u4e00\u523b\u4e5f\u89c9\u5f97\u5230\u9999\u6e2f\u6765\u7684\u610f\u4e49\u4e0d\u662f\u7279\u522b\u7684\u91cd\u8981\uff0c\u65e2\u7136\u4e0d\u91cd\u8981\uff0c\u8fd8\u4e0d\u4e58\u98de\u673a\u8fd4\u56de\u5317\u4eac\u3002</p><p>\u3000\u3000\u5468\u5174\u4e1c\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u90a3\u4e2a\u6d51\u7403\u7684\u4e00\u756a\u5f04\u59ff\u7684\u505a\u4f5c\u7adf\u7136\u5c06\u4f5c\u98ce\u786c\u6717\u7684\u8096\u745e\u6c11\u4ece\u9999\u6e2f\u903c\u56de\uff0c\u4ed6\u4eec\u8f6c\u9053\u5efa\u90ba\u7ec4\u5efa\u8054\u4fe1\u5357\u65b9\u516c\u53f8\uff0c\u53c8\u8f6c\u9053\u9999\u6e2f\uff0c\u51c6\u5907\u4ece\u9999\u6e2f\u5bfb\u53d6\u5408\u4f5c\u8005\uff0c\u8ba9\u8054\u4fe1\u7684\u4ea7\u54c1\u501f\u9999\u6e2f\u8fd9\u4e2a\u8df3\u677f\u6253\u5165\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e00\u6b65\u90fd\u8fd8\u6ca1\u6709\u5f00\u59cb\u5b9e\u65bd\uff0c\u8096\u745e\u6c11\u5c31\u9000\u7f29\u4e86\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u7684\u4fe1\u5fc3\u7ed9\u7231\u8fbe\u7684\u8fd9\u6b3e\u624b\u673a\u51fb\u5012\u4e86\u5417\uff1f</p><p>\u3000\u3000\u5373\u4f7f\u7231\u8fbe\u7684\u624b\u673a\u505a\u5f97\u518d\u6f02\u4eae\u6709\u4ec0\u4e48\u7528\uff1f\u56fd\u5185\u7684\u5e02\u573a\uff0c\u6709\u4e9b\u6e20\u9053\uff0c\u5e76\u4e0d\u662f\u5bf9\u6240\u6709\u5382\u5546\u90fd\u5f00\u53d1\u7684\u3002</p><p>\u3000\u3000\u5468\u5174\u4e1c\u6709\u4e9b\u7422\u78e8\u4e0d\u900f\u8096\u745e\u6c11\u5fc3\u91cc\u7684\u60f3\u6cd5\uff0c\u867d\u7136\u4e0d\u5927\u770b\u5f97\u8d77\u4ece\u90ae\u7535\u90e8\u7b2c\u5341\u4e8c\u7814\u7a76\u9662\u6280\u672f\u5458\u51fa\u8eab\u7684\u8096\u745e\u6c11\uff0c\u4f46\u6bd5\u7adf\u8096\u745e\u6c11\u8fd8\u662f\u4ed6\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u4ed6\u8fdf\u7591\u7684\u95ee\uff1a\u201c\u90a3\u9999\u6e2f\u8fd9\u8fb9\u600e\u4e48\u8c08\uff1f\u201d</p><p>\u3000\u3000\u201c\u8be5\u600e\u4e48\u8c08\u5c31\u600e\u4e48\u8c08\uff0c\u4f60\u662f\u5357\u65b9\u516c\u53f8\u7684\u603b\u7ecf\u7406\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u672c\u8be5\u4ea4\u7ed9\u4f60\u8d1f\u8d23\u3002\u201d\u8096\u745e\u6c11\u8fd9\u65f6\u5019\u4e0d\u518d\u8ba4\u4e3a\u6709\u628a\u63e1\u53bb\u5f00\u62d3\u6d77\u5916\u5e02\u573a\uff0c\u4e5f\u51b3\u5b9a\u8054\u4fe1\u77ed\u671f\u5185\u4e0d\u4f1a\u5c06\u8fd9\u4e2a\u505a\u4e3a\u4e3b\u8981\u5e02\u573a\u76ee\u6807\uff0c\u81ea\u7136\u5c31\u653e\u6743\u4e22\u7ed9\u5468\u5174\u4e1c\u53bb\u641e\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u53ea\u662f\u6076\u8da3\u5473\u7684\u523a\u6fc0\u5468\u5174\u4e1c\u3001\u8096\u745e\u6c11\u4e00\u4e0b\uff0c\u53ef\u6ca1\u6709\u60f3\u5230\u80fd\u5c06\u8096\u745e\u6c11\u4ece\u673a\u573a\u76f4\u63a5\u903c\u56de\u5317\u4eac\uff0c\u5750\u5230\u8f66\u4e0a\uff0c\u95ee\u9648\u5983\u84c9\u600e\u4e48\u4e0e\u5468\u5174\u4e1c\u4ed6\u4eec\u78b0\u5230\u2026\u2026</p><p>\u3000\u3000\u201c\u675c\u98de\u8f6c\u4ea4\u7ed9\u6211\u7684\u673a\u7968\u662f\u5546\u52a1\u8231\uff0c\u5c31\u8ddf\u90a3\u4e2a\u60f9\u4eba\u538c\u6076\u7684\u5bb6\u4f19\u524d\u540e\u6392\uff0c\u5728\u98de\u673a\u4e0a\u4ed6\u5c31\u6ca1\u505c\u8fc7\u8052\u566a\uff0c\u4e0d\u505c\u7684\u5439\u5618\u4ed6\u5bb6\u5982\u4f55\u5982\u4f55\u4e86\u5f97\uff0c\u4e5f\u4e0d\u7ba1\u522b\u4eba\u7231\u542c\u4e0d\u7231\u542c\uff0c\u8033\u7a9d\u5b50\u90fd\u8d77\u8327\u5b50\u4e86\u2026\u2026\u201d\u9648\u5983\u84c9\u5750\u5230\u8f66\u91cc\u624d\u72e0\u72e0\u7684\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u539f\u6765\u4f60\u4eec\u662f\u8ba4\u8bc6\u7684\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u8f7b\u7b11\u7740\uff0c\u4e5f\u4e0d\u6653\u5f97\u5468\u5174\u4e1c\u3001\u8096\u745e\u6c11\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4f1a\u4ece\u5efa\u90ba\u767b\u673a\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u5bb6\u4f19\uff0c\u8ddf\u6211\u4e00\u6837\uff0c\u90fd\u4e0d\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u2026\u2026\u7269\u4ee5\u7c7b\u805a\uff0c\u4eba\u4ee5\u7fa4\u5206\u3002\u201d\u5f20\u606a\u81ea\u5632\u7684\u8ddf\u5510\u5a67\u3001\u9648\u5983\u84c9\u89e3\u91ca\u8096\u745e\u6c11\u3001\u5468\u5174\u4e1c\u4ed6\u4eec\u7684\u6765\u5386\u3002\u9648\u5983\u84c9\u662f\u90a3\u79cd\u5170\u8d28\u8559\u5fc3\u7684\u5973\u5b69\u5b50\uff0c\u53c8\u4e0e\u5510\u5a67\u4ea4\u597d\u591a\u5e74\uff0c\u90fd\u6ca1\u6709\u5fc5\u8981\u5728\u5979\u9762\u524d\u523b\u610f\u9690\u7792\u4ec0\u4e48\uff0c\u5f53\u7136\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5fc5\u8981\u8ddf\u5979\u4ea4\u4ec0\u4e48\u5e95\u3002</p><p>\u3000\u3000\u5510\u5a67\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u8054\u4fe1\u7684\u70b9\u91d1\u5947\u672f\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u89e3\u91ca\u9053\uff0c\u201c\u6d77\u5916\u624b\u673a\u5382\u5546\u7684\u4ea7\u54c1\u8fdb\u5165\u5185\u5730\u5e02\u573a\u53d7\u5230\u914d\u989d\u4f9b\u7ed9\u7684\u9650\u5236\uff0c\u4e0d\u53ef\u80fd\u65e0\u9650\u7684\u63d0\u9ad8\u5185\u5730\u5e02\u573a\u7684\u9500\u91cf\u3002\u5f53\u5185\u5730\u7684\u624b\u673a\u5e02\u573a\u7ee7\u7eed\u589e\u6da8\uff0c\u53d7\u914d\u989d\u9650\u5236\u653f\u7b56\u7684\u4fdd\u62a4\uff0c\u5185\u5730\u624b\u673a\u5e02\u573a\u81ea\u7136\u5c31\u4e3a\u56fd\u4ea7\u624b\u673a\u7a7a\u51fa\u4e00\u5927\u5757\u7684\u7f3a\u53e3\u6765\u3002\u8fd9\u4e00\u5757\u7684\u7f3a\u53e3\uff0c\u662f\u4e0d\u9700\u8981\u4e0e\u56fd\u5916\u624b\u673a\u76f4\u63a5\u7ade\u4e89\u7684\u2026\u2026\u56fd\u4ea7\u624b\u673a\u76ee\u524d\u53ea\u53d1\u51fa\u56db\u5f20\u751f\u4ea7\u6267\u7167\uff0c\u50cf\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\uff0c\u53d6\u5f97\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u76f4\u63a5\u5f15\u8fdb\u56fd\u5916\u624b\u673a\u673a\u578b\uff0c\u4e5f\u4e0d\u9700\u8981\u6295\u5165\u7814\u53d1\uff0c\u6709\u7684\u751a\u81f3\u90fd\u4e0d\u7ec4\u7ec7\u6700\u57fa\u672c\u7684\u751f\u4ea7\uff0c\u62ff\u8fdb\u53e3\u624b\u673a\u8d34\u7740\u724c\u5b50\u76f4\u63a5\u5c31\u53bb\u586b\u8865\u56fd\u5185\u5e02\u573a\u7684\u7a7a\u7f3a\uff0c\u4f60\u8bf4\u8fd9\u4e0d\u662f\u70b9\u91d1\u5947\u672f\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u8fd9\u6837\u554a\u2026\u2026\u201d\u5510\u5a67\u8bf4\u9053\uff0c\u201c\u90a3\u914d\u989d\u9650\u5236\u8fd8\u6709\u4ec0\u4e48\u5b9e\u9645\u7684\u610f\u4e49\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u7684\u786e\u6ca1\u6709\u4ec0\u4e48\u5b9e\u9645\u7684\u610f\u4e49\u2026\u2026\u201d</p></article>"}}}]);
