(window.webpackJsonp=window.webpackJsonp||[]).push([[65],{87:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u518d\u6b21\u53d8\u5f62",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u518d\u6b21\u53d8\u5f62</h2><p>\u5728\u541e\u566c\u5b8c\u6240\u6709\u7684\u5c38\u4f53\u540e\uff0c\u6d3b\u5316\u690d\u7269\u6e10\u6e10\u67af\u840e\u4e0b\u53bb\uff0c\u6700\u540e\u5728\u9ed1\u8272\u706b\u6d77\u4e2d\u5316\u4e3a\u7070\u70ec\u3002\u5384\u8fd0\u7ee7\u7eed\u964d\u4e34\u5230\u8d1d\u6234\u86ee\u65cf\u8eab\u4e0a\uff0c\u8001\u4eba\u3001\u5b69\u5b50\u3001\u5973\u5b50\u90fd\u5df2\u7ecf\u88ab\u4f5c\u4e3a\u796d\u54c1\u732e\u4e0a\uff0c\u5269\u4e0b\u6765\u7684\u81ea\u7136\u4fbf\u662f\u7537\u5b50\u4e86\u3002</p><p>\u56e0\u4e3a\u906d\u5230\u6563\u5854\u6797\u4f1a\u7684\u653b\u51fb\uff0c\u540e\u6765\u53c8\u906d\u5230\u4eba\u9762\u72ee\u7684\u5077\u88ad\uff0c\u5982\u4eca\u8d1d\u6234\u86ee\u65cf\u4e2d\u591a\u662f\u8001\u5f31\u5987\u5b7a\uff0c\u6210\u5e74\u7537\u5b50\u5927\u591a\u6218\u6b7b\u3002\u6b64\u65f6\u5728\u796d\u575b\u4e0b\u7684\uff0c\u4e5f\u53ea\u5269\u4e0b\u56db\u4e94\u5341\u4e2a\u3002</p><p>\u4ed6\u4eec\u5fd0\u5fd1\u4e0d\u5b89\u5730\u6218\u6817\u7740\uff0c\u770b\u7740\u81ea\u5df1\u7684\u4eb2\u4eba\u88ab\u4e00\u6ce2\u6ce2\u9001\u8fdb\u6b7b\u4ea1\u7684\u56fd\u5ea6\uff0c\u5374\u53c8\u65e0\u529b\u53cd\u6297\uff0c\u4e5f\u6ca1\u6709\u80c6\u91cf\u3002\u6700\u540e\uff0c\u8f6e\u5230\u4ed6\u4eec\u81ea\u5df1\u4e86\u3002</p><p>\u88ab\u9a71\u8d76\u7740\uff0c\u4ed6\u4eec\u6218\u6218\u5162\u5162\u5730\u4e0a\u4e86\u796d\u575b\uff0c\u4e0d\u77e5\u9053\u7b49\u5f85\u81ea\u5df1\u7684\u5c06\u4f1a\u662f\u4ec0\u4e48\u547d\u8fd0\u3002\u8482\u5a1c\u5c91\u8eab\u5904\u9ed1\u8272\u7684\u706b\u6d77\u4e2d\uff0c\u70c8\u7130\u5347\u817e\uff0c\u51e0\u4e4e\u8981\u5c06\u5979\u7684\u8eab\u5f62\u5b8c\u5168\u541e\u6ca1\u3002\u8d1d\u6234\u86ee\u65cf\u4eec\u4e0d\u6562\u9760\u8fd1\uff0c\u4ed6\u4eec\u754f\u7f29\u7740\u5c3d\u53ef\u80fd\u5730\u8d34\u7740\u796d\u575b\u7684\u8fb9\u6cbf\u7ad9\u7acb\u3002</p><p>\u516d\u4e2a\u5973\u6027\u796d\u53f8\u626d\u52a8\u86c7\u8170\u8d70\u8fc7\u6765\u3002\u5979\u4eec\u53d1\u51fa\u5a07\u5a9a\u7684\u7b11\u58f0\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u6ee1\u662f\u6311\u9017\u8bf1\u60d1\uff0c\u5728\u86ee\u65cf\u7537\u5b50\u4e2d\u6e38\u8d70\u7740\uff0c\u4eff\u4f5b\u5728\u5bfb\u627e\u6ee1\u610f\u7684\u5bf9\u8c61\u3002\u5982\u679c\u4ec5\u770b\u4e0a\u534a\u8eab\uff0c\u786e\u5b9e\u90fd\u662f\u7f8e\u8c8c\u800c\u6027\u611f\u7684\u5973\u5b50\uff0c\u9762\u5bb9\u7f8e\u8273\uff0c\u8eab\u6750\u706b\u8fa3\uff0c\u4ec5\u4ee5\u80f8\u524d\u7684\u7855\u5927\u4e30\u6ee1\u7a0b\u5ea6\u800c\u8bba\uff0c\u5c31\u7edd\u975e\u5bfb\u5e38\u4eba\u7c7b\u5973\u5b50\u6240\u80fd\u4f01\u53ca\u3002\u4f46\u8fd9\u4e9b\u86ee\u65cf\u7537\u5b50\u5982\u4eca\u8eab\u5904\u9669\u5730\uff0c\u6027\u547d\u582a\u5fe7\uff0c\u54ea\u91cc\u987e\u5f97\u4e0a\u6b23\u8d4f\u6625\u8272\u7f8e\u666f\u3002\u4f55\u51b5\u53ea\u8981\u76ee\u5149\u7a0d\u7a0d\u4e0b\u79fb\uff0c\u770b\u5230\u4e0b\u534a\u8eab\u90a3\u6c34\u6876\u7c97\u7684\u78a7\u7eff\u86c7\u8eab\uff0c\u53ea\u8981\u662f\u4eba\u5c31\u90fd\u5413\u5f97\u9633\u75ff\u4e86\u3002\u5c31\u7b97\u95ed\u4e0a\u773c\u775b\u4e0d\u770b\uff0c\u542c\u5230\u86c7\u5c3e\u5728\u5730\u9762\u4e0a\u62d6\u6765\u62d6\u53bb\u7684\u6c99\u6c99\u58f0\uff0c\u4e5f\u8db3\u4ee5\u4ee4\u4eba\u5fc3\u60ca\u8089\u8df3\u3002</p><p>\u4f55\u51b5\u6709\u4e9b\u4e8b\u60c5\uff0c\u5e76\u4e0d\u662f\u95ed\u4e0a\u773c\u775b\u5f53\u9e35\u9e1f\u5c31\u80fd\u907f\u514d\u7684\u3002</p><p>\u796d\u53f8\u4eec\u5206\u522b\u9009\u4e2d\u4e86\u81ea\u5df1\u7684\u76ee\u6807\uff0c\u7c97\u5927\u7684\u86c7\u5c3e\u76d8\u7ed5\u7740\uff0c\u5c06\u4ed6\u4eec\u62c9\u51fa\u4eba\u7fa4\u3002\u86ee\u65cf\u7537\u5b50\u4eec\u745f\u745f\u53d1\u6296\uff0c\u8138\u8272\u82cd\u767d\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e9b\u5973\u4eba\u9762\u72ee\u4f1a\u5982\u4f55\u5bf9\u5f85\u81ea\u5df1\u3002\u7136\u540e\u2026\u2026\u4ed6\u4eec\u611f\u89c9\u5230\u51b0\u51b7\u7684\u86c7\u5c3e\u5c16\u7aef\u63a2\u5165\u81ea\u5df1\u7684\u8863\u670d\u91cc\uff0c\u626f\u4e0b\u88e4\u5b50\uff0c\u5206\u5f00\u56e0\u4e3a\u7d27\u5f20\u800c\u98a4\u6296\u7684\u81c0\u90e8\u2026\u2026</p><p>\u60e8\u53eb\u58f0\u6b64\u8d77\u5f7c\u4f0f\uff0c\u54cd\u5f7b\u6574\u4e2a\u5927\u5385\u3002\u8fd9\u573a\u666f\u5df2\u7ecf\u4e0d\u80fd\u8bf4\u662f\u6deb\u7cdc\uff0c\u7b80\u76f4\u5c31\u662f\u8840\u8165\u9ed1\u6697\u4ee5\u53ca\u4ee4\u4eba\u5455\u5410\uff0c\u5b8c\u5168\u7f3a\u4e4f\u534a\u70b9\u7f8e\u611f\u2014\u2014\u81f3\u5c11\u4ee5\u6b63\u5e38\u4eba\u7684\u89d2\u5ea6\u6765\u770b\u662f\u5982\u6b64\u3002\u796d\u575b\u4e0b\u7684\u4eba\u9762\u72ee\u4eec\u8138\u8272\u4e5f\u90fd\u4e0d\u751a\u597d\u770b\uff0c\u4ed6\u4eec\u65e0\u4e00\u4f8b\u5916\u5730\u4f4e\u4e0b\u5934\uff0c\u88c5\u4f5c\u6ca1\u770b\u89c1\u3002\u4f46\u5973\u796d\u53f8\u4eec\u4e1d\u6beb\u4e0d\u53d7\u5f71\u54cd\uff0c\u5979\u4eec\u597d\u6574\u4ee5\u6687\u5730\u73a9\u5f04\u7740\uff0c\u9732\u51fa\u6ee1\u8db3\u7684\u7b11\u5bb9\uff0c\u4eff\u4f5b\u8fd9\u662f\u975e\u5e38\u7f8e\u5999\u7684\u4eab\u53d7\u3002</p><p>\u6b63\u5982\u4eba\u7c7b\u5973\u5b50\u7ecf\u53d7\u4e0d\u4f4f\u4eba\u9762\u72ee\u7684\u8e42\u8e8f\uff0c\u4eba\u7c7b\u7537\u5b50\u4e5f\u65e0\u6cd5\u627f\u53d7\u4eba\u9762\u72ee\u7684\u6467\u6b8b\uff0c\u5f88\u5feb\uff0c\u516d\u540d\u7537\u5b50\u5c31\u5df2\u7ecf\u534a\u6b7b\u4e0d\u6d3b\u3002\u5973\u796d\u53f8\u91cd\u65b0\u9009\u62e9\u4e86\u516d\u4e2a\u76ee\u6807\uff0c\u76f8\u540c\u7684\u573a\u666f\u518d\u4e00\u6b21\u4e0a\u6f14\uff0c\u76f4\u5230\u6700\u540e\uff0c\u56e0\u4e3a\u4eba\u6570\u7684\u5173\u7cfb\uff0c\u6070\u597d\u5c31\u53ea\u5269\u4e0b\u4e24\u4e2a\u2014\u2014\u4e8e\u662f\u8fd9\u4e24\u4e2a\u4e0d\u5e78\u8005\u5206\u62c5\u4e86\u516d\u4e2a\u5973\u796d\u53f8\u7684\u86c7\u5c3e\u3002</p><p>\u8482\u5a1c\u5c91\u4e00\u76f4\u5fae\u7b11\u7740\u770b\u7740\u8fd9\u4e00\u5207\uff0c\u76f4\u5230\u6240\u6709\u7684\u8d1d\u6234\u86ee\u65cf\u7537\u5b50\u90fd\u5012\u5728\u5730\u4e0a\uff0c\u516d\u4e2a\u5973\u796d\u53f8\u56de\u5f52\u539f\u4f4d\uff0c\u4e8e\u662f\u5979\u518d\u6b21\u5ff5\u8bf5\u5492\u6587\u3002\u4e00\u5ea6\u6d88\u5931\u7684\u96f7\u9e23\u58f0\u518d\u5ea6\u54cd\u8d77\uff0c\u7a7a\u4e2d\u6cfc\u4e0b\u6dcb\u6f13\u7684\u9c9c\u8840\uff0c\u6574\u6574\u6301\u7eed\u4e86\u5341\u5206\u949f\uff0c\u5c06\u6574\u4e2a\u796d\u575b\u90fd\u67d3\u6210\u79fd\u6076\u7684\u6697\u7ea2\uff0c\u9ed1\u8272\u706b\u7130\u88ab\u7184\u706d\u4e86\u3002</p><p>\u796d\u7940\u7684\u7b2c\u56db\u4e2a\u6b65\u9aa4\u201c\u8840\u96e8\u201d\u5b8c\u6210\u3002</p><p>\u203b\u203b\u203b</p><p>\u6885\u83f2\u65af\u7b49\u5f85\u7740\uff0c\u5979\u77e5\u9053\u4e8b\u60c5\u8fd8\u6ca1\u6709\u7ed3\u675f\u3002\u9ece\u660e\u4e4b\u77f3\u8fd8\u6ca1\u6709\u51fa\u73b0\uff0c\u8fd9\u662f\u4eca\u665a\u51c6\u5907\u732e\u7ed9\u683c\u62c9\u5179\u7279\u7684\u771f\u6b63\u796d\u54c1\u3002\u6b64\u524d\u7684\u8fd9\u4e9b\u8d1d\u6234\u86ee\u65cf\uff0c\u4e0d\u8fc7\u662f\u6b63\u9910\u524d\u7684\u751c\u70b9\u7f62\u4e86\u3002</p><p>\u5979\u7b49\u5f85\u7740\uff0c\u7136\u540e\u5979\u770b\u89c1\u4e86\u683c\u5170\u7279\uff0c\u548c\u5979\u4e00\u8def\u540c\u884c\u800c\u6765\u7684\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\uff0c\u7ea2\u5934\u53d1\u7684\u5766\u7eb3\u65af\u7684\u52a9\u624b\u3002</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u6885\u83f2\u65af\u5176\u5b9e\u4e0d\u751a\u559c\u6b22\u5170\u68ee\u5fb7\u5c14\u7684\u4fe1\u5f92\uff0c\u603b\u89c9\u5f97\u4ed6\u4eec\u90fd\u5f88\u7b28\uff0c\u5f92\u6709\u7406\u60f3\u5374\u65e0\u89c4\u5212\uff0c\u5f92\u6709\u70ed\u60c5\u5374\u65e0\u667a\u6167\u3002\u4e0d\u8fc7\u683c\u5170\u7279\u5012\u662f\u4e2a\u4f8b\u5916\uff0c\u5979\u5bf9\u8fd9\u4e2a\u5e74\u8f7b\u7684\u7267\u5e08\u4e00\u76f4\u8fd8\u5370\u8c61\u4e0d\u9519\u3002</p><p>\u201c\u867d\u7136\u5e7c\u7a1a\u4e86\u70b9\uff0c\u4e0d\u8fc7\u81f3\u5c11\u8111\u5b50\u4e0d\u6b7b\u677f\uff0c\u201d\u5979\u5728\u5fc3\u4e2d\u662f\u5982\u6b64\u8bc4\u4ef7\u7684\uff0c\u201c\u5230\u5e95\u662f\u5e74\u8f7b\u7684\u7f18\u6545\uff0c\u8fd8\u6ca1\u6709\u88ab\u90a3\u5e2e\u8001\u5934\u5b50\u5e26\u574f\u3002\u201d</p><p>\u5176\u5b9e\u4ee5\u5e74\u9f84\u800c\u8bba\uff0c\u6885\u83f2\u65af\u81ea\u5df1\u5c31\u662f\u6807\u51c6\u7684\u5c11\u5973\u3002\u4f46\u6216\u8bb8\u662f\u4ece\u5c0f\u7ecf\u5386\u7684\u4e8b\u60c5\u592a\u591a\uff0c\u5728\u5176\u4ed6\u5b69\u5b50\u8fd8\u61f5\u61f5\u61c2\u61c2\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u6574\u65e5\u5728\u751f\u6b7b\u8fb9\u7f18\u6e38\u8d70\uff0c\u5bfc\u81f4\u5979\u5728\u5fc3\u6001\u4e0a\uff0c\u603b\u662f\u4e0d\u81ea\u89c9\u5730\u5fd8\u8bb0\u81ea\u5df1\u5176\u5b9e\u4e5f\u8fd8\u662f\u4e2a\u5e74\u8f7b\u4eba\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6885\u83f2\u65af\u770b\u5230\u683c\u5170\u7279\u6ca1\u6b7b\uff0c\u8fd8\u662f\u6709\u4e9b\u9ad8\u5174\u7684\u3002\u5f53\u65f6\u5728\u57c3\u65af\u8003\u57ce\u88ab\u4eba\u9762\u72ee\u5305\u56f4\uff0c\u63d0\u5c14\u7267\u5e08\u6218\u6b7b\uff0c\u643a\u5e26\u4fdd\u7ba1\u9ece\u660e\u4e4b\u77f3\u7684\u5766\u7eb3\u65af\u6218\u6b7b\uff0c\u552f\u6709\u683c\u5170\u7279\u5f53\u65f6\u88ab\u4eba\u9762\u72ee\u7fa4\u6df9\u6ca1\uff0c\u8fd8\u4ee5\u4e3a\u88ab\u4e71\u5200\u5206\u5c38\u4e86\uff0c\u539f\u6765\u8fd8\u6d3b\u7740\u3002</p><p>\u4e0d\u4f46\u6d3b\u7740\uff0c\u800c\u4e14\u4f3c\u4e4e\u6d3b\u5f97\u8fd8\u4e0d\u9519\uff0c\u6beb\u53d1\u65e0\u4f24\uff0c\u9664\u4e86\u8eab\u4e0a\u7684\u7267\u5e08\u767d\u888d\u88ab\u626f\u5f97\u7834\u7834\u70c2\u70c2\u3002\u4ed6\u7684\u773c\u795e\u4e2d\u6709\u60ca\u60e7\u60f6\u6050\u4e4b\u8272\uff0c\u4f46\u5e76\u65e0\u5446\u6ede\u611f\uff0c\u5e94\u8be5\u6ca1\u6709\u88ab\u50ac\u7720\u6216\u8005\u63a7\u5236\u3002</p><p>\u4ed6\u4e00\u6b65\u6b65\u8d70\u4e0a\u796d\u575b\uff0c\u7ad9\u5728\u8482\u5a1c\u5c91\u5bf9\u9762\u3002\u201c\u4f60\u4eec\u5230\u5e95\u8981\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u5636\u54d1\u7740\u55d3\u5b50\u95ee\uff0c\u58f0\u97f3\u5fae\u5fae\u98a4\u6296\u3002</p><p>\u8482\u5a1c\u5c91\u5fae\u7b11\u7740\uff0c\u5979\u957f\u5f97\u5341\u5206\u7f8e\u8273\uff0c\u8fd8\u80dc\u8fc7\u624b\u4e0b\u7684\u5973\u796d\u53f8\u4eec\u4e00\u7b79\u3002\u8138\u578b\u5c16\u5c16\uff0c\u9f3b\u633a\u5507\u8584\uff0c\u773c\u775b\u7565\u663e\u72ed\u957f\uff0c\u659c\u7709\u4e0a\u6311\uff0c\u773c\u89d2\u5fae\u5149\u95ea\u70c1\uff0c\u663e\u5f97\u65e2\u5996\u5a9a\u53c8\u90aa\u6c14\u3002\u5979\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u957f\u888d\uff0c\u5341\u5206\u5bbd\u677e\uff0c\u5374\u4f9d\u65e7\u63a9\u76d6\u4e0d\u4f4f\u80f8\u524d\u7684\u6c79\u6d8c\u9f13\u80c0\uff0c\u7a0d\u4e00\u884c\u52a8\uff0c\u4fbf\u4eff\u4f5b\u6709\u4e24\u56e2\u7855\u5927\u7684\u8089\u7403\u6b63\u5728\u957f\u888d\u4e2d\u5f39\u8df3\u3002</p><p>\u5979\u7684\u4e0b\u534a\u8eab\u662f\u86c7\u5f62\uff0c\u76d8\u66f2\u8d77\u6765\uff0c\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u4fbf\u548c\u683c\u5170\u7279\u5dee\u4e0d\u591a\u9ad8\u3002\u5979\u7b11\u541f\u541f\u5730\u5927\u91cf\u7740\u9762\u524d\u7684\u5e74\u8f7b\u4eba\uff0c\u7267\u5e08\u7d27\u5f20\u7740\uff0c\u4e0d\u77e5\u9053\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u7528\u610f\u3002</p><p>\u201c\u522b\u7d27\u5f20\uff0c\u201d\u8482\u5a1c\u5c91\u5a9a\u58f0\u8bf4\uff0c\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u548c\u4f60\u505a\u7b14\u4ea4\u6613\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d</p><p>\u201c\u5e2e\u6211\u4eec\u5b8c\u6210\u796d\u7940\uff0c\u201d\u5979\u7b11\u7740\uff0c\u58f0\u97f3\u53c8\u5a07\u53c8\u5ae9\uff0c\u4eff\u4f5b\u5341\u4f59\u5c81\u7684\u5c11\u5973\uff0c\u201c\u8fd8\u5dee\u6700\u540e\u4e00\u6b65\uff0c\u9700\u8981\u4e00\u4e2a\u4f18\u79c0\u7684\u7537\u4eba\u2026\u2026\u5c31\u50cf\u4f60\u8fd9\u6837\u3002\u201d</p><p>\u7267\u5e08\u60ca\u9a87\u5730\u540e\u9000\uff0c\u4f46\u65e0\u5f62\u7684\u529b\u91cf\u963b\u62e6\u4f4f\u4e86\u4ed6\uff0c\u538b\u5236\u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u4ee4\u4ed6\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u201c\u522b\u90a3\u4e48\u5bb3\u6015\uff0c\u201d\u8482\u5a1c\u5c91\u683c\u683c\u5730\u7b11\u8d77\u6765\uff0c\u4eff\u4f5b\u7ecf\u9a8c\u4e30\u5bcc\u7684\u5973\u5b50\u770b\u5230\u4e00\u4e2a\u5c40\u4fc3\u4e0d\u5b89\u7684\u7537\u5b69\uff0c\u201c\u8fd9\u662f\u5f88\u5feb\u4e50\u7684\u4e8b\u60c5\u2026\u2026\u6216\u8005\uff0c\u4f60\u6253\u7b97\u544a\u8bc9\u6211\u4f60\u4ece\u6ca1\u5c1d\u8bd5\u8fc7\uff1f\u201d\u5979\u4f4e\u7b11\u7740\uff0c\u624b\u6307\u8f7b\u8f7b\u4ece\u7267\u5e08\u7684\u4e0b\u5df4\u4e0a\u62c2\u8fc7\uff0c\u4eff\u4f5b\u82e5\u6709\u82e5\u65e0\u7684\u6311\u9017\uff0c\u201c\u4f60\u4ee5\u524d\u6ca1\u6709\u548c\u5973\u4eba\u2026\u2026\u6709\u8fc7\u90a3\u79cd\u7ecf\u9a8c\uff1f\u201d</p><p>\u7267\u5e08\u60f3\u8eb2\u907f\uff0c\u4f46\u8fd9\u662f\u5f92\u52b3\u7684\uff0c\u8482\u5a1c\u5c91\u7684\u6cd5\u672f\u6682\u65f6\u56f0\u7f1a\u4f4f\u4e86\u4ed6\u7684\u8eab\u4f53\u3002\u201c\u53ef\u662f\uff0c\u201d\u4ed6\u4ece\u54bd\u5589\u91cc\u6324\u51fa\u60e7\u6015\u7684\u58f0\u97f3\uff0c\u201c\u4f60\u662f\u4e2a\u2026\u2026\u201d</p><p>\u201c\u54e6\uff1f\u201d\u8482\u5a1c\u5c91\u504f\u4e86\u504f\u5934\uff0c\u8ba9\u4e00\u5934\u6de1\u84dd\u8272\u7684\u957f\u53d1\u6d41\u6cfb\u4e0b\u6765\uff0c\u4ece\u5de6\u80a9\u4e0a\u62ab\u4e0b\uff0c\u6563\u5728\u80f8\u53e3\uff0c\u201c\u4f60\u4e0d\u559c\u6b22\u6211\u73b0\u5728\u7684\u6a21\u6837\uff1f\u6ca1\u5173\u7cfb\u3002\u201d</p><p>\u5979\u62ac\u8d77\u53cc\u81c2\uff0c\u5341\u6307\u5f20\u5f00\u8df3\u8dc3\u8f6e\u98de\uff0c\u4eff\u4f5b\u5728\u5f39\u594f\u4e00\u5f20\u770b\u4e0d\u89c1\u7684\u7434\u3002\u6697\u94f6\u8272\u7684\u5149\u8292\u4ece\u5979\u8eab\u4e0a\u53d1\u51fa\u6765\uff0c\u8482\u5a1c\u5c91\u7684\u8eab\u4f53\u53d1\u751f\u53d8\u5316\u3002\u5979\u7684\u4e0a\u534a\u8eab\u4f9d\u65e7\u4fdd\u6301\u539f\u72b6\uff0c\u4e0b\u534a\u8eab\u7684\u86c7\u5c3e\u5374\u6e10\u6e10\u7f29\u5c0f\uff0c\u6e10\u6e10\u6d88\u5931\uff0c\u4e00\u9635\u98ce\u6070\u5230\u597d\u5904\u5730\u5439\u6765\uff0c\u5c06\u5979\u7684\u957f\u888d\u6380\u8d77\uff0c\u9732\u51fa\u96ea\u767d\u4fee\u957f\u7684\u4e00\u53cc\u7389\u817f\u3002</p><p>\u5979\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u4e2a\u4eba\u7c7b\u5973\u5b50\u3002</p><p>\u53d8\u5f62\u672f\u771f\u662f\u4e2a\u597d\u9b54\u6cd5\u3002</p></article>"}}}]);
