(window.webpackJsonp=window.webpackJsonp||[]).push([[65],{88:function(p){p.exports={chapterTitle:null,title:"\u7b2c1053\u7ae0 \u76f8\u6bd4\u4e4b\u4e0b\u771f\u5bd2\u9178",content:"<article><h2>\u7b2c1053\u7ae0 \u76f8\u6bd4\u4e4b\u4e0b\u771f\u5bd2\u9178</h2><p>\u3000\u3000\u516b\u6708\u4e8c\u5341\u516d\u65e5\uff0c\u67f3\u5fd7\u6210\u4ee3\u8868\u4e2d\u6676\u5fae\u82af\u4e0e\u5fb7\u4eea\u7b7e\u7f72\u6676\u5706\u5236\u7a0b\u6280\u672f\u8f6c\u8ba9\u534f\u8bae\uff0c\u867d\u7136\u6574\u4e2a\u4ea4\u6613\u8fd8\u53ea\u8fc8\u51fa\u7b2c\u4e00\u6b65\uff0c\u4e1a\u5185\u5374\u662f\u4e00\u7247\u54d7\u7136\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6b27\u7f8e\u5927\u5382\u5728\u6280\u672f\u5c42\u6b21\u4e0a\u4e3b\u8981\u4e13\u6ce8\u4e8e\u9ad8\u9636\u82af\u7247\u751f\u4ea7\uff0c\u53cd\u5e94\u76f8\u5bf9\u5e73\u9759\u4e00\u4e9b\uff0c\u4e9a\u6d32\u6676\u5706\u5236\u9020\u4e1a\u7684\u53cd\u5e94\u5c31\u8981\u5f3a\u70c8\u5f97\u591a\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u6676\u5706\u4ee3\u5de5\u4f01\u4e1a\u7684\u4e3b\u6d41\u5236\u7a0b\u6280\u672f\u5c31\u662f0.18\u5fae\u7c73\uff0c\u800c\u4e14\u6574\u4e2a\u6676\u5706\u4ee3\u5de5\u4ea7\u4e1a\u8fd8\u6ca1\u6709\u4ece\u91d1\u878d\u98ce\u66b4\u7684\u9634\u5f71\u91cc\u5f7b\u5e95\u7684\u8d70\u51fa\u6765\uff0c\u4e9a\u6d32\u5927\u90e8\u5206\u7684\u6676\u5706\u751f\u4ea7\u4f01\u4e1a\u5728\u4e5d\u516b\u5e74\u5168\u5e74\u3001\u4e5d\u4e5d\u5e74\u4e0a\u534a\u5e74\u90fd\u6709\u4e0d\u540c\u7a0b\u5ea6\u7684\u4e8f\u635f\u2014\u2014\u5728\u751f\u4ea7\u6210\u672c\u66f4\u4f4e\u7684\u4e2d\u56fd\u5927\u9646\u5730\u533a\u5d1b\u8d77\u4e00\u4e2a\u5f3a\u6709\u529b\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ed6\u4eec\u4e50\u610f\u770b\u5230\u7684\u3002</p><p>\u3000\u3000\u53f0\u79ef\u7535\u4e03\u6708\u4e2d\u65ec\u8fd8\u56e0\u4e3a\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u6062\u590d\u4e0d\u5982\u9884\u671f\u3001\u6676\u5706\u4ee3\u5de5\u4e1a\u52a1\u4e0a\u534a\u5e74\u51fa\u73b0\u5c0f\u5e45\u4e8f\u635f\u800c\u5ba3\u5e03\u63a8\u5ef6\u5341\u4e8c\u82f1\u5bf8\u65b0\u5382\u7684\u5efa\u8bbe\u671f\u3002\u65b0\u5382\u5efa\u6210\u8ba1\u5212\u5c06\u91c7\u7528\u6700\u65b0\u76840.13\u5fae\u7c73\u5236\u7a0b\u6280\u672f\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4e00\u65e6\u4e2d\u6676\u5fae\u82af\u4e0e\u5fb7\u4eea\u7684\u6280\u672f\u8f6c\u8ba9\u4ea4\u6613\u6700\u7ec8\u5b8c\u6210\u5e76\u4e14\u5171\u540c\u5728\u5927\u9646\u5883\u5185\u518d\u5efa\u4e00\u5230\u4e24\u5ea7\u5927\u578b\u6676\u5706\u5de5\u5382\uff0c\u5373\u4f7f\u773c\u4e0b\u7684\u7ecf\u6d4e\u5f62\u52bf\u8fd8\u5f88\u4e0d\u5229\uff0c\u53f0\u79ef\u7535\u4e3a\u4e86\u4fdd\u6301\u5728\u4e9a\u6d32\u7684\u4f18\u52bf\u5730\u4f4d\uff0c\u4e5f\u8981\u8fc5\u901f\u63a8\u52a8\u65b0\u5382\u7684\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u53f0\u6e7e\u8054\u7535\u8fd8\u6b63\u5728\u8ddf\u5fb7\u4eea\u8fdb\u884c\u5f15\u8fdb0.13\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u7684\u8c08\u5224\uff0c\u773c\u89c1\u7740\u4e2d\u6676\u5fae\u82af\u4ece\u5c41\u80a1\u540e\u8ffd\u4e0a\u53bb\uff0c\u96be\u514d\u6709\u7740\u7ed9\u72d7\u8ffd\u7740\u54ac\u5c41\u80a1\u7684\u5371\u673a\u611f\u3002</p><p>\u3000\u3000\u97e9\u56fd\u7684\u51e0\u5bb6\u6676\u5706\u5382\u5371\u673a\u611f\u66f4\u4e25\u91cd\uff0c\u8fd9\u4e9b\u6676\u5706\u5382\u91c7\u75280.18\u5fae\u7c73\u6280\u672f\u624d\u6709\u4e09\u5bb6\uff0c\u66f4\u4e0d\u7528\u8bf4\u5f15\u8fdb\u6216\u5f00\u53d1\u66f4\u5148\u8fdb\u76840.13\u5fae\u7c73\u6280\u672f\u7684\u8ba1\u5212\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u751a\u81f3\u53d1\u51fa\u4e2d\u56fd\u7535\u5b50\u4ea7\u4e1a\u4e00\u591c\u4e4b\u95f4\u5373\u5c06\u8ffd\u4e0a\u97e9\u56fd\u7684\u60ca\u547c\u58f0\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5317\u4eac\u897f\u5317\u90ca\u4e00\u5904\u540d\u53eb\u897f\u7fe0\u56ed\u7684\u522b\u5885\u533a\uff0c\u6709\u4e00\u680b\u9760\u8fd1\u522b\u5885\u533a\u4eba\u5de5\u6e56\u7684\u8c6a\u534e\u522b\u5885\u867d\u7136\u4e3b\u4eba\u5199\u7740\u5176\u4ed6\u4eba\u7684\u540d\u5b57\uff0c\u6797\u96ea\u6bcf\u56de\u5230\u5317\u4eac\uff0c\u845b\u5efa\u5fb7\u90fd\u7559\u5979\u4f4f\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u771f\u662f\u98ce\u5149\u4e86\uff0c\u8ddf\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u5728\u7f8e\u56fd\u6253\u4e86\u534a\u5e74\u7684\u5b98\u53f8\uff0c\u903c\u7740\u8170\u5706\u8180\u9614\u7684\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u90fd\u4f4e\u5934\u4e86\uff0c\u6ca1\u8fc7\u51e0\u5929\u53c8\u4e0e\u5fb7\u4eea\u8c08\u59a5\u5f15\u8d770.25\u30010.18\u5fae\u7c73\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\uff0c\u201d\u6797\u96ea\u7a7f\u7740\u7761\u88d9\u5750\u5728\u845b\u5efa\u5fb7\u7684\u5927\u817f\u4e0a\uff0c\u96ea\u767d\u7684\u5927\u817f\u4ece\u7761\u88d9\u91cc\u8df7\u8d77\u6765\uff0c\u4e00\u8fb9\u8ddf\u845b\u5efa\u5fb7\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u6d82\u6307\u7532\u6cb9\uff0c\u201c\u8fde\u6211\u8fd9\u4e2a\u5916\u884c\u4eba\u90fd\u77e5\u9053\u9526\u6e56\u8fd9\u6b21\u8981\u5e2e\u56fd\u5185\u7684\u7535\u5b50\u57fa\u7840\u4ea7\u4e1a\u8fde\u5347\u4e24\u7ea7\uff0c\u4f60\u4e0d\u662f\u53f7\u79f0\u662f\u4e2d\u56fd\u7535\u5b50\u4ea7\u4e1a\u638c\u95e8\u4eba\u5417\uff0c\u4f60\u6709\u4ec0\u4e48\u60f3\u6cd5\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u201d\u845b\u5efa\u5fb7\u9634\u9634\u7684\u8bf4\u9053\uff0c\u624b\u6307\u5374\u5728\u6797\u96ea\u7684\u53cc\u817f\u4e0a\u629a\u5f04\uff0c\u4ed6\u5c31\u559c\u6b22\u5f04\u8fd9\u91cc\u7684\u5ae9\u8089\uff0c\u8bf4\u9053\u201c\u80fd\u4e0d\u80fd\u6210\uff0c\u4e5f\u4e0d\u662f\u9526\u6e56\u4e0e\u5fb7\u4eea\u4e24\u5bb6\u4f01\u4e1a\u81ea\u4e2a\u513f\u5c31\u80fd\u8bf4\u7684\u7b97\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5012\u662f\u627e\u4eba\u95ee\u8fc7\u4e86\uff0c0.18\u5fae\u7c73\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\u8fd8\u5c31\u64e6\u7740\u7f8e\u56fd\u5bf9\u534e\u79d1\u6280\u51fa\u53e3\u7ba1\u5236\u7684\u8fb9\uff0c\u5fb7\u4eea\u80cc\u540e\u4f7f\u4e9b\u52b2\uff0c\u8fd8\u5c31\u80fd\u6210\u4e86\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d\u6797\u96ea\u5c06\u5927\u817f\u4e0a\u90a3\u53ea\u67af\u6728\u4f3c\u7684\u624b\u62e8\u5f00\uff0c\u7ed9\u6320\u4e86\u817f\u5fc3\u95f4\u75d2\u75d2\u7684\u96be\u53d7\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u5230\u65f6\u5019\uff0c\u534e\u590fNEC\u5c31\u6709\u53ef\u80fd\u6210\u7b11\u67c4\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u9634\u973e\uff0c\u4e0e\u65e5\u672cNEC\u7535\u5b50\u5408\u8d44\u6210\u7acb\u7684\u6676\u5706\u5382\u534e\u590fNEC\u53bb\u5e74\u5e74\u4e2d\u5728\u5efa\u90ba\u52a8\u5de5\u5efa\u8bbe\uff0c\u8fc4\u4eca\u4e5f\u8fc7\u53bb\u6709\u4e00\u5e74\u7684\u65f6\u95f4\u3002\u7531\u4e8e\u65e5\u65b9\u7684\u79ef\u6781\u914d\u5408\uff0c\u534e\u590fNEC\u5de5\u5382\u5efa\u8bbe\u3001\u8bbe\u5907\u5f15\u8fdb\u90fd\u975e\u5e38\u8fc5\u901f\uff0c\u7b79\u5212\u7740\u79cb\u540e\u5c31\u8fdb\u884c\u8bd5\u751f\u4ea7\uff0c\u867d\u7136\u5efa\u8bbe\u901f\u5ea6\u4e0d\u80fd\u8ddf\u4e2d\u6676\u5fae\u82af\u76f8\u6bd4\uff0c\u4f46\u5efa\u8bbe\u6c34\u5e73\u5728\u884c\u4e1a\u4e5f\u5904\u4e8e\u4e2d\u4e0a\u6e38\u2014\u2014\u4ec5\u51ed\u8fd9\u4e00\u70b9\uff0c\u4e5f\u8db3\u4ee5\u8ba9\u845b\u5efa\u5fb7\u8138\u4e0a\u589e\u5149\u3002</p><p>\u3000\u3000\u7136\u540e\u5728\u638c\u58f0\u80cc\u540e\u5374\u9690\u85cf\u7740\u5de8\u5927\u7684\u5fe7\u60a3\u3002</p><p>\u3000\u3000\u56fd\u5185\u7684\u65e0\u6676\u5706\u5382\u534a\u5bfc\u4f53\u4ea7\u4e1a\uff0c\u4e5f\u5c31\u662f\u534a\u5bfc\u4f53\u82af\u7247\u8bbe\u8ba1\u4ea7\u4e1a\u6781\u4e0d\u53d1\u8fbe\uff0c\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u7535\u5b50\u4f01\u4e1a\u4f17\u591a\uff0c\u4f46\u662f\u6709\u82af\u7247\u8bbe\u8ba1\u80fd\u529b\u5374\u6ca1\u6709\u4e00\u5bb6\uff0c\u534e\u590fNEC\u5efa\u6210\u4e4b\u540e\uff0c\u53ea\u80fd\u4ece\u6d77\u5916\u7684\u82af\u7247\u8bbe\u8ba1\u516c\u53f8\u624b\u91cc\u4e89\u53d6\u82af\u7247\u751f\u4ea7\u8ba2\u5355\u505a\u4ee3\u5de5\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u6839\u636e\u5f53\u521d\u7684\u5408\u8d44\u534f\u8bae\uff0c\u534e\u590fNEC\u7684\u4e1a\u52a1\u7531\u65e5\u65b9\u8d1f\u8d23\u627f\u63fd\uff0c\u4f46\u773c\u4e0b\u82af\u7247\u4ee3\u5de5\u5e02\u573a\u4e00\u7247\u60e8\u6de1\uff0c\u5c31\u8fdeNEC\u7535\u5b50\u5728\u65e5\u672c\u7684\u6676\u5706\u5382\u5f00\u5de5\u7387\u90fd\u6709\u6240\u4e0d\u8db3\uff0c\u65e5\u65b9\u600e\u4e48\u53ef\u80fd\u6709\u591a\u4f59\u7684\u4e1a\u52a1\u4ea4\u7ed9\u534e\u590fNEC\u6765\u505a\uff1f</p><p>\u3000\u3000\u5373\u4f7f\u4e9a\u6d32\u82af\u7247\u4ee3\u5de5\u5e02\u573a\u4e00\u7247\u60e8\u6de1\uff0c\u5efa\u6210\u6295\u4ea7\u7684\u4e2d\u6676\u5fae\u82af\u5374\u80fd\u4ece\u9526\u6e56\u624b\u91cc\u63a5\u5230\u5927\u7b14\u7684\u82af\u7247\u4ee3\u5de5\u4e1a\u52a1\u8ba2\u5355\uff0c\u8fd8\u987a\u5229\u7684\u5b9e\u73b0\u4e86\u76c8\u5229\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u82af\u7247\u8bbe\u8ba1\u80fd\u529b\u4e0a\u7684\u6210\u957f\u5c31\u50cf\u96e8\u540e\u8000\u773c\u7684\u5f69\u8679\u4e00\u6837\u7729\u773c\uff0c\u5728\u7535\u6e90\u63a7\u5236\u3001\u5355\u7247\u673a\u7cfb\u7edf\u63a7\u5236\u3001\u97f3\u9891\u89c6\u9891\u89e3\u7801\u3001\u50a8\u5b58\u5668\u5143\u4ef6\u3001\u5fae\u5904\u7406\u5668\u3001\u5fae\u63a7\u5236\u5668\u7b49\u9886\u57df\u7684\u82af\u7247\u8bbe\u8ba1\u80fd\u529b\u5df2\u7ecf\u4e0d\u5f31\u4e8e\u6d77\u5916\u4e3b\u6d41\u7535\u5b50\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u60f3\u8d77\u6613\u4e91\u98de\u5728\u4fe1\u4ea7\u90e8\u5185\u90e8\u4f1a\u8bae\u4e0a\u5e2e\u9526\u6e56\u8bf4\u7684\u4e00\u53e5\u8bdd\uff1a\u201c\u56fd\u5185\u534a\u5bfc\u4f53\u82af\u7247\u8bbe\u8ba1\u4ea7\u4e1a\u8981\u53d1\u5c55\u8d77\u6765\uff0c\u8fd8\u8981\u7b49\u7740\u6a61\u6811\u56ed\u8f93\u9001\u4eba\u624d\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u6837\u7684\u8bdd\u542c\u7740\u8ba9\u7259\u6839\u76f4\u53d1\u75d2\uff0c\u5374\u8ba9\u4eba\u65e0\u4ece\u53cd\u9a73\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u53d1\u4ec0\u4e48\u6123\uff1f\u201d\u6797\u96ea\u62e8\u8fc7\u845b\u5efa\u5fb7\u7684\u4e0b\u5df4\uff0c\u8ba9\u4ed6\u770b\u7740\u81ea\u5df1\u7684\u773c\u529b\uff0c\u5373\u4f7f\u6709\u4e9b\u538c\u6076\u4ed6\uff0c\u4f46\u662f\u4ed6\u5728\u81ea\u5df1\u7684\u7f8e\u8272\u8bf1\u60d1\u9762\u524d\u8d70\u795e\uff0c\u8fd8\u662f\u8ba9\u5979\u5fc3\u91cc\u9690\u9690\u4e0d\u723d\uff0c\u201c\u6211\u5728\u5efa\u90ba\u7ecf\u5e38\u542c\u522b\u4eba\u8bf4\uff0c\u4e2d\u6676\u5fae\u82af\u4e0e\u534e\u590fNEC\u5efa\u5382\u671f\u524d\u540e\u53ea\u5dee\u4e00\u5e74\uff0c\u91c7\u7528\u7684\u5236\u7a0b\u5de5\u827a\u76f8\u8fd1\uff0c\u6295\u8d44\u89c4\u6a21\u3001\u5efa\u5382\u89c4\u6a21\u76f8\u8fd1\uff0c\u53c8\u90fd\u5efa\u5728\u5efa\u90ba\u5e02\u9ad8\u65b0\u533a\uff0c\u4e24\u5ea7\u5de5\u5382\u7684\u5927\u95e8\u76f8\u8ddd\u4e0d\u8fc7\u4e24\u516c\u91cc\uff0c\u4ee5\u540e\u4e0d\u4f1a\u4e2d\u6676\u5fae\u82af\u4e0e\u534e\u590fNEC\u653e\u5728\u4e00\u8d77\u6bd4\u8f83\uff0c\u90a3\u771f\u662f\u53eb\u6709\u9b3c\u4e86\uff0c\u6211\u5374\u60f3\u534e\u590fNEC\u5927\u6982\u600e\u4e48\u4f7f\u52b2\u90fd\u6bd4\u4e0d\u8fc7\u4eba\u5bb6\u5427\u2014\u2014\u4f60\u8bf4\u4fe1\u4ea7\u90e8\u7684\u4eba\u4f1a\u600e\u4e48\u60f3\uff0c\u803f\u91cd\u9633\u4f1a\u600e\u4e48\u60f3\uff1f\u201d\u5979\u771f\u662f\u4e00\u70b9\u90fd\u4ecb\u610f\u6233\u845b\u5efa\u5fb7\u7684\u75db\u5904\uff0c\u624b\u6307\u5728\u62b5\u7740\u4ed6\u7684\u8138\u988a\u626d\u554a\u626d\u3002</p><p>\u3000\u3000\u201c\u803f\u90e8\u957f\u4f1a\u600e\u4e48\u60f3\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u845b\u5efa\u5fb7\u5fc3\u91cc\u6709\u4e9b\u70e6\u538c\uff0c\u6076\u72e0\u72e0\u7684\u6293\u4f4f\u6797\u96ea\u7684\u81c0\u90e8\u634f\u7740\u3002</p><p>\u3000\u3000\u201c\u6211\u6765\u5317\u4eac\u4e4b\u524d\uff0c\u4e0e\u4e25\u6587\u4ecb\u78b0\u8fc7\u5934\uff0c\u6211\u4eec\u8ba8\u8bba\u8ba8\u8bba\u53bb\uff0c\u90fd\u60f3\u4e0d\u660e\u767d\u9526\u6e56\u5728\u56fd\u5185\u80fd\u6709\u8fd9\u4e48\u591a\u94b1\u2014\u2014\u4f60\u4ece\u4e13\u4e1a\u7684\u773c\u5149\u6765\u5224\u65ad\uff0c\u89c9\u5f97\u6574\u4e2a\u6a61\u6811\u56ed\u8ba1\u5212\u4ece\u4e5d\u4e03\u5e74\u5e74\u521d\u542f\u52a8\u5230\u4eca\u5e74\u9700\u8981\u6295\u4e86\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u8ddf\u4e25\u6587\u4ecb\u4e0a\u8fc7\u5e8a\uff1f\u201d\u845b\u5efa\u5fb7\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8ddf\u4f60\u8bf4\u6b63\u7ecf\u4e8b\uff0c\u201d\u6797\u96ea\u5c06\u845b\u5efa\u5fb7\u6293\u7740\u81ea\u5df1\u5c41\u80a1\u7684\u624b\u62e8\u5f00\uff0c\u201c\u518d\u8bf4\u8001\u5a18\u7231\u8ddf\u8c01\u4e0a\u5e8a\u5c31\u8ddf\u8c01\u4e0a\u5e8a\uff0c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u9664\u975e\u4f60\u5c06\u4f60\u5bb6\u7684\u9ec4\u8138\u5a46\u4f11\u6389\u5a36\u6211\uff0c\u6211\u5c31\u8ba9\u4f60\u7ba1\u6211\u3002\u201d</p><p>\u3000\u3000\u201c\u624d\u4e0d\u4fe1\u4f60\u4e0b\u9762\u8fd9\u5f20\u5634\u4e0d\u5077\u8165\u3002\u201d\u845b\u5efa\u5fb7\u90aa\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6797\u96ea\u4e0d\u8ddf\u4ed6\u7ea0\u7f20\u8fd9\u4e2a\u8bdd\u9898\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u4e9b\u5a92\u4f53\u4f30\u6d4b\u9526\u6e56\u8fd9\u4e24\u5e74\u591a\u5f80\u6a61\u6811\u56ed\u8ba1\u5212\u91cc\u6295\u5165\u4e86\u6709\u4e8c\u5341\u4ebf\u3002\u201d</p><p>\u3000\u3000\u201c\u4e8c\u5341\u4ebf\u6253\u5e95\u90fd\u4e0d\u591f\uff01\u201d\u845b\u5efa\u5fb7\u8bf4\u9053\uff0c\u5bf9\u4e13\u4e1a\u9886\u57df\u7684\u4e8b\u52a1\u8fd8\u662f\u6709\u4e00\u822c\u7684\u5224\u65ad\u80fd\u529b\uff0c\u9526\u6e56\u542f\u52a8\u6a61\u6811\u56ed\u767e\u4eba\u8ba1\u5212\u4ee5\u6765\uff0c\u603b\u5171\u4ece\u6d77\u5916\u5f15\u8fdb\u56db\u767e\u591a\u540d\u5fae\u7535\u5b50\u53ca\u76f8\u5173\u9886\u57df\u7684\u4e2d\u9ad8\u7ea7\u7814\u7a76\u5458\u3001\u5de5\u7a0b\u5e08\uff0c\u66f4\u662f\u4ece\u56fd\u5185\u76f4\u63a5\u62db\u8058\u4e09\u5343\u591a\u540d\u7814\u7a76\u5458\u4ee5\u5de5\u7a0b\u5e08\u8865\u5145\u8fdb\u6a61\u6811\u56ed\uff0c\u201c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\uff0c\u9526\u6e56\u5c31\u5148\u540e\u62ff\u51fa\u4e94\u4ebf\u7684\u771f\u91d1\u767d\u94f6\uff0c\u4f60\u4ee5\u4e3a\u5341\u4e94\u4ebf\u80fd\u5efa\u7acb\u8d77\u4e00\u4e2a\u8fd1\u56db\u5343\u540d\u7814\u7a76\u5458\u89c4\u6a21\u7684\u7814\u7a76\u4e2d\u5fc3\u6765\uff1f\u4f60\u4eec\u5173\u5fc3\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff0c\u4e25\u5bb6\u5728\u8fd9\u65b9\u9762\u6709\u7684\u662f\u4eba\u8109\uff0c\u4ed6\u4eec\u90fd\u67e5\u4e0d\u900f\u9526\u6e56\u7684\u5e95\u7ec6\uff0c\u6211\u600e\u4e48\u80fd\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e25\u5bb6\u4e5f\u67e5\u4e0d\u900f\uff0c\u201d\u6797\u96ea\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u53ea\u662f\u89c9\u5f97\u6709\u4e9b\u7591\u60d1\uff0c\u6211\u4eec\u4f30\u8ba1\u9526\u6e56\u5f80\u6a61\u6811\u56ed\u8ba1\u5212\u6295\u5165\u4e0d\u6b62\u56db\u5341\u4e2a\u4ebf\uff0c\u4f60\u4ee5\u4e3a\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u901a\u8fc7\u65d7\u4e0b\u56fd\u5185\u5916\u6ce8\u518c\u7684\u591a\u5bb6\u516c\u53f8\u6765\u7ed9\u6a61\u6811\u56ed\u8ba1\u5212\u4e70\u5355\uff0c\u771f\u5b9e\u7684\u6295\u5165\uff0c\u9664\u975e\u9526\u6e56\u81ea\u5df1\u8001\u8001\u5b9e\u5b9e\u7684\u5c06\u8d26\u672c\u4ea4\u51fa\u6765\uff0c\u4e0d\u7136\u8c01\u4e5f\u522b\u60f3\u67e5\u6e05\u695a\uff0c\u6211\u4eec\u80fd\u67e5\u56fd\u5185\uff0c\u56fd\u5916\u90e8\u5206\uff0c\u7279\u522b\u90a3\u4e9b\u907f\u7a0e\u5730\u7684\u8d22\u52a1\u8d44\u6599\uff0c\u4e2d\u592e\u51fa\u9762\u90fd\u62ff\u4e0d\u5230\u624b\u2014\u2014\u201d\u845b\u5efa\u5fb7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u624d\u8981\u4f60\u8fd9\u4e2a\u4e13\u5bb6\u6765\u8bc4\u4f30\u554a\u3002\u201d\u6797\u96ea\u6492\u5a07\u9053\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u56fd\u5185\u90e8\u5206\u8fd9\u4e24\u5e74\u6295\u8d44\u56db\u5341\u4ebf\u5e94\u8be5\u5dee\u4e0d\u591a\uff0c\u201d\u845b\u5efa\u5fb7\u6478\u7740\u4e0b\u5df4\u8bf4\u9053\uff0c\u201c\u4f46\u662f\u9526\u6e56\u8fd8\u5728\u7f8e\u56fd\u52a0\u5229\u798f\u5c3c\u4e9a\u8bbe\u7acbESS\u5b9e\u9a8c\u57fa\u5730\uff0c\u636e\u8bf4ESS\u5b9e\u9a8c\u57fa\u5730\u7684\u79d1\u7814\u4eba\u5458\u8d85\u8fc7\u56db\u767e\u4eba\uff0c\u5728\u79d1\u6280\u516c\u53f8\u6797\u7acb\u7684\u7845\u8c37\uff0c\u5982\u6b64\u7684\u79d1\u7814\u89c4\u6a21\u4e5f\u7b97\u76f8\u5f53\u7684\u53ef\u89c2\uff0c\u4ec5\u6bcf\u5e74\u7ed9\u8fd9\u56db\u767e\u540d\u7814\u7a76\u5458\u5f00\u5de5\u8d44\u90fd\u8981\u8d85\u8fc7\u56db\u5343\u4e07\u7f8e\u5143\uff0c\u4f60\u8bf4\u9526\u6e56\u8fd9\u4e09\u5e74\u65f6\u95f4\u5f80ESS\u5b9e\u9a8c\u57fa\u5730\u91cc\u6295\u5165\u4e86\u591a\u5c11\u94b1\uff1f\u9664\u4e86ESS\u4e4b\u5916\uff0c\u9526\u6e56\u8fd8\u5728\u65e5\u672c\u7b51\u6ce2\u5e02\u8bbe\u6709\u5b9e\u9a8c\u57fa\u5730\uff1b\u6b64\u5916\uff0c\u9526\u6e56\u8fd8\u5728\u5168\u7403\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u7684\u4e3b\u8981\u5e02\u573a\u6240\u5728\u5730\u8bbe\u6709\u5341\u56db\u5bb6\u4ea7\u54c1\u8bbe\u8ba1\u4e0e\u5f00\u53d1\u4e2d\u5fc3\u2014\u2014\u8fd9\u4e9b\u53c8\u6295\u5165\u4e86\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e9b\u624d\u662f\u6211\u4eec\u6000\u7591\u7684\u5730\u65b9\uff0c\u6211\u4eec\u9884\u6d4b\u9526\u6e56\u5f80\u6574\u4e2a\u6a61\u6811\u56ed\u8ba1\u5212\u6295\u5165\u7684\u8d44\u91d1\u603b\u6570\u4e0d\u4f4e\u4e8e\u516d\u5341\u4ebf\u2014\u2014\u800c\u9526\u6e56\u4e5d\u4e03\u5e74\u4ece\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u5377\u6765\u7684\u56db\u5341\u4ebf\uff0c\u4e5d\u4e03\u5e74\u3001\u4e5d\u516b\u5e74\u80fd\u51c0\u62ff\u5230\u624b\u7684\u5229\u6da6\u5dee\u4e0d\u591a\u6709\u56db\u5341\u4ebf\uff0c\u603b\u6570\u7ea6\u516b\u5341\u4ebf\u5de6\u53f3\uff0c\u4f46\u662f\u9664\u6a61\u6811\u56ed\u8ba1\u5212\u4e4b\u5916\uff0c\u9526\u6e56\u5728\u4e2d\u6676\u5fae\u82af\u3001\u5728\u65b0\u5149\u7eb8\u4e1a\u3001\u65b0\u5149\u6797\u4e1a\u3001\u9999\u96ea\u6d77\u3001\u76db\u5668\u7535\u8baf\u4ee5\u53ca\u7231\u8fbe\u96c6\u56e2\u81ea\u8eab\u7684\u4ea7\u80fd\u6269\u5f20\u4e0a\u6295\u5165\u4e0d\u4e0b\u4e94\u5341\u4ebf\uff0c\u8fd9\u5f53\u4e2d\u81f3\u5c11\u6709\u4e09\u5341\u4ebf\u4ee5\u4e0a\u7684\u5dee\u989d\uff0c\u201d\u6797\u96ea\u8bf4\u5230\u8fd9\u91cc\u773c\u775b\u53d1\u4eae\uff0c\u773c\u775b\u91cc\u7684\u5149\u8292\u51f6\u72e0\u6bd2\u7edd\uff0c\u201c\u56e0\u4e3a\u9526\u6e56\u5546\u4e8b\u7684\u8d44\u91d1\u7edd\u5927\u591a\u6570\u90fd\u6765\u81ea\u4e8e\u503a\u6743\u4eba\uff0c\u9526\u6e56\u5546\u4e8b\u7684\u8d44\u91d1\u8fd0\u7528\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u662f\u8981\u5bf9\u8fd9\u4e9b\u503a\u6743\u4eba\u516c\u5f00\u7684\uff0c\u9526\u6e56\u5546\u4e8b\u4e0d\u53ef\u80fd\u63d0\u4f9b\u5927\u91cf\u7684\u8d44\u91d1\u7528\u4e8e\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u518d\u8bf4\u9526\u6e56\u5546\u4e8b\u5927\u89c4\u6a21\u878d\u8d44\u4e5f\u662f\u53bb\u5e74\u516d\u6708\u4efd\u4e4b\u540e\u624d\u5f00\u59cb\u7684\uff0c\u4e5f\u65e0\u6cd5\u7ed9\u53bb\u5e74\u516d\u6708\u4efd\u4e4b\u524d\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u4ee5\u53ca\u9526\u6e56\u5728\u56fd\u5185\u7684\u5176\u4ed6\u4ea7\u4e1a\u53d1\u5c55\u63d0\u4f9b\u8d44\u91d1\u2014\u2014\u8fd9\u4e09\u5341\u4ebf\u7684\u5dee\u989d\u91cc\u9762\u5f88\u53ef\u80fd\u5b58\u5728\u5927\u95ee\u9898\uff01\u201d</p></article>"}}}]);
