(window.webpackJsonp=window.webpackJsonp||[]).push([[651],{674:function(p){p.exports={chapterTitle:null,title:"\u7b2c498\u7ae0 \u7ecf\u8425\u7684\u6c14\u5ea6",content:"<article><h2>\u7b2c498\u7ae0 \u7ecf\u8425\u7684\u6c14\u5ea6</h2><p>\u3000\u3000\u8c61\u5c71\u897f\u5317\u9e93\u4e34\u5c0f\u6c5f\u7684\u98ce\u666f\u533a\u91cc\u6709\u4e00\u5bb6\u6e21\u5047\u6751\u5f0f\u7684\u9152\u5e97\uff0c\u539f\u662f\u4e00\u5bb6\u5730\u4ea7\u516c\u53f8\u5f00\u53d1\u7684\u5c0f\u76d8\u522b\u5885\uff0c\u624d\u5efa\u4e86\u516b\u4e5d\u680b\u522b\u5885\uff0c\u5c31\u9677\u5165\u8d22\u52a1\u7ea0\u7eb7\uff0c\u52a0\u4e0a\u8c61\u5c71\u897f\u5317\u9e93\u7684\u5730\u5757\u4e00\u76f4\u6ca1\u6709\u542f\u52a8\u8d77\u6765\uff0c\u90a3\u4e2a\u522b\u5885\u697c\u76d8\u4e5d\u56db\u5e74\u5c31\u6210\u4e86\u70c2\u5c3e\u697c\u3002\u90a3\u91cc\u5f00\u53d1\u4f4f\u5b85\u697c\u4e0d\u4f1a\u5927\u70ed\uff0c\u5468\u56f4\u51b7\u51b7\u6e05\u6e05\uff0c\u516b\u4e5d\u680b\u522b\u5885\u4e5f\u4e0d\u6210\u89c4\u6a21\uff0c\u4e0d\u8fc7\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u521b\u7acb\u4e4b\u540e\uff0c\u90b5\u81f3\u521a\u5c31\u679c\u65ad\u51fa\u624b\uff0c\u5c06\u697c\u76d8\u4f4e\u4ef7\u627f\u63a5\u4e0b\u6765\u3002\u534a\u5e74\u65f6\u95f4\u8fc7\u53bb\uff0c\u8c61\u5c71\u98ce\u666f\u533a\u5185\uff0c\u4e5f\u5e94\u8be5\u8bf4\u8c61\u5c71\u9547\u3001\u65b0\u6865\u9547\u3001\u76ca\u9686\u9547\u8303\u56f4\u5185\u6709\u4e86\u7b2c\u4e00\u5bb6\u4e0a\u89c4\u683c\u7684\u9152\u5e97\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u5728\u8c61\u5c71\u98ce\u666f\u533a\u7684\u897f\u5317\u89d2\u4e0a\uff0c\u5f20\u606a\u5012\u662f\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u53bb\u90a3\u91cc\uff0c\u542c\u4e01\u8389\u534e\u3001\u5468\u5c0f\u5175\u8bf4\u8d77\u53bb\u90a3\u91cc\u5403\u665a\u996d\uff0c\u5012\u4e5f\u86ee\u611f\u5174\u8da3\u7684\u3002\u7b49\u665a\u6674\u5e26\u7740\u5979\u7684\u52a9\u7406\u5f20\u5ead\u8fc7\u6765\u6c47\u5408\uff0c\u5c31\u5f00\u8f66\u51fa\u4e86\u690d\u7269\u56ed\u3002\u5468\u5c0f\u5175\u4e0e\u4e01\u8389\u534e\u5f00\u8fc7\u6765\u7684\u8f66\u662f\u8f86\u516b\u6210\u65b0\u7684\u6851\u5854\u7eb3\uff0c\u8f66\u505c\u5728\u690d\u7269\u56ed\u79bb\u5927\u95e8\u8f83\u8fdc\u7684\u5730\u65b9\uff0c\u5f20\u606a\u770b\u4e86\u770b\u505c\u8f66\u5730\u65b9\u7684\u6811\u7bf1\u6709\u7ed9\u4eba\u6500\u722c\u8fc7\u7684\u75d5\u8ff9\uff0c\u7b11\u7740\u5bf9\u4ed6\u4eec\u4e24\u4eba\u8bf4\uff1a\u201c\u8fd9\u91cc\u6682\u65f6\u8fd8\u4e0d\u5bf9\u5916\u5f00\u653e\uff0c\u4e0d\u8fc7\u8981\u662f\u8ddf\u8b66\u536b\u8bb2\u660e\u4f60\u4eec\u53ea\u662f\u8981\u627e\u4e2a\u5730\u65b9\u8c08\u8c08\u4eba\u751f\u8c08\u8c08\u7406\u60f3\uff0c\u8b66\u536b\u4e5f\u4e0d\u4f1a\u62e6\u7740\u4e0d\u8ba9\u4f60\u4eec\u8fdb\u53bb\u3002\u201d</p><p>\u3000\u3000\u5468\u5c0f\u5175\u3001\u4e01\u8389\u534e\u4e24\u4eba\u8138\u4e00\u7ea2\uff0c\u5468\u5c0f\u5175\u8146\u7740\u8138\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u6211\u4eec\u5c31\u5f53\u606a\u5c11\u8bb8\u8fd9\u91cc\u5bf9\u6211\u4eec\u4fe9\u5f00\u653e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53c8\u4e0d\u662f\u5efa\u4ec0\u4e48\u79c1\u4eba\u573a\u6240\uff0c\u4ec0\u4e48\u8bb8\u4e0d\u8bb8\u7684\u3002\u201d\u5f20\u606a\u6325\u624b\u8ba9\u4ed6\u4eec\u4e0a\u8f66\u3002</p><p>\u3000\u3000\u8d76\u5230\u9152\u5e97\uff0c\u4e01\u6587\u7965\u5df2\u7ecf\u63a5\u5230\u5468\u5c0f\u5175\u7684\u7535\u8bdd\u901a\u77e5\u5148\u8d76\u5230\u9152\u5e97\u91cc\u7b49\u5019\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u6d77\u5dde\u789f\u673a\u9884\u7559\u4e00\u4e2a\u6d4b\u8bd5\u540d\u989d\u7684\u6d88\u606f\u6563\u5e03\u51fa\u53bb\u624d\u4e94\u516d\u5929\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u77ed\u77ed\u4e94\u516d\u5929\uff0c\u4e01\u6587\u7965\u53ea\u591f\u65f6\u95f4\u4e0e\u5176\u4ed6\u516d\u5bb6\u789f\u673a\u5382\u8c08\u59a5\u8054\u8425\u5e76\u80a1\u7684\u4e8b\u60c5\uff0c\u773c\u4e0b\u6700\u8981\u7d27\u7684\u662f\u5148\u5c06\u4e03\u5bb6\u789f\u673a\u5bb6\u7684\u6280\u672f\u4eba\u5458\u90fd\u96c6\u4e2d\u8d77\u6765\uff0c\u4e3a\u53c2\u4e0e\u65b0\u789f\u673a\u6d4b\u8bd5\u505a\u597d\u6280\u672f\u4e0a\u51c6\u5907\uff0c\u81f3\u4e8e\u5176\u4ed6\u4e8b\u60c5\u66f4\u662f\u5343\u5934\u4e07\u7eea\uff0c\u4e01\u6587\u7965\u8fd9\u4e9b\u5929\u6765\u5fd9\u5f97\u7126\u5934\u70c2\u989d\uff0c\u4e0d\u8fc7\u80fd\u770b\u5230\u7684\u524d\u7a0b\u5f02\u5e38\u7684\u660e\u4eae\uff0c\u4e5f\u8ba9\u4ed6\u7684\u610f\u5fd7\u9ad8\u4ea2\u3002\u63a5\u5230\u8389\u534e\u5728\u8f66\u91cc\u6253\u7ed9\u4ed6\u7684\u7535\u8bdd\uff0c\u4ed6\u816e\u90a6\u5b50\u4e0a\u7684\u8089\u90fd\u6fc0\u52a8\u5f97\u6253\u6296\uff0c\u90fd\u4e0d\u6653\u5f97\u8389\u534e\u4e0e\u5468\u5c0f\u5175\u600e\u4e48\u5c31\u673a\u7f18\u5de7\u5408\u80fd\u8bf7\u52a8\u8fd9\u5c0a\u5927\u4eba\u7269\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u5e74\u524d\u4e0e\u4e01\u6587\u7965\u5728\u65b0\u5143\u6a21\u5177\u5382\u89c1\u8fc7\u4e00\u9762\uff0c\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u901a\u6653\u4e16\u6545\u7684\u89d2\u8272\uff0c\u5bf9\u516c\u53f8\u7ba1\u7406\u4e5f\u6709\u4e00\u5957\uff0c\u6d77\u5dde\u5e02\u5192\u51fa\u8fd9\u4e48\u591a\u7684\u789f\u673a\u5382\uff0c\u65b0\u5143\u7535\u5668\u80fd\u505a\u5230\u4eca\u5929\u7684\u5730\u6b65\uff0c\u4e5f\u975e\u5076\u7136\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5e02\u789f\u673a\u4f01\u4e1a\u9664\u4e86\u7231\u8fbe\u7535\u5b50\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5bb6\u4f01\u4e1a\u80fd\u53c2\u4e0e\u65b0\u789f\u673a\u6d4b\u8bd5\u7684\u6d88\u606f\u4f20\u51fa\u53bb\u4e4b\u540e\uff0c\u5404\u5bb6\u789f\u673a\u5382\u5c31\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4e01\u6587\u7965\u80fd\u4ee5\u4e0e\u5f20\u606a\u4e00\u9762\u4e4b\u7f18\u7559\u4e0b\u7684\u5370\u8c61\uff0c\u5c31\u80fd\u627e\u5230\u5927\u81f4\u51c6\u786e\u7684\u65b9\u5411\uff0c\u5e76\u8fc5\u901f\u5c06\u5176\u4ed6\u516d\u5bb6\u789f\u673a\u5382\u8054\u5408\u8d77\u6765\uff0c\u4e5f\u7b97\u662f\u4e0d\u7b80\u5355\u7684\u4eba\u7269\u3002</p><p>\u3000\u3000\u867d\u7136\u5916\u9762\u90fd\u5728\u4f20\uff0c\u9009\u62e9\u53c2\u4e0e\u6d4b\u8bd5\u7684\u789f\u673a\u4f01\u4e1a\uff0cTI\u3001\u65af\u9ad8\u67cf\u3001\u98de\u5229\u6d66\u4e09\u5bb6\u516c\u53f8\u8d77\u4e3b\u5bfc\u4f5c\u7528\uff0c\u4f46\u662f\u8bdd\u8bf4\u56de\u6765\u4e86\uff0c\u8fd9\u4e09\u516c\u53f8\u4e0e\u6d77\u5dde\u5e02\u7684\u789f\u673a\u4f01\u4e1a\u6709\u4ec0\u4e48\u5229\u76ca\u74dc\u845b\uff1f\u8bf4\u767d\u4e86\uff0c\u5176\u4ed6\u4e94\u4e2a\u540d\u989d\uff0c\u53ef\u80fd\u662f\u90a3\u4e09\u5bb6\u516c\u53f8\u8bf4\u7684\u7b97\uff0c\u7559\u7ed9\u6d77\u5dde\u789f\u673a\u4f01\u4e1a\u7684\u8fd9\u4e2a\u540d\u989d\u81ea\u7136\u8fd8\u662f\u7231\u8fbe\u7535\u5b50\u8bf4\u7684\u7b97\u3002</p><p>\u3000\u3000\u90a3\u5f20\u606a\u7684\u6001\u5ea6\u5c06\u662f\u51b3\u5b9a\u6027\u7684\uff0c\u4e01\u6587\u7965\u5fc3\u91cc\u5f88\u6e05\u695a\u8fd9\u4e00\u70b9\uff0c\u4e5f\u6015\u4e4b\u524d\u5bf9\u4ed6\u7684\u63e3\u6d4b\u6709\u504f\u5dee\uff0c\u6bd5\u7adf\u53ea\u662f\u4e00\u9762\u7559\u4e0b\u7684\u5370\u8c61\uff0c\u54ea\u53ef\u80fd\u90a3\u4e48\u51c6\u786e\uff1f</p><p>\u3000\u3000\u673a\u4f1a\u96be\u5f97\uff0c\u5728\u9152\u5e2d\u4e0a\uff0c\u4e01\u6587\u7965\u6beb\u65e0\u4fdd\u7559\u7684\u5c06\u4e03\u5bb6\u5e76\u80a1\u8054\u8425\u7684\u7ec6\u8282\u6089\u6570\u8bf4\u6765\uff0c\u8c01\u90fd\u6653\u5f97\u8fd9\u65f6\u5019\u9632\u5907\u7231\u8fbe\u7535\u5b50\uff0c\u662f\u6beb\u65e0\u5fc5\u8981\u7684\uff0c\u8fd8\u4e0d\u5982\u8892\u8bda\u76f8\u5f85\u663e\u793a\u51fa\u8bda\u610f\u51fa\u6765\u3002</p><p>\u3000\u3000\u65b0\u5143\u7535\u5668\u5728\u4e03\u5bb6\u5382\u91cc\u7684\u5f71\u54cd\u529b\u6700\u5927\uff0c\u4f46\u662f\u4e5f\u6ca1\u6709\u8d85\u8fc7\u592a\u591a\uff0c\u8054\u8425\u5e76\u80a1\u4e4b\u540e\uff0c\u91c7\u7528\u65b0\u5143\u7535\u5668\u7684\u5546\u6807\uff0c\u8fd9\u5012\u6ca1\u6709\u592a\u5927\u7684\u7591\u95ee\uff0c\u5f53\u7136\uff0c\u65b0\u5143\u7535\u5668\u4e5f\u4e0d\u80fd\u5728\u8ba1\u7b97\u65e0\u5f62\u8d44\u4ea7\u4ef7\u503c\u65f6\u5360\u522b\u4eba\u4fbf\u5b9c\uff0c\u76f8\u53cd\u8fd8\u8981\u4e3b\u52a8\u5403\u4e9b\u4e8f\u624d\u884c\u3002</p><p>\u3000\u3000\u6d77\u5dde\u789f\u673a\u5382\u7684\u751f\u4ea7\u591a\u4e3a\u7ec4\u88c5\u6027\u8d28\uff0c\u4ea7\u54c1\u540c\u8d28\u5316\u7684\u73b0\u8c61\u4e25\u91cd\uff0c\u4f46\u4e5f\u4e3a\u4e03\u5bb6\u5382\u751f\u4ea7\u8d44\u6e90\u6574\u5408\u5e26\u6765\u7684\u65b9\u9762\uff0c\u751a\u81f3\u7b80\u5355\u5230\u53ea\u9700\u8981\u66f4\u6362\u673a\u58f3\u4e0e\u5546\u6807\u94ed\u724c\u5c31\u80fd\u5b9e\u73b0\u4ea7\u54c1\u578b\u53f7\u7684\u7edf\u4e00\uff0c\u6700\u591a\u9700\u8981\u6574\u5408\u7684\u662f\u5404\u5bb6\u5382\u4e0d\u540c\u8981\u6c42\u7684\u8d28\u68c0\u7a0b\u5e8f\u3002</p><p>\u3000\u3000\u5176\u6b21\u662f\u7ba1\u7406\u5c42\u4e4b\u95f4\u7684\u6574\u5408\uff0c\u539f\u5148\u4e03\u5bb6\u5382\u90fd\u6709\u4e00\u5957\u73ed\u5b50\uff0c\u4e03\u5957\u73ed\u5b50\u8981\u6574\u5408\u6210\u4e00\u5957\u73ed\uff0c\u8c01\u4e0a\u8c01\u4e0b\uff0c\u8c01\u8f9e\u9000\u4e0d\u7528\uff0c\u4e03\u5bb6\u5382\u7684\u8001\u677f\u8c01\u7559\u4e0b\u6765\u5f53\u9ad8\u5c42\uff0c\u8c01\u9000\u5230\u5e55\u540e\u5f53\u80a1\u4e1c\uff0c\u90fd\u662f\u76f8\u5f53\u5934\u75bc\u7684\u4e8b\u60c5\u3002\u597d\u5728\u8fd9\u4e03\u5bb6\u789f\u673a\u5382\u90fd\u96c6\u4e2d\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u5317\u7247\uff0c\u76f8\u4e92\u95f4\u77e5\u6839\u77e5\u5e95\uff0c\u8c01\u624b\u91cc\u6709\u4eba\u80fd\u7528\uff0c\u8bf4\u4e0d\u5b9a\u5176\u4ed6\u5382\u90fd\u76ef\u7740\u51c6\u5907\u968f\u65f6\u52a8\u624b\u6316\u4eba\uff0c\u81f3\u4e8e\u4e03\u5bb6\u5382\u7684\u8001\u677f\uff0c\u90fd\u9000\u5230\u8463\u4e8b\u4f1a\u5f53\u8463\u4e8b\u4e0d\u5e72\u6d89\u516c\u53f8\u7684\u65e5\u5e38\u8fd0\u8425\u4e5f\u53d6\u5f97\u5171\u8bc6\u3002</p><p>\u3000\u3000\u6700\u590d\u6742\u4e5f\u662f\u6700\u4ee4\u4eba\u5934\u75bc\u7684\u8fd8\u662f\u9500\u552e\u6e20\u9053\u5982\u4f55\u6574\u5408\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u6bcf\u5bb6\u5382\u5728\u5404\u5730\u57fa\u672c\u90fd\u6709\u7ecf\u9500\u5546\uff0c\u6574\u5408\u4e4b\u540e\uff0c\u65b0\u5143\u7535\u5668\u5728\u6bcf\u4e2a\u5730\u65b9\u540c\u65f6\u6709\u516d\u4e03\u5bb6\u7ecf\u9500\u5546\uff0c\u800c\u4e14\u4ee3\u7406\u534f\u8bae\u53c8\u4e0d\u80fd\u8bf4\u7a81\u7136\u4e2d\u6b62\uff0c\u5982\u4f55\u7406\u987a\u540c\u5730\u533a\u7ecf\u9500\u5546\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4e0d\u80fd\u56e0\u4e3a\u8fd9\u4e2a\u56e0\u7d20\u4f7f\u5f97\u6574\u4e2a\u5e02\u573a\u5173\u7cfb\u53d8\u6df7\u4e71\uff0c\u662f\u76f8\u5f53\u5934\u75bc\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u5728\u5730\u65b9\u4e0a\u6210\u7acb\u533a\u57df\u9500\u552e\u516c\u53f8\u5427\uff0c\u201d\u5f20\u606a\u542c\u4e01\u6587\u7965\u8bf4\u5230\u8fd9\u91cc\uff0c\u63d2\u4e86\u4e00\u53e5\u8bdd\uff0c\u201c\u5c06\u5730\u65b9\u4e0a\u7684\u4ee3\u7406\u6388\u6743\u90fd\u6574\u5408\u5230\u4e00\u5bb6\u9500\u552e\u516c\u53f8\u91cc\u53bb\uff0c\u5382\u5546\u8981\u628a\u6e20\u9053\u5546\u634f\u5728\u624b\u91cc\uff0c\u800c\u4e0d\u80fd\u8ba9\u6e20\u9053\u5546\u5c06\u81ea\u5df1\u634f\u5728\u624b\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u5e2d\u9152\uff0c\u5f20\u606a\u90fd\u5f88\u5c11\u8bf4\u8bdd\uff0c\u7279\u522b\u662f\u4e01\u6587\u7965\u3001\u5468\u5c0f\u5175\u8fd8\u6709\u4e01\u8389\u534e\u4e00\u5531\u4e00\u5408\u7684\u8bf4\u5e76\u80a1\u8054\u8425\u7684\u95ee\u9898\uff0c\u5f20\u606a\u90fd\u6ca1\u6709\u63d2\u4ec0\u4e48\u8bdd\uff0c\u9876\u591a\u6709\u4e9b\u5730\u65b9\u6ca1\u6709\u542c\u6e05\u695a\uff0c\u8ba9\u4ed6\u518d\u91cd\u590d\u4e00\u4e0b\uff0c\u5f20\u606a\u7a81\u7136\u63d2\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u800c\u4e14\u662f\u660e\u786e\u7684\u5efa\u8bae\uff0c\u4e01\u6587\u7965\u4ed6\u4eec\u542c\u4e86\u5012\u662f\u4e00\u6014\u3002</p><p>\u3000\u3000\u4e01\u6587\u7965\u4ed6\u4eec\u90fd\u627f\u8ba4\u5f20\u606a\u6709\u7740\u6781\u9ad8\u7684\u3001\u4ed6\u4eba\u671b\u5c18\u83ab\u53ca\u7684\u5546\u4e1a\u5929\u8d4b\uff0c\u4f46\u662f\u4ed6\u5728\u5546\u4e1a\u4e0a\u7684\u5929\u8d4b\u662f\u5bf9\u7231\u8fbe\u7535\u5b50\u6765\u8bf4\u7684\uff0c\u8ddf\u65b0\u5143\u7535\u5668\u5374\u6beb\u65e0\u5173\u7cfb\uff0c\u8981\u63d0\u4ec0\u4e48\u5efa\u8bae\u4e5f\u5e94\u8be5\u53ea\u662f\u6577\u884d\u6027\u8d28\u7684\u5ba2\u5957\u8bdd\uff0c\u6240\u4ee5\u542c\u5230\u5f20\u606a\u63d2\u8fdb\u6765\u4e00\u53e5\u8bdd\uff0c\u4e01\u6587\u7965\u4ed6\u4eec\u9996\u5148\u8e66\u5230\u8111\u5b50\u91cc\u7684\u5c31\u662f\u8fd9\u4e2a\u6982\u5ff5\u2014\u2014\u6ca1\u6709\u5f53\u6210\u91d1\u7389\u826f\u8a00\uff0c\u4e0d\u8fc7\u8fd8\u662f\u4e00\u8138\u611f\u6fc0\u7684\u5938\u5f20\u606a\u7684\u8fd9\u5efa\u8bae\u63d0\u5f97\u597d\u3001\u63d0\u5f97\u5999\u3002</p><p>\u3000\u3000\u9152\u5e2d\u4e0a\u4e5f\u6ca1\u6709\u65f6\u95f4\u7422\u78e8\uff0c\u7b49\u9152\u5e2d\u64a4\u53bb\u9001\u5f20\u606a\u4ed6\u4eec\u5f00\u8f66\u79bb\u5f00\u9152\u5e97\uff0c\u4e01\u6587\u7965\u4e0e\u5468\u5c0f\u5175\u3001\u4e01\u8389\u5f00\u8f66\u51c6\u5907\u56de\u516c\u53f8\u2014\u2014\u4ed6\u4eec\u5bb6\u5728\u4e1c\u5143\uff0c\u8fd9\u6bb5\u65f6\u95f4\u90fd\u4f4f\u516c\u53f8\u7684\u804c\u5de5\u5bbf\u820d\u2014\u2014\u624d\u8d8a\u7422\u78e8\u8d8a\u89c9\u5f97\u5f20\u606a\u7684\u90a3\u53e5\u8bdd\u91cc\u85cf\u7740\u5473\u9053\u3002\u4e5f\u4e0d\u7ba1\u73b0\u5728\u662f\u591c\u91cc\u4e5d\u70b9\u949f\uff0c\u5c06\u65b0\u5382\u7684\u8463\u4e8b\u4ee5\u53ca\u5e02\u573a\u90e8\u7684\u9ad8\u5c42\u90fd\u53eb\u5230\u5382\u5b50\u91cc\uff0c\u5546\u8bae\u4e86\u4e00\u6574\u591c\uff0c\u624d\u89c9\u5f97\u5f20\u606a\u7684\u5efa\u8bae\u5927\u6982\u662f\u76ee\u524d\u60df\u4e00\u80fd\u5728\u77ed\u65f6\u95f4\u91cc\u6709\u6548\u6574\u5408\u539f\u5148\u6563\u4e71\u7684\u6e20\u9053\u8d44\u6e90\u7684\u65b9\u6cd5\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5404\u5bb6\u5382\u90fd\u6709\u9500\u552e\u90e8\u95e8\uff0c\u6574\u4e2a\u4e4b\u540e\uff0c\u5e02\u573a\u90e8\u5c31\u53d8\u5f97\u81c3\u80bf\u4e0d\u582a\uff0c\u6700\u521d\u8003\u8651\u8981\u72e0\u72e0\u7684\u88c1\u4eba\uff0c\u53ea\u4fdd\u7559\u7cbe\u5175\u5f3a\u5c06\u3002\u7136\u800c\u5bf9\u4e8e\u7ec4\u88c5\u578b\u7684\u789f\u673a\u4f01\u4e1a\u6765\u8bf4\uff0c\u4f01\u4e1a\u7684\u91cd\u5fc3\u51e0\u4e4e\u90fd\u653e\u5728\u5e02\u573a\u6e20\u9053\u8fd9\u4e00\u5757\uff0c\u5373\u4f7f\u8981\u4fdd\u7559\u7cbe\u5175\u5f3a\u5c06\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8bbe\u7f6e\u5728\u5e02\u573a\u90e8\u91cc\u4e03\u4e2a\u90e8\u957f\u3001\u5341\u56db\u4e2a\u526f\u90e8\u957f\u3002\u6240\u4ee5\u5e76\u80a1\u8054\u8425\u4e4b\u540e\uff0c\u539f\u5148\u5404\u5bb6\u5382\u7684\u91cd\u5fc3\u4eba\u7269\u5374\u663e\u5f97\u6697\u6f6e\u6c79\u6d8c\u3001\u4eba\u5fc3\u6d6e\u52a8\u3002</p><p>\u3000\u3000\u5230\u5404\u5730\u6ce8\u518c\u6210\u7acb\u533a\u57df\u9500\u552e\u516c\u53f8\uff0c\u5c06\u5404\u5730\u5206\u6563\u7684\u4ee3\u7406\u6388\u6743\u90fd\u6574\u5408\u5230\u9500\u552e\u516c\u53f8\u53bb\uff0c\u518d\u8ba9\u5e02\u573a\u90e8\u65e0\u6cd5\u5b89\u7f6e\u7684\u7cbe\u5e72\u9500\u552e\u4eba\u5458\u4e0b\u53bb\u8d1f\u8d23\u7ba1\u7406\u9500\u552e\u516c\u53f8\uff0c\u5c31\u80fd\u89e3\u51b3\u7edd\u5927\u90e8\u5206\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u597d\u5427\uff0c\u8fd9\u4e2a\u95ee\u9898\u53d6\u5f97\u5171\u8bc6\u4e4b\u540e\uff0c\u4e01\u6587\u7965\u8fd9\u4e9b\u5929\u6765\u6700\u5927\u7684\u56f0\u6270\u5c31\u6682\u65f6\u89e3\u51b3\u4e86\uff0c\u4f46\u662f\u65b0\u7684\u56f0\u6270\u53c8\u968f\u4e4b\u800c\u6765\uff1a\u5f20\u606a\u4e3a\u4ec0\u4e48\u8981\u4e00\u9488\u89c1\u8840\u7684\u5e2e\u65b0\u5143\u7535\u5668\u6307\u51fa\u89e3\u51b3\u95ee\u9898\u7684\u6377\u5f84\uff1f</p><p>\u3000\u3000\u5f53\u521d\uff0c\u5f20\u606a\u4e0d\u501f\u96f6\u90e8\u4ef6\u4f9b\u5e94\u88e2\u7684\u95ee\u9898\u6253\u538b\u79d1\u738b\uff0c\u4e01\u6587\u7965\u627f\u8ba4\u5f20\u606a\u7684\u6c14\u5ea6\u8fc7\u4eba\uff0c\u4e8b\u5b9e\u4e5f\u8bc1\u660e\u79d1\u738b\u59cb\u7ec8\u5bf9\u7231\u8fbe\u7535\u5b50\u9020\u6210\u4e0d\u4e86\u5371\u9669\uff0c\u4f46\u662f\u773c\u4e0b\uff0c\u8981\u8bf4\u662f\u7ecf\u8425\u7684\u6c14\u5ea6\uff0c\u672a\u514d\u8fc7\u4e8e\u7406\u60f3\u4e3b\u4e49\u4e86\u3002\u5c31\u7b97\u7231\u8fbe\u7535\u5b50\u518d\u4e0d\u5c06\u65b0\u5143\u7535\u5668\u653e\u5728\u773c\u91cc\uff0c\u65b0\u5143\u7535\u5668\u53d1\u5c55\u8d77\u6765\u5bf9\u7231\u8fbe\u7535\u5b50\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u70b9\u7684\u51b2\u51fb\uff1f\u96be\u9053\u4ec5\u4ec5\u662f\u60f3\u5411\u79d1\u738b\u7535\u5668\u589e\u52a0\u4e9b\u9ebb\u70e6\uff1f</p><p>\u3000\u30001\u670818\u65e5\uff0c\u4e01\u6587\u7965\u6b63\u5f0f\u627e\u82cf\u6d25\u4e1c\u8c08\u53c2\u4e0e\u65b0\u789f\u673a\u6d4b\u8bd5\u7684\u4e8b\u60c5\uff0c\u82cf\u6d25\u4e1c\u3001\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u3001\u5f20\u4e9a\u5e73\u7b49\u4eba\u4e00\u8d77\u53c2\u89c2\u65b0\u5143\u7535\u5668\uff0c\u65b0\u5143\u7535\u5668\u867d\u7136\u662f\u95f4\u5c0f\u5382\uff0c\u4f46\u662f\u7b49\u4e03\u5bb6\u5382\u5708\u8d77\u6765\u4e4b\u540e\uff0c\u89c4\u6a21\u8fd8\u76f8\u5f53\u53ef\u89c2\uff0c\u53c2\u89c2\u4e4b\u540e\uff0c\u53c8\u8ba4\u771f\u542c\u8fc7\u65b0\u5143\u7535\u5668\u5e76\u80a1\u8054\u8425\u7684\u5177\u4f53\u65b9\u6848\uff0c\u4fbf\u540c\u610f\u9884\u7559\u7ed9\u6d77\u5dde\u789f\u673a\u4f01\u4e1a\u7684\u8fd9\u4e2a\u540d\u989d\u7ed9\u65b0\u5143\u7535\u5668\uff0c\u7231\u8fbe\u7535\u5b50\u751a\u81f3\u8fd8\u613f\u610f\u6d3e\u51fa\u4e24\u540d\u5de5\u7a0b\u5e08\u5bf9\u65b0\u5143\u7535\u5668\u7684\u6d4b\u8bd5\u51c6\u5907\u5de5\u4f5c\u8fdb\u884c\u6307\u5bfc\u3002</p><p>\u3000\u3000\u4e01\u6587\u7965\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5411\u82cf\u6d25\u4e1c\u95ee\u51fa\u4ed6\u5fc3\u91cc\u7684\u56f0\u60d1\uff0c\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u4e24\u5e74\u591a\u65f6\u95f4\u53d1\u5c55\u8d77\u4eca\u5929\u7684\u89c4\u6a21\uff0c\u8d8a\u53d1\u611f\u89c9\u5230\u6d77\u5dde\u5730\u533a\u7535\u5b50\u5de5\u4e1a\u53d1\u5c55\u6574\u4f53\u6ede\u540e\u4f1a\u8d8a\u6765\u8d8a\u4e25\u91cd\u7684\u5236\u7ea6\u7231\u8fbe\u7535\u5b50\uff0c\u4e0e\u5176\u7231\u8fbe\u7535\u5b50\u4e00\u5bb6\u52aa\u529b\u7684\u53bb\u6539\u5584\u6d77\u5dde\u5730\u533a\u7535\u5b50\u5de5\u4e1a\u7684\u6574\u4f53\u73af\u5883\uff0c\u8fd8\u4e0d\u5982\u591a\u62c9\u51e0\u5bb6\u4f01\u4e1a\u4e00\u8d77\u52aa\u529b\uff0c\u6bd5\u7adf\u5f71\u789f\u673a\u53ea\u662f\u7535\u5b50\u5de5\u4e1a\u5f88\u5c0f\u7684\u4e00\u4e2a\u8303\u7574\u3002\u8981\u662f\u5bb3\u6015\u79d1\u738b\u4ee5\u53ca\u4f60\u4eec\u65b0\u5143\u7684\u7ade\u4e89\uff0c\u800c\u4e0d\u6562\u653e\u624b\u6539\u826f\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u5411\u524d\u53d1\u5c55\u7684\u571f\u58e4\uff0c\u7231\u8fbe\u7535\u5b50\u5c31\u6c38\u8fdc\u6210\u4e3a\u4e0d\u4e86\u4e00\u6d41\u7684\u7535\u5b50\u4f01\u4e1a\u2026\u2026\u201d</p></article>"}}}]);
