(window.webpackJsonp=window.webpackJsonp||[]).push([[654],{677:function(p){p.exports={chapterTitle:null,title:"\u7b2c653\u7ae0 \u5927\u573a\u9762",content:"<article><h2>\u7b2c653\u7ae0 \u5927\u573a\u9762</h2><p>\u3000\u300010\u67088\u65e5\uff0c\u67f3\u5fd7\u6210\u4e0e\u6676\u5706\u5382\u9879\u76ee\u53e6\u4e24\u540d\u4e3b\u8981\u6280\u672f\u8d1f\u8d23\u4eba\u5411\u53f0\u79ef\u7535\u63d0\u4ea4\u8f9e\u5448\uff0c\u5f97\u5230\u53f0\u79ef\u7535\u7684\u9996\u80af\u4e4b\u540e\uff0c\u5f53\u65e5\u591c\u95f4\u4e09\u4eba\u5c31\u968f\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u4e58\u98de\u673a\u62b5\u8fbe\u65b0\u52a0\u5761\u3002</p><p>\u3000\u3000\u6b21\u65e5\uff0c10\u67089\u65e5\uff0c\u67f3\u5fd7\u6210\u5728\u72ee\u57ce\u5ba3\u5e03\u6b63\u5f0f\u63a5\u53d7\u8d8a\u79c0\u63a7\u80a1\u4e0e\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u7684\u5171\u540c\u59d4\u6258\u8d1f\u8d23\u8d77\u5728\u5efa\u90ba\u7b79\u5907\u6676\u5706\u5382\u9879\u76ee\u7684\u91cd\u4efb\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u9a7b\u65b0\u52a0\u5761\u5927\u4f7f\u9986\u5b54\u94ed\u8d5e\u5728\u5927\u4f7f\u9986\u63a5\u89c1\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4e00\u884c\u4eba\uff0c\u4ee3\u8868\u5185\u5730\u653f\u5e9c\u6b22\u8fce\u4ed6\u4eec\u8fd9\u4e9b\u534e\u4eba\u4e2d\u7684\u7fd8\u695a\u4e4b\u58eb\u5230\u5185\u5730\u6295\u8d44\u521b\u4e1a\u3002</p><p>\u3000\u3000\u8fc4\u81f3\u6b64\u65f6\uff0c\u53f0\u6e7e\u5f53\u5c40\u4e0e\u53f0\u79ef\u7535\u624d\u6653\u5f97\u539f\u5148\u7684\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u65e9\u5c31\u4ece\u9a6c\u6765\u897f\u4e9a\u8f6c\u79fb\u5230\u5185\u5730\uff0c\u800c\u4e14\u4e3a\u6b64\u7b79\u8c0b\u4e86\u4e0d\u662f\u4e00\u5929\u4e24\u5929\u7684\u5de5\u592b\u3002</p><p>\u3000\u3000\u5f53\u591c\uff0c\u5b54\u94ed\u8d5e\u5927\u4f7f\u5728\u5927\u4f7f\u9986\u4e3a\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4e00\u884c\u4eba\u4e3e\u529e\u62db\u5f85\u9152\u4f1a\u4e0a\uff0c\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u4e0e\u65b0\u52a0\u5761\u96c6\u6210\u7535\u8def\u5236\u9020\u5546\u65b0\u52a0\u5761\u96c6\u7535\u96c6\u56e2\u5747\u6709\u9ad8\u5c42\u5217\u5e2d\u51fa\u5b54\u94ed\u8d5e\u5927\u4f7f\u7684\u62db\u5f85\u9152\u4f1a\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u96c6\u7535\u96c6\u56e2\u5c06\u662f\u5185\u5730\u9996\u5bb6\u516b\u82f1\u5bf8\u6676\u5706\u5382\u7684\u5de5\u827a\u6280\u672f\u4f9b\u7ed9\u5546\uff0c\u5728\u5f53\u591c\u7684\u62db\u5f85\u9152\u4f1a\u4e0a\uff0c\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u5217\u5e2d\u7684\u9ad8\u5c42\u4eba\u58eb\u5411\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u8868\u793a\u4ed6\u4eec\u4e5f\u6709\u610f\u53c2\u4e0e\u5230\u8fd9\u4e2a\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u91cc\u6765\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5728\u65b0\u52a0\u5761\u4e0e\u5f20\u606a\u901a\u7535\u8bdd\u901a\u62a5\u4e86\u6b64\u4e8b\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u5728\u65b0\u52a0\u5761\u5883\u5185\u53c2\u4e0e\u7684\u51e0\u4e2a\u6676\u5706\u5382\u9879\u76ee\u8fd8\u90fd\u4e00\u76f4\u5904\u4e8e\u4e8f\u635f\u72b6\u51b5\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u4e0d\u5927\u53ef\u80fd\u5411\u4ed6\u4eec\u7684\u6676\u5706\u5382\u9879\u76ee\u6ce8\u5165\u592a\u591a\u7684\u8d44\u91d1\u4ee5\u81f4\u5f71\u54cd\u4ed6\u901a\u8fc7\u8d8a\u79c0\u63a7\u80a1\u5bf9\u8fd9\u4e2a\u9879\u76ee\u7684\u63a7\u5236\u7a0b\u5ea6\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u773c\u4e0b\u4e5f\u53ea\u6709\u4ece\u65b0\u52a0\u5761\u5f15\u5165\u6676\u5706\u5236\u9020\u6280\u672f\uff0c\u5373\u4f7f\u8fd9\u4e2a\u9879\u76ee\u51fa\u73b0\u5371\u673a\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u591a\u4e86\u65b0\u52a0\u5761\u6295\u8d44\u7ba1\u7406\u5c40\u8fd9\u4e00\u4e2a\u5408\u4f5c\u8005\uff0c\u4e5f\u80fd\u66f4\u8f7b\u677e\u7684\u8de8\u8d8a\u672a\u6765\u53ef\u80fd\u4f1a\u9047\u5230\u7684\u79cd\u79cd\u969c\u788d\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u7684\u6295\u8d44\u9879\u76ee\u624d\u4ec5\u4ec5\u662f\u5f15\u8fdb\u4e00\u6761\u516b\u82f1\u5bf8\u751f\u4ea7\u7ebf\uff0c\u5c31\u7b97\u6210\u529f\uff0c\u4e5f\u4ec5\u4ec5\u662f\u83b7\u5f97\u65b0\u52a0\u5761\u96c6\u7535\u96c6\u56e20.35\u5fae\u7c73\u7684\u5236\u9020\u5de5\u827a\u6280\u672f\u3002\u65e9\u5728486\u65f6\u4ee3\u7684\u540e\u671f\uff0c\u82f1\u7279\u5c14\u5c31\u4f7f\u7528\u4e0a0.35\u5fae\u7c73\u7684\u5236\u9020\u5de5\u827a\uff0c\u4e5f\u5c31\u662f\u8bf4\u8fd9\u6b21\u6295\u8d44\u7684\u6676\u5706\u5382\u9879\u76ee\u8ddd\u4e16\u754c\u9876\u7ea7\u6280\u672f\u6c34\u51c6\u81f3\u5c11\u8fd8\u6709\u56db\u5230\u4e94\u5e74\u7684\u4ee3\u5dee\u3002\u5f15\u8fdb\u5341\u4e8c\u82f1\u5bf8\u7684\u751f\u4ea7\u7ebf\uff0c\u5f15\u8fdb0.25\u5fae\u7c73\u751a\u81f3\u66f4\u5148\u8fdb\u7684\u5236\u9020\u5de5\u827a\uff0c\u8fd9\u4e9b\u90fd\u662f\u672a\u6765\u9700\u8981\u514b\u670d\u7684\u969c\u788d\u3002\u53ea\u6709\u5c06\u8fd9\u4e9b\u57fa\u7840\u7684\u5de5\u827a\u6280\u672f\u90fd\u5f15\u8fdb\u6765\uff0c\u57f9\u517b\u51fa\u4e00\u6279\u5de5\u7a0b\u6280\u672f\u4e0e\u7814\u53d1\u4eba\u5458\u6765\uff0c\u5185\u5730\u7684\u6676\u5706\u5236\u9020\u624d\u80fd\u7b97\u6709\u4e00\u79cd\u8d70\u4e0a\u771f\u6b63\u53d1\u5c55\u9053\u8def\u7684\u57fa\u7840\uff0c\u8fd9\u6761\u9053\u8def\u8fd8\u662f\u6781\u4e3a\u6f2b\u957f\u7684\u3002</p><p>\u3000\u3000\u8fd9\u6761\u9053\u8def\u60f3\u51ed\u501f\u9526\u6e56\u4e00\u5bb6\u7684\u529b\u91cf\u987a\u5229\u7684\u8d70\u4e0b\u53bb\uff0c\u5176\u4e2d\u7684\u56f0\u96be\u8fd8\u771f\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u591f\u60f3\u8c61\uff0c\u8981\u662f\u6709\u53ef\u80fd\uff0c\u5f20\u606a\u751a\u81f3\u60f3\u7740\u5c06\u5fb7\u4eea\u4e5f\u62c9\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u603b\u662f\u4e13\u6ce8\u7740\u5411\u66f4\u9ad8\u7aef\u7684\u6280\u672f\u9886\u57df\u8fc8\u8fdb\uff0c\u5e76\u9010\u6b65\u7684\u4ece\u4e2d\u4f4e\u7aef\u5e02\u573a\u64a4\u51fa\uff0c\u51ed\u501f\u9526\u6e56\u4e0e\u5fb7\u4eea\u6b64\u65f6\u826f\u597d\u7684\u6280\u672f\u5408\u4f5c\uff0c\u5f15\u8fdb\u5fb7\u4eea\u7684\u6295\u8d44\u662f\u5f88\u6709\u53ef\u80fd\u7684\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u8bf4\u597d\uff0c\u9526\u6e56\u60f3\u8981\u5728\u96c6\u6210\u7535\u8def\u5236\u9020\u7684\u6280\u672f\u4e0a\u8ffd\u8d76\u5fb7\u4eea\uff0c\u5c31\u7b97\u7529\u5f00\u8180\u5b50\u5954\u8dd1\uff0c\u6ca1\u6709\u5341\u5e74\u516b\u5e74\u7684\u65f6\u95f4\u4e5f\u90fd\u662f\u4e0d\u73b0\u5b9e\u7684\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6b64\u65f6\u6ca1\u6709\u5fc5\u8981\u5c31\u6025\u7740\u5411\u5fb7\u4eea\u629b\u51fa\u6a44\u6984\u679d\uff0c\u7b49\u8fd9\u4e2a\u9879\u76ee\u6709\u5efa\u6210\u7684\u5e0c\u671b\u4e4b\u65f6\uff0c\u62ff\u5165\u8d44\u4f5c\u4e3a\u6761\u4ef6\uff0c\u6307\u4e0d\u5b9a\u8fd8\u80fd\u591f\u4ece\u5fb7\u4eea\u83b7\u5f97\u66f4\u5148\u8fdb\u7684\u5236\u9020\u5de5\u827a\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u6253\u7740\u8fd9\u6837\u7684\u4e3b\u610f\uff0c\u5728\u7535\u8bdd\u91cc\u4e0e\u5b59\u5c1a\u4e49\u5546\u8bae\u7740\u8bf4\uff1a\u201c\u65b0\u52a0\u5761\u5f53\u5c40\u603b\u662f\u60f3\u5728\u534e\u4eba\u5708\u5185\u53d1\u6325\u66f4\u591a\u7684\u5f71\u54cd\u529b\uff0c\u5434\u4f5c\u680b\u603b\u7406\u5927\u6982\u60f3\u6210\u7acb\u534e\u4eba\u7684\u65b0\u9886\u8896\u5f0f\u7684\u4eba\u7269\u3002\u8fd9\u6837\u4e5f\u597d\uff0c\u8fd8\u5206\u62c5\u4e86\u6211\u4eec\u7684\u8d44\u91d1\u538b\u529b\uff0c\u6211\u4eec\u7684\u786e\u4e5f\u6ca1\u6709\u5fc5\u8981\u62d2\u7edd\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u65e2\u7136\u7a81\u7136\u60f3\u53c2\u4e0e\u8fdb\u6765\uff0c\u90a3\u5c31\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u591a\u505a\u4e9b\u8d21\u732e\uff0c\u90ed\u5bb6\u6240\u9700\u8981\u7684\u8d44\u91d1\uff0c\u4f3c\u4e4e\u53ef\u4ee5\u8bf7\u65b0\u52a0\u5761\u5f53\u5c40\u5e2e\u5fd9\u89e3\u51b3\uff0c\u5206\u62c5\u4e00\u4e0b\u5185\u5730\u7684\u878d\u8d44\u538b\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u5c3d\u7ba1\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e0e\u7ecf\u6d4e\u5371\u673a\u7684\u53cc\u91cd\u5f71\u54cd\uff0c\u65b0\u52a0\u5761\u7684\u7ecf\u6d4e\u5b9e\u529b\u4e5f\u6bcb\u5eb8\u7f6e\u7591\uff0c\u4e09\u4e24\u4ebf\u7f8e\u5143\u7684\u6295\u8d44\uff0c\u5bf9\u65b0\u52a0\u5761\u5f53\u5c40\u6765\u8bf4\u4e5f\u662f\u4e3e\u91cd\u82e5\u8f7b\u7684\u5c0f\u4e8b\u4e00\u6869\u3002\u6b64\u65f6\u8ffd\u52a0\u5bf9\u6676\u5706\u5382\u9879\u76ee\u7684\u6295\u8d44\uff0c\u4e5f\u65e0\u7591\u662f\u4e00\u9879\u76f8\u5f53\u6709\u6218\u7565\u773c\u5149\u7684\u51b3\u7b56\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u5730\u5c0f\u4eba\u7a00\uff0c\u4eba\u529b\u8d44\u6e90\u7684\u4e0d\u8db3\u4e0e\u4eba\u529b\u8d44\u6e90\u6210\u672c\u7684\u9ad8\u6602\uff0c\u81ea\u8eab\u5e02\u573a\u7684\u72ed\u7a84\u4ee5\u53ca\u4e0e\u5176\u4ed6\u56fd\u5bb6\u4e0e\u5730\u533a\u95f4\u7684\u58c1\u5792\u969c\u788d\u7b49\u79cd\u79cd\u56e0\u7d20\u5bfc\u81f4\u65b0\u52a0\u5761\u7684\u51e0\u5bb6\u6676\u5706\u5382\u5efa\u6210\u540e\u90fd\u5904\u4e8e\u4e8f\u635f\u72b6\u6001\uff0c\u5373\u4f7f\u5982\u6b64\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u5e76\u6ca1\u6709\u653e\u5f03\u5bf9\u5176\u5883\u5185\u6676\u5706\u5382\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u5185\u5730\u5e02\u573a\u7684\u524d\u666f\u4f3c\u4e4e\u66f4\u4e3a\u8bf1\u4eba\uff0c\u4e00\u4e2a\u5341\u4e09\u4ebf\u4eba\u53e3\u6ca1\u6709\u5173\u7a0e\u58c1\u5792\u7684\u5e9e\u5927\u5e02\u573a\uff0c\u4ec5\u8fd9\u4e24\u4e09\u5e74\u5f00\u53d1\u51fa\u6765\u7684\u789f\u673a\u4ea7\u4e1a\uff0c\u5c31\u4e3a\u96c6\u6210\u7535\u8def\u5236\u9020\u63d0\u4f9b\u8fd1\u516d\u4ebf\u7f8e\u5143\u7684\u5e9e\u5927\u5e02\u573a\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5916\u754c\u8fd8\u4e0d\u6653\u5f97\u9526\u6e56\u4ec5\u901a\u8fc7\u8d85\u7ea7VCD\u7684\u89e3\u7801\u82af\u7247\u5c31\u4ece\u8fd9\u4e2a\u96c6\u6210\u7535\u8def\u5236\u9020\u5e02\u573a\u5206\u6389\u4e00\u5927\u5757\u7684\u86cb\u7cd5\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u79cd\u66b4\u5229\u4e5f\u53ea\u80fd\u5076\u5c14\u4eab\u53d7\u4e00\u4e0b\uff0c\u60f3\u8981\u771f\u6b63\u7684\u4ece\u96c6\u6210\u7535\u8def\u5236\u9020\u5e02\u573a\u5206\u4e00\u5927\u5757\u86cb\u7cd5\uff0c\u81ea\u8eab\u5c31\u8981\u5728\u96c6\u6210\u7535\u8def\u5236\u9020\u4e0e\u8bbe\u8ba1\u9886\u57df\u62e5\u6709\u76f8\u5f53\u96c4\u539a\u7684\u6280\u672f\u4e0e\u4eba\u529b\u8d44\u6e90\u624d\u884c\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u6676\u5706\u5382\u9879\u76ee\u7684\u53e6\u4e00\u6295\u8d44\u4eba\uff0c\u90ed\u677e\u5ef6\u4e5f\u662f\u6b22\u8fce\u65b0\u52a0\u5761\u5f53\u5c40\u52a0\u5165\u7684\u3002\u90ed\u6c0f\u5411\u65b0\u5149\u7eb8\u4e1a\u51fa\u552e\u4e91\u6e90\u5236\u6d4630\uff05\u7684\u80a1\u6743\u83b7\u5f971\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u5df2\u7ecf\u76f8\u5f53\u7a0b\u5ea6\u4e0a\u7f13\u89e3\u4e86\u4e91\u6e90\u96c6\u56e2\u7684\u8d44\u91d1\u538b\u529b\uff0c\u518d\u8bf4\u8fd9\u6b21\u5411\u6676\u5706\u5382\u9879\u76ee\u6ce8\u8d44\u4e0d\u9700\u8981\u6324\u5360\u4e91\u6e90\u96c6\u56e2\u73b0\u9636\u6bb5\u7684\u8d44\u91d1\u88e2\u3002</p><p>\u3000\u300010\u670812\u65e5\uff0c\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u5c31\u4e0e\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u4ee5\u53ca\u65b0\u52a0\u5761\u96c6\u7535\u96c6\u56e2\u8349\u7b7e\u4e86\u5408\u4f5c\u534f\u8bae\u3002\u5408\u4f5c\u534f\u8bae\u8349\u6848\u7ea6\u5b9a\u4e86\u5404\u5bb6\u7684\u51fa\u8d44\u6bd4\u4f8b\uff0c\u8d8a\u79c0\u63a7\u80a1\u51fa\u8d443\u4ebf\u7f8e\u5143\u63a7\u80a156\uff05\uff0c\u65b0\u52a0\u5761\u6295\u8d44\u7ba1\u7406\u5c40\u51fa\u8d441\u4ebf\u7f8e\u5143\uff0c\u6301\u80a1\u7ea619\uff05\u5de6\u53f3\uff1b\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u51fa\u8d448000\u4e07\u7f8e\u5143\uff0c\u6301\u80a1\u7ea615\uff05\u5de6\u53f3\uff0c\u67f3\u5fd7\u6210\u53ca\u5176\u6240\u7387\u9886\u7684\u7ba1\u7406\u56e2\u961f\u51fa\u8d442000\u4e07\u7f8e\u5143\u52a0\u4e0a\u6280\u672f\u4e0e\u7ba1\u7406\u4e0a\u7684\u6301\u80a1\u6743\u5171\u8ba110\uff05\u3002</p><p>\u3000\u3000\u4ece\u5e74\u524d\u90ed\u677e\u5ef6\u7b79\u5212\u6676\u5706\u5382\u9879\u76ee\u4ee5\u6765\uff0c\u67f3\u5fd7\u6210\u5c31\u6709\u8ba1\u5212\u7684\u5c06\u4e2a\u4eba\u8d44\u4ea7\u8f6c\u79fb\u51fa\u53f0\u6e7e\uff0c\u5f53\u7136\uff0c\u6709\u4e00\u4e9b\u8d44\u4ea7\uff0c\u6bd4\u5982\u8bf4\u4ed6\u4e2a\u4eba\u5728\u53f0\u79ef\u7535\u7684\u6301\u80a1\uff0c\u662f\u4e0d\u53ef\u80fd\u8f6c\u79fb\u51fa\u6765\u7684\u3002\u8fd9\u4e00\u90e8\u5206\u8d44\u4ea7\uff0c\u4ed6\u4e5f\u6709\u727a\u7272\u6389\u7684\u89c9\u609f\u3002\u6bd4\u8d77\u4e2a\u4eba\u8d44\u4ea7\u7684\u591a\u5be1\uff0c\u4ed6\u4f1a\u5411\u5f80\u4e2a\u4eba\u4e8b\u4e1a\u4e0a\u7684\u6210\u5c31\u4e0e\u5728\u6574\u4e2a\u7535\u5b50\u4ea7\u4e1a\u7684\u4e2a\u4eba\u5730\u4f4d\u3002</p><p>\u3000\u300010\u670813\u65e5\uff0c\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4ee5\u53ca\u9996\u6279\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u53ca\u5bb6\u5c5e\u4e8c\u5341\u4f59\u4eba\u4ece\u65b0\u52a0\u5761\u4e58\u98de\u673a\u76f4\u63a5\u98de\u62b5\u5efa\u90ba\u56fd\u9645\u673a\u573a\u3002</p><p>\u3000\u3000\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u3001\u56fd\u52a1\u9662\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7ec4\u957f\u845b\u5efa\u5fb7\u3001\u90ae\u7535\u90e8\u526f\u90e8\u957f\u3001\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u526f\u7ec4\u957f\u6613\u4e91\u98de\u4e00\u884c\u4eba\u5728\u4e1c\u6d77\u7701\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u3001\u7701\u957f\u674e\u8fdc\u6e56\u3001\u5efa\u90ba\u5e02\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u5efa\u90ba\u5e02\u5e02\u957f\u8096\u660e\u5efa\u3001\u5e38\u59d4\u526f\u5e02\u957f\u3001\u6570\u5b57\u957f\u5eca\u6784\u60f3\u9886\u5bfc\u5c0f\u7ec4\u7ec4\u957f\u738b\u7ef4\u5747\u3001\u526f\u5e02\u957f\u3001\u65b0\u6d66\u9ad8\u65b0\u533a\u4e3b\u4efb\u9ec4\u514b\u7fa4\u3001\u65b0\u6d66\u9ad8\u65b0\u533a\u6295\u8d44\u4fc3\u8d44\u5c40\u59da\u6587\u76db\u7b49\u4eba\u7684\u966a\u540c\u4e0b\uff0c\u9a71\u8f66\u5230\u5efa\u90ba\u56fd\u9645\u673a\u573a\u8fce\u63a5\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u7b49\u4eba\u3002</p><p>\u3000\u3000\u65e0\u53ef\u7f6e\u7591\u6676\u5706\u5382\u9879\u76ee\u5bf9\u4e1c\u6d77\u7701\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u3001\u5efa\u90ba\u5e02\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u4ee5\u53ca\u5bf9\u56fd\u5185\u6574\u4e2a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u5e03\u5c40\u7684\u91cd\u8981\u610f\u4e49\uff0c\u6bd4\u8d77\u6469\u6258\u7f57\u62c9\u5728\u5929\u6d25\u7684\u5efa\u5382\u8ba1\u5212\uff0c\u5efa\u90ba\u7684\u6676\u5706\u5382\u6295\u8d44\u8ba1\u5212\u66f4\u5177\u6709\u7a81\u7834\u6027\u7684\u610f\u4e49\u4e0e\u4ef7\u503c\uff0c\u8fd9\u6bd5\u7adf\u662f\u4e3a\u6574\u4e2a\u533a\u57df\u7535\u5b50\u4ea7\u4e1a\u670d\u52a1\u7684\u3002</p><p>\u3000\u3000\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u62b5\u8fbe\u5efa\u90ba\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u63a5\u673a\u800c\u6765\uff0c\u8fd8\u5c06\u4ee3\u8868\u56fd\u5bb6\u8ba1\u59d4\u5bf9\u4e1c\u6d77\u7701\u4e0e\u5efa\u90ba\u5e02\u7684\u5e02\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u8ba1\u5212\u8fdb\u884c\u5b9e\u5730\u5ba1\u5bdf\uff0c\u4e00\u7ecf\u901a\u8fc7\uff0c\u56fd\u5bb6\u5c31\u5c06\u5728\u653f\u7b56\u4e0a\u5bf9\u4e1c\u6d77\u7701\u3001\u5efa\u90ba\u5e02\u8fdb\u884c\u4ea7\u4e1a\u653f\u7b56\u4e0a\u7684\u503e\u659c\u3002</p><p>\u3000\u3000\u60f3\u5fc5\u6ca1\u6709\u4eba\u613f\u610f\u5f20\u606a\u8fd9\u4e48\u4e00\u4e2a\u60eb\u61d2\u9752\u5e74\u6df7\u5728\u8863\u51a0\u695a\u695a\u7684\u4e2d\u8001\u5e74\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u9526\u6e56\u7684\u5173\u7cfb\u9887\u4e3a\u590d\u6742\uff0c\u4e5f\u6d3e\u4e0d\u51fa\u5408\u9002\u7684\u4eba\u9009\u966a\u540c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u7b49\u4eba\u51fa\u5e2d\u673a\u573a\u7684\u63a5\u673a\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u662f\u8d8a\u79c0\u63a7\u80a1\u7684\u5b9e\u9645\u63a7\u5236\u4eba\uff0c\u4e01\u69d0\u662f\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7684\u8463\u4e8b\u957f\u517c\u603b\u88c1\uff0c\u4f46\u662f\u58f0\u671b\u4e0e\u5f71\u54cd\u90fd\u4e0d\u53ca\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u9648\u4fe1\u751f\uff0c\u4f46\u662f\u7231\u8fbe\u96c6\u56e2\u4e0e\u8d8a\u79c0\u63a7\u80a1\u53c8\u6ca1\u6709\u76f4\u63a5\u7684\u5173\u8054\uff1b\u4ec5\u4ece\u804c\u4f4d\u4e0a\u6765\u770b\uff0c\u4f5c\u4e3a\u9526\u6e56\u7684\u603b\u88c1\u8521\u7eef\u5a1f\u66f4\u6709\u8d44\u683c\u5217\u5e2d\u8fd9\u6837\u7684\u6d3b\u52a8\uff0c\u4f46\u662f\u5728\u9526\u6e56\u4f53\u7cfb\u5185\uff0c\u8521\u7eef\u5a1f\u7684\u5730\u4f4d\u5176\u5b9e\u662f\u4f4e\u4e8e\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u3001\u5468\u6e38\u7b49\u4eba\u7684\u3002</p><p>\u3000\u3000\u771f\u662f\u8ba9\u4eba\u5934\u75bc\u7684\u4f53\u7cfb\u7ed3\u6784\uff0c\u8fd9\u6837\u7684\u5927\u573a\u9762\uff0c\u5f20\u606a\u8981\u4e0d\u4eb2\u81ea\u51fa\u9a6c\uff0c\u7adf\u7136\u5c31\u518d\u627e\u4e0d\u5230\u5408\u9002\u7684\u4eba\u9009\u3002</p><p>\u3000\u3000\u5f20\u606a\u7d22\u6027\u4e0d\u8ba9\u9526\u6e56\u6d3e\u4eba\u53c2\u52a0\u673a\u573a\u7684\u63a5\u5f85\u6d3b\u52a8\uff0c\u5b59\u5c1a\u4e49\u4f5c\u4e3a\u8d8a\u79c0\u63a7\u80a1\u7684\u8463\u4e8b\u957f\u3001\u848b\u8587\u4f5c\u4e3a\u8d8a\u79c0\u63a7\u80a1\u7684\u603b\u88c1\u966a\u540c\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u7b49\u4eba\u5728\u573a\uff0c\u81ea\u7136\u4e5f\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u60f3\u5fc5\u9676\u664b\u3001\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u7b49\u4eba\u4e5f\u80fd\u8c05\u89e3\u3002</p><p>\u3000\u3000\u867d\u7136\u5468\u6e38\u4e5f\u662f\u4ece\u65b0\u52a0\u5761\u8f6c\u9053\u4e58\u540c\u4e00\u73ed\u98de\u673a\u62b5\u8fbe\u5efa\u90ba\uff0c\u4e0d\u8fc7\u4ed6\u5077\u5077\u6478\u6478\u7684\u968f\u666e\u901a\u65c5\u5ba2\u4ece\u5176\u4ed6\u5b89\u68c0\u901a\u9053\u79bb\u5f00\u3002</p><p>\u3000\u3000\u968f\u5468\u6e38\u540c\u65f6\u4e58\u98de\u673a\u62b5\u8fbe\u5efa\u90ba\u56fd\u9645\u673a\u573a\u7684\uff0c\u8fd8\u6709\u6a61\u6811\u56ed\u5fae\u7535\u5b50\u6280\u672f\u57fa\u7840\u5b9e\u9a8c\u5ba4\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\u8c2d\u4e91\u677e\u592b\u5987\u4ee5\u4ed6\u4eec\u4ece\u65b0\u52a0\u5761\u5e26\u9886\u8fc7\u6765\u7684\u6280\u672f\u56e2\u961f\uff0c\u4ed6\u4eec\u4e5f\u662f\u8ddf\u7740\u5468\u6e38\u6084\u65e0\u58f0\u606f\u7684\u4e58\u4e0a\u6a61\u6811\u56ed\u6d3e\u51fa\u6765\u7684\u63a5\u673a\u6c7d\u8f66\u79bb\u5f00\u3002</p></article>"}}}]);
