(window.webpackJsonp=window.webpackJsonp||[]).push([[657],{680:function(p){p.exports={chapterTitle:null,title:"\u7b2c656\u7ae0 \u6625\u68a6\u65e0\u75d5",content:"<article><h2>\u7b2c656\u7ae0 \u6625\u68a6\u65e0\u75d5</h2><p>\u3000\u3000\u9648\u9759\u7a7f\u7740\u4e00\u4ef6\u6d45\u7eff\u8272\u534a\u8896\u886c\u886b\uff0c\u6d01\u767d\u6676\u83b9\u7684\u5c0f\u81c2\u6401\u5728\u8f66\u9f99\u5934\u4e0a\uff0c\u8eab\u5b50\u5fae\u5fae\u524d\u503e\uff0c\u79c0\u4e3d\u7684\u957f\u79c0\u633d\u6210\u53d1\u9afb\uff0c\u9732\u51fa\u5149\u6da6\u5982\u7389\u7684\u4fee\u957f\u9888\u8116\u5b50\u4e0e\u5706\u6da6\u7ea4\u7ec6\u7684\u4e00\u89d2\u6027\u611f\u9501\u9aa8\uff0c\u4ece\u6ca1\u6709\u8fd9\u4e48\u8fd1\u8ddd\u79bb\u7684\u770b\u9648\u9759\u7684\u80cc\u5f71\uff0c\u900f\u7740\u6210\u719f\u4e30\u8174\u7684\u9b45\u529b\uff0c\u9f3b\u7aef\u8426\u7ed5\u7740\u6de1\u6de1\u7684\u4f53\u9999\uff0c\u4e0d\u7ecf\u610f\u95f4\uff0c\u773c\u795e\u8fd8\u80fd\u6ed1\u8fdb\u53bb\u4e00\u4e9b\uff0c\u770b\u5230\u9648\u9759\u5185\u8863\u7684\u7ea2\u8272\u7ec6\u80a9\u5e26\u3002</p><p>\u3000\u3000\u9648\u9759\u7a7f\u7740\u9f50\u819d\u7684\u4e00\u6b65\u88d9\uff0c\u5750\u5230\u8f66\u524d\u6a2a\u6746\u4e0a\uff0c\u9f50\u819d\u88d9\u4f1a\u5f80\u4e0a\u7f29\u4e00\u4e9b\uff0c\u4eae\u4e1d\u7684\u8089\u8272\u4e1d\u889c\uff0c\u5c06\u5c0f\u817f\u6536\u675f\u5f97\u8ff7\u4eba\u7ea4\u5de7\u3002\u9a91\u8f66\u7740\u603b\u514d\u4e0d\u4e86\u817f\u819d\u6328\u5230\u4e00\u8d77\uff0c\u9648\u9759\u817f\u8981\u8d34\u7740\u8f66\u524d\u6a2a\u6746\uff0c\u80cc\u81c0\u5c31\u8981\u5f80\u540e\u62f1\uff0c\u5374\u53c8\u66f4\u8d34\u5230\u5f20\u606a\u7684\u6000\u91cc\uff0c\u8fd9\u65f6\u5019\u5374\u53c8\u540e\u6094\u521a\u624d\u600e\u4e48\u9b3c\u4f7f\u795e\u5dee\u7684\u5c31\u540c\u610f\u8ba9\u5f20\u606a\u8f7d\u5979\uff0c\u5c06\u81ea\u5df1\u903c\u5165\u4e00\u4e2a\u5c34\u5c2c\u7684\u5883\u5730\uff0c\u6700\u8ba9\u5979\u60f6\u7136\u7684\uff0c\u4f3c\u4e4e\u4e0d\u8ba8\u538c\u5728\u8fd9\u4e2a\u5c0f\u7537\u4eba\u6000\u91cc\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u65e0\u6cd5\u6297\u62d2\u7740\u8fd9\u8ff7\u79bb\u9189\u4eba\u7684\u6c14\u606f\uff0c\u603b\u4e0d\u80fd\u8fd9\u65f6\u5019\u63d0\u51fa\u6765\u8d70\u5230\u6821\u95e8\u53e3\uff0c\u62b5\u6321\u7740\u5185\u5fc3\u6df1\u5904\u201c\u8d34\u4e0a\u53bb\u9760\u4e00\u4f1a\u513f\u201d\u7684\u8bf1\u60d1\uff0c\u80cc\u810a\u7d27\u7ef7\u7740\u3002\u575a\u6301\u7740\uff0c\u5374\u603b\u6709\u575a\u6301\u4e0d\u4f4f\u7684\u4e00\u523b\uff0c\u7b97\u4e86\u5427\uff0c\u5c31\u9760\u4e00\u4f1a\u513f\u5427\uff0c\u9648\u9759\u7684\u610f\u5fd7\u9661\u7136\u677e\u61c8\u4e0b\u6765\uff0c\u8eab\u4f53\u8f6f\u7ef5\u7ef5\u7684\u8d34\u5411\u6e29\u70ed\u7684\u80f8\u53e3\uff0c\u5374\u5728\u8fd9\u4e00\u77ac\u95f4\u611f\u89c9\u8f66\u5b50\u9661\u7136\u4e00\u505c\uff0c\u9648\u9759\u5fc3\u91cc\u4e00\u60ca\uff0c\u8eab\u5b50\u6328\u7740\u5f20\u606a\u80f8\u53e3\u7684\u77ac\u95f4\uff0c\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u4f53\u5473\u90a3\u52a8\u4eba\u7684\u611f\u89c9\uff0c\u8eab\u5b50\u53c8\u9661\u7136\u7ef7\u7d27\uff0c\u7591\u60d1\u4e0d\u89e3\u7684\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u634f\u7740\u5239\u8f66\u505c\u4e0b\u6765\uff0c\u89c1\u9648\u9759\u8fd8\u8d56\u5728\u8f66\u6a2a\u6746\u4e0a\u4e0d\u4e0b\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u603b\u4e0d\u4f1a\u8981\u6211\u9a91\u8f66\u8f7d\u4f60\u53bb\u4e1c\u534e\u5bbe\u9986\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u9648\u9759\u6ee1\u8138\u7f9e\u7ea2\uff0c\u624d\u53d1\u89c9\u5df2\u7ecf\u5230\u4e1c\u5927\u6821\u95e8\u53e3\uff0c\u5fd9\u4e0d\u8fed\u9003\u4e5f\u4f3c\u7684\u4ece\u5f20\u606a\u7684\u6000\u91cc\u94bb\u51fa\u6765\uff0c\u60f6\u6025\u7684\u671d\u505c\u5728\u4e1c\u5927\u5357\u95e8\u5e7f\u573a\u4e0a\u7684\u7ea2\u8272\u5954\u9a70\u8d70\u8fc7\u53bb\uff0c\u4e5f\u6ca1\u6709\u80c6\u6c14\u56de\u5934\u4e0e\u5f20\u606a\u8bf4\u544a\u522b\u6216\u9053\u8c22\u7684\u8bdd\uff0c\u5c31\u6015\u4ed6\u6709\u770b\u7a7f\u81ea\u5df1\u6700\u540e\u90a3\u4e00\u77ac\u95f4\u7684\u5fc3\u9b42\u5931\u5b88\uff0c\u795e\u9b42\u98a0\u5012\u7684\u5f00\u7740\u8f66\u56de\u5230\u5bbe\u9986\uff0c\u8fdb\u623f\u95f4\u62c9\u7740\u88ab\u5b50\u8499\u5934\u5c31\u5c06\u81ea\u5df1\u7684\u5a07\u8f6f\u53d1\u70eb\u7684\u8eab\u4f53\u7f69\u8d77\u6765\uff0c\u4f3c\u4e4e\u8fd9\u6837\u5c31\u5c06\u81ea\u5df1\u7ed9\u5b8c\u5168\u63a9\u85cf\u8d77\u6765\u3002</p><p>\u3000\u3000\u8ff7\u79bb\u6df7\u4e71\u7684\u68a6\uff0c\u59cb\u7ec8\u662f\u90a3\u6e05\u6f88\u660e\u6d0c\u7684\u773c\u795e\u8986\u76d6\u7740\u81ea\u5df1\u7684\u96ea\u80a4\u7389\u6da6\u7684\u8d64\u88f8\u5a07\u8eaf\uff0c\u4eff\u4f5b\u5f20\u606a\u8f7d\u5979\u7684\u5355\u8f66\u6839\u672c\u5c31\u6ca1\u6709\u524d\u6a2a\u6746\uff0c\u6d51\u8eab\u98a4\u6817\u7740\u53d1\u70eb\uff0c\u4ece\u68a6\u4e2d\u9192\u6765\u65f6\uff0c\u4e0b\u4f53\u6f6e\u6da6\uff0c\u4f3c\u4e4e\u5185\u88e4\u90fd\u7ed9\u6d78\u900f\u4e86\uff0c\u9648\u9759\u4f9d\u7740\u5e8a\u5934\u800c\u5750\uff0c\u5fc3\u8df3\u5f97\u5389\u5bb3\uff0c\u51e0\u4e4e\u9759\u5bc2\u9ed1\u6697\u7684\u591c\u91cc\u80fd\u89c1\u81ea\u5df1\u7684\u5fc3\u8df3\u58f0\uff0c\u4e5f\u80fd\u6e05\u6670\u56de\u5473\u68a6\u4e2d\u8eab\u4f53\u7ed9\u5f00\u53d1\u7684\u8ff7\u4e71\u611f\u89c9\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0d\u6562\u9759\u4f9d\u5728\u5e8a\u5934\u56de\u5473\u6625\u68a6\u6f6e\u6d8c\u7684\u611f\u89c9\uff0c\u5c31\u6015\u81ea\u5df1\u4f1a\u6c89\u6ca6\u8fdb\u53bb\uff0c\u6253\u5f00\u706f\uff0c\u8ba9\u8c6a\u534e\u7684\u5367\u5ba4\u91cc\u5145\u6ee1\u660e\u4eae\u7684\u706f\u5149\uff0c\u5e0c\u671b\u501f\u7740\u660e\u4eae\u7684\u706f\u5149\u80fd\u5c06\u66a7\u6627\u4e0d\u660e\u7684\u6c14\u6c1b\u51b2\u6de1\u3002\u5c06\u8863\u670d\u8131\u5728\u5e8a\u5934\uff0c\u8d64\u8eab\u8d70\u8fdb\u6dcb\u6d74\u623f\uff0c\u770b\u7740\u660e\u4eae\u955c\u4e2d\u7684\u81ea\u5df1\u7684\u7f8e\u4e3d\u8eab\u4f53\uff0c\u8fd9\u5507\u3001\u8fd9\u989d\u3001\u8fd9\u9888\u3001\u8fd9\u4e73\u6beb\u65e0\u7455\u75b5\uff0c\u767d\u7699\u900f\u4e3d\u7684\u79c0\u4e3d\u8138\u5e9e\u3001\u7ea4\u624b\u76c8\u63e1\u7684\u8170\u3001\u4e30\u8174\u7684\u81c0\u4e0e\u817f\u4ee5\u53ca\u53cc\u817f\u95f4\u9ed1\u5149\u6cb9\u4eae\u7684\u4e09\u89d2\uff0c\u8fd9\u4e8c\u5341\u4e94\u5e74\u7684\u8eab\u4f53\u5df2\u7ecf\u5f7b\u5e95\u6210\u719f\u4e86\u3001\u4e30\u8174\u3001\u80a5\u6c83\u4e86\uff0c\u9648\u9759\u81ea\u5df1\u770b\u4e86\u90fd\u662f\u4e3a\u4e4b\u8ff7\u9189\uff0c\u8fd9\u65f6\u5019\u53ea\u80fd\u8f7b\u8f7b\u7684\u53f9\u4e00\u58f0\uff1a\u201c\u8981\u73cd\u60dc\u81ea\u5df1\u3002\u201d</p><p>\u3000\u3000\u51b2\u4e86\u6fa1\u51fa\u6765\uff0c\u624d\u5c06\u90a3\u6df7\u4e71\u8ff7\u79bb\u7684\u60c5\u601d\u6536\u62fe\u4e86\u5e72\u5e72\u51c0\u51c0\u4e0d\u518d\u6f0f\u4e00\u70b9\u75d5\u8ff9\uff0c\u88f9\u7740\u6d74\u888d\uff0c\u62ff\u4e86\u4e00\u676f\u77ff\u6cc9\u6c34\u5b89\u9759\u7684\u5750\u5728\u7a97\u524d\u2014\u2014\u5c06\u7a97\u5e18\u62c9\u5f00\uff0c\u770b\u7740\u7a97\u5916\u7684\u6708\u5149\uff0c\u7a97\u524d\u6708\u5149\u4e0b\u5a46\u5a11\u7684\u6811\u5f71\uff0c\u601d\u7eea\u8fd8\u662f\u50cf\u90a3\u6708\u5149\u91cc\u7684\u70df\u4e91\u5728\u98d8\u8361\u7740\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u624d\u60f3\u8d77\u5750\u5f20\u606a\u7684\u8f66\u65f6\u4ed6\u4f3c\u4e4e\u8ddf\u81ea\u5df1\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\u6765\u7740\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u8bdd\uff1f</p><p>\u3000\u3000\u662f\u8bf4\u9526\u6e56\u7684\u5b9a\u4ef7\u7b56\u7565\u53ea\u662f\u8981\u9650\u5236\u8d34\u724c\u624b\u673a\u524d\u671f\u7684\u8fc7\u5ea6\u53d1\u5c55\uff1f\u8981\u4e0d\u52a0\u9650\u5236\u52bf\u5fc5\u4f1a\u8bf1\u53d1\u6d77\u5916\u624b\u673a\u5382\u5546\u5c06\u7ec4\u914d\u4ef6\u751f\u4ea7\u57fa\u5730\u63d0\u524d\u5927\u89c4\u6a21\u7684\u8f6c\u79fb\u5230\u56fd\u5185\u6765\u4e0e\u9526\u6e56\u8d34\u8eab\u8089\u535a\uff0c\u9526\u6e56\u6b64\u65f6\u7684\u57fa\u7840\u8fd8\u6709\u4e9b\u5f31\uff0c\u8981\u60f3\u529e\u6cd5\u5c06\u8fd9\u4e00\u8fdb\u7a0b\u5ef6\u957f\u3002\u4ed6\u4eec\u7684\u9ad8\u5b9a\u4ef7\u7b56\u7565\u4e0d\u662f\u9488\u5bf9\u79d1\u738b\u9ad8\u79d1\u4e00\u5bb6\uff1f\u4ed6\u4e5f\u5e0c\u671b\u79d1\u738b\u9ad8\u79d1\u5c06\u5de5\u4f5c\u505a\u7ec6\u4e00\u4e9b\uff0c\u51cf\u5c11\u6295\u673a\u7684\u5fc3\u6001\uff0c\u6216\u8005\u524d\u671f\u7684\u6e14\u5229\u4f1a\u5c11\u8bb8\u591a\uff0c\u4f46\u662f\u8981\u60f3\u771f\u6b63\u6210\u957f\u8d77\u6765\uff0c\u8fd9\u4e00\u6b65\u8fd8\u662f\u8981\u8d70\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u8fd9\u4e48\u8bf4\u7684\uff1f\u6216\u8bb8\u8bf4\u7684\u8bdd\u8981\u5c11\u4e00\u4e9b\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u600e\u4e48\u53ef\u4ee5\u5c06\u8fd9\u4e9b\u5173\u952e\u7684\u8c08\u8bdd\u6f0f\u542c\u4e86\u5462\uff1f</p><p>\u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u4e9b\u4e8b\u60c5\u544a\u8bc9\u81ea\u5df1\uff1f\u4ed6\u603b\u662f\u4fe1\u4efb\u81ea\u5df1\u7684\uff0c\u9648\u9759\u4e5f\u6000\u7591\u5f20\u606a\u5230\u5e95\u6709\u6ca1\u6709\u8bf4\u8fc7\u8fd9\u4e9b\u8bdd\uff0c\u8fd8\u662f\u81ea\u5df1\u4ec5\u4ec5\u662f\u521a\u624d\u68a6\u4e2d\u7684\u81c6\u60f3\u3002</p><p>\u3000\u3000\u68a6\u3002</p><p>\u3000\u3000\u9648\u9759\u51b3\u5b9a\u5c06\u521a\u624d\u7684\u68a6\u5f7b\u5e95\u5fd8\u6389\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u770b\u7740\u9648\u9759\u7ea2\u8272\u7684\u5954\u9a70\u5728\u8def\u706f\u5149\u91cc\u53ea\u5269\u4e0b\u8ff7\u79bb\u7684\u7ea2\u8272\u5c3e\u5f71\uff0c\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u6536\u56de\u89c6\u7ebf\uff0c\u62d0\u8fc7\u8f66\u5934\uff0c\u9a91\u7740\u5355\u8f66\u6253\u7b97\u5f80\u56de\u8d70\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u771f\u662f\u72e0\u5fc3\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u56de\u5934\u770b\u89c1\u7fdf\u4e39\u9752\u6084\u7136\u5750\u5728\u6821\u95e8\u4e00\u4fa7\u5de7\u7b11\u5ae3\u7136\u7684\u671b\u7740\u8fd9\u8fb9\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u4f60\u600e\u4e48\u7ad9\u5728\u8fd9\u91cc\uff0c\u8ddf\u9b3c\u9b42\u4f3c\u7684\uff0c\u5927\u534a\u591c\u7684\uff0c\u4f60\u8fd9\u6837\u4f1a\u628a\u4eba\u5413\u51fa\u5fc3\u810f\u75c5\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u542c\u606a\u5c11\u8fd9\u4e48\u8bf4\uff0c\u8fd8\u771f\u8ba9\u4eba\u4f24\u5fc3\u5462\uff0c\u201d\u7fdf\u4e39\u9752\u70df\u884c\u5a9a\u89c6\u7684\u8d70\u8fc7\u6765\uff0c\u201c\u606a\u5c11\u79bb\u8fd9\u95e8\u8fd8\u6709\u5341\u591a\u7c73\u7684\u65f6\u5019\uff0c\u6211\u521a\u4ece\u8f66\u91cc\u4e0b\u6765\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u591f\u660e\u8273\u52a8\u4eba\u5462\uff0c\u53ef\u60dc\u7a7f\u4e86\u8fd9\u8eab\u7684\u76db\u88c5\u2026\u2026\u201d\u7fdf\u4e39\u9752\u53c8\u62ff\u7740\u5938\u5f20\u7684\u8bed\u8c03\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5c31\u54af\u54af\u7684\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8d76\u5de7\u5c06\u90a3\u4e2a\u5973\u4eba\u53ef\u601c\u4ed3\u60f6\u7684\u6a21\u6837\u770b\u5728\u773c\u91cc\uff0c\u606a\u5c11\u4e0d\u4f1a\u60f3\u7740\u6740\u4eba\u706d\u53e3\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u521a\u624d\u8fd8\u771f\u6ca1\u6709\u6ce8\u610f\u5230\u7fdf\u4e39\u9752\u7684\u51fa\u73b0\uff0c\u89c1\u5979\u7a7f\u7740\u4e00\u4ef6\u9ed1\u8272\u7684\u65e0\u8896\u4f4e\u80f8\u665a\u88c5\uff0c\u62ab\u7740\u4e00\u4ef6\u62ab\u80a9\u5fa1\u5bd2\uff0c\u4e0d\u8fc7\u80f8\u524d\u8fd8\u662f\u9732\u51fa\u96ea\u767d\u8000\u773c\u7684\u5c0f\u7247\u4e73\u808c\uff0c\u575a\u633a\u4e30\u6ee1\u7684\u4e73\u5cf0\u4e0e\u7cbe\u81f4\u660e\u8273\u7684\u5bb9\u989c\u76f8\u6620\u7740\u5b9e\u8ba9\u4eba\u770b\u4e86\u4f1a\u5fc3\u9b42\u4e71\u98a4\uff0c\u7b11\u7740\u95ee\u9053\uff0c\u201c\u8fd9\u4ed8\u6253\u626e\u5230\u54ea\u91cc\u53bb\u8ff7\u60d1\u3001\u52fe\u5f15\u7537\u4eba\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u76db\u4e16\u5e74\u534e\u5f00\u4e1a\uff0c\u76db\u8001\u677f\u63ea\u7740\u6211\u53bb\u6491\u573a\u9762\uff0c\u6211\u6709\u51e0\u4e2a\u80c6\u5b50\u6562\u62d2\u7edd\uff1f\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u54e6\uff01\u201d\u5f20\u606a\u60f3\u8d77\u6765\u4e86\uff0c\u4e16\u7eaa\u9526\u6e56\u5a31\u4e50\u9910\u996e\u96c6\u56e2\u65d7\u4e0b\u7b2c\u4e00\u5bb6\u91cf\u8d29\u5f0fKTV\u4eca\u5929\u6b63\u5f0f\u5f00\u4e1a\uff0c\u6d3b\u52a8\u8981\u4ece\u4e0b\u5348\u4e3e\u529e\u5230\u51cc\u6668\uff0c\u76db\u9752\u8ddf\u81ea\u5df1\u8bf4\u8fc7\u8fd9\u4e8b\uff0c\u504f\u5de7\u8fd9\u4e9b\u5929\u4e13\u6ce8\u5728\u6676\u5706\u5382\u9879\u76ee\uff0c\u5c06\u8fd9\u4e8b\u7ed9\u629b\u4e4b\u8111\u540e\u4e86\uff0c\u77e5\u9053\u675c\u98de\u8981\u62c9\u7740\u8499\u4e50\u53bb\u51d1\u70ed\u95f9\u7684\uff0c\u201c\u675c\u98de\u8ddf\u8499\u4e50\u5462\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8fd8\u5728\u90a3\u91cc\u73a9\u5462\uff0c\u6211\u5148\u56de\u6765\u4e86\uff0c\u771f\u662f\u4e0d\u5de7\u554a\uff0c\u8fd8\u4ee5\u4e3a\u606a\u5c11\u4f1a\u5750\u7740\u4eba\u5bb6\u7684\u8f66\u79bb\u5f00\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u4f60\u8bf4\u7684\u8fd9\u822c\u4e25\u91cd\uff1f\u201d\u5f20\u606a\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u8981\u662f\u9648\u9759\u8d70\u4e4b\u524d\u4ed3\u60f6\u7684\u5f02\u6837\u90fd\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u4ed6\u524d\u4e16\u4e5f\u5c31\u767d\u767d\u5728\u60c5\u573a\u6df7\u8ff9\u90a3\u4e48\u591a\u5e74\u4e86\uff0c\u8bf1\u60d1\u662f\u591f\u8bf1\u60d1\u4e86\uff0c\u5f20\u606a\u53ef\u4e0d\u6562\u518d\u968f\u4fbf\u5f80\u81ea\u5df1\u8eab\u4e0a\u625b\u4e00\u7b14\u60c5\u503a\u3002\u6ca1\u60f3\u5230\u8fd9\u4e00\u5e55\u7ed9\u7fdf\u4e39\u9752\u649e\u89c1\uff0c\u8fd9\u5973\u4eba\u773c\u775b\u8d3c\u6bd2\uff0c\u4ec0\u4e48\u4e8b\u60c5\u843d\u5165\u5979\u7684\u773c\u91cc\u8fd8\u8981\u60f3\u7792\u8fc7\u5979\u7684\u773c\u775b\uff0c\u90a3\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\uff0c\u62cd\u4e86\u62cd\u8f66\u6a2a\u6760\uff0c\u201c\u8981\u4e0d\u8ba9\u4f60\u4e5f\u4eab\u53d7\u4e00\u56de\uff1f\u9001\u4f60\u56de\u5bbe\u9986\u3002\u201d</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u606a\u5c11\u662f\u90a3\u79cd\u80fd\u5c06\u5973\u4eba\u7684\u5fc3\u513f\u3001\u9b42\u513f\u3001\u9aa8\u8089\u90fd\u541e\u566c\u8fdb\u53bb\u4e0d\u5410\u4e00\u70b9\u6e23\u5b50\u7684\u4eba\u4e86\uff0c\u6211\u53ef\u6ca1\u6709\u52c7\u6c14\u73a9\u706b\u81ea\u5a6a\uff0c\u8981\u662f\u54ea\u4e00\u5929\uff0c\u6211\u4e5f\u50cf\u521a\u624d\u90a3\u4e2a\u5973\u4eba\u90a3\u6837\u4ed3\u60f6\u9003\u8d70\uff0c\u90a3\u5c31\u592a\u53ef\u601c\u4e86\u3002\u201d\u7fdf\u4e39\u9752\u5a07\u7b11\u5a9a\u7b11\u7740\uff0c\u5f00\u7740\u73a9\u7b11\uff0c\u7709\u773c\u95f4\u4e5f\u6625\u8272\u4e71\u98de\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u62ff\u8fd9\u79cd\u719f\u5973\u6ca1\u6709\u62db\uff0c\u4e0d\u8fc7\u8ddf\u8fd9\u6837\u8273\u4e3d\u6027\u611f\u7684\u5973\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u5012\u662f\u597d\u4eab\u53d7\uff0c\u8bb0\u5f97\u5979\u4eecMBA\u6625\u5b63\u73ed\u7684\u5165\u5b66\u8003\u8bd5\u5e94\u8be5\u521a\u521a\u7ed3\u675f\uff0c\u95ee\u5979\u8003\u5f97\u600e\u4e48\u6837\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u606a\u5c11\u5c06\u6211\u8fd9\u4e2a\u53ef\u601c\u5973\u4eba\u90fd\u629b\u5230\u8111\u540e\u53bb\u4e86\uff0c\u5c31\u662f\u4e3a\u4e86\u4f60\u8fdf\u6765\u7684\u5173\u5207\uff0c\u5728\u4e1c\u5927\u7684\u8fd9\u5341\u591a\u5929\uff0c\u6211\u53ef\u662f\u536f\u8db3\u4e86\u52b2\u5728\u6e29\u4e66\u554a\uff0c\u611f\u89c9\u8fd8\u4e0d\u5dee\u5427\uff0c\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff0c\u201c\u5f88\u671f\u5f85\u660e\u5e74\u6625\u5929\u80fd\u4e0e\u606a\u5c11\u6210\u4e3a\u6821\u53cb\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6478\u4e86\u6478\u540e\u8111\u52fa\uff0c\u5fc3\u60f3\u773c\u4e0b\u5df2\u7ecf\u591f\u6dfb\u4e71\u4e86\uff0c\u4ed6\u5b81\u53ef\u7fdf\u4e39\u9752\u4e0d\u8981\u8fc7\u6765\uff0c\u4e4b\u524d\u76db\u9752\u8fd8\u5411\u4ed6\u5f81\u8be2\u610f\u89c1\u8bf4\u8981\u5c06\u7fdf\u4e39\u9752\u8c03\u5230\u5efa\u90ba\u6765\u8d1f\u8d23\u4e16\u7eaa\u9526\u6e56\u5728\u5efa\u90ba\u7684\u5a31\u4e50\u4ea7\u4e1a\u3002\u5f20\u606a\u5f88\u59d4\u5a49\u8bf4\u53ca\u65b0\u829c\u90a3\u8fb9\u7684\u4e8b\u60c5\u8fd8\u6ca1\u6709\u5b8c\u5168\u8d70\u4e0a\u8f68\u9053\uff0c\u4e5f\u627e\u4e0d\u5230\u5408\u9002\u7684\u4eba\u9876\u66ff\u7fdf\u4e39\u9752\u5728\u65b0\u829c\u7684\u4f4d\u5b50\u3002\u7684\u786e\u4e4b\u524d\u6709\u901a\u8fc7\u65b0\u829c\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u5411\u5e02\u91cc\u6350\u8d60\u4e00\u5343\u4e07\u5206\u522b\u8d5e\u52a9\u65c5\u6e38\u5b66\u6821\u529e\u5b66\u4e0e\u5efa\u7acb\u521d\u7ea7\u804c\u4e1a\u57f9\u8bad\u4f53\u7cfb\u7684\u4e8b\u60c5\uff0c\u9001\u7fdf\u4e39\u9752\u56de\u6821\u5185\u62db\u5f85\u5bbe\u9986\u7684\u8def\u4e0a\u8ba8\u8bba\u4e86\u4e00\u4e9b\u76f8\u5173\u7684\u4e8b\u60c5\uff0c\u5e0c\u671b\u7fdf\u4e39\u9752\u56de\u65b0\u829c\u540e\u4eb2\u81ea\u5c06\u8fd9\u4e24\u4ef6\u4e8b\u6293\u8d77\u6765\u3002\u7279\u522b\u662f\u521d\u7ea7\u804c\u4e1a\u57f9\u8bad\u4f53\u7cfb\uff0c\u4e00\u662f\u5e0c\u671b\u65b0\u829c\u90a3\u4e9b\u4ece\u521d\u9ad8\u4e2d\u5c31\u7f00\u5b66\u5916\u51fa\u6253\u5de5\u7684\u4eba\u80fd\u6709\u4e00\u4e2a\u597d\u7684\u804c\u4e1a\u8d77\u70b9\uff0c\u800c\u4e0d\u4ec5\u4ec5\u5355\u7eaf\u4f5c\u4e3a\u4f53\u529b\u52b3\u52a8\u8005\u7ed9\u538b\u5728\u793e\u4f1a\u7684\u6700\u4f4e\u5c42\u88ab\u5265\u524a\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u4e3a\u9526\u6e56\u5efa\u7acb\u81ea\u5df1\u7684\u804c\u4e1a\u57f9\u8bad\u4f53\u7cfb\u79ef\u7d2f\u4e00\u4e9b\u7ecf\u9a8c\uff0c\u4e5f\u53ef\u4ee5\u76f4\u63a5\u4ece\u65b0\u829c\u62db\u52df\u4e00\u4e9b\u7d20\u8d28\u4e0d\u9519\u7684\u5458\u5de5\u8fdb\u884c\u57f9\u517b\u3002\u4e0d\u8fc7\u5c31\u7fdf\u4e39\u9752\u7684\u6027\u606a\u4e0e\u80fd\u529b\uff0c\u4f3c\u4e4e\u8d1f\u8d23\u4e16\u7eaa\u9526\u6e56\u65d7\u4e0b\u7684\u5a31\u4e50\u3001\u9152\u5e97\u4ea7\u4e1a\u66f4\u9002\u5408\u4e9b\uff0c\u800c\u4e14\u5979\u672c\u4eba\u53c8\u8db3\u591f\u7684\u52aa\u529b\uff1b\u603b\u4e4b\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u662f\u4e16\u7eaa\u9526\u6e56\u5185\u90e8\u7684\u95ee\u9898\uff0c\u5f20\u606a\u51b3\u5b9a\u4e0d\u4f1a\u8fc7\u591a\u7684\u5e72\u6d89\u3002</p></article>"}}}]);
