(window.webpackJsonp=window.webpackJsonp||[]).push([[659],{682:function(p){p.exports={chapterTitle:null,title:"\u7b2c504\u7ae0 \u5c71\u6e56\u591c\u6708",content:"<article><h2>\u7b2c504\u7ae0 \u5c71\u6e56\u591c\u6708</h2><p>\u3000\u3000\u5168\u56fd\u6625\u79cb\u4e24\u5b63\u7cd6\u9152\u4f1a\u805a\u96c6\u6765\u81ea\u5168\u56fd\u5404\u5730\u7684\u6570\u4e07\u5bb6\u5ba2\u5546\uff0c\u662f\u98df\u54c1\u5236\u9020\u4f01\u4e1a\u8425\u9500\u6e20\u9053\u62db\u5546\u7684\u91cd\u8981\u6865\u5934\u5821\uff0c\u4f46\u5bf9\u5f62\u6210\u76f8\u5bf9\u72ec\u7acb\u4e0e\u5b8c\u5584\u9500\u552e\u6e20\u9053\u4f53\u7cfb\u7684\u4f01\u4e1a\u800c\u8a00\uff0c\u7cd6\u9152\u4f1a\u5c31\u6ca1\u6709\u592a\u5927\u7684\u4f5c\u7528\u3002</p><p>\u3000\u3000\u6574\u5408\u8fdb\u4e91\u6c60\u9152\u4e1a\u7684\u51e0\u5bb6\u9152\u5382\u4e4b\u524d\u4e00\u76f4\u7ecf\u8425\u4f4e\u6863\u767d\u9152\uff0c\u7701\u5185\u7684\u9500\u552e\u6e20\u9053\u4f53\u7cfb\u90fd\u8c08\u4e0d\u4e0a\u5065\u5168\uff0c\u5c42\u6b21\u4e5f\u4f4e\uff0c\u6574\u5408\u4e4b\u540e\u7684\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4e00\u76f4\u5728\u5f00\u53d1\u534e\u5317\u5730\u533a\u7684\u5e02\u573a\uff1b\u7cd6\u9152\u4f1a\u5bf9\u4e91\u6c60\u9152\u4e1a\u6700\u5927\u7684\u610f\u4e49\u4e5f\u5c31\u5728\u4e8e\u65b9\u4fbf\u5efa\u7acb\u5168\u56fd\u8303\u56f4\u7684\u9500\u552e\u6e20\u9053\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u4e91\u6c60\u9152\u4e1a\u501f\u52a9\u7cd6\u9152\u4ea4\u6613\u4f1a\u53d1\u529b\u505a\u4e00\u4e9b\u5927\u7684\u5e02\u573a\u52a8\u4f5c\uff0c\u8fd9\u662f\u53ef\u884c\u7684\uff0c\u4f46\u662f\u8981\u76ee\u6807\u6e05\u695a\u3001\u624b\u6bb5\u660e\u786e\uff0c\u6709\u9650\u7684\u8d44\u91d1\u8981\u7528\u5728\u5173\u952e\u5904\u3002</p><p>\u3000\u3000\u4e00\u70ae\u800c\u7ea2\u5f88\u5bb9\u6613\uff0c\u4e91\u6c60\u9152\u4e1a\u8981\u60f3\u771f\u6b63\u7684\u7acb\u8db3\u4e8e\u5e02\u573a\uff0c\u4f18\u8d28\u53ef\u9760\u7684\u4ea7\u54c1\u7ebf\u3001\u575a\u6301\u8d70\u6587\u5316\u9152\u54c1\u724c\u6269\u5f20\u7684\u8425\u9500\u8def\u7ebf\u3001\u5b8c\u5584\u7684\u9500\u552e\u6e20\u9053\u4f53\u7cfb\u90fd\u662f\u7f3a\u4e00\u4e0d\u53ef\u7684\uff0c\u5176\u4e2d\u4f18\u8d28\u53ef\u9760\u7684\u4ea7\u54c1\u7ebf\u6700\u4e3a\u5173\u952e\uff0c\u4e5f\u662f\u8425\u9500\u81f3\u4e0a\u7684\u4e5d\u5341\u5e74\u4ee3\u6700\u5bb9\u6613\u7ed9\u5ffd\u7565\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u9e45\u5375\u77f3\u780c\u6210\u7684\u6e29\u6cc9\u6d74\u6c60\u91cc\u8ba8\u8bba\u8fd9\u4e9b\u95ee\u9898\uff0c\u5916\u9762\u7684\u5929\u8272\u53d8\u5316\u4e5f\u611f\u89c9\u4e0d\u5230\uff0c\u53ea\u5230\u8981\u665a\u6674\u3001\u5510\u5a67\u5979\u4eec\u4ece\u5317\u5761\u7684\u96ea\u573a\u56de\u6765\uff0c\u5f20\u606a\u4ed6\u4eec\u624d\u8d70\u51fa\u6e29\u6cc9\u6d74\u6c60\uff0c\u4e00\u8d77\u5403\u665a\u9910\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u9910\uff0c\u5f20\u606a\u5012\u5077\u61d2\u4e0d\u80fd\u8c08\u4e8b\u60c5\u4e86\uff0c\u522b\u4eba\u8981\u53bb\u6ce1\u6e29\u6cc9\uff0c\u4ed6\u62c9\u7740\u5510\u5a67\u4ece\u5916\u9762\u8d70\u3002</p><p>\u3000\u3000\u5510\u5a67\u867d\u7136\u559c\u6b22\u5927\u5bb6\u70ed\u70ed\u95f9\u95f9\u7684\u51d1\u5728\u4e00\u8d77\uff0c\u4f46\u662f\u4e5f\u5e0c\u671b\u4e0e\u5f20\u606a\u6709\u72ec\u5904\u7684\u9759\u8c27\uff0c\u8981\u4eab\u53d7\u5728\u6708\u8272\u4e0b\u51dd\u89c6\u5f20\u606a\u6709\u7740\u9690\u9690\u6ca7\u6851\u7684\u6e05\u6f88\u773c\u77b3\u7684\u5e78\u798f\uff0c\u4e0d\u8fc7\u5728\u5f20\u77e5\u884c\u3001\u6881\u683c\u73cd\u9762\u524d\uff0c\u8fd9\u79cd\u60c5\u7eea\u8981\u6536\u655b\u8d77\u6765\uff0c\u5f20\u606a\u62c9\u7740\u5979\u51fa\u53bb\uff0c\u5979\u8fd8\u534a\u63a8\u534a\u5c31\u4e86\u4e00\u756a\uff0c\u88c5\u51fa\u767e\u822c\u65e0\u5948\u7684\u7ed9\u5f20\u606a\u5f3a\u62d6\u51fa\u53bb\u3002</p><p>\u3000\u3000\u591c\u91cc\uff0c\u6708\u8272\u5f88\u597d\uff0c\u5c71\u4e0a\u6d12\u6ee1\u70b9\u70b9\u7684\u661f\u5149\u4f59\u8f89\uff0c\u79bb\u5f00\u5bbe\u9986\uff0c\u4ece\u5c71\u8def\u5f80\u5317\u8d70\u6570\u767e\u7c73\u5c31\u662f\u5929\u5c71\u4e1c\u6700\u5927\u7684\u5c71\u6e56\u6e29\u6cc9\u6e56\uff0c\u8fd9\u91cc\u624d\u662f\u534a\u5c71\u8170\uff0c\u5bd2\u6d41\u90fd\u7ed9\u6321\u6210\u5c71\u810a\u4ee5\u5317\uff0c\u53c8\u662f\u6e29\u6cc9\u805a\u96c6\u5e26\uff0c\u53ea\u6709\u4e9b\u591c\u7684\u6e05\u5bd2\uff0c\u62ab\u7740\u5916\u5957\uff0c\u7a7f\u5f97\u4e25\u5b9e\uff0c\u5012\u4e0d\u89c9\u5f97\u51b7\u3002\u6708\u5149\u4e0b\uff0c\u6e56\u4e0a\u84b8\u817e\u7740\u6c24\u6c32\u7684\u6c34\u6c7d\uff0c\u5510\u5a67\u8fd8\u4ee5\u4e3a\u6e56\u6c34\u662f\u6e29\u7684\uff0c\u4f38\u624b\u6478\u4e86\u6478\uff0c\u4e00\u6837\u7684\u51b0\u51c9\u523a\u9aa8\uff1b\u5012\u662f\u6e56\u6c34\u91cc\u8361\u6f3e\u7740\u4e71\u788e\u661f\u5149\uff0c\u6643\u773c\u8ff7\u4eba\uff0c\u591c\u9759\u8c27\uff0c\u6709\u7740\u4e0d\u77e5\u540d\u7684\u58f0\u97f3\u5728\u5c71\u95f4\u4f20\u8361\uff0c\u50cf\u98ce\u5728\u5c71\u9699\u95f4\u6e38\u8d70\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u5730\u52bf\u72ed\u7a84\u4e00\u4e9b\uff0c\u6cbf\u7740\u6e56\u5cb8\u5f80\u5de6\u8d70\uff0c\u8fd8\u6709\u767e\u5341\u4eba\u5c45\u4f4f\u7684\u5c11\u6570\u6c11\u65cf\u6751\u843d\uff0c\u6751\u6c11\u591a\u4e3a\u4f9d\u9760\u6e29\u6cc9\u6e56\u751f\u5b58\u7684\u6e14\u6c11\u3002\u4e1c\u6d77\u7701\u5883\u5185\uff0c\u5c11\u6570\u6c11\u65cf\u5929\u7136\u5f62\u6210\u7684\u805a\u5c45\u6751\u843d\u6781\u4e3a\u7f55\u89c1\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\uff0c\u8981\u5f00\u53d1\u5929\u4e91\u5c71\u7684\u65c5\u6e38\uff0c\u6c11\u4fd7\u6c11\u65cf\u6587\u5316\u662f\u5f88\u91cd\u8981\u7684\u5143\u7d20\uff0c\u4e0d\u8fc7\u591a\u4f59\u7684\u6751\u5be8\u8981\u8fc1\u51fa\u5c71\u53bb\uff0c\u907f\u514d\u8fd9\u4e9b\u6751\u5be8\u9887\u4e3a\u539f\u59cb\u7684\u751f\u5b58\u65b9\u5f0f\u7834\u574f\u5929\u4e91\u5c71\u7684\u81ea\u7136\u3001\u751f\u6001\u8d44\u6e90\u3002</p><p>\u3000\u3000\u5510\u5a67\u590f\u5929\u65f6\u4e0e\u9648\u5983\u84c9\u5230\u8fc7\u6765\u8fd9\u91cc\uff0c\u5750\u8fc7\u6751\u6c11\u7684\u6e14\u821f\u5230\u6e56\u5fc3\u7684\u5c9b\u4e0a\u53bb\u73a9\uff0c\u90a3\u4e9b\u6e56\u5fc3\u5c9b\u91cc\u6709\u5ea7\u5c9b\u4e0a\u7adf\u4e5f\u6709\u4e00\u773c\u6e29\u6cc9\u6ea2\u51fa\u6765\uff0c\u8fd9\u65f6\u5019\u671b\u8fc7\u53bb\uff0c\u6e56\u5fc3\u7684\u5c0f\u5c9b\u53ea\u7559\u5728\u9ed1\u9ee2\u9ee2\u7684\u5f71\u5b50\u3002</p><p>\u3000\u3000\u5510\u5a67\u4f9d\u5728\u5f20\u606a\u7684\u8eab\u6000\u51ed\u4ed6\u6402\u7740\u81ea\u5df1\uff0c\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u6211\u8981\u80fd\u4e70\u4e0b\u4e00\u5ea7\u5c9b\u5efa\u9020\u4e00\u5ea7\u6728\u5c4b\u5c31\u597d\u4e86\uff0c\u62ff\u7af9\u5b50\u5efa\u4e5f\u65e0\u6240\u8c13\uff0c\u5c31\u5efa\u5728\u6c34\u8fb9\uff0c\u9ec4\u660f\u65f6\u5206\u63a8\u5f00\u95e8\u5750\u5728\u4e34\u6c34\u7684\u9732\u53f0\u4e0a\u770b\u4e66\uff0c\u90a3\u611f\u89c9\u4e00\u5b9a\u5f88\u68d2\u5427\uff1f\u6708\u591c\u4e5f\u4e0d\u5dee\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u95ed\u76ee\u60f3\u8c61\u9ec4\u660f\u6e56\u8fb9\u8bfb\u4e66\u7684\u60c5\u5f62\uff0c\u201c\u55ef\uff0c\u90a3\u4e00\u5b9a\u662f\u975e\u5e38\u96c5\u8da3\u7684\u751f\u6d3b\uff0c\u5176\u5b9e\u4e0d\u7528\u8bfb\u4ec0\u4e48\u4e66\uff0c\u6ce1\u4e00\u6000\u5496\u5561\uff0c\u5750\u5728\u5e06\u5e03\u6905\u5b50\u4e0a\uff0c\u770b\u7740\u5915\u9633\u4e0e\u6e56\u6c34\u3001\u5c71\u810a\u7684\u53d8\u5316\uff0c\u5c31\u662f\u597d\u4eab\u53d7\u2026\u2026\u201d\u770b\u7740\u5510\u5a67\u957f\u5a9a\u7684\u6c34\u79c0\u773c\u7738\uff0c\u201c\u5230\u65f6\u5019\u4f60\u613f\u4e0d\u613f\u8db4\u5728\u6211\u7684\u6000\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u624d\u4e0d\u8981\uff0c\u201d\u5510\u5a67\u5a07\u61a8\u7684\u8bf4\uff0c\u201c\u8981\u6709\u8fd9\u4e48\u4e00\u5ea7\u6728\u5c4b\uff0c\u6211\u5148\u8ddf\u5983\u84c9\u7ea6\u597d\u4e86\uff0c\u4f60\u5df2\u7ecf\u665a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u75db\u5931\u826f\u673a\u7684\u6367\u8d77\u5510\u5a67\u5a07\u5a9a\u7684\u8138\u86cb\uff0c\u4eb2\u543b\u8d77\u5979\u7c89\u5ae9\u7684\u67d4\u5507\uff0c\u542e\u5438\u4e01\u9999\u820c\u5c16\uff0c\u8ba9\u5510\u5a67\u7684\u8eab\u5b50\u5728\u81ea\u5df1\u7684\u6000\u91cc\u53d1\u8f6f\uff0c\u5728\u5979\u8033\u6735\u8bf4\uff1a\u201c\u8fd9\u6837\u7684\u5c0f\u6728\u5c4b\uff0c\u53ea\u6709\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u4e0d\u89c9\u5f97\u5bc2\u5bde\u5417\uff1f\u4e07\u4e00\u5c71\u4e0a\u4e0b\u4e00\u573a\u5927\u96e8\uff0c\u6e56\u6c34\u6da8\u8d77\u6765\uff0c\u5c31\u4f60\u4eec\u4e24\u4e2a\u5973\u5b69\u5b50\u5728\u5c9b\u4e0a\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u96be\u9053\u6ca1\u542c\u8bf4\u8fc7\u6e29\u6cc9\u6e56\u96e8\u5b63\u4e0d\u6da8\u3001\u67af\u6c34\u4e0d\u964d\u5417\uff1f\u201d\u5510\u5a67\u8eab\u5b50\u540e\u4ef0\uff0c\u5fae\u5fae\u6b47\u7740\u6c14\uff0c\u8111\u5b50\u7ed9\u5f20\u606a\u543b\u5f97\u90fd\u6709\u4e9b\u8ff7\u6655\uff0c\u6273\u7740\u6307\u5934\u7f13\u7f13\u9053\uff0c\u201c\u518d\u8bf4\u79bb\u5f00\u4f60\u4eec\uff0c\u5973\u5b69\u5b50\u4e00\u6837\u6d3b\u5f97\u5f88\u7cbe\u5f69\uff0c\u5076\u5c14\u60f3\u60f3\u4f60\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u5076\u5c14\u554a\uff1f\u771f\u662f\u8ba9\u4eba\u5934\u75bc\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u624b\u6258\u7740\u5510\u5a67\u7684\u8111\u888b\uff0c\u95fb\u7740\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u6e21\u5507\u8fc7\u53bb\u64ac\u5f00\u5979\u7684\u7259\u5173\u54c1\u5c1d\u9999\u6d25\u5ae9\u820c\u3002\u5728\u6e56\u8fb9\u6e29\u5b58\u4e86\u8bb8\u4e45\uff0c\u542c\u89c1\u4eba\u58f0\u4f20\u6765\uff0c\u539f\u6765\u662f\u675c\u98de\u3001\u76db\u590f\u3001\u9648\u5983\u84c9\u6253\u5b8c\u724c\u51fa\u6765\u627e\u4ed6\u4eec\uff0c\u5230\u6e56\u8fb9\u6709\u4e00\u6bb5\u7ed9\u704c\u6728\u4e1b\u88f9\u4f4f\u7684\u8def\uff0c\u5927\u6982\u4e00\u8def\u8d70\u8fc7\u6765\uff0c\u675c\u98de\u53c8\u8bb2\u8d77\u5c71\u91cc\u6709\u72fc\u7684\u6545\u4e8b\uff0c\u5f20\u73ab\u4e0d\u6562\u8d70\uff0c\u53c8\u6015\u675c\u98de\u4ed6\u4eec\u5c06\u5979\u7ed9\u4e22\u4e0b\uff0c\u8e72\u5728\u5916\u9762\u5927\u56b7\u5927\u53eb\u3002</p><p>\u3000\u3000\u5f20\u606a\u51fa\u58f0\u8ba9\u5f20\u73ab\u8ddf\u7740\u8d70\u8fc7\u6765\uff0c\u76db\u590f\u8ba9\u675c\u98de\u7275\u7740\u624b\uff0c\u9648\u5983\u84c9\u62c9\u7740\u5f20\u73ab\uff0c\u8270\u96be\u7684\u8d70\u8fc7\u6765\uff0c\u60f3\u5fc5\u8fd9\u4e9b\u4eba\u5f53\u4e2d\uff0c\u76db\u590f\u662f\u6700\u4e0d\u9700\u8981\u88ab\u4fdd\u62a4\u7684\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u771f\u6f02\u4eae\uff01\u201d\u76db\u590f\u770b\u7740\u6e56\u6c34\u91cc\u7684\u7e41\u661f\u5149\u6591\uff0c\u5fcd\u4e0d\u4f4f\u8f7b\u547c\u4e00\u58f0\uff0c\u62b1\u6028\u8d77\u5f20\u606a\uff0c\u201c\u65e9\u77e5\u9053\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u591c\u666f\u53ef\u770b\uff0c\u53ea\u62d6\u7740\u5510\u5a67\u8fc7\u6765\uff0c\u5374\u628a\u6211\u4eec\u90fd\u4e22\u4e00\u8fb9\u4e86\u3002\u201d</p><p>\u3000\u3000\u76db\u590f\u4e0e\u9648\u5983\u84c9\u8fc7\u6765\uff0c\u5510\u5a67\u603b\u7b97\u627e\u5230\u53ef\u4ee5\u503e\u8ff0\u5979\u6e56\u5fc3\u6728\u5c4b\u613f\u666f\u7684\u5bf9\u8c61\uff0c\u675c\u98de\u4e0d\u5c51\u7684\u8bf4\uff1a\u201c\u8fd9\u91cc\u7684\u5730\u8c8c\u4e0e\u6c34\u57df\u3001\u5c71\u4f53\u76f8\u878d\uff0c\u73af\u5883\u91ce\u8da3\u800c\u5e7d\u50fb\uff0c\u4e0e\u65b0\u829c\u5e02\u533a\u76f8\u63a5\u53c8\u8fd1\uff0c\u6700\u9002\u5408\u5f00\u53d1\u6e21\u5047\u6751\u5f0f\u7684\u65c5\u6e38\u623f\u4ea7\uff0c\u4e00\u5ea7\u5c0f\u5c9b\u53ea\u7ed9\u4f60\u4eec\u5efa\u4e00\u5ea7\u6728\u5c4b\uff0c\u592a\u5962\u4f88\u4e86\u2026\u2026\u201d\u53c8\u611f\u6168\u9053\uff0c\u201c\u5973\u4eba\u5962\u4f88\u8d77\u6765\u771f\u75af\u72c2\uff0c\u96be\u602a\u90fd\u5728\u8bf4\uff0c\u5973\u4eba\u7684\u94b1\u597d\u6323\uff0c\u5973\u4eba\u624d\u638c\u63e1\u4ee5\u540e\u6d88\u8d39\u7ecf\u6d4e\u7684\u4e3b\u6d41\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u60f3\u60f3\u90fd\u4e0d\u884c\u554a\uff1f\u201d\u76db\u590f\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u5b66\u8c01\u4e0d\u597d\uff0c\u504f\u5b66\u5f20\u606a\u4e00\u8eab\u94dc\u81ed\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u62c9\u7740\u675c\u98de\u8bf4\uff1a\u201c\u6211\u4eec\u5f80\u8fb9\u4e0a\u7ad9\u7ad9\uff0c\u4e0d\u60f9\u5979\u4eec\u3002\u201d\u53c8\u95ee\u5f20\u73ab\uff0c\u201c\u5c71\u91cc\u771f\u6709\u72fc\uff0c\u8fd8\u6709\u91ce\u732a\uff0c\u4f60\u662f\u8ddf\u6211\u4eec\u5728\u4e00\u8d77\uff0c\u8fd8\u662f\u5979\u4eec\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u3000\u3000\u5f20\u73ab\u5fc3\u865a\u5f97\u5f88\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u5f80\u76db\u590f\u8eab\u8fb9\u9760\u4e86\u9760\uff1a\u201c\u542c\u8bf4\u675c\u98de\u7ed9\u76db\u590f\u59d0\u6253\u8fc7\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4e0b\u4e0d\u7528\u5f20\u606a\u62c9\uff0c\u675c\u98de\u81ea\u5df1\u81ca\u7740\u8138\u8eb2\u4e00\u8fb9\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e24\u5e74\u591a\u6765\uff0c\u675c\u98de\u8fd8\u771f\u7ed9\u5f20\u606a\u4ed6\u4eec\u718f\u5f97\u4e00\u8eab\u94dc\u81ed\uff0c\u770b\u95ee\u9898\u7684\u89c6\u91ce\u4e0e\u89d2\u5ea6\u65e9\u5c31\u8131\u79bb\u540c\u65f6\u671f\u7684\u9ad8\u4e2d\u751f\u7684\u5c40\u9650\uff0c\u6210\u719f\u591a\u4e86\u3002</p><p>\u3000\u3000\u76db\u9752\u3001\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u8fd8\u6709\u5f20\u606a\u4ed6\u5c0f\u53d4\u5f20\u77e5\u975e\u51e0\u4e2a\u4eba\u7684\u4ea7\u4e1a\u4ece\u5f00\u59cb\u505a\u76d7\u7248\u789f\u4e4b\u65f6\u8d77\u5c31\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u518d\u5230\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u96c6\u56e2\uff0c\u518d\u5230\u73b0\u5728\u521a\u6ce8\u518c\u7684\u4e16\u7eaa\u9526\u6e56\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\uff0c\u5df2\u7ecf\u9010\u6e10\u878d\u5408\u5230\u4e00\u8d77\u3002\u8fd9\u4e9b\u4e8b\u52a1\uff0c\u675c\u98de\u90fd\u6709\u4e00\u5b9a\u7a0b\u5ea6\u7684\u53c2\u4e0e\uff0c\u79ef\u7d2f\u4e86\u76f8\u5f53\u591a\u539f\u672c\u662f\u4ed6\u8fd9\u4e2a\u5e74\u7eaa\u63a5\u89e6\u4e0d\u5230\u7684\u7ecf\u9a8c\uff0c\u5f53\u7136\uff0c\u675c\u98de\u8fd8\u8981\u5728\u674e\u829d\u82b3\u9762\u524d\u4fdd\u6301\u4e00\u4e2a\u597d\u5b66\u751f\u7684\u6a21\u6837\uff0c\u4e5f\u6b8a\u4e3a\u4e0d\u6613\u3002</p><p>\u3000\u3000\u9ad8\u4e2d\u751f\u6daf\u5c31\u5269\u4e0b\u6700\u540e\u4e00\u4e2a\u5b66\u671f\uff0c\u675c\u98de\u8fd8\u6709\u4e9b\u72b9\u8c6b\uff0c\u4ed6\u5c06\u5f20\u606a\u62c9\u5230\u4e00\u8fb9\uff0c\u95ee\u4ed6\uff1a\u201c\u4e0b\u4e2a\u5b66\u671f\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u8fc7\uff0c\u8fd8\u8ddf\u73b0\u5728\u4e00\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u4f60\u7684\uff0c\u7ba1\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u8ddf\u675c\u98de\u5728\u4e00\u8d77\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u88c5\u59ff\u6001\uff0c\u62c9\u7740\u675c\u98de\u8e72\u4e0b\u6765\u8bf4\u8bdd\uff1a\u521a\u624d\u8ddf\u5510\u5a67\u5728\u4e00\u8d77\uff0c\u8ba9\u5979\u9760\u5728\u81ea\u5df1\u6000\u91cc\uff0c\u7ad9\u4e45\u4e86\uff0c\u8fd8\u6709\u4e9b\u7d2f\u3002\u522b\u8fc7\u8eab\u5b50\u4e0d\u8ba9\u5510\u5a67\u90a3\u8fb9\u770b\u89c1\uff0c\u638f\u51fa\u70df\u6765\u5206\u7ed9\u675c\u98de\u4e00\u652f\u70b9\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6211\u8ddf\u6211\u5bb6\u8001\u5934\u5b50\u5546\u91cf\uff0c\u4ed6\u552c\u7740\u8138\u8bf4\u4e0d\u4f1a\u4e3a\u6211\u4e0a\u5927\u5b66\u7684\u4e8b\u60c5\u64cd\u5fc3\uff0c\u6211\u7231\u4e0a\u54ea\u4e0a\u54ea\uff0c\u80fd\u4e0a\u54ea\u4e0a\u54ea\uff0c\u5c31\u7b97\u4e0a\u6d77\u5dde\u5927\u5b66\uff0c\u4ed6\u90fd\u6ca1\u6709\u610f\u89c1\u3002\u6211\u5988\u8bf4\u5979\u4eec\u5b66\u6821\u67d0\u67d0\u5b69\u5b50\u8003\u4e0a\u67d0\u67d0\u5927\u5b66\u5982\u4f55\u5982\u4f55\uff0c\u4e0d\u8bb8\u6211\u4e22\u5979\u7684\u8138\uff0c\u5c11\u8bf4\u4e5f\u5f97\u7ed9\u5979\u8003\u4e0a\u4e1c\u5927\uff0c\u5176\u5b9e\u8003\u4e1c\u5927\u8fd8\u662f\u6709\u4e00\u70b9\u96be\u5ea6\u7684\uff0c\u5c31\u60f3\u8ddf\u4f60\u5408\u8ba1\u5408\u8ba1\uff0c\u201d\u675c\u98de\u8e72\u4e0b\u6765\u8bf4\uff0c\u201c\u6211\u770b\u6211\u4e0b\u5b66\u671f\u8981\u8ba4\u771f\u8bfb\u4e66\u4e86\uff0c\u4e0d\u7136\u8fd8\u771f\u591f\u545b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u5c31\u8ba4\u771f\u5b66\u4e60\u554a\uff0c\u6709\u674e\u829d\u82b3\u3001\u5468\u6653\u7490\u6bcd\u5973\u4fe9\uff0c\u6211\u770b\u4f60\u6df7\u5728\u5b66\u6821\u91cc\u4e5f\u5f88\u6ecb\u6da6\u554a\u3002\u201d\u5f20\u606a\u563f\u7136\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u522b\u80e1\u8bf4\uff01\u201d\u675c\u98de\u81ea\u5df1\u5374\u5fc3\u865a\u7684\u5f80\u56de\u770b\u4e86\u4e00\u773c\uff0c\u542c\u89c1\u76db\u590f\u3001\u5510\u5a67\u3001\u9648\u5983\u84c9\u5979\u4eec\u7684\u7ec6\u788e\u7b11\u8bed\u58f0\u4f9d\u65e7\uff0c\u201c\u8bf4\u6b63\u7ecf\u7684\uff0c\u5012\u4e5f\u4e0d\u8bf4\u662f\u8bfb\u4e66\u6ca1\u7528\uff0c\u5173\u952e\u89c9\u5f97\u82b1\u8fd9\u4e48\u5927\u7684\u529b\u6c14\u53bb\u8bfb\u90a3\u4e9b\u4e66\uff0c\u8fd8\u4e0d\u5982\u6263\u51fa\u4e9b\u65f6\u95f4\u5b66\u70b9\u5176\u4ed6\u7684\u4e1c\u897f\uff0c\u4f60\u7ed9\u6211\u7684\u4e66\uff0c\u6211\u90fd\u6709\u5728\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u77a7\u4f60\u90a3\u70b9\u51fa\u606f\uff0c\u5c31\u4e3a\u4e86\u4e0a\u4e00\u4e2a\u4e1c\u6d77\u5927\u5b66\uff0c\u9700\u8981\u5c06\u4f60\u8001\u5b50\u642c\u51fa\u6765\uff1f\u96be\u602a\u4f60\u8001\u5b50\u77a7\u4e0d\u8d77\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u4e1c\u6d77\u5927\u5b66\uff0c\u5176\u5b9e\u6211\u5bb6\u8001\u5934\u5b50\u51fa\u9a6c\uff0c\u4e5f\u633a\u8d39\u52b2\u7684\uff0c\u5c31\u7b97\u641e\u4e2a\u81ea\u8d39\u751f\uff0c\u6d88\u606f\u8fd8\u662f\u4f1a\u7ed9\u4f20\u51fa\u6765\uff0c\u6211\u5988\u4ec0\u4e48\u90fd\u597d\uff0c\u5c31\u662f\u6bd4\u8f83\u7231\u9762\u5b50\uff0c\u4f60\u77e5\u9053\uff0c\u6211\u5bb6\u662f\u6211\u5988\u505a\u4e3b\uff0c\u4f60\u8981\u80fd\u5e2e\u6211\u4eec\u4fe9\u641e\u4e24\u4e2a\u7279\u62db\u540d\u989d\uff0c\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u5c31\u90fd\u89e3\u51b3\u4e86\uff0c\u90fd\u9ad8\u8003\u90fd\u514d\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c0f\u5b50\u601d\u8def\u5f88\u6e05\u6670\u554a\uff0c\u53ea\u662f\u2026\u2026\u201d\u5f20\u606a\u60f3\u8d77\u5468\u747e\u73ba\uff0c\u8fd9\u8001\u5bb6\u4f19\u5728\u4e1c\u6d77\u5927\u5b66\u7684\u5f71\u54cd\u529b\u4e0d\u53ef\u4f4e\u4f30\uff0c\u53c8\u8ddf\u82cf\u9038\u7fa4\u641e\u5728\u4e00\u8d77\uff0c\u60f3\u5fc5\u4e5f\u6500\u4e0a\u674e\u8fdc\u6e56\u7684\u5173\u7cfb\u4e86\uff0c\u8fd9\u65f6\u5019\u518d\u641e\u4e1c\u6d77\u5927\u5b66\u4e24\u4e2a\u7279\u62db\u540d\u989d\uff0c\u53ef\u80fd\u6709\u4e9b\u96be\u5ea6\uff0c\u5f20\u606a\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u54e6\uff0c\u5230\u65f6\u5019\u4e5f\u4e0d\u4f1a\u6ca1\u6709\u529e\u6cd5\uff0c\u7684\u786e\u4e0d\u9700\u8981\u6d6a\u8d39\u592a\u591a\u7684\u65f6\u95f4\uff0c\u4f60\u8fd8\u662f\u6309\u7167\u4f60\u539f\u6765\u7684\u8ba1\u5212\u53bb\u5b89\u6392\u5427\u3002\u201d</p></article>"}}}]);
