(window.webpackJsonp=window.webpackJsonp||[]).push([[66],{88:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u53ea\u5dee\u6700\u540e\u4e00\u6b65",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u53ea\u5dee\u6700\u540e\u4e00\u6b65</h2><p>\u201c\u5982\u4f55\uff0c\u201d\u5979\u95ee\uff0c\u201c\u662f\u4e0d\u662f\u8fd9\u6837\u4f1a\u8ba9\u4f60\u611f\u89c9\u597d\u4e00\u4e9b\u3002\u201d</p><p>\u7267\u5e08\u6ca1\u6709\u56de\u7b54\uff0c\u4f46\u4ed6\u8138\u4e0a\u7684\u60ca\u60e7\u795e\u8272\u786e\u5b9e\u6709\u6240\u7f13\u548c\u3002\u4eba\u7c7b\u662f\u5f88\u5bb9\u6613\u88ab\u5916\u8868\u8499\u853d\u7684\u751f\u7269\uff0c\u5373\u4f7f\u660e\u77e5\u773c\u524d\u7684\u7f8e\u4eba\u5176\u5b9e\u662f\u4e2a\u534a\u4eba\u534a\u86c7\u7684\u602a\u7269\uff0c\u4f46\u65e2\u7136\u53d8\u5f62\u4e86\uff0c\u770b\u8d77\u6765\u4f9d\u65e7\u8fd8\u662f\u4f1a\u5fc3\u7406\u611f\u89c9\u597d\u5f88\u591a\u3002</p><p>\u201c\u770b\u8d77\u6765\uff0c\u6211\u4eec\u73b0\u5728\u53ef\u4ee5\u8c08\u8c08\u4e86\uff0c\u201d\u8482\u5a1c\u5c91\u8bf4\uff0c\u5979\u6b3e\u6b3e\u8d70\u8fd1\uff0c\u8d34\u7740\u7267\u5e08\u7684\u9762\u7ad9\u7acb\uff0c\u73af\u81c2\u6402\u4f4f\u4ed6\u7684\u8116\u5b50\uff0c\u5c06\u9999\u5507\u51d1\u5728\u4ed6\u7684\u8033\u8fb9\uff0c\u201c\u5176\u5b9e\u4ea4\u6613\u5f88\u7b80\u5355\uff0c\u201d\u5979\u8f7b\u58f0\u683c\u683c\u7b11\u7740\uff0c\u543b\u7740\u7267\u5e08\u7684\u8138\u988a\uff0c\u201c\u6211\u60f3\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u62d2\u7edd\u3002\u201d</p><p>\u5979\u4eb2\u543b\u7740\u7267\u5e08\u7684\u8138\uff0c\u8116\u9888\uff0c\u8f7b\u8f7b\u89e3\u5f00\u539f\u672c\u5df2\u7ecf\u7834\u70c2\u7684\u767d\u888d\uff0c\u4e22\u5230\u4e00\u8fb9\uff0c\u7136\u540e\u53bb\u89e3\u886c\u886b\u7684\u7ebd\u6263\u3002\u7267\u5e08\u65e2\u5bb3\u6015\u53c8\u7d27\u5f20\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6b63\u5982\u8482\u5a1c\u5c91\u6240\u8bf4\uff0c\u4ed6\u786e\u5b9e\u8fd8\u4e0d\u66fe\u6709\u8fc7\u548c\u5973\u4eba\u4e0a\u5e8a\u7684\u7ecf\u5386\u3002\u4ed6\u60f3\u6323\u624e\uff0c\u4f46\u8eab\u4f53\u65e0\u6cd5\u79fb\u52a8\uff0c\u201c\u4f60\u6253\u7b97\u505a\u4ec0\u4e48\u2026\u2026\u201d\u4ed6\u5bb3\u6015\u5730\u5598\u606f\u7740\uff0c\u770b\u7740\u81ea\u5df1\u7684\u886c\u886b\u7ebd\u6263\u88ab\u4e00\u4e00\u9897\u9897\u89e3\u5f00\uff0c\u201c\u505a\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d</p><p>\u8482\u5a1c\u5c91\u629a\u6478\u7740\u7267\u5e08\u7684\u80f8\u819b\uff0c\u5979\u7684\u624b\u6307\u7ea4\u7ec6\u4fee\u957f\uff0c\u76ae\u80a4\u96ea\u767d\uff0c\u5374\u9690\u9690\u6cdb\u7740\u5371\u9669\u7684\u94f6\u5149\uff0c\u201c\u4f60\u4e0d\u4f1a\u62d2\u7edd\u7684\u2026\u2026\u201d\u5979\u5a9a\u58f0\u8bf4\uff0c\u201c\u56e0\u4e3a\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u7528\u505a\uff0c\u53ea\u9700\u8981\u4eab\u53d7\u2026\u2026\u201d</p><p>\u5979\u7684\u58f0\u97f3\u53d8\u5f97\u67d4\u5a9a\u5a49\u8f6c\uff0c\u7565\u5e26\u7740\u8f7b\u5fae\u7684\u5598\u606f\uff0c\u4eff\u4f5b\u60c5\u4eba\u95f4\u70ed\u70c8\u7684\u4f4e\u541f\u3002\u968f\u7740\u8bdd\u8bed\uff0c\u5979\u5df2\u7ecf\u5c06\u7267\u5e08\u7684\u886c\u886b\u5b8c\u5168\u89e3\u5f00\uff0c\u9732\u51fa\u8d64\u88f8\u7684\u4e0a\u8eab\u3002\u201c\u4f60\u5f88\u5f3a\u58ee\u5462\u3002\u201d\u8482\u5a1c\u5c91\u8bc4\u4ef7\u8bf4\uff0c\u53cc\u81c2\u4f9d\u65e7\u6402\u7740\u7267\u5e08\uff0c\u987a\u7740\u4ed6\u7684\u80cc\u7f13\u7f13\u4e0b\u79fb\uff0c\u56f4\u5728\u8170\u95f4\uff0c\u7136\u540e\u540e\u9000\u534a\u6b65\uff0c\u6162\u6162\u8e72\u4e0b\u8eab\uff0c\u89e3\u5f00\u7267\u5e08\u7684\u8170\u5e26\uff0c\u8ba9\u4ed6\u7684\u957f\u88e4\u5782\u843d\u5230\u811a\u9762\u3002</p><p>\u6709\u6eda\u70eb\u7684\u4e1c\u897f\u5f39\u8df3\u51fa\u6765\uff0c\u6b63\u6253\u5728\u8482\u5a1c\u5c91\u7684\u8138\u4e0a\uff0c\u201c\u5b83\u5f88\u4e0d\u8001\u5b9e\u5462\u3002\u201d\u5973\u4eba\u9762\u72ee\u8bc4\u4ef7\u7740\uff0c\u5f20\u53e3\u5c06\u5b83\u542b\u4e86\u8fdb\u53bb\u3002\u5979\u7684\u6280\u5de7\u975e\u5e38\u9ad8\u660e\uff0c\u8fdc\u8fdc\u4e0d\u662f\u683c\u5170\u7279\u8fd9\u79cd\u83dc\u9e1f\u80fd\u591f\u6297\u62d2\u7684\uff0c\u5f88\u5feb\uff0c\u5e74\u8f7b\u7684\u7267\u5e08\u5df2\u7ecf\u53ea\u5269\u4e0b\u547c\u54e7\u547c\u54e7\u5598\u606f\u7684\u80fd\u529b\u4e86\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002</p><p>\u8482\u5a1c\u5c91\u611f\u89c9\u5230\u7267\u5e08\u7684\u81c0\u90e8\u808c\u8089\u6536\u7f29\uff0c\u53cc\u817f\u5e76\u7d27\uff0c\u5979\u77e5\u9053\u8fd9\u662f\u8981\u55b7\u53d1\u7684\u524d\u5146\uff0c\u800c\u5979\u5f53\u7136\u4e0d\u80fd\u5bb9\u8bb8\u5982\u6b64\uff0c\u796d\u7940\u4eea\u5f0f\u8fd8\u6ca1\u6709\u5b8c\u6210\u3002</p><p>\u5979\u5c06\u90a3\u4e2a\u6eda\u70eb\u7684\u4e1c\u897f\u5410\u51fa\u6765\uff0c\u76f4\u7acb\u8d77\u8eab\uff0c\u548c\u7267\u5e08\u9762\u5bf9\u9762\u7ad9\u7740\uff0c\u8eab\u4f53\u7d27\u8d34\u3002\u5979\u4f38\u624b\u5230\u7267\u5e08\u80ef\u4e0b\uff0c\u63e1\u4f4f\uff0c\u7136\u540e\u5fae\u5fae\u5206\u5f00\u53cc\u817f\uff0c\u8eab\u4f53\u66f4\u52a0\u8d34\u8fd1\uff0c\u5f15\u5bfc\u7740\u7267\u5e08\u8fdb\u5165\u5979\u7684\u8eab\u4f53\u3002</p><p>\u7267\u5e08\u611f\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u9677\u5165\u4e00\u56e2\u6e29\u6696\u6f6e\u6e7f\u7684\u5305\u88f9\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u6709\u65e0\u5f62\u7684\u5438\u529b\u5c06\u4ed6\u626f\u5165\u3002\u683c\u5170\u7279\u5e74\u8f7b\u7684\u751f\u547d\u4e2d\u4ece\u672a\u6709\u8fc7\u5982\u6b64\u7f8e\u5999\u7684\u7ecf\u9a8c\uff0c\u4ed6\u672c\u80fd\u5730\u60f3\u633a\u8d77\u8170\u6765\u56de\u62bd\u63d2\uff0c\u4f46\u53d1\u73b0\u81ea\u5df1\u7684\u8eab\u4f53\u4e0d\u80fd\u52a8\u5f39\u3002</p><p>\u7267\u5e08\u7528\u6073\u6c42\u7684\u773c\u795e\u770b\u7740\u8482\u5a1c\u5c91\uff0c\u540e\u8005\u5a9a\u7b11\u7740\uff0c\u5148\u626f\u5f00\u5e26\u5b50\uff0c\u5c06\u81ea\u5df1\u8eab\u4e0a\u7684\u957f\u888d\u5b8c\u5168\u892a\u4e0b\uff0c\u9732\u51fa\u4e00\u5177\u6d01\u767d\u65e0\u6687\u7684\u5973\u4f53\uff0c\u7136\u540e\u89e3\u5f00\u4e86\u5b9a\u8eab\u9b54\u6cd5\u3002\u683c\u5170\u7279\u6012\u543c\u4e00\u58f0\uff0c\u5c06\u5973\u4eba\u9762\u72ee\u9996\u9886\u63a8\u5012\u5728\u5730\uff0c\u6251\u4e86\u4e0a\u53bb\u3002</p><p>\u6240\u6709\u53f0\u4e0b\u7684\u4eba\u9762\u72ee\u90fd\u5174\u81f4\u52c3\u52c3\u5730\u89c2\u8d4f\u7740\uff0c\u6885\u83f2\u65af\u4e5f\u5728\u5176\u4e2d\uff0c\u5979\u5f88\u5e78\u8fd0\u5730\u8fd1\u8ddd\u79bb\u6b23\u8d4f\u5230\u4e86\u4e00\u573a\u9887\u5177\u7f8e\u611f\u7684\u6027\u7231\u2014\u2014\u5bf9\u6bd4\u8d77\u6b64\u524d\u7684\u4e24\u573a\uff0c\u8fd9\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u9999\u8273\u6deb\u7cdc\uff0c\u7f8e\u8f6e\u7f8e\u5942\u4e86\u3002\u5979\u5fae\u5fae\u6709\u4e9b\u8138\u7ea2\uff0c\u611f\u89c9\u5fc3\u8df3\u52a0\u5feb\uff0c\u4f46\u540c\u65f6\uff0c\u5979\u4e5f\u5e76\u6ca1\u6709\u4e27\u5931\u8b66\u60d5\u3002\u5411\u683c\u62c9\u5179\u7279\u732e\u796d\u7684\u4eea\u5f0f\uff0c\u51b3\u4e0d\u4f1a\u4ec5\u4ec5\u662f\u4e00\u573a\u6027\u7231\u8fd9\u4e48\u7b80\u5355\uff0c\u8482\u5a1c\u5c91\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\uff1f</p><p>\u5979\u5c06\u76ee\u5149\u4ece\u796d\u575b\u4e2d\u5fc3\u6b63\u5728\u6fc0\u70c8\u7f20\u7ef5\u7684\u4e24\u5177\u8089\u4f53\u4e0a\u79fb\u5f00\uff0c\u53d1\u89c9\u516d\u4f4d\u5973\u796d\u53f8\u5e76\u6ca1\u6709\u50cf\u5176\u4ed6\u4eba\u4e00\u6837\u5173\u6ce8\u7740\u9996\u9886\u3002\u5979\u4eec\u76d8\u65cb\u7740\u4e0b\u8eab\uff0c\u7528\u86c7\u5f62\u94f6\u5251\u6df1\u6df1\u5272\u5f00\u81ea\u5df1\u7684\u5de6\u624b\u624b\u8155\uff0c\u78a7\u7eff\u8272\u7684\u9c9c\u8840\u6d41\u4e0b\u6765\uff0c\u843d\u5728\u5730\u4e0a\u3002</p><p>\u5979\u4eec\u53e3\u4e2d\u4f4e\u58f0\u5ff5\u8bf5\u7740\u542b\u7cca\u800c\u5197\u957f\u7684\u5492\u8bed\uff0c\u9c9c\u8840\u8d8a\u6dcc\u8d8a\u591a\uff0c\u5728\u5730\u4e0a\u5f62\u6210\u4e00\u5927\u6ee9\u3002\u5728\u6bcf\u4e2a\u5973\u796d\u53f8\u7684\u9762\u524d\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u5706\u5f62\u7684\u9ed1\u6d1e\uff0c\u521d\u65f6\u5f88\u5c0f\uff0c\u6e10\u6e10\u6269\u5927\uff0c\u6700\u7ec8\u6e10\u6e10\u7a33\u5b9a\u4e0b\u6765\u3002</p><p>\u5730\u4e0a\u7684\u9c9c\u8840\u5316\u4f5c\u78a7\u7eff\u8272\u706b\u7130\uff0c\u817e\u817e\u71c3\u70e7\uff0c\u5c06\u516d\u4f4d\u5973\u796d\u53f8\u5b8c\u5168\u5305\u88f9\u3002\u5f53\u706b\u7130\u6563\u53bb\u540e\uff0c\u5979\u4eec\u7684\u5f62\u4f53\u5df2\u7ecf\u53d1\u751f\u4e86\u6539\u53d8\uff0c</p><p>\u4f9d\u65e7\u662f\u534a\u4eba\u534a\u86c7\u7684\u8eab\u4f53\uff0c\u4f46\u5374\u4f3c\u4e4e\u53d8\u5927\u4e86\u4e00\u500d\uff0c\u800c\u4e14\u957f\u51fa\u4e86\u516d\u53ea\u624b\u81c2\u3002\u539f\u672c\u63e1\u6301\u7684\u6cd5\u6756\u5df2\u7ecf\u6d88\u5931\uff0c\u6362\u6210\u4e86\u516d\u628a\u5947\u5f62\u602a\u72b6\u7684\u5175\u5668\uff0c\u6bcf\u4e2a\u624b\u8155\u4e0a\u90fd\u5957\u7740\u4e03\u516b\u4e2a\u91d1\u9ec4\u8272\u7684\u624b\u956f\u548c\u94f6\u8272\u81c2\u73af\uff0c\u4e92\u76f8\u649e\u51fb\uff0c\u53ee\u5f53\u4f5c\u54cd\u3002\u5979\u4eec\u7684\u4e0b\u534a\u8eab\u539f\u672c\u662f\u7eaf\u6b63\u7684\u78a7\u7eff\u8272\uff0c\u73b0\u5728\u5219\u9690\u9690\u5e26\u7740\u6697\u94f6\uff0c\u6cdb\u7740\u5fae\u84dd\u7684\u5149\u8292\u3002</p><p>\u6885\u83f2\u65af\u76b1\u7740\u7709\u5934\uff0c\u5979\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u2014\u2014\u5982\u679c\u743c\u6069\u5728\u8fd9\u91cc\uff0c\u4e00\u5b9a\u4f1a\u88ab\u5413\u4e00\u8df3\u3002\u8fd9\u662f\u516d\u81c2\u86c7\u9b54\uff0c\u5728\u6df1\u6e0a\u6076\u9b54\u7684\u4f4d\u9636\u4e2d\u4ec5\u6b21\u4e8e\u5df4\u6d1b\u708e\u9b54\u3002</p><p>\u6df1\u6e0a\u4e2d\u6709\u65e0\u6570\u6076\u9b54\uff0c\u5927\u4f53\u5212\u5206\u4e3a\u5341\u4e2a\u4f4d\u9636\uff0c\u6700\u4f4e\u9636\u7684\u662f\u602f\u9b54\uff0c\u6700\u9ad8\u9636\u7684\u81ea\u7136\u4fbf\u662f\u5df4\u6d1b\u708e\u9b54\u3002\u4f4e\u9636\u6076\u9b54\u4e00\u822c\u90fd\u4e0d\u7a7f\u8863\u670d\uff0c\u8d64\u8eab\u88f8\u4f53\uff0c\u9ad8\u9636\u6076\u9b54\u867d\u7136\u5927\u591a\u7a7f\u8863\u7532\uff0c\u4f46\u5929\u6027\u6df7\u4e71\u7684\u5b83\u4eec\u4e5f\u666e\u904d\u4e0d\u613f\u610f\u4f69\u5e26\u4ec0\u4e48\u8c61\u5f81\u4f4d\u9636\u7684\u5fbd\u7ae0\u6807\u5fd7\u4e4b\u7c7b\u3002\u4e0d\u8fc7\uff0c\u8981\u5224\u65ad\u4e00\u4e2a\u6076\u9b54\u7684\u4f4d\u9636\u9ad8\u4f4e\uff0c\u5176\u5b9e\u538b\u6839\u4e5f\u65e0\u9700\u770b\u4ec0\u4e48\u5fbd\u7ae0\uff0c\u76f4\u63a5\u770b\u5b83\u7684\u5f62\u6001\u4fbf\u53ef\u4ee5\u3002</p><p>\u7edd\u5927\u591a\u6570\u2014\u2014\u5e76\u975e\u5168\u90e8\uff0c\u6709\u6781\u5c11\u6570\u4f8b\u5916\u2014\u2014\u6076\u9b54\uff0c\u90fd\u662f\u65e0\u6027\u7e41\u6b96\u7684\u3002\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u8fdb\u5316\u800c\u6765\u7684\u3002\u7269\u8d28\u754c\u751f\u547d\u7684\u7075\u9b42\uff0c\u82e5\u662f\u88ab\u6076\u9b54\u5f15\u8bf1\u6216\u8005\u63a0\u593a\u8fdb\u6df1\u6e0a\u4e2d\uff0c\u53d7\u5230\u4f4d\u9762\u7684\u540c\u5316\uff0c\u4fbf\u4f1a\u53d8\u6210\u6076\u9b54\u3002\u81f3\u4e8e\u53d8\u6210\u4ec0\u4e48\u4f4d\u9636\u7684\u6076\u9b54\uff0c\u5219\u662f\u4f9d\u636e\u529b\u91cf\u5927\u5c0f\u800c\u51b3\u5b9a\u7684\u3002</p><p>\u540c\u65f6\uff0c\u4f4e\u9636\u6076\u9b54\u5982\u679c\u80fd\u5728\u65e0\u6570\u6b21\u6218\u6597\u53ae\u6740\u4e2d\u5e78\u5b58\u4e0b\u6765\u2014\u2014\u8fd9\u5f88\u96be\uff0c\u4f46\u5e76\u975e\u7edd\u4e0d\u53ef\u80fd\uff0c\u589e\u5f3a\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u90a3\u4e48\u5b83\u5c31\u6709\u53ef\u80fd\u5347\u5165\u66f4\u9ad8\u7684\u4f4d\u9636\u3002\u5347\u9636\u662f\u65e0\u9700\u4ec0\u4e48\u8bc1\u4e66\u6216\u8005\u5609\u5956\u4eea\u5f0f\u6765\u8bc1\u660e\u7684\uff0c\u5b83\u7684\u8eab\u4f53\u5f62\u6001\u5c31\u4f1a\u53d1\u751f\u5de8\u5927\u7684\u53d8\u5316\u3002</p><p>\u5343\u4e07\u53ea\u602f\u9b54\u4e2d\uff0c\u6709\u53ef\u80fd\u4f1a\u6709\u4e00\u53ea\u8db3\u591f\u5f3a\u5927\u5e76\u4e14\u5e78\u8fd0\u7684\uff0c\u5347\u9636\u5230\u5938\u8d5b\u9b54\uff1b\u540c\u6837\u7684\u9053\u7406\uff0c\u5938\u8d5b\u9b54\u4e5f\u6709\u53ef\u80fd\u5347\u9636\u4e3a\u5df4\u5e03\u9b54\uff0c\u4f9d\u6b21\u5f80\u4e0a\u3002\u6700\u9ad8\u4f4d\u9636\u7684\u6076\u9b54\u4fbf\u662f\u5df4\u6d1b\u708e\u9b54\uff0c\u800c\u516d\u81c2\u86c7\u9b54\u5df2\u7ecf\u662f\u4ec5\u6b21\u4e8e\u708e\u9b54\u7684\u6076\u9b54\uff0c\u5728\u6574\u4e2a\u6076\u9b54\u4f4d\u9636\u4e2d\u5c45\u7b2c\u4e8c\u4f4d\u3002</p><p>\u8fd9\u5df2\u7ecf\u662f\u975e\u5e38\u975e\u5e38\u5f3a\u5927\u7684\u6076\u9b54\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u516d\u540d\u5973\u4eba\u9762\u72ee\u796d\u53f8\u88ab\u5947\u5f02\u7684\u529b\u91cf\u8f6c\u5316\u6210\u4e86\u516d\u53ea\u516d\u81c2\u86c7\u9b54\uff0c\u5979\u4eec\u626c\u8d77\u624b\u81c2\uff0c\u624b\u956f\u548c\u81c2\u73af\u649e\u51fb\u7740\uff0c\u53d1\u51fa\u6444\u4eba\u9b42\u9b44\u7684\u9b54\u97f3\u3002\u5728\u5979\u4eec\u9762\u524d\uff0c\u9ed1\u8272\u7684\u5706\u5f62\u7a7a\u95f4\u4e4b\u95e8\u5df2\u7ecf\u7a33\u5b9a\uff0c\u90a3\u662f\u524d\u5f80\u94f6\u8272\u738b\u5bab\u2014\u2014\u683c\u62c9\u5179\u7279\u5728\u65e0\u5c3d\u6df1\u6e0a\u4e2d\u7684\u5c45\u6240\u2014\u2014\u7684\u901a\u9053\u3002</p><p>\u65b0\u751f\u7684\u516d\u81c2\u86c7\u9b54\u4eec\u873f\u8712\u7740\uff0c\u6e38\u5165\u901a\u9053\uff0c\u77ac\u95f4\u6d88\u5931\u4e0d\u89c1\u3002\u5979\u4eec\u5df2\u7ecf\u524d\u5f80\u683c\u62c9\u5179\u7279\u7684\u56fd\u5ea6\uff0c\u5c06\u4f1a\u5728\u4e4c\u9eef\u541b\u4e3b\u548c\u5965\u5580\u65af\u7684\u8840\u6218\u4e2d\u51b2\u950b\u9677\u9635\uff0c\u4e3a\u683c\u62c9\u5179\u7279\u633d\u56de\u8d25\u5c40\u2014\u2014\u5f53\u7136\u4ec5\u51ed\u5979\u4eec\u8fd8\u4e0d\u591f\u3002\u9ed1\u8272\u901a\u9053\u75be\u901f\u584c\u9677\uff0c\u6d88\u5931\uff0c\u4eff\u4f5b\u4ece\u6765\u4e0d\u66fe\u5b58\u5728\u8fc7\u4e00\u822c\u3002</p><p>\u796d\u575b\u4e2d\u5fc3\uff0c\u6b63\u5728\u548c\u683c\u5170\u7279\u75af\u72c2\u4ea4\u5abe\u7684\u8482\u5a1c\u5c91\u6b63\u51b7\u9759\u5730\u89c2\u5bdf\u7740\u8fd9\u4e00\u5207\u3002\u5f53\u770b\u5230\u516d\u540d\u5973\u796d\u53f8\u8e0f\u5165\u6df1\u6e0a\uff0c\u5979\u4f38\u51fa\u53cc\u624b\uff0c\u4e00\u8fb9\u4efb\u7531\u7267\u5e08\u5728\u5979\u8eab\u4f53\u91cc\u51b2\u649e\u9a70\u9a8b\uff0c\u4e00\u8fb9\u6084\u6084\u6bd4\u5212\u4e86\u4e00\u4e2a\u590d\u6742\u7684\u624b\u52bf\uff0c\u4f4e\u58f0\u541f\u8bf5\u51fa\u4e00\u6bb5\u5492\u6587\u3002</p><p>\u567c\u91cc\u556a\u5566\u7684\uff0c\u4eff\u4f5b\u4e0b\u96e8\u4e00\u822c\uff0c\u6570\u4ee5\u5343\u8ba1\u7684\u5c0f\u52a8\u7269\u7a81\u7136\u4ece\u5929\u7a7a\u4e2d\u6389\u843d\uff0c\u6709\u9752\u86d9\u3001\u6709\u86c7\u3001\u6709\u4e4c\u9f9f\uff0c\u751a\u81f3\u6709\u5154\u5b50\uff0c\u5b83\u4eec\u4ece\u9ad8\u7a7a\u6454\u843d\uff0c\u5927\u90e8\u5206\u76f4\u63a5\u6b7b\u4e8e\u975e\u547d\uff0c\u5c0f\u90e8\u5206\u6ca1\u6709\u6454\u6b7b\uff0c\u5374\u5728\u63a5\u4e0b\u6765\u7684\u4e09\u79d2\u949f\u5185\u63a5\u8fde\u53d1\u751f\u7206\u88c2\uff0c\u8eab\u4f53\u81ea\u5185\u5411\u5916\u70b8\u5f00\uff0c\u9aa8\u8089\u6a2a\u98de\uff0c\u9c9c\u8840\u56db\u6e85\uff0c\u5c06\u6574\u5ea7\u796d\u575b\u5f04\u6210\u6c61\u79fd\u4e00\u7247\u3002</p><p>\u4eea\u5f0f\u7684\u7b2c\u4e94\u6b65\u201c\u6b7b\u96e8\u201d\uff0c\u5df2\u7ecf\u5b8c\u6210\u3002</p></article>"}}}]);
