(window.webpackJsonp=window.webpackJsonp||[]).push([[66],{89:function(p){p.exports={chapterTitle:null,title:"\u7b2c1054\u7ae0 \u877c\u8681\u671b\u5927\u6811",content:"<article><h2>\u7b2c1054\u7ae0 \u877c\u8681\u671b\u5927\u6811</h2><p>\u3000\u3000\u201c\u556a\uff01\u201d\u803f\u91cd\u9633\u624b\u62cd\u7740\u6a80\u7ea2\u8272\u7684\u529e\u516c\u684c\u7ad9\u8d77\u6765\uff0c\u77aa\u5927\u773c\u775b\u76ef\u7740\u845b\u5efa\u5fb7\uff0c\u60f3\u53d1\u4e00\u901a\u706b\uff0c\u8fd8\u662f\u514b\u5236\u4e0b\u6765\uff0c\u53ea\u662f\u538b\u7740\u55d3\u5b50\u8bf4\u9053\uff0c\u201c\u90e8\u91cc\u5bf9\u534e\u590f\u7535\u5b50\u5df2\u7ecf\u591f\u652f\u6301\u4e86\uff0c\u51fa\u53f0\u7684\u76f8\u5173\u653f\u7b56\uff0c\u90fd\u662f\u504f\u5411\u4f60\u4eec\u8fd9\u4e9b\u56fd\u5bb6\u961f\u7684\uff0c\u4f60\u4eec\u8fd8\u60f3\u8981\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u8138\u8272\u8baa\u8baa\u7684\uff0c\u6ca1\u6709\u542d\u58f0\uff0c\u4ed6\u770b\u4e0d\u900f\u803f\u91cd\u9633\u662f\u771f\u6012\u8fd8\u662f\u4f6f\u6012\u3002</p><p>\u3000\u3000\u201c\u4e0a\u9762\u5df2\u7ecf\u51b3\u5b9a\u534e\u590f\u7535\u5b50\u5212\u7ed9\u7ecf\u8d38\u59d4\u53bb\u7ba1\uff0c\u6211\u4ee5\u540e\u4e5f\u7701\u5fc3\u4e86\uff0c\u201d\u803f\u91cd\u9633\u82e6\u53e3\u5a46\u5fc3\u7684\u8bf4\u9053\uff0c\u201c\u80fd\u8ddf\u4f60\u8fd9\u6837\u8bf4\u8bdd\u7684\u673a\u4f1a\u4e5f\u4e0d\u591a\u4e86\uff0c\u6211\u8fd8\u662f\u544a\u8bc9\u4f60\u4e00\u53e5\uff1a\u6211\u5e0c\u671b\u4f60\u4eec\u56fd\u5bb6\u961f\u80fd\u52c7\u4e89\u4e0a\u6e38\uff0c\u4e0d\u5e0c\u671b\u770b\u5230\u4f60\u4eec\u6210\u5929\u5728\u90a3\u91cc\u6bd4\u70c2\uff01\u201d</p><p>\u3000\u3000\u51ed\u845b\u5efa\u5fb7\u8138\u76ae\u518d\u539a\uff0c\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u8138\u4e0a\u8fd8\u662f\u706b\u8fa3\u8fa3\u7684\u53d1\u70eb\uff0c\u803f\u91cd\u9633\u813e\u6c14\u786c\u5728\u56fd\u52a1\u9662\u6709\u540d\u7684\uff0c\u4e89\u8bae\u4ea7\u4e1a\u653f\u7b56\u65f6\uff0c\u90fd\u6562\u8ddf\u8d75\u6d4e\u4e1c\u603b\u7406\u5435\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u845b\u5efa\u5fb7\u8fd8\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u4e00\u4e2a\u526f\u90e8\u7ea7\u3001\u73b0\u5728\u8fd8\u7ed9\u803f\u91cd\u9633\u7ba1\u7740\uff0c\u7ed9\u523a\u4e24\u53e5\u6709\u4ec0\u4e48\u8fd8\u53e3\u7684\u4f59\u5730\uff0c\u53ea\u60f3\u7740\u7b49\u534e\u590f\u7535\u5b50\u4ece\u4fe1\u4ea7\u90e8\u5212\u51fa\u6765\u4e4b\u540e\uff0c\u5c31\u4e0d\u4f1a\u53d7\u8fd9\u79cd\u9e1f\u6c14\u4e86\u3002</p><p>\u3000\u3000\u201c\u5373\u4f7f\u9526\u6e56\u7684\u8d44\u91d1\u6765\u6e90\u771f\u6709\u53ef\u7591\u7684\u5730\u65b9\uff0c\u4e5f\u4e0d\u8be5\u662f\u534e\u590f\u7535\u5b50\uff0c\u4e5f\u4e0d\u8be5\u662f\u4fe1\u4ea7\u90e8\u6765\u6345\u51fa\u6765\u3002\u522b\u4eba\u4f1a\u600e\u4e48\u60f3\uff1f\u4f1a\u8bf4\u534e\u590f\u7535\u5b50\u65e0\u80fd\uff0c\u4fe1\u4ea7\u90e8\u65e0\u80fd\uff0c\u53ea\u4f1a\u5728\u80cc\u540e\u6345\u5200\u5b50\u2014\u2014\u603b\u7406\u4eec\u90a3\u91cc\u4f1a\u6709\u4ec0\u4e48\u5224\u65ad\uff0c\u4f60\u7528\u4e9b\u8111\u5b50\u60f3\u60f3\u5427\u3002\u201d\u803f\u91cd\u9633\u8bf4\u5b8c\u8bdd\uff0c\u6325\u4e86\u6325\u624b\u8ba9\u845b\u5efa\u5fb7\u51fa\u53bb\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5fc3\u91cc\u5bf9\u803f\u91cd\u9633\u4e5f\u6068\uff0c\u8c01\u7ed9\u522b\u4eba\u5f53\u5b59\u5b50\u4e00\u6837\u8bad\u4e86\u5fc3\u91cc\u90fd\u4f1a\u6068\u5f97\u75d2\u75d2\u7684\u3002</p><p>\u3000\u3000\u4ed6\u8d70\u51fa\u803f\u91cd\u9633\u7684\u529e\u516c\u5ba4\uff0c\u6c14\u606f\u90fd\u6709\u4e9b\u5598\u4e0d\u5e73\uff0c\u5bf9\u803f\u91cd\u9633\u7684\u786c\u813e\u6c14\u5374\u53c8\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u79bb\u5f00\u90e8\u91cc\u4e58\u8f66\u8fd4\u56de\u534e\u590f\u7535\u5b50\u5728\u6d77\u6dc0\u4e2d\u8def\u7684\u603b\u90e8\u3002</p><p>\u3000\u3000\u8def\u8fc7\u7231\u8fbe\u5927\u53a6\uff0c\u770b\u7740\u5927\u697c\u524d\u90a3\u5929\u84dd\u8272\u5236\u6210\u7684\u73bb\u7483\u94a2\u5e06\u8239\u5f62\u697c\u6807\uff0c\u845b\u5efa\u5fb7\u5fc3\u5934\u5c31\u538b\u7740\u4e00\u9053\u9634\u4e91\u2014\u2014\u8fd9\u8fd1\u4e8c\u5341\u7c73\u9ad8\u7684\u72ec\u7acb\u697c\u6807\u7adf\u7136\u6210\u4e86\u6d77\u6dc0\u4e2d\u8def\u7684\u6807\u5fd7\u4e86\uff0c\u771f\u641e\u4e0d\u660e\u767d\u9526\u6e56\u4e3a\u4ec0\u4e48\u51b3\u5b9a\u5728\u697c\u524d\u7ad6\u8fd9\u4e48\u4e2a\u4e1c\u897f\u3002</p><p>\u3000\u3000\u624b\u673a\u54cd\u8d77\u6765\uff0c\u845b\u5efa\u5fb7\u89c1\u662f\u6797\u96ea\u6253\u6765\u7535\u8bdd\uff0c\u4fbf\u63a5\u901a\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4e2d\u5348\u8981\u5bb4\u8bf7\u6210\u664b\u6770\u4e3b\u4efb\uff0c\u4f60\u6709\u7a7a\u966a\u540c\u4e00\u4e0b\uff1f\u201d\u6797\u96ea\u53d1\u55f2\u7684\u58f0\u97f3\u4ece\u7535\u8bdd\u91cc\u4f20\u6765\uff0c\u5f88\u96be\u60f3\u8c61\u8fd9\u4e48\u4e2a\u5973\u4eba\u5bf9\u5f85\u522b\u4eba\u662f\u5982\u4f55\u7684\u72e0\u51b3\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5728\u56fd\u5bb6\u8ba1\u59d4\u65f6\uff0c\u4e0e\u6210\u664b\u6770\u90fd\u662f\u526f\u4e3b\u4efb\uff0c\u7b97\u662f\u9762\u548c\u5fc3\u4e0d\u548c\u7684\u8001\u5bf9\u624b\u3002\u845b\u5efa\u5fb7\u4ece\u8ba1\u59d4\u8df3\u51fa\u6765\u638c\u8235\u534e\u590f\u7535\u5b50\uff0c\u4e0e\u6210\u664b\u6770\u5c31\u7b97\u6709\u4e9b\u65e7\u6028\uff0c\u4f46\u81f3\u5c11\u8fd8\u6ca1\u6709\u6495\u7834\u8138\u76ae\uff0c\u6240\u4ee5\u89c1\u9762\u5403\u996d\u8fd8\u662f\u76f8\u5f53\u548c\u8c10\u7684\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u8fd9\u6b21\u80fd\u501f\u6c5f\u6e90\u5236\u836f\u7684\u58f3\u4e0a\u5e02\uff0c\u4e5f\u662f\u6797\u96ea\u5728\u4ed6\u7684\u6307\u70b9\u4e0b\u6a0a\u4e0a\u6210\u664b\u6770\u7684\u5173\u7cfb\uff0c\u6210\u664b\u6770\u4e0e\u4e25\u5bb6\u7684\u5173\u7cfb\u4e5f\u76f8\u5f53\u5bc6\u5207\u3002</p><p>\u3000\u3000\u8bf4\u5230\u4e25\u5bb6\uff0c\u6b64\u65f6\u7684\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u5218\u95fb\u6d9b\u5c31\u662f\u4e25\u5bb6\u786c\u6253\u8fdb\u6d77\u5dde\u7684\u9489\u5b50\uff0c\u4e25\u6587\u4ecb\u4e0e\u6797\u96ea\u80fd\u8f83\u4e3a\u6e05\u695a\u7684\u638c\u63e1\u9526\u6e56\u7684\u8d22\u52a1\u3001\u770b\u5230\u9526\u6e56\u8d22\u52a1\u4e2d\u7684\u6f0f\u6d1e\uff0c\u6ca1\u6709\u5218\u95fb\u6d9b\u7684\u5e2e\u52a9\u662f\u4e0d\u53ef\u80fd\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u8d44\u91d1\u6765\u6e90\u91cc\u6709\u4e09\u5341\u4ebf\u7684\u6f0f\u6d1e\uff0c\u8fd9\u7edd\u4e0d\u662f\u4ec0\u4e48\u5c0f\u95ee\u9898\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u5bb6\u4e3a\u4ec0\u4e48\u8981\u76ef\u7740\u9526\u6e56\u54ac\uff1f</p><p>\u3000\u3000\u867d\u7136\u4e25\u6587\u4ecb\u5f53\u5bb6\u7684\u5b8f\u4fe1\u5efa\u8bbe\u662f\u5728\u4eac\u6d25\u5730\u533a\u53d1\u5bb6\uff0c\u4f46\u662f\u4e25\u5bb6\u7684\u6839\u672c\u8fd8\u662f\u5728\u56fd\u5185\u79c1\u8425\u7ecf\u6d4e\u4f53\u6700\u53d1\u8fbe\u7684\u57ce\u5e02\u4e4b\u4e00\u6587\u821f\u3002</p><p>\u3000\u3000\u4e25\u5bb6\u7684\u5229\u76ca\u94fe\u5c31\u4ece\u94a2\u94c1\u3001\u5236\u836f\u3001\u80fd\u6e90\u3001\u7269\u6d41\u3001\u6e2f\u53e3\u5de5\u4e1a\u7b49\u8bf8\u591a\u9886\u57df\u6e17\u900f\u8fdb\u6587\u821f\u7684\u6d77\u6e2f\u7ecf\u6d4e\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u4e25\u5bb6\u80cc\u540e\u64cd\u7eb5\u7684\u4fe1\u901a\u94f6\u884c\u5357\u65b9\u603b\u90e8\u5c31\u8bbe\u5728\u6587\u821f\uff0c\u6587\u821f\u4e5f\u662f\u4fe1\u901a\u94f6\u884c\u6700\u5927\u7684\u8d44\u91d1\u6765\u6e90\u5730\uff0c\u867d\u7136\u4fe1\u901a\u94f6\u884c\u5728\u56fd\u5185\u4e3b\u8981\u57ce\u5e02\u90fd\u8bbe\u6709\u5206\u652f\u673a\u6784\uff0c\u90fd\u6709\u5438\u7eb3\u516c\u4f17\u5b58\u6b3e\u7684\u6743\u9650\uff0c\u4f46\u662f\u4ec5\u6587\u821f\u4e00\u5730\u4f9b\u7ed9\u7684\u8d44\u91d1\u603b\u91cf\u5c31\u5360\u4fe1\u901a\u94f6\u884c\u5438\u7eb3\u516c\u4f17\u4e0e\u4f01\u4e1a\u5b58\u6b3e\u603b\u989d\u7684\u56db\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5b8f\u4fe1\u5efa\u8bbe\u80fd\u5728\u5168\u56fd\u5404\u5730\u653b\u57ce\u63a0\u5730\uff0c\u76f4\u63a5\u7684\u56e0\u7d20\u662f\u6709\u4fe1\u901a\u94f6\u884c\u5728\u80cc\u540e\u652f\u6491\uff0c\u6df1\u5c42\u6b21\u7684\u56e0\u7d20\uff0c\u5c31\u662f\u5b8f\u4fe1\u4e0e\u4e25\u5bb6\u7684\u6839\u57fa\u5728\u6d59\u4e1c\u3001\u5728\u6587\u821f\u3002</p><p>\u3000\u3000\u524d\u4e9b\u5e74\uff0c\u56fd\u5185\u6c11\u8425\u7ecf\u6d4e\u7684\u5730\u4f4d\u4e25\u91cd\u53d7\u538b\u5236\uff0c\u4e25\u5bb6\u4e5f\u9887\u4e3a\u4f4e\u8c03\uff0c\u4e5d\u516b\u5e74\u4e2d\u592e\u5f7b\u5e95\u89e3\u51b3\u6c11\u8425\u7ecf\u6d4e\u4f53\u5728\u56fd\u6c11\u7ecf\u6d4e\u7684\u5730\u4f4d\u95ee\u9898\u4e4b\u540e\uff0c\u5b8f\u4fe1\u7cfb\u7684\u89c4\u6a21\u624d\u6e10\u6e10\u9732\u51fa\u6c34\u9762\uff0c\u4e25\u5bb6\u4e5f\u6b63\u5f0f\u7ec4\u5efa\u5b8f\u4fe1\u6295\u8d44\u63a7\u80a1\u96c6\u56e2\u6765\u76f4\u63a5\u63a7\u5236\u5b8f\u4fe1\u7cfb\u65d7\u4e0b\u7684\u4ea7\u4e1a\u3002</p><p>\u3000\u3000\u6216\u8bb8\u76f4\u5230\u4eca\u5e74\u4e0a\u534a\u5e74\uff0c\u5927\u5bb6\u624d\u7a81\u7136\u7684\u53d1\u73b0\u5728\u5b8f\u4fe1\u5efa\u8bbe\u4e4b\u5916\uff0c\u5b8f\u4fe1\u7cfb\u85cf\u5728\u6d77\u9762\u4e4b\u4e0b\u7684\u89c4\u6a21\u7adf\u662f\u5982\u6b64\u7684\u5e9e\u5927\u2014\u2014\u5e74\u4ea7\u4e09\u767e\u4e07\u5428\u94a2\u3001\u4e09\u767e\u4e07\u5428\u94c1\u89c4\u6a21\u7684\u6587\u821f\u94a2\u94c1\u96c6\u56e2\u4f5c\u4e3a\u6d59\u4e1c\u6700\u5927\u7684\u6c11\u8425\u4f01\u4e1a\uff0c\u603b\u8d44\u4ea7\u89c4\u6a21\u8d85\u8fc7\u4e00\u767e\u4e5d\u5341\u4ebf\uff0c\u5b8f\u4fe1\u5c31\u76f4\u63a5\u6301\u670943\uff05\u7684\u80a1\u6743\uff0c\u4ec5\u8fd9\u4e00\u5bb6\u7684\u89c4\u6a21\u5c31\u5df2\u7ecf\u8d85\u8fc7\u4e4b\u524d\u9732\u51fa\u6c34\u9762\u4e4b\u4e0a\u7684\u5b8f\u4fe1\u5efa\u8bbe\u7684\u89c4\u6a21\uff1b\u53e6\u5916\uff0c\u4f5c\u4e3a\u56fd\u5185\u6700\u5927\u7684\u6c11\u8425\u9020\u8239\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u6587\u821f\u9020\u8239\u4ea7\u4e1a\u91cc\uff0c\u5b8f\u4fe1\u7cfb\u4e5f\u62e5\u6709\u6781\u4e3a\u91cd\u8981\u7684\u5730\u4f4d\u2014\u2014\u8981\u8ba4\u771f\u7684\u53bb\u4f30\u7b97\u56fd\u5185\u6c11\u8425\u7ecf\u6d4e\u4f53\u7684\u89c4\u6a21\uff0c\u9526\u6e56\u7cfb\u8fd8\u4e0d\u662f\u5b8f\u4fe1\u7cfb\u7684\u5bf9\u624b\u3002</p><p>\u3000\u3000\u65b0\u4ead\u7684\u5d1b\u8d77\uff0c\u5c06\u5bf9\u76f4\u7ebf\u8ddd\u79bb\u4e0d\u8fc7\u4e24\u767e\u516c\u91cc\u7684\u6587\u821f\u6d77\u6e2f\u7ecf\u6d4e\u5f62\u6210\u76f4\u63a5\u7684\u7ade\u4e89\u5173\u7cfb\u3002</p><p>\u3000\u3000\u65b0\u4ead\u4ece\u516b\u5341\u5e74\u4ee3\u4e2d\u671f\u5c31\u5f00\u59cb\u7b79\u5efa\u4e1c\u5c71\u6e2f\uff0c\u4e00\u76f4\u62d6\u4e86\u5341\u591a\u5e74\uff0c\u624d\u5728\u9526\u6e56\u7684\u652f\u6301\u4e0b\u53bb\u5e74\u6b63\u5f0f\u542f\u52a8\u2014\u2014\u62d6\u4e86\u5341\u591a\u5e74\u7684\u80cc\u540e\u4e0d\u662f\u6ca1\u6709\u6587\u821f\u80cc\u540e\u4f7f\u574f\u7684\u56e0\u7d20\u5728\u5185\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0d\u4ec5\u652f\u6301\u65b0\u4ead\u5efa\u4e1c\u5c71\u6e2f\uff0c\u66f4\u5728\u65b0\u4ead\u76f4\u63a5\u5bf9\u4e1c\u5c71\u94a2\u94c1\u63a7\u80a1\uff0c\u5c06\u4e1c\u5c71\u94a2\u94c1\u7684\u4ea7\u80fd\u5728\u4e00\u5e74\u65f6\u95f4\u91cc\u63d0\u9ad8\u5230\u4e00\u767e\u4e8c\u5341\u4e07\u5428\uff0c\u66f4\u8ba1\u5212\u4e0e\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u5408\u5e76\u5728\u65b0\u4ead\u7b79\u5efa\u5343\u4e07\u5428\u7ea7\u7684\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\uff0c\u8fd8\u8ba1\u5212\u4e0e\u6d0b\u6d66\u8239\u4e1a\u3001\u5357\u6d0b\u6d77\u8fdc\u5728\u65b0\u4ead\u7b79\u5efa\u5927\u578b\u9020\u8239\u57fa\u5730\u3002</p><p>\u3000\u3000\u4ece\u9526\u6e56\u51fa\u9762\u63a8\u52a8\u4e1c\u5c71\u6e2f\u5efa\u8bbe\uff0c\u8fd9\u5df2\u7ecf\u662f\u6839\u57fa\u4e0a\u7684\u76f4\u63a5\u7ade\u4e89\u4e86\u2014\u2014\u4e5f\u96be\u602a\u4e25\u5bb6\u4f1a\u54ac\u9526\u6e56\u54ac\u8fd9\u4e48\u7d27\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5429\u5490\u53f8\u673a\u9001\u4ed6\u53bb\u5317\u4eac\u996d\u5e97\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u5e74\u5317\u4eac\u7684\u8c6a\u534e\u9152\u5e97\u6797\u7acb\uff0c\u5317\u4eac\u996d\u5e97\u591a\u5c11\u6709\u4e9b\u663e\u65e7\u4e86\uff0c\u4f46\u662f\u5927\u5bb6\u8fd8\u662f\u4e60\u60ef\u6027\u7684\u5f80\u5317\u4eac\u996d\u5e97\u91cc\u8dd1\uff0c\u6bd5\u7adf\u662f\u8001\u724c\u5b50\u7684\u4e94\u661f\u7ea7\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u3002</p><p>\u3000\u3000\u8d76\u5230\u5317\u4eac\u996d\u5e97\uff0c\u845b\u5efa\u5fb7\u8ba9\u79d8\u4e66\u8ddf\u53f8\u673a\u56de\u516c\u53f8\u53bb\uff0c\u4e0d\u7528\u8ddf\u7740\u4ed6\u3002\u4ed6\u8d70\u8fdb\u5305\u53a2\uff0c\u9664\u4e86\u6797\u96ea\u3001\u6210\u664b\u6770\u4e4b\u5916\uff0c\u5b8f\u4fe1\u6295\u8d44\u7684\u526f\u603b\u88c1\u30018818\u7f51\u4e0a\u5546\u57ce\u603b\u88c1\u5f20\u6587\u4fe1\u4e5f\u5728\u573a\uff0c\u8fd8\u5c31\u662f\u534e\u590f\u7535\u5b50\u5efa\u90ba\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u5468\u5174\u4e1c\u2014\u2014\u5468\u5174\u4e1c\u6b64\u65f6\u5df2\u7ecf\u6210\u4e86\u845b\u5efa\u5fb7\u7684\u7231\u5c06\u3002</p><p>\u3000\u3000\u89c1\u6210\u664b\u6770\u4e5f\u6ca1\u6709\u8ba9\u79d8\u4e66\u968f\u540c\uff0c\u845b\u5efa\u5fb7\u8d70\u8fdb\u6765\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u8d76\u5de7\u5230\u5317\u4eac\u6765\u51fa\u5dee\uff0c\u4e25\u603b\u7279\u610f\u5429\u5490\u6211\u4e2d\u5348\u8981\u597d\u597d\u7684\u656c\u4e00\u656c\u4e24\u4f4d\u90e8\u957f\u3002\u201d\u5f20\u6587\u4fe1\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u6587\u4fe1\u662f\u9999\u6e2f\u4eba\uff0c\u4eba\u5f88\u5e74\u8f7b\uff0c\u4e5d\u4e94\u5e74\u6d59\u4e1c\u5546\u4eba\u96c6\u7fa4\u5927\u95f9\u56fd\u503a\u671f\u8d27\u5e02\u573a\u65f6\u4ed6\u5145\u5f53\u64cd\u76d8\u624b\u800c\u5728\u4e1a\u5185\u540d\u58f0\u5927\u632f\uff0c\u90a3\u4e00\u6b21\u8db3\u8db3\u4ee5\u8ba9\u5bf9\u624b\u635f\u5931\u5341\u516b\u4ebf\uff0c\u8fd9\u4e24\u5e74\u4ed6\u6539\u5934\u6362\u9762\u62c5\u4efb\u5b8f\u4fe1\u6295\u8d44\u7684\u9ad8\u5c42\uff0c\u8ba9\u4eba\u5f88\u5bb9\u6613\u8054\u60f3\u4e5d\u4e94\u5e74\u56fd\u503a\u671f\u8d27\u5e02\u573a\u4e00\u6218\u771f\u6b63\u7684\u5e55\u540e\u662f\u4e25\u5bb6\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u845b\u5efa\u5fb7\u4e5f\u53ea\u662f\u542c\u8bf4\u800c\u5df2\uff0c\u76f8\u4e92\u5229\u7528\u800c\u5df2\uff0c\u4e25\u5bb6\u4e5f\u4e0d\u4f1a\u5c06\u5e95\u7ec6\u644a\u5230\u4ed6\u9762\u524d\u6765\u3002</p><p>\u3000\u3000\u770b\u5230\u6797\u96ea\u5f81\u8be2\u7684\u773c\u795e\uff0c\u845b\u5efa\u5fb7\u5fae\u5fae\u7684\u6447\u4e86\u6447\u5934\uff0c\u8868\u793a\u5728\u803f\u91cd\u9633\u90a3\u6761\u8def\u8d70\u4e0d\u901a\u3002</p><p>\u3000\u3000\u6210\u664b\u6770\u6ce8\u610f\u5230\u845b\u5efa\u5fb7\u7684\u5c0f\u52a8\u4f5c\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u803f\u91cd\u9633\u4e5f\u4e0d\u6bd4\u5f53\u5e74\u52c7\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u6210\u664b\u6770\u8fd9\u4e48\u8bf4\uff0c\u845b\u5efa\u5fb7\u624d\u77e5\u9053\u6797\u96ea\u5c06\u8fd9\u4e2a\u5bb6\u4f19\u5df2\u7ecf\u5b8c\u5168\u7ed9\u62d6\u4e86\u4e0b\u6765\uff0c\u6709\u4e9b\u4e8b\u4e0d\u7528\u7792\u7740\u4ed6\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u5c06\u81ea\u5df1\u7ed9\u803f\u91cd\u9633\u5f53\u5b59\u5b50\u8bad\u7684\u8bdd\u90fd\u91cd\u590d\u4e00\u904d\uff0c\u8bf4\u9053\uff1a\u201c\u65e0\u8bba\u662f\u534e\u590f\u7535\u5b50\uff0c\u8fd8\u662f\u4fe1\u4ea7\u90e8\u6765\u6345\u9526\u6e56\u7684\u6f0f\u5b50\uff0c\u53ea\u6015\u522b\u4eba\u4f1a\u53fd\u7b11\u6211\u4eec\u65e0\u80fd\uff0c\u803f\u91cd\u9633\u4e5f\u4e0d\u80af\u4e22\u8fd9\u4e2a\u8138\u3002\u201d</p><p>\u3000\u3000\u201c\u803f\u90e8\u957f\u7684\u8003\u8651\u8fd8\u662f\u6709\u9053\u7406\u4e86\uff0c\u4e0d\u6653\u5f97\u6211\u4eec\u80fd\u4e0d\u80fd\u5e2e\u4e0a\u4ec0\u4e48\u5fd9\uff1f\u201d\u5f20\u6587\u4fe1\u8bf4\u9053\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u6447\u4e86\u6447\u5934\uff0c\u8fd9\u79cd\u4e8b\uff0c\u4e0a\u9762\u6ca1\u6709\u4e2a\u9ed8\u8ba4\u6001\u5ea6\uff0c\u803f\u91cd\u9633\u7684\u6001\u5ea6\u4e5f\u662f\u9996\u9f20\u4e24\u7aef\u3001\u72b9\u8c6b\u4e0d\u5b9a\uff0c\u4ed6\u4eec\u5728\u4e0b\u9762\u778e\u6405\u548c\uff0c\u53ea\u4f1a\u5c06\u803f\u91cd\u9633\u4ee5\u53ca\u803f\u91cd\u9633\u66f4\u4e0a\u9762\u7684\u4eba\u7269\u63a8\u5230\u9526\u6e56\u4e00\u8fb9\u53bb\uff0c\u5c06\u67b7\u9501\u5f80\u81ea\u5df1\u8116\u5b50\u4e0a\u5957\u3002</p><p>\u3000\u3000\u6210\u664b\u6770\u7b11\u7740\u8bf4\uff1a\u201c\u5f90\u5b66\u5e73\u5230\u6c5f\u5357\u4efb\u4e66\u8bb0\uff0c\u662f\u8d75\u6d4e\u4e1c\u603b\u7406\u5f3a\u529b\u652f\u6301\u7684\uff0c\u751a\u81f3\u8fd8\u5c06\u5510\u5b66\u8c26\u8c03\u5230\u6c5f\u5357\u53bb\u914d\u5408\u4ed6\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u80cc\u540e\u5e2e\u9526\u6e56\u8bf4\u8bdd\u7684\u6700\u5927\u8001\u677f\u5c31\u662f\u8d75\u6d4e\u4e1c\u603b\u7406\u2014\u2014\u4e0d\u8fc7\u636e\u6211\u6240\u77e5\uff0c\u4e2d\u6676\u5fae\u82af\u4e0e\u4e1c\u5c71\u6e2f\u4e24\u4ef6\u5927\u4e8b\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u5173\u5fc3\u5012\u662f\u86ee\u591a\u7684\u3002\u201d</p><p>\u3000\u3000\u5728\u4e2d\u6676\u5fae\u82af\u4e4b\u524d\uff0c\u56fd\u5185\u90fd\u4e3a\u5efa\u6676\u5706\u751f\u4ea7\u7ebf\u52aa\u529b\u4e86\u8fd1\u4e8c\u5341\u5e74\uff0c\u524d\u540e\u4e09\u4ee3\u7ebf\u3001\u4e09\u6b21\u52aa\u529b\u90fd\u4ee5\u60e8\u8d25\u544a\u7ec8\uff0c\u4ea4\u5b66\u8d39\u5c31\u4ea4\u6389\u597d\u51e0\u5341\u4ebf\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u76f4\u63a5\u4ece\u6d77\u5916\u62c9\u4e24\u767e\u591a\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u5e08\u56de\u56fd\u6765\u5efa\u5382\uff0c\u54ea\u4e2a\u603b\u7406\u6ca1\u6709\u5173\u5fc3\u8fc7\uff1f</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u53c8\u662f\u5386\u6765\u5c11\u6709\u7684\u5927\u5de5\u7a0b\uff0c\u76f4\u63a5\u5173\u7cfb\u5230\u6c5f\u5357\u7701\u7684\u7ecf\u6d4e\u590d\u82cf\uff0c\u603b\u7406\u4eec\u5173\u5fc3\u4e5f\u662f\u6b63\u5e38\u7684\uff0c\u845b\u5efa\u5fb7\u89c1\u6210\u664b\u6770\u610f\u6709\u6240\u6307\u7684\u8bf4\u51fa\u66fe\u5982\u5723\u6765\uff0c\u6709\u4e9b\u7591\u60d1\u7684\u770b\u7740\u4ed6\uff0c\u770b\u4ed6\u80fd\u4e0d\u80fd\u66b4\u51fa\u66f4\u8db3\u7684\u6599\u6765\u3002</p><p>\u3000\u3000\u201c\u516b\u51e0\u5e74\uff0c\u56fd\u52a1\u9662\u662f\u8c01\u6700\u5148\u652f\u6301\u65b0\u4ead\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u65e9\u5e74\u5728\u519c\u4e1a\u7cfb\u7edf\u65f6\u662f\u7ed9\u8c01\u5f53\u79d8\u4e66\u7684\uff1f\u201d\u6210\u664b\u6770\u6545\u5f04\u7384\u865a\u7684\u8bf4\u9053\uff0c\u201c\u8fd9\u4e48\u626f\u5173\u7cfb\uff0c\u6216\u8005\u8fd8\u6709\u4e9b\u8fdc\uff0c\u4e0d\u8fc7\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u5374\u6070\u6070\u4e0e\u90a3\u4f4d\u524d\u526f\u603b\u7406\u6709\u4e9b\u6545\u65e7\u4e4b\u60c5\u2014\u2014\u773c\u4e0b\u9526\u6e56\u8981\u7ed9\u56fd\u5185\u540c\u65f6\u5f15\u8fdb0.25\u30010.18\u5fae\u7c73\u7684\u6676\u5706\u6280\u672f\uff0c\u5c31\u7b97\u9526\u6e56\u6709\u518d\u5927\u7684\u7bd3\u5b50\uff0c\u603b\u7406\u4eec\u4e5f\u4e0d\u4f1a\u5e0c\u671b\u770b\u5230\u6709\u4eba\u8fd9\u65f6\u5019\u7ad9\u51fa\u6765\u6345\u3002\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4e5f\u77e5\u9053\u95ee\u9898\u5c31\u662f\u51fa\u5728\u8fd9\u91cc\uff0c\u6d89\u53ca\u5230\u4e2d\u6676\u5fae\u82af\u4e0e\u4e1c\u5c71\u6e2f\u7684\u6210\u8d25\uff0c\u8d75\u6d4e\u4e1c\u603b\u7406\u662f\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\u522b\u4eba\u6765\u62c6\u9526\u6e56\u5899\u811a\u7684\u3002</p><p>\u3000\u3000\u8d75\u6d4e\u4e1c\u603b\u7406\u8fc7\u4e8e\u5f3a\u786c\u7684\u6267\u653f\u98ce\u683c\uff0c\u8ba9\u4ed6\u5728\u4f53\u7cfb\u5185\u4e5f\u9887\u4e3a\u5b64\u7acb\u3001\u6811\u654c\u5f88\u591a\uff0c\u8981\u662f\u4ed6\u7acb\u573a\u9c9c\u660e\u7684\u7ad9\u51fa\u6765\u8981\u4fdd\u9526\u6e56\uff0c\u6307\u4e0d\u5b9a\u4f1a\u6709\u66f4\u591a\u4eba\u7684\u7ad9\u51fa\u6765\u5228\u9526\u6e56\u7684\u5899\u811a\u3002</p><p>\u3000\u3000\u51fa\u4e8e\u8fd9\u6837\u7684\u8003\u8651\uff0c\u4ed6\u624d\u60f3\u7740\u662f\u4e0d\u662f\u6002\u607f\u803f\u91cd\u9633\u53bb\u6345\u9526\u6e56\u2014\u2014\u5728\u56fd\u52a1\u9662\uff0c\u803f\u91cd\u9633\u4e5f\u662f\u5f88\u4e0d\u5f97\u8d75\u6d4e\u4e1c\u603b\u7406\u6b22\u5fc3\u7684\u4eba\u3002</p><p>\u3000\u3000\u5c31\u7b97\u8d75\u6d4e\u4e1c\u603b\u7406\u4e0d\u7ad9\u51fa\u6765\uff0c\u9526\u6e56\u80cc\u540e\u4e5f\u6709\u53f6\u5bb6\u5728\u652f\u6491\uff0c\u4e00\u65f6\u4e4b\u95f4\u6216\u8bb8\u8fd8\u4e0d\u80fd\u62ff\u9526\u6e56\u600e\u4e48\u6837\uff0c\u4f46\u662f\u53ea\u8981\u6495\u5f00\u4e00\u9053\u53e3\u5b50\u6446\u5728\u90a3\u91cc\uff0c\u5c31\u4f1a\u8ba9\u4e0a\u9762\u79ef\u7d2f\u8d8a\u6765\u8d8a\u591a\u5bf9\u9526\u6e56\u7684\u8d28\u7591\u2014\u2014\u6ca1\u60f3\u5230\u7adf\u7136\u66fe\u5982\u5723\u526f\u603b\u7406\u4e5f\u662f\u7ad9\u5728\u9526\u6e56\u90a3\u4e00\u8fb9\u7684\u2026\u2026</p><p>\u3000\u3000\u8981\u4e0d\u662f\u6210\u664b\u6770\u8bf4\u51fa\u6765\uff0c\u4e00\u822c\u4eba\u8fd8\u771f\u5bdf\u89c9\u4e0d\u5230\uff0c\u5728\u845b\u5efa\u5fb7\u7684\u5370\u8c61\u91cc\uff0c\u9526\u6e56\u4f3c\u4e4e\u4e0e\u66fe\u5982\u5723\u526f\u603b\u7406\u6ca1\u6709\u4ec0\u4e48\u76f4\u63a5\u63a5\u89e6\u554a\uff1f</p><p>\u3000\u3000\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u5b81\u4fe1\u5176\u65e0\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u5728\u4f53\u7cfb\u5185\u7684\u4eba\u8109\u6bd4\u8d75\u6d4e\u4e1c\u603b\u7406\u8981\u597d\u591a\u4e86\uff0c\u4ed6\u8981\u5e2e\u9526\u6e56\u8bf4\u8bdd\uff0c\u5176\u5b9e\u6bd4\u8d75\u6d4e\u4e1c\u603b\u7406\u66f4\u68d8\u624b\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u60f3\u5230\u803f\u91cd\u9633\u5728\u529e\u516c\u5ba4\u91cc\u8bad\u4ed6\u8ddf\u8bad\u5b59\u5b50\u4e00\u6837\u7684\u8bdd\uff0c\u7422\u78e8\u4e0d\u900f\u8fd9\u662f\u4ed6\u7684\u672c\u610f\uff0c\u8fd8\u662f\u4ed6\u5df2\u7ecf\u770b\u5230\u66fe\u5982\u5723\u526f\u603b\u7406\u4e0e\u9526\u6e56\u7684\u90a3\u5c42\u82e5\u6709\u82e5\u65e0\u7684\u8054\u7cfb\u3002</p><p>\u3000\u3000\u770b\u5230\u845b\u5efa\u5fb7\u4e00\u8138\u7684\u6c89\u9ed8\uff0c\u6797\u96ea\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u4ebf\uff0c\u53c8\u4e0d\u662f\u4e09\u5341\u4e07\uff0c\u4e09\u5341\u4ebf\u7684\u80cc\u540e\u8981\u6ca1\u6709\u4ec0\u4e48\u86db\u4e1d\u9a6c\u8ff9\uff0c\u90a3\u8bf4\u660e\u56fd\u5185\u7ecf\u6d4e\u4f53\u7cfb\u7684\u6f0f\u6d1e\u5c31\u592a\u5927\u4e86\uff0c\u4e5f\u591f\u4e2d\u592e\u9886\u5bfc\u4eec\u597d\u597d\u7684\u53cd\u601d\u4e00\u4e0b\u4e86\u3002\u201d</p><p>\u3000\u3000\u6210\u664b\u6770\u7b11\u7740\u8bf4\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\uff0c\u901a\u8fc7\u4e00\u4e9b\u6e20\u9053\u5c06\u8fd9\u4e2a\u95ee\u9898\u53cd\u5e94\u4e0a\u53bb\u5c31\u53ef\u4ee5\u4e86\uff0c\u4e5f\u4e0d\u8981\u6307\u671b\u80fd\u7acb\u5373\u5c31\u5904\u7406\uff0c\u518d\u8fc7\u4e00\u4e2a\u591a\u6708\u5c31\u662f\u4e94\u5341\u5468\u5e74\u7684\u56fd\u5178\u5927\u5e86\uff0c\u5c31\u7b97\u6ca1\u6709\u8fd9\u4e24\u4e2a\u5927\u5de5\u7a0b\uff0c\u4e0a\u9762\u8c01\u5e0c\u671b\u770b\u5230\u8fd9\u65f6\u5019\u51fa\u5927\u6848\u5b50\uff1f\u201d</p><p>\u3000\u3000\u82e5\u662f\u5c06\u95ee\u9898\u53cd\u5e94\u4e0a\u53bb\uff0c\u4e0d\u7528\u6495\u7834\u8138\u76ae\uff0c\u90a3\u6e20\u9053\u8fd8\u662f\u6709\u5f88\u591a\u7684\uff0c\u603b\u7406\u4eec\u4e5f\u4e60\u60ef\u4e0e\u4e13\u5bb6\u5b66\u8005\u5bf9\u8bdd\uff0c\u968f\u4fbf\u627e\u4e2a\u4e13\u5bb6\u5b66\u8005\u5c06\u95ee\u9898\u6316\u6398\u4e00\u4e0b\u6345\u4e0a\u53bb\u5c31\u53ef\u4ee5\u4e86\u2014\u2014\u53ef\u60dc\u5468\u747e\u73ba\u8fd8\u8eba\u5728\u533b\u9662\uff0c\u8981\u6ca1\u6709\u90a3\u6863\u5b50\u4e8b\uff0c\u4ed6\u5c31\u662f\u6700\u5408\u9002\u7684\u4eba\u9009\u3002</p><p>\u3000\u3000\u7528\u8fc7\u9910\uff0c\u559d\u4e86\u4e9b\u9152\uff0c\u5728\u996d\u5e97\u5f00\u4e86\u623f\u95f4\u4e0e\u6797\u96ea\u5f04\u4e86\u4e00\u56de\u624d\u8ba9\u53f8\u673a\u5f00\u8f66\u8fc7\u6765\u63a5\u4ed6\u4e0e\u5468\u5174\u4e1c\u56de\u534e\u590f\u7535\u5b50\u603b\u90e8\u3002</p><p>\u3000\u3000\u5728\u8f66\u4e0a\uff0c\u5468\u5174\u4e1c\u8ddf\u845b\u5efa\u5fb7\u95ee\u4e86\u4e00\u4ef6\u4e8b\uff1a\u201c\u4e1c\u5927\u5fae\u7535\u5b50\u5b66\u9662\u8fd9\u51e0\u5929\u5c31\u8981\u6b63\u5f0f\u6302\u724c\u6210\u7acb\uff0c\u845b\u90e8\u957f\u4f1a\u4e0d\u4f1a\u51fa\u5e2d\u63ed\u724c\u4eea\u5f0f\uff1f\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u8fd8\u662f\u559c\u6b22\u522b\u4eba\u79f0\u4ed6\u90e8\u957f\uff0c\u526f\u90e8\u7ea7\u662f\u4ed6\u5728\u4f53\u7cfb\u5185\u7684\u5b9a\u4f4d\uff0c\u8fd9\u8ba9\u4ed6\u611f\u89c9\u66f4\u5b9e\u5728\u4e00\u4e9b\uff0c\u4e5f\u8ba9\u4ed6\u66f4\u89c9\u5f97\u4e60\u60ef\u3002</p><p>\u3000\u3000\u542c\u5468\u5174\u4e1c\u63d0\u8d77\u8fd9\u4e8b\uff0c\u845b\u5efa\u5fb7\u5fcd\u4e0d\u4f4f\u53c8\u8981\u76b1\u7709\u5934\u4e86\uff0c\u4ed6\u662f\u63a5\u5230\u9080\u8bf7\uff0c\u4f46\u662f\u4ed6\u90fd\u6ca1\u6709\u8138\u53bb\u53c2\u52a0\uff0c\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u5230\u65f6\u5019\uff0c\u4f60\u4ee3\u8868\u6211\u51fa\u5e2d\u4e00\u4e0b\uff0c\u6211\u54ea\u6709\u65f6\u95f4\u4e13\u7a0b\u4ece\u5317\u4eac\u98de\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56fd\u5185\u9ad8\u6821\u76ee\u524d\u8bbe\u6709\u5fae\u7535\u5b50\u4e13\u4e1a\u7684\u624d\u6709\u516b\u4e5d\u6240\uff0c\u518d\u52a0\u4e2d\u79d1\u9662\u7684\u4e24\u5bb6\u7814\u7a76\u6240\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u4e4b\u524d\uff0c\u5168\u56fd\u6bcf\u5e74\u603b\u5171\u4e5f\u5c31\u57f9\u517b\u4e0d\u5230\u4e00\u767e\u540d\u5fae\u7535\u5b50\u4e13\u4e1a\u7684\u7855\u58eb\u3001\u5b66\u58eb\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\u7d2f\u79ef\u8d77\u6765\uff0c\u901a\u8fc7\u6b63\u89c4\u9662\u6821\u57f9\u517b\u51fa\u6765\u7684\u5fae\u7535\u5b50\u5de5\u827a\u53ca\u8bbe\u8ba1\u4e13\u4e1a\u5de5\u7a0b\u5e08\u90fd\u4e0d\u8db3\u5343\u4eba\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u56fd\u5916\u5bf9\u56fd\u5185\u8fdb\u884c\u6280\u672f\u3001\u8bbe\u5907\u4e0a\u7684\u4e25\u683c\u5c01\u9501\u4e86\uff0c\u4ec5\u51ed\u501f\u6280\u672f\u6c34\u51c6\u672c\u6765\u5c31\u4e0d\u5927\u8fc7\u786c\u7684\u5343\u540d\u5de5\u7a0b\u6280\u672f\u4eba\u5458\uff0c\u7279\u522b\u662f\u5fae\u7535\u5b50\u8bbe\u8ba1\u3001\u5de5\u827a\u4eba\u5458\u7684\u5947\u7f3a\uff0c\u4e5f\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u5c06\u56fd\u5185\u7684\u96c6\u6210\u7535\u8def\u57fa\u7840\u4ea7\u4e1a\u652f\u6491\u8d77\u6765\u2014\u2014\u5176\u5b9e\u56fd\u5185\u6700\u540e\u4e00\u6b21\u51b2\u51fb\u5efa\u8bbe\u6676\u5706\u5382\u5931\u8d25\uff0c\u6700\u4e3b\u8981\u7684\u539f\u56e0\u5c31\u662f\u56e0\u4e3a\u6ca1\u6709\u8db3\u591f\u591a\u7684\u5408\u683c\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u3002</p><p>\u3000\u3000\u8f6c\u673a\u4e5f\u6070\u6070\u5f53\u662f\u9526\u6e56\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0d\u4ec5\u4ece\u6d77\u5916\u62db\u52df\u5927\u91cf\u7684\u5fae\u7535\u5b50\u8bbe\u8ba1\u4eba\u5458\uff0c\u5f53\u7136\u4e86\uff0c\u56fd\u5185\u7684\u5fae\u7535\u5b50\u4e13\u4e1a\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u4e5f\u7ed9\u4ed6\u4eec\u7f51\u7f57\u4e86\u76f8\u5f53\u591a\u7684\u4e00\u90e8\u5206\uff0c\u9526\u6e56\u8fd8\u5728\u5185\u90e8\u5927\u529b\u7684\u57f9\u517b\u8bbe\u8ba1\u4eba\u5458\u2014\u2014\u5de5\u827a\u65b9\u9762\uff0c\u4e3a\u4e86\u6210\u7acb\u4e2d\u6676\u5fae\u82af\uff0c\u67f3\u5fd7\u6210\u5c31\u4ece\u4e1c\u5357\u4e9a\u3001\u53f0\u6e7e\u62c9\u4e86\u8fd1\u4e24\u767e\u540d\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u56de\u6765\uff0c\u4e5f\u5728\u5185\u90e8\u5927\u91cf\u7684\u8fdb\u884c\u4eba\u624d\u57f9\u517b\uff0c\u53ef\u4ee5\u8bf4\uff0c\u6b64\u65f6\u56fd\u5185\u6574\u4e2a\u96c6\u6210\u7535\u8def\u57fa\u7840\u4ea7\u4e1a\u7684\u91cd\u5fc3\u90fd\u5728\u9526\u6e56\u3002</p><p>\u3000\u3000\u6613\u4e91\u98de\u8bf4\u56fd\u5185\u8981\u53d1\u5c55\u96c6\u6210\u7535\u8def\u57fa\u7840\u4ea7\u4e1a\u8981\u7b49\u7740\u9526\u6e56\u5f80\u5916\u8f93\u9001\u4eba\u624d\uff0c\u8fd9\u53e5\u8bdd\u4e00\u70b9\u90fd\u4e0d\u865a\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u9526\u6e56\u4ece\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u4e4b\u521d\u5c31\u5f00\u59cb\u5927\u624b\u7b14\u7684\u652f\u6301\u4e1c\u6d77\u5927\u5b66\u3001\u5efa\u90ba\u90ae\u7535\u5b66\u9662\u53d1\u5c55\u5fae\u7535\u5b50\u4e13\u4e1a\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u4e1c\u6d77\u5927\u5b66\u4ece\u9526\u6e56\u83b7\u5f97\u4e94\u5343\u4e07\u5b66\u79d1\u5efa\u8bbe\u6350\u52a9\uff0c\u4e5d\u516b\u5e74\uff0c\u5b66\u79d1\u5efa\u8bbe\u6350\u52a9\u91d1\u8d85\u8fc7\u4e00\u4ebf\uff0c\u4e5d\u4e5d\u5e74\u4e0a\u534a\u5e74\uff0c\u5b66\u79d1\u5efa\u8bbe\u6350\u52a9\u91d1\u8d85\u8fc7\u516b\u5343\u4e07\uff0c\u8fd9\u4e9b\u5b66\u79d1\u5efa\u8bbe\u6350\u52a9\u91d1\u4e2d\u8fd1\u4e00\u534a\u90fd\u7528\u5728\u5fae\u7535\u5b50\u4e13\u4e1a\u7684\u5b66\u79d1\u5efa\u8bbe\u4e0a\uff0c\u4f46\u662f\u5bf9\u4e1c\u6d77\u5927\u5b66\u5fae\u7535\u5b50\u4e13\u4e1a\u63d0\u5347\u6700\u5927\u7684\u8fd8\u662f\u6a61\u6811\u56ed\u5b9e\u884c\u7684\u53cc\u5bfc\u5e08\u5236\u3002</p><p>\u3000\u3000\u5fae\u7535\u5b50\u4e13\u4e1a\u4eba\u624d\u7684\u57f9\u517b\u4e25\u91cd\u4f9d\u8d56\u4e8e\u5bfc\u5e08\u7684\u6c34\u5e73\uff0c\u7531\u4e8e\u56fd\u5185\u96c6\u6210\u7535\u5668\u57fa\u7840\u4ea7\u4e1a\u7684\u4e25\u91cd\u6ede\u540e\u53d1\u5c55\uff0c\u5bfc\u81f4\u56fd\u5185\u7684\u6559\u5b66\u6c34\u5e73\u76f8\u6bd4\u8f83\u6b27\u7f8e\u53d1\u8fbe\u56fd\u5bb6\u7684\u5dee\u8ddd\u6781\u5927\u3002</p><p>\u3000\u3000\u9526\u6e56\u542f\u52a8\u767e\u4eba\u8ba1\u5212\u4ece\u6d77\u5916\u62db\u8058\u9876\u5c16\u7684\u6280\u672f\u4eba\u624d\uff0c\u8fd9\u4e9b\u4eba\u624d\u624d\u80fd\u7b97\u5fae\u7535\u5b50\u4e13\u4e1a\u4eba\u624d\u57f9\u517b\u7684\u5408\u683c\u5bfc\u5e08\uff0c\u66f4\u4f55\u51b5\u6a61\u6811\u56ed\u6709\u7740\u5f53\u524d\u4e00\u6d41\u7684\u5b9e\u9a8c\u8bbe\u5907\u4e0e\u8bbe\u8ba1\u5e73\u53f0\u4ee5\u53ca\u5927\u91cf\u5b9e\u8df5\u7684\u5de5\u4f5c\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u9526\u6e56\u4e00\u5f00\u59cb\u5c31\u7528\u7cd6\u8863\u94f6\u5f39\u5f00\u8def\uff0c\u4e1c\u5927\u5fae\u7535\u5b50\u4e13\u4e1a\u7684\u6559\u804c\u4eba\u5458\u4f1a\u7b2c\u4e00\u4e2a\u8df3\u51fa\u6765\u53cd\u5bf9\u53cc\u5bfc\u5e08\u5236\uff0c\u5c31\u7b97\u6709\u7cd6\u8863\u94f6\u5f39\u5f00\u8def\uff0c\u4e1c\u5927\u7535\u5b50\u5de5\u7a0b\u7cfb\u5fae\u7535\u5b50\u4e13\u4e1a\u7684\u6559\u6388\u3001\u8bb2\u5e08\u4eec\u4e5f\u627f\u53d7\u7740\u6781\u5927\u7684\u538b\u529b\uff0c\u4e5f\u4fc3\u4f7f\u4e1c\u6d77\u5927\u5b66\u5927\u89c4\u6a21\u4ece\u6d77\u5916\u5f15\u8fdb\u9ad8\u7ea7\u522b\u7684\u6559\u5b66\u79d1\u7814\u4eba\u624d\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u4e4b\u524d\uff0c\u4e1c\u6d77\u5927\u5b66\u5fae\u7535\u5b50\u4e13\u4e1a\u6c34\u5e73\u5728\u56fd\u5185\u516b\u4e5d\u6240\u9ad8\u6821\u91cc\u4e5f\u53ea\u80fd\u7b97\u4e2d\u6e38\uff0c\u4f46\u662f\u5230\u4e5d\u516b\u5e74\uff0c\u590d\u4e98\u3001\u6e05\u534e\u7edd\u4e0d\u6562\u8ddf\u4e1c\u5927\u4e89\u5fae\u7535\u5b50\u4e13\u4e1a\u7b2c\u4e00\u7684\u540d\u5934\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u4e1c\u5927\u5fae\u7535\u5b50\u4e13\u4e1a\u53ca\u4e1c\u5927\u4e13\u7528\u96c6\u6210\u7535\u5668\u8bbe\u8ba1\u5e94\u7528\u4e2d\u5fc3\u7b49\u7814\u7a76\u673a\u6784\u5171\u62db\u6536\u7855\u58eb\u3001\u535a\u58eb\u7814\u7a76\u751f\u603b\u6570\u5c31\u8d85\u8fc7\u4e00\u767e\u56db\u5341\u4eba\uff0c\u8fd8\u8bbe\u7acb\u5fae\u7535\u5b50\u5de5\u827a\u4e0e\u8bbe\u8ba1\u4e24\u4e2a\u672c\u79d1\u4e13\u4e1a\uff0c\u5360\u5230\u5168\u56fd\u5fae\u7535\u5b50\u4e13\u4e1a\u62db\u7855\u3001\u62db\u7855\u603b\u4eba\u6570\u768460\uff05\u3002</p><p>\u3000\u3000\u4eca\u5e74\uff0c\u4e1c\u6d77\u5927\u5b66\u5728\u5fae\u7535\u5b50\u4e13\u4e1a\u4e0a\u7684\u91ce\u5fc3\u66f4\u5927\uff0c\u5c06\u5fae\u7535\u5b50\u4e13\u4e1a\u8fd9\u4e00\u5757\u4ece\u7535\u5b50\u5de5\u7a0b\u7cfb\u62ff\u51fa\u6765\uff0c\u6210\u7acb\u72ec\u7acb\u7684\u5fae\u7535\u5b50\u5b66\u9662\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u7684\u8fd9\u4e00\u51b3\u5b9a\u83b7\u5f97\u6559\u80b2\u90e8\u3001\u79d1\u6280\u90e8\u7b49\u4e2d\u592e\u90e8\u59d4\u4ee5\u53ca\u5730\u65b9\u653f\u5e9c\u7684\u5927\u529b\u652f\u6301\uff0c\u4e2d\u592e\u90e8\u59d4\u518d\u5927\u529b\u652f\u6301\uff0c\u80fd\u638f\u51fa\u7684\u771f\u91d1\u767d\u94f6\u4e5f\u76f8\u5f53\u6709\u9650\uff0c\u603b\u5171\u62e8\u4e86\u4e00\u5343\u4e07\u804a\u8868\u5fc3\u610f\uff0c\u6bd5\u7adf\u6e05\u534e\u3001\u590d\u4e98\u8fd9\u4e9b\u9ad8\u6821\u773c\u775b\u4e5f\u76ef\u7740\uff0c\u771f\u6b63\u5927\u529b\u652f\u6301\u7684\u662f\u5730\u65b9\u653f\u5e9c\u3002</p><p>\u3000\u3000\u5efa\u90ba\u8981\u5b8c\u6210\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u5b8f\u5927\u6218\u7565\u76ee\u6807\uff0c\u8981\u5c06\u5efa\u90ba\u5efa\u8bbe\u6210\u56fd\u5185\u96c6\u6210\u7535\u8def\u57fa\u7840\u4ea7\u4e1a\u7684\u91cd\u9547\uff0c\u5c06\u4e2d\u56fd\u7845\u8c37\u7684\u79f0\u53f7\u4ece\u4e2d\u5173\u6751\u624b\u91cc\u6c38\u4e45\u7684\u593a\u8fc7\u6765\uff0c\u7701\u3001\u5e02\u3001\u9ad8\u65b0\u533a\u4e09\u7ea7\u653f\u5e9c\u603b\u5171\u62e8\u6b3e\u4e24\u4e2a\u4ebf\u7ed9\u4e1c\u6d77\u5927\u5b66\u7528\u4e8e\u5fae\u7535\u5b50\u5b66\u9662\u7684\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u4e24\u4e2a\u4ebf\u662f\u4ec0\u4e48\u6982\u5ff5\u5462\uff1f</p><p>\u3000\u3000\u62e5\u6709\u5e08\u751f\u603b\u6570\u8d85\u8fc7\u4e24\u4e07\u516b\u5343\u4eba\u7684\u4e1c\u6d77\u5927\u5b66\u4e5d\u516b\u5e74\u5168\u5e74\u4ece\u6559\u80b2\u90e8\u4ee5\u53ca\u5730\u65b9\u653f\u5e9c\u83b7\u5f97\u62e8\u6b3e\u603b\u6570\u5c31\u662f\u4e24\u4e2a\u4ebf\u3002</p><p>\u3000\u3000\u5468\u5174\u4e1c\u79bb\u5f00\u540e\uff0c\u845b\u5efa\u5fb7\u75b2\u60eb\u7684\u95ed\u4e0a\u773c\u775b\uff0c\u4e1c\u6d77\u5927\u5b66\u6210\u7acb\u5fae\u7535\u5b50\u5b66\u9662\u7684\u80cc\u540e\u5c31\u662f\u9526\u6e56\u5728\u5927\u529b\u7684\u63a8\u52a8\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u8058\u8bf7\u6a61\u6811\u56ed\u9996\u5e2d\u79d1\u5b66\u5bb6\u8c2d\u4e91\u677e\u3001\u4e2d\u6676\u5fae\u82af\u603b\u88c1\u67f3\u5fd7\u6210\u62c5\u4efb\u5fae\u7535\u5b50\u5b66\u9662\u7684\u540d\u8a89\u9662\u957f\uff0c\u800c\u5fae\u7535\u5b50\u5b66\u9662\u9662\u957f\u97e9\u683c\u540c\u65f6\u517c\u4efb\u6a61\u6811\u56ed\u6b21\u5e2d\u79d1\u5b66\u5bb6\u3001\u6a61\u6811\u56ed\u7b2c\u56db\u6240\u9996\u5e2d\u5de5\u7a0b\u5e08\uff0c\u5fae\u7535\u5b50\u5b66\u9662\u5dee\u4e0d\u591a\u6709\u4e00\u534a\u4ee5\u4e0a\u7684\u6559\u6388\u3001\u8bb2\u5e08\u90fd\u6765\u81ea\u6a61\u6811\u56ed\u3001\u4e2d\u6676\u5fae\u82af\u4ee5\u53ca\u7231\u8fbe\u5fae\u7535\u5b50\u7b49\u4f01\u4e1a\u9ad8\u7ea7\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u4e0e\u7814\u7a76\u5458\u7684\u517c\u804c\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\uff0c\u56fd\u5185\u4e00\u76f4\u5728\u53eb\u5524\u8981\u8d70\u201c\u4ea7\u5b66\u7814\u4e00\u4f53\u201d\u7684\u9053\u8def\uff0c\u771f\u6b63\u80fd\u8d70\u51fa\u8fd9\u4e00\u6b65\u7684\u57ce\u5e02\u5374\u6781\u4e3a\u6709\u9650\uff0c\u800c\u5efa\u90ba\u53c8\u7279\u522b\u7684\u8000\u773c\u3002</p><p>\u3000\u3000\u5efa\u90ba\u7684\u4eba\u624d\u3001\u6280\u672f\u4f18\u52bf\u8d8a\u660e\u663e\uff0c\u5185\u5916\u8d44\u4f01\u4e1a\u81ea\u7136\u5c31\u8d8a\u53ef\u80fd\u9009\u62e9\u5728\u5efa\u5382\u3001\u6210\u7acb\u7814\u7a76\u673a\u6784\u3002</p><p>\u3000\u3000\u6b64\u65f6\u6559\u80b2\u90e8\u3001\u79d1\u6280\u90e8\u624d\u521a\u521a\u5f00\u59cb\u89c4\u5212\u8981\u9009\u62e9\u4e00\u6279\u9ad8\u7b49\u9662\u6240\u4e0e\u7814\u7a76\u673a\u6784\u8bbe\u7acb\u96c6\u6210\u7535\u8def\u4eba\u624d\u57f9\u517b\u57fa\u5730\uff0c\u8fd8\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u6709\u5b9e\u9645\u7684\u52a8\u4f5c\u51fa\u6765\uff0c\u5efa\u90ba\u5e02\u5c31\u5df2\u7ecf\u8fc8\u51fa\u8fd9\u4e48\u5927\u7684\u6b65\u4f10\u4e86\u3002</p><p>\u3000\u3000\u4e5f\u4e0d\u4ec5\u4ec5\u4e1c\u6d77\u5927\u5b66\u6210\u7acb\u5fae\u7535\u5b50\u5b66\u9662\uff0c\u5efa\u90ae\u5fae\u7535\u5b50\u4e13\u4e1a\u4e5f\u5f97\u5230\u6781\u5927\u7684\u52a0\u5f3a\uff0c\u5efa\u90ba\u5e08\u5927\u3001\u4e1c\u6d77\u7406\u5de5\u5927\u7b49\u5efa\u90ba\u5e02\u56db\u6240\u91cd\u70b9\u9ad8\u6821\u4e5f\u5728\u8fd9\u4e24\u5e74\u8bbe\u7acb\u4e86\u5fae\u7535\u5b50\u5de5\u827a\u4e0e\u8bbe\u8ba1\u7814\u7a76\u751f\u4e13\u4e1a\u3002</p><p>\u3000\u3000\u8c01\u662f\u80cc\u540e\u6700\u5927\u7684\u63a8\u52a8\u529b\uff1f\u845b\u5efa\u5fb7\u4e5f\u65e0\u6cd5\u7741\u7740\u773c\u775b\u5c31\u8bf4\u4e0d\u662f\u9526\u6e56\u3002</p><p>\u3000\u3000\u9526\u6e56\u80fd\u6273\u5012\u5417\uff1f</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5fc3\u91cc\u6253\u4e86\u4e2a\u6781\u5927\u7684\u95ee\u9898\uff0c\u9664\u975e\u80fd\u627e\u5230\u4ed6\u4eec\u81f4\u547d\u7684\u7f3a\u9677\uff0c\u4e0d\u7136\u4ec5\u4ec5\u662f\u65e0\u5173\u75db\u75d2\u7684\u4e2d\u4f24\u3001\u6253\u538b\uff0c\u53ea\u6015\u6709\u591a\u5c11\u4eba\u53cd\u5bf9\u9526\u6e56\uff0c\u5c31\u4f1a\u7ad9\u66f4\u591a\u7684\u4eba\u6765\u652f\u6301\u9526\u6e56\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4e5f\u5728\u8003\u8651\u4e1c\u5927\u5fae\u7535\u5b50\u5b66\u9662\u63ed\u724c\u4eea\u5f0f\u81ea\u5df1\u4e0d\u8fc7\u53bb\u4e00\u4e0b\u4f1a\u4e0d\u4f1a\u4e0d\u59a5\u3002</p><p>\u3000\u3000\u867d\u7136NEC\u7535\u5b50\u5411\u534e\u590fNEC\u6d3e\u9063\u4e86\u4e94\u5341\u591a\u540d\u65e5\u672c\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u534f\u52a9\u5efa\u5382\u4ee5\u53ca\u8d1f\u8d23\u5efa\u5382\u540e\u7684\u6b63\u7ecf\u8fd0\u8425\uff0c\u4f46\u662f\u7edd\u4e0d\u80af\u7b54\u5e94\u5e2e\u5fd9\u57f9\u517b\u672c\u571f\u5de5\u7a0b\u5e08\uff0c\u534e\u590fNEC\u7684\u672c\u571f\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u57f9\u517b\u4e5f\u4f9d\u8d56\u4e8e\u4e1c\u5927\u5fae\u7535\u5b50\u5b66\u9662\u2014\u2014\u534e\u590fNEC\u9996\u6279\u4e09\u5341\u540d\u4e13\u5411\u8f93\u9001\u7684\u5de5\u7a0b\u7855\u58eb\u8fd9\u6b21\u5c31\u8981\u8fdb\u5165\u4e1c\u5927\u5fae\u7535\u5b50\u5b66\u9662\u5b66\u4e60\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5fc3\u91cc\u662f\u4e07\u822c\u4e0d\u60f3\u53c2\u52a0\u8fd9\u4e2a\u63ed\u724c\u4eea\u5f0f\uff0c\u4f46\u662f\u4e0d\u53c2\u52a0\uff0c\u8c01\u77e5\u9053\u522b\u4eba\u5728\u80cc\u540e\u53c8\u4f1a\u600e\u4e48\u8bae\u8bba\uff1f\u4e0b\u9762\u4eba\u8bae\u8bba\u5c31\u8bae\u8bba\u597d\u4e86\uff0c\u4e0a\u9762\u4eba\u5462\uff1f\u9664\u4e86\u5730\u65b9\u653f\u5e9c\uff0c\u6559\u80b2\u90e8\u3001\u79d1\u6280\u90e8\u90fd\u4f1a\u6709\u9ad8\u5c42\u5217\u5e2d\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u60f3\u7740\uff0c\u845b\u5efa\u5fb7\u7ed9\u5468\u5174\u4e1c\u6253\u4e86\u4e2a\u7535\u8bdd\uff0c\u544a\u8bc9\u4ed6\uff1a\u201c\u4e1c\u5927\u5fae\u7535\u5b50\u5b66\u9662\u7684\u63ed\u724c\uff0c\u6211\u8fd8\u662f\u8981\u62bd\u65f6\u95f4\u98de\u4e00\u8d9f\u5efa\u90ba\u2026\u2026\u201d\u6302\u4e86\u7535\u8bdd\uff0c\u6068\u4e0d\u5f97\u5c06\u7535\u8bdd\u673a\u7838\u6389\uff0c\u8fd8\u597d\u90a3\u4e2a\u5c0f\u8d64\u4f6c\u4e0d\u5728\u56fd\u5185\u3002</p></article>"}}}]);
