(window.webpackJsonp=window.webpackJsonp||[]).push([[661],{684:function(p){p.exports={chapterTitle:null,title:"\u7b2c660\u7ae0 \u675c\u98de\u7684\u6210\u957f",content:"<article><h2>\u7b2c660\u7ae0 \u675c\u98de\u7684\u6210\u957f</h2><p>\u3000\u3000\u6811\u6797\u5e7d\u6697\u5904\uff0c\u501f\u7740\u5fae\u5f31\u7684\u5149\u80fd\u770b\u6709\u51e0\u5bf9\u7537\u5973\u7684\u8eab\u5f71\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u8981\u4e0d\u662f\u542c\u7740\u5427\u5527\u5427\u5527\u7684\u5543\u5634\u58f0\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e00\u4e2a\u4eba\u5462\uff0c\u770b\u7740\u5468\u747e\u73ba\u4e0e\u8c22\u610f\u8d70\u8fdc\uff0c\u5f20\u606a\u4e5f\u5fcd\u53d7\u4e0d\u4e86\u5c0f\u6811\u6797\u91cc\u66a7\u6627\u7684\u6c14\u6c1b\uff0c\u4ed3\u7687\u9003\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u90a3\u4e2a\u86c7\u874e\u5973\u4eba\u505c\u5728\u6821\u95e8\u5916\u7684\u8f66\u5df2\u7ecf\u5f00\u8d70\u4e86\uff0c\u5f20\u606a\u5e76\u6ca1\u6709\u770b\u5230\u80e1\u5b97\u5e86\u3002\u770b\u5230\u770b\u4e0d\u5230\uff0c\u4e5f\u65e0\u5173\u7d27\u8981\uff0c\u9526\u6e56\u6240\u8fdb\u884c\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e5f\u80fd\u60f3\u8c61\u5230\u7684\u963b\u529b\u90fd\u6765\u81ea\u54ea\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u7f51\u7403\u573a\u524d\u7684\u6797\u836b\u9053\u4e0a\u7ed9\u675c\u98de\u6253\u7535\u8bdd\uff0c\u95ee\u4ed6\u4eba\u5728\u54ea\u91cc\u3002</p><p>\u3000\u3000\u675c\u98de\u8499\u4e50\u4ed6\u4eec\u641e\u7684\u7f51\u5427\u8bbe\u8ba1\u4e0e\u8fd0\u8425\u65b9\u6848\u5927\u8d5b\u4e1a\u5df2\u843d\u4e0b\u5e37\u5e55\uff0c\u5438\u6536\u4e86\u524d\u4e09\u540d\u53c2\u8d5b\u4f5c\u54c1\u7684\u7279\u8d28\u3001\u9080\u8bf7\u524d\u4e09\u540d\u53c2\u8d5b\u8005\u4e00\u8d77\u53c2\u4e0e\u4e86\u6700\u7ec8\u65b9\u6848\u7684\u8bbe\u8ba1\u3002\u5f20\u606a\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u5173\u5fc3\u4e00\u4e0b\uff0c\u53ea\u6653\u5f97\u7f51\u5427\u8fd9\u4e24\u5929\u6b63\u8fdb\u884c\u7f51\u7edc\u5e03\u7ebf\u3001\u6c34\u7535\u5b89\u88c5\uff0c\u521a\u624d\u7ecf\u8fc7\u90a3\u91cc\u65f6\uff0c\u770b\u5230\u4e8c\u697c\u4eba\u5f71\u6643\u52a8\uff0c\u770b\u4f3c\u8fd8\u5728\u65bd\u5de5\uff0c\u53ea\u662f\u6ce8\u610f\u529b\u5148\u7ed9\u90a3\u4e2a\u86c7\u874e\u7f8e\u5973\u5438\u5f15\u8fc7\u6765\u3002</p><p>\u3000\u3000\u95ee\u6e05\u675c\u98de\u4e0e\u8499\u4e50\u8fd8\u5728\u90a3\u91cc\uff0c\u5f20\u606a\u8d70\u5230\u6821\u5185\u5546\u5e97\u91cc\u4e70\u4e9b\u4e00\u4e9b\u996e\u6599\u3001\u5564\u9152\u63d0\u4e86\u4e00\u5927\u515c\u8d70\u8fc7\u53bb\uff0c\u8fdb\u53bb\u770b\u7740\u4e00\u5927\u5806\u4eba\u90fd\u5728\uff0c\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u7b49\u4eba\u90fd\u5728\uff0c\u65bd\u65b0\u98de\u7b49\u51e0\u4e2a\u866b\u4ff1\u4e50\u90e8\u7684\u6210\u5458\u6b63\u62ff\u7740\u5de5\u5177\u8e72\u5728\u5730\u4e0a\u6392\u7ebf\uff0c\u771f\u662f\u7ed9\u675c\u98de\u7269\u5c3d\u5176\u7528\u4e86\u3002\u9648\u5983\u84c9\u3001\u5e2d\u82e5\u7433\u4e5f\u5728\uff0c\u770b\u5230\u7d20\u989c\u6e05\u79c0\u7684\u6c88\u7b71\uff0c\u5f20\u606a\u7b11\u7740\u95ee\u5979\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u7ed9\u675c\u98de\u62d0\u8fdb\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u6c88\u7b71\u6307\u7740\u65f6\u5b66\u658c\u8bf4\u9053\uff1a\u201c\u4ed6\u8ba9\u6211\u586b\u4e86\u4e00\u5f20\u8868\uff0c\u8bf4\u662f\u53ef\u4ee5\u52e4\u5de5\u5956\u5b66\uff0c\u5c31\u7a00\u91cc\u7cca\u6d82\u7ed9\u62c9\u4e86\u8fdb\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6c88\u7b71\u5bb6\u5883\u4e0d\u7b97\u597d\uff0c\u5e94\u8be5\u8bf4\u6797\u573a\u91cc\u7684\u666e\u901a\u804c\u5de5\u5305\u62ec\u6797\u573a\u6240\u5c5e\u4e2d\u5c0f\u5e08\u7684\u6559\u804c\u5de5\u85aa\u8d44\u90fd\u5f88\u4f4e\uff0c\u867d\u7136\u6797\u4e1a\u516c\u53f8\u96c7\u7528\u6797\u573a\u5de5\u4eba\u53c2\u4e0e\u901f\u4e30\u6797\u8425\u6797\u5efa\u8bbe\uff0c\u4f46\u662f\u4e00\u65f6\u8fd8\u65e0\u6cd5\u60e0\u53ca\u5230\u6797\u573a\u7684\u9644\u5c5e\u804c\u5de5\u3002\u5979\u7236\u4eb2\u6c88\u7ea6\u662f\u5f88\u6e05\u4fed\u7684\u4eba\uff0c\u5c31\u7b97\u989d\u5916\u5728\u6797\u573a\u5f00\u4e86\u4e00\u95f4\u68cb\u9986\uff0c\u4e5f\u662f\u4e3a\u5c3d\u5174\u81f4\uff0c\u4e0d\u80fd\u589e\u52a0\u6536\u5165\uff0c\u53cd\u800c\u4f1a\u5f80\u91cc\u57ab\u4e9b\u94b1\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u591a\u6b21\u7ecf\u8fc7\u6797\u573a\u529e\u4e8b\uff0c\u5012\u662f\u4e0e\u6c88\u7ea6\u5173\u7cfb\u719f\u7edc\u8d77\u6765\uff0c\u8fd8\u6bcf\u6b21\u90fd\u4ece\u6c88\u7ea6\u90a3\u91cc\u5f97\u4e9b\u5f62\u788e\u5374\u5e7d\u9999\u5f25\u8fdc\u7684\u5c71\u8336\u3002</p><p>\u3000\u3000\u6c88\u7b71\u5bb6\u7684\u5883\u51b5\uff0c\u8fde\u6c88\u7b71\u7684\u5b66\u8d39\u90fd\u8981\u627e\u4eba\u51d1\uff0c\u5979\u7684\u786e\u9700\u8981\u4e00\u4efd\u52e4\u5de5\u5956\u5b66\u7684\u96f6\u5de5\uff1b\u8fd9\u4e00\u70b9\u5012\u4e0d\u9700\u8981\u989d\u5916\u5429\u5490\uff0c\u76f8\u4fe1\u675c\u98de\u4e5f\u4f1a\u6709\u59a5\u5584\u7684\u5b89\u6392\u3002\u4ed6\u4eec\u7684\u521b\u57df\u6295\u8d44\u7ba1\u7406\u6709\u9650\u516c\u53f8\u4e0e\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u672c\u6765\u5c31\u662f\u4e00\u4f53\u4e24\u9762\u7684\u5173\u7cfb\uff0c\u771f\u6b63\u627f\u62c5\u56fa\u5b9a\u804c\u4f4d\u7684\u6210\u5458\u53ef\u4ee5\u9886\u4e00\u4efd\u867d\u7136\u4e0d\u7b97\u5f88\u591a\u4f46\u4e5f\u8fc7\u5f97\u53bb\u7684\u5de5\u8d44\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u88c5\u996e\u6599\u3001\u5564\u9152\u7684\u5851\u6599\u515c\u9012\u7ed9\u4ed6\u4eec\u5206\u53bb\uff0c\u62ff\u4e86\u4e00\u7f50\u5564\u9152\u4e00\u7f50\u82ac\u8fbe\uff0c\u5c06\u82ac\u8fbe\u9012\u7ed9\u9648\u5983\u84c9\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u5728\u9605\u89c8\u5ba4\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u53ef\u4ee5\u8fc7\u6765\u73a9\u554a\uff0c\u6211\u4e0d\u4f46\u8fc7\u6765\u73a9\uff0c\u521a\u624d\u8fd8\u7ad9\u5728\u7a97\u5b50\u524d\u770b\u591c\u666f\u5462\u3002\u201d\u9648\u5983\u84c9\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u539f\u6765\u9648\u5983\u84c9\u7ad9\u5728\u7a97\u524d\u770b\u5230\u81ea\u5df1\u4e0b\u8f66\u540e\u4e0e\u90a3\u4e2a\u86c7\u874e\u5973\u4eba\u8bf4\u8bdd\u7684\u60c5\u5f62\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u90a3\u662f\u4e2a\u5f88\u51f6\u608d\u7684\u5973\u4eba\uff0c\u201d\u7565\u4e3a\u89e3\u91ca\u4e86\u4e00\u4e0b\u6797\u96ea\u7684\u8eab\u4efd\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u4ee5\u524d\u8fd8\u4ee5\u4e3a\u4e00\u4e2a\u5973\u4eba\u6709\u7740\u6f02\u4eae\u7684\u5916\u8868\u5c31\u4e00\u5b9a\u4f1a\u6709\u7f8e\u4e3d\u7684\u5fc3\u7075\uff0c\u4f60\u8bf4\u90a3\u65f6\u5019\u7684\u6211\u591a\u7eaf\u6d01\uff1f\u201d</p><p>\u3000\u3000\u201c\u54af\u54af\uff0c\u4f60\u4ee5\u4e3a\u8bf4\u8fd9\u4e9b\u6211\u5c31\u4f1a\u4fe1\u5417\uff1f\u201d\u9648\u5983\u84c9\u62ac\u8d77\u773c\u5e18\u770b\u4e86\u4e00\u773c\u5929\u82b1\u540a\u4e0b\u6765\u7684\u4e00\u53ea\u767d\u70bd\u5ae3\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u597d\u7eaf\u6d01\u554a\uff01\u90fd\u5fd8\u4e86\u544a\u8bc9\u4f60\uff0c\u5bbf\u820d\u697c\u524d\u7684\u6c14\u7403\u6211\u53c8\u7ed9\u6362\u4e86\u4e03\u53ea\u2026\u2026\u201d</p><p>\u3000\u3000\u6781\u5c11\u89c1\u9648\u5983\u84c9\u4fcf\u76ae\u800c\u7b11\u7684\u6a21\u6837\uff0c\u662f\u90a3\u79cd\u80fd\u6c81\u4eba\u4eba\u5fc3\u813e\u7684\u8ff7\u4eba\u6c14\u606f\uff0c\u5f20\u606a\u644a\u7740\u624b\u505a\u51fa\u65e0\u53ef\u5948\u4f55\u7684\u8868\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8ba9\u4f60\u53d7\u5230\u5c0f\u5a67\u7684\u59d4\u6258\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u4e0e\u8499\u4e50\u62ff\u7740\u6700\u7ec8\u5b9a\u7a3f\u7684\u7f51\u7edc\u8bbe\u8ba1\u4e0e\u8fd0\u8425\u65b9\u6848\u8d70\u8fc7\u6765\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u5dee\u70b9\u5fd8\u4e86\u81ea\u5df1\u8fd8\u662f\u521b\u534f\u7684\u4e09\u5927\u7406\u4e8b\u4e4b\u4e00\u2026\u2026\u201d\u8f6c\u5934\u5bf9\u9648\u5983\u84c9\u8bf4\uff0c\u201c\u8981\u4e0d\u4f60\u4ee3\u6211\u884c\u4f7f\u534f\u4f1a\u7406\u4e8b\u7684\u804c\u8d23\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u6ca1\u6709\u90a3\u95f2\u5de5\u592b\u5462\u2026\u2026\u201d\u9648\u5983\u84c9\u5634\u91cc\u8bf4\u7740\uff0c\u5c0f\u5de7\u79c0\u7f8e\u7684\u5934\u9885\u8fd8\u662f\u51d1\u8fc7\u6765\u4e00\u8d77\u770b\u6700\u7ec8\u7684\u65b9\u6848\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u4e00\u4e2a\u6708\u4e4b\u524d\u675c\u98de\u4e0e\u8499\u4e50\u4e24\u4eba\u6363\u817e\u51fa\u6765\u7684\u8fd8\u663e\u5ae9\u6da9\u7684\u65b9\u6848\uff0c\u8fd9\u4efd\u51dd\u805a\u4e86\u8bb8\u591a\u5927\u5b66\u751f\u521b\u601d\u7684\u6700\u7ec8\u65b9\u6848\uff0c\u5df2\u7ecf\u7b97\u5f97\u4e0a\u76f8\u5f53\u5b8c\u7f8e\u4e86\uff0c\u5f20\u606a\u5e76\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u80fd\u5236\u5b9a\u51fa\u4e00\u4efd\u66f4\u65e0\u7455\u7684\u65b9\u6848\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u7b26\u5408\u4e5d\u4e03\u5e74\u4ee3\u7279\u8d28\u7684\u65b0\u6982\u5ff5\u7f51\u5427\u5c31\u901a\u8fc7\u8fd9\u79cd\u5f62\u5f0f\u7ed9\u50ac\u751f\u51fa\u6765\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4efd\u65b9\u6848\u63d0\u51fa\u660e\u786e\u7684\u5206\u533a\u7ecf\u8425\u6982\u5ff5\uff0c\u4e5f\u5bf9\u521b\u57df\u7f51\u5427\u672a\u6765\u7684\u4f1a\u5458\u8fdb\u884c\u5206\u5c42\u6536\u8d39\u3002\u8d35\u5bbe\u533a\u3001\u5546\u52a1\u533a\uff0c\u6e38\u620f\u533a\u4e0e\u666e\u901a\u533a\u5206\u79bb\uff1b\u8fd8\u589e\u8bbe\u4e86\u6d3b\u52a8\u4e13\u533a\uff0c\u6765\u5438\u5f15\u7535\u5b50\u5382\u5546\u6765\u7f51\u5427\u4e3e\u884c\u5404\u7c7b\u5ba3\u4f20\u6d3b\u52a8\uff1b\u7279\u8bbe\u4e86\u5973\u751f\u4e13\u533a\u4e0e\u76f8\u5e94\u7684\u5973\u751f\u5361\u2014\u2014\u9700\u6301\u5973\u751f\u5361\u624d\u53ef\u4ee5\u5230\u5973\u751f\u4e13\u533a\u4e0a\u7f51\uff0c\u8fd8\u7ed9\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u8bbe\u7acb\u4e86\u4e00\u4e2a\u4e13\u5c5e\u7684\u5de5\u4f5c\u533a\u57df\u3002\u4f11\u606f\u533a\u7684\u6982\u5ff5\u5f97\u5230\u6781\u5927\u7684\u52a0\u5f3a\uff0c\u770b\u8fc7\u8bbe\u8ba1\u56fe\uff0c\u4f11\u606f\u533a\u4e0e\u5427\u53f0\u878d\u4e3a\u4e00\u4f53\uff0c\u62e5\u6709\u4f11\u95f2\u5496\u5561\u5427\u3001\u9152\u5427\u7684\u7279\u8d28\uff0c\u53ef\u4ee5\u7ed9\u5360\u4e0d\u5230\u673a\u4f4d\u7684\u5b66\u751f\u5728\u6b64\u4f11\u95f2\u6d88\u8d39\u3002\u5728\u5e7f\u544a\u4f4d\u7684\u8bbe\u8ba1\u4e0a\uff0c\u66f4\u662f\u4f53\u73b0\u51fa\u4e1c\u5927\u5b66\u751f\u7684\u5947\u601d\u5999\u60f3\uff0c\u5f53\u7136\uff0c\u9080\u8bf7\u7535\u5b50\u5382\u5546\u5728\u6d3b\u52a8\u4e13\u533a\u8fdb\u884c\u6d3b\u52a8\u5ba3\u4f20\u5c06\u662f\u6700\u5927\u5e7f\u544a\u6536\u5165\u70b9\uff0c\u6b64\u5904\u8fd8\u589e\u8bbe\u4e86\u6587\u5370\u670d\u52a1\u2014\u2014\u8fd9\u662f\u4e5d\u4e03\u5e74\uff0c\u4e1c\u5927\u6821\u5185\u6587\u5370\u793e\u590d\u5370\u4e00\u5f20A4\u7eb8\u90fd\u8981\u6536\u4e00\u5757\u4e94\u6bdb\u94b1\u3002</p><p>\u3000\u3000\u7f51\u6821\u6982\u5ff5\u5728\u65b9\u6848\u91cc\u5f97\u5230\u91cd\u70b9\u7684\u4f53\u73b0\uff0c\u675c\u98de\u4ed6\u4eec\u4e5f\u610f\u8bc6\u5230\u7f51\u6821\u5c06\u662f\u4ed6\u4eec\u6700\u91cd\u8981\u7684\u4e00\u5757\u6536\u5165\u6765\u6e90\uff0c\u7279\u522b\u662f\u4e5d\u5341\u5e74\u4ee3\u672b\u671f\uff0c\u8ba1\u7b97\u673a\u64cd\u4f5c\u53ca\u6280\u80fd\u7684\u57f9\u8bad\u9700\u6c42\u6781\u5927\uff0c\u521b\u57df\u7f51\u53f0\u53ef\u4ee5\u4f9d\u8d56\u4e1c\u5927\u7684\u8d44\u6e90\u5c06\u7f51\u6821\u505a\u5927\uff0c\u8fd8\u53ef\u4ee5\u6df1\u5165\u7684\u505a\u8f6f\u786c\u4ef6\u6280\u80fd\u57f9\u8bad\uff0c\u7b97\u662f\u4e3a\u6a61\u6811\u56ed\u8ba1\u5212\u505a\u8d21\u732e\u3002</p><p>\u3000\u3000\u675c\u98de\u4ed6\u4eec\u8fd8\u4e0e\u5b66\u6821\u8fdb\u884c\u4ea4\u6d89\uff0c\u4e1c\u5927BBS\u5c06\u67b6\u8bbe\u5728\u7f51\u5427\u7684\u670d\u52a1\u5668\u4e0a\uff0c\u7531\u521b\u57df\u7f51\u5427\u4ee3\u4e3a\u7ef4\u62a4\uff0c\u4e1c\u5927\u6bcf\u5e74\u652f\u4ed8\u4e00\u5b9a\u7684\u8d39\u7528\u7ed9\u521b\u57df\u7f51\u5427\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u589e\u8bbe\u4e86\u4e00\u53f0\u6e38\u620f\u670d\u52a1\u5668\u3001\u4e00\u53f0\u7535\u5f71\u670d\u52a1\u5668\u6765\u6ee1\u8db3\u4e0a\u7f51\u7528\u6237\u7684\u4e0d\u540c\u9700\u6c42\uff0c\u8fd9\u4e24\u70b9\u90fd\u662f\u5f20\u606a\u559c\u6b22\uff0c\u8054\u7f51\u6253\u661f\u9645\u6216\u8005\u7ec4\u4e2a\u6697\u9ed1\u7834\u574f\u795e\u7684\u5c40\u57df\u6218\u7f51\uff0c\u6216\u8005\u5750\u5728\u8d35\u5bbe\u533a\u5b89\u9759\u7684\u770b\u7535\u5f71\uff0c\u90fd\u662f\u4e0d\u9519\u7684\u4eab\u53d7\u3002</p><p>\u3000\u3000\u8fd9\u5e74\u5934\uff0c\u4e0a\u7f51\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u8fd8\u662f\u9887\u4e3a\u65e0\u804a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5404\u4e2a\u5206\u533a\u7684\u88c5\u6f62\u98ce\u683c\u4e0e\u6863\u6b21\u90fd\u6709\u5dee\u5f02\uff0c\u8fd9\u4efd\u65b9\u6848\u5bfc\u81f4\u9884\u7b97\u7531\u6700\u521d\u7684\u56db\u767e\u4e07\u98da\u5347\u5230\u4e94\u767e\u4e8c\u5341\u4e07\uff0c\u673a\u53f0\u6570\u5374\u4ece\u539f\u6765\u56db\u767e\u53f0\u964d\u4f4e\u5230\u4e09\u767e\u516d\u5341\u53f0\u3002</p><p>\u3000\u3000\u901a\u8fc7\u8fd9\u4efd\u65b9\u6848\u5c31\u80fd\u5145\u5206\u7684\u8ba4\u8bc6\u5230\u5927\u5b66\u751f\u7684\u521b\u601d\u80fd\u529b\uff0c\u770b\u6765\u641e\u8fd9\u4e2a\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u5e76\u975e\u767d\u8d39\u5fc3\u673a\uff0c\u5f20\u606a\u5c06\u65b9\u6848\u7c97\u7565\u7684\u770b\u4e86\u4e00\u904d\uff0c\u4f3c\u4e4e\u4e5f\u65e0\u6cd5\u63d0\u51fa\u66f4\u597d\u7684\u610f\u89c1\uff0c\u7b11\u7740\u5bf9\u675c\u98de\u3001\u8499\u4e50\u8bf4\uff1a\u201c\u8fd9\u4e48\u591a\u7684\u8111\u5b50\uff0c\u603b\u6bd4\u4f60\u4eec\u4e24\u4e2a\u4eba\u7684\u8111\u5b50\u597d\u4f7f\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u771f\u6b63\u5177\u4f53\u800c\u5fae\u7684\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u611f\u89e6\u662f\u6781\u4e3a\u4e0d\u540c\u7684\uff0c\u675c\u98de\u4e5f\u6e10\u6e10\u80fd\u6478\u7d22\u51fa\u65b9\u5411\u6240\u5728\u3002\u521b\u534f\u6210\u7acb\u524d\u540e\u662f\u622a\u7136\u4e0d\u540c\u7684\u4e24\u79cd\u683c\u5c40\uff0c\u4ec5\u4ec5\u8981\u8ba4\u8bc6\u4f1a\u51fa\u73b0\u622a\u7136\u4e0d\u540c\u7684\u5dee\u8ddd\u80cc\u540e\u7684\u6839\u6e90\uff0c\u8fd9\u4e9b\u90fd\u8db3\u4ee5\u8ba9\u4ed6\u601d\u8003\u597d\u4e45\u4e86\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u5feb\u5230\u5b66\u751f\u5bbf\u820d\u7184\u706f\u7684\u65f6\u95f4\uff0c\u9648\u5983\u84c9\u4e0e\u5e2d\u82e5\u7433\u3001\u6c88\u7b71\u8fd9\u4e9b\u5973\u5b69\u5b50\u8fd8\u6709\u4e00\u4e9b\u534f\u4f1a\u7684\u666e\u901a\u6210\u5458\u90fd\u5148\u56de\u5bbf\u820d\u4e86\u3002\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u4ed6\u4eec\u968f\u4fbf\u6253\u4e2a\u5730\u94fa\u5c31\u80fd\u7761\u4e0b\u3002\u90fd\u662f\u5927\u4e09\u7684\u5b66\u751f\uff0c\u90fd\u6210\u8001\u6cb9\u5b50\u4e86\uff0c\u4e5f\u6562\u4e0d\u56de\u5bbf\u820d\u7761\u89c9\u3002\u7814\u7a76\u751f\u516c\u5bd3\u6ca1\u6709\u4ec0\u4e48\u7ea6\u675f\uff0c\u4e00\u65f6\u4e5f\u4e0d\u6025\u7740\u56de\u53bb\u7761\u89c9\uff0c\u5f20\u606a\u4fbf\u7559\u4e0b\u6765\u62ff\u4e86\u4e00\u5757\u7eb8\u677f\u57ab\u5728\u5c41\u80a1\u4e0b\u6765\u4e0e\u4ed6\u4eec\u80e1\u4e71\u626f\u6de1\uff0c\u542c\u4ed6\u4eec\u8bf4\u8fd9\u4e9b\u5929\u7b79\u5907\u521b\u57df\u7f51\u5427\u7684\u8f9b\u82e6\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u662f\u6cd5\u5b66\u9662\u7684\u5927\u4e09\u5b66\u751f\uff0c\u5374\u6709\u7740\u6781\u6613\u8bf4\u670d\u4ed6\u4eba\u7684\u516c\u5173\u5929\u8d4b\uff0c\u5c06\u5e08\u5927\u3001\u97f3\u4e50\u5b66\u9662\u7684\u6f02\u4eae\u5973\u751f\u7cca\u5f04\u8fdb\u4e1c\u5927\u7684\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u4e5f\u5c31\u7f62\u4e86\uff0c\u5728\u4e00\u5207\u90fd\u6ca1\u6709\u542f\u52a8\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4e3a\u521b\u57df\u7f51\u5427\u9f20\u6807\u57ab\u3001\u676f\u57ab\u7b49\u5e7f\u544a\u4f4d\u627e\u5230\u4e70\u4e3b\uff0c\u8fd8\u7ed9\u521b\u57df\u7f51\u5427\u62c9\u5230\u4e24\u53f0\u4e8c\u5341\u56db\u82f1\u5bf8\u5d4c\u5728\u4e00\u4e8c\u5c42\u5927\u5385\u8fce\u95e8\u7acb\u67f1\u4e0a\u7684\u7b49\u79bb\u5b50\u5c4f\u8d5e\u52a9\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u65e0\u8bba\u662f\u5927\u5c3a\u5bf8\u6db2\u6676\u8fd8\u662f\u7b49\u79bb\u5b50\u5c4f\u90fd\u662f\u5962\u4f88\u7269\uff0c\u5c31\u8fde\u90b5\u81f3\u521a\u7ed9\u81ea\u5df1\u51c6\u5907\u7684\u9752\u5e74\u516c\u5bd3\u5957\u623f\u91cc\u8981\u88c5\u4e24\u5757\u7b49\u79bb\u5b50\u5c4f\u5728\u6d74\u5ba4\u90fd\u4f1a\u89c9\u5f97\u592a\u5962\u4f88\uff0c\u5f20\u606a\u8fd8\u7279\u610f\u8ba9\u90b5\u81f3\u521a\u53bb\u6389\u4e00\u5757\uff0c\u5728\u6d74\u7f38\u5bf9\u9762\u88c5\u4e00\u5757\u7b49\u79bb\u5b50\u5c4f\u4e5f\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u80fd\u4e00\u4e0b\u5b50\u62c9\u5230\u4e24\u5757\u7b49\u79bb\u5b50\u5c4f\u7684\u8d5e\u52a9\uff0c\u4e0d\u6653\u5f97\u4ed6\u662f\u600e\u4e48\u9f13\u52a8\u4ed6\u7684\u4e09\u5bf8\u4e0d\u70c2\u4e4b\u820c\u8bf4\u670d\u7684\uff0c\u4ee3\u4ef7\u4ec5\u4ec5\u662f\u5728\u7b49\u79bb\u5b50\u5c4f\u4e0a\u64ad\u653e\u8d5e\u52a9\u5382\u5546\u7684\u5b66\u4e60\u673a\u5e7f\u544a\u4e00\u5e74\u65f6\u95f4\u3002\u5173\u952e\u7b49\u79bb\u5b50\u5c4f\u4e00\u5929\u4e8c\u5341\u56db\u5c0f\u65f6\u53ef\u4ee5\u6eda\u52a8\u591a\u6761\u5e7f\u544a\uff0c\u7b80\u76f4\u5c31\u662f\u4e3a\u521b\u57df\u7f51\u5427\u5f00\u53d1\u51fa\u4e00\u4e2a\u6781\u6709\u4ef7\u503c\u7684\u5e7f\u544a\u4f4d\uff0c\u5e7f\u64ad\u7ec8\u7aef\u53ea\u8981\u518d\u589e\u52a0\u4e00\u53f0\u8d85\u8584VCD\u789f\u673a\u3001\u5c06\u591a\u6761\u5e7f\u544a\u5236\u4f5c\u6210\u789f\u7247\u6301\u7eed\u4e0d\u505c\u7684\u64ad\u653e\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u8463\u8dc3\u534e\u7684\u6d3b\u52a8\u7ec4\u7ec7\u80fd\u529b\u6781\u5f3a\uff0c\u7f51\u7edc\u8bbe\u8ba1\u4e0e\u8fd0\u8425\u65b9\u6848\u5927\u8d5b\uff0c\u57fa\u672c\u90fd\u662f\u4ed6\u5728\u534f\u52a9\u8499\u4e50\uff0c\u5f20\u606a\u6ca1\u6709\u65f6\u95f4\u53c2\u4e0e\u5927\u8d5b\u6d3b\u52a8\uff0c\u4ec5\u4ece\u624b\u91cc\u7684\u8fd9\u4efd\u6700\u7ec8\u65b9\u6848\uff0c\u5c31\u6653\u5f97\u5927\u8d5b\u4e3e\u5f97\u975e\u5e38\u6210\u529f\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4eba\u679c\u7136\u6ca1\u6709\u8ba9\u81ea\u5df1\u5931\u671b\uff0c\u4e4b\u524d\u53ea\u662f\u7f3a\u4e4f\u6210\u957f\u7684\u571f\u58e4\u7f62\u4e86\uff0c\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u9526\u6e56\u603b\u662f\u65f6\u65f6\u9700\u8981\u65b0\u9c9c\u8840\u6db2\u7684\uff0c\u8fd9\u4e5f\u7b97\u662f\u4e3a\u672a\u6765\u7684\u9526\u6e56\u9020\u8840\u3002</p></article>"}}}]);
