(window.webpackJsonp=window.webpackJsonp||[]).push([[661],{684:function(p){p.exports={chapterTitle:null,title:"\u7b2c506\u7ae0 \u7eaa\u5ff5\u738b\u5c0f\u6ce2",content:"<article><h2>\u7b2c506\u7ae0 \u7eaa\u5ff5\u738b\u5c0f\u6ce2</h2><p>\u3000\u3000\u6b64\u65f6\u7684\u6606\u817e\u5728\u7ebf\u8fd8\u53ea\u662f\u81f4\u529b\u4e8e\u63a8\u51fa\u7b2c\u4e00\u4ee3\u7684\u4e2d\u6587\u641c\u7d22\u5f15\u64ce\u3001\u63d0\u4f9b\u7535\u5b50\u90ae\u4ef6\u7b49\u514d\u8d39\u7f51\u7edc\u670d\u52a1\uff0c\u81f4\u529b\u4e8e\u5efa\u8bbe\u6210\u4e3a\u4e00\u4e2a\u5927\u578b\u7684\u4e2d\u6587\u7f51\u7edc\u865a\u62df\u793e\u533a\u3002</p><p>\u3000\u3000\u76ee\u524d\u6606\u817e\u5728\u7ebf\u5f00\u8bbe8\u4e2a\u8bba\u575b\u30012\u4e2a\u7f51\u520a\u30013\u4e2a\u7279\u8272\u680f\u76ee\u30011\u4e2a\u804a\u5929\u5ba4\uff0c\u5176\u4e2d\u7684\u4f53\u80b2\u6c99\u9f99\u4e5f\u662f\u6881\u6587\u6c5f\u3001\u9a6c\u5411\u4e1c\u4ed6\u4eec\u5728\u83b7\u5f97\u9526\u6e56\u6ce8\u8d44\u4e4b\u524d\u4e3b\u8981\u7ecf\u8425\u7684\u680f\u76ee\uff0c\u8fd9\u65f6\u5019\u5df2\u7ecf\u6210\u4e3a\u4e00\u4e2a\u77e5\u540d\u5ea6\u548c\u8bbf\u95ee\u91cf\u90fd\u76f8\u5f53\u53ef\u89c2\u7684\u4e2d\u6587\u7f51\u7ad9\uff0c\u5728\u4f53\u80b2\u8ff7\u548c\u4f53\u80b2\u5a92\u4f53\u8bb0\u8005\u7b49\u4eba\u7fa4\u4e2d\u4eab\u53d7\u5f88\u9ad8\u7684\u58f0\u8a89\u3002</p><p>\u3000\u3000\u5f53\u7136\u516b\u4e07\u4eba\u6b21\u7684\u65e5\u9875\u9762\u8bbf\u95ee\u91cf\uff0c\u62ff\u5341\u5e74\u4e4b\u540e\u76ee\u5149\u6765\u770b\u8fd8\u6709\u4e9b\u513f\u620f\uff0c\u4f46\u8fd9\u662f\u4e92\u8054\u7f51\u5728\u56fd\u5185\u8fd8\u521a\u521a\u8d77\u6b65\u7684\u4e5d\u4e03\u5e74\u4e8c\u6708\u3002</p><p>\u3000\u3000\u5510\u5a67\u8ddf\u7740\u5f20\u606a\u5f88\u65e9\u5c31\u63a5\u89e6\u4e92\u8054\u7f51\uff0c\u4e5f\u7b97\u662f\u56fd\u5185\u6700\u65e9\u7684\u90a3\u4e00\u6279\u7f51\u6c11\uff0c\u5e73\u65f6\u53ea\u63a5\u89e6\u5230\u8ba1\u7b97\u673a\u5c4f\u5e55\u6253\u5f00\u7684\u4e00\u5e45\u5e45\u7f51\u9875\uff0c\u53ef\u4e0d\u77e5\u9053\u8fd9\u7f51\u9875\u80cc\u540e\u85cf\u7740\u4ec0\u4e48\u4e1c\u897f\uff0c\u6587\u79d1\u751f\uff0c\u5bf9\u6280\u672f\u5c42\u6b21\u7684\u4e1c\u897f\uff0c\u603b\u6709\u4e00\u79cd\u81ea\u7136\u5d07\u62dc\u7684\u76ee\u5149\uff0c\u50cf\u738b\u5f69\u73b2\u8fd9\u6837\u957f\u76f8\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u8111\u5b50\u91cc\u7adf\u7136\u7cbe\u901a\u8ba1\u7b97\u673a\u903b\u8f91\u4ee3\u7801\u4e4b\u7c7b\u7684\u4e13\u4e1a\u77e5\u8bc6\u5728\u5510\u5a67\u770b\u6765\u7b80\u76f4\u5c31\u8981\u7b97\u5947\u8ff9\u4e86\uff1b\u5f20\u606a\u66f4\u89c9\u5f97\u5317\u90ae\u5973\u751f\u3001\u53c8\u7cbe\u901a\u8ba1\u7b97\u673a\u7f16\u7a0b\uff0c\u8fd8\u80fd\u6709\u51e0\u5206\u59ff\u8272\uff0c\u8fd8\u7ed9\u6709\u4e9b\u8fc2\u6c14\u7684\u8868\u54e5\u6881\u6587\u6c5f\u6ce1\u5230\u624b\uff0c\u90a3\u624d\u8981\u7b97\u5947\u8ff9\u5462\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5c31\u5728\u516c\u53f8\u7684\u529e\u516c\u5ba4\u91cc\u5403\u76d2\u996d\uff0c\u672c\u6765\u503c\u73ed\u7684\u4eba\u624b\u5c31\u5c11\uff0c\u9664\u4e86\u9a6c\u5411\u4e1c\u4e0e\u4e24\u540d\u6280\u672f\u4eba\u5458\uff0c\u8fd8\u6709\u5c31\u662f\u4e24\u540d\u7f51\u7ad9\u5185\u5bb9\u7f16\u8f91\u503c\u73ed\uff0c\u90fd\u662f\u8981\u503c\u6ee1\u5341\u4e8c\u5c0f\u65f6\u7684\u73ed\u4e4b\u540e\u518d\u8f6e\u6362\u3002</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u5f20\u606a\u4fbf\u4e0d\u5728\u516c\u53f8\u6ede\u7559\uff0c\u6881\u6587\u6c5f\u4e0b\u5348\u8981\u9876\u66ff\u9a6c\u5411\u4e1c\u5728\u516c\u53f8\u503c\u73ed\uff0c\u4ed6\u4e0e\u5973\u53cb\u738b\u5f69\u73b2\u5c31\u4e0d\u518d\u53bb\u5f90\u5b66\u5e73\u5bb6\u91cc\u505a\u5ba2\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u79bb\u5f00\u516c\u5f00\u4e0a\u8f66\u4e4b\u524d\uff0c\u4ed6\u5bf9\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u8bf4\u9053\uff1a\u201c\u6211\u770b\u8fc7\u4e00\u4e9b\u4e13\u4e1a\u65b9\u9762\u7684\u8d44\u6599\uff0c\u6606\u817e\u5728\u7ebf\u7684\u9996\u9875\u8981\u6210\u4e3a\u95e8\u6237\u4e2d\u8f6c\u7f51\u7ad9\uff0c\u5728\u6d88\u606f\u65f6\u6548\u6027\u65b9\u9762\uff0c\u5728\u4fe1\u606f\u91cf\u3001\u5b9e\u65f6\u52a8\u6001\u5237\u65b0\u65b9\u9762\uff0c\u5728\u641c\u7d22\u5f15\u64ce\u9875\u9762\u53ca\u8bba\u575b\u7d22\u5f15\u9875\u9762\u4e0e\u9996\u9875\u6574\u5408\u65b9\u9762\uff0c\u90fd\u9700\u8981\u5927\u5e45\u5ea6\u7684\u52a0\u5f3a\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8c08\u5230\u4ed6\u5bf9\u6606\u817e\u5728\u7ebf\u7684\u8fdc\u666f\u671f\u8bb8\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u81ea\u7136\u8981\u8ddf\u5f20\u606a\u8c08\u73b0\u65f6\u7684\u56f0\u5883\u3002</p><p>\u3000\u3000\u201c\u7f3a\u94b1\u554a\uff0c\u201d\u9a6c\u5411\u4e1c\u8bf4\u9053\uff0c\u201c\u6606\u817e\u7684\u670d\u52a1\u5668\u8bbe\u7f6e\u5728\u7f8e\u56fd\uff0c\u56fd\u5185\u7f51\u6c11\u8fde\u5165\u9875\u9762\u7684\u65f6\u95f4\u6bd4\u8f83\u957f\uff0c\u6211\u60f3\u81f3\u5c11\u8981\u5148\u628a\u6211\u4eec\u7684\u4f53\u80b2\u6c99\u9f99\u5148\u505a\u6210\u56fd\u5185\u6700\u5927\u7684\u4f53\u80b2\u7f51\u7ad9\uff0c\u786c\u4ef6\u65b9\u9762\u7684\u6295\u8d44\u5fc5\u4e0d\u53ef\u5c11\uff0c\u6211\u4eec\u8ba1\u7b97\u8fc7\uff0c\u53ea\u8981\u5f00\u901a\u56fd\u5185\u7684\u955c\u50cf\u670d\u52a1\u5668\uff0c\u8fde\u5165\u955c\u50cf\u7ad9\u9875\u9762\u7684\u901f\u5ea6\u81f3\u5c11\u80fd\u63d0\u9ad8\u4e00\u500d\u2026\u2026\u53e6\u5916\u5185\u5bb9\u7f16\u8f91\u3001\u4fe1\u606f\u91c7\u96c6\u3001\u7f51\u7ad9\u7ef4\u62a4\uff0c\u90fd\u9700\u8981\u589e\u52a0\u4eba\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7f3a\u94b1\u554a\uff1f\u201d\u5f20\u606a\u534a\u53ea\u811a\u90fd\u8e0f\u8fdb\u8f66\u91cc\uff0c\u5934\u626d\u8fc7\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u53ef\u4ee5\u5411\u6606\u817e\u5728\u7ebf\u518d\u6b21\u6ce8\u8d44\u4e24\u5343\u4e07\uff0c\u5c06\u516c\u53f8\u5347\u7ea7\u4e3a\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u7231\u8fbe\u7535\u5b50\u3001\u65b0\u5149\u7eb8\u4e1a\u4e5f\u4f1a\u8003\u8651\u5728\u6606\u817e\u5728\u7ebf\u6295\u653e\u5e7f\u544a\u3002\u4f60\u4eec\u4e5f\u6e05\u695a\uff0c\u8fd9\u4e00\u9636\u6bb5\uff0c\u7f51\u7ad9\u5e7f\u544a\u7684\u6536\u5165\u5f88\u6709\u9650\uff0c\u8fd9\u4e24\u5343\u4e07\u82b1\u5b8c\uff0c\u4f60\u4eec\u5c31\u8981\u8003\u8651\u81ea\u5df1\u51fa\u53bb\u627e\u94b1\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e24\u5343\u4e07\u554a\uff1f\u201d\u9a6c\u5411\u4e1c\u6320\u6320\u8111\u95e8\uff0c\u201c\u7f51\u7ad9\u67b6\u6784\u521d\u6b65\u5b8c\u6210\uff0c\u8fd8\u9700\u8981\u6301\u7eed\u7684\u5b8c\u5584\uff0c\u8fd9\u4e9b\u94b1\u5dee\u4e0d\u591a\u80fd\u575a\u6301\u4e00\u5e74\u7684\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u8ddf\u4e92\u8054\u7f51\u642d\u8fb9\u7684\u4ec0\u4e48\u4e1c\u897f\u90fd\u6602\u8d35\uff0c\u786c\u4ef6\u8bbe\u5907\u6602\u8d35\u3001\u670d\u52a1\u5668\u79df\u7528\u6602\u8d35\u3001\u6280\u672f\u4eba\u5458\u85aa\u8d44\u6602\u8d35\u2026\u2026\u6295\u8d44\u4e92\u8054\u7f51\u8ddf\u70e7\u94b1\u65e0\u5f02\u3002</p><p>\u3000\u3000\u8fd9\u8fd8\u53ea\u662f\u73b0\u6709\u7f51\u7ad9\u67b6\u6784\u4e0b\u7684\u8fd0\u8425\uff0c\u8981\u8fbe\u5230\u5f20\u606a\u7684\u8981\u6c42\u505a\u6210\u95e8\u6237\u4e2d\u8f6c\u7f51\u7ad9\uff0c\u8fd8\u8981\u8fdb\u4e00\u6b65\u7684\u5347\u7ea7\u4e0e\u4eba\u5458\u6269\u7f16\uff0c\u8d39\u7528\u4f1a\u5448\u51e0\u4f55\u7ea7\u6570\u7684\u589e\u52a0\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u5f88\u62c5\u5fc3\u5f20\u606a\u65e0\u6cd5\u7406\u89e3\uff0c\u8bf4\u8bdd\u7684\u53e3\u6c14\u5f88\u5f31\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u6ca1\u89c1\u8bc6\u8fc7\u6b27\u7f8e\u7f51\u7ad9\u8fd0\u8425\u5546\u70e7\u94b1\u89c4\u6a21\u7684\u6295\u8d44\u8005\uff0c\u5f88\u96be\u7406\u89e3\u5927\u578b\u7f51\u7ad9\u8fd0\u8425\u7684\u6210\u672c\uff1b\u5373\u4f7f\u5341\u5e74\u4e4b\u540e\u7684\u67d0\u4e9b\u4e13\u4e1a\u9662\u6821\u6bd5\u4e1a\u51fa\u6765\u7684\u5927\u5b66\u751f\uff0c\u751a\u81f3\u90fd\u4ee5\u4e3a\u53ea\u8981\u5728\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\u5f80\u65b0\u6d6a\u3001\u641c\u72d0\u968f\u4fbf\u6295\u8d44\u4e2a\u767e\u5341\u4e07\u7b49\u7740\u7eb3\u65af\u8fbe\u514b\u4e00\u4e0a\u5e02\u5c31\u53d8\u51fa\u5341\u4ebf\u516b\u4ebf\u7684\u66b4\u5229\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9a6c\u5411\u4e1c\u5fc3\u865a\u7684\u6a21\u6837\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4e24\u5343\u4e07\u7684\u76ee\u6807\u662f\u9875\u9762\u8bbf\u95ee\u91cf\u8fbe\u5230\u5e73\u5747\u6bcf\u65e5\u767e\u4e07\u4eba\u6b21\uff0c\u6211\u60f3\u8fbe\u5230\u8fd9\u4e2a\u76ee\u6807\uff0c\u6606\u817e\u5728\u7ebf\u5728\u4e1a\u5185\u5c31\u5c0f\u6709\u5f71\u54cd\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5c0f\u6709\u5f71\u54cd\u624d\u6709\u83b7\u5f97\u6d77\u5916\u98ce\u6295\u6216\u79c1\u52df\u6ce8\u8d44\u7684\u53ef\u80fd\uff0c\u90a3\u4e5f\u53ea\u5411\u5927\u578b\u95e8\u6237\u7f51\u7ad9\u8fc8\u51fa\u7b2c\u4e00\u6b65\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u7f51\u7ad9\u8fd0\u8425\u8005\u6765\u8bf4\uff0c\u6709\u4e00\u4e2a\u76ee\u5149\u8fdc\u5927\u3001\u5934\u8111\u6e05\u6670\u3001\u76ee\u6807\u660e\u786e\u7684\u6295\u8d44\u4eba\uff0c\u65e0\u7591\u662f\u4ef6\u5f88\u5e78\u7684\u4e8b\u60c5\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u9a6c\u5411\u4e1c\u5fc3\u91cc\u5c31\u6709\u4e86\u5e95\uff0c\u4e5f\u4e0d\u7528\u591a\u89e3\u91ca\u4ec0\u4e48\u4e86\uff0c\u9001\u5f20\u606a\u4e0e\u5510\u5a67\u4e0a\u8f66\u79bb\u5f00\u3002</p><p>\u3000\u3000\u770b\u7740\u5e26\u7740\u56fd\u52a1\u9662\u90e8\u95e8\u8f66\u724c\u7684\u5927\u6392\u91cf\u9ed1\u8272\u5965\u8fea\u7edd\u5c18\u800c\u53bb\uff0c\u9a6c\u5411\u4e1c\u5f00\u73a9\u7b11\u95ee\u6881\u6587\u6c5f\uff1a\u201c\u4f60\u786e\u5b9a\u4f60\u5c0f\u59e8\u592b\u4e0d\u662f\u67d0\u4e2a\u56fd\u5bb6\u526f\u4e3b\u5e2d\u6216\u526f\u603b\u7406\u7684\u79c1\u751f\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u60f3\u554a\uff0c\u201d\u6881\u6587\u6c5f\u4ece\u6765\u90fd\u770b\u4e0d\u900f\u8fd9\u4e2a\u6bd4\u8f83\u5c0f\u56db\u5c81\u7684\u8868\u5f1f\uff0c\u4ed6\u505a\u6210\u7684\u4e8b\u60c5\uff0c\u8bf4\u51fa\u53bb\u90fd\u6ca1\u6709\u4fe1\uff0c\u201c\u53ef\u662f\u8981\u6709\u4eba\u4fe1\u554a\u3002\u201d</p><p>\u3000\u3000\u738b\u5f69\u73b2\u5410\u5410\u820c\u5934\uff0c\u6402\u7740\u6881\u6587\u6c5f\u7684\u80f3\u818a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u89c1\u8fc7\u771f\u4eba\uff0c\u7684\u786e\u5f88\u96be\u8ba9\u4eba\u7f6e\u4fe1\u554a\u3002\u201d</p><p>\u3000\u3000\u6881\u6587\u6c5f\u4e4b\u524d\u8ddf\u5973\u53cb\u8bf4\u8fc7\u4e00\u4e9b\u9526\u6e56\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u7684\u786e\u5f88\u96be\u8ba9\u4eba\u76f8\u4fe1\uff0c\u5c31\u7b97\u6606\u817e\u5728\u7ebf\u4e5d\u516d\u5e74\u5e74\u4e2d\u83b7\u5f97\u9526\u6e56\u4e94\u767e\u4e07\u6ce8\u8d44\u7684\u4e8b\u5b9e\u9762\u524d\uff0c\u738b\u5f69\u73b2\u4e5f\u4e0d\u5927\u76f8\u4fe1\u8fd9\u4e48\u5927\u7684\u4f01\u4e1a\u4f1a\u662f\u4e00\u4e2a\u4e0d\u6ee1\u4e8c\u5341\u5c81\u7684\u5c11\u5e74\u6363\u817e\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u867d\u7136\u7f8e\u56fd\u51fa\u4e86\u4e00\u4e2a\u6bd4\u5c14\u76d6\u8328\uff0c\u5728\u7f8e\u56fd\u7845\u8c37\u7684\u9752\u5c11\u5e74\u5de8\u5bcc\u4e5f\u5f88\u5e38\u89c1\uff0c\u5bf9\u4e8e\u6e05\u534e\u3001\u5317\u90ae\u7684\u5b66\u751f\uff0c\u7845\u8c37\u662f\u4ed6\u4eec\u5411\u5f80\u7684\u5723\u5730\uff0c\u5bf9\u8fd9\u4e9b\u4e8b\u60c5\u81ea\u7136\u4e0d\u4f1a\u964c\u751f\uff0c\u4f46\u662f\u5f88\u591a\u4eba\u90fd\u575a\u5b9a\u7684\u8ba4\u4e3a\u56fd\u5185\u7f3a\u4e4f\u6bd4\u5c14\u76d6\u8328\u6210\u957f\u7684\u571f\u58e4\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u6bb5\u4e0d\u662f\u8fdb\u4e86\u7535\u5b50\u5de5\u4e1a\u90e8\u5417\uff1f\u4ed6\u4eec\u90e8\u95e8\u6b63\u91c7\u96c6\u6570\u636e\u6392\u4e5d\u4e03\u5e74\u7684\u7535\u5b50\u4f01\u4e1a\u767e\u5f3a\uff0c\u7231\u8fbe\u7535\u5b50\u53ef\u80fd\u5728\u5341\u4e09\u56db\u540d\u5de6\u53f3\u2026\u2026\u4e00\u4e2a\u624d\u6210\u7acb\u4e24\u5e74\u7684\u7535\u5b50\u4f01\u4e1a\u2026\u2026\u201d\u611f\u6168\u7684\u8bed\u6c14\u91cc\uff0c\u6709\u7740\u66f4\u591a\u662f\u5bf9\u672a\u6765\u7684\u671f\u5f85\uff0c\u5728\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u773c\u91cc\uff0c\u5f88\u575a\u5b9a\u7684\u8ba4\u5b9a\u672a\u6765\u5341\u5e74\u662f\u4e92\u8054\u7f51\u7684\u65f6\u4ee3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8def\u8fc7\u4e1c\u5355\u516c\u56ed\u65f6\uff0c\u5510\u5a67\u770b\u5230\u6709\u7a7f\u7d27\u8eab\u3001\u8863\u670d\u7f00\u7740\u4eae\u7247\u7684\u7537\u5b69\u51fa\u5165\uff0c\u626f\u4e86\u626f\u5f20\u606a\u7684\u8863\u8896\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u7537\u5b69\u5b50\u6253\u626e\u597d\u50cf\u8fd8\u633a\u65f6\u9ae6\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u7ed9\u5f90\u5b66\u5e73\u5f00\u8f66\u7684\u53f8\u673a\u5728\u524d\u9762\u542c\u4e86\u8f7b\u7b11\u4e86\u4e24\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u59cb\u8fd8\u6ca1\u6709\u610f\u8bc6\u5230\u4ec0\u4e48\uff0c\u542c\u53f8\u673a\u8fd9\u4e48\u4e00\u7b11\uff0c\u4fa7\u5934\u63a0\u4e86\u4e00\u773c\uff0c\u770b\u5230\u90a3\u4e9b\u5927\u51b7\u5929\u8fd8\u7a7f\u7d27\u8eab\u8863\u3001\u8fd8\u6709\u4e00\u4e2a\u5373\u4f7f\u7a7f\u7740\u539a\u5916\u5957\u3001\u8272\u5f69\u4e5f\u5f88\u9c9c\u4e3d\u7684\u4e24\u4e2a\u7537\u5b69\u5b50\u6b63\u5f80\u4e1c\u5355\u516c\u56ed\u91cc\u9762\u8d70\uff0c\u4ed6\u5012\u662f\u60f3\u8d77\u6765\u4e86\uff0c\u4e1c\u5355\u516c\u56ed\u662f\u5317\u4eac\u540c\u6027\u604b\u7684\u805a\u96c6\u5730\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u7f51\u7edc\u8fd8\u6ca1\u6709\u6d41\u52a8\uff0c\u8fd9\u4e9b\u540c\u6027\u604b\u4eec\u5927\u591a\u5230\u4e1c\u5355\u516c\u56ed\u6765\u201c\u9493\u9c7c\u201d\uff0c\u8fd9\u4e9b\u8863\u7740\u5149\u9c9c\u3001\u624e\u773c\u3001\u773c\u89d2\u7709\u68a2\u5e26\u7740\u5f02\u6837\u98ce\u60c5\u7684\u7537\u5b69\u5b50\uff0c\u5708\u5916\u4eba\u4f1a\u611f\u5230\u8be7\u5f02\u3001\u4e0d\u89e3\uff0c\u5708\u5185\u4eba\u5374\u80fd\u4e00\u773c\u5c31\u8fa9\u8bc6\u51fa\u4ed6\u4eec\u662f\u4e00\u7fa4\u7279\u6b8a\u804c\u4e1a\u8005\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u58f0\u8bf4\uff1a\u201c\u597d\u50cf\u6709\u4eba\u8bf4\u8fd9\u91cc\u662f\u738b\u5c0f\u6ce2\u5c0f\u8bf4\u300a\u4e1c\u5bab\u897f\u5bab\u300b\u6545\u4e8b\u7684\u53d1\u751f\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5510\u5a67\u5012\u662f\u770b\u8fc7\u738b\u5c0f\u6ce2\u7684\u5c0f\u8bf4\uff0c\u4e0d\u8fc7\u5bf9\u5c0f\u8bf4\u7684\u6545\u4e8b\u80cc\u666f\u5374\u4e0d\u5927\u4e86\u89e3\uff0c\u8d34\u7740\u8f66\u7a97\u770b\u7740\u6e10\u884c\u6e10\u8fdc\u7684\u4e1c\u5355\u516c\u56ed\u3002</p><p>\u3000\u3000\u738b\u5c0f\u6ce2\u7684\u5c0f\u8bf4\uff0c\u5f20\u606a\u521d\u8bfb\u65f6\uff0c\u6709\u7740\u65b0\u5947\u3001\u5927\u7b11\u3001\u8352\u8bde\u7b49\u8bf8\u611f\u4ea4\u96c6\u7684\u611f\u89c9\uff0c\u518d\u8bfb\u53ea\u662f\u60b2\u51c9\u800c\u5df2\uff0c\u8fd9\u4e48\u60f3\u7740\uff0c\u4e5f\u7981\u4e0d\u4f4f\u56de\u5934\u671b\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u5510\u5a67\u8bf4\u9053\uff1a\u201c\u738b\u5c0f\u6ce2\u5927\u6982\u662f\u8ddf\u7c73\u5170\u6606\u5fb7\u62c9\u4e00\u6837\u90fd\u662f\u6709\u7740\u5927\u667a\u6167\u7684\u4eba\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u5427\u3002\u201d\u5f20\u606a\u8f7b\u58f0\u8bf4\u9053\u3002\u770b\u5230\u53f8\u673a\u900f\u8fc7\u540e\u89c6\u955c\u671d\u8fd9\u8fb9\u671b\u4e86\u4e24\u773c\uff0c\u6216\u8bb8\u4e5f\u8bfb\u8fc7\u738b\u5c0f\u6ce2\u7684\u5c0f\u8bf4\u6216\u6742\u6587\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u738b\u5c0f\u6ce2\u5728\u56fd\u5185\u7684\u5f71\u54cd\u529b\uff0c\u5f20\u606a\u4ee5\u4e3a\u5012\u662f\u4e0d\u6bd4\u7c73\u5170\u6606\u5fb7\u62c9\u5f31\uff0c\u8bf4\u4ed6\u4eec\u90fd\u662f\u6709\u5927\u667a\u6167\u7684\u4eba\uff0c\u5e94\u8be5\u662f\u6070\u5f53\u7684\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u80fd\u89c1\u4e0a\u4e00\u9762\u5c31\u597d\u4e86\uff0c\u201d\u5510\u5a67\u4e0d\u65e0\u5411\u5f80\u7684\u8bf4\uff0c\u201c\u738b\u5c0f\u6ce2\u4e5f\u4f4f\u5728\u5317\u4eac\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\u554a\uff0c\u6216\u8bb8\u5427\uff0c\u6211\u53c8\u4e0d\u662f\u6587\u5b66\u9752\u5e74\u3002\u201d\u518d\u8fc7\u4e24\u4e09\u5e74\uff0c\u6587\u5b66\u9752\u5e74\u8fd9\u4e2a\u8bcd\u5c31\u8981\u6210\u8d2c\u4e49\u8bcd\uff0c\u8c01\u88ab\u522b\u4eba\u6309\u4e0a\u8fd9\u4e2a\u79f0\u8c13\u5c31\u4f1a\u8ddf\u522b\u4eba\u6025\uff0c\u5f20\u606a\u5634\u89d2\u6d41\u9732\u51fa\u4e00\u4e1d\u6de1\u6de1\u82e5\u65e0\u7684\u6d45\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u60f3\u8d77\u6765\u738b\u5c0f\u6ce2\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u731d\u7136\u901d\u4e16\u3002\u738b\u5c0f\u6ce2\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u731d\u7136\u901d\u4e16\u7684\uff1f\u592a\u5177\u4f53\u7684\u65f6\u95f4\uff0c\u5f20\u606a\u8bb0\u4e0d\u8d77\u6765\uff0c\u4f46\u662f\u5370\u8c61\u4e2d\u5c31\u5728\u4e5d\u4e03\u5e74\u6625\u5929\u56e0\u4e3a\u5fc3\u810f\u75c5\u7a81\u53d1\u82f1\u65e9\u901d\u3002\u6b63\u503c\u4ed6\u521b\u4f5c\u7684\u5dc5\u5cf0\u65f6\u671f\uff0c\u5f20\u606a\u4ee5\u4e3a\uff0c\u738b\u5c0f\u6ce2\u7684\u901d\u4e16\u662f\u4e2d\u56fd\u73b0\u4ee3\u6587\u5b66\u53f2\u4e0a\u6700\u5927\u7684\u635f\u5931\uff0c\u7559\u7ed9\u540e\u4eba\u65e0\u5c3d\u7684\u60cb\u60dc\u4e0e\u6000\u5ff5\u3002</p><p>\u3000\u3000\u660e\u77e5\u9053\u738b\u5c0f\u6ce2\u4f1a\u5728\u4e00\u4e2a\u6708\u6216\u8005\u4e24\u4e2a\u6708\u4e4b\u540e\u5c31\u731d\u7136\u79bb\u4e16\uff0c\u5f20\u606a\u8fd8\u662f\u9009\u62e9\u8896\u624b\u65c1\u89c2\uff0c\u4e0d\u6653\u5f97\u5510\u5a67\u77e5\u9053\u4e4b\u540e\u2014\u2014\u5047\u82e5\u544a\u8bc9\u5979\u4e00\u5207\u7684\u771f\u76f8\u2014\u2014\u4f1a\u4e0d\u4f1a\u62b1\u6028\u81ea\u5df1\u6b64\u65f6\u7684\u6b8b\u9177\u51b7\u8840\uff1f</p><p>\u3000\u3000\u738b\u5c0f\u6ce2\u7684\u6b7b\uff0c\u5e94\u8be5\u662f\u6b7b\u4e8e\u5fc3\u529b\u4ea4\u7601\uff0c\u56e0\u4e3a\u751f\u6d3b\u4e0a\u7684\u3001\u5199\u4f5c\u4e0a\u7684\uff0c\u66f4\u662f\u56e0\u4e3a\u4ed6\u4f53\u4f1a\u5230\u7684\u79cd\u79cd\u4e0d\u81ea\u7531\u3001\u4e0d\u6109\u5feb\u3002\u53c8\u600e\u4e48\u80fd\u8ba9\u4ed6\u4f53\u4f1a\u4e0d\u5230\u90a3\u79cd\u79cd\u4e0d\u81ea\u7531\u3001\u4e0d\u6109\u5feb\u5462\uff1f</p></article>"}}}]);
