(window.webpackJsonp=window.webpackJsonp||[]).push([[662],{685:function(p){p.exports={chapterTitle:null,title:"\u7b2c661\u7ae0 \u6700\u5927\u7684\u56f0\u96be",content:"<article><h2>\u7b2c661\u7ae0 \u6700\u5927\u7684\u56f0\u96be</h2><p>\u3000\u3000\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u6574\u5929\u5fd9\u7740\u521b\u57df\u7f51\u5427\u7684\u4e8b\u60c5\uff0c\u8463\u8dc3\u534e\u7ec4\u7ec7\u4e00\u4e9b\u521b\u4e1a\u57f9\u8bad\u6765\u4e30\u5bcc\u521b\u4e1a\u534f\u4f1a\u7684\u6d3b\u52a8\u3002\u5f20\u606a\u5c31\u6709\u4e9b\u65e0\u6240\u4e8b\u4e8b\u4e86\uff0c\u8fd9\u4e9b\u5929\uff0c\u5012\u662f\u4e0e\u9648\u5983\u84c9\u5728\u56fe\u4e66\u9986\u9605\u89c8\u5ba4\u89c1\u9762\u7684\u673a\u4f1a\u591a\uff0c\u5468\u672b\uff0c\u8981\u56de\u6d77\u5dde\u4e00\u8d9f\uff0c\u9648\u5983\u84c9\u4e5f\u60f3\u56de\u5bb6\u8fc7\u5468\u672b\uff0c\u5f20\u606a\u4fbf\u634e\u4e0a\u5979\u4e00\u540c\u56de\u6d77\u5dde\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5e02\u8bbe\u7acb\u7b2c\u4e8c\u5de5\u4e1a\u56ed\u533a\u7684\u65b9\u6848\u7ecf\u8fc7\u51e0\u4e2a\u6708\u7684\u52aa\u529b\uff0c\u8fd1\u65e5\u83b7\u5f97\u5ba1\u6279\u901a\u8fc7\uff0c\u5c06\u539f\u5c5e\u57ce\u5357\u533a\u7684\u65b0\u6865\u3001\u76ca\u9686\u3001\u8c61\u5c71\u4e09\u9547\u90fd\u5212\u5165\u5230\u8fd9\u4e2a\u540d\u4e3a\u9526\u6e56\u65b0\u57ce\u7684\u5de5\u4e1a\u56ed\u533a\u91cc\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u524d\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u3001\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\u3001\u8c61\u5c71\u98ce\u666f\u56ed\u4e4b\u5916\uff0c\u8fd8\u5728\u73af\u8c61\u5c71\u533a\u57df\u89c4\u5212\u51fa\u4e00\u5927\u7247\u7684\u65b0\u57ce\u793e\u533a\u3002</p><p>\u3000\u3000\u65e9\u5728\u5e74\u521d\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u674e\u8fdc\u6e56\u5230\u6d77\u5dde\u6765\u89c6\u5bdf\u65f6\uff0c\u5f20\u606a\u5c31\u8bf4\u8fc7\u8981\u5728\u8c61\u5c71\u9644\u8fd1\u4e3a\u9526\u6e56\u7684\u5458\u5de5\u5efa\u8bbe\u4e00\u5ea7\u9ad8\u54c1\u8d28\u7684\u751f\u6d3b\u793e\u533a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5949\u884c\u4e00\u4e2a\u51c6\u5219\uff1a\u52aa\u529b\u800c\u8d44\u8d28\u5c1a\u53ef\u7684\u4eba\uff0c\u90fd\u5c06\u8fc7\u4e0a\u4f53\u9762\u800c\u6709\u5c0a\u4e25\u7684\u751f\u6d3b\u3002\u8fd9\u6216\u8bb8\u9526\u6e56\u6587\u5316\u7684\u4e00\u65b9\u9762\uff1b\u4ece\u73b0\u5b9e\u7684\u89d2\u5ea6\u6765\u8003\u8651\uff0c\u9526\u6e56\u8981\u5438\u7eb3\u5927\u91cf\u7684\u9ad8\u7d20\u8d28\u4eba\u624d\u5230\u6d77\u5dde\u6765\uff0c\u5f53\u6d77\u5dde\u7684\u57ce\u5e02\u53d1\u5c55\u8fd8\u7a0d\u7a0d\u6ede\u540e\uff0c\u65e0\u6cd5\u4e3a\u8fd9\u4e9b\u4eba\u5458\u63d0\u4f9b\u4e00\u79cd\u9ad8\u54c1\u8d28\u7684\u751f\u6d3b\uff0c\u9526\u6e56\u5c31\u5e94\u8be5\u81ea\u5df1\u521b\u9020\u8fd9\u4e9b\u6761\u4ef6\u3002</p><p>\u3000\u3000\u9009\u5740\u786e\u5b9a\u4e0b\u6765\uff0c\u6d77\u5dde\u90a3\u8fb9\u5e0c\u671b\u5f20\u606a\u56de\u53bb\u4eb2\u81ea\u786e\u8ba4\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u9ec4\u660f\u65f6\u5206\u4ece\u5efa\u90ba\u51fa\u53d1\uff0c\u5915\u9633\u5149\u4ece\u8f66\u540e\u7a97\u900f\u5c04\u8fdb\u6765\uff0c\u843d\u5728\u80a9\u4e0a\u3001\u819d\u4e0a\u4ee5\u53ca\u9648\u5983\u84c9\u660e\u5a9a\u52a8\u4eba\u7684\u8138\u988a\u4e0a\uff0c\u5728\u8f66\u53a2\u91cc\u80e1\u4e71\u8bf4\u7740\u8bdd\uff0c\u5012\u4e0d\u89c9\u5f97\u592a\u6f2b\u957f\u3002\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u6d77\u5dde\u5230\u5efa\u90ba\u7684\u9ad8\u901f\u63d0\u901f\u7684\u7f18\u6545\uff0c\u8f66\u8fc7\u9ad8\u901f\u5168\u7a0b\u90fd\u4e0d\u9700\u8981\u4e24\u4e2a\u5c0f\u65f6\uff0c\u53ea\u662f\u5728\u5e02\u91cc\u62e5\u585e\u4e0a\u7684\u9053\u8def\u803d\u6401\u4e86\u8f83\u957f\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5230\u6d77\u5dde\u5df2\u7ecf\u662f\u591c\u91cc\u4e03\u70b9\u4e86\uff0c\u76f4\u63a5\u5c06\u9648\u5983\u84c9\u9001\u5230\u4e39\u4e95\u5df7\u3002</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u9648\u5983\u84c9\u4e0b\u8f66\u65f6\u62ac\u5934\u770b\u7740\u4e39\u4e95\u5df7\u4e0e\u4ee5\u524d\u5927\u4e0d\u4e00\u6837\uff0c\u5fcd\u4e0d\u4f4f\u60ca\u8bb6\u7684\u53eb\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u8fd8\u662f\u9648\u5983\u84c9\u5d34\u4e86\u811a\u5462\uff0c\u5f20\u606a\u8eab\u5b50\u63a2\u8fc7\u6765\uff0c\u5faa\u7740\u9648\u5983\u84c9\u7684\u89c6\u7ebf\u770b\u4e86\u4e00\u773c\uff0c\u201c\u54e6\uff0c\u8fd9\u4e2a\u554a\uff0c\u6709\u4ec0\u4e48\u5927\u60ca\u5c0f\u602a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6bd4\u4e4b\u524d\u8499\u7740\u7684\u7eff\u7f51\u6f02\u4eae\u591a\u4e86\u554a\uff0c\u201d\u9648\u5983\u84c9\u611f\u89c9\u5f20\u606a\u6328\u7740\u5979\u8fd1\u4e86\uff0c\u9f3b\u606f\u90fd\u55b7\u5230\u81ea\u5df1\u7684\u8116\u5b50\u91cc\uff0c\u75d2\u75d2\u7684\uff0c\u5979\u53ea\u662f\u4e00\u53ea\u811a\u51fa\u8f66\u95e8\u800c\u5df2\uff0c\u8eab\u5b50\u8fd8\u534a\u5750\u5728\u6905\u4e0a\uff0c\u53ea\u7559\u4e0b\u5f88\u5c0f\u7684\u7f1d\u9699\u8ba9\u5f20\u606a\u770b\u5916\u9762\uff0c\u4e24\u4eba\u96be\u514d\u6328\u5f97\u8fd1\u4e86\uff0c\u9648\u5983\u84c9\u8eab\u5b50\u671d\u8fb9\u4e0a\u8ba9\u4e86\u8ba9\uff0c\u5012\u6ca1\u6709\u6025\u7740\u4e0b\u8f66\uff0c\u4fa7\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u4e39\u4e95\u5df7\u6539\u5efa\u6210\u4e4b\u540e\uff0c\u4f1a\u4e0d\u4f1a\u4e5f\u5c31\u8fd9\u4e48\u6f02\u4eae\uff1f\u201d\u72ed\u7a84\u7684\u8f66\u53a2\uff0c\u8ba9\u4e24\u4eba\u7684\u773c\u775b\u8ddd\u79bb\u5982\u6b64\u4e4b\u524d\uff0c\u5e7d\u6697\u8def\u706f\u5149\u91cc\uff0c\u80fd\u770b\u89c1\u4ed6\u773c\u775b\u91cc\u81ea\u5df1\u7684\u6de1\u5f71\uff0c\u9648\u5983\u84c9\u6ca1\u6765\u7531\u7684\u5fc3\u8df3\u4e86\u4e00\u4e0b\uff0c\u8eab\u5b50\u4e0b\u610f\u8bc6\u7684\u5f80\u4e00\u8fb9\u53c8\u7f29\u4e86\u7f29\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u89c6\u7ebf\u8fd8\u5728\u5916\u9762\u3002</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u4fee\u7f2e\u5de5\u7a0b\u6ca1\u90a3\u4e48\u5bb9\u6613\u5c31\u80fd\u5b8c\u5de5\uff0c\u5f20\u606a\u662f\u60df\u6050\u90b5\u81f3\u521a\u4ed6\u4eec\u7cbe\u5fc3\u96d5\u7422\u4e0d\u591f\u3002\u524d\u51e0\u5929\u7ecf\u8fc7\u8fd9\u91cc\u7684\u4eba\u8fd8\u4f1a\u53d1\u73b0\u4e39\u4e95\u5df7\u7ed9\u7eff\u8272\u7684\u9632\u5760\u7f51\u88f9\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u8fd9\u4e24\u5929\u9632\u5760\u7f51\u5916\u5c42\u53c8\u8499\u4e86\u5927\u5e45\u7684\u5f69\u5370\u5e03\u5e54\uff0c\u662f\u771f\u5b9e\u6bd4\u4f8b\u7684\u4e39\u4e95\u5df7\u5546\u4e1a\u8857\u6548\u679c\u56fe\uff0c\u5165\u591c\u540e\u5728\u956d\u5c04\u706f\u7684\u7167\u8000\u4e0b\uff0c\u7f8e\u8f6e\u7f8e\u5942\u3002</p><p>\u3000\u3000\u5f20\u606a\u54a7\u7740\u5634\u7b11\u4e86\u7b11\uff0c\u8ddf\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u8981\u7ed9\u90a3\u4e2a\u6076\u5a46\u5a18\u989c\u8272\u770b\u7684\u2026\u2026\u201d</p><p>\u3000\u300010\u670816\u65e5\uff0c\u9526\u57ce\u5730\u4ea7\u6b63\u5f0f\u7ed9\u7cbe\u5178\u5730\u4ea7\u3001\u6b63\u6cf0\u96c6\u56e2\u3001\u5609\u4fe1\u5730\u7acb\u5408\u8d44\u6210\u7acb\u7684\u6295\u8d44\u7ba1\u7406\u516c\u53f8\u6b63\u5f0f\u6258\u7ba1\uff0c\u6240\u8c13\u6258\u7ba1\u4e0e\u5e76\u8d2d\u8fd8\u7565\u6709\u4e0d\u540c\uff0c\u4f46\u662f\u80fd\u66f4\u65b9\u4fbf\u7684\u5c06\u9526\u57ce\u5730\u4ea7\u65d7\u4e0b\u7684\u4f18\u8d28\u8d44\u4ea7\u5265\u79bb\u51fa\u6765\uff0c\u800c\u4e0d\u7528\u62c5\u5fc3\u4f1a\u9677\u5165\u503a\u52a1\u6ce5\u6cbc\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u8981\u5c06\u9526\u57ce\u5730\u4ea7\u5b8c\u5168\u636e\u4e3a\u5df1\u6709\uff0c\u4e5f\u8fd8\u8981\u5148\u6e05\u507f\u9526\u57ce\u5730\u4ea7\u7684\u503a\u52a1\u3002</p><p>\u3000\u3000\u603b\u4e4b\u8d75\u9526\u8363\u7236\u5b50\u5bf9\u9526\u57ce\u5730\u4ea7\u4e0d\u518d\u62e5\u6709\u63a7\u5236\u6743\uff0c\u6b64\u65f6\u8fd8\u53ea\u62e5\u6709\u9526\u57ce\u96c6\u56e2\u7684\u7a7a\u58f3\uff0c\u800c\u4e14\u9526\u57ce\u96c6\u56e2\u8fd8\u4e3a\u9526\u57ce\u5730\u4ea7\u627f\u62c5\u4e86\u4e0a\u4ebf\u7684\u503a\u52a1\uff0c\u60f3\u5fc5\u6b64\u65f6\u975e\u5e38\u96be\u8fc7\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u4e3a\u8d75\u9526\u8363\u62c5\u5fc3\uff0c\u9526\u57ce\u843d\u5230\u4eca\u5929\u7684\u5730\u6b65\uff0c\u5927\u6982\u5f88\u591a\u4f1a\u62cd\u624b\u79f0\u5feb\uff0c\u53ef\u60dc\u8fd9\u4e2a\u793e\u4f1a\u72d7\u54ac\u72d7\u7684\u4e8b\u60c5\u53d1\u751f\u8fd8\u662f\u592a\u5c11\u4e86\u3002</p><p>\u3000\u3000\u9526\u57ce\u5730\u4ea7\u65d7\u4e0b\u7684\u4f18\u8d28\u8d44\u4ea7\u65e0\u975e\u5c31\u662f\u4e39\u4e95\u5df7\u4ee5\u897f\u7684\u5927\u7247\u5373\u5c06\u5f00\u53d1\u5b8c\u6210\u7684\u5546\u4e1a\u5730\u4ea7\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u8fd8\u771f\u65e0\u6cd5\u963b\u6b62\u6797\u96ea\u90a3\u4e9b\u86c7\u874e\u5973\u4eba\u6324\u8fdb\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\u8ba1\u5212\uff0c\u4f46\u662f\u5728\u6d77\u5dde\uff0c\u6709\u673a\u4f1a\u8fd8\u662f\u8981\u6253\u4ed6\u4eec\u4e00\u8bb0\u95f7\u68cd\u7684\u3002</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u6b64\u65f6\u5c31\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u5546\u4e1a\u5ba3\u4f20\uff0c\u5c31\u662f\u8981\u5f31\u5316\u6c99\u7530\u897f\u7247\u7684\u5546\u4e1a\u524d\u666f\uff0c\u5f20\u606a\u751a\u81f3\u8ba9\u90b5\u81f3\u521a\u5c06\u4e39\u4e95\u5df7\u672a\u6765\u7684\u5546\u4e1a\u5f00\u53d1\u7b56\u7565\u90fd\u63d0\u524d\u516c\u5e03\u51fa\u6765\uff0c\u8feb\u4f7f\u4ed6\u4eec\u8ddf\u7740\u4e39\u4e95\u5df7\u7684\u5546\u4e1a\u5f00\u53d1\u6b65\u9aa4\u8d70\uff0c\u8feb\u4f7f\u4ed6\u4eec\u65e0\u6cd5\u987a\u5229\u7684\u5c06\u6c99\u7530\u897f\u7247\u7684\u5927\u7247\u5546\u4e1a\u5730\u4ea7\u8131\u624b\u5957\u73b0\u3002</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u672a\u6765\u7684\u5546\u4e1a\u5f00\u53d1\uff0c\u662f\u91c7\u53d6\u53ea\u79df\u4e0d\u552e\u7684\u7b56\u7565\uff0c\u4e3b\u8981\u662f\u4ece\u4fdd\u62a4\u53e4\u8857\u7684\u89d2\u5ea6\u53bb\u8003\u8651\uff1b\u53e6\u5916\uff0c\u4e16\u7eaa\u9526\u6e56\u7ed9\u4e39\u4e95\u5df7\u6240\u5360\u7684\u8d44\u91d1\u5c06\u7531\u9526\u6e56\u989d\u5916\u63d0\u4f9b\uff0c\u4e0d\u4f1a\u6324\u5360\u81ea\u8eab\u7684\u8d44\u91d1\u94fe\u3002</p><p>\u3000\u3000\u9526\u57ce\u5730\u4ea7\u8981\u8ddf\u8fd9\u8fb9\u7684\u6b65\u4f10\u8d70\uff0c\u81ea\u7136\u4f1a\u5341\u5206\u7684\u8f9b\u82e6\uff0c\u7279\u522b\u662f\u5546\u4e1a\u5730\u4ea7\u524d\u51e0\u5e74\u51e0\u4e4e\u90fd\u4e00\u76f4\u662f\u5f00\u8352\u671f\uff0c\u5c06\u4e00\u4e2a\u533a\u57df\u7684\u5546\u4e1a\u6c1b\u56f4\u57f9\u80b2\u6210\u719f\uff0c\u603b\u9700\u8981\u5f88\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u8fd9\u65f6\u95f4\u4e0d\u4ec5\u8981\u5411\u5546\u6237\u51cf\u514d\u79df\u91d1\u3001\u8865\u8d34\u88c5\u6f62\u8d39\uff0c\u8fd8\u8981\u8fdb\u884c\u5b8c\u6574\u7684\u5546\u4e1a\u5f00\u53d1\u7b56\u5212\u4e0e\u5ba3\u4f20\u3002</p><p>\u3000\u3000\u5f85\u6c99\u7530\u897f\u7247\u7684\u5546\u4e1a\u5730\u4ea7\u5efa\u6210\u4e4b\u540e\uff0c\u4ed6\u4eec\u8fd8\u8981\u6295\u5165\u5927\u91cf\u7684\u4eba\u529b\u7269\u529b\u8fdb\u884c\u5b8c\u6574\u7684\u5546\u4e1a\u5f00\u53d1\uff0c\u4e0d\u7136\u4f11\u60f3\u987a\u5229\u7684\u4ece\u6d77\u5dde\u8131\u8eab\uff0c\u4e5f\u7b97\u662f\u8feb\u4f7f\u4ed6\u4eec\u4e3a\u6d77\u5dde\u5e02\u7684\u5546\u8d38\u53d1\u5c55\u505a\u8d21\u732e\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6e21\u8fc7\u524d\u9762\u96be\u6328\u7684\u5f00\u8352\u671f\u4e4b\u540e\uff0c\u7ecf\u8425\u6210\u529f\u7684\u5546\u4e1a\u5730\u4ea7\uff0c\u6536\u76ca\u8fd8\u662f\u9887\u4e30\u7684\uff0c\u4f1a\u4fc3\u4f7f\u5546\u4e1a\u5730\u4ea7\u516c\u53f8\u8fdb\u5165\u826f\u6027\u53d1\u5c55\u5faa\u73af\u3002</p><p>\u3000\u3000\u50cf\u6797\u96ea\u3001\u8c22\u5bb6\u8fd9\u4e9b\u4e60\u60ef\u4f9d\u8d56\u884c\u653f\u8d44\u6e90\u63a0\u593a\u8d22\u5bcc\u7684\u4eba\uff0c\u53c8\u5982\u4f55\u8010\u5f97\u4f4f\u5f00\u8352\u671f\u7684\u5bc2\u5bde\uff1f\u53ea\u6015\u8fd9\u65f6\u5019\u90a3\u4e2a\u86c7\u874e\u5973\u4eba\u6025\u7740\u8dfa\u811a\u3001\u54ac\u7259\u5207\u9f7f\u5462\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u65e2\u7136\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\uff0c\u4e5f\u662f\u9910\u996e\u5a31\u4e50\u5546\u4e1a\u5f00\u53d1\u516c\u53f8\uff0c\u5730\u4ea7\u516c\u53f8\u5f00\u53d1\u51fa\u6765\u7684\u4f18\u8d28\u5546\u4e1a\u5730\u4ea7\u4ea4\u7ed9\u9910\u996e\u5a31\u4e50\u4e50\u5546\u4e1a\u5f00\u53d1\u516c\u53f8\u7ecf\u8425\u3002\u5728\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u7684\u8fd0\u8425\u4e0a\uff0c\u4e16\u7eaa\u9526\u6e56\u5c31\u79ef\u7d2f\u4e86\u4e00\u4e9b\u7ecf\u9a8c\uff0c\u90b5\u81f3\u521a\u4ed6\u4eec\u4e5f\u8ba4\u8bc6\u5230\uff0c\u5546\u4e1a\u5f00\u53d1\u6210\u719f\u7684\u5546\u4e1a\u5730\u4ea7\u7684\u4ef7\u503c\uff0c\u8981\u8fdc\u8fdc\u6bd4\u521a\u5f00\u53d1\u51fa\u6765\u65f6\u9ad8\u7684\u591a\u3002</p><p>\u3000\u3000\u60f3\u505a\u4e00\u756a\u4e8b\u60c5\uff0c\u90fd\u8981\u7a0d\u5fae\u6709\u4e9b\u8010\u5fc3\uff0c\u6797\u96ea\u90a3\u5973\u4eba\u5927\u6982\u4e0d\u4f1a\u7518\u5fc3\u5927\u91cf\u7684\u8d44\u91d1\u7ed9\u9501\u5728\u6d77\u5dde\u800c\u5bf9\u5efa\u90ba\u7684\u80a5\u8089\u65e0\u6cd5\u54ac\u4e00\u53e3\u5427\uff1f\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5634\u89d2\u6302\u7740\u6d45\u7b11\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\u574f\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u62b9\u4e86\u62b9\u8138\uff0c\u5c06\u5634\u89d2\u7684\u6d45\u7b11\u62b9\u6389\uff0c\u201c\u6709\u8fd9\u4e48\u660e\u663e\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u5510\u5a67\u8bf4\u4f60\u8fd9\u4e48\u7b11\u51c6\u4e0d\u662f\u5728\u60f3\u4ec0\u4e48\u597d\u4e8b\uff0c\u6211\u770b\u4e5f\u5dee\u4e0d\u591a\u2026\u2026\u201d\u9648\u5983\u84c9\u5ae3\u7136\u7b11\u7740\uff0c\u9080\u8bf7\u5f20\u606a\u4e0e\u5085\u4fca\u5230\u516b\u9526\u73cd\u7528\u9910\u3002</p><p>\u3000\u3000\u201c\u674e\u660e\u745c\u65e9\u5c31\u51c6\u5907\u597d\u5341\u5168\u5927\u8865\u6c64\u7b49\u4f60\u5427\uff1f\u201d\u5f20\u606a\u8ddf\u5085\u4fca\u5f00\u73a9\u7b11\u7740\u8bf4\uff0c\u53c8\u8ddf\u9648\u5983\u84c9\u8bf4\uff0c\u201c\u6211\u665a\u4e0a\u8fd8\u6709\u4e8b\uff0c\u5c31\u4e0d\u8fdb\u53bb\u8e6d\u4f60\u5bb6\u996d\u5403\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u9648\u5983\u84c9\u8fdb\u4e86\u516b\u9526\u73cd\u7684\u6df1\u7ea2\u8272\u6728\u95e8\uff0c\u5c06\u5085\u4fca\u9001\u5230\u4ed6\u5bb6\u697c\u4e0b\uff0c\u5f20\u606a\u5c31\u81ea\u5df1\u5f00\u7740\u8f66\u79bb\u5f00\u4e86\u3002\u96be\u5f97\u8bb8\u601d\u4e5f\u56de\u4e86\u6d77\u5dde\uff0c\u5f20\u606a\u53ef\u4e0d\u60f3\u56e0\u4e3a\u5176\u4ed6\u4e8b\u60c5\u803d\u6401\u65f6\u95f4\uff0c\u8bb8\u601d\u4e5f\u662f\u4e3a\u5728\u8c61\u5c71\u811a\u4e0b\u5efa\u8bbe\u4e00\u5ea7\u9ad8\u89c4\u683c\u793e\u533a\u7684\u4e8b\u60c5\u8d76\u56de\u6d77\u5dde\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u521a\u5230\uff0c\u4f60\u4eba\u5728\u54ea\u91cc\uff1f\u201d\u5f20\u606a\u62e8\u901a\u8bb8\u601d\u5728\u6d77\u5dde\u7684\u624b\u673a\u53f7\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u7a81\u7136\u6253\u7535\u8bdd\u6765\uff0c\u5bb3\u6211\u7ed9\u70eb\u4e86\u4e00\u4e0b\u2026\u2026\u201d\u8bb8\u601d\u5728\u7535\u8bdd\u90a3\u5934\u6709\u4e9b\u5fae\u60ca\u7684\u5a07\u55d4\u7740\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u5bb6\u91cc\uff0c\u7172\u6c64\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u201d\u8bb8\u601d\u5728\u7535\u8bdd\u90a3\u5934\u7684\u58f0\u97f3\u5a07\u8f6f\uff0c\u201c\u6211\u5728\u4f60\u5bb6\u91cc\u5462\uff0c\u6b63\u5e2e\u7740\u665a\u6674\u59d0\u7172\u6c64\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd8\u4ee5\u4e3a\u8bb8\u601d\u4f1a\u8eb2\u7740\u4e0d\u89c1\u665a\u6674\u5462\uff0c\u5012\u8fd9\u4e24\u4e2a\u5973\u4eba\u51d1\u5230\u4e00\u8d77\uff0c\u5f20\u606a\u81ea\u5df1\u5374\u89c9\u5f97\u5fd0\u5fd1\u8d77\u6765\uff0c\u6709\u4e9b\u4e0d\u6562\u53bb\u89c1\u8fd9\u4e24\u4e2a\u5973\u4eba\u4e86\u3002\u4ed6\u8fd9\u65f6\u5019\u53ef\u6ca1\u6709\u80c6\u91cf\u53bb\u7ec6\u601d\u4e4b\u524d\u505a\u8fc7\u7684\u5206\u4e0d\u6e05\u662f\u8bb8\u601d\u8fd8\u662f\u665a\u6674\u7684\u90a3\u4e2a\u68a6\u3002</p><p>\u3000\u3000\u9a76\u8fdb\u5c0f\u533a\uff0c\u5c31\u770b\u7740\u4ece\u9662\u5b50\u7684\u7a20\u5bc6\u6811\u7bf1\u95f4\u6cc4\u51fa\u661f\u6591\u7684\u706f\u706b\uff0c\u4e8c\u697c\u7684\u706f\u6ca1\u6709\u5f00\uff0c\u665a\u6674\u5bb6\u7684\u706f\u706b\u90fd\u4eae\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c31\u5c06\u8f66\u505c\u5728\u5c0f\u533a\u5165\u53e3\u7684\u8def\u7259\u4e0a\uff0c\u5f80\u5bb6\u91cc\u8d70\u53bb\uff0c\u542c\u7740\u8bb8\u601d\u4e0e\u665a\u6674\u7ec6\u788e\u7684\u8bf4\u8bdd\u58f0\u4ece\u5ba4\u5185\u4f20\u6765\uff0c\u5f88\u98d8\u6e3a\u6773\u7136\u7684\u611f\u89c9\uff0c\u5f80\u5bc6\u7801\u9501\u91cc\u8f93\u5165\u5bc6\u7801\uff0c\u8fdb\u4e86\u9662\u5b50\uff0c\u900f\u8fc7\u73bb\u7483\u7a97\u770b\u89c1\u4e24\u4e2a\u5973\u4eba\u5728\u9910\u5385\u91cc\u51c6\u5907\u665a\u9910\uff0c\u662f\u8fd9\u9759\u8c27\u591c\u8272\u6700\u7f8e\u7684\u98ce\u666f\u3002</p><p>\u3000\u3000\u5927\u6982\u662f\u5bc6\u7801\u9501\u8df3\u8131\u7684\u5361\u5361\u54cd\u63d0\u9192\u4e86\u5979\u4eec\uff0c\u4e00\u8d77\u5f80\u7a97\u5916\u770b\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7ad9\u5728\u9910\u5385\u91cc\u7ee7\u7eed\u51c6\u5907\u4e1c\u897f\uff0c\u665a\u6674\u8dd1\u8fc7\u6765\u7ed9\u5f20\u606a\u5f00\u95e8\uff0c\u89c1\u4ed6\u7684\u8138\u8272\u8baa\u8baa\u7684\uff0c\u95ee\u4ed6\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4f38\u624b\u6402\u4e86\u6402\u665a\u6674\u4e30\u8174\u8bf1\u4eba\u7684\u5a07\u8eaf\uff0c\u5c06\u5979\u653e\u5f00\uff0c\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7a81\u7136\u53d1\u73b0\u6211\u7684\u5fc3\u7406\u538b\u529b\u6bd4\u4f60\u4eec\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u62c9\u5012\u5427\uff0c\u8ba9\u4f60\u5360\u8fd9\u4e48\u5927\u4fbf\u5b9c\uff0c\u8fd9\u65f6\u5019\u8bf4\u4ec0\u4e48\u538b\u529b\u5927\uff0c\u5f53\u65f6\u600e\u4e48\u7cca\u5f04\u4eba\u5bb6\uff1f\u201d\u665a\u6674\u7565\u5e26\u7f9e\u610f\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u7b11\u7740\u5728\u4ed6\u7684\u8170\u4e0a\u6390\u4e86\u4e00\u4e0b\uff0c\u8ba9\u4ed6\u8fdb\u5c4b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u7b11\u4e86\u7b11\uff0c\u60f3\u60f3\u4e5f\u5bf9\u3002\u8fdb\u5c4b\u540e\uff0c\u5012\u662f\u8bb8\u601d\u8fd8\u662f\u62d8\u8c28\u5f97\u5f88\uff0c\u4fcf\u8138\u4e5f\u7ea2\u7740\uff0c\u8ddf\u665a\u6674\u8bf4\u8bdd\u5012\u662f\u81ea\u7136\uff0c\u5c31\u662f\u4e0d\u62ff\u6b63\u773c\u770b\u5f20\u606a\uff1b\u665a\u6674\u5230\u522b\u7684\u5c4b\uff0c\u5f20\u606a\u8981\u53bb\u62c9\u5979\u7684\u624b\uff0c\u4e5f\u4f1a\u60ca\u7f9e\u7684\u7f29\u5f00\u3002</p></article>"}}}]);
