(window.webpackJsonp=window.webpackJsonp||[]).push([[667],{690:function(p){p.exports={chapterTitle:null,title:"\u7b2c666\u7ae0 \u4e0a\u5c97\u804c\u5de5",content:"<article><h2>\u7b2c666\u7ae0 \u4e0a\u5c97\u804c\u5de5</h2><p>\u3000\u3000\u6309\u7167\u5f53\u65f6\u62df\u5b9a\u7684\u65b9\u6848\uff0c\u4e3d\u4e30\u5370\u67d3\u5382\u5f7b\u5e95\u5173\u505c\u4e4b\u540e\uff0c\u804c\u5de5\u90fd\u4ea4\u7531\u4e3b\u7ba1\u90e8\u95e8\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u6765\u8d1f\u8d23\u5b89\u7f6e\u3002</p><p>\u3000\u3000\u6240\u8c13\u960e\u738b\u597d\u8fc7\u3001\u5c0f\u9b3c\u96be\u7f20\uff0c\u8fd9\u65f6\u5019\u518d\u627e\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u95ee\u4e00\u95ee\uff0c\u4ed6\u6216\u8bb8\u5bf9\u90a3\u6b21\u5728\u4e3d\u4e30\u5370\u67d3\u5382\u5927\u53d1\u96f7\u9706\u7684\u4e8b\u60c5\u90fd\u5df2\u7ecf\u5370\u8c61\u5f88\u6de1\u4e86\uff0c\u5bf9\u90a3\u4e9b\u4e00\u76f4\u90fd\u5c0f\u5fc3\u63e3\u6469\u4e0a\u610f\u7684\u666e\u901a\u5b98\u5458\u6765\u8bf4\uff0c\u60df\u6050\u505a\u5f97\u4e0d\u591f\u7ec6\u81f4\u5468\u5168\u3002</p><p>\u3000\u3000\u4f55\u51b5\u90a3\u4e9b\u7ed9\u5389\u58f0\u8bad\u65a5\u3001\u7ed9\u5413\u7834\u80c6\u5b50\u7684\u5b98\u5458\u66f4\u9700\u8981\u8fc1\u6012\u4e0e\u8f6c\u79fb\u8d23\u4efb\u7684\u5bf9\u8c61\u3002</p><p>\u3000\u3000\u4e3d\u4e30\u5370\u67d3\u5382\u7ed9\u5173\u505c\u4e4b\u540e\uff0c\u4e09\u767e\u591a\u540d\u804c\u5de5\u5e76\u6ca1\u6709\u5f97\u5230\u59a5\u5584\u7684\u5b89\u7f6e\uff0c\u800c\u662f\u7b2c\u4e00\u65f6\u95f4\u7ed9\u4e0b\u5c97\u5206\u6d41\u4e86\u3002</p><p>\u3000\u3000\u8ffd\u7a76\u4e3d\u4e30\u5370\u67d3\u5382\u91cd\u5927\u6c61\u67d3\u4e8b\u6545\u8d23\u4efb\u65f6\uff0c\u90a3\u4e00\u6b21\u968f\u7530\u529b\u5c71\u7ad9\u51fa\u6765\u963b\u6320\u5e02\u59d4\u5e38\u59d4\u3001\u526f\u5e02\u957f\u738b\u7ef4\u5747\u4e00\u884c\u4eba\u8fdb\u5382\u53e3\u7684\u51e0\u540d\u5de5\u4eba\u4ee3\u8868\u5c31\u4e0d\u662f\u4e0b\u5c97\u5206\u6d41\u90a3\u4e48\u8f7b\u677e\u4e86\uff0c\u53c8\u6070\u597d\u4e00\u540d\u5de5\u4eba\u4ee3\u8868\u662f\u5382\u5b89\u5168\u73af\u4fdd\u79d1\u79d1\u957f\uff0c\u627f\u62c5\u4e86\u7edd\u5927\u90e8\u5206\u7684\u8d23\u4efb\uff0c\u884c\u653f\u62d8\u7559\u4e4b\u540e\u53c8\u7ed9\u5f00\u9664\u515a\u7c4d\uff0c\u5176\u4ed6\u4eba\u5219\u662f\u7ed9\u76f4\u63a5\u5f00\u9664\uff0c\u8fde\u4e0b\u5c97\u5206\u6d41\u7684\u5b89\u7f6e\u8d39\u90fd\u6ca1\u6709\u62ff\u5230\u4e00\u5206\uff0c\u8fde\u5de5\u5382\u5206\u914d\u7684\u4f4f\u623f\u90fd\u7ed9\u5f3a\u884c\u6536\u56de\u3002</p><p>\u3000\u3000\u5012\u6ca1\u6709\u4eba\u76f4\u63a5\u5c06\u7530\u529b\u5c71\u4e00\u811a\u8e22\u5f00\uff0c\u8ffd\u7a76\u4e3d\u4e30\u5382\u91cd\u5927\u6c61\u67d3\u4e8b\u6545\u65f6\uff0c\u7530\u529b\u5c71\u4e5f\u6ca1\u6709\u53d7\u4ec0\u4e48\u5904\u5206\uff0c\u53ea\u662f\u7ed9\u4e22\u5230\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u7684\u5de5\u4f1a\u505a\u95f2\u804c\u5de5\u4f5c\uff0c\u8fd8\u662f\u4eab\u53d7\u6b63\u79d1\u7ea7\u5f85\u9047\u3002</p><p>\u3000\u3000\u5728\u8bb8\u591a\u4eba\u773c\u91cc\uff0c\u4f3c\u4e4e\u662f\u7530\u529b\u5c71\u7279\u6b8a\u7684\u8eab\u4efd\u8ba9\u4ed6\u4eab\u53d7\u4e86\u7279\u6b8a\u7684\u4f18\u5f85\uff0c\u8d23\u4efb\u8ffd\u7a76\u5230\u4ed6\u8fd9\u4e00\u5c42\uff0c\u5904\u7f5a\u529b\u5ea6\u5c31\u9661\u7136\u51cf\u8f7b\u4e86\uff0c\u4f3c\u4e4e\u4e5f\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u7ee7\u7eed\u5f80\u4e0a\u8ffd\u7a76\u4e86\u3002</p><p>\u3000\u3000\u603b\u8981\u7ec6\u7a76\u8d77\u6765\uff0c\u7530\u529b\u5c71\u7b49\u4eba\u662f\u65e0\u6cd5\u5bf9\u4e3d\u4e30\u5382\u7684\u6c61\u67d3\u4e8b\u6545\u8d1f\u8d23\u4efb\u7684\uff0c\u4e3d\u4e30\u5382\u7684\u751f\u4ea7\u5b89\u6392\u3001\u518d\u6295\u8d44\u90fd\u662f\u53d7\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u63a7\u5236\u7684\uff0c\u8981\u8ffd\u7a76\uff0c\u4e5f\u662f\u5c06\u5e02\u8f7b\u5de5\u5c40\u3001\u603b\u516c\u53f8\u7684\u5934\u5934\u8111\u8111\u6328\u4e2a\u7684\u524a\u4e00\u901a\u3002</p><p>\u3000\u3000\u4e8b\u60c5\u5230\u8fd9\u4e00\u6b65\uff0c\u4f3c\u4e4e\u662f\u8fd9\u4e2a\u5e74\u4ee3\u4e00\u79cd\u6bd4\u8f83\u6807\u51c6\u7684\u5904\u7406\u65b9\u5f0f\uff1b\u66ff\u7f6a\u7f8a\u800c\u5df2\uff0c\u800c\u4e14\u53c8\u5de7\u5999\u7684\u5c06\u6028\u6c14\u79ef\u7d2f\u5230\u7530\u529b\u5c71\u5934\u4e0a\u3002</p><p>\u3000\u3000\u7530\u529b\u5c71\u7684\u5904\u5883\u8fd8\u4e0d\u7b97\u592a\u8270\u96be\uff0c\u8981\u662f\u4e0d\u53bb\u60f3\u90a3\u4e9b\u7ed9\u5f00\u9664\u7684\u3001\u4e0b\u5c97\u5206\u6d41\u7684\u804c\u5de5\uff0c\u8981\u662f\u6ca1\u6709\u90a3\u4e9b\u65f6\u4e0d\u65f6\u4f20\u5230\u8033\u6735\u91cc\u7684\u51b7\u5632\u70ed\u8bbd\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u5904\u7406\u65b9\u5f0f\uff0c\u7530\u529b\u5c71\u6709\u82e6\u4e5f\u65e0\u4ece\u8bc9\u8bf4\uff0c\u5750\u4e86\u51e0\u4e2a\u6708\u7684\u51b7\u677f\u51f3\u4e4b\u540e\u5c31\u4e3b\u52a8\u4ece\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u8f9e\u804c\u4e86\uff0c\u5317\u95e8\u7684\u6d17\u8863\u623f\u662f\u4ed6\u4f84\u5973\u5f00\u7684\uff0c\u4ed6\u5c31\u5148\u8fc7\u6765\u5e2e\u5fd9\u6253\u96f6\u5de5\u3002</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u5c31\u662f\u8fd9\u6837\uff0c\u770b\u5230\u6a61\u6811\u56ed\u73b0\u5728\u5efa\u8d77\u6765\uff0c\u71d5\u5f52\u6e56\u5317\u5cb8\u53d8\u5f97\u8fd9\u4e48\u6f02\u4eae\uff0c\u524d\u5929\u5230\u521b\u4e1a\u56ed\u533a\u91cc\u8d70\u4e00\u8d70\uff0c\u90fd\u4e0d\u6562\u76f8\u4fe1\u90a3\u91cc\u662f\u539f\u6765\u7684\u5370\u67d3\u5382\uff0c\u4e5f\u6653\u5f97\u6211\u4eec\u5f53\u65f6\u786c\u62e6\u7740\u6709\u4e9b\u4e0d\u8bb2\u9053\u7406\uff0c\u201d\u7530\u5c71\u529b\u501f\u8fc7\u5f20\u606a\u9012\u8fc7\u6765\u7684\u4e00\u652f\u9999\u70df\uff0c\u62ff\u8863\u670d\u906e\u7740\u5728\u98ce\u4e2d\u70b9\u71c3\uff0c\u5438\u4e86\u4e24\u53e3\uff0c\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u53ea\u662f\u4e09\u767e\u591a\u804c\u5de5\u5c31\u8fd9\u6837\u83ab\u660e\u5176\u5999\u7684\u90fd\u7ed9\u4e22\u4e86\u996d\u7897\uff0c\u8fd8\u6709\u8d85\u8fc7\u4e00\u534a\u591a\u7684\u4eba\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u627e\u5230\u5de5\u4f5c\uff0c\u6ca1\u6709\u6536\u5165\uff0c\u7279\u522b\u662f\u90a3\u51e0\u4eba\u8ddf\u6211\u7ad9\u51fa\u6765\u4ee3\u8868\uff0c\u90fd\u623f\u5b50\u90fd\u7ed9\u6536\u4e86\uff0c\u6ca1\u6709\u6536\u5165\uff0c\u4e00\u5bb6\u4eba\u8fd8\u8981\u79df\u623f\u5b50\u4f4f\uff0c\u5fc3\u91cc\u53c8\u5835\u5f97\u614c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u5e2e\u7740\u7530\u529b\u5c71\u5230\u5404\u4e2a\u7537\u751f\u5bbf\u820d\u8d70\u4e86\u4e00\u904d\uff0c\u5c06\u810f\u8863\u670d\u90fd\u6536\u4e86\u653e\u5230\u4e00\u8f86\u4e09\u8f6e\u8f66\u4e0a\uff0c\u9001\u5230\u5317\u95e8\u7684\u6d17\u8863\u623f\u91cc\uff0c\u5728\u8def\u4e0a\u542c\u7530\u529b\u5c71\u5c06\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e2a\u5927\u6982\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u60f3\u62bd\u652f\u70df\u6765\u7740\uff0c\u7ed9\u5b59\u9759\u6aac\u593a\u4e86\u8fc7\u53bb\u4e22\u6389\u3002\u5f20\u606a\u77aa\u4e86\u5b59\u9759\u6aac\u4e00\u773c\uff0c\u5b59\u9759\u6aac\u5a07\u7b11\u7740\u5728\u4ed6\u8033\u6735\u8f7b\u58f0\u8bf4\uff1a\u201c\u4f60\u8981\u6ce8\u610f\u7684\u8eab\u4efd\u54e6\u2026\u2026\u201d</p><p>\u3000\u3000\u8c01\u8bf4\u5927\u4e00\u7684\u5b66\u751f\u5c31\u4e0d\u53ef\u4ee5\u62bd\u70df\u4e86\uff1f\u5f20\u606a\u90c1\u95f7\u7684\u60f3\u7740\u3002</p><p>\u3000\u3000\u201c\u7ed9\u5973\u670b\u53cb\u7ba1\u7740\u611f\u89c9\u53d7\u7ea6\u675f\u4e86\u5427\uff1f\u201d\u7530\u529b\u5c71\u7b11\u7740\u6253\u8da3\u8bf4\uff0c\u201c\u8c22\u8c22\u4f60\u4eec\u5c0f\u4e24\u53e3\u5e2e\u6211\u5fd9\uff0c\u8fd8\u542c\u6211\u5520\u53e8\u8bf4\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u6211\u7b49\u4f1a\u513f\u8fd8\u4e8b\u8981\u505a\uff0c\u4e0d\u7136\u8ddf\u4f60\u4eec\u5c0f\u4e24\u53e3\u804a\u804a\u5929\uff0c\u5012\u4e5f\u4e0d\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u5973\u670b\u53cb\u90a3\u4e48\u591a\uff0c\u6211\u53ef\u4e0d\u662f\u5176\u4e2d\u4e00\u4e2a\u2026\u2026\u201d\u5b59\u9759\u6aac\u63f6\u63c4\u7684\u8bf4\u9053\uff0c\u5fd9\u7740\u6487\u6e05\u4e0e\u5f20\u606a\u4e4b\u95f4\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u505a\uff0c\u6d17\u8863\u670d\u3001\u71a8\u8863\u670d\uff0c\u6211\u4e5f\u62ff\u624b\u7684\u2026\u2026\u201d\u5f20\u606a\u4e0d\u7406\u4f1a\u5b59\u9759\u6aac\u7684\u8bdd\uff0c\u95ee\u7530\u529b\u5c71\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u9700\u8981\u5e2e\u5fd9\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u53ea\u9700\u8d76\u5728\u4e94\u70b9\u524d\u5c06\u6d17\u597d\u7684\u8863\u670d\u9001\u56de\u5bbf\u820d\u697c\u7b49\u7740\u4eba\u6765\u53d6\u5c31\u884c\u2026\u2026\u8fd9\u4e2a\u5c31\u4e0d\u7528\u9ebb\u70e6\u4f60\u4eec\u5c0f\u4e24\u53e3\u4e86\uff0c\u201d\u7530\u529b\u5c71\u53ef\u4e0d\u7ba1\u5b59\u9759\u6aac\u7684\u8fa9\u767d\uff0c\u770b\u4e86\u770b\u624b\u8155\u4e0a\u6234\u7740\u7684\u8001\u5f0f\u4e0a\u6d77\u624b\u8868\uff0c\u770b\u4e86\u773c\u524d\u8fd9\u4e2a\u9752\u5e74\u4e00\u773c\uff0c\u5f53\u521d\u4ed6\u4e0e\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u540c\u65f6\u51fa\u73b0\u5728\u4e3d\u4e30\u5370\u67d3\u5382\uff0c\u6307\u51fa\u4e3d\u4e30\u5382\u7684\u6c61\u67d3\u95ee\u9898\u4e4b\u540e\uff0c\u53c8\u95ee\u7f57\u541b\u4e00\u8d77\u79bb\u5f00\uff0c\u4e5f\u4e0d\u6653\u4ed6\u4e0e\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u4ec0\u4e48\u5173\u7cfb\u3002\u7530\u529b\u5c71\u4e5f\u4e0d\u662f\u591a\u5634\u591a\u820c\u7684\u4eba\uff0c\u89c1\u5f20\u606a\u5173\u5fc3\u8fd9\u4e9b\u4e8b\uff0c\u4fbf\u591a\u8bf4\u4e86\u4e00\u4e9b\uff0c\u6307\u4e0d\u5b9a\u80fd\u4f20\u5230\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u7684\u8033\u6735\u91cc\u53bb\uff0c\u8bf4\u9053\uff0c\u201c\u8fd8\u6709\u4e9b\u65f6\u95f4\uff0c\u6211\u8981\u53bb\u9752\u5c71\u673a\u68b0\u5382\u53bb\u4e00\u4e0b\uff0c\u770b\u80fd\u4e0d\u80fd\u6324\u51fa\u51e0\u4e2a\u804c\u4f4d\u51fa\u6765\u63d2\u4e24\u4e09\u4e2a\u4eba\u8fdb\u53bb\uff0c\u6bd5\u7adf\u8fd8\u6709\u597d\u4e9b\u4eba\u5de5\u4f5c\u90fd\u6ca1\u6709\u7740\u843d\u3002\u9760\u4e0b\u5c97\u5206\u6d41\u90a3\u70b9\u8865\u8d34\uff0c\u5750\u5403\u5c71\u7a7a\uff0c\u4e5f\u6491\u4e0d\u4e86\u591a\u5c11\u65f6\u95f4\u3002\u8fd9\u5e74\u5934\uff0c\u56fd\u6709\u4f01\u4e1a\u6548\u76ca\u90fd\u4e0d\u597d\uff0c\u63d2\u4eba\u4e5f\u96be\u63d2\u8fdb\u53bb\uff0c\u8fd8\u662f\u8981\u539a\u7740\u8138\u76ae\u5230\u5904\u6c42\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u7b11\u4e86\u7b11\uff0c\u53c8\u81ea\u5632\u7684\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u79cd\u6328\u8fc7\u6279\u8bc4\u7684\u4eba\uff0c\u4f3c\u4e4e\u4ee5\u524d\u7684\u90a3\u4e9b\u8001\u8138\u4e5f\u4e0d\u5927\u7ba1\u7528\u4e86\uff0c\u8001\u7ed9\u4eba\u8eb2\u7740\u3002\u4f46\u662f\u603b\u8981\u8dd1\u8dd1\uff0c\u4e0d\u8dd1\u8dd1\u4e0d\u662f\u4e00\u70b9\u6307\u671b\u90fd\u6ca1\u6709\uff1f\u90a3\u4e9b\u804c\u5de5\u4e3a\u4e3d\u4e30\u5370\u67d3\u5382\u8d21\u732e\u4e86\u5341\u5e74\u3001\u4e8c\u5341\u5e74\uff0c\u90fd\u505a\u60ef\u624b\u5934\u7684\u5de5\u4f5c\uff0c\u504f\u504f\u5efa\u90ba\u7684\u7eba\u7ec7\u4e1a\u53c8\u592a\u4e0d\u666f\u6c14\uff0c\u6362\u5176\u4ed6\u5de5\u4f5c\uff0c\u5c31\u624b\u751f\uff0c\u73b0\u5728\u91cd\u65b0\u5b66\u4e00\u95e8\u6280\u80fd\uff0c\u4e5f\u96be\uff0c\u6587\u5316\u6c34\u5e73\u9ad8\u7684\u4e5f\u4e0d\u591a\uff0c\u518d\u8bf4\u73b0\u5728\u8fd8\u6709\u591a\u5c11\u5382\u5b50\u8fd8\u4e50\u610f\u62db\u56db\u4e94\u5341\u5c81\u7684\u4e2d\u5e74\u4eba\u6765\u57f9\u517b\uff1f\u201d\u8fd9\u7b11\u5bb9\u4e5f\u662f\u82e6\u6da9\uff0c\u201c\u80fd\u6709\u4e9b\u6742\u6d3b\u505a\u505a\u4e5f\u4e0d\u9519\u4e86\u2026\u2026\u597d\u4e9b\u5bb6\u4f19\u90fd\u7ed9\u6211\u8d76\u7740\u53bb\u505a\u82e6\u529b\u4e86\uff0c\u51e0\u5341\u5e74\u56fd\u5bb6\u5de5\u4eba\uff0c\u505a\u82e6\u529b\u62b9\u4e0d\u4e0b\u9762\u5b50\u554a\uff0c\u4f46\u662f\u53c8\u6709\u529e\u6cd5\uff0c\u5148\u8981\u6d3b\u7740\u5457\u3002\u201d</p><p>\u3000\u3000\u5f53\u521d\uff0c\u9664\u4e86\u8d2d\u5730\u8d39\u7528\u4e4b\u5916\uff0c\u6a61\u6811\u56ed\u8fd8\u4e3a\u4e3d\u4e30\u5370\u67d3\u5382\u4e09\u767e\u591a\u540d\u804c\u5de5\u4e00\u6b21\u6027\u652f\u4ed8\u7ed9\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u4e00\u5343\u4e8c\u767e\u591a\u4e07\u7684\u5b89\u7f6e\u8d39\u7528\u3002</p><p>\u3000\u3000\u8fd9\u7b14\u94b1\u5230\u5e95\u6709\u591a\u5c11\u4f1a\u843d\u5165\u4e09\u767e\u591a\u540d\u4e0b\u5c97\u804c\u5de5\u624b\u91cc\uff0c\u5f20\u606a\u5fc3\u91cc\u6ca1\u6709\u4e00\u70b9\u5e95\uff0c\u80fd\u6709\u4e00\u534a\u7528\u5230\u4e0a\u5c97\u804c\u5de5\u5934\u4e0a\uff0c\u4ed6\u5c31\u5f88\u5fc3\u6ee1\u610f\u8db3\u4e86\uff0c\u542c\u7530\u529b\u5c71\u8fd9\u8bed\u6c14\uff0c\u4f3c\u4e4e\u8fd8\u8981\u5c11\u8bb8\u591a\uff0c\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u8fd9\u8fd8\u662f\u4e5d\u4e03\u5e74\uff0c\u4ed6\u4e0d\u80fd\u592a\u7ec6\u7684\u8ffd\u95ee\u8fd9\u7b14\u94b1\u7684\u6d41\u5411\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8981\u6c42\u8fd9\u7b14\u94b1\u7531\u9526\u6e56\u76f4\u63a5\u53d1\u653e\u7ed9\u4e3d\u4e30\u5370\u67d3\u5382\u7684\u804c\u5de5\uff0c\u6bd5\u7adf\u90a3\u65f6\u5019\u4e09\u767e\u591a\u804c\u5de5\u662f\u7531\u5e02\u7eba\u7ec7\u5de5\u4e1a\u603b\u516c\u53f8\u63a5\u6536\u8fc7\u53bb\u7684\u3002</p><p>\u3000\u3000\u771f\u8981\u575a\u6301\u90a3\u4e48\u505a\u4e86\uff0c\u90a3\u5c31\u8981\u5f97\u7f6a\u4f53\u7cfb\u5185\u7684\u4e00\u5927\u7247\u4eba\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u8981\u5728\u5efa\u90ba\u5e02\u624e\u6839\u7acb\u8db3\uff0c\u603b\u4e0d\u80fd\u5c06\u5e0c\u671b\u5bc4\u6258\u5728\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u4e00\u4e2a\u4eba\u5934\u4e0a\uff0c\u603b\u4e0d\u80fd\u7b49\u5230\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u9ad8\u5347\u4e4b\u540e\uff0c\u6a61\u6811\u56ed\u5728\u5efa\u90ba\u5c31\u5173\u95e8\u5927\u5409\u5427\uff1f</p><p>\u3000\u3000\u6709\u4e9b\u4e8b\u60c5\u8fd8\u662f\u8981\u968f\u6ce2\u9010\u6d41\u7684\uff0c\u4f46\u662f\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u80fd\u8865\u6551\u8fd8\u662f\u8981\u8865\u6551\u7684\u3002</p><p>\u3000\u3000\u201c\u7530\u5e08\u5085\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u53bb\u627e\u6a61\u6811\u56ed\u7684\u4eba\uff0c\u8ba9\u4ed6\u4eec\u5b89\u6392\u4e9b\u5de5\u4f5c\u5c97\u4f4d\uff0c\u6bd5\u7adf\u5382\u5b50\u662f\u7ed9\u4ed6\u4eec\u5360\u8fc7\u53bb\u7684\uff1f\u201d\u5b59\u9759\u6aac\u8003\u8651\u95ee\u9898\u4e0d\u50cf\u5f20\u606a\u8f6c\u90a3\u4e48\u5f2f\u5f2f\uff0c\u76f4\u63a5\u5c31\u95ee\u7530\u529b\u5c71\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u7530\u529b\u5c71\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u201c\u5c31\u7b97\u627e\u4e86\u8fd8\u4e0d\u7ed9\u6321\u5728\u95e8\u5916\uff1f\u90fd\u8bf4\u90a3\u6b21\u5de5\u4eba\u5c01\u62e6\u5382\u95e8\u7684\u4e8b\u60c5\uff0c\u8ba9\u6a61\u6811\u56ed\u5e55\u540e\u7684\u5927\u8001\u677f\u5f88\u4e22\u9762\u5b50\u3002\u4eba\u5bb6\u5fc3\u91cc\u6709\u6028\u6068\uff0c\u8fd8\u4f1a\u63f4\u624b\u5e2e\u4e00\u628a\uff1f\u201d</p><p>\u3000\u3000\u53c8\u4e0d\u6653\u5f97\u8fd9\u662f\u8c01\u7ed9\u9526\u6e56\u6697\u4e2d\u4f7f\u7684\u7eca\u5b50\uff0c\u5f20\u606a\u65e0\u53ef\u5948\u4f55\u7684\u7b11\u4e86\u7b11\uff0c\u4ec0\u4e48\u65f6\u5019\u603b\u5c11\u4e0d\u4e86\u6697\u4e2d\u653e\u51b7\u7bad\u7684\uff0c\u770b\u6765\u66f4\u9700\u8981\u505a\u4e00\u4e9b\u4e8b\u60c5\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u8ba4\u8bc6\u6a61\u6811\u56ed\u5e55\u540e\u7684\u5927\u8001\u677f\u4e0d\uff1f\u201d\u5b59\u9759\u6aac\u72e1\u9ee0\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5728\u7530\u529b\u5c71\u770b\u4e0d\u5230\u7684\u5730\u65b9\u62ff\u811a\u5c16\u8f7b\u8f7b\u7684\u8e22\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6ca1\u89c1\u8fc7\uff0c\u201d\u7530\u529b\u5c71\u6447\u7740\u5934\u7b11\u4e86\u7b11\uff0c\u201c\u90a3\u79cd\u5927\u8001\u677f\uff0c\u542c\u8bf4\u5e02\u91cc\u90fd\u662f\u7f57\u541b\u3001\u738b\u7ef4\u5747\u4eb2\u81ea\u63a5\u5f85\u7684\uff0c\u90fd\u8bf4\u8fd9\u6b21\u6295\u6b21\u4e0a\u767e\u4ebf\u7684\u6676\u5706\u5382\uff0c\u8ddf\u6a61\u6811\u56ed\u662f\u540c\u4e00\u4e2a\u8001\u677f\uff0c\u8fd9\u79cd\u4eba\u7269\uff0c\u6211\u54ea\u91cc\u89c1\u5f97\u5230\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u5168\u56fd\u4eba\u5927\u4ee3\u8868\uff0c\u53c8\u662f\u4e94\u4e00\u52b3\u52a8\u5956\u7ae0\u83b7\u5f97\u8005\uff0c\u4ec0\u4e48\u4eba\u7269\uff0c\u4f60\u89c1\u4e0d\u5230\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u731c\u7530\u529b\u5c71\u53ef\u80fd\u8fd9\u79cd\u6709\u4e9b\u7f9e\u4e8e\u9762\u5bf9\u81ea\u5df1\u7684\u8fd9\u4e24\u4e2a\u8eab\u4efd\uff0c\u4ece\u4ed6\u7684\u8bed\u6c14\u91cc\uff0c\u4ed6\u4f3c\u4e4e\u5f88\u4e3a\u71d5\u5f52\u6e56\u7684\u4e25\u91cd\u6c61\u67d3\u72b6\u51b5\u5185\u759a\uff0c\u5927\u6982\u4e5f\u4e3a\u8fd9\u4e8b\u53d7\u5230\u8bb8\u591a\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u89c9\u5f97\u5427\uff0c\u6a61\u6811\u56ed\u90a3\u8fb9\uff0c\u4f60\u8fd8\u662f\u8981\u8d70\u4e00\u8d70\uff0c\u4e0d\u8d70\u4e00\u8d70\uff0c\u771f\u5c31\u4e00\u70b9\u6307\u671b\u90fd\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u4e5f\u5bf9\uff0c\u96be\u9053\u6211\u8fd8\u80fd\u6307\u671b\u8fd9\u5f20\u8001\u8138\u53bb\u5356\u94b1\uff1f\u201d\u7530\u529b\u5c71\u7b11\u7740\u8bf4\uff0c\u201c\u542c\u8bf4\u6a61\u6811\u56ed\u91cc\u90fd\u62db\u4e9b\u9ad8\u7ea7\u7814\u53d1\u4eba\u624d\uff0c\u4e0d\u8fc7\u4e5f\u603b\u8981\u4e00\u4e9b\u5e72\u6742\u6d3b\u7684\uff0c\u54b1\u4e5f\u4e0d\u80fd\u8981\u6c42\u591a\u9ad8\u7684\u5f85\u9047\u662f\u4e0d\u662f\uff0c\u53ea\u8981\u8ba9\u90a3\u4e9b\u4e0b\u5c97\u804c\u5de5\u8fd8\u8981\u4e00\u4efd\u80fd\u517b\u5bb6\u7cca\u53e3\u7684\u4e8b\u60c5\u53bb\u505a\uff0c\u5c06\u751f\u8ba1\u7ef4\u6301\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u6a61\u6811\u56ed\u5c31\u7b97\u901a\u60c5\u8fbe\u7406\u4e5f\u5b89\u6392\u4e0d\u4e86\u4e00\u4e24\u767e\u53f7\u4eba\uff0c\u7530\u5e08\u5085\u8fd8\u6709\u5176\u4ed6\u522b\u7684\u6253\u7b97\u5417\uff1f\u201d\u5f20\u606a\u89c1\u7530\u529b\u5c71\u53c8\u770b\u624b\u8868\uff0c\u77e5\u9053\u4ed6\u4e0e\u4eba\u7ea6\u597d\u4e86\u8981\u8d76\u7740\u65f6\u95f4\u53bb\u673a\u68b0\u5382\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u6837\u5427\uff0c\u7b49\u5230\u4e94\u70b9\u949f\uff0c\u6211\u4eec\u518d\u8fc7\u6765\u5e2e\u4f60\u53bb\u5bbf\u820d\u697c\u524d\u9001\u8863\u670d\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u7530\u529b\u5c71\u9a91\u7740\u81ea\u884c\u8f66\u79bb\u5f00\uff0c\u5b59\u9759\u6aac\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff1a\u201c\u4e3a\u4ec0\u4e48\u53c8\u626f\u4e0a\u6211\uff0c\u6211\u6709\u4f60\u90a3\u4e48\u60a0\u95f2\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u7231\u53bb\u4e0d\u53bb\uff0c\u6211\u5c31\u662f\u968f\u53e3\u4e00\u8bf4\uff0c\u4e5f\u77e5\u9053\u4f60\u5f88\u5fd9\u3002\u201d\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u4e0d\u8fc7\u4ed6\u7b54\u5e94\u7ed9\u5b59\u9759\u6aac\u53bb\u505a\u82e6\u529b\uff0c\u8fd8\u662f\u8981\u5148\u966a\u7740\u5979\u53bb1978\u6e05\u70b9\u6446\u653e\u8fd9\u51e0\u5929\u8d2d\u8fdb\u6765\u7684\u5668\u76bf\u3001\u9152\u6c34\u7b49\u7b49\uff0c\u4e3a1978\u6b63\u5f0f\u8425\u4e1a\u505a\u51c6\u5907\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6328\u5230\u4e94\u70b9\u949f\u5de6\u53f3\uff0c\u5f20\u606a\u53bb\u5317\u95e8\u7684\u6d17\u8863\u623f\u53bb\u7b49\u7530\u529b\u5c71\uff0c\u5b59\u9759\u6aac\u5634\u91cc\u8bf4\u6ca1\u7a7a\u95f2\uff0c\u8fd8\u662f\u8ddf\u7740\u5f20\u606a\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u662f\u8981\u5c06\u6d17\u51c0\u7684\u8863\u670d\u9001\u5230\u5404\u4e2a\u5bbf\u820d\uff0c\u6bd4\u6536\u8863\u670d\u5de5\u4f5c\u91cf\u5927\uff0c\u8ba9\u5b59\u9759\u6aac\u5728\u5bbf\u820d\u5927\u5385\u5916\u5b88\u7740\u4e09\u8f6e\u8f66\uff0c\u5f20\u606a\u4e0e\u7530\u529b\u5c71\u722c\u4e0a\u722c\u4e0b\u9001\u8863\u670d\u3002</p><p>\u3000\u3000\u5f97\u627f\u8ba4\u7f8e\u5973\u7684\u9b45\u529b\uff0c\u5f20\u606a\u4e0e\u7530\u529b\u5c71\u5c06\u4e00\u680b\u697c\u8dd1\u5b8c\u4e0b\u6765\u65f6\uff0c\u770b\u5230\u4e00\u7fa4\u7537\u751f\u63d0\u7740\u88c5\u810f\u8863\u670d\u7684\u65b9\u4fbf\u888b\u6392\u7740\u961f\u7b49\u5b59\u9759\u6aac\u7ed9\u4ed6\u4eec\u767b\u8bb0\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5973\u5b69\u5b50\u957f\u7684\u6f02\u4eae\u5c31\u662f\u5360\u4fbf\u5b9c\u2026\u2026\u201d\u5f20\u606a\u7ad9\u5728\u4e00\u65c1\u8bf7\u7530\u529b\u5c71\u62bd\u652f\u70df\u6b47\u4e00\u6b47\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u770b\u7740\u5f20\u606a\u5634\u89d2\u7684\u6d45\u7b11\uff0c\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u8fd8\u5f97\u4e00\u672c\u6b63\u7ecf\u7684\u5e2e\u7740\u9001\u810f\u8863\u670d\u6d17\u7684\u7537\u751f\u767b\u8bb0\u3002</p><p>\u3000\u3000\u201c\u672c\u59d1\u5a18\u4eca\u5929\u7b97\u662f\u7ed9\u4f60\u6bc1\u4e86\uff01\u201d\u5750\u7740\u5355\u8f66\u540e\u67b6\u53bb\u7b2c\u4e8c\u680b\u7537\u751f\u5bbf\u820d\u697c\u91cc\uff0c\u5b59\u9759\u6aac\u6390\u7740\u5f20\u606a\u7684\u8170\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u633a\u6709\u6210\u5c31\u611f\u7684\uff0c\u7530\u5e08\u5085\u90fd\u8bf4\u4f60\u5728\u697c\u4e0b\u7ad9\u5341\u5206\u949f\u62b5\u4ed6\u6765\u8dd1\u5341\u8d9f\u4e86\uff1f\u201d\u5f20\u606a\u626d\u7740\u8eab\u5b50\u8981\u4ece\u5979\u7684\u5c0f\u624b\u91cc\u6323\u624e\u51fa\u6765\uff0c\u8fd9\u59ae\u5b50\u6390\u4eba\u662f\u62ff\u6307\u7532\u5c16\u6390\u7684\u3002</p><p>\u3000\u3000\u201c\u4f60\u660e\u5929\u6302\u4e00\u4e2a\u724c\uff0c\u4e0a\u9762\u5199\u7740\u2018\u5f20\u606a\u6536\u8863\u5904\u2019\uff0c\u53bb\u4f60\u4eec\u4e1c\u5927\u7684\u5973\u751f\u5bbf\u820d\u697c\u5927\u5385\u90a3\u91cc\u5750\u534a\u5c0f\u65f6\u770b\u770b\uff0c\u4fdd\u7ba1\u4e5f\u80fd\u4eab\u53d7\u5230\u8fd9\u79cd\u6210\u5c31\u611f\u3002\u201d\u5b59\u9759\u6aac\u65e9\u5c31\u4ece\u675c\u98de\u4ed6\u4eec\u53e3\u91cc\u77e5\u9053\u6c14\u7403\u7684\u4e8b\u60c5\uff0c\u8fd9\u65f6\u5019\u5f20\u606a\u6562\u5230\u5973\u751f\u5bbf\u820d\u4eae\u51fa\u540d\u53f7\uff0c\u6307\u4e0d\u5b9a\u4f1a\u5f15\u8d77\u56f4\u89c2\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u4e00\u8d9f\u8dd1\u4e0b\u6765\uff0c\u5df2\u7ecf\u662f\u591c\u91cc\u4e03\u70b9\u949f\u3002</p><p>\u3000\u3000\u5c06\u6536\u5230\u7684\u810f\u8863\u670d\u9001\u56de\u6d17\u8863\u623f\uff0c\u7530\u529b\u5c71\u558a\u4e0a\u4e00\u4e2a\u4eba\uff0c\u8bf4\u662f\u4e00\u540c\u5230\u9694\u58c1\u7684\u5c0f\u9910\u9986\u91cc\u5403\u996d\u3002\u90a3\u4eba\u6709\u4e9b\u820d\u4e0d\u5f97\u94b1\uff0c\u8bf4\u9053\uff1a\u201c\u5e97\u91cc\u518d\u7b49\u4e00\u4f1a\u513f\u5c31\u8981\u5f00\u996d\uff0c\u4e0d\u5982\u5e97\u91cc\u4e00\u8d77\u5403\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53c8\u4e0d\u7528\u4f60\u638f\u94b1\uff0c\u4f60\u5fc3\u75bc\u4ec0\u4e48\u52b2\uff1f\u201d\u7530\u529b\u5c71\u63a8\u90a3\u4eba\u4e00\u628a\uff0c\u8ddf\u5f20\u606a\u4ecb\u7ecd\u8bf4\uff0c\u201c\u4ed6\u53ef\u80fd\u4f60\u4e0d\u5927\u719f\u6089\uff0c\u674e\u5065\u5149\uff0c\u4e5f\u662f\u5370\u67d3\u5382\u7684\u5de5\u4eba\u4ee3\u8868\uff0c\u8ddf\u7740\u6211\u5374\u7ed9\u6536\u62fe\u7684\u5012\u9709\u86cb\uff0c\u6ca1\u6709\u627e\u5de5\u4f5c\uff0c\u5c31\u662f\u4e00\u76f4\u5728\u6d17\u8863\u623f\u91cc\u5e2e\u5fd9\u2026\u2026\u201d</p><p>\u3000\u3000\u9664\u7530\u529b\u5c71\uff0c\u5f20\u606a\u5bf9\u4e3d\u4e30\u5370\u67d3\u5382\u7684\u5176\u4ed6\u5de5\u4eba\u4ee3\u8868\u8fd8\u771f\u6ca1\u6709\u7559\u610f\u3002</p><p>\u3000\u3000\u201c\u4f60\u5bb6\u4eb2\u621a\uff1f\u201d\u674e\u5065\u5149\u5c0f\u58f0\u95ee\u7530\u529b\u5c71\u3002</p><p>\u3000\u3000\u201c\u7741\u5f00\u773c\u775b\u518d\u4ed4\u7ec6\u8ba4\u8ba4\uff0c\u201d\u7530\u529b\u5c71\u7b11\u7740\u89e3\u91ca\u8bf4\uff0c\u201c\u4e94\u6708\u8ddf\u7f57\u4e66\u8bb0\u53bb\u5382\u91cc\u7684\u90a3\u4e2a\u9752\u5e74\u3002\u70ed\u5fc3\u5e2e\u6211\u6536\u8863\u670d\u3001\u9001\u8863\u670d\u534a\u5929\u4e86\uff0c\u600e\u4e48\u4e0d\u8bf7\u4eba\u5bb6\u5403\u987f\u996d\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u6211\u8bf4\u600e\u4e48\u89c9\u5f97\u9762\u719f\u5462\uff0c\u53ea\u662f\u6ca1\u6562\u8ba4\u554a\u3002\u201d\u674e\u5065\u5149\u4f38\u624b\u7d27\u7d27\u63e1\u8fc7\u6765\uff0c\u201c\u8ddf\u4f60\u7f57\u4e66\u8bb0\u8ba4\u8bc6\u5427\uff1f\u6709\u4e9b\u60c5\u51b5\u4f60\u8981\u5e2e\u6211\u4eec\u8ddf\u7f57\u4e66\u8bb0\u53cd\u5e94\u53cd\u5e94\u2026\u2026\u603b\u516c\u53f8\u8ddf\u8f7b\u5de5\u5c40\u7684\u90a3\u7fa4\u73a9\u827a\u771f\u4e0d\u662f\u73a9\u827a\u513f\uff0c\u8001\u7530\u8fd8\u662f\u5168\u56fd\u5927\u4eba\u4ee3\u8868\u5462\uff0c\u5c31\u8fd9\u6837\u7ed9\u4ed6\u4eec\u6574\u4e0b\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5e9f\u8bdd\u8fd9\u4e48\u591a\u5e72\u4ec0\u4e48\uff0c\u8c01\u4e5f\u6ca1\u6709\u6574\u6211\uff0c\u6211\u81ea\u5df1\u7a9d\u5fc3\u8f9e\u804c\u8fd8\u4e0d\u884c\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u966a\u7740\u7530\u529b\u5c71\u8d70\u9001\u534a\u5929\u8863\u670d\u4e86\uff0c\u4ed6\u6123\u662f\u6ca1\u6709\u95ee\u8d77\u8fc7\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u968f\u4fbf\u4e3a\u81ea\u5df1\u62b1\u6028\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u65c1\u8fb9\u7684\u5c0f\u9910\u9986\uff0c\u7092\u4e86\u4e24\u4e2a\u83dc\uff0c\u4e5f\u6ca1\u6709\u53eb\u9152\u559d\uff0c\u7b49\u5230\u4e5d\u70b9\u949f\uff0c\u7530\u529b\u5c71\u4ed6\u4eec\u8fd8\u8981\u53bb\u5b66\u6821\u6536\u9001\u8863\u670d\u3002</p><p>\u3000\u3000\u5373\u4f7f\u6a61\u6811\u56ed\u8003\u8651\u5b89\u6392\u4e00\u4e9b\u603b\u52a1\u4e0a\u7684\u5de5\u4f5c\u5c97\u4f4d\u51fa\u6765\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u4e00\u4e0b\u5b50\u5c31\u89e3\u51b3\u4e00\u4e24\u767e\u53f7\u4eba\u7684\u5c31\u4e1a\u95ee\u9898\uff0c\u5f20\u606a\u5c31\u662f\u60f3\u95ee\u7530\u529b\u5c71\u6709\u6ca1\u6709\u4ec0\u4e48\u60f3\u6cd5\uff0c\u5403\u996d\u65f6\uff0c\u5c31\u76f4\u63a5\u63d0\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e24\u5e74\uff0c\u56fd\u6709\u4f01\u4e1a\u6548\u76ca\u4e0d\u597d\uff0c\u597d\u4e9b\u5382\u5b50\u90fd\u52c9\u5f3a\u62d6\u7740\uff0c\u4e0b\u5c97\u804c\u5de5\u4e5f\u591a\u4e86\u8d77\u6765\uff0c\u5bf9\u539f\u5148\u7684\u5382\u5b50\u4f9d\u8d56\u60ef\u4e86\uff0c\u4e0b\u5c97\u518d\u5c31\u4e1a\u5c31\u5f88\u56f0\u96be\uff0c\u4e4b\u524d\u6ca1\u6709\u8fd9\u4e48\u6df1\u7684\u8ba4\u8bc6\uff0c\u8fd9\u51e0\u6708\u7b97\u662f\u6709\u4e9b\u611f\u89e6\u2026\u2026\u201d\u7530\u529b\u5c71\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u4e0d\u8981\u770b\u8001\u7530\u7ed9\u5b89\u6392\u5728\u603b\u516c\u53f8\u7684\u5de5\u4f1a\u91cc\uff0c\u5370\u67d3\u5382\u597d\u4e9b\u4eba\u91cd\u65b0\u627e\u5de5\u4f5c\uff0c\u90fd\u662f\u4ed6\u5e2e\u7740\u8dd1\u7684\uff0c\u8bf7\u5ba2\u5403\u996d\u90fd\u662f\u4ed6\u81ea\u5df1\u5f80\u91cc\u57ab\u94b1\u2026\u2026\u201d\u674e\u5065\u5149\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5c31\u4f60\u5e9f\u8bdd\u591a\uff0c\u8981\u4e0d\u4f60\u6765\u7ed9\u8bf4\u8bf4\uff1f\u201d\u7530\u529b\u5c71\u4e0d\u8ba9\u674e\u5065\u5149\u80e1\u4e71\u63d2\u5634\u5c94\u6389\u81ea\u5df1\u7684\u8bdd\u9898\uff0c\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u8fd9\u4e2a\u91cc\u9762\u6709\u4e00\u4e2a\u518d\u5c31\u4e1a\u6e20\u9053\u7684\u95ee\u9898\uff0c\u5de5\u4f5c\u5c97\u4f4d\u8fd8\u662f\u6709\u7684\uff0c\u4f46\u662f\u597d\u4e9b\u5c97\u4f4d\u770b\u5230\u662f\u56db\u4e94\u5341\u5c81\u7684\u4eba\u4e0a\u53bb\u5e94\u5f81\uff0c\u5c31\u7ed9\u95ed\u95e8\u7fb9\u5403\u3002\u8fd9\u4e2a\u4e0e\u4f01\u4e1a\u6c9f\u901a\u7684\u6e20\u9053\uff0c\u4e0d\u5e94\u8be5\u662f\u4e0b\u5c97\u5de5\u4eba\u81ea\u5df1\u53bb\u8ddf\u4f01\u4e1a\u5efa\u7acb\u3002\u597d\u4e9b\u4eba\u8001\u5b9e\u5df4\u4ea4\u7684\uff0c\u53ea\u6653\u5f97\u505a\u81ea\u5df1\u624b\u5934\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u4fe1\u606f\u91cf\u4e5f\u5f88\u7a84\uff0c\u5c31\u7b97\u6709\u5de5\u4f5c\u5c97\u4f4d\u5728\u90a3\u91cc\u7b49\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u6653\u5f97\u600e\u4e48\u53bb\u4e89\u53d6\u3002\u8fd8\u6709\u4e00\u4e2a\u5c31\u662f\u518d\u5c31\u4e1a\u57f9\u8bad\u7684\u95ee\u9898\uff0c\u5de5\u4f5c\u5c97\u4f4d\u7684\u6027\u8d28\u53c8\u4e0d\u53ef\u80fd\u5b8c\u5168\u76f8\u540c\uff0c\u4e09\u56db\u5341\u5c81\u7684\u4eba\uff0c\u6b63\u662f\u5bb6\u91cc\u7684\u9876\u6881\u67f1\uff0c\u8c01\u9ad8\u5174\u518d\u53bb\u65b0\u7684\u5de5\u5382\u5f53\u5f92\u5de5\uff1f\u6211\u662f\u529b\u6709\u672a\u902e\uff0c\u6211\u60f3\u5e02\u91cc\u5e94\u8be5\u51fa\u94b1\u6210\u7acb\u4e00\u4e2a\u4fc3\u8fdb\u518d\u5c31\u4e1a\u7684\u673a\u6784\uff0c\u5efa\u7acb\u518d\u5c31\u4e1a\u6e20\u9053\u3001\u8fdb\u884c\u518d\u5c31\u4e1a\u57f9\u8bad\u4e4b\u7c7b\u7684\uff0c\u6211\u5199\u4e86\u4e00\u4e2a\u6750\u6599\uff0c\u524d\u4e9b\u65f6\u95f4\u4ea4\u7ed9\u5e02\u52b3\u52a8\u5c40\u4ee5\u53ca\u5206\u7ba1\u7684\u80e1\u5e02\u957f\u90fd\u4e00\u76f4\u6ca1\u6709\u5f97\u5230\u56de\u5e94\u3002\u4f60\u8981\u662f\u5173\u5fc3\u8fd9\u4e2a\u95ee\u9898\uff0c\u6211\u8fd9\u91cc\u8fd8\u6709\u4e00\u4efd\uff0c\u4ea4\u7ed9\u4f60\u770b\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ece\u7530\u529b\u5c71\u624b\u91cc\u63a5\u8fc7\u4ed6\u51c6\u5907\u597d\u7684\u6750\u6599\uff0c\u820d\u4e0d\u5f97\u590d\u5370\u7684\u94b1\uff0c\u6750\u6599\u90fd\u6284\u5728\u4fe1\u7eb8\u4e0a\uff0c\u4fe1\u7eb8\u7684\u62ac\u5934\u8fd8\u5370\u7740\u201c\u4e3d\u4e30\u5370\u67d3\u5382\u5382\u957f\u5ba4\u201d\u7684\u5b57\u6837\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u7530\u529b\u5c71\u4ed6\u4eec\u8fd8\u8981\u56de\u6d17\u8863\u623f\u505a\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u544a\u8bc9\u7530\u529b\u5c71\u4e0d\u8981\u5fd8\u4e86\u660e\u5929\u53bb\u627e\u6a61\u6811\u56ed\u7684\u4eba\u78b0\u78b0\u8fd0\u6c14\uff0c\u8fd8\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u4ed6\u4eec\u8981\u662f\u4e00\u70b9\u60c5\u9762\u90fd\u4e0d\u8bb2\uff0c\u9886\u7740\u5de5\u4eba\u5c06\u51e0\u4e2a\u51fa\u5165\u53e3\u5835\u4e0a\uff0c\u5382\u5b50\u4e0d\u80fd\u8fd9\u4e48\u4fbf\u5b9c\u5c31\u7ed9\u4ed6\u4eec\u5360\u8fc7\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54ea\u80fd\u554a\uff1f\u4eba\u4eec\u53ef\u662f\u6b63\u7ecf\u5230\u5efa\u90ba\u6765\u6295\u8d44\u7684\uff0c\u80fd\u5e2e\u5fd9\u89e3\u51b3\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u201d\u7530\u529b\u5c71\u4e5f\u4e0d\u591a\u95ee\u4ec0\u4e48\uff0c\u5c31\u62c9\u7740\u674e\u5065\u5149\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5012\u662f\u674e\u5065\u5149\u89c9\u5f97\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u8c08\u5f00\uff0c\u6709\u4e9b\u4e0d\u613f\u610f\u79bb\u5f00\uff0c\u51fa\u4e86\u5c0f\u9910\u9986\u8fd8\u8ddf\u7530\u529b\u5c71\u62b1\u6028\uff1a\u201c\u4f60\u5012\u662f\u4ec0\u4e48\u90fd\u4e0d\u95ee\uff0c\u4f60\u5230\u5e95\u77e5\u9053\u4eba\u5bb6\u662f\u8c01\uff0c\u5c31\u5c06\u6750\u6599\u4ea4\u7ed9\u4eba\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u4e0d\u4e86\u4eca\u665a\u4e0d\u7761\u89c9\u591a\u6284\u4e00\u4efd\uff0c\u51e0\u5341\u9875\u4fe1\u7eb8\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u4e5f\u771f\u662f\u6709\u610f\u601d\uff0c\u4ed6\u597d\u50cf\u5f88\u6307\u671b\u4f60\u80fd\u5e2e\u4ed6\u5c06\u6750\u6599\u4ea4\u7ed9\u7f57\u541b\uff0c\u5374\u4ec0\u4e48\u4e5f\u4e0d\u660e\u8bf4\uff0c\u771f\u662f\u6709\u610f\u601d\u2026\u2026\u201d\u5b59\u9759\u6aac\u770b\u7740\u7530\u529b\u5c71\u4ed6\u4eec\u80cc\u5f71\u79bb\u5f00\u3002</p><p>\u3000\u3000\u201c\u53ef\u80fd\u78b0\u4e86\u4e0d\u5c11\u58c1\u5427\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u7530\u529b\u5c71\u8eab\u4e0a\u6709\u7740\u4e0d\u5c11\u65f6\u4ee3\u7684\u5c40\u9650\u6027\uff0c\u4f46\u662f\u6bd4\u8d77\u90a3\u4e9b\u5b98\u8001\u7237\uff0c\u7530\u529b\u5c71\u8981\u503c\u5f97\u5c0a\u656c\u591a\u4e86\u3002</p><p>\u3000\u3000\u9a91\u7740\u5355\u8f66\u8f7d\u7740\u5b59\u9759\u6aac\u56de1978\uff0c\u5f20\u606a\u5728\u9152\u5427\u89d2\u843d\u91cc\u5750\u4e0b\u6765\uff0c\u8ba4\u771f\u7684\u770b\u7740\u7530\u529b\u5c71\u6240\u5199\u7684\u6750\u6599\u3002</p><p>\u3000\u3000\u56fd\u6709\u4f01\u4e1a\u4e0b\u5c97\u804c\u5de5\u5927\u89c4\u6a21\u7684\u51fa\u73b0\u8fd8\u662f\u5728\u4e5d\u516b\u5e74\u4e4b\u540e\uff0c\u4e3a\u9769\u9664\u65e7\u5f0a\uff0c\u89e3\u51b3\u56fd\u6709\u4f01\u4e1a\u6548\u7387\u4f4e\u4e0b\u7684\u95ee\u9898\uff0c\u5168\u9762\u5b9e\u65bd\u56fd\u6709\u4f01\u4e1a\u6539\u5236\uff0c\u52a0\u4e0a\u56fd\u9000\u6c11\u8fdb\u7684\u5927\u80cc\u666f\uff0c\u4ec5\u4e5d\u516b\u5e74\u5e95\u5168\u56fd\u5c31\u7d2f\u79ef\u589e\u52a0\u4e0b\u5c97\u804c\u5de5\u4e00\u5343\u4f59\u4e07\u4eba\u3002</p><p>\u3000\u3000\u5176\u4ed6\u5230\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u4e0b\u5c97\u804c\u5de5\u7684\u95ee\u9898\u5df2\u7ecf\u4e0d\u5bb9\u5ffd\u89c6\u4e86\uff0c\u4f46\u662f\u56fd\u5185\u4e0d\u5bb9\u5ffd\u89c6\u7684\u95ee\u9898\u8fd8\u6709\u5f88\u591a\uff0c\u6bd4\u5982\u8bf4\u516c\u52a1\u5458\u7684\u4f4f\u623f\u95ee\u9898\uff0c\u4e8b\u60c5\u603b\u8981\u4e00\u4ef6\u4e00\u4ef6\u7684\u53bb\u89e3\u51b3\u3002</p><p>\u3000\u3000\u4e0b\u5c97\u804c\u5de5\u751f\u6d3b\u4fdd\u969c\u4ee5\u53ca\u518d\u5c31\u4e1a\u76f8\u5173\u65b9\u9762\u7684\u653f\u7b56\u4e00\u76f4\u62d6\u5230\u4e5d\u516b\u5e74\u79cb\u540e\u624d\u9646\u7eed\u51fa\u53f0\uff0c\u5730\u65b9\u4e0a\u7684\u63aa\u65bd\u5219\u66f4\u6ede\u540e\u4e00\u4e9b\uff0c\u7279\u522b\u662f\u7ecf\u6d4e\u57fa\u7840\u5dee\u7684\u5730\u533a\uff0c\u6761\u4ef6\u4f1a\u66f4\u52a0\u8270\u96be\u3002</p><p>\u3000\u3000\u4e0d\u662f\u4e0b\u5c97\u804c\u5de5\u6216\u5176\u5bb6\u5c5e\uff0c\u5f88\u96be\u4f53\u4f1a\u8fd9\u4e2a\u65f6\u4ee3\u5e26\u7ed9\u4ed6\u4eec\u7684\u9635\u75db\u662f\u591a\u4e48\u7684\u96be\u6328\u3002</p><p>\u3000\u3000\u201c\u6211\u80fd\u770b\u770b\uff1f\u201d\u5b59\u9759\u6aac\u5e2e\u5f20\u606a\u62ff\u4e86\u4e00\u676f\u996e\u6599\u8fc7\u6765\uff0c\u9012\u7ed9\u4ed6\uff0c\u81ea\u5df1\u4e5f\u62ff\u4e86\u676f\u7ea2\u7eff\u989c\u8272\u5206\u5c42\u3001\u676f\u58c1\u4e0a\u5d4c\u7740\u4e00\u679a\u6a31\u6843\u7684\u996e\u6599\uff0c\u5750\u5728\u5f20\u606a\u5bf9\u9762\u6709\u6ecb\u6709\u5473\u7684\u5438\u7740\uff0c\u773c\u775b\u5374\u5f0a\u7740\u5f20\u606a\u624b\u91cc\u7684\u6750\u6599\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6750\u6599\u9012\u7ed9\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u529e\u8fd9\u6837\u4e00\u4e2a\u670d\u52a1\u673a\u6784\u4e0d\u8981\u56db\u5341\u4e07\u7684\u7ecf\u8d39\uff0c\u5e02\u91cc\u4e3a\u4ec0\u4e48\u4e0d\u7ed9\u4ed6\u4eec\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u51ed\u4ec0\u4e48\u7ed9\u4ed6\u4eec\uff0c\u4e3a\u4ec0\u4e48\u8981\u7ed9\u4ed6\u4eec\uff0c\u8c01\u7ed9\u4ed6\u4eec\uff0c\u94b1\u4ece\u54ea\u91cc\u6765\uff1f\u201d\u5f20\u606a\u5634\u89d2\u6302\u7740\u51b7\u6f20\u7684\u7b11\uff0c\u6709\u65f6\u5019\u4f1a\u7981\u4e0d\u4f4f\u7684\u51b7\u6f20\uff0c\u4e0d\u662f\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u800c\u662f\u5bf9\u67d0\u79cd\u5e0c\u671b\u7684\u51b7\u6f20\uff0c\u4ed6\u592a\u719f\u6089\u8fd9\u4e2a\u4f53\u7cfb\u4e86\uff0c\u201c\u8fd8\u662f\u8fd9\u4e2a\u6750\u6599\u91cc\u7684\u6570\u636e\uff0c\u8c01\u4f1a\u627f\u8ba4\uff1f\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u8fd8\u662f\u505a\u4e86\u4e00\u4e9b\u7ec6\u81f4\u6df1\u5165\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0b\u5c97\u804c\u5de5\u6f6e\u8fd8\u6ca1\u6709\u6d8c\u73b0\uff0c\u4f46\u662f\u622a\u6b62\u5230\u4e5d\u4e03\u5e74\uff0c\u5efa\u90ba\u5e02\u4ee5\u201c\u653e\u957f\u5047\u201d\u3001\u201c\u505c\u85aa\u7559\u804c\u201d\u3001\u201c\u4e24\u4e0d\u6293\u201d\u7b49\u540d\u4e49\u5b9e\u9645\u4e0b\u5c97\u5931\u4e1a\u7684\u804c\u5de5\u5c06\u8fd1\u4e24\u4e07\u4eba\uff0c\u5176\u4e2d\u8fd1\u4e00\u534a\u4eba\u662f\u5c31\u4e1a\u5f31\u52bf\u7fa4\u4f53\u3002</p><p>\u3000\u3000\u8fd9\u662f\u7530\u529b\u5c71\u6750\u6599\u91cc\u53cd\u5e94\u7684\u6570\u636e\uff0c\u5f20\u606a\u80fd\u591f\u60f3\u8c61\u5b9e\u9645\u7684\u6570\u636e\u53ef\u80fd\u8fd8\u8981\u4e25\u91cd\u4e00\u4e9b\uff0c\u4f46\u8fd9\u662f\u4ece\u6765\u90fd\u672a\u6ca1\u6709\u7ed9\u5b98\u65b9\u7ed9\u627f\u8ba4\u8fc7\u7684\u6570\u636e\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u8fde\u201c\u4e0b\u5c97\u201d\u8fd9\u4e2a\u8bcd\uff0c\u653f\u5e9c\u90fd\u5f88\u5fcc\u8bb3\u63d0\u8d77\u6765\uff0c\u4f55\u51b5\u8fd8\u8981\u627f\u8ba4\u5efa\u90ba\u5e02\u5185\u5b58\u5728\u4e24\u4e07\u4eba\u7684\u4e0b\u5c97\u804c\u5de5\uff1f</p><p>\u3000\u3000\u5e02\u52b3\u52a8\u90e8\u95e8\u770b\u5230\u7530\u529b\u5c71\u4e0a\u4ea4\u7684\u6750\u6599\uff0c\u5927\u6982\u6068\u4e0d\u5f97\u5403\u4e0b\u53bb\u5427\uff1f</p></article>"}}}]);
