(window.webpackJsonp=window.webpackJsonp||[]).push([[667],{690:function(p){p.exports={chapterTitle:null,title:"\u7b2c511\u7ae0 \u91ce\u5fc3",content:"<article><h2>\u7b2c511\u7ae0 \u91ce\u5fc3</h2><p>\u3000\u3000\u6b27\u7f8e\u7535\u5b50\u5382\u5546\u4e13\u4e1a\u5316\u7ecf\u8425\u601d\u8def\u5bf9\u5fb7\u5dde\u4eea\u5668\uff08TI\uff09\u6709\u4e2a\u6df1\u8fdc\u7684\u5f71\u54cd\uff0c\u4f7f\u5f97\u5fb7\u5dde\u4eea\u5668\u4e00\u76f4\u4ee5\u6765\u5c31\u6709\u8fd9\u6837\u7684\u4f20\u7edf\uff1a\u5373\u4f7f\u5728\u6280\u672f\u7814\u53d1\u4e2d\u641e\u51fa\u4ec0\u4e48\u4ea7\u54c1\u6280\u672f\uff0c\u5b81\u53ef\u5c06\u76f8\u5173\u4ea7\u54c1\u6280\u672f\u8f6c\u8ba9\u51fa\u53bb\uff0c\u5fb7\u5dde\u4eea\u5668\u4e5f\u7edd\u5c11\u6d89\u53ca\u6700\u7ec8\u4ea7\u54c1\u9886\u57df\uff0c\u800c\u662f\u4e13\u6ce8\u7684\u626e\u6f14\u534a\u5bfc\u4f53\u5143\u5668\u4ef6\u4f9b\u5e94\u5546\u4e0e\u670d\u52a1\u5546\u7684\u89d2\u8272\u3002</p><p>\u3000\u3000\u7531\u4e8e\u5fb7\u5dde\u4eea\u5668\u6709\u8fd9\u6837\u7684\u7ecf\u8425\u601d\u8def\u4e0e\u4f20\u7edf\uff0c\u90a3\u81ea\u7136\u5c31\u662f\u9526\u6e56\u73b0\u9636\u6bb5\u6700\u9002\u5408\u7684\u5408\u4f5c\u4f19\u4f34\uff0c\u5728\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5e02\u573a\u4e0d\u5b58\u5728\u76f4\u63a5\u7ade\u4e89\uff0c\u5f20\u606a\u5c31\u4e0d\u7528\u592a\u62c5\u5fc3\u5fb7\u5dde\u4eea\u5668\u4f1a\u6545\u610f\u5728\u4ea7\u54c1\u6280\u672f\u4e0a\u3001\u82af\u7247\u4f9b\u5e94\u4e0a\u5361\u9526\u6e56\u7684\u8116\u5b50\u3002</p><p>\u3000\u3000\u7531\u4e8e\u5fb7\u5dde\u4eea\u5668\u6709\u8fd9\u6837\u7684\u7ecf\u8425\u601d\u8def\u4e0e\u4f20\u7edf\uff0c\u9648\u4fe1\u751f\u5728\u4ed6\u7ed3\u675f\u5fb7\u5dde\u4eea\u5668\u7684\u670d\u52a1\u671f\u4e4b\u540e\u8f6c\u4efb\u9526\u6e56\u5e76\u4e0d\u5b58\u5728\u5b9e\u6027\u8d28\u7684\u963b\u788d\uff0c\u786e\u77e5\u9648\u4fe1\u751f\u672c\u4eba\u7684\u610f\u613f\uff0c\u5fb7\u5dde\u4eea\u5668\u9ad8\u5c42\u5305\u62ec\u9996\u5e2d\u6267\u884c\u5b98\u5b89\u5409\u4f2f\u5728\u5185\uff0c\u90fd\u53ea\u662f\u8868\u793a\u76f8\u5f53\u7684\u9057\u61be\uff1b\u4f46\u53c8\u5bf9\u9648\u4fe1\u751f\u8f6c\u4efb\u9526\u6e56\u4fc3\u8fdb\u4e24\u5bb6\u516c\u53f8\u4e4b\u95f4\u5173\u7cfb\u6709\u7740\u5f88\u9ad8\u7684\u671f\u5f85\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8f6c\u4efb\u9526\u6e56\u7684\u4e8b\u60c5\u53d6\u5f97\u5fb7\u5dde\u4eea\u5668\u9ad8\u5c42\u7684\u8c05\u89e3\uff0c\u53c8\u9274\u4e8e\u9526\u6e56\u7d27\u63a5\u4e0b\u6765\u5c31\u60f3\u4ece\u5fb7\u5dde\u4eea\u5668\u5f15\u8fdb\u534a\u5bfc\u4f53\u82af\u7247\u5c01\u88c5\u3001\u6210\u54c1\u6d4b\u8bd5\u4e0e\u624b\u673a\u6210\u54c1\u751f\u4ea7\u4e24\u4e2a\u9879\u76ee\uff0c\u5f20\u606a\u5e0c\u671b\u524d\u671f\u7684\u9879\u76ee\u8c08\u5224\u5c31\u76f4\u63a5\u6210\u4e3a\u9648\u4fe1\u751f\u8f6c\u4efb\u9526\u6e56\u7684\u8fc7\u5ea6\u671f\uff0c\u7531\u4e8e\u9648\u4fe1\u751f\u4e0e\u5fb7\u5dde\u4eea\u5668\u7684\u8058\u7528\u671f\u8fd8\u6709\u4e00\u4e2a\u534a\u6708\u7684\u65f6\u95f4\uff0c\u6240\u4ee5\u9700\u8981\u4e0e\u5fb7\u5dde\u4eea\u5668\u5c31\u6b64\u7b7e\u7f72\u4e00\u4efd\u6b63\u5f0f\u7684\u8c05\u89e3\u534f\u8bae\uff0c\u624d\u80fd\u8ba9\u9648\u4fe1\u751f\u5728\u524d\u671f\u9879\u76ee\u8c08\u5224\u4e2d\u4ee3\u8868\u9526\u6e56\u7684\u6743\u76ca\uff0c\u4e5f\u662f\u4e3a\u9648\u4fe1\u751f\u8f6c\u4efb\u9526\u6e56\u5f7b\u5e95\u94fa\u5e73\u9053\u8def\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u534a\u5bfc\u4f53\u82af\u7247\u5c01\u88c5\u3001\u6210\u54c1\u6d4b\u8bd5\u9879\u76ee\u5e76\u4e0d\u6d89\u53ca\u5173\u952e\u7684\u6838\u5fc3\u6280\u672f\uff0c\u800c\u4e14\u8fd9\u4e00\u5757\u4e1a\u52a1\uff0c\u76f8\u5bf9\u8bf4\u6765\u52b3\u52a8\u529b\u6210\u672c\u5360\u4e86\u5f88\u9ad8\u7684\u6bd4\u4f8b\uff0c\u76f8\u5173\u4ea7\u4e1a\u8f6c\u79fb\u5230\u52b3\u529b\u52a8\u6210\u672c\u4f4e\u5ec9\u7684\u4e2d\u56fd\uff0c\u5fb7\u5dde\u4eea\u5668\u81ea\u8eab\u4e5f\u6709\u8fd9\u6837\u7684\u610f\u5411\uff0c\u8fd9\u6837\u4e5f\u624d\u80fd\u5728\u5927\u4e2d\u534e\u533a\u7684\u82af\u7247\u4ee3\u5de5\u4e1a\u52a1\u4e0a\u4e0e\u53f0\u6e7e\u7684\u53f0\u79ef\u7535\u7ade\u4e89\u7ee7\u7eed\u4fdd\u6301\u4f18\u52bf\uff0c\u5408\u8d44\u5c31\u6210\u4e86\u6700\u4f73\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\u63d0\u4f9b\u5b8c\u6574\u7684\u6280\u672f\u4e0e\u5c11\u91cf\u8d44\u91d1\uff0c\u9526\u6e56\u63d0\u4f9b\u751f\u4ea7\u57fa\u5730\u4e0e\u4e3b\u8981\u5efa\u5382\u8d44\u91d1\uff0c\u5f20\u606a\u4e0d\u592a\u8ba1\u8f83\u524d\u671f\u7684\u5229\u76ca\u5f97\u5931\uff0c\u4f46\u662f\u8981\u6c42\u540e\u7eed\u7684\u6280\u672f\u5f00\u53d1\u4ea4\u4e5f\u7531\u4e24\u5bb6\u5408\u8d44\u7684\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u8fdb\u884c\uff0c\u5927\u4e0d\u4e86\u6cf0\u4e16\u5149\u7535\u7814\u7a76\u9662\u66f4\u540d\u4e3a\u6cf0\u5fd2\u5fae\u7535\u5b50\u4e0e\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\uff0c\u7edd\u4e0d\u5e0c\u671b\u7b49\u73b0\u5728\u7684\u5c01\u88c5\u6280\u672f\u8fc7\u65f6\u4e4b\u540e\uff0c\u8fd8\u8981\u4ece\u5fb7\u5dde\u4eea\u5668\u91cd\u65b0\u5f15\u8fdb\u65b0\u7684\u6280\u672f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\u9664\u4e86\u4e13\u7cbe\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\u4e4b\u5916\uff0c\u624b\u91cc\u8fd8\u6709\u5b8c\u6574\u7684\u624b\u673a\u4ea7\u54c1\u6280\u672f\uff0c\u4f46\u662f\u8fd9\u4e9b\u4ea7\u54c1\u6280\u672f\uff0c\u4e0e\u8bfa\u57fa\u4e9a\u3001\u6469\u6258\u7f57\u62c9\u3001\u7231\u7acb\u4fe1\u8fd9\u4e9b\u4e13\u4e1a\u751f\u4ea7\u624b\u673a\u4ea7\u54c1\u7684\u7535\u5b50\u5382\u5546\u76f8\u6bd4\uff0c\u5c31\u8981\u6574\u6574\u843d\u540e\u4e00\u4ee3\u3002</p><p>\u3000\u3000\u6309\u7167\u5fb7\u5dde\u4eea\u5668\u4e00\u60ef\u7684\u7ecf\u8425\u601d\u8def\uff0c\u4ed6\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u5bf9\u9526\u6e56\u541d\u556c\u4ed6\u4eec\u7684\u624b\u673a\u4ea7\u54c1\u6280\u672f\uff0c\u4e0d\u6d89\u53ca\u6838\u5fc3\u6280\u672f\u7684\u8f6c\u8ba9\uff0c\u901a\u8fc7\u7f8e\u56fd\u56fd\u5bb6\u5ba1\u67e5\u4e5f\u662f\u4f1a\u6709\u4ec0\u4e48\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u62ff\u5fb7\u5dde\u4eea\u5668\u7684\u624b\u673a\u4ea7\u54c1\u6280\u672f\u7ec4\u88c5\u51fa\u6765\u7684\u539f\u578b\u673a\uff0c\u5f20\u606a\u867d\u7136\u65e0\u6cd5\u4ece\u6280\u672f\u4e0a\u7ed9\u4e88\u4ec0\u4e48\u8bc4\u4ef7\uff0c\u4f46\u662f\u76f4\u89c2\u4e0a\u8ddf\u79fb\u52a8\u901a\u8baf\u6a21\u62df\u4fe1\u53f7\u65f6\u4ee3\u7684\u5927\u54e5\u5927\u6709\u5f97\u4e00\u62fc\u3002</p><p>\u3000\u3000\u4eca\u540e\u5341\u5e74\uff0c\u624b\u673a\u4ea7\u54c1\u8bbe\u8ba1\u7684\u601d\u8def\u6c38\u8fdc\u90fd\u805a\u7126\u5728\u4e09\u4e2a\u8bcd\u516d\u4e2a\u5b57\u4e0a\uff1a\u201c\u5927\u65b9\u3001\u96c5\u81f4\u3001\u65f6\u5c1a\u201d\uff0c\u5fb7\u5dde\u4eea\u5668\u624b\u673a\u4ea7\u54c1\u6280\u672f\u62fc\u88c5\u51fa\u6765\u7684\u539f\u578b\u673a\uff0c\u79bb\u4e0a\u8ff0\u8981\u6c42\u5dee\u8ddd\u6709\u5341\u4e07\u516b\u5343\u91cc\u4e4b\u9065\u3002</p><p>\u3000\u3000\u6240\u8c13\u804a\u80dc\u4e8e\u65e0\uff0c\u6709\u4e0e\u5927\u54e5\u5927\u76f8\u5ab2\u7f8e\u7684\u5168\u5957\u624b\u673a\u4ea7\u54c1\u6280\u672f\uff0c\u603b\u6bd4\u4ec0\u4e48\u90fd\u6ca1\u6709\u5f3a\u3002</p><p>\u3000\u3000\u9664\u4e86\u9009\u62e9\u4e0e\u5fb7\u5dde\u4eea\u5668\u5408\u4f5c\u4e4b\u5916\uff0c\u5f20\u606a\u6839\u672c\u5c31\u6ca1\u6709\u4fe1\u5fc3\u4ece\u8bfa\u57fa\u4e9a\u3001\u6469\u6258\u7f57\u6258\u3001\u7231\u7acb\u4fe1\u7b49\u624b\u673a\u5382\u5546\u624b\u91cc\u62ff\u5230\u4ea7\u54c1\u6280\u672f\u3002</p><p>\u3000\u3000\u770b\u5230\u56fd\u5916\u624b\u673a\u5382\u5546\u5728\u56fd\u5185\u5e02\u573a\u63a0\u593a\u6210\u6027\uff0c\u56fd\u5185\u5382\u5546\u4e0d\u53ef\u80fd\u4e0d\u52a8\u5fc3\uff0c\u4f46\u662f\u56fd\u4ea7\u624b\u673a\u8fdf\u8fdf\u65e0\u6cd5\u63a8\u51fa\uff0c\u539f\u56e0\u662f\u591a\u65b9\u9762\u7684\uff0c\u6709\u4e00\u70b9\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\uff0c\u5c31\u662f\u8ddf\u8fd9\u4e9b\u6d77\u5916\u624b\u673a\u5382\u5546\u7684\u5c01\u9501\u4e0e\u538b\u5236\u6709\u7740\u5f88\u5927\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4ec5\u8fd9\u4e9b\u6280\u672f\u662f\u4e0d\u591f\u7684\uff0c\u5f20\u606a\u4e0d\u5962\u671b\u80fd\u62ff\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\u2014\u2014\u90a3\u7edd\u5bf9\u662f\u5962\u671b\u2014\u2014\u4f46\u662f\u624b\u673a\u7535\u6e90\u7ba1\u7406\u6280\u672f\uff0c\u5f20\u606a\u4e5f\u4e0d\u5962\u671b\u80fd\u62ff\u5230\u5fb7\u5dde\u7684\u6700\u65b0\u6280\u672f\uff0c\u54ea\u6015\u843d\u540e\u4e24\u4ee3\u7684\u6280\u672f\u4e5f\u884c\uff0c\u6bd5\u7adf\uff0c\u8fd9\u4e00\u9879\u6280\u672f\uff0c\u4e0d\u4ec5\u624b\u673a\u4f1a\u7528\u5230\uff0c\u5bf9MP3\u7b49\u76f8\u5173\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u90fd\u6709\u89e6\u7c7b\u65c1\u901a\u7684\u4f5c\u7528\uff0c\u6709\u76f8\u5173\u6280\u672f\u57fa\u7840\uff0c\u6210\u6c49\u748b\u9886\u5bfc\u7684ESS\u6280\u672f\u653b\u5173\u5c0f\u7ec4\u5c31\u53ef\u4ee5\u8282\u7ea6\u5b9d\u8d35\u7684\u6280\u672f\u7814\u53d1\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\u4e5f\u4e0d\u6101\u9526\u6e56\u638f\u4e0d\u51fa\u94b1\u6765\uff0c\u9526\u6e56\u5728\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u6240\u5360\u7684\u6743\u76ca\uff0c\u4fdd\u8bc1\u4ed6\u4eec\u5728\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\u91cc\u6709\u76f8\u5f53\u5f3a\u7684\u652f\u4ed8\u80fd\u529b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6280\u672f\u3001\u8bbe\u5907\u8bbe\u8fdb\u6c38\u8fdc\u662f\u957f\u671f\u626f\u76ae\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5728\u5fb7\u5dde\u4eea\u5668\u603b\u90e8\u53c2\u89c2\u8bbf\u95ee\u4e86\u4e94\u5929\uff0c\u6700\u5927\u7684\u6210\u5c31\uff0c\u5c31\u662f\u51e0\u4e4e\u53c2\u89c2\u4e86\u5fb7\u5dde\u4eea\u5668\u6240\u6709\u5f15\u4ee5\u4e3a\u50b2\u7684\u6280\u672f\u6210\u5c31\uff0c\u7ed9\u9526\u6e56\u7279\u522b\u662f\u9526\u6e56\u4e0b\u9762\u7684ESS\u660e\u786e\u4e86\u5341\u4e8c\u9879\u65e5\u540e\u91cd\u70b9\u8981\u653b\u514b\u7684\u7814\u7a76\u65b9\u5411\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5c31\u9648\u4fe1\u751f\u7684\u8f6c\u4efb\u95ee\u9898\uff0c\u4e0e\u5fb7\u5dde\u4eea\u5668\u7b7e\u7f72\u8c05\u89e3\u534f\u8bae\uff0c\u5b9e\u9645\u4e0a\u63d0\u524d\u4e00\u4e2a\u534a\u6708\u7ed3\u675f\u4e86\u9648\u4fe1\u751f\u5728\u5fb7\u5dde\u4eea\u5668\u7684\u4efb\u671f\uff0c\u9526\u6e56\u4e3a\u6b64\u8fd8\u8981\u989d\u5916\u652f\u4ed8\u5fb7\u5dde\u4eea\u5668\u8fd1\u4e8c\u5341\u4e07\u7f8e\u91d1\u4f5c\u4e3a\u8865\u507f\u3002</p><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\uff08TI\uff09\u4e2d\u56fd\u533a\u526f\u603b\u88c1\u5468\u6b63\u9752\u5c06\u63a5\u66ff\u9648\u4fe1\u751f\u603b\u88c1\u7684\u4f4d\u7f6e\uff0c\u800c\u5fb7\u5dde\u4eea\u5668\u7ec4\u5efa\u5927\u4e2d\u534e\u533a\u516c\u53f8\u7684\u8ba1\u5212\uff0c\u4e5f\u5c06\u7531\u4e8e\u9648\u4fe1\u751f\u7684\u53bb\u804c\u800c\u6682\u65f6\u9694\u6d45\u3002</p><p>\u3000\u30002\u670819\u65e5\uff0c\u9648\u4fe1\u751f\u5b9e\u9645\u5df2\u7ecf\u6210\u4e3a\u9526\u6e56\u7684\u4e00\u5458\uff0c\u5f20\u606a\u5e0c\u671b\u4ed6\u4e0e\u8096\u664b\u6210\u7559\u5728\u8fbe\u62c9\u65af\u4e3b\u6301\u4e0e\u5fb7\u5dde\u4eea\u5668\u7684\u9879\u76ee\u8c08\u5224\uff0c\u5f20\u606a\u5fc3\u91cc\u975e\u5e38\u6e05\u695a\uff0c\u4ed6\u5bf9\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u8981\u4e0a\u624b\u673a\u9879\u76ee\uff0c\u6700\u5927\u7684\u963b\u529b\u4e0d\u5728\u5f15\u8fdb\u548c\u8fdb\u4e00\u6b65\u5f00\u53d1\u65b0\u7684\u4ea7\u54c1\u6280\u672f\u4e0a\uff0c\u800c\u5728\u56fd\u5185\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u56fd\u5185\u5bf9\u79fb\u52a8\u901a\u8baf\u8bbe\u5907\u751f\u4ea7\u6ca1\u6709\u4e13\u95e8\u7684\u6761\u6587\uff0c\u6700\u7ec8\u7684\u9879\u76ee\u5ba1\u6279\u6743\u8fd8\u662f\u5728\u90ae\u7535\u90e8\u624b\u91cc\uff0c\u606a\u5c11\u662f\u8bf4\u8fd9\u65b9\u9762\u7684\u963b\u529b\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6469\u6258\u7f57\u62c9\u8fd9\u4e9b\u624b\u673a\u5382\u5546\u4e0e\u90ae\u7535\u90e8\u5927\u4f6c\u7684\u5173\u7cfb\uff0c\u4e0d\u8981\u95ee\u522b\u4eba\uff0c\u95ee\u53f6\u5efa\u658c\u5c31\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0e\u53f6\u5efa\u658c\u89c1\u8fc7\u9762\uff0c\u77e5\u9053\u8fd9\u4e48\u4e2a\u4eba\u3002\u76db\u946b\u662f\u7231\u7acb\u4fe1\u5728\u534e\u4e1c\u5730\u533a\u7684\u603b\u4ee3\u7406\u5546\uff0c\u5f53\u521d\u7231\u7acb\u4fe1\u8981\u6324\u5165\u56fd\u5185\u7684\u5e02\u573a\uff0c\u4f46\u662f\u7531\u4e8e\u6469\u6258\u7f57\u62c9\u4e0e\u90ae\u7535\u90e8\u7684\u5173\u7cfb\u5bc6\u5207\uff0c\u786c\u751f\u751f\u6324\u4e0d\u8fdb\u90ae\u7535\u90e8\u65d7\u4e0b\u7684\u9500\u552e\u6e20\u9053\u4f53\u7cfb\uff0c\u53ea\u80fd\u53e6\u8f9f\u8e4a\u5f84\uff0c\u5728\u90ae\u7535\u90e8\u9500\u552e\u6e20\u9053\u4e4b\u5916\uff0c\u91cd\u65b0\u5efa\u7acb\u5b8c\u6574\u7684\u4e09\u7ea7\u9500\u552e\u4f53\u7cfb\uff0c\u4e5f\u7531\u4e8e\u76db\u946b\u516c\u53f8\u7684\u51fa\u8272\u8868\u73b0\uff0c\u624d\u8ba9\u7231\u7acb\u4fe1\u5728\u4e5d\u4e94\u5e74\u6210\u529f\u7684\u64ac\u5f00\u56fd\u5185\u7684\u5e02\u573a\u3002\u968f\u540e\u591a\u5bb6\u6d77\u5916\u624b\u673a\u5382\u5546\u501f\u9274\u7231\u7acb\u4fe1\u7684\u7ecf\u9a8c\u6324\u5165\u56fd\u5185\u5e02\u573a\uff1b\u6240\u4ee5\u6d77\u5916\u624b\u673a\u5382\u5546\u4e0e\u90ae\u7535\u90e8\u4e4b\u95f4\u7684\u732b\u817b\uff0c\u53f6\u5efa\u658c\u518d\u662f\u6e05\u695a\u4e0d\u8fc7\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u5e02\u573a\u7684\u95ee\u9898\u597d\u89e3\u51b3\uff0c\u8981\u6e20\u9053\u6709\u6e20\u9053\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u56fd\u5185\u7684\u5f71\u54cd\u8fd8\u7b97\u53ef\u4ee5\uff0c\u4f46\u662f\u90ae\u7535\u90e8\u5185\u90e8\u53ef\u80fd\u5b58\u5728\u201c\u5b81\u4e0e\u5916\u8d3c\u3001\u4e0d\u4e0e\u5bb6\u5974\u201d\u7684\u5a9a\u6d0b\u601d\u7ef4\uff0c\u60f3\u5728\u4e5d\u4e03\u5e74\u62ff\u5230\u90ae\u7535\u90e8\u7684\u4e00\u7eb8\u6279\u6587\u7edd\u5bf9\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u6392\u9664\u90ae\u7535\u90e8\u5e0c\u671b\u5c06\u7b2c\u4e00\u5f20\u6279\u6587\u7559\u7ed9\u90ae\u7535\u90e8\u4e0b\u5c5e\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u56fd\u5185\u7684\u884c\u4e1a\u5784\u65ad\u867d\u7136\u8ba9\u4eba\u89c9\u5f97\u5f88\u64cd\u5a18\uff0c\u4f46\u662f\u53c8\u65e0\u6cd5\u907f\u514d\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5148\u641e\u5b9a\u6279\u6587\u518d\u4e0eTI\u8c08\u9879\u76ee\u5f15\u8fdb\u7684\u4e8b\u60c5\u624d\u66f4\u59a5\u5f53\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u201d\u5f20\u606a\u6001\u5ea6\u660e\u786e\u7684\u8bf4\uff0c\u201c\u5c31\u7b97\u6279\u6587\u7684\u95ee\u9898\u4e00\u65f6\u89e3\u51b3\u4e0d\u4e0b\u6765\uff0c\u5e76\u4e0d\u5f71\u54cd\u9526\u6e56\u4eceTI\u5f15\u8fdb\u624b\u673a\u4ea7\u54c1\u6280\u672f\u5e76\u8fdb\u4e00\u6b65\u7684\u5f00\u53d1\uff0c\u5927\u4e0d\u4e86\uff0c\u6211\u4eec\u4ece\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u5148\u505a\u8d77\u6765\uff1b\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u8fd8\u5e0c\u671b\u4f60\u80fd\u4e0e\u6210\u6c49\u748b\u4e00\u8d77\u5bfb\u627e\u624b\u673a\u57fa\u5e26\u82af\u7247\u5f00\u53d1\u65b9\u9762\u5408\u9002\u7684\u6280\u672f\u4eba\u5458\uff0c\u80fd\u6709\u4e00\u4e2a\u56e2\u961f\u5219\u66f4\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u5fd8\u4e03\u516b\u5e74\u540e\u8054\u53d1\u79d1\u7684\u6210\u529f\u7ecf\u9a8c\uff0c\u8054\u79d1\u53d1\u4e5d\u4e5d\u5e74\u5e95\u624d\u4ece\u7f8e\u56fd\u4e00\u5bb6\u624b\u673a\u57fa\u5e26\u82af\u7247\u7814\u53d1\u516c\u53f8\u6316\u5230\u4eba\u624b\u3002\u5177\u4f53\u662f\u4ece\u54ea\u5bb6\u516c\u53f8\u6316\u5230\u4eba\uff0c\u5f20\u606a\u4e5f\u4e0d\u8bb0\u5f97\uff0c\u5927\u6982\u8bb0\u5f97\u4e00\u76f4\u5230\u96f6\u4e00\u5e74\u521d\uff0c\u8054\u53d1\u79d1\u7684\u624b\u673a\u82af\u7247\u90e8\u95e8\u624d\u5f00\u59cb\u6b63\u5f0f\u7684\u7814\u53d1\u624b\u673a\u57fa\u5e26\u82af\u7247\uff0c\u96f6\u4e09\u5e74\u5e95\u5c31\u5f62\u6210\u91cf\u4ea7\u7684\u80fd\u529b\uff0c\u96f6\u4e03\u5e74\u5c31\u5360\u636e\u5168\u7403\u624b\u673a\u82af\u7247\u5e02\u573a17\uff05\u7684\u4efd\u989d\u3002</p><p>\u3000\u3000\u53ea\u8981\u627e\u5230\u5408\u9002\u7684\u4eba\u624b\uff0c\u9526\u6e56\u4ece\u4e5d\u4e03\u5e74\u5f00\u59cb\u8fdb\u884c\u624b\u673a\u57fa\u5e26\u82af\u7247\u5f00\u53d1\uff0c\u76f8\u4fe1\u4e0d\u4f1a\u6bd4\u8054\u53d1\u79d1\u7684\u901f\u5ea6\u6162\u3002\u7279\u522b\u5230\u4e86\u96f6\u56db\u3001\u96f6\u4e94\u5e74\u4e4b\u540e\uff0c\u9526\u6e56\u5728\u79fb\u52a8\u901a\u8baf\u4e0a\u8fd8\u4e0d\u80fd\u5f62\u6210\u81ea\u5df1\u7684\u6838\u5fc3\u6280\u672f\uff0c\u53ef\u80fd\u4f1a\u76f8\u5f53\u5403\u4e8f\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u70b9\u70b9\u5934\uff0c\u5f20\u606a\u6b64\u65f6\u7684\u51b3\u5b9a\u5012\u7b26\u5408\u4ed6\u8fd9\u4e9b\u5929\u5634\u91cc\u4e00\u76f4\u5ff5\u53e8\u4e0d\u505c\u7684\u7cfb\u7edf\u5316\u7ecf\u8425\u7b56\u7565\uff1a\u5efa\u7acb\u5b8c\u6574\u7684\u4ea7\u54c1\u7ed3\u6784\uff0c\u6240\u4ee5\u8981\u4eceTI\u8d2d\u5356\u624b\u673a\u4ea7\u54c1\u6280\u672f\u4e30\u5bcc\u201cIDEA\u7231\u8fbe\u201d\u8fd9\u4e2a\u54c1\u724c\u4e0b\u7684\u4ea7\u54c1\u7ebf\uff1b\u5efa\u7acb\u5b8c\u5584\u7684\u6280\u672f\u7ed3\u6784\uff0c\u6240\u4ee5\u4e00\u5f00\u59cb\u5c31\u7d27\u8ddf\u7740\u7784\u51c6\u624b\u673a\u6838\u5fc3\u5143\u5668\u4ef6\u57fa\u5e26\u82af\u7247\u7684\u5236\u9020\u6280\u672f\u3002</p><p>\u3000\u3000\u867d\u7136\u8981\u627e\u5230\u5408\u9002\u7684\u4eba\u624b\u5e76\u975e\u6613\u4e8b\uff0c\u51e0\u5bb6\u624b\u673a\u82af\u7247\u7684\u4e13\u4e1a\u5382\u5546\u5305\u62ec\u5fb7\u5dde\u4eea\u5668\u5728\u5185\u5bf9\u8fd9\u4e00\u5757\u7684\u6280\u672f\u5c01\u9501\u90fd\u975e\u5e38\u7684\u4e25\u683c\uff0c\u4f46\u4e5f\u4e0d\u662f\u7edd\u975e\u53ef\u80fd\u3002\u5728\u624b\u673a\u57fa\u5e26\u82af\u7247\u7814\u53d1\u9886\u57df\uff0c\u534e\u88d4\u79d1\u5b66\u5bb6\u7684\u8868\u73b0\u76f8\u5f53\u7684\u51fa\u8272\uff0c\u800c\u8fd9\u4e9b\u4eba\u5728\u4e0e\u539f\u516c\u53f8\u7684\u8058\u7528\u534f\u8bae\u671f\u6ee1\u4e4b\u540e\uff0c\u5c31\u6709\u53ef\u80fd\u5e26\u7740\u624b\u673a\u57fa\u5e26\u82af\u7247\u7684\u4e30\u5bcc\u7814\u53d1\u7ecf\u9a8c\u52a0\u76df\u9526\u6e56\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4ee5\u4e3a\uff0c\u53ea\u8981\u56fd\u5185\u7535\u5b50\u5382\u5546\u591a\u51e0\u5bb6\u6709\u5f20\u606a\u8fd9\u6837\u7684\u91ce\u5fc3\uff0c\u56fd\u5185\u7535\u5b50\u4f01\u4e1a\u8981\u8ffd\u8d76\u6b27\u7f8e\u5e76\u975e\u9065\u9065\u65e0\u671f\u7684\u4e8b\u60c5\u3002</p></article>"}}}]);
