(window.webpackJsonp=window.webpackJsonp||[]).push([[669],{692:function(p){p.exports={chapterTitle:null,title:"\u7b2c668\u7ae0 \u4e3a\u4ec0\u4e48\u7231\u7838\u8f66",content:"<article><h2>\u7b2c668\u7ae0 \u4e3a\u4ec0\u4e48\u7231\u7838\u8f66</h2><p>\u3000\u3000\u201c\u54e5\u54e5\uff0c\u4f60\u7684\u574f\u6bdb\u75c5\u600e\u4e48\u4e0d\u6539\u4e00\u6539\uff0c\u8dd1\u5230\u54ea\u91cc\u90fd\u8981\u7ea0\u7f20\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff1f\u201d\u5750\u5728\u6cd5\u62c9\u5229\u8dd1\u8f66\u91cc\u7684\u8273\u4e3d\u5973\u90ce\u6ca1\u6709\u987a\u4ece\u7684\u542c\u90a3\u9752\u5e74\u7684\u5429\u5490\uff0c\u62b1\u6028\u4e00\u53e5\uff0c\u53c8\u671d\u5b59\u9759\u6aac\u6b49\u610f\u7684\u7b11\u4e86\u7b11\uff0c\u4e3a\u9752\u5e74\u4e0d\u793c\u8c8c\u7684\u642d\u8baa\u800c\u9053\u6b49\uff0c\u201c\u6211\u4eec\u8ba4\u8bc6\u8def\u7684\uff0c\u8c22\u8c22\u4f60\u7684\u6307\u70b9\u3002\u201d</p><p>\u3000\u3000\u90a3\u9752\u5e74\u5374\u6ca1\u6709\u8c0e\u8a00\u7ed9\u6233\u7834\u7684\u7f9e\u803b\u81ea\u89c9\uff0c\u4e5f\u4e0d\u4ecb\u610f\u5b59\u9759\u6aac\u521a\u624d\u6307\u8def\u65f6\u80e1\u8bf4\u516b\u9053\uff0c\u7ee7\u7eed\u6d8e\u7740\u8138\u642d\u8baa\u9053\uff1a\u201c\u4f60\u7684\u5355\u8f66\u8ddf\u6211\u7684\u8f66\u5f88\u914d\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u505c\u4e0b\u6765\u8f66\u60f3\u8ddf\u4f60\u8ba4\u8bc6\uff0c\u201d\u4e0d\u6653\u5f97\u4ed6\u4e24\u6307\u4ece\u54ea\u91cc\u5939\u51fa\u4e00\u5f20\u540d\u7247\u9012\u8fc7\u6765\uff0c\u201c\u6211\u4eec\u4f1a\u5728\u5efa\u90ba\u7559\u5f88\u957f\u7684\u65f6\u95f4\uff0c\u5e0c\u671b\u80fd\u6709\u5e78\u8bf7\u4f60\u5171\u8fdb\u665a\u9910\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7684\u5355\u8f66\u4e5f\u662f\u90a3\u79cd\u660e\u8273\u7684\u6a59\u8272\uff0c\u4e0e\u8dd1\u8f66\u7684\u989c\u8272\u76f8\u8fd1\uff0c\u4f46\u662f\u542c\u8fd9\u9752\u5e74\u8fd9\u4e48\u8bf4\uff0c\u5b59\u9759\u6aac\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u561f\u7740\u5634\uff0c\u8ba9\u4ed6\u5e2e\u6276\u7740\u8f66\u3002</p><p>\u3000\u3000\u5f20\u606a\u597d\u4e0d\u5bb9\u6613\u5c06\u89c6\u7ebf\u4ece\u90a3\u5973\u90ce\u7684\u80f8\u812f\u4e0a\u6536\u56de\u6765\uff0c\u5e2e\u5b59\u9759\u6aac\u6276\u8fc7\u8f66\uff0c\u53c8\u4ece\u90a3\u9752\u5e74\u4e24\u6307\u95f4\u5c06\u90a3\u5f20\u70eb\u91d1\u7684\u7cbe\u7f8e\u540d\u7247\u7ed9\u62bd\u4e86\u51fa\u6765\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5374\u8146\u7740\u8138\u5bf9\u90a3\u9752\u5e74\u65c1\u8fb9\u7684\u660e\u8273\u5973\u90ce\u7b11\u7740\u8bf4\uff1a\u201c\u5de7\u8d1e\u5c0f\u59d0\uff0c\u201d\u867d\u7136\u90a3\u4e2a\u97e9\u56fd\u9752\u5e74\u7684\u8bed\u8c03\u6709\u4e9b\u602a\u5f02\uff0c\u5f20\u606a\u8fd8\u662f\u542c\u6e05\u4ed6\u5524\u660e\u8273\u5973\u90ce\u7684\u540d\u5b57\uff0c\u201c\u542c\u5230\u4f60\u4f1a\u5728\u5efa\u90ba\u7559\u5f88\u957f\u65f6\u95f4\uff0c\u5fc3\u91cc\u771f\u662f\u9ad8\u5174\uff0c\u5e0c\u671b\u80fd\u6709\u5e78\u8bf7\u4f60\u5171\u8fdb\u665a\u9910\uff0c\u540d\u7247\u4e0a\u7684\u7535\u8bdd\u80fd\u8054\u7cfb\u5230\u4f60\u5417\uff1f\u201d</p><p>\u3000\u3000\u65e0\u803b\u7684\u5c31\u6015\u9047\u5230\u66f4\u65e0\u803b\u7684\uff0c\u90a3\u9752\u5e74\u89c1\u5f20\u606a\u773c\u775b\u8086\u65e0\u5fcc\u60ee\u7684\u4ece\u4ed6\u59b9\u59b9\u7684\u7f8e\u4e3d\u8138\u86cb\u6253\u91cf\u5230\u9ad8\u9ad8\u8038\u8d77\u7684\u80f8\u812f\u4e0a\uff0c\u6709\u4e9b\u50bb\u773c\uff0c\u5374\u53c8\u7ed9\u6fc0\u8d77\u4e9b\u6012\u6c14\uff0c\u77aa\u7740\u773c\u775b\u5f20\u606a\uff1a\u201c\u4f60\u5c0f\u5b50\u773c\u775b\u770b\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u7ee7\u7eed\u8c03\u620f\u4e24\u4e2a\u53ef\u7231\u7684\u97e9\u56fd\u9752\u5e74\uff0c\u8170\u95f4\u8089\u5374\u7ed9\u5b59\u9759\u6aac\u62ff\u624b\u6307\u5c16\u6390\u4e86\u4e00\u4e0b\uff0c\u770b\u7740\u97e9\u56fd\u5973\u90ce\u79c0\u7f8e\u7684\u7709\u5934\u4e5f\u5fae\u5fae\u7684\u76b1\u4e86\u8d77\u6765\uff0c\u5b09\u76ae\u7b11\u8138\u7684\u770b\u7740\u7537\u9752\u5e74\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u6211\u7684\u773c\u775b\u6bd4\u4f60\u66f4\u65e0\u793c\u5417\uff1f\u201d</p><p>\u3000\u3000\u7537\u9752\u5e74\u89c1\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u8ba4\u8bc6\uff0c\u4e24\u4eba\u7684\u52a8\u4f5c\u8fd8\u6709\u4e9b\u4eb2\u6635\uff0c\u5fc3\u60f3\u6216\u8bb8\u662f\u60c5\u4fa3\uff0c\u538c\u6076\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5fc3\u60f3\u8fd9\u5bb6\u4f19\u8fd8\u771f\u662f\u60f9\u4eba\u538c\uff0c\u51ed\u4ec0\u4e48\u5360\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff1f\u53d1\u52a8\u8d70\u8f66\u5b50\u8d70\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u660e\u9ec4\u8272\u6cd5\u5229\u62c9\u8dd1\u8f66\u6d88\u5931\u7684\u65b9\u5411\uff0c\u82e5\u6709\u6240\u601d\u3002</p><p>\u3000\u3000\u201c\u55ec\uff0c\u201d\u5b59\u9759\u6aac\u4f38\u624b\u8fc7\u6765\u5e2e\u5f20\u606a\u6342\u4f4f\u9f3b\u5934\uff0c\u8ba5\u8bbd\u9053\uff0c\u201c\u9f3b\u8840\u5feb\u6d41\u51fa\u6765\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u9f3b\u5b50\u7ed9\u63e1\u5728\u5b59\u9759\u6aac\u5fae\u51c9\u67d4\u817b\u7684\u624b\u5fc3\u91cc\uff0c\u90a3\u5fae\u9999\u7684\u89e6\u611f\u8ba9\u4eba\u5fc3\u60b8\uff0c\u5f20\u606a\u4fa7\u5934\u770b\u4e86\u5b59\u9759\u6aac\u4e00\u773c\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u54ea\u6709\uff1f\u6211\u4e0d\u662f\u5e2e\u4f60\u9a71\u8d76\u65e0\u804a\u4eba\u58eb\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u9a97\u8c01\u554a\uff0c\u201d\u5b59\u9759\u6aac\u5a07\u7b11\u7740\uff0c\u53c8\u9644\u5230\u5f20\u606a\u8033\u8fb9\u8f7b\u58f0\u7684\u8bf4\uff0c\u201c\u6211\u8981\u662f\u7537\u4eba\uff0c\u4e5f\u4f1a\u76ef\u7740\u90a3\u5bf9\u8ddf\u8f66\u524d\u706f\u4f3c\u7684\u5927\u80f8\u770b\u7684\uff0c\u771f\u7fa1\u6155\u554a\uff0c\u90fd\u4e0d\u6653\u5f97\u90a3\u5973\u4eba\u5e73\u65f6\u5403\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u3000\u3000\u8f66\u524d\u706f\u554a\uff1f\u8fd9\u6bd4\u55bb\u8fd8\u771f\u59a5\u5e16\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u773c\u795e\u77a5\u4e86\u5b59\u9759\u6aac\u7684\u80f8\u90e8\u4e00\u773c\uff0c\u867d\u7136\u6ca1\u6709G\u7f69\u676f\u90a3\u4e48\u5938\u5f20\uff0c\u5bf9\u4e8e\u9752\u6625\u5c11\u5973\u6765\u8bf4\uff0c\u53d1\u80b2\u4e5f\u662f\u76f8\u5f53\u8bf1\u4eba\u4e86\uff0c\u4e30\u633a\u5c16\u7fd8\uff0c\u53ef\u60dc\u7ed9\u886c\u886b\u5e26\u857e\u4e1d\u8936\u8fb9\u7684\u7ffb\u9886\u7ed9\u63a9\u9970\u4f4f\u4e86\uff0c\u5f20\u606a\u559c\u6b22\u5979\u590f\u5b63\u7a7f\u7740T\u6064\u6216\u540a\u5e26\u88d9\u7684\u6a21\u6837\uff0c\u90a3\u65f6\u80fd\u770b\u5230\u5979\u7684\u817f\u6709\u591a\u957f\u3001\u8170\u6709\u591a\u7ec6\u3001\u80f8\u6709\u591a\u633a\u3001\u81c0\u6709\u591a\u7fd8\uff0c\u53ef\u60dc\u5230\u79cb\u540e\uff0c\u5b59\u9759\u6aac\u591a\u559c\u6b22\u7a7f\u90a3\u4e48\u5bbd\u677e\u7684\u5de5\u88c5\u4f11\u95f2\u8863\u88e4\uff0c\u5927\u597d\u8eab\u6750\u90fd\u7ed9\u906e\u6321\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u5355\u8f66\u4f60\u9a91\u53bb\u5427\uff0c\u7a81\u7136\u5f88\u8ba8\u538c\u8fd9\u989c\u8272\u3002\u201d\u5b59\u9759\u6aac\u4f38\u624b\u906e\u7740\u5f20\u606a\u7684\u773c\u775b\uff0c\u4e0d\u8ba9\u4ed6\u7684\u8d3c\u773c\u5728\u81ea\u5df1\u8eab\u4e0a\u4e71\u7785\uff0c\u5979\u90fd\u4e0d\u6653\u5f97\u81ea\u5df1\u5bf9\u5f20\u606a\u7684\u6001\u5ea6\u5df2\u7ecf\u6084\u7136\u53d1\u751f\u7684\u53d8\u5316\uff0c\u6362\u4f5c\u4ed6\u65f6\uff0c\u5979\u8bf4\u4e0d\u5b9a\u4f1a\u5728\u5f20\u606a\u9762\u524d\u5c06\u80f8\u633a\u4e00\u633a\u518d\u8ba5\u8bbd\u4ed6\u4e24\u53e5\u3002</p><p>\u3000\u3000\u201c\u6211\u4e00\u4e2a\u4eba\u9a91\uff1f\u201d\u5f20\u606a\u6709\u4e9b\u5934\u75bc\u5462\uff0c\u8fd9\u8f86\u5973\u5f0f\u5355\u5f0f\uff0c\u989c\u8272\u53c8\u8fd9\u4e48\u660e\u8273\uff0c\u4ed6\u9a91\u7740\u8f7d\u5b59\u9759\u6aac\u5012\u65e0\u6240\u8c13\uff0c\u8981\u662f\u4ed6\u4e00\u4e2a\u4eba\u9a91\u7740\u5728\u4e1c\u5927\u6821\u56ed\u91cc\u4e71\u6643\u8361\uff0c\u4e5f\u591f\u60f9\u4eba\u77a9\u76ee\u7684\uff0c\u201c\u6211\u9a91\u7740\u9001\u4f60\u53bb\u9152\u5427\u2026\u2026\u201d\u5f20\u606a\u5b81\u53ef\u5c06\u591a\u7ed5\u51e0\u6b65\u8def\u5c06\u5355\u8f66\u4e22\u9152\u5427\u524d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u8981\u4e0d\u665a\u4e0a\u6211\u4eec\u5077\u5077\u5728\u90a3\u8f66\u4e0a\u5212\u4e24\u9053\uff1f\u201d\u5b59\u9759\u6aac\u8138\u5fae\u8d34\u7740\u5f20\u606a\u7684\u8170\uff0c\u5750\u5728\u540e\u8f66\u67b6\u4e0a\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u4f4e\u5934\u770b\u4e86\u4ece\u81ea\u5df1\u814b\u4e0b\u94bb\u51fa\u6765\u7684\u5b59\u9759\u6aac\u7f8e\u4e3d\u7684\u5c0f\u8138\uff0c\u201c\u8fd9\u4e48\u53d8\u6001\u7684\u4e8b\u60c5\u6211\u53ef\u4e0d\u505a\u3002\u201d</p><p>\u3000\u3000\u201c\u55ec\uff0c\u8c01\u5f53\u521d\u62ff\u7740\u677f\u7816\u7838\u4eba\u5bb6\u5954\u9a70\u7684\uff1f\u201d\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u8fd9\u5e74\u5934\u6bc1\u5728\u81ea\u5df1\u624b\u91cc\u7684\u597d\u8f66\u4e5f\u4e0d\u5c11\uff0c\u5c31\u8fde\u53f6\u5c0f\u6850\u7684\u90a3\u8f86\u5954\u9a70\u4e5f\u662f\u53d7\u81ea\u5df1\u7275\u7d2f\u7ed9\u8c22\u5251\u5357\u6307\u4f7f\u7ed9\u7838\u7a00\u70c2\uff0c\u7b11\u4e86\u7b11\uff0c\u201c\u60c5\u51b5\u4e0d\u4e00\u6837\uff0c\u4eba\u5bb6\u53ea\u662f\u8ddf\u4f60\u642d\u8baa\uff0c\u662f\u5bf9\u4f60\u5a07\u8273\u7f8e\u8c8c\u7684\u5145\u5206\u80af\u5b9a\uff0c\u8fd9\u6837\u5c31\u8dd1\u53bb\u6bc1\u522b\u4eba\u7684\u8f66\uff0c\u4f1a\u4e0d\u4f1a\u4e0d\u592a\u597d\u5ba2\uff1f\u201d</p><p>\u3000\u3000\u201c\u54af\u54af\uff0c\u201d\u5b59\u9759\u6aac\u5a07\u7b11\u8d77\u6765\uff0c\u201c\u90a3\u5c31\u7ed5\u8fc7\u90a3\u5c0f\u5b50\u4e00\u56de\uff0c\u6562\u5728\u8001\u5a18\u9762\u524d\u5f97\u745f\uff0c\u4e0b\u6b21\u5c31\u76f4\u63a5\u5728\u4ed6\u5c0f\u767d\u8138\u4e0a\u5212\u4e24\u9053\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u80cc\u810a\u5bd2\u98a4\u4e86\u4e00\u4e0b\uff0c\u8fd9\u59ae\u5b50\u8bf4\u8fd9\u8bdd\u90fd\u54ac\u7259\u5207\u9f7f\u8bf4\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u53c8\u6362\u4e86\u5a07\u5a9a\u7684\u8bed\u8c03\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u77e5\u9053\u6211\u6700\u6b23\u8d4f\u4f60\u54ea\u4e00\u70b9\uff1f\u201d</p><p>\u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u94f6\u53a6\u5e7f\u573a\u524d\u7ed9\u845b\u836b\u5747\u90a3\u4e00\u62f3\uff0c\u8981\u662f\u4f60\u4eb2\u624b\u6325\u51fa\u53bb\u7684\uff0c\u5c31\u5b8c\u7f8e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u771f\u662f\u66b4\u529b\u554a\uff0c\u8fd9\u79cd\u5c0f\u4e8b\uff0c\u90fd\u8981\u52b3\u6211\u4eb2\u81ea\u51fa\u624b\uff1f\u201d\u5f20\u606a\u6487\u4e86\u6487\u5634\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u4e00\u5bb6\u97e9\u56fd\u9910\u5385\uff0c\u770b\u7740\u5e97\u5802\u524d\u7684\u544a\u793a\uff0c\u4eca\u5929\u665a\u4e0a\u5c06\u9996\u6b21\u8425\u4e1a\uff0c\u4e5f\u662f\u5b66\u5e9c\u5df7\u8fc4\u4eca\u4e3a\u6b62\u4e0d\u591a\u7684\u88c5\u6f62\u5b8c\u6bd5\u6b63\u5f0f\u5f00\u8425\u7684\u5546\u5bb6\u4e4b\u4e00\uff0c\u5b59\u9759\u6aac\u6307\u7740\u97e9\u56fd\u9910\u5385\u660e\u4eae\u7684\u5927\u5802\u8bf4\u9053\uff1a\u201c\u5212\u8f66\u7684\u4e8b\u5c31\u7b97\u4e86\uff0c\u665a\u4e0a\u8bf7\u6211\u6765\u8fd9\u91cc\u5403\u665a\u996d\u5427\u3002\u5929\u5929\u5403\u4f60\u4eec\u5b66\u6821\u7684\u6559\u5de5\u98df\u5802\uff0c\u90fd\u5403\u817b\u5473\u4e86\uff0c\u597d\u4e0d\u5bb9\u6613\u71ac\u5230\u5b66\u5e9c\u5df7\u6709\u9910\u9986\u5f00\u4e1a\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u884c\uff01\u201d\u5f20\u606a\u81ea\u5df1\u4e5f\u60f3\u6362\u6362\u53e3\u5473\uff0c\u97e9\u56fd\u6ce1\u83dc\u5076\u5c14\u5403\u4e00\u5403\u4e5f\u86ee\u723d\u53e3\u7684\uff0c\u97e9\u56fd\u4e5f\u5c31\u6ce1\u83dc\u503c\u5f97\u5168\u4e16\u754c\u70ab\u8000\u4e86\u3002\u52301978\u524d\uff0c\u8ba9\u5b59\u9759\u6aac\u4e0b\u6765\uff0c\u5e2e\u5979\u5c06\u5355\u8f66\u9501\u5728\u9152\u5427\u95e8\u524d\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u3001\u82cf\u4e00\u5a77\u5979\u4eec\u5728\u9152\u5427\u91cc\uff0c\u4f55\u5f26\u4e5f\u5728\uff0c\u6b63\u5728\u5427\u53f0\u91cc\u9762\u64e6\u62ed\u73bb\u7483\u5668\u76bf\u3002\u5979\u4eec\u770b\u5230\u5b59\u9759\u6aac\u4e0e\u5f20\u606a\uff0c\u9996\u5148\u95ee\u5979\u4eec\u6709\u6ca1\u6709\u770b\u5230\u90a3\u8f86\u660e\u9ec4\u8272\u7684\u6cd5\u62c9\u5229\u3002</p><p>\u3000\u3000\u539f\u6765\u90a3\u5bb6\u4f19\u5f00\u7740\u90a3\u8f86\u62c9\u98ce\u7684\u6cd5\u62c9\u5229\u5728\u5b66\u5e9c\u5df7\u4e0a\u515c\u4e86\u4e24\u5708\uff0c\u56db\u767e\u591a\u4e07\u7684\u8dd1\u8f66\uff0c\u6bd4\u76db\u590f\u90a3\u8f86\u83b2\u82b1\u8dd1\u8f66\u53ef\u8981\u5a07\u8d35\u591a\u4e86\uff0c\u60f3\u4e0d\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u90fd\u96be\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4ec5\u770b\u5230\u4e86\uff0c\u67d0\u4eba\u8fd8\u7ed9\u642d\u8baa\u4e86\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8ddf\u5979\u4eec\u8bf4\uff0c\u201c\u90a3\u8dd1\u8f66\u8ddf\u67d0\u4eba\u7684\u5355\u8f66\u989c\u8272\u4e00\u6837\uff0c\u60f9\u5f97\u67d0\u4eba\u4e0d\u9ad8\u5174\u9a91\u5979\u7684\u5355\u8f66\uff0c\u8fd8\u5f97\u52b3\u6211\u9001\u5979\u8fc7\u6765\uff0c\u8981\u77e5\u9053\u6211\u5206\u5206\u949f\u90fd\u662f\u51e0\u767e\u4e07\u4e0a\u4e0b\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u81f3\u5c11\u5728\u90a3\u4e2a\u5973\u4eba\u7684\u90a3\u5bf9\u8f66\u524d\u706f\u4e0a\u635f\u5931\u4e86\u597d\u51e0\u767e\u4e07\u2026\u2026\u201d\u5b59\u9759\u6aac\u8ba5\u7b11\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8f66\u524d\u706f\uff1f\u201d\u82cf\u4e00\u5a77\u7591\u60d1\u4e0d\u89e3\u7684\u95ee\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5728\u80f8\u524d\u6bd4\u5212\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u8f66\u524d\u706f\u5417\uff1f\u201d</p><p>\u3000\u3000\u56db\u4e2a\u5973\u5b69\u5b50\u5728\u5427\u53f0\u8fb9\u4e0a\u8086\u65e0\u5fcc\u60ee\u7684\u5927\u7b11\u8d77\u6765\u3002</p><p>\u3000\u3000\u7ed9\u522b\u4eba\u5f53\u7b11\u8bdd\u8bb2\uff0c\u603b\u662f\u5f88\u65e0\u8da3\u7684\uff0c\u5f20\u606a\u644a\u644a\u624b\uff0c\u8bf4\u8fd8\u8981\u53bb\u56fe\u4e66\u9986\u770b\u4e66\uff0c\u5c31\u4e0d\u7406\u4f1a\u8fd9\u4e9b\u75af\u5973\u4eba\u9003\u5f00\u4e86\u3002\u8981\u662f\u8dd1\u521b\u57df\u7f51\u5427\u53bb\uff0c\u6307\u4e0d\u5b9a\u8499\u4e50\u3001\u65f6\u5b66\u658c\u90a3\u4e9b\u5bb6\u4f19\u4e5f\u5bf9\u8f66\u524d\u706f\u7684\u5173\u6ce8\u8fd8\u70ed\u5207\u4e00\u4e9b\uff0c\u5fc3\u60f3\u8981\u771f\u662f\u6cd5\u62c9\u5229\u8dd1\u8f66\u655e\u5f00\u7bf7\u8ba9\u90a3\u4e2a\u97e9\u56fd\u5927\u80f8\u5973\u90ce\u9ad8\u9ad8\u7684\u633a\u7740\u80f8\u90e8\u5750\u8f66\u540e\u8eab\u4e0a\uff0c\u5927\u6982\u4f1a\u5f15\u8d77\u4e1c\u5927\u6ee1\u6821\u56ed\u7684\u5c16\u53eb\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u73b0\u5728\u5bf9\u4ec0\u4e48\u5b9d\u9a6c\u9999\u8f66\u4e4b\u7c7b\u53ef\u6ca1\u6709\u7279\u522b\u6df1\u7684\u611f\u89e6\u3002</p><p>\u3000\u3000\u90a3\u90fd\u662f\u5728\u8fd9\u4e2a\u4e16\u754c\u672a\u80fd\u53d1\u751f\u8fc7\u7684\u524d\u5c18\u5f80\u4e8b\uff0c\u5f20\u606a\u5927\u5b66\u6bd5\u4e1a\u540e\u4e0e\u8499\u4e50\u540c\u4e00\u5bb6\u516c\u53f8\uff0c\u516c\u53f8\u6709\u4e00\u4e2a\u5f88\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u62ff\u5f20\u606a\u7684\u6807\u51c6\u6765\u8861\u91cf\uff0c\u5dee\u4e0d\u591a\u6709\u63a5\u8fd1\u4e5d\u5341\u5206\u3002\u90a3\u5973\u5b69\u5b50\u5728\u5efa\u90ba\u7684\u4eb2\u621a\u7ed9\u5979\u4ecb\u7ecd\u4e86\u4e00\u4e2a\u76f8\u4eb2\u5bf9\u8c61\uff0c\u76f8\u4eb2\u65f6\uff0c\u90a3\u5973\u5b69\u5b50\u62c9\u7740\u5f20\u606a\u4e0e\u516c\u53f8\u53e6\u5916\u4e00\u4e2a\u5973\u5b69\u5b50\u5192\u5145\u60c5\u4fa3\u53bb\u538b\u573a\u5b50\uff0c\u90a3\u7537\u7684\u957f\u7684\u771f\u591f\u5bd2\u789c\u7684\uff0c\u8ddf\u9a6c\u4e91\u4f3c\u7684\uff0c\u6bd4\u9a6c\u4e91\u8fd8\u76ae\u9ed1\u3002\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u5f53\u5373\u5c31\u4e0d\u4e50\u610f\u4e0e\u90a3\u7537\u7684\u4ea4\u5f80\uff0c\u5dee\u70b9\u6492\u817f\u5c31\u8dd1\u3002\u8fd9\u4ef6\u4e8b\uff0c\u5f20\u606a\u56de\u516c\u53f8\u8fd8\u7ecf\u5e38\u6765\u9017\u5973\u5b69\u5b50\u4e50\u3002\u90a3\u7537\u7684\u8fd8\u771f\u770b\u4e0a\u90a3\u5973\u5b69\u4e86\uff0c\u9887\u4e3a\u70ed\u5207\u7684\u8ffd\u6c42\u8d77\u6765\uff0c\u4eba\u867d\u7136\u6ca1\u6709\u51fa\u6765\uff0c\u7535\u8bdd\u5374\u7ecf\u5e38\u6253\u5230\u516c\u53f8\u91cc\u6765\uff0c\u5948\u4f55\u5973\u5b69\u5b50\u7684\u70ed\u5ea6\u59cb\u7ec8\u7ef4\u5728\u96f6\u5ea6\u4ee5\u4e0b\u3002\u4e8b\u60c5\u7684\u8f6c\u673a\u662f\u4e00\u4e2a\u4e0b\u96e8\u7684\u9ec4\u660f\uff0c\u516c\u53f8\u91cc\u7684\u540c\u4e8b\u90fd\u6ca1\u6709\u5e26\u4f1e\uff0c\u4e00\u7fa4\u4eba\u9876\u7740\u96e8\u671d\u8fdc\u5904\u7684\u516c\u4ea4\u8f66\u7ad9\u51b2\u8fc7\u53bb\uff0c\u4e00\u8f86\u6e56\u84dd\u8272\u7684\u5b9d\u9a6c\u8f66\u5728\u4f17\u4eba\u9762\u524d\u505c\u4e0b\u6765\uff0c\u90a3\u7537\u5934\u4ece\u8f66\u7a97\u91cc\u8bf4\u8fd9\u4e48\u5927\u96e8\u9001\u90a3\u5973\u5b69\u5b50\u4e00\u7a0b\u3002\u597d\u5427\uff0c\u53ef\u4ee5\u60f3\u8c61\u5f53\u65f6\u7684\u60c5\u5f62\uff0c\u7537\u540c\u4e8b\u5fcc\u6068\u3001\u5973\u540c\u4e8b\u7fa1\u6155\uff0c\u90a3\u5973\u5b69\u5b50\u77dc\u6301\u7684\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u94bb\u8fdb\u90a3\u8f86\u5b9d\u9a6c\u8f66\u91cc\u2026\u2026</p><p>\u3000\u3000\u4e8b\u540e\u516c\u53f8\u7684\u5973\u540c\u4e8b\u90fd\u8bf4\u90a3\u7537\u4eba\u6ca1\u5f20\u606a\u8bf4\u7684\u90a3\u4e48\u5bd2\u789c\uff0c\u5f20\u606a\u90a3\u65f6\u5019\u8d77\u770b\u5230\u597d\u8f66\u5c31\u6709\u60f3\u7838\u7684\u51b2\u52a8\u3002\u6bd4\u8d77\u4ed6\u6765\uff0c\u8499\u4e50\u66f4\u51c4\u60e8\u4e9b\uff0c\u4ed6\u90a3\u65f6\u5019\u65e9\u5c31\u6709\u8ffd\u90a3\u5973\u5b69\u5b50\u7684\u5fc3\u601d\uff0c\u53ea\u662f\u4e00\u65f6\u8fd8\u6ca1\u6709\u52c7\u6c14\u8868\u8fbe\u51fa\u6765\u7f62\u4e86\u3002</p><p>\u3000\u3000\u60f3\u8d77\u524d\u5c18\u5f80\u4e8b\uff0c\u5f20\u606a\u73b0\u5728\u4e5f\u53ea\u662f\u7565\u6709\u611f\u6168\u800c\u5df2\uff0c\u53c8\u638f\u51fa\u90a3\u5f20\u4ece\u90a3\u4e2a\u97e9\u56fd\u9752\u5e74\u624b\u6307\u95f4\u62bd\u8fc7\u6765\u7684\u540d\u7247\u770b\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u4e09\u661f\uff08\u4e2d\u56fd\uff09\u6295\u8d44\u6709\u9650\u516c\u53f8\u4e13\u52a1\u674e\u5728\u6d19\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u5934\u53e9\u4e86\u53e9\u989d\u5934\uff0c\u8fd8\u771f\u662f\u4e00\u4e2a\u8ba9\u4eba\u5934\u75bc\u7684\u4eba\u7269\u554a\uff0c\u4e0d\u6653\u5f97\u674e\u5728\u6d19\u51fa\u73b0\u5728\u4e1c\u5927\u7684\u6821\u56ed\u91cc\u662f\u8981\u505a\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u4e4b\u524d\uff0c\u97e9\u56fd\u4e09\u661f\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u8bbe\u4e09\u661f\uff08\u5efa\u90ba\uff09\u534a\u5bfc\u4f53\uff0c\u5efa\u90ba\u4e09\u661f\u7684\u534a\u5bfc\u4f53\u5de5\u5382\u53ea\u662f\u8fdb\u884c\u82af\u7247\u5c01\u88c5\u4e0e\u6d4b\u8bd5\u7684\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u4e48\u6838\u5fc3\u9ad8\u65b0\u6280\u672f\uff0c\u9526\u6e56\u5728\u6d77\u5dde\u6295\u8d44\u5efa\u8bbe\u7684\u82af\u7247\u5c01\u88c5\u4e0e\u6d4b\u8bd5\u5de5\u5382\u5165\u79cb\u4e5f\u5efa\u6210\u6295\u4ea7\uff0c\u76ee\u524d\u4ec5\u56fd\u5185\u5f71\u789f\u5382\u5546\u6240\u9700\u8981\u7684\u89e3\u7801\u82af\u7247\u5c01\u88c5\u4e0e\u4ea7\u54c1\u6d4b\u8bd5\u4e1a\u52a1\uff0c\u5c31\u8db3\u4ee5\u8ba9\u9526\u6e56\u5728\u6d77\u5dde\u7684\u82af\u7247\u5c01\u88c5\u5de5\u5382\u5403\u9971\u3002\u5bf9\u4e8e\u5408\u4f5c\u516c\u53f8\u5fb7\u5dde\u4eea\u5668\u4e0e\u65af\u9ad8\u67cf\u6765\u8bf4\uff0c\u867d\u7136\u8ba9\u51fa\u82af\u7247\u5c01\u88c5\u4e1a\u52a1\u4e0a\u7684\u4e00\u5c0f\u90e8\u5206\u5229\u6da6\uff0c\u5374\u53ef\u4ee5\u907f\u514d\u5173\u7a0e\u4e0a\u7684\u4e00\u4e9b\u635f\u5931\uff0c\u56fd\u5185\u4eba\u529b\u6210\u672c\u7684\u4f4e\u5ec9\uff0c\u8fd8\u80fd\u7ee7\u7eed\u964d\u4f4e\u4e86\u82af\u7247\u5c01\u88c5\u4e0a\u7684\u6210\u672c\u3002\u9526\u6e56\u8981\u662f\u6ca1\u6709\u6295\u8d44\u5efa\u9020\u82af\u7247\u5c01\u88c5\u6d4b\u8bd5\u5de5\u5382\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u8003\u8651\u5c06\u8fd9\u4e00\u5757\u7684\u4e1a\u52a1\u4ea4\u7ed9\u56fd\u5185\u7684\u67d0\u5bb6\u6210\u719f\u4f01\u4e1a\u53bb\u505a\uff0c\u4e09\u661f\u5728\u5efa\u90ba\u7684\u534a\u5bfc\u4f53\u5de5\u5382\u4e5f\u662f\u8003\u8651\u7684\u5bf9\u8c61\u4e4b\u4e00\u3002\u8fd9\u65f6\u5019\u5f53\u7136\u4e0d\u4f1a\u518d\u53bb\u8003\u8651\u522b\u7684\u5382\u5bb6\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u9690\u5f62\u7684\u7ade\u4e89\uff0c\u5916\u4eba\u8fd8\u770b\u4e0d\u51fa\u4ec0\u4e48\u6765\uff0c\u9526\u6e56\u4e0e\u4e09\u661f\u5728\u789f\u673a\u5e02\u573a\u7684\u7ade\u4e89\u5c31\u8981\u76f4\u63a5\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\uff0c\u97e9\u56fd\u4e09\u661f\u5728\u60e0\u5dde\u6295\u8d44\u5efa\u8bbe\u7684\u7535\u5b50\u5de5\u5382\u6240\u751f\u4ea7\u7684VCD\u5f71\u789f\u673a\u4e00\u5ea6\u5360\u636e\u56fd\u5185\u789f\u673a\u5e02\u573a\u8fd1\u4e00\u534a\u7684\u4efd\u989d\u3002\u4f46\u662f\u4e09\u661f\u8fc7\u4e8e\u6025\u5207\u4e86\uff0c\u4e5f\u4e0d\u60f3\u5c06\u76f8\u5f53\u5927\u7684\u4e00\u5757\u5229\u6da6\u8ba9\u7ed9\u65af\u9ad8\u67cf\uff0c\u800c\u662f\u76f4\u63a5\u91c7\u7528\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u751f\u4ea7\u789f\u673a\u3002\u867d\u7136\u91c7\u6293\u4e86\u5e02\u573a\u7684\u5148\u673a\uff0c\u4e5d\u4e03\u5e74\u72e0\u635e\u4e86\u4e00\u7b14\uff0c\u4f46\u662f\u789f\u673a\u8d28\u91cf\u4e0d\u591f\u7a33\u5b9a\uff0c\u5f53\u7231\u8fbe\u7535\u5b50\u5c06\u91c7\u7528\u96c6\u6210\u5ea6\u66f4\u9ad8\u3001\u8d28\u91cf\u66f4\u7a33\u5b9a\u7684\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u751f\u4ea7\u7684\u789f\u673a\u63a8\u5411\u5e02\u573a\uff0c\u52a0\u4e0a\u5927\u80c6\u7684\u5e02\u573a\u8fd0\u4f5c\uff0c\u98ce\u5934\u5c31\u8fc5\u901f\u7ed9\u7231\u8fbe\u7535\u5b50\u62a2\u8d70\u4e86\u3002</p><p>\u3000\u3000\u4e09\u661f\u789f\u673a\u76ee\u524d\u5728\u56fd\u5185\u5e02\u573a\u53ea\u80fd\u7b97\u4e8c\u6d41\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u789f\u673a\u4ea7\u54c1\u4e0a\u7684\u7ade\u4e89\u5931\u5229\uff0c\u4e0e\u4e09\u661f\u81ea\u8eab\u7684\u7ade\u4e89\u7b56\u7565\u6709\u5f88\u5927\u7684\u5173\u7cfb\uff0c\u6028\u6068\u8fd8\u4e0d\u80fd\u8f6c\u79fb\u5230\u7231\u8fbe\u7535\u5b50\u7684\u5934\u4e0a\u3002\u4f46\u662f\u9999\u96ea\u6d77\u5408\u8d44\u6848\uff0c\u97e9\u56fd\u4e09\u661f\u5728\u60e0\u5c71\u5efa\u7acb\u5176\u5bb6\u7535\u751f\u4ea7\u57fa\u5730\u7684\u8ba1\u5212\u5374\u662f\u771f\u771f\u5207\u5207\u7684\u7ed9\u5f20\u606a\u4ed6\u4eec\u7ed9\u6405\u9ec4\u4e86\u3002</p><p>\u3000\u3000\u97e9\u56fd\u4e09\u661f\u5e76\u6ca1\u6709\u653e\u5f03\u5c06\u5176\u751f\u4ea7\u57fa\u5730\u8f6c\u79fb\u5230\u4e2d\u56fd\u7684\u52aa\u529b\uff0c\u76f8\u53cd\u5728\u5468\u747e\u745c\u8c03\u804c\u6c5f\u5357\u7701\u4e4b\u540e\uff0c\u5728\u5468\u747e\u745c\u7684\u7275\u9488\u5f15\u7ebf\u4e4b\u4e0b\uff0c\u4e0e\u91d1\u5c71\u5e02\u94f6\u5cad\u7535\u51b0\u7bb1\u5382\u8fc5\u901f\u8fbe\u6210\u5408\u8d44\u534f\u8bae\uff0c\u5728\u91d1\u5c71\u6295\u8d44\u4e94\u5343\u4e07\u7f8e\u5143\u5efa\u8bbe\u4ed6\u4eec\u5728\u56fd\u5185\u7684\u7b2c\u4e00\u4e2a\u5bb6\u7535\u5236\u9020\u57fa\u5730\uff1b\u4e5f\u8ba9\u5468\u747e\u745c\u752b\u81f3\u6c5f\u5357\u7701\u5c31\u5f97\u5230\u4e00\u9879\u5927\u7684\u653f\u7ee9\u3002</p><p>\u3000\u3000\u867d\u7136\u97e9\u56fd\u4e09\u661f\u5728\u4e2d\u56fd\u7684\u6295\u8d44\u90fd\u662f\u5728\u4e09\u661f\uff08\u4e2d\u56fd\uff09\u6295\u8d44\u6709\u9650\u516c\u53f8\u7684\u6846\u67b6\u4e0b\u8fdb\u884c\u7684\uff0c\u4e5f\u5c31\u662f\u4e09\u661f\uff08\u4e2d\u56fd\uff09\u6295\u8d44\u516c\u53f8\u662f\u4e09\u661f\u5728\u4e2d\u56fd\u7684\u603b\u90e8\u673a\u6784\u3002</p><p>\u3000\u3000\u622a\u6b62\u5230\u4e5d\u4e03\u5e74\u4e09\u661f\u5728\u4e2d\u56fd\u53ea\u6709\u4e9b\u9646\u9646\u7eed\u7eed\u7684\u6295\u8d44\uff0c\u8fd8\u6ca1\u6709\u5927\u89c4\u6a21\u7684\u5c06\u5176\u751f\u4ea7\u57fa\u5730\u79fb\u5230\u4e2d\u56fd\u6765\uff0c\u8fd8\u6ca1\u6709\u5927\u89c4\u6a21\u7684\u5f00\u53d1\u4e2d\u56fd\u7684\u5e02\u573a\uff0c\u4f46\u662f\u8fd9\u4e00\u5207\uff0c\u4e0d\u4f1a\u592a\u4e45\u8fdc\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0e\u524d\u5c18\u5f80\u4e8b\u76f8\u6bd4\uff0c\u4eca\u4e16\u7684\u97e9\u56fd\u4e0e\u97e9\u56fd\u4e09\u661f\u90fd\u6709\u6781\u5927\u7684\u4e0d\u540c\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5439\u88ad\u97e9\u56fd\uff0c\u4f7f\u5f97\u97e9\u56fd\u63d0\u524d\u8fd1\u4e24\u4e2a\u6708\u5168\u9762\u7684\u7206\u53d1\u91d1\u878d\u5371\u673a\uff0c\u622a\u6b62\u5230\u5341\u6708\u5e95\uff0c\u97e9\u56fd\u4e09\u5341\u5bb6\u5927\u4f01\u4e1a\u5df2\u7ecf\u4e5d\u5bb6\u7533\u8bf7\u7834\u4ea7\u4fdd\u62a4\u6216\u5b9e\u9645\u8fdb\u5165\u7834\u4ea7\u7a0b\u5e8f\uff0c\u7834\u5165\u56f0\u96be\u7684\u91d1\u878d\u673a\u6784\u4e0e\u4f01\u4e1a\u4e0d\u8ba1\u5176\u6570\uff0c\u7ecf\u6d4e\u5371\u673a\u7684\u7a0b\u5ea6\u4e0e\u89c4\u6a21\u6bd4\u524d\u4e16\u66f4\u5927\u3002</p><p>\u3000\u3000\u8fd9\u662f\u7531\u4e8e\u56fd\u9645\u8d27\u5e01\u6295\u8d44\u8005\u5728\u5931\u53bb\u9999\u6e2f\u8fd9\u4e2a\u91cd\u5927\u76ee\u6807\u4e4b\u540e\u53d1\u6cc4\u4e0e\u6295\u673a\u7684\u529b\u91cf\u66f4\u96c6\u4e2d\u4e8e\u97e9\u56fd\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u97e9\u56fd\u4e09\u661f\u867d\u7136\u6ca1\u6709\u75c5\u5165\u818f\u8093\uff0c\u4e5f\u79ef\u6781\u51fa\u552e\u91cd\u5de5\u3001\u5316\u5de5\u7b49\u65b9\u9762\u7684\u4e1a\u52a1\uff0c\u5168\u529b\u4fdd\u5b58\u5728\u7535\u5b50\u3001\u5bb6\u7535\u3001\u534a\u5bfc\u4f53\u65b9\u9762\u7684\u6838\u5fc3\u4e1a\u52a1\uff0c\u4e3a\u6539\u5584\u8d22\u52a1\u72b6\u51b5\u505a\u51fa\u4e00\u4e9b\u6210\u7ee9\uff0c\u4f46\u662f\u4ee4\u97e9\u56fd\u4e09\u661f\u610f\u6599\u4e0d\u5230\u7684\uff0c\u5176\u9886\u5bfc\u4eba\u3001\u88ab\u55bb\u4e3a\u97e9\u56fd\u7ecf\u8425\u4e4b\u795e\u7684\u674e\u5065\u7199\u5e74\u9677\u5165\u4e5d\u4e94\u5e74\u603b\u7edf\u653f\u6cbb\u884c\u8d3f\u6848\u7684\u4e11\u95fb\u800c\u88ab\u5224\u5904\u4e24\u5e74\u6709\u671f\u5f92\u5211\u7f13\u671f\u4e00\u5e74\u7684\u5211\u7f5a\u4e4b\u540e\uff0c\u4e5d\u4e03\u5e74\u6ca1\u6709\u7b49\u5230\u97e9\u56fd\u603b\u7edf\u7684\u7279\u8d66\uff0c\u518d\u6b21\u9677\u5165\u4e3e\u56fd\u9707\u60ca\u7684\u9003\u7a0e\u6848\u4e11\u95fb\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u4e5d\u6708\u4e2d\u65ec\uff0c\u4e00\u5bb6\u4e0d\u8d77\u773c\u7684\u97e9\u56fd\u5a92\u4f53\u7206\u6599\u6307\u51fa\u674e\u5065\u7199\u901a\u8fc7\u533f\u540d\u8d26\u6237\u63a7\u5236\u4e09\u661f\u96c6\u56e2\u65d7\u4e0b\u8bb8\u591a\u516c\u53f8\u7684\u80a1\u4efd\uff0c\u8fd9\u4e9b\u533f\u540d\u8d26\u6237\u662f\u97e9\u56fd\u4e09\u661f\u521b\u4e1a\u4eba\u3001\u674e\u5065\u7199\u7684\u7236\u4eb2\u674e\u79c9\u5586\u5728\u8fc7\u4e16\u524d\u8bbe\u7acb\u4e86\u3002\u5728\u674e\u79c9\u5586\u901d\u4e16\u4e4b\u540e\uff0c\u674e\u5065\u7199\u63a5\u638c\u4e09\u661f\u96c6\u56e2\uff0c\u4e5f\u5b9e\u9645\u63a7\u5236\u4e86\u8fd9\u4e9b\u533f\u540d\u8d26\u6237\u3002\u6b64\u4e3e\u8ba9\u674e\u5065\u7199\u5b9e\u9645\u63a7\u5236\u4e09\u661f\u96c6\u56e2\u7684\u540c\u65f6\u907f\u514d\u4e86\u5de8\u989d\u7684\u9057\u4ea7\u7a0e\u3002</p><p>\u3000\u3000\u6216\u8bb8\u662f\u97e9\u56fd\u68c0\u5bdf\u90e8\u95e8\u4e0e\u4e09\u661f\u96c6\u56e2\u8fbe\u6210\u548c\u89e3\u534f\u8bae\uff0c\u674e\u5065\u7199\u5728\u5341\u6708\u4e0a\u65ec\u5ba3\u5e03\u8f9e\u53bb\u5728\u4e09\u661f\u96c6\u56e2\u5185\u7684\u6240\u6709\u884c\u653f\u804c\u52a1\uff0c\u9000\u51fa\u5bf9\u4e09\u661f\u96c6\u56e2\u7684\u76f4\u63a5\u9886\u5bfc\uff0c\u5e76\u5ba3\u5e03\u674e\u6c0f\u5bb6\u65cf\u5c06\u6350\u8d60\u5408\u8ba1\u516b\u4ebf\u7f8e\u5143\u7684\u5bb6\u65cf\u8d44\u4ea7\u7ed9\u56fd\u5bb6\u5e2e\u52a9\u6e21\u8fc7\u773c\u4e0b\u7684\u7ecf\u6d4e\u5371\u673a\uff1b\u97e9\u56fd\u68c0\u5bdf\u90e8\u95e8\u4e5f\u6ca1\u6709\u7ee7\u7eed\u8ffd\u5bdf\u9003\u7a0e\u6848\u3002</p><p>\u3000\u3000\u674e\u5065\u7199\u7684\u526f\u624b\uff0c\u4e09\u661f\u96c6\u56e2\u8463\u4e8b\u4f1a\u526f\u4e3b\u5e2d\u3001\u4e09\u661f\u96c6\u56e2\u6218\u7565\u4f01\u5212\u5ba4\u7684\u5ba4\u957f\u6734\u9e64\u6d19\u63a5\u66ff\u674e\u5065\u7199\u5728\u4e09\u661f\u96c6\u56e2\u5185\u7684\u804c\u52a1\uff0c\u6210\u4e3a\u4e09\u661f\u96c6\u56e2\u65b0\u7684\u638c\u95e8\u4eba\u3002\u6218\u7565\u4f01\u5212\u5ba4\u5728\u4e09\u661f\u96c6\u56e2\u5185\u7684\u5730\u4f4d\uff0c\u76f8\u5f53\u4e8e\u7231\u8fbe\u96c6\u56e2\u7684\u5927\u9879\u76ee\u90e8\uff0c\u6734\u9e64\u6d19\u7684\u786e\u662f\u674e\u5065\u7199\u7ecf\u8425\u7406\u5ff5\u7684\u5fe0\u5b9e\u7ee7\u627f\u8005\u3002</p><p>\u3000\u3000\u4e09\u661f\u96c6\u56e2\u7684\u674e\u5065\u7199\u5bb6\u65cf\u5927\u6982\u5f88\u90c1\u95f7\uff0c\u4e00\u5bb6\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c0f\u5a92\u4f53\u5982\u4f55\u77e5\u9053\u4ed6\u4eec\u6781\u529b\u63a9\u9970\u4e86\u8fd1\u5341\u5e74\u7684\u9003\u7a0e\u79d8\u5bc6\u3002</p><p>\u3000\u3000\u5341\u5e74\u524d\uff0c\u4e09\u661f\u96c6\u56e2\u5728\u4e9a\u6d32\u5730\u533a\u8fd8\u662f\u4e00\u5bb6\u4e09\u6d41\u7684\u4f01\u4e1a\uff0c\u6240\u9003\u7684\u9057\u4ea7\u7a0e\u989d\u4e5f\u6ca1\u6709\u591a\u5c11\u94b1\uff0c\u81f3\u5c11\u6bd4\u4ed6\u4eec\u8fd9\u6b21\u88ab\u8feb\u6350\u8d60\u7684\u5bb6\u65cf\u8d44\u4ea7\u8981\u5c11\u5f97\u591a\u3002</p><p>\u3000\u3000\u4e09\u661f\u96c6\u56e2\u7684\u5d1b\u8d77\uff0c\u6070\u6070\u662f\u674e\u5065\u7199\u62c5\u4efb\u9886\u5bfc\u4eba\u4e4b\u540e\u6240\u505a\u51fa\u7684\u52aa\u529b\u3002</p><p>\u3000\u3000\u7531\u4e8e\u8fd9\u5bb6\u5a92\u4f53\u51e0\u4e4e\u7206\u51fa\u907f\u7a0e\u6848\u7684\u6240\u6709\u7ec6\u8282\uff0c\u52a0\u4e0a\u5927\u91cf\u97e9\u56fd\u5a92\u4f53\u7684\u8ddf\u8fdb\u62a5\u9053\uff0c\u674e\u5065\u7199\u5bb6\u65cf\u624b\u5fd9\u811a\u4e71\uff0c\u52a0\u4e0a\u8fd9\u6bb5\u65f6\u95f4\uff0c\u97e9\u56fd\u4e0a\u4e0b\u56e0\u4e3a\u7206\u53d1\u7ecf\u6d4e\u5371\u673a\u800c\u9e21\u98de\u72d7\u8df3\uff0c\u4e00\u65f6\u95f4\u4e5f\u65e0\u6687\u53bb\u8ffd\u7a76\u5230\u5e95\u662f\u8c01\u80cc\u53db\u4e86\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u6216\u8bb8\u60f3\u4e0d\u5230\uff0c\u8981\u6ca1\u6709\u9ed1\u624b\u5728\u5e55\u540e\u64cd\u4f5c\uff0c\u907f\u7a0e\u6848\u8981\u62d6\u5230\u5341\u4e00\u5e74\u4e4b\u540e\u3001\u674e\u5065\u7199\u610f\u6b32\u5c06\u90a3\u4e9b\u533f\u540d\u8d26\u6237\u7684\u63a7\u5236\u6743\u8f6c\u79fb\u7ed9\u4ed6\u7684\u513f\u5b50\u624d\u906d\u5230\u5f7b\u5e95\u7206\u5149\u3002</p><p>\u3000\u3000\u4e09\u661f\u96c6\u56e2\u5c31\u8fd9\u6837\u504f\u79bb\u4ed6\u4eec\u65e2\u5b9a\u7684\u5386\u53f2\u8f68\u8ff9\u3002</p><p>\u3000\u3000\u5f20\u606a\u6653\u5f97\u674e\u5065\u7199\u8fd8\u4f1a\u5728\u5e55\u540e\u7ee7\u7eed\u9886\u5bfc\u4e09\u661f\u96c6\u56e2\uff0c\u9694\u4e86\u4e00\u5c42\uff0c\u603b\u4e0d\u6bd4\u4ee5\u524d\u90a3\u4e48\u5f97\u5fc3\u5e94\u624b\uff0c\u6216\u8bb8\u4f5c\u4e3a\u4e0e\u97e9\u56fd\u68c0\u5bdf\u90e8\u95e8\u7684\u59a5\u534f\u6761\u4ef6\uff0c\u674e\u5065\u7199\u9000\u5165\u5bf9\u4e09\u661f\u96c6\u56e2\u7684\u76f4\u63a5\u9886\u5bfc\uff0c\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\u4e5f\u90fd\u7eb7\u7eb7\u79bb\u5f00\u4e09\u661f\u96c6\u56e2\u5728\u97e9\u56fd\u7684\u603b\u90e8\uff0c\u5230\u6d77\u5916\u5206\u90e8\u4efb\u804c\u79ef\u7d2f\u804c\u52a1\u4e0a\u7684\u7ecf\u9a8c\uff0c\u5728\u97e9\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u773c\u91cc\uff0c\u4e5f\u662f\u674e\u5065\u7199\u5bb6\u65cf\u4e3b\u52a8\u524a\u5f31\u5bf9\u4e09\u661f\u96c6\u56e2\u63a7\u5236\u6743\u7684\u8868\u73b0\u3002</p><p>\u3000\u3000\u5374\u4e0d\u6653\u5f97\u8fd9\u5b8c\u5168\u662f\u4ee5\u9000\u4e3a\u8fdb\u7684\u7b56\u7565\uff0c\u4e09\u661f\u96c6\u56e2\u7684\u547d\u8109\u4e0e\u672a\u6765\u7684\u751f\u673a\u4e0d\u5728\u97e9\u56fd\uff0c\u6070\u6070\u662f\u90a3\u4e9b\u6d77\u5916\u5e02\u573a\uff0c\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\u63a7\u5236\u4e86\u4e09\u661f\u7684\u6d77\u5916\u5e02\u573a\uff0c\u4ee5\u540e\u91cd\u56de\u4e09\u661f\u603b\u90e8\u8fd8\u6709\u4ec0\u4e48\u56f0\u96be\u7684\uff1f</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u674e\u5728\u6d19\u51fa\u73b0\u5728\u5efa\u90ba\u7684\u539f\u56e0\u5427\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u662f\u4e09\u661f\u96c6\u56e2\u521b\u59cb\u4eba\u674e\u79c9\u5586\u6b21\u5b50\u7684\u6b21\u5b50\uff0c\u5a92\u4f53\u5bf9\u674e\u5065\u7199\u5bb6\u65cf\u7684\u5973\u6027\u6210\u5458\u62a5\u9053\u6781\u5c11\uff0c\u5f20\u606a\u5012\u8fd8\u4e0d\u77e5\u9053\u90a3\u4e2a\u660e\u8273\u5973\u90ce\u662f\u8c01\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u65e0\u8bba\u97e9\u56fd\u662f\u5426\u7206\u53d1\u7ecf\u6d4e\u5371\u673a\uff0c\u65e0\u8bba\u4e09\u661f\u96c6\u56e2\u5f53\u524d\u662f\u5426\u9677\u5165\u4e86\u56f0\u5883\uff0c\u674e\u5728\u6d19\u8fd9\u6837\u7684\u5bb6\u65cf\u5b50\u5f1f\u603b\u4e0d\u4f1a\u59d4\u5c48\u4e86\u81ea\u5df1\uff0c\u4e00\u8f86\u6cd5\u62c9\u5229\u9a6c\u62c9\u5185\u7f57\u8dd1\u8f66\u5b9e\u5728\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u5f00\u7740\u4e00\u8f86\u6cd5\u62c9\u5229\u9a6c\u62c9\u5185\u7f57\u8dd1\u8f66\u5230\u5904\u62c9\u98ce\u5c31\u662f\u4e0d\u5b66\u65e0\u672f\u7684\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u674e\u5065\u7199\u5e94\u8be5\u65e9\u5c31\u770b\u51fa\u4e2d\u56fd\u5e02\u573a\u7684\u91cd\u8981\u6027\uff0c\u4f5c\u4e3a\u7ed9\u97e9\u56fd\u4eba\u6367\u4e3a\u7ecf\u8425\u4e4b\u795e\u7684\u674e\u5065\u7199\u4e0d\u4f1a\u6d3e\u4e00\u4e2a\u4e0d\u5b66\u65e0\u672f\u7684\u5e9f\u7269\u5230\u4e2d\u56fd\u5e02\u573a\u6765\u79ef\u7d2f\u804c\u52a1\u4e0a\u7684\u7ecf\u9a8c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e0d\u4f1a\u8f7b\u89c6\u8fd9\u4e2a\u674e\u5728\u6d19\uff0c\u53ea\u662f\u591a\u60f3\u4e5f\u65e0\u76ca\uff0c\u4e0b\u5348\u867d\u7136\u6709\u8bfe\uff0c\u4f46\u662f\u4ed6\u66f4\u613f\u610f\u5230\u56fe\u4e66\u9986\u5750\u4e00\u4e0b\u5348\u3002</p><p>\u3000\u3000\u9ec4\u660f\u65f6\uff0c\u63a5\u5230\u8d75\u9e4f\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u4e3d\u4e30\u5370\u67d3\u5382\u4e0b\u5c97\u804c\u5de5\u7684\u95ee\u9898\uff0c\u7ecf\u8fc7\u5e02\u603b\u5de5\u4f1a\u4e0e\u5e02\u52b3\u52a8\u5c40\u7684\u534f\u8c03\uff0c\u7b97\u662f\u5f7b\u5e95\u89e3\u51b3\u4e86\u3002\u6a61\u6811\u56ed\u9664\u4e86\u5c3d\u53ef\u80fd\u7ed9\u4e0b\u5c97\u804c\u5de5\u5b89\u6392\u5de5\u4f5c\u5c97\u4f4d\u4e4b\u5916\uff0c\u8fd8\u989d\u5916\u63d0\u51fa\u4e00\u767e\u4e07\u7684\u8d44\u91d1\u4f5c\u4e3a\u4fc3\u8fdb\u56e0\u4e3a\u5404\u79cd\u539f\u56e0\u5f85\u5c97\u7684\u56fd\u4f01\u804c\u5de5\u518d\u5c31\u4e1a\u7684\u57fa\u91d1\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u8fd8\u6253\u6765\u5b89\u6170\u7684\u7535\u8bdd\uff0c\u5728\u7535\u8bdd\u5c06\u7530\u529b\u5c71\u72e0\u72e0\u7684\u8d2c\u8d23\u4e86\u4e00\u901a\uff0c\u8bf4\u662f\u8981\u5a92\u4f53\u597d\u597d\u5ba3\u626c\u4e00\u4e0b\u9526\u6e56\u7684\u4e49\u4e3e\uff0c\u867d\u7136\u5728\u4ed6\u773c\u91cc\u9526\u6e56\u6350\u94b1\u6709\u4e9b\u88ab\u8feb\u3001\u53d7\u59d4\u5c48\u7684\u610f\u5473\uff0c\u4f46\u603b\u4e0d\u80fd\u8ba9\u9526\u6e56\u767d\u6350\u8fd9\u7b14\u94b1\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u662f\u987a\u6c34\u63a8\u821f\u7684\u8bf4\u4e86\u4e9b\u5ba2\u5957\u8bdd\uff0c\u4e8b\u60c5\u4fbf\u7b97\u89e3\u51b3\u4e86\u3002\u53ea\u8981\u9526\u6e56\u4e0d\u4e3a\u6b64\u6028\u6068\uff0c\u7530\u529b\u5c71\u80fd\u4ece\u9526\u6e56\u201c\u8bb9\u201d\u5230\u94b1\uff0c\u4e5f\u4e0d\u4f1a\u53d7\u5230\u591a\u5c11\u8d23\u5907\u7684\uff0c\u6bd5\u7adf\u4ed6\u7b79\u5230\u94b1\u4e86\uff0c\u80fd\u89e3\u51b3\u4e00\u4e9b\u5b9e\u9645\u7684\u95ee\u9898\uff0c\u53c8\u6ca1\u6709\u7ed9\u5e02\u533a\u589e\u52a0\u4ec0\u4e48\u8d1f\u62c5\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u4fbf\u6682\u65f6\u7b97\u662f\u8fd9\u4e48\u8fc7\u53bb\u4e86\uff0c\u5f20\u606a\u5fc3\u60f3\u7530\u529b\u5c71\u4e5f\u6ca1\u6709\u5de5\u592b\u518d\u8dd1\u5230\u5bbf\u820d\u697c\u4e0b\u6536\u8863\u670d\u4e86\u3002</p></article>"}}}]);
