(window.webpackJsonp=window.webpackJsonp||[]).push([[67],{89:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e94\u8282 \u4e4c\u9eef\u541b\u4e3b\u964d\u4e34",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e94\u8282 \u4e4c\u9eef\u541b\u4e3b\u964d\u4e34</h2><p>\u8482\u5a1c\u5c91\u75af\u72c2\u5730\u5927\u7b11\u8d77\u6765\uff0c\u5979\u731b\u7136\u4f4e\u5934\uff0c\u4e00\u53e3\u54ac\u5728\u7267\u5e08\u7684\u80a9\u5934\u3002\u7267\u5e08\u6b63\u6c14\u5598\u5401\u5401\u5730\u5728\u5979\u8eab\u4e0a\u51b2\u523a\u7740\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u5feb\u8981\u8fbe\u5230\u5feb\u4e50\u7684\u5dc5\u5cf0\u3002\u9661\u7136\u80a9\u5934\u4f20\u6765\u4e00\u9635\u5267\u75db\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u4fa7\u5934\u770b\u53bb\uff0c\u89c1\u8482\u5a1c\u5c91\u6b63\u671d\u4ed6\u5fae\u7b11\u7740\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u4f9d\u65e7\u53c8\u5a07\u53c8\u5a9a\uff0c\u4f46\u773c\u4e2d\u5374\u6709\u79cd\u4ee4\u4ed6\u4e0d\u5bd2\u800c\u6817\u7684\u715e\u6c14\uff0c\u5634\u89d2\u9c9c\u8840\u6dcb\u6f13\uff0c\u4eff\u4f5b\u98df\u4eba\u7684\u91ce\u517d\u3002</p><p>\u7267\u5e08\u6fc0\u6ce0\u6ce0\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u9661\u7136\u6e05\u9192\u8fc7\u6765\uff0c\u4ed6\u610f\u8bc6\u5230\u81ea\u5df1\u6b63\u5728\u548c\u4e00\u53ea\u90aa\u6076\u7684\u602a\u7269\u4ea4\u5408\uff0c\u800c\u4e14\u6b63\u5728\u5e2e\u52a9\u5979\u5b8c\u6210\u67d0\u4e2a\u90aa\u6076\u7684\u4eea\u5f0f\u3002\u5f3a\u70c8\u7684\u8d1f\u7f6a\u611f\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4ed6\u7684\u8033\u8fb9\u4eff\u4f5b\u542c\u5230\u6668\u66e6\u4e4b\u795e\u7684\u6012\u58f0\u5475\u65a5\u3002\u7267\u5e08\u7528\u529b\u60f3\u63a8\u5f00\u8eab\u4e0b\u7684\u5996\u8273\u5973\u5b50\uff0c\u4f46\u8482\u5a1c\u5c91\u5374\u7b11\u541f\u541f\u7684\uff0c\u62b1\u5f97\u66f4\u7d27\uff0c\u8ba9\u4e24\u4e2a\u4eba\u7684\u8eab\u4f53\u66f4\u52a0\u7d27\u5bc6\u5730\u878d\u5408\u5728\u4e00\u8d77\u3002</p><p>\u201c\u73b0\u5728\u5c31\u60f3\u8d70\u4e48\uff1f\u201d\u5979\u5a9a\u7b11\u7740\uff0c\u4f38\u51fa\u820c\u5934\u8214\u7740\u5634\u89d2\u7684\u9c9c\u8840\uff0c\u201c\u662f\u592a\u65e9\u4e86\uff1f\u8fd8\u662f\u592a\u8fdf\u4e86\uff1f\u201d</p><p>\u73ab\u7470\u8272\u7684\u5149\u8292\u4ece\u5979\u4eec\u5934\u4e0a\u73b0\u51fa\uff0c\u900f\u660e\u7684\u7403\u4f53\u4ece\u865a\u65e0\u4e2d\u88ab\u7f13\u7f13\u63a8\u51fa\u6765\uff0c\u6b63\u662f\u9ece\u660e\u4e4b\u77f3\u3002\u8482\u5a1c\u5c91\u62ac\u8d77\u53f3\u81c2\uff0c\u9065\u9065\u638c\u63e1\u7740\uff0c\u8eab\u4e3a\u90aa\u9b54\u7684\u4fe1\u5f92\uff0c\u5979\u4e5f\u4e0d\u6562\u76f4\u63a5\u78b0\u89e6\u8fd9\u6668\u66e6\u4e4b\u795e\u521b\u9020\u7684\u5723\u7269\uff0c\u4f46\u901a\u8fc7\u4e00\u5b9a\u7684\u9b54\u6cd5\u5374\u662f\u53ef\u4ee5\u6682\u65f6\u63a7\u5236\u7684\u3002</p><p>\u5170\u68ee\u5fb7\u5c14\u662f\u592a\u9633\u795e\u3001\u6625\u4e4b\u795e\u3001\u5149\u8f89\u4e4b\u795e\u3001\u6668\u66e6\u4e4b\u795e\uff0c\u8d39\u4f26\u56fd\u5ea6\u6700\u5f3a\u5927\u7684\u795e\u7947\u4e4b\u4e00\u3002\u4fe1\u5f92\u79f0\u9882\u4ed6\u7684\u70ed\u60c5\u548c\u5584\u826f\uff0c\u654c\u4eba\u8ba5\u8bbd\u4ed6\u662f\u4e2a\u51b2\u52a8\u7684\u7406\u60f3\u4e3b\u4e49\u8005\u3002\u4ed6\u618e\u6068\u4e00\u5207\u90aa\u6076\u2014\u2014\u5c24\u5176\u618e\u6068\u4ea1\u7075\u3002</p><p>\u4f5c\u4e3a\u6668\u66e6\u4e4b\u795e\u521b\u9020\u7684\u5723\u7269\uff0c\u9ece\u660e\u4e4b\u77f3\u5bf9\u4e00\u5207\u90aa\u6076\u7684\u602a\u7269\u90fd\u6709\u5a01\u80c1\u548c\u6740\u4f24\u529b\uff0c\u4f46\u5b83\u66f4\u662f\u4e00\u5207\u4ea1\u7075\u7684\u5669\u8017\u3002</p><p>\u6b63\u5728\u6df1\u6e0a\u4e2d\u548c\u683c\u62c9\u5179\u7279\u4f5c\u6218\u7684\u5965\u5580\u65af\uff0c\u53f7\u79f0\u4ea1\u7075\u541b\u738b\uff0c\u867d\u7136\u662f\u4e2a\u6076\u9b54\u9886\u4e3b\uff0c\u5374\u4ee5\u7edf\u9a6d\u6267\u638c\u65e0\u7a77\u65e0\u5c3d\u7684\u4ea1\u7075\u800c\u8457\u79f0\u3002\u5018\u82e5\u683c\u62c9\u5179\u7279\u80fd\u83b7\u5f97\u8fd9\u5757\u9ece\u660e\u4e4b\u77f3\uff0c\u51fb\u8d25\u5965\u5580\u65af\uff0c\u4fbf\u5927\u6709\u5e0c\u671b\u3002</p><p>\u8482\u5a1c\u5c91\u5a07\u7b11\u7740\uff0c\u4e00\u8fb9\u63a7\u5236\u7740\u9ece\u660e\u4e4b\u77f3\uff0c\u7f13\u7f13\u4e0b\u964d\uff0c\u4e00\u8fb9\u5c06\u7267\u5e08\u62b1\u5f97\u66f4\u7d27\u3002\u5979\u7684\u4fee\u957f\u53cc\u817f\u6709\u529b\u5730\u73af\u6263\u5728\u7267\u5e08\u7684\u540e\u8170\u4e0a\uff0c\u8ba9\u4ed6\u66f4\u52a0\u6df1\u5165\u81ea\u5df1\u7684\u4f53\u5185\u3002\u7267\u5e08\u6323\u624e\u7740\uff0c\u4f46\u8fd9\u53ea\u80fd\u66f4\u589e\u52a0\u5f7c\u6b64\u7684\u5feb\u611f\uff0c\u4ed6\u7c97\u91cd\u5730\u5598\u606f\uff0c\u611f\u89c9\u81ea\u5df1\u5df2\u7ecf\u5230\u4e86\u7206\u53d1\u7684\u8fb9\u7f18\u3002</p><p>\u8482\u5a1c\u5c91\u7b49\u5f85\u7684\u5c31\u662f\u8fd9\u4e00\u523b\uff0c\u5979\u5f20\u5f00\u53cc\u81c2\uff0c\u9ad8\u58f0\u5411\u5979\u6240\u4fe1\u4ef0\u7684\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\u5ff5\u51fa\u7977\u8bcd\u3002\u8fd9\u5e76\u975e\u5492\u6587\uff0c\u800c\u662f\u7948\u7977\uff0c\u6240\u4ee5\u7528\u7684\u4fbf\u662f\u4eba\u9762\u72ee\u7684\u8bed\u8a00\uff0c\u53f0\u4e0b\u7684\u6885\u83f2\u65af\u4e5f\u80fd\u542c\u5f97\u660e\u767d\u3002</p><p>\u201c\u6697\u4e4b\u4e3b\uff0c\u5f71\u4e4b\u738b\uff0c\u4e4c\u9eef\u7edf\u6cbb\u7740\u591c\u7a7a\uff01\u6211\u4e3b\u683c\u62c9\u5179\u7279\uff0c\u6211\u6c90\u6d74\u5728\u60a8\u7684\u6012\u706b\u4e2d\uff0c\u8bf7\u8d50\u4e88\u6211\u65e0\u4e0a\u7684\u8363\u5149\uff01\u201d</p><p>\u94f6\u8272\u7684\u516d\u8292\u661f\u5728\u865a\u7a7a\u4e2d\u5c55\u73b0\uff0c\u5f53\u4e2d\u73b0\u51fa\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\u7684\u865a\u50cf\u6765\u3002\u548c\u5e73\u7d20\u4eba\u4eec\u89c2\u5ff5\u4e2d\u72f0\u72de\u6050\u6016\u7684\u6076\u9b54\u5f62\u8c61\u4e0d\u540c\uff0c\u683c\u62c9\u5179\u7279\u66f4\u8fd1\u4f3c\u4eba\u7c7b\u3002\u4ed6\u8eab\u6750\u9ad8\u5927\uff0c\u9762\u5bb9\u82f1\u4fca\uff0c\u9edd\u9ed1\u7684\u76ae\u80a4\u6cdb\u7740\u5fae\u5fae\u7684\u94f6\u5149\u3002\u5018\u82e5\u4e0d\u662f\u90a3\u7565\u663e\u5c16\u957f\u7684\u8033\u6735\u548c\u9ec4\u8272\u7684\u7360\u7259\uff0c\u51e0\u4e4e\u4efb\u4f55\u4eba\u7c7b\u90fd\u4f1a\u5728\u7b2c\u4e00\u773c\u5c06\u4ed6\u89c6\u4e3a\u540c\u7c7b\uff0c\u800c\u4e14\u662f\u4e2a\u7f8e\u7537\u5b50\u3002</p><p>\u4ed6\u7a7f\u7740\u9ed1\u8272\u7684\u7cbe\u7f8e\u76ae\u7532\uff0c\u4e0a\u9762\u6ee1\u662f\u94f6\u8272\u9970\u54c1\uff0c\u5f70\u663e\u7740\u8fd9\u4f4d\u6076\u9b54\u541b\u4e3b\u7684\u72ec\u7279\u559c\u597d\u3002\u683c\u62c9\u5179\u7279\u7aef\u5750\u5728\u738b\u5ea7\u4e0a\uff0c\u4e00\u53ea\u624b\u968f\u610f\u5730\u642d\u7740\u819d\u76d6\uff0c\u53e6\u5916\u4e00\u53ea\u624b\u8f7b\u629a\u5728\u8170\u95f4\u7684\u86c7\u5f62\u957f\u5251\u4e0a\u3002\u90a3\u662f\u683c\u62c9\u5179\u7279\u6700\u559c\u6b22\u7684\u6b66\u5668\uff0c\u80fd\u591f\u5728\u5207\u5f00\u5bf9\u624b\u76ae\u80a4\u7684\u540c\u65f6\u6e17\u900f\u8fdb\u5165\u5f3a\u529b\u8150\u8680\u9178\u6db2\u3002\u4ed6\u7684\u9ed1\u8272\u624b\u638c\u4e0a\u957f\u7740\u516d\u6839\u624b\u6307\u3002</p><p>\u683c\u62c9\u5179\u7279\u7684\u865a\u50cf\u5728\u7a7a\u4e2d\u5fae\u5fae\u4f4e\u5934\uff0c\u770b\u7740\u4e0b\u65b9\u7684\u796d\u575b\uff0c\u4ee5\u53ca\u8482\u5a1c\u5c91\uff0c\u4ed6\u7684\u773c\u4e2d\u95ea\u70c1\u7740\u5996\u5f02\u7684\u7eff\u5149\u3002\u65e0\u5c3d\u6df1\u6e0a\u548c\u7269\u8d28\u754c\u4e4b\u95f4\u7684\u4f4d\u9762\u58c1\u969c\u592a\u8fc7\u5f3a\u5927\uff0c\u52a0\u4e0a\u8bf8\u795e\u7684\u963b\u6320\uff0c\u683c\u62c9\u5179\u7279\u5e76\u4e0d\u4f1a\u628a\u81ea\u5df1\u7684\u672c\u4f53\u771f\u6b63\u79fb\u5230\u7269\u8d28\u754c\u6765\u2014\u2014\u4ed6\u53ef\u4ee5\u505a\u5230\u8fd9\u70b9\uff0c\u4f46\u9700\u8981\u6d88\u8017\u592a\u591a\u529b\u91cf\uff0c\u5f97\u4e0d\u507f\u5931\uff1b\u800c\u4e14\u8fd9\u592a\u5371\u9669\u4e86\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5f88\u4e45\u4ee5\u524d\u4ed6\u5c31\u5df2\u7ecf\u5728\u8fd9\u65b9\u9762\u5403\u8fc7\u4e00\u6b21\u5927\u4e8f\u3002\u81ea\u6b64\u4e4b\u540e\u4e4c\u9eef\u541b\u4e3b\u518d\u4e5f\u4e0d\u613f\u610f\u771f\u6b63\u8fdb\u5165\u7269\u8d28\u754c\uff0c\u8fd9\u6b21\u4e5f\u540c\u6837\uff0c\u4ed6\u4ec5\u4ec5\u662f\u9063\u51fa\u4e86\u4e00\u4e2a\u6295\u5f71\uff0c\u5e76\u975e\u672c\u4f53\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u80fd\u770b\u89c1\u81ea\u5df1\u7684\u4e3b\u4eba\u5728\u9762\u524d\u51fa\u73b0\uff0c\u8482\u5a1c\u5c91\u4f9d\u65e7\u6fc0\u52a8\u4e07\u5206\u3002\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u5e76\u975e\u672c\u4f53\uff0c\u4f46\u4e5f\u5e76\u975e\u662f\u865a\u50cf\uff0c\u4ece\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u6765\u8bf4\uff0c\u5176\u5b9e\u6295\u5f71\u4e5f\u53ef\u4ee5\u89c6\u4e3a\u5206\u8eab\uff0c\u7c7b\u4f3c\u4e8e\u795e\u7947\u7684\u5316\u8eab\u3002\u5b83\u5b8c\u5168\u53ef\u4ee5\u89c6\u4e3a\u5f31\u5316\u7248\u7684\u683c\u62c9\u5179\u7279\u3002</p><p>\u201c\u8d5e\u7f8e\u4e4c\u9eef\u4e4b\u4e3b\uff01\u201d\u8482\u5a1c\u5c91\u9ad8\u58f0\u558a\u53eb\u7740\u3002\u6b64\u65f6\u5979\u4ef0\u9762\u8eba\u5728\u796d\u575b\u4e0a\uff0c\u8eab\u4e0a\u8db4\u7740\u7684\u662f\u6b63\u5728\u64a4\u51fa\u548c\u7ee7\u7eed\u6df1\u5165\u51b2\u523a\u4e2d\u6323\u624e\u7684\u7267\u5e08\u3002\u5728\u4ed6\u4eec\u7684\u5934\u9876\u4e0a\uff0c\u9ece\u660e\u4e4b\u77f3\u5728\u7f13\u7f13\u8f6c\u52a8\uff0c\u53d1\u7740\u73ab\u7470\u8272\u7684\u5149\u8f89\u3002</p><p>\u796d\u575b\u4e0b\uff0c\u6240\u6709\u7684\u4eba\u9762\u72ee\u90fd\u530d\u5310\u4e0b\u6765\uff0c\u9ad8\u58f0\u91cd\u590d\uff0c\u6885\u83f2\u65af\u4e5f\u5728\u5176\u4e2d\uff0c\u4f46\u5979\u7684\u8eab\u4f53\u5df2\u7ecf\u7d27\u7ef7\uff0c\u84c4\u52bf\u5f85\u53d1\uff0c\u51c6\u5907\u7740\u53bb\u62a2\u593a\u9ece\u660e\u4e4b\u77f3\u3002\u5979\u5df2\u7ecf\u611f\u89c9\u5230\u81ea\u5df1\u8eab\u4f53\u7684\u67d0\u4e9b\u90e8\u5206\u5728\u8f7b\u5fae\u5730\u626d\u66f2\uff0c\u8fd9\u662f\u53d8\u5316\u672f\u5c06\u8981\u5931\u6548\u7684\u8ff9\u8c61\u3002</p><p>\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u7f13\u7f13\u964d\u4e0b\uff0c\u76f4\u5230\u548c\u7267\u5e08\u7684\u8eab\u4f53\u91cd\u5408\u4e3a\u4e00\u4f53\u3002\u7267\u5e08\u731b\u7136\u98a4\u52a8\u4e86\u4e00\u4e0b\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u5f53\u4ed6\u518d\u6b21\u7741\u5f00\u7684\u65f6\u5019\uff0c\u53cc\u773c\u5df2\u7ecf\u95ea\u70c1\u7740\u5996\u5f02\u7684\u7eff\u5149\uff0c\u8138\u4e0a\u9732\u51fa\u6e29\u6587\u5c14\u96c5\u7684\u7b11\u5bb9\u6765\uff0c\u5634\u89d2\u5fae\u5fae\u7fd8\u8d77\u7684\u66f2\u7ebf\u5374\u53c8\u900f\u7740\u4e9b\u8bb8\u653e\u8361\u4e0d\u7f81\u3002</p><p>\u7267\u5e08\u7684\u8eab\u4f53\u5df2\u7ecf\u88ab\u683c\u62c9\u5179\u7279\u4fb5\u5360\u3002</p><p>\u8fd9\u6b63\u662f\u8482\u5a1c\u5c91\u7684\u8ba1\u5212\u3002\u9ece\u660e\u4e4b\u77f3\u6709\u7740\u592a\u8fc7\u4e8e\u5f3a\u5927\u7684\u5d07\u5584\u529b\u91cf\uff0c\u7eb5\u7136\u662f\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u524d\u6765\uff0c\u4e5f\u96be\u4ee5\u5c06\u5b83\u5e26\u56de\u6df1\u6e0a\u2014\u2014\u8981\u6682\u65f6\u62ff\u8d77\u9ece\u660e\u4e4b\u77f3\u4e0d\u96be\uff0c\u7528\u4e00\u4e2a\u9b54\u6cd5\u9694\u7a7a\u63a7\u5236\u5373\u53ef\uff0c\u8fde\u8482\u5a1c\u5c91\u90fd\u53ef\u4ee5\u505a\u5230\uff0c\u4f46\u82e5\u8981\u5c06\u5b83\u5e26\u56de\u6df1\u6e0a\uff0c\u7a7f\u8d8a\u4f4d\u9762\uff0c\u5c31\u5fc5\u987b\u4eb2\u624b\u63e1\u6301\u7740\uff0c\u800c\u8fd9\u662f\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u90fd\u4e0d\u6562\u5192\u9669\u5c1d\u8bd5\u7684\u3002</p><p>\u8fd9\u5c31\u9700\u8981\u501f\u52a9\u4e00\u4e2a\u5a92\u4ecb\uff0c\u4e00\u4e2a\u6298\u8877\uff0c\u4e00\u4e2a\u80fd\u4e0d\u88ab\u9ece\u660e\u4e4b\u77f3\u4f24\u5bb3\u7684\u9053\u5177\u3002</p><p>\u4e5f\u5c31\u662f\u683c\u5170\u7279\uff0c\u5170\u68ee\u5fb7\u5c14\u7684\u7267\u5e08\u4e86\u3002</p><p>\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u4fb5\u5360\u4e86\u7267\u5e08\u7684\u8eab\u4f53\uff0c\u5c06\u539f\u4e3b\u4eba\u7684\u7075\u9b42\u538b\u5236\u5230\u4e00\u4e2a\u9ed1\u6697\u7684\u89d2\u843d\u3002\u4ed6\u5e76\u6ca1\u6709\u6025\u4e8e\u53bb\u62ff\u9ece\u660e\u4e4b\u77f3\uff0c\u5306\u5306\u8fd4\u56de\u6df1\u6e0a\uff0c\u8fd9\u6837\u505a\u592a\u715e\u98ce\u666f\u3002\u867d\u7136\u6076\u9b54\u4e0d\u80fd\u5728\u7269\u8d28\u754c\u505c\u7559\u592a\u4e45\uff0c\u4e0d\u8fc7\u65f6\u95f4\u8fd8\u7b97\u5145\u88d5\u3002\u8482\u5a1c\u5c91\u7acb\u4e0b\u8fd9\u6837\u5927\u7684\u529f\u52b3\uff0c\u4f5c\u4e3a\u4e3b\u4eba\uff0c\u683c\u62c9\u5179\u7279\u5e94\u8be5\u7ed9\u4e88\u4e00\u4e9b\u9002\u5f53\u7684\u5956\u52b1\u3002</p><p>\u4ed6\u7a0d\u7a0d\u9002\u5e94\u4e86\u4e00\u4e0b\u7267\u5e08\u7684\u8eab\u4f53\uff0c\u968f\u5373\u5f00\u59cb\u5728\u8482\u5a1c\u5c91\u7684\u8eab\u4f53\u91cc\u6a2a\u51b2\u76f4\u649e\u8d77\u6765\u3002\u867d\u7136\u56e0\u4e3a\u662f\u4fb5\u5360\u51e1\u4eba\u7684\u8eaf\u58f3\uff0c\u5668\u5b98\u5e76\u6ca1\u6709\u53d8\u5927\uff0c\u4f46\u4ed6\u7684\u529b\u91cf\u6bd4\u7267\u5e08\u5927\u5f97\u591a\uff0c\u7ecf\u9a8c\u4e30\u5bcc\u66f4\u975e\u5e38\u4eba\u53ef\u6bd4\u2014\u2014\u683c\u62c9\u5179\u7279\u7d20\u6765\u662f\u4ee5\u60c5\u6b32\u6deb\u90aa\u800c\u8457\u79f0\u7684\uff0c\u5728\u6df1\u6e0a\u6076\u9b54\u9886\u4e3b\u4e2d\u4ed6\u62e5\u6709\u6570\u91cf\u6700\u4e3a\u5e9e\u5927\u7684\u540e\u5bab\uff0c\u800c\u4e14\u7537\u5973\u90fd\u5305\u62ec\u3002\u5b8c\u5168\u4e0d\u9700\u8981\u4efb\u4f55\u6280\u5de7\uff0c\u51e0\u4e4e\u662f\u4e00\u77ac\u95f4\u5c31\u5c06\u5973\u4eba\u9762\u72ee\u9996\u9886\u9001\u4e0a\u4e86\u9ad8\u6f6e\u3002\u7d27\u63a5\u7740\u53c8\u662f\u7b2c\u4e8c\u6b21\u3001\u7b2c\u4e09\u6b21\uff0c\u76f4\u5230\u8482\u5a1c\u5c91\u6000\u7591\u81ea\u5df1\u4eca\u665a\u4f1a\u88ab\u8e42\u8e8f\u81f3\u6b7b\uff0c\u5979\u7684\u6076\u9b54\u4e3b\u4eba\u7ec8\u4e8e\u6ee1\u610f\u5730\u5728\u5979\u7684\u4f53\u5185\u53d1\u6cc4\u51fa\u6765\u3002</p><p>\u201c\u505a\u5f97\u4e0d\u9519\u3002\u201d\u6076\u9b54\u5938\u5956\u7740\uff0c\u629a\u6478\u7740\u8482\u5a1c\u5c91\u7684\u8138\uff0c\u4ece\u5979\u8eab\u4f53\u91cc\u7f13\u7f13\u9000\u51fa\u3002\u4ed6\u7ad9\u8d77\u6765\uff0c\u8482\u5a1c\u5c91\u8fde\u5fd9\u4e5f\u8ddf\u7740\u722c\u8d77\u8eab\uff0c\u8dea\u5728\u6076\u9b54\u7684\u53cc\u817f\u95f4\uff0c\u7528\u5507\u820c\u4e3a\u4ed6\u505a\u4e8b\u540e\u7684\u6e05\u7406\u670d\u52a1\u3002\u6076\u9b54\u4e00\u8fb9\u4eab\u53d7\u7740\uff0c\u4f38\u624b\u53bb\u53d6\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u9ece\u660e\u4e4b\u77f3\u3002</p></article>"}}}]);
