(window.webpackJsonp=window.webpackJsonp||[]).push([[672],{695:function(p){p.exports={chapterTitle:null,title:"\u7b2c671\u7ae0 \u4f4e\u5934\u6298\u8170",content:"<article><h2>\u7b2c671\u7ae0 \u4f4e\u5934\u6298\u8170</h2><p>\u3000\u3000\u4e34\u5f00\u8f66\u524d\uff0c\u675c\u98de\u8d76\u8fc7\u6765\uff0c\u6253\u5f00\u8b66\u8f66\u5f80\u91cc\u63a2\u5934\u770b\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ac\u811a\u8981\u8e39\u4ed6\uff1a\u201c\u4f60\u600e\u4e48\u624d\u8d76\u8fc7\u6765\u51d1\u70ed\u95f9\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u65f6\u95f4\u4e5f\u5f88\u7d27\u7684\uff0c\u6211\u4e2d\u5348\u5c31\u542c\u65f6\u5b66\u658c\u4ed6\u4eec\u8bf4\u4e86\uff0c\u5c31\u662f\u6ca1\u62bd\u51fa\u7a7a\u6765\uff0c\u201d\u675c\u98de\u62b1\u6028\u4e86\u4e00\u53e5\uff0c\u773c\u775b\u7d27\u5f80\u674e\u99a8\u4e88\u8eab\u4e0a\u7785\u4e86\u4e24\u773c\uff0c\u563f\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u8fd8\u6709\u522b\u7684\u4e8b\u60c5\u8981\u5fd9\uff0c\u665a\u4e0a\u5c31\u4e0d\u7b49\u4f60\u4eec\u4e00\u8d77\u5403\u996d\u4e86\uff1b\u518d\u8bf4\u4f60\u4eec\u4fe9\u6765\u5077\u5077\u6478\u6478\u8fc7\u6765\u5403\u97e9\u56fd\u83dc\u4e5f\u6ca1\u6709\u558a\u4e0a\u6211\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u5077\u5077\u6478\u6478\uff1f\u201d\u5b59\u9759\u6aac\u77aa\u7740\u773c\u775b\u770b\u7740\u675c\u98de\uff0c\u201c\u4e0d\u662f\u6ca1\u627e\u5230\u4f60\u5417\uff1f\u8c01\u6653\u5f97\u4f60\u5c0f\u5b50\u8dd1\u54ea\u91cc\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u96be\u9053\u4eca\u5929\u624b\u673a\u6ca1\u6709\u4fe1\u53f7\uff1f\u201d\u675c\u98de\u6545\u4f5c\u7591\u60d1\u7684\u63f6\u63c4\u9053\uff0c\u4e34\u65f6\u524d\u53c8\u77a5\u4e86\u674e\u99a8\u4e88\u4e24\u773c\uff0c\u6562\u60c5\u4ed6\u662f\u8d76\u8fc7\u6765\u770b\u8fd9\u4f4d\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u7684\uff0c\u4ed6\u4eca\u5929\u90fd\u4e0d\u5728\u5b66\u5e9c\u5df7\uff0c\u51fa\u53bb\u529e\u4e8b\u53bb\u4e86\uff1b\u60f9\u5f97\u674e\u99a8\u4e88\u7f8e\u7738\u6a2a\u98de\uff0c\u5374\u53c8\u62b1\u6028\u4e0d\u5f97\u3002</p><p>\u3000\u3000\u5f85\u8b66\u8f66\u542f\u52a8\u8d77\u6765\uff0c\u540e\u8f66\u53a2\u91cc\u53c8\u6324\u4e86\u4e24\u540d\u6c11\u8b66\uff0c\u5b59\u9759\u6aac\u5750\u5728\u5185\u4fa7\uff0c\u7d27\u7d27\u8d34\u7740\u5f20\u606a\uff0c\u6328\u7740\u4ed6\u7684\u80f3\u818a\uff0c\u5c0f\u58f0\u95ee\u4ed6\u5bcc\u6625\u9601\u662f\u6307\u4ec0\u4e48\uff0c\u4f55\u7eaa\u4e91\u600e\u4e48\u4f1a\u5728\u5bcc\u6625\u9601\u94d0\u8d70\u90a3\u4e48\u591a\u65e5\u672c\u4eba\u3001\u97e9\u56fd\u4eba\u3002\u5f20\u606a\u53c8\u4e0d\u80fd\u8fd9\u65f6\u5019\u544a\u8bc9\u5979\u5bcc\u6625\u9601\u96c6\u4f53\u4e70\u6625\u4e8b\u4ef6\u7684\u6345\u51fa\u4ed6\u4e0e\u53f6\u5efa\u658c\u90fd\u6709\u53c2\u4e0e\uff0c\u5012\u662f\u5750\u5728\u5f20\u606a\u53f3\u9996\u7684\u6c11\u8b66\u6765\u4e86\u5174\u8da3\uff0c\u5c06\u5916\u754c\u4f20\u95fb\u7684\u5bcc\u6625\u9601\u96c6\u4f53\u4e70\u6625\u4e8b\u4ef6\u7684\u7248\u672c\u8bf4\u7ed9\u5b59\u9759\u6aac\u542c\uff1a\u201c\u90a3\u4e9b\u5bb6\u4f19\uff0c\u5230\u4e2d\u56fd\u6765\u6295\u8d44\u5efa\u5382\u53ef\u4ee5\uff0c\u8981\u662f\u4ee5\u4e3a\u9876\u7740\u6295\u8d44\u7684\u540d\u4e49\u6765\u8fdd\u6cd5\u4e71\u7eaa\uff0c\u4e00\u6837\u5c06\u4ed6\u4eec\u8d76\u56de\u8001\u5bb6\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u76f4\u5230\u4f55\u7eaa\u4e91\u5728\u8b66\u8f66\u5916\u81ea\u66b4\u4ed6\u662f\u5bcc\u6625\u9601\u4e8b\u4ef6\u7684\u4fa6\u529e\u8b66\u5b98\u4e4b\u540e\uff0c\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u4e24\u4eba\u7684\u8138\u8272\u53d8\u4e86\u51e0\u53d8\uff0c\u6001\u5ea6\u5c31\u6709\u4e9b\u5f02\u6837\u4e86\uff0c\u4e00\u76f4\u5c0f\u58f0\u62ff\u97e9\u8bed\u4ea4\u5934\u63a5\u8033\u8bf4\u8bdd\uff1b\u5f20\u606a\u4e5f\u542c\u4e0d\u6e05\u695a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8b66\u8f66\u5230\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\uff0c\u5f20\u606a\u3001\u5b59\u9759\u6aac\u624d\u4e0e\u674e\u5728\u6d19\u4ed6\u4eec\u5206\u5f00\uff0c\u4f55\u7eaa\u4e91\u975e\u5e38\u4f53\u8d34\u7684\u6d3e\u4e86\u4e00\u540d\u5e74\u8f7b\u7684\u5973\u6c11\u8b66\u7ed9\u4ed6\u4eec\u505a\u7b14\u5f55\uff0c\u8fd8\u62ff\u51fa\u4ed6\u73cd\u85cf\u7684\u94c1\u89c2\u97f3\u6c8f\u4e0a\u8336\u6765\u4f3a\u5019\u3002</p><p>\u3000\u3000\u8fd9\u4e8b\u60c5\u975e\u540c\u5c0f\u53ef\uff0c\u4f55\u7eaa\u4e91\u4e00\u65f6\u95f4\u4e5f\u6ca1\u6709\u5de5\u592b\u966a\u5f20\u606a\u3002\u7ed9\u4ed6\u5e26\u5230\u6d3e\u51fa\u6240\u7684\u4e24\u62e8\u4eba\uff0c\u90fd\u662f\u8dfa\u4e00\u8dfa\u811a\u80fd\u9707\u5730\u6447\u4e09\u6447\u7684\u4eba\u7269\uff0c\u5f20\u606a\u81ea\u4e0d\u7528\u8bf4\u2014\u2014\u867d\u7136\u8bf4\u5f20\u606a\u81ea\u5df1\u4e50\u610f\u5230\u6d3e\u51fa\u6240\u6765\u7684\uff0c\u674e\u5728\u6d19\u662f\u4e09\u661f\u96c6\u56e2\u5728\u4e2d\u56fd\u7684\u4e3b\u8981\u8d1f\u8d23\u4eba\u4e4b\u4e00\uff0c\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u65e0\u6570\u7684\u7535\u8bdd\u6253\u8fdb\u6765\u8be2\u95ee\u60c5\u51b5\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u4f55\u7eaa\u4e91\u8d70\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u6345\u5230\u5e02\u5c40\u5916\u4e8b\u5904\uff0c\u674e\u5728\u6d19\u8fd8\u771f\u662f\u4e2a\u4eba\u7269\uff0c\u4ed6\u662f\u4e09\u661f\u5728\u534e\u6295\u8d44\u516c\u53f8\u7684\u4e13\u52a1\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u6b21\u5230\u5efa\u90ba\u6765\uff0c\u662f\u4ee5\u79c1\u4eba\u7684\u8eab\u4efd\uff0c\u5efa\u90ba\u5e02\u91cc\u6ca1\u6709\u8c01\u8d1f\u8d23\u63a5\u5f85\uff0c\u4ed6\u4eec\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u6709\u4e00\u5bb6\u534a\u5bfc\u4f53\u516c\u53f8\uff0c\u8fd9\u5bb6\u516c\u53f8\u7684\u603b\u7ecf\u7406\u8fd9\u65f6\u5019\u5835\u5230\u6211\u4eec\u6d3e\u51fa\u6240\u95e8\u53e3\uff0c\u6211\u8fd8\u5f97\u6d3e\u4eba\u63a5\u5f85\uff1b\u5e02\u5c40\u5916\u4e8b\u5904\u4e00\u4f1a\u513f\u4f1a\u6d3e\u8b66\u5458\u8fc7\u6765\u8054\u5408\u4fa6\u5bdf\uff0c\u97e9\u56fd\u9a7b\u4e0a\u6d77\u7684\u603b\u9886\u4e8b\u4e5f\u5df2\u7ecf\u5750\u8f66\u5728\u8def\u4e0a\u4e86\uff0c\u4e0d\u9700\u8981\u4e09\u4e2a\u5c0f\u65f6\u5c31\u80fd\u8d76\u8fc7\u6765\u2026\u2026\u674e\u5728\u6d19\u4ed6\u4eec\u8868\u793a\uff0c\u97e9\u56fd\u603b\u9886\u4e8b\u4e0d\u8d76\u8fc7\u6765\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u5f00\u53e3\u56de\u7b54\u4efb\u4f55\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8981\u7b49\u4e09\u5c0f\u65f6\u554a\uff0c\u201d\u5f20\u606a\u65e0\u804a\u7684\u770b\u4e86\u770b\u8155\u8868\uff0c\u95f9\u5230\u73b0\u5728\uff0c\u624d\u591c\u91cc\u4e03\u70b9\u949f\uff0c\u201c\u6ca1\u5173\u7cfb\uff0c\u4f60\u5148\u62db\u547c\u4ed6\u4eec\u5b66\u4e60\u6211\u4eec\u7684\u5bf9\u5916\u5f00\u653e\u653f\u7b56\u5427\uff0c\u201d\u53c8\u95ee\u9053\uff0c\u201c\u90a3\u4e2a\u53eb\u91d1\u5357\u52c7\u7684\u7559\u5b66\u751f\u8fd8\u6709\u90a3\u4e2a\u5973\u4eba\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u91d1\u5357\u52c7\u539f\u5148\u662f\u4e09\u661f\u96c6\u56e2\u5728\u534e\u516c\u53f8\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u5230\u4f60\u4eec\u5b66\u6821\u7559\u5b66\u4e5f\u662f\u53d7\u4e09\u661f\u96c6\u56e2\u7684\u8d44\u52a9\uff1b\u90a3\u4e2a\u5973\u4eba\u53eb\u674e\u99a8\u4e88\uff0c\u4e5f\u662f\u4e09\u661f\u96c6\u56e2\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\uff0c\u662f\u674e\u5065\u7199\u7684\u5802\u4f84\u5973\uff0c\u4eca\u5e74\u624d\u4e8c\u5341\u5c81\uff0c\u8fd9\u6b21\u5230\u56fd\u5185\u6765\u529e\u7406\u7684\u662f\u65c5\u6e38\u7b7e\u8bc1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u539f\u6765\u53eb\u674e\u99a8\u4e88\u554a\u2026\u2026\u201d\u5f20\u606a\u5e76\u4e0d\u5173\u5fc3\u674e\u99a8\u4e88\u7684\u5e74\u9f84\uff0c\u674e\u99a8\u4e88\u867d\u7136\u5a92\u4f53\u5c11\u6709\u520a\u767b\u5979\u7684\u7167\u7247\uff0c\u4e0d\u8fc7\u674e\u99a8\u4e88\u96f6\u4e8c\u5e74\u5728\u65e5\u672c\u7559\u5b66\u65f6\u81ea\u7f22\u8eab\u4ea1\u7684\u65b0\u95fb\u4f20\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u7531\u4e8e\u662f\u4e09\u661f\u96c6\u56e2\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\uff0c\u53c8\u662f\u5728\u7559\u5b66\u516c\u5bd3\u91cc\u81ea\u7f22\u8eab\u4ea1\uff0c\u8fd9\u6837\u7684\u65b0\u95fb\u81ea\u7136\u5168\u4e16\u754c\u65b0\u95fb\u5a92\u4f53\u90fd\u4e89\u7740\u8f6c\u8f7d\uff0c\u6709\u4f20\u8a00\u8bf4\u662f\u5bb6\u65cf\u7ed9\u5979\u6307\u5b9a\u7684\u5a5a\u59fb\u4e0d\u5408\u5979\u5fc3\u610f\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u5a92\u4f53\u4f20\u8a00\u800c\u5df2\uff0c\u8fd9\u6837\u7684\u5bb6\u65cf\u79d8\u8f9b\uff0c\u5916\u754c\u53c8\u600e\u4e48\u53ef\u80fd\u8f7b\u6613\u7684\u77e5\u9053\u5462\uff1f</p><p>\u3000\u3000\u4e0d\u8fc7\u60f3\u60f3\u4e5f\u591f\u53ef\u60dc\uff0c\u8fd9\u4e48\u4e00\u4e2a\u6709\u7740\u5929\u4f7f\u9762\u5bb9\u3001\u9b54\u9b3c\u80f8\u90e8\u7684\u5973\u4eba\u6216\u8bb8\u518d\u6709\u56db\u4e94\u5e74\u7684\u65f6\u95f4\u5c31\u5c06\u8981\u5c06\u5e74\u8f7b\u7684\u751f\u547d\u90fd\u7efd\u653e\u5b8c\u6bd5\uff1b\u5f53\u7136\uff0c\u5979\u7684\u4eba\u751f\u8f68\u8ff9\u8bf4\u4e0d\u5b9a\u65e9\u5c31\u626d\u8f6c\u4e86\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u201c\u4f60\u53c8\u5728\u60f3\u90a3\u5bf9\u8f66\u524d\u706f\uff1f\u201d\u5b59\u9759\u6aac\u89c1\u5f20\u606a\u9677\u5165\u5931\u795e\u72b6\u6001\uff0c\u4f38\u51fa\u767d\u5ae9\u7684\u624b\u638c\u5728\u4ed6\u7684\u773c\u524d\u6447\u4e86\u6447\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8f66\u524d\u706f\uff1f\u201d\u4f55\u7eaa\u4e91\u7591\u60d1\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5f53\u7136\u4e0d\u4f1a\u8ddf\u4f55\u7eaa\u4e91\u89e3\u91ca\u4ec0\u4e48\u662f\u8f66\u524d\u706f\uff0c\u53ea\u662f\u7565\u5e26\u7f9e\u6da9\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u53c8\u662f\u4ec0\u4e48\u4eba\u7269\uff1f\u770b\u5230\u5b59\u9759\u6aac\u795e\u8272\u7684\u5f02\u6837\u5904\uff0c\u5c31\u4f1a\u610f\u7684\u5927\u7b11\u8d77\u6765\uff0c\u6709\u6c11\u8b66\u8fdb\u6765\u8bf4\u53c8\u6709\u7535\u8bdd\u627e\u4ed6\uff0c\u4f55\u7eaa\u4e91\u624d\u5148\u51fa\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u521a\u624d\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u5b59\u9759\u6aac\u4e0d\u4f9d\u4e0d\u6320\u7684\u95ee\u5f20\u606a\uff0c\u521a\u624d\u63d0\u5230\u90a3\u4e2a\u97e9\u56fd\u7f8e\u4eba\uff0c\u5f20\u606a\u7684\u5931\u795e\u5f88\u8ba9\u4eba\u6000\u7591\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u90a3\u4e2a\u674e\u99a8\u4e88\u5728\u5bb6\u91cc\u4e00\u5b9a\u4e5f\u662f\u53d7\u6c14\u5305\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u8fd9\u4e5f\u770b\u5f97\u51fa\u6765\uff1f\u201d\u5b59\u9759\u6aac\u4e0d\u89e3\u7684\u770b\u7740\u5f20\u606a\uff0c\u201c\u8fd8\u662f\u8bf4\u4f60\u7684\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\u53c8\u968f\u610f\u6cdb\u6ee5\u6210\u707e\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u2018\u53c8\u2019\uff1f\u201d\u5f20\u606a\u7591\u60d1\u7684\u770b\u7740\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u5b59\u9759\u6aac\u62bf\u5507\u7b11\u7740\u3002</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u8d70\u8fdb\u6765\uff0c\u8bf4\u662f\u90b5\u5fc3\u6587\u4e0e\u53f6\u5c0f\u6850\u7684\u7535\u8bdd\uff0c\u4ed6\u4eec\u521a\u5728\u90b5\u5fc3\u6587\u8001\u5bb6\u529e\u4e86\u5a5a\u5bb4\u56de\u5efa\u90ba\u5c31\u542c\u8bf4\u8fd9\u56de\u4e8b\uff0c\u542c\u4f55\u7eaa\u4e91\u89e3\u91ca\u8fc7\uff0c\u8bf4\u662f\u8981\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u4f55\u7eaa\u4e91\u8bf4\u9053\uff1a\u201c\u53f6\u54e5\u4e5f\u8981\u8d76\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8fc7\u6765\u51d1\u4ec0\u4e48\u70ed\u95f9\uff1f\u201d\u5f20\u606a\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u4e5f\u884c\uff0c\u53f6\u5c0f\u6850\u8fc7\u6765\uff0c\u6211\u6b63\u597d\u6709\u4e8b\u8ddf\u5979\u8bf4\u3002\u201d</p><p>\u3000\u3000\u9526\u6e56\u90a3\u8fb9\uff0c\u6709\u9a6c\u6d77\u9f99\u4ee3\u4e3a\u89e3\u91ca\u2014\u2014\u9a6c\u6d77\u9f99\u4ed6\u4eec\u5728\u6d3e\u51fa\u6240\u95e8\u5916\u7684\u8f66\u91cc\u5019\u7740\uff1b\u5f20\u606a\u5230\u73b0\u5728\u5c31\u63a5\u8fc7\u59da\u6587\u76db\u3001\u91d1\u56fd\u6d77\u3001\u738b\u7ef4\u5747\u4e09\u4e2a\u4eba\u7684\u7535\u8bdd\uff0c\u4e8b\u60c5\u901a\u62a5\u7ed9\u8fd9\u4e09\u4e2a\u4eba\u4e5f\u5c31\u8db3\u591f\u4e86\uff0c\u5c31\u4e0d\u6015\u6709\u8c01\u4f1a\u80e1\u4e71\u8df3\u51fa\u6765\u7ed9\u4f55\u7eaa\u4e91\u65bd\u52a0\u538b\u529b\u4e86\u3002</p><p>\u3000\u3000\u5e02\u516c\u5b89\u5c40\u5916\u4e8b\u5904\u4ee5\u4e00\u540d\u526f\u5904\u957f\u5e26\u961f\uff0c\u4e09\u540d\u8b66\u5b98\u8d76\u4e86\u8fc7\u6765\uff0c\u4ed6\u4eec\u65e9\u5c31\u5f97\u5230\u4e0a\u9762\u5173\u7167\uff0c\u53ea\u4f8b\u884c\u7a0b\u5e8f\u53c8\u7ed9\u5f20\u606a\u505a\u4e86\u4e00\u4efd\u7b14\u5f55\uff0c\u4e5f\u6ca1\u6709\u8981\u6c42\u8fd9\u4e8b\u53ea\u5f52\u5e02\u5c40\u5916\u4e8b\u5904\u5904\u7406\uff0c\u5c31\u5230\u53e6\u5916\u4e00\u95f4\u4fa6\u8baf\u5ba4\u7ed9\u674e\u5728\u6d19\u4ed6\u4eec\u518d\u505a\u7b14\u5f55\uff0c\u5c31\u7b97\u674e\u5728\u6d19\u4ed6\u4eec\u4e0d\u80af\u5f00\u53e3\uff0c\u6cd5\u5b9a\u7a0b\u5e8f\u8fd8\u662f\u8981\u8d70\u7684\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u4e1c\u6d77\u5927\u5b66\u5916\u4e8b\u5904\u5904\u957f\u4ee5\u53ca\u56fd\u5546\u9662\u9662\u957f\u5d14\u56fd\u6052\u4ee5\u53ca\u56fd\u5546\u9662\u4e5d\u4e03\u7ea7\u5e74\u7ea7\u4e3b\u4efb\u9b4f\u4e1c\u5f3a\u8fd8\u6709\u5e2d\u82e5\u7433\u90fd\u5929\u584c\u4e0b\u6765\u4f3c\u7684\u8d76\u4e86\u8fc7\u6765\u3002\u5e2d\u82e5\u7433\u4ece\u65f6\u5b66\u658c\u4ed6\u4eec\u5634\u91cc\u77e5\u9053\u5f20\u606a\u5728\u97e9\u56fd\u9910\u5385\u4e0e\u7559\u5b66\u751f\u53d1\u751f\u7ea0\u7eb7\u7ed9\u6d3e\u51fa\u6240\u6c11\u8b66\u5e26\u8d70\uff0c\u4e0d\u53ef\u80fd\u4e0d\u901a\u77e5\u9662\u91cc\u3002</p><p>\u3000\u3000\u504f\u504f\u6ca1\u6709\u4eba\u901a\u77e5\u97f3\u4e50\u5b66\u9662\u90a3\u8fb9\uff0c\u5c31\u597d\u50cf\u5b59\u9759\u6aac\u8ddf\u6765\u73a9\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u5fc3\u60c5\u5e94\u4ed8\u5d14\u56fd\u6052\u3001\u9b4f\u4e1c\u5f3a\u4ed6\u4eec\uff0c\u8ba9\u4f55\u7eaa\u4e91\u5c06\u4ed6\u4eec\u6321\u5728\u4fa6\u8baf\u5ba4\u5916\u5e94\u4ed8\uff0c\u4ed6\u4e0e\u5b59\u9759\u6aac\u8eb2\u5728\u4fa6\u8baf\u5ba4\u91cc\u4e0b\u4e94\u5b50\u68cb\u73a9\u3002</p><p>\u3000\u3000\u5927\u6982\u662f\u4f55\u7eaa\u4e91\u70e6\u4e0d\u8fc7\u4e1c\u5927\u7684\u6765\u4eba\uff0c\u8d70\u8fdb\u6765\u6c42\u5f20\u606a\u89c1\u4ed6\u4eec\u4e00\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5b66\u6821\u90a3\u5973\u7684\u8fd8\u633a\u51f6\uff0c\u4ee5\u4e3a\u6211\u4eec\u6d3e\u51fa\u6240\u6263\u4e0b\u5728\u8c11\u5f85\u4f60\u4eec\u4fe9\u4f3c\u7684\u2026\u2026\u6211\u5df2\u7ecf\u5c06\u57fa\u672c\u60c5\u51b5\u8ddf\u4ed6\u4eec\u901a\u62a5\u4e86\uff0c\u4ed6\u4eec\u521a\u624d\u5148\u53bb\u8ddf\u674e\u5728\u6d19\u4ed6\u4eec\u4ea4\u6d41\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u5c06\u56f4\u68cb\u5b50\u6536\u8d77\u6765\u85cf\u597d\uff0c\u5d14\u56fd\u6052\u3001\u9b4f\u4e1c\u5f3a\u3001\u5e2d\u82e5\u7433\uff0c\u8fd8\u6709\u6821\u5916\u4e8b\u5904\u3001\u7559\u5b66\u751f\u670d\u52a1\u4e2d\u5fc3\u4e3b\u4efb\u5f20\u6653\u4e1c\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u3001\u9b4f\u4e1c\u5f3a\u3001\u5e2d\u82e5\u7433\u3001\u5f20\u6653\u4e1c\u56db\u4eba\u8138\u4e0a\u8868\u60c5\u5404\u5f02\uff0c\u5927\u6982\u4f55\u7eaa\u4e91\u5f3a\u786c\u7684\u6001\u5ea6\u5df2\u7ecf\u4ece\u6821\u5916\u4e8b\u5904\u4f55\u6653\u5149\u526f\u5904\u957f\u5634\u91cc\u4f20\u56de\u5230\u4ed6\u4eec\u7684\u8033\u6735\u91cc\uff0c\u52a0\u4e0a\u4e5f\u4e86\u89e3\u5230\u57fa\u672c\u7684\u60c5\u51b5\uff0c\u4e5f\u5c31\u6ca1\u6709\u5929\u5d29\u4e0b\u6765\u7684\u60f6\u6050\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u521a\u4e0e\u91d1\u5357\u52c7\u4ed6\u4eec\u4ea4\u6d89\u8fc7\uff0c\u4ed6\u4eec\u8868\u793a\uff0c\u53ea\u8981\u4f60\u4e3a\u6253\u4eba\u7684\u4e8b\u60c5\u8ba4\u9519\u9053\u6b49\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u518d\u8ffd\u7a76\uff0c\u5b66\u6821\u65b9\u9762\u4e5f\u4e0d\u4f1a\u518d\u8ffd\u7a76\u2026\u2026\u201d\u5f20\u6653\u4e1c\u8fc7\u6765\u4e4b\u524d\u4ece\u5d14\u56fd\u6052\u90a3\u91cc\u77e5\u9053\u5f20\u606a\u8fd9\u540d\u5b66\u751f\u7684\u5bb6\u5ead\u80cc\u666f\uff0c\u7236\u4eb2\u521a\u722c\u4e0a\u65b0\u829c\u5e02\u5e38\u59d4\u526f\u5e02\u957f\u7684\u4f4d\u5b50\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u773c\u524d\u8fd9\u540d\u5b66\u751f\u4e5f\u662f\u96be\u7f20\u7684\u4e3b\uff0c\u5b66\u6821\u8fc7\u4e8e\u5f3a\u6309\u7740\u4ed6\u4f4e\u5934\u4e5f\u4e0d\u5408\u9002\u3002</p><p>\u3000\u3000\u201c\u9053\u6b49\u8ba4\u9519\u554a\u2026\u2026\u201d\u5f20\u606a\u6253\u4e86\u54c8\u54c8\uff0c\u5fc3\u60f3\u674e\u5728\u6d19\u4e5f\u8981\u6709\u8fd9\u4e2a\u8138\u9762\u624d\u884c\uff0c\u8bf4\u9053\uff0c\u201c\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u4e0d\u4f1a\u5148\u9053\u6b49\u8ba4\u9519\uff1f\u81f3\u4e8e\u8fde\u7d2f\u5927\u5bb6\u665a\u996d\u90fd\u5403\u4e0d\u597d\uff0c\u6211\u5f88\u62b1\u6b49\uff0c\u6211\u53ef\u4ee5\u9053\u6b49\uff0c\u81f3\u4e8e\u8fd9\u4ef6\u4e8b\u5427\uff0c\u6211\u770b\u8fd8\u662f\u7b49\u4ed6\u4eec\u97e9\u56fd\u603b\u9886\u4e8b\u8d76\u8fc7\u6765\u518d\u8bf4\u597d\u4e86\u3002\u6211\u60f3\u6211\u4eec\u7684\u653f\u5e9c\uff0c\u603b\u4e0d\u81f3\u4e8e\u56e0\u4e3a\u4eba\u5bb6\u603b\u9886\u4e8b\u8d76\u8fc7\u6765\uff0c\u5c31\u8981\u5f3a\u8feb\u4ed6\u4eec\u7684\u4e00\u540d\u666e\u901a\u5b66\u751f\u4f4e\u5934\u8ba4\u9519\u5427\u3002\u201d</p><p>\u3000\u3000\u6298\u817e\u5230\u73b0\u5728\uff0c\u7684\u786e\u628a\u665a\u996d\u7684\u4e8b\u60c5\u90fd\u5fd8\u4e86\uff0c\u4f55\u7eaa\u4e91\u5f97\u5230\u5f20\u606a\u7684\u63d0\u793a\uff0c\u8d76\u7d27\u5429\u5490\u624b\u4e0b\u7684\u8b66\u5458\u51fa\u53bb\u4e70\u76d2\u996d\uff0c\u53c8\u95ee\u5d14\u56fd\u6052\u4ed6\u4eec\uff1a\u201c\u5d14\u9662\u957f\u3001\u5f20\u5904\u957f\uff0c\u4f60\u4eec\u4e5f\u6ca1\u6709\u5403\u665a\u996d\u5427\uff1f\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u54ed\u4e27\u7740\u8138\uff0c\u54ea\u6709\u5fc3\u60c5\u5403\u665a\u996d\u554a\uff1f\u8fd9\u65f6\u5019\u6ca1\u6709\u9b4f\u4e1c\u5f3a\u3001\u5e2d\u82e5\u7433\u8bf4\u8bdd\u7684\u5730\u65b9\uff0c\u5f20\u6653\u4e1c\u5012\u662f\u4e3a\u9694\u58c1\u7684\u674e\u5728\u6d19\u8003\u8651\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7ed9\u4e09\u4e2a\u97e9\u56fd\u5ba2\u4eba\u51c6\u5907\u4e09\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u554a\uff0c\u672a\u5fc5\u5bf9\u5efa\u90ba\u7684\u5feb\u9910\u5408\u610f\uff0c\u8fd8\u662f\u7b97\u4e86\u5427\u3002\u201d\u4f55\u7eaa\u4e91\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u4ed6\u4e0d\u80fd\u5bf9\u674e\u5728\u6d19\u4ed6\u4eec\u600e\u4e48\u7740\uff0c\u4f46\u4e5f\u6ca1\u6709\u5fc3\u60c5\u4f3a\u5019\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u97e9\u56fd\u9a7b\u4e0a\u6d77\u603b\u9886\u4e8b\u97e9\u52c7\u6cf0\u4e5d\u70b9\u534a\u949f\u8d76\u5230\u5efa\u90ba\uff0c\u7701\u91cc\u6ca1\u6709\u4eba\u51fa\u9762\uff0c\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u4e0e\u5e02\u653f\u5e9c\u5916\u4e8b\u529e\u516c\u5ba4\u7684\u9886\u5bfc\u63a5\u5f85\u4e86\u4ed6\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u624d\u4e0d\u60f3\u63d2\u624b\u8fd9\u9e1f\uff0c\u4f46\u4ed6\u662f\u5206\u7ba1\u5916\u4e8b\u5de5\u4f5c\u7684\u526f\u5e02\u957f\uff0c\u4ed6\u8981\u4e0d\u51fa\u9762\uff0c\u5c31\u53ea\u80fd\u5c06\u4e8b\u60c5\u63a8\u7ed9\u5e02\u957f\u8096\u660e\u5efa\u4e86\uff1b\u8096\u660e\u5efa\u518d\u63a8\u8131\uff0c\u8fd8\u4e0d\u662f\u8981\u7f57\u541b\u4eb2\u81ea\u51fa\u9762\u89e3\u51b3\u3002\u867d\u7136\u80e1\u5b97\u5e86\u5bf9\u7f57\u541b\u6ee1\u8179\u610f\u89c1\uff0c\u5374\u4e5f\u4e0d\u6562\u8ddf\u4ed6\u516c\u7136\u5531\u5bf9\u53f0\u620f\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u51fa\u9762\u4e4b\u524d\uff0c\u4e5f\u5df2\u7ecf\u53d7\u5230\u7f57\u541b\u7684\u7535\u8bdd\u5173\u7167\uff1a\u201c\u8fd9\u53ea\u662f\u4e00\u540d\u666e\u901a\u9ad8\u6821\u5b66\u751f\u53d7\u4eba\u8fb1\u9a82\u4e4b\u540e\u96be\u514d\u6709\u4e9b\u51b2\u52a8\u7684\u53cd\u5e94\u800c\u5df2\uff0c\u5e02\u91cc\u8981\u6709\u5e02\u91cc\u7684\u653f\u6cbb\u7acb\u573a\uff0c\u4e5f\u4e0d\u8981\u628a\u5e02\u91cc\u7684\u60c5\u51b5\u968f\u4fbf\u8ddf\u5916\u4eba\u900f\u9732\u2026\u2026\u201d</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u809a\u5b50\u91cc\u65e9\u5c31\u7834\u53e3\u5927\u9a82\u5f00\u4e86\uff0c\u5f20\u606a\u8981\u662f\u666e\u901a\u9ad8\u6821\u5b66\u751f\uff0c\u4e8b\u60c5\u4f1a\u641e\u8fd9\u4e48\u590d\u6742\uff1f\u65e9\u5c31\u6309\u7740\u90a3\u5b66\u751f\u7684\u5934\u9885\u7ed9\u4eba\u5bb6\u8d54\u793c\u9053\u6b49\u4e22\u7ed9\u5b66\u6821\u5904\u5206\u7b97\u4e86\uff0c\u8fd8\u9700\u8981\u97e9\u56fd\u9a7b\u4e0a\u6d77\u603b\u9886\u4e8b\u591c\u91cc\u9a71\u8f66\u8d76\u5230\u5efa\u90ba\u6765\u89e3\u51b3\u95ee\u9898\uff1f</p><p>\u3000\u3000\u5e02\u91cc\u7684\u653f\u6cbb\u7acb\u573a\u662f\u4ec0\u4e48\uff1f\u4e4b\u524d\u7684\u5bcc\u6625\u9601\u4e70\u6625\u4e8b\u4ef6\u5c31\u7ad6\u4e86\u6807\u6746\u3002\u8fd9\u79cd\u4e24\u9762\u90fd\u4e0d\u8ba8\u597d\u7684\u4e8b\u60c5\uff0c\u80e1\u5b97\u5e86\u4e07\u822c\u65e0\u5948\u5c31\u53c8\u65e0\u6cd5\u63a8\u8131\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u97e9\u56fd\u4e09\u661f\u96c6\u56e2\u5728\u4e9a\u6d32\u7684\u5730\u4f4d\u5982\u4f55\u4e86\u5f97\uff0c\u4f46\u5728\u4e1c\u6d77\u7701\uff0c\u5728\u5efa\u90ba\u5e02\uff0c\u5176\u91cd\u8981\u6027\u5374\u8fd8\u662f\u4e0d\u80fd\u8ddf\u9526\u6e56\u76f8\u63d0\u5e76\u8bba\u7684\u3002\u9526\u6e56\u7684\u60c5\u51b5\u53c8\u4e0d\u80fd\u968f\u4fbf\u900f\u9732\uff0c\u90a3\u771f\u7684\u53ea\u80fd\u5f53\u6210\u666e\u901a\u9ad8\u6821\u751f\u7ea0\u7eb7\u5904\u7406\u4e86\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u809a\u5b50\u91cc\u95f7\u7740\u4e00\u56e2\u90c1\u6c14\uff0c\u5148\u6307\u793a\u5e02\u5e9c\u5916\u4e8b\u529e\u516c\u5ba4\u7684\u5b98\u5458\u524d\u53bb\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u4e86\u89e3\u60c5\u51b5\uff0c\u4f46\u4e0d\u5141\u8bb8\u4ed6\u4eec\u63d2\u624b\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u7684\u4e8b\u60c5\uff0c\u4ed6\u5219\u4eb2\u81ea\u5230\u8def\u53e3\u4eb2\u81ea\u63a5\u97e9\u56fd\u9a7b\u534e\u603b\u9886\u4e8b\u97e9\u52c7\u6cf0\uff0c\u7136\u540e\u4e00\u8d77\u5230\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u89e3\u51b3\u95ee\u9898\u3002</p><p>\u3000\u3000\u97e9\u52c7\u6cf0\u53c8\u4e0d\u662f\u50bb\u74dc\uff0c\u4e8b\u60c5\u62d6\u5230\u73b0\u5728\uff0c\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u3001\u674e\u99a8\u4e88\u4e09\u4e2a\u4eba\u90fd\u8fd8\u7ed9\u6263\u5728\u57fa\u5c42\u6d3e\u51fa\u6240\uff0c\u4ed6\u600e\u4e48\u80fd\u4e0d\u660e\u767d\u5efa\u90ba\u5e02\u653f\u5e9c\u7684\u7acb\u573a\uff1f\u4ed6\u4e0e\u80e1\u5b97\u5e86\u76f8\u9047\uff0c\u4e5f\u4e0d\u63d0\u6297\u8bae\u4e0d\u6297\u8bae\u7684\u8bdd\u3002\u771f\u8981\u518d\u6345\u51fa\u4e0e\u5bcc\u6625\u9601\u4e70\u6625\u4e8b\u4ef6\u7c7b\u4f3c\u7684\u4e8b\u60c5\u51fa\u6765\uff0c\u8fd9\u65f6\u5019\u63d0\u51fa\u5916\u4ea4\u6297\u8bae\uff0c\u4e0d\u8fc7\u5f92\u589e\u56fd\u9645\u7b11\u6599\u800c\u5df2\uff0c\u4f55\u51b5\u97e9\u56fd\u56fd\u5185\u7ecf\u6d4e\u5371\u673a\u8086\u8650\uff0c\u867d\u7136\u6700\u5927\u7684\u671f\u5f85\u5bc4\u6258\u5728\u7f8e\u56fd\u8eab\u4e0a\uff0c\u4e5f\u4e0d\u80fd\u5bf9\u8eab\u8fb9\u7684\u4e2d\u56fd\u89c6\u800c\u4e0d\u89c1\u3002\u968f\u968f\u4fbf\u4fbf\u5c06\u4e8b\u4ef6\u5347\u7ea7\uff0c\u5e76\u4e0d\u662f\u4e00\u540d\u5408\u683c\u5916\u4ea4\u5b98\u4f1a\u505a\u7684\u8822\u4e8b\u3002</p><p>\u3000\u3000\u97e9\u52c7\u6cf0\u4e0e\u80e1\u5b97\u5e86\u8d76\u5230\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\uff0c\u5148\u770b\u8fc7\u5f20\u606a\u4ed6\u4eec\u4e0e\u65c1\u89c2\u8005\u7684\u7b14\u5f55\uff0c\u53c8\u53bb\u4e34\u65f6\u7ed9\u6362\u7740\u63a5\u5f85\u5ba4\u94ed\u724c\u7684\u4fa6\u8baf\u5ba4\u4e0e\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u3001\u674e\u99a8\u4e88\u4ed6\u4eec\u89c1\u9762\u4e86\u89e3\u60c5\u51b5\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u4e0d\u60f3\u53bb\u89c1\u5f20\u606a\uff0c\u5728\u6d3e\u51fa\u6240\u7684\u63a5\u5f85\u5ba4\u91cc\uff0c\u4e0e\u4e1c\u5927\u56fd\u5546\u9662\u7684\u5d14\u56fd\u6052\u3001\u5916\u4e8b\u5904\u5904\u957f\u5f20\u6653\u4e1c\u4ed6\u4eec\u89c1\u9762\uff0c\u60c5\u51b5\u4ed6\u65e9\u5c31\u4e86\u89e3\u8fc7\u4e86\uff0c\u5c31\u770b\u97e9\u56fd\u4eba\u90a3\u8fb9\u9700\u8981\u600e\u4e48\u7684\u53f0\u9636\u4e0b\u3002</p><p>\u3000\u3000\u5f20\u6653\u4e1c\u5c31\u671f\u5f85\u80fd\u5927\u4e8b\u5316\u5c0f\u3001\u5c0f\u4e8b\u5316\u4e86\uff0c\u5d14\u56fd\u6052\u4e5f\u662f\u8fd9\u5fc3\u601d\uff0c\u5948\u4f55\u5f20\u606a\u5728\u4ed6\u4eec\u770b\u6765\u5c31\u8ddf\u8305\u5395\u91cc\u7684\u77f3\u5934\u4e00\u6837\u53c8\u81ed\u53c8\u786c\uff0c\u504f\u504f\u5bb6\u91cc\u8fd8\u6709\u4e9b\u80cc\u666f\u4e0d\u5bb9\u5c0f\u89c6\uff0c\u52a0\u4e0a\u4ed6\u4e0e\u8499\u4e50\u3001\u675c\u98de\u524d\u6bb5\u65e5\u5b50\u4e00\u9f50\u641e\u4e86\u4e2a\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\uff0c\u5f88\u53d7\u6821\u65b9\u7684\u5173\u6ce8\uff0c\u5728\u5b66\u751f\u5f53\u4e2d\u4e5f\u9887\u6709\u5f71\u54cd\uff0c\u8fd9\u6837\u7684\u5b66\u751f\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u80fd\u62ff\u51fa\u5f3a\u786c\u59ff\u6001\u6765\u5a01\u903c\u5229\u8bf1\uff1b\u6d3e\u51fa\u6240\u65b9\u9762\u7684\u6001\u5ea6\u53c8\u662f\u51fa\u5947\u7684\u5f3a\u786c\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u6700\u662f\u96be\u71ac\uff0c\u5fc3\u60f3\u81ea\u5df1\u662f\u56fd\u5546\u9662\u4e5d\u4e03\u7ea7\u7684\u5e74\u7ea7\u4e3b\u4efb\u603b\u662f\u8981\u8d1f\u76f4\u63a5\u7684\u7ba1\u7406\u8d23\u4efb\uff0c\u6765\u4e4b\u524d\u5f20\u6653\u4e1c\u8bf4\u4e86\u5f88\u591a\u5371\u8a00\u8038\u542c\u7684\u8bdd\uff0c\u89c1\u5f20\u6653\u5f97\u3001\u5d14\u56fd\u6052\u966a\u81ea\u5df1\u5e72\u5750\u4e86\u4e24\u4e2a\u591a\u5c0f\u65f6\uff0c\u8fd9\u65f6\u5019\u8fde\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u4e0e\u97e9\u56fd\u9a7b\u6caa\u603b\u9886\u4e8b\u90fd\u60ca\u52a8\u4e86\uff0c\u770b\u5230\u80e1\u5b97\u5e86\u9634\u90c1\u7684\u8138\u8272\u8d70\u8fdb\u63a5\u5f85\u5ba4\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u65e0\u7591\u662f\u5929\u5d29\u5730\u88c2\u3001\u672b\u65e5\u6765\u4e34\uff0c\u5bf9\u5f20\u606a\u90a3\u8e1e\u50b2\u72c2\u72f7\u7684\u59ff\u6001\uff0c\u81ea\u7136\u66f4\u662f\u6068\u4e4b\u5165\u9aa8\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u6765\u4e4b\u524d\uff0c\u9b4f\u4e1c\u5f3a\u4e5f\u8bd5\u56fe\u620f\u5f20\u606a\u4f4e\u5934\u8ba4\u9519\uff0c\u5c06\u8fd9\u4e8b\u60c5\u7ed9\u5316\u89e3\u51b3\u6389\uff0c\u5948\u4f55\u5f20\u606a\u542c\u4ed6\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u6253\u4e86\u4e00\u4e2a\u54c8\u6b20\uff0c\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\u5c31\u5c06\u4ed6\u4eec\u8bf7\u51fa\u4fa6\u8baf\u5ba4\u3002</p><p>\u3000\u3000\u4e4b\u540e\uff0c\u6709\u4e09\u4e2a\u7537\u5973\u8d76\u5230\u6d3e\u51fa\u6240\u6765\uff0c\u5c31\u4e0e\u4ed6\u4eec\u6253\u4e86\u4e2a\u7167\u9762\uff0c\u4e5f\u6ca1\u6709\u62db\u547c\u4e00\u58f0\uff0c\u5c31\u8fdb\u53bb\u627e\u5f20\u606a\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u51fa\u6765\u3002</p><p>\u3000\u3000\u770b\u7740\u80e1\u5b97\u5e86\u9634\u90c1\u7684\u5750\u5728\u957f\u6905\u4e0a\uff0c\u53ea\u8ddf\u5d14\u56fd\u6052\u3001\u5f20\u6653\u4e1c\u6253\u4e86\u4e00\u58f0\u62db\u547c\uff0c\u770b\u4e86\u5e2d\u82e5\u7433\u4e00\u773c\uff0c\u5c31\u5750\u5230\u6905\u5b50\u4e0a\u4e00\u58f0\u4e0d\u542d\u3002\u9b4f\u4e1c\u5f3a\u60f6\u6050\u4e0d\u5b89\uff0c\u6ca1\u6709\u52c7\u6c14\u76f4\u63a5\u8ddf\u80e1\u5b97\u5e86\u8bf4\u8bdd\uff0c\u8ddf\u5d14\u56fd\u6052\u8bf4\u9053\uff1a\u201c\u5d14\u9662\u957f\uff0c\u8981\u4e0d\u6211\u8fdb\u53bb\u518d\u529d\u4e0b\u5f20\u606a\uff1f\u90a3\u4e09\u4e2a\u97e9\u56fd\u4eba\u4e0d\u662f\u8bf4\u53ea\u8981\u4ed6\u4f4e\u5934\u8ba4\u9519\u5c31\u4e0d\u4f1a\u518d\u8ffd\u7a76\u4e86\u5417\uff1f\u201d\u8fd9\u8bdd\u81ea\u7136\u4e5f\u662f\u8bf4\u7ed9\u80e1\u5b97\u5e86\u542c\u7684\u3002</p><p>\u3000\u3000\u201c\u4f4e\u5934\u8ba4\u9519\uff1f\u201d\u80e1\u5b97\u5e86\u62ac\u8d77\u5934\uff0c\u770b\u4e86\u9b4f\u4e1c\u5f3a\u4e00\u773c\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u4f4e\u5934\u8ba4\u9519\uff1f\u201d\u4ed6\u809a\u5b50\u5374\u9a82\u5f00\u4e86\uff1a\u8fd9\u6d51\u7403\u8981\u80fd\u4f4e\u5934\u8ba4\u9519\uff0c\u5c31\u4e0d\u4f1a\u5c06\u70ed\u8336\u6cfc\u4eba\u5bb6\u8138\u4e0a\u4e86\uff01\u5173\u952e\u662f\u4e1c\u6d77\u7701\u8c01\u6709\u8d44\u683c\u8ba9\u8fd9\u6d51\u7403\u8ddf\u8fd9\u4e09\u4e2a\u97e9\u56fd\u4eba\u4f4e\u5934\u8ba4\u9519\uff0c\u5c06\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u62ac\u51fa\u6765\u5417\uff1f\u80e1\u5b97\u5e86\u5fc3\u91cc\u6092\u90c1\u96be\u5e73\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u662f\u81ea\u5df1\u644a\u4e0a\u8fd9\u5dee\u4e8b\uff1f\u62ac\u5934\u89c1\u4e1c\u5927\u7684\u51e0\u540d\u9886\u5bfc\u90fd\u4e00\u8138\u9519\u6115\uff0c\u624d\u60f3\u8d77\u8fd8\u6ca1\u6709\u5c06\u5e02\u91cc\u7684\u7acb\u573a\u4e0e\u4ed6\u4eec\u901a\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u6211\u4e86\u89e3\u8fc7\u4e86\uff0c\u5c31\u662f\u666e\u901a\u7684\u5c0f\u7ea0\u7eb7\u800c\u5df2\uff0c\u6ca1\u6709\u8c01\u5bf9\u8c01\u9519\uff0c\u54ea\u4e00\u65b9\u9762\u8981\u613f\u610f\u4e3b\u52a8\u8ba4\u9519\u548c\u89e3\uff0c\u6211\u4eec\u4e5f\u662f\u6b22\u8fce\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u90fd\u4e0d\u613f\u610f\u548c\u89e3\u5462\uff1f\u201d\u5f20\u6653\u4e1c\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u90fd\u4e0d\u613f\u610f\u63a5\u53d7\u548c\u89e3\u2026\u2026\u201d\u80e1\u5b97\u5e86\u770b\u4e86\u4f55\u7eaa\u4e91\u4e00\u773c\uff0c\u6c14\u9f13\u9f13\u7684\u8bf4\u9053\uff0c\u201c\u90a3\u5c31\u6309\u5916\u4e8b\u7ba1\u7406\u529e\u6cd5\u3001\u6cbb\u5b89\u7ba1\u7406\u6761\u4f8b\u4e0a\u7684\u89c4\u5b9a\u5904\u7406\u2026\u2026\u4ed6\u4eec\u7231\u95f9\u968f\u4ed6\u4eec\u95f9\u53bb\u3002\u201d</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u7684\u6001\u5ea6\uff0c\u60df\u4e00\u8ba9\u5d14\u56fd\u6052\u3001\u5f20\u6653\u4e1c\u4ed6\u4eec\u80fd\u8054\u60f3\u5230\u7684\u5c31\u662f\u5f20\u606a\u5bb6\u91cc\u7684\u89e6\u624b\u5df2\u7ecf\u4f38\u5230\u5efa\u90ba\u5e02\u91cc\u6765\u4e86\uff0c\u4e0d\u8fc7\u6ca1\u53ef\u80fd\u5e02\u91cc\u4f1a\u7ed9\u4e00\u540d\u666e\u901a\u9ad8\u6821\u751f\u6491\u8170\uff0c\u5bf9\u65b9\u53ef\u4ee5\u4e09\u661f\u96c6\u56e2\u674e\u5065\u7199\u5bb6\u65cf\u7684\u7ee7\u627f\u4eba\u4e4b\u4e00\uff0c\u4e5f\u4e09\u661f\u9a7b\u534e\u7684\u9ad8\u7ea7\u5b98\u5458\uff0c\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u6709\u534a\u5bfc\u4f53\u516c\u53f8\uff0c\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7269\uff0c\u5e02\u91cc\u4e0d\u53bb\u8d39\u529b\u7684\u5df4\u7ed3\u8ba8\u597d\uff0c\u90a3\u5c31\u8bf4\u660e\u4e00\u4e2a\u95ee\u9898\uff0c\u6709\u6bd4\u8fd9\u4e2a\u4eba\u7269\u66f4\u9700\u8981\u8ba8\u597d\u5df4\u7ed3\u7684\u5bf9\u8c61\u3002\u90fd\u8bf4\u5b98\u573a\u662f\u4e00\u5f20\u5bc6\u96c6\u7684\u7f51\uff0c\u5f20\u606a\u7684\u7236\u4eb2\u5f20\u77e5\u884c\u5728\u65b0\u829c\u521a\u63d0\u62d4\u5f53\u4e0a\u5e02\u59d4\u5e38\u59d4\uff0c\u4e0e\u5efa\u90ba\u5e02\u91cc\u6216\u8005\u7701\u91cc\u6709\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u90fd\u662f\u4ee4\u4eba\u592a\u610f\u5916\u7684\u4e8b\u60c5\u3002\u65e2\u7136\u80e1\u5b97\u5e86\u4ee3\u8868\u5e02\u91cc\u90fd\u8868\u6001\u4e86\uff0c\u5d14\u56fd\u6052\u3001\u5f20\u6653\u4e1c\u4ed6\u4eec\u5c31\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\uff0c\u53ea\u662f\u5f88\u5947\u602a\uff0c\u5f20\u77e5\u884c\u8bf7\u6258\u7684\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u7ed9\u6821\u65b9\u6253\u58f0\u62db\u547c\uff1f\u4e0d\u8fc7\u8fd9\u4e2a\u4e5f\u4e0d\u662f\u5f88\u91cd\u8981\u7684\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u6bd5\u7adf\u89c1\u8bc6\u6d45\uff0c\u770b\u95ee\u9898\u6ca1\u6709\u5d14\u56fd\u6052\u3001\u5f20\u6653\u4e1c\u8fd9\u4e48\u8001\u9053\uff0c\u53ea\u4ece\u80e1\u5b97\u5e86\u8bdd\u91cc\u542c\u51fa\u4e00\u4e9b\u6028\u6c14\u6765\uff0c\u5fc3\u91cc\u5c31\u53c8\u60ca\u53c8\u6015\uff0c\u5c31\u6015\u662f\u4ed6\u6700\u540e\u7ed9\u62ff\u51fa\u6765\u625b\u5305\u627f\u62c5\u8d23\u4efb\u3002\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5c06\u60f6\u6050\u8f6c\u79fb\u6210\u5bf9\u5f20\u606a\u7684\u6028\u6068\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u5f20\u606a\u7ea8\u7ed4\u5b50\u5f1f\u7684\u4e60\u6027\u5728\u60f9\u662f\u751f\u975e\u3001\u60f9\u51fa\u4e8b\u60c5\u5374\u53c8\u4e0d\u80af\u4f4e\u5934\u8ba4\u9519\u6240\u9020\u6210\u7684\u4e25\u91cd\u540e\u679c\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u60f3\u95ee\u9898\u66f4\u52a0\u5355\u7eaf\u4e86\uff0c\u5728\u5979\u770b\u6765\uff0c\u771f\u5c31\u662f\u4e00\u5207\u5f88\u666e\u901a\u7684\u5c0f\u7ea0\u7eb7\u800c\u5df2\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4e2d\u56fd\u4eba\u7ed9\u97e9\u56fd\u4eba\u9053\u6b49\uff1f\u5f20\u606a\u53c8\u6ca1\u6709\u505a\u9519\u4ec0\u4e48\uff0c\u7ed9\u8fd9\u51e0\u4e2a\u97e9\u56fd\u4eba\u8fd9\u4e48\u8fb1\u9a82\uff0c\u5c06\u624b\u91cc\u7aef\u7740\u7684\u8336\u6cfc\u51fa\u53bb\uff0c\u90a3\u4e0d\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u53cd\u5e94\u5417\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u53ef\u662f\u80e1\u5b97\u5e86\u5728\u51fa\u9762\u5e2e\u4f60\u89e3\u51b3\u7ea0\u7eb7\u554a\u2026\u2026\u201d\u53f6\u5efa\u658c\u542c\u8bf4\u662f\u80e1\u5b97\u5e86\u966a\u7740\u97e9\u56fd\u9a7b\u6caa\u603b\u9886\u4e8b\u5230\u6d3e\u51fa\u6240\uff0c\u671d\u5f20\u606a\u6324\u7709\u5f04\u773c\uff0c\u201c\u4e0d\u6653\u5f97\u8fd9\u5bb6\u4f19\u4f1a\u5728\u809a\u5b50\u600e\u4e48\u9a82\u4f60\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4ed6\u7684\u4efd\u5185\u804c\u8d23\uff0c\u53c8\u80fd\u6028\u5f97\u4e86\u8c01\uff1f\u201d\u5f20\u606a\u53ef\u4e0d\u540c\u60c5\u80e1\u5b97\u5e86\u7684\u5c34\u5c2c\u5904\u5883\uff0c\u60f3\u6765\u4ed6\u4e5f\u4e0d\u4f1a\u8fdb\u4fa6\u8baf\u5ba4\u6765\u770b\u671b\u81ea\u5df1\u7684\uff0c\u4e00\u8fb9\u8ddf\u53f6\u5efa\u658c\u6253\u5c94\u804a\u5929\uff0c\u4e00\u8fb9\u8ddf\u5b59\u9759\u6aac\u4e0b\u4e94\u5b50\u68cb\uff0c\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u7ad9\u5728\u5b59\u9759\u6aac\u540e\u9762\u5e2e\u7740\u5979\u53c2\u8c0b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u7b97\u662f\u8981\u76f4\u63a5\u8ddf\u4e09\u661f\u5bf9\u7740\u5e72\u4e86\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0e\u7f57\u4e66\u8bb0\u901a\u8fc7\u7535\u8bdd\uff0c\u8ba9\u5e02\u91cc\u6682\u65f6\u4e0d\u8981\u900f\u9732\u6211\u7684\u8eab\u4efd\uff0c\u8fd9\u79cd\u662f\u975e\u95ee\u9898\u4e0a\uff0c\u60f3\u5fc5\u80e1\u5b97\u5e86\u8fd8\u4e0d\u4f1a\u64c5\u81ea\u4e3b\u5f20\u7ed9\u522b\u4eba\u62ff\u4f4f\u75db\u811a\uff1b\u4e0d\u8fc7\u4e5f\u96be\u8bf4\uff0c\u4e8b\u60c5\u95f9\u8fd9\u4e48\u5927\uff0c\u5468\u747e\u73ba\u4ed6\u4eec\u662f\u6e05\u695a\u7684\uff0c\u90a3\u4e2a\u53eb\u91d1\u5357\u52c7\u7684\u7559\u5b66\u751f\uff0c\u6070\u5de7\u53c8\u662f\u5468\u747e\u73ba\u7684\u7559\u5b66\u751f\uff0c\u4e8b\u540e\u8c01\u77e5\u9053\u5468\u747e\u73ba\u4f1a\u4e0d\u4f1a\u900f\u9732\u6d88\u606f\u7ed9\u4ed6\u4eec\u77e5\u9053\uff1f\u201d\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff0c\u201c\u867d\u7136\u6ca1\u6709\u591a\u5927\u610f\u601d\uff0c\u6211\u8fd9\u65f6\u5019\u53ea\u662f\u60f3\u4ee5\u4e00\u540d\u666e\u901a\u5b66\u751f\u7684\u8eab\u4efd\u8ba9\u4ed6\u4eec\u4f4e\u5934\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7684\u786e\u6ca1\u591a\u5927\u7684\u610f\u601d\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u60f3\u60f3\u554a\uff0c\u4f60\u8981\u771f\u662f\u666e\u901a\u7684\u5b66\u751f\uff0c\u90a3\u5c31\u4e0d\u662f\u4ed6\u4eec\u97e9\u56fd\u4eba\u4f1a\u4e0d\u4f1a\u4e3b\u52a8\u4f4e\u5934\u7684\u95ee\u9898\u4e86\uff0c\u800c\u662f\u5e02\u91cc\u3001\u800c\u4e1c\u6d77\u5927\u5b66\u3001\u800c\u662f\u5f62\u5f62\u8272\u8272\u7684\u4eba\u90fd\u4f1a\u60f3\u65b9\u8bbe\u6cd5\u7684\u8feb\u4f7f\u4f60\u5411\u8fd9\u4e09\u4e2a\u97e9\u56fd\u4eba\u4f4e\u5934\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7684\u786e\u2026\u2026\u201d\u5f20\u606a\u82e6\u6da9\u4e86\u7684\u7b11\u4e86\u4e00\u58f0\uff0c\u867d\u7136\u4ed6\u786e\u5b9a\u80fd\u8feb\u4f7f\u674e\u5728\u6d19\u4ed6\u4eec\u4f4e\u5934\uff0c\u4f46\u53ef\u60dc\u5e76\u4e0d\u662f\u4ee5\u4e00\u540d\u666e\u901a\u9ad8\u6821\u5b66\u751f\u7684\u8eab\u4efd\uff0c\u800c\u662f\u5728\u7701\u91cc\u3001\u5728\u5e02\u91cc\u7684\u773c\u91cc\uff0c\u81ea\u5df1\u6bd4\u674e\u5728\u6d19\u66f4\u5f3a\u52bf\u3001\u66f4\u91cd\u8981\u7f62\u4e86\uff0c\u53c8\u671d\u53f6\u5efa\u658c\u62b1\u6028\uff0c\u201c\u4f60\u8bf4\u8fd9\u8bdd\u771f\u662f\u6ca1\u610f\u601d\uff0c\u597d\u5fc3\u60c5\u90fd\u641e\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u73b0\u5b9e\u4e9b\u5427\uff0c\u201d\u53f6\u5efa\u658c\u563f\u7136\u4e00\u7b11\uff0c\u201c\u4f60\u4e0d\u62c5\u5fc3\u4e09\u661f\u77e5\u9053\u4f60\u7684\u8eab\u4efd\u4e4b\u540e\uff0c\u4f1a\u7279\u522b\u9488\u5bf9\u9526\u6e56\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u70b9\u5012\u4e0d\u6015\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u201c\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u4eec\u4e0e\u79d1\u738b\u7684\u6069\u6028\u4e5f\u4e0d\u6d45\uff0c\u4e3a\u4ec0\u4e48\u6211\u6ca1\u6709\u7279\u522b\u53bb\u538b\u5236\u79d1\u738b\uff1f\u4e3a\u4ec0\u4e48\u8981\u7279\u522b\u7684\u538b\u5236\u79d1\u738b\u5462\uff1f\u9526\u6e56\u53ea\u8981\u7529\u5f00\u8180\u5b50\u8dd1\u5c06\u79d1\u738b\u8fdc\u8fdc\u7529\u5728\u540e\u9762\u5c31\u53ef\u4ee5\u4e86\u3002\u4e09\u661f\u771f\u8981\u4e8b\u4e8b\u9488\u5bf9\u9526\u6e56\uff0c\u4ed6\u4eec\u7684\u7ecf\u8425\u4e4b\u9053\u5c31\u4f1a\u7834\u7efd\u767e\u51fa\uff0c\u8fd8\u4f1a\u5f15\u8d77\u4e09\u661f\u7ade\u4e89\u5bf9\u624b\u5bf9\u9526\u6e56\u7684\u5173\u5fc3\u4e0e\u540c\u60c5\uff0c\u4e09\u661f\u53cd\u800c\u5c31\u5bb9\u6613\u5bf9\u4ed8\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8be5\u4e0d\u4f1a\u8981\u6545\u610f\u6fc0\u6012\u4e09\u661f\u7684\u4eba\u5427\uff1f\u201d\u53f6\u5efa\u658c\u604d\u7136\u5927\u609f\uff0c\u8be1\u5f02\u7684\u7b11\u9053\uff0c\u201c\u4f60\u5c0f\u5b50\u5fc3\u773c\u771f\u591f\u9634\u9669\u7684\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5750\u5728\u684c\u5b50\u5bf9\u9762\u62ac\u5934\u770b\u4e86\u53f6\u5efa\u658c\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u89c1\u8fc7\u6bd4\u8fd9\u5c0f\u5b50\u66f4\u9634\u9669\u7684\u4eba\u4e86\uff01\u4f60\u8bf4\u4ed6\u80fd\u4e0d\u9634\u9669\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0b\u4f60\u7684\u68cb\uff0c\u201d\u5f20\u606a\u6307\u4e86\u6307\u68cb\u76d8\uff0c\u53c8\u8f6c\u8fc7\u6765\u8ddf\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u4e5f\u4e0d\u662f\uff0c\u6362\u4f5c\u5176\u4ed6\u97e9\u56fd\u68d2\u69cc\u8bf4\u90a3\u6837\u7684\u8bdd\uff0c\u90a3\u7897\u70ed\u8336\uff0c\u6211\u4e5f\u662f\u4f1a\u6beb\u4e0d\u72b9\u8c6b\u6cfc\u51fa\u53bb\u7684\u2026\u2026\u4e0d\u8fc7\u674e\u5728\u6d19\u4ee5\u79c1\u4eba\u7684\u8eab\u4efd\u5230\u5efa\u90ba\u6765\uff0c\u603b\u4e0d\u80fd\u53ea\u662f\u4e3a\u4e86\u5bfb\u4eb2\u8bbf\u53cb\uff0c\u4e09\u661f\u96c6\u56e2\u4e0d\u662f\u8ba1\u5212\u5728\u534e\u6295\u8d44\u5efa\u4e00\u5ea7\u5927\u578b\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u5417\uff1f\u6211\u6000\u7591\u4ed6\u5230\u5efa\u90ba\u662f\u8fdb\u884c\u521d\u6b65\u7684\u8003\u5bdf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u6709\u53ef\u80fd\uff0c\u9526\u6e56\u641e\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u52a8\u9759\u8fd9\u4e48\u5927\uff0c\u60f3\u4e0d\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u90fd\u96be\u3002\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u8ba9\u4e09\u661f\u5c06\u4ed6\u4eec\u7684\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u8bbe\u5728\u5efa\u90ba\uff0c\u6709\u4ec0\u4e48\u7279\u522b\u7684\u574f\u5904\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e09\u661f\u96c6\u56e2\u5728\u5317\u4eac\u5df2\u7ecf\u5efa\u6709\u4e00\u5ea7\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\uff0c\u4ed6\u4eec\u7b79\u5efa\u7814\u53d1\u4e2d\u5fc3\uff0c\u662f\u4e3a\u4e09\u661f\u5236\u9020\u57fa\u5730\u5927\u89c4\u6a21\u8f6c\u79fb\u5230\u56fd\u5185\u6765\u505a\u51c6\u5907\u7684\uff0c\u662f\u4e3a\u4f7f\u4ed6\u4eec\u7684\u4ea7\u54c1\u6700\u5feb\u7684\u9002\u5e94\u56fd\u5185\u7684\u5e02\u573a\uff0c\u5e76\u4e0d\u662f\u8981\u5728\u5efa\u90ba\u53d1\u5c55\u4ec0\u4e48\u6838\u5fc3\u6280\u672f\u2026\u2026\u4e0d\u53d1\u5c55\u6838\u5fc3\u6280\u672f\u4e5f\u5c31\u7b97\u4e86\uff0c\u4ed6\u4eec\u5bf9\u5f85\u56fd\u5185\u6280\u672f\u4eba\u5458\u4e0e\u7814\u53d1\u4eba\u5458\u7684\u57f9\u517b\u4e0e\u85aa\u916c\u653f\u7b56\u53c8\u662f\u5438\u8840\u6027\u8d28\u7684\uff0c\u8fd9\u6837\u7684\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\uff0c\u5bf9\u5efa\u90ba\u805a\u96c6\u4eba\u529b\u3001\u6280\u672f\u8d44\u6e90\u7684\u4f5c\u7528\u4e0d\u5927\uff0c\u5374\u53c8\u6765\u8ddf\u6a61\u6811\u56ed\u4e89\u8d44\u6e90\uff0c\u6709\u673a\u4f1a\u4e0d\u5f04\u4ed6\u4eec\u4e00\u4e0b\uff0c\u6211\u672a\u514d\u4e5f\u592a\u8001\u5b9e\u539a\u9053\u4e86\u2026\u2026\u6211\u5c31\u5e0c\u671b\u674e\u5728\u6d19\u90a3\u5c0f\u5b50\u770b\u5230\u5efa\u90ba\u5c31\u751f\u6068\u610f\uff0c\u5c31\u7ed5\u7740\u8d70\u3002\u201d</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5faa\u7740\u5386\u53f2\u7684\u8f68\u8ff9\u524d\u8fdb\uff0c\u4e09\u661f\u4f1a\u5728\u4e5d\u516b\u5e74\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u8bbe\u4ed6\u4eec\u5728\u534e\u6700\u5927\u7684\u4ea7\u54c1\u7814\u53d1\u673a\u6784\u3002\u8fd9\u4e00\u4e16\uff0c\u5386\u53f2\u7684\u8f68\u8ff9\u867d\u7136\u53d1\u751f\u6539\u53d8\uff0c\u5e76\u6ca1\u6709\u4e25\u683c\u9075\u5faa\u65e7\u8ff9\uff0c\u4f46\u662f\u53ea\u8981\u4e09\u661f\u5bf9\u534e\u5e02\u573a\u6218\u7565\u601d\u60f3\u4e0d\u53d8\uff0c\u4ed6\u4eec\u5230\u5efa\u90ba\u5e02\u7b79\u5efa\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u7684\u8ba1\u5212\u53cd\u800c\u6781\u6709\u53ef\u80fd\u4f1a\u63d0\u524d\u5b9e\u65bd\uff0c\u800c\u4e0d\u5927\u53ef\u80fd\u53d6\u6d88\u8fd9\u9879\u8ba1\u5212\uff1b\u5f20\u606a\u5e0c\u671b\u80fd\u6405\u4e00\u6405\u5c40\u3002</p><p>\u3000\u3000\u201c\u8c01\u6653\u5f97\u5462\uff1f\u4e09\u661f\u9a7b\u534e\u7684\u4eba\u5458\u5e76\u975e\u65e0\u80fd\u4e4b\u8f88\uff0c\u518d\u8bf4\u674e\u5728\u6d19\u8fd9\u4e2a\u4eba\u8fd8\u4e0d\u80fd\u6700\u540e\u51b3\u5b9a\u4e09\u661f\u5728\u534e\u7684\u8fd0\u8425\u7b56\u7565\u3002\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p></article>"}}}]);
