(window.webpackJsonp=window.webpackJsonp||[]).push([[678],{701:function(p){p.exports={chapterTitle:null,title:"\u7b2c677\u7ae0 \u60ca\u4eba\u7684\u51b3\u5b9a",content:"<article><h2>\u7b2c677\u7ae0 \u60ca\u4eba\u7684\u51b3\u5b9a</h2><p>\u3000\u3000\u5916\u4eba\u5f88\u96be\u7406\u89e3\u80cc\u540e\u7a76\u7adf\u85cf\u7740\u600e\u6837\u7684\u4e89\u6267\uff0c\u5916\u754c\u7684\u8d28\u7591\u4e0d\u65ad\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u5374\u4e00\u76f4\u4fdd\u6301\u6c89\u9ed8\uff0c\u6216\u8bb8\u5728\u89c2\u5bdf\u5916\u754c\u7684\u53cd\u5e94\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u4e3e\u68cb\u4e0d\u5b9a\u3001\u5185\u90e8\u5206\u6b67\u8f83\u5927\u65f6\uff0c\u8fd9\u65e0\u7591\u662f\u6700\u4f73\u7684\u59ff\u6001\u3002</p><p>\u3000\u30006\u65e5\u4e0b\u5348\uff0c\u4f4d\u4e8e\u6d77\u6dc0\u533a\u4e2d\u5173\u6751\u7231\u8fbe\u5927\u53a6\u5185\uff0c\u7231\u8fbe\u96c6\u56e2\u5728\u5176\u9a7b\u4eac\u5206\u516c\u53f8\u5185\u53ec\u5f00\u8bb0\u8005\u4f1a\u8868\u793a\u6e05\u9192\u8ba4\u77e5\u516c\u53f8\u7684\u53d1\u5c55\u73b0\u72b6\u4e0e\u6d77\u5916\u5382\u5546\u4e4b\u95f4\u7684\u5dee\u8ddd\u5c24\u4e3a\u91cd\u8981\uff0c\u516c\u53f8\u90fd\u4e0d\u9700\u8981\u6fc0\u8fdb\u800c\u60d8\u987e\u540e\u679c\u7684\u5e02\u573a\u7b56\u7565\uff0c\u4e0d\u613f\u610f\u6210\u4e3a\u966a\u886c\u5916\u8d44\u54c1\u724c\u7684\u7eff\u53f6\uff0c\u6709\u5916\u8d44\u54c1\u724c\u53c2\u52a0\u7ade\u6807\u7684\u5e7f\u544a\u65f6\u6bb5\uff0c\u6709\u53ef\u80fd\u4f1a\u76f4\u63a5\u9009\u62e9\u653e\u5f03\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u80fd\u6e05\u695a\u7684\u770b\u5230\u7231\u8fbe\u96c6\u56e2\u7684\u77db\u5934\u6240\u6307\uff0c\u53ea\u662f\u4e4b\u524d\u6ca1\u6709\u9884\u6599\u5230\u4f1a\u6709\u4e00\u5bb6\u4f01\u4e1a\u516c\u5f00\u7ad9\u51fa\u6765\u62b5\u5236\u4e2d\u592e\u7535\u89c6\u53f0\u63a5\u53d7\u5916\u8d44\u54c1\u724c\u5165\u573a\uff0c\u7231\u8fbe\u96c6\u56e2\u53ec\u5f00\u8fd9\u6837\u7684\u8bb0\u8005\u4f1a\u4ee4\u5a92\u4f53\u76f8\u5f53\u60ca\u8c14\u3002</p><p>\u3000\u3000\u60f3\u60f3\u4e5f\u4e0d\u5947\u602a\uff0c\u56f4\u7ed5\u7740\u592e\u89c6\u7b2c\u4e00\u5957\u8282\u76ee\u9ec4\u91d1\u5e7f\u544a\u65f6\u4ee3\u7684\u7ade\u6807\uff0c\u4e0d\u4ec5\u662f\u7ade\u6807\u4f01\u4e1a\u4e4b\u95f4\u7684\u7ade\u4e89\uff0c\u66f4\u662f\u7ade\u6807\u4f01\u4e1a\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u4e4b\u95f4\u7684\u535a\u5f08\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u9009\u62e9\u8fd9\u6837\u7684\u65f6\u673a\u53d1\u8868\u8fd9\u6837\u7684\u58f0\u660e\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u65e0\u7591\u4ece\u4fa7\u9762\u8868\u660e\u7231\u8fbe\u96c6\u56e2\u5bf9\u8fd9\u5c4a\u7684\u592e\u89c6\u6807\u738b\u6709\u7740\u6df1\u539a\u7684\u5174\u8da3\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u6070\u597d\u8fd9\u6b21\u592e\u89c6\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u6700\u6709\u529b\u7684\u7ade\u593a\u8005\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u5f53\u7136\u5e0c\u671b\u7ade\u6807\u7684\u573a\u9762\u8d8a\u70ed\u70c8\u8d8a\u597d\uff0c\u5e0c\u671b\u6709\u66f4\u591a\u7684\u3001\u66f4\u6709\u5b9e\u529b\u7684\u7ade\u6807\u4f01\u4e1a\u53c2\u4e0e\u8fdb\u6765\uff1b\u7231\u8fbe\u96c6\u56e2\u5374\u4e0d\u5e0c\u671b\u6700\u7ec8\u4e3a\u592e\u89c6\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u652f\u4ed8\u8fc7\u9ad8\u7684\u4ee3\u4ef7\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u6709\u8fdd\u5e38\u4f8b\u5728\u5341\u4e00\u6708\u4e4b\u524d\u5c31\u63d0\u524d\u516c\u5e03\u7b2c\u4e09\u5b63\u5ea6\u7684\u8d22\u52a1\u6570\u636e\uff0c\u968f\u540e\u591a\u5bb6\u5a92\u4f53\u53c8\u6b65\u8c03\u4e00\u81f4\u7684\u629b\u51fa\u5bf9\u4e0a\u5c4a\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u79d1\u738b\u7535\u5668\u4eca\u5e74\u6709\u53ef\u80fd\u5de8\u989d\u4e8f\u635f\u7684\u5e02\u573a\u5206\u6790\u62a5\u544a\uff0c\u80cc\u540e\u7684\u8054\u7cfb\u4ee5\u53ca\u77db\u5934\u6240\u6307\uff0c\u90fd\u662f\u4e0d\u8a00\u81ea\u660e\u7684\u3002\u7231\u8fbe\u96c6\u56e2\u5c31\u662f\u8981\u5728\u8fd9\u4e2a\u5173\u952e\u65f6\u523b\uff0c\u4fc3\u8fdb\u5a92\u4f53\u4e0e\u516c\u4f17\u5bf9\u592e\u89c6\u5e7f\u544a\u6807\u738b\u7684\u6982\u5ff5\u8fdb\u884c\u53cd\u7701\uff0c\u6253\u538b\u5176\u4ed6\u6f5c\u5728\u7684\u7ade\u6807\u4f01\u4e1a\u5bf9\u592e\u89c6\u6807\u738b\u7684\u671f\u5f85\u503c\u3002</p><p>\u3000\u3000\u7231\u8fbe\u653e\u5f03\u5bf9\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u7684\u7ade\u6807\uff0c\u8fd9\u672c\u8eab\u4e0d\u4f1a\u4ea7\u751f\u591a\u5927\u7684\u95ee\u9898\uff1b\u8981\u662f\u8fd9\u5c4a\u592e\u89c6\u5e7f\u544a\u6807\u738b\u7ed9\u5916\u8d44\u54c1\u724c\u7ade\u5f97\uff0c\u4f1a\u9020\u6210\u600e\u6837\u7684\u5f71\u54cd\uff0c\u5374\u662f\u8c01\u4e5f\u65e0\u6cd5\u9884\u6599\u7684\u3002</p><p>\u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u5317\u4eac\u7535\u89c6\u53f0\u7ec4\u7ec7\u4eba\u624b\u8d70\u5230\u8857\u5934\u8fdb\u884c\u201c\u5f53\u5916\u8d44\u54c1\u724c\u6210\u4e3a\u592e\u89c6\u6807\u738b\u201d\u7684\u73b0\u573a\u8c03\u67e5\uff0c\u5f55\u5236\u7684\u8282\u76ee\u5e76\u6ca1\u6709\u5728\u5317\u4eac\u7535\u89c6\u53f0\u4e8c\u5957\u4e03\u70b9\u534a\u949f\u7684\u65b0\u95fb\u8c03\u67e5\u680f\u76ee\u91cc\u64ad\u51fa\uff0c\u4f46\u662f\u5728\u6b64\u4e4b\u524d\uff0c\u9648\u9759\u5728\u79d1\u738b\u9a7b\u4eac\u529e\u4e8b\u5904\u91cc\u63a5\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u7535\u8bdd\u901a\u77e5\uff0c\u9080\u8bf7\u5979\u4e0e\u5176\u4ed6\u53c2\u4e0e\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u4f01\u4e1a\u4ee3\u8868\u5230\u6885\u5730\u4e9a\u4e2d\u5fc3\u53c2\u52a0\u7ade\u6807\u65b9\u6848\u7684\u5ea7\u8c08\u4f1a\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0e\u5b50\u5609\u8d76\u5230\u592e\u89c6\u7684\u6885\u5730\u4e9a\u4e2d\u5fc3\uff0c\u5728\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u7684\u5e26\u9886\u4e0b\uff0c\u8d70\u5230\u5927\u697c\u4e09\u697c\u7684\u4f1a\u5ba2\u5927\u5385\uff0c\u770b\u5230\u5085\u4fca\u5750\u5728\u5927\u5385\u4e00\u89d2\u7684\u6c99\u53d1\u4e0a\uff0c\u624d\u6653\u5f97\u5f20\u606a\u4e0e\u7231\u8fbe\u96c6\u56e2\u8fd0\u8425\u90e8\u4e0b\u5c5e\u7684\u5e02\u573a\u603b\u76d1\u53ca\u9500\u552e\u516c\u53f8\u603b\u88c1\u6768\u4e91\u4ee5\u53ca\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u5468\u4e00\u5e73\u65e9\u5728\u4ed6\u4eec\u53ec\u5f00\u8bb0\u8005\u4f1a\u521a\u7ed3\u675f\u4e4b\u65f6\u5c31\u7ed9\u8bf7\u5230\u6885\u5730\u4e9a\u4e2d\u5fc3\u6765\uff0c\u8fd9\u65f6\u5019\u4ed6\u4eec\u6b63\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u9886\u5bfc\u8fdb\u884c\u78cb\u5546\u3002</p><p>\u3000\u3000\u53d7\u9080\u8fc7\u6765\u53c2\u52a0\u5ea7\u8c08\u4f1a\u7684\u4e3b\u8981\u662f\u53bb\u5e74\u7ade\u6807\u65b0\u64ad\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u680f\u76ee\u4e4b\u95f4\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u7684\u4f01\u4e1a\u4ee3\u8868\u3002\u56fd\u5185\u51e0\u4e4e\u6240\u6709\u7684\u5730\u65b9\u7535\u89c6\u53f0\u90fd\u540c\u6b65\u8f6c\u64ad\u4e2d\u592e\u4e00\u5957\u7684\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u8282\u76ee\uff0c\u56e0\u800c\u8fd9\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u7684\u5e02\u573a\u4ef7\u503c\u6700\u9ad8\u3002</p><p>\u3000\u3000\u9664\u6b64\u8fd9\u4e9b\u4f01\u4e1a\u4ee3\u8868\u4e4b\u5916\uff0c\u9648\u9759\u8fd8\u770b\u5230\u8054\u4fe1\u96c6\u56e2\u7684\u8096\u745e\u6c11\u3002\u8054\u4fe1\u96c6\u56e2\u4e5f\u8981\u52a0\u5f3a\u660e\u5e74\u7684\u5e02\u573a\u5ba3\u4f20\u529b\u5ea6\uff0c\u505a\u5927\u4ed6\u4eec\u7684\u624b\u673a\u4e1a\u52a1\u3002\u4e1c\u5174\u3001\u8054\u8baf\u4e5f\u4e0d\u4f1a\u9519\u8fc7\u592e\u89c6\u7684\u8fd9\u6b21\u62db\u5546\u5927\u4f1a\uff0c\u53ea\u662f\u9648\u9759\u5bf9\u4e1c\u5174\u3001\u8054\u8baf\u7684\u7ba1\u7406\u5c42\u4e0d\u5927\u719f\u6089\uff0c\u4e5f\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u4ed6\u4eec\u7684\u4ee3\u8868\u5728\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u9886\u5bfc\u8c08\u5b8c\u8bdd\uff0c\u4e0e\u6768\u4e91\u3001\u5468\u4e00\u5e73\u8fd4\u56de\u4f1a\u5ba2\u5927\u5385\uff0c\u770b\u5230\u9648\u9759\u4e0e\u8c22\u5b50\u5609\uff0c\u8d70\u8fc7\u53bb\u8ddf\u5979\u4eec\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u624d\u7b97\u660e\u767d\u4f60\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u4f60\u4eec\u8fd9\u6b21\u53ef\u771f\u662f\u9009\u62e9\u4e2d\u592e\u7535\u89c6\u53f0\u4f5c\u5bf9\u624b\u5462\u2026\u2026\u201d\u7ad9\u5728\u6885\u5730\u4e9a\u4e2d\u592e\u5bbd\u655e\u660e\u4eae\u7684\u5927\u5385\u91cc\uff0c\u9648\u9759\u51dd\u7738\u770b\u7740\u773c\u524d\u8fd9\u4e2a\u5e74\u5c11\u5f97\u5fd7\u3001\u610f\u6c14\u98ce\u53d1\u7684\u9752\u5e74\uff0c\u660e\u7738\u7693\u9f7f\u7684\u7b11\u7740\u8bf4\uff0c\u201c\u5bf9\u592e\u89c6\u7684\u6001\u5ea6\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u5f3a\u786c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u7528\u4e86\u2018\u6697\u6e21\u9648\u4ed3\u2019\u8fd9\u79cd\u8001\u5957\u7684\u8ba1\u8c0b\u2026\u2026\u201d\u8c22\u5b50\u5609\u561f\u7740\u5634\u8f7b\u8bed\u8bf4\u9053\u3002\u5979\u4e0e\u9648\u9759\u5bf9\u9526\u6e56\u53ef\u4ee5\u8bf4\u662f\u77e5\u6839\u77e5\u5e95\uff0c\u5f53\u7136\u90fd\u5f88\u6e05\u695a\u8fd9\u6b21\u5f88\u53ef\u80fd\u662f\u9999\u96ea\u6d77\u7ad9\u51fa\u6765\u7ade\u593a\u592e\u89c6\u6807\u738b\u5e7f\u544a\uff0c\u504f\u504f\u662f\u7231\u8fbe\u96c6\u56e2\u8fd9\u65f6\u5019\u8df3\u51fa\u6765\u5438\u5f15\u5a92\u4f53\u4e0e\u5176\u4ed6\u7ade\u6807\u4f01\u4e1a\u7684\u773c\u7403\uff0c\u53ef\u4e0d\u6b63\u662f\u201c\u660e\u4fee\u6808\u9053\u3001\u6697\u6e21\u9648\u4ed3\u201d\u7684\u8ba1\u8c0b\uff1f\u5176\u4ed6\u53c2\u4e0e\u7ade\u6807\u7684\u4f01\u4e1a\u53c8\u4e0d\u77e5\u9053\u7231\u8fbe\u96c6\u56e2\u4e0e\u9999\u96ea\u6d77\u7684\u6839\u5e95\u3002</p><p>\u3000\u3000\u867d\u7136\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u96c6\u56e2\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u4f46\u662f\u4e24\u5bb6\u4f01\u4e1a\u6839\u636e\u5176\u5e02\u573a\u89c4\u6a21\u53ca\u8d44\u91d1\u5b9e\u529b\uff0c\u5bf9\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u7684\u7ade\u4e89\u80fd\u529b\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u5f20\u606a\u603b\u4e0d\u80fd\u5c06\u7231\u8fbe\u96c6\u56e2\u7684\u8d44\u91d1\u65e0\u507f\u7684\u62e8\u4ed8\u7ed9\u9999\u96ea\u6d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7406\u4f1a\u8c22\u5b50\u5609\u7684\u8ba5\u8bbd\uff0c\u65e0\u8bba\u662f\u7231\u8fbe\u8fd8\u662f\u9999\u96ea\u6d77\uff0c\u90fd\u7edf\u4e00\u5728\u9526\u6e56\u7684\u6574\u4f53\u6218\u7565\u4e4b\u4e2d\uff0c\u7b11\u7740\u56de\u7b54\u9648\u9759\u7684\u7591\u60d1\uff1a\u201c\u6001\u5ea6\u600e\u6837\u624d\u7b97\u4e0d\u5f3a\u786c\uff1f\u592e\u89c6\u4e3a\u6807\u738b\u5e7f\u544a\u6807\u4ef7\u5341\u4e2a\u4ebf\uff0c\u6211\u4eec\u4e56\u4e56\u7684\u5168\u989d\u652f\u4ed8\uff1f\u201d\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u8fde\u7d2f\u4f60\u4eec\u4e5f\u7ed9\u6405\u548c\u8fdb\u6765\u53c2\u52a0\u8fd9\u4ec0\u4e48\u5ea7\u8c08\u4f1a\uff0c\u4e0d\u4f1a\u53c8\u8981\u6211\u627f\u62c5\u4ec0\u4e48\u8d23\u4efb\u5427\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u8138\u6709\u4e9b\u5fae\u7ea2\uff0c\u4e4b\u524d\u8bf4\u627f\u62c5\u8d23\u4efb\u7684\u8bdd\uff0c\u53ea\u662f\u5b50\u5609\u7684\u5201\u86ee\u4efb\u6027\uff0c\u6362\u4e86\u4e2a\u8bdd\u9898\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8c01\u7559\u4e0b\u6765\u53c2\u52a0\u5ea7\u8c08\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5468\u4e00\u5e73\u3001\u6768\u4e91\u5c31\u7ad9\u5728\u4e0d\u8fdc\u5904\uff0c\u5476\u4e86\u5476\u5634\u8bf4\u9053\uff1a\u201c\u8bfa\uff0c\u4ed6\u4eec\u4fe9\u7559\u4e0b\u6765\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u53c8\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u7684\u4efb\u6027\uff0c\u6bd5\u7adf\u7ed9\u4f60\u4eec\u6dfb\u4e86\u4e9b\u9ebb\u70e6\uff0c\u8fd9\u6837\u5427\uff0c\u9526\u6e56\u4ece\u5fb7\u4eea\u6240\u83b7\u5f97\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u90fd\u53ef\u4ee5\u6388\u6743\u79d1\u738b\u9ad8\u79d1\u514d\u8d39\u4f7f\u7528\uff0c\u7b97\u662f\u4e00\u70b9\u8865\u507f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u9648\u9759\u6123\u4e86\u6123\uff0c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u9526\u6e56\u5f53\u521d\u53ef\u662f\u82b1\u8d39\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\u4ece\u5fb7\u4eea\u624b\u4e2d\u63a5\u53d7\u5168\u5957\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u8fd9\u5f53\u4e2d\u6ca1\u6709\u6700\u6838\u5fc3\u7684\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\uff0c\u4f46\u662f\u9664\u4e86\u57fa\u5e26\u82af\u7247\u6280\u672f\u4e4b\u5916\uff0c\u5176\u4ed6\u7ec4\u914d\u4ef6\u6280\u672f\u53ef\u4ee5\u8bf4\u662f\u5e94\u6709\u5c3d\u6709\uff0c\u8fde\u9526\u6e56\u4e00\u65f6\u95f4\u4e5f\u65e0\u6cd5\u6d88\u5316\u591a\u5c11\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u8bf4\u8d70\u4e86\uff0c\u9648\u9759\u5374\u8fd8\u6ca1\u6709\u4ece\u521a\u624d\u7684\u9547\u60ca\u4e2d\u56de\u8fc7\u795e\u6765\uff0c\u8c22\u5b50\u5609\u4f38\u624b\u5728\u5979\u9762\u524d\u62db\u4e86\u62db\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u786e\u5b9a\u6ca1\u6709\u4ed6\u521a\u624d\u4e0d\u662f\u5728\u5f00\u73a9\u7b11\uff0c\u8fd8\u662f\u8bf4\u4f60\u5df2\u7ecf\u7ed9\u4ed6\u9a97\u4e0a\u5e8a\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8ddf\u4ec0\u4e48\u554a\uff1f\u201d\u9648\u9759\u5c06\u5b50\u5609\u7684\u624b\u62e8\u5f00\uff0c\u4fcf\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u5b50\u5609\u7684\u8bdd\u5012\u662f\u8bf4\u4e2d\u5979\u85cf\u5728\u5fc3\u91cc\u7684\u9690\u79d8\uff0c\u8fd8\u60f3\u7740\u5c06\u90a3\u4e2a\u68a6\u7ed9\u5f7b\u5e95\u5fd8\u6389\u3002\u4f46\u662f\u5979\u4ecd\u7136\u65e0\u6cd5\u7406\u89e3\u5f20\u606a\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u6216\u8005\u8bf4\u662f\u5f20\u606a\u521a\u624d\u7684\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u9526\u6e56\u8981\u516c\u5e03\u7684\u4e0d\u662f\u7b80\u7b80\u5355\u5355\u7684\u4e00\u6b3e\u624b\u673a\u65b9\u6848\uff0c\u800c\u662f\u5fb7\u5dde\u4eea\u5668\u79ef\u7d2f\u4e86\u8fd1\u5341\u5e74\u3001\u9526\u6e56\u82b1\u8d39\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\u4ece\u5fb7\u4eea\u624b\u4e2d\u63a5\u53d7\u7684\u4e13\u5229\u6280\u672f\u3002\u867d\u7136\u7ed9\u4e1a\u5185\u8d2c\u4f4e\u7684\u4e8c\u6d41\u6280\u672f\uff0c\u867d\u7136\u53ea\u662f\u514d\u8d39\u6388\u6743\u4f7f\u7528\uff0c\u4f46\u662f\u65e0\u8bba\u6b64\u65f6\u7684\u79d1\u738b\u9ad8\u79d1\u5982\u4f55\u52aa\u529b\uff0c\u90fd\u4e0d\u53ef\u80fd\u4ece\u5176\u4ed6\u6d77\u5916\u624b\u673a\u5382\u5546\u624b\u91cc\u83b7\u5f97\u8fd9\u79cd\u6388\u6743\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u79bb\u5f00\u4f1a\u5ba2\u5927\u5385\u65f6\uff0c\u5f20\u606a\u4e0e\u8096\u745e\u6c11\u5bf9\u770b\u4e86\u4e00\u773c\u3002</p><p>\u3000\u3000\u867d\u7136\u7231\u8fbe\u4e0e\u8054\u4fe1\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u6709\u7ade\u4e89\uff0c\u4f46\u662f\u5728\u5916\u8d44\u54c1\u724c\u8fdb\u5165\u56fd\u5185\u5e02\u573a\u7684\u95ee\u9898\u4e0a\uff0c\u8054\u4fe1\u53c8\u4f1a\u88ab\u8feb\u9009\u62e9\u4e0e\u7231\u8fbe\u7ad9\u5728\u540c\u4e00\u4e2a\u7acb\u573a\u4e0a\u3002</p><p>\u3000\u3000\u76f4\u5230\u770b\u7740\u5f20\u606a\u7684\u80cc\u666f\u4ece\u4f1a\u5ba2\u5927\u5385\u7684\u5165\u53e3\u6276\u68af\u7f13\u7f13\u4e0b\u6c89\u6d88\u5931\u4e0d\u89c1\uff0c\u8096\u745e\u6c11\u90fd\u82e5\u6709\u6240\u601d\u7684\u624b\u629a\u7740\u989d\u5934\uff0c\u6ca1\u6709\u8d70\u4e0a\u524d\u642d\u8bdd\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u591a\u6708\uff0c\u5728\u9999\u6e2f\u673a\u573a\u627f\u8499\u5f20\u606a\u8d60\u9001\u7684\u90a3\u53ea\u7231\u8fbei19\u624b\u673a\u8fd8\u8eba\u5728\u4ed6\u7684\u516c\u6587\u5305\u91cc\u3002\u867d\u7136\u8054\u4fe1\u8fd8\u6ca1\u6709\u80fd\u529b\u5b9e\u65bd\u50cf\u6a61\u6811\u56ed\u90a3\u6837\u7684\u5e9e\u5927\u6784\u60f3\uff0c\u4f46\u662f\u8096\u745e\u6c11\u4e00\u5f00\u59cb\u5c31\u60f3\u7740\u5148\u5728\u8054\u4fe1\u96c6\u56e2\u65d7\u4e0b\u6210\u7acb\u4e00\u4e2a\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\u4e0e\u5e94\u7528\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u6536\u5230\u5f20\u606a\u7684\u8d60\u793c\uff0c\u4ed6\u7684\u8fd9\u79cd\u5fc3\u601d\u6108\u53d1\u7684\u575a\u5b9a\u3002</p><p>\u3000\u3000\u9664\u4e86\u4ece\u7b2c\u5341\u4e8c\u7814\u7a76\u9662\u62bd\u8c03\u6280\u672f\u529b\u91cf\u4e4b\u5916\uff0c\u8096\u745e\u6c11\u66f4\u5e0c\u671b\u4ece\u5916\u90e8\u76f4\u63a5\u9ad8\u85aa\u8058\u8bf7\u5728\u624b\u673a\u8bbe\u8ba1\u4e0e\u624b\u673a\u5e94\u7528\u6280\u672f\u5f00\u53d1\u4e0a\u6709\u4e30\u5bcc\u7ecf\u9a8c\u7684\u4e13\u4e1a\u4eba\u58eb\u3002\u4f46\u662f\u4e8b\u60c5\u7684\u53d1\u5c55\u5e76\u4e0d\u603b\u662f\u5982\u4eba\u610f\u7684\uff0c\u6ca1\u6709\u5f3a\u529b\u7684\u652f\u6491\uff0c\u56fd\u5185\u7684\u8fd0\u8425\u65b9\u6848\u901a\u5e38\u662f\u591a\u65b9\u9762\u610f\u89c1\u7684\u6298\u4e2d\u3002\u79fb\u52a8\u901a\u8baf\u7ec8\u7aef\u8bbe\u5907\u8bbe\u8ba1\u4e2d\u5fc3\u4e0e\u5e94\u7528\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u603b\u7b97\u662f\u5f88\u5feb\u5c31\u6302\u724c\u4e86\uff0c\u4f46\u662f\u90e8\u91cc\u901a\u8fc7\u7684\u65b9\u6848\uff0c\u5411\u5916\u90e8\u76f4\u63a5\u9ad8\u85aa\u8058\u8bf7\u4e13\u4e1a\u4eba\u58eb\u53ea\u96505\u4eba\uff0c\u54ac\u54ac\u7259\u5f00\u51fa\u4e94\u5341\u4e07\u7684\u603b\u5e74\u85aa\u3002\u4e0e\u5176\u4ece\u5916\u90e8\u62db\u8058\u534a\u751f\u4e0d\u719f\u7684\u534a\u4e13\u4e1a\u4eba\u58eb\uff0c\u8fd8\u4e0d\u5982\u81ea\u5df1\u82b1\u8d39\u529b\u6c14\u57f9\u517b\uff0c\u8096\u745e\u6c11\u8ddf\u90e8\u91cc\u4e89\u53d6\u7684\u6761\u4ef6\u5c31\u662f\u82b1\u56db\u5341\u4e07\u8058\u4e00\u540d\u771f\u6b63\u7684\u4e13\u5bb6\u6765\u9886\u5bfc\u4e24\u4e2a\u4e2d\u5fc3\u7684\u5de5\u4f5c\uff0c\u518d\u82b1\u5341\u4e07\u8058\u8bf7\u56db\u540d\u521a\u6bd5\u4e1a\u7684\u5927\u5b66\u751f\uff0c\u603b\u5e74\u85aa\u4ecd\u7136\u63a7\u5236\u5728\u4e94\u5341\u4e07\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u5357\u65b9\u516c\u53f8\u62e5\u6709\u4ee3\u5de5\u5305\u9500\u6388\u6743\u7684\u6743\u529b\uff0c\u4e5f\u662f\u59a5\u534f\u7684\u7ed3\u679c\uff0c\u8096\u745e\u6c11\u4e5f\u9887\u4e3a\u65e0\u5948\uff0c\u5468\u5174\u4e1c\u5728\u90e8\u91cc\u83b7\u5f97\u90a3\u4e9b\u8001\u9886\u5bfc\u7684\u652f\u6301\uff0c\u4e5f\u4e0d\u662f\u4ed6\u80fd\u5b8c\u5168\u538b\u5236\u4f4f\u7684\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u89c6\u7ebf\u770b\u7740\u9648\u9759\u90a3\u8fb9\uff0c\u4ed6\u4e0d\u8ba4\u8bc6\u9648\u9759\uff0c\u6bd5\u7adf\u79d1\u738b\u9ad8\u79d1\u76ee\u524d\u53ea\u8ddf\u8054\u4fe1\u7684\u5357\u65b9\u516c\u53f8\u6709\u8054\u7edc\uff0c\u4f46\u662f\u8fd9\u4e48\u4e00\u4e2a\u6f02\u4eae\u7684\u5973\u4eba\uff0c\u5e94\u9080\u51fa\u73b0\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u4e09\u697c\u7684\u4f1a\u5ba2\u5927\u697c\uff0c\u8fd8\u4e0e\u9526\u6e56\u5e55\u540e\u7684\u90a3\u4e2a\u4eba\u7269\u6709\u8fc7\u7b80\u77ed\u800c\u4eb2\u5bc6\u7684\u4ea4\u6d41\uff0c\u4ec5\u4ec5\u662f\u8fd9\u4e9b\u56e0\u7d20\uff0c\u5c31\u8db3\u4ee5\u5f15\u8d77\u8096\u745e\u6c11\u7684\u6ce8\u610f\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u65e0\u6cd5\u6d88\u5316\u5f20\u606a\u521a\u624d\u5e26\u7ed9\u5979\u7684\u9707\u61be\uff0c\u770b\u5230\u8096\u745e\u6c11\u770b\u5411\u8fd9\u8fb9\uff0c\u671d\u4ed6\u8d70\u8fc7\u53bb\uff0c\u843d\u843d\u5927\u65b9\u7684\u4f38\u51fa\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8096\u603b\u4f60\u597d\uff0c\u6211\u662f\u79d1\u738b\u9ad8\u79d1\u7684\u8463\u4e8b\u957f\u9648\u9759\uff0c\u5bf9\u4f60\u4ef0\u6155\u5df2\u4e45\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u5230\u9648\u9759\u8d70\u8fc7\u6765\uff0c\u8096\u745e\u6c11\u5c31\u5ba2\u6c14\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u542c\u5979\u81ea\u6211\u4ecb\u7ecd\u5b8c\uff0c\u5fc3\u60f3\u9053\uff1a\u79d1\u738b\u9ad8\u79d1\u4e5f\u662f\u6d77\u5dde\u7684\u516c\u53f8\uff0c\u8fd9\u4e48\u4e00\u4e2a\u6f02\u4eae\u7684\u5973\u4eba\u4e0e\u90a3\u5c0f\u5b50\u8ba4\u8bc6\uff0c\u5012\u4e5f\u4e0d\u5947\u602a\u3002</p><p>\u3000\u3000\u5728\u624b\u673a\u4ee3\u5de5\u5305\u9500\u95ee\u9898\u4e0a\uff0c\u8096\u745e\u6c11\u4e0e\u5468\u5174\u4e1c\u7b49\u4eba\u6709\u5f88\u5927\u7684\u5206\u6b67\uff0c\u4f46\u662f\u5468\u5174\u4e1c\u7b49\u4eba\u83b7\u5f97\u90e8\u91cc\u7684\u652f\u6301\uff0c\u8096\u745e\u6c11\u4e3a\u6b64\u5341\u5206\u75db\u82e6\uff0c\u5982\u6b64\u77ed\u89c6\u7684\u8ffd\u9010\u773c\u524d\u7684\u5c0f\u5229\uff0c\u6700\u7ec8\u5374\u662f\u4f1a\u635f\u5bb3\u5230\u8054\u4fe1\u7684\u6839\u5e95\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u8096\u745e\u6c11\u5bf9\u79d1\u738b\u9ad8\u79d1\u5e76\u6ca1\u6709\u4ec0\u4e48\u610f\u89c1\uff0c\u6bd5\u7adf\u79d1\u738b\u9ad8\u79d1\u62ff\u7231\u8fbe\u7684\u624b\u673a\u6a21\u5757\u4e0e\u4ea7\u54c1\u65b9\u6848\u5957\u4e0a\u8054\u4fe1\u7684\u724c\u5b50\u4e5f\u8ddf\u7740\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\uff0c\u7b97\u662f\u5f00\u521b\u4e86\u624b\u673a\u54c1\u724c\u4ee3\u5de5\u5305\u9500\u5168\u65b0\u7684\u5546\u4e1a\u6a21\u5f0f\u3002</p><p>\u3000\u3000\u5c31\u51ed\u501f\u8fd9\u4efd\u521b\u65b0\u80fd\u529b\uff0c\u79d1\u738b\u9ad8\u79d1\u5c31\u662f\u4e00\u5bb6\u503c\u5f97\u5c0a\u91cd\u7684\u4f01\u4e1a\uff0c\u53ef\u60dc\u7684\u662f\u8054\u4fe1\u81ea\u5df1\u6ca1\u6709\u51fa\u606f\uff0c\u6ca1\u6709\u62b5\u6321\u4f4f\u76f4\u63a5\u6536\u53d6\u5de8\u989d\u8d34\u724c\u8d39\u7528\u7684\u8bf1\u60d1\u3002\u6b63\u662f\u8fd9\u4e00\u70b9\uff0c\u8054\u4fe1\u60f3\u8981\u8d85\u8d8a\u7231\u8fbe\u7684\u96be\u5ea6\u5c06\u4f1a\u66f4\u9ad8\uff0c\u53ef\u7b11\u7684\u662f\uff0c\u81ea\u5df1\u5728\u4e00\u4e2a\u591a\u6708\u524d\u8fd8\u4ee5\u4e3a\u7231\u8fbe\u7ed9\u8054\u4fe1\u7529\u5728\u540e\u9762\u5462\u3002</p><p>\u3000\u3000\u4e0d\u6e05\u695a\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u4e4b\u524d\u7684\u62db\u5546\u65b9\u6848\u662f\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u62ff\u5230\u4f01\u4e1a\u4ee3\u8868\u5ea7\u8c08\u4f1a\u4e0a\u8ba8\u8bba\u7684\u65b9\u6848\uff0c\u4e5f\u4e0d\u662f\u90a3\u4e48\u96be\u4ee5\u8ba9\u4eba\u63a5\u53d7\u3002</p><p>\u3000\u3000\u62db\u5546\u65b9\u6848\u4e0e\u53bb\u5e74\u7c7b\u4f3c\uff0c\u4f17\u4eba\u4eca\u5929\u8d76\u8fc7\u6765\u6700\u5173\u6ce8\u7684\u8fd8\u662f\u5916\u8d44\u54c1\u724c\u4f1a\u4e0d\u4f1a\u53c2\u4e0e\u7ade\u6807\u3001\u5982\u4f55\u53c2\u4e0e\u7ade\u6807\u7684\u95ee\u9898\uff0c\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u526f\u4e3b\u4efb\u51fa\u9762\u7ed9\u4e88\u89e3\u7b54\u3002</p><p>\u3000\u3000\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f465\u79d2\u949f\u7684\u5e7f\u544a\u65f6\u6bb5\u662f\u6700\u5177\u4ef7\u503c\u7684\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\uff0c\u5c06\u675c\u7edd\u5916\u8d44\u54c1\u724c\u53c2\u4e0e\u8fd9\u4e00\u65f6\u6bb5\u7684\u5e7f\u544a\u7ade\u6807\uff1b\u4e03\u70b9\u949f\u81f3\u4e5d\u70b9\u949f\u4e4b\u95f4\u5176\u4f59\u7684\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u53ca\u91cd\u70b9\u6863\u76ee\u51a0\u540d\u6743\uff0c\u5bf9\u5916\u8d44\u54c1\u724c\u5b9e\u884c\u7ade\u6807\u4ef7\u6ea2\u4ef750\uff05\u7684\u8fdb\u5165\u95e8\u69db\uff0c\u5176\u4f59\u65f6\u6bb5\u5b9e\u884c\u6ea2\u4ef730\uff05\u7684\u8fdb\u5165\u95e8\u69db\u3002</p><p>\u3000\u3000\u5728\u5ea7\u8c08\u4f1a\u4e4b\u540e\uff0c\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u53ec\u5f00\u8bb0\u8005\u4f1a\u516c\u5e03\u4e86\u8fd9\u4e00\u65b9\u6848\uff0c\u8ddd\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u6b63\u5f0f\u53ec\u5f00\uff0c\u90fd\u4e0d\u8db3\u56db\u5341\u516b\u4e2a\u5c0f\u65f6\u3002</p><p>\u3000\u3000\u592e\u89c6\u4e5f\u6ca1\u6709\u8981\u5927\u8086\u5ba3\u626c\u5916\u8d44\u54c1\u724c\u5c06\u53c2\u4e0e\u8fd9\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u610f\u601d\uff0c\u9664\u4e86\u901a\u8fc7\u8bb0\u8005\u4f1a\u516c\u5e03\u6d88\u606f\u4e4b\u5916\uff0c\u5e76\u6ca1\u6709\u518d\u901a\u8fc7\u592e\u89c6\u81ea\u8eab\u7684\u5f3a\u5927\u6e20\u9053\u5bf9\u516c\u4f17\u5e7f\u800c\u544a\u4e4b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u4fe1\u751f\u662f\u76f4\u63a5\u4e58\u665a\u95f4\u7684\u98de\u673a\u4ece\u91d1\u5c71\u98de\u62b5\u5317\u4eac\uff0c\u8d76\u5230\u4e2d\u5173\u6751\u7684\u7231\u8fbe\u5927\u53a6\uff0c\u624d\u77e5\u9053\u592e\u89c6\u6700\u7ec8\u516c\u5e03\u7684\u62db\u5546\u65b9\u6848\uff1a\u201c\u867d\u7136\u8feb\u4f7f\u592e\u89c6\u5bf9\u5916\u8d44\u54c1\u724c\u7b51\u8d77\u9ad8\u9ad8\u7684\u58c1\u5792\u907f\u514d\u56fd\u5185\u6700\u4e3b\u8981\u7684\u5e7f\u544a\u8d44\u6e90\u8fc7\u4e8e\u8f7b\u677e\u7684\u7ed9\u5916\u8d44\u54c1\u724c\u636e\u4e3a\u5df1\u7528\uff0c\u4f46\u662f\u5916\u8d44\u54c1\u724c\u5728\u5e02\u573a\u5ba3\u4f20\u4e0a\u6709\u7740\u4e30\u5bcc\u7684\u7ecf\u9a8c\uff0c\u800c\u56fd\u5185\u5bf9\u5916\u5f00\u653e\u7684\u5927\u8d8b\u52bf\u4e5f\u4e0d\u53ef\u80fd\u9006\u8f6c\uff0c\u4ed6\u4eec\u53ea\u8981\u9002\u5e94\u4e86\u56fd\u5185\u7684\u5e02\u573a\uff0c\u5c31\u4e0d\u4f1a\u8ba9\u6211\u4eec\u592a\u8f7b\u677e\u2026\u2026\u201d</p><p>\u3000\u3000\u5e74\u521d\u4ece\u5609\u91cc\u5730\u4ea7\u5f00\u53d1\u96c6\u56e2\u624b\u91cc\u6574\u4f53\u8d2d\u5165\u7684\u7231\u8fbe\u5927\u53a6\uff0c\u5df2\u7ecf\u6309\u7167\u5f20\u606a\u7684\u610f\u56fe\u6539\u9020\u6210\u9526\u6e56\u5728\u5317\u4eac\u7684\u57fa\u5730\uff0c\u5728\u7231\u8fbe\u5927\u53a6\u9876\u5c42\u7684\u8c6a\u534e\u4f1a\u8bae\u5385\u91cc\uff0c\u7855\u5927\u7684\u4f1a\u8bae\u684c\u65c1\u5750\u7740\u5ed6\u5ed6\u6570\u4eba\u3002</p><p>\u3000\u3000\u9664\u4e86\u521a\u521a\u4ece\u673a\u573a\u8d76\u6765\u7684\u9648\u4fe1\u751f\u4e4b\u5916\uff0c\u6768\u4e91\u3001\u5468\u4e00\u5e73\u4e5f\u662f\u521a\u521a\u4ece\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u8d76\u56de\u6765\uff0c\u9676\u884c\u5065\u3001\u53f6\u5c0f\u6850\u62b5\u8fbe\u5317\u4eac\u6709\u4e24\u5929\u4e86\uff0c\u4f46\u662f\u4ed6\u4eec\u884c\u4e8b\u4f4e\u8c03\uff0c\u4e0e\u9999\u96ea\u6d77\u7684\u5176\u4ed6\u968f\u884c\u5de5\u4f5c\u4eba\u5458\u6682\u65f6\u8fd8\u6ca1\u6709\u51fa\u73b0\u5728\u5a92\u4f53\u9762\u524d\uff0c\u82cf\u6d25\u4e1c\u4e5f\u662f\u4e0b\u5348\u7684\u98de\u673a\u4ece\u6d77\u5dde\u76f4\u63a5\u98de\u62b5\u5317\u4eac\uff0c\u6b64\u5916\uff0c\u5c31\u662f\u5317\u4eac\u5206\u516c\u53f8\u7684\u526f\u603b\u7ecf\u7406\u59da\u575a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u4f1a\u8bae\u684c\u7684\u4e00\u7aef\uff0c\u9762\u5bf9\u7740\u843d\u5730\u5927\u7a97\uff0c\u770b\u7740\u5916\u9762\u4e2d\u5173\u6751\u7684\u591c\u7a7a\u3002\u4e2d\u5173\u6751\u5728\u4e5d\u4e03\u5e74\u4e4b\u524d\uff0c\u53d1\u5c55\u6bd4\u8f83\u7f13\u6162\uff0c\u800c\u662f\u5728\u4e5d\u516b\u5e74\u89c4\u5212\u91cd\u65b0\u8c03\u6574\u4e4b\u540e\uff0c\u624d\u5728\u5317\u4eac\u5e02\u653f\u5e9c\u7684\u5927\u529b\u652f\u6301\u4e0b\u9ad8\u901f\u53d1\u5c55\u8d77\u6765\u3002\u5f20\u606a\u53ef\u662f\u5bc4\u671b\u4e8e\u5efa\u90ba\u7684\u6570\u5b57\u957f\u5eca\u6784\u60f3\u80fd\u6700\u7ec8\u8d85\u8d8a\u4e2d\u5173\u6751\uff0c\u4e0d\u7136\u8fd8\u4e0d\u5982\u5c06\u6a61\u6811\u56ed\u8ba1\u5212\u653e\u5728\u5317\u4eac\u6765\u5b9e\u65bd\u3002</p><p>\u3000\u3000\u201c\u4f60\u5230\u91d1\u5c71\u8d70\u4e86\u4e00\u5708\uff0c\u611f\u89c9\u5982\u4f55\u554a\uff1f\u201d\u5f20\u606a\u95ee\u9648\u4fe1\u751f\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u91d1\u5c71\u6295\u8d44\u5efa\u8bbe\u4ed6\u4eec\u5728\u56fd\u5185\u7684\u7b2c\u4e00\u4e2a\u5bb6\u7535\u5236\u9020\u57fa\u5730\uff0c\u6b63\u6cf0\u53c8\u4e0e\u5609\u4fe1\u7535\u5b50\u8ba1\u5212\u5728\u91d1\u5c71\u6295\u8d44\u5efa\u9020\u7535\u5b50\u5236\u9020\u57fa\u5730\uff0c\u4e1c\u5927\u4e0e\u674e\u5728\u6d19\u7684\u7ea0\u7eb7\u6216\u8bb8\u662f\u5076\u7136\u89e6\u53d1\u7684\uff0c\u4f46\u662f\u4e0e\u97e9\u56fd\u4e09\u661f\u4e4b\u95f4\u7684\u8f83\u91cf\u5374\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\uff0c\u60f3\u907f\u514d\u90fd\u907f\u514d\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4ece\u65e5\u672c\u56de\u6765\u540e\uff0c\u5c31\u6293\u7d27\u65f6\u95f4\u53bb\u91d1\u5c71\u8dd1\u4e86\u4e00\u5708\u3002</p><p>\u3000\u3000\u4ece\u4e09\u6708\u521d\u52a0\u5165\u9526\u6e56\u4ee5\u6765\uff0c\u9648\u4fe1\u751f\u4e5f\u662f\u5598\u606f\u672a\u5b9a\u3001\u5404\u5730\u5954\u6ce2\uff0c\u505c\u7559\u5728\u7a7a\u4e2d\u7684\u65f6\u95f4\u4e0d\u89c1\u5f97\u6bd4\u7ad9\u5728\u5730\u9762\u4e0a\u7684\u65f6\u95f4\u5c11\u591a\u5c11\u3002</p><p>\u3000\u3000\u4ece\u91d1\u5c71\u98de\u62b5\u5317\u4eac\uff0c\u4e5f\u6ca1\u6709\u673a\u4f1a\u5148\u53bb\u5bbe\u9986\u5c06\u884c\u56ca\u653e\uff0c\u5c31\u76f4\u63a5\u4e0e\u52a9\u624b\u8d76\u5230\u7231\u8fbe\u5927\u53a6\u8ddf\u5f20\u606a\u6c47\u62a5\u91d1\u5c71\u4e4b\u884c\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u201c\u4ece\u4e5d\u4e8c\u5e74\u4e2d\u97e9\u5efa\u4ea4\u4ee5\u6765\uff0c\u4e09\u661f\u5728\u534e\u6295\u8d44\u5efa\u5382\uff0c\u5c06\u56fd\u5185\u5f53\u6210\u4e2d\u4f4e\u6863\u5bb6\u7535\u4e0e\u7535\u5b50\u4ea7\u54c1\u7684\u503e\u9500\u5730\uff0c\u4f46\u662f\u4ed6\u4eec\u5728\u4e2d\u4f4e\u7aef\u3001\u6613\u6a21\u4eff\u7684\u6d88\u8d39\u5e02\u573a\u4e0a\uff0c\u6bd4\u8d77\u56fd\u5185\u7684\u4f01\u4e1a\uff0c\u5e76\u4e0d\u5177\u5907\u4ec0\u4e48\u4f18\u52bf\uff0c\u6240\u4ee5\u4ed6\u4eec\u8fd9\u51e0\u5e74\u5728\u534e\u7684\u53d1\u5c55\u5e76\u4e0d\u7b97\u987a\u5229\uff0c\u4ee5\u4e09\u661f\u5728\u60e0\u5dde\u7684\u789f\u673a\u5de5\u5382\u4e3a\u4f8b\uff0c\u4e5d\u4e94\u5e74\uff0c\u586b\u8865\u5e02\u573a\u7a7a\u767d\u65f6\uff0c\u4e09\u661f\u789f\u673a\u5728\u56fd\u5185\u5927\u884c\u5176\u9053\uff0c\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\uff0c\u56fd\u5185\u789f\u673a\u5382\u5546\u8fc5\u901f\u5d1b\u8d77\uff0c\u4e09\u661f\u789f\u673a\u5728\u5e02\u573a\u4e0a\u5c31\u8fd1\u4e4e\u4e8e\u9500\u58f0\u533f\u8ff9\u4e86\u2026\u2026\u4ece\u76ee\u524d\u7684\u5404\u79cd\u8ff9\u8c61\u6765\uff0c\u6bd4\u5982\u8bf4\u4e09\u661f\u62e9\u5730\u7b79\u5efa\u5927\u578b\u7814\u53d1\u4e2d\u5fc3\u7684\u8ba1\u5212\uff0c\u5728\u91d1\u5c71\u7684\u9ad8\u6c34\u51c6\u5bb6\u7535\u5236\u9020\u57fa\u5730\uff0c\u8fd8\u6709\u5728\u534e\u6295\u653e\u5e7f\u544a\u6240\u9488\u5bf9\u7684\u6d88\u8d39\u7fa4\u4f53\u6709\u6240\u6539\u53d8\uff0c\u90fd\u53ef\u4ee5\u770b\u51fa\u4ed6\u4eec\u5bf9\u534e\u7684\u7ecf\u8425\u601d\u8def\u5df2\u7ecf\u53d1\u751f\u4e86\u8f6c\u53d8\uff0c\u4ed6\u4eec\u53ef\u80fd\u4f1a\u9000\u51fa\u4e2d\u4f4e\u7aef\u6d88\u8d39\u5e02\u573a\uff0c\u6216\u8005\u5728\u4e2d\u4f4e\u7aef\u6d88\u8d39\u5e02\u573a\u4e0e\u56fd\u5185\u7684\u5382\u5546\u5408\u4f5c\uff0c\u96c6\u4e2d\u7cbe\u529b\u4e13\u6ce8\u4e8e\u4e2d\u9ad8\u7aef\u6d88\u8d39\u5e02\u573a\u7684\u5f00\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u5185\u5341\u4e09\u4ebf\u4eba\u53e3\u7684\u5e73\u5747\u6d88\u8d39\u6c34\u5e73\u7684\u786e\u5904\u5728\u4e00\u4e2a\u6bd4\u8f83\u4f4e\u7684\u6c34\u51c6\u4e4b\u4e0a\uff0c\u63d0\u5230\u56fd\u5185\u7684\u5e02\u573a\uff0c\u5f88\u5bb9\u6613\u5c31\u5c06\u773c\u5149\u653e\u5728\u4e2d\u4f4e\u7aef\u6d88\u8d39\u5e02\u573a\uff0c\u6b8a\u4e0d\u77e5\uff0c\u5728\u5341\u4e09\u4ebf\u4eba\u53e3\u8fd9\u4e2a\u7684\u5e9e\u5927\u57fa\u6570\u4e0a\uff0c\u6709\u56db\u5230\u516d\u5343\u4e07\u4eba\u7684\u6d88\u8d39\u6c34\u51c6\uff0c\u4e0d\u6bd4\u65e5\u97e9\u4e24\u56fd\u7684\u5e73\u5747\u6c34\u5e73\u4f4e\u2026\u2026\u8fd9\u662f\u5229\u6da6\u6700\u4e30\u539a\u3001\u8d2d\u4e70\u529b\u6700\u5f3a\u7684\u9ad8\u573a\uff0c\u6211\u4eec\u4ee5\u540e\u8981\u8ddf\u4e09\u661f\u594b\u529b\u4e89\u593a\u7684\u662f\u8fd9\u4e00\u5757\u7684\u5e02\u573a\uff0c\u8981\u5728\u8fd9\u4e00\u5757\u5e02\u573a\u4e0e\u4e09\u661f\uff0c\u4e0e\u5916\u8d44\u54c1\u724c\u5e73\u5206\u79cb\u8272\uff0c\u9526\u6e56\u624d\u80fd\u7b97\u7ad9\u7a33\u7684\u811a\u8ddf\u3002\u6211\u8981\u9526\u6e56\u53c2\u4e0e\u5230\u5168\u7403\u7ade\u4e89\u4e2d\u53bb\uff0c\u800c\u4e0d\u662f\u533a\u57df\u7ecf\u6d4e\u7684\u5438\u8840\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u533a\u57df\u7ecf\u6d4e\u7684\u5438\u8840\u673a\uff1f\u201d\u9648\u4fe1\u751f\u8fd8\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u65b0\u540d\u8bcd\uff0c\u7591\u60d1\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4e00\u5bb6\u4f01\u4e1a\u65e0\u8d23\u4efb\u6392\u653e\u6c61\u67d3\u7269\u3001\u727a\u7272\u73af\u5883\u6765\u83b7\u5f97\u9ad8\u989d\u5229\u6da6\uff0c\u4e0d\u662f\u5728\u5438\u533a\u57df\u73af\u5883\u8d44\u6e90\u7684\u8840\uff1f\u4e00\u5bb6\u4f01\u4e1a\u4ee5\u4f4e\u4ef7\u96c7\u4f63\u52b3\u52a8\u529b\u6765\u7ef4\u6301\u9ad8\u989d\u5229\u6da6\uff0c\u4e0d\u662f\u5728\u5438\u533a\u57df\u4eba\u529b\u8d44\u6e90\u7684\u8840\uff1f\u4e00\u5bb6\u4f01\u4e1a\u901a\u8fc7\u884c\u8d3f\u7b49\u4e0d\u6cd5\u624b\u6bb5\u5b98\u5546\u52fe\u7ed3\u76f4\u63a5\u4ece\u5e02\u653f\u5de5\u7a0b\u3001\u571f\u5730\u4e70\u5356\u3001\u4f01\u4e1a\u5e76\u8d2d\u4e2d\u635e\u53d6\u66b4\u5229\uff0c\u4e0d\u662f\u5728\u5438\u98df\u8fd9\u4e2a\u5730\u65b9\u3001\u8fd9\u4e2a\u56fd\u5bb6\u7684\u8840\uff1f\u201d\u5f20\u606a\u5e73\u9759\u7684\u89e3\u91ca\u9053\uff0c\u201c\u9526\u6e56\u4e0d\u8981\u6210\u4e3a\u8fd9\u6837\u7684\u4f01\u4e1a\uff0c\u6211\u60f3\u8bf8\u4f4d\u8981\u662f\u4e3a\u8fd9\u6837\u7684\u4f01\u4e1a\u670d\u52a1\uff0c\u4eba\u751f\u4e5f\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u6210\u5c31\u611f\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4ed6\u4eec\u542c\u4e86\u8fd9\u756a\u8bdd\u90fd\u9ed8\u7136\u65e0\u8bed\uff0c\u90fd\u4e0d\u6653\u5f97\u56fd\u5185\u6709\u591a\u5c11\u4f01\u4e1a\u5bb6\u542c\u4e86\u8fd9\u756a\u8bdd\u4e0d\u4f1a\u9732\u51fa\u4e0d\u5c51\u7684\u8868\u60c5\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e9b\u662f\u4e0d\u662f\u53ef\u4ee5\u4f5c\u4e3a\u9526\u6e56\u7684\u6838\u5fc3\u6587\u5316\u6765\u53d1\u5c55\uff1f\u201d\u82cf\u6d25\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4ee5\u540e\u518d\u8bf4\uff0c\u6211\u4e5f\u662f\u968f\u610f\u60f3\u5230\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8fd8\u662f\u8ba9\u9648\u603b\u7ee7\u7eed\u8bf4\u4ed6\u7684\u91d1\u5c71\u4e4b\u884c\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6b63\u6cf0\u96c6\u56e2\u4e0e\u5609\u4fe1\u7535\u5b50\u5728\u91d1\u5c71\u641e\u7535\u5b50\u5de5\u4e1a\u57fa\u5730\uff0c\u5e94\u8be5\u662f\u9488\u5bf9\u6211\u4eec\u9526\u6e56\u5427\u3002\u4ed6\u4eec\u770b\u5230\u9526\u6e56\u6709\u8d70\u5411\u5143\u5668\u4ef6\u4f9b\u5e94\u5546\u7684\u53ef\u80fd\uff0c\u4ed6\u4eec\u6ca1\u6709\u6838\u5fc3\u6280\u672f\u4f46\u662f\u53ef\u4ee5\u901a\u8fc7\u4e3a\u6d77\u5916\u5143\u5668\u4ef6\u5382\u5546\u4ee3\u5de5\u7684\u65b9\u5f0f\u53c2\u4e0e\u5230\u5143\u5668\u4ef6\u4f9b\u5e94\u5e02\u573a\u4e2d\u6765\u5206\u4e00\u676f\u7fb9\u2026\u2026\u4ee5\u56fd\u5185\u4f4e\u5ec9\u7684\u52b3\u52a8\u529b\u4ef7\u683c\uff0c\u4ed6\u4eec\u5f53\u7136\u4e5f\u662f\u80fd\u5206\u5f97\u4e00\u676f\u7fb9\u7684\uff0c\u62ff\u606a\u5c11\u7684\u8bdd\u6765\u8bf4\uff0c\u4ed6\u4eec\u662f\u533a\u57df\u7ecf\u6d4e\u7684\u5438\u8840\u673a\uff0c\u5438\u98df\u5ec9\u4ef7\u52b3\u52a8\u529b\u7684\u8840\uff0c\u201d\u9648\u4fe1\u751f\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u4e0d\u6653\u5f97\u4ed6\u4eec\u4e0e\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u7684\u63a5\u89e6\u7a0b\u5ea6\uff0c\u5c31\u6015\u4e09\u661f\u4e0e\u4ed6\u4eec\u8054\u5408\u8d77\u6765\u5b8c\u6210\u5728\u56fd\u5185\u7684\u4ea7\u4e1a\u94fe\u5e03\u5c40\uff0c\u90a3\u53ef\u80fd\u4e0d\u9700\u8981\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u9526\u6e56\u5728\u5173\u7a0e\u58c1\u5792\u4e0e\u52b3\u52a8\u529b\u4ef7\u683c\u4e0a\u7684\u4f18\u52bf\u5c31\u80fd\u8361\u7136\u65e0\u5b58\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ea\u6709\u4e24\u5e74\u7684\u65f6\u95f4\u554a\uff0c\u8fd8\u771f\u662f\u4e0d\u8f7b\u677e\u554a\uff0c\u201d\u5f20\u606a\u63c9\u4e86\u63c9\u7709\u5934\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u505a\u4e86\u4e00\u4e2a\u51b3\u5b9a\uff0c\u4f60\u4eec\u4e0d\u8981\u611f\u5230\u592a\u60ca\u8be7\uff0c\u6211\u60f3\u9526\u6e56\u4ece\u5fb7\u4eea\u624b\u91cc\u83b7\u5f97\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u53ef\u80fd\u6709\u9488\u5bf9\u6027\u7684\u5411\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u5185\u7684\u4f01\u4e1a\u516c\u5f00\uff0c\u8bb8\u4ed6\u4eec\u514d\u8d39\u4f7f\u7528\u2026\u2026\u201d</p></article>"}}}]);
