(window.webpackJsonp=window.webpackJsonp||[]).push([[679],{702:function(p){p.exports={chapterTitle:null,title:"\u7b2c678\u7ae0 \u7b2c\u56db\u5c4a\u592e\u89c6\u6807\u738b",content:"<article><h2>\u7b2c678\u7ae0 \u7b2c\u56db\u5c4a\u592e\u89c6\u6807\u738b</h2><p>\u3000\u3000\u867d\u7136\u4e09\u661f\u9a7b\u534e\u603b\u90e8\u5728\u5efa\u56fd\u95e8\u90a3\u91cc\uff0c\u674e\u5728\u6d19\u3001\u674e\u99a8\u4e88\u4ee5\u53ca\u91d1\u5357\u52c7\u8fd8\u662f\u9009\u62e9\u4e0b\u69bb\u5728\u738b\u5e9c\u4e95\u5927\u996d\u5e97\u3002</p><p>\u3000\u3000\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u901a\u8fc7\u8bb0\u8005\u4f1a\u516c\u5e03\u8fd9\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u7ade\u6807\u65b9\u6848\u65f6\uff0c\u674e\u5728\u6d19\u4e09\u4eba\u5750\u5728\u738b\u5e9c\u5df7\u5927\u996d\u5e97\u4e00\u5c42\u5927\u5802\u7684\u5496\u5561\u5385\u91cc\u559d\u5496\u5561\uff0c\u662f\u4e09\u661f\u9a7b\u534e\u6295\u8d44\u6709\u9650\u516c\u53f8\u526f\u603b\u88c1\u674e\u653f\u54f2\u6253\u6765\u7535\u8bdd\u901a\u77e5\u4ed6\u8fd9\u4e2a\u6d88\u606f\u3002</p><p>\u3000\u3000\u4e13\u52a1\u53ea\u662f\u5728\u97e9\u56fd\u624d\u6709\u7684\u804c\u52a1\uff0c\u4e09\u661f\u9a7b\u534e\u6295\u8d44\u516c\u53f8\u7684\u9ad8\u7ea7\u7ba1\u7406\u804c\u52a1\u4f53\u7cfb\u5e76\u4e0d\u662f\u4eff\u88ad\u4e09\u661f\u603b\u90e8\u3002\u674e\u5728\u6d19\u5728\u4e09\u661f\u9a7b\u534e\u6295\u8d44\u516c\u53f8\u62c5\u4efb\u97e9\u56fd\u603b\u90e8\u624d\u4f1a\u6709\u7684\u4e13\u52a1\u804c\u52a1\uff0c\u4e5f\u4f53\u73b0\u4ed6\u5728\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u7684\u7279\u6b8a\u5730\u4f4d\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u653e\u4e0b\u7535\u8bdd\uff0c\u5bf9\u91d1\u5357\u52c7\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4e2d\u4e86\uff0c\u653f\u6cbb\u5f71\u54cd\u662f\u8fd9\u4e2a\u56fd\u5bb6\u5fc5\u5b9a\u4f1a\u8003\u8651\u7684\u4e1c\u897f\u3002\u7231\u8fbe\u6709\u52c7\u6c14\u7ad9\u51fa\u6765\u8ddf\u4e2d\u592e\u7535\u89c6\u53f0\u53eb\u677f\uff0c\u4e5f\u662f\u6211\u4eec\u4e8b\u5148\u6ca1\u6709\u9884\u6599\u5230\u7684\uff0c\u770b\u6765\u6211\u4eec\u9700\u8981\u4e0e\u8fd9\u4e2a\u56fd\u5bb6\u7684\u4e00\u4e9b\u4eba\u5c55\u5f00\u66f4\u5bc6\u5207\u7684\u5408\u4f5c\u624d\u884c\u3002\u4f60\u8fc7\u6765\u5e2e\u6211\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u5e2e\u4f60\uff0c\u4e5f\u8981\u7b49\u6211\u7684\u8bfe\u7a0b\u4fee\u5b8c\u518d\u8bf4\u2026\u2026\u201d\u91d1\u5357\u52c7\u81ea\u8c13\u5bf9\u8fd9\u4e2a\u56fd\u5bb6\u8ba4\u8bc6\u5df2\u7ecf\u8db3\u591f\u6df1\u523b\uff0c\u8981\u662f\u4e09\u661f\u80fd\u6beb\u65e0\u969c\u788d\u7684\u53c2\u52a0\u6807\u738b\u5e7f\u544a\u7684\u7ade\u6807\uff0c\u90a3\u624d\u53eb\u4ed6\u611f\u5230\u5947\u602a\u5462\uff0c\u4f46\u662f\u592e\u89c6\u4eca\u665a\u516c\u5e03\u7684\u65b9\u6848\u4f1a\u5982\u6b64\u7684\u4e25\u5389\uff0c\u4e5f\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4e2a\u65b9\u6848\u629b\u51fa\u6765\uff0c\u51e0\u5e74\u5185\u60f3\u6709\u591a\u5927\u6539\u53d8\u4e5f\u4e0d\u5927\u53ef\u80fd\u3002</p><p>\u3000\u3000\u9644\u8fd1\u7684\u684c\u5b50\u6709\u968f\u884c\u7684\u4eba\u5458\u5360\u7740\uff0c\u4e5f\u4e0d\u6015\u5728\u516c\u5171\u573a\u6240\u8c08\u8bdd\u4f1a\u7ed9\u522b\u4eba\u5077\u542c\u5230\uff1b\u91d1\u5357\u52c7\u4e0e\u674e\u5728\u6d19\u4ea4\u8c08\u5728\u534e\u5f00\u53d1\u5e02\u573a\u7684\u4e00\u4e9b\u770b\u6cd5\u3002</p><p>\u3000\u3000\u9526\u6e56\u7edd\u5bf9\u662f\u4e00\u4e2a\u503c\u5f97\u91cd\u89c6\u7684\u5bf9\u624b\uff0c\u674e\u5728\u6d19\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u7ed9\u6cfc\u5230\u8138\u4e0a\u7684\u90a3\u676f\u70ed\u8336\uff0c\u91d1\u5357\u52c7\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u7ed9\u72e0\u72e0\u6247\u8fc7\u6765\u7684\u4e24\u8bb0\u8033\u5149\uff0c\u4f46\u662f\u9762\u5bf9\u4e00\u4e2a\u503c\u5f97\u91cd\u89c6\u7684\u5bf9\u624b\uff0c\u4e0d\u5e94\u8be5\u968f\u968f\u4fbf\u4fbf\u5c31\u5c06\u5fc3\u91cc\u7684\u6028\u6068\u6302\u5728\u8138\u4e0a\u3002</p><p>\u3000\u3000\u4e09\u661f\u4e5f\u521a\u521a\u8c03\u6574\u5bf9\u534e\u7684\u5e02\u573a\u7b56\u7565\uff0c\u867d\u7136\u8bf4\u5728\u4e9a\u6d32\uff0c\u4e09\u661f\u8981\u7edd\u5bf9\u5f3a\u8fc7\u9526\u6e56\u8fd9\u79cd\u516c\u53f8\uff0c\u4f46\u662f\u5728\u4e2d\u56fd\uff0c\u4e09\u661f\u8fd8\u65e0\u6cd5\u8bf4\u5c31\u80fd\u7acb\u9a6c\u5c06\u9526\u6e56\u78be\u5f97\u7c89\u8eab\u788e\u9aa8\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4e0d\u60f3\u72ec\u81ea\u4e00\u4eba\u95f7\u5728\u623f\u95f4\u91cc\uff0c\u5750\u5230\u5496\u5561\u5385\uff0c\u53c8\u662f\u542c\u522b\u4eba\u8c08\u5546\u4e1a\u4e0a\u7684\u4e8b\u60c5\uff0c\u5979\u65e0\u804a\u7684\u62ff\u7740\u6c64\u5319\u6405\u52a8\u84b8\u817e\u7740\u70ed\u6c7d\u7684\u5496\u5561\uff0c\u5fc3\u60f3\u8fd8\u4e0d\u5982\u56de\u623f\u95f4\u53bb\u770b\u90a3\u672c\u5c0f\u8bf4\u597d\u4e86\uff0c\u5076\u5c14\u4e5f\u6367\u8d77\u8138\u988a\u542c\u4ed6\u4eec\u5728\u804a\u4e9b\u4ec0\u4e48\uff0c\u4e5f\u5c31\u5bf9\u9526\u6e56\u7684\u8bdd\u9898\u611f\u4e9b\u5174\u8da3\uff0c\u5c31\u50cf\u5979\u4ece\u5c0f\u5e0c\u671b\u542c\u5979\u7237\u7237\u521b\u7acb\u4e09\u661f\u7684\u6545\u4e8b\u4e00\u6837\uff0c\u603b\u89c9\u5f97\u80fd\u53ea\u624b\u521b\u7acb\u5e9e\u5927\u4f01\u4e1a\u7684\u4eba\u90fd\u6709\u7740\u975e\u51e1\u7684\u9b45\u529b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4e00\u4e2a\u5e9e\u5927\u800c\u6709\u6f5c\u529b\u7684\u5e02\u573a\uff0c\u5317\u4eac\u8fd9\u4e48\u591a\u7684\u9ad8\u7ea7\u9152\u5e97\uff0c\u5165\u4f4f\u53ef\u4e0d\u90fd\u662f\u6211\u4eec\u8fd9\u4e9b\u5916\u56fd\u4eba\uff0c\u8fd9\u4e2a\u56fd\u5bb6\u6709\u7740\u4e0d\u5bb9\u5c0f\u89c6\u7684\u9ad8\u7aef\u8d2d\u4e70\u529b\uff0c\u6211\u4eec\u4e09\u661f\u8981\u5168\u9762\u7684\u6e17\u900f\u8fdb\u5165\u8fd9\u4e2a\u5e02\u573a\uff0c\u8981\u6709\u66f4\u591a\u7684\u6280\u5de7\u624d\u80fd\u8f7b\u677e\u7684\u8d8a\u8fc7\u8fd9\u4e2a\u56fd\u5bb6\u4e3a\u4fdd\u62a4\u672c\u571f\u4f01\u4e1a\u6240\u8bbe\u7f6e\u7684\u4f53\u5236\u969c\u788d\u3002\u8fd9\u4e2a\u56fd\u5bb6\u5730\u65b9\u4e0e\u5730\u65b9\u4e4b\u95f4\u7684\u5229\u76ca\u662f\u4e0d\u4e00\u81f4\u7684\uff0c\u5730\u65b9\u4e0a\u7684\u4f01\u4e1a\u4eff\u5192\u522b\u5904\u4f01\u4e1a\u7684\u4ea7\u54c1\uff0c\u751a\u81f3\u4f1a\u5f97\u5230\u5730\u65b9\u653f\u5e9c\u7684\u4fdd\u62a4\uff0c\u6240\u4ee5\u6211\u4eec\u8981\u505a\u7684\u4e8b\u60c5\u5e76\u4e0d\u56f0\u96be\uff0c\u5173\u952e\u662f\u8981\u5c06\u5730\u65b9\u7684\u5229\u76ca\u3001\u67d0\u4e9b\u5173\u8054\u4f01\u4e1a\u7684\u5229\u76ca\uff0c\u90fd\u7ed3\u5408\u5230\u4e09\u661f\u5728\u8fd9\u4e2a\u56fd\u5bb6\u7684\u4ea7\u4e1a\u94fe\u5e03\u5c40\u4e2d\u6765\uff0c\u4f60\u5c31\u4f1a\u53d1\u73b0\u4f1a\u6709\u65e0\u6570\u7684\u4eba\u4e3b\u52a8\u5e2e\u6211\u4eec\u8d8a\u8fc7\u4f53\u5236\u4e0a\u7684\u969c\u788d\uff0c\u6bd5\u7adf\u8fd9\u4e2a\u56fd\u5bb6\u8fd8\u4e0d\u4f1a\u964d\u4e0b\u4ed6\u4eec\u2018\u5bf9\u5916\u5f00\u653e\u2019\u7684\u65d7\u5e1c\u3002\u8fd9\u4e2a\u56fd\u5bb6\uff0c\u53d7\u9ad8\u7b49\u6559\u80b2\u7684\u9752\u5e74\u5f88\u591a\uff0c\u4f46\u662f\u52b3\u52a8\u529b\u4ef7\u683c\u53c8\u6781\u4e3a\u4f4e\u5ec9\uff0c\u4e09\u661f\u5e94\u8be5\u5c06\u4e3b\u8981\u7684\u5236\u9020\u57fa\u5730\u90fd\u8f6c\u79fb\u8fc7\u6765\uff0c\u4e0d\u4ec5\u4e3a\u5360\u9886\u8fd9\u4e2a\u5e02\u573a\uff0c\u4e5f\u80fd\u4e3a\u4e09\u661f\u7684\u5168\u7403\u6218\u7565\u63d0\u4f9b\u6210\u672c\u4e0a\u7684\u4f18\u52bf\u3002\u201d</p><p>\u3000\u3000\u674e\u5728\u6d19\u8bf4\u9053\uff1a\u201c\u9a7b\u534e\u516c\u53f8\u7684\u884c\u52a8\u80fd\u529b\u592a\u5f31\u4e86\uff0c\u7ecf\u8425\u601d\u8def\u4e5f\u6709\u95ee\u9898\uff0c\u674e\u4f1a\u957f\u4e00\u518d\u5f3a\u8c03\uff0c\u9664\u4e86\u8001\u5a46\u3001\u5b50\u5973\u4e0d\u80fd\u53d8\uff0c\u5176\u4ed6\u7684\u90fd\u53ef\u4ee5\u7edf\u7edf\u7684\u6539\u53d8\uff0c\u8fd9\u53e5\u8bdd\uff0c\u540c\u6837\u9002\u7528\u9a7b\u534e\u516c\u53f8\u7684\u5934\u4e0a\uff0c\u9700\u8981\u5f7b\u5e95\u7684\u6539\u53d8\u2026\u2026\u540e\u5929\u4f60\u966a\u6211\u53bb\u6885\u5730\u4e9a\u4e2d\u5fc3\uff0c\u6211\u5bf9\u9a7b\u534e\u516c\u53f8\u7684\u90a3\u4e9b\u4eba\u4e0d\u591f\u653e\u5fc3\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u6574\u665a\u7684\u5fc3\u601d\u4e0d\u5728\u592e\u89c6\u7684\u7ade\u6807\u65b9\u6848\u4e0a\uff0c\u800c\u5f20\u606a\u7684\u90a3\u53e5\u8bdd\u5728\u5979\u5fc3\u91cc\u6380\u8d77\u8f69\u7136\u5927\u6ce2\u3002</p><p>\u3000\u3000\u79bb\u5f00\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\uff0c\u9648\u9759\u8ba9\u79d1\u738b\u9a7b\u5317\u4eac\u529e\u4e8b\u5904\u7684\u6c7d\u8f66\u76f4\u63a5\u9001\u5979\u4e0e\u5b50\u5609\u56de\u5bbe\u9986\u3002</p><p>\u3000\u3000\u5728\u5bbe\u9986\u7684\u623f\u95f4\u91cc\uff0c\u8c22\u5b50\u5609\u8e66\u8e66\u8df3\u8df3\u6ca1\u4e2a\u6d88\u505c\uff0c\u903c\u7740\u9648\u9759\u8bf4\u4ea4\u5f85\u5979\u4e0e\u5f20\u606a\u4e4b\u95f4\u6709\u4ec0\u4e48\u4e0d\u544a\u544a\u4eba\u7684\u79d8\u5bc6\uff1a\u201c\u4f60\u4e00\u5b9a\u7ed9\u4ed6\u9a97\u4e0a\u5e8a\u4e86\uff0c\u4e0d\u8fc7\u4f60\u4e5f\u7b97\u5927\u5927\u8d5a\u5230\u4e00\u7b14\u5462\uff0c\u4f60\u770b\u90c1\u840d\u7ed9\u5468\u5bcc\u660e\u90a3\u4e2a\u4e2d\u8001\u5e74\u4eba\u5f53\u4e86\u4e00\u5e74\u591a\u7684\u60c5\u5987\uff0c\u8fd8\u4e0d\u662f\u8fd9\u6837\uff01\u201d\u666e\u901a\u4eba\u5f88\u96be\u7406\u89e3\u5b50\u5609\u7684\u4f26\u7406\u89c2\uff0c\u5b50\u5609\u4e0e\u90c1\u840d\u5f88\u8c08\u5f97\u6765\uff0c\u4e5f\u662f\u9648\u9759\u65e0\u6cd5\u7406\u89e3\u7684\uff0c\u5c31\u65f6\u5019\u542c\u5979\u62ff\u81ea\u5df1\u80e1\u6405\u86ee\u7f20\uff0c\u9648\u9759\u5934\u76ae\u90fd\u6c14\u5f97\u53d1\u9ebb\uff0c\u6342\u7740\u8033\u6735\u5927\u53eb\uff1a\u201c\u6211\u8bf4\u6ca1\u6709\u5c31\u6ca1\u6709\uff0c\u4f60\u5c0f\u5973\u5b69\u5b50\u5bb6\u5bb6\u7684\uff0c\u600e\u4e48\u52a8\u4e0d\u52a8\u5c31\u8bf4\u8fd9\u4e48\u7c97\u4fd7\u7684\u8bdd\uff01\u54ea\u6709\u4e0a\u5e8a\u554a\uff1f\u201d\u8c22\u5b50\u5609\u5938\u5f20\u7684\u5927\u53eb\uff1a\u201c\u554a\uff0c\u4f60\u4eec\u7b2c\u4e00\u6b21\u5728\u91ce\u5730\u554a\uff01\u201d\u9648\u9759\u7ed9\u64a9\u62e8\u7684\u4e0d\u884c\uff0c\u6ee1\u5c4b\u5b50\u7684\u8ffd\u8d76\u7740\u5b50\u5609\uff0c\u8c22\u5b50\u5609\u4e00\u8fb9\u9003\u4e00\u8fb9\u95ee\u9648\u9759\u505a\u90a3\u4e8b\u7684\u6ecb\u5473\u5982\u4f55\uff0c\u5404\u79cd\u7a00\u5947\u53e4\u602a\u7684\u95ee\u9898\u90fd\u968f\u53e3\u629b\u51fa\u6765\uff0c\u9648\u9759\u7d2f\u4e86\u6ee1\u8eab\u6c57\u624d\u5c06\u5b50\u5609\u6d3b\u8e66\u4e71\u8df3\u7684\u8eab\u5b50\u6293\u4f4f\u538b\u5728\u5e8a\u4e0a\u634f\u7740\u5979\u7684\u8138\u988a\u4e0d\u8ba9\u5979\u80e1\u8bf4\u516b\u9053\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u4e3e\u624b\u8868\u793a\u6c42\u9976\uff0c\u5f85\u9648\u9759\u677e\u5f00\u624b\uff0c\u63c9\u4e86\u63c9\u7ed9\u634f\u75bc\u7684\u816e\u7ed1\u5b50\u95ee\u9648\u9759\uff1a\u201c\u771f\u662f\u6ca1\u6709\u529e\u6cd5\u89e3\u91ca\u554a\uff0c\u4ed6\u603b\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u5c06\u9526\u6e56\u8d39\u5c3d\u5343\u8f9b\u4e07\u82e6\u4ece\u5fb7\u4eea\u641e\u6765\u7684\u6280\u672f\u5411\u79d1\u738b\u9ad8\u79d1\u516c\u5f00\u5427\uff0c\u4f1a\u4e0d\u4f1a\u4ed6\u5bf9\u4f60\u6709\u4f01\u56fe\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u600e\u4e48\u6653\u5f97\uff1f\u201d\u9648\u9759\u7d2f\u5f97\u5598\u6c14\uff0c\u4ef0\u8eba\u5728\u5e8a\u4e0a\uff0c\u770b\u7740\u540a\u7740\u6c34\u6676\u706f\u7684\u5929\u82b1\u677f\uff0c\u5fc3\u601d\u5f53\u7136\u4f1a\u5f80\u4e00\u4e9b\u4e0d\u8be5\u60f3\u7684\u5730\u65b9\u53bb\u60f3\uff0c\u53ea\u662f\u8fd9\u4e9b\u53ef\u80fd\u6027\u90fd\u592a\u4f4e\u4e86\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u4ed6\u6709\u4f01\u56fe\u4e5f\u6ca1\u6709\u4ec0\u4e48\u62c5\u5fc3\u7684\uff0c\u771f\u80fd\u514d\u8d39\u4f7f\u7528\u9526\u6e56\u7684\u6280\u672f\uff0c\u4f60\u966a\u4ed6\u4e0a\u5e8a\u5c31\u662f\u4e86\uff0c\u516d\u54e5\u90a3\u8fb9\uff0c\u6211\u4f1a\u5e2e\u4f60\u4fdd\u5b88\u79d8\u5bc6\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u80e1\u8bf4\u516b\u9053\uff01\u201d\u9648\u9759\u7ffb\u8fc7\u8eab\u53bb\u6390\u5b50\u5609\u7684\u5634\u5df4\u3002</p><p>\u3000\u3000\u201c\u54af\u54af\u2026\u2026\u201d\u8c22\u5b50\u5609\u7b11\u5c94\u4e86\u6c14\uff0c\u6574\u4e2a\u8eab\u5b50\u50cf\u5c0f\u867e\u4e00\u6837\u5f13\u4e86\u8d77\u6765\uff0c\u53c8\u8f6c\u8eab\u7ed9\u9648\u9759\u8bf4\uff0c\u201c\u8981\u4e0d\u8981\u8ddf\u90c1\u840d\u8bf4\u4e00\u4e0b\uff0c\u5979\u94c1\u5b9a\u4e5f\u4f1a\u8ba4\u4e3a\u4f60\u8ddf\u90a3\u5bb6\u4f19\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u6211\u542c\u5c94\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4fe9\u4e00\u8d77\u90fd\u542c\u5c94\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d\u9648\u9759\u505a\u51fa\u4e00\u4e2a\u6df1\u4ee5\u4e3a\u7136\u7684\u8868\u60c5\uff0c\u603b\u4e4b\uff0c\u5f20\u606a\u662f\u4e2a\u4ee4\u5979\u8d39\u89e3\u7684\u5c0f\u7537\u4eba\uff0c\u4f46\u613f\u665a\u4e0a\u4e0d\u8981\u518d\u505a\u90a3\u6837\u7684\u68a6\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u65e2\u6ca1\u6709\u6253\u7535\u8bdd\u627e\u5f20\u606a\u518d\u6b21\u786e\u8ba4\u8fd9\u4e8b\uff0c\u4e5f\u6ca1\u6709\u5c06\u8fd9\u4e8b\u544a\u8bc9\u522b\u4eba\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e3a\u4ece\u5fb7\u4eea\u624b\u91cc\u83b7\u5f97\u90a3\u4e9b\u6280\u672f\u652f\u4ed8\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\uff0c\u8fd9\u8fd8\u662f\u56e0\u4e3a\u9526\u6e56\u8fd9\u4e24\u5e74\u6765\u4e0e\u5fb7\u4eea\u6709\u7740\u826f\u597d\u7684\u5408\u4f5c\u5173\u7cfb\uff0c\u6709\u7740\u76f8\u5f53\u4e00\u90e8\u5206\u7684\u5171\u540c\u5229\u76ca\uff0c\u8fd8\u6709\u9648\u4fe1\u751f\u8fd9\u6837\u7684\u5173\u952e\u4eba\u7269\u4ece\u4e2d\u7275\u7ebf\u642d\u6865\uff1b\u6362\u4f5c\u56fd\u5185\u5176\u4ed6\u4f01\u4e1a\uff0c\u5c31\u7b97\u820d\u5f97\u62ff\u51fa\u8fd9\u7b14\u5de8\u8d44\u51fa\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8ba9\u5fb7\u4eea\u52a8\u5fc3\u8f6c\u8ba9\u76f8\u5173\u6280\u672f\u3002</p><p>\u3000\u3000\u5f20\u606a\u7adf\u7136\u7a81\u7136\u8bf4\u8981\u5c06\u8fd9\u4e9b\u6280\u672f\u7edf\u7edf\u5411\u79d1\u738b\u9ad8\u79d1\u516c\u5f00\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7684\u786e\u8ba9\u4eba\u96be\u4ee5\u7f6e\u4fe1\u3002</p><p>\u3000\u300011\u67087\u65e5\uff0c\u4e00\u5207\u90fd\u663e\u5f97\u5f88\u5e73\u9759\uff0c\u5218\u660e\u8f89\u6253\u6765\u7535\u8bdd\uff0c\u4ed6\u4ece\u4eca\u5929\u8d77\u5df2\u7ecf\u6b63\u5f0f\u4ece\u79d1\u738b\u79bb\u804c\u4e86\u3002\u4e0a\u5348\uff0c\u8c22\u5251\u5357\u76f4\u63a5\u4ece\u91d1\u5c71\u4e58\u98de\u673a\u98de\u62b5\u5317\u4eac\uff0c\u90c1\u840d\u4e5f\u4ece\u6d77\u5dde\u98de\u62b5\u5317\u4eac\u3002\u90c1\u840d\u62c5\u4efb\u79d1\u738b\u9ad8\u79d1\u603b\u88c1\u4e4b\u540e\uff0c\u4e0d\u518d\u79d1\u738b\u7535\u5668\u62c5\u4efb\u4ec0\u4e48\u804c\u52a1\uff0c\u5979\u8d76\u8fc7\u6765\uff0c\u8bf4\u662f\u8981\u518d\u4e00\u6b21\u611f\u53d7\u4e00\u4e0b\u592e\u89c6\u6807\u738b\u5e7f\u544a\u7ade\u6807\u65f6\u70ed\u8840\u6cb8\u817e\u7684\u573a\u9762\uff0c\u8fd8\u8981\u4e13\u7a0b\u62dc\u8bbf\u8054\u4fe1\u4e0e\u90ae\u7535\u90e8\u7684\u9ad8\u5c42\u3002</p><p>\u3000\u3000\u5728\u673a\u573a\u7ed9\u90c1\u840d\u63a5\u673a\u65f6\uff0c\u9648\u9759\u8bd5\u63a2\u6027\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u8981\u662f\u80fd\u4ece\u9526\u6e56\u83b7\u5f97\u4ed6\u4eec\u4ece\u5fb7\u4eea\u8d2d\u4e70\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u79d1\u738b\u9ad8\u79d1\u80fd\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u90c1\u840d\u76f4\u63a5\u5c31\u95ee\u9053\uff1a\u201c\u6ca1\u53d1\u70e7\u5427\uff0c\u8fd9\u4e8b\u60f3\u90fd\u4e0d\u7528\u53bb\u60f3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5982\u679c\u5462\uff1f\u201d\u8c22\u5b50\u5609\u8ffd\u95ee\u9053\u3002</p><p>\u3000\u3000\u90c1\u840d\u60f3\u76f4\u63a5\u5c06\u8fd9\u4e2a\u95ee\u9898\u7ed9\u5ffd\u7565\u6389\uff0c\u53c8\u7f20\u4e0d\u8fc7\u8c22\u5b50\u5609\u671f\u5f85\u7684\u773c\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u771f\u80fd\u8fd9\u6837\uff0c\u6211\u4eec\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u5c06\u6574\u4e2a\u79d1\u738b\u90fd\u5e76\u5230\u79d1\u738b\u9ad8\u79d1\u7684\u65d7\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u8c22\u5b50\u5609\u505a\u51fa\u4e00\u4ed8\u604d\u7136\u5927\u609f\u7684\u6a21\u6837\uff0c\u8ddf\u9648\u9759\u6324\u7709\u5f04\u773c\uff0c\u6402\u7740\u5979\u7684\u8116\u5b50\u8bf4\u9053\uff0c\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u8fd9\u4e48\u505a\u662f\u4e3a\u4e86\u6253\u51fb\u516d\u54e5\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u54ea\u6709\u8fd9\u4e48\u65e0\u5398\u5934\u7684\u7406\u7531\uff1f\u5979\u4e0e\u5b50\u5609\u6628\u591c\u90fd\u6ca1\u6709\u7761\u8e0f\u5b9e\uff0c\u5c31\u662f\u6ca1\u6709\u731c\u5230\u5f20\u606a\u7684\u610f\u56fe\u662f\u4ec0\u4e48\uff0c\u6216\u8bb8\u771f\u662f\u4e24\u4eba\u4e00\u8d77\u90fd\u542c\u5c94\u4e86\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u90c1\u840d\u83ab\u660e\u5176\u79d2\uff0c\u5f88\u96be\u5c06\u9648\u9759\u7684\u8bdd\u5f53\u6210\u6b63\u7ecf\u4e8b\u53bb\u601d\u8003\u3002</p><p>\u3000\u3000\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\uff0c\u8fd9\u4e00\u5929\u90fd\u6ca1\u6709\u4ec0\u4e48\u6d88\u606f\u4f20\u51fa\u6765\uff0c\u636e\u8bf4\u56f4\u5728\u7231\u8fbe\u96c6\u56e2\u5317\u4eac\u5206\u516c\u53f8\u697c\u5916\u7684\u8bb0\u8005\u65e0\u6570\uff0c\u4f46\u662f\u5f20\u606a\u7b49\u9526\u6e56\u9ad8\u5c42\u5f53\u5929\u90fd\u8f6c\u79fb\u5230\u522b\u7684\u5730\u65b9\u53bb\u4e86\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6d88\u606f\u4f20\u51fa\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u300011\u67088\u65e5\uff0c\u4e0e\u5f80\u5e74\u4e0d\u4e00\u6837\u7684\u5730\u65b9\uff0c\u4eca\u5e74\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7531\u592e\u89c6\u5206\u7ba1\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u526f\u53f0\u957f\u94b1\u6587\u4e1c\u4eb2\u81ea\u4e3b\u6301\uff0c\u867d\u7136\u6709\u7740\u79cd\u79cd\u4e25\u683c\u7684\u9650\u5236\uff0c\u4e09\u661f\u3001\u7d22\u5c3c\u8fd9\u4e9b\u5916\u8d44\u54c1\u724c\u4e5f\u662f\u9996\u6b21\u53c2\u4e0e\u8fd9\u6b21\u56fd\u5185\u7684\u5e7f\u544a\u76db\u4f1a\u3002</p><p>\u3000\u3000\u7b2c\u4e00\u5c4a\u6807\u738b\u4f01\u4e1a\u5b54\u5e9c\u5bb6\u9152\u5728\u5931\u53bb\u6807\u738b\u5149\u73af\u4e4b\u540e\u5e02\u573a\u70ed\u5ea6\u8fc5\u901f\u9000\u53bb\uff0c\u7b2c\u4e8c\u5c4a\u6807\u738b\u4f01\u4e1a\u7231\u8fbe\u7535\u5b50\u4f9d\u65e7\u98ce\u5149\u65e0\u9650\uff0c\u7b2c\u4e09\u5c4a\u6807\u738b\u4f01\u4e1a\u79d1\u738b\u7535\u5668\u5374\u8fde\u4e00\u5e74\u7684\u98ce\u5149\u90fd\u6ca1\u6709\u4eab\u53d7\u5230\u5c31\u9677\u5165\u5de8\u4e8f\u7684\u9634\u4e91\u3002\u4e09\u5c4a\u6807\u738b\u4f01\u4e1a\u4e09\u79cd\u4e0d\u540c\u7684\u7ed3\u5c40\uff0c\u4fc3\u4f7f\u5a92\u4f53\u4e0e\u516c\u4f17\u5bf9\u592e\u89c6\u6807\u738b\u6982\u5ff5\u8fdb\u884c\u53cd\u601d\uff0c\u4e5f\u6709\u4e00\u4e9b\u5b66\u8005\u9010\u6e10\u8ba4\u8bc6\u5230\u54c1\u724c\u6025\u5267\u6269\u5f20\u5bfc\u81f4\u4e0e\u751f\u4ea7\u3001\u7ba1\u7406\u3001\u7269\u6d41\u7b49\u73af\u8282\u7684\u4e25\u91cd\u8131\u8282\uff0c\u5bf9\u4f01\u4e1a\u7684\u53d1\u5c55\u6709\u4e00\u79cd\u62d4\u82d7\u52a9\u957f\u7684\u5371\u5bb3\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u53cd\u601d\u5982\u4f55\uff0c\u592e\u89c6\u8fd9\u5c4a\u7684\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u8fd8\u662f\u518d\u4e00\u6b21\u70ed\u70ed\u95f9\u95f9\u7684\u5f00\u9523\u4e86\uff0c\u524d\u6765\u53c2\u52a0\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u4f01\u4e1a\u4ee3\u8868\u6570\u4ee5\u5343\u8ba1\uff0c\u6bd4\u524d\u4e09\u5c4a\u7684\u89c4\u6a21\u66f4\u5927\uff0c\u62a5\u540d\u65f6\u5c31\u7ecf\u8fc7\u4e86\u4e00\u8f6e\u7b5b\u9009\u3002</p><p>\u3000\u3000\u53d7\u5230\u5a92\u4f53\u8ffd\u6367\u7684\u4e0d\u662f\u4e0a\u5c4a\u6807\u738b\u4f01\u4e1a\u79d1\u738b\u7535\u5668\uff0c\u800c\u7b2c\u4e8c\u5c4a\u6807\u738b\u4f01\u4e1a\u3001\u8fd9\u4e00\u5c4a\u6807\u738b\u5e7f\u544a\u65f6\u6bb5\u6700\u6709\u529b\u4e5f\u662f\u6700\u79ef\u6781\u7684\u7ade\u593a\u8005\u7231\u8fbe\u96c6\u56e2\u3002</p><p>\u3000\u3000\u4e25\u683c\u8bf4\u6765\uff0c\u7231\u8fbe\u96c6\u56e2\u4e0e\u7b2c\u4e8c\u5c4a\u6807\u738b\u4f01\u4e1a\u7231\u8fbe\u7535\u5b50\u662f\u622a\u7136\u4e0d\u540c\u7684\uff0c\u7231\u8fbe\u7535\u5b50\u4e0e\u7231\u8fbe\u96c6\u56e2\u751a\u81f3\u5728\u8d22\u52a1\u4e0a\u6ca1\u6709\u76f4\u63a5\u5173\u8054\u7684\uff0c\u4f46\u662f\u7231\u8fbe\u5546\u6807\u6240\u6709\u6743\u5374\u662f\u5f52\u5c5e\u4e8e\u7231\u8fbe\u96c6\u56e2\u6240\u6709\uff0c\u7231\u8fbe\u7535\u5b50\u6bcf\u5e74\u9700\u4e3a\u6b64\u652f\u4ed8\u4e00\u5b9a\u7684\u8d39\u7528\uff0c\u8fd8\u8981\u72ec\u81ea\u627f\u62c5\u789f\u673a\u529f\u80fd\u6027\u5ba3\u4f20\u5e7f\u544a\u7684\u8d39\u7528\u3002</p><p>\u3000\u3000\u9648\u9759\u4ece\u8f66\u91cc\u4e0b\u6765\u6234\u7740\u58a8\u955c\uff0c\u8c22\u5251\u5357\u4e5f\u5c06\u58a8\u955c\u6234\u4e0a\uff0c\u7ed9\u5a92\u4f53\u8bb0\u8005\u56f4\u8ffd\u5835\u622a\u8fde\u73e0\u70ae\u5f0f\u7684\u95ee\u4ee5\u8f9b\u8fa3\u523a\u8033\u7684\u8bdd\u9898\uff0c\u603b\u8981\u7528\u58a8\u955c\u63a9\u9970\u4e00\u4e0b\u50f5\u786c\u7684\u3001\u5c34\u5c2c\u7684\u9762\u5bb9\u3002\u8c22\u5b50\u5609\u3001\u90c1\u840d\u7d22\u6027\u8bf4\u8981\u5728\u8f66\u5b50\u591a\u7ed5\u4e00\u5708\u518d\u4e0b\u8f66\u8fdb\u592e\u89c6\u5927\u95e8\uff0c\u663e\u7136\u662f\u4e0d\u60f3\u4e00\u8d77\u7ed9\u5a92\u4f53\u8bb0\u8005\u56f4\u5835\u5728\u95e8\u53e3\u3002</p><p>\u3000\u3000\u9762\u5bf9\u79d1\u738b\u5de8\u4e8f\u3001\u7ba1\u7406\u5c42\u6709\u4ec0\u4e48\u53cd\u601d\u3001\u660e\u5e74\u4f1a\u91c7\u53d6\u600e\u6837\u7684\u63aa\u65bd\u3001\u8981\u662f\u518d\u5ea6\u4ea7\u751f\u4e8f\u635f\u600e\u4e48\u529e\u3001\u4f1a\u4e0d\u4f1a\u4e3a\u53bb\u5e74\u7684\u5192\u8fdb\u7b56\u7565\u540e\u6094\u4e4b\u540e\u7c7b\u7684\u95ee\u9898\uff0c\u7684\u786e\u4f1a\u8ba9\u4eba\u611f\u5230\u5f88\u96be\u582a\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u9762\u5bf9\uff0c\u4e0d\u5f97\u4e0d\u8010\u5fc3\u7684\u53bb\u89e3\u91ca\uff0c\u6bd5\u7adf\u8fd9\u5e76\u4e0d\u662f\u5411\u5a92\u4f53\u89e3\u91ca\u4ec0\u4e48\uff0c\u800c\u662f\u8981\u901a\u8fc7\u5a92\u4f53\u5411\u7ecf\u9500\u5546\u3001\u516c\u4f17\u89e3\u91ca\u4eca\u5e74\u4e8f\u635f\u7684\u539f\u56e0\u5728\u4e8e\u5916\u90e8\u73af\u5883\uff0c\u79d1\u738b\u4e5f\u6709\u80fd\u529b\u5728\u660e\u5e74\u626d\u8f6c\u5c40\u52bf\u3002</p><p>\u3000\u3000\u76f4\u5230\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u6768\u4e91\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u51fa\u73b0\uff0c\u8bb0\u8005\u7684\u6ce8\u610f\u529b\u624d\u7ed9\u5438\u5f15\u8fc7\u53bb\uff0c\u9648\u9759\u4e5f\u6084\u7136\u62b9\u6389\u989d\u5934\u7684\u6c57\u6c34\uff0c\u5fc3\u60f3\u5f20\u606a\u8ba9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u90fd\u4eb2\u81ea\u51fa\u9762\uff0c\u5927\u6982\u662f\u5411\u5a92\u4f53\u575a\u5b9a\u7231\u8fbe\u8981\u593a\u8fd9\u5c4a\u6807\u738b\u7684\u4fe1\u5fc3\u5427\u3002</p><p>\u3000\u3000\u6ca1\u6709\u770b\u5230\u5f20\u606a\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u9999\u96ea\u6d77\u7684\u4eba\uff0c\u4ed6\u4eec\u5927\u6982\u4f1a\u8d81\u8bb0\u8005\u4e0d\u6ce8\u610f\u7684\u65f6\u5019\u5077\u5077\u6478\u6478\u7684\u8fdb\u573a\u5427\u3002\u4e09\u661f\u3001\u7d22\u5c3c\u7b49\u5916\u8d44\u54c1\u724c\u7684\u4f01\u4e1a\u4ee3\u8868\u8d70\u51fa\u592e\u89c6\u7684\u5927\u95e8\uff0c\u4e5f\u53d7\u5230\u8bb0\u8005\u7684\u70ed\u5207\u5173\u6ce8\uff0c\u9648\u9759\u4ed6\u4eec\u624d\u5f97\u4ee5\u4e58\u7535\u68af\u524d\u5f80\u7ade\u6807\u4f1a\u573a\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u8bb0\u8005\u4e0d\u5141\u8bb8\u8fdb\u5165\u7684\u7ade\u6807\u4f1a\u573a\uff0c\u4eba\u624d\u8f7b\u677e\u4e0b\u6765\uff0c\u8c22\u5b50\u5609\u4e0e\u90c1\u840d\u6ca1\u6709\u8bb0\u8005\u7ea0\u7f20\uff0c\u8fd8\u5148\u4e8e\u5979\u4eec\u8fdb\u4e86\u4f1a\u573a\u3002</p><p>\u3000\u3000\u4e0e\u4e0a\u4e00\u5c4a\u62db\u5546\u5927\u4f1a\u4e00\u6837\uff0c\u6700\u5148\u62ff\u51fa\u6765\u7ade\u6807\u7684\u5e7f\u544a\u65f6\u6bb5\uff0c\u8fd8\u662f\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e24\u4e2a\u8282\u76ee\u4e4b\u95f4\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u3002\u8fd9\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u9650\u5236\u5916\u8d44\u54c1\u724c\u53c2\u4e0e\uff0c\u7ade\u6807\u65b9\u5f0f\u8ddf\u53bb\u5e74\u4e00\u6837\uff0c\u4e5f\u662f\u91c7\u53d6\u6697\u6807\u4e0e\u660e\u6807\u76f8\u7ed3\u5408\u7684\u65b9\u5f0f\u3002\u5148\u91c7\u53d6\u6697\u6807\u6295\u9009\uff0c\u4f1a\u573a\u4e0a\u6709\u610f\u53c2\u52a0\u7ade\u593a\u7684\u5382\u5546\u5c06\u62a5\u4ef7\u5199\u5728\u4fe1\u5c01\u91cc\uff0c\u62a5\u4ef7\u6700\u9ad8\u7684\u56db\u5341\u5bb6\u5382\u5546\u518d\u53c2\u52a0\u660e\u6807\u9636\u6bb5\u7684\u7ade\u4ef7\uff0c\u6697\u6807\u5165\u56f4\u4ef7\u7801\u5373\u4e3a\u660e\u6807\u5e95\u4ef7\u3002</p><p>\u3000\u3000\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\u540c\u65f6\u5165\u56f4\uff0c\u4e00\u70b9\u90fd\u4e0d\u4ee4\u9648\u9759\u5947\u602a\uff0c\u5979\u6653\u5f97\u7231\u8fbe\u8981\u63a9\u62a4\u9999\u96ea\u6d77\u7ade\u593a\u8fd9\u5c4a\u7684\u6807\u738b\uff0c\u53ea\u662f\u4e0d\u6653\u5f97\u7231\u8fbe\u5728\u7ade\u6807\u65f6\u4f1a\u600e\u4e48\u63a9\u62a4\uff1f</p><p>\u3000\u3000\u56db\u5341\u5bb6\u5165\u56f4\u7684\u5382\u5546\u4ee3\u8868\u79fb\u5230\u4f1a\u573a\u6b63\u4e2d\u592e\u7684\u533a\u57df\u53c2\u4e0e\u660e\u6807\u9636\u6bb5\u7684\u7ade\u4ef7\uff0c\u7231\u8fbe\u7684\u4ee3\u8868\u662f\u7231\u8fbe\u96c6\u56e2\u8fd0\u8425\u90e8\u603b\u88c1\u82cf\u6d25\u4e1c\uff0c\u9999\u96ea\u6d77\u7684\u4ee3\u8868\u662f\u9999\u96ea\u6d77\u7535\u5668\u96c6\u56e2\u603b\u88c1\u9676\u884c\u5065\u3002\u82cf\u6d25\u4e1c\u5750\u5728\u660e\u4ef7\u7ade\u6807\u533a\u57df\u7684\u4fa7\u540e\u4e00\u89d2\u4e0a\uff0c\u9676\u884c\u5065\u5750\u5728\u7ade\u62cd\u53f0\u5e95\u4e0b\u7684\u4f4d\u5b50\u4e0a\uff0c\u4e24\u4eba\u4e00\u524d\u4e00\u540e\uff0c\u79bb\u5f97\u8001\u8fdc\uff0c\u9648\u9759\u4e00\u65f6\u60f3\u4e0d\u660e\u767d\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u5750\u3002\u5b50\u5609\u62ff\u624b\u6b63\u6345\u5979\u7684\u8170\uff0c\u8f6c\u8fc7\u5934\uff0c\u770b\u89c1\u5f20\u606a\u8fd9\u65f6\u5019\u624d\u59d7\u59d7\u6765\u8fdf\u7684\u8fdb\u573a\uff0c\u7adf\u7136\u7a7f\u7740\u7684\u662f\u4e00\u4ef6\u8fd0\u52a8\u670d\uff0c\u6c14\u5598\u5401\u5401\u7684\uff0c\u989d\u5934\u8fd8\u6709\u4e9b\u70ed\u6c57\uff0c\u597d\u50cf\u521a\u521a\u8dd1\u5b8c\u6b65\u8fc7\u6765\u3002\u8981\u4e0d\u662f\u4ed6\u624b\u91cc\u62ff\u7740\u5165\u573a\u7684\u5609\u5bbe\u8bc1\uff0c\u53ef\u80fd\u79bb\u4f1a\u573a\u5927\u95e8\u4e94\u7c73\u7684\u8303\u56f4\u90fd\u8fdb\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8981\u73a9\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u8c22\u5b50\u5609\u5c0f\u58f0\u95ee\u9648\u9759\uff0c\u201c\u9999\u96ea\u6d77\u5f88\u4f4e\u8c03\u554a\uff0c\u51e0\u4e4e\u5f15\u4e0d\u8d77\u522b\u4eba\u7684\u6ce8\u610f\uff1b\u53c2\u52a0\u660e\u4ef7\u7ade\u6807\u7684\u4f01\u4e1a\u4ee3\u8868\uff0c\u6ce8\u610f\u529b\u90fd\u5728\u82cf\u6d25\u4e1c\u8eab\u4e0a\uff0c\u8981\u89c2\u5bdf\u82cf\u6d25\u4e1c\u7684\u8868\u60c5\uff0c\u90fd\u8981\u56de\u8fc7\u5934\u6765\uff0c\u4f1a\u4e0d\u4f1a\u5c31\u56e0\u6b64\u5206\u6563\u4e86\u6ce8\u610f\u529b\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e5f\u8981\u73a9\u8fd9\u79cd\u96d5\u866b\u5c0f\u6280\u554a\uff01\u201d\u8c22\u5251\u5357\u8f7b\u58f0\u8bf4\u4e86\u4e00\u53e5\uff0c\u8bed\u6c14\u5012\u4e5f\u8bf4\u4e0d\u4e0a\u4e0d\u5c51\u6216\u8005\u8bbd\u523a\u3002</p><p>\u3000\u3000\u8fd8\u6253\u7b97\u6682\u65f6\u4e0d\u53bb\u60f3\u524d\u5929\u665a\u4e0a\u5f20\u606a\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u770b\u5230\u5f20\u606a\u8d70\u8fdb\u4f1a\u573a\uff0c\u9648\u9759\u7684\u5fc3\u53c8\u4e0d\u4e89\u6c14\u7684\u7830\u7830\u4e71\u8df3\u8d77\u6765\uff0c\u5dee\u4e0d\u591a\u662f\u9057\u5fd8\u5f88\u4e45\u7684\u611f\u89c9\uff0c\u89c9\u5f97\u81ea\u5df1\u5f88\u4e0d\u4e89\u6c14\uff0c\u5f3a\u8feb\u7740\u4e0d\u56de\u5934\u53bb\u770b\u5f20\u606a\uff0c\u5fc3\u91cc\u80e1\u601d\u4e71\u60f3\u7740\uff0c\u7ade\u4ef7\u5df2\u7ecf\u5f00\u59cb\uff0c\u4e3b\u6301\u7ade\u4ef7\u7684\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u526f\u53f0\u957f\u94b1\u6587\u4e1c\uff0c\u7ade\u6807\u5e95\u4ef7\u662f\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u6bd4\u53bb\u5e74\u7684\u7ade\u6807\u5e95\u4ef7\u7565\u4f4e\u3002\u9648\u9759\u5fc3\u7eea\u5f88\u4e71\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u542c\u6e05\u695a\u94b1\u6587\u4e1c\u524d\u9762\u5728\u8bf4\u4ec0\u4e48\uff0c\u53ea\u542c\u89c1\u4ed6\u5ba3\u5e03\u7ade\u4ef7\u5f00\u59cb\uff0c\u82cf\u6d25\u4e1c\u5c31\u76f4\u63a5\u4e3e\u724c\u558a\u9053\uff1a\u201c\u4e00\u4ebf\u4e94\u5343\u4e07\u2026\u2026\u201d</p><p>\u3000\u3000\u5168\u573a\u8f70\u7136\uff0c\u4e0d\u4ec5\u5916\u573a\u7684\u4f01\u4e1a\u4ee3\u8868\u90fd\u6fc0\u52a8\u7684\u7ad9\u8d77\u6765\uff0c\u660e\u4ef7\u7ade\u6807\u533a\u57df\u5185\u7684\u4f01\u4e1a\u4ee3\u8868\u4e5f\u90fd\u60c5\u4e0d\u81ea\u7981\u7684\u7ad9\u8d77\u6765\u671d\u7a33\u5982\u6cf0\u5c71\u5750\u5728\u89d2\u843d\u91cc\u7684\u82cf\u6d25\u4e1c\u770b\u53bb\u3002\u8fde\u7ad9\u5728\u7ade\u62cd\u53f0\u524d\u7684\u94b1\u6587\u4e1c\u90fd\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\uff0c\u4ed6\u80af\u5b9a\u5c31\u6ca1\u6709\u60f3\u5230\u7b2c\u4e00\u6b21\u62a5\u4ef7\uff0c\u7231\u8fbe\u5c31\u76f4\u63a5\u5728\u5e95\u4ef7\u4e0a\u7ffb\u4e86\u6574\u5341\u500d\u3002</p><p>\u3000\u3000\u5982\u6b64\u7684\u6c14\u52bf\u6c79\u6c79\uff0c\u5982\u6b64\u7684\u52bf\u5728\u5fc5\u5f97\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e4b\u524d\u6709\u5fc3\u4e89\u4e00\u4e89\u6807\u738b\u7684\u4f01\u4e1a\u770b\u5230\u7231\u8fbe\u96c6\u56e2\u5982\u6b64\u6c14\u52bf\u6c79\u6c79\u7684\u59ff\u6001\uff0c\u90fd\u7981\u4e0d\u4f4f\u72b9\u8c6b\u8d77\u6765\u3002</p><p>\u3000\u3000\u4e00\u4ebf\u4e94\u5343\u4e07\u7684\u4ef7\u7801\uff0c\u4f3c\u4e4e\u8fd8\u80fd\u63a5\u53d7\uff0c\u4e0d\u7518\u5fc3\u7ed9\u7231\u8fbe\u8fd9\u4e48\u8f7b\u6613\u7684\u5c31\u5c06\u8fd9\u4e00\u5c4a\u7684\u6807\u738b\u62ff\u8fc7\u53bb\uff0c\u5750\u5728\u660e\u4ef7\u7ade\u6807\u533a\u57df\u5185\u7684\u4f01\u4e1a\u4ee3\u8868\u5fc3\u60c5\u590d\u6742\u5f97\u5f88\u3002\u53ef\u60dc\u7559\u7ed9\u4ed6\u4eec\u8003\u8651\u7684\u65f6\u95f4\u592a\u77ed\uff0c\u7231\u8fbe\u4e00\u6b65\u558a\u5230\u4f4d\u3001\u558a\u5230\u4e00\u4ebf\u4e94\u5343\u4e07\u7684\u62a5\u4ef7\u65b9\u5f0f\uff0c\u51fa\u4e4e\u4ed6\u4eec\u7684\u9884\u6599\uff0c\u4e0d\u5728\u9884\u6848\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u5750\u5728\u90a3\u91cc\uff0c\u53c8\u65e0\u6cd5\u7d27\u6025\u627e\u4eba\u5546\u91cf\uff0c\u591a\u5c11\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\u7684\u72fc\u72c8\u3002</p><p>\u3000\u3000\u94b1\u6587\u4e1c\u7ad9\u5728\u7ade\u62cd\u53f0\u524d\uff0c\u4e24\u6b21\u62a5\u4ef7\u540e\uff0c\u4ed6\u7684\u989d\u5934\u90fd\u7981\u4e0d\u4f4f\u8981\u6e17\u73b0\u6c57\u6c34\u6765\uff0c\u4ed6\u770b\u5230\u53f0\u4e0b\u6709\u4e9b\u4f01\u4e1a\u4ee3\u8868\u8822\u8822\u6b32\u52a8\u5374\u53c8\u77bb\u524d\u987e\u540e\uff0c\u8981\u662f\u4e00\u6b21\u62a5\u4ef7\u5c31\u8ba9\u7231\u8fbe\u96c6\u56e2\u8f7b\u6613\u7684\u5c06\u6807\u738b\u5e7f\u544a\u593a\u53bb\uff0c\u90a3\u4ed6\u8fd9\u6b21\u62a2\u7740\u8df3\u51fa\u6765\u4e3b\u6301\u6807\u738b\u5e7f\u544a\u7684\u7ade\u62cd\u4e0d\u5c31\u6210\u4e86\u4e00\u6b21\u7b11\u8bdd\uff1f\u4e4b\u524d\u7cbe\u5fc3\u51c6\u5907\u7684\u90a3\u4e9b\u718f\u67d3\u6c14\u6c1b\u7684\u5c0f\u624b\u6bb5\u3001\u5c0f\u5305\u8884\uff0c\u4f3c\u4e4e\u90fd\u6d3e\u4e0d\u4e0a\u7528\u573a\uff0c\u4ed6\u6068\u4e0d\u5f97\u5c06\u624b\u91cc\u7684\u5c0f\u69cc\u4e22\u7ed9\u522b\u4eba\u3002</p><p>\u3000\u3000\u8be5\u6b7b\u7684\uff0c\u8fd8\u662f\u9526\u6e56\u7684\u4ee3\u8868\u524d\u5929\u63d0\u51fa\u8981\u6c42\uff0c\u8bf4\u662f\u5e0c\u671b\u80fd\u6709\u4e00\u4e2a\u4efd\u91cf\u66f4\u91cd\u7684\u4eba\u7269\u6765\u4e3b\u6301\u6807\u738b\u5e7f\u544a\u7684\u7ade\u62cd\u3002</p><p>\u3000\u3000\u94b1\u6587\u4e1c\u5185\u5fc3\u5728\u714e\u71ac\uff0c\u53f0\u4e0b\u90a3\u4e9b\u4f01\u4e1a\u4ee3\u8868\u4e5f\u5728\u714e\u71ac\uff0c\u4e00\u4ebf\u4e94\u5343\u4e07\u7684\u4ef7\u7801\uff0c\u660e\u660e\u8fd8\u53ef\u4ee5\u63a5\u53d7\uff0c\u4f46\u731c\u4e0d\u900f\u7231\u8fbe\u96c6\u56e2\u7684\u5e95\u9650\u5728\u54ea\u91cc\uff0c\u53c8\u6015\u6b64\u65f6\u7684\u6fc0\u70c8\u7ade\u4ef7\u4f1a\u5c06\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u7684\u5e73\u5747\u4ef7\u7801\u90fd\u7092\u6210\u5929\u4ef7\uff0c\u6700\u7ec8\u7684\u7ed3\u5c40\u53ea\u53ef\u80fd\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u5f97\u5229\uff0c\u800c\u7ade\u6807\u7684\u4f01\u4e1a\u90fd\u8981\u4e3a\u6b64\u652f\u4ed8\u9ad8\u6602\u7684\u4ee3\u4ef7\u3002\u6216\u8005\u653e\u5f03\u5bf9\u6807\u738b\u7684\u5962\u5ff5\uff0c\u5927\u5bb6\u548c\u548c\u6c14\u6c14\u7684\u53bb\u5e73\u5206\u592e\u89c6\u5176\u4ed6\u7684\u5e7f\u544a\u8d44\u6e90\uff1f</p><p>\u3000\u3000\u7231\u8fbe\u7684\u5d1b\u8d77\uff0c\u4ec5\u4ec5\u662f\u5728\u789f\u673a\u8fd9\u4e00\u4e2a\u72ed\u5c0f\u7684\u4ea7\u4e1a\u9886\u57df\u62e5\u6709\u522b\u4eba\u65e0\u6cd5\u6297\u9006\u7684\u5f71\u54cd\u529b\uff0c\u4eca\u5929\u80fd\u5750\u5728\u8fd9\u4e2a\u5927\u5385\u91cc\u7684\u4f01\u4e1a\u4ee3\u8868\uff0c\u54ea\u4e00\u5bb6\u4f01\u4e1a\u4e0d\u662f\u5728\u884c\u4e1a\u5185\u5904\u4e8e\u6570\u4e00\u6570\u4e8c\u7684\u4f4d\u5b50\uff1f\u5373\u4f7f\u6682\u65f6\u8fd8\u4e0d\u662f\u884c\u4e1a\u5185\u7684\u9f99\u5934\u4f01\u4e1a\uff0c\u4e5f\u60f3\u501f\u7740\u592e\u89c6\u5e7f\u544a\u8d44\u6e90\u7684\u9b54\u529b\u53bb\u6210\u4e3a\u8ba9\u4e07\u4eba\u666f\u4ef0\u7684\u884c\u4e1a\u9f99\u5934\u3002\u4e00\u5e74\u53ea\u6709\u4e00\u6b21\u673a\u4f1a\uff0c\u8c01\u4e5f\u4e0d\u7518\u5fc3\u4e89\u4e5f\u4e0d\u4e89\u5c31\u653e\u5f03\u6389\u3002</p><p>\u3000\u3000\u201c\u4e00\u4ebf\u516d\u5343\u4e07\uff01\u201d\u5728\u94b1\u6587\u4e1c\u8981\u7b2c\u4e09\u6b21\u558a\u4ef7\u7684\u65f6\u5019\uff0c\u5c31\u5750\u5728\u7ade\u6807\u53f0\u4e0b\u7684\u9676\u884c\u5065\u4e3e\u724c\u62a5\u4ef7\u3002</p><p>\u3000\u3000\u9676\u884c\u5065\u7684\u62a5\u4ef7\uff0c\u5e26\u7ed9\u4f17\u4eba\u4e0d\u662f\u9707\u61be\uff0c\u800c\u662f\u7a81\u5982\u5176\u6765\u7684\u8f7b\u677e\uff0c\u4eff\u4f5b\u7231\u8fbe\u96c6\u56e2\u538b\u5728\u4f17\u4eba\u5fc3\u5934\u7684\u90a3\u5757\u5de8\u77f3\u7ed9\u642c\u8d70\uff1a\u7ec8\u4e8e\u6709\u4f01\u4e1a\u8df3\u51fa\u6765\u6b82\u51fb\u7231\u8fbe\u4e86\uff0c\u5374\u53c8\u5f00\u59cb\u5bfb\u601d\u4e3e\u77403\u53f7\u724c\u7684\u90a3\u4e2a\u4eba\u5230\u5e95\u4ee3\u8868\u54ea\u5bb6\u4f01\u4e1a\uff0c\u4f4e\u5934\u770b\u624b\u91cc\u4e34\u65f6\u53d1\u4e0b\u6765\u7684\u56db\u5341\u5bb6\u5165\u56f4\u4f01\u4e1a\u7684\u540d\u5355\u3002</p><p>\u3000\u3000\u9999\u96ea\u6d77\u3002</p><p>\u3000\u3000\u7edd\u5927\u591a\u6570\u4eba\u5bf9\u9999\u96ea\u6d77\u5e76\u4e0d\u964c\u751f\uff0c\u5728\u5bb6\u7535\u5236\u9020\u4e1a\uff0c\u9999\u96ea\u6d77\u66fe\u4e0e\u9752\u5c9b\u6d77\u5c14\u9f50\u540d\uff0c\u4f46\u662f\u9999\u96ea\u6d77\u5230\u4e5d\u4e09\u5e74\u4e4b\u540e\uff0c\u5c31\u6709\u4e9b\u540e\u7ee7\u65e0\u529b\u3002\u9752\u5c9b\u6d77\u5c14\u7684\u7535\u51b0\u7bb1\u4ea7\u91cf\u4ece\u4e5d\u4e09\u5e74\u7684\u4e09\u5341\u4e07\u53f0\u6fc0\u589e\u5230\u4e5d\u516d\u5e74\u7684\u4e00\u767e\u516d\u5341\u4e07\u53f0\uff0c\u800c\u9999\u96ea\u6d77\u7684\u4ea7\u91cf\u5374\u8fde\u7eed\u4e09\u5e74\u90fd\u672a\u80fd\u7a81\u7834\u4e09\u5341\u4e07\u53f0\uff0c\u803d\u6401\u4e86\u4e09\u5e74\uff0c\u4e0e\u9752\u5c9b\u6d77\u5c14\u7b97\u662f\u5929\u5dee\u5730\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u662f\u9996\u5148\u80fd\u60f3\u5230\u7684\u5370\u8c61\uff0c\u9999\u96ea\u6d77\u8fd9\u4e24\u5e74\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u5927\u7684\u5e02\u573a\u52a8\u4f5c\u6765\u5438\u5f15\u4f17\u4eba\u7684\u773c\u7403\uff0c\u5165\u56f4\u4f01\u4e1a\u540d\u5355\u4e0a\u4e5f\u6ca1\u6709\u8be6\u7ec6\u7684\u8d44\u6599\u4ecb\u7ecd\uff0c\u77ed\u65f6\u95f4\u91cc\u4e5f\u60f3\u4e0d\u5230\u592a\u591a\u7684\u4e1c\u897f\u3002</p><p>\u3000\u3000\u6709\u4eba\u9690\u7ea6\u80fd\u60f3\u5230\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u7535\u5b50\u4f3c\u4e4e\u6709\u7740\u5173\u8054\uff0c\u4f46\u662f\u9999\u96ea\u6d77\u7a81\u7136\u8df3\u51fa\u6765\u72d9\u51fb\u7231\u8fbe\u7684\u62a5\u4ef7\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u4e5f\u5f3a\u884c\u5236\u6b62\u4ed6\u4eec\u53bb\u5f80\u6df1\u91cc\u60f3\u3002\u5c31\u7b97\u6709\u4eba\u60f3\u5230\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u5173\u8054\uff0c\u4f46\u662f\u4ed6\u4eec\u65e0\u610f\u6807\u738b\u5e7f\u544a\uff0c\u81ea\u7136\u4f1a\u9009\u62e9\u6c89\u9ed8\uff0c\u5173\u952e\u662f\u90a3\u51e0\u5bb6\u6709\u610f\u67d3\u6307\u6807\u738b\u5e7f\u544a\u7684\u4f01\u4e1a\u4ee3\u8868\u8981\u8fc5\u901f\u9192\u609f\u8fc7\u6765\u624d\u884c\u3002</p><p>\u3000\u3000\u9999\u96ea\u6d77\u52a0\u4ef7\u4e00\u5343\u4e07\uff0c\u5c06\u62a5\u4ef7\u9661\u7eed\u53c8\u62ac\u9ad8\u4e86\u4e00\u4e2a\u53f0\u9636\uff0c\u5927\u5bb6\u90fd\u5728\u60f3\u8fd9\u5e94\u8be5\u8fdc\u8fdc\u6ca1\u6709\u8fbe\u5230\u7231\u8fbe\u7684\u5e95\u9650\u3002</p><p>\u3000\u3000\u5f53\u4e2d\u592e\u7535\u89c6\u53f0\u526f\u53f0\u957f\u94b1\u6587\u4e1c\u8fde\u7eed\u558a\u4ef7\u4e24\u6b21\u4e4b\u540e\uff0c\u5750\u5728\u660e\u6807\u7ade\u4ef7\u533a\u57df\u5185\u7684\u4f01\u4e1a\u4ee3\u8868\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u8f6c\u8fc7\u5934\u6765\u770b\u82cf\u6d25\u4e1c\u7684\u53cd\u5e94\u2026\u2026</p><p>\u3000\u3000\u201c\u679c\u7136\u662f\u8fd9\u6837\uff0c\u201d\u8c22\u5251\u5357\u6324\u7740\u7709\u5934\uff0c\u8f7b\u58f0\u8ddf\u8eab\u8fb9\u7684\u9648\u9759\u3001\u90c1\u840d\u3001\u5b50\u5609\u5206\u6790\u9053\uff0c\u201c\u5176\u4ed6\u4f01\u4e1a\u90fd\u5728\u4e3a\u53c2\u52a0\u8fd9\u5c4a\u62db\u5546\u5927\u4f1a\u800c\u5927\u8086\u5ba3\u626c\uff0c\u9999\u96ea\u6d77\u5374\u4e00\u76f4\u90fd\u4e0d\u9732\u58f0\u8272\uff0c\u9ebb\u75f9\u4e86\u6240\u6709\u4eba\u7684\u6ce8\u610f\u529b\u2026\u2026\u592e\u89c6\u600e\u4e48\u4f1a\u8ba9\u4e0d\u719f\u6089\u4e1a\u52a1\u7684\u94b1\u6587\u4e1c\u4e0a\u53f0\u4e3b\u6301\u7ade\u62cd\uff1f\u6362\u4f5c\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u4e3b\u4efb\u4e0a\u6765\u4e3b\u6301\uff0c\u9999\u96ea\u6d77\u62a5\u4ef7\u4e4b\u540e\uff0c\u5979\u9996\u5148\u4f1a\u70b9\u51fa\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u96c6\u56e2\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5c31\u7b97\u6ca1\u6709\u789f\u673a\u5382\u5546\u8df3\u51fa\u6765\u6405\u5c40\uff0c\u4e5f\u5f88\u53ef\u80fd\u6709\u5176\u4ed6\u4eba\u5fcd\u4e0d\u4f4f\u51fa\u624b\u72d9\u51fb\u9999\u96ea\u6d77\uff0c\u54ea\u91cc\u4f1a\u8ba9\u4ed6\u4eec\u8fd9\u4e48\u8f7b\u677e\uff1f\u73b0\u5728\uff0c\u592a\u665a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u4e0d\u5c51\u7684\u8bf4\u9053\uff1a\u201c\u9886\u5bfc\u6bcf\u5929\u90fd\u559d\u9152\u966a\u5c0f\u79d8\u8fd8\u6307\u5bfc\u5973\u6f14\u5458\u7684\u5de5\u4f5c\uff0c\u54ea\u6709\u65f6\u95f4\u518d\u53bb\u770b\u7ade\u6807\u4f01\u4e1a\u7684\u8d44\u6599\u554a\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u626f\u4e86\u626f\u5b50\u5609\u7684\u8863\u8896\uff0c\u8ba9\u5979\u6ce8\u610f\u5973\u5b69\u5b50\u7684\u8eab\u4efd\uff0c\u8fd9\u4e9b\u8bdd\u54ea\u91cc\u662f\u5e94\u8be5\u5979\u8bf4\u51fa\u53e3\u7684\uff1f</p><p>\u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u82cf\u6d25\u4e1c\u624b\u91cc\u62ff\u7740\u53f7\u724c\u6602\u9996\u770b\u7740\u7ad9\u5728\u7ade\u62cd\u53f0\u4e0a\u7684\u94b1\u6587\u4e1c\uff0c\u4f01\u4e1a\u4ee3\u8868\u4e0e\u94b1\u6587\u4e1c\u90fd\u76ef\u7740\u82cf\u6d25\u4e1c\u4e0e\u4ed6\u624b\u91cc\u7684\u53f7\u724c\uff0c\u6ca1\u6709\u4eba\u4f1a\u6000\u7591\u82cf\u6d25\u4e1c\u5728\u6700\u540e\u4e00\u523b\u4f1a\u679c\u65ad\u4e3e\u8d77\u624b\u4e2d\u7684\u53f7\u724c\uff0c\u94b1\u6587\u4e1c\u4e5f\u4e0d\u6000\u7591\uff0c\u5c31\u7b97\u4ed6\u7b2c\u4e09\u6b21\u558a\u4ef7\uff0c\u4e3e\u7740\u5c0f\u69cc\u7f13\u6162\u7838\u4e0b\u53bb\u7684\u77ac\u95f4\u4e5f\u6ca1\u6709\u6000\u7591\uff0c\u76f4\u5230\u7ade\u62cd\u69cc\u53d1\u51fa\u6e05\u8106\u7684\u54cd\u58f0\uff0c\u4f17\u4eba\u624d\u7ed9\u60ca\u9192\u4e86\u4f3c\u7684\u3002</p><p>\u3000\u3000\u8fd9\u5c4a\u5e7f\u544a\u6807\u738b\u5c31\u8fd9\u6837\u53ea\u7ecf\u8fc7\u4e24\u8f6e\u7ade\u4ef7\u5c31\u843d\u5165\u9999\u96ea\u6d77\u7684\u56ca\u4e2d\u3002</p><p>\u3000\u3000\u94b1\u6587\u4e1c\u4f3c\u4e4e\u4e5f\u5fd8\u4e86\u6309\u7167\u6b63\u5e38\u7684\u7a0b\u5e8f\u8981\u5f53\u4f17\u5ba3\u5e03\u9999\u96ea\u6d77\u662f\u8fd9\u5c4a\u592e\u89c6\u6807\u738b\u5e7f\u544a\u7684\u5f97\u4e3b\uff0c\u4ed6\u72fc\u72c8\u7684\u8d70\u4e0b\u7ade\u62cd\u53f0\uff0c\u627e\u4eba\u8981\u9999\u96ea\u6d77\u7684\u4f01\u4e1a\u8d44\u6599\uff0c\u5168\u573a\u4f3c\u4e4e\u4e5f\u5fd8\u4e86\u8981\u7ed9\u521a\u521a\u7ade\u62cd\u5f97\u592e\u89c6\u6807\u738b\u5e7f\u544a\u7684\u9999\u96ea\u6d77\u4f01\u4e1a\u4ee3\u8868\u4ee5\u70ed\u70c8\u7684\u638c\u58f0\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6709\u6ce8\u610f\u5230\u5f20\u606a\u4e2d\u9014\u8fdb\u573a\uff0c\u8fd9\u65f6\u5019\u5fcd\u4e0d\u4f4f\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4ed6\u53cc\u624b\u62b1\u5728\u80f8\u524d\uff0c\u5634\u89d2\u6302\u7740\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u4f3c\u4e4e\u65e9\u5c31\u9884\u6599\u5230\u8fd9\u6837\u7684\u5c40\u9762\u3002\u4ed6\u5728\u60f3\uff0c\u5c31\u7b97\u6709\u4eba\u53ca\u65f6\u53cd\u5e94\u8fc7\u6765\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u54ea\u5bb6\u4f01\u4e1a\u80fd\u7ecf\u5f97\u4f4f\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\u7684\u5939\u51fb\uff0c\u6700\u7ec8\u4e0d\u8fc7\u662f\u8ba9\u9999\u96ea\u6d77\u6216\u8005\u7231\u8fbe\u4e3a\u6807\u738b\u5e7f\u544a\u591a\u652f\u4ed8\u51e0\u5343\u4e07\u7684\u4ee3\u4ef7\u7f62\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u5374\u6108\u52a0\u660e\u767d\u5f20\u606a\u5728\u706b\u8f66\u4e0a\u8bf4\u7684\u90a3\u53e5\u8bdd\uff1a\u201c\u9526\u6e56\u8fd9\u6b21\u9009\u62e9\u7684\u5bf9\u624b\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u2026\u2026\u201d\u9999\u96ea\u6d77\u4ee5\u4e00\u4ebf\u516d\u5343\u4e07\u7684\u4ef7\u7801\u5c31\u8f7b\u677e\u5c06\u6807\u738b\u5e7f\u544a\u6536\u5165\u56ca\u4e2d\uff0c\u6240\u8c13\u7684\u6807\u738b\u5e7f\u544a\uff0c\u5e76\u4e0d\u662f\u4ec5\u4ec5\u6307\u65b0\u95fb\u8054\u64ad\u540e\u7684\u7b2c\u4e00\u4e2a5\u79d2\u949f\u5e7f\u544a\uff0c\u53ef\u4ee5\u8bf4\u662f\u4e00\u4e2a\u5e7f\u544a\u5957\u9910\uff0c\u81f3\u5c11\u8981\u6bd4\u7167\u524d\u4e09\u5c4a\u6807\u738b\u4f01\u4e1a\u6240\u4eab\u53d7\u5230\u7684\u4f18\u60e0\u5f85\u9047\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u77ed\u6682\u7684\u6df7\u4e71\uff0c\u94b1\u6587\u4e1c\u518d\u6b21\u8d70\u5230\u7ade\u62cd\u53f0\u4e0a\uff0c\u5ba3\u5e03\u9999\u96ea\u6d77\u4e3a\u8fd9\u5c4a\u7684\u6807\u738b\u5e7f\u544a\u7684\u7ade\u6807\u5f97\u4e3b\uff0c\u9676\u884c\u5065\u7ad9\u8d77\u6765\u63a5\u53d7\u5168\u573a\u8f70\u9e23\u822c\u7684\u638c\u58f0\uff0c\u63a5\u4e0b\u6765\u5176\u4ed6\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u680f\u76ee\u4e4b\u95f4\u7684\u5341\u4e8c\u67615\u79d2\u7684\u7ade\u593a\u5012\u4e5f\u6ca1\u6709\u56e0\u4e3a\u4e00\u5f00\u59cb\u7684\u60ca\u8be7\u800c\u7f3a\u4e4f\u8db3\u591f\u7684\u70ed\u60c5\uff0c\u7531\u4e8e\u8fd9\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u9650\u5236\u540c\u884c\u4e1a\u4e0d\u8d85\u8fc7\u4e09\u5bb6\u4f01\u4e1a\u4e0a\u699c\uff0c\u6839\u636e\u884c\u4e1a\u5185\u7ade\u4e89\u6fc0\u70c8\u7a0b\u5ea6\u7684\u4e0d\u540c\uff0c\u5206\u522b\u7ade\u51fa\u516d\u5343\u4e07\u5230\u4e00\u4ebf\u56db\u5343\u4e07\u7684\u9ad8\u4ef7\u3002</p><p>\u3000\u3000\u51fa\u4e4e\u4f17\u4eba\u610f\u6599\u7684\uff0c\u82cf\u6d25\u4e1c\u9664\u4e86\u7b2c\u4e00\u6b21\u4e3e\u724c\u4e4b\u5916\uff0c\u4e4b\u524d\u5c31\u4ece\u5934\u9ed8\u5750\u5230\u5c3e\uff0c\u6ca1\u6709\u518d\u4e3e\u724c\u3002\u5f53\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u96c6\u56e2\u4e4b\u95f4\u7684\u8054\u7cfb\u7ed9\u6709\u5fc3\u4eba\u6316\u6398\u51fa\u6765\u5e76\u4f20\u64ad\u5f00\uff0c\u53c2\u4e0e\u62db\u5546\u5927\u4f1a\u4f01\u4e1a\u4ee3\u8868\u624d\u604d\u7136\u5927\u609f\uff0c\u7231\u8fbe\u96c6\u56e2\u6240\u6709\u7684\u884c\u4e3a\u4e0d\u8fc7\u662f\u4fdd\u9999\u96ea\u6d77\u5750\u4e0a\u8fd9\u5c4a\u592e\u89c6\u6807\u738b\u7684\u5b9d\u5ea7\u3002</p><p>\u3000\u3000\u9996\u8f6e\u5e7f\u544a\u8d44\u6e90\u7ade\u6807\u7ed3\u675f\u4e4b\u540e\uff0c\u6709\u7b80\u77ed\u7684\u4f11\u606f\uff0c\u63a5\u4e0b\u6765\u7684\u7ade\u4ef7\u7a0b\u5e8f\u4f5c\u4e86\u4e9b\u8c03\u6574\uff0c\u592e\u89c6\u5c06\u6b21\u8981\u65f6\u6bb5\u7684\u5e7f\u544a\u8d44\u6e90\u5148\u62ff\u51fa\u6765\u7ade\u4ef7\uff0c\u5c06\u4e03\u70b9\u949f\u5230\u4e5d\u70b9\u949f\u7684\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u4e0e\u91cd\u8981\u8282\u76ee\u51a0\u540d\u6743\u7684\u7ade\u6807\u653e\u5230\u4e0b\u5348\u4e3e\u884c\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8fd9\u65f6\u5019\u5c06\u7ade\u6807\u53f7\u724c\u4ea4\u7ed9\u7231\u8fbe\u96c6\u56e2\u8fd0\u8425\u90e8\u65d7\u4e0b\u5e02\u573a\u603b\u76d1\u53ca\u9500\u552e\u516c\u53f8\u603b\u88c1\u6768\u4e91\u624b\u4e2d\uff0c\u6574\u4e2a\u4e0a\u5348\uff0c\u6768\u4e91\u4e24\u6b21\u4e3e\u724c\uff0c\u4ee5\u4e0d\u5230\u4e00\u5343\u4e24\u767e\u4e07\u7684\u4ee3\u4ef7\u62ff\u4e0b\u4e24\u6761\u5e7f\u544a\uff0c\u4e09\u661f\u4e3e\u724c\u4e09\u6b21\uff0c\u6210\u5de7\u4e00\u6b21\uff0c\u60f3\u5fc5\u6709\u610f\u72d9\u51fb\u5916\u8d44\u54c1\u724c\u7684\u4f01\u4e1a\u4e0d\u6b62\u7231\u8fbe\u4e00\u5bb6\uff0c\u6210\u529f\u7684\u4e00\u6b21\u7ade\u62cd\u4ef7\u7ed9\u62ac\u9ad8\u5230\u516b\u767e\u4e07\u624d\u5f97\u4ee5\u6210\u4ea4\uff0c\u4f5c\u4e3a\u5916\u8d44\u54c1\u724c\uff0c\u8fd8\u8981\u4e3a\u6b21\u8981\u5e7f\u544a\u65f6\u6bb5\u591a\u652f\u4ed830\uff05\u7684\u6ea2\u4ef7\u3002</p><p>\u3000\u3000\u4e0a\u5348\u7ed9\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\u8054\u624b\u6405\u548c\u4e86\u4e00\u4e0b\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6380\u8d77\u591a\u5927\u7684\u9ad8\u6f6e\u6765\uff0c\u592e\u89c6\u679c\u65ad\u7684\u5c06\u665a\u95f4\u9ec4\u91d1\u65f6\u6bb5\u7684\u5e7f\u544a\u8d44\u6e90\u8c03\u6574\u5230\u4e0b\u5348\u7ade\u62cd\uff0c\u4e5f\u662f\u671f\u671b\u597d\u597d\u8c03\u6574\u4e00\u4e0b\uff0c\u80fd\u5728\u4e0b\u5348\u6380\u8d77\u9ad8\u6f6e\u6765\u3002</p><p>\u3000\u3000\u8fd9\u662f\u573a\u5185\u4f01\u4e1a\u4ee3\u8868\u7684\u611f\u53d7\uff0c\u4f46\u5bf9\u4e8e\u56f4\u62e5\u5728\u573a\u5916\u7684\u5a92\u4f53\u8bb0\u8005\u800c\u8a00\uff0c\u611f\u53d7\u5374\u662f\u4e0d\u540c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u624d\u53d1\u73b0\u4e4b\u524d\u5bf9\u9999\u96ea\u6d77\u7684\u62a5\u9053\u8fd1\u4e4e\u4e8e\u65e0\uff0c\u90a3\u5c31\u610f\u5473\u7740\u6709\u66f4\u591a\u7684\u65b0\u95fb\u7206\u70b9\u53ef\u4ee5\u6316\u6398\uff0c\u800c\u7231\u8fbe\u96c6\u56e2\u518d\u6b21\u653e\u5f03\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u7684\u7ade\u593a\uff0c\u4e0e\u5a92\u4f53\u4e4b\u524d\u7684\u9884\u6599\uff0c\u7ed3\u5c40\u76f8\u5dee\u592a\u8fdc\uff0c\u65e0\u7591\u4e5f\u52fe\u5f15\u5f97\u8bb0\u8005\u4eec\u7684\u6781\u5927\u5174\u8da3\uff0c\u4e09\u5c4a\u6807\u738b\u4f01\u4e1a\u4e2d\uff0c\u7231\u8fbe\u662f\u60df\u4e00\u4e00\u5bb6\u4fdd\u6301\u9ad8\u76c8\u5229\u7684\u4f01\u4e1a\uff0c\u4ed6\u4eec\u7684\u7ecf\u8425\u7b56\u7565\u4e0e\u601d\u60f3\uff0c\u81ea\u7136\u503c\u5f97\u5173\u6ce8\uff1b\u7279\u522b\u662f\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u96c6\u56e2\u4e4b\u95f4\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\u5173\u8054\uff0c\u66f4\u8ba9\u4eba\u89c9\u5f97\u6709\u5f88\u591a\u7684\u6545\u4e8b\u53ef\u4ee5\u6316\u6398\u3002</p><p>\u3000\u3000\u9676\u884c\u5065\u4e0e\u53f6\u5c0f\u6850\u4ee5\u53ca\u5176\u4ed6\u5341\u4e09\u67615\u79d2\u5e7f\u544a\u7684\u7ade\u5f97\u4f01\u4e1a\u4ee3\u8868\u90fd\u7559\u5728\u4f1a\u573a\u5185\u63a5\u53d7\u4e2d\u592e\u7535\u89c6\u53f0\u7b80\u77ed\u7684\u8bbf\u95ee\uff0c\u82cf\u6d25\u4e1c\u3001\u6768\u4e91\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u5219\u968f\u7740\u5176\u4ed6\u4f01\u4e1a\u4ee3\u8868\u5148\u8d70\u51fa\u4f1a\u573a\u3002\u5f53\u7136\uff0c\u4ed6\u4eec\u4e5f\u8d70\u4e0d\u51fa\u4f1a\u573a\u591a\u8fdc\uff0c\u5c31\u7ed9\u8702\u7fa4\u4e00\u6837\u7684\u8bb0\u8005\u56f4\u4e0a\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4eec\u6709\u5f88\u591a\u95ee\u9898\u8981\u95ee\uff0c\u201d\u82cf\u6d25\u4e1c\u9762\u5bf9\u65e0\u6570\u652f\u9012\u5230\u8eab\u524d\u7684\u91c7\u8bbf\u8bdd\u7b52\u3001\u5f55\u97f3\u7b14\uff0c\u505a\u4e86\u4e2a\u624b\u52bf\u8ba9\u8bb0\u8005\u4eec\u7a0d\u5b89\u6bcb\u8e81\uff0c\u201c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4e0e\u7231\u8fbe\u96c6\u56e2\u5728\u524d\u5929\u591c\u91cc\u7d27\u6025\u505a\u51fa\u4e00\u9879\u91cd\u8981\u7684\u51b3\u5b9a\uff0c\u5e0c\u671b\u7c4d\u6b64\u673a\u4f1a\u8bf7\u8bb0\u8005\u670b\u53cb\u4eec\u5e2e\u6211\u4eec\u4f20\u8fbe\u51fa\u53bb\uff1a\u6211\u4eec\u51b3\u5b9a\u5c06\u4eca\u5e74\u56db\u6708\u4efd\u82b1\u8d39\u5de8\u8d44\u4ece\u5fb7\u4eea\u624b\u4e2d\u8d2d\u5f97\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u65e0\u507f\u5411\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u56ed\u533a\u5185\u7684\u4f01\u4e1a\u516c\u5f00\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8bdd\u58f0\u521a\u843d\uff0c\u4f1a\u573a\u5185\u5916\u987f\u65f6\u54d1\u96c0\u65e0\u58f0\uff0c\u9677\u5165\u5947\u5999\u800c\u8be7\u7136\u7684\u6c89\u9ed8\u4e4b\u4e2d\uff0c\u5373\u4f7f\u6ca1\u6709\u542c\u6e05\u82cf\u6d25\u4e1c\u8bf4\u8bdd\u7684\u4eba\uff0c\u4e5f\u7531\u4e8e\u7a81\u5982\u5176\u6765\u7684\u5b89\u9759\u800c\u4e0d\u7531\u81ea\u4e3b\u7684\u95ed\u4e0a\u5634\u3001\u8f6c\u8fc7\u5934\u6765\u3002</p></article>"}}}]);
