(window.webpackJsonp=window.webpackJsonp||[]).push([[680],{703:function(p){p.exports={chapterTitle:null,title:"\u7b2c523\u7ae0 \u7a7a\u4e2d\u5c0f\u59d0",content:"<article><h2>\u7b2c523\u7ae0 \u7a7a\u4e2d\u5c0f\u59d0</h2><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u8bbe\u6709\u5206\u516c\u53f8\uff0c\u5f20\u606a\u4ed6\u4eec\u4e34\u65f6\u51b3\u5b9a\u5230\u5317\u4eac\u6765\uff0c\u5206\u516c\u53f8\u7ecf\u7406\u59da\u575a\u62c9\u53f8\u673a\u5f00\u4e86\u4e00\u8f86\u5546\u52a1\u8f66\u8d76\u5230\u673a\u573a\u8fce\u63a5\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8ddf\u4ed6\u7684\u52a9\u7406\u8fde\u7eed\u5750\u4e86\u4e24\u73ed\u98de\u673a\uff0c\u5728\u98de\u884c\u9014\u4e2d\u8fd8\u4e0d\u5f97\u7a0d\u6b47\uff0c\u6709\u4e9b\u8033\u9e23\u76ee\u7729\uff0c\u770b\u89c1\u7a7a\u95f4\u72ed\u7a84\u7684\u5546\u52a1\u8f66\u5c31\u5934\u75bc\uff1b\u5546\u52a1\u8f66\u8fd8\u6709\u4e9b\u6f0f\u6cb9\uff0c\u8f66\u53a2\u91cc\u5f25\u6f2b\u6709\u9690\u7ea6\u7684\u6c7d\u6cb9\u5473\uff0c\u6700\u8ba9\u4eba\u53d7\u4e0d\u4e86\uff0c\u82cf\u6d25\u4e1c\u575a\u6301\u8981\u5750\u673a\u573a\u5927\u5df4\u53bb\u5e02\u533a\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u8ddf\u7740\u82cf\u6d25\u4e1c\u5750\u673a\u573a\u5927\u5df4\uff0c\u4ed6\u4eec\u9519\u8fc7\u4e00\u73ed\u8f66\uff0c\u8fd9\u73ed\u8f66\u4e0a\u7684\u4eba\u5c31\u5f88\u5c11\u3002\u5f20\u606a\u62ff\u4e86\u4e00\u4efd\u5317\u4eac\u5f53\u5730\u7684\u62a5\u7eb8\uff0c\u5750\u5728\u4e2d\u95f4\u9760\u8fc7\u9053\u7684\u4f4d\u5b50\uff0c\u5916\u9762\u5929\u8272\u5df2\u9ed1\uff0c\u501f\u7740\u8f66\u9876\u706f\u5149\u6b63\u8981\u770b\u62a5\u7eb8\u7684\u65f6\u5019\uff0c\u611f\u89c9\u524d\u9762\u5149\u7ebf\u4e00\u6697\uff0c\u62ac\u5934\u770b\u89c1\u51e0\u540d\u7a7f\u7740\u7edb\u7d2b\u8272\u5236\u670d\u7684\u7a7a\u4e2d\u5c0f\u59d0\u8d70\u8fdb\u6765\uff0c\u538b\u7740\u819d\u7684\u88d9\u6446\u4e0b\u662f\u9ad8\u5f39\u4eae\u4e1d\u7684\u88e4\u889c\uff0c\u7ea4\u7ec6\u4fee\u957f\u7684\u5c0f\u817f\u90fd\u5341\u5206\u7684\u6da6\u76ee\u517b\u773c\uff0c\u7a7a\u4e58\u5236\u670d\u662f\u77ed\u895f\u6536\u8170\u7684\u90a3\u79cd\uff0c\u5c06\u8170\u80a2\u886c\u5f97\u5a09\u5a77\u6709\u59ff\u3002\u8fd9\u5e74\u5934\uff0c\u822a\u7a7a\u516c\u53f8\u90fd\u80fd\u6311\u9009\u5230\u9ad8\u54c1\u8d28\u7684\u7f8e\u5973\uff0c\u51e0\u4e2a\u5973\u5b69\u5b50\u7709\u76ee\u6e05\u79c0\uff0c\u4e71\u54c4\u54c4\u7684\u8f66\u53a2\u5728\u5979\u4eec\u4e0a\u8f66\u540e\u5c31\u987f\u65f6\u5b89\u9759\u4e0b\u6765\uff0c\u773c\u775b\u90fd\u8f6c\u4e0d\u53ca\uff0c\u5c31\u5269\u4e0b\u5979\u4eec\u51e0\u4e2a\u5973\u5b69\u5b50\u7ad9\u5728\u8fc7\u9053\u91cc\u53fd\u53fd\u53fd\u55b3\u55b3\u7684\u65c1\u82e5\u65e0\u4eba\u7684\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u770b\u6765\u5750\u673a\u573a\u5927\u5df4\u7684\u51b3\u5b9a\u662f\u6b63\u786e\u7684\uff0c\u5f20\u606a\u5c06\u62a5\u7eb8\u4e22\u5230\u4e00\u65c1\uff0c\u8fd9\u4e00\u8def\u770b\u7f8e\u5973\u5230\u5e02\u533a\uff0c\u5012\u6bd4\u770b\u62a5\u7eb8\u8212\u5766\u3002</p><p>\u3000\u3000\u201c\u6c14\u6b7b\u4e86\uff0c\u59d1\u5976\u5976\u597d\u6b79\u4e5f\u7b97\u8ddf\u5929\u59ff\u56fd\u8272\u6cbe\u4e9b\u8fb9\uff0c\u4ece\u9999\u6e2f\u98de\u56de\u6765\uff0c\u8fd9\u4eba\u5634\u5df4\u751c\u7740\u4e00\u53e3\u4e00\u4e2a\u52b2\u7684\u5524\u4eba\u5bb6\u59d0\u59d0\uff0c\u4e0b\u98de\u673a\u524d\u8fd8\u8bf4\u8981\u8bf7\u6211\u5403\u996d\uff0c\u8fd9\u79cd\u89d2\u8272\uff0c\u59d1\u5976\u5976\u89c1\u591a\u4e86\uff0c\u90fd\u7231\u7406\u4e0d\u7406\u2026\u2026\u624d\u9694\u5929\u7684\u5de5\u592b\uff0c\u8fd9\u4f1a\u513f\u5728\u4eba\u5bb6\u8138\u4e0a\u7784\u4e86\u4e24\u4e09\u56de\uff0c\u7a7a\u6d1e\u6d1e\u7684\u773c\u795e\uff0c\u628a\u4eba\u5bb6\u5f53\u6210\u7a7a\u6c14\u4f3c\u7684\uff0c\u6211\u53ea\u5f53\u4ed6\u88c5\u8154\u4f5c\u52bf\uff0c\u8fd8\u4f5c\u8d31\u81ea\u5df1\u7684\u8dd1\u5230\u4ed6\u9762\u524d\u8dd1\u4e86\u4e24\u4e09\u56de\uff0c\u5929\u554a\uff01\u96be\u9053\u6211\u6539\u98de\u56fd\u5185\u822a\u73ed\uff0c\u53d8\u5316\u5c31\u8fd9\u4e48\u5927\u5417\uff1f\u201d</p><p>\u3000\u3000\u6e05\u8106\u7684\u58f0\u97f3\u5728\u8f66\u53a2\u91cc\u6d41\u7a9c\uff0c\u60f9\u6765\u5176\u4ed6\u5973\u5b69\u5b50\u8086\u65e0\u5fcc\u60ee\u7684\u94f6\u94c3\u4f3c\u7684\u9644\u548c\u7684\u7b11\u58f0\uff1b\u542c\u8fd9\u5973\u5b69\u5b50\u54ac\u7259\u5207\u9f7f\u7684\u6837\u5b50\uff0c\u597d\u50cf\u90a3\u4eba\u771f\u7684\u60f9\u5230\u5979\u4e86\uff0c\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\uff0c\u8bf4\u8bdd\u7684\u5973\u5b69\u5b50\u8fd8\u5728\u540e\u9762\u7ed9\u6321\u4f4f\u4e86\u8138\uff0c\u524d\u9762\u4e00\u4e2a\u5973\u5b69\u5b50\u56de\u5934\u5b89\u6170\u5979\u8bf4\uff1a\u201c\u5c0f\u6613\uff0c\u4f60\u4f24\u5fc3\u4ec0\u4e48\uff0c\u8981\u6028\u4e5f\u53ea\u662f\u90a3\u6709\u773c\u65e0\u73e0\u7537\u6028\u81ea\u5df1\u75db\u5931\u826f\u673a\uff0c\u96be\u5f97\u9047\u4e0a\u4f60\u6625\u5fc3\u6cdb\u6ee5\u3002\u4f60\u8fd8\u6015\u4f60\u5929\u59ff\u56fd\u8272\u9493\u4e0d\u5230\u91d1\u9f9f\u5a7f\uff1f\u4f60\u8ba9\u4f60\u7238\u5c06\u4ed6\u4eec\u90e8\u59d4\u7684\u5355\u8eab\u5c0f\u4f19\u5b50\u90fd\u6392\u961f\u5355\u819d\u8dea\u5230\u4f60\u9762\u524d\u6328\u4e2a\u8ba9\u4f60\u6311\u9009\uff0c\u987a\u5e26\u8ba9\u6211\u4eec\u59d0\u51e0\u4e2a\u4e5f\u6cbe\u70b9\u5149\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6b63\u8981\u6487\u8fc7\u5934\u770b\u540e\u9762\u90a3\u4e2a\u6ee1\u8179\u7262\u9a9a\u7684\u5973\u5b69\u5b50\u957f\u4ec0\u4e48\u6837\u5b50\uff0c\u542c\u58f0\u97f3\u771f\u662f\u4e0d\u9519\uff0c\u5750\u5728\u524d\u6392\u7684\u5085\u4fca\u5374\u8be1\u5f02\u7684\u56de\u8fc7\u5934\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u610f\u8bc6\u8d77\u4ec0\u4e48\u4e8b\u60c5\u6765\uff0c\u524d\u9762\u7684\u5973\u5b69\u5b50\u8d70\u8fc7\u53bb\uff0c\u8fd8\u6307\u7740\u5f20\u606a\u65c1\u8fb9\u7684\u7a7a\u4f4d\u7f6e\u5bf9\u540e\u9762\u7684\u5973\u5b69\u8bf4\uff1a\u201c\u4e0d\u8981\u751f\u6c14\u4e86\uff0c\u8fd9\u4f4d\u5b50\u8ba9\u7ed9\u4f60\uff0c\u7b97\u662f\u7ed9\u4f60\u8865\u507f\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u8bdd\u8bf4\u5f97\u9690\u6666\uff0c\u5f20\u606a\u8fd8\u662f\u542c\u51fa\u6765\u8fd9\u5973\u5b69\u5b50\u662f\u5728\u5938\u81ea\u5df1\uff1b\u5973\u5b69\u5b50\u8ddf\u5973\u5b69\u5b50\u5728\u4e00\u8d77\uff0c\u4e5f\u662f\u5f88\u8bb2\u7a76\u6b23\u8d4f\u4e2a\u7537\u8272\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b20\u7740\u8eab\u5b50\u632a\u5230\u9760\u8f66\u7a97\u7684\u4f4d\u5b50\u4e0a\uff0c\u5c06\u9760\u8fc7\u9053\u7684\u4f4d\u5b50\u8ba9\u51fa\u53bb\u3002</p><p>\u3000\u3000\u201c\u522b\u8bf4\u4e86\u90a3\u4e48\u597d\u5fc3\uff0c\u6211\u624d\u4e0d\u7a00\u7f55\u2026\u2026\u201d\u540e\u9762\u90a3\u5973\u5b69\u5b50\u5f2f\u7740\u8170\u63d0\u8d77\u62c9\u6746\u884c\u674e\u7bb1\uff0c\u7ad9\u8d77\u6765\u8981\u5c06\u884c\u674e\u7bb1\u585e\u5230\u884c\u674e\u67b6\u4e0a\uff0c\u624d\u770b\u6e05\u5f20\u606a\u8138\u4e0a\u8d4f\u7f8e\u89c2\u8272\u7684\u671f\u5f85\u795e\u8272\uff0c\u201c\u554a\u2026\u2026\u201d\u624b\u4e00\u8f6f\uff0c\u62c9\u6746\u884c\u674e\u7bb1\u5c31\u671d\u524d\u6392\u5085\u4fca\u5934\u4e0a\u7838\u4e0b\u53bb\uff0c\u540e\u9762\u7684\u5973\u5b69\u5b50\u90fd\u60ca\u4e0d\u4f4f\u5c16\u53eb\u8d77\u6765\u3002</p><p>\u3000\u3000\u597d\u5728\u5085\u4fca\u4e00\u76f4\u6ce8\u610f\u7740\u8fd9\u8fb9\uff0c\u624b\u53ca\u65f6\u6258\u8d77\u884c\u674e\u7bb1\uff0c\u7ad9\u8d77\u6765\u5c06\u884c\u674e\u7bb1\u63a5\u8fc7\u6765\uff0c\u5bf9\u90a3\u5973\u5b69\u5b50\u8bf4\uff1a\u201c\u6211\u5e2e\u4f60\u653e\u4e0a\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u5973\u5b69\u5b50\u6ee1\u8138\u7ea2\u6655\uff0c\u6709\u7684\u5973\u5b69\u5b50\u8116\u5b50\u624e\u7740\u4e1d\u5dfe\uff0c\u90a3\u5973\u5b69\u5b50\u9732\u51fa\u5236\u670d\u9886\u895f\u4fee\u957f\u6676\u83b9\u7684\u8116\u5b50\u90fd\u6577\u4e86\u7c89\u4f3c\u7684\u4e00\u7247\u6843\u7ea2\u8273\u8272\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u56de\u624d\u8ba4\u51fa\u6765\u8fd9\u5973\u5b69\u5b50\u662f\u4ece\u9999\u6e2f\u98de\u56de\u5efa\u90ba\u501f\u7ed9\u4ed6\u968f\u8eab\u542c\u7684\u90a3\u4e2a\u7a7a\u4e2d\u5c0f\u59d0\uff0c\u8981\u4e0d\u662f\u542c\u5230\u5979\u521a\u624d\u7684\u7262\u9a9a\u8bdd\uff0c\u4e5f\u610f\u8bc6\u4e0d\u5230\u662f\u540c\u4e00\u4e2a\u4eba\u3002\u98de\u9999\u6e2f\u8981\u7b97\u56fd\u9645\u822a\u7ebf\u5427\uff0c\u867d\u7136\u90fd\u662f\u4e1c\u65b9\u822a\u7a7a\u516c\u53f8\u7684\u98de\u673a\uff0c\u4f46\u662f\u9694\u5929\u8f6c\u98de\u56fd\u5185\u822a\u73ed\u7684\u53ef\u80fd\u6027\u4e5f\u592a\u5c0f\uff0c\u518d\u8bf4\u4e1c\u822a\u7684\u5973\u5b69\u5b50\u4e2a\u4e2a\u9753\u4e3d\u9ad8\u6311\uff0c\u6807\u51c6\u7684\u53d1\u5f0f\u3001\u6807\u51c6\u7684\u7edb\u7d2b\u8272\u5236\u670d\uff0c\u8bf4\u6807\u51c6\u7684\u9e45\u86cb\u5c0f\u8138\u52a0\u52a8\u4eba\u7684\u5927\u773c\u674f\u76ee\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u5f20\u606a\u53ea\u89c9\u5f97\u6709\u4e9b\u9762\u719f\uff0c\u5750\u98de\u673a\u4e0a\u4e00\u76f4\u8ddf\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8bf4\u4e8b\u60c5\uff0c\u4e5f\u6ca1\u6709\u5f80\u7ec6\u91cc\u770b\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u771f\u6ca1\u610f\u8bc6\u5230\u4f60\u4eca\u5929\u6539\u98de\u56fd\u5185\u822a\u73ed\uff0c\u7785\u4e86\u4e24\u773c\u5c31\u89c9\u5f97\u9762\u719f\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4f60\u54ea\u4e2a\u59d0\u59d0\u6216\u8005\u59b9\u59b9\u3002\u8fd8\u662f\u60f3\u8981\u5728\u4f60\u4eb2\u621a\u9762\u524d\u6536\u655b\u4e00\u4e9b\uff0c\u90fd\u6ca1\u6562\u7ec6\u770b\u3002\u201d\u5f20\u606a\u80e1\u626f\u7740\uff0c\u8eab\u5b50\u53c8\u671d\u8f66\u7a97\u8fb9\u6324\u4e86\u6324\uff0c\u7ed9\u8fd9\u5973\u5b69\u5b50\u8ba9\u51fa\u66f4\u5927\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u5c31\u662f\u90a3\u4e2a\u6709\u2026\u2026\u7537\uff1f\u201d\u8d70\u8fc7\u53bb\u7684\u90a3\u5973\u5b69\u5b50\u770b\u89c1\u5c0f\u6613\u7684\u884c\u674e\u7bb1\u5dee\u70b9\u7838\u5230\u4eba\uff0c\u8f6c\u8fc7\u8eab\u6765\u8981\u5e2e\u5979\u4e00\u628a\uff0c\u770b\u89c1\u5c0f\u6613\u76ef\u7740\u5f20\u606a\u6ee1\u8138\u7ea2\u6655\uff0c\u518d\u542c\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5c31\u77e5\u9053\u539f\u6765\u662f\u5c0f\u6613\u7684\u7262\u9a9a\u5bf9\u8c61\u3002</p><p>\u3000\u3000\u201c\u5bf9\uff0c\u6211\u5c31\u662f\u4f60\u4eec\u521a\u624d\u8bf4\u7684\u90a3\u4e2a\u6709\u773c\u65e0\u73e0\u7537\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\u3002</p><p>\u3000\u3000\u201c\u563b\u2026\u2026\u201d\u90a3\u5973\u5b69\u5b50\u55e4\u7b11\u4e00\u58f0\uff0c\u53c8\u8fde\u5fd9\u5c06\u81ea\u5df1\u6487\u6e05\uff0c\u201c\u8fd9\u4e2a\u4e5f\u662f\u5c0f\u6613\u8bf4\u7684\uff0c\u4e0d\u5e72\u6211\u4ec0\u4e48\u4e8b\u3002\u201d\u63d0\u8d77\u884c\u674e\u7bb1\u8981\u5c06\u63a8\u5230\u884c\u674e\u67b6\u4e0a\uff0c\u660e\u660e\u8eab\u6750\u9ad8\u6311\uff0c\u8fd8\u8e2e\u7740\u811a\u63d0\u7740\u8170\uff0c\u6324\u5151\u7740\u5f20\u606a\u5e2e\u5979\u4e00\u628a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u529e\u6cd5\uff0c\u7ad9\u8d77\u6765\u5e2e\u90a3\u5973\u5b69\u5b50\u5c06\u884c\u674e\u7bb1\u653e\u5230\u884c\u674e\u67b6\u4e0a\uff0c\u540e\u9762\u7684\u5973\u5b69\u5b50\u6324\u8fc7\u6765\uff0c\u63a8\u7740\u5c0f\u6613\u5f80\u5f20\u606a\u8eab\u4e0a\u6324\uff0c\u540e\u9762\u4e00\u4e2a\u5973\u5b69\u5b50\u8fd8\u51d1\u5230\u5c0f\u6613\u8033\u6735\u8bf4\uff1a\u201c\u662f\u86ee\u5e05\u7684\u554a\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u5047\u88c5\u6ca1\u6709\u542c\u89c1\uff1b\u8fd9\u7fa4\u7a7a\u4e2d\u5c0f\u59d0\u51d1\u5230\u4e00\u8d77\uff0c\u5403\u7537\u4eba\u8c46\u8150\u4e5f\u662f\u5f88\u653e\u5f97\u5f00\u7684\u3002</p><p>\u3000\u3000\u5012\u662f\u5c0f\u6613\u6709\u4e9b\u614c\u4e71\uff0c\u60f3\u5230\u521a\u624d\u5f00\u73a9\u7b11\u7684\u8bdd\u843d\u5230\u5f53\u4e8b\u4eba\u8033\u6735\u91cc\uff0c\u53c8\u8ba9\u540c\u4e8b\u6324\u5151\u7740\u5632\u7b11\uff0c\u5c31\u89c9\u5f97\u8138\u70eb\u3002\u5979\u8981\u5750\u540e\u6392\u53bb\uff0c\u5374\u7ed9\u51e0\u4e2a\u5973\u5b69\u5b50\u6293\u4f4f\u624b\u811a\u786c\u585e\u5f20\u606a\u65c1\u8fb9\u7684\u4f4d\u5b50\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f97\u653e\u5f03\u4ed6\u7684\u8d4f\u7f8e\u89c2\u8272\u8ba1\u5212\uff0c\u62ff\u7740\u62a5\u7eb8\u7ee7\u7eed\u88c5\u4e00\u672c\u6b63\u7ecf\u7684\u770b\u8d77\u6765\uff0c\u5012\u662f\u5750\u5728\u540e\u6392\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u5934\u8d34\u7740\u5ea7\u4f4d\u4e2d\u95f4\u7684\u7a7a\u9699\u4e0d\u505c\u8ddf\u5f20\u606a\u8bf4\u8bdd\u3002\u5927\u5df4\u5f00\u52a8\u6ca1\u51e0\u5206\u949f\uff0c\u5f20\u606a\u5c31\u4ece\u8fd9\u4e2a\u53eb\u4f55\u6653\u96ef\u7684\u5973\u5b69\u5b50\u90a3\u91cc\u77e5\u9053\u4ed6\u8eab\u8fb9\u7684\u5c0f\u6613\u6613\u99a8\uff0c\u56e0\u4e3a\u5979\u5988\u5988\u751f\u4e86\u75c5\u624d\u4e34\u65f6\u8c03\u5230\u5efa\u90ba\u98de\u5317\u4eac\u7684\u822a\u73ed\u80fd\u9694\u51e0\u5929\u5c31\u56de\u5bb6\u4e00\u8d9f\uff0c\u5979\u4e4b\u524d\u4e5f\u662f\u98de\u5317\u4eac\u4e0e\u5efa\u90ba\u7684\u822a\u7ebf\uff0c\u8c03\u5230\u9999\u6e2f\u56fd\u9645\u822a\u7ebf\u6ca1\u51e0\u4e2a\u6708\uff0c\u8fd9\u6b21\u7b97\u662f\u4e34\u65f6\u8c03\u56de\u6765\u3002</p><p>\u3000\u3000\u65c5\u9014\u591a\u6709\u8273\u9047\uff0c\u8fd9\u8bdd\u5012\u662f\u4e0d\u5047\uff0c\u5f20\u606a\u5728\u524d\u4e16\u5c31\u6709\u5145\u5206\u7684\u4f53\u4f1a\uff0c\u53ea\u662f\u4eca\u751f\u53cd\u800c\u6536\u655b\u8d77\u6765\uff0c\u4e0d\u6562\u60f9\u592a\u591a\u7684\u9ebb\u70e6\uff0c\u6628\u5929\u5411\u6613\u99a8\u501f\u968f\u8eab\u542c\u968f\u53e3\u8bf4\u8981\u8bf7\u4eba\u5bb6\u5403\u996d\uff0c\u4e0d\u8fc7\u662f\u968f\u53e3\u4eba\u60c5\uff0c\u8fd9\u4f1a\u513f\u4ed6\u5c31\u52aa\u529b\u5c06\u8fd9\u4e8b\u60c5\u5fd8\u6389\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u793c\u8c8c\uff0c\u5230\u5e02\u533a\u4e0b\u8f66\u65f6\uff0c\u8fd8\u662f\u4e0e\u6613\u99a8\u3001\u4f55\u6653\u96ef\u7b49\u51e0\u4e2a\u4e1c\u822a\u7684\u5973\u5b69\u5b50\u4ea4\u6362\u4e86\u8054\u7cfb\u65b9\u5f0f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4eba\u751f\u591a\u6709\u5de7\u5408\uff0c\u8fd9\u8bdd\u5012\u662f\u4e00\u70b9\u90fd\u5047\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u5230\u5e02\u533a\u5df2\u7ecf\u5feb\u591c\u91cc\u4e5d\u70b9\u949f\u4e86\uff0c\u53f8\u673a\u6362\u4e86\u4e00\u90e8\u4e0d\u6f0f\u6cb9\u7684\u5546\u52a1\u8f66\u63a5\u4ed6\u4eec\u53bb\u5bbe\u9986\uff0c\u7a0d\u4f5c\u4f11\u606f\u5c31\u51fa\u6765\u5403\u996d\u3002\u5bbe\u9986\u79bb\u4e1c\u76f4\u95e8\u8fd1\uff0c\u4e1c\u76f4\u95e8\u5185\u7684\u9910\u996e\u4e00\u6761\u8857\u7c0b\u8857\u4ee5\u7c0b\u4e3a\u540d\u81ea\u7136\u662f\u5403\u98df\u7684\u597d\u53bb\u5904\u3002\u5317\u4eac\u8857\u5934\u521d\u6625\u7684\u591c\u665a\u6bd4\u8f83\u51b7\u6e05\uff0c\u8fd9\u65f6\u5019\u51fa\u6765\u5403\u665a\u996d\u5df2\u7ecf\u5f88\u665a\u4e86\uff0c\u53bb\u5176\u4ed6\u996d\u5e97\u5403\u996d\u8bf4\u4e0d\u5b9a\u4f1a\u7ed9\u544a\u4e4b\u53a8\u5e08\u5feb\u5230\u4e0b\u73ed\u65f6\u95f4\u6055\u4e0d\u4f3a\u5019\uff0c\u7c0b\u8857\u5230\u5904\u90fd\u662f\u5168\u5929\u4e8c\u5341\u56db\u5c0f\u65f6\u8425\u4e1a\u7684\u996d\u9986\uff0c\u8fd9\u4e2a\u70b9\u53bb\u7c0b\u8857\u5403\u591c\u5bb5\u7684\u8001\u5916\u4e5f\u6781\u591a\u3002</p><p>\u3000\u3000\u7c0b\u8857\u9ebb\u8fa3\u5c0f\u9f99\u867e\u6bd4\u8f83\u51fa\u540d\uff0c\u5f20\u606a\u5374\u559c\u6b22\u8fd9\u91cc\u7684\u9999\u8fa3\u87f9\uff0c\u82cf\u6d25\u4e1c\u4e0e\u4ed6\u52a9\u7406\u4e58\u98de\u673a\u65f6\u95f4\u592a\u957f\uff0c\u6709\u4e9b\u75b2\u60eb\uff0c\u5f20\u606a\u62c9\u5230\u4ed6\u4eec\u5230\u5929\u4e8c\u9601\u5403\u9999\u8fa3\u87f9\u5f00\u80c3\u3002\u8ddf\u7740\u670d\u52a1\u5458\u8d70\u8fdb\u5927\u5802\uff0c\u5c31\u542c\u89c1\u5927\u5802\u9760\u5916\u89d2\u5eca\u67f1\u6709\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u98d8\u6765\uff1a\u201c\u53e3\u53e3\u58f0\u58f0\u8bf4\u8981\u8bf7\u59d1\u5976\u5976\u5403\u996d\u8c22\u59d1\u5976\u5976\u8d4f\u8138\u5c06\u968f\u8eab\u542c\u501f\u7ed9\u4ed6\uff0c\u8fd9\u4e2b\u6ca1\u957f\u8bb0\u6027\u662f\u4e00\u56de\u4e8b\uff0c\u4f55\u6653\u96ef\u8bdd\u91cc\u90fd\u63d0\u9192\u4ed6\u4e24\u4e09\u56de\u4e86\uff0c\u8fd9\u4e2b\u7adf\u7136\u8ddf\u59d1\u5976\u5976\u88c5\u50bb\uff0c\u597d\u50cf\u59d1\u5976\u5976\u627e\u4e0d\u5230\u5c0f\u5f00\u8bf7\u5ba2\u5403\u996d\u4f3c\u7684\u2026\u2026\u5509\uff0c\u8fd9\u4e16\u9053\uff0c\u7537\u4eba\u600e\u4e48\u5c31\u90fd\u5815\u843d\u4e86\uff01\u59d0\u59b9\u4eec\uff0c\u8981\u628a\u8fd9\u4e9b\u5815\u843d\u7684\u7537\u4eba\u90fd\u62ef\u6551\u8fc7\u6765\uff0c\u4efb\u52a1\u5f88\u8270\u5de8\u554a\uff01\u201d</p><p>\u3000\u3000\u6613\u99a8\u80cc\u5bf9\u5927\u95e8\u5750\u7740\u8bf4\u5f97\u7709\u98de\u8272\u821e\u3001\u58f0\u60c5\u5e76\u8302\uff0c\u4f55\u6653\u96ef\u5979\u4eec\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\u8d70\u8fdb\u6765\uff0c\u8138\u90fd\u618b\u8d70\u5f62\u4e86\uff0c\u5728\u6613\u99a8\u611f\u5230\u8be7\u5f02\u626d\u8fc7\u5934\u6765\u4e4b\u9645\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u7206\u7b11\u8d77\u6765\u2026\u2026</p><p>\u3000\u3000\u770b\u5230\u6613\u99a8\u4f4e\u5934\u627e\u5730\u7f1d\u7684\u6837\u5b50\uff0c\u5f20\u606a\u771f\u4e0d\u5fcd\u5fc3\u518d\u8d70\u8fdb\u53bb\uff0c\u4e0d\u8fc7\u5fc3\u6027\u4f7f\u7136\uff0c\u4ed6\u5012\u662f\u5f88\u81ea\u7136\u8d70\u8fc7\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e0d\u662f\u8d76\u8fc7\u6765\u8bf7\u59d1\u5976\u5976\u4eec\u5ba2\u5417\uff1f\u201d</p><p>\u3000\u3000\u6613\u99a8\u8138\u8db4\u5728\u5979\u65c1\u4eba\u4e00\u5973\u5b69\u5b50\u7684\u80a9\u4e0a\uff0c\u4e0d\u597d\u610f\u601d\u770b\u5f20\u606a\uff0c\u5979\u81ea\u5df1\u7684\u80a9\u8180\u90fd\u7b11\u5f97\u62bd\u6410\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e0b\u5b50\u603b\u4e0d\u80fd\u539a\u7740\u8138\u76ae\u518d\u4e0d\u8bf7\u5ba2\u4e86\uff0c\u6613\u99a8\u5979\u4eec\u51e0\u4e2a\u5973\u5b69\u6765\u5f97\u7a0d\u65e9\uff0c\u4e00\u5927\u76d8\u9ebb\u8fa3\u5c0f\u9f99\u867e\u3001\u4e00\u5927\u76d8\u9999\u8fa3\u87f9\u90fd\u5df2\u7ecf\u4e0a\u6765\uff0c\u51d1\u5230\u4e00\u684c\uff0c\u5f20\u606a\u95ee\u6613\u99a8\u5979\u5988\u5988\u4e0d\u662f\u751f\u75c5\u5417\uff0c\u5979\u600e\u4e48\u6ca1\u6709\u76f4\u63a5\u56de\u5bb6\u5c31\u8ddf\u7740\u4f55\u6653\u96ef\u5979\u4eec\u4e00\u8d77\u51fa\u6765\u9b3c\u6df7\uff1f</p></article>"}}}]);
