(window.webpackJsonp=window.webpackJsonp||[]).push([[681],{704:function(p){p.exports={chapterTitle:null,title:"\u7b2c524\u7ae0 \u76ae\u6761\u5ba2",content:"<article><h2>\u7b2c524\u7ae0 \u76ae\u6761\u5ba2</h2><p>\u3000\u3000\u51e0\u4e2a\u5973\u5b69\u5b50\u6324\u5151\u7740\u8ba9\u5f20\u606a\u4e0e\u6613\u99a8\u6328\u7740\u5750\uff0c\u6613\u99a8\u6ca1\u8138\u62ac\u5934\u89c1\u4eba\uff0c\u53ea\u4f4e\u5934\u5265\u867e\u5403\uff0c\u522b\u4eba\u90fd\u4e0d\u8bf4\u8fd9\u4e8b\uff0c\u5979\u81ea\u5df1\u5fcd\u4e0d\u4f4f\u7b11\u5c06\u5634\u91cc\u7684\u867e\u8089\u545b\u5230\u7897\u789f\u91cc\uff0c\u773c\u6cea\u90fd\u545b\u4e86\u51fa\u6765\uff0c\u7b11\u8d77\u6765\u5c31\u6b62\u4e0d\u4f4f\uff0c\u62bd\u4e86\u51e0\u5f20\u7eb8\u5dfe\u62ff\u7740\u624b\u888b\u8eb2\u6d17\u624b\u95f4\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u5f97\u5728\u5927\u5df4\u4e0a\u804a\u5929\u8bf4\u6613\u99a8\u5979\u5988\u5988\u751f\u75c5\u5979\u624d\u98de\u56fd\u5185\u822a\u7ebf\uff0c\u597d\u65b9\u4fbf\u56de\u5317\u4eac\u966a\u5979\u5988\u5988\uff0c\u95ee\u4f55\u6653\u96ef\u6613\u99a8\u5979\u600e\u4e48\u6ca1\u6709\u76f4\u63a5\u56de\u5bb6\u5c31\u51fa\u6765\u9b3c\u6df7\u4e86\uff0c\u5370\u6709\u4e1c\u822a\u6807\u5fd7\u7684\u62c9\u6746\u884c\u674e\u7bb1\u90fd\u5e26\u5230\u996d\u9986\u91cc\u6765\u3002</p><p>\u3000\u3000\u201c\u5207\uff0c\u5979\u5988\u5988\u54ea\u91cc\u6709\u751f\u75c5\uff1f\u5979\u7ed9\u5979\u5bb6\u91cc\u6446\u4e86\u4e00\u9053\uff0c\u7ed9\u9a97\u56de\u5317\u4eac\u76f8\u4eb2\u6765\u4e86\uff0c\u5979\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5c31\u62c9\u7740\u6211\u4eec\u6765\u8fd9\u91cc\u8150\u8d25\u4e86\u2026\u2026\u201d\u4f55\u6653\u96ef\u8fd9\u4e48\u8bf4\u7740\uff0c\u8fd8\u62c5\u5fc3\u5403\u8fd9\u4e48\u8fa3\uff0c\u76ae\u80a4\u5403\u4e0d\u6d88\uff0c\u201c\u5b8c\u86cb\u4e86\uff0c\u660e\u5929\u53c8\u8981\u957f\u75d8\u75d8\u4e86\uff0c\u5c0f\u6613\u6bcf\u6b21\u81ea\u5df1\u4e0d\u5f00\u5fc3\uff0c\u5c31\u8fd9\u4e48\u9677\u5bb3\u522b\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u6613\u99a8\u8eb2\u8fdb\u6d17\u624b\u95f4\u597d\u534a\u5929\u624d\u8865\u4e86\u5986\u51fa\u6765\uff0c\u4f55\u6653\u96ef\u95ee\u5f20\u606a\u662f\u4ec0\u4e48\u516c\u53f8\u7684\uff0c\u5458\u5de5\u56fd\u5185\u56fd\u5916\u51fa\u5dee\u90fd\u80fd\u4e58\u98de\u673a\u800c\u4e14\u8fd8\u80fd\u5750\u5546\u52a1\u8231\u7684\u516c\u53f8\u53ef\u4e0d\u591a\u89c1\u3002\u5750\u673a\u573a\u5927\u5df4\u5230\u5e02\u533a\u65f6\u5c31\u5f20\u606a\u8ddf\u4e1c\u822a\u51e0\u4e2a\u5973\u5b69\u5b50\u4e92\u7559\u8054\u7cfb\u65b9\u5f0f\uff0c\u5f20\u606a\u6709\u4e09\u53ea\u624b\u673a\uff0c\u6709\u4e24\u53ea\u624b\u673a\u5e73\u65f6\u90fd\u653e\u5728\u5085\u4fca\u8eab\u8fb9\uff0c\u5f20\u606a\u968f\u610f\u7559\u4e86\u4e24\u4e2a\u53f7\u7801\u4e2d\u7684\u4e00\u4e2a\uff0c\u5728\u673a\u573a\u5927\u5df4\u4e0a\u8fd8\u6ca1\u6709\u70ed\u7edc\u5230\u8981\u5c06\u771f\u6b63\u7684\u8eab\u4efd\u544a\u8bc9\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u90fd\u51d1\u5230\u4e00\u684c\u5403\u996d\u4e86\uff0c\u4f55\u6653\u96ef\u5f00\u53e3\u95ee\u8d77\u6765\uff0c\u518d\u80e1\u4e71\u642a\u585e\u8fc7\u53bb\uff0c\u53c8\u8981\u4f24\u8fd9\u51e0\u4e2a\u4e1c\u822a\u5973\u5b69\u5b50\u7684\u5fc3\u4e86\uff0c\u5f20\u606a\u6307\u7740\u5317\u4eac\u5206\u516c\u53f8\u7684\u7ecf\u7406\u59da\u575a\u4e0e\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\u8d75\u9e4f\u95ee\u4f55\u6653\u96ef\uff1a\u201c\u4f60\u8be5\u4e0d\u4f1a\u770b\u4e0a\u8fd9\u4e24\u4f4d\u5144\u5f1f\u4e86\u5427\uff1f\u201d\u6307\u7740\u59da\u575a\u3001\u8d75\u9e4f\u8bf4\u9053\uff0c\u201c\u90a3\u4e2a\uff0c\u628a\u4f60\u4eec\u7684\u540d\u7247\u7ed9\u4e1c\u822a\u7684\u7f8e\u5973\u53d1\u4e00\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u7684\u7ecf\u7406\u59da\u575a\u8fd8\u6709\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\u8d75\u9e4f\u90fd\u662f\u4e8c\u5341\u516d\u4e03\u5c81\u7684\u5e74\u8f7b\u4eba\uff0c\u7537\u4eba\u5230\u4e86\u597d\u8272\u800c\u6155\u827e\u7684\u5e74\u9f84\u4e4b\u540e\uff0c\u8fd9\u5929\u6027\u6fc0\u53d1\u51fa\u6765\u5c31\u6cef\u706d\u4e0d\u4e86\uff0c\u4f55\u51b5\u8fd8\u90fd\u662f\u4e13\u6ce8\u4e8e\u4e8b\u4e1a\u7684\u5149\u68cd\uff0c\u5e73\u65f6\u5de5\u4f5c\u4e2d\u80fd\u63a5\u89e6\u5230\u7f8e\u5973\u591a\u534a\u662f\u540d\u82b1\u6709\u4e3b\u6216\u8005\u81ea\u89c6\u751a\u9ad8\uff0c\u540c\u684c\u5403\u996d\u4e00\u684c\u4e94\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u7f8e\u5973\u7684\u673a\u7387\u5b9e\u5728\u592a\u5c0f\u4e86\uff0c\u8fd9\u4e9b\u5973\u5b69\u5b50\u8eab\u8fb9\u90fd\u662f\u7f8e\u5973\uff0c\u81ea\u89c6\u4e5f\u5c31\u6ca1\u90a3\u4e48\u9ad8\uff1b\u59da\u575a\u3001\u8d75\u9e4f\u53ea\u5728\u5927\u8001\u677f\u9762\u524d\u82e6\u65e0\u8868\u73b0\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u4e0d\u8bf4\u8981\u5728\u7f8e\u5973\u9762\u524d\u8868\u73b0\u51fa\u8272\uff0c\u5c31\u7b97\u5728\u5927\u8001\u677f\u9762\u524d\uff0c\u4e5f\u8981\u62a2\u7740\u51fa\u98ce\u5934\uff0c\u59da\u575a\u3001\u8d75\u9e4f\u5404\u81ea\u5c06\u540d\u7247\u9012\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u7231\u8fbe\u7535\u5b50\u554a\uff01\u201d\u4e00\u4e2a\u53eb\u5c0f\u4f73\u7684\u5973\u5b69\u5b50\u60ca\u5947\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u5fc3\u60f3\u5979\u4eec\u8fd9\u4e9b\u98de\u56fd\u5185\u56fd\u9645\u822a\u7ebf\u7684\u5973\u5b69\u5b50\u5927\u516c\u53f8\u4e5f\u89c1\u8bc6\u591a\u4e86\uff0c\u4e16\u754c\u4e94\u767e\u5f3a\u5728\u5979\u4eec\u773c\u91cc\u6216\u8bb8\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u6ca1\u5fc5\u8981\u4e3a\u542c\u5230\u7231\u8fbe\u7535\u5b50\u800c\u5c16\u53eb\uff0c\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5374\u542c\u90a3\u5973\u5b69\u5b50\u5174\u594b\u7684\u62c9\u7740\u6613\u99a8\u8bf4\uff0c\u201c\u4f60\u8bf4\u4e0a\u56de\u5728\u53bb\u9999\u6e2f\u7684\u98de\u673a\u770b\u5230\u4e24\u4e2a\u9876\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u662f\u4e0d\u662f\u5c31\u662f\u7231\u8fbe\u7535\u5b50\u7684\uff0c\u4e00\u4e2a\u8fd8\u662f\u7231\u8fbe\u7535\u5b50\u7684\u5e7f\u544a\u6a21\u7279\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u6b62\u4e24\u4e2a\u7f8e\u5973\uff0c\u53ea\u662f\u5176\u4e2d\u4e24\u4e2a\u7279\u522b\u7684\u6f02\u4eae\uff0c\u6c5f\u9edb\u513f\u6bd4\u7535\u89c6\u91cc\u8fd8\u8981\u6f02\u4eae\uff0c\u6211\u4eec\u90a3\u73ed\u4eba\u5c31\u8f6e\u6d41\u5230\u5546\u52a1\u8231\u770b\u7f8e\u5973\uff0c\u90fd\u6328\u4e58\u52a1\u957f\u9a82\u4e86\u4e00\u901a\uff0c\u8bf4\u6211\u4eec\u90a3\u662f\u9a9a\u6270\u4e58\u5ba2\uff0c\u201d\u6613\u99a8\u8bf4\u8bdd\u4e5f\u4e0d\u770b\u7740\u5f20\u606a\uff0c\u201c\u540e\u6765\u5230\u5206\u516c\u53f8\u4e00\u8bf4\uff0c\u624d\u77e5\u9053\u53e6\u4e00\u4e2a\u5927\u7f8e\u5973\u5750\u8fc7\u6211\u4eec\u4e1c\u822a\u597d\u51e0\u6b21\u822a\u73ed\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5e94\u8be5\u662f\u5e74\u524d\u8bb8\u601d\u5979\u4eec\u53bb\u9999\u6e2f\u90a3\u4e00\u6b21\uff0c\u4e5f\u53ea\u6709\u90a3\u6b21\uff0c\u5979\u662f\u4e0e\u6c5f\u9edb\u513f\u4e58\u540c\u4e00\u67b6\u98de\u673a\u53bb\u9999\u6e2f\uff0c\u540c\u884c\u8fd8\u6709\u5b59\u9759\u9999\u4e0e\u8bb8\u7ef4\u3002\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u672c\u8eab\u5c31\u662f\u7f8e\u5973\u4e86\uff0c\u8fd8\u8fd9\u4e48\u5173\u5fc3\u7f8e\u5973\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f53\u7136\u8981\u5173\u5fc3\u7f8e\u5973\u554a\uff0c\u5728\u66f4\u6f02\u4eae\u7684\u7f8e\u5973\u9762\u524d\u5c31\u4f1a\u81ea\u5351\u554a\uff0c\u201d\u5c0f\u4f73\u662f\u4e2a\u5fc3\u673a\u5355\u7eaf\u7684\u5973\u5b69\u5b50\uff0c\u201c\u4f60\u6ca1\u770b\u89c1\u6613\u99a8\u56de\u6765\u65f6\u5174\u594b\u7684\u6837\u5b50\uff0c\u6211\u4eec\u4e1c\u822a\u7f8e\u5973\u4e5f\u4e0d\u5c11\uff0c\u773c\u5149\u6311\u5254\u5f97\u5f88\uff0c\u5176\u5b9e\u770b\u7535\u89c6\uff0c\u6c5f\u9edb\u513f\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u6613\u99a8\u6f02\u4eae\u591a\u5c11\u2026\u2026\u53e6\u5916\u4e00\u4e2a\u5927\u7f8e\u5973\u4e5f\u662f\u4f60\u4eec\u516c\u53f8\u7684\u554a\uff1f\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u8ff7\u604b\u5236\u670d\u7cfb\u5217\u7684\u7537\u4eba\u6765\u8bf4\u2026\u2026\u5f20\u606a\u7785\u4e86\u6613\u99a8\u4e24\u773c\uff0c\u8fd9\u4f1a\u513f\u5979\u6536\u655b\u8d77\u5a07\u86ee\u7684\u6027\u606a\uff0c\u6e29\u5a49\u52a8\u4eba\u7684\u5750\u5728\u90a3\u91cc\uff0c\u9762\u5bb9\u7cbe\u81f4\u3001\u8584\u65bd\u6de1\u5986\uff0c\u679c\u7136\u4ece\u9aa8\u5b50\u91cc\u900f\u9732\u51fa\u65e0\u5c3d\u7684\u5236\u670d\u8bf1\u60d1\u51fa\u6765\uff0c\u4e0d\u6653\u5f97\u4e1c\u822a\u7a7a\u59d0\u7684\u5236\u670d\u54ea\u91cc\u6709\u552e\uff0c\u6c5f\u9edb\u513f\u7684\u786e\u4e5f\u4e0d\u80fd\u7b97\u6bd4\u5979\u6f02\u4eae\u591a\u5c11\u3002\u5f20\u606a\u54a7\u4e86\u54a7\u5634\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u516c\u53f8\u7684\uff0c\u6211\u4eec\u516c\u53f8\u5c31\u5355\u8eab\u5149\u68cd\u591a\u2026\u2026\u201d\u5f20\u606a\u5176\u5b9e\u8fd8\u662f\u86ee\u60f3\u542c\u542c\u4e1c\u822a\u5973\u5b69\u5b50\u5bf9\u7231\u8fbe\u789f\u673a\u7684\u5370\u8c61\u3002</p><p>\u3000\u3000\u4f55\u6653\u96ef\u5979\u4eec\u4e5f\u4e0d\u5411\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8981\u540d\u7247\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u7684\u59da\u575a\u5934\u4e0a\u90fd\u6302\u7ecf\u7406\u5934\u8854\u4e86\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5728\u5979\u4eec\u773c\u91cc\u81ea\u7136\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u201c\u8001\u7532\u9c7c\u201d\u4e86\u3002</p><p>\u3000\u3000\u9ebb\u8fa3\u5c0f\u9f99\u867e\u3001\u9999\u8fa3\u87f9\uff0c\u5403\u5f97\u70ed\u70ed\u95f9\u95f9\uff0c\u8fc7\u4e86\u5341\u4e00\u70b9\u624d\u7ed3\u675f\u3002</p><p>\u3000\u3000\u6613\u99a8\u3001\u4f55\u6653\u96ef\u5979\u4eec\u51e0\u4e2a\u4e1c\u822a\u7684\u5973\u5b69\u5b50\u8fd8\u8981\u53bb\u9644\u8fd1\u7684KTV\u5531\u6b4c\uff0c\u5f20\u606a\u4ed6\u4eec\u8bf7\u5ba2\u5403\u996d\uff0c\u5979\u4eec\u4fbf\u8bf7\u5f20\u606a\u4ed6\u4eec\u53bb\u5531\u6b4c\u3002</p><p>\u3000\u3000\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u3001\u5085\u4fca\u90fd\u662f\u5c5e\u4e8e\u88ab\u5e74\u8f7b\u5973\u5b69\u5b50\u6dd8\u6c70\u7684\u90a3\u7c7b\u4eba\uff0c\u5012\u662f\u59da\u575a\u3001\u8d75\u9e4f\u542c\u5230\u6613\u99a8\u3001\u4f55\u6653\u96ef\u5979\u4eec\u9080\u8bf7\u53bb\u5531\u6b4c\u5c31\u6709\u4e9b\u8dc3\u8dc3\u6b32\u8bd5\u3002</p><p>\u3000\u3000\u5f20\u606a\u672c\u6765\u6ca1\u6709\u591a\u5927\u7684\u5174\u81f4\uff0c\u5c31\u5f53\u662f\u840d\u6c34\u76f8\u9022\u7684\u8def\u4eba\u5076\u5c14\u805a\u5728\u4e00\u8d77\u7528\u9910\u7559\u4e0b\u4e00\u7b14\u6c34\u58a8\u70df\u96e8\u4f3c\u7684\u6de1\u6de1\u56de\u5fc6\u5373\u53ef\uff0c\u72b9\u8c6b\u7740\u60f3\u627e\u501f\u53e3\u63a8\u8f9e\u6389\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u6345\u4e86\u6345\u4ed6\u7684\u8170\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u662f\u9971\u6c49\u5b50\u4e0d\u77e5\u997f\u6c49\u5b50\u9965\uff0c\u9526\u6e56\u4e0a\u4e0b\u90a3\u4e48\u591a\u5e74\u8f7b\u6709\u4e3a\u7684\u5149\u68cd\uff0c\u4ed6\u4eec\u7684\u4e2a\u4eba\u751f\u6d3b\u95ee\u9898\u4e5f\u662f\u6211\u4eec\u8981\u5173\u5fc3\u7684\uff1b\u4f60\u8bf4\u56fd\u5185\u80fd\u6279\u53d1\u7f8e\u5973\u7684\u5730\u65b9\u6709\u54ea\u91cc\u80fd\u8ddf\u822a\u7a7a\u516c\u53f8\u6bd4\uff0c\u522b\u7684\u5173\u7cfb\uff0c\u4e0d\u9700\u8981\u6c9f\u901a\uff0c\u8ddf\u822a\u7a7a\u516c\u53f8\u5012\u662f\u8981\u6253\u597d\u5173\u7cfb\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u77a5\u773c\u770b\u89c1\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\u8d75\u9e4f\u6b63\u8ddf\u996d\u684c\u4e0a\u804a\u5f97\u9ed8\u5951\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\u4ea4\u6362\u8054\u7cfb\u65b9\u5f0f\u3002\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u603b\u662f\u4f17\u4eba\u4e89\u62a2\u7684\uff0c\u8981\u4e0d\u80fd\u8d81\u70ed\u6253\u94c1\uff0c\u8f6c\u773c\u5c31\u6210\u4e3a\u4ed6\u4eba\u6000\u91cc\u7684\u4f9d\u4eba\u5c0f\u9e1f\u4e86\uff0c\u7559\u4e0b\u8054\u7cfb\u65b9\u5f0f\u53ef\u6ca1\u6709\u4ec0\u4e48\u7528\uff0c\u4f60\u5f97\u6709\u65f6\u95f4\u8ddf\u4eba\u5bb6\u8054\u7cfb\u554a\uff1b\u82cf\u6d25\u4e1c\u8fd9\u4e48\u8bf4\u662f\u4e3a\u4ed6\u7684\u90e8\u4e0b\u7740\u60f3\u3002\u5f20\u606a\u8038\u8038\u80a9\uff0c\u772f\u8d77\u773c\u775b\u5bf9\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u54b1\u4eec\u4e0d\u6210\u62c9\u76ae\u6761\u7684\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0e\u8001\u4e01\u56de\u5bbe\u9986\uff0c\u660e\u5929\u5177\u4f53\u7684\u4e8b\u60c5\u8fd8\u5f97\u662f\u6211\u8ddf\u8001\u4e01\u8dd1\uff0c\u201d\u82cf\u6d25\u4e1c\u8bf4\u9053\uff0c\u201c\u4f60\u8d1f\u8d23\u53bb\u62c9\u76ae\u6761\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\uff0c\u662f\u4e09\u91cc\u5c6f\u9152\u5427\u8857\u5174\u76db\u4e00\u65f6\u7684\u65f6\u671f\uff0c\u4e0d\u8fc7\u9152\u5427\u91cc\u5f88\u5c11\u6709\u8bda\u5fc3\u8bda\u610f\u542c\u97f3\u4e50\u559d\u9152\u7684\u5ba2\u4eba\uff0c\u8981\u627e\u5341\u5143\u94b1\u4e00\u74f6\u9152\u7684\u9152\u5427\u5c31\u8981\u8d39\u4e00\u756a\u5de5\u592b\uff0c\u5728\u91cf\u8d29\u5f0f\u7684KTV\u51fa\u73b0\u4e4b\u524d\uff0c\u56fd\u4eba\u901a\u5e38\u610f\u4e49\u4e0a\u90fd\u89c9\u5f97\u5a31\u4e50\u573a\u6240\u662f\u85cf\u6c61\u7eb3\u57a2\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u94b1\u67dc\u6700\u65e9\u4e5d\u4e94\u5e74\u624d\u5c06\u91cf\u8d29\u5f0fKTV\u5f15\u5165\u56fd\u5185\uff0c\u91cf\u8d29\u5f0fKTV\u6ca1\u6709\u8863\u7740\u66b4\u9732\u7684\u670d\u52a1\u5458\u3001\u5305\u53a2\u95e8\u90fd\u5d4c\u7740\u5927\u5757\u7684\u900f\u655e\u660e\u4eae\u7684\u73bb\u7483\uff1b\u91cf\u8d29\u5f0fKTV\u53e6\u4e00\u5927\u7279\u70b9\u5c31\u662f\u5c06\u660e\u7801\u6807\u4ef7\u7684\u8d85\u5e02\u5f15\u8fdb\u5e97\u5802\u4e4b\u5185\uff0c\u6ca1\u6709\u6696\u6627\u4e0d\u660e\u7684\u9152\u5355\uff0c\u4f7f\u5f97\u5a31\u4e50\u573a\u6240\u6210\u4e3a\u666e\u901a\u4eba\u7fa4\u53ef\u4ee5\u51fa\u5165\u7684\u573a\u6240\uff0c\u5f20\u606a\u8ba9\u76db\u9752\u4ed6\u4eec\u591a\u53bb\u5df2\u5728\u4e0a\u6d77\u5f00\u8bbe\u4e86\u4e00\u5bb6\u5206\u5e97\u7684\u94b1\u67dc\u53bb\u89c2\u6469\u89c2\u6469\uff0c\u8fd9\u624d\u662f\u4ed6\u4eec\u65e5\u540e\u53d1\u5c55\u5a31\u4e50\u4ea7\u4e1a\u7684\u65b9\u5411\u3002</p><p>\u3000\u3000\u5f20\u606a\u5927\u7ea6\u96f6\u4e09\u5e74\u5de6\u53f3\u770b\u8fc7\u4e00\u672c\u4ecb\u7ecd\u7f8e\u56fd\u300a\u5a31\u4e50\u7ecf\u6d4e\u300b\u7684\u4e13\u8457\uff0c\u539a\u91cd\u7684\u4e00\u672c\u4e66\uff0c\u5f20\u606a\u5bf9\u5176\u4e2d\u4e00\u53e5\u8bdd\u6700\u6709\u5370\u8c61\uff1a\u201c\u5a31\u4e50\u7ecf\u6d4e\u5c06\u53d6\u4ee3\u519b\u706b\u7ecf\u6d4e\u201d\uff0c\u800c\u4e14\u96f6\u96f6\u5e74\u4e4b\u540e\uff0c\u56fd\u5185\u5927\u89c4\u6a21\u6295\u8d44\u5a31\u4e50\u4ea7\u4e1a\u7684\u8d8b\u52bf\u4e5f\u8d8a\u6765\u8d8a\u660e\u663e\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u4e0d\u77e5\u9053\u4e5d\u4e03\u5e74\u65e9\u6625\u7684\u5317\u4eac\u54ea\u4e2a\u5730\u65b9\u6709\u91cf\u8d29\u5f0fKTV\u53ef\u4f9b\u5927\u5bb6\u5065\u5eb7\u7684\u5a31\u4e50\uff0c\u6613\u99a8\u3001\u4f55\u6653\u96ef\u8fd9\u51e0\u4e2a\u90fd\u662f\u5bb6\u4f4f\u5317\u4eac\u7684\u6f02\u4eae\u5973\u5b69\u5b50\uff0c\u6027\u683c\u5f00\u6717\uff0c\u5f88\u7231\u73a9\uff0c\u4f46\u662f\u6c14\u8d28\u90fd\u5f88\u5e72\u51c0\uff0c\u5012\u4e0d\u6653\u5f97\u5979\u4eec\u5728\u4e1c\u76f4\u95e8\u9644\u8fd1\u6709\u4ec0\u4e48\u5e72\u51c0\u7684\u5a31\u4e50\u573a\u6240\u53ef\u4ee5\u63a8\u8350\u53bb\u3002</p><p>\u3000\u3000\u51fa\u79df\u8f66\u5728\u4e00\u5bb6\u53eb\u201c\u7cd6\u679c\u201d\u7684\u591c\u5e97\u524d\u505c\u4e0b\u6765\uff0c\u5c3d\u7ba1\u7c89\u7ea2\u8272\u7684\u706f\u5149\u6709\u4e9b\u6696\u6627\uff0c\u4e0d\u8fc7\u8fd9\u5bb6\u5e97\u7684\u7c89\u7ea2\u706f\u5149\u6bd4\u8d77\u4e00\u822c\u7684\u8fea\u5427\u4e0e\u591c\u603b\u4f1a\u8981\u660e\u4eae\u8bb8\u591a\u3002\u6839\u636e\u5a31\u4e50\u573a\u6240\u7684\u706f\u5149\u89c4\u5219\uff0c\u706f\u5149\u8d8a\u660e\u4eae\u7684\u5e97\u8d8a\u201c\u5e72\u51c0\u201d\uff0c\u8fd9\u5bb6\u591c\u5e97\u5373\u4f7f\u6709\u4e9b\u201c\u5a9a\u201d\u8272\uff0c\u4f46\u57fa\u672c\u80fd\u80af\u5b9a\u662f\u5bb6\u5065\u5eb7\u7684\u5e97\uff0c\u8fdb\u51fa\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u4e5f\u5f88\u591a\uff0c\u8bf4\u660e\u8fd9\u5bb6\u5e97\u4e5f\u5f88\u65f6\u5c1a\u3002</p><p>\u3000\u3000\u8dcc\u4eba\u773c\u955c\u7684\uff0c\u5374\u662f\u90a3\u4e2a\u4e00\u76f4\u8868\u73b0\u5f88\u6587\u9759\u7684\u53eb\u5c0f\u4f73\u7684\u5973\u5b69\u5b50\u638f\u51fa\u8fd9\u5bb6\u591c\u5e97\u7684\u4f1a\u5458\u91d1\u5361\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u7a7a\u4e58\u670d\u52a1\u4eba\u5458\u88ab\u56fd\u4eba\u666e\u904d\u7684\u8ba4\u4e3a\u662f\u4e00\u9879\u9ad8\u8d35\u800c\u4f53\u9762\u7684\u5de5\u4f5c\uff0c\u81ea\u7136\u4e5f\u5c31\u4f5c\u4e3a\u4e00\u79cd\u7279\u6b8a\u7684\u8d44\u6e90\u4f1a\u503e\u5411\u7279\u6b8a\u9636\u5c42\u503e\u659c\uff1b\u4e5d\u5341\u5e74\u4ee3\uff0c\u5404\u5927\u822a\u7a7a\u516c\u53f8\u7684\u7a7a\u4e2d\u5c0f\u59d0\u91cc\uff0c\u76f8\u5f53\u591a\u7684\u4e00\u6279\u4eba\u5bb6\u5883\u90fd\u975e\u5e38\u7684\u597d\uff0c\u5c31\u7b97\u6709\u90e8\u6302\u9ed1\u724c\u7684\u5927\u6392\u91cf\u5965\u8fea\u8fc7\u6765\u5c06\u6613\u99a8\u3001\u4f55\u6653\u96ef\u63a5\u8d70\uff0c\u5f20\u606a\u4e5f\u80fd\u5e73\u9759\u7684\u63a5\u53d7\u3002</p><p>\u3000\u3000\u5927\u5802\u4f1a\u5458\u533a\u7684\u65f6\u9ae6\u5973\u5b69\u5b50\u5f88\u591a\uff0c\u5f20\u606a\u7ed9\u6613\u99a8\u5979\u4eec\u62c9\u8fc7\u6765\u662f\u5531\u6b4c\u7684\uff0c\u8fdb\u4e86\u95e8\u4e0a\u5d4c\u7740\u5927\u5757\u900f\u660e\u73bb\u7483\u7684\u5305\u53a2\u5531\u6b4c\uff0c\u4e0d\u8fc7\u8fd8\u4e0d\u662f\u4ee5\u540e\u5e38\u89c1\u7684\u91cf\u8d29\u5f0fKTV\u5f62\u5f0f\uff0c\u5e97\u5185\u6ca1\u6709\u660e\u7801\u6807\u4ef7\u7684\u8d85\u5e02\uff0c\u4e0d\u8fc7\u9152\u5355\u6807\u4ef7\u4e5f\u5f88\u5408\u7406\uff0c\u71d5\u4eac\u5564\u9152\u4e5f\u624d\u4e8c\u5341\u5143\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u4f55\u6653\u96ef\u4e0e\u90a3\u4e2a\u62ff\u51fa\u4f1a\u5458\u91d1\u5361\u7684\u5c0f\u4f73\u8fd8\u62c9\u7740\u59da\u575a\u3001\u8d75\u9e4f\u5f53\u82e6\u529b\u51c6\u5907\u5230\u9644\u8fd1\u7684\u8d85\u5e02\u5077\u5077\u4e70\u4e9b\u9152\u6c34\u8fdb\u6765\u3002\u4f55\u6653\u96ef\u521a\u51fa\u53bb\uff0c\u53c8\u6298\u8eab\u8fdb\u6765\uff0c\u62c9\u7740\u6613\u99a8\u5c31\u5f80\u5916\u8d70\uff1a\u201c\u4f60\u90a3\u4e2a\u76f8\u4eb2\u5bf9\u8c61\u6402\u7740\u4e24\u4e2a\u5973\u5b69\u5b50\u5728\u9694\u58c1\u5305\u53a2\u6b63\u5531\u5f97\u6b22\uff01\u201d</p></article>"}}}]);
