(window.webpackJsonp=window.webpackJsonp||[]).push([[682],{705:function(p){p.exports={chapterTitle:null,title:"\u7b2c681\u7ae0 \u9526\u6e56\u7684\u96c4\u5fc3",content:"<article><h2>\u7b2c681\u7ae0 \u9526\u6e56\u7684\u96c4\u5fc3</h2><p>\u3000\u3000\u592e\u89c6\u65b0\u95fb\u90e8\u7684\u5206\u7ba1\u526f\u53f0\u957f\u8d75\u8fdb\u52c7\u76f8\u53ec\uff0c\u867d\u7136\u4e4b\u524d\u6ca1\u6709\u63a5\u89e6\u8fc7\uff0c\u5f20\u606a\u5012\u662f\u4e0d\u80fd\u9a73\u4ed6\u7684\u9762\u5b50\uff0c\u4e0d\u80fd\u5c06\u592e\u89c6\u7684\u4eba\u90fd\u5f97\u7f6a\u5e72\u51c0\uff0c\u5c31\u7b97\u9526\u6e56\u65e5\u540e\u7684\u6c5f\u6e56\u5730\u4f4d\u7a33\u56fa\u4e86\uff0c\u6253\u4e00\u62e8\u4eba\u3001\u62c9\u4e00\u62e8\u4eba\u4e5f\u624d\u662f\u6b63\u786e\u7684\u5236\u8861\u4e4b\u9053\uff0c\u4f55\u51b5\u8fd9\u65f6\u5019\u6839\u57fa\u672a\u7a33\uff1f\u65e2\u7136\u7ed9\u4eba\u5bb6\u4ece\u4eba\u7fa4\u91cc\u63ea\u51fa\u6765\uff0c\u5f20\u606a\u4e8c\u8bdd\u4e0d\u8bf4\u5c31\u8ddf\u7740\u65b0\u95fb\u90e8\u7684\u5de5\u4f5c\u4eba\u5458\u5f80\u91cc\u8d70\u53bb\uff0c\u66f4\u4e0d\u7528\u8bf4\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u4eba\u8fd8\u7ed9\u6263\u5728\u90a3\u91cc\u5462\u3002</p><p>\u3000\u3000\u62e5\u6709\u79fb\u52a8\u901a\u8baf\u7684\u624b\u6bb5\u4e4b\u540e\uff0c\u6d88\u606f\u4f20\u64ad\u7684\u8fc5\u901f\u591a\u5c11\u8ba9\u4eba\u63aa\u624b\u4e0d\u8db3\u3002\u521a\u8d70\u51fa\u7535\u68af\uff0c\u5f20\u606a\u5c31\u611f\u89c9\u8863\u515c\u91cc\u7684\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u5085\u4fca\u968f\u8eab\u643a\u5e26\u7684\u624b\u673a\u4e5f\u90fd\u540c\u65f6\u9e23\u53eb\u8d77\u6765\uff0c\u5413\u4e86\u4f17\u4eba\u4e00\u60ca\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u79fb\u52a8\u901a\u8baf\u7684\u4fe1\u53f7\u8fd8\u6ca1\u6709\u8986\u76d6\u7535\u68af\u5185\u90e8\uff0c\u51fa\u7535\u68af\u624b\u673a\u4e00\u8d77\u54cd\u8d77\uff0c\u4f30\u8ba1\u62e8\u6253\u7535\u8bdd\u7684\u4eba\u4e5f\u7126\u6025\u7684\u7b49\u4e86\u4e00\u4f1a\u513f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u8863\u515c\u91cc\u638f\u51fa\u624b\u673a\u4e00\u770b\uff0c\u8fd9\u624d\u591a\u5927\u4f1a\u513f\u5de5\u592b\u554a\uff0c\u7f57\u541b\u7684\u7535\u8bdd\u5c31\u5df2\u7ecf\u6253\u4e0a\u95e8\u6765\u4e86\u3002\u5f20\u606a\u6ca1\u6709\u6025\u7740\u63a5\uff0c\u51d1\u8fc7\u53bb\u770b\u4e86\u770b\u5085\u4fca\u624b\u91cc\u7684\u4e24\u53ea\u624b\u673a\u4e0a\u90fd\u663e\u793a\u662f\u8c01\u6253\u6765\u7684\u7535\u8bdd\uff1a\u674e\u8fdc\u6e56\u7684\u79d8\u4e66\u9ad8\u771f\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u5efa\u90ba\u7684\u5ea7\u673a\u53f7\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8fc7\u90a3\u53ea\u663e\u793a\u5ea7\u673a\u53f7\u7801\u7684\u624b\u673a\u5148\u63a5\u901a\u3002</p><p>\u3000\u3000\u201c\u5582\uff0c\u8bf7\u627e\u4e00\u4e0b\u5f20\u606a\uff0c\u6211\u662f\u7701\u59d4\u7684\u8d75\u53cb\u5353\u2026\u2026\u201d</p><p>\u3000\u3000\u7701\u59d4\u5e38\u59d4\u3001\u7701\u59d4\u79d8\u4e66\u957f\u8d75\u53cb\u5353\uff0c\u5f20\u606a\u4ece\u6765\u90fd\u6ca1\u6709\u8ddf\u4ed6\u76f4\u63a5\u6253\u8fc7\u4ea4\u9053\uff0c\u8d75\u53cb\u5353\u5f53\u7136\u662f\u66ff\u9676\u664b\u5728\u6253\u8fd9\u4e2a\u7535\u8bdd\uff0c\u6ca1\u60f3\u5230\u9676\u664b\u4e5f\u6709\u6309\u5948\u4e0d\u4f4f\u7684\u65f6\u5019\u554a\uff01\u9ad8\u771f\u81ea\u7136\u4e5f\u5c31\u662f\u5e2e\u674e\u8fdc\u6e56\u5728\u62e8\u8fd9\u4e2a\u7535\u8bdd\uff0c\u4ee5\u53ca\u7f57\u541b\u90fd\u662f\u5728\u5f97\u77e5\u7231\u8fbe\u96c6\u56e2\u5ba3\u5e03\u8fd9\u4e2a\u60ca\u4eba\u7684\u51b3\u5b9a\u4e4b\u540e\u7b2c\u4e00\u65f6\u95f4\u60f3\u5230\u76f4\u63a5\u627e\u5f20\u606a\u786e\u8ba4\u6d88\u606f\u7684\u771f\u4f2a\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u5c06\u8fd9\u90e8\u5206\u6280\u672f\u62ff\u51fa\u6765\u5171\u4eab\u6bd4\u5728\u4e5d\u4e03\u5e74\u76f4\u63a5\u62ff\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u51fa\u6765\u53d1\u653e\u66f4\u8981\u4ee4\u4eba\u611f\u5230\u9707\u60ca\uff0c\u5bf9\u5efa\u90ba\u5e02\u7684\u6570\u5b57\u957f\u5eca\u6784\u60f3\u6709\u7740\u6700\u76f4\u63a5\u7684\u4fc3\u8fdb\u4f5c\u7528\uff0c\u65e0\u6570\u8d2a\u56fe\u514d\u8d39\u6280\u672f\u7684\u4f01\u4e1a\u5c06\u5230\u5efa\u90ba\u5e02\u3001\u6d77\u5dde\u5e02\u6295\u8d44\u5efa\u5382\u3001\u8bbe\u7acb\u7814\u53d1\u673a\u6784\uff0c\u8fc5\u901f\u5f62\u6210\u89c4\u6a21\u53ef\u89c2\u7684\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u3002</p><p>\u3000\u3000\u8fd9\u662f\u9676\u664b\u3001\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u7ad9\u5728\u81ea\u5df1\u7684\u89d2\u8272\uff0c\u9996\u5148\u5c31\u80fd\u8003\u8651\u5230\u7684\u9526\u6e56\u8fd9\u4e2a\u60ca\u4eba\u51b3\u5b9a\u6240\u80fd\u5e26\u6765\u7684\u597d\u5904\u3002\u5c31\u7b97\u7f57\u541b\u6709\u7740\u6700\u81ea\u79c1\u7684\u6253\u7b97\uff0c\u90fd\u6ca1\u6709\u8138\u8981\u6c42\u9526\u6e56\u505a\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u4f46\u662f\u9526\u6e56\u5374\u4e3b\u52a8\u505a\u51fa\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u5982\u4f55\u4ee4\u4ed6\u4eec\u4e0d\u60ca\u8be7\uff1f\u9996\u5148\u4e5f\u60f3\u5230\u8fd9\u4e16\u754c\u662f\u4e0d\u662f\u53d8\u4e86\uff0c\u6709\u4eba\u6562\u8ddf\u4ed6\u4eec\u5f00\u73a9\u7b11\u4e86\uff1f</p><p>\u3000\u3000\u597d\u5427\uff0c\u56db\u4e2a\u4eba\u7684\u7535\u8bdd\uff0c\u4e00\u4e2a\u90fd\u6020\u6162\u4e0d\u5f97\uff0c\u5f20\u606a\u81ea\u7136\u4e5f\u4e0d\u80af\u4e00\u70b9\u5173\u5b50\u90fd\u4e0d\u5356\u5c31\u76f4\u8a00\u76f8\u544a\uff0c\u8ddf\u8d75\u53cb\u5353\u3001\u9ad8\u771f\u3001\u7f57\u541b\u7b80\u77ed\u7684\u901a\u4e86\u7535\u8bdd\uff0c\u786e\u5b9a\u8fd9\u4e2a\u51b3\u5b9a\u786e\u5b9e\u662f\u9526\u6e56\u505a\u51fa\u6765\u7684\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u4f1a\u6d3e\u5468\u6e38\u4ee3\u8868\u9526\u6e56\u5f53\u9762\u5411\u7701\u59d4\u7701\u653f\u5e9c\u3001\u5e02\u59d4\u5e02\u653f\u5e9c\u6c47\u62a5\uff0c\u4ed6\u5728\u7535\u8bdd\u4e09\u8a00\u4e24\u8bed\u4e5f\u89e3\u91ca\u4e0d\u6e05\u3002</p><p>\u3000\u3000\u51fa\u7535\u68af\u5230\u592e\u89c6\u65b0\u95fb\u90e8\u7684\u7b2c\u4e00\u4f1a\u5ba2\u5ba4\uff0c\u4e0d\u8fdc\u7684\u4e00\u6bb5\u8def\uff0c\u53c8\u63a5\u5230\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u7684\u7535\u8bdd\uff0c\u5012\u662f\u6d77\u5dde\u90a3\u8fb9\u6c89\u5f97\u4f4f\u6c14\uff0c\u6216\u8bb8\u6d88\u606f\u8fd8\u6ca1\u6709\u4f20\u56de\u53bb\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6253\u7535\u8bdd\u8fc7\u6765\u3002\u8fd9\u4e1c\u897f\u5f88\u96be\u8bf4\uff0c\u8ddf\u4f20\u67d3\u75c5\u4f3c\u7684\uff0c\u800c\u4e14\u5927\u90e8\u5206\u624b\u8fb9\u90fd\u6709\u624b\u673a\uff0c\u4e0d\u4f1a\u50cf\u4ee5\u5f80\u6d88\u606f\u7684\u4f20\u64ad\u8fc5\u901f\u4f1a\u5f88\u6162\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4ed6\u968f\u8eab\u7684\u624b\u673a\u4e5f\u6682\u65f6\u4ea4\u7ed9\u5085\u4fca\u5904\u7f6e\uff0c\u8981\u662f\u6d77\u5dde\u90a3\u8fb9\u7684\u7535\u8bdd\uff0c\u5c31\u8bf4\u4f1a\u7531\u4e01\u69d0\u8d1f\u8d23\u51fa\u9762\u89e3\u91ca\uff0c\u5efa\u90ba\u90a3\u8fb9\uff0c\u7531\u5468\u6e38\u8d1f\u8d23\u89e3\u91ca\uff0c\u5176\u4ed6\u7684\u7535\u8bdd\uff0c\u770b\u60c5\u51b5\u5904\u7f6e\u3002\u5f20\u606a\u8d70\u8fdb\u592e\u89c6\u65b0\u95fb\u90e8\u7684\u4f1a\u5ba2\u5385\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u90fd\u7ed9\u7559\u5728\u8fd9\u91cc\uff0c\u9676\u884c\u5065\u3001\u53f6\u5c0f\u6850\u4e5f\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u6b63\u4e0e\u592e\u89c6\u526f\u53f0\u957f\u8d75\u8fdb\u52c7\u6b63\u5728\u804a\u5929\u3002</p><p>\u3000\u3000\u8d75\u8fdb\u52c7\u662f\u4e2a\u5934\u9876\u6709\u4e9b\u5fae\u79c3\u7684\u4e2d\u5e74\u4eba\uff0c\u8eab\u6750\u6709\u4e9b\u5fae\u7684\u53d1\u798f\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u4f5c\u4e3a\u5206\u7ba1\u65b0\u95fb\u90e8\u7684\u526f\u53f0\u957f\uff0c\u5730\u4f4d\u5728\u6bd4\u5176\u4ed6\u526f\u53f0\u957f\u7565\u9ad8\uff0c\u770b\u7740\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u4ed6\u7ad9\u8d77\u6765\u4f38\u8fc7\u624b\u6765\u63e1\u7740\u8bf4\u9053\uff1a\u201c\u5bf9\u9526\u6e56\u5bfb\u6839\u7a76\u5e95\u7684\u65b0\u95fb\uff0c\u6211\u4eec\u90fd\u662f\u8981\u6263\u7740\u4e0d\u53d1\u7684\u3002\u77e5\u9053\u9526\u6e56\u6709\u65e2\u5b9a\u7684\u65b0\u95fb\u7b56\u7565\uff0c\u4f46\u662f\u5462\uff0c\u6211\u4eec\u592e\u89c6\u603b\u8981\u4eab\u53d7\u4e00\u4e9b\u7279\u6b8a\u5f85\u9047\uff0c\u5c31\u63d0\u4e09\u4e2a\u95ee\u9898\u2026\u2026\u5c31\u7b97\u662f\u5f53\u6ee1\u8db3\u6211\u4e2a\u4eba\u7684\u597d\u5947\u5fc3\u4e5f\u884c\u3002\u201d\u8d75\u8fdb\u52c7\u4f38\u51fa\u4e09\u4e2a\u624b\u6307\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4ecb\u7ecd\u8d75\u8fdb\u52c7\u8eab\u8fb9\u7684\u592e\u89c6\u65b0\u95fb\u90e8\u4e3b\u4efb\u4f0d\u6653\u51ef\u7ed9\u5f20\u606a\u8ba4\u8bc6\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u592e\u89c6\u65b0\u95fb\u90e8\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u9000\u4e86\u51fa\u53bb\uff0c\u4e0d\u53c2\u4e0e\u9ad8\u5c42\u95f4\u7684\u8c08\u8bdd\u3002</p><p>\u3000\u3000\u9762\u5bf9\u8d75\u8fdb\u52c7\u7684\u8981\u6c42\uff0c\u5f20\u606a\u4e5f\u5f88\u96be\u62d2\u7edd\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u4f01\u56fe\uff0c\u8fd8\u7684\u786e\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u8d75\u8fdb\u52c7\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u6211\u5c31\u5f00\u59cb\u63d0\u95ee\u4e86\u2026\u2026\u7b2c\u4e00\u95ee\u9898\uff0c\u5bf9\u9526\u6e56\u7684\u8fd9\u6b21\u51b3\u5b9a\uff0c\u4f5c\u4e3a\u6280\u672f\u7684\u8f6c\u8ba9\u65b9\uff0c\u5fb7\u4eea\u662f\u4ec0\u4e48\u6001\u5ea6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4ece\u5fb7\u4eea\u63a5\u53d7\u76f8\u5173\u6280\u672f\u4e4b\u65f6\uff0c\u5e76\u6ca1\u6709\u7b7e\u7f72\u7981\u6b62\u6280\u672f\u5411\u7b2c\u4e09\u65b9\u6269\u6563\u6216\u516c\u5f00\u7684\u534f\u8bae\uff0c\u53e6\u5916\uff0c\u6280\u672f\u7684\u516c\u5f00\uff0c\u5bf9\u5fb7\u4eea\u6709\u5229\u800c\u65e0\u5f0a\uff0c\u5373\u4f7f\u8fd9\u6837\uff0c\u6211\u4eec\u6628\u5929\u8fd8\u662f\u7d27\u6025\u8054\u7cfb\u5fb7\u4eea\u7684\u9ad8\u5c42\u8fdb\u884c\u78cb\u5546\uff0c\u8fd9\u4e2a\u51b3\u5b9a\u5df2\u7ecf\u5f97\u5230\u5fb7\u4eea\u7684\u8c05\u89e3\u3002\u201d</p><p>\u3000\u3000\u201c\u975e\u5e38\u6807\u51c6\u7684\u56de\u7b54\u554a\uff0c\u201d\u8d75\u8fdb\u52c7\u6478\u4e86\u6478\u5fae\u79c3\u7684\u5934\u9876\uff0c\u56de\u8fc7\u5934\u95ee\u9648\u4fe1\u751f\uff0c\u201c\u8fd9\u4e2a\u95ee\u9898\u8bf7\u9648\u603b\u4f60\u6765\u56de\u7b54\uff0c\u662f\u4e0d\u662f\u4e5f\u662f\u540c\u6837\u7684\u7b54\u6848\uff1f\u5e94\u8be5\u8fd8\u6709\u5176\u4ed6\u5b9e\u8d28\u6027\u7684\u5185\u5bb9\uff0c\u541d\u4e8e\u900f\u9732\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u7b97\u7b2c\u4e8c\u95ee\u9898\uff1f\u201d\u9648\u4fe1\u751f\u5f00\u73a9\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u4e0d\uff0c\u201d\u8d75\u8fdb\u52c7\u6447\u624b\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u624d\u95ee\u7b2c\u4e8c\u95ee\u9898\uff0c\u9526\u6e56\u7684\u610f\u56fe\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5f88\u95ee\u9898\u5f88\u5927\u554a\uff0c\u53ef\u4ee5\u62b5\u4e24\u4e2a\u95ee\u9898\uff1f\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54c8\u54c8\uff0c\u201d\u8d75\u8fdb\u52c7\u5927\u7b11\u8d77\u6765\uff0c\u201c\u53ea\u770b\u606a\u5c11\u7684\u8d44\u6599\u4e0d\u8bc6\u606a\u5c11\u7684\u98ce\u91c7\uff0c\u8fd9\u53e5\u8bdd\u5c31\u5b8c\u5168\u80fd\u611f\u53d7\u5230\u3002\u884c\uff0c\u8fd9\u7b97\u662f\u6211\u7684\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u592e\u89c6\u65b0\u95fb\u90e8\uff0c\u5f97\u8981\u7b97\u534a\u4e2a\u60c5\u62a5\u90e8\u95e8\uff0c\u5f20\u606a\u4e0d\u4ee5\u4e3a\u8d75\u8fdb\u52c7\u5bf9\u81ea\u5df1\u7684\u60c5\u51b5\u6709\u4ec0\u4e48\u4e0d\u6e05\u695a\u7684\u3002</p><p>\u3000\u3000\u684c\u4e0a\u653e\u7740\u51e0\u53ea\u7eb8\u676f\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u90fd\u53d7\u5230\u8336\u6c34\u6b3e\u5f85\uff0c\u5012\u662f\u81ea\u5df1\u8fc7\u6765\uff0c\u8981\u5148\u6ee1\u8db3\u8d75\u8fdb\u52c7\u7684\u597d\u5947\u5fc3\u3002\u5f20\u606a\u4f38\u51fa\u624b\u6307\u91ae\u4e86\u8336\u6c34\u5728\u73bb\u7483\u51e0\u4e0a\u753b\u4e86\u4e00\u5706\u5708\uff0c\u5728\u5706\u5708\u70b9\u5185\u70b9\u4e86\u51e0\u6ef4\u6c34\u6e0d\uff0c\u5728\u5706\u5708\u5916\u70b9\u4e86\u51e0\u6ef4\u6c34\u6e0d\uff0c\u8bf4\u9053\uff1a\u201c\u7684\u786e\u662f\u6709\u4e9b\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u4f01\u56fe\u3002\u4e0d\u8bf4\u592a\u8fdc\uff0c\u5c31\u8bf4\u56fd\u5185\u7684\u624b\u673a\u4ea7\u4e1a\uff0c\u51e0\u5bb6\u56fd\u4ea7\u624b\u673a\u5382\u5546\uff0c\u5305\u62ec\u9526\u6e56\u5728\u5185\uff0c\u6709\u4e00\u9053\u8584\u8584\u7684\u57ce\u5899\u56f4\u7740\uff0c\u5374\u5982\u56f0\u57ce\u4e4b\u517d\uff0c\u56f4\u7740\u4e00\u5757\u80a5\u8089\u5728\u5fd8\u60c5\u7684\u6495\u54ac\uff0c\u5374\u5fd8\u4e86\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u5899\u5916\u89ca\u89ce\u7740\u8981\u51b2\u8fdb\u6765\uff0c\u4ed6\u4eec\u4e0d\u4ec5\u8981\u5403\u6389\u8fd9\u5757\u80a5\u8089\uff0c\u751a\u81f3\u60f3\u5c06\u5899\u5185\u7684\u56f0\u517d\u90fd\u4e00\u8d77\u5543\u7b6e\u5e72\u51c0\u3002\u8fd9\u9053\u5899\u7684\u6839\u57fa\u5df2\u7ecf\u6d6e\u52a8\uff0c\u4e00\u63a8\u5c31\u8981\u5012\u6389\uff0c\u9526\u6e56\u505a\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u662f\u770b\u5230\u8fd9\u6837\u5c8c\u5c8c\u53ef\u5371\u7684\u5c40\u52bf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u8fd9\u6b21\u516c\u7136\u62b5\u5236\u5916\u8d44\u54c1\u724c\u5165\u573a\u4e5f\u662f\u8fd9\u79cd\u8003\u8651\uff1f\u201d\u8d75\u8fdb\u52c7\u5f88\u81ea\u7136\u5c31\u8054\u60f3\u5230\u7231\u8fbe\u96c6\u56e2\u524d\u4e9b\u5929\u7684\u884c\u4e3a\uff0c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6216\u8bb8\u592a\u5927\u516c\u65e0\u79c1\uff0c\u4e5f\u8bf4\u670d\u4e0d\u4e86\u522b\u4eba\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u505a\u51fa\u8fd9\u9879\u51b3\u5b9a\uff0c\u9526\u6e56\u77ed\u671f\u7684\u5229\u76ca\u6216\u8bb8\u53d7\u635f\uff0c\u4e5f\u4e0d\u8bf4\u53d7\u635f\uff0c\u53ea\u662f\u65e0\u6cd5\u8fbe\u5230\u77ed\u671f\u5229\u76ca\u6700\u5927\u5316\u7f62\u4e86\uff0c\u4f46\u662f\u8003\u8651\u5230\u957f\u8fdc\u7684\u5229\u76ca\uff0c\u5728\u5efa\u90ba\u3001\u5728\u6d77\u5dde\uff0c\u5f62\u6210\u5b8c\u6574\u800c\u575a\u5b9e\u7684\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u5bf9\u9526\u6e56\u5229\u5927\u4e8e\u5f0a\uff0c\u6211\u4eec\u8981\u627f\u8ba4\u8fd9\u6837\u4e00\u4e2a\u4e8b\u5b9e\uff1a\u5c31\u50cf\u6a61\u6811\u56ed\u8ba1\u5212\u987a\u5229\u5c55\u5f00\uff0c\u9526\u6e56\u4e5f\u6ca1\u6709\u80fd\u529b\u72ec\u529b\u505a\u5b8c\u6240\u6709\u7684\u529f\u8bfe\u2026\u2026\u9526\u6e56\u9009\u62e9\u7684\u5bf9\u624b\u4e0d\u662f\u8054\u4fe1\uff0c\u4e0d\u662f\u8054\u8baf\uff0c\u4e5f\u4e0d\u662f\u4e1c\u5174\uff0c\u800c\u662f\u4e09\u661f\u3001\u662f\u7231\u7acb\u4fe1\u3001\u662f\u6469\u6258\u7f57\u62c9\uff0c\u5f88\u8270\u96be\uff0c\u4f46\u9526\u6e56\u5fc5\u987b\u8981\u505a\u8fd9\u6837\u7684\u9009\u62e9\uff0c\u6240\u4ee5\u6211\u4eec\u5fc5\u987b\u8981\u8ba1\u7b97\u90a3\u5835\u5899\u5012\u5d29\u7684\u65f6\u95f4\uff0c\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u53c8\u644a\u624b\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u56de\u7b54\u8fd9\u4e48\u591a\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u8bdd\uff0c\u591f\u4e24\u4e2a\u95ee\u9898\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\uff0c\u4e5f\u5c31\u662f\u4e0d\u516c\u5f00\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e4b\u524d\u62c5\u4efb\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u65f6\uff0c\u5c31\u4e0e\u8d75\u8fdb\u52c7\u6709\u8fc7\u63a5\u89e6\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8d75\u53f0\u957f\u662f\u4e0d\u662f\u653e\u6211\u4eec\u56de\u53bb\u5403\u4e2d\u9910\uff1f\u592e\u89c6\u7684\u5de5\u4f5c\u9910\uff0c\u6211\u4eec\u53ef\u4e0d\u6562\u4eab\u53d7\u3002\u201d</p><p>\u3000\u3000\u4f5c\u4e3a\u56fd\u5185\u65b0\u95fb\u8206\u8bba\u4f53\u7cfb\u6700\u4e0a\u5c42\u7684\u4eba\u7269\u4e4b\u4e00\uff0c\u8d75\u8fdb\u52c7\u5bf9\u9526\u6e56\u4ee5\u53ca\u5f20\u606a\u4e2a\u4eba\u7684\u60c5\u51b5\u6bd4\u592e\u89c6\u5176\u4ed6\u51e0\u540d\u526f\u53f0\u957f\u8981\u6e05\u695a\u7684\u591a\u3002\u6709\u4e9b\u5bf9\u9526\u6e56\u8ffd\u6839\u7a76\u5e95\u7684\u62a5\u9053\uff0c\u8fd8\u662f\u4ed6\u4eb2\u81ea\u6263\u4e0b\u4e0d\u53d1\u7684\uff0c\u8fd9\u662f\u4e00\u4e2a\u6ce5\u6c99\u4ff1\u4e0b\u7684\u65f6\u4ee3\uff0c\u5373\u4f7f\u9526\u6e56\u7684\u80cc\u666f\u6e05\u6e05\u767d\u767d\uff0c\u4f46\u662f\u5c06\u771f\u5b9e\u7684\u9526\u6e56\u516c\u5e03\u4e8e\u4f17\uff0c\u4e5f\u4f1a\u5f15\u8fdb\u5e7f\u6cdb\u7684\u8d28\u7591\uff0c\u5f53\u524d\u7684\u653f\u6cbb\u6c1b\u56f4\u662f\u4e0d\u5141\u8bb8\u51fa\u73b0\u8fd9\u79cd\u8d28\u7591\u7684\uff1b\u8fd9\u4e5f\u662f\u5f53\u524d\u65b0\u95fb\u5a92\u4f53\u5904\u7406\u4e00\u4e9b\u654f\u611f\u6027\u62a5\u9053\u65f6\u4e0d\u80fd\u903e\u8d8a\u7684\u7ea2\u7ebf\u3002\u4e4b\u524d\u96c6\u4e2d\u62a5\u9053\u5efa\u90ba\u7684\u6676\u5706\u5382\u9879\u76ee\uff0c\u4e5f\u662f\u523b\u610f\u6a21\u7cca\u6389\u9526\u6e56\u7684\u80cc\u666f\u3002</p><p>\u3000\u3000\u56fd\u5185\u7684\u5a92\u4f53\u4e00\u822c\u53ea\u5173\u6ce8\u7231\u8fbe\u96c6\u56e2\uff0c\u800c\u9ad8\u5c42\u4eba\u7269\u5374\u80fd\u76f4\u89c6\u6838\u5fc3\uff0c\u5173\u6ce8\u9526\u6e56\u7684\u52a8\u6001\u3002\u771f\u771f\u5207\u5207\u7684\u77e5\u9053\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u4e4b\u95f4\u5173\u8054\u5173\u7cfb\u7684\u4eba\uff0c\u4e5f\u4ec5\u4ec5\u9650\u4e8e\u4e00\u5c0f\u64ae\u3002\u516c\u4f17\u83b7\u5f97\u4fe1\u606f\u7684\u6e20\u9053\u6bd5\u7adf\u6709\u9650\uff0c\u800c\u4e14\u662f\u53d7\u63a7\u5236\u7684\u3002</p><p>\u3000\u3000\u8d75\u8fdb\u52c7\u601d\u8003\u65f6\u7684\u4e60\u60ef\u52a8\u4f5c\u5c31\u662f\u624b\u6307\u8f7b\u53e9\u4e0b\u9894\uff0c\u4ed6\u5728\u601d\u8003\u5f20\u606a\u7ed9\u4ed6\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u7684\u7b54\u6848\uff0c\u8f7b\u58f0\u611f\u6168\u8bf4\u9053\uff1a\u201c\u5927\u667a\u82e5\u611a\u7684\u6c14\u5ea6\uff0c\u56e0\u6b64\u4e4b\u524d\u7edd\u5c11\u89c1\u8bc6\u5230\uff0c\u6240\u4ee5\u624d\u6709\u5982\u6b64\u65fa\u76db\u7684\u597d\u5947\u5fc3\uff0c\u771f\u662f\u60ed\u6127\u3002\u201d\u77e5\u9053\u9526\u6e56\u629b\u51fa\u8fd9\u6837\u7684\u6d88\u606f\uff0c\u4f17\u4eba\u4e00\u5b9a\u4f1a\u5fd9\u5f97\u8ddf\u8f66\u8f71\u8f98\u4f3c\u7684\uff0c\u9526\u6e56\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u52bf\u5fc5\u8fd8\u4f1a\u60ca\u52a8\u66f4\u9ad8\u5c42\uff0c\u4e5f\u4e0d\u4fbf\u5c06\u4ed6\u4eec\u633d\u7559\u4e0b\u6765\u7a77\u8ffd\u4e0d\u820d\u7684\u8ffd\u95ee\u4e0b\u53bb\uff0c\u518d\u6b21\u4f38\u51fa\u624b\u4e0e\u5f20\u606a\u63e1\u522b\uff0c\u201c\u4e0b\u5348\u7684\u7ade\u6807\u8fd8\u8981\u7ee7\u7eed\u8fdb\u884c\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u7f3a\u5e2d\u5427\uff1f\u6211\u603b\u5f52\u6709\u5e78\u80fd\u8bf7\u4f60\u4eec\u4e2d\u7684\u4e00\u4f4d\u5171\u540c\u5348\u9910\uff0c\u867d\u7136\u4e5f\u53ea\u662f\u5de5\u4f5c\u9910\u2026\u2026\u201d\u4ed6\u60f3\u5404\u5168\u9762\u7684\u4e86\u89e3\u8fd9\u5bb6\u4f01\u4e1a\uff0c\u800c\u4e0d\u4ec5\u4ec5\u662f\u901a\u8fc7\u51b7\u51b0\u51b0\u7684\u8d44\u6599\uff0c\u90a3\u5c31\u8981\u591a\u63a5\u89e6\u8fd9\u5bb6\u4f01\u4e1a\u5185\u90e8\u6d3b\u751f\u751f\u7684\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u4fa7\u5934\u5bf9\u6768\u4e91\u3001\u9676\u884c\u5065\u3001\u53f6\u5c0f\u6850\u4e09\u4eba\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u8bf4\u4f60\u4eec\u7559\u4e0b\u6765\u4e0e\u8d75\u53f0\u957f\u591a\u4ea4\u6d41\u591a\u4ea4\u6d41\uff1f\u201d</p><p>\u3000\u3000\u9999\u96ea\u6d77\u7ade\u5f97\u6807\u738b\u5e7f\u544a\uff0c\u4eab\u53d7\u600e\u6837\u7684\u4f18\u60e0\u653f\u7b56\u8fd8\u8981\u8ddf\u592e\u89c6\u7ee7\u7eed\u8c08\u5224\uff0c\u867d\u7136\u94b1\u6587\u4e1c\u662f\u5206\u7ba1\u7684\u526f\u53f0\u957f\uff0c\u4f46\u662f\u5728\u4eca\u5929\u7684\u5c40\u52bf\u4e0b\uff0c\u60f3\u5fc5\u94b1\u6587\u4e1c\u4e0e\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u4e5f\u4e0d\u4f1a\u7279\u522b\u7684\u5201\u96be\u9999\u96ea\u6d77\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4e00\u540d\u592e\u89c6\u7684\u5de5\u4f5c\u4eba\u5458\u6572\u95e8\u8fdb\u6765\u5728\u8d75\u8fdb\u52c7\u8ddf\u524d\u8033\u8bed\u4e86\u51e0\u53e5\uff0c\u8d75\u8fdb\u52c7\u8bf4\u9053\uff1a\u201c\u90ae\u7535\u90e8\u803f\u91cd\u9633\u90e8\u957f\u7684\u5bfb\u4eba\u7535\u8bdd\u5df2\u7ecf\u6253\u5230\u6211\u7684\u529e\u516c\u684c\u4e0a\u4e86\u3002\u6211\u8be5\u600e\u4e48\u56de\u7b54\uff1f\u8fd8\u662f\u8ddf\u8054\u91cd\u9633\u90e8\u957f\u8bf4\u4f60\u4eec\u6025\u7740\u53bb\u5403\u5348\u9910\u521a\u79bb\u5f00\uff1f\u201d</p><p>\u3000\u3000\u901f\u5ea6\u597d\u5feb\uff0c\u5f20\u606a\u5fc3\u60f3\u8096\u745e\u6c11\u53ea\u6015\u4eba\u8fd8\u6ca1\u6709\u51fa\u592e\u89c6\u5927\u95e8\u5427\uff1f</p><p>\u3000\u3000\u90e8\u59d4\u91cc\u6709\u51e0\u4e2a\u90e8\u957f\u662f\u9526\u6e56\u6562\u4e0d\u7ed9\u9762\u5b50\u7684\uff1f\u629b\u51fa\u8fd9\u4e48\u5927\u7684\u9975\uff0c\u5c31\u662f\u6307\u671b\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u62a2\u7740\u4e0a\u94a9\uff0c\u8fd9\u4e09\u5bb6\u4f01\u4e1a\u81ea\u7136\u4e5f\u662f\u4e0d\u7518\u5fc3\u53ea\u4e0e\u5176\u4ed6\u4f01\u4e1a\u4eab\u53d7\u540c\u7b49\u7684\u6743\u529b\u3002\u884c\u554a\uff0c\u53e3\u5b50\u65e2\u7136\u653e\u5f00\u4e86\uff0c\u4e5f\u4e0d\u53ef\u80fd\u53ea\u9488\u5bf9\u8fd9\u4e09\u5bb6\u4f01\u4e1a\u8bbe\u7f6e\u4ec0\u4e48\u969c\u788d\uff0c\u4f46\u662f\u4ed6\u4eec\u60f3\u8981\u591a\u5f97\u5230\u4e9b\u4ec0\u4e48\uff0c\u5c31\u4e5f\u8981\u505a\u51fa\u4ed8\u51fa\u7684\u51c6\u5907\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9648\u4fe1\u751f\u4e00\u773c\uff0c\u9648\u4fe1\u751f\u5fc3\u9886\u795e\u4f1a\uff0c\u5bf9\u8d75\u8fdb\u52c7\u8bf4\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8bf7\u90ae\u7535\u90e8\u90a3\u91cc\u7684\u7535\u8bdd\u8f6c\u5230\u6211\u7684\u624b\u673a\u4e0a\u6765\uff1f\u201d\u8fc5\u901f\u62ff\u7b14\u6284\u4e0b\u624b\u673a\u53f7\u7801\u4ea4\u7ed9\u592e\u89c6\u7684\u5de5\u4f5c\u4eba\u5458\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0e\u90ae\u7535\u90e8\u90e8\u957f\u803f\u91cd\u9633\u7b80\u77ed\u901a\u8fc7\u7535\u8bdd\uff0c\u53c8\u4e0e\u8054\u4fe1\u96c6\u56e2\u603b\u88c1\u8096\u745e\u6c11\u901a\u8fc7\u7535\u8bdd\uff0c\u7ea6\u5b9a\u4e0b\u5348\u5728\u7231\u8fbe\u96c6\u56e2\u5317\u4eac\u5206\u516c\u53f8\u89c1\u9762\u6d3d\u8c08\uff0c\u901a\u77e5\u53f8\u673a\u5c06\u8f66\u5f00\u5230\u592e\u89c6\u5458\u5de5\u7684\u5185\u90e8\u505c\u8f66\u573a\uff0c\u5f20\u606a\u4ed6\u4eec\u4ece\u90a3\u91cc\u4e0a\u8f66\u3002\u521a\u5750\u5230\u8f66\u4e0a\uff0c\u7535\u5b50\u5de5\u4e1a\u90e8\u7684\u7535\u8bdd\u5c31\u6253\u5f00\u9648\u4fe1\u751f\u7684\u624b\u673a\u4e0a\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u4e24\u5bb6\u516c\u53f8\u7684\u52a8\u4f5c\u8981\u7b97\u6162\u4e86\u534a\u62cd\uff0c\u4e09\u5bb6\u516c\u53f8\u4e00\u8d77\u8c08\u597d\u4e86\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u5962\u671b\u4ed6\u4eec\u7b54\u5e94\u591a\u82db\u523b\u7684\u6761\u4ef6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f00\u8f66\u8fd4\u56de\u5317\u4eac\u5206\u516c\u53f8\u9014\u4e2d\uff0c\u63a5\u5230\u5317\u4eac\u5206\u516c\u53f8\u526f\u603b\u7ecf\u7406\u59da\u575a\u7684\u7535\u8bdd\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u5728\u592e\u89c6\u5de5\u4f5c\u4eba\u5458\u7684\u63a9\u62a4\u4e0b\u64a4\u51fa\u4f1a\u573a\uff0c\u5176\u4ed6\u5a92\u4f53\u7684\u8bb0\u8005\u90fd\u62e5\u5230\u5206\u516c\u53f8\u697c\u4e0b\uff0c\u4e5f\u6709\u8bb8\u591a\u4f01\u4e1a\u6d3e\u4eba\u76f4\u63a5\u5230\u5206\u516c\u53f8\u8be2\u95ee\u8fdb\u4e00\u6b65\u7684\u8be6\u60c5\u3002\u5206\u516c\u53f8\u5411\u5916\u516c\u5e03\u7684\u51e0\u53ea\u5ea7\u673a\u90fd\u5dee\u70b9\u7ed9\u6253\u7206\u6389\u4e86\uff0c\u6bd4\u62e8\u8fdb\u7535\u53f0\u70ed\u7ebf\u8fd8\u96be\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5085\u4fca\u627e\u4e86\u4e00\u4e2a\u4eba\u770b\u4e0d\u5230\u89d2\u843d\u4e0b\u8f66\u6765\uff0c\u7b49\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u8fdb\u697c\u7684\u6df7\u4e71\u7a0d\u5e73\u606f\u4e4b\u540e\uff0c\u4ed6\u4eec\u624d\u8d81\u7740\u522b\u4eba\u4e0d\u6ce8\u610f\u7684\u5f53\u513f\u8d70\u8fdb\u697c\u91cc\u53bb\u3002</p></article>"}}}]);
