(window.webpackJsonp=window.webpackJsonp||[]).push([[684],{707:function(p){p.exports={chapterTitle:null,title:"\u7b2c683\u7ae0 \u9650\u5236",content:"<article><h2>\u7b2c683\u7ae0 \u9650\u5236</h2><p>\u3000\u3000\u8096\u745e\u6c11\u5fc3\u91cc\u4e07\u822c\u4e0d\u60f3\u4e0e\u8054\u8baf\u7684\u5b59\u5fd7\u521a\u3001\u4e1c\u5174\u7684\u5ed6\u7ea2\u96f7\u540c\u8def\uff0c\u4f46\u60f3\u5230\u8fd9\u53ef\u80fd\u662f\u7231\u8fbe\u7684\u5b89\u6392\uff0c\u4e07\u822c\u65e0\u5948\uff0c\u4e5f\u53ea\u6709\u5404\u81ea\u5750\u8f66\u62b5\u7231\u8fbe\u96c6\u56e2\u4f4d\u4e8e\u4e2d\u5173\u6751\u7684\u5206\u516c\u53f8\uff1b\u5750\u5728\u8f66\u91cc\u4e5f\u5728\u60f3\u7231\u8fbe\u8fd9\u4e48\u5b89\u6392\u7684\u7528\u610f\u3002</p><p>\u3000\u3000\u4e0e\u5468\u8fb9\u7684\u5efa\u7b51\u4e0d\u540c\uff0c\u7231\u8fbe\u5927\u53a6\u6709\u4e00\u5ea7\u7cbe\u5fc3\u4fee\u6cbb\u7684\u524d\u5ead\u82b1\u56ed\uff0c\u751f\u8da3\u76ce\u7136\u7684\u6797\u6811\uff0c\u5c06\u73bb\u7483\u5e55\u5899\u906e\u8986\u7684\u73b0\u4ee3\u5efa\u7b51\u4e0e\u4e1c\u65b9\u5ead\u56ed\u5b8c\u7f8e\u76f8\u878d\u5408\uff0c\u8fd8\u7ed9\u884c\u7ecf\u7684\u65c5\u4eba\u63d0\u8fc7\u4e00\u4e2a\u4f11\u61a9\u7684\u573a\u6240\u3002\u72ec\u7acb\u7ad6\u4e8e\u524d\u5ead\u82b1\u56ed\u4e00\u89d2\u7684\u5de8\u578b\u5e06\u72b6\u697c\u6807\u6709\u7740\u73b0\u4ee3\u96d5\u5851\u7684\u8d28\u611f\uff0c\u4f7f\u5f97\u8fd9\u680b\u5341\u4e8c\u5c42\u697c\u9ad8\u7684\u5efa\u7b51\u8fe5\u7136\u6709\u4e00\u79cd\u522b\u5177\u98ce\u683c\u7684\u6c14\u6d3e\u3002</p><p>\u3000\u3000\u8054\u8baf\u4e0e\u4e1c\u5174\u7684\u603b\u90e8\u90fd\u4e0d\u5728\u5317\u4eac\uff0c\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u5c31\u7b97\u5e38\u5230\u5317\u4eac\u6765\u516c\u5e72\uff0c\u4e5f\u5c11\u6709\u673a\u4f1a\u7ecf\u8fc7\u6b64\u5730\uff0c\u624d\u7b2c\u4e00\u6b21\u770b\u5230\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u5206\u516c\u53f8\u5927\u53a6\u3002</p><p>\u3000\u3000\u8fd9\u662f\u7231\u8fbe\u96c6\u56e2\u5728\u9996\u90fd\u663e\u793a\u51fa\u6765\u7684\u6c14\u6d3e\uff0c\u4e0d\u662f\u7b80\u5355\u7684\u8d22\u5927\u6c14\u7c97\uff0c\u5f70\u663e\u51fa\u6765\u7684\u7cbe\u81f4\uff0c\u4f3c\u4e4e\u4e5f\u662f\u7231\u8fbe\u4ea7\u54c1\u7684\u67d0\u79cd\u6c14\u8d28\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4e0e\u5317\u4eac\u5206\u516c\u53f8\u526f\u603b\u7ecf\u7406\u59da\u575a\u7ad9\u5728\u697c\u524d\u7684\u53f0\u9636\u4e0a\u76f8\u8fce\u2014\u2014\u867d\u7136\u7231\u8fbe\u96c6\u56e2\u4e00\u5f00\u59cb\u5c31\u8868\u660e\u8fd1\u671f\u4e0d\u4f1a\u5411\u5a92\u4f53\u8fdb\u4e00\u6b65\u900f\u9732\u76f8\u5173\u6d88\u606f\u7684\u6001\u5ea6\uff0c\u4f46\u662f\u4ecd\u6709\u4e0d\u5c11\u5a92\u4f53\u8bb0\u8005\u575a\u5b88\u5728\u7231\u8fbe\u5927\u53a6\u7684\u697c\u4e0b\uff0c\u770b\u5230\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4e0b\u697c\u6765\uff0c\u7d27\u63a5\u7740\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u7684\u8001\u603b\u53c8\u5148\u540e\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u5b88\u5728\u8fd9\u8fb9\u7684\u8bb0\u8005\u987f\u65f6\u5174\u594b\u8d77\u6765\uff0c\u5341\u591a\u4eba\uff0c\u62ff\u7740\u76f8\u673a\u5c31\u62e5\u8fc7\u6765\u3002</p><p>\u3000\u3000\u90ae\u7535\u90e8\u76ee\u524d\u4e3a\u6b62\u53ea\u53d1\u653e\u7684\u56db\u5f20\u624b\u673a\u6267\u7167\u5c31\u843d\u5728\u7231\u8fbe\u3001\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u56db\u5bb6\u516c\u53f8\u624b\u4e2d\uff0c\u8bf4\u662f\u5b8c\u5168\u7edf\u6cbb\u56fd\u4ea7\u624b\u673a\u4ea7\u4e1a\u7684\u56db\u5de8\u5934\u4e00\u70b9\u4e5f\u4e0d\u8fc7\u3002\u5f53\u7231\u8fbe\u96c6\u56e2\u4e2d\u5348\u5728\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u5ba3\u5e03\u90a3\u4e2a\u60ca\u4e16\u9a87\u4fd7\u7684\u51b3\u5b9a\u4e4b\u540e\uff0c\u5a92\u4f53\u9996\u5148\u5c31\u60f3\u77e5\u9053\u8054\u4fe1\u3001\u8054\u8baf\u4ee5\u53ca\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u7684\u6001\u5ea6\uff0c\u5948\u4f55\u4e09\u5bb6\u516c\u53f8\u7684\u8001\u603b\u4e0e\u5de5\u4f5c\u4eba\u5458\u90fd\u4e09\u7f04\u5176\u53e3\u3001\u8bb3\u83ab\u5982\u6df1\uff0c\u8fd9\u65f6\u5019\u770b\u5230\u56db\u5bb6\u516c\u53f8\u8001\u603b\u5728\u7231\u8fbe\u5927\u53a6\u524d\u76f8\u805a\uff0c\u5982\u4f55\u4ee4\u4eba\u4e0d\u5174\u594b\uff1f</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u8fd9\u8fb9\u65e9\u6709\u9884\u5907\uff0c\u5de5\u4f5c\u4eba\u5458\u8fc5\u901f\u62a4\u9001\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u4ee5\u53ca\u4ed6\u4eec\u7684\u968f\u884c\u4eba\u5458\u8fdb\u5165\u5927\u53a6\u91cc\u9762\uff0c\u5c06\u8ffd\u9010\u8fc7\u6765\u7684\u8bb0\u8005\u6321\u5728\u540e\u9762\u4e0d\u8ba9\u9760\u8fd1\uff0c\u76f4\u5230\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u966a\u8096\u745e\u6c11\u7b49\u4eba\u8fdb\u4e86\u7535\u68af\uff0c\u59da\u575a\u624d\u5bf9\u8bb0\u8005\u505a\u4e86\u4e00\u4e2a\u7b80\u77ed\u7684\u8bf4\u660e\u3002</p><p>\u3000\u3000\u8054\u4fe1\u662f\u90ae\u7535\u90e8\u5e74\u540e\u5728\u539f\u534e\u5149\u7535\u5b50\u516c\u53f8\u57fa\u7840\u4e0a\u62fc\u51d1\u51fa\u6765\u7684\u5927\u578b\u4f01\u4e1a\uff0c\u5c5e\u4e8e\u7535\u5b50\u4ea7\u4e1a\u9886\u57df\u65b0\u5174\u7684\u52bf\u529b\u3002\u8054\u8baf\u3001\u4e1c\u5174\u7684\u6839\u57fa\u5219\u8981\u6df1\u539a\u5f97\u591a\uff0c\u6210\u7acb\u4e8e\u516b\u4e09\u5e74\u7684\u8054\u8baf\u7535\u5b50\u53bb\u5e74\u4e94\u6708\u521d\u5c31\u5728\u6df1\u5733\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u4e0a\u5e02\u4ea4\u6613\uff0c\u662f\u56fd\u5185\u4e3a\u6570\u4e0d\u591a\u7684\u51e0\u5bb6\u62e5\u6709\u7535\u5b50\u7ec4\u4ef6\u751f\u4ea7\u80fd\u529b\u7684\u7535\u5b50\u5382\u5546\uff0c\u4e3b\u8981\u4ea7\u54c1\u6709\u7535\u8bdd\u673a\u3001\u6253\u5370\u673a\u3001\u8ba1\u7b97\u673a\uff0c\u4e5d\u516d\u5e74\uff0c\u8054\u8baf\u7535\u5b50\u4e0e\u56fd\u5185\u6700\u5927\u5f69\u7535\u4f01\u4e1a\u4e4b\u4e00\u3001\u4eca\u5e74\u5e74\u521d\u8fd8\u8fdb\u5165\u789f\u673a\u4ea7\u4e1a\u7684\u4e1c\u5174\u7535\u5b50\u5206\u5217\u7535\u5b50\u767e\u5f3a\u4f01\u4e1a\u6392\u540d\u7b2c\u4e03\u3001\u7b2c\u5341\u4e00\u3002</p><p>\u3000\u3000\u65e9\u5728\u201c\u516b\u4e94\u201d\u671f\u95f4\uff0c\u56fd\u5bb6\u786e\u5b9a\u7684\u300aGSM\u6570\u5b57\u79fb\u52a8\u901a\u4fe1\u624b\u673a\u57fa\u672c\u6280\u672f\u4e0e\u8bbe\u5907\u7684\u7814\u7a76\u8bfe\u9898\u300b\u653b\u5173\u9879\u76ee\u5c31\u7531\u7535\u5b50\u5de5\u4e1a\u90e8\u65d7\u4e0b\u7684\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\u627f\u62c5\uff0c\u53bb\u5e74\u5e74\u4e2d\u5c31\u5b8c\u6210\u6027\u80fd\u6837\u673a\u5e76\u901a\u8fc7\u6280\u672f\u9274\u5b9a\uff0c\u53bb\u5e74\u5e74\u5e95\uff0c\u5728\u7535\u5b50\u5de5\u4e1a\u90e8\u7684\u4fc3\u4f7f\u4e0b\uff0c\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\uff08\u7535\u5b50\u4e03\u6240\uff09\u4e0e\u8054\u8baf\u3001\u4e1c\u5174\u4e24\u5bb6\u516c\u53f8\u8fdb\u884c\u4ea7\u5b66\u7814\u5408\u4f5c\uff0c\u5c06\u76f8\u5173\u6280\u672f\u540c\u65f6\u8f6c\u8ba9\u7ed9\u8054\u8baf\u4e0e\u4e1c\u5174\u3002\u4e5f\u5c31\u662f\u8bf4\u8054\u8baf\u4e0e\u4e1c\u5174\u4e24\u5bb6\u516c\u53f8\u540c\u65f6\u5177\u5907\u4e86\u6570\u5b57\u624b\u673a\u7684\u57fa\u7840\u6280\u672f\uff0c\u8fd9\u4e5f\u662f\u8054\u8baf\u3001\u4e1c\u5174\u80fd\u4e0e\u8054\u4fe1\u3001\u7231\u8fbe\u540c\u65f6\u4ece\u90ae\u7535\u90e8\u83b7\u5f97\u624b\u673a\u6267\u7167\u7684\u5173\u952e\u56e0\u7d20\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7231\u8fbe\u4ece\u5fb7\u4eea\u83b7\u5f97\u7684\u662f\u4e8c\u4e09\u6d41\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u4f46\u662f\u4ee5\u6b64\u6280\u672f\u4e0e\u5de5\u827a\u4e3a\u57fa\u7840\u751f\u4ea7\u51fa\u6765\u7684i08\u4ecd\u4e0d\u5931\u4e3a\u56fd\u5185\u5e02\u573a\u4e0a\u7684\u4e3b\u6d41\u5546\u7528\u673a\u578b\uff0c\u8054\u8baf\u4e0e\u4e1c\u5174\u624b\u91cc\u638c\u63e1\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u79bb\u5546\u7528\u5316\u8fd8\u662f\u5341\u4e07\u516b\u5343\u91cc\u7684\u9065\u8fdc\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7684\u51b3\u5b9a\uff0c\u8054\u8baf\u4e0e\u4e1c\u5174\u662f\u6700\u4e3a\u5174\u594b\u7684\u3002\u867d\u8bf4\u4ed6\u4eec\u624b\u91cc\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u79bb\u5546\u7528\u5316\u8fd8\u6709\u5f88\u8fdc\u7684\u8ddd\u79bb\uff0c\u5373\u4f7f\u60f3\u81ea\u79f0\u4e3a\u56db\u6d41\u4e94\u6d41\u7684\u6280\u672f\u90fd\u5f88\u52c9\u5f3a\uff0c\u4f46\u662f\u6bd5\u7adf\u6709\u4e2a\u6280\u672f\u5e95\u5b50\u5728\u90a3\u91cc\uff0c\u800c\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u6240\u5c5e\u7684\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\u662f\u56fd\u5185\u8fc4\u4eca\u60df\u4e00\u7684\u4e13\u4e1a\u79fb\u52a8\u901a\u8baf\u7814\u7a76\u673a\u6784\u3002\u4e14\u4e0d\u7ba1\u6c34\u5e73\u5982\u4f55\uff0c\u4f46\u662f\u81f3\u5c11\u5728\u4e5d\u4e03\u5e74\u4e4b\u524d\uff0c\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u7814\u7a76\u9886\u57df\u7684\u4e13\u5bb6\u51e0\u4e4e\u90fd\u96c6\u4e2d\u5728\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\u91cc\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5728\u83b7\u5f97\u5fb7\u4eea\u7684\u6280\u672f\u4e4b\u540e\uff0c\u534a\u5e74\u4e4b\u540e\u5c31\u63a8\u51fa\u6210\u719f\u7684\u5546\u4e1a\u5316\u624b\u673a\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u5b8c\u5168\u6709\u7406\u7531\u76f8\u4fe1\uff0c\u4ed6\u4eec\u5728\u83b7\u5f97\u7231\u8fbe\u96c6\u56e2\u516c\u5f00\u7684\u5168\u90e8\u6280\u672f\u4e4b\u540e\uff0c\u901f\u5ea6\u4e0d\u4f1a\u6162\u8fc7\u7231\u8fbe\u96c6\u56e2\u592a\u591a\u3002</p><p>\u3000\u3000\u53ef\u60dc\u56fd\u5185\u5916\u5a92\u4f53\u5bf9\u7231\u8fbe\u4e0e\u5fb7\u4eea\u7684\u90a3\u6b21\u6280\u672f\u8d44\u4ea7\u4ea4\u6613\u8fdb\u884c\u62a5\u9053\u65f6\uff0c\u4f3c\u4e4e\u5fd8\u4e86\u63d0\u53ca\u5fb7\u4eea\u76f8\u5173\u7684\u6280\u672f\u56e2\u961f\u4e5f\u968f\u7740\u90a3\u6b21\u4ea4\u6613\u90fd\u8f6c\u5165\u7231\u8fbe\u5728\u7f8e\u56fd\u7845\u8c37\u7684\u5b9e\u9a8c\u57fa\u5730ESS\u7684\u65d7\u4e0b\u3002</p><p>\u3000\u3000\u5728\u7231\u8fbe\u5e76\u8d2d\u4e4b\u524d\uff0cESS\u5c31\u53ea\u662f\u7845\u8c37\u4e00\u5bb6\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c0f\u79d1\u6280\u516c\u53f8\uff0c\u56e0\u4e3a\u4e0e\u65b0\u79d1\u7535\u5b50\u5408\u4f5c\u7814\u7a76\u789f\u673a\u89e3\u7801\u82af\u9047\u632b\u624d\u88ab\u8feb\u552e\u7ed9\u7231\u8fbe\u96c6\u56e2\uff0c\u7ed9\u7231\u8fbe\u5e76\u8d2d\u4e4b\u540e\u5230\u73b0\u5728\u4e5f\u6ca1\u6709\u4ec0\u4e48\u60ca\u8273\u4e4b\u4f5c\u5438\u5f15\u5a92\u4f53\u4e0e\u516c\u4f17\u7684\u76ee\u5149\u3002</p><p>\u3000\u3000\u5728\u8bb8\u591a\u4eba\u7684\u773c\u91cc\uff0c\u8fd9\u4e48\u4e00\u5bb6\u79d1\u6280\u516c\u53f8\u80fd\u534a\u8def\u63a5\u624b\u5c06\u5fb7\u4eea\u7684\u624b\u673a\u6280\u672f\u6d88\u5316\uff0c\u53ea\u6015\u4e5f\u662f\u5316\u4e86\u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\uff0c\u5374\u4e0d\u6653\u5f97\u9664\u4e86\u5e74\u521d\u5c31\u6ce8\u5165\u7684\u4e24\u5343\u4e07\u7f8e\u91d1\u4e4b\u5916\uff0c\u9526\u6e56\u4ece\u89e3\u7801\u82af\u7247\u4e0a\u6bcf\u4e2a\u6708\u83b7\u5f97\u8fd1\u5343\u4e07\u7f8e\u5143\u7684\u6536\u76ca\uff0c\u9664\u4e86\u4e00\u90e8\u5206\u9010\u6708\u652f\u4ed8\u4ece\u5fb7\u4eea\u6536\u8d2d\u6280\u672f\u7684\u8d39\u7528\u4e4b\u5916\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u90fd\u4f1a\u6295\u7ed9ESS\u3002\u8fd9\u4e9b\u8d44\u91d1\u9664\u4e86\u6295\u5165\u65e2\u5b9a\u7684\u7814\u53d1\u4efb\u52a1\u4e4b\u5916\uff0c\u8fd8\u6709\u5c31\u662f\u4e0d\u65ad\u7684\u62db\u63fd\u4eba\u624b\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6a61\u6811\u56ed\u7684\u57fa\u5efa\u5de5\u4f5c\u90fd\u8fd8\u6ca1\u6709\u5b8c\u5168\u51c6\u5907\u59a5\u5f53\uff0c\u9664\u4e86\u5fc5\u8981\u7684\u7b79\u5907\u4eba\u5458\u7559\u5728\u56fd\u5185\u4e4b\u5916\uff0c\u8fd9\u6b21\u201c\u767e\u4eba\u8ba1\u5212\u201d\u62db\u8058\u5230\u7684\u4e2d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u90fd\u4f1a\u5c3d\u53ef\u80fd\u5148\u5b89\u6392\u5230ESS\u8fdb\u884c\u77ed\u671f\u7684\u6280\u672f\u4ea4\u6d41\uff0c\u5f53\u7136\uff0c\u4eba\u6570\u89c4\u6a21\u66f4\u52a0\u5e9e\u5927\u7684\u662f\u4ece\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u62bd\u8c03\u7684\u4e0e\u8fd1\u671f\u4ece\u56fd\u5185\u62db\u8058\u7684\u4e00\u90e8\u5206\u6280\u672f\u4eba\u5458\uff0c\u4ed6\u4eec\u90fd\u5728\u6a61\u6811\u56ed\u8ba1\u5212\u542f\u52a8\u4e4b\u540e\u9646\u7eed\u9001\u5230ESS\u8fdb\u884c\u4e13\u4e1a\u6280\u672f\u4e0a\u7684\u57f9\u8bad\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u5c06\u524d\u671f\u7684\u7814\u53d1\u8854\u63a5\u4e0e\u6574\u5408\u5de5\u4f5c\u5148\u5f00\u5c55\u8d77\u6765\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u8bf4\uff0cESS\u6b64\u65f6\u6280\u672f\u56e2\u961f\u7684\u9635\u5bb9\uff0c\u7edd\u975e\u4e00\u5e74\u524d\u80fd\u591f\u5ab2\u7f8e\u3002\u5f53\u7136\uff0c\u5f85\u5efa\u90ba\u7684\u6a61\u6811\u56ed\u5b8c\u5168\u5efa\u6210\u4e4b\u540e\uff0c\u5927\u90e8\u5206\u4eba\u90fd\u4f1a\u5206\u6d41\u5230\u56fd\u5185\u6765\u5f00\u5c55\u7814\u53d1\u5de5\u4f5c\uff0c\u8fd9\u90e8\u5206\u5df2\u7ecf\u80fd\u591f\u5c06\u6a61\u6811\u56ed\u56db\u4e2a\u91cd\u70b9\u5b9e\u9a8c\u7684\u6846\u67b6\u7ed9\u652f\u6491\u8d77\u6765\u3002</p><p>\u3000\u3000\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u4e0e\u9648\u4fe1\u751f\u90fd\u4e0d\u964c\u751f\uff0c\u6bd5\u7adf\u9648\u4fe1\u751f\u62c5\u4efb\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u4e03\u5e74\uff0c\u4e0e\u56fd\u5185\u5927\u7684\u7535\u5b50\u5382\u5546\u90fd\u6709\u6216\u6df1\u6216\u6d45\u7684\u4ea4\u5f80\uff0c\u5728\u7535\u5b50\u4ea7\u4e1a\u9886\u57df\u6709\u7740\u6781\u5927\u7684\u5f71\u54cd\u3002\u82cf\u6d25\u4e1c\u8981\u7b97\u540e\u8d77\u4e4b\u79c0\uff0c\u53ef\u4ee5\u8bf4\u662f\u56e0\u4e3a\u7231\u8fbe\u7684\u5d1b\u8d77\u800c\u540d\u95fb\u5929\u4e0b\uff0c\u4e0d\u7ba1\u80cc\u540e\u90a3\u4e2a\u9752\u5e74\u62e5\u6709\u600e\u6837\u7684\u5947\u8ff9\u4e4b\u624b\uff0c\u82cf\u6d25\u4e1c\u4f5c\u4e3a\u5177\u4f53\u7684\u7ba1\u7406\u8005\uff0c\u52b3\u82e6\u529f\u9ad8\uff0c\u6b64\u65f6\u4e5f\u5df2\u7ecf\u5bb6\u8d22\u4ebf\u4e07\u3002</p><p>\u3000\u3000\u5929\u6653\u5f97\uff0c\u4e5d\u4e03\u5e74\uff0c\u56fd\u5185\u7684\u4ebf\u4e07\u5bcc\u7fc1\u662f\u591a\u4e48\u7684\u7a00\u7f55\uff01</p><p>\u3000\u3000\u6700\u4ee4\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u597d\u5947\u7684\uff0c\u5f53\u7136\u8fd8\u662f\u90a3\u4e2a\u9690\u85cf\u5728\u9634\u5f71\u4e4b\u4e0b\u3001\u636e\u8bf4\u8fd8\u6ca1\u6709\u6ee1\u4e8c\u5341\u5c81\u7684\u9752\u5e74\u3002\u4f5c\u4e3a\u5728\u5404\u81ea\u5730\u76d8\u90fd\u62e5\u6709\u6781\u5927\u6743\u5a01\u7684\u4e2d\u5e74\u4eba\uff0c\u8981\u6ca1\u6709\u5fc5\u8981\uff0c\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u5b9e\u5728\u4e0d\u60f3\u8dd1\u8fc7\u6765\u8ddf\u4e00\u4e2a\u672a\u6ee1\u4e8c\u5341\u5c81\u7684\u9752\u5e74\u5e73\u8d77\u5e73\u5750\uff0c\u4eca\u5929\u6025\u51b2\u51b2\u8dd1\u7684\u8fc7\u6765\uff0c\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u5e73\u8d77\u5e73\u5750\u7684\u5730\u4f4d\u3002\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u522b\u626d\uff0c\u4f46\u662f\u6709\u673a\u4f1a\u89c1\u9762\uff0c\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u8fd8\u662f\u4e0d\u4f1a\u9519\u8fc7\u7684\uff0c\u4f55\u51b5\u8fd9\u6b21\u53c8\u4e8b\u5173\u91cd\u5927\uff0c\u5012\u4e0d\u6653\u5f97\u8054\u4fe1\u7684\u8096\u745e\u6c11\u662f\u600e\u4e48\u60f3\u7684\u3002</p><p>\u3000\u3000\u7531\u4e8e\u4e4b\u524d\u5076\u5c14\u53d1\u751f\u7684\u5c0f\u6469\u64e6\uff0c\u8096\u745e\u6c11\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u5173\u6ce8\u66f4\u7ec6\u5fc3\u3002\u770b\u4e0a\u53bbi19\u5728\u6027\u80fd\u4e0a\u6bd4i08\u63d0\u9ad8\u4e0d\u591a\uff0c\u751a\u81f3\u4e3a\u4e86\u4fdd\u8bc1\u624b\u673a\u7684\u8f7b\u8584\uff0c\u4e00\u4e9b\u6027\u80fd\u751a\u81f3\u53d6\u6d88\u6389\uff0ci19\u7684\u6210\u529f\u4f3c\u4e4e\u66f4\u591a\u7684\u4f53\u73b0\u5728\u4ea7\u54c1\u8bbe\u8ba1\u4e0a\u3002\u8096\u745e\u6c11\u5728\u9999\u6e2f\u542f\u5fb7\u673a\u573a\u83b7\u5f97\u5f20\u606a\u8d60\u9001\u7684\u90a3\u53ea\u6837\u673a\u4e4b\u540e\uff0c\u5c31\u8ba9\u8054\u4fe1\u65d7\u4e0b\u7684\u5de5\u7a0b\u5e08\u8be6\u7ec6\u7684\u89e3\u6790\u4e86i19\u7684\u6280\u672f\u7279\u70b9\uff0c\u4e0d\u63d0\u7231\u8fbe\u72ec\u6709\u7684\u590d\u5f0f\u97f3\u8f68\u6280\u672f\u4e0e\u84dd\u5c4f\u6280\u672f\uff0c\u53d1\u73b0\u6ca1\u6709\u4e00\u5b9a\u7684\u6280\u672f\u6c34\u51c6\u662f\u65e0\u6cd5\u5b9e\u73b0i19\u90a3\u4ee4\u4eba\u60ca\u8273\u7684\u5916\u5f62\u7684\u3002\u8fd9\u65f6\u5019\uff0c\u8096\u745e\u6c11\u5c31\u6653\u5f97\u7231\u8fbe\u96c6\u56e2\u5728\u6d77\u5916\u5e94\u8be5\u6709\u4e00\u4e2a\u76f8\u5bf9\u5f3a\u5927\u7684\u6280\u672f\u56e2\u961f\u4e3a\u540e\u76fe\uff0c\u5f53\u7136\uff0c\u4e5f\u65e0\u6cd5\u60f3\u8c61ESS\u7684\u6280\u672f\u56e2\u961f\u9635\u5bb9\u6709\u591a\u5e9e\u5927\uff0c\u6bd5\u7adf\u5728\u7f8e\u56fd\u7845\u8c37\u7ef4\u6301\u4e00\u4e2a\u6280\u672f\u56e2\u961f\u7684\u8d39\u7528\uff0c\u53ef\u80fd\u662f\u56fd\u5185\u7684\u5341\u500d\u3001\u4e8c\u5341\u500d\u3002</p><p>\u3000\u3000\u7535\u68af\u505c\u5728\u5341\u4e8c\u5c42\uff0c\u7535\u68af\u5916\u4e0d\u662f\u56fd\u5185\u5bfb\u5e38\u529e\u516c\u697c\u91cc\u5e38\u89c1\u7684\u8fc7\u9053\u4e0e\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u623f\u95f4\uff0c\u7535\u68af\u5916\u662f\u4e00\u4e2a\u5bbd\u655e\u660e\u4eae\u7684\u5927\u5385\uff0c\u8fd9\u4e2a\u5927\u5385\u53ea\u6015\u8981\u5360\u4e86\u6574\u4e2a\u697c\u5c42\u8fd1\u4e00\u534a\u7684\u9762\u79ef\uff0c\u529e\u516c\u5ba4\u3001\u4f1a\u5ba2\u5ba4\u4e4b\u7c7b\u7684\u9694\u95f4\u5728\u5927\u5385\u7684\u53e6\u4e00\u4fa7\u3002</p><p>\u3000\u3000\u968f\u884c\u4eba\u5458\u53e6\u6709\u4eba\u62db\u5f85\uff0c\u8096\u745e\u6c11\u4e0e\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u5728\u4f1a\u5ba2\u5ba4\u91cc\u7a0d\u5750\u7247\u523b\uff0c\u82cf\u6d25\u4e1c\u7a0d\u53bb\u4fbf\u8fd4\uff0c\u5f20\u606a\u8ddf\u5728\u4ed6\u540e\u9762\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u90fd\u6653\u5f97\u7ad9\u5728\u7231\u8fbe\u96c6\u56e2\u80cc\u540e\u662f\u4e00\u4e2a\u4e0d\u6ee1\u4e8c\u5341\u5c81\u7684\u9752\u5e74\uff0c\u5f53\u5f20\u606a\u8d70\u8fdb\u6765\u65f6\uff0c\u4ed6\u4eec\u770b\u5230\u7684\u662f\u4e00\u4e2a\u773c\u795e\u91cc\u6d41\u9732\u51fa\u95f2\u6563\u610f\u6001\u3001\u767d\u7699\u8138\u5e9e\uff0c\u4e66\u5377\u6c14\u6d53\u91cd\u3001\u8eab\u6750\u7565\u7626\u770b\u4e0a\u53bb\u5374\u53c8\u5145\u6ee1\u7cbe\u529b\u7684\u9752\u5e74\uff0c\u8981\u4e0d\u662f\u8096\u745e\u6c11\u4e0e\u9648\u4fe1\u751f\u8fd9\u65f6\u5019\u7ad9\u8d77\u6765\uff0c\u4ed6\u4eec\u4e5f\u65e0\u6cd5\u5c31\u76f8\u4fe1\u773c\u524d\u8fd9\u9752\u5e74\u5c31\u662f\u90a3\u4e2a\u7ad9\u5728\u7231\u8fbe\u96c6\u56e2\u80cc\u540e\u7684\u90a3\u4e2a\u9752\u5e74\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u5b59\u5fd7\u521a\u8fd8\u662f\u5ed6\u7ea2\u96f7\uff0c\u4eca\u540e\u5341\u5e74\u5728\u4e1a\u5185\u90fd\u8dfa\u4e00\u8dfa\u811a\u90fd\u8981\u8ba9\u4eba\u611f\u89c9\u5927\u5730\u5728\u6296\u7684\u91cd\u8981\u89d2\u8272\uff0c\u6b64\u65f6\u4ed6\u4eec\u6b63\u503c\u58ee\u5e74\uff0c\u4e5f\u6216\u8bb8\u6b63\u610f\u6c14\u98ce\u53d1\u6709\u7740\u5927\u5fd7\u5411\u60f3\u5f15\u9886\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u51b2\u51fa\u91cd\u56f4\uff0c\u5f20\u606a\u5bf9\u4ed6\u4eec\u5e76\u4e0d\u964c\u751f\uff0c\u62a5\u7aef\u591a\u6709\u89c1\u95fb\uff0c\u4f46\u90a3\u662f\u4e0d\u6653\u5f97\u9694\u4e86\u591a\u5c11\u5c42\u7eb1\u7684\u8868\u6f14\uff0c\u5f53\u4e0d\u5f97\u771f\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u795e\u518d\u77a5\u5230\u8096\u745e\u6c11\u8138\u4e0a\uff0c\u624d\u54c2\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5927\u613f\u610f\u51fa\u6765\uff0c\u5c31\u6015\u7ed9\u522b\u4eba\u5e26\u6765\u56f0\u6270\uff0c\u518d\u8bf4\u7231\u8fbe\u80fd\u5230\u8fd9\u4e00\u6b65\uff0c\u4e5f\u662f\u673a\u8fd0\u597d\u4e00\u4e9b\u7f62\u4e86\uff0c\u5176\u4ed6\u90fd\u662f\u82cf\u3001\u9648\u7b49\u4eba\u7684\u529f\u52b3\u3002\u4e09\u4f4d\u8001\u603b\u8fc7\u6765\uff0c\u6211\u4e5f\u4e0d\u6562\u626d\u626d\u634f\u634f\u8eb2\u7740\u4e0d\u51fa\u6765\uff0c\u5c31\u6015\u8ba9\u4f60\u4eec\u8bef\u4f1a\u9526\u6e56\u5728\u7aef\u4ec0\u4e48\u67b6\u5b50\u3002\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u5728\u809a\u5b50\u91cc\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8868\u73b0\u5230\u8138\u4e0a\u6765\uff0c\u4ed6\u4eec\u53ef\u662f\u5728\u4f1a\u5ba2\u5385\u91cc\u5750\u5b9a\u4e4b\u540e\uff0c\u5f20\u606a\u624d\u51fa\u9762\u7684\u3002\u8fd9\u793c\u8282\u4e0a\u7684\u7ec6\u5904\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u53bb\u8ffd\u7a76\uff0c\u8fd9\u65f6\u5019\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u7231\u8fbe\u96c6\u56e2\u662f\u5904\u4e8e\u5f3a\u52bf\u5730\u4f4d\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u8fc7\u6765\u7684\u610f\u56fe\uff0c\u5f20\u606a\u7528\u811a\u8dbe\u5934\u90fd\u80fd\u60f3\u5230\u3002</p><p>\u3000\u3000\u5927\u5bb6\u56f4\u7740\u73bb\u7483\u8336\u51e0\u800c\u5750\uff0c\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u8fc2\u56de\uff0c\u5c31\u76f4\u5954\u4e3b\u9898\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u65e2\u7136\u51b3\u5b9a\u5411\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u3001\u5efa\u90ba\u9ad8\u65b0\u56ed\u533a\u4e24\u4e2a\u56ed\u533a\u5185\u7684\u4f01\u4e1a\u516c\u5f00\u4ece\u5fb7\u4eea\u83b7\u5f97\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u90a3\u4e09\u5bb6\u516c\u53f8\u5230\u4e24\u5730\u6295\u8d44\u5efa\u9020\u5de5\u5382\u4e0e\u7814\u53d1\u673a\u6784\uff0c\u90a3\u662f\u6bcb\u5eb8\u7f6e\u7591\u7684\uff0c\u5173\u952e\u4e09\u5bb6\u516c\u53f8\u5e76\u4e0d\u60f3\u5b8c\u5168\u7ed9\u7231\u8fbe\u96c6\u56e2\u63a7\u5236\u4f4f\u5c40\u52bf\uff0c\u4e5f\u65e0\u6cd5\u60f3\u8c61\u8981\u662f\u6574\u4e2a\u5728\u6d77\u5dde\u3001\u5efa\u90ba\u6240\u5f62\u6210\u7684\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u90fd\u88ab\u7231\u8fbe\u4e00\u5bb6\u63a7\u5236\u4e4b\u540e\u7684\u6050\u6016\u60c5\u5f62\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u8fd9\u65f6\u5019\u4e5f\u65e0\u6cd5\u516c\u7136\u62b5\u5236\u7231\u8fbe\u96c6\u56e2\u8fd9\u4e2a\u51b3\u5b9a\uff0c\u6df1\u5ea6\u7684\u53c2\u4e0e\u8fdb\u6765\uff0c\u65e0\u7591\u662f\u4ed6\u4eec\u6b64\u65f6\u6700\u4f73\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u5b59\u5fd7\u521a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e2d\u5348\u8ddf\u90e8\u91cc\u9886\u5bfc\u6c47\u62a5\u4e86\u7231\u8fbe\u96c6\u56e2\u7684\u51b3\u5b9a\uff0c\u90e8\u91cc\u9886\u5bfc\u542c\u4e86\u90fd\u5f88\u632f\u594b\u3002\u6570\u5b57\u624b\u673a\u7684\u7814\u5236\uff0c\u7535\u5b50\u4e03\u6240\u4ece\u4e5d\u4e09\u5e74\u8d77\u5c31\u5f00\u59cb\u8fdb\u884c\u7814\u53d1\uff0c\u76ee\u524d\u4e5f\u53d6\u5f97\u4e00\u7cfb\u5217\u7684\u6210\u679c\uff0c\u76ee\u524d\uff0c\u8fd9\u4e9b\u6280\u672f\u6210\u679c\uff0c\u5f52\u8054\u8baf\u4e0e\u4e1c\u5174\u4e24\u5bb6\u516c\u53f8\u6240\u6709\uff0c\u7231\u8fbe\u7684\u51b3\u5b9a\u5f88\u6709\u9b44\u529b\uff0c\u90e8\u91cc\u4e0b\u5348\u7ecf\u8fc7\u7814\u7a76\uff0c\u53c8\u4e0e\u7535\u5b50\u4e03\u6240\u7684\u9886\u5bfc\u8fdb\u884c\u78cb\u5546\uff0c\u4e5f\u51b3\u5b9a\u5c06\u76f8\u5173\u6280\u672f\u62ff\u51fa\u6765\u4e0e\u7231\u8fbe\u96c6\u56e2\u4e92\u901a\u6709\u65e0\u3001\u53d6\u957f\u8865\u77ed\uff0c\u4e5f\u53ef\u4ee5\u62ff\u51fa\u6765\u7ed9\u4e24\u4e2a\u56ed\u533a\u7684\u4f01\u4e1a\u4f7f\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u7535\u5b50\u5de5\u4e1a\u90e8\u6b64\u4e3e\uff0c\u9664\u4e86\u60f3\u5f97\u5230\u7231\u8fbe\u96c6\u56e2\u8fd9\u6b21\u516c\u5f00\u7684\u5168\u90e8\u6280\u672f\u4e4b\u5916\uff0c\u8fd8\u60f3\u5171\u540c\u4e3b\u5bfc\u8fd9\u6b21\u6280\u672f\u5927\u516c\u5f00\u7684\u8fdb\u7a0b\u3002\u8096\u745e\u6c11\u5fc3\u91cc\u867d\u7136\u5bf9\u8054\u8baf\u3001\u4e1c\u5174\u624b\u91cc\u7684\u6280\u672f\u5f88\u4e0d\u5c51\uff0c\u8fd9\u65f6\u5019\u5374\u4e0d\u80fd\u5f53\u9762\u62c6\u4ed6\u4eec\u7684\u53f0\uff0c\u90ae\u7535\u90e8\u4e0b\u5c5e\u79d1\u7814\u9662\u6240\u5728\u624b\u673a\u5236\u9020\u6280\u672f\u4e0a\u7684\u79ef\u7d2f\u4e00\u7a77\u4e8c\u767d\uff0c\u4e5f\u6ca1\u6709\u8d44\u683c\u8868\u793a\u4e0d\u5c51\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u8bf4\u9053\uff1a\u201c\u7535\u5b50\u90e8\u7684\u2018\u6177\u6168\u2019\uff0c\u771f\u662f\u8ba9\u53f9\u670d\u554a\uff0c\u4e92\u901a\u6709\u65e0\u3001\u53d6\u957f\u8865\u77ed\u7684\u5efa\u8bae\u4e5f\u5f88\u597d\uff0c\u6bd5\u7adf\u8054\u8baf\u4e0e\u4e1c\u5174\u4e24\u5bb6\u516c\u53f8\uff0c\u5f88\u591a\u5730\u65b9\uff0c\u90fd\u662f\u5176\u957f\u5904\u7684\uff0c\u4e0d\u9700\u8981\u518d\u4ece\u7231\u8fbe\u96c6\u56e2\u53d6\u5176\u957f\u8865\u5df1\u77ed\u4e86\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u4e0d\u59a8\u5c06\u9700\u8981\u53d6\u957f\u8865\u77ed\u7684\u6280\u672f\u5217\u4e2a\u8868\u51fa\u6765\uff0c\u6211\u60f3\uff0c\u4e5f\u6ca1\u6709\u591a\u5c11\u5427\u3002\u6211\u4e5f\u4e0d\u6015\u4f60\u4eec\u7b11\u8bdd\uff0c\u8054\u4fe1\u73b0\u5728\u5728\u624b\u673a\u5236\u9020\u6280\u672f\u7684\u79ef\u7d2f\u4e0a\u4e00\u7a77\u4e8c\u767d\uff0c\u8981\u8bf4\u53d6\u957f\u8865\u77ed\uff0c\u90a3\u7231\u8fbe\u8fd9\u6b21\u516c\u5f00\u51fa\u6765\u7684\u6280\u672f\uff0c\u6211\u4eec\u90fd\u8981\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u8fd9\u8bdd\u5df2\u7ecf\u662f\u7b11\u91cc\u85cf\u5200\u4e86\uff0c\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u7684\u8054\u8baf\u3001\u4e1c\u5174\u5f97\u5230\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\u7684\u652f\u6301\uff0c\u5728\u6570\u5b57\u624b\u673a\u6280\u672f\u7b97\u662f\u6709\u4e2a\u5e95\u5b50\uff0c\u6d88\u5316\u3001\u5438\u6536\u7684\u80fd\u529b\u8981\u8fdc\u8fdc\u8d85\u8fc7\u8054\u4fe1\uff0c\u8096\u745e\u6c11\u751a\u81f3\u6000\u7591\u8054\u4fe1\u624b\u91cc\u73b0\u6709\u7684\u6280\u672f\u529b\u91cf\u6709\u6ca1\u6709\u80fd\u529b\u5438\u6536\u3001\u6d88\u5316\u8fd9\u4e9b\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u8981\u662f\u7ed9\u8054\u8baf\u3001\u4e1c\u5174\u987a\u5229\u4e00\u6b21\u6027\u5c06\u5168\u90e8\u6280\u672f\u62ff\u8fc7\u6765\uff0c\u8054\u4fe1\u4e0d\u4ec5\u8ffd\u8d76\u7231\u8fbe\u65e0\u671b\uff0c\u8fd8\u8981\u7ed9\u8054\u8baf\u3001\u4e1c\u5174\u8fdc\u8fdc\u7684\u8d76\u8d85\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5ed6\u7ea2\u96f7\u7b11\u9053\uff1a\u201c\u8054\u4fe1\u7684\u6280\u672f\u529b\u91cf\u662f\u5dee\u70b9\uff0c\u4e0d\u662f\u8fd8\u627e\u4e86\u4e00\u5bb6\u4f01\u4e1a\u4ee3\u5de5\u5417\uff1f\u8054\u4fe1\u53ef\u4ee5\u627e\u90a3\u5bb6\u516c\u53f8\u4e00\u8d77\u8fc7\u6765\u53d6\u957f\u8865\u77ed\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5ed6\u7ea2\u96f7\u8eab\u6750\u9ad8\u5927\u5065\u58ee\uff0c\u5149\u770b\u5916\u8868\uff0c\u6709\u56db\u80a2\u53d1\u8fbe\u5934\u8111\u7b80\u5355\u7684\u4e9b\u5fae\u9519\u89c9\uff0c\u5634\u91cc\u5012\u4e0d\u8f6f\u5f31\u3002\u90ae\u7535\u90e8\u624b\u91cc\u624d\u53d1\u51fa\u56db\u5f20\u724c\u7167\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u5e02\u573a\u90a3\u4e48\u5927\uff0c\u7ade\u4e89\u4e0d\u662f\u90a3\u4e48\u5f3a\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u8fd8\u6709\u4e9b\u5fd7\u6c14\uff0c\u81f3\u5c11\u8fd9\u65f6\u5019\u6ca1\u6709\u60f3\u5230\u8981\u7ed9\u56fd\u5185\u5176\u4ed6\u4f01\u4e1a\u8d34\u724c\u3002\u6709\u65f6\u5019\uff0c\u8054\u4fe1\u641e\u4e9b\u5c0f\u52a8\u4f5c\u53ef\u4ee5\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u641e\uff0c\u53ef\u80fd\u5c31\u8981\u7ed9\u90ae\u7535\u90e8\u6311\u6bdb\u75c5\u3002\u65e2\u7136\u8096\u745e\u6c11\u7ed9\u4ed6\u4eec\u5236\u9020\u969c\u788d\uff0c\u5ed6\u7ea2\u96f7\u4e5f\u4e0d\u60dc\u53cd\u5507\u76f8\u8ba5\u7684\u3002</p><p>\u3000\u3000\u8054\u4fe1\u80cc\u540e\u7ad9\u7740\u90ae\u7535\u90e8\uff0c\u8054\u8baf\u3001\u4e1c\u5174\u80cc\u540e\u7ad9\u7740\u7535\u5b50\u90e8\uff0c\u4e0d\u7ba1\u65e5\u540e\u662f\u5206\u662f\u5408\uff0c\u9526\u6e56\u90fd\u65e0\u6cd5\u5f00\u7f6a\u7684\uff0c\u4e5f\u65e0\u6cd5\u5bf9\u8fd9\u4e09\u5bb6\u4f01\u4e1a\u505a\u51fa\u4ec0\u4e48\u9650\u5236\u3002\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u8d2a\u56fe\u8054\u8baf\u3001\u4e1c\u5174\u624b\u91cc\u843d\u540e\u7684\u6280\u672f\uff0c\u4ed6\u5c06\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u4e09\u4eba\u53ec\u96c6\u5230\u4e00\u8d77\u8c08\uff0c\u5c31\u662f\u5e0c\u671b\u4e24\u8fb9\u80fd\u4e92\u76f8\u5236\u8098\uff0c\u9526\u6e56\u624d\u6709\u987a\u52bf\u4e3b\u5bfc\u5c40\u52bf\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u6700\u5173\u952e\u7684\uff0c\u4e0d\u80fd\u8ba9\u4ee3\u5de5\u8d34\u724c\u8fd9\u4e00\u884c\u4e3a\u4e0d\u53d7\u63a7\u5236\u7684\u6cdb\u6ee5\u4e0b\u53bb\u3002\u73b0\u5728\u90ae\u7535\u90e8\u624d\u53d1\u56db\u5f20\u6267\u7167\u8fd8\u597d\uff0c\u7b49\u53d1\u4e86\u4e8c\u5341\u5f20\u3001\u4e09\u5341\u5f20\u6267\u7167\uff0c\u6574\u4e2a\u6df7\u4e71\u7684\u5c40\u52bf\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u53bb\u63a7\u5236\u7684\u53ef\u80fd\uff0c\u56fd\u4ea7\u624b\u673a\u8bf4\u4e0d\u5b9a\u8fd8\u771f\u8981\u518d\u6b21\u6bc1\u5728\u8fd9\u4e0a\u9762\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8096\u745e\u6bbf\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u4ed6\u4eec\u4e92\u653e\u51b7\u7bad\uff0c\u4e5f\u4e0d\u4f1a\u5fd8\u4e86\u7ed9\u9526\u6e56\u65bd\u52a0\u538b\u529b\u3002\u5f20\u606a\u5012\u4e0d\u4ecb\u610f\uff0c\u53cc\u624b\u62b1\u80f8\u9760\u5728\u6c99\u53d1\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e2d\u5348\u5728\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\uff0c\u5df2\u7ecf\u505a\u51fa\u8bf4\u660e\uff0c\u8fd9\u90e8\u5206\u6280\u672f\u8d44\u4ea7\uff0c\u4e24\u4e2a\u56ed\u533a\u7684\u4f01\u4e1a\u5982\u4f55\u5206\u4eab\uff0c\u4f1a\u7ec4\u6210\u4e00\u4e2a\u4e13\u95e8\u7684\u673a\u6784\u6765\u8d1f\u8d23\u5ba1\u67e5\u3002\u600e\u4e48\u53bb\u5ba1\u67e5\uff0c\u5ba1\u67e5\u7684\u6761\u4ef6\u4e0e\u89c4\u5b9a\u4e5f\u7531\u8fd9\u4e2a\u673a\u6784\u53bb\u5236\u5b9a\u3002\u8054\u8baf\u3001\u4e1c\u5174\u65e2\u7136\u613f\u610f\u5c06\u624b\u91cc\u7684\u6280\u672f\u62ff\u51fa\u6765\uff0c\u53c2\u4e0e\u673a\u6784\uff0c\u81ea\u7136\u4e5f\u662f\u8d23\u65e0\u65c1\u8d37\u7684\uff0c\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u5e0c\u671b\u8054\u4fe1\u80fd\u51fa\u4e00\u5206\u529b\uff0c\u5171\u540c\u5c06\u8fd9\u4e2a\u5de5\u4f5c\u505a\u597d\u3002\u5236\u5b9a\u89c4\u5219\uff0c\u53ea\u662f\u8981\u907f\u514d\u8fd9\u90e8\u5206\u6280\u672f\u8d44\u4ea7\u7ed9\u6ee5\u7528\uff0c\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u8fd8\u6709\u6211\u4eec\u7231\u8fbe\uff0c\u90fd\u662f\u4ece\u90ae\u7535\u90e8\u62ff\u624b\u673a\u6267\u7167\u7684\u5382\u5546\uff0c\u76f8\u4fe1\u638c\u63e1\u5168\u90e8\u7684\u6280\u672f\u4e5f\u4e0d\u4f1a\u6ee5\u7528\u2026\u2026\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u5c31\u6697\u6697\u5174\u594b\uff0c\u80fd\u53c2\u52a0\u8fdb\u6765\uff0c\u53c8\u80fd\u62ff\u5230\u8fd9\u6b21\u516c\u5f00\u7684\u5168\u90e8\u6280\u672f\uff0c\u76ee\u6807\u5c31\u7b97\u662f\u8fbe\u6210\u3002\u8096\u745e\u6c11\u5fc3\u91cc\u5374\u662f\u4e0d\u613f\uff0c\u8981\u662f\u8fd9\u6837\u7684\u7ed3\u679c\uff0c\u8054\u4fe1\u6ce8\u5b9a\u8981\u5728\u56db\u5bb6\u516c\u53f8\u91cc\u57ab\u5e95\uff0c\u8fd9\u6837\u7684\u7ed3\u679c\u4ed6\u600e\u4e48\u7518\u5fc3\uff1f\u6b63\u8981\u8bf4\u8bdd\uff0c\u5374\u542c\u89c1\u5f20\u606a\u8bdd\u950b\u4e00\u8f6c\uff0c\u201c\u5ed6\u603b\u521a\u624d\u6307\u51fa\u8054\u4fe1\u627e\u5176\u4ed6\u4f01\u4e1a\u8d34\u724c\u4ee3\u5de5\u624b\u673a\u7684\u95ee\u9898\uff0c\u5f53\u7136\u4e86\uff0c\u6211\u4eec\u662f\u6ca1\u6709\u591a\u5634\u7684\u8d44\u683c\uff0c\u5c31\u6211\u4e2a\u4eba\u89c9\u5f97\uff0c\u8fd9\u5bb6\u7ed9\u8054\u4fe1\u4ee3\u5de5\u7684\u4f01\u4e1a\u8981\u6ca1\u6709\u4e00\u70b9\u6280\u672f\u57fa\u7840\u7684\u8bdd\uff0c\u5c31\u5e2e\u7740\u8054\u4fe1\u8d34\u724c\u4ee3\u5de5\u591a\u5c11\u6709\u4e9b\u4e0d\u5408\u9002\uff0c\u4f20\u51fa\u53bb\uff0c\u5bf9\u8054\u4fe1\u4e5f\u6709\u4e0d\u5229\u7684\u5f71\u54cd\uff0c\u5bf9\u8fd9\u5bb6\u4f01\u4e1a\uff0c\u6211\u5e0c\u671b\u53ef\u4ee5\u5f00\u4e00\u4e2a\u7279\u4f8b\uff0c\u7231\u8fbe\u4f1a\u6388\u6743\u8fd9\u5bb6\u4f01\u4e1a\u4f7f\u7528\u8fd9\u6b21\u6240\u516c\u5f00\u7684\u6240\u6709\u6280\u672f\uff0c\u5e2e\u52a9\u8fd9\u5bb6\u4f01\u4e1a\u83b7\u5f97\u4e00\u5b9a\u7684\u6280\u672f\u57fa\u7840\u2026\u2026\u81f3\u4e8e\u5176\u4ed6\u7684\u8d34\u724c\u4ee3\u5de5\u4f01\u4e1a\uff0c\u6211\u770b\u4e5f\u8fd9\u4e48\u5904\u7406\u6bd4\u8f83\u5408\u9002\u3002\u201d</p><p>\u3000\u3000\u9664\u4e86\u79d1\u738b\u9ad8\u79d1\uff0c\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u5176\u4ed6\u7b2c\u4e8c\u5bb6\u8d34\u724c\u4ee3\u5de5\u4f01\u4e1a\uff0c\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\u662f\u8bf4\uff0c\u53ea\u8981\u54ea\u5bb6\u4f01\u4e1a\u83b7\u5f97\u8d34\u724c\u6388\u6743\uff0c\u5c31\u7406\u5e94\u83b7\u5f97\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u8fd9\u8bdd\u600e\u4e48\u542c\u8d77\u6709\u4e9b\u522b\u626d\uff1f\u8096\u745e\u6c11\u5fc3\u91cc\u7422\u78e8\u7740\uff0c\u4e00\u65f6\u4e5f\u6ca1\u6709\u660e\u767d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u7684\u8bdd\u8bf4\u53cd\u4e86\u5427\uff1f\u201d\u5b59\u5fd7\u521a\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u5e94\u8be5\u83b7\u5f97\u5168\u90e8\u6280\u672f\u6388\u6743\u7684\u4f01\u4e1a\u624d\u6709\u8d44\u683c\u8d34\u724c\u4ee3\u5de5\u624b\u673a\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u662f\u8fd9\u610f\u601d\u5417\uff1f\u201d\u5f20\u606a\u6478\u4e86\u6478\u8111\u888b\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u81ea\u5df1\u90fd\u4e0d\u6653\u5f97\u5728\u8bf4\u4ec0\u4e48\u3002\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u5fc3\u795e\u4e00\u51db\uff0c\u6210\u7acb\u8fd9\u4e2a\u673a\u6784\u7684\u76ee\u7684\u679c\u7136\u4e0d\u5355\u7eaf\uff0c\u5f20\u606a\u7684\u8bdd\u53cd\u8fc7\u6765\u542c\u5c31\u662f\u8981\u9650\u5236\u8d34\u724c\u4ee3\u5de5\uff0c\u800c\u4e14\u8fd8\u8981\u5c06\u9650\u5236\u8d34\u724c\u4ee3\u5de5\u7684\u6743\u529b\u7f6e\u5165\u8fd9\u4e2a\u673a\u6784\u4e4b\u4e2d\uff0c\u8fd9\u4e2a\u673a\u6784\u53ef\u4e0d\u6b63\u662f\u5177\u5907\u4e86\u5bf9\u884c\u4e1a\u5177\u6709\u4e00\u5b9a\u7ea6\u675f\u529b\u7684\u534f\u4f1a\u6027\u8d28\uff1f\u4e4b\u540e\u80fd\u62ff\u5230\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\uff0c\u9664\u975e\u7edd\u7136\u4e0d\u4f7f\u7528\u76f8\u5173\u6280\u672f\uff0c\u4e0d\u7136\u4e5f\u5c31\u8981\u53d7\u5230\u76f8\u5173\u7684\u7ea6\u675f\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e5f\u4e0d\u5e0c\u671b\u8054\u4fe1\u7684\u54c1\u724c\u7ed9\u522b\u5bb6\u4f01\u4e1a\u8d34\u724c\u4ee3\u5de5\u5305\u9500\uff0c\u8fd9\u5f88\u53ef\u80fd\u4f1a\u6bc1\u4e86\u8054\u4fe1\u54c1\u724c\u7684\u6839\u57fa\uff0c\u4f46\u662f\u8981\u4e0d\u8981\u7ed9\u522b\u5bb6\u4f01\u4e1a\u8d34\u724c\u7684\u6743\u529b\u5728\u90e8\u91cc\uff0c\u8096\u745e\u6c11\u53ea\u8bf4\u4f1a\u8ba4\u771f\u8003\u8651\uff0c\u4e5f\u65e0\u6cd5\u5f53\u573a\u5c31\u7ed9\u51fa\u4ec0\u4e48\u7b54\u590d\u3002\u5f53\u7136\uff0c\u4ed6\u4e5f\u60f3\u5230\u8fd9\u4e2a\u6761\u4ef6\u5bf9\u8054\u4fe1\u662f\u6709\u5229\u7684\uff0c\u81f3\u5c11\u8054\u4fe1\u73b0\u5728\u5df2\u7ecf\u6709\u4e86\u4e00\u5bb6\u8d34\u724c\u5382\u5546\u79d1\u738b\u9ad8\u79d1\uff0c\u90a3\u81ea\u7136\u5c31\u4e0d\u53ef\u80fd\u5f3a\u8feb\u8054\u4fe1\u8054\u6d88\u4e0e\u79d1\u738b\u9ad8\u79d1\u4e4b\u95f4\u7684\u8d34\u724c\u534f\u8bae\uff0c\u8981\u662f\u79d1\u738b\u9ad8\u79d1\u80fd\u53d1\u5c55\u8d77\u6765\uff0c\u8054\u4fe1\u4e0d\u4ec5\u53ef\u4ee5\u6536\u53d6\u9ad8\u989d\u7684\u8d34\u724c\u8d39\u7528\uff0c\u8fd8\u53ef\u4ee5\u5c06\u79d1\u738b\u9ad8\u79d1\u7684\u5e02\u573a\u4e1a\u7ee9\u4e00\u8d77\u5e76\u8fc7\u6765\u8ba1\u7b97\uff0c\u7b97\u662f\u7ed9\u90ae\u7535\u90e8\u9a73\u56de\u4e9b\u9762\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u6709\u5173\u7cfb\uff0c\u5927\u5bb6\u96be\u5f97\u5750\u5728\u4e00\u8d77\uff0c\u4ec0\u4e48\u6761\u4ef6\u90fd\u53ef\u4ee5\u655e\u5f00\u4e86\u8c08\uff0c\u4eab\u53d7\u6743\u5229\u5c31\u5e94\u8be5\u5c65\u884c\u7684\u4e49\u52a1\u3002\u5230\u5e95\u5e94\u8be5\u600e\u6837\u9632\u6b62\u6280\u672f\u8d44\u4ea7\u88ab\u6ee5\u7528\uff0c\u5de5\u4f5c\u8fd8\u662f\u6709\u70b9\u590d\u6742\u7684\uff0c\u8054\u4fe1\u4e0d\u53c2\u4e0e\u8fdb\u6765\uff0c\u7231\u8fbe\u4e0e\u8054\u8baf\u3001\u4e1c\u5174\u5c31\u8f9b\u82e6\u4e00\u4e9b\uff1b\u8054\u8baf\u3001\u4e1c\u5174\u4e5f\u4e0d\u53c2\u4e0e\u8fdb\u6765\uff0c\u7231\u8fbe\u52c9\u5f3a\u4e5f\u662f\u53ef\u4ee5\u5c06\u5de5\u4f5c\u5f00\u5c55\u4e0b\u53bb\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8bdd\u91cc\u7684\u610f\u601d\u5df2\u7ecf\u662f\u5f88\u660e\u786e\u4e86\uff0c\u8054\u4fe1\u53ef\u4ee5\u4e0d\u7b54\u5e94\u8fd9\u4e9b\u6761\u4ef6\uff0c\u90a3\u8054\u4fe1\u5c31\u662f\u8981\u63a5\u53d7\u522b\u4eba\u7684\u5ba1\u67e5\uff0c\u800c\u4e0d\u662f\u4e00\u540c\u53bb\u5ba1\u67e5\u522b\u4eba\u4e86\uff0c\u60f3\u5f97\u5230\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u90a3\u66f4\u662f\u4e0d\u53ef\u80fd\u3002</p></article>"}}}]);
