(window.webpackJsonp=window.webpackJsonp||[]).push([[684],{707:function(p){p.exports={chapterTitle:null,title:"\u7b2c527\u7ae0 \u521b\u827a\u4f1a\u6240",content:"<article><h2>\u7b2c527\u7ae0 \u521b\u827a\u4f1a\u6240</h2><p>\u3000\u3000\u4eca\u5929\u9002\u5de7\u5468\u65e5\uff0c\u4e0e\u5f90\u5b66\u5e73\u8fb9\u4e0b\u68cb\u8fb9\u804a\u5929\uff0c\u76f4\u5230\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u5468\u4e00\u5e73\u4eca\u5929\u624d\u4ece\u6d77\u5dde\u8d76\u5230\u5317\u4eac\uff0c\u59da\u575a\u5f00\u8f66\u5c06\u5468\u4e00\u5e73\u4ece\u673a\u573a\u63a5\u5230\u5e02\u533a\uff0c\u518d\u5230\u6728\u6a28\u91cc\u6765\u63a5\u5f20\u606a\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u4ece\u5f90\u5b66\u5e73\u8fd9\u91cc\u5f97\u5230\u7684\u4fe1\u606f\u7edd\u4e0d\u80fd\u8ba9\u4eba\u9ad8\u5174\uff0c\u5728\u4e2d\u592e\u5404\u90e8\u59d4\u91cc\uff0c\u6613\u4e91\u98de\u4e0e\u90ae\u7535\u90e8\u7684\u4e3b\u8981\u5b98\u5458\u90fd\u8981\u7b97\u4e0a\u5c11\u6709\u7684\u6027\u683c\u9c9c\u660e\u7684\u4e00\u7fa4\u4eba\uff0c\u5f53\u521d\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u652f\u6301\u7535\u5b50\u5de5\u4e1a\u90e8\u7ec4\u5efa\u8054\u5408\u901a\u4fe1\u6709\u9650\u516c\u53f8\uff0c\u6709\u610f\u6253\u7834\u90ae\u7535\u90e8\u5bf9\u7535\u4fe1\u4ea7\u4e1a\u7684\u5784\u65ad\uff0c\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u8fd8\u60f3\u6709\u8fdb\u4e00\u6b65\u52a8\u4f5c\u65f6\uff0c\u6613\u4e91\u98de\u4e0e\u65f6\u4efb\u90ae\u7535\u90e8\u90e8\u957f\u3001\u6b64\u65f6\u5df2\u7ecf\u9000\u5c45\u4e8c\u7ebf\u5230\u4eba\u5927\u4efb\u804c\u7684\u5468\u5b66\u5e86\u4e24\u4eba\u5c31\u6562\u5c06\u8f9e\u804c\u4e66\u9012\u5230\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u7684\u529e\u516c\u684c\u4e0a\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u5468\u5b66\u5e86\u4ecd\u7136\u662f\u575a\u5b9a\u7684\u575a\u6301\u8981\u7531\u90ae\u7535\u90e8\u5bf9\u7535\u4fe1\u884c\u4e1a\u8fdb\u884c\u5784\u65ad\u7ecf\u8425\u3002</p><p>\u3000\u3000\u8fd9\u6837\u4e00\u4e9b\u6027\u683c\u5f3a\u52bf\u7684\u6267\u638c\u7535\u4fe1\u4e1a\u7684\u5b98\u5458\uff0c\u8981\u64ac\u5f00\u4ed6\u4eec\u7684\u624b\uff0c\u8ba9\u5e9e\u5927\u7684\u901a\u4fe1\u5e02\u573a\u5728\u4e5d\u4e03\u5e74\u5c31\u5411\u56fd\u5185\u6c11\u8425\u4f01\u4e1a\u9732\u51fa\u4e00\u9053\u72ed\u7a84\u7684\u900f\u7740\u5149\u4eae\u7684\u7f1d\u9699\uff0c\u53ef\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u76f4\u63a5\u653b\u514b\u90ae\u7535\u90e8\u7684\u6838\u5fc3\u5821\u5792\u662f\u4e0d\u73b0\u5b9e\u7684\uff0c\u73b0\u9636\u6bb5\u8fd8\u9700\u8981\u5728\u5916\u90e8\u7ec6\u5fc3\u7684\u7f16\u7f51\u9020\u52bf\u3002</p><p>\u3000\u3000\u79bb\u5f00\u5f90\u5b66\u5e73\u5bb6\uff0c\u5f20\u606a\u5750\u5728\u8f66\u91cc\uff0c\u51dd\u89c6\u8f66\u7a97\u5916\u4e3b\u8981\u7531\u7070\u767d\u8272\u8c03\u7ec4\u6210\u7684\u5317\u4eac\u8857\u5934\u3002</p><p>\u3000\u3000\u5f20\u606a\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u901a\u8fc7\u8868\u54e5\u6881\u6587\u6c5f\u5148\u8ddf\u4ed6\u672a\u6765\u7684\u5cb3\u7236\u738b\u65ed\u741b\u8054\u7cfb\u4e0a\uff0c\u4f46\u662f\u60f3\u60f3\u65f6\u4efb\u90ae\u7535\u90e8\u8ba1\u5efa\u53f8\u526f\u53f8\u7ea7\u9ad8\u7ea7\u5b98\u5458\u3001\u6280\u672f\u5b98\u50da\u51fa\u8eab\u7684\u738b\u65ed\u741b\u5374\u4e0d\u4e00\u5b9a\u5bf9\u5973\u513f\u627e\u4e86\u4e00\u4e2a\u4e66\u5446\u6c14\u4e25\u91cd\u7684\u7537\u670b\u53cb\u6ee1\u610f\uff0c\u523b\u610f\u7684\u53bb\u7ecf\u8425\u8fd9\u5c42\u5173\u7cfb\uff0c\u672a\u5fc5\u4f1a\u5f88\u987a\u5229\u3002</p><p>\u3000\u3000\u4e09\u6708\u521d\u6625\uff0c\u5317\u4eac\u7684\u98ce\u6c99\u5f88\u5927\uff0c\u4ed6\u4eec\u5230\u5317\u4eac\u6765\u524d\u4e00\u5929\uff0c\u5317\u4eac\u8fd8\u522e\u8fc7\u6c99\u5c18\u66b4\uff0c\u906e\u5929\u95ed\u65e5\uff0c\u5927\u4e2d\u5348\u8ddf\u9634\u6c89\u7684\u9ec4\u660f\u4e00\u6837\u660f\u6697\uff1b\u4e0b\u5348\u5929\u9634\uff0c\u98ce\u6c99\u4e0d\u5927\uff0c\u98ce\u5374\u4e0d\u5c11\uff0c\u63a0\u8fc7\u679d\u53f6\u7a00\u758f\u7684\u6797\u68a2\uff0c\u53d1\u51fa\u5947\u5f02\u7684\u54cd\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u59da\u575a\uff1a\u201c\u5317\u4eac\u6709\u4ec0\u4e48\u597d\u4e00\u4e9b\u805a\u9910\u5730\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u521b\u827a\u4f1a\u6240\u662f\u4e00\u5bb6\u5e74\u8f7b\u521b\u4e1a\u8005\u6bd4\u8f83\u591a\u7684\u5730\u65b9\uff0c\u6c1b\u56f4\u4e5f\u4e0d\u9519\uff0c\u4e0d\u6653\u5f97\u606a\u5c11\u4f1a\u4e0d\u4f1a\u5acc\u6863\u6b21\u4f4e\uff0c\u201d\u59da\u575a\u8bf4\u9053\uff0c\u201c\u6211\u53ef\u4ee5\u53bb\u501f\u4f1a\u5458\u5361\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u4e00\u5e73\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6761\u4ef6\u4e5f\u591f\u8270\u82e6\u7684\u3002\u201d</p><p>\u3000\u3000\u82e5\u8bf4\u6761\u4ef6\u597d\u4e00\u4e9b\u7684\u805a\u9910\u5730\u70b9\uff0c\u8fd8\u771f\u5c31\u662f\u8fd9\u4e9b\u5b9e\u884c\u4f1a\u5458\u5236\u7684\u4f1a\u6240\uff0c\u5f20\u606a\u665a\u4e0a\u8981\u8bf7\u6606\u817e\u5728\u7ebf\u7684\u51e0\u4e2a\u4e3b\u521b\u4eba\u5458\u4e00\u8d77\u5403\u996d\uff0c\u4e0d\u60f3\u592a\u968f\u4fbf\u3002\u4e5f\u4e0d\u4ec5\u4ec5\u4eca\u5929\uff0c\u4ed6\u5728\u5317\u4eac\u9700\u8981\u4e00\u4e2a\u7a0d\u4e3a\u6b63\u5f0f\u4e9b\u7684\u573a\u6240\u8fdb\u884c\u793e\u4ea4\u6d3b\u52a8\uff0c\u603b\u4e0d\u80fd\u4e00\u4e9b\u5546\u52a1\u4e0a\u7684\u4f1a\u8c08\u90fd\u8981\u53bb\u6628\u5929\u591c\u91cc\u73a9\u51e0\u4e4e\u901a\u5bb5\u7684\u201c\u7cd6\u679c\u201d\u591c\u5e97\u8fdb\u884c\u5427\u3002</p><p>\u3000\u3000\u7adf\u7136\u8981\u501f\u4f1a\u5458\u5361\u53bb\u4f1a\u6240\u5403\u665a\u996d\uff0c\u8bf4\u51fa\u53bb\u4e5f\u771f\u591f\u4e22\u4eba\u7684\uff0c\u53ea\u662f\u5f20\u606a\u6ca1\u6709\u8fd9\u65b9\u9762\u7684\u81ea\u89c9\uff0c\u89c9\u5f97\u59da\u575a\u7684\u5efa\u8bae\u8fd8\u4e0d\u9519\uff0c\u5c31\u8ba9\u4ed6\u6253\u7535\u8bdd\u5148\u843d\u5b9e\u3002\u8fd9\u8fb9\u518d\u8ba9\u5085\u4fca\u901a\u77e5\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u53bb\u521b\u7ef4\u4f1a\u6240\u5403\u996d\uff0c\u53c8\u901a\u77e5\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u521b\u7ef4\u4f1a\u6240\u7684\u5730\u70b9\u3002</p><p>\u3000\u3000\u521b\u827a\u4f1a\u6240\u7684\u786e\u4e0d\u662f\u4e00\u4e2a\u5f88\u8c6a\u534e\u7684\u5730\u65b9\uff0c\u6ca1\u6709\u7279\u522b\u9ad8\u6863\u7684\u8f7f\u8f66\uff0c\u4e0d\u8fc7\u5317\u4eac\u5206\u516c\u53f8\u7684\u6851\u5854\u7eb3\u505c\u5230\u95e8\u5916\u7684\u505c\u8f66\u573a\u91cc\uff0c\u8fd8\u662f\u76f8\u5f53\u788d\u773c\u7684\uff1b\u5f20\u606a\u5728\u5317\u4eac\u4e5f\u6709\u4e13\u95e8\u7684\u5907\u8f66\u3002</p><p>\u3000\u3000\u59da\u575a\u7684\u670b\u53cb\u65e9\u5728\u4f1a\u6240\u95e8\u53e3\u606d\u5019\uff0c\u662f\u7231\u8fbe\u7535\u5b50\u5728\u5317\u4eac\u7684\u7ecf\u9500\u5546\uff0c\u59d3\u9a6c\u3002\u9a6c\u745e\u8fd9\u4eba\uff0c\u5f20\u606a\u6709\u63a5\u89e6\u8fc7\uff1b\u4ed6\u4e5f\u8ba4\u5f97\u5f20\u606a\uff0c\u770b\u89c1\u59da\u575a\u5f00\u7684\u90a3\u8f86\u7834\u6851\u5854\u90a3\u9a76\u8fc7\u6765\uff0c\u8fd8\u6709\u4e9b\u53d1\u6123\uff0c\u770b\u89c1\u5f20\u606a\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u624d\u56de\u8fc7\u795e\u6765\uff0c\u70ed\u60c5\u6d0b\u6ea2\u7684\u8dd1\u8fc7\u6765\u63e1\u4f4f\u5f20\u606a\u7684\u624b\uff1a\u201c\u606a\u5c11\u53ea\u6709\u8fd9\u56de\u5230\u5317\u4eac\u8bb0\u5f97\u6211\u8fd9\u4e2a\u8001\u670b\u53cb\uff1b\u600e\u4e48\u5230\u5317\u4eac\u5750\u8fd9\u4e2a\u8f66\uff0c\u8981\u4e0d\u8981\u628a\u6211\u7684\u8f66\u62ff\u8fc7\u53bb\u5f00\uff1f\u201d</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u5341\u4e00\u6708\uff0c\u7231\u8fbe\u7535\u5b50\u7ade\u6807\u592e\u89c6\u6807\u738b\u4e00\u4e3e\u6210\u540d\uff0c\u4e3a\u4e86\u5e94\u4ed8\u5982\u6f6e\u6c34\u6d8c\u6765\u7684\u5a92\u4f53\u8bb0\u8005\uff0c\u9a6c\u745e\u9886\u7740\u4ed6\u4eec\u7684\u5458\u5de5\u5e2e\u4e86\u597d\u4e9b\u5929\u7684\u5fd9\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e00\u98de\u51b2\u5929\uff0c\u4f5c\u4e3a\u7231\u8fbe\u7535\u5b50\u5728\u5317\u4eac\u7684\u7ecf\u9500\u5546\uff0c\u9a6c\u745e\u4e5f\u662f\u8d5a\u5f97\u6ee1\u94b5\u6ee1\u76c6\uff0c\u5f53\u7136\u4e5f\u4e0d\u6b62\u5728\u7231\u8fbe\u7535\u5b50\u4e00\u5bb6\u5934\u4e0a\u8d5a\u94b1\uff0c\u662f\u7231\u8fbe\u7535\u5b50\u5c06\u4ed6\u6367\u4e0a\u5317\u4eac\u4e00\u7c7b\u7535\u5668\u7ecf\u9500\u5546\u7684\u5730\u4f4d\uff0c\u540c\u65f6\u4e5f\u6210\u4e3a\u9999\u96ea\u6d77\u7b49\u591a\u5bb6\u7535\u5668\u5382\u5546\u5728\u5317\u4eac\u7684\u7ecf\u9500\u5546\uff0c\u89c4\u6a21\u4e00\u4e0b\u5b50\u5c31\u58ee\u5927\u8d77\u6765\u3002\u4e24\u5e74\u524d\uff0c\u4ed6\u4e5f\u53ea\u662f\u5f00\u6851\u5854\u7eb3\u800c\u5df2\uff0c\u4f5c\u4e3a\u4e00\u7c7b\u7ecf\u9500\u5546\u7f34\u7ed9\u7231\u8fbe\u7535\u5b50\u7684\u4e94\u767e\u4e07\u4fdd\u8bc1\u91d1\u8fd8\u4e1c\u501f\u897f\u62fc\u51d1\u51fa\u6765\u7684\uff0c\u73b0\u5728\uff0c\u5f20\u606a\u987a\u7740\u4ed6\u7684\u624b\u52bf\u671b\u8fc7\u53bb\uff0c\u4ed6\u5df2\u7ecf\u6362\u4e86\u4e00\u8f86\u7687\u51a0\u4e86\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u9664\u4e86\u4e00\u4e9b\u5e74\u8f7b\u7684\u4f01\u4e1a\u5bb6\u4e4b\u5916\uff0c\u8fd8\u5e38\u6709\u4e00\u4e9b\u6f14\u827a\u5708\u7684\u670b\u53cb\u51fa\u5165\uff0c\u606a\u5c11\u8981\u4e0d\u8981\u6211\u5e2e\u5fd9\u4ecb\u7ecd\uff1f\u201d\u9a6c\u745e\u8ba8\u597d\u7684\u4ecb\u7ecd\u521b\u827a\u4f1a\u6240\u7684\u7279\u8272\uff0c\u4ece\u76ae\u5939\u91cc\u638f\u51fa\u4e00\u5f20\u91d1\u5361\uff0c\u6ca1\u6709\u76f4\u63a5\u9001\u7ed9\u5f20\u606a\uff0c\u800c\u662f\u9012\u7ed9\u59da\u575a\uff0c\u201c\u6211\u5f88\u4e0d\u60f3\u5c06\u8fd9\u5f20\u5361\u9001\u7ed9\u59da\u7ecf\u7406\u7684\uff0c\u6211\u60f3\u59da\u7ecf\u7406\u63a5\u5f85\u603b\u90e8\u7684\u9ad8\u5c42\u6bcf\u6b21\u90fd\u634e\u6211\u5c31\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u59da\u575a\u770b\u4e86\u5f20\u606a\u7b11\u610f\u6e29\u548c\uff0c\u5c31\u5c06\u9a6c\u745e\u9001\u7ed9\u4f1a\u5458\u91d1\u5361\u6536\u4e0b\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u4ed6\u4eec\u4ece\u6e05\u534e\u56ed\u8d76\u8fc7\u6765\u8fd8\u8981\u4e00\u4f1a\u65f6\u95f4\uff0c\u8fd9\u65f6\u5019\u53c8\u662f\u5317\u4eac\u7684\u4ea4\u901a\u9ad8\u5cf0\u6bb5\uff0c\u6ca1\u6709\u5fc5\u8981\u5728\u5916\u9762\u7b49\u4ed6\u4eec\uff0c\u5f20\u606a\u4ed6\u4eec\u5148\u8fdb\u4e86\u4f1a\u6240\u3002</p><p>\u3000\u3000\u5230\u4f1a\u6240\u6765\u7528\u665a\u9910\u7684\u4eba\u4e0d\u591a\uff0c\u516b\u4e5d\u70b9\u949f\u4e4b\u540e\uff0c\u4f1a\u6240\u624d\u4f1a\u771f\u6b63\u70ed\u95f9\u8d77\u6765\u3002\u9a6c\u745e\u662f\u8fd9\u91cc\u7684\u5e38\u5ba2\uff0c\u8d70\u8fdb\u53bb\uff0c\u4e00\u4e9b\u4eba\u8dd1\u8fc7\u6765\u8ddf\u4ed6\u6253\u62db\u547c\uff0c\u9a6c\u745e\u90fd\u4e00\u4e00\u4e3a\u5f20\u606a\u4ecb\u7ecd\uff0c\u4ecb\u7ecd\u5f20\u606a\u65f6\u53c8\u542b\u6df7\u800c\u8fc7\uff0c\u4ed6\u6653\u5f97\u5f20\u606a\u4e0d\u559c\u6b22\u592a\u5f20\u626c\uff0c\u8fc4\u4eca\u90fd\u6ca1\u6709\u5a92\u4f53\u4e0a\u9732\u8fc7\u9762\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u8fd8\u6709\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\u8d75\u9e4f\u5148\u8d76\u4e86\u8fc7\u6765\u6c47\u5408\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u5927\u6982\u8fc7\u4e86\u534a\u4e2a\u5c0f\u65f6\u624d\u8d76\u5230\uff0c\u6881\u6587\u6c5f\u7684\u5973\u670b\u53cb\u738b\u5f69\u73b2\u4e5f\u5728\uff0c\u5979\u4e00\u76f4\u7ed9\u6881\u6587\u6c5f\u62c9\u8fc7\u53bb\u5f53\u514d\u8d39\u7684\u6280\u672f\u52b3\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u7740\u73b0\u5728\u53ef\u4ee5\u5c06\u6606\u817e\u5728\u7ebf\u7eb3\u5165\u9526\u6e56\u7684\u6574\u4e2a\u6846\u67b6\u4e4b\u5185\uff0c\u4ed6\u5bf9\u6606\u817e\u5728\u7ebf\u7b2c\u4e8c\u6b21\u4e24\u5343\u4e07\u7684\u6ce8\u8d44\u5c31\u76f4\u63a5\u4ee5\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7684\u540d\u4e49\u8fdb\u884c\u7684\uff0c\u4ee5\u540e\u4e5f\u5c06\u6606\u817e\u5728\u7ebf\u7eb3\u5165\u8fd0\u8425\u90e8\u7684\u7ba1\u7406\u8303\u7574\u4e4b\u5185\uff1b\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u5bf9\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u90fd\u8fd8\u4e0d\u592a\u719f\u6089\uff0c\u6240\u4ee5\u8981\u7ea6\u51fa\u6765\u4e00\u8d77\u5403\u996d\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u9690\u6027\u5bcc\u8c6a\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ee3\u66ff\u4ed6\u6267\u638c\u7231\u8fbe\u7535\u5b50\uff0c\u7ecf\u5a92\u4f53\u5ba3\u5e03\uff0c\u540d\u6c14\u5f88\u5927\uff0c\u9a6c\u745e\u770b\u5230\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e00\u540c\u51fa\u73b0\u5728\u5317\u4eac\uff0c\u6b64\u5916\u8fd8\u6709\u4e00\u76f4\u5728\u7231\u8fbe\u7535\u5b50\u8d1f\u8d23\u4eba\u529b\u8d44\u6e90\u3001\u884c\u653f\u3001\u91c7\u8d2d\u7b49\u90e8\u95e8\u5de5\u4f5c\u3001\u5374\u5f88\u5c11\u5728\u5a92\u4f53\u524d\u9732\u9762\u7684\u5468\u4e00\u5e73\uff0c\u95ee\u9053\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u53c8\u8981\u641e\u4ec0\u4e48\u5927\u52a8\u4f5c\uff0c\u52b3\u9a7e\u4e09\u4f4d\u51fa\u9a6c\uff1f\u201d</p><p>\u3000\u3000\u4ed6\u8fd9\u4e48\u95ee\uff0c\u4e5f\u662f\u60f3\u5bfb\u627e\u6709\u4ec0\u4e48\u9002\u5408\u7684\u5546\u673a\uff0c\u5f53\u7136\uff0c\u8981\u662f\u6d89\u53ca\u5230\u7231\u8fbe\u7535\u5b50\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u5f20\u606a\u4ed6\u4eec\u5b8c\u5168\u53ef\u4ee5\u4e0d\u7406\u4f1a\uff0c\u62ff\u8bdd\u642a\u585e\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6beb\u4e0d\u4ecb\u610f\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u8fb9\u7684\u5206\u516c\u53f8\u4f1a\u5347\u683c\uff0c\u8fd8\u8981\u65b0\u7b79\u5907\u4e00\u4e2a\u5de5\u7a0b\u7814\u53d1\u4e2d\u5fc3\uff0c\u5012\u4e5f\u6ca1\u6709\u7279\u5927\u7684\u52a8\u4f5c\u80fd\u8ba9\u9a6c\u603b\u671f\u5f85\u2026\u2026\u201d</p><p>\u3000\u3000\u4ec5\u4ec5\u662f\u5206\u516c\u53f8\u5347\u683c\u3001\u7b79\u5907\u5de5\u7a0b\u7814\u53d1\u4e2d\u5fc3\uff0c\u6ca1\u6709\u5fc5\u8981\u5f20\u606a\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u540c\u65f6\u51fa\u9a6c\uff0c\u5173\u952e\u662f\u8981\u6781\u77ed\u7684\u65f6\u95f4\u91cc\u628a\u8fd9\u4e9b\u4e8b\u60c5\u505a\u6210\uff0c\u800c\u4e14\u8981\u505a\u51fa\u58f0\u52bf\u6765\uff0c\u6240\u4ee5\u5f20\u606a\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u624d\u4eb2\u81ea\u51fa\u9a6c\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u90fd\u53ef\u4ee5\u8fc5\u901f\u5feb\u901f\u7684\u505a\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u59da\u575a\u6709\u4e9b\u660e\u767d\u5468\u4e00\u5e73\u4e3a\u4ec0\u4e48\u4eca\u5929\u4f1a\u4e58\u98de\u673a\u4ece\u6d77\u5dde\u8d76\u6765\uff0c\u5317\u4eac\u5206\u516c\u53f8\u4e4b\u524d\u96b6\u5c5e\u4e8e\u5e02\u573a\u90e8\u4e4b\u4e0b\uff0c\u73b0\u5728\u8981\u5347\u683c\u6210\u4e00\u7ea7\u90e8\u95e8\uff0c\u5468\u4e00\u5e73\u662f\u603b\u90e8\u6311\u9009\u51fa\u6765\u6311\u5927\u6881\u7684\u4eba\u9009\u3002</p><p>\u3000\u3000\u9152\u684c\u4e0a\uff0c\u9a6c\u5411\u4e1c\u5f00\u8d77\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u4e24\u4eba\u73a9\u7b11\uff0c\u8bf4\u8d77\u4e00\u4ef6\u6709\u8da3\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e24\u4e2a\u661f\u671f\u524d\uff0c\u738b\u5f69\u73b2\u7236\u6bcd\u5916\u51fa\uff0c\u738b\u5f69\u73b2\u5077\u5077\u6478\u6478\u7ea6\u6881\u6587\u6c5f\u53bb\u5979\u5bb6\u73a9\uff0c\u6ca1\u60f3\u5230\u5979\u7236\u6bcd\u4e2d\u9014\u56de\u5bb6\u62ff\u4e1c\u897f\uff0c\u5c06\u4e24\u4eba\u5835\u5bb6\u91cc\uff0c\u8fd9\u624d\u8ba9\u4ed6\u4eec\u4fe9\u4eba\u7684\u604b\u60c5\u63d0\u524d\u5728\u738b\u5f69\u73b2\u7236\u6bcd\u9762\u524d\u66b4\u5149\u3002</p><p>\u3000\u3000\u738b\u5f69\u73b2\u7236\u4eb2\u738b\u65ed\u741b\u65e9\u5e74\u519b\u65c5\u51fa\u8eab\uff0c\u9000\u4f0d\u540e\u5230\u90ae\u7535\u90e8\u5de5\u4f5c\uff0c\u53c8\u8003\u5165\u5f53\u65f6\u7684\u5317\u4eac\u90ae\u7535\u5b66\u9662\u73b0\u5728\u5317\u4eac\u90ae\u7535\u5927\u5b66\uff0c\u8fd8\u66fe\u8fdb\u5165\u6e05\u534e\u7535\u5b50\u8fdb\u4fee\uff0c\u6210\u4e3a\u90ae\u7535\u90e8\u6280\u672f\u5b98\u50da\u7684\u4ee3\u8868\u4eba\u7269\u4e4b\u4e00\u3002\u738b\u65ed\u741b\u770b\u5230\u6881\u6587\u6c5f\u5728\u4ed6\u5bb6\uff0c\u5fc3\u91cc\u81ea\u7136\u4e0d\u60a6\uff0c\u5230\u5e95\u662f\u77e5\u8bc6\u5206\u5b50\u51fa\u8eab\uff0c\u4e0e\u738b\u5f69\u73b2\u6bcd\u4eb2\u4e5f\u6ca1\u6709\u5f53\u573a\u7ffb\u8138\u5c06\u6881\u6587\u6c5f\u8d76\u51fa\u53bb\uff0c\u8fd8\u5f88\u6709\u793c\u8c8c\u7684\u5c06\u6881\u6587\u6c5f\u7559\u4e0b\u5403\u665a\u996d\u3002\u5403\u665a\u996d\u65f6\uff0c\u738b\u65ed\u741b\u8981\u6881\u6587\u6c5f\u966a\u4ed6\u559d\u4e24\u676f\uff0c\u6881\u6587\u6c5f\u867d\u7136\u4e4b\u524d\u6ef4\u9152\u4e0d\u6cbe\uff0c\u4e0d\u8fc7\u6ca1\u6562\u62d2\u7edd\u3002\u4e24\u676f\u4e0b\u809a\u5b50\u5c31\u4e0d\u77e5\u9053\u4e1c\u5357\u897f\u5317\uff0c\u4e5f\u6ca1\u6709\u9a6c\u4e0a\u5c31\u9189\u5012\uff0c\u4e0e\u738b\u65ed\u741b\u5728\u9152\u684c\u4e0a\u8ba8\u8bba\u8d77\u4e00\u672c\u6b63\u7ecf\u7684\u5b66\u672f\u95ee\u9898\uff0c\u5c31\u7b97\u559d\u9189\u9152\uff0c\u4e89\u8bba\u95ee\u9898\u8d77\u6765\u8fd8\u5f6c\u5f6c\u6709\u793c\uff0c\u636e\u738b\u5f69\u73b2\u81ea\u5df1\u8bf4\uff0c\u4ed6\u7236\u4eb2\u770b\u7740\u8db4\u5728\u684c\u5b50\u6d41\u53e3\u6c34\u7761\u7740\u7684\u6881\u6587\u6c5f\u8bf4\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u8fd8\u7b97\u4e0d\u9519\u2026\u2026\u201d\u7b97\u662f\u9ed8\u8ba4\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u975e\u4f46\u5982\u6b64\uff0c\u738b\u65ed\u741b\u8fd8\u7ecf\u5e38\u8ba9\u738b\u5f69\u73b2\u9886\u7740\u6881\u6587\u6c5f\u56de\u53bb\u5403\u996d\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u7684\u56e0\u7978\u5f97\u798f\u60f9\u5f97\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u5f20\u606a\u5012\u6ca1\u6709\u60f3\u5230\u8868\u54e5\u5df2\u7ecf\u83b7\u5f97\u4ed6\u672a\u6765\u5cb3\u7236\u7684\u627f\u8ba4\uff0c\u5f53\u5373\u4eb2\u5207\u79f0\u547c\u738b\u5f69\u73b2\uff1a\u201c\u8868\u5ac2\uff0c\u5565\u65f6\u5019\u8bf7\u6211\u8fd9\u4e2a\u5c0f\u8868\u5f1f\u53bb\u4f60\u5bb6\u73a9\u73a9\u53bb\uff1f\u201d</p><p>\u3000\u3000\u738b\u5f69\u73b2\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u6881\u6587\u6c5f\u5927\u5927\u54a7\u54a7\u7684\u8bf4\u9053\uff1a\u201c\u660e\u5929\u5427\uff0c\u660e\u5929\u665a\u4e0a\u6211\u8981\u53bb\u5f69\u73b2\u5bb6\u5403\u996d\uff0c\u5f69\u73b2\u4ed6\u7238\u559c\u6b22\u4e0b\u68cb\uff0c\u6c34\u5e73\u8ddf\u4f60\u5dee\u4e0d\u591a\uff0c\u4f60\u660e\u5929\u53bb\u5c31\u8d1f\u8d23\u966a\u4ed6\u7238\u7238\u4e0b\u68cb\u2026\u2026\u201d</p></article>"}}}]);
