(window.webpackJsonp=window.webpackJsonp||[]).push([[685],{708:function(p){p.exports={chapterTitle:null,title:"\u7b2c528\u7ae0 \u5317\u4eac\u5206\u516c\u53f8",content:"<article><h2>\u7b2c528\u7ae0 \u5317\u4eac\u5206\u516c\u53f8</h2><p>\u3000\u3000\u4f1a\u6240\u591c\u91cc\u6709\u821e\u4f1a\uff0c\u9a6c\u745e\u8bf4\u7684\u4e0d\u9519\uff0c\u521b\u827a\u4f1a\u6240\u6709\u8bb8\u591a\u6f14\u827a\u5708\u7684\u4eba\u58eb\u51fa\u6ca1\uff0c\u5f20\u606a\u5c31\u770b\u89c1\u597d\u51e0\u4e2a\u6b64\u65f6\u8fd8\u9ed8\u9ed8\u65e0\u95fb\u3001\u4eca\u540e\u51e0\u5e74\u4f1a\u5d2d\u9732\u5934\u89d2\u7684\u5708\u5185\u4eba\uff0c\u4e5f\u6709\u51e0\u4e2a\u8fd9\u65f6\u5c31\u5c0f\u6709\u540d\u6c14\u7684\u660e\u661f\uff0c\u5979\u4eec\u90fd\u6ca1\u6709\u660e\u661f\u67b6\u5b50\uff0c\u4e5f\u4e3b\u52a8\u767b\u53f0\u732e\u6b4c\uff1b\u8fd8\u6709\u4e00\u4e9b\u8eab\u6750\u9ad8\u6311\u3001\u5916\u8868\u9753\u4e3d\u7684\u6a21\u7279\uff0c\u90fd\u53ef\u4ee5\u9080\u8bf7\u5979\u4eec\u5171\u821e\uff0c\u4f1a\u6240\u591c\u573a\u8ba9\u5979\u4eec\u641e\u5f97\u6c14\u6c1b\u70ed\u70c8\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u5230\u821e\u6c60\u91cc\u53bb\u51d1\u70ed\u95f9\uff0c\u4ed6\u672c\u4eba\u5bf9\u90a3\u4e9b\u5373\u5c06\u6210\u540d\u6216\u8005\u5c06\u6c38\u8fdc\u6ca1\u843d\u7684\u5973\u5b69\u5b50\u4e0d\u611f\u5174\u8da3\uff0c\u5c06\u9a6c\u5411\u4e1c\u3001\u59da\u575a\u3001\u8d75\u9e4f\u7b49\u4eba\u7559\u5728\u96d5\u7a97\u8d34\u7eb8\u7684\u5c0f\u5305\u53a2\u91cc\u5546\u8bae\u4e8b\u60c5\uff0c\u4e4b\u524d\u6709\u63d0\u5230\u5317\u4eac\u5206\u516c\u53f8\u5347\u683c\uff0c\u5f20\u606a\u544a\u8bc9\u4ed6\u4eec\uff1a\u201c\u7231\u8fbe\u96c6\u56e2\u6210\u7acb\u4e4b\u540e\uff0c\u5317\u4eac\u5206\u516c\u53f8\u4f1a\u6210\u4e3a\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u5206\u652f\u673a\u6784\uff0c\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u662f\u4e00\u4f53\u4e24\u9762\uff0c\u5317\u4eac\u5206\u516c\u53f8\u7edf\u7b79\u8d1f\u8d23\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u5728\u5317\u4eac\u7684\u4e8b\u52a1\u2026\u2026\u201d\u5bf9\u5468\u4e00\u5e73\u8bf4\uff0c\u201c\u8fd9\u4e2a\u51b3\u5b9a\u5f88\u5306\u5fd9\uff0c\u6ca1\u6709\u65f6\u95f4\u8ddf\u4f60\u5546\u91cf\u5c31\u76f4\u63a5\u5c06\u4f60\u53eb\u5230\u5317\u4eac\u6765\uff0c\u6211\u5e0c\u671b\u4f60\u6765\u8d1f\u8d23\u5317\u4eac\u7684\u4e8b\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6628\u5929\u4e0a\u5348\u4ece\u674e\u4e49\u6c5f\u90a3\u91cc\u77e5\u9053\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\u674e\u8fdc\u6e56\u5bf9\u5236\u6d46\u9879\u76ee\u7559\u4e86\u5fc3\uff0c\u7d27\u6025\u5c06\u4e01\u69d0\u4ece\u6d77\u5dde\u3001\u5c06\u82cf\u6d25\u4e1c\u4ece\u9999\u6e2f\u53eb\u5230\u5efa\u90ba\u5546\u8bae\u5bf9\u7b56\uff0c\u5468\u4e00\u5e73\u8fd9\u4e9b\u5728\u6d77\u5dde\u7684\u4eba\u5458\u5e76\u4e0d\u6e05\u695a\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u6628\u5929\u4e0b\u5348\uff0c\u5468\u4e00\u5e73\u624d\u63a5\u5230\u901a\u77e5\u8ba9\u4ed6\u8d76\u53bb\u5317\u4eac\uff0c\u6d77\u5dde\u673a\u573a\u98de\u5f80\u5317\u4eac\u7684\u822a\u73ed\u5f88\u5c11\uff0c\u4ed6\u7b49\u5230\u4eca\u5929\u4e2d\u5348\u5c31\u80fd\u8d77\u884c\uff0c\u5230\u5317\u4eac\u4e4b\u540e\uff0c\u624d\u6e05\u695a\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002</p><p>\u3000\u3000\u867d\u7136\u7a81\u7136\u4e9b\uff0c\u4f46\u6bd5\u7adf\u4ece\u6b64\u80fd\u72ec\u6321\u4e00\u9762\uff0c\u5468\u4e00\u5e73\u662f\u4e50\u4e8e\u63a5\u53d7\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5468\u4e00\u5e73\u6ca1\u6709\u5f02\u8bae\uff0c\u53c8\u5bf9\u59da\u575a\u8bf4\u9053\uff1a\u201c\u63a5\u4e0b\u6765\u5317\u4eac\u5206\u516c\u53f8\u8fd9\u8fb9\u7684\u62c5\u5b50\u4f1a\u6bd4\u8f83\u91cd\uff0c\u6211\u5e0c\u671b\u4f60\u6765\u62c5\u4efb\u5317\u4eac\u5206\u516c\u53f8\u7684\u526f\u603b\u7ecf\u7406\uff0c\u534f\u52a9\u5468\u4e00\u5e73\u5904\u7406\u597d\u516c\u53f8\u5728\u5317\u4eac\u7684\u4e8b\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u59da\u575a\u5f53\u7136\u6ca1\u6709\u610f\u89c1\uff0c\u4e4b\u524d\u7684\u5317\u4eac\u5206\u516c\u53f8\u53ea\u662f\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u8bbe\u5728\u5317\u4eac\u7684\u4e34\u65f6\u529e\u4e8b\u673a\u6784\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u8054\u5408\u4e00\u5927\u6279\u9500\u552e\u4e3b\u7ba1\u96c6\u4f53\u8df3\u69fd\u5230\u79d1\u738b\u4e4b\u540e\uff0c\u5f20\u606a\u5c31\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u573a\u90e8\u8fdb\u884c\u6574\u987f\uff0c\u5728\u5e02\u573a\u90e8\u4e0b\u8bbe\u7acb\u9500\u552e\u5206\u90e8\u7edf\u7ba1\u9500\u552e\u5de5\u4f5c\uff0c\u7701\u5e02\u529e\u4e8b\u5904\u4e00\u5f8b\u88c1\u64a4\uff0c\u5728\u5927\u533a\u8bbe\u7acb\u9500\u552e\u5206\u516c\u53f8\uff0c\u534f\u8c03\u5927\u533a\u8303\u56f4\u5185\u5404\u7701\u5e02\u7684\u7269\u6d41\u3001\u552e\u540e\u3001\u5e02\u573a\u8425\u9500\u7b49\u5de5\u4f5c\u3002\u5317\u4eac\u529e\u4e8b\u5904\u5f97\u4ee5\u4fdd\u7559\u5e76\u72ec\u7acb\u6ce8\u518c\u6210\u516c\u53f8\uff0c\u81ea\u7136\u662f\u56e0\u4e3a\u5317\u4eac\u7684\u7279\u6b8a\u6027\uff1b\u7231\u8fbe\u7535\u5b50\u9700\u8981\u5728\u5317\u4eac\u8bbe\u4e00\u4e2a\u70b9\u3002</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u4e5f\u53ea\u662f\u5e02\u573a\u90e8\u8bbe\u7684\u4e00\u4e2a\u70b9\u800c\u5df2\uff0c\u52a0\u4e0a\u5185\u52e4\u3001\u8d22\u52a1\u3001\u53f8\u673a\u3001\u5546\u52a1\u52a9\u7406\u4ee5\u53ca\u59da\u575a\u672c\u4eba\u5728\u5185\uff0c\u5317\u4eac\u5206\u516c\u53f8\u624d\u516b\u540d\u5458\u5de5\u3002\u5206\u516c\u53f8\u5728\u6e05\u534e\u56ed\u5317\u95e8\u7684\u5927\u738b\u5e84\u9644\u8fd1\u79df\u4e86\u56db\u5c42\u5c0f\u697c\u4e2d\u7684\u4e00\u5c42\u697c\u529e\u516c\uff0c\u516d\u4e03\u5341\u5e74\u4ee3\u7684\u7816\u697c\uff0c\u4e00\u5c42\u697c\u4e5f\u5c31\u4e00\u767e\u56db\u5341\u5e73\u7c73\uff0c\u5f20\u606a\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8fd9\u4e9b\u9ad8\u5c42\u5230\u5317\u4eac\uff0c\u90fd\u6ca1\u6709\u5230\u5206\u516c\u53f8\u843d\u8fc7\u811a\u3002\u5206\u516c\u53f8\u6709\u4e24\u90e8\u8f66\uff0c\u4e00\u8f86\u4e8c\u624b\u7684\u6851\u5854\u7eb3\uff0c\u59da\u575a\u5e73\u65f6\u5f00\uff0c\u4e00\u8f86\u5546\u52a1\u8f66\u8fd8\u6709\u4e9b\u6f0f\u6cb9\uff0c\u8f66\u7a97\u4e0d\u6253\u5f00\uff0c\u6ee1\u8f66\u6cb9\u6c14\u5473\uff0c\u5728\u7231\u8fbe\u7535\u5b50\u7684\u4e8c\u7ea7\u90e8\u95e8\u5f53\u4e2d\uff0c\u5317\u4eac\u5206\u516c\u53f8\u4e5f\u8981\u6392\u5230\u672b\u51e0\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u6d3e\u7231\u8fbe\u7535\u5b50\u7684\u5143\u8001\u7ea7\u5e72\u5c06\u5468\u4e00\u5e73\u6765\u91cd\u65b0\u7ec4\u5efa\u5317\u4eac\u5206\u516c\u53f8\uff0c\u59da\u575a\u80fd\u80af\u5b9a\u4f1a\u6709\u4e00\u756a\u65b0\u6c14\u8c61\uff0c\u4ed6\u5f53\u7136\u4e50\u610f\u7ed9\u5468\u4e00\u5e73\u5f53\u52a9\u624b\uff0c\u518d\u8bf4\u4ed6\u7684\u4e2a\u4eba\u85aa\u8d44\u3001\u5f85\u9047\uff0c\u90fd\u4f1a\u56e0\u6b64\u4e0a\u8c03\u4e00\u7ea7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0e\u8868\u54e5\u6881\u6587\u6c5f\u7ea6\u597d\u665a\u4e9b\u65f6\u95f4\u4f5c\u4e3a\u4eb2\u621a\u8ddf\u968f\u53bb\u4ed6\u5973\u670b\u53cb\u738b\u5f69\u73b2\u5bb6\u5403\u665a\u996d\uff0c\u5f20\u606a\u767d\u5929\u5219\u4e0e\u5468\u4e00\u5e73\u3001\u59da\u575a\u6d41\u7a9c\u5230\u6d77\u6dc0\u533a\u4e3a\u65e5\u540e\u7684\u5317\u4eac\u5206\u516c\u53f8\u91cd\u65b0\u9009\u62e9\u529e\u516c\u5730\u5740\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u8981\u6c42\u5f88\u7b80\u5355\uff0c\u8981\u80fd\u7acb\u5373\u5c31\u7528\u4e0a\uff0c\u7acb\u5373\u80fd\u7528\u4e0a\u4e0d\u4ec5\u662f\u8981\u5c06\u539f\u5317\u4eac\u5206\u516c\u53f8\u7684\u4eba\u5458\u642c\u8fdb\u6765\u529e\u516c\u2014\u2014\u5317\u4eac\u5206\u516c\u53f8\u624d\u516d\u4e03\u540d\u5458\u5de5\uff0c\u9009\u5b9a\u529e\u516c\u5730\u70b9\uff0c\u5f53\u5929\u5c31\u80fd\u90fd\u642c\u8fdb\u53bb\uff0c\u6ca1\u6709\u4ec0\u4e48\u96be\u5ea6\u2014\u2014\u800c\u662f\u8981\u6c42\u5c06\u6606\u817e\u5728\u7ebf\u7684\u8bbe\u5907\u3001\u4eba\u5458\u80fd\u8fc5\u901f\u642c\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u9664\u4e86\u4f5c\u4e3a\u5373\u5c06\u7ec4\u5efa\u7684\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u5206\u652f\u673a\u6784\uff0c\u8fd8\u5c06\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u5bf9\u6606\u817e\u5728\u7ebf\u8fdb\u884c\u63a7\u80a1\uff0c\u8fd8\u80fd\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u5bf9\u5373\u5c06\u6210\u7acb\u7684\u5de5\u7a0b\u5e94\u7528\u4e2d\u5fc3\u8fdb\u884c\u63a7\u80a1\u5e76\u8d1f\u8d23\u884c\u653f\u4e8b\u52a1\u4e0a\u7684\u6307\u5bfc\uff0c\u8fd8\u5c06\u8d1f\u8d23\u7231\u8fbe\u96c6\u56e2\u4e0e\u5317\u4eac\u9ad8\u5c42\u7684\u6c9f\u901a\u3002</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u4e0d\u4ec5\u662f\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u6865\u5934\u5821\uff0c\u66f4\u662f\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u95e8\u8138\uff0c\u66f4\u8981\u8ba9\u7701\u91cc\u611f\u53d7\u5230\u9526\u6e56\u5728\u5317\u4eac\u53d1\u5c55\u7684\u201c\u51b3\u5fc3\u201d\u3002</p><p>\u3000\u3000\u4e2d\u5173\u6751\u79d1\u6280\u56ed\u662f1988\u5e745\u6708\u7ecf\u56fd\u52a1\u9662\u6279\u51c6\u5efa\u56ed\uff0c\u4f46\u662f\u4e2d\u5173\u6751\u7684\u771f\u6b63\u98de\u901f\u53d1\u5c55\u662f\u5728\u4e5d\u4e5d\u5e74\u4e4b\u540e\uff0c\u4e0d\u8fc7\u4e5d\u4e03\u5e74\u521d\u6625\uff0c\u4e2d\u5173\u6751\u79d1\u6280\u56ed\u7684\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u5b8c\u5907\uff0c\u7279\u522b\u662f\u4e00\u4e9b\u65b0\u5efa\u6210\u7684\u5199\u5b57\u697c\uff0c\u53ef\u4f9b\u9009\u62e9\u7684\u4f59\u5730\u5f88\u591a\u3002</p><p>\u3000\u3000\u4e0a\u5348\uff0c\u5f00\u8f66\u6cbf\u7740\u6d77\u6dc0\u4e2d\u8857\u7b49\u6d77\u6dc0\u533a\u4e3b\u8981\u8857\u9053\u778e\u8f6c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u8f66\u201c\u8d70\u9a6c\u89c2\u697c\u201d\u5bb9\u6613\uff0c\u59da\u575a\u5374\u4e0d\u8f7b\u677e\uff0c\u53ea\u8981\u5f20\u606a\u7a0d\u6ee1\u610f\u7684\u5927\u697c\uff0c\u4ed6\u5c31\u8981\u7acb\u5373\u6253\u7535\u8bdd\u901a\u77e5\u5206\u516c\u53f8\u7684\u5458\u5de5\u6536\u96c6\u8be5\u697c\u76d8\u7684\u79df\u552e\u8d44\u6599\uff0c\u4ed6\u603b\u4e0d\u80fd\u5962\u671b\u5f20\u606a\u803d\u6401\u51e0\u5929\u65f6\u95f4\u8ba9\u4ed6\u4ece\u5bb9\u4e0d\u8feb\u7684\u5c06\u8d44\u6599\u6574\u7406\u624d\u505a\u51b3\u5b9a\uff1b\u770b\u5f20\u606a\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7b49\u4eba\u9f50\u805a\u4eac\u57ce\u7684\u67b6\u5f0f\uff0c\u5927\u6982\u6068\u4e0d\u5f97\u4eca\u5929\u4e0b\u5348\u5c31\u5c06\u5206\u516c\u53f8\u4eca\u540e\u7684\u529e\u516c\u5730\u70b9\u786e\u5b9a\u4e0b\u6765\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e5f\u786e\u5b9e\u5982\u6b64\uff0c\u5728\u4e2d\u5173\u6751\u79d1\u56ed\u91cc\u8f6c\u4e86\u8fd1\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5728\u5f20\u606a\u7a0d\u6ee1\u610f\u7684\u5341\u4e8c\u680b\u5199\u5b57\u697c\u91cc\uff0c\u53ea\u6709\u56db\u680b\u6709\u5b8c\u6574\u7684\u529e\u516c\u697c\u5c42\u5411\u5916\u79df\u552e\uff0c\u5f20\u606a\u76f4\u63a5\u5c06\u7ea2\u7b14\u5708\u4f4f\u8fd8\u521a\u521a\u5bf9\u5916\u53d1\u552e\u7684\u5609\u91cc\u5927\u53a6\uff0c\u5bf9\u59da\u575a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u5c31\u8d70\u4e0a\u95e8\u53bb\uff0c\u82e5\u662f\u5408\u9002\uff0c\u6211\u4eec\u53ef\u4ee5\u8003\u8651\u5c06\u8fd9\u680b\u697c\u7ed9\u4e70\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5317\u4eac\u7f3a\u4e4f\u9ad8\u5c42\u5efa\u7b51\uff0c\u8fd9\u4e00\u70b9\u5341\u5e74\u540e\u90fd\u6ca1\u6709\u6539\u53d8\uff0c\u8fd9\u662f\u5317\u4eac\u5e02\u653f\u5e9c\u4e3a\u4e86\u4fdd\u62a4\u9996\u90fd\u5386\u53f2\u6587\u5316\u540d\u57ce\u7684\u9700\u8981\u800c\u5236\u5b9a\u7684\u9650\u5236\u653f\u7b56\uff1b\u65b0\u843d\u6210\u7684\u5609\u91cc\u5927\u53a6\u5728\u5468\u56f4\u697c\u7fa4\u91cc\u5f62\u8c61\u593a\u76ee\uff0c\u697c\u9ad8\u624d\u5341\u516b\u5c42\uff0c\u603b\u5efa\u7b51\u9762\u79ef\u4e0d\u8fc7\u4e24\u4e07\u5e73\u65b9\u7c73\u3002</p><p>\u3000\u3000\u5317\u4eac\u4e09\u73af\u4e5d\u56db\u5e74\u5e95\u624d\u5efa\u6210\u901a\u8f66\uff0c\u4e5d\u4e03\u5e74\u521d\u6625\uff0c\u56db\u73af\u5feb\u901f\u901a\u9053\u8fd8\u5728\u89c4\u5212\u4e4b\u4e2d\uff0c\u4f4d\u4e8e\u5317\u4eac\u897f\u5317\u90e8\u6d77\u6dc0\u533a\u7684\u4e2d\u5173\u6751\u79d1\u6280\u56ed\u8fdc\u6ca1\u6709\u5341\u5e74\u540e\u7684\u7e41\u534e\uff0c\u4e13\u95e8\u7684\u7535\u8111\u5e02\u573a\u624d\u4e24\u5ea7\u800c\u5df2\uff0c\u8fd9\u91cc\u7684\u5199\u5b57\u697c\u4ef7\u683c\u5728\u7ecf\u5386\u4e5d\u4e09\u5e74\u66b4\u8dcc\u4e4b\u540e\uff0c\u8981\u4e00\u76f4\u5230\u4e5d\u4e5d\u5e74\u624d\u5f00\u59cb\u4e95\u55b7\u5f0f\u7684\u590d\u82cf\uff0c\u8fd9\u4e0d\u4ec5\u4e0e\u56fd\u5185\u697c\u5e02\u53d1\u5c55\u606f\u606f\u76f8\u5173\uff0c\u4e5f\u4e0e\u4e2d\u5173\u6751\u79d1\u6280\u56ed\u4e5d\u4e5d\u5e74\u4e4b\u540e\u624d\u5f97\u5230\u771f\u6b63\u7684\u98de\u901f\u53d1\u5c55\u4e5f\u5f88\u5927\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u6b64\u65f6\u5728\u4e2d\u5173\u6751\u8d2d\u7f6e\u7269\u4e1a\u662f\u5408\u9002\u7684\uff0c\u4e0d\u7136\u4e5d\u4e5d\u5e74\u4e4b\u540e\uff0c\u5199\u5b57\u697c\u79df\u91d1\u4e5f\u4f1a\u98de\u901f\u4e0a\u6da8\uff0c\u589e\u52a0\u989d\u5916\u7684\u7ecf\u8425\u6210\u672c\uff0c\u8d2d\u7f6e\u7269\u4e1a\u5c31\u53ef\u4ee5\u5f52\u5165\u516c\u53f8\u7684\u8d44\u4ea7\uff0c\u8fd8\u53ef\u4ee5\u6539\u5584\u516c\u53f8\u7684\u8d44\u4ea7\u7ed3\u6784\u3002</p><p>\u3000\u3000\u6700\u5173\u952e\u7684\uff0c\u5728\u6d77\u6dc0\u4e2d\u8857\u62e5\u6709\u4e00\u680b\u81ea\u5df1\u7684\u5199\u5b57\u697c\uff0c\u5728\u91cd\u89c6\u8138\u9762\u7684\u5e74\u4ee3\uff0c\u7684\u786e\u80fd\u66ff\u9526\u6e56\u8d62\u5f97\u989d\u5916\u7684\u5c0a\u91cd\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4efb\u4f55\u7684\u63d0\u524d\u7ea6\u5b9a\uff0c\u5f20\u606a\u3001\u5468\u4e00\u5e73\u3001\u59da\u575a\u3001\u5085\u4fca\u76f4\u63a5\u8fdb\u5165\u5609\u91cc\u5927\u697c\u5e95\u5c42\u7684\u79df\u552e\u4e2d\u5fc3\uff0c\u670d\u52a1\u53f0\u5750\u7740\u4e24\u540d\u7a7f\u7edb\u7d2b\u8272\u5236\u670d\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5de5\u4f5c\u5236\u670d\u4e0e\u4e1c\u822a\u7a7a\u59d0\u5236\u670d\u5f88\u7c7b\u4f3c\uff0c\u4e4d\u8d70\u6765\u8fd8\u4ee5\u4e3a\u8d70\u8fdb\u67d0\u4e9b\u9ad8\u7ea7\u5bbe\u9986\uff0c\u8fd9\u5bb6\u516c\u53f8\u7684\u8001\u677f\u6709\u5236\u670d\u8bf1\u60d1\u7684\u5fc3\u7406\u503e\u5411\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u4f1a\u8c08\u533a\u91cc\u7a7a\u65e0\u4e00\u4eba\uff0c\u5dee\u4e0d\u591a\u5230\u4e2d\u5348\u5c31\u9910\u65f6\u95f4\uff0c\u5373\u4f7f\u4e0d\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u4f1a\u8c08\u533a\u7684\u4eba\u4e5f\u5e94\u8be5\u5f88\u5c11\u3002\u867d\u8bf4\u670d\u52a1\u53f0\u91cc\u7684\u4e24\u540d\u5de5\u4f5c\u4eba\u5458\u4e5f\u90fd\u76f8\u8c8c\u7aef\u5e84\uff0c\u4f46\u662f\u5f20\u606a\u5e76\u6ca1\u6709\u65f6\u95f4\u6d6a\u8d39\u5728\u6f02\u4eae\u5973\u5b69\u5b50\u8eab\u4e0a\uff0c\u76f4\u63a5\u8981\u6c42\u89c1\u5979\u4eec\u7684\u7ecf\u7406\uff0c\u7ecf\u7406\u4e5f\u662f\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u5e74\u7ea6\u4e8c\u5341\u4e03\u516b\u5c81\uff0c\u8fd9\u65f6\uff0c\u5468\u4e00\u5e73\u8868\u660e\u4ed6\u7684\u8eab\u4efd\uff0c\u8981\u6c42\u4e0e\u5979\u4eec\u516c\u53f8\u7684\u8001\u677f\u76f4\u63a5\u5546\u8c08\u8d2d\u697c\u4e8b\u5b9c\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u56fd\u5185\u53ea\u6015\u6ca1\u6709\u51e0\u5bb6\u516c\u53f8\u80fd\u6bd4\u7231\u8fbe\u7535\u5b50\u66f4\u51fa\u540d\uff0c\u5468\u4e00\u5e73\u90a3\u5f20\u7231\u8fbe\u7535\u5b50\u4eba\u529b\u8d44\u6e90\u53ca\u884c\u653f\u90e8\u603b\u7ecf\u7406\u7684\u540d\u7247\u8fd8\u662f\u76f8\u5f53\u7ba1\u7528\uff0c\u5609\u91cc\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\u603b\u88c1\u521a\u521a\u9a7e\u8f66\u51fa\u53bb\uff0c\u63a5\u542c\u5230\u7535\u8bdd\uff0c\u5148\u8ba9\u4ed6\u4eec\u516c\u53f8\u8fd9\u8fb9\u5b89\u6392\u5f20\u606a\u4ed6\u4eec\u7528\u5348\u9910\u3002</p><p>\u3000\u3000\u5609\u91cc\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\u603b\u88c1\u662f\u9999\u6e2f\u4eba\uff0c\u59d3\u5434\uff0c\u5434\u542f\u7acb\uff0c\u56db\u5341\u5c81\uff0c\u773c\u775b\u6709\u4e9b\u6c34\u80bf\uff0c\u4eba\u5f88\u80d6\uff0c\u5012\u662f\u7ed9\u4ed6\u62ff\u516c\u6587\u5305\u7684\u52a9\u7406\u5a07\u5c0f\u7ea4\u7626\u3002\u542c\u5434\u542f\u7acb\u8bf4\u4ed6\u65e9\u5e74\u4e5f\u5728\u9999\u6e2f\u505a\u5730\u4ea7\uff0c\u5982\u6b64\u9999\u6e2f\u5730\u4ea7\u5df2\u7ecf\u7ed9\u51e0\u5bb6\u5927\u5730\u4ea7\u5546\u5784\u65ad\uff0c\u4ed6\u4e5d\u4e8c\u5e74\u5c31\u5230\u5185\u5730\u53d1\u5c55\u5730\u4ea7\uff0c\u8d76\u4e0a\u4e5d\u4e8c\u5e74\u5317\u4eac\u623f\u4ea7\u4e95\u55b7\u5f0f\u53d1\u5c55\uff0c\u4e5f\u7ecf\u5386\u4e86\u4e5d\u4e09\u5e74\u5317\u4eac\u623f\u4ea7\u5782\u76f4\u5f0f\u4e0b\u964d\u5f0f\u7684\u6ce1\u6cab\u7834\u706d\uff0c\u8fd9\u4e09\u56db\u5e74\uff0c\u5728\u4ed6\u624b\u91cc\uff0c\u5609\u91cc\u5730\u4ea7\u5df2\u7ecf\u53d1\u5c55\u5f97\u9887\u5177\u89c4\u6a21\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u516b\u516d\u5e74\u5168\u5bb6\u8fc1\u5f80\u9999\u6e2f\uff0c\u4ece\u516b\u516d\u5e74\u8d77\u5c31\u5728\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u7684\u3001\u9999\u6e2f\u7b2c\u4e09\u5927\u5730\u4ea7\u516c\u53f8\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u5de5\u4f5c\uff0c\u5148\u540e\u62c5\u4efb\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u9500\u552e\u526f\u603b\u76d1\u3001\u8d22\u52a1\u526f\u603b\u76d1\u3001\u526f\u603b\u88c1\u3001\u6267\u884c\u603b\u88c1\uff0c\u5434\u542f\u7acb\u65e9\u5e74\u5728\u9999\u6e2f\u505a\u8fc7\u5730\u4ea7\uff0c\u90a3\u4e0d\u8ba4\u8bc6\u5b59\u5c1a\u4e49\u7684\u53ef\u80fd\u6027\u5c31\u975e\u5e38\u7684\u5c0f\u3002\u5f20\u606a\u63d0\u8d77\u5b59\u5c1a\u4e49\uff0c\u5434\u542f\u7acb\u679c\u7136\u8ba4\u5f97\uff0c\u5434\u542f\u7acb\u4e0e\u5b59\u5c1a\u4e49\u79c1\u4ea4\u4e5f\u76f8\u5f53\u4e0d\u9519\uff0c\u53ea\u662f\u8fd9\u65f6\u5434\u542f\u7acb\u5728\u5185\u5730\u53d1\u5c55\u3001\u5b59\u5c1a\u4e49\u7559\u5728\u9999\u6e2f\uff0c\u8054\u7edc\u5c11\u4e86\u3002\u5434\u542f\u7acb\u4ece\u5f20\u606a\u90a3\u91cc\u8981\u6765\u5b59\u5c1a\u4e49\u6b64\u65f6\u7684\u8054\u7cfb\u65b9\u5f0f\uff0c\u5f53\u4e0b\u5c31\u7ed9\u5b59\u5c1a\u4e49\u62e8\u7535\u8bdd\u3002</p></article>"}}}]);
