(window.webpackJsonp=window.webpackJsonp||[]).push([[689],{712:function(p){p.exports={chapterTitle:null,title:"\u7b2c531\u7ae0 \u950b\u8292\u6bd5\u9732",content:"<article><h2>\u7b2c531\u7ae0 \u950b\u8292\u6bd5\u9732</h2><p>\u3000\u3000\u6881\u6587\u6c5f\u770b\u5230\u5c0f\u533a\u95e8\u53e3\u8ddf\u4ed6\u4eec\u8d77\u51b2\u7a81\u7684\u4e24\u4e2a\u4eba\u5c31\u7ad9\u5728\u5f69\u73b2\u5979\u7238\u7238\u8eab\u540e\uff0c\u5fc3\u60ca\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u795e\u8272\u5982\u5e38\uff0c\u4ed6\u624b\u91cc\u53ea\u6709\u63d0\u7740\u4e00\u74f6\u9152\uff0c\u8fa9\u8bc6\u8138\u5f62\uff0c\u4e5f\u77e5\u9053\u524d\u9762\u7a7f\u4e2d\u5c71\u88c5\u7684\u4e2d\u5e74\u4eba\u662f\u738b\u5f69\u73b2\u7684\u7236\u4eb2\u738b\u65ed\u741b\uff0c\u773c\u795e\u90fd\u6ca1\u6709\u779f\u540e\u9762\u4e24\u4eba\u4e00\u773c\uff0c\u606d\u656c\u793c\u8c8c\u7684\u79f0\u547c\u738b\u65ed\u741b\uff1a\u201c\u738b\u4f2f\u4f2f\u597d\u2026\u2026\u201d\u4e56\u987a\u5f97\u771f\u50cf\u8ddf\u7740\u6881\u6587\u6c5f\u4e32\u95e8\u7684\u5c0f\u5c3e\u5df4\u4e00\u6837\uff0c\u5c06\u624b\u91cc\u63d0\u7740\u7684\u4e00\u74f6\u9152\u9012\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u738b\u65ed\u741b\u4e5f\u6ca1\u6709\u4ecb\u610f\u53ea\u6709\u4e00\u74f6\u9152\uff0c\u770b\u5230\u6881\u6587\u6c5f\u521a\u8fdb\u95e8\u65f6\u5fc3\u60ca\u7684\u53cd\u5e94\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u6ca1\u60f3\u5230\u6211\u8fd8\u8bf7\u4e86\u522b\u4eba\uff1f\u201d\u4ed6\u7a0d\u7a0d\u5f80\u65c1\u8fb9\u8ba9\u4e86\u4e00\u4e0b\uff0c\u738b\u5f69\u73b2\u4ece\u978b\u67dc\u91cc\u62ff\u51fa\u62d6\u978b\u7ed9\u6881\u6587\u6c5f\u3001\u5f20\u606a\u6362\uff0c\u8d81\u7740\u5f20\u606a\u3001\u6881\u6587\u6c5f\u6276\u7740\u978b\u67dc\u6362\u62d6\u978b\u4e4b\u9645\uff0c\u4ecb\u7ecd\u4ed6\u8eab\u540e\u7684\u4e24\u4eba\uff0c\u201c\u8fd9\u4f4d\u662f\u90ae\u7535\u90e8\u4e0b\u5c5e\u8054\u4fe1\u516c\u53f8\u7684\u8096\u745e\u6c11\u603b\u7ecf\u7406\uff0c\u4e0e\u6211\u662f\u591a\u5e74\u7684\u597d\u53cb\uff0c\u6587\u6c5f\u4f60\u53c8\u4e0d\u80fd\u559d\u9152\uff0c\u4eca\u5929\u6b63\u597d\u62c9\u4f60\u8096\u53d4\u53d4\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u8fd9\u4f4d\u662f\u8096\u53d4\u53d4\u7684\u52a9\u7406\uff0c\u5468\u5174\u4e1c\uff0c\u539f\u6765\u4e5f\u662f\u90e8\u59d4\u91cc\u7684\u6709\u4e3a\u9752\u5e74\uff0c\u7ed9\u8096\u745e\u6c11\u786c\u62a2\u5230\u8054\u4fe1\u516c\u53f8\u53bb\u4e86\uff0c\u5174\u4e1c\u5e74\u7eaa\u90fd\u6bd4\u4f60\u4eec\u5927\uff0c\u4f60\u4eec\u5c31\u53eb\u4ed6\u5468\u5927\u54e5\u5f97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8054\u4fe1\u516c\u53f8\uff1f\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u8111\u5b50\u91cc\u5bf9\u8054\u4fe1\u516c\u53f8\u6ca1\u6709\u4ec0\u4e48\u5370\u8c61\uff1b\u5f53\u7136\uff0c\u90ae\u7535\u90e8\u4e0b\u5c5e\u7684\u4f01\u4e1a\u591a\u5982\u725b\u6bdb\uff0c\u516c\u53f8\u89c4\u6a21\u6709\u5927\u6709\u5c0f\uff0c\u5f20\u606a\u8981\u90fd\u6709\u5370\u8c61\u624d\u53eb\u6709\u9b3c\u5462\u3002</p><p>\u3000\u3000\u542c\u7740\u738b\u65ed\u741b\u5e2e\u5fd9\u4ecb\u7ecd\uff0c\u5f20\u606a\u4e0d\u52a8\u58f0\u8272\u7684\u70b9\u5934\u62db\u547c\uff0c\u773c\u795e\u9510\u5229\uff0c\u5c06\u8096\u745e\u6c11\u3001\u5468\u5174\u4e1c\u8138\u4e0a\u7684\u7ec6\u5fae\u8868\u60c5\u90fd\u770b\u5728\u773c\u5e95\uff0c\u8096\u745e\u6c11\u56db\u5341\u5c81\u5de6\u53f3\uff0c\u5b9e\u9645\u5e74\u9f84\u53ef\u80fd\u6ca1\u6709\u56db\u5341\u5c81\uff0c\u7ed9\u4eba\u8001\u6210\u6301\u91cd\u7684\u611f\u89c9\uff0c\u773c\u89d2\u6709\u4e9b\u9c7c\u5c3e\u7eb9\uff0c\u4e4d\u770b\u89c1\u5f20\u606a\u4e0e\u6881\u6587\u6c5f\u7ad9\u5728\u95e8\u5916\uff0c\u4e5f\u5c31\u773c\u76ae\u5b50\u655b\u4e86\u655b\uff0c\u8fd8\u4f38\u624b\u8098\u9876\u4e86\u4e00\u4e0b\u4ed6\u8eab\u8fb9\u7684\u5468\u5174\u4e1c\uff0c\u8ba9\u5468\u5174\u4e1c\u6ce8\u610f\u81ea\u5df1\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u597d\u50cf\u521a\u624d\u5728\u5c0f\u533a\u95e8\u53e3\u7684\u51b2\u7a81\u6ca1\u6709\u53d1\u751f\u8fc7\u4e00\u6837\uff0c\u5f20\u606a\u6362\u4e86\u62d6\u978b\u8d70\u8fdb\u5c4b\uff0c\u770b\u5230\u738b\u65ed\u741b\u7684\u6001\u5ea6\uff0c\u4ed6\u5e94\u8be5\u4ece\u4ed6\u5973\u513f\u90a3\u91cc\u77e5\u9053\u4e00\u4e9b\u4e8b\u60c5\uff0c\u8096\u745e\u6c11\u7684\u51fa\u73b0\uff0c\u5f53\u7136\u5c31\u4e0d\u662f\u7eaf\u7cb9\u7684\u51d1\u70ed\u95f9\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u4e4d\u770b\u5230\u8096\u745e\u6c11\u3001\u5468\u5174\u4e1c\u4e24\u4eba\uff0c\u7279\u522b\u662f\u5468\u5174\u4e1c\u521a\u624d\u5728\u5c0f\u533a\u95e8\u53e3\u94bb\u51fa\u8f66\u7a97\u86ee\u6a2a\u7684\u6a21\u6837\u7559\u7ed9\u4ed6\u7684\u5370\u8c61\u592a\u6df1\uff0c\u795e\u8272\u53ef\u6ca1\u8fd9\u4e48\u5bb9\u6613\u5c31\u7f13\u4e0b\u6765\uff0c\u6362\u978b\u7684\u65f6\u5019\u8fd8\u5fcd\u4e0d\u4f4f\u76ef\u7740\u8096\u745e\u6c11\u3001\u5468\u5174\u4e1c\u4e24\u4eba\u770b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u738b\u65ed\u741b\u53cd\u624b\u4eb2\u70ed\u7684\u7275\u7740\u624b\u521a\u8981\u5230\u6c99\u53d1\u524d\u5750\u4e0b\u65f6\uff0c\u624d\u88c5\u4f5c\u604d\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u7684\u6837\u5b50\uff0c\u8ddf\u8096\u745e\u6c11\u8bf4\uff1a\u201c\u697c\u4e0b\u505c\u7684\u90a3\u8f86\u522b\u514b\u662f\u8096\u53d4\u53d4\u7684\u8f66\uff1f\u6211\u4eec\u8fc7\u6765\u65f6\uff0c\u770b\u5230\u8f66\u8eab\u7ed9\u522b\u4eba\u5212\u4e86\u597d\u51e0\u9053\u6746\uff0c\u6709\u4e9b\u60e8\u4e0d\u5fcd\u7779\u2026\u2026\u201d\u5634\u91cc\u53eb\u5f97\u4eb2\u70ed\uff0c\u8fd9\u4e48\u8bf4\u7740\uff0c\u597d\u50cf\u8fd9\u4e8b\u8ddf\u4ed6\u771f\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u662f\u561b\uff1f\u201d\u8096\u745e\u6c11\u6de1\u6de1\u7684\u95ee\u4e86\u4e00\u58f0\uff0c\u8138\u4e0a\u7684\u9634\u6c89\u4e5f\u5c31\u4e00\u63a0\u800c\u8fc7\uff0c\u6ca1\u5c06\u505c\u5728\u697c\u4e0b\u7684\u8f66\u5b50\u5f53\u56de\u4e8b\u3002</p><p>\u3000\u3000\u5374\u662f\u5468\u5174\u4e1c\u5750\u4e0d\u4f4f\uff0c\u7ad9\u8d77\u6765\u8bf4\u8981\u4e0b\u53bb\u770b\u770b\uff0c\u8096\u745e\u6c11\u538b\u7740\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u4e0d\u5c31\u8f66\u5b50\u7ed9\u8fc7\u8def\u4eba\u5212\u51e0\u9053\u5212\u75d5\u5417\uff1f\u660e\u5929\u9001\u8f66\u884c\u4fee\u53bb\u5c31\u53ef\u4ee5\u4e86\u3002\u201d\u6ca1\u8ba9\u5468\u5174\u4e1c\u4e0b\u697c\u53bb\u770b\u8f66\u5b50\u7ed9\u5212\u6210\u4ec0\u4e48\u6837\u3002</p><p>\u3000\u3000\u542c\u8096\u745e\u6c11\u7684\u8bed\u6c14\u6709\u4e9b\u6559\u8bad\u5468\u5174\u4e1c\u7684\u610f\u5473\uff0c\u591a\u5c11\u6709\u4e9b\u813e\u6c14\u7ed9\u6fc0\u51fa\u6765\uff0c\u5f20\u606a\u4e5f\u5c31\u6487\u5634\u4e00\u7b11\uff0c\u8d8a\u53d1\u786e\u5b9a\u8096\u745e\u6c11\u4e5f\u662f\u77e5\u9053\u81ea\u5df1\u8eab\u4efd\u7684\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u4e0e\u8096\u745e\u6c11\u8ddf\u6253\u592a\u6781\u62f3\u4f3c\u7684\u8c01\u4e5f\u4e0d\u5c06\u5c0f\u533a\u95e8\u53e3\u7684\u51b2\u7a81\u5f53\u56de\u4e8b\uff0c\u8096\u745e\u6c11\u751a\u81f3\u80fd\u6309\u637a\u4f4f\u4e0d\u53bb\u7ba1\u4ed6\u4eec\u7684\u8f66\u7ed9\u7cdf\u8e4b\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u6881\u6587\u6c5f\u624d\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u8fd9\u4efd\u9690\u5fcd\u4e4b\u529b\uff0c\u662f\u5e74\u8f7b\u6c14\u76db\u6027\u606a\u5374\u53c8\u6709\u4e9b\u602f\u5f31\u7684\u4ed6\u4e07\u4e07\u90fd\u505a\u4e0d\u5230\u7684\u3002</p><p>\u3000\u3000\u738b\u65ed\u741b\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ea\u89c9\u5f97\u6c14\u6c1b\u6709\u4e9b\u5f02\u5e38\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u4e0b\u53bb\u770b\u770b\u5427\uff0c\u5c0f\u533a\u6cbb\u5b89\u633a\u597d\uff0c\u4e00\u822c\u4e0d\u4f1a\u6709\u4eba\u505a\u8fd9\u7f3a\u5fb7\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u8fd9\u624d\u8ba9\u5468\u5174\u4e1c\u4e0b\u53bb\u770b\u4e00\u770b\uff0c\u4e0d\u7136\u4e5f\u7740\u4e86\u75d5\u8ff9\u3002\u8fd9\u5e74\u5934\uff0c\u4e00\u8f86\u8c6a\u534e\u8f7f\u8f66\u8fd8\u662f\u5f88\u73cd\u8d35\u7684\uff0c\u8f66\u5b50\u5728\u697c\u4e0b\u7ed9\u522b\u4eba\u5212\u4e86\uff0c\u603b\u4e0d\u80fd\u4e0d\u95fb\u4e0d\u95ee\u5427\u3002</p><p>\u3000\u3000\u5468\u5174\u4e1c\u51fa\u95e8\u4e0b\u697c\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u95e8\u7981\u5bf9\u8bb2\u54cd\u4e86\u8d77\u6765\uff0c\u738b\u5f69\u73b2\u5728\u53a8\u623f\u91cc\u5e2e\u5979\u5988\u7684\u5fd9\u4e0d\u60f3\u6d17\u624b\u51fa\u6765\uff0c\u558a\u6881\u6587\u6c5f\u53bb\u5f00\u95e8\u9501\u8ba9\u5468\u5174\u4e1c\u4e0a\u6765\uff0c\u8fd9\u8fb9\u4e0d\u63a7\u5236\uff0c\u5468\u5174\u4e1c\u90fd\u8fdb\u4e0d\u5230\u5165\u6237\u7535\u68af\u3002</p><p>\u3000\u3000\u5f20\u606a\u626d\u56de\u5934\u770b\u89c1\u6302\u7740\u7684\u5bf9\u8bb2\u5668\u4e0a\u6709\u4e00\u5757\u5c0f\u663e\u793a\u5c4f\uff0c\u4e5d\u4e03\u5e74\uff0c\u53ef\u89c6\u95e8\u7981\u7cfb\u7edf\u5012\u5f88\u5c11\u89c1\uff0c\u4e5f\u627e\u4e0d\u5230\u8bdd\u9898\u8ddf\u8096\u745e\u6c11\u5468\u65cb\uff0c\u5f53\u4f5c\u770b\u5230\u597d\u73a9\u7684\u4e1c\u897f\uff0c\u7ad9\u8d77\u6765\u53bb\u770b\u6881\u6587\u6c5f\u600e\u4e48\u64cd\u4f5c\u8fd9\u95e8\u7981\u7cfb\u7edf\u4e86\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u5c06\u5bf9\u8bb2\u8bdd\u7b52\u62ff\u8d77\uff0c\u9ed1\u767d\u663e\u793a\u5c4f\u5c31\u6fc0\u6d3b\u4e86\uff0c\u867d\u7136\u753b\u9762\u4e0d\u662f\u5f88\u6e05\u6670\uff0c\u8fd8\u662f\u80fd\u770b\u89c1\u5468\u5174\u4e1c\u5fae\u5fae\u626d\u66f2\u7684\u8138\u4e0a\u6124\u6012\u7684\u8868\u60c5\uff0c\u624b\u634f\u62f3\u9876\u7740\u5899\u58c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e78\u707e\u4e50\u7978\u7684\u56de\u5934\u8ddf\u8096\u745e\u6c11\u8bf4\uff1a\u201c\u8096\u53d4\u53d4\uff0c\u8f66\u5b50\u60c5\u51b5\u53ef\u80fd\u6709\u4e9b\u4e25\u91cd\u5462\uff0c\u4f60\u770b\u5468\u5927\u54e5\u8fd9\u8868\u60c5\uff0c\u597d\u50cf\u5f88\u607c\u706b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u591a\u5927\u7684\u4e8b\u60c5\uff0c\u8fd8\u65a4\u65a4\u8ba1\u8f83\u2026\u2026\u201d\u8096\u745e\u6c11\u8d70\u8fc7\u6765\uff0c\u6309\u4e86\u4e00\u4e0b\u95e8\u9501\u6309\u94ae\uff0c\u4ece\u753b\u9762\u80fd\u770b\u89c1\u7535\u68af\u95e8\u6253\u5f00\uff0c\u770b\u5230\u5468\u5174\u4e1c\u8d70\u8fdb\u7535\u68af\u7684\u6700\u540e\u753b\u9762\uff0c\u5927\u6982\u662f\u542c\u5230\u5f20\u606a\u4e0e\u8096\u745e\u6c11\u7684\u5bf9\u8bdd\uff0c\u795e\u60c5\u6536\u655b\u4e86\u4e9b\uff0c\u4e0d\u6653\u5f97\u5f20\u606a\u7684\u8eab\u4efd\u4e5f\u7f62\uff0c\u6653\u5f97\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u54ea\u6709\u4ed6\u653e\u8086\u7684\u4f59\u5730\uff1f</p><p>\u3000\u3000\u738b\u65ed\u741b\u89c1\u5f20\u606a\u7ad9\u5728\u95e8\u8fb9\u4e0d\u5750\u56de\u53bb\uff0c\u4ee5\u4e3a\u4ed6\u5bf9\u53ef\u89c6\u95e8\u7981\u7cfb\u7edf\u611f\u5174\u8da3\uff0c\u6709\u5fc3\u66ff\u8096\u745e\u6c11\u5356\u5f04\u4e00\u4e0b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u662f\u8054\u4fe1\u516c\u53f8\u6700\u65b0\u5f00\u53d1\u51fa\u6765\u7684\u79d1\u7814\u6210\u679c\uff0c\u8fd9\u5c0f\u533a\u91cc\uff0c\u5c31\u8fd9\u680b\u516c\u5bd3\u5b89\u88c5\u4e86\u53ef\u89c6\u95e8\u7981\u7cfb\u7edf\uff0c\u6211\u4eec\u8fd9\u680b\u697c\u91cc\u7684\u4f4f\u6237\u554a\uff0c\u662f\u7ed9\u4ed6\u4eec\u5f53\u6210\u8bd5\u9a8c\u54c1\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u90fd\u4e0d\u6653\u5f97\u62ff\u600e\u6837\u8bed\u6c14\u56de\u5e94\u738b\u65ed\u741b\u9887\u4e3a\u81ea\u5f97\u7684\u4ecb\u7ecd\uff0c\u7b11\u7740\u534a\u5f00\u73a9\u7b11\u7684\u95ee\uff0c\u201c\u6ca1\u4eba\u591c\u91cc\u7ed9\u5173\u7535\u68af\u91cc\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8d77\u5bf9\u8bb2\u8bdd\u7b52\uff0c\u770b\u7740\u9ed1\u767d\u5c4f\u793a\u6fc0\u6d3b\u6444\u5165\u7535\u68af\u95e8\u524d\u7684\u666f\u50cf\uff0c\u56fe\u50cf\u8d28\u91cf\u6709\u4e9b\u7c97\u7cd9\u3002\u82e5\u8bf4\u662f\u6279\u91cf\u751f\u4ea7\u7684\u6210\u54c1\uff0c\u5f20\u606a\u8fd8\u6709\u4e9b\u5174\u8da3\uff0c\u53ef\u89c6\u95e8\u7981\u7cfb\u7edf\u4e5d\u4e03\u5e74\uff0c\u5728\u56fd\u5185\u7684\u786e\u5f88\u5c11\u89c1\uff1b\u542c\u738b\u65ed\u741b\u9887\u4e3a\u81ea\u8c6a\u7684\u4ecb\u7ecd\u8bf4\u662f\u8054\u4fe1\u516c\u53f8\u6700\u65b0\u7684\u7814\u53d1\u6210\u679c\uff0c\u4ed6\u5c31\u4e00\u70b9\u5174\u8da3\u90fd\u6ca1\u6709\u4e86\u3002\u7ed9\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4e00\u4e2a\u7814\u7a76\u5ba4\u4e8c\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u8981\u662f\u6574\u4e0d\u51fa\u8fd9\u5957\u53ef\u89c6\u95e8\u7981\u7cfb\u7edf\uff0c\u5f20\u606a\u80fd\u5c06\u90a3\u73ed\u6280\u672f\u4eba\u5458\u90fd\u8d76\u56de\u5bb6\u65b0\u62db\u4e00\u6279\u3002</p><p>\u3000\u3000\u738b\u65ed\u741b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5012\u4e5f\u662f\u6709\u8fd9\u62c5\u5fc3\uff0c\u5e78\u597d\u6ca1\u51fa\u4ec0\u4e48\u72b6\u51b5\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u542c\u51fa\u5f20\u606a\u8bdd\u91cc\u7684\u8f7b\u8511\u8bed\u6c14\uff0c\u4e0d\u9634\u4e0d\u9633\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u6bd4\u8d77\u7231\u8fbe\u7535\u5b50\u4ea7\u54c1\u7684\u6280\u672f\u542b\u91cf\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u80fd\u5fcd\u6c14\u541e\u58f0\uff0c\u5f53\u7136\u662f\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5f20\u606a\u5634\u89d2\u5fae\u659c\u7740\u9732\u51fa\u4e00\u4e2a\u8ba9\u4eba\u770b\u4e86\u5fcd\u4e0d\u4f4f\u6709\u63cd\u4eba\u51b2\u52a8\u7684\u7b11\u5bb9\uff1a\u201c\u8096\u603b\u662f\u8bf4\u6211\u4eec\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u554a\uff1f\u8fd8\u7b97\u6709\u90a3\u4e48\u4e00\u70b9\u6280\u672f\u542b\u91cf\uff0c\u4f46\u4e5f\u5c31\u90a3\u4e48\u4e00\u70b9\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u201d\u6ca1\u7406\u4f1a\u8096\u745e\u6c11\u7684\u8138\u8272\uff0c\u4fa7\u5934\u5bf9\u738b\u65ed\u741b\u8bf4\u9053\uff0c\u201c\u738b\u4f2f\u4f2f\uff0c\u539f\u6765\u4f60\u8ddf\u8096\u603b\u628a\u6211\u7684\u5e95\u7ec6\u90fd\u6478\u900f\u4e86\uff0c\u201d\u542c\u5230\u5468\u5174\u4e1c\u5728\u5916\u9762\u6572\u95e8\uff0c\u5f20\u606a\u8f6c\u5934\u770b\u5411\u8096\u745e\u6c11\u4e00\u773c\uff0c\u4f38\u624b\u5c06\u95e8\u6253\u5f00\uff0c\u534a\u8fb9\u8138\u63a2\u51fa\u53bb\uff0c\u8f6c\u773c\u95f4\u5c31\u53d8\u4e86\u4e00\u4e2a\u8138\u8272\uff0c\u5b9a\u775b\u76ef\u7740\u5468\u5174\u4e1c\uff1a\u201c\u5468\u52a9\u7406\uff0c\u8f66\u5b50\u600e\u4e48\u6837\uff1f\u201d\u8bed\u6c14\u9634\u6c89\u800c\u9510\u5229\u3002</p><p>\u3000\u3000\u4eba\u7684\u786e\u662f\u8bb2\u6c14\u52bf\u7684\uff0c\u5468\u5174\u4e1c\u8ba9\u5f20\u606a\u5b9a\u775b\u770b\u7740\uff0c\u5fc3\u91cc\u5c31\u6709\u4e9b\u53d1\u865a\uff0c\u51fa\u7535\u68af\u65f6\uff0c\u80f8\u81c6\u95f4\u8fd8\u6709\u4e9b\u6012\u706b\uff0c\u9877\u523b\u95f4\u5c31\u50cf\u6c14\u7403\u7ed9\u6233\u7834\u4e00\u6837\uff0c\u6cc4\u4e86\u5e72\u51c0\uff1a\u8096\u745e\u6c11\u8fd9\u4e24\u5929\u5728\u770b\u7684\u7231\u8fbe\u7535\u5b50\u7684\u65b0\u95fb\u8d44\u6599\uff0c\u90fd\u8fd8\u662f\u4ed6\u5e2e\u5fd9\u67e5\u51fa\u6765\u7684\u3002\u4f46\u662f\u77ac\u95f4\u4ed6\u53c8\u4e3a\u81ea\u5df1\u7684\u80c6\u602f\u611f\u5230\u65e0\u6bd4\u7684\u7f9e\u8fb1\uff0c\u6068\u610f\u4ece\u5fc3\u81c6\u610f\u6e17\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7f29\u56de\u5934\u6765\uff0c\u8138\u4e0a\u53c8\u6302\u4e0a\u73a9\u4e16\u4e0d\u606d\u7684\u7b11\u5bb9\uff0c\u522b\u4eba\u90fd\u4e0d\u6653\u5f97\u90a3\u8f6c\u773c\u95f4\u7684\u5de5\u592b\u53d1\u751f\u4ec0\u4e48\uff0c\u4ed6\u662f\u5934\u63a2\u51fa\u95e8\u5916\u8bf4\u8bdd\u7684\uff0c\u95e8\u91cc\u4eba\u4e5f\u542c\u4e0d\u51fa\u4ed6\u7684\u8bed\u6c14\u6709\u4ec0\u4e48\u4e0d\u5bf9\u52b2\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u6765\u90fd\u4e0d\u4ecb\u610f\u8ba9\u4eba\u6068\u7684\uff0c\u53ea\u8981\u8ba9\u8fd9\u4e9b\u6068\u4f60\u7684\u4eba\u540c\u65f6\u5fcc\u60ee\u4f60\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u8981\u662f\u6362\u4f5c\u666e\u901a\u4eba\uff0c\u8dcc\u788e\u4e86\u9152\u74f6\u8fd8\u7ed9\u515c\u5934\u515c\u8138\u7834\u53e3\u9a82\u4e86\u4e00\u901a\uff0c\u4e5f\u53ea\u80fd\u5fcd\u6c14\u541e\u58f0\u3002</p><p>\u3000\u3000\u738b\u65ed\u741b\u4e5f\u62c5\u5fc3\u8f66\u5728\u697c\u4e0b\u51fa\u95ee\u9898\uff0c\u867d\u7136\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u9762\u5b50\u4e0a\u603b\u6302\u4e0d\u4f4f\uff0c\u5173\u5fc3\u7684\u95ee\u9053\uff1a\u201c\u8f66\u5b50\u600e\u4e48\u6837\uff0c\u8981\u4e0d\u8981\u901a\u77e5\u5c0f\u533a\u7684\u8b66\u536b\u5ba4\uff1f\u201d</p><p>\u3000\u3000\u201c\u8f66\u5b50\u95ee\u9898\u4e0d\u5927\u5c31\u7b97\u4e86\uff0c\u4e0d\u8981\u5f71\u54cd\u5927\u5bb6\u7684\u60c5\u7eea\uff0c\u201d\u8096\u745e\u6c11\u6015\u5468\u5174\u4e1c\u4e0d\u8bc6\u5f62\u52bf\uff0c\u62a2\u7740\u62ff\u8bdd\u5835\u4ed6\uff0c\u201c\u80fd\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u5927\u60ca\u5c0f\u602a\u7684\u3002\u201d</p><p>\u3000\u3000\u5468\u5174\u4e1c\u8fd9\u624d\u5fcd\u4f4f\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u8138\u8272\u603b\u4e0d\u662f\u90a3\u4e48\u597d\u770b\u3002\u738b\u65ed\u741b\u662f\u505a\u6280\u672f\u51fa\u8eab\uff0c\u4e0d\u8c19\u4eba\u4e8b\uff0c\u6881\u6587\u6c5f\u3001\u5468\u5174\u4e1c\u4e0d\u5927\u4f1a\u63a9\u9970\uff0c\u4ed6\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4e0e\u8096\u745e\u6c11\u4f1a\u5728\u5c0f\u533a\u95e8\u53e3\u5c31\u8d77\u4e86\u51b2\u7a81\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u522b\u5904\u53bb\uff0c\u62db\u547c\u5927\u5bb6\u5230\u5ba2\u5385\u91cc\u5750\u4e0b\u804a\u5929\uff0c\u8fd8\u60f3\u7740\u8981\u8ba9\u5927\u5bb6\u5c06\u4e0d\u5f00\u5fc3\u7684\u4e8b\u60c5\u5fd8\u6389\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u996d\u51c6\u5907\u597d\u8fd8\u6709\u4e00\u4e9b\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0b\u68cb\u5427\uff0c\u201d\u8096\u745e\u6c11\u7adf\u7136\u8138\u4e0a\u8fd8\u6302\u7740\u7b11\u5bb9\u5bf9\u5f20\u606a\uff0c\u201c\u542c\u8bf4\u4f60\u68cb\u98ce\u9510\u5229\uff0c\u4e5f\u662f\u4e00\u756a\u5c11\u5e74\u5f97\u5fd7\u7684\u6a21\u6837\uff0c\u80fd\u5426\u8ba9\u6211\u9886\u6559\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9053\u542c\u9014\u8bf4\uff0c\u603b\u505a\u4e0d\u4e86\u51c6\uff0c\u201d\u5f20\u606a\u6de1\u7136\u4e00\u7b11\uff0c\u201c\u8096\u603b\u8981\u662f\u627e\u4e0d\u5230\u5bf9\u624b\uff0c\u6211\u5c31\u52c9\u4e3a\u5176\u96be\u4e86\u3002\u201d</p></article>"}}}]);
