(window.webpackJsonp=window.webpackJsonp||[]).push([[69],{91:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e03\u8282 \u518d\u751f\u53d8\u6545",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e03\u8282 \u518d\u751f\u53d8\u6545</h2><p>\u70ed\u8840\u4ece\u8154\u5b50\u91cc\u55b7\u51fa\u6765\uff0c\u5934\u9885\u6eda\u843d\u5728\u5730\uff0c\u8138\u4e0a\u5374\u9732\u51fa\u5b89\u8be6\u795e\u8272\u3002\u6885\u83f2\u65af\u94f6\u5251\u4e0a\u7684\u6668\u66e6\u5723\u5149\u51c0\u5316\u4e86\u7267\u5e08\u7684\u7075\u9b42\uff0c\u8ba9\u4ed6\u6446\u8131\u6076\u9b54\u7684\u63a7\u5236\uff0c\u987a\u5229\u8fdb\u5165\u6668\u66e6\u4e4b\u795e\u7684\u795e\u56fd\uff0c\u907f\u514d\u4e86\u88ab\u5e26\u5165\u6df1\u6e0a\u7684\u5384\u8fd0\u3002\u6076\u9b54\u4f4e\u543c\u7740\uff0c\u63a7\u5236\u7740\u65e0\u5934\u5c38\u4f53\u518d\u6b21\u540e\u9000\uff0c\u4ed6\u4e5f\u88ab\u94f6\u5251\u4e0a\u643a\u5e26\u7684\u5723\u5149\u88ab\u518d\u6b21\u707c\u4f24\u4e86\u3002\u56e0\u4e3a\u4e00\u5f00\u59cb\u4fb5\u5360\u4e86\u7267\u5e08\u7684\u8eab\u4f53\uff0c\u540c\u65f6\u4e5f\u4e60\u60ef\u6027\u5730\u987a\u5e26\u8bfb\u53d6\u4e86\u7267\u5e08\u7684\u90e8\u5206\u8bb0\u5fc6\uff0c\u6240\u4ee5\u6076\u9b54\u6e05\u695a\u77e5\u9053\u8fd9\u4e2a\u7a81\u7136\u88ad\u51fb\u8005\u7684\u8eab\u4efd\u2014\u2014\u4f46\u4ed6\u6ca1\u60f3\u5230\u8fd9\u4e2a\u5973\u5723\u6b66\u58eb\u5c45\u7136\u5982\u6b64\u8fa3\u624b\uff0c\u5bf9\u6614\u65e5\u7684\u6218\u53cb\u6beb\u4e0d\u7559\u60c5\u3002</p><p>\u521a\u624d\u7267\u5e08\u7684\u8eab\u4f53\u5df2\u7ecf\u5f80\u6885\u83f2\u65af\u649e\u8fc7\u6765\uff0c\u53ea\u8981\u5973\u5723\u6b66\u58eb\u7a0d\u7a0d\u72b9\u8c6b\uff0c\u6076\u9b54\u5c31\u53ef\u4ee5\u4e58\u52bf\u53cd\u51fb\uff0c\u4ed6\u8981\u9a71\u9000\u7267\u5e08\u7684\u7075\u9b42\uff0c\u593a\u53d6\u8eab\u4f53\u63a7\u5236\u6743\uff0c\u4e0d\u8fc7\u662f\u8f6c\u5ff5\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u8f7b\u800c\u6613\u4e3e\u3002\u4f46\u6885\u83f2\u65af\u8fde\u534a\u5206\u8fdf\u7591\u90fd\u6ca1\u6709\uff0c\u4e00\u5251\u5c31\u5288\u4e86\u4e0b\u6765\uff0c\u6839\u672c\u6ca1\u6709\u7ed9\u6076\u9b54\u673a\u4f1a\u3002</p><p>\u6df1\u6e0a\u5728\u4e0b\uff0c\u6076\u9b54\u5728\u5fc3\u91cc\u8bc5\u5492\u7740\uff0c\u63d0\u5c14\u90a3\u8001\u6b8b\u5e9f\u4ec0\u4e48\u65f6\u5019\u5c45\u7136\u6559\u51fa\u8fd9\u79cd\u5723\u6b66\u58eb\u6765\uff1f</p><p>\u4f46\u8fd9\u663e\u7136\u4e0d\u662f\u53d1\u7262\u9a9a\u7684\u65f6\u5019\uff0c\u800c\u4e14\u63d0\u5c14\u4e5f\u4e0d\u4f1a\u542c\u4e00\u4e2a\u6076\u9b54\u9886\u4e3b\u62b1\u6028\u3002\u683c\u62c9\u5179\u7279\u6025\u6025\u540e\u9000\uff0c\u6885\u83f2\u65af\u5374\u518d\u5ea6\u903c\u4e0a\u524d\u6765\uff0c\u4f46\u88ab\u4e00\u9053\u65e0\u5f62\u7684\u5899\u58c1\u963b\u6321\u4f4f\u4e86\u3002\u8482\u5a1c\u5c91\u5728\u65c1\u8fb9\u53ca\u65f6\u91ca\u653e\u51fa\u4e00\u4e2a\u9b54\u6cd5\uff0c\u5e2e\u52a9\u4e86\u5979\u7684\u4e3b\u4eba\u3002</p><p>\u5728\u521a\u624d\u7684\u6668\u66e6\u5723\u5149\u8f70\u70b8\u4e2d\uff0c\u8482\u5a1c\u5c91\u4e5f\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\u88ab\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\u7834\u9664\u4e86\u53d8\u5f62\u672f\uff0c\u53c8\u6062\u590d\u5230\u534a\u4eba\u534a\u86c7\u7684\u5f62\u6001\u3002\u6b64\u523b\u5979\u5168\u8eab\u523a\u75db\u7740\uff0c\u53cc\u624b\u865a\u865a\u5f20\u5f00\uff0c\u94fa\u5f00\u4e00\u9762\u65e0\u5f62\u7684\u5899\u58c1\uff0c\u6321\u4f4f\u6885\u83f2\u65af\uff0c\u8981\u4e3a\u6076\u9b54\u4e89\u53d6\u65f6\u95f4\u3002</p><p>\u6885\u83f2\u65af\u4e00\u611f\u89c9\u649e\u4e0a\u963b\u788d\uff0c\u6beb\u4e0d\u8fdf\u7591\uff0c\u7ffb\u8155\u4e00\u5251\u76f4\u5288\u4e0b\u6765\u3002\u7830\u5730\u4e00\u58f0\uff0c\u65e0\u5f62\u7684\u5899\u88ab\u51fb\u6210\u7c89\u788e\uff0c\u94f6\u5251\u900f\u58c1\u800c\u8fc7\uff0c\u780d\u5411\u6b63\u5728\u7ef4\u6301\u6cd5\u672f\u7684\u8482\u5a1c\u82a9\u3002\u5973\u4eba\u9762\u72ee\u9996\u9886\u614c\u5fd9\u4e4b\u95f4\u62ac\u8d77\u5de6\u81c2\u4e00\u6321\uff0c\u5979\u7684\u5de6\u624b\u624b\u8155\u4e0a\u5e26\u7740\u4e00\u53ea\u94f6\u8272\u624b\u956f\uff0c\u662f\u5979\u81ea\u5df1\u5236\u6210\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u80fd\u5f62\u6210\u4e00\u5708\u9632\u5fa1\u529b\u573a\uff0c\u62b5\u6321\u5200\u5251\u653b\u51fb\u3002\u5979\u5bf9\u81ea\u5df1\u7684\u5236\u9020\u6210\u679c\u4e00\u5411\u5f88\u6709\u4fe1\u5fc3\uff0c\u4f46\u8fd9\u6b21\u4f3c\u4e4e\u906d\u9047\u4e86\u610f\u5916\u3002</p><p>\u94f6\u5251\u4e00\u6325\u800c\u4e0b\uff0c\u649e\u4e0a\u624b\u956f\uff0c\u53ea\u662f\u7a0d\u7a0d\u4e00\u7a92\uff0c\u968f\u5373\u52a0\u901f\u5288\u4e0b\u3002\u8482\u5a1c\u82a9\u60e8\u53eb\u7740\uff0c\u86c7\u8eab\u873f\u8712\u540e\u9000\uff0c\u5979\u7684\u5de6\u624b\u9f50\u8155\u800c\u65ad\uff0c\u9c9c\u8840\u55b7\u6d8c\uff0c\u90a3\u53ea\u94f6\u8272\u624b\u956f\u4e5f\u88ab\u5288\u6210\u4e86\u4e24\u622a\uff0c\u6389\u843d\u5728\u5730\u3002</p><p>\u8fd9\u4e00\u7167\u9762\u4ea4\u624b\u8bf4\u6765\u867d\u957f\uff0c\u5176\u5b9e\u53ea\u662f\u7535\u5149\u706b\u77f3\u4e4b\u95f4\u3002\u6885\u83f2\u65af\u649e\u7834\u529b\u573a\u5899\uff0c\u4e00\u5251\u5288\u65ad\u8482\u5a1c\u82a9\u7684\u624b\u8155\uff0c\u5c06\u5979\u8feb\u5f00\uff0c\u968f\u5373\u53c8\u671d\u6076\u9b54\u51b2\u6765\u3002</p><p>\u5df2\u7ecf\u8fdf\u4e86\u3002</p><p>\u6076\u9b54\u7ec8\u4e8e\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u4ed6\u5ff5\u51fa\u4e00\u4e2a\u5f3a\u6709\u529b\u7684\u5355\u5b57\uff0c\u90aa\u6076\u7684\u529b\u91cf\u4ece\u4ed6\u7684\u638c\u5fc3\u55b7\u8584\u800c\u51fa\uff0c\u5c06\u5973\u5723\u6b66\u58eb\u7684\u8eab\u4f53\u6b7b\u6b7b\u5b9a\u4f4f\u3002</p><p>\u4f46\u4e8b\u60c5\u5df2\u7ecf\u53d8\u5f97\u9ebb\u70e6\u4e86\u3002</p><p>\u7267\u5e08\u4e00\u6b7b\uff0c\u6076\u9b54\u867d\u7136\u4f9d\u65e7\u80fd\u5360\u636e\u8fd9\u5177\u8eaf\u4f53\uff0c\u4f46\u5df2\u7ecf\u6beb\u65e0\u610f\u4e49\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u968f\u7740\u7075\u9b42\u88ab\u6668\u66e6\u4e4b\u795e\u63a5\u8d70\uff0c\u7267\u5e08\u8eab\u4f53\u7684\u7edd\u7f18\u4f5c\u7528\u5df2\u7ecf\u5b8c\u5168\u4e27\u5931\u3002\u9ece\u660e\u4e4b\u77f3\u4f9d\u65e7\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u6563\u53d1\u7740\u5fae\u5fae\u7684\u73ab\u7470\u8272\u5149\u8292\uff0c\u4f3c\u4e4e\u6709\u4e9b\u9eef\u6de1\uff0c\u4f46\u6076\u9b54\u4e0d\u6562\u76f4\u63a5\u78b0\u89e6\u3002</p><p>\u8be5\u6b7b\uff0c\u5f97\u53e6\u5916\u518d\u627e\u4e2a\u5408\u9002\u7684\u8eab\u4f53\u6765\u3002</p><p>\u8fd9\u91cc\u76ee\u524d\u8fd8\u6d3b\u7740\u7684\uff0c\u9664\u4e86\u4eba\u9762\u72ee\uff0c\u5c31\u662f\u88ab\u6076\u9b54\u5b9a\u4f4f\u7684\u6885\u83f2\u65af\u3002\u4eba\u9762\u72ee\u662f\u90aa\u6076\u602a\u7269\uff0c\u81ea\u7136\u538b\u6839\u4e0d\u9002\u5408\uff0c\u90a3\u4e48\uff0c\u53ea\u6709\u4e00\u4e2a\u9009\u62e9\u2026\u2026</p><p>\u900f\u8fc7\u7267\u5e08\u7684\u65e0\u5934\u5c38\u4f53\uff0c\u6076\u9b54\u5c06\u773c\u5149\u843d\u5728\u5973\u5723\u6b66\u58eb\u7684\u8138\u4e0a\u3002\u867d\u7136\u6885\u83f2\u65af\u7684\u886c\u886b\u4e0a\u6ca1\u6709\u4efb\u4f55\u5fbd\u7ae0\u6807\u8bb0\uff0c\u4f46\u6076\u9b54\u8bfb\u53d6\u4e86\u7267\u5e08\u7684\u90e8\u5206\u8bb0\u5fc6\uff0c\u6240\u4ee5\u4ed6\u5f88\u6e05\u695a\u8fd9\u4e2a\u5c11\u5973\u7684\u8eab\u4efd\u3002</p><p>\u6885\u83f2\u65af\u4e0d\u662f\u5170\u68ee\u5fb7\u5c14\u7684\u4fe1\u5f92\uff0c\u4f46\u5979\u662f\u63d0\u5c14\u7684\u5723\u6b66\u58eb\u3002\u540c\u4e3a\u5584\u795e\uff0c\u63d0\u5c14\u548c\u5170\u68ee\u5fb7\u5c14\u5173\u7cfb\u878d\u6d3d\uff0c\u4ed6\u4eec\u7684\u6559\u4f1a\u4e5f\u5f80\u5f80\u7ed3\u6210\u540c\u76df\uff0c\u76f8\u4e92\u534f\u52a9\u3002\u4fbf\u5982\u8fd9\u6b21\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u53bb\u535a\u5f97\u4e4b\u95e8\uff0c\u4fbf\u662f\u4e24\u4e2a\u6559\u4f1a\u5408\u4f5c\uff0c\u90fd\u6d3e\u51fa\u4e86\u4e24\u4f4d\u9ad8\u9636\u6210\u5458\u3002</p><p>\u552f\u6709\u5170\u68ee\u5fb7\u5c14\u7684\u9ad8\u9636\u7267\u5e08\u624d\u80fd\u6fc0\u53d1\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\uff0c\u7528\u4ee5\u9a71\u6563\u761f\u75ab\u3002\u6885\u83f2\u65af\u6ca1\u6709\u8fd9\u79cd\u80fd\u529b\uff0c\u4f46\u4f5c\u4e3a\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u5979\u81f3\u5c11\u4e5f\u662f\u53ef\u4ee5\u53bb\u62ff\u9ece\u660e\u4e4b\u77f3\u7684\u3002</p><p>\u6076\u9b54\u4f4e\u6c89\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u7684\u9ed1\u8272\u8eab\u5f71\u4ece\u7267\u5e08\u7684\u8eaf\u4f53\u4e2d\u5347\u8d77\uff0c\u817e\u5728\u7a7a\u4e2d\u3002\u4e27\u5931\u4e86\u6076\u9b54\u529b\u91cf\u7684\u652f\u6491\uff0c\u5df2\u7ecf\u6b7b\u4ea1\u7684\u7267\u5e08\u8eab\u4f53\u5f80\u540e\u8f6f\u8f6f\u5012\u4e0b\u3002</p><p>\u4fb5\u5360\u5973\u5b50\u7684\u8eab\u4f53\uff0c\u5bf9\u4e8e\u683c\u62c9\u5179\u7279\u6765\u8bf4\u4e5f\u4e0d\u662f\u4ec0\u4e48\u65b0\u9c9c\u7ecf\u9a8c\uff0c\u4ed6\u751a\u81f3\u591a\u6b21\u53d8\u5316\u6210\u5973\u5b50\u53bb\u52fe\u5f15\u6df1\u6e0a\u4e2d\u7684\u5176\u4ed6\u6076\u9b54\u3002\u4ed6\u7684\u8eab\u5f71\u7f13\u7f13\u964d\u4f4e\uff0c\u9760\u8fd1\u5973\u5723\u6b66\u58eb\uff0c\u521a\u624d\u5728\u548c\u6885\u83f2\u65af\u7684\u77ed\u6682\u4ea4\u624b\u4e2d\uff0c\u4ed6\u5df2\u7ecf\u53d1\u89c9\u5bf9\u65b9\u7684\u529b\u91cf\u5f3a\u5927\u8d85\u4e4e\u5bfb\u5e38\uff0c\u4e0d\u8fc7\uff0c\u51e1\u4eba\u7ec8\u7a76\u4e5f\u53ea\u662f\u51e1\u4eba\u3002</p><p>\u552f\u4e00\u4ee4\u4ed6\u4e0d\u5feb\u7684\u662f\uff0c\u8482\u5a1c\u5c91\u5c45\u7136\u4f1a\u8ba9\u4e00\u4e2a\u63d0\u5c14\u7684\u5973\u5723\u6b66\u58eb\u6df7\u8fdb\u6765\uff0c\u8fd9\u4e5f\u672a\u514d\u592a\u4e0d\u8c28\u614e\u4e86\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e5f\u6709\u4e9b\u5947\u602a\uff0c\u56e0\u4e3a\u6309\u9053\u7406\u8bf4\uff0c\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\u662f\u53ef\u4ee5\u770b\u7834\u4efb\u4f55\u53d8\u5f62\u9b54\u6cd5\u7684\u3002\u6885\u83f2\u65af\u4e00\u76f4\u5728\u796d\u575b\u4e0b\uff0c\u5979\u53c8\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u867d\u7136\u4f2a\u88c5\u6210\u4eba\u9762\u72ee\uff0c\u81ea\u5df1\u4e5f\u7406\u5e94\u65e9\u5c31\u5bdf\u89c9\u5230\u90a3\u4ee4\u4eba\u538c\u6076\u7684\u795e\u5723\u6c14\u606f\u624d\u5bf9\u554a\u3002</p><p>\u662f\u5979\u8eab\u4e0a\u6709\u4ec0\u4e48\u529b\u91cf\u5f3a\u5927\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u963b\u65ad\u4e86\u81ea\u5df1\u7684\u610f\u8bc6\u63a2\u67e5\uff1f</p><p>\u6076\u9b54\u5fae\u5fae\u76b1\u7740\u7709\u5934\uff0c\u4ed4\u7ec6\u5730\u5c06\u5973\u5723\u6b66\u58eb\u7684\u5168\u8eab\u4e0a\u4e0b\u6253\u91cf\u4e86\u4e00\u904d\u3002\u4ed6\u7684\u76ee\u5149\u8db3\u4ee5\u6e05\u695a\u5730\u7a7f\u900f\u6885\u83f2\u65af\u8eab\u4e0a\u7684\u886c\u886b\uff0c\u770b\u5230\u8eab\u4f53\u6bcf\u4e00\u5bf8\u808c\u80a4\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u53ef\u7591\u7684\u7269\u54c1\u3002\u5973\u5723\u6b66\u58eb\u7684\u53f3\u624b\u65e0\u540d\u6307\u4e0a\u6234\u7740\u4e00\u679a\u975e\u5e38\u7cbe\u81f4\u5c0f\u5de7\u7684\u6212\u6307\uff0c\u5018\u82e5\u4e0d\u7559\u5fc3\u6839\u672c\u4e0d\u4f1a\u53d1\u89c9\uff0c\u4f46\u6076\u9b54\u7b80\u5355\u5730\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u77e5\u9053\u90a3\u4e0d\u8fc7\u662f\u4e00\u679a\u5f88\u5bfb\u5e38\u7684\u6212\u6307\uff0c\u867d\u7136\u505a\u5de5\u4e0d\u9519\uff0c\u4f46\u5e76\u6ca1\u6709\u9644\u7740\u4efb\u4f55\u9b54\u6cd5\u3002</p><p>\u5947\u602a\u554a\u3002</p><p>\u6076\u9b54\u5fc3\u4e2d\u9690\u7ea6\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u4e00\u65f6\u4e5f\u6349\u6478\u4e0d\u900f\u3002\u4ed6\u5df2\u7ecf\u8d34\u8fd1\u6885\u83f2\u65af\uff0c\u8bd5\u56fe\u4fb5\u5360\u5979\u7684\u8eab\u4f53\uff0c\u4f46\u5374\u88ab\u67d0\u79cd\u529b\u91cf\u963b\u62e6\u4f4f\u4e86\u3002\u6076\u9b54\u5bf9\u6b64\u5e76\u4e0d\u611f\u5230\u610f\u5916\uff0c\u80fd\u591f\u6210\u4e3a\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u81ea\u7136\u662f\u610f\u5fd7\u5fc3\u667a\u6781\u5176\u575a\u6bc5\u7684\u4eba\u7269\uff0c\u63d0\u5c14\u7684\u795e\u529b\u4e5f\u4f1a\u5b88\u62a4\u7740\u5979\uff0c\u4e0d\u4f1a\u90a3\u4e48\u5bb9\u6613\u88ab\u90aa\u9b54\u9644\u4f53\u3002\u4f46\u5f53\u4ed6\u5c1d\u8bd5\u51e0\u6b21\u4e4b\u540e\uff0c\u5f00\u59cb\u9690\u9690\u5bdf\u89c9\u5230\u8b66\u5146\u3002</p><p>\u4e0d\u5bf9\uff0c\u8fd9\u662f\u2026\u2026\u795e\u7947\u7684\u529b\u91cf\uff1f</p><p>\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u81ea\u7136\u4f1a\u6709\u63d0\u5c14\u795e\u529b\u7684\u5b88\u62a4\uff0c\u8fd9\u5728\u6076\u9b54\u7684\u610f\u6599\u4e4b\u4e2d\u3002\u4f46\u4e0e\u6b64\u540c\u65f6\uff0c\u4ed6\u8fd8\u611f\u89c9\u5230\u4e86\u53e6\u5916\u4e00\u79cd\u529b\u91cf\u2014\u2014\u5b8c\u5168\u4e0d\u540c\u4e8e\u63d0\u5c14\u7684\u795e\u529b\uff0c\u611f\u89c9\u4e0a\u53cd\u5012\u6709\u4e9b\u8fd1\u4f3c\u6076\u9b54\u81ea\u5df1\u3002\u8fd9\u8ba9\u4ed6\u6014\u4e86\u4e00\u4e0b\uff0c\u7b80\u76f4\u8981\u6000\u7591\u81ea\u5df1\u7684\u610f\u8bc6\u51fa\u4e86\u95ee\u9898\u3002</p><p>\u6076\u9b54\u6025\u901f\u5728\u8111\u6d77\u4e2d\u641c\u5bfb\u7740\uff0c\u56de\u5fc6\u7740\u5343\u767e\u5e74\u6765\u7d2f\u79ef\u548c\u638c\u63e1\u7684\u76f8\u5173\u77e5\u8bc6\uff0c\u6700\u540e\u4ed6\u5f97\u51fa\u4e86\u4e00\u4e2a\u4ee4\u4ed6\u5174\u594b\u5f02\u5e38\u7684\u7ed3\u8bba\u3002\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u683c\u62c9\u5179\u7279\u4f4e\u58f0\u81ea\u8bed\uff0c\u201c\u597d\u6781\u4e86\uff0c\u8fd9\u6837\u4e00\u6765\u2026\u2026\u201d</p><p>\u6b63\u5f53\u4ed6\u4e3a\u81ea\u5df1\u7684\u610f\u5916\u53d1\u73b0\u9690\u9690\u5fc3\u52a8\u65f6\uff0c\u4e00\u6761\u4eba\u5f71\u9661\u5730\u4ece\u796d\u575b\u65c1\u8fb9\u7684\u5730\u4e0b\u7834\u571f\u800c\u51fa\uff0c\u8dc3\u4e86\u51fa\u6765\uff0c\u4f38\u624b\u5c31\u53bb\u6293\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u9ece\u660e\u4e4b\u77f3\u3002\u8fd9\u4eba\u7684\u52a8\u4f5c\u5feb\u5f97\u51fa\u5947\uff0c\u8fde\u6076\u9b54\u548c\u8482\u5a1c\u82a9\u90fd\u6ca1\u77ac\u95f4\u53cd\u5e94\u8fc7\u6765\uff0c\u6885\u83f2\u65af\u6b63\u597d\u9762\u5bf9\u7740\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5df2\u7ecf\u5927\u534a\u5929\u4e0d\u89c1\u8e2a\u5f71\u7684\u743c\u6069\u3002</p></article>"}}}]);
