(window.webpackJsonp=window.webpackJsonp||[]).push([[69],{92:function(p){p.exports={chapterTitle:null,title:"\u7b2c106\u7ae0 \u4e25\u91cd\u7684\u540e\u679c",content:"<article><h2>\u7b2c106\u7ae0 \u4e25\u91cd\u7684\u540e\u679c</h2><p>\u3000\u3000\u675c\u5c0f\u5c71\u8d76\u6765\u65f6\uff0c\u8138\u8272\u94c1\u9752\uff0c\u5634\u5507\u54ac\u5f97\u53d1\u767d\uff0c\u9ed1\u7626\u7684\u8138\u7ef7\u5f97\u7d27\u7d27\u7684\u3002\u4ed6\u63a5\u5230\u7535\u8bdd\u65f6\uff0c\u897f\u57ce\u533a\u5e38\u59d4\u6210\u5458\u6b63\u5728\u53ec\u5f00\u4f1a\u8bae\uff0c\u8ba8\u8bba\u6700\u8fd1\u70ed\u8bae\u7684\u8bdd\u9898\uff1a\u8c03\u6574\u57ce\u5efa\u89c4\u5212\u601d\u8def\u3002</p><p>\u3000\u3000\u8c03\u6574\u57ce\u5efa\u89c4\u5212\u7684\u601d\u8def\uff0c\u867d\u7136\u5728\u6c11\u95f4\u6709\u8f83\u591a\u7684\u8d5e\u540c\u58f0\u97f3\uff0c\u4f46\u4e0d\u7b26\u5408\u666e\u901a\u653f\u5e9c\u5b98\u5458\u5bf9\u57ce\u5e02\u5efa\u8bbe\u7684\u8ba4\u8bc6\uff0c\u5728\u5e02\u91cc\u4e89\u8bae\u5f88\u5927\u3002</p><p>\u3000\u3000\u867d\u7136\u672a\u5fc5\u8ba4\u540c\u8bb8\u9e3f\u4f2f\u6587\u7ae0\u63d0\u51fa\u7684\u89c2\u70b9\uff0c\u4f46\u662f\u57ce\u5357\u533a\u5df2\u7ecf\u6709\u6240\u52a8\u4f5c\uff0c\u897f\u57ce\u533a\u4e5f\u4e0d\u80fd\u89c6\u82e5\u65e0\u7779\u3002</p><p>\u3000\u3000\u57ce\u5357\u533a\u653f\u5e9c\u5e76\u6ca1\u6709\u575a\u51b3\u7684\u53cd\u5bf9\u539f\u6709\u7684\u57ce\u5efa\u89c4\u5212\u601d\u8def\uff0c\u4f46\u662f\u5728\u5b9e\u9645\u884c\u52a8\u4e2d\uff0c\u5374\u62e5\u62a4\u8bb8\u9e3f\u4f2f\u63d0\u51fa\u7684\u5851\u9020\u6d77\u5dde\u7279\u8272\u7684\u601d\u8def\uff0c\u65b0\u5149\u9020\u7eb8\u5382\u8001\u5382\u6539\u5efa\u9910\u996e\u4f11\u95f2\u5e7f\u573a\u4e0e\u56db\u51e4\u6865\u5468\u8fb9\u73af\u5883\u6574\u6cbb\u7ed3\u5408\u8d77\u6765\uff0c\u6210\u4e3a\u5f53\u524d\u6bd4\u8f83\u70ed\u95e8\u7684\u4e00\u4e2a\u8bdd\u9898\uff0c\u5e76\u9884\u8ba1\u53d6\u5f97\u826f\u597d\u7684\u7ecf\u6d4e\u6548\u679c\uff0c\u8001\u5382\u6539\u5236\uff0c\u5382\u5730\u51fa\u79df\u6bcf\u5e74\u5c06\u83b7\u53d6\u79df\u91d1\u4e09\u767e\u591a\u4e07\uff0c\u7e41\u8363\u57ce\u5357\u533a\u7684\u9910\u996e\u4e1a\uff0c\u4fc3\u8fdb\u5c31\u4e1a\uff1b\u8fd8\u5c06\u53d6\u5f97\u826f\u597d\u7684\u793e\u4f1a\u6548\u679c\uff0c\u56db\u51e4\u6865\u73af\u5883\u6574\u6cbb\uff0c\u65b0\u5efa\u5e02\u6c11\u4f11\u95f2\u7eff\u5730\uff0c\u65b0\u589e\u5e02\u6c11\u996e\u98df\u4f11\u95f2\u573a\u6240\u3002</p><p>\u3000\u3000\u5927\u6982\u662f\u57ce\u5357\u533a\u653f\u5e9c\u4eca\u5e74\u6700\u51fa\u5f69\u7684\u4e00\u9879\u653f\u7ee9\uff0c\u8fd8\u987a\u5e26\u5c06\u65b0\u5149\u9020\u7eb8\u5382\u8fd1\u4e00\u5e74\u6765\u95f9\u7684\u662f\u975e\u906e\u63a9\u53bb\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u8fd9\u65f6\u5019\u5f53\u7136\u80fd\u77e5\u9053\u8bb8\u9e3f\u4f2f\u6587\u7ae0\u91cc\u7684\u601d\u8def\u5b9e\u9645\u4e0a\u5c31\u662f\u5510\u5b66\u8c26\u4e0e\u5468\u5bcc\u660e\u7684\u601d\u8def\uff0c\u6ca1\u5fc5\u8981\u6025\u7740\u53cd\u5bf9\u539f\u6765\u7684\u57ce\u5efa\u89c4\u5212\u601d\u8def\uff0c\u4f46\u662f\u5b66\u57ce\u5357\u533a\u7684\u4f5c\u6cd5\uff0c\u5728\u5b9e\u9645\u884c\u52a8\u4e2d\uff0c\u7b56\u5e94\u4e00\u4e0b\u5510\u5b66\u8c26\u4e0e\u5468\u5bcc\u660e\uff0c\u624d\u662f\u4e24\u8fb9\u90fd\u4e0d\u5f97\u7f6a\u7684\u4e0a\u4e0a\u4e4b\u7b56\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0b\u5348\u7ec4\u533a\u59d4\u6210\u5458\u5f00\u4e86\u534a\u5929\u7684\u4f1a\uff0c\u8fd8\u6ca1\u6709\u8ba8\u8bba\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u8fc7\u4e86\u4e0b\u73ed\u65f6\u95f4\uff0c\u4f1a\u8bae\u8fd8\u50cf\u4e00\u6f6d\u6b7b\u6c34\u90a3\u6837\u62d6\u7740\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u77e5\u9053\u513f\u5b50\u675c\u98de\u51fa\u4e86\u4e8b\uff0c\u5f53\u5373\u7ec8\u6b62\u53ef\u80fd\u4f1a\u62d6\u5f88\u4e45\u7684\u4f1a\u8bae\uff0c\u5e26\u7740\u79d8\u4e66\u5c31\u5f80\u4e00\u4e2d\u8d76\u3002</p><p>\u3000\u3000\u5728\u675c\u5c0f\u5c71\u8d76\u5f80\u4e00\u4e2d\u7684\u9014\u4e2d\uff0c\u5f20\u606a\u5728\u7535\u8bdd\u5c06\u4e8b\u60c5\u7684\u7ecf\u8fc7\u5927\u6982\u8ddf\u4ed6\u8bb2\u4e86\u4e00\u904d\u3002\u675c\u5c0f\u5c71\u5728\u6d77\u5dde\u4e0d\u7b97\u80cc\u666f\u6df1\u539a\uff0c\u4f46\u4ed6\u5750\u5728\u897f\u57ce\u533a\u59d4\u4e66\u8bb0\u7684\u4f4d\u7f6e\uff0c\u81ea\u7136\u6709\u4ed6\u7684\u8fc7\u4eba\u4e4b\u5904\uff0c\u4e8b\u60c5\u80fd\u5982\u4f55\u89e3\u51b3\u597d\uff0c\u5173\u952e\u8981\u770b\u675c\u5c0f\u5c71\u7684\u624b\u8155\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u5c06\u9690\u7792\u4ec0\u4e48\u7ec6\u8282\uff0c\u5f53\u7136\u8981\u5c06\u81ea\u5df1\u8bf4\u6210\u78b0\u5de7\u9047\u4e0a\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u6551\u62a4\u8f66\u5df2\u7ecf\u5c06\u66f9\u5149\u660e\u63a5\u8d70\uff0c\u675c\u5c0f\u5c71\u76f4\u63a5\u8ba9\u79d8\u4e66\u53bb\u533b\u9662\uff0c\u8981\u4ed6\u5c3d\u4e00\u5207\u624b\u6bb5\u4e0d\u8ba9\u66f9\u5149\u660e\u53d1\u751f\u610f\u5916\u3002\u66f9\u5149\u660e\u8981\u662f\u6b7b\u7fd8\u7fd8\uff0c\u675c\u5c0f\u5c71\u80fd\u8010\u518d\u5927\uff0c\u4e5f\u4e0d\u80fd\u5c06\u4e8b\u60c5\u5168\u515c\u4e0b\u6765\uff1b\u8fd8\u6709\u4e00\u70b9\u5c31\u662f\u4e0d\u80fd\u8ba9\u5916\u4eba\u63a5\u89e6\u66f9\u5149\u660e\uff0c\u62a2\u6551\u5f52\u62a2\u6551\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8ba9\u66f9\u5149\u660e\u9192\u8fc7\u6765\u80e1\u8bf4\u516b\u9053\u3002\u8eab\u4e3a\u4e00\u4e2d\u653f\u6559\u5904\u4e3b\u4efb\u7684\u66f9\u5149\u660e\u5c5e\u4e8e\u5e02\u6559\u80b2\u5c40\u884c\u653f\u7f16\u5236\uff0c\u662f\u526f\u79d1\u7ea7\u56fd\u5bb6\u5e72\u90e8\uff0c\u4e0d\u662f\u6beb\u65e0\u80fd\u8010\u7684\u5e73\u6c11\u767e\u59d3\uff0c\u7ed9\u5b66\u751f\u6253\u5f97\u8fd9\u4e48\u60e8\uff0c\u96be\u9053\u5e02\u6559\u80b2\u5c40\u7684\u5b98\u50da\u5c31\u6ca1\u6709\u5154\u6b7b\u72d0\u60b2\u7684\u611f\u89c9\uff1f\u5e02\u6559\u80b2\u5c40\u5c31\u6ca1\u6709\u4e00\u70b9\u813e\u6c14\uff1f\u4e8b\u60c5\u95f9\u51fa\u6765\uff0c\u8eab\u4e3a\u897f\u57ce\u533a\u59d4\u4e66\u8bb0\u7684\u675c\u5c0f\u5c71\u4e00\u6837\u65e0\u6cd5\u6536\u62fe\uff0c\u6240\u4ee5\u8981\u5148\u63a7\u5236\u5c40\u52bf\u3002</p><p>\u3000\u3000\u675c\u98de\u8fd9\u65f6\u5019\u624d\u77e5\u9053\u540e\u6015\uff0c\u8111\u5b50\u6709\u4e9b\u6df7\u4e71\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u4ee5\u4e3a\u675c\u5c0f\u5c71\u4f1a\u4e0b\u8f66\u7ed9\u675c\u98de\u4e00\u811a\uff0c\u6ca1\u60f3\u5230\u675c\u5c0f\u5c71\u53ea\u80fd\u6253\u5f00\u8f66\u95e8\uff0c\u5f88\u6c89\u9ed8\u7684\u773c\u795e\u8ba9\u4ed6\u8ddf\u675c\u98de\u5148\u4e0a\u8f66\u518d\u8bf4\uff0c\u8f66\u5b50\u62d0\u5230\u5df7\u5b50\u53e3\uff0c\u505c\u5728\u8def\u706f\u7167\u4e0d\u89c1\u7684\u9ed1\u6697\u4e2d\uff0c\u675c\u5c0f\u5c71\u8ba9\u53f8\u673a\u53bb\u5b66\u6821\u627e\u738b\u708e\u658c\u8ddf\u674e\u829d\u82b3\u3002</p><p>\u3000\u3000\u4ed6\u6765\u65f6\u6ca1\u6709\u6362\u8f66\uff0c\u8bf4\u4e0d\u5b9a\u5b66\u6821\u6709\u8001\u5e08\u8ba4\u5f97\u4ed6\u7684\u8f66\uff0c\u4f1a\u7ed9\u522b\u4eba\u592a\u591a\u8054\u60f3\u7684\u7a7a\u95f4\uff0c\u53ea\u8981\u5c06\u66f9\u5149\u660e\u63a7\u5236\u8d77\u6765\uff0c\u5c06\u738b\u708e\u658c\u4e0e\u674e\u829d\u82b3\u4e24\u4e2a\u5f53\u4e8b\u4eba\u5524\u8fc7\u6765\u5546\u8bae\u5bf9\u7b56\uff0c\u624d\u662f\u6700\u7a33\u59a5\u7684\u65b9\u6cd5\u3002</p><p>\u3000\u3000\u53f8\u673a\u8d70\u540e\uff0c\u675c\u5c0f\u638f\u51fa\u70df\u6765\u62bd\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u5c06\u70df\u76d2\u9012\u5230\u5f20\u606a\u9762\u524d\uff1a\u201c\u4f60\u62bd\u4e0d\u62bd\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c34\u5c2c\u7684\u6447\u4e86\u6447\u5934\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4e8b\uff0c\u4f60\u5c0f\u5b50\u4e0d\u7b80\u5355\uff0c\u4e0d\u8981\u5728\u53d4\u53d4\u9762\u524d\u85cf\u7740\u6396\u7740\u4e86\uff0c\u7537\u4eba\u62bd\u70df\u5f88\u6b63\u5e38\u7684\u3002\u201d\u675c\u5c0f\u5c71\u7684\u773c\u775b\u5f88\u4eae\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u51b7\u9759\u5904\u7f6e\uff0c\u8fd9\u4e8b\u4e0d\u6653\u5f97\u4f1a\u95f9\u6210\u4ec0\u4e48\u6837\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4f38\u624b\u63a5\u8fc7\u4e00\u652f\u70df\uff1b\u675c\u98de\u95f7\u58f0\u95f7\u6c14\u4e5f\u4f38\u8fc7\u6765\u624b\u6765\u62ff\u70df\uff0c\u675c\u5c0f\u5c71\u51fa\u5947\u7684\u6ca1\u6709\u963b\u6b62\uff0c\u53ea\u662f\u53f9\u6c14\u7684\u8bf4\uff1a\u201c\u4f60\u5c31\u4e0d\u80fd\u7ed9\u4f60\u8001\u5b50\u7701\u5fc3\uff01\u201d</p><p>\u3000\u3000\u675c\u98de\u95f7\u7740\u58f0\u4e0d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u89c9\u5f97\u5947\u602a\uff0c\u8981\u662f\u4ee5\u524d\u7684\u81ea\u5df1\u95ef\u8fd9\u4e48\u5927\u7684\u7978\uff0c\u65e9\u4e0d\u77e5\u9053\u8981\u7ed9\u7238\u7238\u8bad\u6210\u4ec0\u4e48\u6837\u5b50\uff1b\u675c\u5c0f\u5c71\u4e5f\u4e0d\u662f\u90a3\u79cd\u7eb5\u5bb9\u5b50\u5973\u7684\u4eba\uff0c\u4e0d\u7136\u675c\u98de\u65e9\u5c31\u517b\u6210\u98de\u626c\u8dcb\u6248\u7684\u6027\u683c\u4e86\u3002</p><p>\u3000\u3000\u4e09\u4e2a\u4eba\u5728\u72ed\u7a84\u7684\u8f66\u53a2\u91cc\u5438\u7740\u70df\uff0c\u4e5f\u6ca1\u4eba\u63d0\u5c06\u8f66\u7a97\u6253\u5f00\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u80fd\u542c\u89c1\u96ea\u82b1\u843d\u5728\u8f66\u9876\u4e0a\u7684\u58f0\u97f3\uff0c\u6253\u5f00\u9876\u706f\u3002\u7c0c\u7c0c\u7684\u542c\u89c1\u8e29\u96ea\u7684\u58f0\u97f3\uff0c\u501f\u7740\u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u770b\u89c1\u4e09\u4e2a\u4eba\u5f71\u5f80\u8fd9\u8fb9\u8d70\u6765\u3002</p><p>\u3000\u3000\u738b\u708e\u658c\u4e0e\u674e\u829d\u82b3\u94bb\u8fdb\u8f66\u91cc\u6765\uff0c\u53f8\u673a\u7ad9\u5728\u5916\u9762\u3002</p><p>\u3000\u3000\u738b\u708e\u658c\u795e\u8272\u8fd8\u7b97\u6b63\u5e38\uff0c\u674e\u829d\u82b3\u5374\u4e0d\u6562\u62ac\u5934\u770b\u4eba\uff0c\u54ea\u6015\u5f20\u606a\u4e0e\u675c\u98de\u90fd\u662f\u5979\u7684\u5b66\u751f\u3002</p><p>\u3000\u3000\u201c\u675c\u4e66\u8bb0\uff0c\u7ed9\u4f60\u6dfb\u9ebb\u70e6\u4e86\u3002\u201d\u738b\u708e\u658c\u7684\u58f0\u97f3\u5f88\u5e72\u6da9\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u6ca1\u6709\u5e94\u4ed6\u7684\u8bdd\uff0c\u53ea\u662f\u4e0d\u505c\u7684\u62bd\u70df\uff0c\u5f20\u606a\u6653\u5f97\u4ed6\u662f\u7b49\u533b\u9662\u90a3\u8fb9\u7684\u60c5\u51b5\u518d\u51b3\u5b9a\u5982\u4f55\u5584\u540e\u3002</p><p>\u3000\u3000\u738b\u708e\u658c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u5f20\u79d8\u4e66\u957f\u5c06\u7535\u8bdd\u6253\u5230\u5b66\u6821\u91cc\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u7b54\u5e94\u8d76\u56de\u5bb6\u5403\u996d\uff0c\u8fd9\u65f6\u5019\u90fd\u5feb\u516b\u70b9\u949f\uff0c\u7238\u5988\u4e00\u5b9a\u6025\u6b7b\u4e86\uff0c\u80fd\u6253\u7535\u8bdd\u5230\u5b66\u6821\u627e\u81ea\u5df1\uff0c\u90a3\u662f\u6253\u7535\u8bdd\u7ed9\u665a\u6674\u624d\u77e5\u9053\u81ea\u5df1\u7684\u884c\u8e2a\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u6c89\u9ed8\u7740\u5c06\u5927\u54e5\u5927\u9012\u7ed9\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u4e0b\u8f66\u907f\u5f00\u522b\u4eba\uff0c\u76f4\u63a5\u7ed9\u5bb6\u91cc\u6302\u4e86\u7535\u8bdd\uff0c\u662f\u5988\u5988\u63a5\u7684\u7535\u8bdd\uff1a\u201c\u4f60\u4e2a\u6b7b\u5c0f\u5b50\uff0c\u7b54\u5e94\u56de\u6765\u5403\u996d\uff0c\u600e\u4e48\u521a\u4ece\u7701\u57ce\u56de\u6765\u5c31\u5230\u5b66\u6821\u53bb\u4e86\uff0c\u5510\u5a67\u90fd\u7b49\u4f60\u534a\u5929\u4e86\uff0c\u5927\u5bb6\u8fd9\u4f1a\u513f\u90fd\u6ca1\u6709\u5403\u996d\uff0c\u5c31\u7b49\u4f60\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u5bb6\u91cc\u8fd8\u6709\u8c01\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8ddf\u675c\u98de\u5728\u4e00\u8d77\uff1f\u201d\u7238\u7238\u5f20\u77e5\u884c\u7684\u58f0\u97f3\u51fa\u73b0\u5728\u7535\u8bdd\u91cc\u3002</p><p>\u3000\u3000\u201c\u6d88\u606f\u4f20\u8fd9\u4e48\u5feb\uff1f\u201d\u5f20\u606a\u8be7\u5f02\u7684\u95ee\uff0c\u8fd9\u624d\u8fc7\u53bb\u4e0d\u5230\u4e24\u4e2a\u5c0f\u65f6\uff1f\u8f66\u91cc\u5f88\u5b89\u9759\uff0c\u5927\u54e5\u5927\u91cc\u7684\u58f0\u97f3\u8f66\u91cc\u4eba\u90fd\u80fd\u542c\u5f97\u89c1\uff0c\u675c\u5c0f\u5c71\u5fcd\u4e0d\u4f4f\u503e\u8fc7\u8eab\u5b50\u6765\u3002</p><p>\u3000\u3000\u201c\u521a\u63a5\u5230\u7535\u8bdd\u542c\u4eba\u8bf4\u8d77\u8fd9\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u5750\u5728\u675c\u53d4\u53d4\u7684\u8f66\u91cc\uff0c\u5c31\u505c\u5728\u4e00\u4e2d\u8fb9\u7684\u5df7\u5b50\u91cc\uff0c\u6211\u53ef\u80fd\u4f1a\u665a\u4e00\u70b9\u624d\u56de\u5bb6\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u6211\u4eec\u5148\u5403\u996d\u4e86\uff0c\u5c31\u4e0d\u7b49\u4f60\u4e86\u3002\u201d\u5f20\u77e5\u884c\u6ca1\u6709\u7ec6\u95ee\uff0c\u8f66\u5b50\u505c\u5728\u5df7\u5b50\u91cc\uff0c\u989d\u5916\u5429\u5490\u4ec0\u4e48\u8ba9\u675c\u5c0f\u5c71\u542c\u5230\u53cd\u800c\u4f1a\u751f\u95f4\u9699\uff0c\u5f20\u606a\u5904\u4e8b\u8ba9\u4ed6\u653e\u5fc3\uff0c\u4f1a\u77e5\u9053\u600e\u4e48\u505a\u7684\u3002\u518d\u8bf4\u8fd9\u65f6\u5019\u4e5f\u4e0d\u80fd\u8ddf\u675c\u5c0f\u5c71\u901a\u4ec0\u4e48\u6c14\uff0c\u8c01\u77e5\u9053\u540e\u679c\u5230\u5e95\u4e25\u4e0d\u4e25\u91cd\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5927\u54e5\u5927\u9012\u7ed9\u8fd8\u7ed9\u675c\u5c0f\u5c71\u3002</p><p>\u3000\u3000\u201c\u675c\u4e66\u8bb0\uff0c\u90a3\u4e9b\u6559\u5e08\u7684\u5634\u5df4\u2026\u2026\u201d\u738b\u708e\u658c\u4e5f\u6ca1\u6709\u60f3\u5230\u6d88\u606f\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u5c31\u4f20\u904d\u4e86\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u8fd8\u7b97\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u4e8b\u60c5\u603b\u5f52\u7792\u4e0d\u4f4f\u7684\uff0c\u518d\u8bf4\u60f3\u770b\u6211\u597d\u620f\u7684\u4eba\u4e5f\u591a\u3002\u201d</p><p>\u3000\u3000\u53c8\u5728\u6c89\u9ed8\u4e2d\u6e21\u8fc7\u8270\u96be\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u5927\u54e5\u5927\u518d\u6b21\u54cd\u8d77\u6765\u65f6\uff0c\u5df2\u7ecf\u5230\u4e5d\u70b9\u534a\u4e86\u3002</p><p>\u3000\u3000\u662f\u675c\u5c0f\u5c71\u79d8\u4e66\u4ece\u4eba\u6c11\u533b\u751f\u6253\u6765\u7684\u7535\u8bdd\uff0c\u66f9\u5149\u660e\u7ecf\u8fc7\u6025\u6551\uff0c\u6ca1\u6709\u751f\u547d\u5371\u9669\uff0c\u53ea\u662f\u53d7\u7684\u4f24\u6709\u4e9b\u91cd\uff0c\u5de6\u7709\u9aa8\u9aa8\u88c2\u3001\u9f3b\u6881\u9aa8\u65ad\u65ad\uff0c\u53f3\u988a\u9aa8\u9aa8\u88c2\uff0c\u5de6\u817f\u80eb\u9aa8\u9aa8\u6298\uff0c\u8f6f\u7ec4\u7ec7\u632b\u4f24\u591a\u5904\uff0c\u8111\u632f\u8361\u3002\u5de6\u817f\u80eb\u9aa8\u9aa8\u6298\u5927\u6982\u90a3\u662f\u66f9\u5149\u660e\u5728\u4e8c\u697c\u697c\u68af\u53e3\u6b63\u5077\u7aa5\u8d77\u52b2\u65f6\u7ed9\u675c\u98de\u4e00\u811a\u8e39\u4e0b\u697c\u68af\u6240\u81f4\uff0c\u4e0d\u7136\u7ed9\u675c\u98de\u6253\u8fd9\u4e48\u60e8\u4e0d\u4f1a\u6ca1\u6709\u8fd8\u624b\u4e4b\u529b\u3002</p><p>\u3000\u3000\u5728\u675c\u98de\u53d1\u75af\u63cd\u66f9\u5149\u660e\u65f6\uff0c\u738b\u708e\u658c\u614c\u4e71\u8eb2\u5f00\uff0c\u4ed6\u6ca1\u6709\u770b\u5230\u675c\u98de\u52a8\u624b\u7684\u60c5\u5f62\uff0c\u542c\u5230\u675c\u5c0f\u5c71\u590d\u8ff0\u66f9\u5149\u660e\u7684\u4f24\u60c5\u65f6\uff0c\u4ed6\u624d\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff1a\u8fd9\u4e9b\u5e72\u90e8\u5b50\u5f1f\u771f\u4e0d\u628a\u4eba\u547d\u5f53\u56de\u4e8b\u554a\uff1f\u674e\u829d\u82b3\u8138\u8272\u82cd\u767d\uff0c\u5f20\u53e3\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6e05\u695a\u675c\u98de\u4e3a\u4ec0\u4e48\u4f1a\u53d1\u75af\u7684\u72c2\u63cd\u66f9\u5149\u660e\uff0c\u9876\u591a\u4e00\u811a\u5c06\u6b63\u51c6\u5907\u6349\u5978\u7684\u66f9\u5149\u660e\u8e22\u4e0b\u697c\u68af\u5c31\u53ef\u4ee5\u4e86\uff0c\u95ee\u675c\u98de\uff0c\u675c\u98de\u5c31\u7792\u8fc7\u8fd9\u8282\u4e0d\u63d0\uff0c\u597d\u50cf\u8fd9\u4e48\u63cd\u66f9\u5149\u660e\u8fd8\u4e0d\u89e3\u6068\uff0c\u5f20\u606a\u5f53\u7136\u65e0\u8bed\u4e86\u3002\u8fd8\u597d\uff0c\u66f9\u5149\u660e\u6027\u547d\u65e0\u5fe7\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u597d\u8bf4\uff0c\u5fc3\u91cc\u8f7b\u677e\u4e0b\u6765\uff0c\u53c8\u671d\u675c\u5c0f\u5c71\u90a3\u91cc\u63a5\u8fc7\u4e00\u652f\u70df\uff0c\u81ea\u987e\u81ea\u7684\u70b9\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u89c1\u5f20\u606a\u5728\u8f66\u91cc\u62bd\u70df\uff0c\u4e0b\u610f\u8bc6\u7684\u7709\u5934\u626c\u4e86\u626c\uff0c\u5934\u7a81\u7136\u53c8\u6298\u4e0b\u53bb\uff0c\u773c\u775b\u9ed8\u7136\u65e0\u5149\u3002</p><p>\u3000\u3000\u201c\u738b\u4e66\u8bb0\uff0c\u8fd9\u4e8b\u600e\u4e48\u5904\u7406\uff1f\u201d\u738b\u708e\u658c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u95ee\uff0c\u4ed6\u5728\u8f66\u91cc\u67af\u5750\u4e86\u5c06\u8fd1\u4e24\u4e2a\u5c0f\u65f6\uff0c\u675c\u5c0f\u5c71\u90fd\u6ca1\u6709\u8ddf\u4ed6\u8bf4\u8fc7\u4e24\u53e5\u8bdd\uff0c\u5728\u5f02\u5e38\u538b\u4ef0\u7684\u6c14\u6c1b\u4e0b\uff0c\u5feb\u8fbe\u5230\u4ed6\u5fc3\u7406\u627f\u53d7\u80fd\u529b\u7684\u6781\u9650\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u70df\u7070\u5f39\u5230\u70df\u7070\u76d2\u91cc\uff0c\u7709\u5934\u626c\u4e86\u626c\uff1a\u201c\u80fd\u600e\u4e48\u529e\uff0c\u771f\u76f8\u4e0d\u662f\u90fd\u5230\u5904\u4f20\u64ad\u5f00\u53bb\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u2026\u2026\u201d\u738b\u708e\u658c\u541e\u541e\u5410\u5410\uff0c\u4ed6\u5f53\u7136\u5e0c\u671b\u4e8b\u60c5\u8fd9\u6837\u7ed3\u675f\u5c31\u597d\uff0c\u201c\u66f9\u5149\u660e\u4e0d\u4f1a\u5c31\u8fd9\u6837\u5c31\u7b97\u7684\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u62ac\u773c\u76ef\u7740\u738b\u708e\u658c\uff0c\u4e8f\u4ed6\u5728\u4e00\u4e2d\u6821\u957f\u4f4d\u7f6e\u6df7\u4e86\u8fd9\u4e48\u4e45\uff0c\u90fd\u6ca1\u6709\u534a\u5927\u5c11\u5e74\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7ed9\u66f9\u5149\u660e\u5f00\u53e3\u7684\u673a\u4f1a\u5c31\u884c\uff0c\u5b66\u6821\u8fd9\u8fb9\u4f60\u5904\u7406\u4e00\u4e0b\uff0c\u542c\u8bf4\u66f9\u5149\u660e\u4e0d\u600e\u4e48\u5f97\u4eba\u5fc3\uff1f\u201d\u66f9\u5149\u660e\u5f97\u4e0d\u5f97\u4eba\u5fc3\uff0c\u675c\u5c0f\u5c71\u4e0d\u77e5\u9053\uff0c\u4f46\u662f\u4ed6\u4e0d\u5e0c\u671b\u770b\u5230\u6709\u4eba\u7ad9\u51fa\u6765\u5e2e\u66f9\u5149\u660e\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u662f\u7684\uff0c\u66f9\u5149\u660e\u662f\u5f88\u4e0d\u5f97\u4eba\u5fc3\uff0c\u5bf9\u5b66\u751f\u4e5f\u5f88\u7c97\u66b4\uff0c\u675c\u98de\u8fd9\u4e48\u505a\uff0c\u867d\u7136\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4f46\u662f\u5e74\u8f7b\u4eba\uff0c\u96be\u514d\u813e\u6c14\u6025\u8e81\u4e86\u4e9b\u2026\u2026\u201d\u738b\u708e\u658c\u7ec8\u4e8e\u5f00\u4e86\u7a8d\uff0c\u987a\u7740\u675c\u5c0f\u5c71\u7684\u8bdd\u8bf4\uff0c\u201c\u6211\u4f1a\u8ba9\u674e\u8001\u5e08\u5199\u4efd\u60c5\u51b5\u8bf4\u660e\uff0c\u5b66\u6821\u4f1a\u5bf9\u8fd9\u6837\u7684\u8001\u5e08\u8fdb\u884c\u5904\u7406\uff0c\u6700\u597d\u80fd\u5c06\u9010\u4eba\u6c11\u6559\u5e08\u7684\u961f\u4f0d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9632\u4eba\u4e4b\u53e3\u80dc\u4e8e\u9632\u5ddd\uff0c\u66f9\u5149\u660e\u5149\u68cd\u4e00\u6761\uff0c\u8fd8\u80fd\u5835\u4ed6\u7684\u53e3\uff1f\u201d\u5f20\u606a\u6697\u9a82\u4e00\u58f0\uff1a\u771f\u8822\uff01\u8981\u4e0d\u662f\u675c\u98de\uff0c\u771f\u61d2\u5f97\u7ba1\u8fd9\u4e8b\uff0c\u770b\u5230\u738b\u708e\u658c\u7684\u5634\u8138\uff0c\u611f\u89c9\u5403\u4e86\u53ea\u82cd\u8747\u3002\u5012\u662f\u674e\u829d\u82b3\u8737\u5728\u90a3\u91cc\uff0c\u695a\u695a\u53ef\u601c\uff0c\u5bf9\u8f66\u53a2\u91cc\u6b63\u8fdb\u884c\u7684\u9634\u8c0b\u8be1\u8ba1\u6709\u4e9b\u4e8e\u5fc3\u4e0d\u5fcd\u3002\u5f20\u606a\u5bf9\u738b\u708e\u658c\u8bf4\uff0c\u201c\u738b\u6821\u957f\uff0c\u53f8\u673a\u5728\u5916\u9762\u7ad9\u4e24\u4e2a\u5c0f\u65f6\u4e86\uff0c\u4f60\u662f\u4e0d\u662f\u8ba9\u4ed6\u8fdb\u6765\u8eb2\u4e00\u8eb2\uff1f\u201d</p><p>\u3000\u3000\u738b\u708e\u658c\u6123\u4e86\u6123\uff0c\u89c1\u675c\u5c0f\u5c71\u7684\u7709\u5934\u626c\u4e86\u626c\uff0c\u5fcd\u58f0\u6ca1\u8bf4\u8bdd\uff0c\u7070\u6e9c\u6e9c\u7684\u63a8\u95e8\u4e0b\u8f66\uff0c\u53f8\u673a\u8fdb\u6765\uff0c\u8138\u51b0\u5f97\u94c1\u9752\uff0c\u5916\u5957\u90fd\u7ed9\u96ea\u6fe1\u6e7f\u4e86\u3002\u9886\u5bfc\u7684\u79d8\u4e66\u5411\u6765\u4e0d\u7792\u53f8\u673a\u3001\u79d8\u4e66\uff0c\u4f55\u51b5\u66f9\u5149\u660e\u6ca1\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u63a5\u4e0b\u6765\u7684\u9634\u8c0b\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u60ca\u4e16\u9a87\u4fd7\uff0c\u8ba9\u53f8\u673a\u7559\u5728\u8f66\u91cc\u6696\u548c\u6696\u548c\u4e5f\u65e0\u6240\u8c13\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u675c\u5c0f\u5c71\u4e00\u773c\uff0c\u4ed6\u77a5\u773c\u770b\u7740\u674e\u829d\u82b3\uff0c\u6709\u4e9b\u8bdd\u4f3c\u4e4e\u5835\u5728\u5634\u91cc\u5f88\u96be\u51fa\u53e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u989d\u5934\uff0c\u5bf9\u674e\u829d\u82b3\u8bf4\uff1a\u201c\u674e\u8001\u5e08\uff0c\u6211\u77e5\u9053\u6211\u5728\u4f60\u773c\u91cc\u4ece\u6765\u5c31\u4e0d\u662f\u4ec0\u4e48\u597d\u5b66\u751f\uff0c\u4f46\u662f\u6709\u4e9b\u8bdd\uff0c\u4f60\u8fd9\u65f6\u5019\u8981\u542c\u6211\u7684\u3002\u66f9\u5149\u660e\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u4e5f\u662f\u4ed6\u4e0d\u6000\u597d\u5fc3\u5728\u524d\uff0c\u6028\u4e0d\u4e86\u675c\u98de\u4e0b\u624b\u91cd\uff0c\u7b97\u4ed6\u7f6a\u6709\u5e94\u5f97\u3002\u4f60\u4e5f\u4e0d\u60f3\u8eab\u8d25\u540d\u88c2\u3001\u5bb6\u7834\u4eba\u6563\uff0c\u5c31\u53ea\u80fd\u8ba9\u4e8b\u60c5\u8fd9\u6837\u8fc7\u53bb\uff0c\u8fd9\u5bf9\u675c\u98de\u4e5f\u597d\u2026\u2026\u201d\u5f20\u606a\u505c\u4e86\u505c\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u4e0d\u4f60\u5148\u56de\u5bb6\u60f3\u60f3\u53bb\uff1f\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u5b9b\u82e5\u6728\u5076\u4e00\u6837\u7684\u4e0b\u4e86\u8f66\uff0c\u738b\u708e\u658c\u5728\u8f66\u5916\u6572\u4e86\u6572\u7a97\uff0c\u675c\u5c0f\u5c71\u538c\u70e6\u7684\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u4ed6\u8ddf\u674e\u829d\u82b3\u5148\u8d70\u3002</p><p>\u3000\u3000\u8f66\u91cc\u53ea\u5269\u4e0b\u56db\u4e2a\u4eba\uff0c\u5f20\u606a\u89c1\u675c\u5c0f\u5c71\u4e5f\u677e\u4e86\u4e00\u53e3\u6c14\u7684\u6837\u5b50\uff0c\u6307\u7740\u675c\u98de\u5de6\u8138\u988a\u7684\u624b\u6307\u5370\u8bf4\uff1a\u201c\u75bc\u5427\uff1f\u6211\u4e0b\u624b\u4e5f\u91cd\u4e86\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u6df7\u8d26\u5c0f\u5b50\uff0c\u4e0b\u624b\u662f\u60f3\u8981\u4eba\u547d\uff0c\u5979\u503c\u5f97\u4f60\u8fd9\u6837\uff1f\u201d\u675c\u5c0f\u5c71\u77aa\u773c\u770b\u7740\u675c\u98de\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u4e0d\u662f\u90a3\u6837\uff1f\u201d\u675c\u98de\u729f\u58f0\u8bf4\u3002</p><p>\u3000\u3000\u8fd8\u4ee5\u4e3a\u675c\u5c0f\u5c71\u4f1a\u8d4f\u675c\u98de\u4e00\u5df4\u638c\uff0c\u6ca1\u60f3\u5230\u675c\u5c0f\u5c71\u542c\u5230\u8fd9\u8bdd\uff0c\u95f7\u58f0\u5750\u5728\u90a3\u91cc\uff0c\u4e5f\u6ca1\u6709\u5f88\u751f\u6c14\u7684\u6837\u5b50\u3002\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u675c\u5c0f\u5c71\u9057\u4f20\u7ed9\u675c\u98de\u75f4\u604b\u719f\u5973\u7684\u4f20\u7edf\uff1f</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u6320\u4e86\u6320\u5934\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u8fd9\u4e8b\u8fd8\u8981\u5e02\u6559\u80b2\u5c40\u4fdd\u6301\u6c89\u9ed8\u624d\u884c\u3002\u6211\u8ddf\u5e02\u6559\u80b2\u5c40\u7684\u90a3\u73ed\u4eba\u4e0d\u719f\uff0c\u5510\u5e02\u957f\u4e4b\u524d\u505a\u526f\u5e02\u957f\u65f6\uff0c\u4e3b\u7ba1\u8fc7\u6559\u80b2\u2026\u2026\u201d</p><p>\u3000\u3000\u4e8b\u60c5\u4e0d\u5927\uff0c\u53ea\u8981\u8ba9\u66f9\u5149\u660e\u95ed\u53e3\u5c31\u53ef\u4ee5\uff0c\u53ea\u8981\u66f9\u5149\u660e\u4e0d\u95f9\u817e\uff0c\u6559\u80b2\u5c40\u7684\u90a3\u4e9b\u5934\u5934\u8111\u8111\u53c8\u600e\u4e48\u4f1a\u7ad9\u51fa\u6765\u9488\u5bf9\u675c\u5c0f\u5c71\uff1f\u4ec5\u4ec5\u662f\u6253\u62db\u547c\uff0c\u675c\u5c0f\u5c71\u7684\u9762\u5b50\u662f\u8db3\u591f\u4e86\uff0c\u54ea\u91cc\u9700\u8981\u5510\u5b66\u8c26\u51fa\u9762\uff1f</p><p>\u3000\u3000\u8981\u5c01\u4f4f\u66f9\u5149\u660e\u7684\u53e3\uff0c\u76f4\u63a5\u7ed9\u4ed6\u4e00\u4e2a\u4f4d\u7f6e\u90a3\u662f\u518d\u5408\u9002\u4e0d\u8fc7\u7684\uff0c\u5510\u5b66\u8c26\u4e0d\u4ec5\u4e0e\u6559\u80b2\u5c40\u7684\u5934\u5934\u8111\u8111\u719f\uff0c\u5510\u5b66\u8c26\u8fd8\u53ef\u4ee5\u76f4\u63a5\u8fc7\u95ee\u6559\u80b2\u5c40\u7684\u4eba\u4e8b\u8c03\u6574\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u675c\u5c0f\u5c71\u80fd\u76f4\u63a5\u51b3\u5b9a\u897f\u57ce\u533a\u7684\u6559\u80b2\u884c\u653f\u7f16\u5236\uff0c\u4f46\u662f\u533a\u5c5e\u6559\u80b2\u5c40\u624d\u6b63\u79d1\u7ea7\uff0c\u8981\u7ed9\u66f9\u5149\u660e\u5b89\u6170\uff0c\u603b\u4e0d\u80fd\u76f4\u63a5\u8ba9\u4ed6\u53bb\u505a\u897f\u57ce\u533a\u6559\u80b2\u5c40\u957f\u7684\u4f4d\u7f6e\u3002\u5f53\u7136\uff0c\u5c31\u7b97\u5230\u897f\u57ce\u533a\u6559\u80b2\u5c40\u5f53\u4e00\u540d\u526f\u5c40\u957f\uff0c\u7ea7\u522b\u8fd8\u662f\u526f\u79d1\uff0c\u5374\u7edd\u5bf9\u6bd4\u4e00\u4e2d\u7684\u653f\u6559\u5904\u4e3b\u4efb\u5403\u9999\uff0c\u60f3\u5fc5\u66f9\u5149\u660e\u4e5f\u4e0d\u4f1a\u62d2\u7edd\uff0c\u53ea\u662f\u8fd9\u79cd\u8c03\u6574\uff0c\u592a\u7740\u75d5\u8ff9\u4e86\uff0c\u8fd8\u662f\u5728\u5e02\u5c5e\u4e2d\u5b66\u91cc\u8c03\u6574\u4e0d\u5f15\u4eba\u6ce8\u610f\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u675c\u5c0f\u5c71\u7684\u5927\u54e5\u5927\u7ed9\u5bb6\u91cc\u6302\u7535\u8bdd\uff0c\u662f\u7238\u7238\u63a5\u7684\uff0c\u5927\u6982\u5728\u7b49\u8fd9\u8fb9\u7684\u7535\u8bdd\u3002\u5f20\u606a\u5c06\u5b9e\u60c5\u544a\u8bc9\u4e4b\u540e\uff0c\u5c06\u5927\u54e5\u5927\u9012\u7ed9\u675c\u5c0f\u5c71\uff0c\u8fd9\u79cd\u4e8b\u8fd8\u662f\u7531\u675c\u5c0f\u5c71\u4eb2\u81ea\u63d0\u8d77\u7684\u597d\u3002\u53cd\u6b63\u4eba\u5728\u8f66\u91cc\uff0c\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u5927\u5bb6\u90fd\u542c\u5f97\u89c1\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u63d0\u51fa\u5e0c\u671b\u6559\u80b2\u5c40\u9886\u5bfc\u80fd\u51fa\u9762\u5b89\u6170\u4e00\u4e0b\u66f9\u5149\u660e\u7684\u60c5\u7eea\uff0c\u4f46\u662f\u6559\u80b2\u5c40\u90a3\u8fb9\u7684\u60c5\u7eea\u8fd8\u9700\u8981\u5510\u5b66\u8c26\u4eb2\u81ea\u5b89\u629a\u3002\u5f20\u77e5\u884c\u5c31\u8bf4\u4e86\u53e5\u201c\u6211\u77e5\u9053\u4e86\u2026\u2026\u201d\u5c31\u6302\u6389\u7535\u8bdd\uff0c\u5927\u6982\u8fc7\u4e86\u5341\u5206\u949f\u624d\u53c8\u6253\u8fc7\u6765\uff0c\u8bf4\uff1a\u201c\u5510\u5e02\u957f\u4e5f\u5f88\u91cd\u89c6\u8fd9\u4ef6\u4e8b\uff0c\u5e0c\u671b\u6211\u4e0e\u803f\u5c40\u957f\u5148\u5230\u533b\u9662\u770b\u671b\u4e00\u4e0b\u66f9\u8001\u5e08\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8fc7\u53bb\u63a5\u4f60\u3002\u201d\u675c\u5c0f\u5c71\u9694\u7740\u7535\u8bdd\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\u7528\uff0c\u5510\u5e02\u957f\u7684\u8f66\u9001\u6211\u8fc7\u53bb\uff0c\u6211\u4eec\u5728\u533b\u9662\u6c47\u5408\u5427\u3002\u201d</p><p>\u3000\u3000\u4e8b\u60c5\u6ca1\u5fc5\u8981\u8ba9\u5510\u5a67\u77e5\u9053\uff0c\u4eca\u5929\u665a\u4e0a\u82e5\u53ea\u662f\u5510\u5029\u4e0e\u5979\u5988\u5728\u5bb6\u91cc\u505a\u5ba2\uff0c\u8fd9\u65f6\u5019\u5e94\u8be5\u5df2\u7ecf\u5403\u8fc7\u665a\u996d\u56de\u5bb6\u4e86\uff0c\u8fd9\u4e48\u665a\u4e8c\u53f7\u8f66\u8fd8\u5728\u673a\u5173\u5927\u9662\uff0c\u90a3\u8bf4\u660e\u5510\u5b66\u8c26\u4e5f\u662f\u5bb6\u91cc\u505a\u5ba2\uff0c\u9001\u5510\u5b66\u8c26\u4e00\u5bb6\u4eba\u56de\u5e08\u9662\u5c0f\u533a\uff0c\u6070\u597d\u7ecf\u8fc7\u6d77\u5dde\u533b\u9662\u3002</p><p>\u3000\u3000\u4ece\u4e00\u4e2d\u8d76\u5230\u6d77\u5dde\u533b\u9662\u8def\u9014\u8fd1\uff0c\u5f20\u606a\u4e0e\u675c\u5c0f\u5c71\u3001\u675c\u98de\u5148\u8d76\u5230\u6d77\u5dde\u533b\u9662\uff0c\u4e0e\u5148\u5230\u8fd9\u91cc\u7684\u675c\u5c0f\u5c71\u7684\u79d8\u4e66\u6c47\u5408\uff0c\u539f\u5148\u968f\u6551\u62a4\u8f66\u9001\u66f9\u5149\u660e\u5230\u533b\u9662\u7684\u6570\u5b66\u7ec4\u7ec4\u957f\u5434\u70b3\u751f\u4e0e\u53e6\u4e00\u540d\u8001\u5e08\u5df2\u7ecf\u7ed9\u652f\u8d70\u56de\u5bb6\u4e86\u3002</p><p>\u3000\u3000\u4f17\u4eba\u7ad9\u5728\u533b\u9662\u5927\u95e8\u524d\u7684\u53f0\u9636\u4e0a\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u5510\u5b66\u8c26\u7684\u5750\u9a91\u9ed1\u8272\u5c3c\u6851\u624d\u5f00\u8fdb\u533b\u9662\u6025\u8bca\u697c\u4e0b\uff0c\u5f20\u77e5\u884c\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u5510\u5a67\u5934\u4f38\u51fa\u6765\uff0c\u8ddf\u5f20\u606a\u4ed6\u4eec\u6447\u4e86\u6447\u624b\uff0c\u9ed1\u8272\u5c3c\u6851\u5c31\u79bb\u5f00\u6025\u8bca\u697c\uff0c\u5510\u5b66\u8c26\u6b64\u65f6\u662f\u4e0d\u4f1a\u9732\u9762\u7684\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8d70\u8fc7\u6765\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u53ea\u7eb8\u888b\uff0c\u9012\u7ed9\u5f20\u606a\uff1a\u201c\u4f60\u4eec\u90fd\u6ca1\u6709\u5403\u4e1c\u897f\u5427\uff0c\u5148\u586b\u70b9\u809a\u5b50\uff0c\u803f\u5c40\u957f\u8fd8\u8981\u8fc7\u4e00\u4f1a\u513f\u624d\u80fd\u5230\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4ed6\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4f1a\u513f\u624d\u89c9\u5f97\u997f\uff0c\u6253\u5f00\u7eb8\u888b\u770b\u4e86\u770b\uff0c\u90fd\u662f\u70e4\u9e2d\u3001\u9e21\u817f\u4e4b\u7c7b\u7684\u719f\u98df\uff0c\u4e0d\u6653\u5f97\u5988\u5988\u8bb0\u7740\uff0c\u8fd8\u662f\u5510\u5a67\u60f3\u8d77\u6765\u7684\u3002\u5f20\u606a\u5c06\u7eb8\u888b\u5148\u9012\u7ed9\u675c\u5c0f\u5c71\uff0c\u52a0\u4e0a\u53f8\u673a\u4e94\u4e2a\u4eba\uff0c\u8f6c\u773c\u95f4\u5c31\u5c06\u4e00\u888b\u5b50\u719f\u98df\u5206\u98df\u5e72\u51c0\uff0c\u610f\u72b9\u672a\u5c3d\uff0c\u4f46\u7ec8\u7b97\u586b\u4e86\u586b\u809a\u5b50\u3002</p><p>\u3000\u3000\u4e00\u76f4\u7b49\u5230\u5e02\u6559\u80b2\u5c40\u957f\u803f\u5efa\u5fe0\u8fc7\u6765\uff0c\u5927\u5bb6\u624d\u8d70\u8fdb\u6025\u8bca\u75c5\u623f\u3002</p></article>"}}}]);
