(window.webpackJsonp=window.webpackJsonp||[]).push([[691],{714:function(p){p.exports={chapterTitle:null,title:"\u7b2c533\u7ae0 \u7ecf\u6d4e\u5730\u4f4d",content:"<article><h2>\u7b2c533\u7ae0 \u7ecf\u6d4e\u5730\u4f4d</h2><p>\u3000\u3000\u591c\u91cc\uff0c\u542c\u7740\u7a97\u5916\u5317\u4eac\u6df1\u591c\u91cc\u522e\u8fc7\u6797\u68a2\u7684\u98ce\u58f0\uff0c\u5f20\u606a\u8ddf\u6b63\u5728\u8fbe\u62c9\u65af\u4e0eTI\u8fdb\u884c\u624b\u673a\u4ea7\u54c1\u6280\u672f\u5f15\u8fdb\u8c08\u5224\u7684\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u901a\u4e86\u7535\u8bdd\uff0c\u8d8a\u6d0b\u7535\u8bdd\u6709\u4e9b\u6ecb\u6ecb\u58f0\uff0c\u4e0e\u7a97\u5916\u7684\u98ce\u58f0\u5f88\u63a5\u8fd1\uff0c\u901a\u8bdd\u6548\u679c\u751a\u81f3\u6bd4\u4e0d\u4e0a\u5bbd\u5e26\u89c6\u9891\uff0c\u5f20\u606a\u4e00\u8fb9\u6000\u5ff5\u7740\u51e0\u5e74\u540e\u7684\u5bbd\u5e26\u63a5\u5165\u670d\u52a1\uff0c\u4e00\u8fb9\u5728\u7535\u8bdd\u91cc\u5c06\u60c5\u51b5\u901a\u62a5\u7ed9\u9648\u4fe1\u751f\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5bf9\u8096\u745e\u6c11\u6709\u5370\u8c61\uff0c\u6bd5\u7adfTI\u662f\u56fd\u5185\u7535\u5b50\u3001\u901a\u4fe1\u8bbe\u5907\u5382\u5546\u7684\u4e3b\u8981\u5143\u5668\u4ef6\u4f9b\u5e94\u5546\u4e4b\u4e00\uff0c\u8054\u4fe1\u7684\u51fa\u73b0\uff0c\u7684\u786e\u6709\u52a9\u4e8e\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u8bc1\u63d0\u524d\u53d1\u653e\uff0c\u4e5f\u5c31\u662f\u8bf4\u9526\u6e56\u9020\u52bf\u8ba1\u5212\u8981\u63d0\u524d\u8fdb\u884c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e0c\u671b\u8096\u664b\u6210\u7559\u5728\u8fbe\u62c9\u65af\u4e0eTI\u8fdb\u884c\u540e\u7eed\u7684\u8c08\u5224\uff0c\u9648\u4fe1\u751f\u5c3d\u5feb\u8fd4\u56de\u56fd\u5185\u4e3b\u6301\u5927\u5c40\u3002</p><p>\u3000\u30003\u67087\u65e5\uff0c\u5317\u4eac\u8d77\u4e86\u6c99\u5c18\u66b4\uff0c\u5f20\u606a\u5728\u53f6\u81fb\u6c11\u5bb6\u91cc\u559d\u8336\uff0c\u770b\u7740\u7a97\u5916\u6666\u5982\u9ec4\u660f\u7684\u5929\u8272\uff0c\u62c5\u5fc3\u8fbe\u62c9\u65af\u76f4\u8fbe\u5317\u4eac\u7684\u5317\u4eac\u80fd\u5426\u987a\u5229\u964d\u843d\u3002\u62c5\u5fc3\u4e5f\u6ca1\u6709\u7528\uff0c\u4ed6\u8fd8\u8981\u5e2e\u53f6\u81fb\u6c11\u4e00\u8d77\u5206\u67903\u67082\u65e5\uff0c\u56fd\u9645\u6e38\u8d44\u5bf9\u6cf0\u94e2\u7684\u4e00\u6b21\u88ad\u6270\u6027\u8d28\u7684\u51fa\u51fb\uff0c\u6cf0\u56fd\u653f\u5e9c\u52a8\u7528\u56fd\u5bb6\u50a8\u5907\u8fdb\u884c\u53cd\u51fb\uff0c\u56fd\u9645\u6e38\u8d44\u7a0d\u7a0d\u53d7\u632b\u5c31\u5043\u65d7\u606f\u9f13\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u8868\u9762\u4e0a\u770b\u53bb\u8fd8\u662f\u4e00\u7247\u6b23\u6b23\u5411\u8363\u554a\uff0c\u201d\u53f6\u81fb\u6c11\u5c06\u4e00\u53e0\u8bb0\u5f55\u6ee1\u7ecf\u6d4e\u6570\u636e\u7684\u6253\u5370\u7eb8\u4e22\u684c\u4e0a\uff0c\u201c\u8fd9\u6bb5\u65f6\u95f4\u6765\uff0c\u6211\u4e13\u95e8\u7814\u7a76\u4e86\u56fd\u9645\u6e38\u8d44\u7684\u653b\u51fb\u56fd\u5bb6\u8d27\u5e01\u4f53\u7cfb\u7684\u65b9\u5f0f\uff0c\u7279\u522b\u7d22\u7f57\u65af\u91cf\u5b50\u57fa\u91d1\u653b\u51fb\u82f1\u9551\u7684\u90a3\u6b21\u7ecf\u5178\u4e4b\u5f79\uff0c\u8fd9\u4e00\u6b21\u5e94\u8be5\u662f\u8bd5\u63a2\u6027\u7684\u51fa\u51fb\uff0c\u53ef\u60dc\u554a\uff0c\u4eba\u4eec\u66f4\u5bb9\u6613\u63a5\u53d7\u597d\u7684\u6d88\u606f\uff0c\u6cf0\u56fd\u653f\u5e9c\u52a8\u7528\u56fd\u5bb6\u50a8\u5907\u51fb\u9000\u56fd\u9645\u6e38\u8d44\u7684\u653b\u51fb\uff0c\u5012\u662f\u8ba9\u66f4\u591a\u7684\u6295\u8d44\u4eba\u5bf9\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u4f53\u7cfb\u7684\u5b9e\u529b\u4e0e\u7a33\u5b9a\u5145\u6ee1\u4fe1\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u80fd\u591f\u80af\u5b9a\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u591a\u534a\u4e0d\u5927\u60f3\u82b1\u592a\u591a\u7684\u7cbe\u529b\u53bb\u7814\u7a76\uff0c\u4ed6\u53cc\u624b\u62b1\u7740\u8336\u676f\uff0c\u76d8\u817f\u5750\u5728\u53f6\u81fb\u6c11\u5bb6\u7684\u9ad8\u80cc\u592a\u5e08\u6905\u4e0a\uff0c\u4ed6\u559d\u7740\u8336\uff0c\u684c\u4e0a\u7684\u90a3\u53e0\u5370\u6ee1\u7ecf\u6d4e\u6570\u636e\u7684\u6253\u5370\u7eb8\uff0c\u4ed6\u662f\u61d2\u5f97\u770b\u7684\uff1a\u201c\u4e8c\u53d4\u4e0d\u6015\u5224\u65ad\u51fa\u9519\uff1f\u6211\u770b\u5230\u597d\u51e0\u7bc7\u6587\u7ae0\u53c8\u5f00\u59cb\u6279\u9a73\u4f60\u7684\u5371\u673a\u89c2\u70b9\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u91d1\u878d\u5371\u673a\u4f1a\u4e0d\u4f1a\u66b4\u53d1\uff0c\u8c01\u80fd\u6709\u767e\u5206\u767e\u7684\u628a\u63e1\uff1f\u4f46\u662f\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u4f53\u7cfb\u5b58\u5728\u4e25\u91cd\u7f3a\u9677\uff0c\u90a3\u662f\u786e\u51ff\u65e0\u7591\u7684\u3002\u867d\u7136\u6709\u4e9b\u7ecf\u6d4e\u5b66\u8005\u4e5f\u9010\u6e10\u8ba4\u8bc6\u5230\u8fd9\u4e9b\u95ee\u9898\uff0c\u53ea\u4e0d\u8fc7\u53c8\u8ba4\u4e3a\u4e1c\u5357\u4e9a\u5730\u533a\u9ad8\u901f\u7684\u7ecf\u6d4e\u53d1\u5c55\uff0c\u5bf9\u8fd9\u4e9b\u7f3a\u9677\u6709\u6781\u5927\u7684\u5f25\u8865\u4f5c\u7528\uff0c\u7206\u53d1\u91d1\u878d\u5371\u673a\u7684\u53ef\u80fd\u6027\u6781\u5fae\u2026\u2026\u4ed6\u4eec\u5ffd\u7565\u6389\u5916\u90e8\u56e0\u7d20\uff0c\u4e5f\u5ffd\u7565\u6389\u5176\u4e2d\u53ef\u80fd\u5b58\u5728\u7684\u653f\u6cbb\u56e0\u7d20\uff0c\u201d\u53f6\u81fb\u6c11\u672c\u8eab\u5c31\u662f\u4e9a\u6d32\u7ecf\u6d4e\u95ee\u9898\u7684\u4e13\u5bb6\uff0c\u804a\u8d77\u4e9a\u6d32\u7ecf\u6d4e\u95ee\u9898\u6765\u4f83\u4f83\u800c\u8c08\uff0c\u201c\u4f60\u90fd\u4e0b\u51b3\u5fc3\u5c06\u7231\u8fbe\u7535\u5b50\u51fa\u552e\u4e86\uff0c\u6211\u8fd8\u62c5\u5fc3\u4ec0\u4e48\uff1f\u54e6\uff0c\u5bf9\u4e86\uff0c\u542c\u8bf4\u674e\u8fdc\u6e56\u76ef\u4e0a\u4f60\u4eec\u4e86\uff0c\u60f3\u5c06\u4f60\u4eec\u53bb\u5e74\u63d0\u51fa\u6765\u7684\u5236\u6d46\u9879\u76ee\u5217\u5165\u7701\u653f\u5e9c\u91cd\u70b9\u4ea7\u4e1a\u9879\u76ee\u6765\u6293\uff0c\u662f\u4e0d\u662f\u770b\u4e2d\u4f60\u5c06\u7231\u8fbe\u7535\u5b50\u5356\u51fa\u53bb\u4e4b\u540e\u7684\u94b1\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u9664\u4e86\u8fd9\u4e2a\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u82e6\u7b11\u9053\uff0c\u201c\u73b0\u5728\u5230\u5904\u90fd\u662f\u552f\u7ecf\u6d4e\u8bba\u7684\u5e02\u573a\uff0c\u73af\u5883\u4e0e\u751f\u6001\u662f\u53ef\u4ee5\u9002\u5ea6\u727a\u7272\u7684\uff0c\u751a\u81f3\u727a\u7272\u4e00\u90e8\u5206\u4eba\u7684\u5229\u76ca\u4e5f\u662f\u53ef\u4ee5\u7684\uff0c\u8fd9\u662f\u5468\u747e\u73ba\u5728\u6700\u8fd1\u4e00\u7bc7\u6587\u7ae0\u91cc\u7684\u89c2\u70b9\uff0c\u6211\u5012\u60f3\u5c06\u4ed6\u63ea\u5230\u8ddf\u524d\u6765\u95ee\u95ee\uff0c\u4ed6\u5230\u5e95\u60f3\u727a\u7272\u54ea\u4e00\u90e8\u5206\u4eba\u7684\u5229\u76ca\uff1f\u53e6\u5916\u554a\uff0c\u5728\u5baa\u6cd5\u91cc\uff0c\u662f\u600e\u4e48\u7ed9\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u4e0b\u5b9a\u4e49\u7684\uff1f\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u53ea\u662f\u56fd\u6c11\u7ecf\u6d4e\u7684\u5fc5\u8981\u8865\u5145\u800c\u5df2\uff0c\u8fd8\u9700\u8981\u63a5\u53d7\u653f\u5e9c\u7684\u76d1\u7763\u3001\u7ba1\u7406\u4e0e\u6307\u5bfc\u3002\u8fd9\u4e9b\u90fd\u662f\u67b7\u9501\uff0c\u6211\u8fd9\u4e9b\u5929\u5c31\u4e3a\u8fd9\u4e9b\u4e8b\u60c5\u5934\u75bc\uff0c\u9526\u6e56\u7ec6\u80f3\u81c2\u7626\u817f\u7684\uff0c\u53c8\u4e0d\u6562\u8ddf\u674e\u4ee3\u7701\u957f\u7ffb\u8138\uff0c\u53ea\u80fd\u60f3\u522b\u7684\u529e\u6cd5\u89e3\u5957\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u8fd9\u4e9b\u5929\u8eb2\u5728\u5317\u4eac\u7684\u539f\u56e0\uff1f\u201d\u53f6\u81fb\u6c11\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u6211\u4eec\u5728\u5236\u6d46\u9879\u76ee\u4e4b\u5916\u53e6\u6709\u8ba1\u5212\uff0c\u539f\u6765\u6084\u65e0\u58f0\u606f\u7684\u8fdb\u884c\uff0c\u4f46\u662f\u60f3\u5230\u53ef\u80fd\u5728\u674e\u4ee3\u7701\u957f\u9762\u524d\u884c\u4e0d\u901a\uff0c\u6240\u4ee5\u5148\u5230\u5317\u4eac\u6765\u9020\u4e9b\u58f0\u52bf\uff0c\u4e3a\u6211\u4eec\u539f\u5148\u5b9a\u4e0b\u7684\u8ba1\u5212\u6269\u5927\u4e00\u4e9b\u5f71\u54cd\uff0c\u4e5f\u5e0c\u671b\u80fd\u5f71\u54cd\u5230\u8fd9\u4f4d\u674e\u4ee3\u7701\u957f\u2026\u2026\u201d\u5f20\u606a\u5c06\u4ed6\u4e0b\u4e00\u6b65\u7684\u6253\u7b97\u7b80\u7565\u7684\u8ddf\u53f6\u81fb\u6c11\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u5f88\u591a\u95ee\u9898\u4e4b\u524d\u90fd\u6709\u6c9f\u901a\uff0c\u53ea\u662f\u90ae\u7535\u90e8\u7ec4\u5efa\u8054\u4fe1\u96c6\u56e2\u662f\u65b0\u51fa\u73b0\u7684\u60c5\u51b5\uff0c\u5f20\u606a\u51e0\u4e4e\u80fd\u80af\u5b9a\u8054\u4fe1\u5c06\u662f\u9526\u6e56\u624b\u673a\u9879\u76ee\u5728\u56fd\u5185\u7684\u6700\u5927\u7ade\u4e89\u8005\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u770b\u7a97\u5916\u98ce\u58f0\u6b62\u606f\u4e86\uff0c\u5929\u8272\u4e5f\u5206\u660e\u4e86\u4e9b\uff0c\u6c99\u5c18\u66b4\u6e10\u6e10\u6b62\u606f\uff0c\u5f20\u606a\u770b\u4e86\u770b\u624b\u8868\uff0c\u5bf9\u53f6\u81fb\u6c11\u8bf4\u9053\uff1a\u201c\u770b\u6765\u4eca\u5929\u7684\u5317\u4eac\u673a\u573a\u80fd\u6b63\u5e38\u8d77\u964d\uff0c\u6211\u8fd9\u8981\u8d76\u53bb\u673a\u573a\u63a5\u4eba\u2026\u2026\u7231\u8fbe\u96c6\u56e2\u7684\u65b0\u603b\u88c1\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u5728\u5317\u4eac\u7d27\u6025\u4e70\u4e0b\u8fd9\u8f86\u8f66\u5c31\u662f\u4e3a\u4e86\u8fce\u63a5\u7231\u8fbe\u96c6\u56e2\u7684\u65b0\u603b\u88c1\uff1f\u201d\u53f6\u81fb\u6c11\u770b\u7740\u505c\u5728\u7a97\u524d\u8349\u576a\u4e0a\u7684\u51ef\u8fea\u62c9\u514b\uff0c\u4e94\u7c73\u591a\u957f\u7684\u8f66\u8eab\uff0c\u7684\u786e\u975e\u5e38\u6c14\u6d3e\u3002</p><p>\u3000\u3000\u201c\u7559\u5728\u5317\u4eac\u5145\u573a\u9762\uff0c\u201d\u5f20\u606a\u7b11\u7740\u89e3\u91ca\u8bf4\uff0c\u201c\u6211\u4e2a\u4eba\u4e0d\u949f\u610f\uff0c\u597d\u50cf\u56fd\u4eba\u53ea\u8ba4\u5f97\u8fd9\u8f66\uff0c\u8bf4\u662f\u7f8e\u56fd\u8c6a\u534e\u8f66\u7684\u5178\u8303\u2026\u2026\u5145\u573a\u9762\uff0c\u81ea\u7136\u662f\u5145\u7ed9\u522b\u4eba\u770b\u7684\u3002\u54e6\uff0c\u660e\u5929\u8981\u6211\u6d3e\u8fd9\u8f66\u6765\u63a5\u4e8c\u53d4\u60a8\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\u4e86\uff0c\u201d\u53f6\u81fb\u6c11\u542b\u7b11\u62cd\u7740\u5f20\u606a\u7684\u80a9\u8180\uff0c\u201c\u6211\u4eec\u5c31\u4e0d\u7528\u5ba2\u5957\u4e86\uff0c\u5916\u7ecf\u8d38\u90e8\u4e5f\u4e0d\u81f3\u4e8e\u7a77\u5230\u6ca1\u8f66\u53ef\u5750\u3002\u201d</p><p>\u3000\u3000\u4ece\u53f6\u81fb\u6c11\u5bb6\u51fa\u6765\uff0c\u5f20\u606a\u5c31\u4e0e\u5085\u4fca\u5750\u4e0a\u5317\u4eac\u5206\u516c\u53f8\u65b0\u8d2d\u5165\u7684\u51ef\u8fea\u62c9\u514b\u53bb\u673a\u573a\u63a5\u9648\u4fe1\u751f\uff1b\u6bd4\u8d77\u539f\u5148\u5317\u4eac\u5206\u516c\u53f8\u7684\u90a3\u8f86\u6851\u5854\u7eb3\uff0c\u7684\u786e\u6709\u5929\u58e4\u4e4b\u522b\uff0c\u53ea\u662f\u5f20\u606a\u5750\u5728\u8f66\u91cc\uff0c\u5012\u6ca1\u6709\u611f\u89c9\u5230\u7279\u522b\u7684\u660e\u663e\uff0c\u8fd9\u82b1\u51a0\u52a0\u76fe\u724c\u7684\u8f66\u6807\uff0c\u8fd8\u662f\u592a\u62db\u6447\u4e86\uff0c\u5e78\u597d\u6ca1\u6709\u4e70\u52a0\u957f\u578b\u7684\u5e1d\u5a01\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30003\u67088\u65e5\uff0c\u5987\u5973\u8282\u8fd9\u4e00\u5929\uff0c\u5317\u4eac\u4e2d\u5173\u6751\u79d1\u6280\u56ed\u5185\uff0c\u5728\u6d77\u6dc0\u4e2d\u8857\u897f\u9996\uff0c\u5728\u4e8c\u5341\u7c73\u9ad8\u8239\u5e06\u5f62\u72ec\u7acb\u697c\u6807\u8fd8\u6ca1\u6709\u7ad6\u7acb\u3001\u524d\u5ead\u5e7f\u573a\u8fd8\u6ca1\u6709\u6539\u9020\u597d\u7684\u5341\u516b\u5c42\u9ad8\u697c\u524d\uff0c\u7231\u8fbe\u7535\u5b50\u4e0eTI\u9080\u8bf7\u5317\u4eac\u5f53\u5730\u7684\u5a92\u4f53\u53d1\u5e03\u8054\u5408\u58f0\u660e\uff1a\u9648\u4fe1\u751f\u63d0\u524d\u7ed3\u675f\u4ed6\u5728TI\u7684\u4efb\u671f\uff0c\u62c5\u4efb\u7231\u8fbe\u7535\u5b50\u6bcd\u516c\u53f8\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u4e00\u804c\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8d70\u5230\u62ff\u7740\u8bb2\u8bdd\u7a3f\u8d70\u5230\u53f0\u524d\uff0c\u773c\u76ae\u770b\u4e0a\u53bb\u90fd\u6709\u4e9b\u6d6e\u80bf\uff0c\u6709\u4e9b\u75b2\u60eb\uff0c\u8fd8\u6ca1\u6709\u5012\u8fc7\u65f6\u5dee\u6765\uff0c\u4e0d\u8fc7\u795e\u60c5\u9887\u4e3a\u632f\u594b\uff0c\u4ed6\u5728\u955c\u5934\u524d\u5148\u603b\u7ed3\u5728TI\u4e94\u5e74\u4efb\u671f\u7684\u7ecf\u5386\uff0c\u53c8\u5bf9\u5728\u7231\u8fbe\u96c6\u56e2\u7684\u4efb\u804c\u8868\u8fbe\u51fa\u65e0\u9650\u7684\u671f\u5f85\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u8fc7\u53bb\u4e24\u5e74\u91cc\uff0cTI\u4e0e\u7231\u8fbe\u5728\u789f\u673a\u9886\u57df\u6709\u7740\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u3001\u673a\u82af\u5de5\u5382\u4ee5\u53ca\u5728\u5f00\u53d1\u65b0\u4e00\u4ee3\u5f71\u789f\u673a\u65f6\u7684\u5408\u4f5c\uff0c\u90fd\u662f\u4e24\u5bb6\u516c\u53f8\u8fd9\u4e24\u5e74\u6765\u5728\u6280\u672f\u4e0a\u3001\u751f\u4ea7\u4e0a\u5bc6\u5207\u5408\u4f5c\u7684\u660e\u8bc1\uff0c\u6211\u76f8\u4fe1\u4e24\u5bb6\u516c\u53f8\u4ee5\u540e\u4f1a\u6709\u7740\u66f4\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u6211\u5e0c\u671b\u6211\u80fd\u53d1\u6325\u94ae\u5e26\u4f5c\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u7684\u8bb2\u8bdd\u91cc\u6709\u7740\u660e\u663e\u7684\u6697\u793a\uff0c\u4ed6\u7684\u8bdd\u97f3\u521a\u843d\uff0c\u5230\u573a\u7684\u5a92\u4f53\u8bb0\u8005\u5c31\u4e3e\u8d77\u597d\u51e0\u53ea\u624b\u95ee\u4ed6\u7231\u8fbe\u4e0eTI\u4e0b\u4e00\u6b65\u7684\u5408\u4f5c\u5230\u5e95\u4f1a\u662f\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u6700\u521d\u5408\u8d44\u7b79\u5efa\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\uff0c\u5728\u4e1a\u5185\u5f15\u8d77\u8f70\u52a8\u8fd8\u6709\u9650\uff0c\u6bd5\u7adf\u5f53\u65f6\u5e76\u6ca1\u6709\u5411\u5916\u754c\u516c\u5e03\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u7684\u7814\u7a76\u9886\u57df\uff1b\u673a\u82af\u5de5\u5382\u662f\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u5728\u7cbe\u5bc6\u5149\u7535\u5668\u4ef6\u5236\u9020\u4e0a\u7684\u4e00\u4e2a\u5927\u7684\u7a81\u7834\uff0c\u76f4\u63a5\u786e\u5b9a\u7231\u8fbe\u7535\u5b50\u5728\u789f\u673a\u5e02\u573a\u65e0\u4eba\u80fd\u64bc\u52a8\u7684\u5730\u4f4d\uff0c\u751a\u81f3\u76f4\u63a5\u538b\u8fc7\u540c\u65f6\u671f\u592e\u89c6\u6807\u738b\u79d1\u738b\u7535\u5668\u7684\u5149\u8292\uff0c\u53ef\u89c1\u5f71\u54cd\u529b\u4e4b\u5927\uff1b\u4e00\u6708\u521d\u7684\u5408\u4f5c\u76f4\u63a5\u5bfc\u81f4\u5f71\u789f\u673a\u7684\u66f4\u65b0\u6362\u4ee3\u3002</p><p>\u3000\u3000\u5a92\u4f53\u8bb0\u8005\u5f53\u7136\u5bf9\u7231\u8fbe\u4e0eTI\u4e0b\u4e00\u6b65\u53ef\u80fd\u7684\u5bc6\u5207\u5408\u4f5c\u597d\u5947\u5fc3\u5207\uff0c\u6240\u8c13\u72b9\u62b1\u7435\u7436\u534a\u906e\u9762\uff0c\u4e0d\u5c06\u65b0\u95fb\u5a92\u4f53\u7684\u80c3\u53e3\u9493\u8d77\u6765\uff0c\u5982\u4f55\u8ba9\u4ed6\u4eec\u5e2e\u7740\u5927\u9020\u58f0\u52bf\uff1f\u9648\u4fe1\u751f\u8bf4\u4e86\u4e00\u4e9b\u4f3c\u662f\u800c\u975e\u7684\u8bdd\u5c06\u95ee\u9898\u642a\u585e\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u9664\u4e86\u8fd9\u4e2a\u4e4b\u5916\uff0c\u5a92\u4f53\u8bb0\u8005\u6700\u5173\u5fc3\u9648\u4fe1\u751f\u5230\u7231\u8fbe\u5c31\u4efb\u540e\u7684\u85aa\u8d44\u5f85\u9047\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u62c5\u4efbTI\u4e2d\u56fd\u533a\u603b\u88c1\u4e94\u5e74\uff0c\u542b\u671f\u6743\u6fc0\u52b1\u5728\u5185\u7684\u85aa\u8d44\u603b\u989d\u9ad8\u8fbe\u56db\u5343\u4e07\u4e4b\u591a\uff0c\u7231\u8fbe\u96c6\u56e2\u4f5c\u4e3a\u56fd\u5185\u4e00\u5bb6\u80a1\u6743\u5173\u7cfb\u590d\u6742\u7684\u6c11\u8425\u4f01\u4e1a\u80fd\u591f\u652f\u4ed8\u9648\u4fe1\u751f\u6bcf\u5e74\u81f3\u5c11\u516b\u767e\u4e07\u7684\u9ad8\u85aa\u5417\uff1f\u8fd9\u81ea\u7136\u662f\u5a92\u4f53\u8bb0\u8005\u8feb\u5207\u60f3\u77e5\u9053\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u6ca1\u6709\u6b63\u9762\u56de\u7b54\u5a92\u4f53\u8bb0\u8005\u7684\u95ee\u9898\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u80fd\u529b\u4e0e\u624d\u5e72\uff0c\u5728\u7231\u8fbe\u96c6\u56e2\u4f1a\u5f97\u5230\u5e94\u6709\u7684\u80af\u5b9a\uff0c\u6211\u73b0\u5728\u53ea\u662f\u6709\u4e9b\u62c5\u5fc3\u6211\u7684\u80fd\u529b\u4e0e\u624d\u5e72\u80fd\u4e0d\u80fd\u5bf9\u5f97\u8d77\u7231\u8fbe\u96c6\u56e2\u652f\u4ed8\u7ed9\u6211\u7684\u85aa\u8d44\u2026\u2026\u5f53\u7136\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u5728\u6d77\u5916\u7559\u5b66\u5341\u5e74\u7684\u5b66\u5b50\uff0c\u6211\u516b\u4e5d\u5e74\u56de\u5230\u56fd\u5185\uff0c\u5185\u5fc3\u4e00\u76f4\u6e34\u671b\u80fd\u4e3a\u56fd\u5185\u7684\u4f01\u4e1a\u670d\u52a1\uff0c\u6211\u8981\u611f\u8c22\u7231\u8fbe\u96c6\u56e2\u7ed9\u6211\u8fd9\u4e2a\u673a\u4f1a\uff0c\u6211\u5e0c\u671b\u5728\u82e5\u5e72\u5e74\u540e\uff0c\u6211\u7684\u786e\u4e3a\u56fd\u5185\u7684\u7535\u5b50\u5de5\u4e1a\u53d1\u5c55\u505a\u8fc7\u4e00\u4e9b\u5b9e\u5b9e\u5728\u5728\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u9648\u4fe1\u751f\u8fd8\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u4e0e\u5609\u91cc\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\u7b7e\u7f72\u534f\u8bae\uff0c\u6574\u4f53\u8d2d\u5165\u5a92\u4f53\u955c\u5934\u524d\u8fd9\u5ea7\u5341\u516b\u5c42\u9ad8\u3001\u6c14\u6d3e\u975e\u51e1\u7684\u5929\u84dd\u8272\u5927\u697c\u4f5c\u4e3a\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u53d1\u5c55\u57fa\u5730\u3002</p><p>\u3000\u3000\u51fa\u5e2d\u4eca\u5929\u4eea\u5f0f\u5916\u7ecf\u8d38\u90e8\u526f\u90e8\u957f\u7684\u53f6\u81fb\u6c11\u5728\u5a92\u4f53\u524d\u9732\u8fc7\u9762\u4e4b\u540e\uff0c\u5c31\u8eb2\u5230\u5e55\u540e\u4e0e\u5f20\u606a\u8bf4\u8bdd\uff1a\u201c\u6628\u5929\uff0c\u4f60\u8ddf\u6211\u53d1\u7684\u7262\u9a9a\uff0c\u6211\u6709\u60f3\u8fc7\uff0c\u4e00\u4e2a\u552f\u7ecf\u6d4e\u8bba\uff0c\u6211\u770b\u8fd9\u70b9\u884c\u4e0d\u5927\u901a\uff0c\u9700\u8981\u66f4\u79d1\u5b66\u7684\u53d1\u5c55\u89c2\uff1b\u8fd8\u6709\u4e00\u4e2a\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u5730\u4f4d\u7684\u95ee\u9898\uff0c\u8fd9\u4e00\u70b9\uff0c\u4e2d\u592e\u5df2\u7ecf\u6709\u8ba8\u8bba\uff0c\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u5c31\u516c\u5f00\u63d0\u51fa\uff1a\u516c\u4e0e\u975e\u516c\u8981\u5e73\u7b49\u7ade\u4e89\u3001\u76f8\u4e92\u4fc3\u8fdb\uff0c\u4f46\u662f\u8fd9\u4e00\u70b9\u8fd8\u6ca1\u6709\u843d\u5b9e\u5230\u5177\u4f53\u7684\u4ea7\u4e1a\u653f\u7b56\u4e0a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4ece\u9526\u6e56\u4e0e\u8054\u4fe1\u5f00\u59cb\uff1f\u201d</p></article>"}}}]);
