(window.webpackJsonp=window.webpackJsonp||[]).push([[696],{719:function(p){p.exports={chapterTitle:null,title:"\u7b2c695\u7ae0 \u8f66\u4e2d\u516d\u5c0f\u65f6",content:"<article><h2>\u7b2c695\u7ae0 \u8f66\u4e2d\u516d\u5c0f\u65f6</h2><p>\u3000\u3000\u897f\u9910\u5385\u8ddd\u5e9c\u5929\u5927\u53a6\u4e5f\u53ea\u6709\u56db\u4e94\u5206\u949f\u7684\u8def\uff0c\u5f20\u606a\u5c06\u8f66\u9a76\u5230\u5e9c\u5929\u5927\u53a6\u524d\uff0c\u60f3\u95ee\u9648\u9759\u662f\u4e0d\u662f\u8981\u4e0b\u8f66\u56de\u53bb\u7761\u89c9\uff0c\u5728\u897f\u9910\u5385\u8c08\u8bdd\u65f6\uff0c\u5979\u5c31\u6709\u5728\u6253\u54c8\u6b20\uff0c\u773c\u76ae\u5b50\u677e\u8f6f\uff0c\u5c31\u5dee\u4e00\u70b9\u5728\u897f\u9910\u5385\u9910\u684c\u4e0a\u8db4\u4e0b\u6765\u7761\u8fc7\u53bb\uff0c\u5f20\u606a\u4fa7\u8fc7\u8138\u5374\u770b\u89c1\u9648\u9759\u8fd9\u4f1a\u513f\u5934\u6b6a\u5728\u4e00\u8fb9\u5df2\u7ecf\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u770b\u5979\u8138\u4e0a\u6709\u7740\u9759\u8c27\u5165\u7761\u7684\u5a34\u7f8e\uff0c\u4e0d\u5fcd\u5fc3\u63a8\u9192\u5979\uff0c\u6781\u5ea6\u6e34\u7761\u4e4b\u65f6\uff0c\u7ed9\u63a8\u9192\u7684\u611f\u89c9\u662f\u5f88\u96be\u53d7\u7684\uff0c\u5fc3\u60f3\u8fde\u7eed\u7ed9\u79d1\u738b\u9ad8\u79d1\u52a0\u4e0a\u4e24\u9053\u5149\u73af\u2014\u2014\u83b7\u5f97\u6570\u5b57\u624b\u673a\u6280\u672f\u5168\u90e8\u6388\u6743\u6210\u4e3a\u8fd1\u671f\u5185\u552f\u4e00\u7684\u624b\u673a\u8d34\u724c\u5546\u3001\u6210\u4e3a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u4e03\u5bb6\u4ee3\u8868\u4e4b\u4e00\u2014\u2014\u8fd9\u6bb5\u65f6\u95f4\u91cc\u9648\u9759\u538b\u529b\u662f\u5927\u4e86\u5f88\u591a\uff0c\u770b\u5230\u8fd9\u4e48\u4e00\u4e2a\u7f8e\u4eba\u513f\u52b3\u788c\u6210\u5982\u6b64\u6194\u60b4\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u591a\u5c11\u751f\u51fa\u4e9b\u601c\u60dc\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u5e73\u7f13\u7684\u9a76\u5165\u6cb3\u6ee8\u7eff\u5316\u5e26\u95f4\u505c\u4e86\u4e0b\u6765\uff0c\u6253\u7b97\u7b49\u5979\u81ea\u7136\u7761\u9192\u518d\u8bf4\u3002</p><p>\u3000\u3000\u9648\u9759\u517b\u6210\u826f\u597d\u7684\u4e60\u60ef\uff0c\u5373\u4f7f\u5728\u5e02\u5185\u8f66\u901f\u4e0d\u5feb\uff0c\u4e5f\u4f1a\u5c06\u5b89\u5168\u5e26\u7cfb\u4e0a\u3002\u5f20\u606a\u770b\u7740\u5b89\u5168\u5e26\u52d2\u5728\u9648\u9759\u7684\u80f8\u524d\uff0c\u5c06\u80f8\u90e8\u52d2\u51fa\u73b0\u4e2a\u9ad8\u8038\u4e30\u6ee1\u7684\u5f62\u72b6\u6765\uff0c\u501f\u7740\u900f\u8fdb\u8f66\u7a97\u6765\u7684\u8def\u706f\u5149\u770b\u7740\u9648\u9759\u7684\u9f3b\u606f\u6709\u4e9b\u6025\u4fc3\uff0c\u5fc3\u60f3\u6216\u8bb8\u662f\u5b89\u5168\u5e26\u52d2\u7740\u4e86\uff0c\u8f7b\u624b\u8f7b\u811a\u5e2e\u7740\u5c06\u5b89\u5168\u5e26\u89e3\u5f00\uff0c\u60f3\u8ba9\u9648\u9759\u7761\u66f4\u8212\u670d\u4e9b\u3002</p><p>\u3000\u3000\u9648\u9759\u9192\u4e86\uff0c\u610f\u8bc6\u5230\u5f20\u606a\u8eab\u5b50\u53cd\u4fa7\u8fc7\u6765\uff0c\u4e00\u53ea\u80f3\u818a\u5c31\u6a2a\u5728\u80f8\u524d\uff0c\u5dee\u4e00\u70b9\u5c31\u8981\u8d34\u4e0a\u6765\uff0c\u8fd8\u80fd\u611f\u89c9\u5230\u5f20\u606a\u707c\u70ed\u7684\u9f3b\u606f\u6251\u5728\u8033\u6839\u4e0a\u3002\u9648\u9759\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u662f\u907f\u514d\u5b89\u5168\u5e26\u4e00\u4e0b\u5b50\u7f29\u56de\u53bb\u7684\u52a8\u9759\u592a\u5927\u60ca\u9192\u81ea\u5df1\u8fd9\u624d\u4e00\u53ea\u624b\u6293\u4f4f\u5b89\u5168\u5e26\u6162\u6162\u7684\u8ba9\u5b89\u5168\u5e26\u7f29\u8fdb\u53bb\u3002\u6293\u7740\u5b89\u5168\u5e26\u7684\u624b\u81ea\u7136\u4e5f\u8981\u8ddf\u7740\u6162\u6162\u7684\u5230\u8f66\u53a2\u53e6\u4e00\u4fa7\uff0c\u5f20\u606a\u7684\u8eab\u5b50\u53cd\u4fa7\u8fc7\u6765\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u4f46\u662f\u8fd9\u4e00\u77ac\u95f4\u9648\u9759\u5fc3\u795e\u4e00\u51db\uff0c\u53ea\u5f53\u5f20\u606a\u60f3\u8d81\u7740\u81ea\u5df1\u7761\u7740\u4e86\u505a\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6c57\u6bdb\u90fd\u7d27\u5f20\u7684\u7ad6\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u4ed6\u4f1a\u505a\u4ec0\u4e48\uff1f\u9648\u9759\u5fc3\u91cc\u5f88\u7d27\u5f20\uff0c\u4e5f\u4e0d\u6653\u5f97\u662f\u4e0d\u662f\u8981\u5047\u88c5\u9192\u8fc7\u6765\u7684\u6837\u5b50\u5236\u6b62\u4ed6\uff0c\u53c8\u60f3\u8ba9\u4ed6\u543b\u4e00\u4e0b\u6216\u8005\u62b1\u4e00\u4e0b\u4e5f\u6ca1\u6709\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u60f3\uff0c\u5fc3\u91cc\u5c31\u6709\u4e9b\u6e34\u671b\u8d77\u6765\u4e86\u3002\u521a\u624d\u90fd\u4e0d\u6653\u5f97\u600e\u4e48\u5c31\u7761\u7740\u4e86\uff0c\u771f\u662f\u5934\u75bc\u554a\uff0c\u9677\u5165\u8fd9\u79cd\u8fdb\u9000\u4e0d\u5f97\u7684\u5883\u9047\uff0c\u4ed6\u8981\u662f\u543b\u4e00\u4e0b\u6216\u8005\u62b1\u4e00\u4e0b\u8fd8\u4e0d\u6ee1\u8db3\u600e\u4e48\u529e\uff1f</p><p>\u3000\u3000\u4ed6\u4f1a\u6478\u81ea\u5df1\u7684\u80f8\u90e8\u5417\uff1f\u8fd9\u5bb6\u4f19\u5728\u9910\u5385\u91cc\u5c31\u65f6\u4e0d\u65f6\u77a5\u90a3\u91cc\u770b\u3002\u8fd9\u4e48\u60f3\u7740\uff0c\u80f8\u524d\u7684\u84d3\u857e\u7adf\u7136\u53d1\u8d77\u75d2\u6765\uff0c\u8fd9\u5bb6\u4f19\u624b\u5c31\u5728\u81ea\u5df1\u80f8\u524d\uff0c\u4e00\u5b9a\u662f\u60f3\u6478\u90a3\u91cc\u3002\u9648\u9759\u5fc3\u91cc\u7f9e\u602f\u4e0d\u5b89\uff0c\u600e\u4e48\u53ef\u4ee5\u6709\u8fd9\u79cd\u671f\u5f85\u7684\u5fc3\u60c5\u5462\uff1f\u8d8a\u662f\u8981\u6291\u5236\u5fc3\u91cc\u7684\u5ff5\u5934\uff0c\u80f8\u524d\u7684\u4e24\u70b9\u84d3\u857e\u8d8a\u75d2\u75d5\u96be\u6291\uff0c\u8eab\u4f53\u5185\u4e5f\u6291\u7740\u4e00\u56e2\u96be\u4ee5\u660e\u72b6\u7684\u706b\u5728\u80f8\u8179\u95f4\u6d41\u7a9c\u3002</p><p>\u3000\u3000\u5374\u662f\u5f20\u606a\u60f3\u5c06\u5b89\u5168\u5e26\u653e\u56de\u53bb\u4e00\u76f4\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u907f\u514d\u78b0\u5230\u9648\u9759\u7684\u8eab\u4f53\uff0c\u770b\u5230\u9648\u9759\u5fae\u5fae\u6f6e\u7ea2\u7684\u8138\u5e9e\u5728\u5fae\u5f31\u7684\u8def\u706f\u5149\u4e0b\u5f02\u6837\u7684\u5a07\u5a9a\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u8d2a\u5fc3\u7684\u591a\u770b\u4e24\u773c\uff0c\u5f53\u7136\u5149\u7ebf\u8fd9\u4e48\u6697\u4e5f\u770b\u4e0d\u771f\u5207\uff0c\u5012\u662f\u624b\u91cc\u7684\u52a8\u4f5c\u5c31\u66f4\u6162\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u80fd\u611f\u89c9\u5230\u5f20\u606a\u5728\u51dd\u89c6\u7684\u81ea\u5df1\uff0c\u90a3\u79cd\u76ee\u5149\u51dd\u671b\u6765\u7684\u538b\u529b\uff0c\u51ed\u7740\u5973\u6027\u7684\u76f4\u89c9\u5c31\u80fd\u611f\u89c9\u5230\uff0c\u8fd8\u6709\u90a3\u8ba9\u4eba\u5fc3\u601d\u51cc\u4e71\u7684\u707c\u70ed\u9f3b\u606f\uff0c\u7b80\u76f4\u5c31\u662f\u8981\u8ba9\u4eba\u610f\u4e71\u60c5\u8ff7\u3002</p><p>\u3000\u3000\u9648\u9759\u6253\u5b9a\u4e3b\u610f\u4e0d\u9192\u8fc7\u6765\uff1a\u4e0d\u9192\u8fc7\u6765\uff0c\u8fd9\u5bb6\u4f19\u9876\u591a\u52a8\u624b\u52a8\u811a\u7684\u6478\u51e0\u4e0b\uff1b\u8981\u662f\u9192\u8fc7\u6765\uff0c\u8fd9\u5bb6\u4f19\u6076\u5411\u80c6\u8fb9\u751f\uff0c\u8981\u505a\u5176\u4ed6\u66f4\u8fc7\u5206\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u662f\u6323\u624e\u597d\u8fd8\u662f\u4e0d\u6323\u624e\u597d\uff1f\u7b49\u4e86\u7247\u523b\uff0c\u611f\u89c9\u5f20\u606a\u8fd9\u5bb6\u4f19\u975e\u4f46\u6ca1\u6709\u4e0b\u624b\u6478\u81ea\u5df1\uff0c\u624b\u8fd8\u5728\u5f80\u56de\u7f29\u3002\u8fd9\u5bb6\u4f19\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u80c6\u5c0f\u4e86\uff1f\u80f8\u524d\u7684\u75d2\u75d5\u8d8a\u6765\u8d8a\u96be\u8010\uff0c\u8fd9\u5bb6\u4f19\u600e\u4e48\u53ef\u4ee5\u80c6\u5c0f\u9000\u7f29\u5462\uff1f\u9648\u9759\u5fc3\u91cc\u6697\u6068\u3002</p><p>\u3000\u3000\u9648\u9759\u5fc3\u91cc\u60f3\u7b97\u4e86\uff0c\u8eab\u5b50\u633a\u4e86\u633a\uff0c\u7b49\u7740\u8f6c\u4e00\u4e0b\u8eab\u5c31\u5047\u88c5\u9192\u8fc7\u6765\uff0c\u4e5f\u514d\u5f97\u6233\u7a7f\u8fd9\u5bb6\u4f19\u7684\u5fc3\u601d\u8ba9\u4ed6\u4e0b\u4e0d\u4e86\u53f0\uff0c\u6ca1\u60f3\u5230\u8eab\u5b50\u8fd9\u4e00\u633a\uff0c\u80f8\u90e8\u5c31\u64e6\u4e0a\u5f20\u606a\u5f80\u56de\u7f29\u7684\u624b\u638c\uff0c\u80fd\u611f\u89c9\u5230\u5f20\u606a\u7ea4\u7ec6\u7684\u624b\u6307\u9694\u7740\u4e00\u5c42\u7ed2\u7ebf\u8863\u64e6\u8fc7\u80f8\u524d\u90a3\u4e24\u70b9\u84d3\u857e\uff0c\u5fc3\u95f4\u7684\u75d5\u8ff9\u5728\u90a3\u4e00\u77ac\u95f4\u6e32\u6cc4\u800c\u53bb\uff0c\u8eab\u4f53\u7684\u706b\u9661\u7136\u53d8\u6210\u4e00\u56e2\u95ea\u7535\u5728\u4f53\u5185\u4e71\u7a9c\uff0c\u662f\u90a3\u79cd\u65e0\u6cd5\u8a00\u55bb\u7684\u5947\u5999\u4e4b\u611f\u3002</p><p>\u3000\u3000\u8fd9\u5bb6\u4f19\u7adf\u7136\u6478\u81ea\u5df1\u7684\u80f8\uff0c\u9648\u9759\u5f53\u7136\u4e0d\u6562\u518d\u9192\u8fc7\u53bb\uff0c\u8fd8\u8981\u6781\u529b\u8ba9\u81ea\u5df1\u7684\u547c\u5438\u4e0d\u8981\u592a\u6025\u4fc3\uff0c\u5fc3\u91cc\u60f3\u7ed9\u8fd9\u5bb6\u4f19\u5360\u4e86\u5927\u4fbf\u5b9c\u4e86\uff0c\u4ed6\u8fd8\u4f1a\u4e0d\u4f1a\u60f3\u7740\u505a\u5176\u4ed6\u4e8b\u60c5\uff1f</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u9648\u9759\u5750\u5728\u8f66\u4e0a\u7761\u8fd8\u4e71\u52a8\u8eab\u5b50\uff0c\u624b\u6307\u64e6\u4e86\u9648\u9759\u4e30\u6ee1\u7684\u80f8\u90e8\u4e00\u4e0b\u3002\u9648\u9759\u91cc\u9762\u7a7f\u7740\u5185\u8863\u8fd8\u6709\u7ed2\u7ebf\u8863\uff0c\u5f20\u606a\u65e0\u610f\u8bc6\u7684\u78b0\u4e86\u4e00\u4e0b\uff0c\u53c8\u4e0d\u662f\u63e1\u4e0a\u53bb\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u4ec0\u4e48\u7279\u522b\u7684\u611f\u89c9\uff0c\u8fd8\u62c5\u5fc3\u9648\u9759\u8981\u662f\u9192\u8fc7\u6765\u4f1a\u4ea7\u751f\u8bef\u4f1a\u4e0d\u597d\u89e3\u91ca\u5462\uff0c\u89c1\u9648\u9759\u8f6c\u4e86\u4e00\u4e0b\u8eab\u5b50\u53c8\u7761\u8fc7\u53bb\uff0c\u5c31\u653e\u5fc3\u4e86\uff0c\u8c03\u4f4e\u5ea7\u6905\u5b50\uff0c\u770b\u7740\u8f66\u7a97\u524d\u4f4e\u5782\u7684\u7709\u6708\uff0c\u9694\u7740\u8f66\u7a97\u73bb\u7483\u8fd8\u80fd\u542c\u89c1\u4e0d\u8fdc\u5904\u758f\u6e2f\u6cb3\u6f7a\u6f7a\u7684\u6d41\u6c34\u58f0\u3002</p><p>\u3000\u3000\u9648\u9759\u7b49\u4e86\u7247\u523b\u4e0d\u89c1\u5f20\u606a\u518d\u6709\u522b\u7684\u52a8\u9759\uff0c\u5fc3\u91cc\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u4ed6\u771f\u60f3\u8981\u505a\u5176\u4ed6\u4e8b\u60c5\uff0c\u8be5\u600e\u4e48\u62d2\u7edd\u4ed6\uff1f\u8fd9\u5bb6\u4f19\u771f\u662f\u7684\uff0c\u660e\u660e\u6709\u8bb8\u601d\u8fd9\u6837\u5343\u5a07\u767e\u5a9a\u7684\u5973\u5b50\u76f8\u4f34\uff0c\u8fd8\u6709\u5510\u5a67\u8fd9\u4e48\u5a07\u67d4\u53ef\u7231\u7684\u5973\u53cb\uff0c\u600e\u4e48\u8fd8\u53ef\u4ee5\u5bf9\u5176\u4ed6\u5973\u4eba\u52a8\u5fc3\u601d\uff1f\u8ba9\u81ea\u5df1\u600e\u4e48\u62d2\u7edd\u624d\u597d\uff01</p><p>\u3000\u3000\u9648\u9759\u5f88\u60f3\u6df1\u6df1\u7684\u53f9\u4e00\u53e3\u6c14\uff0c\u4f46\u662f\u4e0d\u60f3\u8ba9\u5f20\u606a\u77e5\u9053\u81ea\u5df1\u521a\u624d\u5df2\u7ecf\u9192\u4e86\uff0c\u8fd8\u662f\u5c06\u90a3\u53e3\u6c14\u538b\u7740\u7f13\u7f13\u7684\u547c\u51fa\u53bb\uff0c\u9661\u7136\u611f\u89c9\u5230\u80f8\u524d\u7684\u5b89\u5168\u5e26\u7ed9\u89e3\u5f00\u4e86\uff0c\u60f3\u8d77\u4e0a\u8f66\u524d\u7cfb\u4e0a\u5b89\u5168\u5e26\u4e86\uff0c\u96be\u9053\u8bf4\u8fd9\u5bb6\u4f19\u521a\u624d\u662f\u5e2e\u81ea\u5df1\u89e3\u5b89\u5168\u5e26\u8ba9\u81ea\u5df1\u7761\u5f97\u66f4\u8212\u670d\u4e00\u4e9b\uff1f</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u9648\u9759\u60f3\u660e\u767d\u4e86\uff0c\u521a\u624d\u660e\u660e\u662f\u81ea\u5df1\u8eab\u5b50\u633a\u4e0a\u53bb\u7ed9\u8fd9\u5bb6\u4f19\u6478\u561b\uff0c\u5fc3\u95f4\u77ac\u65f6\u7ed9\u7f9e\u8d67\u6d8c\u6ee1\uff0c\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\uff01\u53ea\u662f\u90a3\u77ac\u95f4\u76f8\u89e6\u7684\u611f\u89c9\u592a\u7f8e\u5999\uff0c\u4e24\u817f\u95f4\u4e5f\u6f6e\u4e4e\u4e4e\u7684\uff0c\u4e0d\u7531\u7684\u8ba9\u4eba\u60f3\u8d77\u90a3\u4e2a\u6625\u5fc3\u51cc\u4e71\u7684\u68a6\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9648\u9759\u8eab\u5b50\u53c8\u52a8\u4e86\u4e00\u4e0b\uff0c\u8f7b\u58f0\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u9192\u4e86\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u4e5f\u987a\u52bf\u5047\u88c5\u9192\u8fc7\u6765\uff0c\u7d20\u624b\u63a9\u7740\u7ea2\u5507\uff0c\u53d1\u51fa\u5fae\u8bb6\u7684\u53eb\u58f0\uff1a\u201c\u554a\uff0c\u6211\u521a\u521a\u5728\u8f66\u91cc\u7761\u7740\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u8f66\u5b50\u521a\u51fa\u9910\u5385\u7684\u505c\u8f66\u573a\uff0c\u4f60\u5c31\u7761\u7740\u4e86\uff0c\u4e0d\u60f3\u53eb\u9192\u4f60\uff0c\u5c31\u5c06\u8f66\u505c\u5230\u7eff\u5316\u5e26\u91cc\uff0c\u8fd9\u4e9b\u5929\u7d2f\u574f\u4e86\u5427\uff1f\u201d\u8f66\u505c\u5728\u79bb\u8def\u706f\u7a0d\u8fdc\u7684\u5730\u65b9\uff0c\u5728\u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u5f20\u606a\u4e5f\u770b\u4e0d\u771f\u5207\u9648\u9759\u8138\u4e0a\u7684\u795e\u8272\uff0c\u8138\u8272\u6f6e\u7ea2\uff0c\u4e5f\u53ea\u5f53\u5149\u7ebf\u6697\u7684\u7f18\u6545\uff0c\u6ca1\u5f80\u522b\u5904\u60f3\uff0c\u53ea\u89c9\u5f97\u5979\u7684\u7738\u5b50\u5728\u5e7d\u6697\u7684\u5149\u91cc\u989d\u5916\u7684\u660e\u4eae\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u60f3\u6210\u4e3a\u9526\u6e56\u7684\u2018\u68cb\u5b50\u2019\uff0c\u4e5f\u8981\u8db3\u591f\u7684\u52aa\u529b\u624d\u884c\uff0c\u82e6\u4e9b\uff0c\u7d2f\u4e9b\uff0c\u4e5f\u662f\u5f53\u7136\u7684\u2026\u2026\u201d\u9648\u9759\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\u53c8\u8981\u6211\u8d1f\u62c5\u4ec0\u4e48\u8d23\u4efb\u5427\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u884c\uff0c\u8981\u4e0d\u8ba9\u4f60\u5728\u8f66\u91cc\u518d\u7761\u4e00\u4f1a\u513f\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u5417\uff1f\u201d\u9648\u9759\u770b\u7740\u5f20\u606a\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u7f9e\u6da9\u7684\u8bf4\uff0c\u201c\u8fd9\u51e0\u5929\u56de\u516c\u5bd3\u603b\u7761\u4e0d\u597d\uff0c\u5f88\u591a\u4e8b\u60c5\u60c5\u4e0d\u81ea\u7981\u7684\u5f80\u8111\u5b50\u91cc\u94bb\uff0c\u53ef\u80fd\u662f\u4eba\u592a\u4ea2\u594b\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u524d\u4e24\u5e74\uff0c\u9526\u6e56\u6b65\u6b65\u90fd\u5982\u5c65\u8584\u51b0\uff0c\u6211\u4e5f\u5f88\u5c11\u80fd\u7761\u597d\uff0c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u8fd8\u5c31\u6709\u540c\u75c5\u76f8\u601c\u7684\u611f\u89c9\u4e86\uff0c\u505a\u5973\u5f3a\u4eba\u4e0d\u5bb9\u6613\u554a\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u53c8\u503e\u8fc7\u8eab\u624b\u6a2a\u8fc7\u53bb\u5e2e\u9648\u9759\u5c06\u5ea7\u6905\u9760\u80cc\u8c03\u4f4e\uff0c\u201c\u8ba9\u4f60\u518d\u7761\u4e00\u4f1a\u513f\uff0c\u53cd\u6b63\u8fd9\u4f1a\u513f\u6211\u4e5f\u6ca1\u6709\u5730\u65b9\u53ef\u4ee5\u6253\u53d1\u65f6\u95f4\u3002\u201d</p><p>\u3000\u3000\u4e0e\u9648\u9759\u5403\u665a\u996d\u65f6\u8fd8\u65e9\uff0c\u5403\u8fc7\u665a\u996d\u8fd8\u4e0d\u5230\u516b\u70b9\u949f\uff0c\u9648\u9759\u624d\u7761\u4e86\u5341\u5206\u949f\u4e0d\u5230\u3002\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\u627e\u4e0a\u5934\u6765\uff0c\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u662f\u65e0\u6240\u4e8b\u4e8b\uff0c\u5fc3\u60f3\u8001\u5988\u5927\u6982\u8fd8\u8ddf\u5c0f\u8205\u4e00\u5bb6\u4e0e\u738b\u5f69\u73b2\u6bcd\u5973\u5750\u4e00\u8d77\u5520\u5bb6\u5e38\u5427\uff0c\u4ed6\u624d\u4e0d\u60f3\u8fd9\u65f6\u5019\u53bb\u51d1\u4ec0\u4e48\u70ed\u95f9\u3002\u8fd9\u4e24\u5929\uff0c\u665a\u6674\u4f4f\u5728\u5b66\u6821\u90a3\u8fb9\u7684\u623f\u5b50\u91cc\uff0c\u627e\u5176\u4ed6\u4eba\u4e5f\u4e0d\u5408\u9002\uff0c\u5927\u591a\u6570\u4eba\uff0c\u8fd9\u65f6\u5019\u4e0d\u662f\u5728\u9152\u5e2d\u4e0a\uff0c\u5c31\u662f\u521a\u79bb\u5f00\u9152\u5e2d\uff0c\u8fd8\u4e0d\u5982\u5728\u6cb3\u8fb9\u7eff\u5316\u5e26\u4e0e\u9648\u9759\u8fd9\u4e48\u4e00\u4e2a\u4f73\u4eba\u591a\u5446\u4e00\u4f1a\u513f\u3002</p><p>\u3000\u3000\u5f20\u606a\u53cd\u8fc7\u8eab\u4f38\u624b\u8fc7\u6765\u5e2e\u7740\u8c03\u6905\u80cc\uff0c\u8d34\u8fd9\u4e48\u8fd1\uff0c\u611f\u89c9\u7ed9\u4ed6\u62b1\u7740\u4f3c\u7684\uff0c\u9648\u9759\u8eab\u4f53\u91cc\u9690\u9690\u53d1\u70eb\uff0c\u6709\u7740\u60f3\u62e6\u8170\u5c06\u8fd9\u5bb6\u4f19\u6b7b\u6b7b\u62b1\u4f4f\u7684\u51b2\u52a8\u3002\u4f46\u662f\u600e\u4e48\u53ef\u4ee5\u5462\uff1f\u8fd9\u5bb6\u4f19\u6bd4\u81ea\u5df1\u5c0f\u8fd9\u4e48\u591a\uff0c\u53c8\u6709\u8bb8\u601d\u8fd9\u4e48\u5343\u5a07\u767e\u5a9a\u7684\u5973\u4eba\uff0c\u9648\u9759\u624b\u6293\u7740\u5ea7\u6905\u4e24\u8fb9\u7684\u6905\u5957\u8fb9\uff0c\u5fc3\u60f3\u8fd9\u5bb6\u4f19\u8981\u60f3\u5728\u8f66\u91cc\u505a\u4e9b\u4ec0\u4e48\uff0c\u81ea\u5df1\u5f53\u771f\u6ca1\u6709\u529b\u6c14\u53bb\u62d2\u7edd\u4e86\uff0c\u5fc3\u601d\u51cc\u4e71\u7684\u95ed\u4e0a\u773c\u775b\uff0c\u542c\u7740\u5f20\u606a\u4ec0\u4e48\u4e5f\u6ca1\u6709\u505a\u7684\u5750\u56de\u53bb\uff0c\u4f3c\u4e4e\u4e5f\u8eba\u5728\u5ea7\u6905\u4e0a\u5728\u770b\u7740\u8f66\u7a97\u5916\u7684\u591c\u8272\uff0c\u5fc3\u91cc\u53c8\u6709\u4e9b\u5931\u843d\u3002</p><p>\u3000\u3000\u5f88\u662f\u5947\u602a\uff0c\u5fc3\u601d\u867d\u7136\u51cc\u4e71\uff0c\u4f46\u5f88\u5feb\u5c31\u6c89\u9759\u5165\u7761\uff0c\u4e5f\u4e0d\u6653\u5f97\u7761\u4e86\u591a\u4e45\uff0c\u9648\u9759\u518d\u6b21\u9192\u8fc7\u6765\u65f6\uff0c\u8138\u8d34\u7740\u5f20\u606a\u7684\u80a9\u8180\uff0c\u89c1\u5f20\u606a\u773c\u7738\u5b50\u5728\u9eef\u6de1\u7684\u5149\u7ebf\u91cc\u6676\u6676\u53d1\u4eae\uff0c\u6709\u7740\u6e05\u6f88\u7684\u5e7d\u5149\u6d6e\u5728\u773c\u7738\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u9648\u9759\u62ac\u624b\u770b\u4e86\u770b\u96c5\u81f4\u7684\u8367\u5149\u7897\u8868\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e24\u70b9\u4e86\uff0c\u201c\u4f60\u600e\u4e48\u4e0d\u53eb\u9192\u6211\uff1f\u90fd\u803d\u6401\u4f60\u4e94\u516d\u4e2a\u5c0f\u65f6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u803d\u6401\u65f6\u95f4\uff1f\u6211\u8fd9\u4eba\u6700\u662f\u6e05\u95f2\u4e0d\u8fc7\uff0c\u201d\u5f20\u606a\u542f\u52a8\u8f66\u5b50\u9000\u51fa\u6cb3\u8fb9\u7eff\u5316\u5e26\uff0c\u7b11\u7740\u5bf9\u9648\u9759\u8bf4\uff0c\u201c\u4e0b\u56de\u4f60\u8981\u662f\u8fd8\u7761\u4e0d\u7740\uff0c\u6211\u8fd8\u53ef\u4ee5\u514d\u8d39\u966a\u4f60\uff0c\u73b0\u5728\u9001\u4f60\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u4e0d\u6653\u5f97\u600e\u4e48\u56de\u5e94\u8fd9\u53e5\u8bdd\uff0c\u4fbf\u62bf\u7740\u5634\u4e0d\u8bf4\u4ec0\u4e48\u3002</p></article>"}}}]);
