(window.webpackJsonp=window.webpackJsonp||[]).push([[697],{720:function(p){p.exports={chapterTitle:null,title:"\u7b2c539\u7ae0 \u65e0\u8a00\u7684\u644a\u724c",content:"<article><h2>\u7b2c539\u7ae0 \u65e0\u8a00\u7684\u644a\u724c</h2><p>\u3000\u3000\u83b7\u5f97\u65e5\u5143\u8d37\u6b3e\u7684\u878d\u8d44\u6210\u672c\u4ee5\u53ca\u5229\u7387\u90fd\u8981\u6bd4\u5728\u56fd\u5185\u878d\u8d44\u4f4e\u4e00\u4e9b\uff0c\u4f46\u662f\u5bf9\u6295\u8d44\u9879\u76ee\u7684\u8981\u6c42\u76f8\u5f53\u4e25\u683c\uff1a\u516b\u5343\u4e07\u7f8e\u91d1\u7684\u8d37\u6b3e\uff0c\u8981\u670930\uff05\u7684\u8d44\u91d1\u8981\u62ff\u6765\u4e0a\u5e9f\u6c34\u5904\u7406\u914d\u5957\u9879\u76ee\u3002</p><p>\u3000\u3000\u5373\u4fbf\u662f\u6b27\u7f8e\u5bf9\u73af\u5883\u4fdd\u62a4\u8981\u6c42\u6700\u4e3a\u4e25\u683c\u7684\u56fd\u5bb6\uff0c\u7eaf\u7cb9\u9020\u7eb8\u4f01\u4e1a\u7684\u73af\u4fdd\u6295\u8d44\u6bd4\u4f8b\u4e5f\u6ca1\u6709\u8fd9\u4e48\u9ad8\u3002</p><p>\u3000\u3000\u8fd1\u4e24\u4ebf\u7684\u73af\u4fdd\u6295\u8d44\uff0c\u9664\u4e86\u4e8c\u671f\u5de5\u7a0b\u5e9f\u6c34\u5904\u7406\u8bbe\u65bd\u914d\u5957\u6240\u9700\u4e4b\u5916\uff0c\u8fd8\u5305\u62ec\u4e00\u671f\u5de5\u7a0b6000\u4e07\u7684\u73af\u4fdd\u8bbe\u65bd\u6280\u672f\u6539\u9020\u8d44\u91d1\u3002\u4e8c\u671f\u5de5\u7a0b\u5df2\u7ecf\u5168\u9762\u542f\u52a8\uff0c\u9884\u8ba1\u8981\u5230\u4eca\u5e74\u79cb\u5929\u624d\u80fd\u6b63\u5f0f\u6295\u4ea7\uff0c\u4f46\u662f\u4e00\u671f\u5de5\u7a0b\u7684\u73af\u4fdd\u8bbe\u65bd\u6280\u672f\u6539\u9020\u5df2\u7ecf\u5b8c\u6210\u3002</p><p>\u3000\u3000\u8f66\u961f\u5230\u65b0\u5149\u7eb8\u4e1a\u540e\uff0c\u7531\u5468\u6e38\u8d1f\u8d23\u5168\u7a0b\u8bb2\u89e3\uff0c\u91cd\u70b9\u662f\u53c2\u89c2\u65b0\u5149\u7eb8\u4e1a\u521a\u521a\u5f15\u8fdb\u7684\u5168\u7403\u6700\u5148\u8fdb\u7684\u5e9f\u6c34\u5904\u7406\u8bbe\u5907\uff0c\u8981\u8ba9\u674e\u8fdc\u6e56\u770b\u5230\u65b0\u5149\u7eb8\u4e1a\u5728\u73af\u5883\u4fdd\u62a4\u4e0a\u6beb\u65e0\u82b1\u5de7\u7684\u5de8\u5927\u6295\u5165\uff0c\u5468\u6e38\u4ecb\u7ecd\u8bf4\uff1a\u201c\u5de8\u989d\u7684\u73af\u4fdd\u6295\u5165\uff0c\u5bf9\u4f01\u4e1a\u800c\u8a00\u662f\u6ca1\u6709\u591a\u5c11\u7ecf\u6d4e\u6548\u76ca\u7684\uff0c\u9664\u4e86\u524d\u671f\u7684\u4e00\u6b21\u6027\u8bbe\u5907\u6295\u8d44\u5916\uff0c\u8fd8\u9700\u8981\u5f88\u5927\u7684\u6301\u7eed\u6027\u6295\u5165\u4e0e\u540e\u671f\u7684\u7ba1\u7406\uff0c\u57fa\u672c\u4e0a\u662f\u7eaf\u7cb9\u7684\u6295\u5165\uff0c\u4f46\u662f\u6211\u4eec\u613f\u610f\u627f\u62c5\u4e0b\u6765\uff0c\u8fd9\u662f\u4f01\u4e1a\u5e94\u5c3d\u7684\u793e\u4f1a\u8d23\u4efb\uff0c\u4e5f\u662f\u4f01\u4e1a\u957f\u8fdc\u53ef\u6301\u7eed\u53d1\u5c55\u7684\u6839\u672c\u539f\u5219\u2026\u2026\u524d\u9762\u5de5\u4e1a\u5e9f\u6c34\u7ecf\u8fc7\u5904\u7406\uff0c\u5dee\u4e0d\u591a\u80fd\u8fbe\u5230\u4eba\u80fd\u996e\u7528\u7684\u6807\u51c6\uff0c\u201d\u5468\u6e38\u8ba9\u9020\u7eb8\u4e8b\u4e1a\u90e8\u603b\u7ecf\u7406\u5468\u590d\u4eb2\u81ea\u8dd1\u4e0a\u53bb\u62ff\u7eb8\u676f\u6ed4\u4e00\u676f\u6c34\u51fa\u6765\uff0c\u8ba9\u674e\u8fdc\u6e56\u4e0e\u7701\u91cc\u966a\u540c\u7684\u5b98\u5458\u770b\u6c34\u7684\u6d01\u51c0\u5ea6\uff0c\u201c\u8fd9\u4e9b\u6c34\u90fd\u53ef\u4ee5\u5faa\u73af\u5229\u7528\uff0c\u8fd9\u95f4\u5382\u5df2\u7ecf\u5b8c\u5168\u5b9e\u73b0\u5de5\u4e1a\u5e9f\u6c34\u7684\u96f6\u6392\u653e\u2026\u2026\u201d</p><p>\u3000\u3000\u4e3a\u4e86\u9a8c\u8bc1\u5468\u6e38\u7684\u8bdd\uff0c\u5468\u590d\u8fd8\u5c0f\u62bf\u4e86\u4e00\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u81ea\u6765\u6c34\u7684\u6f02\u767d\u7c89\u5473\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7728\u7728\u773c\u775b\uff0c\u5c31\u7b97\u77e5\u9053\u5904\u7406\u4e0b\u6765\u80fd\u8fbe\u5230\u996e\u7528\u6c34\u7684\u6807\u51c6\uff0c\u4f46\u662f\u770b\u5230\u9884\u5904\u7406\u6c60\u91cc\u5de5\u4e1a\u5e9f\u6c34\u7070\u9ed1\u6c61\u79fd\u7684\u6837\u5b50\uff0c\u4ed6\u662f\u7edd\u6ca1\u6709\u52c7\u6c14\u53bb\u559d\u4e00\u53e3\u7684\uff0c\u8fd9\u7b80\u76f4\u8003\u9a8c\u4eba\u7684\u5fc3\u7406\u627f\u53d7\u80fd\u529b\u3002</p><p>\u3000\u3000\u901a\u8fc7\u82cf\u9038\u7fa4\uff0c\u8c22\u5251\u5357\u770b\u5230\u8fc7\u65b0\u5149\u7eb8\u4e1a\u6700\u65e9\u63d0\u4ea4\u7684\u5236\u6d46\u9879\u76ee\u610f\u5411\u4e66\uff0c\u610f\u5411\u4e66\u91cc\u660e\u786e\u63d0\u53ca\u8981\u4fdd\u8bc1\u5145\u8db3\u7684\u539f\u6599\u6728\u6750\u4f9b\u5e94\uff0c\u4f46\u9700\u4ee5\u4e0d\u635f\u5bb3\u7701\u5185\u4e0e\u5468\u8fb9\u7701\u5e02\u7684\u5929\u7136\u6797\u751f\u6001\u7cfb\u7edf\u4e3a\u524d\u63d0\u3002</p><p>\u3000\u3000\u6539\u9769\u5f00\u653e\u8fd1\u4e8c\u5341\u5e74\uff0c\u727a\u7272\u751f\u6001\u73af\u5883\u3001\u751a\u81f3\u727a\u7272\u4e00\u90e8\u5206\u4eba\u7684\u5229\u76ca\u6765\u53d1\u5c55\u7ecf\u6d4e\u7684\u552f\u7ecf\u6d4e\u8bba\u601d\u6f6e\u5728\u56fd\u5185\u5360\u636e\u4e3b\u6d41\u3002</p><p>\u3000\u3000\u5728\u7701\u91cc\uff0c\u4e0a\u5236\u6d46\u9879\u76ee\u6216\u8bb8\u5bf9\u7701\u5185\u5df2\u7136\u5f88\u8106\u5f31\u7684\u5929\u7136\u6797\u4f53\u7cfb\u4f1a\u9020\u6210\u6781\u5927\u7684\u538b\u529b\uff0c\u5374\u4e0d\u662f\u4e0d\u80fd\u5fcd\u53d7\u7684\uff1b\u800c\u5f20\u606a\u5c06\u674e\u8fdc\u6e56\u4e00\u884c\u4eba\u9886\u5230\u65b0\u5149\u7eb8\u4e1a\u9020\u4ef7\u6602\u8d35\u7684\u6c61\u6c34\u5904\u7406\u8bbe\u65bd\u524d\uff0c\u5c31\u662f\u8981\u660e\u786e\u7684\u544a\u8bc9\u674e\u8fdc\u6e56\uff0c\u9526\u6e56\u51fa\u4e8e\u751f\u6001\u4e0e\u73af\u5883\u56e0\u7d20\u4e0a\u7684\u8003\u8651\u6682\u7f13\u5236\u6d46\u9879\u76ee\uff0c\u7edd\u4e0d\u662f\u65e0\u7aef\u627e\u6765\u7684\u501f\u53e3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u60f3\u9526\u6e56\u662f\u4e0b\u5b9a\u51b3\u5fc3\u4e0d\u4f1a\u8ba9\u7701\u91cc\u7275\u7740\u9f3b\u5b50\u8d70\u7684\uff0c\u4ed6\u4eec\u8981\u6309\u7167\u81ea\u5df1\u7684\u610f\u56fe\u3001\u6267\u884c\u81ea\u5df1\u7684\u610f\u5fd7\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u751f\u5b58\u3001\u53d1\u5c55\u3002</p><p>\u3000\u3000\u9648\u9759\u6084\u7136\u4e0d\u8bed\uff0c\u82e5\u6709\u6240\u601d\uff0c\u77a5\u773c\u770b\u7740\u7ad9\u5728\u4e00\u65c1\u3001\u4f3c\u4e4e\u8fd9\u4e00\u5207\u90fd\u4e0e\u4ed6\u65e0\u5173\u7684\u5f20\u606a\uff0c\u4ed6\u7684\u786e\u8868\u73b0\u5f97\u5f88\u60a0\u95f2\uff0c\u51e0\u4e4e\u5f88\u96be\u60f3\u8c61\u56fd\u5185\u7684\u9020\u7eb8\u4f01\u4e1a\u4f1a\u4e3a\u73af\u5883\u652f\u4ed8\u5982\u6b64\u9ad8\u6602\u7684\u6210\u672c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8f66\u961f\u4ece\u65b0\u6865\u9547\u7684\u9020\u7eb8\u5de5\u4e1a\u56ed\u51fa\u6765\uff0c\u674e\u8fdc\u6e56\u5df2\u7ecf\u5b8c\u5168\u80fd\u660e\u767d\u5f20\u606a\u7684\u610f\u56fe\u4e86\uff0c\u9526\u6e56\u5e76\u6ca1\u6709\u8fd1\u671f\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u8ba1\u5212\uff1b\u8fd9\u7b97\u662f\u644a\u724c\u4e86\u3002</p><p>\u3000\u3000\u5728\u4ece\u65b0\u6865\u9547\u524d\u5f80\u8c61\u5c71\u5357\u9e93\u6d77\u88d5\u56fd\u9645\u5b66\u6821\u65f6\uff0c\u674e\u8fdc\u6e56\u7279\u610f\u5c06\u82cf\u9038\u7fa4\u53eb\u5230\u4ed6\u7684\u8f66\u4e0a\uff0c\u9664\u4e86\u4ed6\u4e0e\u82cf\u9038\u7fa4\u4e4b\u5916\uff0c\u8fd8\u6709\u4ed6\u7684\u79d8\u4e66\u9ad8\u771f\u4e0e\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u9646\u6587\u592b\u3002</p><p>\u3000\u3000\u201c\u65b0\u5149\u90a3\u4e2a\u526f\u603b\u6ed4\u4e0b\u90a3\u676f\u6c34\u559d\u65f6\uff0c\u8bb8\u591a\u62a5\u793e\u8bb0\u8005\u90fd\u62a2\u7740\u62cd\u4e0b\u8fd9\u4e00\u77ac\u95f4\uff0c\u201d\u674e\u8fdc\u6e56\u611f\u6168\u9053\uff0c\u201c\u56fd\u5916\u5a92\u4f53\u4e00\u76f4\u6279\u8bc4\u56fd\u5185\u4f01\u4e1a\u4e3a\u6c42\u53d1\u5c55\u727a\u7272\u73af\u5883\uff0c\u5728\u56fd\u5185\u51e0\u4e4e\u627e\u4e0d\u5230\u4e00\u6761\u5e72\u51c0\u7684\u6cb3\u6d41\uff0c\u8981\u662f\u7167\u7247\u6d17\u51fa\u6765\uff0c\u62ff\u51fa\u53bb\u56de\u656c\u90a3\u4e9b\u9976\u820c\u7684\u56fd\u5916\u5a92\u4f53\u5012\u662f\u597d\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u5bf9\u65b0\u5149\u7eb8\u4e1a\u7684\u505a\u6cd5\u6709\u6240\u4e0d\u5c51\uff0c\u4f46\u662f\u8f66\u91cc\u7684\u4eba\u5728\u653f\u6cbb\u4e0a\u90fd\u6210\u719f\u7684\uff0c\u4e5f\u5c31\u8c01\u90fd\u65e0\u6cd5\u5f00\u53e3\u63d0\u51fa\u6279\u8bc4\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u201c\u56fd\u52a1\u9662\u4ece\u4e5d\u4e09\u5e74\u641e\u73af\u5883\u4fdd\u62a4\u4e16\u7eaa\u884c\uff0c\u4e0b\u51b3\u5fc3\u6574\u987f\u9020\u7eb8\u4f01\u4e1a\uff0c\u201d\u82cf\u9038\u7fa4\u5bdf\u989c\u89c2\u8272\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u77eb\u6789\u8fc7\u6b63\u4e5f\u4e0d\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8ddf\u8fd9\u4e9b\u8bb0\u8005\u6253\u58f0\u62db\u547c\uff0c\u4e0d\u8981\u523b\u610f\u5ba3\u626c\u65b0\u5149\u7eb8\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u5bf9\u9646\u6587\u592b\u6765\u8bf4\uff0c\u4e0d\u5b58\u5728\u7ad9\u961f\u7684\u95ee\u9898\uff0c\u4ed6\u5904\u4e8e\u8fd9\u6837\u7684\u4f4d\u5b50\u4e0a\uff0c\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u8fd9\u6bd5\u7adf\u662f\u503c\u5f97\u5927\u529b\u63d0\u5021\u7684\u4e8b\u60c5\uff0c\u8fd9\u79cd\u4e8b\u90fd\u8981\u6253\u62db\u547c\uff0c\u8ba9\u90a3\u4e9b\u62a5\u793e\u7684\u8bb0\u8005\u4f1a\u5982\u4f55\u770b\u7701\u91cc\uff1f\u201d\u674e\u8fdc\u6e56\u6325\u4e86\u6325\u624b\uff0c\u773c\u775b\u671b\u5411\u8f66\u7a97\u5916\u8fdc\u5904\u7b3c\u7740\u4e00\u5c42\u6de1\u6de1\u96fe\u972d\u7684\u8c61\u5c71\uff0c\u4ed6\u51b3\u5fc3\u4e0d\u53bb\u7ba1\u8fd9\u6837\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e00\u65e6\u8ba9\u5a92\u4f53\u5927\u8086\u5ba3\u626c\u65b0\u5149\u7eb8\u4e1a\u5728\u73af\u4fdd\u4e0a\u7684\u6295\u5165\uff0c\u76f8\u5f53\u4e8e\u9ed8\u8ba4\u9526\u6e56\u65e0\u9650\u5236\u62d6\u5ef6\u5236\u6d46\u9879\u76ee\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u82cf\u9038\u7fa4\u63e3\u6d4b\u9886\u5bfc\u7684\u610f\u56fe\u8fd8\u662f\u6709\u4e00\u624b\u7684\uff0c\u4ed6\u770b\u5230\u674e\u8fdc\u6e56\u795e\u8272\u95f4\u6709\u4e9b\u5fc3\u6c14\u90c1\u79ef\uff0c\u5fc3\u91cc\u5fae\u5fae\u4e00\u7b11\uff1a\u5f20\u606a\u4ed6\u4eec\u5229\u7528\u79cd\u79cd\u624b\u6bb5\u4e0e\u65b9\u5f0f\uff0c\u751a\u81f3\u4e0d\u60dc\u8feb\u4f7f\u4e00\u7701\u4e4b\u957f\u674e\u8fdc\u6e56\u8ba9\u6b65\uff0c\u4e5f\u8981\u8ba9\u9526\u6e56\u53d1\u5c55\u8d2f\u5f7b\u4ed6\u4eec\u81ea\u5df1\u7684\u610f\u5fd7\uff0c\u770b\u4e0a\u53bb\u662f\u4ed6\u4eec\u8d62\u4e86\uff0c\u4f46\u662f\u674e\u8fdc\u6e56\u5fc3\u91cc\u7684\u82a5\u8482\u672a\u5fc5\u80fd\u5c31\u6b64\u6d88\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8f66\u961f\u662f\u987a\u7740\u7a7f\u8d8a\u690d\u7269\u56ed\u7684\u6811\u836b\u752c\u9053\u9a76\u5165\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\uff0c\u8c22\u665a\u6674\u4e0e\u5979\u7684\u52a9\u624b\u5f20\u5ead\u4ee5\u53ca\u56fd\u6d77\u56fd\u9645\u5b66\u6821\u62db\u8058\u7684\u7ba1\u7406\u4eba\u5458\u90fd\u5728\u6821\u95e8\u53e3\u606d\u8fce\u674e\u8fdc\u6e56\u4e00\u884c\u4eba\u7684\u5230\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u8c22\u665a\u6674\u9010\u6e10\u6de1\u5316\u5979\u66fe\u4f5c\u4e3a\u9526\u6e56\u4e3b\u5bfc\u8005\u7684\u89d2\u8272\uff0c\u800c\u5c06\u5927\u90e8\u529b\u7684\u7cbe\u529b\u90fd\u6295\u5165\u5230\u8fd9\u5ea7\u9ad8\u6559\u80b2\u6c34\u51c6\u7684\u79c1\u7acb\u5b66\u6821\u4e0a\u6765\uff1b\u51c6\u786e\u7684\u8bf4\uff0c\u662f\u5728\u65b0\u5149\u7eb8\u4e1a\u4e0a\u6797\u4e1a\u9879\u76ee\u524d\u5915\uff0c\u6d77\u88d5\u5c31\u5f7b\u5e95\u64a4\u51fa\u9526\u6e56\u4e86\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u8d70\u4e0b\u8f66\uff0c\u5e73\u9759\u7684\u770b\u7740\u8fd9\u4e2a\u53ef\u80fd\u662f\u7701\u5185\u6700\u5bcc\u88d5\u3001\u4e0e\u5144\u5f1f\u53d4\u4f2f\u95f9\u7ffb\u4e0d\u518d\u5f80\u6765\u7684\u5973\u4eba\uff0c\u7684\u786e\u5f88\u6f02\u4eae\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\u6709\u7740\u96cd\u5bb9\u4f18\u96c5\u7684\u6c14\u5ea6\uff0c\u5e94\u8be5\u662f\u5979\u7684\u7eb5\u5bb9\uff0c\u624d\u8ba9\u5f20\u606a\u6709\u673a\u4f1a\u6210\u5c31\u4e00\u4e2a\u7480\u74a8\u593a\u76ee\u7684\u9526\u6e56\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u5e74\uff0c\u56fd\u5185\u6d8c\u73b0\u7684\u8d35\u65cf\u5b66\u6821\u4e0d\u5c11\uff0c\u4e5f\u6709\u4e0d\u5c11\u5c06\u5206\u6821\u5f00\u5230\u4e1c\u6d77\u7701\u5883\u5185\uff0c\u4f46\u90fd\u662f\u4ee5\u76c8\u5229\u4e3a\u76ee\u7684\u7684\uff0c\u50cf\u8c22\u665a\u6674\u4e0d\u8ba1\u6210\u672c\u5f80\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u6295\u5165\u7684\uff0c\u7edd\u65e0\u4ec5\u6709\u3002\u76ee\u524d\u80fd\u770b\u5230\u7684\u8fd8\u4ec5\u4ec5\u662f\u786c\u4ef6\u7684\u6295\u5165\uff0c\u6d77\u88d5\u56fd\u9645\u5b66\u6821\u5c06\u91c7\u53d6\u4e0e\u56fd\u5185\u6559\u80b2\u4f53\u5236\u622a\u7136\u4e0d\u540c\u7684\u6559\u80b2\u7406\u5ff5\u4e0e\u65b9\u5f0f\uff0c\u8fd9\u4e00\u70b9\u8fd8\u65e0\u6cd5\u770b\u51fa\u6765\u3002</p><p>\u3000\u3000\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u6210\u7acb\u4e4b\u540e\uff0c\u4e5f\u4e0d\u662f\u5411\u793e\u4f1a\u516c\u5f00\u62db\u751f\uff0c\u524d\u671f\u4e5f\u4ec5\u4ec5\u662f\u8003\u8651\u62db\u6536\u9526\u6e56\u4f53\u7cfb\u5185\u5458\u5de5\u7684\u5b50\u5973\uff0c\u5f53\u7136\uff0c\u751f\u4ea7\u4e00\u7ebf\u7684\u666e\u901a\u5458\u5de5\u73b0\u5728\u8fd8\u5f88\u96be\u627f\u53d7\u4e9b\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u7684\u9ad8\u6602\u6559\u80b2\u8d39\u7528\uff0c\u4f46\u662f\u9526\u6e56\u60f3\u4ece\u5168\u7403\u5404\u5730\u62db\u52df\u9876\u5c16\u7684\u6280\u672f\u4e0e\u7ba1\u7406\u4eba\u624d\uff0c\u5c31\u4e0d\u5f97\u4e0d\u4e3a\u4ed6\u4eec\u7684\u5bb6\u5ead\u8003\u8651\u5468\u5168\u3002\u7edd\u5927\u591a\u6570\u7684\u534e\u4eba\u7236\u6bcd\u662f\u613f\u610f\u4e3a\u4ed6\u4eec\u5b50\u5973\u7684\u6559\u80b2\u95ee\u9898\u5728\u5de5\u4f5c\u9009\u62e9\u4e0a\u505a\u51fa\u8ba9\u6b65\u7684\uff0c\u504f\u504f\u9ad8\u77e5\u5206\u5b50\u7684\u5bb6\u5ead\u90fd\u4e3a\u56fd\u5185\u7684\u6559\u80b2\u73b0\u72b6\u582a\u5fe7\uff0c\u8f7b\u677e\u3001\u80fd\u5145\u5206\u53d1\u6398\u5b69\u5b50\u5929\u6027\u4e0e\u624d\u80fd\u7684\u6559\u80b2\uff0c\u624d\u662f\u4ed6\u4eec\u6240\u8ffd\u6c42\u7684\u3002</p><p>\u3000\u3000\u542c\u8c22\u665a\u6674\u4ecb\u7ecd\u8fc7\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u7684\u529e\u5b66\u524d\u666f\uff0c\u674e\u8fdc\u6e56\u5fc3\u601d\u4e00\u52a8\uff0c\u4fa7\u5934\u8ddf\u61d2\u6563\u5c11\u5e74\u4f3c\u7684\u5750\u5728\u4e00\u65c1\u3001\u5f88\u5c11\u8bf4\u8bdd\u3001\u773c\u795e\u56db\u5904\u4e71\u8f6c\u7684\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u4e3a\u65d7\u4e0b\u7684\u5458\u5de5\u8003\u8651\u5f88\u957f\u4e45\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u54ea\u91cc\u662f\u6211\u60f3\u7684\uff1f\u9526\u6e56\u8fd9\u4e2a\u56e2\u961f\u90fd\u5584\u4e8e\u4e3a\u81ea\u5df1\u672a\u6765\u7684\u4eba\u751f\u89c4\u5212\u3002\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u79cd\u4e0b\u68a7\u6850\u6811\u624d\u80fd\u5f15\u6765\u91d1\u51e4\u51f0\uff0c\u201d\u624b\u8db4\u5728\u684c\u4e0a\uff0c\u95ee\u674e\u8fdc\u6e56\u53f3\u624b\u7684\u5510\u5b66\u8c26\uff0c\u201c\u8fd9\u8bdd\u662f\u8fd9\u4e48\u8bf4\u7684\u5427\uff1f\u201d\u53c8\u8ddf\u674e\u8fdc\u6e56\u8bf4\u9053\uff0c\u201c\u8981\u60f3\u62db\u8058\u9876\u5c16\u7684\u6280\u672f\u4e0e\u7ba1\u7406\u4eba\u624d\uff0c\u5fc5\u987b\u6709\u8ba9\u4ed6\u4eec\u5411\u5f80\u7684\u751f\u6d3b\u73af\u5883\u624d\u884c\uff0c\u6362\u4f5c\u975e\u6d32\uff0c\u7ed9\u4ed6\u4eec\u518d\u9ad8\u7684\u5e74\u85aa\uff0c\u90fd\u6ca1\u5730\u65b9\u82b1\u53bb\uff0c\u8c01\u9ad8\u5174\u53bb\uff1f\u6709\u4e9b\u6761\u4ef6\uff0c\u5e02\u91cc\u4e00\u65f6\u4e0d\u5177\u5907\uff0c\u9526\u6e56\u5c31\u8981\u4e3a\u65d7\u4e0b\u7684\u5458\u5de5\u4f18\u5148\u8003\u8651\uff0c\u9664\u4e86\u5b66\u6821\u4e4b\u5916\uff0c\u9526\u6e56\u8fd8\u8981\u8003\u8651\u6295\u8d44\u5efa\u8bbe\u4e00\u4e2a\u5927\u578b\u7684\u793e\u533a\uff0c\u8ba9\u90a3\u4e9b\u5916\u4e61\u7c4d\u7684\u5458\u5de5\u5728\u6d77\u5dde\u5b89\u5bb6\u4e50\u4e1a\u2026\u2026\u8003\u8651\u5230\u9526\u6e56\u65e5\u540e\u7684\u53d1\u5c55\uff0c\u8fd9\u4e2a\u793e\u533a\u603b\u8981\u5bb9\u7eb3\u51e0\u4e07\u4eba\u624d\u591f\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u65d7\u4e0b\u542b\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7231\u8fbe\u96c6\u56e2\u5728\u6d77\u5dde\u62e5\u6709\u5458\u5de5\u624d\u4e94\u591a\u5343\u4eba\uff0c\u5f20\u606a\u7684\u8bdd\u8bf4\u5f97\u968f\u4fbf\uff0c\u5374\u4e5f\u900f\u9732\u51fa\u9526\u6e56\u4ee5\u6d77\u5dde\u4e3a\u91cd\u5fc3\u53d1\u5c55\u7684\u957f\u671f\u89c4\u5212\u6765\u3002</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u5927\u9020\u58f0\u52bf\uff0c\u53c8\u662f\u8d2d\u697c\u3001\u53c8\u662f\u4e0e\u5317\u4eac\u7684\u4f01\u4e8b\u4e1a\u5355\u4f4d\u5f00\u5c55\u5e7f\u6cdb\u7684\u5408\u4f5c\uff0c\u8ba9\u674e\u8fdc\u6e56\u4ea7\u751f\u9526\u6e56\u6709\u5c06\u603b\u90e8\u8fc1\u5f80\u5317\u4eac\u610f\u5411\u7684\u731c\u6d4b\uff0c\u674e\u8fdc\u6e56\u4e5f\u6015\u5f20\u606a\u5c11\u5e74\u5fc3\u6027\u53d1\u4f5c\u4e0d\u6309\u89c4\u5219\u51fa\u724c\uff0c\u8fd9\u624d\u662f\u4ed6\u8fd9\u6b21\u6d77\u5dde\u4e4b\u884c\u7684\u76ee\u7684\u3002</p><p>\u3000\u3000\u5230\u8fd9\u65f6\uff0c\u674e\u8fdc\u6e56\u7b97\u662f\u7a0d\u7a0d\u5b89\u5fc3\u4e0b\u6765\u3002</p></article>"}}}]);
