(window.webpackJsonp=window.webpackJsonp||[]).push([[70],{92:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516b\u8282 \u635e\u4e86\u4e2a\u7a7a",content:"<article><h2>\u7b2c\u4e8c\u5341\u516b\u8282 \u635e\u4e86\u4e2a\u7a7a</h2><p>\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u743c\u6069\u7684\u5377\u8f74\u4e2d\uff0c\u6709\u4e00\u5f20\u7d22\u5c14\u77f3\u884c\u672f\u5377\u8f74\u3002\u7d22\u5c14\u602a\u662f\u4e00\u79cd\u7c7b\u4f3c\u5730\u7403\u4e0a\u7a7f\u5c71\u7532\u7684\u602a\u7269\uff0c\u80fd\u591f\u7a7f\u5c71\u8d8a\u77f3\uff0c\u5728\u5730\u4e0b\u884c\u8d70\u3002\u5deb\u5e08\u4eec\u6a21\u4eff\u7d22\u5c14\u602a\u7684\u80fd\u529b\uff0c\u521b\u9020\u51fa\u4e86\u8fd9\u4e2a\u9b54\u6cd5\u3002\u7c97\u7565\u8bf4\u8d77\u6765\uff0c\u6709\u70b9\u7c7b\u4f3c\u795e\u8bdd\u4f20\u8bf4\u4e2d\u7684\u571f\u884c\u4e4b\u672f\u3002</p><p>\u82e5\u8981\u8bba\u8d77\u6765\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u7ed9\u743c\u6069\u7684\u4e03\u516b\u5f20\u5377\u8f74\u4e2d\uff0c\u7d22\u5c14\u77f3\u884c\u672f\u662f\u5176\u4e2d\u6700\u5f3a\u5927\u7684\u9b54\u6cd5\u4e86\u3002\u5018\u82e5\u5deb\u5e08\u76f4\u63a5\u65bd\u5c55\uff0c\u9700\u8981\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e94\u5c42\u624d\u80fd\u505a\u5230\u3002\u6240\u5e78\uff0c\u7d22\u5c14\u77f3\u884c\u672f\u4e5f\u662f\u53d8\u5316\u5b66\u6d3e\u7684\u9b54\u6cd5\uff0c\u6b63\u662f\u743c\u6069\u4e13\u4e1a\u7cbe\u7814\u7684\u8303\u7574\uff0c\u8fd9\u8ba9\u4ed6\u65bd\u5c55\u5377\u8f74\u53d8\u5f97\u5bb9\u6613\u591a\u4e86\uff0c\u4e5f\u5b89\u5168\u591a\u4e86\u3002</p><p>\u5f53\u6885\u83f2\u65af\u53d8\u6210\u4eba\u9762\u72ee\u79bb\u5f00\u540e\uff0c\u743c\u6069\u5c31\u4e00\u76f4\u9759\u9759\u5730\u7b49\u5f85\u7740\uff0c\u76f4\u5230\u796d\u7940\u4eea\u5f0f\u5f00\u59cb\uff0c\u6240\u6709\u7684\u4eba\u9762\u72ee\u8d70\u5165\u9ed1\u6697\u3002\u4ed6\u4ece\u6b21\u5143\u888b\u91cc\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u957f\u73bb\u7483\u74f6\uff0c\u91cc\u9762\u88c5\u6ee1\u7070\u84dd\u8272\u7684\u836f\u6c34\uff0c\u743c\u6069\u6253\u5f00\u74f6\u76d6\uff0c\u55c5\u4e86\u55c5\uff0c\u7136\u540e\u76d6\u4e0a\u74f6\u76d6\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6536\u8fdb\u957f\u888d\u5185\u4fa7\u7684\u540e\u4ee3\u91cc\u3002\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u603b\u7b97\u8fd8\u6709\u70b9\u826f\u5fc3\uff0c\u867d\u7136\u4e8c\u8bdd\u4e0d\u8bf4\u628a\u743c\u6069\u6253\u53d1\u51fa\u6765\uff0c\u5374\u4e5f\u7740\u5b9e\u7ed9\u4e86\u4e9b\u597d\u4e1c\u897f\uff0c\u6d3b\u5316\u9f99\u9cde\u76fe\u3001\u9632\u5fa1\u80f8\u9488\u3001\u5377\u8f74\uff0c\u8fd8\u6709\u8fd9\u4e9b\u836f\u6c34\u3002</p><p>\u4ed6\u5148\u5728\u81ea\u5df1\u8eab\u4e0a\u63a5\u8fde\u53e0\u52a0\u4e86\u201c\u732b\u4e4b\u4f18\u96c5\u201d\u548c\u201c\u725b\u4e4b\u86ee\u529b\u201d\uff0c\u7136\u540e\u5c55\u5f00\u7d22\u5c14\u77f3\u884c\u672f\u5377\u8f74\uff0c\u6f5c\u5165\u5730\u4e0b\u3002</p><p>\u4ed6\u6478\u7d22\u7740\uff0c\u65f6\u800c\u63a2\u5934\u5bdf\u770b\uff0c\u6700\u7ec8\u4e5f\u8fdb\u5165\u795e\u6bbf\u4e4b\u4e2d\uff0c\u5c31\u8eb2\u5728\u796d\u575b\u65c1\u8fb9\u4e00\u4e2a\u9690\u853d\u7684\u5730\u65b9\u3002\u6240\u6709\u7684\u4e00\u5207\uff0c\u4ed6\u90fd\u770b\u5728\u773c\u91cc\uff0c\u5f53\u770b\u5230\u6885\u83f2\u65af\u53d8\u6210\u4eba\u9762\u72ee\uff0c\u9762\u4e0d\u6539\u8272\u5730\u5978\u6740\u4e00\u540d\u86ee\u65cf\u5973\u5b50\u540e\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u743c\u6069\u771f\u7684\u6709\u51e0\u5206\u4f69\u670d\u3002\u81ea\u5ea6\u82e5\u662f\u6362\u4e86\u81ea\u5df1\uff0c\u53ea\u6015\u505a\u4e0d\u5230\u8fd9\u4e48\u4ece\u5bb9\u3002</p><p>\u743c\u6069\u5bf9\u8fd9\u4e2a\u5973\u5b69\u8d8a\u6765\u8d8a\u597d\u5947\uff0c\u5b9e\u5728\u96be\u4ee5\u60f3\u50cf\u63d0\u5c14\u5c45\u7136\u4f1a\u6709\u8fd9\u6837\u7684\u5723\u6b66\u58eb\uff0c\u800c\u4e14\u8fd8\u662f\u201c\u795e\u9009\u8005\u201d\u3002\u5f53\u7136\uff0c\u4ed6\u5176\u5b9e\u4e5f\u5e76\u4e0d\u592a\u4e86\u89e3\u771f\u6b63\u7684\u5723\u6b66\u58eb\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u7684\uff0c\u8111\u4e2d\u7684\u5370\u8c61\u5927\u591a\u8fd8\u662f\u6765\u81ea\u4e0a\u8f88\u5b50\u770b\u7684\u90a3\u4e9b\u5947\u5e7b\u5c0f\u8bf4\uff0c\u672a\u5fc5\u80fd\u5f53\u771f\u3002</p><p>\u5728\u4e94\u8f6e\u6c61\u79fd\u4e4b\u96e8\u7684\u6d17\u793c\u540e\uff0c\u743c\u6069\u770b\u5230\u4e86\u9ece\u660e\u4e4b\u77f3\uff0c\u63a5\u7740\u770b\u89c1\u683c\u62c9\u5179\u7279\u7684\u865a\u5f71\u3002\u4ed6\u81ea\u7136\u4e0d\u8ba4\u8bc6\u683c\u62c9\u5179\u7279\uff0c\u4e0d\u8fc7\u731c\u4e5f\u80fd\u731c\u5230\u3002</p><p>\u683c\u62c9\u5179\u7279\u4fb5\u5360\u4e86\u7267\u5e08\u7684\u8eab\u4f53\uff0c\u53bb\u53d6\u9ece\u660e\u4e4b\u77f3\uff0c\u743c\u6069\u5fcd\u8010\u7740\uff0c\u4ed6\u77e5\u9053\u6885\u83f2\u65af\u4f1a\u52a8\u624b\u7684\u3002\u6668\u66e6\u5723\u5149\u81ea\u5929\u800c\u964d\uff0c\u51fb\u6740\u51fb\u4f24\u4e86\u5927\u91cf\u4eba\u9762\u72ee\uff0c\u8ba9\u743c\u6069\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u53ef\u7b97\u662f\u610f\u6599\u4e4b\u5916\u7684\u60ca\u559c\u3002\u6700\u540e\uff0c\u6885\u83f2\u65af\u679c\u7136\u52a8\u624b\uff0c\u4e00\u5251\u5288\u4e0b\u4e86\u7267\u5e08\u7684\u5934\uff0c\u903c\u8feb\u6076\u9b54\u79bb\u4f53\u800c\u51fa\u3002</p><p>\u6885\u83f2\u65af\u88ab\u5b9a\u4f4f\u4e86\uff0c\u8fd9\u6709\u70b9\u9ebb\u70e6\u2026\u2026\u4e0d\u8fc7\uff0c\u4f3c\u4e4e\u4e5f\u672a\u5fc5\u5462\u3002</p><p>\u743c\u6069\u6253\u7684\u4e3b\u610f\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u4ed6\u77e5\u9053\u4eba\u9762\u72ee\u65e2\u7136\u8981\u5c06\u9ece\u660e\u4e4b\u77f3\u732e\u796d\u7ed9\u683c\u62c9\u5179\u7279\uff0c\u6076\u9b54\u662f\u5fc5\u5b9a\u4f1a\u767b\u573a\u7684\u3002\u540c\u65f6\u4ed6\u4e5f\u77e5\u9053\uff0c\u6076\u9b54\u4e0d\u53ef\u80fd\u5728\u7269\u8d28\u754c\u505c\u7559\u5f88\u957f\u65f6\u95f4\uff0c\u5b83\u7ec8\u7a76\u8981\u8fd4\u56de\u6df1\u6e0a\u3002\u743c\u6069\u4e0d\u6307\u671b\u80fd\u51fb\u6740\u6076\u9b54\uff0c\u4ed6\u53ea\u6253\u7b97\u89c1\u673a\u884c\u4e8b\uff0c\u5c3d\u53ef\u80fd\u5730\u62d6\u5ef6\u65f6\u95f4\uff0c\u76f4\u5230\u6076\u9b54\u81ea\u5df1\u88ab\u8feb\u8fd4\u56de\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u6709\u4e00\u5c42\u6253\u7b97\u3002\u4ee5\u524d\u5728\u5b66\u6821\u7684\u65f6\u5019\uff0c\u6559\u6388\u66fe\u7ecf\u8bb2\u89e3\u8fc7\u53ec\u5524\u90aa\u9b54\uff08\u5305\u62ec\u6076\u9b54\u548c\u9b54\u9b3c\uff09\u7684\u4e00\u4e9b\u4eea\u5f0f\uff0c\u5176\u4e2d\u5728\u53ec\u5524\u6076\u9b54\u7684\u65f6\u5019\u5c24\u5176\u9700\u8981\u8c28\u614e\u3002\u56e0\u4e3a\u5982\u679c\u62db\u6765\u6076\u9b54\uff0c\u4f46\u4eea\u5f0f\u88ab\u4eba\u7834\u574f\uff0c\u65e0\u6cd5\u6309\u8ba1\u5212\u5411\u6076\u9b54\u732e\u4e0a\u796d\u54c1\uff0c\u90a3\u4e48\u6781\u5176\u5bb9\u6613\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u6076\u9b54\u4f1a\u52c3\u7136\u5927\u6012\uff0c\u5bf9\u5728\u573a\u6240\u6709\u4eba\u2014\u2014\u5305\u62ec\u5b83\u7684\u4fe1\u5f92\u548c\u654c\u4eba\u2014\u2014\u8fdb\u884c\u731b\u70c8\u7684\u653b\u51fb\u3002\u6076\u9b54\u662f\u4e0d\u4f1a\u6709\u5174\u81f4\u53bb\u4ed4\u7ec6\u5206\u8fa8\u8c01\u624d\u662f\u7f6a\u9b41\u7978\u9996\uff0c\u8c01\u662f\u81ea\u5df1\u4fe1\u5f92\u2014\u2014\u5982\u679c\u5b83\u8fd9\u4e48\u505a\u7684\u8bdd\uff0c\u90a3\u4e5f\u4e0d\u662f\u5929\u6027\u6df7\u4e71\u90aa\u6076\u7684\u6076\u9b54\u4e86\u3002</p><p>\u5982\u679c\u7834\u574f\u4eea\u5f0f\uff0c\u62a2\u8d70\u9ece\u660e\u4e4b\u77f3\uff0c\u90a3\u4e48\u683c\u62c9\u5179\u7279\u4e5f\u5e94\u8be5\u4f1a\u607c\u6012\u5730\u653b\u51fb\u795e\u6bbf\u5185\u6240\u6709\u4eba\u5427\u3002\u5230\u65f6\u5019\u5b9a\u7136\u4e00\u7247\u6df7\u4e71\uff0c\u743c\u6069\u518d\u51ed\u501f\u7d22\u5c14\u77f3\u884c\u672f\u6f5c\u5165\u5730\u4e0b\uff0c\u6216\u8005\u9003\u51fa\u795e\u6bbf\uff0c\u907f\u5f00\u6076\u9b54\u7684\u6012\u706b\uff0c\u7b49\u65f6\u95f4\u5230\u4e86\uff0c\u6076\u9b54\u8fd4\u56de\u6df1\u6e0a\uff0c\u518d\u51fa\u6765\u6536\u53d6\u80dc\u5229\u679c\u5b9e\u3002\u81f3\u4e8e\u6885\u83f2\u65af\uff0c\u5c31\u53ea\u80fd\u7948\u7977\u5979\u51ed\u501f\u81ea\u8eab\u7684\u5f3a\u608d\u5b9e\u529b\uff0c\u80fd\u5728\u6076\u9b54\u7684\u65e0\u5dee\u522b\u6012\u706b\u653b\u51fb\u4e2d\u5e78\u5b58\u4e0b\u6765\u5427\u3002</p><p>\u8fd9\u5f53\u7136\u662f\u6700\u5982\u610f\u7684\u7b97\u76d8\uff0c\u743c\u6069\u4e5f\u77e5\u9053\u672a\u5fc5\u4f1a\u6253\u5f97\u901a\uff0c\u53ea\u80fd\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u3002\u4e0d\u8fc7\u4ece\u76ee\u524d\u7684\u60c5\u5f62\u6765\u770b\uff0c\u4f3c\u4e4e\u8ddd\u79bb\u9884\u671f\u8d8a\u6765\u8d8a\u8fdc\u3002\u5176\u5b9e\u6885\u83f2\u65af\u5df2\u7ecf\u963b\u6320\u4e86\u796d\u7940\uff0c\u6253\u65ad\u683c\u62c9\u5179\u7279\u7684\u8ba1\u5212\uff0c\u4f46\u6076\u9b54\u4f3c\u4e4e\u5e76\u6ca1\u6709\u9677\u5165\u72c2\u66b4\uff0c\u800c\u662f\u975e\u5e38\u51b7\u9759\u5730\u53cd\u51fb\uff0c\u63a7\u5236\u5c40\u52bf\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u743c\u6069\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff1a\u683c\u62c9\u5179\u7279\u6700\u6700\u6709\u540d\u7684\uff0c\u65e2\u4e0d\u662f\u4ed6\u7684\u6deb\u90aa\u597d\u8272\uff0c\u540e\u5bab\u5e9e\u5927\uff0c\u7537\u5973\u901a\u5403\uff1b\u4e5f\u4e0d\u662f\u4ed6\u7cbe\u64c5\u9634\u8c0b\uff0c\u8be1\u8ba1\u65e0\u7a77\uff1b\u5f53\u7136\u66f4\u4e0d\u662f\u4ed6\u7684\u624b\u638c\u957f\u7740\u516d\u6839\u624b\u6307\u3002</p><p>\u683c\u62c9\u5179\u7279\u6700\u6700\u8457\u540d\u7684\uff0c\u662f\u4ed6\u88ab\u654c\u4eba\u534a\u662f\u6050\u60e7\u534a\u662f\u8f7b\u8511\u5730\u79f0\u547c\u4e3a\u201c\u6700\u50cf\u9b54\u9b3c\u7684\u6076\u9b54\u201d\u3002</p><p>\u5728\u6df1\u6e0a\u7684\u5343\u4e07\u6076\u9b54\u4e2d\uff0c\u683c\u62c9\u5179\u7279\u662f\u7edd\u5bf9\u7684\u5f02\u7c7b\u3002\u5982\u679c\u4ec5\u8bba\u5934\u8111\uff0c\u8bba\u601d\u7ef4\uff0c\u8bba\u7075\u9b42\uff0c\u4ed6\u5176\u5b9e\u5b8c\u5168\u5e94\u8be5\u5f52\u7c7b\u5230\u4e5d\u5c42\u5730\u72f1\u90a3\u4e9b\u90aa\u6076\u5374\u5b88\u5e8f\u7684\u9b54\u9b3c\u91cc\u53bb\u3002\u4e4c\u9eef\u541b\u4e3b\u6c38\u8fdc\u4f18\u96c5\u3001\u51b7\u9759\u3001\u4ece\u5bb9\u4e0d\u8feb\uff0c\u51b3\u4e0d\u4f1a\u60ca\u614c\u5931\u63aa\u4e71\u6765\u4e00\u6c14\uff0c\u5c31\u8fd9\u70b9\u6765\u8bf4\uff0c\u4ed6\u538b\u6839\u5c31\u5e94\u8be5\u662f\u4e2a\u9b54\u9b3c\u624d\u5bf9\u3002</p><p>\u773c\u770b\u6076\u9b54\u5b9a\u4f4f\u4e86\u6885\u83f2\u65af\uff0c\u6253\u7b97\u4fb5\u5360\u5979\u7684\u8eab\u4f53\uff0c\u743c\u6069\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u6ca1\u6709\u65f6\u95f4\u3002\u7ea0\u7f20\u8fd9\u4e48\u4e45\uff0c\u683c\u62c9\u5179\u7279\u7684\u6295\u5f71\u4e5f\u5e94\u8be5\u5feb\u8981\u8fd4\u56de\u6df1\u6e0a\u4e86\u5427\uff0c\u53ea\u8981\u80fd\u518d\u62d6\u5ef6\u4e00\u4f1a\u5c31\u597d\u3002\u4ed6\u7948\u7977\u7740\uff0c\u638f\u51fa\u4e00\u74f6\u52a0\u901f\u836f\u6c34\u704c\u4e0b\uff0c\u9f13\u8db3\u52c7\u6c14\u4ece\u5730\u4e0b\u8df3\u51fa\u6765\uff0c\u4f38\u624b\u53bb\u6293\u7a7a\u4e2d\u7684\u9ece\u660e\u4e4b\u77f3\u3002</p><p>\u4ed6\u5f53\u5e74\u8ddf\u7740\u7530\u4f2f\u5149\u7ec3\u529f\uff0c\u5200\u6cd5\u662f\u538b\u6839\u6ca1\u5b66\u6210\uff0c\u5185\u529f\u53ea\u7ec3\u5230\u57fa\u7840\u5c31\u518d\u4e5f\u65e0\u6cd5\u8fdb\u6b65\uff0c\u5012\u662f\u8f7b\u529f\u8fd8\u9a6c\u9a6c\u864e\u864e\u3002\u6b64\u65f6\u84c4\u52bf\u5df2\u4e45\uff0c\u7a81\u7136\u8dc3\u51fa\uff0c\u53c8\u5148\u559d\u4e86\u52a0\u901f\u836f\u6c34\uff0c\u52a8\u4f5c\u6bd4\u5e73\u65f6\u5feb\u4e0a\u4e00\u500d\uff0c\u65e0\u8bba\u683c\u62c9\u5179\u7279\u8fd8\u662f\u8482\u5a1c\u5c91\u90fd\u6ca1\u6709\u7acb\u523b\u53cd\u5e94\u8fc7\u6765\uff0c\u5c45\u7136\u771f\u88ab\u4ed6\u6293\u5230\u4e86\u9ece\u660e\u4e4b\u77f3\u3002</p><p>\u4e0d\uff0c\u5176\u5b9e\u5e76\u6ca1\u6709\u6293\u5230\u3002</p><p>\u4eff\u4f5b\u7a81\u7136\u53d8\u6210\u4e86\u955c\u4e2d\u82b1\u3001\u6c34\u4e2d\u6708\u4e00\u822c\uff0c\u743c\u6069\u7684\u624b\u5c45\u7136\u76f4\u63a5\u4ece\u9ece\u660e\u4e4b\u77f3\u5f53\u4e2d\u7a7f\u4e86\u8fc7\u53bb\uff0c\u4ed6\u6ca1\u6709\u78b0\u5230\u4efb\u4f55\u5b9e\u4f53\uff0c\u5b8c\u5168\u6293\u4e86\u4e2a\u7a7a\u3002\u8fd9\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u6545\u8ba9\u743c\u6069\u6014\u4f4f\u4e86\uff0c\u5446\u7acb\u5f53\u5730\uff0c\u7136\u540e\u4ed6\u611f\u89c9\u5230\u4fa7\u9762\u4f20\u6765\u7684\u731b\u70c8\u98ce\u58f0\uff0c\u773c\u89d2\u4f59\u5149\u77a5\u5230\u4e00\u6761\u6c34\u6876\u7c97\u7684\u78a7\u7eff\u86c7\u5c3e\u6b63\u5728\u51cc\u7a7a\u62bd\u6765\u3002\u6025\u6012\u4ea4\u52a0\u7684\u8482\u5a1c\u5c91\u6ca1\u6709\u91ca\u653e\u9b54\u6cd5\uff0c\u76f4\u63a5\u7528\u86c7\u5c3e\u7838\u6765\u3002</p><p>\u201c\u73ca\u5609\uff01\u201d</p><p>\u743c\u6069\u53eb\u51fa\u53e3\u4ee4\uff0c\u4e00\u76f4\u6302\u5728\u8170\u95f4\u7684\u9f99\u9cde\u76fe\u6d6e\u7a7a\u800c\u8d77\uff0c\u66ff\u4e3b\u4eba\u62b5\u6321\u8fd9\u4e00\u51fb\u3002\u4f46\u8482\u5a1c\u5c91\u7684\u529b\u91cf\u8fdc\u8fdc\u8d85\u51fa\u9884\u8ba1\uff0c\u556a\u5730\u4e00\u58f0\u95f7\u54cd\uff0c\u9f99\u9cde\u76fe\u88ab\u9707\u98de\u51fa\u53bb\u3002\u86c7\u5c3e\u6765\u52bf\u7a0d\u7f13\uff0c\u4f46\u4f9d\u65e7\u91cd\u91cd\u5730\u51fb\u5728\u743c\u6069\u8eab\u4e0a\u3002</p><p>\u957f\u888d\u4e0a\u7684\u80f8\u9488\u9aa4\u7136\u95ea\u70c1\uff0c\u6cdb\u51fa\u5fae\u5fae\u84dd\u5149\uff0c\u743c\u6069\u7684\u8eab\u5468\u51fa\u73b0\u4e00\u5c42\u65e0\u5f62\u529b\u573a\uff0c\u66ff\u4ed6\u5378\u4e0b\u5927\u90e8\u5206\u51b2\u51fb\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u4f9d\u65e7\u88ab\u91cd\u91cd\u9707\u98de\u4e86\u51fa\u53bb\uff0c\u76f4\u8d8a\u8fc7\u796d\u575b\uff0c\u649e\u5728\u5927\u5385\u7684\u5899\u58c1\u4e0a\uff0c\u6454\u843d\u4e0b\u6765\u3002\u4ed6\u547b\u541f\u7740\uff0c\u611f\u89c9\u81ea\u5df1\u7684\u9aa8\u5934\u90fd\u8981\u6563\u67b6\u4e86\u3002</p><p>\u9ece\u660e\u4e4b\u77f3\u4f9d\u65e7\u60ac\u6d6e\u5728\u796d\u575b\u4e0a\u7a7a\uff0c\u6563\u53d1\u7740\u5fae\u5fae\u7684\u73ab\u7470\u8272\u5149\u8292\u3002\u743c\u6069\u4e0d\u77e5\u9053\u81ea\u5df1\u4e3a\u4ec0\u4e48\u78b0\u4e0d\u5230\u5b83\uff0c\u4eff\u4f5b\u5e7b\u5f71\u4e00\u822c\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u603b\u4e4b\u8fd9\u6b21\u5192\u9669\u53ea\u6015\u662f\u5f7b\u5e95\u5931\u8d25\u4e86\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u5374\u4e5f\u4e0d\u80fd\u675f\u624b\u5f85\u6bd9\u3002</p></article>"}}}]);
