(window.webpackJsonp=window.webpackJsonp||[]).push([[704],{727:function(p){p.exports={chapterTitle:null,title:"\u7b2c703\u7ae0 \u8fd4\u56de\u6821\u56ed",content:"<article><h2>\u7b2c703\u7ae0 \u8fd4\u56de\u6821\u56ed</h2><p>\u3000\u3000\u4e8c\u5341\u4e94\u65e5\uff0c\u7231\u8fbe\u96c6\u56e2\u53ec\u5f00\u7ecf\u9500\u5546\u5927\u4f1a\uff1b\u4e8c\u5341\u516d\u65e5\uff0c\u79d1\u738b\u7535\u5668\u53ec\u5f00\u7ecf\u9500\u5546\u5927\u4f1a\uff1b\u4e8c\u5341\u4e03\u65e5\uff0c\u65b0\u5143\u7535\u5668\u53ec\u5f00\u7ecf\u9500\u5546\u5927\u4f1a\u3002\u8fd9\u5f53\u4e2d\uff0c\u81ea\u7136\u662f\u4ee5\u7231\u8fbe\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u6700\u70ed\u95f9\uff0c\u4e0d\u4ec5\u6709\u5bf9\u5916\u5f00\u53d1\u7684\u4ea7\u54c1\u5c55\u89c8\uff0c\u5f53\u5929\u4e0b\u5348\u8fd8\u4e0e\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u8054\u5408\u4e3e\u529e\u4e86\u6570\u5b57\u624b\u673a\u6280\u672f\u53d1\u5c55\u8bba\u575b\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u5728\u4e8c\u5341\u4e94\u65e5\u771f\u6b63\u5f00\u59cb\u7684\u7231\u8fbe\u7535\u5b50\u4ea7\u54c1\u5c55\u89c8\u4e2d\uff0c\u9664\u4e86\u63a8\u51fa\u4e00\u6b3e\u4e2d\u7aef\u7684\u5bb6\u5ead\u5f71\u9662\u4ea7\u54c1\u4e4b\u5916\uff0c\u5e76\u6ca1\u6709\u5a92\u4f53\u6240\u671f\u5f85\u7684\u65b0\u6b3e\u6570\u5b57\u624b\u673a\u6216\u8005\u5176\u4ed6\u5168\u65b0\u7c7b\u578b\u7684\u7535\u5b50\u4ea7\u54c1\u63a8\u51fa\u3002\u5728\u65b0\u6280\u672f\u5c55\u793a\u65b9\u9762\uff0c\u4e5f\u4ec5\u4ec5\u5c55\u793a\u4e86\u4e00\u9879\u673a\u82af\u5149\u5934\u81ea\u52a8\u8c03\u6574\u6280\u672f\uff0c\u8fd9\u9879\u6280\u672f\u5e94\u7528\u4e8e\u6700\u65b0\u7684\u789f\u673a\u4e0a\uff0c\u8bfb\u789f\u7ea0\u9519\u80fd\u529b\u5f97\u5230\u8fdb\u4e00\u6b65\u7684\u589e\u5f3a\u3002</p><p>\u3000\u3000\u867d\u7136\u7231\u8fbe\u96c6\u56e2\u5e02\u573a\u90e8\u95e8\u5c06\u4ea7\u54c1\u5c55\u89c8\u529e\u5f97\u70ed\u70ed\u95f9\u95f9\uff0c\u4f46\u5728\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u7b49\u4eba\u7684\u773c\u775b\u91cc\uff0c\u5374\u6709\u4e9b\u5931\u671b\u4e86\uff0c\u5e76\u6ca1\u6709\u770b\u5230\u9526\u6e56\u6709\u4ee4\u4eba\u60ca\u8273\u7684\u65b0\u4ea7\u54c1\u3001\u65b0\u6280\u672f\u5c55\u793a\u51fa\u6765\uff0c\u90fd\u5728\u6697\u60f3\uff1a\u9526\u6e56\u8fd9\u6b21\u662f\u4e0d\u662f\u6709\u4e9b\u6258\u5927\u4e86\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u662f\u523b\u610f\u538b\u7740\u9526\u6e56\u7684\u57fa\u8c03\uff0c\u4e0d\u7ba1\u5916\u754c\u7684\u53cd\u5e94\u5982\u4f55\uff0c\u8fd9\u4e09\u5929\uff0c\u9526\u6e56\u9ad8\u5c42\u90fd\u6781\u5c11\u9732\u9762\uff0c\u7ecf\u9500\u5546\u5927\u4f1a\u4e5f\u597d\uff0c\u4ea7\u54c1\u5c55\u89c8\u4e5f\u597d\uff0c\u90fd\u662f\u7531\u5e02\u573a\u90e8\u95e8\u53bb\u6267\u884c\uff1b\u6570\u5b57\u624b\u673a\u6280\u672f\u53d1\u5c55\u8bba\u575b\uff0c\u9648\u4fe1\u751f\u4e5f\u53ea\u9732\u4e86\u4e00\u4e0b\u9762\u5c31\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7b49\u4eba\u5374\u7a9d\u5728\u8c61\u5c71\u5047\u65e5\u9152\u5e97\u91cc\uff0c\u522b\u4eba\u7684\u76ee\u5149\u7ed9\u5e02\u533a\u91cc\u7eb7\u7e41\u70ed\u95f9\u7684\u6d3b\u52a8\u6240\u5438\u5f15\uff0c\u8fd9\u91cc\u5c31\u50cf\u4e16\u5916\u6843\u6e90\uff0c\u65b9\u4fbf\u8eb2\u8d77\u6765\u8ba8\u8bba\u9526\u6e56\u672a\u6765\u7684\u6280\u672f\u53d1\u5c55\u5927\u8ba1\uff0c\u4e5f\u5c06\u4e3b\u4efb\u5de5\u7a0b\u5e08\u7ea7\u522b\u7684\u7814\u53d1\u4eba\u5458\u4e00\u8d77\u62c9\u8fdb\u6765\u8ba8\u8bba\u3002</p><p>\u3000\u3000\u4e8c\u5341\u516b\u65e5\uff0c\u70ed\u95f9\u4e86\u4e00\u5468\u7684\u6d77\u5dde\u4e5f\u9010\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u5f20\u606a\u968f\u8c2d\u4e91\u677e\u3001\u97e9\u683c\u3001\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\u3001\u97e6\u5143\u771f\u7b49\u7814\u53d1\u4eba\u5458\u5206\u4e58\u4e09\u8f86\u5927\u5df4\u8f66\u62b5\u8fbe\u5efa\u90ba\u3002</p><p>\u3000\u3000\u5c31\u8fd9\u6837\uff0c\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u5728\u6084\u65e0\u58f0\u606f\u4e2d\u6b63\u5f0f\u542f\u52a8\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5efa\u90ba\u7684\u79cb\u5929\u5f88\u77ed\u6682\uff0c\u8fd9\u65f6\u5019\u5bd2\u98ce\u745f\u745f\u5df2\u7ecf\u6709\u51ac\u5929\u7684\u6a21\u6837\u4e86\uff0c\u53c8\u503c\u5bd2\u6d41\u5357\u4e0b\uff0c\u521a\u5439\u8fc7\u5efa\u90ba\uff0c\u67cf\u6cb9\u8def\u79ef\u4e86\u4e00\u5c42\u68a7\u6850\u6811\u53f6\uff0c\u67af\u9ec4\u7684\u6811\u53f6\u968f\u7740\u98ce\u4e71\u8f6c\u3002</p><p>\u3000\u3000\u8d70\u5728\u6e56\u8fb9\uff0c\u98ce\u5f88\u5927\uff0c\u5439\u5f97\u5934\u53d1\u4e71\u626c\uff0c\u5f20\u606a\u5c06\u624b\u63d2\u5728\u8863\u515c\u91cc\uff0c\u80cc\u7740\u98ce\uff0c\u89c6\u7ebf\u505c\u5728\u5929\u9645\u7684\u4e00\u62b9\u8f7b\u4e91\u5e7d\u8fdc\u5904\u3002\u98ce\u5439\u8d77\u5343\u8936\u4e07\u76b1\u7684\u6e56\u6c34\uff0c\u6e56\u5cb8\u7684\u6a61\u6811\u5728\u5915\u9633\u7684\u7167\u5c04\u4e0b\uff0c\u6709\u7740\u7f24\u7eb7\u6591\u6593\u7684\u8272\u5f69\u3002</p><p>\u3000\u3000\u60f3\u8d77\u8bb8\u601d\u90a3\u65e5\u6e05\u6668\u505a\u8fc7\u7684\u68a6\uff0c\u8fd9\u6e56\u8fb9\u5c31\u5e94\u8be5\u79cd\u4e0a\u6a61\u6811\u2026\u2026</p><p>\u3000\u3000\u7ed9\u6e56\u7554\u9ad8\u5927\u7684\u3001\u679d\u7e41\u53f6\u8302\u7684\u6a61\u6811\u63a9\u6620\u7684\uff0c\u662f\u65b0\u5efa\u6210\u7684\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\uff0c\u5b9e\u9a8c\u56ed\u8d76\u5728\u5341\u4e00\u6708\u4e0b\u65ec\u4e4b\u524d\u843d\u6210\uff0c\u5982\u4eca\u53ef\u4ee5\u8bf4\u662f\u5728\u56fd\u5185\u6781\u4e3a\u8c6a\u534e\u7684\u6280\u672f\u56e2\u961f\u8fdb\u5165\uff0c\u5c06\u6210\u4e3a\u9526\u6e56\u672a\u6765\u53d1\u5c55\u7684\u6838\u5fc3\u52a8\u529b\u3002</p><p>\u3000\u3000\u53ef\u60dc\u8fd8\u8fdc\u8fdc\u4e0d\u591f\u554a\uff01</p><p>\u3000\u3000\u5c31\u7b97\u8fdc\u8fdc\u4e0d\u591f\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u603b\u4e0d\u80fd\u6307\u671b\u4e00\u53e3\u5403\u6210\u80d6\u5b50\u3002\u8fd9\u4e9b\u70e6\u4eba\u7684\u4e8b\u60c5\u5c31\u6682\u65f6\u629b\u4e4b\u8111\u540e\u5427\uff0c\u5f20\u606a\u624b\u63d2\u5728\u8863\u515c\u91cc\uff0c\u9006\u7740\u98ce\u88f9\u7740\u98ce\u8863\u5f80\u5b66\u5e9c\u5df7\u65b9\u5411\u8d70\u53bb\u3002</p><p>\u3000\u3000\u661f\u671f\u4e94\u7684\u4e0b\u5348\uff0c\u5728\u5b66\u5e9c\u5df7\u6e38\u8361\u7684\u5b66\u751f\u60c5\u4fa3\u5f88\u591a\uff0c\u5dee\u4e0d\u591a\u79bb\u5f00\u4e00\u4e2a\u6708\uff0c\u79bb\u5f00\u8fd9\u79cd\u60a0\u95f2\u8212\u9002\u7684\u751f\u6d3b\u4e5f\u5feb\u4e00\u4e2a\u6708\uff0c\u518d\u53eb\u4eba\u6000\u5ff5\u3002\u5f20\u606a\u4e0d\u6025\u7740\u8054\u7cfb\u675c\u98de\uff0c\u5728\u8857\u5fc3\u3001\u6761\u6728\u62fc\u6210\u7684\u957f\u6905\u4e0a\u5750\u4e0b\uff0c\u6ce8\u89c6\u7740\u5c4b\u810a\u4e0a\u9759\u5bc2\u800c\u5b64\u72ec\u7684\u5915\u9633\uff0c\u638f\u51fa\u70df\u6765\uff0c\u70b9\u4e0a\u4e00\u652f\u6162\u6162\u7684\u62bd\u8d77\u6765\uff0c\u6ce8\u89c6\u5728\u773c\u524d\u6765\u6765\u5f80\u5f80\u7684\u9752\u6625\u7684\u4eba\u7fa4\uff0c\u4f3c\u4e4e\u6d6e\u5728\u534a\u7a7a\u4e2d\u53bb\u770b\u8fd9\u4e00\u5207\u7684\u611f\u89c9\uff0c\u611f\u5230\u4e00\u4e1d\u7684\u5b64\u5bc2\u3002</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u5728\u5915\u9633\u5149\u8f89\u91cc\u82f1\u4fca\u800c\u51dd\u6bc5\u7684\u4fa7\u8138\uff0c\u9648\u5983\u84c9\u7a81\u7136\u611f\u609f\u4ed6\u7ed9\u70df\u96fe\u8f7b\u62e2\u4f4f\u7684\u5b64\u72ec\uff0c\u624b\u91cc\u6367\u7740\u4f20\u5355\uff0c\u4e0d\u6653\u5f97\u8981\u4e0d\u8981\u8d70\u5230\u4ed6\u9762\u524d\u5c06\u4f20\u5355\u9012\u8fc7\u53bb\u8bf4\u4e00\u58f0\u201c\u597d\u4e45\u4e0d\u89c1\u201d\u4e4b\u7c7b\u7684\u8bdd\u3002\u867d\u7136\u5728\u6d77\u5dde\u89c1\u8fc7\u4e00\u9762\uff0c\u4f46\u8fd8\u611f\u89c9\u4ed6\u79bb\u5f00\u4e86\u5f88\u4e45\u4f3c\u7684\u3002\u5728\u6d77\u5dde\u7684\u4ed6\uff0c\u7ed9\u795e\u79d8\u800c\u8000\u773c\u7684\u5149\u73af\u56f4\u7740\u3001\u6df7\u8ff9\u4e8e\u6210\u5e74\u4eba\u7684\u4e16\u754c\u91cc\uff0c\u610f\u6c14\u98ce\u53d1\u7684\u4ed6\u662f\u964c\u751f\u7684\uff1b\u8fd9\u4e00\u523b\uff0c\u624d\u80fd\u611f\u53d7\u5230\u4ed6\u7684\u60c5\u7eea\u4ee5\u53ca\u4ed6\u7709\u5934\u95f4\u91cc\u9501\u7740\u6de1\u6de1\u7684\u5b64\u72ec\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u662f\u4e5d\u4e03\u5e74\u7684\u51ac\u5929\u4e86\uff0c\u5386\u53f2\u7684\u8f68\u8ff9\u4e5f\u6e10\u6e10\u9762\u76ee\u5168\u975e\uff0c\u5750\u5728\u8fd9\u7269\u975e\u4eba\u662f\u7684\u5b66\u5e9c\u5df7\u91cc\uff0c\u90a3\u4e9b\u94ed\u523b\u5728\u5fc3\u95f4\u7684\u75d5\u8ff9\u7adf\u7136\u6d6e\u52a8\u8d77\u6765\uff0c\u5f20\u606a\u5c06\u70df\u5934\u6390\u706d\uff0c\u56de\u8fc7\u5934\u770b\u89c1\u9648\u5983\u84c9\u7ad9\u5728\u8eab\u540e\uff0c\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\u7684\u6a21\u6837\uff0c\u624b\u91cc\u6367\u7740\u4f20\u5355\u3002</p><p>\u3000\u3000\u201c\u540c\u5b66\uff0c\u53d1\u4f20\u5355\u554a\uff0c\u53d1\u4f20\u5355\u7684\u5973\u5b69\u5b50\u90fd\u6709\u4f60\u8fd9\u6837\u6c34\u7075\uff0c\u80fd\u4e0d\u80fd\u4ecb\u7ecd\u4e00\u4e24\u4e2a\u7ed9\u6211\u505a\u670b\u53cb\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4f38\u51fa\u624b\uff0c\u770b\u7740\u9648\u5983\u84c9\u5634\u89d2\u7684\u6d45\u7b11\u5c31\u50cf\u6ef4\u5230\u5ba3\u7eb8\u4e0a\u7684\u4e00\u6ef4\u58a8\u6e10\u6e10\u7684\u6e6e\u67d3\u5f00\uff0c\u90a3\u4e1d\u5b64\u5bc2\u5c31\u6536\u655b\u8fdb\u5fc3\u95f4\u770b\u4e0d\u5230\u75d5\u8ff9\u4e86\u3002</p><p>\u3000\u3000\u521b\u57df\u7f51\u5427\u7ecf\u8fc7\u4e24\u4e2a\u6708\u7684\u7b79\u5907\uff0c\u660e\u5929\u5c31\u8981\u6b63\u5f0f\u5bf9\u5916\u8425\u4e1a\uff0c\u5f20\u606a\u770b\u5230\u5ba3\u4f20\u5355\u4e0a\u6709\u514d\u8d39\u65f6\u95f4\u7684\u5ba3\u4f20\u8bed\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u675c\u98de\u8fd9\u5c0f\u5b50\uff0c\u660e\u5929\u5c31\u7b49\u7740\u8865\u73bb\u7483\u5427\u3002\u201d\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\u7ad9\u4e86\u8d77\u6765\uff0c\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u603b\u7b97\u56de\u5230\u8fd9\u6837\u7684\u751f\u6d3b\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u5ae3\u7136\u800c\u7b11\uff0c\u5979\u66f4\u559c\u6b22\u770b\u5230\u4e00\u4e2a\u6175\u61d2\u6563\u6162\u7684\u5f20\u606a\uff0c\u800c\u4e0d\u662f\u6574\u65e5\u6df7\u8ff9\u5728\u6210\u4eba\u4e16\u754c\u91cc\u3001\u7ed9\u795e\u79d8\u5149\u73af\u56f4\u4f4f\u7684\u5f20\u606a\uff0c\u5c06\u624b\u91cc\u7684\u4f20\u5355\u626c\u4e86\u626c\uff0c\u610f\u601d\u662f\u95ee\u4ed6\u8981\u4e0d\u8981\u5e2e\u81ea\u5df1\u53d1\u4f20\u5355\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e2e\u7740\u9648\u5983\u84c9\u5f88\u5feb\u7684\u5c06\u4f20\u5355\u6563\u5b8c\uff0c\u60f3\u7740\u675c\u98de\u8fd9\u5c0f\u5b50\u4eca\u5929\u5fc5\u5b9a\u7126\u8651\u96be\u5b89\uff0c\u5fc3\u5acc\u7f51\u5427\u90a3\u8fb9\u4eba\u591a\u6742\u4e71\uff0c\u4fbf\u53ea\u6253\u4e86\u4e2a\u7535\u8bdd\u901a\u77e5\u4ed6\u4e00\u58f0\u201c\u6211\u56de\u6765\u4e86\u201d\uff0c\u4fbf\u4e0e\u9648\u5983\u84c9\u5355\u72ec\u53bb\u5403\u665a\u996d\u3002</p><p>\u3000\u3000\u6015\u7ed9\u675c\u98de\u4ed6\u4eec\u649e\u5230\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u8fd8\u662f\u4ece\u5357\u8fb9\u7684\u5df7\u5b50\u53e3\u7ed5\u5230\u5185\u5df7\u91cc\u91cd\u65b0\u8425\u4e1a\u7684\u6c88\u8bb0\u8336\u9910\u5385\u3002\u7ad9\u5728\u67dc\u53f0\u524d\u70b9\u4e86\u4e00\u4efd\u4e45\u8fdd\u7684\u8304\u6c41\u76d6\u6d47\u996d\uff0c\u5f20\u606a\u4e00\u8fb9\u638f\u7968\u5939\u4e00\u8fb9\u95ee\u9648\u5983\u84c9\u5403\u4ec0\u4e48\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u7968\u5939\u7adf\u7136\u662f\u7a7a\u7684\u3002\u8fd9\u4e9b\u5929\uff0c\u4ed6\u54ea\u6709\u81ea\u5df1\u7528\u94b1\u7684\u673a\u4f1a\uff1f\u4e5f\u6839\u672c\u6ca1\u6709\u53bb\u6ce8\u610f\u7968\u5939\u6709\u6ca1\u6709\u73b0\u91d1\uff0c\u53ea\u5f97\u8146\u7740\u8138\u8ba9\u9648\u5983\u84c9\u5230\u67dc\u53f0\u524d\u6765\u5e2e\u4ed6\u4ed8\u8d26\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5b66\u5e9c\u5df7\u4f4d\u4e8e\u4e09\u6821\u4e4b\u95f4\uff0c\u50cf\u9648\u5983\u84c9\u8fd9\u822c\u7684\u5bb9\u989c\u4e0e\u6c14\u8d28\uff0c\u5373\u4f7f\u5728\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u5927\u8fd9\u4e24\u6240\u7f8e\u5973\u5982\u4e91\u7684\u5b66\u6821\u4e5f\u662f\u51e4\u6bdb\u9e9f\u89d2\uff0c\u51fa\u73b0\u5728\u5b66\u5e9c\u5df7\u6c88\u8bb0\u8336\u9910\u5385\u91cc\u81ea\u7136\u60f9\u4eba\u77a9\u76ee\u3002\u5468\u56f4\u7684\u7537\u5b69\u5b50\u5fc3\u91cc\u90fd\u5728\u60f3\uff0c\u8c01\u80fd\u6709\u5e78\u8bf7\u5230\u5979\u5403\u665a\u996d\uff0c\u5c31\u5df2\u7ecf\u662f\u5403\u5230\u5929\u5927\u7684\u72d7\u5c4e\u8fd0\u4e86\uff0c\u7adf\u7136\u8fd8\u8981\u8ba9\u5979\u6765\u4ed8\u8d26\uff1f\u5468\u56f4\u7684\u7537\u751f\u770b\u5230\u8fd9\u4e00\u5e55\u90fd\u5feb\u51fa\u79bb\u6124\u6012\u4e86\uff0c\u773c\u775b\u77aa\u5f97\u8d3c\u5706\uff0c\u8981\u5c06\u5f20\u606a\u751f\u541e\u6d3b\u5265\u4e86\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u9876\u7740\u5de8\u5927\u7684\u538b\u529b\uff0c\u4e0e\u9648\u5983\u84c9\u5e76\u80a9\u7ad9\u5728\u67dc\u53f0\uff0c\u8f7b\u58f0\u611f\u6168\u8bf4\uff1a\u201c\u5509\uff0c\u7537\u4eba\u8981\u5403\u8f6f\u996d\uff0c\u5fc3\u7406\u7d20\u8d28\u8fd8\u8981\u4e0d\u662f\u4e00\u822c\u5f3a\u624d\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u6251\u54e7\u4e00\u7b11\uff0c\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u4e00\u5b9a\u8981\u80e1\u8a00\u4e71\u8bed\u5c06\u81ea\u5df1\u8bf4\u7684\u5982\u6b64\u4e0d\u582a\uff0c\u8fd9\u5a07\u6028\u542b\u7f9e\u7684\u795e\u6001\u8ba9\u5468\u56f4\u7537\u751f\u5ac9\u5992\u5f97\u4e03\u5b54\u751f\u70df\uff0c\u518d\u770b\u81ea\u5df1\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\uff0c\u5fc3\u60f3\u5373\u4f7f\u906e\u4f4f\u8138\u62fc\u8eab\u6750\u4e5f\u62fc\u4e0d\u8fc7\uff0c\u4e0d\u5982\u6740\u6389\u7701\u5fc3\u3002\u5374\u4e0d\u6653\u5f97\u4ed6\u4eec\u7684\u795e\u8272\u5df2\u7ecf\u8ba9\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u5927\u4e3a\u4e0d\u5feb\uff0c\u8fd9\u65f6\u5019\u5c31\u6709\u4e00\u4e2a\u5973\u5b69\u5b50\u51b7\u54fc\u4e86\u4e00\u58f0\u6389\u5934\u5c31\u8d70\uff0c\u7537\u5b69\u5b50\u624b\u8db3\u65e0\u63aa\u4e0d\u6653\u5f97\u8ffd\u8fd8\u662f\u4e0d\u8ffd\uff0c\u591a\u534a\u7537\u5b69\u5b50\u5fc3\u91cc\u5374\u5728\u60f3\uff1a\u957f\u8fd9\u6a21\u6837\u8fd8\u6709\u8138\u53d1\u813e\u6c14\uff1f</p><p>\u3000\u3000\u5f20\u606a\u624d\u4e0d\u7406\u4f1a\u522b\u4eba\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\uff0c\u62ff\u5230\u7968\u5355\uff0c\u4e0e\u9648\u5983\u84c9\u627e\u4e86\u4e00\u5904\u9760\u7a97\u7684\u5ea7\u4f4d\u5750\u4e0b\u3002\u6b64\u65f6\u7a97\u5916\u5929\u8272\u669d\u6697\uff0c\u8def\u706f\u5df2\u7ecf\u4eae\u8d77\u6765\uff0c\u7b3c\u7f69\u7740\u79cd\u690d\u6709\u56db\u6392\u68a7\u6850\u7684\u5b66\u5e9c\u5df7\u5916\u8857\u3002\u98ce\u5439\u8fc7\uff0c\u6709\u6811\u53f6\u98d8\u843d\uff0c\u542c\u7740\u98ce\u58f0\u5c31\u89c9\u5f97\u5bd2\u51b7\uff0c\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5efa\u90ba\u7684\u79cb\u5929\u771f\u77ed\uff0c\u6ca1\u60f3\u5230\u79bb\u5f00\u4e00\u4e2a\u6708\uff0c\u7adf\u7136\u9519\u8fc7\u5efa\u90ba\u7684\u7b2c\u4e00\u4e2a\u79cb\u5929\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5f88\u5728\u610f\u79cb\u5929\uff1f\u201d\u9648\u5983\u84c9\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u89c9\u5f97\u4ed6\u4e0d\u50cf\u90a3\u79cd\u53f9\u6625\u60b2\u79cb\u7684\u4eba\u554a\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u624b\u6258\u7740\u8138\u988a\u770b\u7740\u7a97\u5916\u7684\u66ae\u8272\uff0c\u5bf9\u5efa\u90ba\u7684\u6df1\u79cb\u6709\u7740\u6df1\u523b\u7684\u8bb0\u5fc6\uff0c\u8fd9\u79cd\u8bb0\u5fc6\u65e0\u4ece\u8ddf\u522b\u4eba\u8bf4\u8d77\uff0c\u53ea\u662f\u7b11\u7740\u8bf4\uff0c\u201c\u5efa\u90ba\u51ac\u5929\u5bd2\u51b7\u3001\u590f\u5929\u95f7\u70ed\uff0c\u68a7\u6850\u6811\u5230\u6625\u5929\u4f1a\u957f\u7ed2\u7403\uff0c\u6211\u5bf9\u8fd9\u4e9b\u4e1c\u897f\u6709\u8f7b\u5fae\u7684\u8fc7\u654f\uff0c\u90a3\u5c31\u5269\u4e0b\u79cb\u5929\u503c\u5f97\u671f\u5f85\uff0c\u504f\u504f\u5efa\u90ba\u7684\u79cb\u5929\u53c8\u592a\u77ed\u6682\uff0c\u8fd9\u624d\u8fc7\u53bb\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u8fd9\u5929\u6c14\u5c31\u5df2\u7ecf\u5165\u51ac\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8336\u9910\u5385\u867d\u8bf4\u662f\u5168\u65b0\u7684\uff0c\u4f46\u662f\u5e03\u5c40\u4e0e\u4ee5\u5f80\u65e0\u5f02\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u6311\u5728\u9760\u7a97\u7684\u5ea7\u4f4d\uff0c\u53ef\u4ee5\u770b\u5230\u9910\u5385\u53e6\u4e00\u89d2\u60ac\u6302\u7740\u7684\u7535\u89c6\u673a\u6b63\u5728\u64ad\u653e\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6210\u7acb\u7684\u4e13\u9898\u62a5\u9053\uff0c\u662f\u5efa\u90ba\u7535\u89c6\u53f0\u7684\u8282\u76ee\uff0c\u8282\u76ee\u91cc\u7684\u5609\u5bbe\u6b63\u8ddf\u4e3b\u6301\u4eba\u63b0\u7740\u624b\u6307\u5934\u5982\u6570\u5bb6\u73cd\u7684\u5386\u6570\u6210\u7acb\u8fd9\u4e48\u4e00\u4e2a\u534f\u4f1a\u5bf9\u56fd\u5185\u624b\u673a\u4ea7\u4e1a\u7684\u597d\u5904\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u7684\u4f01\u4e1a\u8981\u80fd\u591a\u51e0\u5bb6\u50cf\u7231\u8fbe\u7535\u5b50\u8fd9\u6837\u4e3b\u52a8\u627f\u62c5\u8d77\u8d23\u4efb\uff0c\u5c31\u4e0d\u4f1a\u56e0\u4e3a\u91cd\u590d\u5f15\u8fdb\u3001\u76f2\u76ee\u5f15\u8fdb\u800c\u6d6a\u8d39\u5927\u91cf\u7684\u8d44\u6e90\uff0c\u8282\u7ea6\u4e0b\u6765\u7684\u94b1\u4e0d\u6653\u5f97\u53ef\u4ee5\u7ed9\u8d2b\u56f0\u5730\u533a\u5efa\u591a\u5c11\u6240\u5b66\u6821\u3002\u201d\u5750\u5728\u9694\u58c1\u684c\u7684\u4e00\u540d\u7537\u4eba\u8ddf\u4ed6\u7684\u540c\u4f34\u611f\u6168\uff0c\u8a00\u8bed\u95f4\u6709\u7740\u4e0d\u4ec5\u7684\u60cb\u60dc\u6124\u6168\u3002</p><p>\u3000\u3000\u201c\u4f01\u4e1a\u8981\u627f\u62c5\u8d77\u8fd9\u79cd\u8d23\u4efb\u5417\uff1f\u201d\u9648\u5983\u84c9\u542c\u5230\u522b\u4eba\u7684\u8bae\u8bba\uff0c\u7591\u60d1\u7684\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4f01\u4e1a\u7f3a\u4e4f\u8fdc\u89c1\uff0c\u4f01\u4e1a\u8981\u627f\u62c5\u591a\u5927\u7684\u8d23\u4efb\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u4ed6\u8fd9\u65f6\u80fd\u4ee5\u4e00\u4e2a\u65c1\u89c2\u8005\u7684\u59ff\u6001\u53bb\u5ba1\u89c6\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u80fd\u770b\u5230\u8fd9\u80cc\u540e\u85cf\u7740\u79cd\u79cd\u5bf9\u9526\u6e56\u7684\u4e0d\u5229\u4e4b\u5904\uff0c\u9910\u5385\u91cc\u5b66\u751f\u7684\u8bae\u8bba\u4e5f\u53ea\u662f\u542c\u542c\u800c\u5df2\uff0c\u4e0d\u8fc7\u9648\u5983\u84c9\u95ee\u8d77\uff0c\u5012\u662f\u613f\u610f\u8ba4\u771f\u6559\u5979\uff0c\u60f3\u7740\u5979\u4ee5\u540e\u8981\u662f\u7ed9\u81ea\u5df1\u5f53\u79d8\u4e66\u3001\u5f53\u52a9\u7406\uff0c\u751f\u6d3b\u8be5\u591a\u6ecb\u6da6\u3002</p></article>"}}}]);
