(window.webpackJsonp=window.webpackJsonp||[]).push([[706],{729:function(p){p.exports={chapterTitle:null,title:"\u7b2c547\u7ae0 \u674e\u4ee3\u6843\u50f5",content:"<article><h2>\u7b2c547\u7ae0 \u674e\u4ee3\u6843\u50f5</h2><p>\u3000\u3000\u4ece\u4e2d\u73af\u94f6\u53a6\u5e7f\u573a\u56db\u5341\u516d\u5c42\u7684\u9ad8\u5904\u671b\u51fa\u53bb\uff0c\u538b\u7740\u7709\u5934\u770b\u7740\u8fdc\u5904\u5c71\u5dc5\u7684\u8584\u4e91\uff0c\u8c22\u5251\u5357\u5634\u5507\u5fae\u5fae\u6324\u7740\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u5f02\u52a8\uff0c\u8ba9\u4ed6\u5306\u5fd9\u4ece\u6d77\u5dde\u8d76\u5230\u9999\u6e2f\u6765\u3002</p><p>\u3000\u3000\u201c\u5c31\u5728\u8fd9\u697c\u524d\uff0c\u5f20\u606a\u8ba9\u845b\u836b\u5747\u5239\u8f66\u6e85\u4e86\u4e00\u8eab\u6c34\uff0c\u5f20\u606a\u6307\u4f7f\u5085\u4fca\u56de\u4e86\u845b\u836b\u5747\u53f3\u773c\u7a9d\u4e00\u62f3\uff1f\u201d\u8c22\u5251\u5357\u6487\u5634\u542b\u7740\u9634\u67d4\u7684\u6d45\u7b11\uff0c\u8d70\u8fc7\u53bb\u786e\u5b9a\u529e\u516c\u5ba4\u7684\u95e8\u5173\u5bc6\u5b9e\u4e86\uff0c\u514d\u5f97\u6709\u4eba\u7ecf\u8fc7\u65e0\u610f\u95f4\u542c\u5230\u4ec0\u4e48\uff0c\u56de\u5934\u770b\u7740\u7ad9\u5728\u7a97\u524d\u7684\u8c22\u77bb\uff0c\u201c\u4f60\u4eec\u4ee5\u4e3a\u5609\u4fe1\u5b9e\u4e1a\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u5f02\u52a8\u662f\u5f20\u606a\u5728\u95f9\u5c0f\u5b69\u5b50\u813e\u6c14\uff1f\u201d\u8c22\u5251\u5357\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u6240\u6709\u7684\u50b2\u6c14\u90fd\u6298\u5728\u4e00\u4e2a\u4eba\u624b\u91cc\uff0c\u4f60\u4ee5\u4e3a\u4ed6\u5c31\u662f\u65f6\u4e0d\u65f6\u95f9\u4e9b\u5c0f\u5b69\u5b50\u813e\u6c14\u7684\u4eba\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fd9\u4e24\u5e74\u5728\u8c22\u5bb6\u5a01\u52bf\u6e10\u91cd\uff0c\u8bed\u6c14\u5982\u6b64\u4e25\u5389\uff0c\u8c22\u77bb\u5012\u4e5f\u4e0d\u80fd\u8868\u793a\u4e0d\u6ee1\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u628a\u4e00\u4e2a\u6562\u4e0e\u674e\u8fdc\u6e56\u5206\u5ead\u6297\u793c\u7684\u4eba\u60f3\u5f97\u592a\u7b80\u5355\uff0c\u201d\u8c22\u5251\u5357\u7684\u8bed\u6c14\u91cc\u6ca1\u6709\u592a\u591a\u7684\u611f\u60c5\u8272\u5f69\uff0c\u201c\u4ed6\u4eec\u8fd9\u6bb5\u65f6\u95f4\u7684\u91cd\u5fc3\u5c31\u662f\u5609\u4fe1\u5b9e\u4e1a\u7684\u589e\u53d1\u5e76\u8d2d\u8ba1\u5212\uff0c\u4ed6\u4eec\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u5f02\u52a8\u53ea\u662f\u4ed6\u4eec\u5728\u4e1c\u5357\u4e9a\u9020\u52bf\u7684\u7b2c\u4e00\u6b65\uff0c\u53ea\u662f\u770b\u8d77\u6765\u65f6\u95f4\u6709\u4e9b\u5de7\u5408\u800c\u5df2\uff0c\u6216\u8bb8\u662f\u8fd9\u5c0f\u5b50\u60f3\u662f\u628a\u6211\u4eec\u5f80\u6b67\u8def\u5f15\uff0c\u8feb\u4f7f\u6211\u4eec\u81ea\u4e71\u9635\u811a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u4e0d\u662f\u6ca1\u8111\u5b50\u7684\u4eba\uff0c\u6709\u4e9b\u4e8b\u5b9e\u662f\u62b9\u4e0b\u8138\u6765\u627f\u8ba4\u7684\uff0c\u4ed6\u76b1\u7709\u770b\u7740\u7a97\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u845b\u836b\u5747\u4f3c\u4e4e\u5bf9\u90a3\u4e2a\u4eba\u6709\u5f88\u6df1\u7684\u6028\u6c14\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u96be\u9053\u4e0d\u597d\u5417\uff1f\u201d\u8c22\u5251\u5357\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u80fd\u60f3\u8c61\u624b\u91cc\u634f\u7740\u4e09\u56db\u5341\u4ebf\u8d44\u91d1\u7684\u9526\u6e56\u5417\uff1f\u5f88\u591a\u4e8b\u60c5\u4e0d\u662f\u4f60\u4e0d\u5e0c\u671b\u5c31\u4e0d\u4f1a\u53d1\u751f\u7684\uff0c\u9526\u6e56\u5373\u5c06\u8d85\u8d8a\u6b63\u6cf0\u96c6\u56e2\u5728\u7701\u5185\u7684\u5730\u4f4d\uff0c\u76ee\u524d\u770b\u6765\uff0c\u8fd9\u4f3c\u4e4e\u662f\u8c01\u4e5f\u963b\u6b62\u4e0d\u4e86\u7684\u4e8b\u60c5\u4e86\u3002\u4f60\u5e94\u8be5\u8ba4\u771f\u7814\u7a76\u4e00\u4e0b\u9526\u6e56\u8fd1\u4e09\u5e74\u6765\u7684\u53d1\u5c55\u8f68\u8ff9\uff0c\u5f20\u606a\u8fd9\u4e2a\u4eba\u6211\u5f88\u4e0d\u559c\u6b22\uff0c\u4f46\u662f\u4ed6\u8fd9\u4e2a\u4eba\u5374\u53c8\u8ba9\u4f60\u65e0\u6cd5\u5ffd\u89c6\uff1b\u6211\u4eec\u4e0d\u9700\u8981\u63a9\u8033\u76d7\u94c3\uff0c\u8fd9\u5bf9\u6211\u4eec\u6ca1\u6709\u4e00\u70b9\u597d\u5904\u3002\u524d\u4e9b\u65e5\u5b50\u9648\u4fe1\u751f\u4ee3\u8868\u9526\u6e56\u5728\u5317\u4eac\u5ba3\u79f0\u8981\u8ba9\u7231\u8fbe\u96c6\u56e2\u5168\u9762\u8fdb\u519b\u6d88\u8d39\u7535\u5b50\u4ea7\u4e1a\uff0c\u8fd8\u8981\u6295\u8d44\u516d\u5230\u516b\u4ebf\u6253\u9020\u4e00\u4e2a\u5927\u578b\u7684\u79d1\u6280\u56ed\u9879\u76ee\u6765\u5f25\u8865\u57fa\u7840\u6280\u672f\u7814\u53d1\u4e0a\u7684\u4e0d\u8db3\uff0c\u5f88\u591a\u4eba\u90fd\u4ee5\u4e3a\u8fd9\u53c8\u662f\u9526\u6e56\u7684\u4e00\u4e2a\u5927\u5ffd\u60a0\uff0c\u6211\u5374\u76f8\u4fe1\u9526\u6e56\u5c06\u9648\u4fe1\u751f\u6316\u8fc7\u53bb\u7edd\u4e0d\u662f\u4ec5\u4ec5\u5e0c\u671b\u9648\u4fe1\u751f\u5728\u6700\u540e\u5173\u5934\u5e2e\u5fd9\u5c06\u7231\u8fbe\u7535\u5b50\u5356\u6389\u2026\u2026\u770b\u5427\uff0c\u9526\u6e56\u5c06\u6c38\u8fdc\u90fd\u662f\u6211\u4eec\u79d1\u738b\u6240\u5fc5\u987b\u8981\u6500\u8d8a\u8fc7\u53bb\u7684\u4e00\u5ea7\u5c71\uff0c\u6709\u65f6\u5019\uff0c\u4f60\u8fd8\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u8fd9\u5ea7\u5c71\u8981\u51ed\u6211\u4eec\u81ea\u5df1\u7684\u529b\u91cf\u53bb\u6500\u8d8a\u975e\u5e38\u56f0\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u8981\u628a\u5609\u4fe1\u7535\u5b50\u5f80\u91cc\u626f\u5f97\u66f4\u6df1\uff1f\u201d\u8c22\u77bb\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4ec5\u4ec5\u662f\u8fd9\u4e2a\u95ee\u9898\uff0c\u8fd9\u4e9b\u5929\u6765\uff0c\u6211\u4e00\u76f4\u5728\u601d\u8003\u4e09\u53d4\u8ddf\u6211\u8bf4\u7684\u8bdd\uff0c\u65e0\u6cd5\u6b63\u89c6\u81ea\u5df1\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u5f88\u53ef\u80fd\u5c31\u6c38\u8fdc\u8d85\u8d8a\u4e0d\u8fc7\u53bb\u3002\u9648\u4fe1\u751f\u5230\u7231\u8fbe\u96c6\u56e2\u8d74\u4efb\uff0c\u5916\u7ecf\u8d38\u90e8\u7684\u526f\u90e8\u957f\u53f6\u81fb\u6c11\u8df3\u51fa\u6765\u66ff\u4ed6\u4eec\u6447\u65d7\u5450\u558a\uff0c\u8fd9\u4e0d\u662f\u5076\u7136\uff0c\u8fd9\u662f\u53f6\u5bb6\u516c\u7136\u8df3\u51fa\u6765\u652f\u6301\u9526\u6e56\uff0c\u8fd9\u4e5f\u662f\u674e\u8fdc\u6e56\u6700\u7ec8\u9009\u62e9\u8ba9\u6b65\u7684\u4e3b\u8981\u56e0\u7d20\u4e4b\u4e00\uff0c\u5f20\u606a\u4e0e\u53f6\u5bb6\u7684\u5173\u7cfb\uff0c\u6700\u521d\u662f\u4ece\u53f6\u5efa\u658c\u5f00\u59cb\u7684\uff0c\u4f46\u662f\u4ed6\u4e0e\u53f6\u5bb6\u4e4b\u95f4\u7684\u5173\u7cfb\u4e4b\u6df1\uff0c\u4f3c\u4e4e\u4e5f\u8d85\u8131\u6211\u4eec\u7684\u60f3\u8c61\u4e86\u3002\u53f6\u81fb\u6c11\u4ece\u53bb\u5e74\u5165\u79cb\u4ee5\u6765\u5c31\u4e00\u76f4\u5531\u8870\u4e9a\u6d32\u7684\u7ecf\u6d4e\u8d8b\u52bf\uff0c\u4ed6\u7684\u89c2\u70b9\u91cc\u6709\u4e9b\u5730\u65b9\u7684\u786e\u662f\u503c\u5f97\u6df1\u601d\u4e0e\u5fe7\u8651\u7684\uff0c\u4f46\u6211\u4eec\u4e0d\u80fd\u56e0\u564e\u5e9f\u98df\uff0c\u201d\u8c22\u5251\u5357\u8bf4\u9053\uff0c\u201c\u6211\u8ddf\u4e09\u53d4\uff0c\u8ddf\u5927\u8205\u5546\u91cf\u8fc7\uff0c\u5fc5\u8981\u7684\u51c6\u5907\u662f\u5fc5\u987b\u8981\u505a\u7684\u2026\u2026\u8fd8\u6709\uff0c\u51ed\u501f\u6211\u4eec\u81ea\u8eab\u7684\u529b\u91cf\u8981\u538b\u8fc7\u9526\u6e56\uff0c\u4f3c\u4e4e\u5f88\u56f0\u96be\u7684\uff0c\u8981\u627f\u8ba4\u8fd9\u4e00\u70b9\uff0c\u5f88\u56f0\u96be\uff0c\u4f60\u4e0d\u8981\u770b\u6211\u73b0\u5728\u8bf4\u5f97\u8fd9\u4e48\u8f7b\u677e\uff0c\u5728\u5bb6\u91cc\uff0c\u6211\u7ed9\u4e09\u53d4\u72e0\u72e0\u7684\u8bad\u8fc7\uff0c\u6240\u4ee5\u4f60\u4e5f\u4e0d\u8981\u602a\u6211\u5bf9\u4f60\u8bf4\u8bdd\u96be\u542c\uff1b\u6211\u4eec\u8981\u5c06\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u66f4\u6df1\u7684\u626f\u8fdb\u6765\uff0c\u4f46\u662f\u4e0d\u80fd\u56e0\u6b64\u5c06\u4e3b\u52a8\u6743\u62f1\u624b\u8ba9\u7ed9\u4ed6\u4eec\uff0c\u5c31\u8981\u8d39\u4e00\u756a\u8111\u7b4b\u2026\u2026\u6ca1\u60f3\u5230\u9999\u6e2f\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u6211\u60f3\u5f20\u606a\u8ba9\u5085\u4fca\u7ed9\u845b\u836b\u5747\u7684\u90a3\u4e00\u62f3\u5927\u6982\u4e5f\u6fc0\u8d77\u4ed6\u7684\u6027\u5b50\u5427\uff01\u201d</p><p>\u3000\u3000\u845b\u666f\u8bda\u6b7b\u540e\uff0c\u5bb6\u65cf\u8d22\u5bcc\u8fdb\u884c\u5206\u5272\uff0c\u845b\u660e\u4fe1\u7ee7\u627f\u7684\u8d44\u4ea7\u662f\u6574\u4e2a\u6b63\u6cf0\u96c6\u56e2\u7684\u56db\u4e94\u500d\uff0c\u8981\u4e0e\u845b\u660e\u4fe1\u6253\u4ea4\u9053\uff0c\u8fd8\u8981\u5360\u636e\u4e3b\u52a8\uff0c\u5374\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u4f46\u662f\u8c22\u5251\u5357\u4e5f\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u624b\u91cc\u7684\u634f\u7740\u4e09\u56db\u5341\u4ebf\u8d44\u91d1\u7684\u9526\u6e56\u9664\u4e86\u5c06\u845b\u660e\u4fe1\u7236\u5b50\u626f\u8fdb\u6765\u8fd8\u8981\u600e\u6837\u624d\u80fd\u5bf9\u4ed8\u2026\u2026</p><p>\u3000\u3000\u201c\u90a3\u6211\u4eec\u73b0\u5728\u8981\u600e\u4e48\u505a\uff1f\u201d\u8c22\u77bb\u95ee\u9053\uff0c\u201c\u845b\u836b\u5747\u4f3c\u4e4e\u60f3\u9488\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u5f02\u52a8\u91c7\u53d6\u4e00\u4e9b\u63aa\u65bd\uff0c\u6309\u7167\u4f60\u7684\u60f3\u6cd5\uff0c\u6211\u4eec\u771f\u8981\u8ddf\u7740\u52a8\uff0c\u5c31\u53ef\u4ee5\u7ed9\u5f15\u5165\u6b67\u9014\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u8981\u4efb\u4ed6\u7684\u6027\u5b50\u624d\u597d\uff0c\u201d\u8c22\u5251\u538b\u7740\u5634\u89d2\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u4e0d\u51fa\u6765\u7684\u9634\u6092\uff0c\u53c8\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u9053\uff0c\u201c\u6211\u9700\u4f60\u6765\u914d\u5408\u7740\u5531\u9ed1\u8138\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u5531\uff1f\u201d\u8c22\u77bb\u8bf4\u9053\uff0c\u201c\u6211\u77e5\u9053\u4f60\u9b3c\u4e3b\u610f\u591a\uff0c\u4e0d\u8fc7\u6211\u6f14\u620f\u8fd8\u662f\u6709\u4e00\u624b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u549a\u549a\u549a\u2026\u2026\u201d</p><p>\u3000\u3000\u529e\u516c\u5ba4\u95e8\u4e2d\u95f4\u5d4c\u7740\u4e00\u5757\u73bb\u7483\uff0c\u6620\u51fa\u6572\u95e8\u4eba\u7684\u7a88\u7a95\u8eab\u5f71\uff0c\u770b\u7740\u95e8\u628a\u5728\u626d\u52a8\uff0c\u8c22\u5251\u5357\u77e5\u9053\u662f\u9648\u9759\uff0c\u5fd9\u8d70\u8fc7\u6765\u5e2e\u5979\u5f00\u95e8\u3002</p><p>\u3000\u3000\u9648\u9759\u5bf9\u845b\u836b\u5747\u6ee1\u8179\u610f\u89c1\uff0c\u4e4b\u524d\u63d0\u524d\u56de\u6d77\u5dde\uff0c\u8fd9\u6b21\u53c8\u966a\u8c22\u5251\u5357\u5230\u9999\u6e2f\uff0c\u6765\u56de\u5954\u6ce2\u4e5f\u5f88\u8f9b\u82e6\uff0c\u5979\u8fdf\u7591\u7684\u770b\u4e86\u770b\u8c22\u5251\u5357\u4e0e\u8c22\u77bb\u4e24\u4eba\uff1a\u201c\u4e24\u4e2a\u5927\u8001\u7237\u4eec\uff0c\u5927\u767d\u5929\u7684\u5728\u529e\u516c\u5ba4\u53cd\u9501\u7740\u95e8\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u8c22\u77bb\u6b6a\u7740\u5634\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u5251\u5357\u53c8\u4e0d\u662f\u628a\u4e00\u4e2a\u5c0f\u59d1\u5a18\u9501\u5c4b\u91cc\uff0c\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5478\uff01\u201d\u9648\u9759\u5550\u5634\u8f7b\u5478\u4e86\u4e00\u58f0\uff0c\u201c\u8c01\u7ba1\u5f97\u4e86\u4ed6\u8fd9\u4e48\u591a\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u52a8\u4f5c\uff0c\u845b\u836b\u5747\u8d77\u521d\u662f\u4ee5\u8f7b\u677e\u81ea\u5982\u7684\u59ff\u6001\u53bb\u9762\u5bf9\uff0c\u4f46\u662f\u51e0\u5929\u6765\u53cd\u9988\u56de\u6765\u7684\u9500\u552e\u6570\u636e\u5374\u4e0d\u5927\u4e50\u89c2\uff0c\u5c31\u4e0d\u5bb9\u4ed6\u4e0d\u5c0f\u5fc3\u5bf9\u5f85\u3002\u5f53\u4ed6\u5efa\u8bae\u91c7\u53d6\u9488\u5bf9\u6027\u63aa\u65bd\u65f6\uff0c\u8c22\u77bb\u90fd\u6ca1\u6709\u673a\u4f1a\u8df3\u51fa\u6765\u5531\u9ed1\u8138\uff0c\u9648\u9759\u5c31\u5148\u63d0\u51fa\u575a\u51b3\u7684\u53cd\u5bf9\u610f\u89c1\uff1a\u201c\u7231\u8fbe\u6709\u7231\u8fbe\u7684\u8282\u594f\u3001\u79d1\u738b\u4e5f\u5e94\u8be5\u6709\u79d1\u738b\u7684\u8282\u594f\uff0c\u4e0d\u5e94\u8be5\u4ea6\u6b65\u4ea6\u8d8b\u8ddf\u7740\u522b\u4eba\u7684\u6b65\u4f10\u8d70\uff1b\u4f55\u51b5\u6211\u4ee5\u4e3a\u76ee\u524d\u5728\u4e1c\u5357\u4e9a\u7684\u52a8\u4f5c\u5df2\u7ecf\u591f\u5927\u4e86\uff0c\u8574\u542b\u5f88\u591a\u4e0d\u53ef\u63a7\u7684\u98ce\u9669\uff0c\u5e94\u8be5\u6ce8\u610f\u73b0\u91d1\u6d41\u4e0e\u5e94\u6536\u8d26\u6b3e\u7684\u95ee\u9898\uff0c\u5e02\u573a\u5360\u6709\u7387\u662f\u7b2c\u4e00\u8fd8\u662f\u7b2c\u4e8c\uff0c\u5728\u76ee\u524d\u9636\u6bb5\u5e76\u6ca1\u6709\u5b9e\u8d28\u6027\u7684\u610f\u4e49\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u6b63\u7684\u8d22\u5bcc\u5374\u5f80\u5f80\u8574\u85cf\u5728\u98ce\u9669\u4e4b\u4e2d\u7684\uff0c\u201d\u845b\u836b\u5747\u5e76\u4e0d\u5426\u8ba4\u76ee\u524d\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u6fc0\u8fdb\u6269\u5f20\u8574\u542b\u7740\u4e00\u5b9a\u7684\u98ce\u9669\uff0c\u4f46\u662f\u4ed6\u6709\u4ed6\u81ea\u5df1\u7684\u575a\u6301\uff0c\u201c\u8fd9\u662f\u79d1\u738b\u80fd\u8fc5\u901f\u5728\u5c40\u90e8\u5e02\u573a\u8d85\u8d8a\u7231\u8fbe\u7684\u673a\u4f1a\uff0c\u53ea\u6709\u6293\u4f4f\u8fd9\u4e2a\u673a\u4f1a\uff0c\u6211\u4eec\u624d\u6709\u53ef\u80fd\u5728\u5185\u5730\u7684\u5e02\u573a\u8ffd\u8d76\u4e0a\u7231\u8fbe\u7684\u6b65\u4f10\u2026\u2026\u201d\u845b\u836b\u5747\u95ee\u8c22\u5251\u5357\uff0c\u201c\u5251\u5357\uff0c\u4f60\u6765\u8bf4\u4ec0\u4e48\u770b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u554a\uff0c\u201d\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u8fd8\u662f\u5148\u542c\u542c\u4f60\u4eec\u7684\u610f\u89c1\u2026\u2026\u201d\u4fa7\u8fc7\u5934\u95ee\u8c22\u77bb\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u597d\u7684\u770b\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u9759\u8bf4\u7684\u73b0\u91d1\u6d41\u4e0e\u5e94\u6536\u8d26\u6b3e\u7684\u95ee\u9898\uff0c\u6211\u89c9\u5f97\u7684\u786e\u6709\u5fc5\u8981\u6ce8\u610f\u4e00\u4e0b\uff0c\u6bd5\u7adf\u79d1\u738b\u4e0d\u662f\u8170\u7f20\u767e\u4ebf\u7684\u845b\u5bb6\uff0c\u7a0d\u4e0d\u6ce8\u610f\uff0c\u8d44\u91d1\u88e2\u5c31\u53ef\u80fd\u51fa\u73b0\u8ba9\u4eba\u5934\u75bc\u7684\u9ebb\u70e6\u2026\u2026\u201d\u8c22\u77bb\u9634\u4e0d\u9634\u3001\u9633\u4e0d\u9633\u7684\u8bf4\u9053\uff0c\u201c\u6211\u8fd9\u4eba\u6ca1\u5230\u56fd\u5916\u7559\u5b66\u89c1\u8bc6\u8fc7\u4e16\u9762\uff0c\u8d85\u8fc7\u7231\u8fbe\u53c8\u80fd\u600e\u4e48\u6837\uff0c\u4e16\u754c\u6bd4\u7231\u8fbe\u7535\u5b50\u5b9e\u529b\u66f4\u5f3a\u7684\u7535\u5b50\u4f01\u4e1a\u6bd4\u6bd4\u7686\u662f\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u56e0\u4e3a\u4e2a\u4eba\u610f\u6c14\u786c\u8981\u5c06\u516c\u53f8\u5e26\u5165\u5371\u9669\u7684\u5883\u5730\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u201d\u845b\u836b\u5747\u7709\u5934\u4e00\u7ad6\uff0c\u201c\u4f60\u8bf4\u6211\u8fd9\u4e48\u505a\u662f\u56e0\u4e3a\u4e2a\u4eba\u610f\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9664\u4e86\u8fd9\u4e2a\u8fd8\u80fd\u662f\u4ec0\u4e48\uff0c\u201d\u8c22\u77bb\u51b7\u7136\u4e00\u7b11\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u5728\u4e1c\u5357\u4e9a\u6709\u4e9b\u5f02\u52a8\uff0c\u6211\u4eec\u5c31\u8981\u4fee\u6b63\u5e74\u521d\u62df\u5b9a\u7684\u5e02\u573a\u8ba1\u5212\uff0c\u63a5\u4e0b\u6765\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u90fd\u8981\u8fdb\u5165\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u6211\u4eec\u4e5f\u8981\u9891\u9891\u4fee\u6539\u6211\u4eec\u5728\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u8ba1\u5212\uff1f\u201d\u8c22\u77bb\u51b7\u5632\u70ed\u8bbd\u9053\uff0c\u201c\u4e0d\u5c31\u662f\u773c\u7a9d\u7ed9\u6253\u4e86\u4e00\u62f3\u5417\uff1f\u6709\u5fc5\u8981\u8ddf\u8fd9\u79cd\u4eba\u65a4\u65a4\u8ba1\u8f83\uff0c\u4e5f\u4e0d\u6015\u5931\u4e86\u4f60\u845b\u5bb6\u5927\u5c11\u7237\u7684\u8eab\u4efd\uff01\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u77a5\u773c\u770b\u89c1\u845b\u836b\u5747\u9edd\u9ed1\u7684\u8138\u6c14\u5f97\u9171\u7ea2\uff0c\u7ed9\u8c22\u77bb\u4e22\u4e86\u4e00\u4e2a\u4e25\u5389\u7684\u773c\u795e\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff01\u201d</p><p>\u3000\u3000\u201c\u6211\u54ea\u91cc\u6709\u80e1\u8bf4\u516b\u9053\uff0c\u201d\u8c22\u77bb\u677f\u7740\u8138\uff0c\u6545\u610f\u4e0d\u4e70\u8c22\u5251\u5357\u7684\u8d26\uff0c\u201c\u5609\u4fe1\u5b9e\u4e1a\u4e3a\u4ec0\u4e48\u8fd9\u6bb5\u65f6\u95f4\u4f1a\u5728\u4e1c\u5357\u4e9a\u52a0\u5927\u5e7f\u544a\u6295\u653e\uff0c\u539f\u56e0\u662f\u4ec0\u4e48\uff0c\u8fd8\u9700\u8981\u6211\u6765\u8bf4\u5417\uff1f\u8c01\u62c9\u5c4e\u8c01\u6765\u64e6\u5c41\u80a1\uff0c\u6ca1\u5fc5\u8981\u5c06\u6574\u95f4\u516c\u53f8\u90fd\u626f\u8fdb\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u591f\u4e86\uff01\u201d\u8c22\u5251\u5357\u62cd\u4e86\u4e00\u4e0b\u684c\u5b50\u8ba9\u8c22\u77bb\u4f4f\u5634\uff0c\u201c\u8bf4\u8bdd\u8981\u6ce8\u610f\u5206\u5bf8\uff01\u201d</p><p>\u3000\u3000\u8c22\u77bb\u9876\u66ff\u9648\u9759\u5230\u9999\u6e2f\u6765\u7763\u4fc3\u6d77\u5916\u5206\u516c\u53f8\u7684\u7ecf\u8425\uff0c\u8868\u73b0\u4e00\u76f4\u5f88\u6c89\u9ed8\uff0c\u5e76\u4e0d\u5e72\u6d89\u845b\u836b\u5747\u4e3b\u5bfc\u6d77\u5916\u516c\u53f8\u7684\u4e1a\u52a1\uff0c\u845b\u836b\u5747\u5374\u6ca1\u6709\u60f3\u5230\u4ed6\u4f1a\u7a81\u7136\u95f4\u5c31\u53d1\u96be\uff0c\u624b\u811a\u90fd\u6c14\u5f97\u53d1\u6296\uff0c\u867d\u7136\u8c22\u5251\u5357\u5bf9\u8c22\u77bb\u4e25\u5389\u7684\u58f0\u97f3\u8ba9\u4ed6\u597d\u53d7\u4e00\u4e9b\uff0c\u4f46\u662f\u770b\u5230\u8c22\u77bb\u6311\u8845\u7684\u6000\u7591\u773c\u795e\u8ba9\u4ed6\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u5fc3\u5934\u8d77\u4e86\u6bdb\uff0c\u7ad9\u8d77\u6765\u6307\u7740\u8c22\u77bb\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5c41\u80a1\u6211\u6765\u64e6\uff0c\u4e0d\u52b3\u4f60\u4e00\u5206\u529b\u6c14\uff0c\u6d77\u5916\u5206\u516c\u53f8\u91cc\uff0c\u4f60\u90a3\u70b9\u80a1\u4efd\uff0c\u6211\u90fd\u4e70\u4e0b\u6765\uff0c\u6240\u4ee5\u8bf7\u4f60\u4e0d\u8981\u5bf9\u6d77\u5916\u5206\u516c\u53f8\u7684\u4e1a\u52a1\u6307\u624b\u5212\u811a\uff01\u201d</p><p>\u3000\u3000\u201c\u6ca1\u90a3\u4e48\u8f7b\u677e\u5427\uff1f\u201d\u8c22\u77bb\u8df7\u8d77\u4e8c\u90ce\u817f\uff0c\u201c\u5927\u96c5\u96c6\u56e2\u4e5f\u89c9\u5f97\u6d77\u5916\u5206\u516c\u53f8\u5728\u4e1c\u5357\u4e9a\u7684\u6269\u5f20\u8fc7\u4e8e\u884c\u9669\uff0c\u96be\u9053\u4f60\u4e5f\u8981\u5c06\u5927\u96c5\u96c6\u56e2\u5728\u6d77\u5916\u5206\u516c\u53f8\u7684\u80a1\u4efd\u4e70\u4e0b\u6765\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5982\u679c\u6709\u5206\u6b67\uff0c\u8fd9\u4e48\u89e3\u51b3\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u201d\u845b\u836b\u5747\u4fa7\u5934\u770b\u4e86\u8c22\u5251\u5357\u4e00\u773c\uff0c\u201c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u522b\u4eba\u7684\u60f3\u6cd5\u6211\u4e0d\u7ba1\uff0c\u201d\u8c22\u5251\u5357\u8bf4\u9053\uff0c\u201c\u6211\u662f\u652f\u6301\u4f60\uff0c\u6211\u4e2a\u4eba\u4e5f\u7edd\u4e0d\u4f1a\u4ece\u6d77\u5916\u516c\u53f8\u64a4\u80a1\u7684\u3002\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u7ed9\u8c22\u77bb\u4eca\u5929\u7a81\u5982\u5176\u6765\u7684\u53d1\u96be\u641e\u5f97\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\uff0c\u8c22\u5251\u5357\u7684\u8868\u6001\u8ba9\u4ed6\u5fc3\u91cc\u591a\u5c11\u597d\u53d7\u4e00\u4e9b\uff0c\u6d77\u5916\u5206\u516c\u53f8\u7684\u6ce8\u8d44\u989d\u5ea6\u624d\u4e00\u4e2a\u4ebf\u800c\u5df2\uff0c\u8c22\u77bb\u4e0e\u5927\u96c5\u96c6\u56e2\u8981\u9000\u51fa\u53bb\u5c31\u9000\u51fa\u53bb\uff0c\u53cd\u800c\u80fd\u8ba9\u4ed6\u4e2a\u4eba\u5bf9\u6d77\u5916\u5206\u516c\u53f8\u63a7\u80a1\uff0c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u597d\u7684\u5462\uff1f</p><p>\u3000\u3000\u201c\u8fd8\u6709\u5e94\u6536\u8d26\u6b3e\u7684\u95ee\u9898\uff0c\u201d\u8c22\u77bb\u53c8\u6162\u6761\u4e1d\u7406\u7684\u8bf4\u9053\uff0c\u201c\u8fd9\u4e2a\u95ee\u9898\u8be5\u5982\u4f55\u89e3\u51b3\uff0c\u603b\u4e0d\u80fd\u8ba9\u6d77\u5916\u5206\u516c\u53f8\u65e0\u9650\u5236\u5360\u7528\u79d1\u738b\u7684\u8d27\u6b3e\u5427\uff1f\u6d77\u5916\u5206\u516c\u53f8\u7684\u73b0\u91d1\u6d41\u6ca1\u6709\u95ee\u9898\uff0c\u79d1\u738b\u7684\u8d44\u91d1\u88e2\u5374\u8981\u65ad\u4e86\uff01\u201d</p><p>\u3000\u3000\u6d77\u5916\u5206\u516c\u53f8\u72ec\u7acb\u6ce8\u518c\u6ce8\u8d44\uff0c\u8d1f\u8d23\u6d77\u5916\u5e02\u573a\u7684\u5f00\u62d3\uff0c\u8d62\u4e8f\u5374\u662f\u8981\u81ea\u8d1f\u7684\uff0c\u4e0d\u8fc7\u6d77\u5916\u5206\u516c\u53f8\u5728\u4e00\u5f00\u59cb\u6ce8\u8d44\u65f6\uff0c\u5404\u5bb6\u80a1\u4e1c\u7684\u6ce8\u8d44\u6bd4\u4f8b\u662f\u7b49\u540c\u4e8e\u79d1\u738b\u7684\uff0c\u60df\u4e00\u4e0d\u540c\u7684\u662f\uff0c\u845b\u836b\u5747\u7236\u5b50\u5bf9\u79d1\u738b\u7684\u6301\u80a1\u662f\u901a\u8fc7\u5609\u4fe1\u7535\u5b50\u5b9e\u73b0\u7684\uff0c\u5bf9\u6d77\u5916\u5206\u516c\u53f8\u7684\u6301\u80a1\uff0c\u5374\u662f\u845b\u836b\u5747\u76f4\u63a5\u6ce8\u8d44\u56db\u5343\u4e07\u6e2f\u5143\uff0c\u6240\u4ee5\u5728\u5b9e\u8d28\u4e0a\uff0c\u6d77\u5916\u5206\u516c\u53f8\u4e0e\u79d1\u738b\u4e0d\u4f1a\u6709\u592a\u5c16\u9510\u7684\u5229\u76ca\u77db\u76fe\u3002\u5373\u4f7f\u845b\u836b\u5747\u60f3\u79c1\u4eba\u51fa\u8d44\u5c06\u8c22\u77bb\u4e0e\u5927\u96c5\u96c6\u56e2\u7684\u80a1\u4efd\u4e70\u4e0b\u6765\uff0c\u90a3\u5c31\u6709\u4e86\u5229\u76ca\u77db\u76fe\uff0c\u8c22\u77bb\u4e0e\u5927\u96c5\u96c6\u56e2\u4e0d\u540c\u610f\u6d77\u5916\u5206\u516c\u53f8\u7ee7\u7eed\u5360\u7528\u79d1\u738b\u7684\u8d27\u6b3e\uff0c\u90a3\u4e5f\u662f\u8bf4\u5f97\u8fc7\u53bb\u7684\u3002</p><p>\u3000\u3000\u201c\u4e24\u4e09\u4e2a\u4ebf\u7684\u8d27\u6b3e\uff0c\u6211\u79c1\u4eba\u8fd8\u57ab\u5f97\u8d77\uff0c\u7edd\u4e0d\u4f1a\u62d6\u79d1\u738b\u7684\u540e\u817f\uff0c\u201d\u845b\u836b\u5747\u79c1\u4eba\u540d\u4e0b\u4e5f\u5c31\u4e24\u4e09\u4ebf\u7684\u8d44\u4ea7\uff0c\u5176\u4ed6\u7684\u90fd\u5728\u4ed6\u8001\u5b50\u845b\u660e\u4fe1\u7684\u540d\u4e0b\uff0c\u4f46\u662f\u8ba9\u8c22\u77bb\u4e0d\u9634\u4e0d\u9633\u63ed\u75ae\u75a4\u7684\u8bdd\u60f9\u6bdb\u4e86\u6027\u5b50\uff0c\u4e5f\u5c31\u8003\u8651\u4e0d\u4e86\u592a\u591a\u4e86\uff0c\u5e02\u573a\u6025\u901f\u6269\u5f20\u671f\u8981\u6293\u4f4f\u96be\u5f97\u7684\u673a\u9047\u5c31\u52bf\u5fc5\u5192\u4e00\u5b9a\u7684\u98ce\u9669\uff0c\u845b\u836b\u5747\u8fd8\u4e0d\u77e5\u9053\u4ed6\u8981\u5192\u7684\u98ce\u9669\u7a76\u7adf\u6709\u591a\u5927\uff0c\u4ed6\u770b\u5230\u9648\u9759\u3001\u8c22\u77bb\u4ed6\u4eec\u90fd\u754f\u9996\u754f\u5c3e\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u5c51\uff0c\u201c\u771f\u6b63\u7684\u5de8\u989d\u8d22\u5bcc\u8574\u4e8e\u98ce\u9669\u4e4b\u4e2d\uff0c\u6211\u60f3\u6709\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u4f60\u4eec\u4f1a\u4f53\u4f1a\u5230\u8fd9\u53e5\u8bdd\u7684\u6df1\u523b\u542b\u4e49\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u76ee\u7684\u5df2\u8fbe\uff0c\u8c22\u5251\u5357\u89c1\u8c22\u77bb\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u677f\u7740\u8138\u4e25\u5389\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u8981\u4e0d\u8981\u6211\u5c06\u4f60\u5728\u79d1\u738b\u7684\u80a1\u4efd\u4e5f\u4e70\u4e0b\u6765\uff1f\u201d</p><p>\u3000\u3000\u8c22\u77bb\u8baa\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5de5\u4f5c\u4e2d\u603b\u4e0d\u4f1a\u4e00\u70b9\u5206\u6b67\u90fd\u6ca1\u6709\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u5206\u6b67\uff0c\u6709\u4f60\u8fd9\u4e48\u8bf4\u8bdd\u7684\uff1f\u81f3\u4e8e\u7a76\u7adf\u5982\u4f55\u89e3\u51b3\uff0c\u4e5f\u8981\u53ec\u5f00\u8463\u4e8b\u4f1a\u8ba8\u8bba\u51b3\u5b9a\uff0c\u4e0d\u662f\u6211\u4eec\u56db\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u968f\u4fbf\u8bf4\u8bf4\u5c31\u7b97\u6570\u7684\uff0c\u201d\u8c22\u5251\u5357\u8138\u8272\u5f88\u4e0d\u597d\u770b\uff0c\u5c06\u684c\u524d\u7684\u6587\u4ef6\u5939\u4e00\u5408\uff0c\u201c\u4f60\u4eec\u5148\u51fa\u53bb\u5427\uff01\u201d\u5c06\u9648\u9759\u4e5f\u4e0d\u7559\u60c5\u7684\u8d76\u4e86\u51fa\u53bb\u3002</p></article>"}}}]);
