(window.webpackJsonp=window.webpackJsonp||[]).push([[706],{729:function(p){p.exports={chapterTitle:null,title:"\u7b2c705\u7ae0 \u5468\u672b\u4e09\u4eba\u7ec4",content:"<article><h2>\u7b2c705\u7ae0 \u5468\u672b\u4e09\u4eba\u7ec4</h2><p>\u3000\u3000\u4e03\u70b9\u949f\u4e0a\u7f51\u4eba\u7fa4\u9646\u7eed\u8fdb\u573a\u65f6\u8fd8\u80fd\u7ef4\u6301\u4f4f\u6b21\u5e8f\uff0c\u5f53\u673a\u4f4d\u9646\u7eed\u5360\u6ee1\u5269\u4e0b\u4e0d\u5230\u56db\u5341\u4e2a\u7a7a\u4f4d\u65f6\uff0c\u573a\u5916\u6b21\u5e8f\u5f00\u59cb\u6709\u4e9b\u5931\u63a7\uff0c\u4e92\u76f8\u63a8\u6421\u7740\u60f3\u8981\u6324\u8fdb\u6765\uff0c\u7ed9\u5b89\u6392\u5728\u573a\u5916\u7ef4\u6301\u6b21\u5e8f\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\u7ed9\u773c\u524d\u7684\u573a\u9762\u5413\u4f4f\uff0c\u8eb2\u56de\u5230\u5e97\u91cc\u6765\u3002\u90a3\u4e2a\u4f4d\u7f6e\u7684\u4eba\u7fa4\u9661\u7136\u6ca1\u6709\u4eba\u963b\u6b62\uff0c\u4e00\u4e0b\u5b50\u5c31\u51b2\u5230\u73bb\u7483\u95e8\u524d\u6ca1\u80fd\u6536\u4f4f\uff0c\u6574\u5757\u5927\u95e8\u73bb\u7483\u7ed9\u63a8\u5012\uff0c\u5728\u5730\u4e0a\u7838\u4e86\u7c89\u788e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5550\u4e86\u4e00\u53e3\uff0c\u81ea\u5df1\u8fd9\u5f20\u5634\u8fd8\u662f\u771f\u81ed\uff0c\u8bf4\u5565\u6765\u5565\u3002</p><p>\u3000\u3000\u597d\u5728\u73bb\u7483\u662f\u7ed9\u63a8\u5012\u5728\u5730\u7838\u788e\u7684\uff0c\u9664\u4e86\u56db\u6e85\u7684\u73bb\u7483\u5c51\uff0c\u6ca1\u6709\u8c01\u7ed9\u73bb\u7483\u76f4\u63a5\u5272\u4f24\uff0c\u73bb\u7483\u7838\u788e\u7684\u5de8\u54cd\u4e5f\u5c06\u573a\u5916\u63a8\u6421\u3001\u62e5\u6324\u7740\u62a2\u7740\u60f3\u8fdb\u573a\u7684\u4eba\u7fa4\u7ed9\u9707\u4f4f\uff0c\u5012\u662f\u8ba9\u573a\u5916\u7684\u6df7\u4e71\u5c40\u9762\u6682\u65f6\u7ed9\u51b7\u5374\u4e0b\u6765\u3002\u8d81\u7740\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u8e29\u7740\u73bb\u7483\u5c51\u8d70\u51fa\u6765\uff0c\u5c06\u5916\u573a\u7684\u6b21\u5e8f\u7ed9\u7ef4\u6301\u4f4f\uff0c\u5927\u58f0\u544a\u8bc9\u7b49\u7740\u60f3\u514d\u8d39\u4e0a\u7f51\u7684\u4eba\u7fa4\uff1a\u201c\u51fa\u4e86\u5c0f\u72b6\u51b5\uff0c\u524d\u9762\u90fd\u662f\u73bb\u7483\u6e23\uff0c\u5927\u5bb6\u4e0d\u8981\u5f80\u524d\u6324\u4e86\u3002\u201d\u5c06\u8fdb\u573a\u5de5\u4f5c\u6682\u65f6\u505c\u4e0b\u6765\uff0c\u675c\u98de\u5e26\u7740\u4e24\u4e09\u4e2a\u5c06\u540e\u9762\u6392\u961f\u7684\u4eba\u758f\u6563\u5f00\u3002\u8499\u4e50\u5728\u5e97\u91cc\u4e5f\u8fc5\u901f\u7ec4\u7ec7\u4eba\u624b\u5c06\u788e\u73bb\u7483\u5c51\u6e05\u626b\u5e72\u51c0\u3002</p><p>\u3000\u3000\u521b\u57df\u7f51\u5427\u4e3b\u8981\u5212\u5206\u4e09\u7c7b\u4e3b\u8981\u533a\u57df\uff0c\u4e00\u697c\u4e3a\u666e\u901a\u4f1a\u5458\u533a\uff0c\u4e5f\u662f\u5360\u5730\u6700\u591a\u3001\u673a\u53f0\u6700\u591a\u7684\u5206\u533a\uff1b\u4e8c\u697c\u4e3a\u5546\u52a1\u533a\u3001\u5973\u751f\u4e13\u533a\u4e0e\u8d35\u5bbe\u4e13\u533a\u3002</p><p>\u3000\u3000\u666e\u901a\u4f1a\u5458\u533a\u4e0e\u5973\u751f\u4e13\u533a\u7684\u4e0a\u7f51\u4ef7\u683c\u6bcf\u5c0f\u65f6\u4e94\u5143\uff0c\u5546\u52a1\u533a\u7684\u4e0a\u7f51\u4ef7\u683c\u6bcf\u5c0f\u65f6\u516b\u5143\uff0c\u8d35\u5bbe\u533a\u7684\u5305\u53a2\u4e0a\u7f51\u4ef7\u683c\u6bcf\u5c0f\u65f6\u5341\u4e8c\u5143\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u662f\u54ea\u4e2a\u5c0f\u5b50\u60f3\u51fa\u6765\u7684\u998a\u4e3b\u610f\uff0c\u4e24\u4e2a\u5973\u751f\u4e13\u533a\u5206\u522b\u8bbe\u7f6e\u5728\u5357\u5317\u4e34\u7a97\u7684\u4e24\u4fa7\uff0c\u7ed9\u5546\u52a1\u533a\u5305\u56f4\u7740\uff0c\u53ea\u7528\u9f50\u80f8\u9ad8\u7684\u900f\u660e\u94a2\u5316\u73bb\u7483\u5206\u9694\u5f00\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u65e0\u8bba\u5750\u5230\u5546\u52a1\u533a\u54ea\u4e2a\u89d2\u843d\uff0c\u90fd\u53ef\u4ee5\u5f88\u65b9\u4fbf\u7684\u770b\u5230\u5728\u5973\u751f\u4e13\u533a\u4e0a\u7f51\u7684\u5973\u5b69\u5b50\u3002\u8d35\u5bbe\u533a\u7684\u5305\u53a2\u5219\u662f\u7528\u78e8\u7802\u73bb\u7483\u505a\u9694\u65ad\uff0c\u62ff\u65f6\u5b66\u658c\u7684\u8bdd\u8bf4\uff0c\u8d35\u5bbe\u533a\u4e0e\u5546\u52a1\u533a\u7684\u4e0a\u7f51\u4ef7\u683c\u5e94\u8be5\u8c03\u6362\u4e00\u4e0b\u624d\u5408\u7406\u3002</p><p>\u3000\u3000\u53ea\u6709\u666e\u901a\u4f1a\u5458\u533a\u7684\u673a\u4f4d\u5728\u514d\u8d39\u65f6\u95f4\u65e0\u507f\u5bf9\u5916\u5f00\u653e\uff0c\u7ecf\u8fc7\u4e00\u5f00\u59cb\u7684\u6df7\u4e71\uff0c\u5546\u52a1\u533a\u3001\u5973\u751f\u4e13\u533a\u3001\u8d35\u5bbe\u533a\u4e5f\u5f00\u59cb\u5bf9\u5916\u5f00\u653e\uff0c\u673a\u4f4d\u5f88\u5feb\u4e5f\u7ed9\u5360\u6ee1\uff0c\u8fd9\u65f6\u5019\u7efc\u5408\u5427\u53f0\u524d\u7684\u4f11\u606f\u533a\u57df\u4e5f\u5bf9\u5916\u5f00\u653e\u4e86\uff0c\u5750\u6ee1\u7b49\u5019\u522b\u4eba\u4e0b\u673a\u7684\u5b66\u751f\u3002</p><p>\u3000\u3000\u9664\u4e86\u8ba9\u5f20\u606a\u4e0d\u5e78\u8a00\u4e2d\u3001\u5927\u95e8\u73bb\u7483\u7ed9\u6324\u788e\u4e4b\u5916\uff0c\u4eca\u5929\u7684\u5f00\u573a\u518d\u6ca1\u6709\u4ec0\u4e48\u8ba9\u4eba\u4e0d\u6ee1\u610f\u7684\u5730\u65b9\u4e86\u3002</p><p>\u3000\u3000\u7ed9\u73bb\u7483\u5e97\u6253\u8fc7\u7535\u8bdd\uff0c\u7ad9\u5728\u4f11\u606f\u5496\u5561\u5385\u5f0f\u7684\u7efc\u5408\u5427\u53f0\u91cc\uff0c\u675c\u98de\u957f\u957f\u7684\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u634f\u7740\u62f3\u5934\u671d\u5f20\u606a\u6325\u4e86\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u505a\u6ca1\u505a\u6210\uff0c\u603b\u7b97\u662f\u2018\u505a\u2019\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d</p><p>\u3000\u3000\u89c1\u675c\u98de\u4e00\u4ed8\u5f88\u4eab\u53d7\u7684\u6837\u5b50\uff0c\u5f20\u606a\u8e22\u4e86\u4ed6\u4e00\u811a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5f88\u4eab\u53d7\u662f\u5427\uff1f\u4e0d\u8981\u5f97\u610f\u5f97\u592a\u65e9\uff0c\u82e6\u4e86\u7d2f\u4e86\uff0c\u6709\u4f60\u53eb\u5a18\u7684\u65f6\u5019\u2026\u2026\u201d\u5c06\u94ed\u724c\u522b\u5728\u80f8\u53e3\uff0c\u8bf4\u9053\uff0c\u201c\u554a\uff0c\u6211\u8981\u53bb\u697c\u4e0a\u5de5\u4f5c\uff0c\u4e0d\u8ddf\u4f60\u5728\u8fd9\u91cc\u80e1\u626f\u4e86\uff0c\u7701\u5f97\u4f60\u4eec\u53c8\u62b1\u6028\u6211\u4e0d\u505a\u6b63\u7ecf\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u9760\uff0c\u4f60\u90a3\u6d3b\uff0c\u8c01\u90fd\u60f3\u62a2\u7740\u53bb\u505a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u53ef\u4e0d\u7ba1\u675c\u98de\u7684\u62b1\u6028\u4e0e\u7fa1\u6155\uff0c\u8f6c\u8eab\u4e0a\u4e86\u4e8c\u697c\uff0c\u8f6c\u5973\u751f\u4e13\u533a\u8d70\u53bb\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u8ba1\u7b97\u673a\u8fdc\u8fdc\u6ca1\u6709\u5230\u666e\u53ca\u7684\u7a0b\u5ea6\u3002\u5c31\u7b97\u6240\u6709\u7684\u5927\u5b66\u90fd\u5f00\u8bbe\u4e86\u8ba1\u7b97\u673a\u8bfe\u7a0b\uff0c\u4f46\u5bf9\u4e8e\u8bb8\u591a\u8fdb\u5927\u5b66\u4e4b\u540e\u624d\u5f00\u59cb\u63a5\u89e6\u8ba1\u7b97\u673a\u7684\u5b66\u751f\u6765\u8bf4\uff0c\u5e76\u4e0d\u662f\u4e0a\u51e0\u5802\u67af\u71e5\u65e0\u5473\u7684\u8ba1\u7b97\u673a\u8bfe\u5c31\u80fd\u8ba9\u4ed6\u4eec\u719f\u6089\u4e0a\u7f51\u8fd9\u4e2a\u65b0\u9c9c\u4e8b\u60c5\u3002\u751a\u81f3\u5f88\u591a\u5927\u4e00\u7684\u5b66\u751f\u7cca\u91cc\u7cca\u6d82\u7684\u4e0a\u8fc7\u51e0\u5802\u8ba1\u7b97\u673a\u8bfe\uff0c\u751a\u81f3\u8fde\u600e\u4e48\u5f00\u5173\u673a\u90fd\u4e0d\u6e05\u695a\uff0c\u66f4\u4e0d\u7528\u8bf4\u4e0a\u7f51\u6d4f\u89c8\u7f51\u9875\u3001\u641c\u7d22\u8d44\u6599\u3001\u6ce8\u518c\u90ae\u7bb1\u3001\u8ba2\u9605\u90ae\u4ef6\u4e86\u3002</p><p>\u3000\u3000\u675c\u98de\u4ed6\u4eec\u8003\u8651\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u521b\u57df\u7f51\u5427\u8fd8\u7279\u610f\u8bf7\u4e86\u4e00\u6279\u201c\u7f51\u7edc\u5de5\u7a0b\u5e08\u201d\u7a7f\u63d2\u5728\u5404\u4e2a\u533a\u57df\u5185\u8d1f\u8d23\u6307\u5bfc\u5f88\u53ef\u80fd\u662f\u7b2c\u4e00\u6b21\u63a5\u89e6\u7f51\u7edc\u7684\u7f51\u6c11\u5982\u4f55\u4e0a\u7f51\u51b2\u6d6a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fd7\u6240\u8c13\u7684\u5de5\u4f5c\uff0c\u81ea\u7136\u5c31\u662f\u81ea\u613f\u65e0\u507f\u7684\u62c5\u5f53\u5973\u751f\u4e13\u533a\u7684\u201c\u7f51\u7edc\u5de5\u7a0b\u5e08\u201d\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\u8fd9\u9879\u6709\u610f\u4e49\u7684\u5de5\u4f5c\u5f20\u606a\u624d\u5e72\u4e86\u4e00\u4e0a\u5348\uff0c\u53f6\u5efa\u658c\u7684\u7535\u8bdd\u5c31\u6253\u8fc7\u6765\u3002\u624b\u673a\u5728\u88e4\u515c\u91cc\u54cd\u8d77\u65f6\uff0c\u5f20\u606a\u6b63\u6559\u5e08\u5927\u4e00\u4e2a\u957f\u76f8\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u8f93\u5165\u7f51\u5740\u3002\u90a3\u5973\u5b69\u5b50\u7ed9\u624b\u673a\u94c3\u97f3\u5413\u4e86\u4e00\u8df3\uff0c\u624b\u4e5f\u6296\u4e86\u4e00\u4e0b\uff0c\u89e6\u7740\u5f20\u606a\u7684\u624b\u5fc3\uff0c\u53c8\u731b\u7684\u6536\u56de\u53bb\uff1b\u5f20\u606a\u521a\u8981\u8bf4\u58f0\u5bf9\u4e0d\u8d77\uff0c\u4eba\u5bb6\u5973\u5b69\u5b50\u7c89\u8138\u7ea2\u7740\u3001\u773c\u775b\u4f3c\u770b\u975e\u770b\uff0c\u4f3c\u4e4e\u5e0c\u671b\u5f20\u606a\u6478\u56de\u53bb\u3002\u5f20\u606a\u6697\u53f9\uff1a\u81ea\u5df1\u7559\u5728\u8fd9\u91cc\u662f\u5360\u4eba\u5bb6\u4fbf\u5b9c\u8fd8\u662f\u7ed9\u4eba\u5bb6\u5360\u4fbf\u5b9c\u554a\uff1f\u4e5f\u5c31\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u63a5\u8d77\u53f6\u5efa\u658c\u7535\u8bdd\u3002</p><p>\u3000\u3000\u76db\u946b\u8fd9\u6bb5\u65f6\u95f4\u5728\u897f\u5357\u3001\u534e\u5317\u3001\u534e\u4e2d\u3001\u534e\u5357\u7b49\u5730\u7684\u91cd\u8981\u57ce\u5e02\u653b\u57ce\u63a0\u5730\uff1b\u6b63\u503c\u76db\u946b\u4e95\u55b7\u53d1\u5c55\u671f\uff0c\u53f6\u5efa\u658c\u60f3\u7701\u5fc3\u3001\u60f3\u5b8c\u5168\u62bd\u8eab\u51fa\u6765\uff0c\u662f\u4e0d\u53ef\u80fd\u7684\u3002\u867d\u7136\u5c06\u65e5\u5e38\u4e8b\u52a1\u4ea4\u7ed9\u90b5\u5fc3\u6587\u7ba1\u7406\uff0c\u4ed6\u4ecd\u7136\u662f\u76db\u946b\u7684\u8463\u4e8b\u957f\uff0c\u4ed6\u4ece\u9999\u6e2f\u56de\u6765\u7684\u8fd9\u4e24\u4e2a\u6708\uff0c\u5c31\u4e3a\u76db\u946b\u7684\u4e8b\u52a1\u56db\u5904\u5954\u8d70\uff0c\u4e1d\u6beb\u4e0d\u5f97\u677e\u61c8\u3002\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u8fd8\u662f\u4e0a\u56de\u626d\u7740\u674e\u5728\u6d19\u3001\u674e\u99a8\u4e88\u4e00\u8d77\u53bb\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u65f6\u4e0e\u53f6\u5efa\u658c\u89c1\u9762\u6765\u7740\uff0c\u6643\u4e00\u6643\u90fd\u5feb\u4e00\u4e2a\u6708\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u53f6\u5efa\u658c\u627e\u81ea\u5df1\u4ec0\u4e48\u4e8b\uff0c\u53f6\u5efa\u658c\u95ee\u4ed6\u4eba\u5728\u54ea\u91cc\uff0c\u5f20\u606a\u544a\u8bc9\u4ed6\u5730\u5740\uff0c\u53f6\u5efa\u658c\u8ba9\u4ed6\u51fa\u53bb\u3002\u5f20\u606a\u63a2\u5934\u8d34\u7740\u73bb\u7483\u5f80\u697c\u4e0b\uff0c\u53f6\u5efa\u658c\u7684\u9ed1\u8272\u5927\u7687\u51a0\u6b63\u505c\u5728\u697c\u4e0b\u5462\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u80fd\u8fd9\u4e48\u60a0\u95f2\u7684\u8fc7\u5468\u672b\u4e86\uff1f\u201d\u5f20\u606a\u5c06\u624b\u673a\u5939\u5728\u8116\u5b50\u95f4\u95ee\u53f6\u5efa\u658c\uff0c\u6b6a\u7740\u8eab\u5b50\u5728\u8ba1\u7b97\u673a\u4e0a\u6253\u5b57\uff0c\u544a\u8bc9\u4ed6\u521a\u521a\u6307\u5bfc\u4e0a\u7f51\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\u81ea\u5df1\u4eca\u5929\u6709\u4e8b\u8981\u8d76\u7740\u51fa\u53bb\u8bf7\u5979\u627e\u5176\u4ed6\u4eba\u6307\u5bfc\uff0c\u90a3\u4e2a\u5973\u5b69\u5b50\u63a5\u8fc7\u952e\u76d8\u98de\u5feb\u7684\u6253\u51fa\u4e00\u884c\u5b57\uff1a\u201c\u6211\u660e\u5929\u8fd8\u4f1a\u8fc7\u6765\u4e0a\u7f51\uff0c\u8fd8\u80fd\u518d\u627e\u4f60\u6559\u6211\u4e0a\u7f51\u5417\uff1f\u201d\u5f20\u606a\u8be7\u5f02\u7684\u770b\u4e86\u90a3\u5973\u5b69\u5b50\u4e00\u773c\uff0c\u8fd9\u6253\u5b57\u901f\u5ea6\u4e0d\u50cf\u662f\u521a\u63a5\u89e6\u8ba1\u7b97\u673a\u7684\u554a\uff0c\u5fc3\u60f3\u81ea\u5df1\u4eca\u5929\u662f\u4e0d\u662f\u7b97\u662f\u7ed9\u8c03\u620f\u4e86\uff1f\u6302\u4e86\u624b\u673a\uff0c\u8ddf\u90a3\u5973\u5b69\u5b50\u8bf4\uff1a\u201c\u660e\u5929\u518d\u8bf4\u5427\u3002\u201d\u7d27\u8d76\u7740\u9003\u4e86\u51fa\u53bb\uff0c\u5728\u4e00\u697c\u5927\u5385\u91cc\u6ca1\u770b\u5230\u675c\u98de\uff0c\u5c31\u5148\u94bb\u8fdb\u53f6\u5efa\u658c\u7684\u8f66\u91cc\uff0c\u53d1\u73b0\u59da\u6587\u76db\u4e5f\u5728\u3002</p><p>\u3000\u3000\u201c\u9634\u8c0b\u7684\u5473\u9053\u592a\u91cd\u4e86\uff0c\u6253\u5f00\u8f66\u95e8\u5c31\u76f4\u7a9c\u9f3b\u5b50\u91cc\uff0c\u201d\u5f20\u606a\u5750\u8fdb\u6765\u8f66\u91cc\uff0c\u5bf9\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u8bf4\u9053\uff0c\u201c\u4e0d\u8981\u8bf4\u53ea\u662f\u5468\u672b\u60f3\u5230\u8981\u62c9\u6211\u4e00\u8d77\u53bb\u900d\u9065\u5feb\u6d3b\u7684\uff0c\u8fd9\u8bdd\u9b3c\u90fd\u4e0d\u4fe1\uff0c\u6709\u4ec0\u4e48\u4e8b\u5feb\u8bf4\u5427\uff0c\u8981\u662f\u4e0d\u5408\u6211\u610f\uff0c\u6211\u8fd8\u662f\u7ee7\u7eed\u8c03\u620f\u5973\u5927\u5b66\u751f\u53bb\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u662f\u53ea\u60f3\u7740\u62c9\u4f60\u4e00\u8d77\u51fa\u6765\u900d\u9065\u5feb\u6d3b\uff0c\u6587\u76db\u6253\u4ec0\u4e48\u9b3c\u6ce8\u610f\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u8fd9\u4e48\u660e\u663e\u5417\uff1f\u201d\u59da\u6587\u76db\u7591\u60d1\u7684\u95ee\u9053\uff0c\u201c\u6211\u7684\u76ee\u5149\u96be\u9053\u5f88\u671f\u5f85\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u521a\u521a\u7ed9\u5e08\u5927\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\u8c03\u620f\u4e86\u4e00\u628a\uff0c\u5979\u770b\u6211\u7684\u773c\u795e\uff0c\u8ddf\u4f60\u5dee\u4e0d\u591a\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u867d\u7136\u4eca\u5929\u662f\u5468\u672b\uff0c\u59da\u6587\u76db\u5374\u7edd\u4e0d\u4f1a\u6709\u7a7a\u95f2\u65f6\u95f4\u7684\uff0c\u738b\u7ef4\u5747\u521a\u521a\u8d70\u9a6c\u4e0a\u4efb\u5750\u4e0a\u9ad8\u65b0\u533a\u4e3b\u4efb\u7684\u4f4d\u5b50\uff0c\u53c8\u8d76\u7740\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u521a\u521a\u6210\u7acb\uff0c\u8bb8\u591a\u82af\u7247\u3001\u8f6f\u4ef6\u6280\u672f\u90fd\u4f1a\u4fa7\u91cd\u7684\u9488\u5bf9\u5efa\u90ba\u9ad8\u65b0\u533a\u7684\u4f01\u4e1a\u516c\u5f00\uff0c\u5efa\u90ba\u9ad8\u65b0\u533a\u5f53\u7136\u8981\u505a\u597d\u76f8\u5e94\u7684\u5f15\u5bfc\u5de5\u4f5c\uff0c\u751a\u81f3\u8981\u5728\u76f8\u5173\u5de5\u4f5c\u4e2d\u8d77\u4e3b\u5bfc\u5730\u4f4d\uff0c\u59da\u6587\u76db\u4ed6\u4eec\u8fd9\u65f6\u5019\u5927\u6982\u4f1a\u5fd9\u5f97\u751a\u81f3\u60f3\u4e09\u53e3\u547c\u5438\u5e76\u6210\u4e24\u53e3\u5427\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u7b11\u7740\u6376\u4e86\u5f20\u606a\u4e00\u8bb0\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u5f52\u8981\u5403\u4e2d\u996d\u7684\uff0c\u8bf7\u4f60\u5403\u4e2d\u996d\u3002\u201d</p><p>\u3000\u3000\u201c\u5bb4\u65e0\u597d\u5bb4\uff0c\u8fd8\u662f\u6211\u8bf7\u4f60\u4eec\u4fe9\u5427\u3002\u7b49\u4f1a\u513f\u6709\u4ec0\u4e48\u8bdd\u597d\u76f4\u63a5\u56de\u7edd\uff0c\u4e0d\u4f1a\u56e0\u4e3a\u5403\u4f60\u7684\u5634\u8f6f\u7ed9\u5360\u4e86\u5927\u4fbf\u5b9c\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u5bf9\u53f6\u5efa\u658c\u7684\u53f8\u673a\u8bf4\u9053\uff0c\u201c\u6210\u54e5\uff0c\u6211\u4eec\u5c31\u662f\u5728\u91cc\u9762\u5df7\u5b50\u7684\u6c88\u8bb0\u8336\u9910\u5385\u5403\u996d\uff0c\u4f60\u627e\u4e2a\u5730\u65b9\u505c\u8f66\uff0c\u53bb\u90a3\u91cc\u627e\u6211\u4eec\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u65e0\u8bed\u7684\u7b11\u4e86\u7b11\uff0c\u53ea\u5f97\u4e0b\u8f66\u8ddf\u7740\u5f20\u606a\u5f80\u91cc\u5df7\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u5582\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u611f\u89c9\u4e00\u9635\u98ce\u76f4\u7a9c\u8fc7\u6765\uff0c\u4e4d\u56de\u4e00\u770b\uff0c\u5b59\u9759\u6aac\u9a91\u7740\u5979\u90a3\u8f86\u6854\u9ec4\u8272\u7684\u5c0f\u81ea\u884c\u8f66\u6a2a\u51b2\u76f4\u649e\u8fc7\u6765\u3002\u524d\u8f6e\u51e0\u4e4e\u8d34\u7740\u5f20\u606a\u7684\u5c0f\u817f\u624d\u5239\u4f4f\uff0c\u5b59\u9759\u6aac\u660e\u6f88\u7684\u773c\u795e\u7591\u60d1\u7684\u5728\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u4e09\u4e2a\u4eba\u7684\u8138\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u4f3c\u4e4e\u5728\u6349\u8d3c\u5bfb\u8d43\uff1a\u201c\u558f\uff0c\u4f60\u4eec\u4e09\u4e2a\u600e\u4e48\u4f1a\u5927\u767d\u5929\u5c31\u4e00\u8d77\u51fa\u6765\u6d3b\u52a8\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u53c8\u591a\u4e86\u4e00\u4e2a\u8e6d\u996d\u7684\u3002\u201d\u5f20\u606a\u6487\u7740\u5634\uff0c\u4e0d\u4ee5\u4e3a\u5b59\u9759\u6aac\u8fd9\u65f6\u5019\u8fd8\u4f1a\u8001\u8001\u5b9e\u5b9e\u7684\u8d70\u5f00\uff0c\u6628\u5929\u8d76\u56de\u6765\uff0c\u90fd\u6ca1\u6765\u5f97\u53ca\u53bb1978\u9732\u4e2a\u8138\uff0c\u8fd8\u4ee5\u4e3a\u5b59\u9759\u6aac\u4f1a\u62b1\u6028\u4ec0\u4e48\uff0c\u6ca1\u60f3\u5230\u5979\u538b\u6839\u5c31\u6ca1\u6709\u63d0\u8fd9\u4e8b\uff0c\u8ba9\u4ed6\u5fc3\u91cc\u6709\u4e9b\u5c0f\u5931\u671b\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u771f\u662f\u6709\u4e8b\u8981\u627e\u5f20\u606a\u5546\u8bae\uff0c\u8fd8\u662f\u4e0e\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6709\u5173\u7684\u4e8b\u60c5\u3002\u9526\u6e56\u5c06\u76f8\u5173\u6280\u672f\u516c\u5f00\uff0c\u80fd\u8fc5\u901f\u6d88\u5316\u8fd9\u4e9b\u6280\u672f\u3001\u5e76\u63a8\u9648\u51fa\u65b0\u7684\u7fa4\u4f53\uff0c\u4e0d\u4e00\u5b9a\u662f\u56fd\u5185\u4f01\u4e1a\u4e0e\u673a\u6784\uff0c\u90a3\u4e9b\u5230\u6d77\u5916\u7559\u5b66\u5e76\u7559\u5728\u6d77\u5916\u4ece\u4e8b\u76f8\u5173\u9886\u57df\u5de5\u4f5c\u7684\u4eba\u5458\u4e5f\u8bb8\u66f4\u9002\u5408\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u51fa\u6765\u5403\u996d\u7684\u65f6\u95f4\u6bd4\u8f83\u65e9\u8336\u9910\u5385\u91cc\u8fd8\u6ca1\u6709\u591a\u5c11\u4eba\uff0c\u70b9\u8fc7\u9910\uff0c\u627e\u4e86\u4e00\u4e2a\u89d2\u843d\u5750\u4e0b\u6765\uff0c\u59da\u6587\u76db\u8bf4\u660e\u8fc7\u6765\u627e\u5f20\u606a\u7684\u6765\u610f\uff1a\u201c\u5e02\u91cc\u60f3\u5438\u5f15\u6d77\u5916\u7559\u5b66\u4eba\u5458\u56de\u56fd\u521b\u4e1a\uff0c\u4f46\u662f\u5982\u4f55\u521b\u9020\u826f\u597d\u7684\u6761\u4ef6\u5438\u5f15\u8fd9\u4e9b\u6d77\u5916\u5b66\u5b50\u5f52\u6765\uff0c\u5f88\u4f24\u8111\u7b4b\uff0c\u6211\u5c31\u5728\u60f3\uff0c\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e0e\u521b\u4e1a\u56ed\u4e24\u8005\u7ed3\u5408\u8d77\u6765\uff0c\u4e5f\u8bb8\u80fd\u521b\u9020\u4e00\u90e8\u5206\u8d28\u7684\u571f\u58e4\u2026\u2026\u201d</p><p>\u3000\u3000\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u80fd\u63d0\u51fa\u4e00\u4e2a\u6280\u672f\u5e73\u53f0\uff0c\u521b\u4e1a\u56ed\u80fd\u63d0\u4f9b\u521b\u4e1a\u670d\u52a1\uff0c\u4e5f\u4e3a\u4f18\u79c0\u7684\u521b\u4e1a\u9879\u76ee\u63d0\u4f9b\u98ce\u9669\u6295\u8d44\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u4ed6\u6653\u5f97\u6709\u4e9b\u6d77\u5916\u5b66\u5b50\u4e0d\u7518\u5fc3\u4e00\u8f88\u5b50\u7ed9\u522b\u4eba\u6253\u5de5\uff0c\u5c31\u7b97\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u5f00\u51fa\u518d\u597d\u7684\u6761\u4ef6\uff0c\u90fd\u672a\u5fc5\u80fd\u5c06\u8fd9\u4e9b\u4eba\u62db\u81f3\u9ebe\u4e0b\uff0c\u4e3a\u4ed6\u4eec\u63d0\u4f9b\u521b\u4e1a\u7684\u571f\u58e4\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u8fd8\u53ef\u4ee5\u901a\u8fc7\u98ce\u9669\u6295\u8d44\u5bf9\u8fd9\u7c7b\u79d1\u6280\u578b\u4f01\u4e1a\u4fdd\u7559\u4e00\u5b9a\u7684\u63a7\u5236\u6743\uff0c\u7b97\u662f\u4e00\u79cd\u6298\u4e2d\u7684\u505a\u6cd5\uff1b\u5f53\u7136\uff0c\u8fd9\u4e9b\u6d77\u5f52\u4eba\u5458\u4f1a\u5e26\u4e9b\u5176\u4ed6\u6280\u672f\u56de\u6765\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p></article>"}}}]);
