(window.webpackJsonp=window.webpackJsonp||[]).push([[709],{732:function(p){p.exports={chapterTitle:null,title:"\u7b2c708\u7ae0 \u738b\u6d77\u7c9f\u7684\u610f\u56fe",content:"<article><h2>\u7b2c708\u7ae0 \u738b\u6d77\u7c9f\u7684\u610f\u56fe</h2><p>\u3000\u3000\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u4f4d\u4e8e\u4e8c\u697c\u8d35\u5bbe\u4e13\u533a\u5185\u4fa7\uff0c\u78e8\u7802\u73bb\u7483\u505a\u5168\u9694\u65ad\uff0c\u4e0d\u9508\u94a2\u7684\u94ed\u724c\uff0c\u4ece\u5916\u9762\u770b\u4e0a\u53bb\u7cbe\u81f4\u5178\u96c5\uff0c\u4f46\u662f\u5de5\u4f5c\u5ba4\u91cc\u7684\u8ba1\u7b97\u673a\u914d\u5236\u6bd4\u5916\u9762\u7684\u8d35\u5bbe\u4e13\u533a\u8981\u5dee\u591a\u4e86\uff0c\u751a\u81f3\u53ca\u4e0d\u4e0a\u5546\u52a1\u533a\u7684\u8ba1\u7b97\u673a\u914d\u5236\u3002\u4e3a\u6b64\u65bd\u65b0\u98de\u7b49\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u53eb\u82e6\u8fde\u5929\uff0c\u65f6\u5b66\u658c\u8bf4\u675c\u98de\u3001\u8499\u4e50\u5df2\u7ecf\u5f7b\u5e95\u5c06\u4ed6\u4eec\u5978\u5546\u8d2a\u8d22\u7684\u4e11\u964b\u672c\u8d28\u66b4\u9732\u51fa\u6765\u4e86\uff0c\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u5374\u4e0d\u52a8\u4e8e\u8877\u3002</p><p>\u3000\u3000\u8d35\u5bbe\u4e13\u533a\u7684\u8ba1\u7b97\u673a\u867d\u7136\u6ca1\u6709\u914d\u6db2\u6676\u663e\u793a\u5668\uff0c\u4f46\u90fd\u914d\u4e0a\u5341\u4e03\u5bf8\u7684\u7eaf\u5e73\u663e\u793a\u5668\uff0c\u5728\u56fd\u5185\u5df2\u7ecf\u7b97\u662f\u9ad8\u7aef\u914d\u5236\u4e86\u3002</p><p>\u3000\u3000\u7eaf\u5e73\u663e\u793a\u5668\u662f\u4e5d\u56db\u5e74\u663e\u793a\u5668\u5382\u5546\u4e3a\u51cf\u5c0f\u7403\u9762\u5c4f\u5e55\u7279\u522b\u662f\u5c4f\u5e55\u56db\u89d2\u7684\u5931\u771f\u4e0e\u663e\u793a\u5668\u7684\u53cd\u5149\u73b0\u8c61\u624d\u7814\u5236\u51fa\u6765\u7684\u5e73\u9762\u76f4\u89d2\u663e\u793a\u65b0\u6280\u672f\uff0c\u4e5f\u5c31\u662f\u5728\u4eca\u5e74\u4e0b\u534a\u5e74\uff0c\u624d\u6709\u51e0\u5bb6\u663e\u793a\u5668\u5382\u5546\u91c7\u7528\u8fd9\u79cd\u65b0\u6280\u672f\u5927\u89c4\u6a21\u7684\u751f\u4ea7\u5546\u7528\u663e\u793a\u5668\u3002</p><p>\u3000\u3000\u5e73\u9762\u76f4\u89d2\u663e\u793a\u6280\u672f\uff0c\u5f20\u606a\u4e0d\u5927\u5173\u5fc3\uff0c\u4f46\u662f\u5bf9TFT\u6db2\u6676\u6280\u672f\u5341\u5206\u70ed\u5fc3\u3002</p><p>\u3000\u3000TFT\u6db2\u6676\u6280\u672f\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u91cc\u5e94\u7528\u6781\u4e3a\u5e7f\u6cdb\uff0c\u518d\u8fc7\u4e9b\u5e74\uff0cTFT\u6db2\u6676\u5c4f\u51e0\u4e4e\u5c06\u662f\u6240\u6709\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u7684\u5173\u952e\u914d\u4ef6\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0cTFT\u6db2\u6676\u663e\u793a\u6280\u672f\uff0c\u65e5\u672c\u624d\u53d1\u5c55\u5230\u7b2c\u4e09\u4ee3\uff0c\u53f0\u6e7e\u3001\u97e9\u56fd\u7684\u7535\u5b50\u4f01\u4e1a\u4e5f\u5df2\u7ecf\u5f00\u59cb\u5927\u89c4\u6a21\u7684\u5f15\u8fdb\u3002</p><p>\u3000\u3000\u5f20\u606a\u867d\u7136\u5bf9TFT\u6db2\u6676\u6280\u672f\u5f88\u773c\u998b\uff0c\u4f46\u4e5f\u6653\u5f97\u8d2a\u591a\u56bc\u4e0d\u70c2\u3002\u9526\u6e56\u6ca1\u6709\u8db3\u591f\u7684\u5b9e\u529b\u4e0e\u6280\u672f\u529b\u91cf\u53bb\u5f15\u8fdb\u3001\u6d88\u5316\u7b2c\u4e09\u4ee3\u7684TFT\u6db2\u6676\u6280\u672f\u4e0e\u751f\u4ea7\u7ebf\uff0c\u5149\u5f15\u8fdb\u4e5f\u5c31\u7b97\u4e86\uff0c\u8fd8\u8981\u6295\u5165\u5de8\u5927\u7684\u4eba\u529b\u3001\u7269\u529b\u7d27\u8ddf\u7740\u65e5\u97e9\u5382\u5546\u7684\u6b65\u4f10\u53bb\u53d1\u5c55\u7b2c\u56db\u4ee3\u6280\u672f\u3002\u518d\u8bf4\u6b64\u65f6\u8fd8\u6ca1\u6709\u8fce\u6765TFT\u7684\u8f89\u714c\u65f6\u4ee3\uff0c\u8fd8\u7ed9\u9526\u6e56\u7559\u4e0b\u76f8\u5bf9\u8f83\u5bbd\u88d5\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u9526\u6e56\u4ece\u5fb7\u4eea\u63a5\u624b\u7684STN\u6db2\u6676\u6280\u672f\u8fd8\u662f\u5f53\u524d\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u663e\u793a\u5c4f\u7684\u4e3b\u6d41\uff0cSTN\u6db2\u6676\u663e\u793a\u6280\u672f\u7684\u5f15\u8fdb\u8d39\u7528\u4e0d\u9ad8\uff0c\u4f46\u662f\u751f\u4ea7\u7ebf\u7684\u5f15\u8fdb\u4ef7\u683c\u4e5f\u4e0d\u662f\u591a\u4f4e\u3002\u5728\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u6210\u7acb\u6676\u5c4f\u7535\u5b50\u6709\u9650\u516c\u53f8\u5dee\u4e0d\u591a\u5c31\u6d88\u8017\u6389\u4e00\u5343\u4e94\u767e\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u3002\u6240\u5e78\u56fd\u5185\u4e4b\u524d\u5c31\u5f15\u8fdb\u591a\u6761\u6280\u672f\u89c4\u6a21\u8f83\u4f4e\u7684TN\u6db2\u6676\u751f\u4ea7\u7ebf\uff0cTN\u6db2\u6676\u751f\u4ea7\u7ebf\u7684\u914d\u5957\u4f01\u4e1a\u5927\u90fd\u53ef\u4ee5\u4e3a\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u6709\u9650\u516c\u53f8\u6240\u7528\u3002</p><p>\u3000\u3000\u867d\u7136\u8bf4TFT\u6db2\u6676\u663e\u793a\u6280\u672f\u6ce8\u5b9a\u4f1a\u8fce\u6765\u5176\u8f89\u714c\u7684\u65f6\u4ee3\uff0c\u4f46\u662fSTN\u6db2\u6676\u663e\u793a\u6280\u672f\u5728\u4e2d\u4f4e\u7aef\u5e94\u7528\u4e0a\u4f1a\u957f\u671f\u4fdd\u6301\u5e7f\u6cdb\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u4e2d\u4f4e\u7aef\u7684\u6280\u672f\uff0c\u56fd\u5185\u4f01\u4e1a\u5f15\u8fdb\u5c31\u8f83\u4e3a\u5bb9\u6613\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u6709\u9650\u516c\u53f8\u9762\u4e34\u7684\u7ade\u4e89\u5c31\u4f1a\u6bd4\u8f83\u5927\u3002\u5c31\u5f20\u606a\u6240\u8bb0\u5f97\u7684\u524d\u5c18\u5f80\u4e8b\uff0c\u534e\u663e\u7535\u5b50\u3001\u4e1c\u663e\u7535\u5b50\u7b49\u56fd\u5185\u8f83\u77e5\u540d\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u90fd\u5728\u4eca\u540e\u51e0\u5e74\u76f8\u7ee7\u5f15\u8fdbSTN\u6db2\u6676\u663e\u793a\u6280\u672f\u4e0e\u751f\u4ea7\u7ebf\uff0c\u6700\u7ec8\u6709\u4e0a\u767e\u5bb6\u7535\u5b50\u4f01\u4e1a\u91cd\u590d\u5f15\u8fdb\u76f8\u5173\u6280\u672f\u8fdb\u5165\u8be5\u9886\u57df\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u6570\u5b57\u624b\u673a\u6280\u672f\u516c\u5f00\u6388\u6743\u4e5f\u5305\u62ecSTN\u6db2\u6676\u663e\u793a\u6280\u672f\u53ca\u76f8\u5e94\u914d\u4ef6\u6280\u672f\uff0c\u50cf\u534e\u663e\u7535\u5b50\u3001\u4e1c\u663e\u7535\u5b50\u8fd9\u4e9b\u6709\u610f\u8fdb\u5165STN\u6db2\u6676\u5e02\u573a\u7684\u4f01\u4e1a\uff0c\u53ea\u9700\u8981\u76f4\u63a5\u5230\u5efa\u90ba\u6216\u6d77\u5dde\u6295\u8d44\u5efa\u5382\u5c31\u53ef\u4ee5\u7533\u8bf7\u83b7\u5f97\u6388\u6743\u65e0\u507f\u4f7f\u7528\u76f8\u5173\u6280\u672f\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u8bf4\uff0c\u9526\u6e56\u8fd9\u6b21\u7684\u51b3\u5b9a\uff0c\u540c\u884c\u4e1a\u4e3a\u6b64\u6b22\u6b23\u9f13\u821e\u3001\u4e3b\u7ba1\u90e8\u95e8\u90ae\u7535\u90e8\u5fc3\u601d\u96be\u63e3\u3001\u9526\u6e56\u5185\u90e8\u5374\u8981\u4e3a\u6b64\u989d\u5916\u7684\u627f\u62c5\u7279\u522b\u91cd\u7684\u538b\u529b\u3002\u5c31\u62ffSTN\u6db2\u6676\u5e02\u573a\u6765\u8bf4\uff0c\u9526\u6e56\u53ef\u4ee5\u8bf4\u662f\u65e0\u7aef\u7aef\u7684\u7ed9\u65d7\u4e0b\u7684\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u6709\u9650\u516c\u53f8\u62db\u6765\u8bb8\u591a\u6709\u5b9e\u529b\u7684\u7ade\u4e89\u8005\uff0c\u5c06\u5927\u7247\u7684\u5e02\u573a\u62f1\u624b\u8ba9\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u79cd\u517b\u72d7\u54ac\u5154\u5b50\u7684\u6218\u7565\uff0c\u5e76\u4e0d\u662f\u9526\u6e56\u6bcf\u4e2a\u5458\u5de5\u90fd\u80fd\u7406\u89e3\u7684\u3002\u5f20\u606a\u624d\u4e0d\u7ba1\u4e0b\u9762\u4eba\u80fd\u4e0d\u80fd\u7406\u89e3\uff0c\u6ca1\u6709\u8fd9\u79cd\u8c41\u51fa\u53bb\u7684\u6c14\u52bf\u4e0e\u51b3\u5fc3\uff0c\u9526\u6e56\u4f55\u65f6\u624d\u80fd\u5c06\u4e09\u661f\u8fd9\u6837\u7684\u4f01\u4e1a\u8e29\u5728\u811a\u4e0b\uff1f</p><p>\u3000\u3000\u6218\u7565\u8981\u9ad8\u77bb\u8fdc\u77a9\uff0c\u5728\u5177\u4f53\u7684\u4ea7\u4e1a\u53d1\u5c55\u4e0a\uff0c\u996d\u8fd8\u662f\u8981\u4e00\u53e3\u4e00\u53e3\u7684\u5403\uff0c\u8def\u8fd8\u662f\u8981\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\uff0c\u5f20\u606a\u8981\u6c42\u9526\u6e56\u5148\u56f4\u7ed5STN\u6db2\u6676\u663e\u793a\u6280\u672f\u8fdb\u884c\u6280\u672f\u4e0e\u4eba\u624d\u7684\u50a8\u5907\uff0c\u5bf9TFT\u6db2\u6676\u663e\u793a\u6280\u672f\uff0c\u5148\u5f00\u59cb\u5916\u56f4\u5e94\u7528\u6280\u672f\u7684\u5f00\u53d1\uff0c\u5c31\u7b97\u6ca1\u6709\u751f\u4ea7TFT\u6db2\u6676\u7684\u80fd\u529b\uff0c\u4e5f\u8981\u4e0d\u843d\u540e\u4e8e\u6d77\u5916\u7535\u5b50\u5382\u5546\u5c06TFT\u6db2\u6676\u5c4f\u9996\u5148\u5e94\u7528\u4e8e\u624b\u673a\u3001MP3\u3001PDA\u7b49\u7535\u5b50\u4ea7\u54c1\u4e0a\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u770b\u5230\u5f20\u606a\u7ad9\u5728\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u524d\u8d70\u795e\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6ca1\u770b\u5230\u8d35\u5bbe\u4e13\u533a\u6709\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u5728\uff0c\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u5728\u53d1\u4ec0\u4e48\u6123\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u90fd\u6000\u7591\u6700\u8fd1\u662f\u4e0d\u662f\u8d70\u706b\u5165\u9b54\u4e86\uff0c\u52a8\u4e0d\u52a8\u5c31\u8d70\u795e\u60f3\u5230\u9526\u6e56\u7684\u6280\u672f\u53d1\u5c55\u6218\u7565\u4e0a\u53bb\u3002\u82b1\u679d\u62db\u5c55\u7684\u5b59\u9759\u6aac\u4e0a\u697c\u6765\u5c31\u5f15\u8d77\u8bb8\u591a\u4eba\u7684\u77a9\u76ee\uff0c\u5f20\u606a\u5f53\u7136\u8fd9\u65f6\u5019\u4e0d\u4f1a\u8ddf\u5979\u89e3\u91ca\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e8b\u60c5\u8d70\u795e\uff0c\u53ea\u8bf4\u9053\uff0c\u201c\u5f53\u4eca\u7684\u5927\u5b66\u751f\u90fd\u86ee\u6709\u94b1\u7684\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u5927\u5b66\u751f\u7684\u6d88\u8d39\u6c34\u5e73\u666e\u901a\u8f83\u4f4e\uff0c\u4f46\u4e0d\u5f97\u4e0d\u627f\u8ba4\u8fd8\u5b58\u5728\u7740\u4e00\u5b9a\u6570\u91cf\u7684\u9ad8\u6d88\u8d39\u7fa4\u4f53\u3002\u8d35\u5bbe\u4e13\u533a\u7684\u4e0a\u7f51\u4ef7\u683c\u6bcf\u5c0f\u65f6\u5341\u4e8c\u5143\uff0c\u8fd9\u4e00\u533a\u57df\u7684\u8ba1\u7b97\u673a\u4ece\u4e03\u70b9\u5f00\u4e1a\u5230\u73b0\u5728\u5c31\u6ca1\u6709\u7a7a\u95f2\u8fc7\uff0c\u4ec5\u8d35\u5bbe\u4e13\u533a\u4e09\u5341\u53f0\u673a\u4e0a\u5348\u7684\u8425\u4e1a\u989d\u5c31\u8fbe\u5230\u4e00\u5343\u516b\u767e\u5143\uff0c\u8fd8\u4e0d\u5305\u62ec\u996e\u6599\u9500\u552e\u4e0e\u8d35\u5bbe\u4e13\u533a\u5e7f\u544a\u6295\u653e\u7684\u6536\u5165\uff0c\u7f51\u5427\u7684\u5229\u6da6\u8fd8\u771f\u662f\u8bf1\u4eba\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4f1a\u5728\u4e4e\u8fd9\u4e9b\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u868a\u5b50\u817f\u4e0a\u8089\u4e5f\u662f\u8089\u2026\u2026\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u521b\u57df\u7f51\u5427\u6709\u8d62\u5229\uff0c\u867d\u7136\u5bf9\u5e9e\u5927\u7684\u9526\u6e56\u6765\u8bf4\u5c31\u50cf\u868a\u5b50\u817f\u4e0a\u8089\uff0c\u5f20\u606a\u8fd8\u771f\u8981\u5c06\u5634\u51d1\u4e0a\u53bb\u54ac\u4e00\u53e3\uff0c\u6bd5\u7adf\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u5728\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u6301\u670970\uff05\u7684\u80a1\u6743\uff0c\u9664\u975e\u675c\u98de\u4e0e\u8499\u4e50\u4ece\u5176\u4ed6\u6e20\u9053\u83b7\u5f97\u878d\u8d44\uff0c\u5c06\u6a61\u6811\u56ed\u6240\u6301\u7684\u80a1\u4efd\u56de\u8d2d\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u5728\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u91cc\u8fd8\u6ca1\u6709\u5c06\u5ea7\u6905\u6342\u70ed\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u5c31\u8d76\u56de\u6765\u4e86\uff0c\u675c\u98de\u6253\u7535\u8bdd\u95ee\u4ed6\u8981\u4e0d\u8981\u518d\u4e00\u8d77\u51fa\u53bb\u559d\u70b9\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u4e00\u8d77\u53bb\uff1f\u201d\u5f20\u606a\u95ee\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u8981\u53bb\uff0c\u770b\u90a3\u5bb6\u4f19\u6bd4\u770b\u4f60\u8fd8\u4e0d\u987a\u773c\uff0c\u201d\u5b59\u9759\u6aac\u5658\u7740\u5634\u6447\u6447\u5934\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4f60\u7b49\u4f1a\u513f\u53bb\u770b\u623f\u5b50\u4e0d\u8981\u4e22\u4e0b\u6211\u3002\u201d</p><p>\u3000\u3000\u8981\u4e0d\u662f\u4e8b\u60c5\u7275\u6d89\u5230\u8499\u4e50\uff0c\u5f20\u606a\u624d\u61d2\u5f97\u53bb\u7406\u4f1a\u738b\u6d77\u7c9f\u8fd9\u6837\u7684\u89d2\u8272\uff0c\u5de6\u53f3\u4e5f\u65e0\u804a\uff0c\u62ff\u738b\u6d77\u7c9f\u6253\u53d1\u65f6\u95f4\u4e5f\u662f\u53ef\u4ee5\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u697c\u4e0b\u5927\u5385\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4e5f\u5728\uff0c\u738b\u6d77\u7c9f\u4e00\u5de6\u4e00\u53f3\u63fd\u7740\u675c\u98de\u3001\u8499\u4e50\u7684\u80a9\u8180\uff0c\u663e\u5f97\u6781\u4e3a\u4eb2\u70ed\uff0c\u8499\u4e50\u7684\u53cd\u5e94\u81ea\u7136\u51b7\u6de1\u751a\u81f3\u6709\u7740\u63a9\u9970\u4e0d\u4f4f\u7684\u53d7\u4f24\uff0c\u770b\u5230\u5f20\u606a\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u5403\u996d\u5427\uff0c\u8fd9\u91cc\u603b\u8981\u7559\u4e2a\u7ba1\u4e8b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4e5f\u4e0d\u8d70\u8fdc\uff0c\u5c31\u5728\u9694\u58c1\u627e\u5bb6\u597d\u4e00\u70b9\u7684\u9910\u9986\uff0c\u4f60\u603b\u8be5\u8981\u5403\u4e2d\u996d\u7684\uff0c\u201d\u738b\u6d77\u7c9f\u529d\u9053\uff0c\u201c\u505a\u8001\u677f\u5c31\u8981\u76f8\u4fe1\u81ea\u5df1\u7684\u5458\u5de5\u3002\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u6709\u4e9b\u65e0\u63aa\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5979\u62c5\u5fc3\u8499\u4e50\u5fc3\u91cc\u4f1a\u53d7\u4f24\uff0c\u4f46\u662f\u7537\u53cb\u5982\u6b64\u79ef\u6781\u7684\u6002\u607f\uff0c\u53c8\u4e0d\u4fbf\u5f00\u53e3\u8bf4\u7b97\u4e86\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4ed6\u4eec\u6653\u5f97\u8499\u4e50\u5bf9\u5e2d\u82e5\u7433\u7684\u5fc3\u601d\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u5fc3\u601d\u90fd\u4e0d\u4f1a\u592a\u654f\u611f\uff0c\u751a\u81f3\u8ba4\u4e3a\u8499\u4e50\u5c31\u4e0d\u8be5\u5728\u5e2d\u82e5\u7433\u8fd9\u68f5\u6811\u4e0a\u540a\u6b7b\u3002\u5e2d\u82e5\u7433\u6f02\u4eae\u5f52\u6f02\u4eae\uff0c\u4f46\u662f\u4e1c\u5927\u8fd9\u4e00\u7ea7\u6570\u7684\u7f8e\u5973\u53c8\u4e0d\u662f\u5c31\u5979\u4e00\u4e2a\u3002\u5c31\u7b97\u4e1c\u5927\u7f8e\u5973\u7d27\u7f3a\uff0c\u4e0d\u662f\u8fd8\u6709\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u5927\u8fd9\u4e24\u4e2a\u7f8e\u5973\u624e\u5806\u7684\u5730\u65b9\uff1f\u8fd9\u4e9b\u5929\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u6b7b\u547d\u7684\u8ba8\u597d\u5b59\u9759\u6aac\uff0c\u591c\u91cc\u6709\u4e8b\u6ca1\u4e8b\u5c31\u52301978\u901b\u4e24\u5708\u559d\u676f\u9152\uff0c\u5c31\u6307\u671b\u5b59\u9759\u6aac\u4ecb\u7ecd\u97f3\u4e50\u5b66\u9662\u5b66\u821e\u8e48\u7684\u5973\u5b69\u5b50\u7ed9\u4ed6\u4eec\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u201c\u4e00\u8d77\u53bb\u5427\u2026\u2026\u201d\u5f20\u606a\u6de1\u6de1\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u8499\u4e50\u8fd9\u5c0f\u5b50\u8fde\u60c5\u654c\u90fd\u4e0d\u6562\u9762\u5bf9\uff0c\u600e\u4e48\u5c06\u5e2d\u82e5\u7433\u4ece\u60c5\u654c\u624b\u91cc\u4e89\u8fc7\u6765\uff1f\u8fd9\u5e74\u5934\u53ef\u4e0d\u65f6\u5174\u201c\u9ed8\u9ed8\u7684\u770b\u7740\u604b\u4eba\u4ece\u522b\u4eba\u90a3\u91cc\u83b7\u5f97\u5e78\u798f\u201d\u90a3\u4e00\u5957\u3002</p><p>\u3000\u3000\u89c1\u5f20\u606a\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u8499\u4e50\u52c9\u4e3a\u5176\u96be\u7684\u7b54\u5e94\u4e0b\u6765\u3002\u4e5f\u4e0d\u53bb\u522b\u7684\u5730\u65b9\uff0c\u9694\u58c1\u5c31\u6709\u4e00\u95f4\u683c\u8c03\u4e0d\u9519\u7684\u6e58\u83dc\u9986\uff0c\u53c8\u558a\u4e0a\u65bd\u65b0\u98de\uff0c\u4e00\u884c\u4eba\u8d70\u4e86\u8fdb\u6765\u627e\u4e86\u4e00\u95f4\u4e8c\u697c\u4e34\u7a97\u7684\u5305\u53a2\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5403\u8fc7\u4e2d\u996d\uff0c\u8ddf\u7740\u8fc7\u6765\u5c31\u662f\u51d1\u70ed\u95f9\uff0c\u8fd8\u60f3\u770b\u770b\u738b\u6d77\u7c9f\u8fd9\u4e48\u70ed\u5207\u7684\u5c06\u70ed\u8138\u8d34\u8d77\u6765\u6709\u4ec0\u4e48\u610f\u56fe\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5728\u4e1c\u5927\u8bfb\u5b8c\u7535\u5b50\u5de5\u7a0b\u535a\u58eb\u5b66\u4f4d\u4e4b\u540e\u5c31\u7559\u6821\u4efb\u6559\uff0c\u53c8\u4e0d\u7518\u5fc3\u6559\u4e66\u3001\u505a\u5b66\u95ee\u7684\u6e05\u5bd2\uff0c\u5c31\u8f9e\u53bb\u6559\u804c\u521b\u7acb\u4e86\u540d\u4e3a\u6d77\u7c9f\u79d1\u6280\u6709\u9650\u516c\u53f8\u7684\u8f6f\u4ef6\u516c\u53f8\uff0c\u5f00\u53d1\u4e86\u4e00\u6b3e\u8d22\u52a1\u8f6f\u4ef6\u3002\u5177\u4f53\u5efa\u90ba\u5e02\u6709\u51e0\u5bb6\u5355\u4f4d\u5728\u7528\u4ed6\u5f00\u53d1\u7684\u8d22\u52a1\u8f6f\u4ef6\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u53bb\u505a\u4ec0\u4e48\u8c03\u67e5\uff0c\u53ea\u662f\u738b\u6d77\u7c9f\u5634\u91cc\u8bf4\u5f97\u5f88\u98ce\u5149\u3002\u4e0d\u8fc7\u4e0d\u80fd\u5426\u8ba4\u738b\u6d77\u7c9f\u8fd9\u4eba\u53e3\u624d\u4e0d\u9519\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u4ece\u5e2d\u82e5\u7433\u90a3\u91cc\u77e5\u9053\u675c\u98de\u4ed6\u4eec\u6295\u8d44\u6ce8\u8d44\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u4e3b\u8981\u662f\u5f00\u53d1\u7f51\u5427\u7ba1\u7406\u7cfb\u7edf\u8f6f\u4ef6\uff0c\u5c06\u4e2d\u56fd\u8f6f\u4ef6\u4ea7\u4e1a\u7684\u524d\u666f\u63cf\u8ff0\u91d1\u5149\u707f\u70c2\uff0c\u542c\u5f97\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4ed6\u4eec\u9891\u9891\u70b9\u5934\u9644\u548c\uff0c\u867d\u7136\u8499\u4e50\u5bf9\u4ed6\u5fc3\u91cc\u6709\u6240\u62b5\u89e6\uff0c\u5374\u4e5f\u62ff\u4e0d\u51fa\u8a00\u8bed\u53cd\u9a73\u4ed6\uff0c\u6068\u4e0d\u80fd\u9a6c\u4e0a\u5c31\u643a\u624b\u8d77\u6765\u6295\u5165\u4e2d\u56fd\u8f6f\u4ef6\u53d1\u5c55\u7684\u5b8f\u4f1f\u4e8b\u4e1a\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0a\u4e2a\u6708\u968f\u79d1\u6280\u90e8\u8003\u5bdf\u56e2\u524d\u5f80\u5370\u5ea6\u73ed\u52a0\u7f57\u5c14\u8f6f\u4ef6\u56ed\u8003\u5bdf\uff0c\u6211\u5c31\u5728\u60f3\uff0c\u5370\u5ea6\u963f\u4e09\u4ec0\u4e48\u5730\u65b9\u90fd\u843d\u540e\u6211\u4eec\u4e2d\u56fd\u4e00\u5927\u622a\uff0c\u4ed6\u4eec\u7684\u6559\u80b2\u57fa\u7840\u6bd4\u4e2d\u56fd\u5dee\uff0c\u5e73\u5747\u53d7\u6559\u80b2\u7a0b\u5ea6\u4e5f\u6bd4\u4e2d\u56fd\u5dee\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u8f6f\u4ef6\u51fa\u53e3\u80fd\u529b\u8fbe\u5230\u5341\u4ebf\u3001\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u89c4\u6a21\uff0c\u4e3a\u4ec0\u4e48\u6211\u4eec\u8fde\u5370\u5ea6\u963f\u4e09\u7684\u5341\u5206\u4e4b\u4e00\u3001\u4e8c\u5341\u5206\u4e4b\u4e00\u90fd\u8fbe\u4e0d\u5230\uff1f\u201d\u738b\u6d77\u7c9f\u6177\u6168\u6fc0\u6602\u7684\u8bf4\u9053\uff0c\u201c\u6211\u770b\u5173\u952e\u662f\u4e2d\u56fd\u7684\u6295\u8d44\u8005\u76ee\u5149\u592a\u4fdd\u5b88\uff0c\u770b\u4e0d\u5230\u8f6f\u4ef6\u4ea7\u4e1a\u7684\u5de8\u5927\u6f5c\u529b\uff0c\u4e0d\u6562\u5f80\u8f6f\u4ef6\u4ea7\u4e1a\u91cc\u6295\u8d44\u2026\u2026\u201d</p></article>"}}}]);
