(window.webpackJsonp=window.webpackJsonp||[]).push([[710],{733:function(p){p.exports={chapterTitle:null,title:"\u7b2c550\u7ae0 \u522b\u4eba\u7684\u52fe\u5fc3\u6597\u89d2",content:"<article><h2>\u7b2c550\u7ae0 \u522b\u4eba\u7684\u52fe\u5fc3\u6597\u89d2</h2><p>\u3000\u3000\u5efa\u90ba\u996d\u5e97\u4e8c\u697c\u7684\u5bb4\u4f1a\u5385\u4e0e\u5e73\u65f6\u6b8a\u6613\uff0c\u5bb4\u4f1a\u5927\u5385\u5e03\u7f6e\u5728\u81ea\u52a9\u9152\u4f1a\u7684\u5f62\u5f0f\uff0c\u8fd8\u60ac\u6302\u7740\u201c\u6b22\u8fce\u65e5\u672c\u7b2c\u4e00\u7269\u4ea7\u682a\u5f0f\u4f1a\u793e\u89c2\u5149\u8bbf\u95ee\u56e2\u62b5\u90ba\u201d\u7684\u6a2a\u5e45\uff0c\u6709\u4e9b\u6f02\u4eae\u7684\u5973\u4eba\u7a7f\u7740\u548c\u670d\u3001\u6728\u5c50\u6e38\u8776\u4e00\u6837\u5728\u4eba\u7fa4\u91cc\u7a7f\u68ad\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u51ac\u5929\u5efa\u90ba\u5e02\u53d1\u751f\u8fc7\u201c\u5bcc\u6625\u9601\u4e70\u6625\u201d\u4e8b\u4ef6\u4e4b\u540e\uff0c\u5230\u56fd\u5185\u6765\u7684\u65e5\u672c\u4eba\u5404\u65b9\u9762\u90fd\u6536\u655b\u591a\u4e86\uff0c\u4e5f\u6216\u8bb8\u662f\u5efa\u90ba\u996d\u5e97\u4f5c\u4e3a\u7701\u5e02\u4e24\u7ea7\u653f\u5e9c\u5916\u4e8b\u63a5\u5f85\u573a\u6240\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u4e0a\u697c\u6765\uff0c\u7aef\u7740\u9152\u676f\u7ad9\u5728\u697c\u68af\u53e3\u7684\u4e24\u4e2a\u62ff\u65e5\u8bed\u4ea4\u8c08\u7684\u4e2d\u5e74\u4eba\u8fd8\u606d\u656c\u7684\u97a0\u4e86\u4e2a\u8eac\u3002</p><p>\u3000\u3000\u9ec4\u514b\u7fa4\u770b\u5230\u8fd9\u60c5\u5f62\uff0c\u624d\u8bb0\u5f97\u5e02\u653f\u5e9c\u4eca\u5929\u5728\u5efa\u90ba\u996d\u5e97\u6709\u5916\u4e8b\u63a5\u5f85\u6d3b\u52a8\uff0c\u8fd9\u4e2a\u5916\u4e8b\u63a5\u5f85\u6d3b\u52a8\u6ca1\u6709\u901a\u77e5\u4ed6\u53c2\u4e0e\uff0c\u4ed6\u4eec\u7684\u5305\u53a2\u5728\u5bb4\u4f1a\u5927\u5385\u7684\u91cc\u89d2\uff0c\u7a7f\u8fc7\u53bb\u8981\u662f\u9047\u5230\u8ba4\u8bc6\u7684\u4eba\uff0c\u5012\u6709\u4e9b\u5c34\u5c2c\u3002\u9ec4\u514b\u7fa4\u7709\u5934\u5fae\u5fae\u76b1\u7740\uff0c\u95ee\u9886\u8def\u7684\u4f8d\u5e94\u751f\uff1a\u201c\u80fd\u4e0d\u80fd\u7ed9\u6211\u4eec\u5b89\u6392\u522b\u7684\u5305\u53a2\uff0c\u8fd9\u91cc\u592a\u70ed\u95f9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f85\u5e94\u751f\u9000\u5230\u4e00\u8fb9\u62ff\u5bf9\u8bb2\u673a\u4e0e\u670d\u52a1\u53f0\u8054\u7edc\u6362\u5305\u53a2\u7684\u4e8b\u60c5\uff0c\u67f3\u5fd7\u519b\u5efa\u8bae\u662f\u4e0d\u662f\u4e0b\u697c\u7b49\uff0c\u8f6c\u5934\u5c06\u4e0b\uff0c\u770b\u89c1\u7ea2\u5149\u6ee1\u9762\u3001\u5e26\u7740\u4e9b\u9152\u610f\u7684\u80e1\u5b97\u5e86\u5728\u4e00\u884c\u4eba\u7684\u7c07\u62e5\u4e0b\u62ac\u9636\u800c\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u67f3\u5927\u79d8\u4e0e\u9ec4\u526f\u5e02\u957f\u4e5f\u5728\u8fd9\u91cc\uff1f\u65e9\u77e5\u9053\u9ec4\u526f\u5e02\u957f\u5728\u8fd9\u91cc\uff0c\u6211\u5c31\u4e0d\u6765\u51d1\u8fd9\u4e2a\u70ed\u95f9\u4e86\u2026\u2026\u201d\u80e1\u5b97\u5e86\u5634\u91cc\u8fd9\u4e48\u8bf4\uff0c\u811a\u6b65\u5374\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u5927\u6982\u662f\u4e0d\u60f3\u7ad9\u5728\u697c\u68af\u9636\u4e0a\u4ef0\u89c6\u9ec4\u514b\u7fa4\u5427\uff0c\u4e0a\u697c\u68af\u91cc\u8fd8\u5e26\u7740\u4e9b\u7684\u9189\u610f\u8fd9\u65f6\u5374\u4e00\u626b\u800c\u7a7a\uff0c\u773c\u795e\u9510\u5229\u7684\u626b\u8fc7\u5f20\u606a\u3001\u8bb8\u601d\uff0c\u6ca1\u6709\u610f\u8bc6\u5230\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u773c\u795e\u5374\u5728\u8bb8\u601d\u79c0\u7f8e\u67d4\u5a9a\u7684\u8138\u4e0a\u505c\u4e86\u4e24\u4e09\u79d2\uff0c\u624d\u70ed\u60c5\u7684\u8ddf\u67f3\u5fd7\u519b\u63e1\u624b\uff0c\u201c\u90fd\u6ca1\u6709\u4eba\u544a\u8bc9\u6211\u67f3\u5927\u79d8\u4f60\u4e5f\u51fa\u5e2d\u4eca\u5929\u7684\u6d3b\u52a8\u2026\u2026\u201d</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u7684\u7ea7\u522b\u6216\u8bb8\u8bf4\u4e0d\u4e0a\u5f88\u9ad8\uff0c\u6240\u8c13\u72d0\u5047\u864e\u5a01\uff0c\u4e1c\u6d77\u7701\u80fd\u6709\u51e0\u4e2a\u5b98\u5458\u5728\u4ed6\u9762\u524d\u4e0d\u4fdd\u6301\u7b11\u8138\u7684\uff1f</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u662f\u5efa\u90ba\u5e02\u653f\u5e9c\u5206\u7ba1\u62db\u5546\u5f15\u8d44\u7684\u5e38\u52a1\u526f\u5e02\u957f\u3001\u5e02\u59d4\u5e38\u59d4\u3002\u4f5c\u4e3a\u56fd\u5185\u6709\u9650\u7684\u526f\u7701\u7ea7\u8ba1\u5212\u5355\u5217\u5e02\uff0c\u5efa\u90ba\u5e02\u7684\u4e00\u4e9b\u5173\u952e\u804c\u52a1\u901a\u5e38\u610f\u4e49\u662f\u88ab\u89c6\u4e3a\u57f9\u517b\u63a5\u73ed\u4eba\u7684\u6377\u5f84\uff0c\u80e1\u5b97\u5e86\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u5750\u5230\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u5e02\u957f\u7684\u4f4d\u5b50\uff0c\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u65e0\u6cd5\u8bb0\u4f4f\u4e1c\u6d77\u7701\u90a3\u4e48\u591a\u5385\u7ea7\u5b98\u5458\u7684\u4eba\u751f\u5386\u7a0b\uff0c\u4e0d\u8fc7\u5bf9\u4e5d\u4e03\u5e74\u5728\u4e1c\u6d77\u7701\u8fd8\u5c5e\u4e8e\u653f\u6cbb\u65b0\u661f\u7684\u80e1\u5b97\u5e86\u6709\u4e9b\u5370\u8c61\uff0c\u4e5d\u516b\u5e74\u4ed6\u8c03\u51fa\u4e1c\u6d77\u5230\u522b\u7701\u62c5\u4efb\u526f\u7701\u957f\u4e4b\u540e\u5e76\u6ca1\u6709\u8d70\u4e0a\u4ed5\u9014\u7684\u5feb\u8f66\u9053\uff0c\u53cd\u800c\u6c89\u5bc2\u65e0\u58f0\u4e86\uff0c\u4e0d\u6392\u9664\u53d7\u5230\u653f\u6cbb\u503e\u8f67\uff0c\u4e0d\u8fc7\u4ed5\u9014\u4e0a\u6709\u7455\u75b5\u7684\u53ef\u80fd\u6027\u5c45\u591a\uff0c\u90bb\u91cc\u574a\u95f4\u5012\u6709\u4e9b\u771f\u5047\u96be\u8fa8\u7684\u4f20\u8a00\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u662f\u54ea\u79cd\u53ef\u80fd\uff0c\u5f20\u606a\u90fd\u8981\u4e0e\u8fd9\u6837\u7684\u4eba\u7269\u4fdd\u6301\u8ddd\u79bb\u7684\u3002</p><p>\u3000\u3000\u867d\u7136\u540c\u662f\u5efa\u90ba\u5e02\u653f\u5e9c\u7684\u526f\u5e02\u957f\uff0c\u4f46\u662f\u80e1\u5b97\u5e86\u662f\u5e38\u59d4\u6210\u5458\uff0c\u653f\u6cbb\u5730\u4f4d\u6bd4\u9ec4\u514b\u7fa4\u8981\u9ad8\u5f97\u591a\uff0c\u4ed6\u8bed\u6c14\u91cc\u591a\u5c11\u8868\u73b0\u51fa\u5bf9\u9ec4\u514b\u7fa4\u7684\u8f7b\u89c6\u4e0e\u9aa8\u5b50\u91cc\u4e5f\u900f\u7740\u4e9b\u51b7\u6de1\u3002\u5b98\u573a\u4e0a\u7684\u9634\u8c0b\u7b97\u8ba1\u3001\u62c9\u5e2e\u7ed3\u6d3e\u3001\u52fe\u5fc3\u6597\u89d2\u5386\u6765\u90fd\u662f\u5982\u6b64\uff1b\u548c\u548c\u772d\u772d\u7684\u5b98\u573a\uff0c\u9664\u4e86\u65b0\u95fb\u8054\u64ad\uff0c\u5176\u4ed6\u5730\u65b9\uff0c\u5f20\u606a\u8fd8\u90fd\u6ca1\u6709\u89c1\u5230\u8fc7\u3002</p><p>\u3000\u3000\u9ec4\u514b\u7fa4\u7684\u6001\u5ea6\u4e5f\u8f6f\u4e2d\u5e26\u786c\uff0c\u54c8\u54c8\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u7ea6\u67f3\u5927\u79d8\u51fa\u6765\u5403\u996d\uff0c\u6ca1\u60f3\u8bef\u95ef\u80e1\u5e02\u957f\u4f60\u7684\u5730\u76d8\uff0c\u6211\u4eec\u6b63\u6253\u7b97\u64a4\u9000\u8bf7\u670d\u52a1\u5458\u53e6\u5916\u5b89\u6392\u5305\u53a2\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u4e00\u8d77\u8fc7\u53bb\uff1f\u201d\u80e1\u5b97\u5e86\u8bf4\u8fd9\u53e5\u8bdd\u91cc\u773c\u775b\u53ea\u770b\u4e86\u9ec4\u514b\u7fa4\u4e00\u77ac\uff0c\u53c8\u8f6c\u8fc7\u5934\u6765\u4eb2\u70ed\u7684\u63e1\u7740\u67f3\u5fd7\u519b\u7684\u624b\uff0c\u201c\u9ec4\u526f\u5e02\u957f\u8bf4\u662f\u6211\u7684\u5730\u76d8\uff0c\u6211\u53ea\u662f\u7ed9\u4eba\u585e\u4e86\u4e00\u5f20\u6f14\u8bb2\u7a3f\u800c\u5df2\uff0c\u6f14\u8bb2\u7a3f\u6211\u73b0\u5728\u90fd\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u770b\u6765\uff0c\u600e\u4e48\u53c8\u80fd\u7b97\u662f\u6211\u7684\u5730\u76d8\uff1f\u4f60\u4eec\u5148\u627e\u5730\u65b9\uff0c\u6211\u7b49\u4f1a\u513f\u8fc7\u6765\u8ddf\u4f60\u559d\u4e00\u676f\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u4eec\u5148\u8fc7\u53bb\u5427\uff01\u201d\u5f20\u606a\u8f7b\u58f0\u8bf4\u9053\uff0c\u9886\u8def\u7684\u4f8d\u5e94\u751f\u8054\u7edc\u597d\u5305\u53a2\u4f4d\u7f6e\uff0c\u5f20\u606a\u5148\u4e00\u6b65\u8de8\u4e0b\u697c\u68af\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u7684\u773c\u775b\u7684\u786e\u5c06\u5f20\u606a\u6ee4\u8fc7\u53bb\u4e86\uff0c\u4e5f\u4e0d\u6653\u5f97\u5f20\u606a\u662f\u4e0d\u662f\u5df2\u7ecf\u4e60\u60ef\u8fd9\u79cd\u8f7b\u89c6\u4e86\uff0c\u67f3\u5fd7\u519b\u6253\u7b97\u5e2e\u80e1\u5b97\u5e86\u8865\u6551\u4e00\u4e0b\uff0c\u5c06\u5f20\u606a\u3001\u8bb8\u601d\u4ecb\u7ecd\u7ed9\u4ed6\uff1b\u5f20\u606a\u8fd9\u65f6\u5019\u63d0\u51fa\u8bf4\u8d70\uff0c\u4ed6\u5012\u4e0d\u4fbf\u6025\u7740\u4ecb\u7ecd\uff0c\u7b49\u80e1\u5b97\u5e86\u7b49\u4e0b\u627e\u6765\u656c\u9152\u65f6\u518d\u8bf4\u4e0d\u8fdf\u3002\u770b\u9ec4\u514b\u7fa4\u7684\u53cd\u5e94\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u4e3b\u52a8\u8981\u4ecb\u7ecd\u5f20\u606a\u7684\u610f\u601d\uff0c\u6bd5\u7adf\u8981\u8bf4\u5931\u793c\u7684\u8bdd\uff0c\u4e5f\u662f\u80e1\u5b97\u5e86\u5148\u5931\u793c\uff1b\u80e1\u5b97\u5e86\u7684\u773c\u775b\u4e0d\u4ec5\u5c06\u5f20\u606a\u6ee4\u8fc7\u53bb\u4e86\uff0c\u6068\u4e0d\u5f97\u5c06\u9ec4\u514b\u7fa4\u4e5f\u6ee4\u8fc7\u53bb\uff0c\u9ec4\u514b\u7fa4\u4e5f\u7684\u786e\u627e\u4e0d\u5230\u4ecb\u7ecd\u5f20\u606a\u7ed9\u80e1\u5b97\u5e86\u8ba4\u8bc6\u7684\u65f6\u673a\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u51fa\u8bf4\u8d70\uff0c\u80e1\u5b97\u5e86\u7684\u773c\u775b\u624d\u8f6c\u5230\u4ed6\u8eab\u8fb9\uff0c\u4e4b\u524d\u8981\u602a\u5f20\u606a\u4eca\u5929\u7684\u7a7f\u7740\u592a\u6ca1\u6709\u6c14\u6d3e\u4e86\uff0c\u80e1\u5b97\u5e86\u8fd9\u624d\u8f6c\u5ff5\u60f3\u8d77\u4ed6\u53ef\u80fd\u662f\u8c01\uff0c\u62ac\u624b\u521a\u8981\u8bf4\u53e5\u8bdd\uff0c\u90a3\u8fb9\u9ec4\u514b\u7fa4\u4e00\u542c\u5f20\u606a\u8bf4\u8981\u5148\u8fc7\u53bb\uff0c\u5c31\u62a2\u7740\u4e00\u6b65\u63fd\u7740\u67f3\u5fd7\u519b\u7684\u80a9\u8180\u8ba9\u4ed6\u5148\u4e0b\u697c\u68af\uff0c\u6839\u672c\u5c31\u4e0d\u7ed9\u80e1\u5b97\u5e86\u63a5\u89e6\u5f20\u606a\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u522b\u4eba\u90fd\u6709\u4e9b\u62c5\u5fc3\u9526\u6e56\u53c8\u5728\u73a9\u653f\u6cbb\u5671\u5934\uff0c\u9ec4\u514b\u7fa4\u5374\u8ba4\u771f\u5206\u6790\u8fc7\u9526\u6e56\u7684\u8d44\u6599\uff0c\u8ba4\u4e3a\u9526\u6e56\u8fd9\u6b21\u662f\u771f\u6b63\u5bf9\u5f85\u79d1\u6280\u56ed\u9879\u76ee\u7684\u2014\u2014\u4e5f\u4e0d\u80fd\u8bf4\u5bf9\u5f85\u5236\u6d46\u9879\u76ee\u7684\u6001\u5ea6\u5c31\u4e0d\u8ba4\u771f\uff0c\u53ea\u662f\u6761\u4ef6\u8fd8\u4e0d\u6210\u719f\u800c\u5df2\u3002</p><p>\u3000\u3000\u867d\u7136\u5e02\u91cc\u8fd8\u6ca1\u6709\u7edf\u4e00\u7684\u6001\u5ea6\uff0c\u5c31\u662f\u56e0\u4e3a\u5e02\u91cc\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u5f62\u6210\u7edf\u4e00\u7684\u6001\u5ea6\uff0c\u8fd9\u65f6\u5019\u6311\u5934\u5728\u65e5\u540e\u5206\u529f\u52b3\u65f6\u624d\u80fd\u663e\u51fa\u91cd\u8981\u6027\u6765\uff0c\u9ec4\u514b\u7fa4\u624d\u4e0d\u80fd\u62c5\u5fc3\u5e02\u91cc\u4f1a\u7518\u5fc3\u5c06\u9ad8\u8fbe\u4e03\u516b\u4ebf\u7684\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u6295\u8d44\u63a8\u7ed9\u522b\u7684\u7ade\u4e89\u57ce\u5e02\u2014\u2014\u518d\u8bf4\u4ed6\u5934\u4e0a\u8fd8\u6234\u7740\u65b0\u6d66\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u7684\u5934\u8854\uff0c\u4e3b\u52a8\u51fa\u51fb\uff0c\u4e5f\u4e0d\u7b97\u592a\u51fa\u683c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u770b\u7740\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u5fd9\u4e0d\u8fed\u7684\u62e5\u7740\u5f20\u606a\u4e0b\u4e86\u697c\u68af\uff0c\u80e1\u5b97\u5e86\u5c31\u8d8a\u53d1\u786e\u8ba4\u4ed6\u662f\u8fd9\u4e9b\u5929\u5728\u7701\u91cc\u6380\u8d77\u8f69\u7136\u5927\u6ce2\u7684\u90a3\u4e2a\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u9752\u5e74\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u5fc3\u91cc\u4e5f\u540e\u6094\u4e0d\u8fed\uff0c\u6ca1\u60f3\u5230\u9ec4\u514b\u7fa4\u62a2\u5148\u4e00\u6b65\u4e0e\u5f20\u606a\u8054\u7edc\u4e0a\uff1b\u603b\u4e0d\u80fd\u8fd9\u65f6\u5019\u8ffd\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u674e\u8fdc\u6e56\u5206\u5ead\u6297\u793c\u7684\u4e8b\u60c5\uff0c\u53ea\u5728\u5c0f\u8303\u56f4\u5185\u4f20\u5f00\uff0c\u4f46\u662f\u5609\u4fe1\u5b9e\u4e1a\u516c\u5e03\u9ad8\u8fbe\u4e09\u5341\u4ebf\u6e2f\u5143\u7684\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\uff0c\u624d\u5728\u7701\u5185\u6380\u8d77\u771f\u6b63\u7684\u8f69\u7136\u5927\u6ce2\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u5ffd\u60a0\u7740\u8981\u5c06\u559c\u9a6c\u62c9\u96c5\u5c71\u70b8\u5f00\u4e00\u4e2a\u7f3a\u53e3\u7684\u725f\u5176\u4e2d\u8fd8\u5728\u7ee7\u7eed\u56fd\u5185\u5404\u5730\u7ee7\u7eed\u5ffd\u60a0\uff0c\u4f46\u662f\u9ad8\u5c42\u5df2\u7ecf\u5f00\u59cb\u5bf9\u5357\u5fb7\u7684\u52a8\u5411\u4ea7\u751f\u8b66\u89c9\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u4e00\u4e2a\u516c\u7136\u7ad9\u5230\u5a92\u4f53\u4e0e\u516c\u4f17\u9762\u524d\u7684\u6240\u8c13\u5185\u5730\u201c\u9996\u5bcc\u201d\uff0c\u4e5d\u516d\u5e74\uff0c\u798f\u5e03\u65af\u5c06\u4ed6\u6392\u5217\u5728\u534e\u4eba\u5bcc\u8c6a\u699c\u7b2c374\u4f4d\uff0c\u4e2a\u4eba\u8d22\u5bcc\u4e3a1\u4ebf\u7f8e\u91d1\u3002</p><p>\u3000\u3000\u6839\u636e\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u516c\u5e03\u7684\u8d44\u6599\uff0c\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u4e00\u5e74\u7684\u9500\u989d\u552e\u5c31\u8d85\u8fc7\u56db\u5341\u4ebf\uff0c\u867d\u7136\u6ca1\u6709\u516c\u5f00\u7684\u8425\u5229\u6570\u636e\uff0c\u4f46\u662f\u4e00\u4e9b\u8d22\u7ecf\u5a92\u4f53\u9884\u6d4b\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u4e00\u5e74\u8d5a\u7684\u94b1\u5c11\u8bf4\u4e5f\u8981\u6709\u5341\u4e2a\u4ebf\uff1b\u8fd9\u4e5f\u4e0e\u9526\u6e56\u4e5d\u516d\u5e74\u7684\u6295\u8d44\u89c4\u6a21\u76f8\u9002\u5e94\u3002</p><p>\u3000\u3000\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u6210\u529f\u5b9e\u65bd\uff0c\u9526\u6e56\u624b\u91cc\u5c06\u634f\u7740\u4ec5\u4e09\u5341\u516b\u4ebf\u7684\u73b0\u91d1\uff0c\u8fd9\u662f\u4ec0\u4e48\u6982\u5ff5\uff1f</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u6709\u5236\u6d46\u9879\u76ee\u7684\u5148\u8f66\u4e4b\u9274\uff0c\u7701\u91cc\u3001\u5e02\u91cc\u7684\u786e\u62c5\u5fc3\u9526\u6e56\u8fd9\u6b21\u629b\u51fa\u79d1\u6280\u56ed\u9879\u76ee\u53c8\u662f\u5728\u73a9\u653f\u6cbb\u5671\u5934\uff0c\u6240\u4ee5\u4ee5\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u5468\u4e00\u5e73\u4e3a\u4ee3\u8868\u7684\u9526\u6e56\u9ad8\u5c42\u5728\u5916\u9762\u5927\u9020\u58f0\u52bf\uff0c\u7701\u91cc\u3001\u5e02\u91cc\u8868\u73b0\u65e2\u51b7\u6de1\u53c8\u8fdf\u949d\u2014\u2014\u5f53\u7136\u4e5f\u4e0d\u6392\u9664\u674e\u8fdc\u6e56\u62b9\u4e0d\u4e0b\u9762\u5b50\u7684\u56e0\u7d20\uff0c\u522b\u4eba\u4e5f\u8981\u7167\u987e\u5230\u674e\u8fdc\u6e56\u7684\u8138\u9762\u3002</p><p>\u3000\u3000\u4f46\u662f\u5609\u4fe1\u5b9e\u4e1a3\u670828\u65e5\u516c\u5e03\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u4e4b\u540e\uff0c\u7701\u91cc\u7684\u6001\u5ea6\u4e0d\u53bb\u7ba1\u4ed6\u2014\u2014\u674e\u8fdc\u6e56\u90a3\u91cc\u4e0d\u4f1a\u8fd9\u4e48\u5bb9\u6613\u5c31\u8f6c\u8fc7\u5f2f\u6765\u2014\u2014\u5efa\u90ba\u5e02\u91cc\u5404\u4eba\u7684\u6001\u5ea6\u5374\u6084\u7136\u53d1\u751f\u8f6c\u53d8\u3002</p><p>\u3000\u3000\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u6210\u529f\u5b9e\u65bd\uff0c\u9526\u6e56\u624b\u91cc\u5c06\u634f\u7740\u4e09\u5341\u516b\u4ebf\u7684\u5de8\u8d44\uff0c\u96be\u9053\u5f20\u606a\u4f1a\u628a\u8fd9\u4e09\u5341\u516b\u4ebf\u7684\u5de8\u989d\u8d44\u672c\u5806\u6ee1\u8c6a\u5b85\u522b\u9662\u6570\u7740\u73a9\uff1f</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c3\u670828\u65e5\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u5728\u9999\u6e2f\uff0c\u4e01\u69d0\u98de\u5f80\u7f8e\u56fd\uff0c\u82cf\u6d25\u4e1c\u6ede\u7559\u5728\u5317\u4eac\uff0c\u7559\u5728\u6d77\u5dde\u7684\u51e0\u540d\u9ad8\u5c42\u5e76\u4e0d\u53c2\u4e0e\u8fd9\u79cd\u5c42\u6b21\u7684\u51b3\u7b56\uff0c\u6d77\u5dde\u5e02\u91cc\u5bf9\u6b64\u4e8b\u53c8\u4fdd\u6301\u6c89\u9ed8\uff0c\u8054\u7cfb\u4e0d\u4e0a\u4eba\uff0c\u5efa\u90ba\u5e02\u91cc\u5c31\u7b97\u6709\u4eba\u60f3\u8981\u8868\u73b0\u5f97\u70ed\u60c5\u4e00\u4e9b\u4e5f\u6ca1\u6709\u95e8\u8def\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u6ca1\u6709\u60f3\u5230\u9ec4\u514b\u7fa4\u4f1a\u6709\u672c\u4e8b\u5148\u622a\u5230\u5f20\u606a\u672c\u4eba\uff1b\u4ed6\u4eba\u521a\u4ece\u5176\u4ed6\u5730\u65b9\u7684\u9152\u684c\u4e0a\u4e0b\u6765\uff0c\u5230\u5efa\u90ba\u996d\u5e97\u8fd8\u5e26\u7740\u4e9b\u9189\u610f\uff0c\u770b\u5230\u9ec4\u514b\u7fa4\u5c31\u60f3\u7740\u6545\u610f\u62ff\u634f\u4e00\u4e0b\u59ff\u6001\uff0c\u53c8\u5fd9\u7740\u8ddf\u67f3\u5fd7\u519b\u70ed\u7edc\u4e00\u4e0b\uff0c\u8111\u5b50\u91cc\u8fd8\u76d8\u65cb\u7740\u5f20\u606a\u65c1\u8fb9\u90a3\u4e2a\u5973\u4eba\u7684\u60ca\u8273\u5bb9\u989c\uff0c\u4e5f\u5c31\u51e0\u53e5\u8bdd\u7684\u5de5\u592b\uff0c\u771f\u7684\u5c31\u5c06\u5f20\u606a\u7ed9\u5ffd\u89c6\u6389\u4e86\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u5c31\u6ca1\u6709\u5fc3\u60c5\u7ba1\u5916\u4e8b\u63a5\u5f85\u7684\u4e8b\u60c5\uff0c\u5306\u5306\u5c06\u79d8\u4e66\u585e\u7ed9\u4ed6\u7684\u8bb2\u8bdd\u7a3f\u7167\u672c\u5ba3\u79d1\u7684\u8bfb\u5b8c\uff0c\u5fc3\u6709\u6240\u601d\u7684\u4e0e\u65e5\u672c\u7b2c\u4e00\u7269\u4ea7\u682a\u5f0f\u4f1a\u793e\u89c2\u5149\u8bbf\u95ee\u56e2\u7684\u4ee3\u8868\u559d\u7740\u9152\uff0c\u5374\u8ba9\u79d8\u4e66\u53bb\u670d\u52a1\u53f0\u6253\u542c\u9ec4\u514b\u7fa4\u3001\u67f3\u5fd7\u519b\u5c06\u5f20\u606a\u9886\u5230\u54ea\u4e2a\u5305\u53a2\u53bb\u4e86\u3002</p><p>\u3000\u3000\u79d8\u4e66\u8dd1\u51fa\u53bb\u4e00\u4f1a\u513f\uff0c\u56de\u6765\u5374\u544a\u8bc9\u4ed6\u9ec4\u514b\u7fa4\u3001\u67f3\u5fd7\u519b\u4ed6\u4eec\u79bb\u5f00\u5efa\u90ba\u996d\u5e97\u5230\u522b\u5904\u53bb\u4e86\uff0c\u80e1\u5b97\u5e86\u773c\u73e0\u5b50\u5dee\u70b9\u77aa\u51fa\u6765\uff0c\u4ed6\u5f53\u7136\u80fd\u60f3\u5230\u662f\u9ec4\u514b\u7fa4\u5728\u6363\u9b3c\uff0c\u76ee\u7684\u5c31\u662f\u4e0d\u60f3\u8ba9\u4ed6\u5206\u4e00\u676f\u7fb9\u3002</p></article>"}}}]);
