(window.webpackJsonp=window.webpackJsonp||[]).push([[710],{733:function(p){p.exports={chapterTitle:null,title:"\u7b2c709\u7ae0 \u9634\u8c0b\u7684\u5473\u9053",content:"<article><h2>\u7b2c709\u7ae0 \u9634\u8c0b\u7684\u5473\u9053</h2><p>\u3000\u3000\u56fd\u5185\u4e00\u76f4\u90fd\u5728\u5927\u529b\u7684\u6276\u6301\u8f6f\u4ef6\u4ea7\u4e1a\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\uff0c\u8f6f\u4ef6\u4f01\u4e1a\u90fd\u88ab\u5f53\u6210\u201c\u5ba0\u513f\u201d\u6765\u5bf9\u5f85\uff0c\u4eab\u6709\u5404\u79cd\u653f\u7b56\u7684\u4f18\u60e0\u2014\u2014\u7a0e\u6536\u3001\u4eba\u624d\u3001\u571f\u5730\u7b49\u7b49\uff0c\u4f46\u5c31\u5982\u540c\u5927\u591a\u5a07\u751f\u60ef\u517b\u7684\u5b69\u5b50\u4e00\u6837\uff0c\u8fd9\u682a\u88ab\u5c3d\u5fc3\u5475\u62a4\u3001\u5bc4\u4e88\u539a\u671b\u7684\u5e7c\u82d7\u5374\u59cb\u7ec8\u6ca1\u6709\u6210\u957f\u8d77\u6765\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5439\u5f97\u5929\u82b1\u4e71\u5760\uff0c\u5c06\u6210\u4e3a\u201c\u4e2d\u56fd\u7684\u5fae\u8f6f\u201d\u5f53\u6210\u6d77\u7c9f\u79d1\u6280\u594b\u6597\u7684\u76ee\u6807\u81ea\u7136\u9f13\u821e\u4eba\u5fc3\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u7b49\u4eba\u542c\u5f97\u610f\u4e71\u60c5\u8ff7\uff0c\u5e2d\u82e5\u7433\u81ea\u7136\u4e5f\u662f\u4e00\u8138\u5d07\u62dc\u7684\u51dd\u89c6\u7740\u5979\u7684\u7537\u53cb\uff0c\u5e2d\u82e5\u7433\u7684\u795e\u60c5\u53c8\u8ba9\u8499\u4e50\u6697\u81ea\u795e\u4f24\uff0c\u5012\u662f\u675c\u98de\u795e\u8272\u81ea\u82e5\uff0c\u624b\u7aef\u7740\u9152\u676f\uff0c\u65f6\u4e0d\u65f6\u7ed9\u5f20\u606a\u9012\u4e00\u4e2a\u773c\u8272\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u6697\u4e50\uff1a\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u5927\u6982\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u60f3\u5230\u675c\u98de\u5bf9\u8f6f\u4ef6\u7684\u76d7\u7248\u6210\u672c\u77e5\u9053\u5f97\u6709\u591a\u8be6\u7ec6\u3002\u56fd\u5185\u8f6f\u4ef6\u4ea7\u4e1a\u6700\u5927\u7684\u74f6\u9888\u5728\u54ea\u91cc\uff1f\u5176\u4ed6\u4eba\u6216\u8bb8\u4e0d\u660e\u767d\uff0c\u675c\u98de\u5fc3\u91cc\u5341\u5206\u6e05\u695a\u3002\u738b\u6d77\u7c9f\u4e5f\u8bf4\u4e2d\u4e00\u4e2a\u95ee\u9898\uff0c\u4e2d\u56fd\u8f6f\u4ef6\u4ea7\u4e1a\u5f97\u4e0d\u5230\u53d1\u5c55\uff0c\u56e0\u4e3a\u8f6f\u4ef6\u4f01\u4e1a\u7f3a\u4e4f\u8db3\u591f\u7684\u8d22\u529b\u652f\u6491\u7814\u53d1\uff0c\u675c\u98de\u5374\u770b\u5230\u8f6f\u4ef6\u4ea7\u4e1a\u7684\u6076\u52a3\u5546\u4e1a\u73af\u5883\uff0c\u6839\u672c\u6027\u7684\u539f\u56e0\u8fd8\u662f\u56fd\u4eba\u5bf9\u8f6f\u4ef6\u7b49\u5185\u5bb9\u6027\u7684\u77e5\u8bc6\u4ea7\u6743\u7684\u4ef7\u503c\u4e0d\u4e88\u8ba4\u540c\u3002\u516b\u5341\u5e74\u4ee3\uff0c\u6d77\u5916\u7535\u5b50\u5382\u5546\u5de8\u5934\u4e3a\u4f7f\u5176\u786c\u4ef6\u8bbe\u5907\u6253\u5165\u56fd\u5185\u5e02\u573a\uff0c\u5927\u591a\u91c7\u7528\u201c\u8d2d\u786c\u4ef6\u9001\u8f6f\u4ef6\u201d\u7684\u5e02\u573a\u7b56\u7565\uff0c\u66f4\u52a0\u6df1\u56fd\u4eba\u7684\u8fd9\u79cd\u8ba4\u77e5\u89c2\u5ff5\u3002</p><p>\u3000\u3000\u76db\u9752\u3001\u90b5\u81f3\u521a\u7b49\u4eba\u4e0b\u534a\u5e74\u5df2\u7ecf\u653e\u5f03\u5bf9\u76d7\u7248\u4ea7\u4e1a\u7684\u7ecf\u8425\uff0c\u4e4b\u524d\u5728\u6df1\u5733\u7b49\u5730\u6210\u7acb\u7684\u5927\u5927\u5c0f\u5c0f\u7684\u5de5\u4f5c\u5ba4\u3001\u79d1\u6280\u516c\u53f8\u4e5f\u8131\u79bb\u8d22\u52a1\u4e0a\u7684\u7275\u8fde\uff0c\u7b97\u662f\u5f7b\u5e95\u7684\u91d1\u76d8\u6d17\u624b\u3001\u6539\u90aa\u5f52\u6b63\u4e86\u3002\u4ed6\u4eec\u4ece\u76d7\u7248\u4ea7\u4e1a\u91cc\u5377\u8d70\u7684\u5de8\u989d\u8d44\u91d1\u4e5f\u6210\u4e3a\u4eca\u5929\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u4e0e\u5730\u4ea7\u96c6\u56e2\u53d1\u5c55\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u770b\u7740\u675c\u98de\u95ea\u70c1\u7684\u773c\u8272\uff0c\u5f20\u606a\u4e5f\u53ea\u662f\u7b11\u7740\u4e0d\u8bf4\u8bdd\uff0c\u675c\u98de\u4e4b\u6240\u4ee5\u613f\u610f\u6295\u8d44\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u5f00\u53d1\u7f51\u5427\u7ba1\u7406\u7cfb\u7edf\uff0c\u4e3b\u8981\u8fd8\u662f\u521b\u57df\u7f51\u5427\u81ea\u8eab\u6709\u9700\u6c42\uff0c\u8fd8\u6709\u5c31\u662f\u6295\u5165\u7684\u8d44\u91d1\u6bd4\u8f83\u6709\u9650\uff0c\u8fd8\u80fd\u5c06\u65bd\u65b0\u98de\u8fd9\u4e9b\u6280\u672f\u4eba\u624d\u6293\u5728\u624b\u91cc\u3002\u8bf4\u8d77\u6765\uff0c\u4ed6\u5bf9\u7f51\u5427\u7ba1\u7406\u7cfb\u7edf\u8f6f\u4ef6\u7684\u5e02\u573a\u6f5c\u529b\uff0c\u90fd\u672a\u5fc5\u6709\u591a\u5927\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u770b\u5230\u5f20\u606a\u4e0e\u675c\u98de\u7684\u6de1\u6f20\u795e\u8272\uff0c\u5fc3\u91cc\u5c31\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u4fa7\u8fc7\u5934\u6765\u95ee\u4ed6\u4eec\uff1a\u201c\u4f60\u4eec\u4ee5\u4e3a\u4e2d\u56fd\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u662f\u4e0d\u662f\u5927\u6709\u53ef\u4e3a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u4e0d\u60f3\u8bf4\u4ec0\u4e48\uff0c\u675c\u98de\u5374\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u8bf4\u8d77\u8f6f\u4ef6\uff0c\u6211\u5c31\u60f3\u8d77\u4e86\u91d1\u5c71\u8f6f\u4ef6\uff0c\u597d\u50cf\u8fd9\u4e24\u5e74\u91d1\u5c71\u8f6f\u4ef6\u4e5f\u6ca1\u6709\u4ee5\u524d\u98ce\u5149\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u91d1\u5c71\u8868\u9762\u4e0a\u4f9d\u65e7\u98ce\u5149\u65e0\u9650\uff0c\u4f46\u662f\u771f\u6b63\u4e86\u89e3\u5927\u4f17\u8f6f\u4ef6\u5e02\u573a\u7684\u4eba\u90fd\u6653\u5f97\u8fd9\u4e24\u5e74\u91d1\u5c71\u6240\u505a\u7684\u5de5\u4f5c\u5c31\u662f\u201c\u8d54\u672c\u8d5a\u5406\u559d\u201d\u3002</p><p>\u3000\u3000\u91d1\u5c71\u5f53\u5e74\u51ed\u501fDOS\u7248WPS\u6587\u5b57\u5904\u7406\u8f6f\u4ef6\u800c\u98ce\u5149\u65e0\u9650\uff0c\u5728\u5fae\u8f6f\u7684windows\u9010\u6e10\u6210\u4e3a\u4e3b\u6d41\u64cd\u4f5c\u7cfb\u7edf\u4e4b\u540e\uff0c\u4e0e\u5176\u6346\u7ed1\u7684\u4e2d\u6587Office\u4e5f\u540c\u65f6\u5e2d\u5377\u4e86\u56fd\u5185\u5e02\u573a\uff0c\u76d7\u7248\u5927\u91cf\u904d\u53ca\u4e5f\u4f7f\u5f97\u91d1\u5c71\u7684WPS\u6ca1\u6709\u4efb\u4f55\u4ef7\u683c\u4f18\u52bf\uff0c\u5e02\u573a\u840e\u7f29\u5f97\u5389\u5bb3\uff0c\u91d1\u5c71\u8fd8\u5fc5\u987b\u6839\u636eWindows\u64cd\u4f5c\u7cfb\u7edf\u7684\u6bcf\u4e00\u6b21\u5347\u7ea7\u6295\u5165\u5927\u91cf\u7684\u4eba\u529b\u3001\u7269\u529b\uff0c\u5bf9WPS\u8fdb\u884c\u5347\u7ea7\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u9762\u7684\u9053\u9053\uff0c\u65bd\u65b0\u98de\u8fd9\u4e9b\u7eaf\u6280\u672f\u6027\u7684\u4eba\u624d\u90fd\u4e0d\u6e05\u695a\uff0c\u8499\u4e50\u4ed6\u7684\u9605\u5386\u6bd5\u7adf\u8fd8\u6709\u9650\uff0c\u738b\u6d77\u7c9f\u89c1\u675c\u98de\u4e00\u4e0b\u5b50\u5c31\u8bf4\u5230\u70b9\u4e0a\uff0c\u5c31\u6653\u5f97\u4e0d\u662f\u90a3\u4e48\u597d\u7cca\u5f04\uff0c\u6ca1\u6709\u70b9\u771f\u624d\u5b9e\u6599\uff0c\u5f88\u96be\u6253\u52a8\u773c\u524d\u8fd9\u4eba\uff0c\u4e0d\u8fc7\u5f20\u606a\u51b7\u6de1\u6d6e\u52a8\u7684\u76ee\u5149\u53c8\u8ba9\u4ed6\u5fc3\u865a\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u56fd\u5185\u7684\u5927\u4f17\u8f6f\u4ef6\u5e02\u573a\uff0c\u8fd9\u4e24\u5e74\u7531\u4e8e\u76d7\u7248\u6a2a\u884c\uff0c\u56fd\u5185\u7684\u5927\u4f17\u8f6f\u4ef6\u5e02\u573a\u7684\u786e\u6709\u4e9b\u840e\u9761\u4e0d\u632f\uff0c\u4e0d\u8fc7\u6211\u4e0a\u4e2a\u6708\u4e0e\u79d1\u6280\u90e8\u7684\u5b98\u5458\u4ea4\u6d41\u8fc7\uff0c\u542c\u8bf4\u56fd\u5185\u8981\u52a0\u5f3a\u6253\u51fb\u76d7\u7248\u65b9\u9762\u7684\u529b\u5ea6\uff0c\u6211\u76f8\u4fe1\u8fd9\u4e00\u5757\u7684\u5e02\u573a\u4f1a\u5f88\u5feb\u5f97\u5230\u6539\u5584\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u4e00\u7b11\uff1a\u518d\u7ed9\u4f60\u5341\u5e74\uff0c\u8fd9\u4e00\u5757\u7684\u5e02\u573a\u90fd\u4e0d\u4f1a\u5f97\u5230\u6839\u672c\u6027\u7684\u6539\u5584\u3002\u4e0d\u8fc7\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u51fa\u53e3\u53cd\u9a73\u4ed6\uff0c\u542c\u4ed6\u4f3c\u4e4e\u8fd8\u6709\u8bdd\u8981\u8bf4\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u7531\u4e8e\u56fd\u5185\u4f01\u4e1a\u4f1a\u8ba1\u7535\u7b97\u5316\u7684\u9700\u6c42\uff0c\u52a0\u4e0a\u653f\u5e9c\u4e3a\u6276\u6301\u8f6f\u4ef6\u4ea7\u4e1a\u800c\u7279\u522b\u5236\u4f5c\u4e00\u4e9b\u4ea7\u4e1a\u653f\u7b56\uff0c\u4f7f\u5f97\u4e2d\u56fd\u7684\u8d22\u52a1\u8f6f\u4ef6\u6709\u6781\u5927\u7684\u5e02\u573a\uff0c\u53e6\u5916\uff0c\u56fd\u5185\u7684\u4f01\u4e1a\u8d8a\u6765\u8d8a\u6ce8\u91cd\u4fe1\u606f\u5316\u7ba1\u7406\uff0c\u4fe1\u606f\u5316\u5de5\u7a0b\u884d\u884c\u7684\u4f01\u4e1a\u7ea7\u8f6f\u4ef6\u5e02\u573a\u4e5f\u662f\u5f02\u5e38\u7684\u5e9e\u5927\uff0c\u6211\u4eec\u6d77\u7c9f\u516c\u53f8\u5c31\u662f\u770b\u4e2d\u8fd9\u4e24\u70b9\uff0c\u4e3b\u8981\u5f00\u53d1\u4f01\u4e1a\u7ea7\u5e94\u7528\u8f6f\u4ef6\u3002\u5f53\u7136\uff0c\u8fd9\u8fd8\u53ea\u662f\u56fd\u5185\u7684\u5e02\u573a\uff0c\u50cf\u5370\u5ea6\u7684\u8f6f\u4ef6\u51fa\u53e3\uff0c\u4eca\u5e74\u5c31\u5c06\u8fbe\u5230\u4e8c\u5341\u4ebf\u7f8e\u5143\uff0c\u4e2d\u56fd\u8f6f\u4ef6\u51fa\u53e3\u7684\u6f5c\u5728\u89c4\u6a21\u81f3\u5c11\u4e0d\u5e94\u8be5\u6bd4\u5370\u5ea6\u5dee\uff0c\u90a3\u5c11\u8bf4\u4e5f\u6709\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u6d77\u5916\u5e02\u573a\u6f5c\u529b\u53ef\u6316\u6398\u2026\u2026\u201d\u738b\u6d77\u7c9f\u6781\u4e3a\u60f3\u8bf4\u670d\u675c\u98de\u3001\u5f20\u606a\u8ba4\u8bc6\u4ed6\u7684\u89c2\u70b9\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u738b\u6d77\u7c9f\u8fd9\u53ae\u8ff7\u60d1\u4eba\u7684\u672c\u4e8b\u5012\u4e5f\u6709\u51e0\u5206\uff0c\u4e2d\u56fd\u4e0e\u5370\u5ea6\u7684\u60c5\u51b5\u80fd\u7b80\u5355\u7c7b\u6bd4\u5417\uff1f\u7b49\u4e2d\u56fd\u8f6f\u4ef6\u5916\u5305\u4ea7\u4e1a\u505a\u8d77\u6765\u4e4b\u540e\uff0c\u8f6f\u4ef6\u51fa\u53e3\u89c4\u6a21\u662f\u80fd\u53d8\u5927\u4e86\u4e00\u4e9b\uff0c\u4f46\u662f\u8f6f\u4ef6\u5916\u5305\u9664\u4e86\u5265\u524a\u5ec9\u4ef7\u7684\u8111\u529b\u52b3\u52a8\u5269\u4f59\u4ef7\u683c\uff0c\u5bf9\u56fd\u5185\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u53d1\u5c55\u5230\u5e95\u6709\u591a\u5927\u7684\u4fc3\u8fdb\u4f5c\u7528\uff1f\u4e0e\u90a3\u4e9b\u9760\u5265\u524a\u5ec9\u4ef7\u52b3\u52a8\u529b\u7684\u8840\u6c57\u4ee3\u5de5\u5382\u53c8\u6709\u591a\u5927\u7684\u533a\u522b\uff1f\u8fd8\u662f\u8bf4\u8f6f\u4ef6\u5f00\u53d1\u4eba\u5458\u5728\u8fd9\u4e9b\u8f6f\u4ef6\u5916\u5305\u516c\u53f8\u91cc\u80fd\u83b7\u5f97\u591a\u5927\u7684\u6210\u957f\uff1f</p><p>\u3000\u3000\u81f3\u4e8e\u56fd\u5185\u975e\u5e38\u5177\u6709\u4e2d\u56fd\u7279\u8272\u7684\u8d22\u52a1\u8f6f\u4ef6\u5e02\u573a\uff0c\u7531\u4e8e\u8fd9\u4e00\u5e02\u573a\u7684\u4e3b\u8981\u8d2d\u4e70\u529b\u662f\u653f\u5e9c\u90e8\u95e8\u4e0e\u56fd\u6709\u4f01\u4e1a\uff0c\u8fd9\u7c7b\u8f6f\u4ef6\u516c\u53f8\u6210\u529f\u7684\u6838\u5fc3\u7ade\u4e89\u529b\u662f\u516c\u5173\uff0c\u800c\u975e\u6280\u672f\u3002</p><p>\u3000\u3000\u666e\u901a\u7684\u4f01\u4e1a\u7528\u6237\u5927\u591a\u4e0d\u9700\u8981\u4e3a\u8f6f\u4ef6\u4e0e\u540e\u7eed\u7684\u7ef4\u62a4\u670d\u52a1\u591a\u82b1\u94b1\u3002\u7531\u4e8e\u56fd\u5185\u4f01\u4e1a\u7ba1\u7406\u7684\u57fa\u7840\u5f88\u8584\u5f31\uff0c\u4fe1\u606f\u5316\u7ba1\u7406\u7684\u6c34\u5e73\u5f88\u5dee\uff0c\u8fd9\u4e9b\u4f01\u4e1a\u7528\u6237\u5bf9\u7ba1\u7406\u8f6f\u4ef6\u7684\u9700\u6c42\u4e94\u82b1\u516b\u95e8\uff0c\u975e\u5e38\u7684\u968f\u610f\u3002\u51e0\u4e4e\u6bcf\u5f00\u53d1\u4e00\u4e2a\u4f01\u4e1a\u7528\u6237\u5c31\u8981\u4e13\u95e8\u4e3a\u5176\u5b9a\u5236\u4e00\u5957\u8f6f\u4ef6\uff0c\u8f6f\u4ef6\u516c\u53f8\u5fc5\u987b\u6295\u5165\u5927\u91cf\u7684\u3001\u4f4e\u6548\u7684\u4eba\u529b\u3001\u7269\u529b\u53bb\u6ee1\u8db3\u8fd9\u4e9b\u4f01\u4e1a\u7528\u6237\u7684\u9700\u6c42\uff0c\u8fd9\u4e00\u5757\u7684\u5229\u6da6\u7387\u5c31\u53d8\u5f97\u76f8\u5f53\u4f4e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5374\u4e0d\u5f00\u53e3\u8bf4\u8bdd\uff0c\u66f4\u4e0d\u4f1a\u95f2\u7740\u6ca1\u4e8b\u8ddf\u738b\u6d77\u7c9f\u5728\u996d\u684c\u4e0a\u8fa9\u8bba\u3002\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u7684\u4e8b\u52a1\uff0c\u4ed6\u90fd\u60f3\u7531\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u81ea\u5df1\u505a\u51b3\u7b56\uff0c\u54ea\u6015\u56e0\u4e3a\u7ecf\u9a8c\u4e0d\u8db3\u8499\u53d7\u635f\u5931\u4e5f\u65e0\u6240\u8c13\uff0c\u6709\u65f6\u5019\u7ecf\u9a8c\u6559\u8bad\u4e5f\u662f\u96be\u5f97\u7684\u8d22\u5bcc\u3002\u5012\u662f\u5f20\u606a\u8d8a\u662f\u8fd9\u822c\u59ff\u6001\uff0c\u4e5f\u8ba9\u738b\u6d77\u7c9f\u5fc3\u91cc\u8d8a\u662f\u4e0d\u5b89\uff0c\u8d8a\u8bf4\u8d8a\u5fc3\u865a\uff0c\u6700\u540e\u90fd\u53ea\u8ba4\u7740\u5f20\u606a\uff0c\u8ddf\u4ed6\u8bf4\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u90fd\u8bf4\u6765\u542c\u542c\uff0c\u83ab\u4e0d\u662f\u4f60\u8ba4\u4e3a\u6211\u6ca1\u6709\u8d44\u683c\u542c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5475\uff0c\u6211\u53c2\u52a0\u521b\u534f\uff0c\u5c31\u662f\u6df7\u5403\u7b49\u6b7b\u73a9\u7684\uff0c\u521b\u57df\u7f51\u5427\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u6ca1\u6709\u53c2\u52a0\u8fc7\uff0c\u4f60\u8981\u95ee\u6211\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u6211\u6ca1\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u9664\u4e86\u675c\u98de\u4e4b\u5916\uff0c\u5728\u5ea7\u7684\u4eba\u4e2d\uff0c\u4e5f\u53ea\u6709\u8499\u4e50\u77e5\u9053\u5f20\u606a\u9690\u85cf\u5728\u80cc\u540e\u7684\u771f\u5b9e\u5f71\u54cd\u529b\uff0c\u4ed6\u89c1\u5f20\u606a\u8fd9\u6837\u8868\u6001\uff0c\u4ed6\u81ea\u7136\u6700\u5f00\u5fc3\u3002\u7531\u4e8e\u5e2d\u82e5\u7433\u7684\u56e0\u7d20\uff0c\u4ed6\u5fc3\u91cc\u5bf9\u738b\u6d77\u7c9f\u5c31\u6709\u62b5\u89e6\uff0c\u4f46\u5bf9\u738b\u6d77\u7c9f\u7684\u8fd9\u756a\u8bdd\u8fd8\u7f3a\u4e4f\u8db3\u591f\u7684\u8fa9\u8bc6\u529b\uff0c\u4e0d\u8981\u8bf4\u4ed6\u4e86\uff0c\u5c31\u662f\u675c\u98de\uff0c\u5bf9\u4f01\u4e1a\u7ea7\u8f6f\u4ef6\u5e02\u573a\u7684\u8ba4\u8bc6\u4e5f\u6709\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u867d\u8bf4\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u7ed9\u9f13\u52a8\u5f97\u70ed\u8840\u6cb8\u817e\uff0c\u4f46\u662f\u4e09\u4e2a\u5173\u952e\u4eba\u7269\uff0c\u90fd\u4e00\u4ed8\u5e73\u6de1\u5982\u6c34\u7684\u6a21\u6837\uff0c\u738b\u6d77\u7c9f\u5c31\u6709\u4e9b\u5fc3\u51b7\u4e86\uff0c\u4ed6\u7684\u89c2\u5bdf\u529b\u4e5f\u5f88\u5f3a\uff0c\u6ce8\u610f\u5230\u5f20\u606a\u7684\u51b7\u6de1\u6001\u5ea6\u5bf9\u8499\u4e50\u3001\u675c\u98de\u4e24\u4e2a\u4eba\u7684\u5f71\u54cd\uff0c\u53c8\u770b\u4e0d\u900f\u5f20\u606a\u7684\u5e95\u7ec6\uff0c\u5fc3\u91cc\u6709\u4e9b\u6028\u6068\uff0c\u5374\u63a9\u9970\u7740\u6ca1\u6709\u8868\u9732\u51fa\u6765\uff0c\u5374\u6653\u5f97\u4ed6\u4eca\u5929\u575a\u6301\u8981\u8fc7\u6765\u7684\u610f\u56fe\u65e0\u6cd5\u5b9e\u73b0\u4e86\u3002</p><p>\u3000\u3000\u8499\u4e50\u3001\u675c\u98de\u8981\u6ca1\u6709\u7279\u6b8a\u7684\u5173\u7cfb\uff0c\u60f3\u8981\u4ece\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u83b7\u5f97\u4e00\u5343\u4e07\u7684\u878d\u8d44\uff0c\u738b\u6d77\u7c9f\u6253\u6b7b\u90fd\u4e0d\u4fe1\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u7684\u5173\u7cfb\u3002\u4ece\u5e2d\u82e5\u7433\u90a3\u91cc\u77e5\u9053\u76f8\u5173\u4fe1\u606f\uff0c\u738b\u6d77\u7c9f\u770b\u4e2d\u7684\u4e0d\u4ec5\u4ec5\u662f\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u624b\u91cc\u7684\u4e00\u5343\u4e07\u8d44\u91d1\u2014\u2014\u8fd9\u90e8\u5206\u8d44\u91d1\u5df2\u7ecf\u7ed9\u7f51\u5427\u6d88\u8017\u6389\u5927\u534a\uff0c\u4ed6\u5e0c\u671b\u5148\u5c06\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u62c9\u4e0b\u6c34\uff0c\u7136\u540e\u901a\u8fc7\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u4e0e\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u7684\u7279\u6b8a\u5173\u7cfb\uff0c\u4ece\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u91cc\u5438\u8840\u3002\u738b\u6d77\u7c9f\u54ea\u91cc\u66fe\u60f3\u5230\uff0c\u521d\u6218\u5c31\u544a\u632b\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u8fd8\u60f3\u8bf4\u4e9b\u4ec0\u4e48\u6700\u540e\u52aa\u529b\u4e00\u628a\uff0c\u8fd9\u65f6\u5019\u4ece\u697c\u68af\u90a3\u8fb9\u4e0a\u6765\u51e0\u4e2a\u4eba\u3002\u5f20\u606a\u8f6c\u8eab\u770b\u8fc7\u53bb\uff0c\u5374\u662f\u80e1\u91d1\u661f\u4e0e\u6821\u5b66\u751f\u4f1a\u7684\u51e0\u540d\u5b66\u751f\u5e72\u90e8\uff0c\u5c31\u8fde\u4e4b\u524d\u8131\u79bb\u866b\u4ff1\u4e50\u90e8\u7684\u90b5\u5f6c\u4e5f\u8ddf\u4ed6\u4eec\u8d70\u5728\u4e00\u8d77\uff0c\u4e0d\u6653\u5f97\u56e0\u4e3a\u4ec0\u4e48\u4e8b\u803d\u6401\u5230\u73b0\u5728\u624d\u627e\u5730\u65b9\u5403\u4e2d\u996d\u3002</p><p>\u3000\u3000\u521b\u534f\u5728\u6821\u5185\u7684\u6d3b\u52a8\u5ba4\u5c31\u4e0e\u6821\u5b66\u751f\u4f1a\u7684\u529e\u516c\u5ba4\u6328\u7740\uff0c\u521b\u534f\u4e0e\u5b66\u751f\u4f1a\u867d\u7136\u6709\u4e9b\u660e\u4e89\u6697\u6597\uff0c\u4f46\u662f\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u8499\u4e50\u3001\u65bd\u65b0\u98de\u3001\u5e2d\u82e5\u7433\u4ed6\u4eec\u4e0e\u5b66\u751f\u4f1a\u7684\u51e0\u540d\u5b66\u751f\u5e72\u90e8\u90fd\u662f\u6709\u79c1\u4eba\u4ea4\u60c5\u7684\uff0c\u89c1\u8fc7\u9762\uff0c\u81ea\u7136\u8981\u6253\u62db\u547c\u3002\u5f53\u7136\u4e5f\u6709\u5c06\u6028\u6068\u6446\u5230\u8138\u4e0a\u7684\uff0c\u80e1\u91d1\u661f\u770b\u5230\u5f20\u606a\uff0c\u5fc3\u91cc\u81ea\u7136\u5c31\u5f88\u4e0d\u75db\u5feb\uff0c\u8fdc\u8fdc\u7684\u770b\u7740\uff0c\u6ca1\u6709\u8d70\u8fc7\u6765\u6253\u62db\u547c\u3002\u7ec4\u5efa\u521b\u534f\u65f6\uff0c\u90b5\u5f6c\u7ed9\u5f20\u606a\u6559\u8bad\u4e00\u987f\uff0c\u53c8\u4e0e\u8499\u4e50\u4ed6\u4eec\u6709\u77db\u76fe\uff0c\u6700\u7ec8\u9009\u62e9\u8131\u79bb\u866b\u4ff1\u4e50\u90e8\u4e5f\u4e0d\u80af\u6574\u4f53\u52a0\u5165\u521b\u534f\uff0c\u8fd9\u65f6\u5019\uff0c\u81ea\u7136\u4e5f\u662f\u677f\u7740\u8138\uff0c\u4e00\u4ed8\u5f88\u4e0d\u75db\u5feb\u7684\u6a21\u6837\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5f20\u606a\u624d\u4e3b\u52a8\u8ddf\u738b\u6d77\u7c9f\u8bf4\u7b2c\u4e00\u53e5\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u603b\u627e\u6211\u4eec\uff0c\u4f3c\u4e4e\u662f\u627e\u9519\u5bf9\u8c61\u4e86\uff0c\u738b\u603b\u7684\u516c\u53f8\u662f\u5f00\u53d1\u8d22\u52a1\u8f6f\u4ef6\u7684\uff0c\u90a3\u6211\u4ecb\u7ecd\u4e00\u4e2a\u6709\u7528\u7684\u4eba\u7ed9\u738b\u603b\u4f60\u8ba4\u8bc6\uff0c\u201d\u671d\u80e1\u91d1\u661f\u90a3\u8fb9\u5476\u5476\u5634\uff0c\u201c\u90a3\u8fb9\u90a3\u5c0f\u5b50\u662f\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\uff0c\u80e1\u526f\u5e02\u957f\u6ca1\u6709\u513f\u5b50\uff0c\u5f85\u4ed6\u53ef\u4ee5\u8bf4\u662f\u89c6\u82e5\u5df1\u51fa\u2026\u2026\u201d</p></article>"}}}]);
