(window.webpackJsonp=window.webpackJsonp||[]).push([[711],{734:function(p){p.exports={chapterTitle:null,title:"\u7b2c551\u7ae0 \u7ed9\u522b\u4eba\u505a\u7684\u9898\u76ee",content:"<article><h2>\u7b2c551\u7ae0 \u7ed9\u522b\u4eba\u505a\u7684\u9898\u76ee</h2><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u968f\u7740\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u4ece\u5e02\u653f\u5e9c\u5916\u4e8b\u63a5\u5f85\u7684\u81ea\u52a9\u9152\u4f1a\u79bb\u5f00\uff0c\u4e0b\u4e86\u697c\uff0c\u9ec4\u514b\u7fa4\u5c31\u8bf4\u5efa\u90ba\u996d\u5e97\u4e34\u65f6\u7684\u9644\u697c\u91cc\u65b0\u5f00\u4e86\u4e00\u5bb6\u6c88\u8bb0\u9e45\u9986\u4e0d\u9519\uff0c\u5efa\u8bae\u53bb\u6c88\u8bb0\u9e45\u9986\u7528\u9910\u3002</p><p>\u3000\u3000\u6c88\u8bb0\u9e45\u9986\u5c31\u5f00\u7740\u5efa\u90ba\u996d\u5e97\u7684\u4e34\u8857\u9644\u697c\u5e95\u5c42\uff0c\u4ece\u5efa\u90ba\u996d\u5e97\u8d70\u51fa\u53bb\uff0c\u62d0\u4e2a\u5f2f\u591a\u8d70\u4e24\u6b65\u8def\u5c31\u5230\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u5927\u559c\u6b22\u5403\u9e45\u8089\uff0c\u9e45\u8089\u8f83\u80a5\uff0c\u4ed6\u60df\u5728\u6c88\u8bb0\u9e45\u9986\u5403\u7684\u80a5\u9e45\u6ca1\u6709\u817b\u5473\uff0c\u5750\u8f66\u8fdb\u5efa\u90ba\u996d\u5e97\u4e4b\u524d\u770b\u5230\u6c88\u8bb0\u9e45\u9986\u7528\u7ea2\u8272\u5f69\u706f\u88c5\u9970\u7684\u62db\u724c\uff0c\u4e0b\u8f66\u65f6\uff0c\u5f20\u606a\u8fd8\u8ddf\u8bb8\u601d\u8bf4\u8d77\u53ef\u4ee5\u8ba9\u6c88\u8bb0\u9e45\u9986\u5c06\u4ed6\u4eec\u7684\u62db\u724c\u83dc\u80a5\u9e45\u4e0e\u9e45\u809d\u9001\u5230\u5efa\u90ba\u996d\u5e97\u91cc\u6765\u3002</p><p>\u3000\u3000\u9ec4\u514b\u7fa4\u8fd9\u4e48\u8bf4\uff0c\u660e\u91cc\u662f\u8ba8\u5f20\u606a\u7684\u6b22\u559c\uff0c\u5b9e\u9645\u4e0a\u662f\u8981\u907f\u5f00\u80e1\u5b97\u5e86\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u5fc3\u91cc\u662f\u660e\u767d\u7684\uff0c\u5fc3\u60f3\u80e1\u5b97\u5e86\u6700\u540e\u4e00\u523b\u5e94\u8be5\u610f\u8bc6\u5230\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u7b49\u4f1a\u513f\u8981\u662f\u627e\u4e0d\u5230\u4eba\u53ea\u6015\u4f1a\u53eb\u5a18\uff0c\u53ea\u662f\u9ec4\u514b\u7fa4\u5f00\u51fa\u4e86\u53e3\uff0c\u4ed6\u5c31\u4e0d\u80fd\u76f4\u63a5\u5426\u5b9a\u6389\uff0c\u6240\u8c13\u4e3b\u968f\u5ba2\u4fbf\uff0c\u5173\u952e\u8fd8\u8981\u770b\u5f20\u606a\u7684\u610f\u613f\uff1b\u603b\u4e4b\u4e5f\u662f\u9ec4\u514b\u7fa4\u5f97\u7f6a\u80e1\u5b97\u5e86\uff0c\u4ed6\u5927\u4e0d\u4e86\u65e5\u540e\u9047\u5230\u80e1\u5b97\u5e86\u6253\u4e00\u4e2a\u62db\u547c\u9053\u4e2a\u6b49\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u9ec4\u514b\u7fa4\u6ca1\u6709\u592a\u6df1\u7684\u4e86\u89e3\uff0c\u5bf9\u80e1\u5b97\u5e86\u5374\u6709\u5148\u5165\u4e3a\u4e3b\u7684\u758f\u79bb\u611f\uff0c\u9ec4\u514b\u7fa4\u8bf4\u5230\u5916\u9762\u7684\u6c88\u8bb0\u9e45\u9986\uff0c\u4ed6\u4e5f\u5c31\u987a\u7740\u9ec4\u514b\u7fa4\u7684\u8bed\u6c14\u8bf4\u597d\u3002</p><p>\u3000\u3000\u4ece\u9ec4\u514b\u7fa4\u4e0e\u80e1\u5b97\u5e86\u4e24\u4eba\u7684\u53cd\u5e94\uff0c\u5f20\u606a\u610f\u8bc6\u5230\u5efa\u90ba\u5e02\u91cc\u8fd8\u6ca1\u6709\u5bf9\u9526\u6e56\u629b\u51fa\u6765\u7684\u79d1\u6280\u56ed\u9879\u76ee\u5f62\u6210\u7edf\u4e00\u7684\u610f\u89c1\uff0c\u662f\u9ec4\u514b\u7fa4\u81ea\u5df1\u6709\u7acb\u529f\u7684\u610f\u613f\uff0c\u53e6\u627e\u5730\u65b9\u5403\u996d\u4e0d\u8fc7\u662f\u4e0d\u60f3\u8ba9\u80e1\u5b97\u5e86\u6cbe\u8fb9\uff1b\u8fd9\u4e48\u8bf4\u6765\uff0c\u67f3\u5fd7\u519b\u4e5f\u662f\u79c1\u81ea\u51fa\u52a8\uff0c\u5e76\u975e\u53d7\u9676\u664b\u4e66\u8bb0\u7684\u5429\u5490\u5173\u6ce8\u8fd9\u4ef6\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5403\u996d\u65f6\uff0c\u81ea\u7136\u8981\u63d0\u5230\u79d1\u6280\u56ed\u9879\u76ee\uff0c\u65e2\u7136\u8fd9\u6b21\u53ea\u662f\u9ec4\u514b\u7fa4\u4e0e\u67f3\u5fd7\u519b\u7684\u79c1\u81ea\u51fa\u52a8\uff0c\u5f20\u606a\u7684\u53e3\u98ce\u5c31\u54ac\u5f97\u5f88\u7d27\uff0c\u5927\u6982\u5c06\u5df2\u7ecf\u516c\u5f00\u51fa\u6765\u7684\u6d88\u606f\u5728\u9ec4\u3001\u67f3\u4e8c\u4eba\u4e4b\u95f4\u590d\u8ff0\u4e86\u4e00\u904d\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u4eb2\u53e3\u8bf4\u6765\uff0c\u611f\u89c9\u81ea\u7136\u8981\u6bd4\u9605\u8bfb\u5a92\u4f53\u7684\u62a5\u9053\u66f4\u6709\u771f\u5b9e\u611f\uff0c\u9ec4\u514b\u7fa4\u4e5f\u5c31\u662f\u60f3\u4eb2\u81ea\u786e\u8ba4\u4e00\u4e0b\uff0c\u7136\u540e\u624d\u66f4\u6709\u628a\u63e1\u6311\u5934\u8bf4\u670d\u5e02\u91cc\u79ef\u6781\u53bb\u4e89\u53d6\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u3002</p><p>\u3000\u3000\u4ed6\u4e5f\u662f\u5f53\u521d\u5728\u4e1c\u6d77\u5927\u5b66\u65b0\u6d66\u6821\u533a\u53c2\u52a0\u65b0\u751f\u519b\u8bad\u9605\u5175\u65f6\u770b\u5230\u67f3\u5fd7\u519b\u5728\u64cd\u573a\u5916\u7684\u4fbf\u9053\u5524\u4f4f\u5f20\u606a\u2014\u2014\u80e1\u5b97\u5e86\u5f53\u65f6\u4e5f\u5728\u573a\u7684\uff0c\u53ea\u662f\u79bb\u5f97\u8fdc\uff0c\u5f53\u65f6\u6ca1\u6709\u770b\u6e05\u5f20\u606a\u7684\u76f8\u8c8c\u2014\u2014\u9ec4\u514b\u7fa4\u4fbf\u627e\u5230\u67f3\u5fd7\u519b\uff0c\u5e0c\u671b\u4ed6\u80fd\u5e2e\u81ea\u5df1\u5f15\u8350\u7ed9\u5f20\u606a\uff1b\u4e0d\u7136\u4ed6\u4e5f\u627e\u4e0d\u5230\u5f15\u8350\u4eba\u80fd\u4e0e\u5f20\u606a\u642d\u4e0a\u7ebf\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u72ee\u5b50\u56ed\u7684\u522b\u5885\u597d\u4e45\u90fd\u6ca1\u6709\u4f4f\u4eba\uff0c\u4e5f\u6ca1\u6709\u8ba9\u4eba\u5b9a\u65f6\u6536\u62fe\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u591c\u91cc\u4f4f\u8fdb\u53bb\uff0c\u88ab\u8925\u6c89\u7538\u7538\u7684\u5438\u8db3\u4e86\u6c5f\u8fb9\u7684\u6f6e\u6c14\uff0c\u8863\u67dc\u91cc\u53ea\u6709\u4e00\u5e8a\u6bef\u5b50\u662f\u5e72\u71e5\u7684\uff0c\u5f20\u606a\u4fbf\u4e0e\u8bb8\u601d\u88f9\u7740\u6bef\u5b50\u4f9d\u504e\u5750\u5728\u4e09\u697c\u98d8\u7a97\u4e0a\u542c\u7740\u7a97\u5916\u7684\u6c5f\u6d9b\u58f0\u8bf4\u8bdd\uff0c\u5c4b\u91cc\u7184\u4e86\u706f\uff0c\u5929\u8fb9\u7684\u6708\u7259\u7684\u51b7\u8f89\u6d12\u4e0b\u6765\uff0c\u7ed9\u9759\u8c27\u7684\u591c\u8272\u67d3\u4e0a\u51e0\u5206\u5bd2\u610f\u3002\u5f20\u606a\u51dd\u89c6\u8bb8\u601d\u7684\u7ea2\u5507\u5a07\u6da6\u3001\u773c\u7738\u67d4\u5a9a\uff0c\u5c06\u5979\u5a07\u8f6f\u7684\u8eab\u4efd\u653e\u5728\u81ea\u5df1\u7684\u676f\u91cc\uff0c\u8f7b\u8f7b\u7684\u543b\u7740\uff0c\u5728\u6c5f\u6d9b\u7ec6\u788e\u7684\u62cd\u5cb8\u58f0\u91cc\uff0c\u89e3\u5f00\u8bb8\u601d\u7684\u8863\u886b\uff0c\u5c31\u5728\u7a97\u53f0\u4e0a\u62b5\u6b7b\u7f20\u7ef5\u3002</p><p>\u3000\u3000\u4e91\u96e8\u8fc7\u540e\uff0c\u8bb8\u601d\u7c89\u9762\u6f6e\u7ea2\uff0c\u8eab\u4e0a\u7684\u8d64\u88f8\u5728\u6708\u8272\u91cc\u7ec6\u817b\u5982\u7389\u7684\u808c\u80a4\u4e5f\u4f3c\u6577\u4e86\u4e00\u5c42\u6843\u7ea2\u7684\u7c89\uff0c\u5f02\u6837\u7684\u5a07\u8273\uff0c\u5f20\u606a\u7231\u4e0d\u91ca\u624b\u7684\u6402\u7740\u8bb8\u601d\u73b2\u73d1\u6709\u81f4\u7684\u5a07\u8eaf\u5750\u5728\u7a97\u53f0\u4e0a\uff0c\u76ef\u7740\u5317\u9762\u9ed1\u6697\u7684\u95ea\u7740\u7cbc\u5149\u7684\u6c5f\u9762\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eca\u5929\u4f3c\u4e4e\u5bf9\u9ec4\u514b\u7fa4\u4e0d\u591f\u70ed\u60c5\u554a\uff1f\u201d\u8bb8\u601d\u6293\u8d77\u6bef\u5b50\u7684\u4e00\u89d2\uff0c\u5c06\u5979\u4e0e\u5f20\u606a\u8d64\u88f8\u7684\u8eab\u4f53\u4e00\u8d77\u63a9\u4f4f\uff0c\u5979\u4e0d\u4e60\u60ef\u8eab\u4f53\u66b4\u9732\u5728\u6e05\u51b7\u7684\u6708\u5149\u4e0b\uff0c\u5012\u662f\u4eab\u53d7\u7740\u4e24\u4eba\u808c\u80a4\u76f8\u6328\u7684\u9500\u9b42\u3002\u9526\u6e56\u8fd9\u4e9b\u5929\u5927\u9020\u58f0\u52bf\uff0c\u65e0\u975e\u5c31\u662f\u60f3\u8ba9\u7701\u91cc\u3001\u5efa\u90ba\u5e02\u91cc\u5165\u4ed6\u5f40\u4e2d\uff0c\u597d\u4e0d\u5bb9\u6613\u5efa\u90ba\u5e02\u91cc\u6709\u4eba\u6311\u5934\uff0c\u5f20\u606a\u5374\u6ca1\u6709\u8868\u73b0\u51fa\u5e94\u6709\u7684\u70ed\u60c5\u3002</p><p>\u3000\u3000\u201c\u8fd1\u800c\u751f\u72ce\uff0c\u201d\u5f20\u606a\u5e2e\u8bb8\u601d\u5c06\u6bef\u5b50\u6396\u597d\uff0c\u611f\u53d7\u7740\u5ae9\u6ed1\u5a07\u8eaf\u5728\u6000\u91cc\u7684\u9500\u9b42\u89e6\u611f\uff0c\u201c\u9ec4\u514b\u7fa4\u672a\u5fc5\u6709\u6211\u5fc3\u7518\u60c5\u613f\u5c06\u6240\u6709\u529f\u7ee9\u90fd\u9001\u7ed9\u4ed6\u7684\u6c14\u5ea6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u9700\u8981\u5bf9\u65b9\u6709\u600e\u4e48\u6c14\u5ea6\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6c14\u5ea6\uff0c\u53ef\u80fd\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u9ec4\u514b\u7fa4\u4e00\u4e2a\u4eba\u7684\u95ee\u9898\u3002\u65b0\u6d66\u7684\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\uff0c88\u5e74\u5c31\u7531\u7701\u653f\u5e9c\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u5171\u540c\u521b\u5efa\uff0c91\u5e74\u5c31\u6210\u4e3a\u56fd\u5185\u9996\u6279\u56fd\u5bb6\u7ea7\u9ad8\u65b0\u533a\uff0c\u4e5f\u662f\u4e5d\u4e00\u5e74\uff0c\u9ec4\u514b\u7fa4\u4ece\u5e02\u5236\u9769\u5382\u957f\u7684\u4f4d\u5b50\u4e0a\u8c03\u5230\u9ad8\u65b0\u533a\u62c5\u4efb\u56ed\u533a\u526f\u4e3b\u4efb\uff0c\u53c8\u5148\u540e\u517c\u4efb\u65b0\u6d66\u533a\u533a\u59d4\u526f\u4e66\u8bb0\u3001\u533a\u59d4\u4e66\u8bb0\u3001\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u3001\u515a\u7ec4\u4e66\u8bb0\u7b49\u804c\uff0c\u73b0\u5728\u53c8\u8df3\u4e86\u4e00\u7ea7\u5f53\u4e0a\u5efa\u90ba\u5e02\u526f\u5e02\u957f\uff0c\u4ed6\u7684\u5b98\u8def\u5012\u662f\u987a\u7545\uff0c\u4f46\u662f\u9ad8\u65b0\u56ed\u533a\u7684\u53d1\u5c55\u5462\uff1f\u73b0\u5728\u90fd\u4e5d\u4e03\u5e74\u4e86\uff0c\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u4ea7\u503c\u5360\u5efa\u90ba\u5e02\u751f\u4ea7\u603b\u503c\u768412\uff05\u90fd\u4e0d\u5230\uff0c\u5168\u533a\u89c4\u5212\u9762\u79ef16.5\u5e73\u65b9\u516c\u91cc\uff0c\u5df2\u5f00\u53d1\u4e0d\u8db3\u56db\u5206\u4e4b\u4e00\uff0c\u524d\u540e\u5171\u5f15\u8fdb\u8fd1\u4e09\u767e\u5bb6\u4f01\u4e1a\uff0c\u771f\u6b63\u5177\u6709\u9ad8\u65b0\u79d1\u6280\u7279\u5f81\u7684\u4f01\u4e1a\u4e0d\u5230\u4e8c\u5341\u5bb6\uff0c\u8fd9\u6837\u7684\u6210\u7ee9\uff0c\u5f88\u96be\u8ba9\u4eba\u4fe1\u4efb\u554a\uff01\u201d\u5f20\u606a\u54a7\u5634\u8f7b\u7b11\u7740\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u662f\u629b\u51fa\u79d1\u6280\u56ed\u9879\u76ee\u4e0d\u5047\uff0c\u4e5f\u660e\u786e\u79d1\u6280\u56ed\u521d\u671f\u7684\u6295\u8d44\u91d1\u989d\u5927\u7ea6\u5728\u516d\u5230\u5341\u4ebf\u4e4b\u95f4\uff0c\u4e5f\u660e\u786e\u8bf4\u660e\u79d1\u6280\u56ed\u9879\u76ee\u6700\u7ec8\u662f\u4e3a\u7231\u8fbe\u96c6\u56e2\u5168\u9762\u8fdb\u519b\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u505a\u51c6\u5907\uff0c\u4f46\u662f\u79d1\u6280\u56ed\u9879\u76ee\u600e\u4e48\u80fd\u5c06\u8fd9\u7b14\u8d44\u91d1\u82b1\u6389\uff0c\u53ea\u6015\u5efa\u90ba\u5e02\u7684\u5b98\u5458\u90fd\u6ca1\u6709\u4ec0\u4e48\u5177\u4f53\u7684\u5370\u8c61\u5427\uff0c\u4ed6\u4eec\u603b\u4e0d\u80fd\u6307\u671b\u9526\u6e56\u6765\u505a\u57fa\u5efa\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u7ed9\u5efa\u90ba\u5e02\u91cc\u7684\u90a3\u4e9b\u5b98\u8001\u7237\u4eec\u51fa\u4e86\u4e00\u9053\u5f88\u5927\u7684\u9898\u76ee\u5462\uff1f\u201d\u8bb8\u601d\u62ac\u5934\u671b\u7740\u5f20\u606a\u67d4\u5a9a\u7684\u7b11\u7740\uff0c\u201c\u5c31\u8981\u770b\u8c01\u7b54\u5f97\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u56fd\u5185\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\uff0c\u4eac\u6caa\u7ca4\u5c5e\u4e8e\u7b2c\u4e00\u96c6\u56e2\uff0c\u6b66\u6c49\u3001\u676d\u5dde\u3001\u5efa\u90ba\u7b49\u57ce\u5e02\u5c5e\u4e8e\u7b2c\u4e8c\u96c6\u56e2\u7684\u5e8f\u5217\uff0c\u73b0\u5728\uff0c\u56fd\u5185\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u5e94\u8be5\u8bf4\u8fd8\u5904\u4e8e\u8d77\u6b65\u4e0d\u4e45\u7684\u9636\u6bb5\uff0c\u4f46\u662f\u7b2c\u4e00\u96c6\u56e2\u4e0e\u7b2c\u4e8c\u96c6\u56e2\u4e4b\u95f4\u7684\u5dee\u8ddd\u5374\u5df2\u7ecf\u62c9\u5f00\u5dee\u8ddd\u4e86\uff1b\u8fd8\u597d\uff0c\u8fd9\u79cd\u5dee\u8ddd\u8fd8\u53ea\u662f\u4ea7\u4e1a\u88e2\u5c3e\u7aef\u4e0a\u7684\u5dee\u8ddd\uff0c\u81f3\u5c11\u6280\u672f\u8d44\u6e90\u3001\u4eba\u529b\u8d44\u6e90\uff0c\u8fd8\u6ca1\u6709\u5411\u4eac\u6caa\u7ca4\u53d1\u751f\u660e\u663e\u7684\u503e\u659c\uff0c\u5efa\u90ba\u5e02\u5171\u6709\u4e09\u5341\u516d\u6240\u9ad8\u7b49\u9662\u6821\u4e0e\u6570\u91cf\u66f4\u52a0\u5e9e\u5927\u7684\u79d1\u5b66\u7814\u7a76\u673a\u6784\uff0c\u5728\u5fae\u7535\u5b50\u9886\u57df\uff0c\u4e5d\u4e03\u5e74\u5efa\u90ba\u5e02\u7684\u79d1\u7814\u673a\u6784\u4e0e\u9ad8\u7b49\u9662\u6240\u7684\u6c34\u5e73\u5728\u56fd\u5185\u8fd8\u6709\u5f88\u5927\u7684\u4f18\u52bf\uff0c\u5efa\u90ba\u5e02\u8fd9\u65f6\u5019\u8981\u8ffd\u8d76\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\u673a\u4f1a\uff0c\u53ea\u662f\u4e0d\u6653\u5f97\u8c01\u80fd\u80dc\u4efb\u554a\u3002\u201d\u5f20\u606a\u80fd\u770b\u5230\u672a\u6765\u5341\u591a\u5e74\u7684\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u7684\u53d1\u5c55\u8d8b\u52bf\uff0c\u4e1c\u6d77\u7701\u5b8c\u5168\u6709\u53ef\u80fd\u5728\u4eca\u540e\u5341\u591a\u5e74\u7684\u65f6\u95f4\u91cc\u4ee5\u5efa\u90ba\u4e3a\u4e2d\u5fc3\u53d1\u5c55\u51fa\u4e00\u4e2a\u8d85\u7ea7\u89c4\u6a21\u7684\u4ee5\u7535\u5b50\u4ea7\u4e1a\u4e3a\u4e3b\u7684\u9ad8\u65b0\u4ea7\u4e1a\u7fa4\u51fa\u6765\uff0c\u7136\u800c\u53e6\u4e00\u6bb5\u771f\u6b63\u7684\u53d1\u5c55\u5386\u53f2\u4e8b\u5b9e\uff1a\u4e1c\u6d77\u7701\u7684\u7535\u5b50\u4ea7\u4e1a\u53ea\u662f\u6ca6\u4e3a\u6d77\u5916\u7535\u5b50\u5382\u5546\u7684\u52a0\u5de5\u5382\uff0c\u5efa\u90ba\u5e02\u62ff\u4e0d\u51fa\u4e00\u4e24\u5bb6\u80fd\u6446\u5230\u53f0\u9762\u4e0a\u7684\u9ad8\u65b0\u79d1\u6280\u4f01\u4e1a\u3002\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u9b44\u529b\u9886\u5bfc\u4e1c\u6d77\u7701\u7684\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u8d70\u51fa\u7a98\u8feb\u7684\u56f0\u5c40\uff0c\u8fd9\u4e5f\u4f1a\u6210\u5c31\u6b64\u4eba\u4eba\u751f\u4ed5\u9014\u7684\u8f89\u714c\uff1f\u674e\u8fdc\u6e56\u6216\u8bb8\u53ef\u4ee5\u62ff\u8fd9\u4e2a\u7ed9\u4ed6\u7684\u653f\u7ee9\u518d\u6dfb\u4e0a\u8000\u773c\u7684\u5149\u8292\uff0c\u4f46\u662f\u4ed6\u4e0d\u4f1a\u662f\u8d2f\u5f7b\u59cb\u7ec8\u7684\u4eba\u7269\uff1b\u518d\u8bf4\u674e\u8fdc\u6e56\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5f20\u606a\u4e5f\u6276\u6301\u4e0d\u8d77\u3002\u6216\u8bb8\u7b49\u4e0d\u5230\u8fd9\u6837\u4e00\u4e2a\u8ba9\u4eba\u6ee1\u610f\u7684\u4eba\u9009\uff0c\u4f46\u662f\u9526\u6e56\u53d1\u5c55\u7684\u6b65\u4f10\u4e0d\u4f1a\u56e0\u6b64\u505c\u987f\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6402\u7740\u8bb8\u601d\uff0c\u968f\u610f\u7684\u8bf4\u7740\u8bdd\uff0c\u8bc4\u70b9\u7740\u5efa\u90ba\u5e02\u91cc\u7684\u5b98\u5458\uff0c\u8bf4\u5230\u80e1\u5b97\u5e86\u65f6\uff0c\u8bb8\u601d\u62ac\u5934\u95ee\u5f20\u606a\uff1a\u201c\u90a3\u4e2a\u80e1\u5b97\u5e86\u4f60\u63a5\u89e6\u8fc7\u5417\uff1f\u4f60\u5bf9\u4ed6\u4e5f\u5f88\u51b7\u6de1\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u554a\uff0c\u201d\u5f20\u606a\u5c06\u80e1\u91d1\u661f\u7684\u4e8b\u60c5\u8bf4\u7ed9\u8bb8\u601d\u542c\uff0c\u201c\u90a3\u80e1\u91d1\u661f\u53ef\u80fd\u662f\u80e1\u5b97\u5e86\u7684\u5b50\u4f84\uff0c\u6211\u8fd9\u4eba\u5224\u65ad\u4e8b\u60c5\u6216\u8005\u8bc6\u4eba\uff0c\u90fd\u5f88\u4e3b\u89c2\u7684\uff0c\u6709\u4e9b\u4eba\u4e0d\u559c\u6b22\u5c31\u600e\u4e48\u4e5f\u559c\u6b22\u4e0d\u6765\u7684\uff0c\u4e0d\u559c\u6b22\uff0c\u6001\u5ea6\u81ea\u7136\u5c31\u70ed\u7edc\u4e0d\u8d77\u6765\uff0c\u6211\u73b0\u5728\u4f3c\u4e4e\u4e5f\u4e0d\u9700\u8981\u523b\u610f\u53bb\u8ba8\u597d\u8c01\u3002\u201d</p><p>\u3000\u3000\u201c\u80fd\u8fd9\u6837\u60f3\u5c31\u597d\uff0c\u201d\u8bb8\u601d\u5728\u5f20\u606a\u7684\u6000\u91cc\u633a\u8d77\u8170\uff0c\u808c\u80a4\u76f8\u8d34\uff0c\u4f38\u624b\u53bb\u6478\u5f20\u606a\u524a\u7626\u7684\u8138\u988a\uff0c\u201c\u771f\u62c5\u5fc3\u8fd9\u4e48\u591a\u4e8b\uff0c\u4f60\u600e\u4e48\u627f\u62c5\u5f97\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e60\u60ef\u4e86\u5c31\u597d\uff0c\u4e0d\u662f\u8fd8\u6709\u4f60\u4eec\u5417\uff1f\u201d\u5f20\u606a\u7684\u624b\u5fcd\u4e0d\u4f4f\u5728\u8bb8\u601d\u5a07\u633a\u7684\u80f8\u53e3\u63c9\u7740\uff0c\u8981\u5c06\u8bb8\u601d\u5a07\u67d4\u5b8c\u7f8e\u7684\u8eab\u4f53\u63c9\u8fdb\u81ea\u5df1\u7684\u8eab\u4f53\u91cc\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u8c03\u7740\u60c5\uff0c\u697c\u4e0b\u6709\u8f66\u5b50\u9a76\u8fc7\u6765\u7684\u52a8\u9759\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u8c01\u8fd9\u65f6\u5019\u6478\u4e0a\u95e8\u6765\uff0c\u8fd8\u6b63\u597d\u5c06\u4ed6\u5835\u5728\u5bb6\u91cc\u3002\u8bb8\u601d\u8981\u7ad9\u8d77\u6765\u7a7f\u8863\u670d\uff0c\u5f20\u606a\u6402\u7740\u5979\u8bf4\uff1a\u201c\u4e0d\u6025\uff0c\u542c\u542c\u662f\u8c01\uff01\u522b\u662f\u6211\u8001\u7238\u534a\u591c\u4ece\u65b0\u829c\u8d76\u56de\u6765\uff1f\u201d</p><p>\u3000\u3000\u8f66\u706f\u6253\u5165\u697c\u4e0b\u7684\u5927\u5385\uff0c\u706f\u5149\u6f2b\u5c04\uff0c\u8fde\u7740\u697c\u4e0a\u7684\u623f\u95f4\u4e5f\u4eae\u5802\u4e86\u4e00\u4e9b\uff0c\u6709\u5085\u4fca\u4e0e\u9a6c\u6d77\u9f99\u7761\u5728\u697c\u4e0b\uff0c\u542c\u5230\u697c\u4e0b\u7684\u8bf4\u8bdd\u58f0\uff0c\u7adf\u7136\u662f\u53f6\u5efa\u658c\uff1b\u8fd9\u5bb6\u4f19\u534a\u591c\u4e0d\u6253\u7535\u8bdd\u5c31\u51b2\u8fc7\u6765\uff0c\u60f3\u5fc5\u662f\u6765\u8ba9\u81ea\u5df1\u96be\u582a\u7684\uff0c\u8bb8\u601d\u5fc3\u865a\u7684\u722c\u8d77\u6765\u7a7f\u8863\u670d\uff0c\u5f20\u606a\u5012\u662f\u4e0d\u7126\u6025\uff0c\u679c\u7136\u8fc7\u4e86\u4e00\u4f1a\u513f\u5c31\u542c\u89c1\u53f6\u5efa\u658c\u7ad9\u5728\u697c\u68af\u53e3\u51b2\u7740\u697c\u4e0a\u558a\uff1a\u201c\u7ed9\u4f60\u4eec\u4e94\u5206\u949f\u65f6\u95f4\u7a7f\u8863\u670d\uff0c\u4e0d\u7136\u6211\u4eec\u5c31\u51b2\u4e0a\u6765\u6349\u5978\u4e86\u3002\u201d\u53f6\u5efa\u658c\u65c1\u8fb9\u6709\u4eba\u5728\u7b11\uff0c\u8fd9\u7b11\u58f0\u542c\u8d77\u6765\u5f88\u719f\u6089\uff0c\u4e00\u65f6\u4e5f\u60f3\u4e0d\u8d77\u662f\u8c01\uff0c\u5f20\u606a\u5fc3\u91cc\u7eb3\u95f7\u4e86\uff1a\u80fd\u8ddf\u53f6\u5efa\u658c\u5927\u534a\u591c\u6df7\u5728\u4e00\u8d77\u7684\u4eba\u4f1a\u662f\u8c01\uff1f\u504f\u504f\u7b11\u58f0\u542c\u8d77\u6765\u8fd8\u8fd9\u4e48\u719f\u6089\u3002</p></article>"}}}]);
