(window.webpackJsonp=window.webpackJsonp||[]).push([[712],{735:function(p){p.exports={chapterTitle:null,title:"\u7b2c552\u7ae0 \u4eac\u57ce\u6765\u5ba2",content:"<article><h2>\u7b2c552\u7ae0 \u4eac\u57ce\u6765\u5ba2</h2><p>\u3000\u3000\u8bb8\u601d\u6015\u7ed9\u53f6\u5efa\u658c\u803b\u7b11\uff0c\u8eb2\u5728\u4e09\u697c\u4e0d\u80af\u4e0b\u53bb\uff0c\u518d\u8bf4\u53f6\u5efa\u658c\u8fd8\u5e26\u4e86\u522b\u4eba\u6765\uff1b\u5f20\u606a\u4e0b\u4e86\u697c\uff0c\u6ca1\u770b\u89c1\u53f6\u5efa\u658c\u5728\u5ba2\u5385\u91cc\uff0c\u5085\u4fca\u6307\u4e86\u6307\u53a8\u623f\uff0c\u542c\u89c1\u53f6\u5efa\u658c\u6b63\u5429\u5490\u4eba\u5728\u53a8\u623f\u627e\u73bb\u7483\u676f\uff0c\u95fb\u5230\u6709\u6d53\u90c1\u7684\u5496\u5561\u9999\u6c14\u6251\u9f3b\u800c\u6765\uff0c\u6ca1\u8bb0\u5f97\u8fd9\u680b\u623f\u5b50\u5b58\u6709\u5496\u5561\u554a\uff1f\u80fd\u627e\u5230\u5496\u5561\u58f6\u5c31\u80fd\u627e\u5230\u5496\u5561\u676f\uff0c\u627e\u73bb\u7483\u676f\u505a\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5085\u4fca\u4e0e\u9a6c\u6d77\u9f99\u53bb\u4f11\u606f\uff0c\u8c01\u6653\u5f97\u53f6\u5efa\u658c\u534a\u591c\u95ef\u8fdb\u6765\u6709\u4ec0\u4e48\u4e8b\u8981\u95f9\u817e\u591a\u4e45\u3002\u8d70\u8fdb\u53a8\u623f\uff0c\u770b\u89c1\u53f6\u5efa\u658c\u6b63\u5f00\u542f\u4e00\u74f6\u7ea2\u74f6\uff0c\u53a8\u623f\u4e2d\u95f4\u7684\u5927\u7406\u77f3\u64cd\u4f5c\u77f3\u653e\u5728\u4e00\u5806\u5364\u83dc\uff0c\u7ea2\u9152\u3001\u5364\u83dc\u8fd8\u6709\u5496\u5561\u90fd\u662f\u53f6\u5efa\u658c\u5e26\u8fc7\u6765\u7684\uff0c\u53ea\u662f\u8fd9\u54c1\u5473\u9a73\u6742\u4e86\u4e9b\uff1b\u59da\u6587\u76db\u4ece\u6a71\u67dc\u91cc\u627e\u51fa\u73bb\u7483\u676f\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u606a\u5c11\u7792\u4eba\u8fd8\u771f\u662f\u7792\u5f97\u7d27\u554a\uff0c\u6211\u540e\u6765\u8fd8\u4e0e\u59da\u575a\u805a\u4e86\u4e24\u56de\uff0c\u4ed6\u4e5f\u53ea\u5b57\u4e0d\u63d0\u4f60\uff01\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u7684\u786e\u6ca1\u6709\u60f3\u5230\u719f\u6089\u7684\u7b11\u58f0\u7adf\u662f\u59da\u6587\u76db\uff0c\u6709\u4e9b\u5947\u602a\u59da\u6587\u76db\u8fd9\u65f6\u5019\u4f1a\u51fa\u73b0\u5728\u5efa\u90ba\uff0c\u65e2\u7136\u53f6\u5efa\u658c\u9886\u4ed6\u8fc7\u6765\uff0c\u5b9a\u6709\u6545\u4e8b\u53ef\u4ee5\u542c\uff1b\u59da\u575a\u662f\u5317\u4eac\u5206\u516c\u53f8\u7684\u526f\u603b\u7ecf\u7406\uff0c\u5f20\u606a\u5f53\u521d\u8fd8\u5429\u5490\u8981\u4ed6\u4e0e\u59da\u6587\u76db\u641e\u597d\u5173\u7cfb\uff0c\u6ca1\u60f3\u5230\u59da\u6587\u76db\u76f4\u63a5\u627e\u4e0a\u95e8\u6765\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u8bf4\u8fd9\u58f0\u97f3\u600e\u4e48\u542c\u8d77\u6765\u719f\u6089\uff0c\u8d76\u5230\u5efa\u90ba\u8ba9\u6211\u505a\u5730\u4e3b\u4e4b\u8c0a\u7684\uff1f\u201d\u4e0a\u56de\u5728\u5317\u4eac\u7684\u591c\u5e97\uff0c\u59da\u6587\u76db\u5012\u662f\u4e0d\u4ecb\u610f\u4ed6\u5bb6\u91cc\u6307\u5b9a\u7684\u672a\u5a5a\u59bb\u6613\u99a8\u4e0e\u5f20\u606a\u4ed6\u4eec\u6df7\u5728\u4e00\u8d77\u2014\u2014\u4ed6\u5c0f\u5b50\u4e0e\u8ba1\u59d4\u9ad8\u79d1\u53f8\u7684\u9646\u5065\u4e5f\u5728\u6ce1\u5317\u5916\u7684\u5b66\u751f\u59b9\u2014\u2014\u4ea4\u8c08\u8d77\u6765\uff0c\u6027\u60c5\u9887\u4e3a\u76f8\u6295\uff0c\u59da\u6587\u76db\u6700\u540e\u8fd8\u575a\u6301\u8981\u5c3d\u5730\u65b9\u4e4b\u8c0a\uff0c\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5728\u5efa\u90ba\u6211\u5012\u4e0d\u6562\u8ddf\u53f6\u54e5\u4e89\u8fd9\u4e2a\u5730\u4e3b\uff0c\u201d\u53c8\u95ee\u53f6\u5efa\u658c\uff0c\u201c\u6211\u79bb\u5f00\u9999\u6e2f\u65f6\u6253\u7b97\u76f4\u63a5\u53bb\u5317\u4eac\uff0c\u6ca1\u6709\u4e70\u5230\u673a\u7968\u624d\u4e34\u65f6\u51b3\u5b9a\u5148\u5230\u5efa\u90ba\u6765\u5904\u7406\u4e00\u4e9b\u4e8b\u60c5\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5f53\u7136\u4ee5\u4e3a\u4f60\u76f4\u63a5\u53bb\u5317\u4eac\u4e86\u554a\uff01\u201d\u53f6\u5efa\u658c\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6587\u76db\u5728\u4e1c\u822a\u7684\u773c\u7ebf\u4e0d\u5c11\uff0c\u4f60\u4ee5\u4e3a\u4f60\u625b\u7740\u4e00\u7247\u6811\u53f6\u6321\u5728\u8138\u4e0a\u5c31\u80fd\u7792\u8fc7\u8c01\u554a\uff0c\u4e24\u4e2a\u4eba\u8fd8\u5728\u98de\u673a\u4e0a\u537f\u537f\u6211\u6211\uff1f\u6211\u60f3\u554a\uff0c\u4f60\u5230\u5efa\u90ba\u6ca1\u6709\u8054\u7cfb\u6211\u5927\u6982\u662f\u6015\u6211\u6253\u6270\u4f60\u4eec\u53cc\u5bbf\u53cc\u98de\uff0c\u6240\u4ee5\u5c31\u76f4\u63a5\u534a\u591c\u5230\u8fd9\u91cc\u6765\u902e\u4f60\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u5374\u5728\u60f3\uff1a\u4ee5\u540e\u4e0e\u4e0d\u540c\u7684\u5973\u4eba\u662f\u4e0d\u662f\u8981\u9009\u62e9\u5750\u4e0d\u540c\u516c\u53f8\u7684\u98de\u673a\uff1f</p><p>\u3000\u3000\u5e74\u540e\u5230\u5904\u5954\u6ce2\uff0c\u5c31\u662f\u4e0a\u56de\u53bb\u9999\u6e2f\u4ece\u5efa\u90ba\u5750\u98de\u673a\u624d\u53bb\u53f6\u5bb6\u8001\u5b85\u770b\u671b\u53f6\u7956\u8303\u8001\u4eba\uff0c\u4f46\u662f\u53f6\u5efa\u658c\u4eba\u6ca1\u5728\u5efa\u90ba\uff0c\u8fd9\u6bb5\u65f6\u95f4\u8fd8\u6ca1\u6709\u8ddf\u53f6\u5efa\u658c\u8bf4\u4e0a\u8bdd\uff0c\u5012\u662f\u6ca1\u60f3\u5230\u53f6\u5efa\u658c\u4e0e\u59da\u6587\u76db\u8ba4\u8bc6\uff0c\u770b\u8d77\u6765\u5173\u7cfb\u8fd8\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f53\u521d\u4e0e\u59da\u6587\u76db\u5728\u5317\u4eac\u76f8\u9047\u65f6\uff0c\u5c31\u77e5\u9053\u4ed6\u7684\u80cc\u666f\u4e0d\u6d45\u2014\u2014\u6613\u99a8\u662f\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u7684\u5973\u513f\u786e\u51ff\u65e0\u7591\u2014\u2014\u540e\u6765\u4ece\u6881\u6587\u6c5f\u7684\u51c6\u4e08\u4eba\u738b\u65ed\u741b\u90a3\u91cc\u8fdb\u4e00\u6b65\u5f97\u5230\u786e\u8ba4\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u4e0e\u5468\u5174\u4e1c\u90fd\u662f\u90ae\u7535\u90e8\u516c\u8ba4\u7684\u516c\u5b50\u7237\uff0c\u4f46\u662f\u59da\u6587\u76db\u4e0e\u5468\u5174\u4e1c\u4e89\u8054\u4fe1\u96c6\u56e2\u603b\u7ecf\u7406\u52a9\u7406\u7684\u4f4d\u5b50\u8d25\u4e0b\u9635\u6765\uff0c\u5f20\u606a\u5f53\u65f6\u4e5f\u6ca1\u6709\u523b\u610f\u4ece\u738b\u65ed\u741b\u5634\u91cc\u8fdb\u4e00\u6b65\u63a2\u542c\u6d88\u606f\u2014\u2014\u788d\u7740\u81ea\u5df1\u6b64\u65f6\u7684\u8eab\u4efd\uff0c\u867d\u7136\u5360\u7740\u6881\u6587\u6c5f\u8868\u5f1f\u7684\u4fbf\u5b9c\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u8ba9\u738b\u65ed\u741b\u4ea4\u6d45\u8a00\u6df1\u3001\u8a00\u65e0\u4e0d\u5c3d\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7ad9\u5728\u9910\u5385\u4e0e\u5ba2\u5385\u4e4b\u95f4\u62f1\u5f62\u6a90\u53f0\u4e0b\uff0c\u63a2\u5934\u770b\u4e86\u770b\u697c\u68af\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u8be5\u4e0d\u4f1a\u4ece\u540e\u7a97\u722c\u51fa\u53bb\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u65e2\u7136\u4e0e\u8bb8\u601d\u7684\u4e8b\u8ba9\u4e1c\u822a\u7684\u4eba\u4f20\u5230\u59da\u6587\u76db\u7684\u8033\u6735\u91cc\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u85cf\u7740\u6396\u7740\u4e86\uff0c\u53f6\u5efa\u658c\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u662f\u8bf4\u660e\u59da\u6587\u76db\u662f\u53ef\u4ee5\u4fe1\u4efb\u7684\u4eba\u2014\u2014\u867d\u7136\u4e0e\u59da\u6587\u76db\u53ea\u89c1\u8fc7\u4e00\u9762\uff0c\u4ed6\u8eab\u4e0a\u6709\u4e16\u5bb6\u5b50\u5f1f\u4e0d\u7f81\u7684\u4e00\u9762\uff0c\u4f46\u662f\u5374\u6ca1\u6709\u8ba9\u4eba\u538c\u5f03\u7684\u813e\u6c14\u3002</p><p>\u3000\u3000\u5f20\u606a\u63a5\u8fc7\u59da\u6587\u76db\u624b\u91cc\u9012\u8fc7\u6765\u7684\u7ea2\u9152\uff0c\u8bf4\u4e86\u58f0\u8c22\u8c22\uff0c\u89c1\u53f6\u5efa\u658c\u671d\u7740\u697c\u68af\u53e3\u6324\u7709\u5f04\u773c\uff0c\u8d70\u5230\u5ba2\u5385\u91cc\uff0c\u7b49\u4e86\u4e00\u4f1a\u513f\u6ca1\u89c1\u8bb8\u601d\u81ea\u5df1\u8d70\u4e0b\u6765\uff0c\u77e5\u9053\u5979\u4e0d\u60f3\u8fd9\u65f6\u5019\u51fa\u6765\u89c1\u59da\u6587\u76db\uff0c\u53f6\u5efa\u658c\u5173\u7cfb\u719f\u4e86\uff0c\u5012\u662f\u65e0\u6240\u8c13\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5927\u534a\u591c\u7ed9\u4f60\u5435\u9192\u8fd8\u4e0d\u591f\uff0c\u4f60\u8fd8\u60f3\u5435\u9192\u4ec0\u4e48\u4eba\uff1f\u201d</p><p>\u3000\u3000\u591c\u9759\u66f4\u6df1\uff0c\u697c\u4e0b\u8c08\u8bdd\uff0c\u8bb8\u601d\u5728\u697c\u4e0a\u542c\u4e86\u4e00\u6e05\u4e8c\u695a\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5c31\u6ca1\u6709\u5f3a\u8feb\u81ea\u5df1\u4e0b\u697c\u53bb\uff0c\u62b1\u7740\u6bef\u5b50\u5148\u53bb\u623f\u95f4\u7761\u89c9\u4e86\uff0c\u7a7a\u8c03\u6253\u4e86\u534a\u591c\uff0c\u88ab\u8925\u7684\u6f6e\u6c14\u5439\u5e72\u4e86\u4e9b\uff0c\u4e5f\u4e0d\u7ba1\u53f6\u5efa\u658c\u534a\u591c\u6765\u627e\u6709\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u77e5\u9053\u5f20\u606a\u7684\u662f\u9b3c\u8bdd\uff0c\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u76f4\u60f3\u4ecb\u7ecd\u6587\u76db\u7ed9\u4f60\u8ba4\u8bc6\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4f60\u4eec\u5df2\u6709\u673a\u4f1a\u78b0\u4e0a\u9762\uff0c\u201d\u5c06\u53a8\u623f\u64cd\u4f5c\u53f0\u4e0a\u7684\u5364\u83dc\u4e0e\u7ea2\u9152\u8fd8\u6709\u5496\u5561\u58f6\u90fd\u642c\u5230\u5ba2\u5385\u7684\u73bb\u7483\u51e0\u4e0a\uff0c\u201c\u4e0d\u6653\u5f97\u51e0\u65f6\u80fd\u6293\u5230\u4f60\u7684\u4eba\uff0c\u6587\u76db\u8fd9\u6b21\u7b97\u662f\u5230\u5efa\u90ba\u4e0b\u8f66\u4f0a\u59cb\uff0c\u6211\u4eec\u8d81\u7740\u4eca\u591c\u8fd8\u6709\u4e9b\u6708\u8272\u5c31\u7b97\u7ed9\u4ed6\u7f6e\u9152\u6d17\u5c18\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u6709\u4e9b\u60ca\u8bb6\uff0c\u521d\u6b21\u89c1\u9762\u65f6\uff0c\u59da\u6587\u76db\u5c31\u900f\u9732\u5bf9\u90ae\u7535\u90e8\u66ae\u6c14\u6c89\u6c89\u7684\u90e8\u59d4\u4f5c\u98ce\u4e0d\u6ee1\u8981\u5230\u5730\u65b9\u4e0a\u6765\uff0c\u4f46\u662f\u5f20\u606a\u4ece\u4ed6\u7684\u8bdd\u8bed\u731c\u4ed6\u5bb6\u91cc\u5374\u66f4\u5e0c\u671b\u4ed6\u5728\u90e8\u59d4\u91cc\u517b\u7740\u8d44\u5386\u7b49\u7ea7\u522b\u4e0a\u53bb\u4e86\u518d\u653e\u5230\u5730\u65b9\u4e0a\u80fd\u6709\u4e00\u4e2a\u9ad8\u7684\u8d77\u70b9\uff0c\u8fd9\u624d\u8fc7\u53bb\u4e00\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u59da\u6587\u76db\u600e\u4e48\u5c31\u7a81\u7136\u8c03\u52a8\u5230\u5efa\u90ba\u6765\u4e86\uff1f\u5f20\u606a\u6ca1\u60f3\u7740\u6025\u7740\u95ee\u8be6\u60c5\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u4eca\u591c\u7ed9\u4f60\u5435\u9192\u4e0d\u51a4\uff0c\u4ee5\u540e\u5230\u5efa\u90ba\u53c8\u591a\u4e86\u4e00\u4e2a\u559d\u9152\u7684\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u5012\u4e0d\u6025\u7740\u95ee\u6587\u76db\u8c03\u5230\u5efa\u90ba\u54ea\u4e2a\u90e8\u95e8\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u80fd\u62ff\u6765\u4e0b\u9152\uff1f\u201d\u5f20\u606a\u4e0d\u6025\u7740\u95ee\u59da\u6587\u76db\u7684\u6765\u610f\uff0c\u8fd8\u6101\u4ed6\u81ea\u5df1\u4e0d\u6025\u7740\u8bf4\uff1f</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c8\u54c8\u4e00\u7b11\uff0c\u77e5\u9053\u60f3\u8ba9\u5f20\u606a\u4e3b\u52a8\u95ee\u8bdd\u592a\u96be\uff0c\u62cd\u4e86\u62cd\u59da\u6587\u76db\u7684\u80a9\u8180\uff1a\u201c\u4f60\u81ea\u5df1\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u5bb6\u91cc\u672c\u4e0d\u60f3\u8ba9\u6211\u8fd9\u65e9\u5c31\u51fa\u6765\uff0c\u201d\u59da\u6587\u76db\u5766\u7136\u8bf4\uff0c\u201c\u8d44\u5386\u6d45\u4e86\u4e9b\uff0c\u5728\u90ae\u7535\u90e8\u6df7\u4e86\u4e94\u5e74\u591a\uff0c\u6b63\u5904\u7ea7\u8fd8\u6ca1\u6709\u6df7\u4e0a\uff0c\u5230\u5730\u65b9\u8fd8\u662f\u8981\u5728\u5e95\u5c42\u6323\u624e\uff1b\u53ea\u662f\u4eac\u57ce\u91cc\u6700\u8fd1\u6c34\u6bd4\u8f83\u6df1\uff0c\u6211\u5bb6\u91cc\u624d\u628a\u6211\u6253\u53d1\u5230\u4e1c\u6d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u8bf4\u56fd\u5185\u653f\u5c40\u7a33\u5b9a\uff0c\u4f46\u662f\u5728\u5927\u7684\u4f1a\u8bae\u4e0e\u6743\u529b\u673a\u6784\u8c03\u6574\u4e4b\u524d\uff0c\u603b\u4f1a\u6709\u4e9b\u5c0f\u98ce\u5728\u5439\u5c0f\u6d6a\u5728\u6253\uff0c\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u59da\u6587\u76db\u4e5f\u4e0d\u662f\u60f9\u4e8b\u751f\u975e\u7684\u4e3b\uff0c\u53ea\u8981\u5728\u5317\u4eac\u5c0f\u5fc3\u8c28\u614e\u4e00\u4e9b\uff0c\u98ce\u6d6a\u4e5f\u5439\u6253\u4e0d\u5230\u4ed6\u8eab\u4e0a\uff0c\u59da\u6587\u76db\u8fd9\u8bdd\u8bf4\u5f97\u4e0d\u5c3d\u4e0d\u5b9e\uff1a\u4ed6\u5230\u5730\u65b9\u4e0a\uff0c\u5e94\u8be5\u662f\u4ed6\u81ea\u5df1\u4e89\u53d6\u7684\u5427\uff1f\u867d\u7136\u6ca1\u6709\u6df1\u8c08\u8fc7\uff0c\u521d\u6b21\u89c1\u9762\u7684\u5370\u8c61\u5c31\u6bd4\u8f83\u6df1\u8c08\uff0c\u5e94\u8be5\u4f1a\u6709\u4e00\u4e9b\u62b1\u8d1f\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u8981\u8bf4\u5f97\u8ddf\u6d41\u653e\u4e00\u6837\u51c4\u695a\uff0c\u5efa\u90ba\u867d\u4e0d\u5982\u5317\u4eac\uff0c\u4f46\u4e5f\u80fd\u7b97\u6e29\u67d4\u4e61\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u80fd\u51fa\u6765\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\uff0c\u201d\u59da\u6587\u76db\u6447\u4e86\u6447\u5934\uff0c\u6536\u655b\u8d77\u8bf4\u7b11\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff0c\u201c\u4e0e\u5bb6\u91cc\u59a5\u534f\u4e86\u2026\u2026\u90a3\u5c31\u4e0d\u80fd\u5230\u5730\u65b9\u4e0a\u7ee7\u7eed\u53ae\u6df7\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u6216\u8bb8\u662f\u53d7\u5230\u5468\u5174\u4e1c\u7684\u523a\u6fc0\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u201c\u90a3\u662f\u4e0d\u5bb9\u6613\uff0c\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u6587\u76db\u4ed6\u7237\u7237\u662f\u4e2d\u987e\u59d4\u7684\u524d\u59d4\u5458\u987e\u957f\u5e9a\uff0c\u4e0d\u662f\u59da\u67d0\u67d0\u2014\u2014\u201d\u6307\u7740\u5f20\u606a\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u4e00\u5b9a\u6ca1\u6709\u731c\u5230\u5427\uff1f\u5916\u4eba\u90fd\u5f88\u96be\u60f3\u5230\u8fd9\u5c42\u5173\u7cfb\uff0c\u201d\u53c8\u8bf4\u8d77\u59da\u6587\u76db\u5f02\u59d3\u7684\u7f18\u6545\uff0c\u201c\u5341\u5e74\u6d69\u52ab\u65f6\uff0c\u6587\u76db\u5bc4\u517b\u5728\u660c\u5e73\u59d3\u59da\u7684\u4eba\u5bb6\uff0c\u987e\u8001\u7237\u5b50\u4fbf\u8ba9\u6587\u76db\u59d3\u59da\u5ff5\u7740\u522b\u4eba\u7684\u6069\u60c5\uff1b\u6211\u5bb6\u8001\u7237\u5b50\u524d\u6bb5\u65f6\u95f4\u5230\u5317\u4eac\u53bb\u770b\u671b\u987e\u5bb6\u8001\u7237\u5b50\uff0c\u6253\u4e86\u5305\u7968\uff0c\u987e\u5bb6\u8001\u7237\u5b50\u624d\u540c\u610f\u6587\u76db\u5230\u5efa\u90ba\u6765\uff0c\u6211\u5bb6\u8001\u7237\u5b50\u4ec0\u4e48\u65f6\u5019\u5e2e\u522b\u4eba\u6253\u8fc7\u5305\u7968\uff1f\u201d\u53f6\u5efa\u658c\u8f6c\u5934\u95ee\u59da\u6587\u76db\uff0c\u201c\u4f60\u7ed9\u6211\u8001\u5bb6\u5b50\u704c\u4e86\u4ec0\u4e48\u8ff7\u9b42\u6c64\uff1f\u201d\u59da\u6587\u76db\u563f\u7136\u4e00\u8bf4\uff0c\u53f6\u5efa\u658c\u5012\u6ca1\u6709\u7a77\u8ffd\u4e0d\u820d\uff0c\u53c8\u56de\u8fc7\u5934\u6765\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6587\u76db\u7684\u5de5\u4f5c\u5173\u7cfb\u521a\u5230\u5efa\u90ba\uff0c\u5177\u4f53\u5230\u54ea\u4e2a\u90e8\u95e8\u5de5\u4f5c\u8fd8\u6ca1\u6709\u5b9a\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u786e\u6ca1\u6709\u60f3\u5230\u59da\u6587\u76db\u662f\u524d\u987e\u95ee\u59d4\u5458\u987e\u957f\u5e9a\u7684\u5b59\u5b50\uff0c\u59da\u6587\u76db\u771f\u8981\u51b3\u5b9a\u4e0b\u6765\uff0c\u90a3\u4e5f\u5feb\u7684\u5f88\uff0c\u6bd5\u7adf\u7ea7\u522b\u4e0d\u7b97\u592a\u9ad8\uff0c\u63a5\u6536\u8d77\u6765\u4e5f\u4e0d\u7b97\u592a\u9ebb\u70e6\uff0c\u7701\u91cc\u3001\u5e02\u91cc\u968f\u4fbf\u63d2\u4e2a\u526f\u5904\u7ea7\u7684\u89d2\u8272\uff0c\u8ddf\u73a9\u4f3c\u7684\uff0c\u7279\u4e8b\u7279\u529e\uff0c\u51e0\u5929\u51e0\u4e2a\u7535\u8bdd\u5c31\u80fd\u89e3\u51b3\u95ee\u9898\u3002</p><p>\u3000\u3000\u4e2d\u987e\u59d4\u662f\u4e3a\u4e86\u89e3\u51b3\u4e2d\u592e\u9886\u5bfc\u673a\u6784\u65b0\u8001\u4ea4\u66ff\u3001\u4f7f\u4e2d\u592e\u59d4\u5458\u4f1a\u5e74\u8f7b\u5316\u540c\u65f6\u8ba9\u4e00\u4e9b\u8001\u540c\u5fd7\u5728\u9000\u51fa\u4e00\u7ebf\u4e4b\u540e\u7ee7\u7eed\u53d1\u6325\u4e00\u5b9a\u7684\u4f5c\u7528\u800c\u6210\u7acb\u7684\u8fc7\u6e21\u6027\u7ec4\u7ec7\uff0c\u4e5d\u4e8c\u5e74\u7ed3\u675f\u5386\u53f2\u4f7f\u547d\u800c\u64a4\u6d88\uff0c\u987e\u95ee\u59d4\u5458\u90fd\u662f\u4e2d\u592e\u5143\u8001\u7ea7\u7684\u4eba\u7269\u3002\u987e\u957f\u5e9a\u3001\u53f6\u7956\u5baa\u4e5f\u7684\u786e\u80fd\u79f0\u5f97\u4e0a\u662f\u4e2d\u592e\u7684\u5143\u8001\uff0c\u800c\u4e14\u4e24\u4eba\u5728\u4e2d\u592e\u65f6\u5173\u7cfb\u5bc6\u5207\uff0c\u8fd9\u4e2a\u5012\u662f\u5e38\u542c\u53f6\u7956\u8303\u8001\u4eba\u62c9\u5bb6\u5e38\u65f6\u8bf4\u8d77\u3002\u539f\u6765\u8fd9\u662f\u53f6\u7956\u8303\u8001\u4eba\u4ea4\u5f85\u4e0b\u6765\u7684\u4efb\u52a1\uff0c\u5f20\u606a\u5012\u4e0d\u6562\u5ffd\u89c6\uff1a\u53ea\u662f\u4e0d\u6653\u5f97\u59da\u6587\u76db\u62ff\u4ec0\u4e48\u8ddf\u4ed6\u5bb6\u91cc\u59a5\u534f\uff0c\u5a5a\u4e8b\u5417\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5c06\u9152\u676f\u653e\u4e0b\u6765\uff0c\u8eab\u4f53\u5750\u6b63\uff0c\u95ee\u59da\u6587\u76db\uff1a\u201c\u4f60\u8fd9\u6b21\u51b3\u5b9a\u5230\u5730\u65b9\u4e0a\u6765\u6709\u4ec0\u4e48\u8ba1\u5212\uff1f\u201d\u4e2d\u592e\u5143\u8001\u7ea7\u4eba\u7269\u54ea\u4e00\u4e2a\u4e0d\u662f\u95e8\u751f\u65e0\u6570\uff0c\u53f6\u7956\u8303\u8ba9\u8001\u4e0a\u7ea7\u987e\u957f\u5e9a\u540c\u610f\u59da\u6587\u76db\u5230\u5730\u65b9\u4e0a\u6765\uff0c\u5e94\u8be5\u4f1a\u6709\u5b89\u6392\uff0c\u5f20\u606a\u8fd9\u624d\u95ee\u59da\u6587\u76db\u81ea\u5df1\u6709\u4ec0\u4e48\u89c4\u5212\u3002\u4ed6\u8fd9\u4e48\u95ee\u7684\u65f6\u5019\uff0c\u8111\u5b50\u91cc\u5374\u5728\u76d8\u65cb\u59da\u6587\u76db\u4e0e\u5468\u5174\u4e1c\u4e89\u8054\u4fe1\u96c6\u56e2\u603b\u7ecf\u7406\u52a9\u7406\u8d25\u4e0b\u9635\u6765\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u8001\u7237\u5b50\u540c\u610f\u653e\u6211\u5230\u5730\u65b9\u4e0a\u6765\uff0c\u5efa\u90ba\u5374\u662f\u6211\u81ea\u5df1\u9009\u7684\uff0c\u201d\u59da\u6587\u76db\u8bf4\u9053\uff0c\u201c\u8981\u6309\u7167\u4ed6\u4eec\u7684\u610f\u613f\uff0c\u4ed6\u4eec\u5012\u662f\u5c5e\u610f\u6211\u5230\u6c5f\u5357\u7701\u6211\u5c0f\u59d1\u592b\u9648\u65b0\u6c11\u90a3\u91cc\u53bb\uff0c\u4e0d\u8fc7\u53bb\u6c5f\u5357\u7701\uff0c\u4e0e\u7559\u5728\u90e8\u59d4\u53c8\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u4e0d\u8fc7\u662f\u5728\u5bb6\u4eba\u7fbd\u7ffc\u4e0b\u53ae\u6df7\u800c\u5df2\u3002\u201d</p></article>"}}}]);
