(window.webpackJsonp=window.webpackJsonp||[]).push([[717],{740:function(p){p.exports={chapterTitle:null,title:"\u7b2c557\u7ae0 \u7834\u51b0\u4e0e\u7ed3\u7f51",content:"<article><h2>\u7b2c557\u7ae0 \u7834\u51b0\u4e0e\u7ed3\u7f51</h2><p>\u3000\u3000\u5f20\u606a\u60e6\u8bb0\u7740\u8bb8\u601d\u4e0b\u5348\u4f1a\u57281978\u9152\u5427\u7b49\u4ed6\uff0c\u4e00\u5e2d\u9152\u559d\u5230\u4e0b\u5348\u4e24\u70b9\u949f\uff0c\u4fbf\u5306\u5306\u6563\u4e86\u3002\u738b\u7ef4\u5747\u4e0e\u91d1\u56fd\u6d77\u7ed3\u4f34\u800c\u53bb\uff0c\u5e02\u653f\u5e9c\u4e0e\u7701\u516c\u5b89\u5385\u7684\u5927\u697c\u7d27\u6328\u7740\u3002\u5f20\u77e5\u884c\u8981\u4e0e\u5b54\u957f\u6cb3\u53bb\u63a8\u4ecb\u4f1a\u73b0\u573a\uff0c\u4e0b\u5348\u8fd8\u8981\u62bd\u65f6\u95f4\u53bb\u7701\u4ea4\u901a\u5385\u8c08\u65b0\u829c\u9ad8\u901f\u516c\u8def\u9879\u76ee\u7684\u4e8b\u60c5\uff0c\u51c6\u5907\u4eca\u5e74\u4e0a\u534a\u5e74\u5c31\u5c06\u9ad8\u901f\u516c\u8def\u5efa\u8d77\u6765\uff0c\u4ee5\u540e\u65b0\u829c\u5230\u5efa\u90ba\u7684\u65f6\u95f4\u5c31\u7f29\u77ed\u4e86\u4e00\u534a\u8fd8\u4e0d\u6b62\uff0c\u8fd9\u5bf9\u63a8\u52a8\u65b0\u829c\u5e02\u7684\u7ecf\u6d4e\u53d1\u5c55\u6781\u4e3a\u91cd\u8981\uff1b\u529e\u5b8c\u4e8b\u60c5\uff0c\u591c\u91cc\u56de\u65b0\u829c\u53bb\uff0c\u4e5f\u4e0d\u518d\u5efa\u90ba\u803d\u6401\u4e00\u591c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c31\u62c9\u7740\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u53bb\u5b66\u5e9c\u5df7\u2014\u2014\u59da\u6587\u76db\u5de5\u4f5c\u5173\u7cfb\u8c03\u5230\u5efa\u90ba\u5e02\u91cc\uff0c\u804c\u52a1\u8fd8\u6ca1\u6709\u5b89\u6392\uff0c\u4eba\u5c31\u6bd4\u8f83\u81ea\u7531\uff0c\u4ed6\u7684\u804c\u52a1\u4e5f\u8981\u7b49\u5efa\u90ba\u5e02\u91cc\u5bf9\u79d1\u6280\u56ed\u9879\u76ee\u8ba8\u8bba\u51fa\u5177\u4f53\u7684\u65b9\u6848\u624d\u80fd\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u56db\u6708\u4e0b\u5348\u7684\u5b66\u5e9c\u5df7\u60a0\u95f2\u800c\u81ea\u5f97\uff0c\u867d\u7136\u4e0d\u662f\u5468\u672b\uff0c\u7275\u7740\u624b\u5728\u68a7\u6850\u6811\u4e0b\u6563\u6b65\u7684\u5b66\u751f\u8fd8\u662f\u5f88\u591a\u3002\u4e0b\u5348\u65f6\u5149\u7684\u9152\u5427\u6bd4\u8336\u5ea7\u6216\u5496\u5561\u5427\u8981\u51b7\u6e05\u4e00\u4e0b\uff0c\u5f20\u606a\u4ed6\u4eec\u63a8\u95e8\u8fdb\u6765\uff0c\u770b\u89c1\u8bb8\u601d\u6b63\u5750\u5728\u5427\u53f0\u4e0a\u4e0e\u7ad9\u5728\u5427\u53f0\u91cc\u7684\u5b59\u9759\u6aac\u804a\u5929\uff0c\u5b59\u9759\u6aac\u770b\u89c1\u5f20\u606a\uff0c\u8ba5\u8bbd\u4ed6\u8bf4\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u8fd9\u8f88\u5b50\u5c31\u88c5\u7f29\u5934\u4e4c\u9f9f\u4e0d\u6562\u8fc7\u6765\u5462\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62bf\u7740\u5634\u7b11\uff0c\u4e5f\u4e0d\u540c\u60c5\u5f20\u606a\u3002</p><p>\u3000\u3000\u9152\u5427\u91cc\u6253\u5de5\u7684\u4e24\u540d\u5927\u5b66\u751f\uff0c\u5f20\u606a\u4e0d\u8ba4\u8bc6\uff0c\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u4e0b\u5348\u4e0d\u5728\u8fd9\u91cc\uff0c\u4ee4\u5c0f\u71d5\u5012\u662f\u80f8\u524d\u6302\u7740\u7ecf\u7406\u7684\u94ed\u724c\uff0c\u8fd8\u6709\u4e24\u4e2a\u591a\u6708\u624d\u6bd5\u4e1a\uff0c\u5374\u6b63\u5f0f\u627f\u62c5\u8d77\u9152\u5427\u7684\u7ba1\u7406\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u56de\u5934\u8ddf\u53f6\u5efa\u658c\u8bf4\uff1a\u201c\u8981\u662f\u5b66\u5e9c\u5df7\u62c6\u6389\uff0c\u4ee4\u5c0f\u71d5\u662f\u4e0d\u662f\u8981\u7b97\u5931\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u5b66\u5e9c\u5df7\u8981\u62c6\u6389\uff1f\u201d\u5b59\u9759\u6aac\u7f8e\u7738\u77aa\u5706\uff0c\u96ea\u767d\u7ea4\u7ec6\u7684\u624b\u638c\u6491\u5728\u51b0\u51b7\u7684\u6697\u8272\u5927\u7406\u77f3\u5427\u53f0\u4e0a\uff0c\u6068\u4e0d\u80fd\u63ea\u4f4f\u5f20\u606a\u7684\u9886\u895f\u95ee\u8be6\u7ec6\uff0c\u201c\u6211\u8fd9\u8001\u677f\u5a18\u8fd8\u6ca1\u6709\u8fc7\u763e\u5462\uff0c\u5b66\u5e9c\u5df7\u600e\u4e48\u53ef\u80fd\u62c6\u6389\uff1f\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5012\u4e0d\u62c5\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u8bf4\u8981\u62c6\u4e24\u4e09\u5e74\u4e86\uff0c\u73b0\u5728\u8fd8\u4e0d\u89c1\u52a8\u9759\uff0c\u54ea\u8fd9\u4e48\u5bb9\u6613\u80fd\u62c6\uff1f\u201d\u5979\u8d70\u8fc7\u6765\u62db\u547c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u7b49\u4eba\u5750\u5230\u9152\u5427\u91cc\u9762\u5750\u4e0b\uff0c\u5012\u5f88\u6709\u7ecf\u7406\u7684\u8303\u513f\uff0c\u201c\u8981\u559d\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u73cd\u85cf\u5728\u8fd9\u91cc\u7684\u829d\u534e\u4ed5\u65e9\u5c31\u8ba9\u5b59\u9759\u6aac\u5077\u559d\u5e72\u51c0\u4e86\uff0c\u4e2d\u5348\u559d\u4e86\u9152\u8fc7\u6765\uff0c\u5927\u5bb6\u53ea\u60f3\u559d\u676f\u6e05\u8336\uff0c\u4fbf\u8ba9\u4ee4\u5c0f\u71d5\u5e2e\u5fd9\u6ce1\u676f\u6e05\u8336\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8fd9\u91cc\u7684\u6e05\u8336\u6bd4\u7ea2\u9152\u8fd8\u8d35\uff01\u201d\u5b59\u9759\u6aac\u8fd8\u662f\u597d\u5947\u5f20\u606a\u8bf4\u5b66\u5e9c\u5df7\u8981\u62c6\u6389\u7684\u4e8b\u60c5\uff0c\u62c9\u4e86\u5f20\u6905\u5b50\u5750\u5230\u8bb8\u601d\u7684\u65c1\u8fb9\uff0c\u5979\u7a7f\u7740\u84dd\u8272\u7684\u9488\u7ec7\u5916\u5957\uff0c\u8896\u5b50\u634b\u8d77\u6765\uff0c\u9732\u51fa\u5ae9\u85d5\u96ea\u767d\u7684\u5c0f\u81c2\uff0c\u8ff7\u4eba\u7684\u4e0b\u5df4\u78d5\u5728\u6905\u5b50\u80cc\u4e0a\uff0c\u867d\u8bf4\u5979\u7684\u7d20\u989c\u5df2\u8fd1\u5b8c\u7f8e\uff0c\u5374\u4e60\u60ef\u5316\u7cbe\u81f4\u7684\u5986\u5bb9\uff0c\u66f4\u6dfb\u8ff7\u79bb\u7684\u6027\u611f\uff0c\u522b\u4eba\u90fd\u4e0d\u6653\u5f97\u5979\u8fd8\u662f\u97f3\u4e50\u5b66\u9662\u5927\u4e09\u7684\u5b66\u751f\uff0c\u76ef\u7740\u5f20\u606a\uff0c\u201c\u5b66\u5e9c\u5df7\u8981\u62c6\u6389\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u6487\u5634\u4e0d\u7406\u4f1a\u5b59\u9759\u6aac\uff0c\u7b97\u662f\u7ed9\u5979\u7559\u4e2a\u5ff5\u60f3\uff0c\u4ecb\u7ecd\u59da\u6587\u76db\u7ed9\u5b59\u9759\u6aac\u3001\u8bb8\u601d\u8ba4\u8bc6\uff0c\u59da\u6587\u76db\u5bf9\u5b59\u9759\u9999\u8fd8\u6709\u4e9b\u5370\u8c61\uff0c\u7ae5\u5e74\u4e5f\u5e38\u8ddf\u7740\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u540e\u9762\u8f6c\uff0c\u5b59\u5bb6\u8fc1\u5230\u5efa\u90ba\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u4ec0\u4e48\u8054\u7cfb\u4e86\uff1b\u5b59\u9759\u6aac\u5728\u5efa\u90ba\u51fa\u751f\uff0c\u518d\u4e4b\u540e\u5b59\u5c1a\u4e49\u4e00\u5bb6\u8fc1\u5f80\u9999\u6e2f\uff0c\u4e0e\u53f6\u5bb6\u7684\u8054\u7cfb\u4e5f\u5c11\u4e86\uff0c\u5b59\u9759\u6aac\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u5979\u5bb6\u4e0e\u987e\u5bb6\u8fd8\u80fd\u7b97\u5f97\u4e0a\u4e16\u4ea4\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u97f3\u4e50\u5b66\u9662\u7684\u5973\u5b69\u5b50\u90fd\u6709\u4f60\u8fd9\u822c\u6f02\u4eae\uff1f\u201d</p><p>\u3000\u3000\u60f3\u5fc5\u6765\u4e4b\u524d\uff0c\u5b59\u9759\u6aac\u542c\u8bb8\u601d\u8bf4\u8fc7\u59da\u6587\u76db\u7684\u4e8b\u60c5\uff0c\u77aa\u5927\u773c\u775b\u770b\u7740\u59da\u6587\u76db\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u4e2a\u5bb6\u91cc\u6307\u5b9a\u7684\u672a\u5a5a\u59bb\u8981\u662f\u540c\u610f\u4f60\u5728\u5916\u9762\u4e71\u6765\uff0c\u6211\u5012\u4e0d\u4ecb\u610f\u4ecb\u7ecd\u6211\u4eec\u5b66\u9662\u7684\u5973\u5b69\u5b50\u7ed9\u4f60\u8ba4\u8bc6\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u5bf9\u59da\u6587\u76db\u8bf4\uff1a\u201c\u5f20\u606a\u90fd\u5728\u9759\u6aac\u624b\u91cc\u5403\u8fc7\u5927\u82e6\u5934\uff0c\u97f3\u4e50\u5b66\u9662\u6f02\u4eae\u5973\u5b69\u5b50\u662f\u591a\uff0c\u53ef\u60dc\u73ab\u7470\u5e26\u523a\u7684\u4e5f\u591a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u4ee4\u5c0f\u71d5\u6c8f\u4e86\u4e00\u58f6\u6e05\u8336\u7aef\u6765\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u5e2e\u6211\u7ed9\u6731\u5c0f\u519b\u6253\u4e2a\u7535\u8bdd\uff0c\u6211\u7ea6\u4ed6\u8fc7\u6765\u559d\u8336\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u8c03\u51fa\u4e1c\u6d77\uff0c\u674e\u4e49\u6c5f\u4e5f\u5230\u6d77\u5dde\u4efb\u804c\uff0c\u6731\u5c0f\u519b\u7559\u5728\u7701\u653f\u5e9c\u529e\u516c\u5385\u5c31\u5931\u53bb\u4e86\u5c4f\u969c\uff0c\u8fd9\u8fb9\u4e0e\u674e\u8fdc\u6e56\u7684\u5173\u7cfb\u53c8\u50f5\u6301\u7740\uff0c\u867d\u7136\u674e\u4e49\u6c5f\u662f\u6c5f\u4e2d\u5c0f\u9c7c\uff0c\u8fd8\u4e0d\u865e\u674e\u8fdc\u6e56\u7684\u6012\u706b\u4f1a\u8fc1\u5230\u4ed6\u7684\u5934\u4e0a\u2014\u2014\u674e\u8fdc\u6e56\u4e5f\u672a\u5fc5\u77e5\u9053\u624b\u4e0b\u6709\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\u2014\u2014\u6240\u8c13\u960e\u738b\u597d\u8fc7\u3001\u5c0f\u9b3c\u96be\u7f20\uff0c\u8981\u60f3\u6307\u671b\u7701\u653f\u5e9c\u529e\u516c\u5385\u91cc\u90a3\u4e9b\u5b98\u50da\u4eec\u6ca1\u6709\u52bf\u5229\u773c\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u60f3\u5fc5\u4ee4\u5c0f\u71d5\u7684\u7537\u53cb\u6731\u5c0f\u519b\u8fd9\u6bb5\u65f6\u95f4\u76f8\u5f53\u7684\u90c1\u95f7\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u3001\u6731\u5c0f\u519b\u5c5e\u4e8e\u7ed9\u8499\u7740\u773c\u775b\u5728\u4e0b\u9762\u6323\u624e\u7684\u5c0f\u4eba\u7269\uff0c\u867d\u7136\u77e5\u9053\u5f20\u606a\u7684\u5bb6\u5ead\u80cc\u666f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f46\u662f\u90fd\u4e0d\u6e05\u695a\u5b9e\u9645\u7684\u60c5\u51b5\uff0c\u66f4\u4e0d\u77e5\u9053\u9526\u6e56\u4e0e\u674e\u8fdc\u6e56\u8fd9\u6bb5\u65f6\u95f4\u7684\u6069\u6028\u3002\u5728\u7701\u653f\u5e9c\u91cc\u7684\u51b7\u6696\u53d8\u5316\uff0c\u6731\u5c0f\u519b\u80fd\u5207\u8eab\u4f53\u4f1a\u5230\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u96be\u9053\u4ec5\u4ec5\u662f\u7701\u653f\u5e9c\u91cc\u7684\u9760\u5c71\u76f8\u7ee7\u79bb\u5f00\u4e86\u5417\uff1f\u5012\u4e5f\u4e0d\u662f\u8bf4\u6731\u5c0f\u519b\u4e0d\u60f3\u53e6\u6295\u9635\u8425\uff0c\u4ed6\u4e0d\u8fc7\u662f\u7701\u653f\u5e9c\u91cc\u7684\u5c0f\u89d2\u8272\uff0c\u4ed6\u60f3\u53e6\u6295\u9635\u8425\u8c01\u642d\u7406\u4ed6\u554a\uff1f\u6625\u8282\u793c\u5012\u9001\u53bb\u4e0d\u5c11\uff0c\u5374\u6ca1\u6709\u4ec0\u4e48\u6548\u679c\uff0c\u51b7\u677f\u51f3\u5df2\u7ecf\u5750\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u4e5f\u4e3a\u6731\u5c0f\u519b\u7684\u8fd1\u51b5\u5e72\u7740\u6025\uff0c\u5927\u534a\u5e74\u6765\uff0c\u53ea\u662f\u60c5\u51b5\u53d8\u7cdf\u4e4b\u540e\uff0c\u5979\u8fd8\u6ca1\u6709\u9047\u5230\u8fc7\u5f20\u606a\uff0c\u4e5f\u4e0d\u6653\u5f97\u9047\u5230\u5f20\u606a\u8be5\u4ece\u4f55\u8bf4\u8d77\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5f20\u606a\u4e3b\u52a8\u63d0\u8d77\uff0c\u8d70\u5230\u5427\u53f0\u540e\u62ff\u8d77\u7535\u8bdd\u8ba9\u6731\u5c0f\u519b\u8d76\u7d27\u4ece\u5355\u4f4d\u6e9c\u51fa\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6731\u5c0f\u519b\u63a5\u5230\u4ee4\u5c0f\u71d5\u7535\u8bdd\u65f6\uff0c\u7f16\u4e86\u4e2a\u501f\u53e3\u8ddf\u79d1\u5ba4\u4e3b\u4efb\u8bf7\u4e86\u5047\uff0c\u62ff\u7740\u516c\u6587\u5305\u8981\u51fa\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u65f6\u649e\u5230\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u9646\u6587\u592b\uff0c\u9646\u6587\u592b\u4e00\u8fb9\u4e0a\u53f0\u9636\uff0c\u4e00\u8fb9\u62ff\u7740\u624b\u673a\u5728\u901a\u7535\u8bdd\uff1a\u201c\u738b\u5e02\u957f\uff0c\u665a\u4e0a\u8981\u4e00\u8d77\u5403\u996d\uff0c\u6211\u4e5f\u4e0d\u6e05\u695a\u674e\u7701\u957f\u6709\u6ca1\u6709\u522b\u7684\u5dee\u4e8b\u4ea4\u529e\u7ed9\u6211\u53bb\u505a\uff0c\u8fd9\u6837\u5b50\u5427\uff0c\u4e0b\u73ed\u4e4b\u524d\u6211\u518d\u8ddf\u4f60\u901a\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u5fc3\u91cc\u4e00\u865a\uff0c\u4e0d\u6653\u5f97\u662f\u4f4e\u5934\u8d70\u8fc7\u6765\u8fd8\u662f\u7b49\u9646\u6587\u592b\u6253\u5b8c\u7535\u8bdd\u4e0a\u524d\u6253\u58f0\u62db\u547c\uff0c\u8fdf\u7591\u4e0d\u51b3\uff0c\u811a\u6b65\u5c31\u6709\u4e9b\u62d6\u6c93\uff0c\u9646\u6587\u592b\u770b\u5230\u4ed6\uff0c\u5374\u5148\u671d\u4ed6\u62db\u62db\u624b\uff0c\u8ba9\u4ed6\u7b49\u4e00\u4f1a\u513f\uff0c\u6302\u6389\u7535\u8bdd\uff0c\u624d\u5bf9\u4ed6\u8bf4\uff1a\u201c\u4f60\u8ddf\u6211\u53bb\u529e\u516c\u5ba4\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u5fc3\u91cc\u7684\u60f4\u60f4\uff0c\u9646\u6587\u592b\u5c11\u8bf4\u6709\u4e24\u4e2a\u6708\u770b\u5230\u81ea\u5df1\u6ca1\u6709\u4e3b\u52a8\u8bf4\u8fc7\u8bdd\u4e86\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u8fdb\u4e86\u529e\u516c\u5ba4\uff0c\u62ff\u4e86\u8336\u676f\u7ed9\u81ea\u5df1\u5012\u4e86\u4e00\u676f\u6c34\uff0c\u6731\u5c0f\u519b\u5f53\u7136\u4e0d\u5962\u671b\u9646\u6587\u592b\u4f1a\u7ed9\u4ed6\u5012\u6c34\uff0c\u7ad9\u5728\u90a3\u91cc\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5012\u4e5f\u5fd8\u4e86\u5e94\u8be5\u4e3b\u52a8\u53bb\u5e2e\u9646\u6587\u592b\u5012\u6c34\u3002</p><p>\u3000\u3000\u201c\u5750\u5427\u2026\u2026\u201d\u9646\u6587\u592b\u5750\u5230\u529e\u516c\u684c\u540e\u9762\uff0c\u6307\u7740\u79bb\u4ed6\u529e\u516c\u684c\u7a0d\u8fdc\u7684\u6c99\u53d1\u8ba9\u6731\u5c0f\u519b\u5750\u4e0b\u6765\uff0c\u820c\u5c16\u5254\u7740\u6c34\u9762\u6d6e\u7740\u7684\u8336\u6c34\uff0c\u62bf\u4e86\u4e00\u53e3\u70ed\u6c34\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u4f60\u8fdb\u529e\u516c\u5385\u4e5f\u6709\u4e00\u5e74\u534a\u7684\u65f6\u95f4\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u524d\u5e74\u521d\u51ac\uff0c\u6709\u4e00\u5e74\u534a\u4e86\u2026\u2026\u201d\u6731\u5c0f\u519b\u5c41\u80a1\u4e0d\u6562\u5750\u5b9e\uff0c\u4e24\u53ea\u624b\u8001\u5b9e\u7684\u653e\u5728\u819d\u76d6\u4e0a\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6709\u4e00\u5e74\u534a\u4e86\u2026\u2026\u201d\u9646\u6587\u592b\u91cd\u590d\u4e86\u6731\u5c0f\u519b\u7684\u53e5\uff0c\u4ed6\u60f3\u8d77\u81ea\u5df1\u5e94\u8be5\u662f\u4e5d\u56db\u5e74\u51ac\u5929\u89c1\u5f20\u606a\u7b2c\u4e00\u9762\u5427\uff0c\u8fd8\u662f\u4e0e\u5f20\u606a\u4e00\u8d77\u53bb\u63a5\u5f90\u5b66\u5e73\u7684\u673a\uff0c\u4ed6\u521a\u624d\u63a5\u5230\u738b\u7ef4\u5747\u7684\u7535\u8bdd\uff0c\u738b\u7ef4\u5747\u5728\u7535\u8bdd\u63d0\u5230\u4ed6\u4eca\u5929\u4e2d\u5348\u4e0e\u5f20\u77e5\u884c\u3001\u91d1\u56fd\u6d77\u3001\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4e00\u8d77\u7528\u9910\u7684\u4e8b\uff0c\u738b\u7ef4\u5747\u5728\u7535\u8bdd\u9080\u8bf7\u4ed6\u665a\u4e0a\u805a\u4e00\u805a\uff0c\u9646\u6587\u592b\u4e00\u65f6\u8fd8\u6ca1\u6709\u7406\u6e05\u5934\u7eea\u5c31\u770b\u89c1\u6731\u5c0f\u519b\u51fa\u529e\u516c\u697c\uff0c\u77e5\u9053\u6731\u5c0f\u519b\u5f53\u521d\u641e\u51fa\u9999\u96ea\u6d77\u7684\u4e8b\u60c5\u88ab\u6392\u6324\u51fa\u7ecf\u8d38\u59d4\u7ecf\u674e\u4e49\u6c5f\u4ecb\u7ecd\u8fdb\u7701\u653f\u5e9c\u7684\uff0c\u4e5f\u77e5\u9053\u6731\u5c0f\u519b\u4e0e\u5f20\u606a\u8ba4\u8bc6\uff0c\u4e0b\u610f\u8bc6\u7684\u5c31\u5524\u4f4f\u6731\u5c0f\u519b\u3002\u4ece3\u670828\u65e5\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u53d1\u5e03\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u7684\u516c\u544a\u4ee5\u53ca\u7231\u8fbe\u96c6\u56e2\u5728\u6e2f\u6210\u7acb\u4ea7\u54c1\u5f00\u53d1\u4e2d\u5fc3\uff0c\u674e\u8fdc\u6e56\u7684\u6001\u5ea6\u53c8\u518d\u5ea6\u53d1\u751f\u8f6c\u53d8\uff0c\u9646\u6587\u592b\u662f\u80fd\u611f\u53d7\u5230\u7684\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u6765\u7f13\u548c\u50f5\u6301\u7684\u6c14\u6c1b\uff0c\u800c\u5efa\u90ba\u5e02\u91cc\u7684\u53cd\u5e94\u76f8\u5bf9\u8fdf\u949d\uff0c\u603b\u4e0d\u80fd\u8fd9\u65f6\u5019\u8fd8\u8ba9\u674e\u8fdc\u6e56\u4eb2\u81ea\u627e\u9526\u6e56\u6216\u8005\u4eb2\u81ea\u6566\u4fc3\u5efa\u90ba\u5e02\u51fa\u9762\u5c06\u79d1\u6280\u56ed\u9879\u76ee\u7559\u5728\u4e1c\u6d77\u3002\u4ed6\u501f\u7740\u5012\u6c34\u7684\u5de5\u592b\uff0c\u5c06\u6709\u4e9b\u95ee\u9898\u60f3\u4e86\u60f3\uff0c\u6216\u8bb8\u662f\u5efa\u90ba\u5e02\u91cc\u5df2\u7ecf\u4e3b\u52a8\u51fa\u51fb\u4e86\uff0c\u8fd9\u65e0\u7591\u80fd\u7ed9\u674e\u8fdc\u6e56\u6709\u4e2a\u53f0\u9636\u4e0b\uff0c\u8fd9\u65f6\u5019\u624d\u60f3\u8d77\u6765\u5c06\u6731\u5c0f\u519b\u53eb\u8fc7\u6765\u7684\u786e\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff0c\u53c8\u4e0d\u80fd\u4e00\u53e5\u8bdd\u4e0d\u8bf4\u5c06\u4ed6\u6253\u53d1\u6389\uff0c\u4fbf\u5173\u5fc3\u7684\u626f\u4e86\u4e24\u53e5\uff0c\u201c\u90a3\u5728\u8fd9\u91cc\u5de5\u4f5c\u4e86\u4e00\u5e74\u534a\uff0c\u6709\u4ec0\u4e48\u6210\u957f\u3001\u6709\u4ec0\u4e48\u4f53\u609f\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u8981\u8bf4\u6709\u6210\u957f\u4e5f\u5c31\u662f\u5f90\u5b66\u5e73\u8d70\u4e4b\u524d\u7ed9\u5b9a\u4e86\u526f\u4e3b\u4efb\u79d1\u5458\uff0c\u6731\u5c0f\u519b\u542c\u9646\u6587\u592b\u8fd9\u4e48\u5173\u5fc3\u7684\u95ee\u8bdd\uff0c\u5fc3\u91cc\u8fd8\u662f\u5f88\u5fd0\u5fd1\u4e86\u4e00\u4e0b\uff0c\u5fc3\u60f3\uff1a\u83ab\u4e0d\u662f\u53c8\u8981\u7ed9\u626b\u5730\u51fa\u95e8\uff1f\u518d\u60f3\u5230\u9a6c\u4e0a\u8981\u53bb\u8ddf\u5f20\u606a\u89c1\u9762\uff0c\u4e5f\u5e78\u597d\u9646\u6587\u592b\u63d0\u524d\u8bf4\u51fa\u6765\uff0c\u60c5\u51b5\u6709\u591a\u7cdf\u7cd5\u8fd8\u80fd\u627e\u4e2a\u4eba\u8bc9\u82e6\uff0c\u4e0d\u8981\u4ee5\u540e\u627e\u4e0d\u5230\u8ddf\u5f20\u606a\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u627e\u4eba\u8bc9\u82e6\u90fd\u6ca1\u6709\u5730\u65b9\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u4e4b\u524d\u4e5f\u8981\u7b97\u5f90\u5b66\u5e73\u4e00\u7cfb\u7684\u4eba\uff0c\u8fd9\u6bb5\u65f6\u95f4\u600e\u4e48\u5c31\u5df4\u7ed3\u674e\u8fdc\u6e56\u8fd9\u4e48\u4eb2\u5bc6\u5462\uff1f\u6731\u5c0f\u519b\u662f\u4f53\u4f1a\u4e0d\u5230\u674e\u8fdc\u6e56\u7684\u624b\u8155\u7684\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u62ff\u7740\u4e9b\u5957\u8bdd\u5e94\u4ed8\u9646\u6587\u592b\uff0c\u6b64\u7c7b\u6750\u6599\u4e5f\u5199\u591a\u4e86\uff0c\u52a0\u4e0a\u5bb6\u5ead\u7684\u57f9\u517b\uff0c\u573a\u9762\u8bdd\u65e9\u5c31\u8bf4\u987a\u53e3\u4e86\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u9646\u6587\u592b\u542c\u4e86\u6ca1\u7cbe\u6253\u5f69\uff0c\u4e5f\u77e5\u9053\u6ca1\u53ef\u80fd\u4ece\u6731\u5c0f\u519b\u90a3\u91cc\u542c\u5230\u4ec0\u4e48\u771f\u5fc3\u8bdd\uff0c\u60f3\u7740\u8981\u5c06\u6731\u5c0f\u519b\u6253\u53d1\uff0c\u968f\u53e3\u95ee\u9053\uff0c\u201c\u521a\u770b\u5230\u4f60\u8981\u51fa\u53bb\uff0c\u662f\u4e3a\u624b\u5934\u7684\u5de5\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u606a\u5728\u5b66\u5e9c\u5df7\u7ea6\u6211\u53bb\u559d\u8336\u2026\u2026\u201d\u4ed6\u4e5f\u662f\u54ac\u7259\u8bf4\u51fa\u8fd9\u4e8b\uff0c\u6bd4\u8d77\u7ed9\u626b\u5730\u51fa\u95e8\uff0c\u60c5\u51b5\u603b\u4e0d\u80fd\u4f1a\u66f4\u7cdf\uff0c\u201c\u521a\u8ddf\u51af\u5904\u957f\u8bf7\u4e86\u5047\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u9646\u6587\u592b\u5012\u662f\u5fae\u8bb6\u7684\u53d1\u51fa\u4e00\u58f0\u611f\u53f9\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u5728\u5efa\u90ba\u7684\u6d3b\u52a8\u5f00\u59cb\u6d3b\u8dc3\u8d77\u6765\uff0c\u53ea\u662f\u60f3\u4e0d\u901a\u5f20\u606a\u53eb\u6731\u5c0f\u519b\u8fd9\u4e00\u53f7\u65e0\u5173\u7d27\u8981\u7684\u4eba\u7269\u8fc7\u53bb\u505a\u4ec0\u4e48\uff0c\u65e2\u7136\u5f20\u606a\u521a\u4e0e\u738b\u7ef4\u5747\u5403\u8fc7\u4e2d\u996d\uff0c\u53c8\u7ea6\u6731\u5c0f\u519b\u8fc7\u53bb\u559d\u8336\uff0c\u8fd9\u4e48\u8bf4\uff0c\u6731\u5c0f\u519b\u5728\u5f20\u606a\u7684\u5fc3\u76ee\u4e2d\u8fd8\u662f\u6709\u4e00\u5b9a\u5730\u4f4d\u7684\uff0c\u4e0d\u65b9\u4fbf\u7ec6\u95ee\u4ec0\u4e48\uff0c\u7ed5\u8fc7\u529e\u516c\u684c\u8d70\u5230\u6731\u5c0f\u519b\u8ddf\u524d\uff0c\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u6837\u554a\uff0c\u4f60\u8d76\u7d27\u8fc7\u53bb\u5427\uff0c\u6211\u803d\u6401\u4f60\u4e0d\u5c11\u65f6\u95f4\u5427\uff0c\u8981\u4e0d\u8981\u4ece\u5c0f\u8f66\u73ed\u53eb\u90e8\u8f66\u9001\u4f60\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u7a81\u7136\u4eb2\u5207\u7684\u6001\u5ea6\u8ba9\u6731\u5c0f\u519b\u90fd\u6709\u4e9b\u53d1\u8499\u4e86\uff0c\u5f20\u606a\u8fd9\u4eba\u7684\u9b54\u529b\u6709\u8fd9\u4e48\u5927\u5417\uff0c\u8fd8\u6709\uff0c\u9646\u6587\u592b\u4e0d\u662f\u5df2\u7ecf\u8f6c\u6295\u674e\u8fdc\u6e56\u95e8\u4e0b\u4e86\u5417\uff1f\u6b63\u8981\u611f\u8c22\u9646\u6587\u592b\u7684\u597d\u610f\u5e76\u5a49\u62d2\u6389\uff0c\u9646\u6587\u592b\u5fc6\u7ecf\u6253\u5f00\u4ed6\u7684\u529e\u516c\u5ba4\u95e8\u5429\u5490\u4eba\uff1a\u201c\u5c0f\u5f20\uff0c\u5c0f\u6731\u51fa\u53bb\u6709\u4e9b\u4e8b\u60c5\uff0c\u5c0f\u8f66\u73ed\u6709\u8c01\u5728\u5bb6\u5f00\u8f66\u9001\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u5c42\u575a\u51b0\u5230\u73b0\u5728\u4e5f\u5c31\u5269\u4e0b\u6700\u540e\u8584\u8584\u7684\u4e00\u5c42\uff0c\u4e00\u6345\u5c31\u8981\u7834\u4e86\uff0c\u9646\u6587\u592b\u5929\u5929\u90fd\u8ddf\u5728\u674e\u8fdc\u6e56\u8eab\u8fb9\uff0c\u53c8\u600e\u4e48\u53ef\u80fd\u611f\u89c9\u4e0d\u5230\u5462\uff1f\u7b49\u6731\u5c0f\u519b\u8d70\u51fa\u53bb\uff0c\u9646\u6587\u592b\u624d\u62ff\u7740\u5f85\u6279\u9605\u7684\u6587\u4ef6\u53bb\u674e\u8fdc\u6e56\u7684\u529e\u516c\u5ba4\uff0c\u5fc3\u91cc\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u4e3b\u52a8\u8ddf\u674e\u8fdc\u6e56\u63d0\u8d77\u738b\u7ef4\u5747\u665a\u4e0a\u7ea6\u89c1\u9762\u7684\u4e8b\u60c5\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6731\u5c0f\u519b\u5750\u7701\u653f\u5e9c\u7684\u5c0f\u8f66\u76f4\u63a5\u5230\u5b66\u5e9c\u5df7\u4e0b\uff0c\u4e5f\u5c31\u6ca1\u6709\u803d\u6401\u591a\u5c11\u65f6\u95f4\uff0c\u63a8\u5f001978\u539a\u91cd\u7684\u5d4c\u5f69\u8272\u78e8\u7802\u73bb\u7483\u7684\u95e8\u6249\uff0c\u770b\u5230\u5973\u670b\u53cb\u4ee4\u5c0f\u71d5\u7ad9\u5728\u5427\u53f0\u540e\u671d\u91cc\u89d2\u5476\u5476\u5634\uff0c\u770b\u8fc7\u6765\uff0c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u8bb8\u601d\u3001\u5b59\u9759\u6aac\u966a\u4e00\u4e2a\u4ed6\u6ca1\u6709\u89c1\u8fc7\u7684\u9752\u5e74\u5750\u5728\u9152\u53f0\u7684\u91cc\u89d2\u3002\u5f20\u606a\u6b63\u597d\u56de\u5934\u770b\u5230\u8fd9\u91cc\uff0c\u62db\u624b\u8ba9\u4ed6\u8fc7\u53bb\uff1a\u201c\u8fc7\u6765\u8fc7\u6765\uff0c\u4ecb\u7ecd\u4e2a\u4eba\u7ed9\u4f60\u8ba4\u8bc6\u2026\u2026\u4f60\u540c\u5b66\u7b80\u5fd7\u5eb7\u5728\u7701\u57ce\u5427\uff1f\u4f60\u6253\u7535\u8bdd\u7ea6\u4ed6\u4e00\u8d77\u8fc7\u6765\uff0c\u521a\u624d\u5fd8\u4e86\u8ddf\u4ee4\u5c0f\u71d5\u8bf4\u4e86\u3002\u201d</p><p>\u3000\u3000\u59da\u6587\u76db\u5728\u5efa\u90ba\u6ce8\u5b9a\u8981\u4e00\u756a\u4f5c\u4e3a\uff0c\u5f20\u606a\u4e5f\u51b3\u5b9a\u6210\u5168\u4ed6\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8ba9\u6731\u5c0f\u519b\u3001\u7b80\u5fd7\u5eb7\u7b49\u4eba\u4e5f\u8ddf\u7740\u4e00\u8d77\u6210\u957f\uff0c\u62c9\u4e86\u4e00\u5f20\u6905\u5b50\u8ba9\u6731\u5c0f\u519b\u5750\u5230\u4ed6\u4e0e\u59da\u6587\u76db\u4e4b\u95f4\uff0c\u8ddf\u59da\u6587\u76db\u8bf4\uff1a\u201c\u6731\u5c0f\u519b\u5728\u7701\u653f\u5e9c\u529e\u516c\u5385\u5de5\u4f5c\uff0c\u4ed6\u7236\u4eb2\u4e5f\u662f\u7701\u5385\u91cc\u7684\u8001\u673a\u5173\uff0c\u4f60\u5bf9\u5efa\u90ba\u53ea\u6015\u4e5f\u4e0d\u662f\u5f88\u719f\u6089\uff0c\u53c8\u4e0d\u80fd\u603b\u8ba9\u53f6\u54e5\u7ed9\u4f60\u5f53\u5411\u5bfc\uff0c\u6211\u7ed9\u4f60\u627e\u6765\u4e2a\u5411\u5bfc\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u59da\u6587\u76db\u4e5f\u77e5\u9053\u60f3\u5728\u5efa\u90ba\u6210\u4ec0\u4e48\u4e8b\uff0c\u5728\u673a\u5173\u91cc\u603b\u8981\u6709\u4fe1\u4efb\u8fc7\u7684\u4eba\u53ef\u4ee5\u5dee\u4f7f\uff0c\u4ed6\u6b64\u65f6\u7684\u7ea7\u522b\u504f\u4f4e\uff0c\u60f3\u8981\u627e\u5230\u5408\u9002\u7684\u4eba\u4e5f\u96be\uff0c\u7279\u522b\u662f\u8981\u8ddf\u9526\u6e56\u4fdd\u6301\u7d27\u5bc6\u7684\u8054\u7cfb\uff0c\u603b\u4e0d\u80fd\u4e8b\u4e8b\u90fd\u62c9\u4e0a\u53f6\u5efa\u658c\uff0c\u5c31\u7b97\u4e0e\u5f20\u606a\u5173\u7cfb\u719f\u7edc\u4e4b\u540e\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4e8b\u4e8b\u90fd\u627e\u5f20\u606a\u89e3\u51b3\u3002</p></article>"}}}]);
