(window.webpackJsonp=window.webpackJsonp||[]).push([[717],{740:function(p){p.exports={chapterTitle:null,title:"\u7b2c716\u7ae0 \u5f71\u54cd\u529b",content:"<article><h2>\u7b2c716\u7ae0 \u5f71\u54cd\u529b</h2><p>\u3000\u3000\u4e00\u4e0b\u5348\u7684\u8463\u4e8b\u4f1a\u8bae\u7728\u773c\u95f4\u5c31\u8fc7\u53bb\u4e86\uff0c\u4e3b\u8981\u8fd8\u662f\u90b5\u81f3\u521a\u629b\u51fa\u6765\u7684\u8ba1\u5212\u6709\u4e9b\u60ca\u4e16\u9a87\u4fd7\u4e86\uff0c\u4e00\u4e0b\u5b50\u5c31\u6293\u4f4f\u5927\u5bb6\u7684\u6ce8\u610f\u529b\uff0c\u81ea\u7136\u5c31\u4e0d\u4f1a\u6ce8\u610f\u5230\u65f6\u95f4\u7684\u6d41\u901d\u3002\u5929\u8272\u6697\u4e0b\u6765\uff0c\u5927\u5bb6\u7684\u809a\u5b50\u90fd\u997f\u5f97\u5495\u5495\u53eb\uff0c\u8981\u4e0d\u662f\u5b59\u9759\u6aac\u5728\u4f1a\u8bae\u5ba4\u5916\u9762\u7b49\u4e86\u8bb8\u4e45\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u8fd9\u8fb9\u8fd8\u4e0d\u4f1a\u4e2d\u6b62\u4f1a\u8bae\u3002\u8fd8\u662f\u5f20\u606a\u5148\u6309\u637a\u4e0d\u4f4f\u63d0\u51fa\u5148\u627e\u5730\u65b9\u5403\u996d\uff0c\u53cd\u6b63\u4e5f\u6ca1\u6709\u5916\u4eba\u5728\u573a\uff0c\u5728\u996d\u684c\u4e0a\u4e00\u6837\u53ef\u4ee5\u8c08\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u7684\u60c5\u7eea\u90fd\u8fd8\u9677\u5728\u4e0b\u5348\u7684\u4f1a\u8bae\u91cc\uff0c\u90fd\u5efa\u8bae\u53bb\u9694\u58c1\u7684\u6a61\u6811\u56ed\u5458\u5de5\u98df\u5802\u7528\u9910\uff0c\u4ece\u8fd9\u8fb9\u53bb\u6a61\u6811\u56ed\u521b\u4e1a\u56ed\u533a\u6709\u4e00\u6761\u5e7d\u9759\u7684\u6797\u836b\u5c0f\u5f84\uff0c\u8fb9\u8d70\u8fb9\u804a\uff0c\u4e5f\u6ca1\u6709\u65c1\u4eba\u6253\u6270\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u76db\u9752\u4ed6\u4eec\u8d70\u5728\u4e00\u8d77\uff0c\u65e0\u5948\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5165\u9b54\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8001\u90b5\u7684\u8ba1\u5212\uff0c\u4f60\u4e0d\u662f\u4e5f\u5f88\u80af\u5b9a\uff1f\u4e0d\u8fc7\u4f60\u771f\u662f\u4e0d\u7b80\u5355\u5462\uff0c\u8001\u90b5\u7ede\u5c3d\u8111\u6c41\u60f3\u4e86\u4e24\u4e09\u4e2a\u6708\u7684\u4e1c\u897f\uff0c\u8ba9\u4f60\u4e09\u8a00\u4e24\u8bed\u5c31\u731c\u4e86\u516b\u4e5d\u4e0d\u79bb\u5341\uff0c\u771f\u4e0d\u6e05\u695a\u4f60\u7684\u8111\u5b50\u662f\u7528\u4ec0\u4e48\u505a\u51fa\u6765\u7684\uff1f\u201d\u76db\u9752\u611f\u6168\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4e00\u70b9\u5e95\u6c14\uff0c\u6562\u8df3\u51fa\u6765\u663e\u6446\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u65b0\u7684\u9ad8\u65b0\u533a\u89c4\u5212\u533a\u57df\uff0c\u4f60\u4eec\u90fd\u672a\u5fc5\u6709\u6211\u719f\u6089\u3002\u54ea\u5757\u533a\u57df\u9002\u5408\u4ec0\u4e48\u7528\u9014\uff0c\u6211\u53ef\u4e0d\u53ea\u662f\u4ece\u4e09\u4e2a\u6708\u4e4b\u524d\u5c31\u5f00\u59cb\u8003\u8651\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6d3b\u5f97\u7d2f\u4e0d\u7d2f\uff1f\u201d\u5b59\u9759\u6aac\u4eb2\u70ed\u7684\u6402\u7740\u8bb8\u601d\u7684\u80f3\u818a\u8d70\u5728\u524d\u9762\uff0c\u542c\u5230\u5f20\u606a\u5728\u90a3\u91cc\u663e\u6446\uff0c\u56de\u5934\u6b6a\u5934\u8111\u888b\u95ee\u4e86\u4ed6\u4e00\u53e5\u3002\u5f20\u606a\u8fd8\u60f3\u56de\u4e00\u53e5\uff0c\u5b59\u9759\u6aac\u5374\u53c8\u8f6c\u8fc7\u5934\u53bb\u8ddf\u8bb8\u601d\u8bf4\u8bdd\uff0c\u5979\u53ea\u7a81\u7136\u7684\u611f\u6168\u4e00\u4e0b\uff0c\u6ca1\u6709\u8981\u5f20\u606a\u56de\u7b54\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f97\u671d\u76db\u9752\u4ed6\u4eec\u644a\u644a\u624b\uff0c\u81ea\u5632\u7684\u8bf4\uff1a\u201c\u5927\u5bb6\u90fd\u8fd9\u4e48\u6d3b\u7740\uff0c\u4e5f\u6ca1\u6240\u8c13\u7d2f\u4e0e\u4e0d\u7d2f\u3002\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u5f88\u719f\u6089\u4e86\uff0c\u52a0\u4e0a\u5f20\u606a\u7684\u5e74\u7eaa\u5c11\uff0c\u5927\u5bb6\u5728\u8a00\u8bed\u4e0a\u90fd\u5f88\u968f\u4fbf\uff0c\u4f46\u662f\u8bdd\u8bf4\u56de\u6765\uff0c\u5f20\u606a\u5728\u5927\u5c40\u8fd9\u79cd\u9ad8\u5c4b\u5efa\u74f4\u7684\u9ad8\u77bb\u8fdc\u77a9\uff0c\u624d\u662f\u4ee4\u76db\u9752\u4ed6\u4eec\u6700\u53f9\u670d\u3001\u6700\u656c\u4f69\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u9526\u6e56\u73af\u73af\u76f8\u6263\u7684\u53d1\u5c55\u5230\u4eca\u5929\u7684\u5730\u6b65\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u4e00\u6b65\u90fd\u6ca1\u6709\u504f\u501a\u3002\u4ee5\u5f80\u90a3\u4e9b\u88ab\u8a89\u4e3a\u5546\u4e1a\u5929\u624d\u7684\u4eba\u7269\uff0c\u5728\u5f20\u606a\u9762\u524d\uff0c\u5927\u6982\u4e0d\u582a\u4e00\u63d0\u5427\u3002\u5efa\u90ba\u5e02\u91cc\u63d0\u51fa\u6570\u5b57\u957f\u5eca\u6784\u60f3\uff0c\u4e5f\u662f\u53d7\u9526\u6e56\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u523a\u6fc0\u4e0e\u542f\u53d1\uff0c\u53ef\u4ee5\u8bf4\uff0c\u5f20\u606a\u786e\u786e\u5b9e\u5b9e\u8981\u7b97\u662f\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u7075\u9b42\u4eba\u7269\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u6b63\u56e0\u4e3a\u90b5\u81f3\u521a\u540e\u6765\u8be6\u7ec6\u89e3\u91ca\u7684\u8ba1\u5212\u7ec6\u8282\uff0c\u4e0e\u5f20\u606a\u4e4b\u524d\u7684\u731c\u6d4b\u7b26\u5408\u7a0b\u5ea6\u5f88\u9ad8\uff0c\u624d\u5c06\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u4e09\u4eba\u7684\u5174\u8da3\u5b8c\u5168\u5438\u5f15\u8fc7\u6765\u3002</p><p>\u3000\u3000\u76db\u9752\u8fd9\u65f6\u5019\u660e\u767d\u90b5\u81f3\u521a\u4e3a\u4ec0\u4e48\u575a\u6301\u8981\u5f20\u606a\u53c2\u52a0\u4e86\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7684\u8ba1\u5212\u6b63\u9762\u7684\u53bb\u5f62\u5bb9\uff0c\u5f88\u5b8f\u4f1f\u5f88\u5e9e\u5927\u5f88\u6709\u8bf1\u60d1\u529b\uff0c\u4f46\u662f\u6781\u5ea6\u7684\u5192\u9669\uff0c\u603b\u8d44\u4ea7\u89c4\u6a21\u4e0d\u8db3\u4e09\u4ebf\u3001\u6b63\u5f0f\u6210\u7acb\u90fd\u4e0d\u8db3\u4e00\u5e74\u7684\u5730\u4ea7\u516c\u53f8\uff0c\u7a81\u7136\u63d0\u51fa\u8981\u8fd0\u8425\u8fd9\u4e48\u5e9e\u5927\u7684\u5546\u4e1a\u8ba1\u5212\uff0c\u591a\u5c11\u7ed9\u4eba\u6709\u4e9b\u610f\u60f3\u5929\u5f00\u7684\u611f\u89c9\u3002\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u90fd\u662f\u5546\u573a\u8001\u624b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5f62\u6210\u81ea\u5df1\u72ec\u7279\u7684\u6295\u8d44\u7406\u5ff5\uff0c\u6210\u719f\u7684\u6295\u8d44\u8005\uff0c\u90fd\u4f1a\u7ed9\u81ea\u5df1\u5212\u5b9a\u4e00\u6761\u7ebf\uff0c\u4fdd\u5b88\u5e76\u4e0d\u80fd\u8bf4\u662f\u4e0d\u597d\u7684\u54c1\u8d28\uff0c\u90b5\u81f3\u521a\u65e0\u8bba\u8bf4\u7684\u518d\u5929\u82b1\u4e71\u5760\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u5bf9\u5982\u6b64\u5e9e\u5927\u7684\u8ba1\u5212\u90fd\u4f1a\u6709\u5929\u7136\u7684\u8c28\u60d5\u4e0e\u62b5\u89e6\u3002</p><p>\u3000\u3000\u4e0d\u8981\u4f4e\u4f30\u5f20\u606a\u5bf9\u4ed6\u4eec\u4e09\u4eba\u7684\u5f71\u54cd\u529b\uff0c\u6709\u4e86\u5f20\u606a\u4e4b\u524d\u7684\u731c\u6d4b\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5efa\u3001\u53f6\u5efa\u658c\u4e09\u4eba\u5bf9\u90b5\u81f3\u521a\u63a5\u4e0b\u6765\u8be6\u7ec6\u7684\u8ba1\u5212\u89e3\u5256\u90fd\u5174\u8da3\u6d53\u539a\uff0c\u8c28\u614e\u662f\u81ea\u7136\u7684\uff0c\u5374\u6ca1\u6709\u4e00\u70b9\u7684\u62b5\u89e6\uff0c\u4e5f\u6ca1\u6709\u6000\u7591\u4e16\u7eaa\u9526\u6e56\u6709\u64cd\u4f5c\u8fd9\u4e2a\u5e9e\u5927\u8ba1\u5212\u7684\u80fd\u529b\uff0c\u4e00\u4e0b\u5348\u53ea\u662f\u5728\u8ba8\u8bba\u6574\u4e2a\u8ba1\u5212\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4ec5\u80fd\u5f71\u54cd\u5230\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5efa\u3001\u53f6\u5efa\u658c\u7b49\u4eba\uff0c\u5bf9\u5efa\u90ba\u5e02\u91cc\u7684\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u7684\u5f71\u54cd\u529b\u4e5f\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u9700\u8981\u9526\u6e56\u76f4\u63a5\u53c2\u4e0e\uff0c\u53ea\u9700\u8981\u4ed6\u80fd\u80af\u5b9a\u8fd9\u9879\u76ee\u8ba1\u5212\uff0c\u5c31\u80fd\u5728\u4e00\u7a0b\u5ea6\u4e0a\u5f71\u54cd\u5230\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u5b9e\u6743\u4eba\u7269\u5bf9\u8fd9\u9879\u8ba1\u5212\u7684\u6001\u5ea6\uff0c\u6ca1\u6709\u7f57\u541b\u3001\u738b\u7ef4\u5747\u8fd9\u4e9b\u5b9e\u6743\u4eba\u7269\u7684\u652f\u6301\uff0c\u8ba1\u5212\u518d\u5b8c\u7f8e\uff0c\u4e5f\u6ca1\u6709\u5b9e\u65bd\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u603b\u6709\u4e00\u4e9b\u62c5\u5fc3\uff0c\u4ed6\u4e5f\u6ca1\u6709\u8ddf\u5b59\u5c1a\u4e49\u4ed6\u4eec\u8d70\u5728\u4e00\u8d77\uff0c\u51d1\u5230\u5f20\u606a\u3001\u76db\u9752\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u90b5\u7ed9\u6211\u8bf4\u8fd9\u4e2a\u8ba1\u5212\uff0c\u6211\u4e00\u76f4\u90fd\u5f88\u5fd0\u5fd1\uff0c\u4f1a\u4e0d\u4f1a\u592a\u5927\u4e86\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u76db\u9752\u7b11\u7740\u8bf4\uff1a\u201c\u9526\u6e56\u53bb\u5e74\u5e74\u521d\u629b\u51fa\u6797\u7eb8\u4e00\u4f53\u5316\u8ba1\u5212\u7684\u65f6\u5019\uff0c\u591a\u5c11\u4eba\u8bf4\u4ed6\u75af\u4e86\uff1f\u4ed6\u53ef\u6ca1\u6709\u75af\uff0c\u4f60\u7b97\u7b97\u770b\uff0c\u4e00\u4e2a\u5236\u6d46\u9879\u76ee\uff0c\u5c06\u591a\u5c11\u4eba\u7ed5\u4e86\u8fdb\u53bb\uff1f\u53bb\u5e74\u5e74\u521d\u7684\u9526\u6e56\uff0c\u4e0d\u6bd4\u8fd9\u65f6\u5019\u7684\u4e16\u7eaa\u9526\u6e56\u5f3a\u591a\u5c11\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6797\u7eb8\u4e00\u4f53\u5316\uff0c\u53ef\u4e0d\u662f\u6ca1\u6709\u5b9e\u65bd\u5417\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u5475\uff0c\u662f\u6ca1\u5b9e\u65bd\uff0c\u201d\u76db\u9752\u8bf4\u9053\uff0c\u201c\u4f46\u662f\u4eca\u5e74\u7231\u8fbe\u7535\u5b50\u501f\u58f3\u4e0a\u5e02\u3001\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u3001\u4e0a\u624b\u673a\u9879\u76ee\uff0c\u7ec4\u5efa\u7231\u8fbe\u96c6\u56e2\uff0c\u54ea\u4e00\u4e2a\u52a8\u4f5c\u5c0f\u4e86\uff1f\u8fd9\u4e9b\u52a8\u4f5c\uff0c\u96be\u9053\u4e0d\u662f\u4f60\u5728\u629b\u51fa\u6797\u7eb8\u4e00\u4f53\u5316\u8ba1\u5212\u65f6\u5c31\u5df2\u7ecf\u6316\u597d\u7684\u5751\u5417\uff1f\u6bd4\u4f60\u554a\uff0c\u6211\u4eec\u8fd9\u4e2a\u8ba1\u5212\uff0c\u8fd8\u771f\u6709\u4e9b\u62ff\u4e0d\u51fa\u53f0\u9762\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u644a\u644a\u624b\uff0c\u8868\u793a\u6ca1\u8bdd\u53ef\u8bf4\uff0c\u4ed6\u4e00\u76f4\u90fd\u5f88\u6e05\u695a\u90b5\u81f3\u521a\u5728\u8d44\u672c\u8fd0\u4f5c\u65b9\u9762\u7684\u80fd\u529b\u4e0e\u5bf9\u5e02\u573a\u7684\u654f\u9510\u76f4\u89c9\uff0c\u90b5\u81f3\u521a\u80fd\u5728\u4e5d\u4e03\u5e74\u5c31\u629b\u51fa\u8fd9\u4e48\u5e9e\u5927\u7684\u6574\u4f53\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u8ba1\u5212\uff0c\u8fd8\u771f\u662f\u6ca1\u6709\u8ba9\u81ea\u5df1\u5931\u671b\u554a\u3002\u5c0f\u53d4\u575a\u6301\u8981\u81ea\u5df1\u8fc7\u6765\u53c2\u52a0\u4e16\u7eaa\u9526\u6e56\u7684\u8463\u4e8b\u4f1a\u8bae\uff0c\u4ed6\u5fc3\u77e5\u809a\u660e\uff1a\u8fd9\u4e9b\u8001\u72d0\u72f8\u554a\uff0c\u4e0d\u5c31\u770b\u4e2d\u81ea\u5df1\u8fd8\u6709\u90a3\u4e48\u4e9b\u4f5c\u7528\u5417\uff1f\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u90fd\u5b8c\u5168\u90fd\u90b5\u81f3\u521a\u5438\u5f15\u8fc7\u6765\uff0c\u8fd8\u6709\u5c0f\u53d4\uff0c\u4ed6\u4eec\u4e94\u4e2a\u4eba\u51d1\u5728\u4e00\u8d77\uff0c\u4f3c\u4e4e\u665a\u996d\u4e0d\u5403\u90fd\u6ca1\u6709\u95ee\u9898\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6402\u7740\u8bb8\u601d\u7684\u80f3\u818a\uff0c\u9080\u5979\u5403\u8fc7\u996d\u53bb1978\u5750\u4e00\u5750\uff0c\u8bb8\u601d\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u6653\u5f97\u5403\u8fc7\u996d\u8fd8\u8981\u4e0d\u8981\u8c08\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e48\u5e9e\u5927\u7684\u8ba1\u5212\uff0c\u534a\u5929\u7684\u8ba8\u8bba\u65f6\u95f4\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u5bf9\u6574\u4e2a\u8ba1\u5212\u8fd8\u8981\u8bb8\u591a\u7684\u7591\u95ee\u3002\u8fd9\u4e9b\u7591\u95ee\u5f97\u4e0d\u5230\u89e3\u51b3\uff0c\u4ed6\u4eec\u600e\u4e48\u53ef\u80fd\u638f\u94b1\u51fa\u6765\uff1f\u6ca1\u6709\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7684\u6ce8\u8d44\uff0c\u5c31\u6ca1\u6709\u8db3\u591f\u7684\u8d44\u91d1\u53bb\u542f\u52a8\u8fd9\u9879\u8ba1\u5212\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u5979\u7236\u4eb2\u8d70\u5728\u540e\u9762\uff0c\u8fd8\u6709\u5802\u53d4\u845b\u660e\u5fb7\u4ee5\u53ca\u53f6\u5efa\u658c\u90fd\u56f4\u7740\u90b5\u81f3\u521a\u4e0e\u5f20\u606a\u7684\u5c0f\u53d4\u5f20\u77e5\u975e\u8d70\u8def\u8fd8\u5728\u4e0d\u4e2d\u65ad\u7684\u8ba8\u8bba\u751f\u610f\u4e0a\u7684\u4e8b\u60c5\uff0c\u8111\u888b\u65e0\u529b\u7684\u6298\u4e0b\u6765\uff0c\u90fd\u662f\u4e8b\u4e1a\u81f3\u4e0a\u7684\u4e00\u7fa4\u602a\u80ce\uff0c\u5979\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u660e\u767d\u5728\u8d22\u5bcc\u540e\u9762\u518d\u6dfb\u52a0\u4e00\u4e2a\u96f6\u6709\u591a\u5927\u7684\u610f\u4e49\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u65e0\u5948\uff0c\u8d70\u8def\u79bb\u5c0f\u53d4\u4ed6\u4eec\u8fdc\u8fdc\u7684\uff1b\u675c\u98de\u5374\u5f88\u5174\u594b\uff0c\u80fd\u53c2\u4e0e\u5982\u6b64\u5b8f\u5927\u7684\u5546\u4e1a\u8ba1\u5212\u4e4b\u4e2d\uff0c\u54ea\u6015\u4f5c\u4e3a\u4e00\u4e2a\u65c1\u89c2\u8005\uff0c\u773c\u754c\u4e5f\u4f1a\u5f97\u5230\u6781\u5927\u7684\u63d0\u9ad8\uff0c\u4ed6\u5c31\u50cf\u5c0f\u5c3e\u5df4\u4e00\u6837\uff0c\u7f00\u5728\u540e\u9762\uff0c\u542c\u524d\u9762\u4eba\u8ba8\u8bba\u3002</p><p>\u3000\u3000\u6881\u519b\u5148\u53bb\u6a61\u6811\u56ed\u5458\u5de5\u9910\u5385\u5b89\u6392\u665a\u9910\u7684\u4e8b\u60c5\uff0c\u5176\u5b9e\u4e5f\u4e0d\u7528\u6881\u519b\u7279\u610f\u8fc7\u53bb\uff0c\u6a61\u6811\u56ed\u90a3\u8fb9\u77e5\u9053\u5f20\u606a\u4e5f\u4f1a\u4e00\u8d77\u53bb\u7528\u9910\uff0c\u8d75\u9e4f\u5c31\u4eb2\u81ea\u7559\u4e0b\u6765\u5b89\u6392\u665a\u5bb4\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u6a61\u6811\u56ed\u5458\u5de5\u9910\u5385\uff0c\u5f20\u77e5\u975e\u3001\u90b5\u81f3\u521a\u4ed6\u4eec\u624d\u6682\u65f6\u505c\u6b62\u8ba8\u8bba\uff0c\u770b\u7740\u6a61\u6811\u56ed\u884c\u653f\u603b\u7ecf\u7406\u8d75\u9e4f\u4eb2\u81ea\u4e3a\u4ed6\u4eec\u7684\u665a\u5bb4\u505a\u5b89\u6392\uff0c\u5f88\u8fc7\u610f\u4e0d\u53bb\uff0c\u5f20\u77e5\u975e\u9080\u8bf7\u4ed6\u4e00\u8d77\u7559\u4e0b\u6765\u7528\u9910\u3002</p><p>\u3000\u3000\u8fd9\u9910\u6bd5\u7adf\u662f\u4e16\u7eaa\u9526\u6e56\u7684\u665a\u5bb4\uff0c\u996d\u684c\u4e0a\u4ed6\u4eec\u4e5f\u4f1a\u6309\u637a\u4e0d\u4f4f\u7684\u8ba8\u8bba\u8ba1\u5212\u7684\u7ec6\u8282\uff0c\u5c31\u7b97\u8d75\u9e4f\u518d\u6bb7\u52e4\uff0c\u5f20\u606a\u90fd\u4e0d\u4fbf\u76f4\u63a5\u8ba9\u4ed6\u4e5f\u4e00\u8d77\u7528\u9910\uff0c\u5f20\u77e5\u975e\u5f00\u53e3\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u4e0d\u4ec5\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u96c6\u56e2\u7684\u8463\u4e8b\u957f\uff0c\u4e5f\u662f\u5f20\u606a\u7684\u5802\u53d4\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u53ea\u6709\u4ed6\u5f00\u53e3\u8bf4\u3002</p><p>\u3000\u3000\u9526\u6e56\u53d1\u5c55\u5230\u4eca\u5929\uff0c\u77ed\u77ed\u4e09\u5e74\u65f6\u95f4\u91cc\uff0c\u4e5f\u51ff\u5b9e\u5192\u51fa\u51e0\u540d\u540e\u8d77\u4e4b\u79c0\uff0c\u50cf\u7ba1\u7406\u4e0a\u6709\u8d75\u9e4f\u3001\u59da\u575a\uff0c\u6280\u672f\u4e0a\u6709\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\uff0c\u4ed6\u4eec\u4e0e\u6881\u519b\u4e00\u6837\uff0c\u90fd\u975e\u5e38\u7684\u5e74\u8f7b\uff0c\u8d70\u51fa\u793e\u4f1a\u5de5\u4f5c\u6ca1\u51e0\u5e74\uff0c\u5374\u6709\u7740\u6781\u4e3a\u51fa\u8272\u7684\u609f\u6027\u4e0e\u624d\u534e\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u80fd\u51fa\u5934\uff0c\u4e5f\u4e0e\u9526\u6e56\u6781\u4e3a\u5305\u5bb9\u7684\u7528\u4eba\u5236\u5ea6\u6709\u5173\uff0c\u5f88\u96be\u60f3\u8c61\u5728\u4f20\u7edf\u7684\u4f01\u4e1a\u91cc\uff0c\u4ed6\u4eec\u80fd\u8fd9\u4e48\u8fc5\u901f\u7684\u8d70\u5230\u73b0\u5728\u7684\u5c97\u4f4d\u4e0a\u6765\u3002</p><p>\u3000\u3000\u6709\u5f20\u606a\u8fd9\u4e2a\u602a\u80ce\u5728\uff0c\u8d75\u9e4f\u3001\u59da\u575a\u3001\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\u4ed6\u4eec\u5192\u51fa\u5934\uff0c\u5728\u9526\u6e56\u5185\u90e8\uff0c\u5012\u662f\u53d8\u5f97\u975e\u5e38\u5bb9\u6613\u7406\u89e3\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u3001\u90b5\u81f3\u521a\u3001\u76db\u9752\u4ed6\u4eec\u5bf9\u6b64\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8d39\u89e3\u7684\u5730\u65b9\uff0c\u4e0d\u8981\u8bf4\u6881\u519b\u4e86\uff0c\u5c31\u7b97\u675c\u98de\u73b0\u5728\u51fa\u624b\u5c31\u662f\u62ff\u4e00\u5343\u4e07\u53bb\u5f00\u6295\u8d44\u516c\u53f8\uff0c\u7b2c\u4e00\u7b14\u6295\u8d44\u9879\u76ee\u5c31\u662f\u82b1\u516d\u767e\u4e07\u5f00\u7f51\u5427\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u53bb\u8d28\u7591\u675c\u98de\u6709\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u3002</p><p>\u3000\u3000\u6a21\u8303\u7684\u4f5c\u7528\u771f\u7684\u5f88\u91cd\u8981\u3002\u867d\u7136\u8d75\u9e4f\u3001\u59da\u575a\u7b49\u7b49\u4e00\u6279\u5e74\u8f7b\u4eba\u8d70\u4e0a\u91cd\u8981\u5c97\u4f4d\uff0c\u6709\u65f6\u5019\u56e0\u4e3a\u7ecf\u9a8c\u4e0d\u8db3\u514d\u4e0d\u4e86\u4f1a\u72af\u4e00\u4e9b\u5c0f\u8fc7\u5931\uff0c\u4f46\u662f\u4ed6\u4eec\u7ed9\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u5e26\u6765\u8fdc\u6bd4\u540c\u884c\u4f01\u4e1a\u8d85\u51fa\u592a\u591a\u7684\u6d3b\u529b\u4e0e\u62fc\u640f\u7cbe\u795e\u3002</p><p>\u3000\u3000\u5230\u4e86\u5305\u53a2\u91cc\uff0c\u90b5\u81f3\u521a\u4ed6\u4eec\u53c8\u8feb\u4e0d\u53ca\u5f85\u7684\u8ba8\u8bba\u8d77\u6765\u3002</p><p>\u3000\u3000\u6253\u52a8\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5c31\u80fd\u62ff\u51fa\u4e09\u4e2a\u4ebf\u7684\u8d44\u91d1\u51fa\u6765\uff0c\u52a0\u4e0a\u4e16\u7eaa\u9526\u6e56\u81ea\u8eab\u7684\u6d41\u52a8\u8d44\u91d1\uff0c\u5c31\u80fd\u6709\u56db\u4e2a\u591a\u4ebf\u7684\u8d44\u91d1\u53bb\u542f\u52a8\u8fd9\u4e2a\u9879\u76ee\uff0c\u5dee\u4e0d\u591a\u4e5f\u591f\u7528\u4e86\uff0c\u5173\u952e\u4ed6\u4eec\u8981\u6e38\u8bf4\u5efa\u90ba\u5e02\u4e0e\u9ad8\u65b0\u533a\u90fd\u652f\u6301\u4ed6\u4eec\u7684\u8ba1\u5212\uff0c\u8fd8\u8981\u5c06\u53ef\u80fd\u7684\u7ade\u4e89\u8005\u6392\u65a5\u5728\u5916\uff0c\u96be\u5ea6\u4e5f\u4e0d\u662f\u4e00\u822c\u7684\u5927\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u4f60\u8bf4\u4ec0\u4e48\u65f6\u673a\u627e\u738b\u7ef4\u5747\u3001\u7f57\u541b\u8c08\u8fd9\u4e9b\u4e8b\u60c5\u5408\u9002\uff1f\u201d\u521a\u5750\u4e0b\u6765\uff0c\u90b5\u81f3\u521a\u4e00\u4ed8\u865a\u5fc3\u6c42\u6559\u7684\u6837\u5b50\u95ee\u5f20\u606a\u3002</p></article>"}}}]);
