(window.webpackJsonp=window.webpackJsonp||[]).push([[72],{94:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u8282 \u8bf4\u597d\u7684\uff0c\u6211\u6765\u53d6\u4e86",content:"<article><h2>\u7b2c\u4e09\u5341\u8282 \u8bf4\u597d\u7684\uff0c\u6211\u6765\u53d6\u4e86</h2><p>\u968f\u7740\u6076\u9b54\u7684\u9000\u8d70\uff0c\u5927\u5385\u91cc\u4e00\u7247\u5bc2\u9759\uff0c\u743c\u6069\u7b80\u76f4\u53ef\u4ee5\u542c\u5230\u81ea\u5df1\u7684\u5fc3\u8df3\u548c\u547c\u5438\u3002\u4ed6\u5947\u602a\u7740\uff0c\u867d\u7136\u8482\u5a1c\u5c91\u5df2\u7ecf\u88ab\u83ab\u540d\u5176\u5999\u5730\u51fb\u6740\uff0c\u6885\u83f2\u65af\u88ab\u5b9a\u4f4f\uff0c\u4f46\u81f3\u5c11\u796d\u575b\u4e0b\u8fd8\u6709\u8fd9\u4e48\u591a\u4eba\u9762\u72ee\uff0c\u96be\u9053\u90fd\u6b7b\u5149\u4e86\u4e0d\u6210\u2026\u2026\u800c\u4e14\uff0c\u521a\u624d\u662f\u8c01\u5728\u6697\u4e2d\u63d2\u624b\uff1f</p><p>\u6076\u9b54\u9000\u8d70\u4e4b\u524d\uff0c\u4f3c\u4e4e\u53eb\u4e86\u4e00\u4e2a\u540d\u5b57\uff1a\u201c\u6b27\u51ef\u201d\u3002\u6b27\u51ef\u662f\u8c01\uff1f\u743c\u6069\u56de\u5fc6\u7740\uff0c\u6700\u7ec8\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u5468\u56f4\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u52a8\u9759\uff0c\u90a3\u4e2a\u6697\u4e2d\u63d2\u624b\u7684\u5bb6\u4f19\u6ca1\u6709\u51fa\u73b0\uff0c\u4e0d\u77e5\u9053\u662f\u8eb2\u85cf\u7740\u8fd8\u662f\u5df2\u7ecf\u9000\u8d70\u3002\u4f46\u662f\uff0c\u5982\u679c\u4ed6\u6709\u4ec0\u4e48\u56fe\u8c0b\u7684\u8bdd\uff0c\u6b64\u65f6\u5df2\u7ecf\u5c18\u57c3\u843d\u5b9a\uff0c\u5e94\u8be5\u5927\u6447\u5927\u6446\u5730\u51fa\u6765\u624d\u5bf9\u5427\u3002</p><p>\u662f\u7279\u5730\u6765\u7ed9\u81ea\u5df1\u5e2e\u5fd9\u7684\u4e48\uff1f\u743c\u6069\u53ef\u5b9e\u5728\u60f3\u4e0d\u8d77\u81ea\u5df1\u6709\u8fd9\u6837\u4e00\u4e2a\u670b\u53cb\u3002\u800c\u4e14\uff0c\u542c\u6076\u9b54\u7684\u53e3\u6c14\uff0c\u8fd9\u4e2a\u53eb\u201c\u6b27\u51ef\u201d\u7684\u5bb6\u4f19\uff0c\u4f3c\u4e4e\u8fd8\u548c\u683c\u62c9\u5179\u7279\u5f88\u719f\u8bc6\u3002</p><p>\u5230\u5e95\u662f\u8c01\uff1f</p><p>\u6e10\u6e10\u5730\uff0c\u4ed6\u6062\u590d\u4e86\u4e00\u70b9\u529b\u6c14\uff0c\u8fd9\u4e5f\u662f\u591a\u5e74\u4fee\u70bc\u5185\u529f\u7684\u597d\u5904\uff0c\u6062\u590d\u80fd\u529b\u6bd4\u8f83\u5f3a\uff0c\u6216\u8005\u8bf4\u6bd4\u8f83\u6297\u6253\u3002\u743c\u6069\u6276\u7740\u5899\u58c1\uff0c\u6447\u6447\u6643\u6643\u7ad9\u8d77\u6765\uff0c\u4ed6\u53d1\u73b0\u796d\u575b\u5468\u56f4\u7684\u4eba\u9762\u72ee\u679c\u7136\u90fd\u5df2\u7ecf\u65ad\u6c14\u8eab\u4ea1\u4e86\uff0c\u5c0f\u90e8\u5206\u662f\u88ab\u6668\u66e6\u5723\u5149\u6240\u6740\uff0c\u4f46\u5927\u90e8\u5206\u5374\u5168\u8eab\u840e\u7f29\u3001\u76ae\u8089\u53d1\u9ed1\uff0c\u5706\u7741\u7684\u773c\u775b\u91cc\u5145\u6ee1\u6050\u60e7\uff0c\u663e\u7136\u662f\u6b7b\u4e8e\u67d0\u79cd\u90aa\u6cd5\u3002\u6668\u66e6\u4e4b\u795e\u7684\u5723\u5149\u795e\u96f7\u867d\u7136\u8bdb\u6740\u90aa\u6076\uff0c\u4f46\u5374\u7edd\u4e0d\u4f1a\u662f\u8fd9\u79cd\u60c5\u5f62\u3002</p><p>\u743c\u6069\u5fae\u5fae\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u4f46\u4e8b\u60c5\u5df2\u7ecf\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u603b\u4e5f\u662f\u6bd4\u8f83\u7406\u60f3\u7684\u7ed3\u679c\u3002\u683c\u62c9\u5179\u7279\u9000\u8d70\uff0c\u4eba\u9762\u72ee\u5168\u519b\u8986\u6ca1\uff0c\u9ece\u660e\u4e4b\u77f3\u4f9d\u65e7\u5728\u796d\u575b\u4e0a\u7a7a\u60ac\u6d6e\u7740\uff0c\u81ea\u5df1\u548c\u6885\u83f2\u65af\u5b89\u7136\u65e0\u6059\u2014\u2014\u4e0d\uff0c\u4e0d\u662f\u5b89\u7136\u65e0\u6059\uff0c\u81ea\u5df1\u5dee\u70b9\u88ab\u6454\u6b7b\uff0c\u6885\u83f2\u65af\u5219\u88ab\u6076\u9b54\u7684\u6cd5\u672f\u5b9a\u4f4f\u4e86\u3002</p><p>\u5f88\u597d\u3002</p><p>\u743c\u6069\u77e5\u9053\u5b9a\u8eab\u672f\u5927\u591a\u4e5f\u662f\u6709\u65f6\u95f4\u9650\u5236\u7684\uff0c\u4e0d\u53ef\u80fd\u628a\u4e00\u4e2a\u4eba\u6c38\u8fdc\u5b9a\u4e0b\u53bb\u3002\u5982\u6885\u83f2\u65af\u8fd9\u822c\u5f3a\u608d\u7684\u5723\u6b66\u58eb\uff0c\u5bfb\u5e38\u5deb\u5e08\u7684\u5b9a\u8eab\u6cd5\u672f\u53ef\u80fd\u90fd\u672a\u5fc5\u80fd\u538b\u5236\u5f97\u4f4f\uff0c\u4f46\u65e2\u7136\u8fd9\u6b21\u7684\u65bd\u6cd5\u8005\u662f\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\uff0c\u90a3\u4e48\u4f30\u8ba1\u4e00\u65f6\u534a\u523b\uff0c\u5979\u662f\u4e0d\u4f1a\u6062\u590d\u884c\u52a8\u80fd\u529b\u4e86\u3002</p><p>\u90a3\u4e48\uff0c\u662f\u4e0d\u662f\u610f\u5473\u7740\u6b64\u65f6\u743c\u6069\u53ef\u4ee5\u4e3a\u6240\u6b32\u4e3a\u4e86\u5462\u3002</p><p>\u788d\u4e8b\u7684\u4eba\u90fd\u8d70\u4e86\uff0c\u6216\u8005\u6b7b\u4e86\uff0c\u6216\u8005\u81f3\u5c11\u6682\u65f6\u6ca1\u6709\u51fa\u6765\u6253\u6270\u7684\u610f\u601d\u2026\u2026\u4e0d\uff0c\u8fc7\u4e86\u8fd9\u4e48\u4e45\u90fd\u8fd8\u6ca1\u52a8\u9759\uff0c\u90a3\u4e2a\u6697\u4e2d\u7684\u5bb6\u4f19\u5e94\u8be5\u5df2\u7ecf\u79bb\u5f00\u4e86\u5427\u3002\u89ca\u89ce\u5df2\u4e45\u7684\u5bf9\u8c61\u6b64\u65f6\u6b63\u5b8c\u5168\u4e27\u5931\u53cd\u6297\u80fd\u529b\u2014\u2014\u66f4\u4ee4\u4eba\u9ad8\u5174\u7684\uff0c\u662f\u5979\u7684\u94f6\u7532\u5728\u6b64\u524d\u5df2\u7ecf\u81ea\u5df1\u8131\u4e0b\u6765\u4e86\uff0c\u73b0\u5728\u6b63\u5728\u743c\u6069\u7684\u6b21\u5143\u888b\u91cc\u3002\u5426\u5219\u90a3\u4ef6\u94f6\u7532\u9887\u4e3a\u590d\u6742\uff0c\u4eff\u4f5b\u4e25\u4e1d\u5408\u7f1d\uff0c\u8fde\u4e3a\u4e00\u4f53\uff0c\u743c\u6069\u8fd8\u771f\u4e0d\u77e5\u9053\u600e\u6837\u624d\u80fd\u8131\u4e0b\u6765\u3002</p><p>\u4ed6\u9760\u7740\u5899\u7ad9\u7acb\u7740\uff0c\u4f11\u606f\u7740\uff0c\u5c3d\u5feb\u6062\u590d\u4f53\u529b\uff0c\u540c\u65f6\u53ec\u56de\u9f99\u9cde\u76fe\u5728\u8eab\u4fa7\u6e38\u79fb\u7740\uff0c\u9632\u6b62\u6709\u4ec0\u4e48\u7a81\u7136\u88ad\u51fb\u3002\u5176\u5b9e\u743c\u6069\u5e76\u6ca1\u600e\u4e48\u53d7\u4f24\uff0c\u4ed6\u53ea\u662f\u88ab\u8482\u5a1c\u5c91\u86c7\u5c3e\u626b\u4e2d\uff0c\u53c8\u6709\u9f99\u9cde\u76fe\u3001\u9632\u5fa1\u80f8\u9488\u4e8b\u5148\u6321\u4e86\u4e00\u6321\uff0c\u771f\u843d\u5230\u4ed6\u8eab\u4e0a\u7684\u51b2\u51fb\u529b\u5df2\u7ecf\u4e0d\u8db3\u4e24\u6210\uff0c\u5e76\u4e0d\u66fe\u4f24\u7b4b\u52a8\u9aa8\u3002</p><p>\u796d\u575b\u4e0b\u904d\u5730\u662f\u4eba\u9762\u72ee\u7684\u5c38\u4f53\uff0c\u796d\u575b\u4e0a\u662f\u4e00\u52a8\u4e0d\u52a8\u88ab\u5b9a\u4f4f\u7684\u6885\u83f2\u65af\uff0c\u8bfa\u5927\u4e00\u5ea7\u795e\u6bbf\u4e2d\uff0c\u4f3c\u4e4e\u53ea\u5269\u4e0b\u743c\u6069\u4e00\u4e2a\u4eba\u6709\u6d3b\u52a8\u80fd\u529b\u4e86\u3002</p><p>\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u6d41\u901d\uff0c\u743c\u6069\u7f13\u7f13\u547c\u5438\u7740\uff0c\u8fd0\u4f7f\u7740\u4ed6\u90a3\u70b9\u5fae\u8584\u5f97\u51e0\u4e4e\u65e0\u6cd5\u5bdf\u89c9\u7684\u771f\u6c14\u5728\u4f53\u5185\u6e38\u8d70\uff0c\u9a71\u6563\u75bc\u75db\u3002\u6700\u7ec8\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u5927\u7ea6\u6062\u590d\u4e86\u4e03\u516b\u6210\u7684\u4f53\u529b\u3002\u743c\u6069\u638f\u51fa\u9774\u5b50\u91cc\u7684\u5315\u9996\uff0c\u68c0\u67e5\u7740\u5730\u4e0a\u7684\u4eba\u9762\u72ee\uff0c\u51c6\u5907\u4e07\u4e00\u8c01\u8981\u6ca1\u6b7b\u900f\uff0c\u5c31\u518d\u7ed9\u5b83\u8865\u4e0a\u4e00\u4e0b\u3002\u4e0d\u8fc7\u4e8b\u5b9e\u8bc1\u660e\u4e0d\u9700\u8981\u4e86\uff0c\u6240\u6709\u7684\u4eba\u9762\u72ee\u90fd\u6b7b\u5f97\u4e0d\u80fd\u518d\u6b7b\u4e86\u3002</p><p>\u4ed6\u8d34\u7740\u5899\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8d70\u51fa\u5927\u5385\uff0c\u641c\u5bfb\u7740\u795e\u6bbf\uff0c\u770b\u8fd8\u6709\u6ca1\u6709\u6f0f\u7f51\u4e4b\u9c7c\u3002\u4ed6\u6ca1\u6709\u770b\u89c1\u534a\u4e2a\u6d3b\u7740\u7684\u4eba\u9762\u72ee\uff0c\u5012\u662f\u5728\u540e\u6bbf\u53d1\u73b0\u4e86\u4e00\u4e2a\u5c0f\u623f\u95f4\u3002\u94c1\u94f8\u7684\u95e8\u9501\u7740\uff0c\u743c\u6069\u6253\u4e0d\u5f00\uff0c\u4ed6\u4e5f\u6ca1\u51c6\u5907\u53ef\u4ee5\u5f00\u9501\u7684\u6572\u51fb\u672f\uff0c\u53ea\u597d\u5148\u4e0d\u7ba1\u5b83\u3002\u4f46\u4ece\u95e8\u7f1d\u91cc\u9690\u7ea6\u900f\u51fa\u7684\u7075\u5149\u5224\u65ad\uff0c\u4ed6\u6000\u7591\u91cc\u9762\u4e00\u5b9a\u50a8\u85cf\u7740\u4e00\u4e9b\u9b54\u6cd5\u7269\u54c1\u3002</p><p>\u5f85\u4f1a\u518d\u56de\u6765\u770b\u770b\u3002</p><p>\u4ed6\u56de\u5230\u5927\u5385\uff0c\u6885\u83f2\u65af\u4f9d\u65e7\u5b9a\u5b9a\u5730\u7ad9\u5728\u796d\u575b\u4e0a\uff0c\u5468\u56f4\u4e00\u4e2a\u6d3b\u4eba\u90fd\u6ca1\u6709\u3002\u4e8e\u662f\u4ed6\u5c0f\u5fc3\u5730\u7a7f\u8fc7\u4eba\u9762\u72ee\u4eec\u7684\u5c38\u4f53\uff0c\u8d70\u4e0a\u796d\u575b\uff0c\u6765\u5230\u6885\u83f2\u65af\u5bf9\u9762\u3002</p><p>\u5973\u5723\u6b66\u58eb\u9759\u9759\u5730\u770b\u7740\u4ed6\u3002</p><p>\u5979\u4f3c\u4e4e\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u81f3\u5c11\u5728\u88ab\u6076\u9b54\u5b9a\u4f4f\u4e4b\u540e\u4e00\u76f4\u6ca1\u53d1\u51fa\u58f0\u97f3\u3002\u666e\u901a\u7684\u5b9a\u8eab\u672f\u662f\u505a\u4e0d\u5230\u8fd9\u70b9\u7684\uff0c\u6076\u9b54\u4f7f\u7528\u7684\u663e\u7136\u662f\u9ad8\u7ea7\u7248\u672c\u3002\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u5927\u6982\u662f\u4ec0\u4e48\u6cd5\u672f\uff0c\u50cf\u683c\u62c9\u5179\u7279\u8fd9\u79cd\u5f3a\u5927\u6076\u9b54\uff0c\u5b8c\u5168\u6709\u80fd\u529b\u81ea\u521b\u9b54\u6cd5\u2014\u2014\u800c\u4e14\u4e5f\u7ecf\u5e38\u8fd9\u4e48\u505a\uff0c\u8fd9\u4e9b\u6cd5\u672f\u4e00\u822c\u90fd\u4e0d\u4f1a\u51fa\u73b0\u5728\u51e1\u4eba\u7684\u8bb0\u8f7d\u4e2d\uff0c\u53ea\u6709\u4ed6\u4eec\u81ea\u5df1\u77e5\u9053\u3002</p><p>\u597d\u5427\uff0c\u7f8e\u8272\u5f53\u524d\uff0c\u4e0d\u8981\u60f3\u8fd9\u4e48\u65e0\u804a\u7684\u4e8b\u60c5\u3002</p><p>\u4ed6\u5fae\u7b11\u7740\uff0c\u6253\u91cf\u7740\u6885\u83f2\u65af\u3002\u5973\u5723\u6b66\u58eb\u7684\u8138\u4e0a\u4f9d\u65e7\u5e73\u9759\uff0c\u5979\u4eff\u4f5b\u5728\u4efb\u4f55\u65f6\u5019\u90fd\u662f\u8fd9\u6837\u9547\u5b9a\u81ea\u82e5\uff0c\u5373\u4fbf\u660e\u660e\u5df2\u7ecf\u8eab\u9677\u7edd\u5883\u3002\u743c\u6069\u5f88\u6000\u7591\u8fd9\u662f\u4e00\u79cd\u4f2a\u88c5\uff0c\u6216\u8005\u8bf4\u662f\u4e00\u79cd\u865a\u5f20\u58f0\u52bf\uff0c\u4e0d\u8fc7\u5766\u767d\u5730\u8bf4\uff0c\u5c31\u7b97\u662f\u5982\u6b64\uff0c\u8fd9\u4e5f\u662f\u5f88\u4e86\u4e0d\u8d77\u7684\u4e8b\u60c5\uff0c\u81f3\u5c11\u743c\u6069\u6000\u7591\u81ea\u5df1\u5c31\u505a\u4e0d\u5230\uff0c\u81f3\u5c11\u6ca1\u6cd5\u505a\u5f97\u8fd9\u4e48\u597d\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u4e16\u754c\u4e0a\u80fd\u5904\u53d8\u4e0d\u60ca\u7684\u4eba\u5f88\u591a\uff0c\u89c6\u751f\u6b7b\u4e3a\u7b49\u95f2\u7684\u4eba\u4e5f\u4e0d\u5c11\uff0c\u743c\u6069\u81ea\u5df1\u867d\u7136\u505a\u4e0d\u5230\uff0c\u4f46\u5e76\u4e0d\u6000\u7591\u6709\u4eba\u80fd\u505a\u5230\u3002\u53ea\u662f\u2026\u2026\u6885\u83f2\u65af\u662f\u4e2a\u5e74\u8f7b\u7684\u5973\u5b69\uff0c\u800c\u4e14\u5e94\u8be5\u8fd8\u662f\u5904\u5973\u5427\uff0c\u4e0d\u77e5\u9053\u5979\u5728\u88ab\u5f3a\u5978\u7684\u65f6\u5019\uff0c\u4f1a\u4e0d\u4f1a\u4f9d\u7136\u8fd9\u6837\u9547\u5b9a\u4ece\u5bb9\u5462\uff1f</p><p>\u743c\u6069\u7b11\u7740\uff0c\u4f38\u624b\u629a\u6478\u6885\u83f2\u65af\u7684\u8138\u86cb\u3002\u867d\u7136\u957f\u5e74\u5728\u5916\u5954\u6ce2\uff0c\u98ce\u5439\u65e5\u6652\uff0c\u4f46\u5c11\u5973\u7684\u76ae\u80a4\u5374\u975e\u5e38\u7ec6\u5ae9\u67d4\u6ed1\uff0c\u5168\u65e0\u534a\u70b9\u7c97\u7cd9\u4e4b\u611f\uff0c\u6478\u8d77\u6765\u624b\u611f\u975e\u5e38\u597d\u3002\u53ea\u80fd\u8bf4\uff0c\u9752\u6625\u679c\u7136\u662f\u65e0\u654c\u7684\u3002</p><p>\u4ed6\u7684\u8eab\u4f53\u6e10\u6e10\u8d34\u5f97\u66f4\u8fd1\uff0c\u51c6\u5907\u53bb\u4eb2\u543b\u5979\uff0c\u4e00\u8fb9\u89c2\u5bdf\u7740\u5c11\u5973\u7684\u53cd\u5e94\u3002\u6885\u83f2\u65af\u7684\u8138\u4e0a\u4f9d\u65e7\u6ca1\u6709\u8868\u60c5\uff0c\u773c\u795e\u4e2d\u5374\u95ea\u8fc7\u4e00\u4e1d\u60ca\u614c\uff0c\u8fd9\u8ba9\u743c\u6069\u5728\u5fc3\u91cc\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u679c\u7136\uff0c\u7eb5\u7136\u662f\u518d\u575a\u6bc5\u518d\u521a\u5f3a\u7684\u5973\u5b50\uff0c\u9762\u5bf9\u7537\u4eba\u7684\u4fb5\u72af\uff0c\u4e5f\u8fd8\u662f\u4f1a\u6050\u60e7\u7684\u3002</p><p>\u743c\u6069\u6ee1\u610f\u5730\u543b\u7740\u5c11\u5973\u7684\u8138\uff0c\u8f7b\u8f7b\u54ac\u556e\u7740\u5979\u5706\u6da6\u7684\u8033\u5782\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\uff1a\u201c\u6211\u5e2e\u4f60\u62ff\u56de\u9ece\u660e\u4e4b\u77f3\uff0c\u4f60\u5c31\u662f\u6211\u7684\u2014\u2014\u6211\u4eec\u8bf4\u597d\u7684\uff0c\u73b0\u5728\u6211\u8981\u6765\u53d6\u4e86\u3002\u201d</p></article>"}}}]);
