(window.webpackJsonp=window.webpackJsonp||[]).push([[724],{747:function(p){p.exports={chapterTitle:null,title:"\u7b2c563\u7ae0 \u654c\u76df",content:"<article><h2>\u7b2c563\u7ae0 \u654c\u76df</h2><p>\u3000\u3000\u8c22\u5251\u5357\u542c\u6797\u96ea\u4f3c\u55d4\u975e\u55d4\u7684\u62b1\u6028\u7740\uff0c\u5a9a\u6001\u6d41\u9732\u7684\u773c\u7738\u91cc\u95ea\u8fc7\u96be\u4ee5\u81ea\u6291\u7684\u4e0d\u60a6\u795e\u8272\uff0c\u4ed6\u95ee\u9ad8\u771f\uff1a\u201c\u6211\u8bf4\u9519\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u9ad8\u771f\u5634\u5507\u62bf\u4e86\u4e00\u4e0b\uff0c\u9732\u51fa\u4e00\u4e2a\u4e0d\u4ee5\u4e3a\u610f\u7684\u3001\u5634\u89d2\u62bd\u52a8\u7684\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u716e\u719f\u7684\u9e2d\u5b50\u98de\u4e86\uff0c\u6797\u603b\u81ea\u7136\u4e0d\u5f00\u5fc3\u4e86\uff1b\u6211\u662f\u597d\u4e0d\u5bb9\u6613\u624d\u5c06\u5979\u54c4\u5f00\u5fc3\uff0c\u4f60\u4e00\u6765\u53c8\u8bf4\u5230\u5979\u7684\u75db\u5904\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u7709\u5934\u5fae\u8e59\uff0c\u9ad8\u771f\u8fd9\u8bdd\u6253\u60c5\u9a82\u4fcf\u4f3c\u7684\u5f88\u662f\u8f7b\u6d6e\uff0c\u4e5f\u4e0d\u63a9\u9970\u4e0e\u8fd9\u5973\u4eba\u7684\u4eb2\u6635\u5173\u7cfb\uff0c\u5979\u8d70\u5230\u4f4d\u5b50\u4e0a\u5750\u4e0b\uff0c\u5305\u53a2\u91cc\u7a7a\u8c03\u6e29\u6696\u6253\u5f97\u9ad8\uff0c\u5979\u5c06\u5916\u5957\u8131\u4e0b\u6401\u5728\u819d\u76d6\u4e0a\uff0c\u62c9\u7740\u5f20\u6905\u5b50\u8ba9\u8c22\u5251\u5357\u5750\u81ea\u5df1\u8eab\u8fb9\uff0c\u6ca1\u6709\u8bf4\u8bdd\u5e94\u4ed8\u522b\u4eba\u7684\u610f\u601d\uff0c\u5168\u770b\u8fd9\u5973\u4eba\u81ea\u5df1\u5982\u4f55\u8868\u6f14\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u62c9\u7740\u8c22\u6c49\u9756\u5750\u81ea\u5df1\u8eab\u8fb9\uff0c\u8ddf\u4ed6\u89e3\u91ca\u9053\uff1a\u201c\u5efa\u90ba\u5e02\u91cc\u53ec\u5f00\u5e38\u59d4\u4f1a\u8bae\u8ba8\u8bba\u53d1\u5c55\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u65b0\u6218\u7565\uff0c\u4e0b\u5348\uff0c\u7701\u91cc\u674e\u7701\u957f\u4e3b\u6301\u53ec\u5f00\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\uff0c\u8ba8\u8bba\u7684\u662f\u4e1c\u6d77\u7701\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u65b0\u6218\u7565\u7684\u95ee\u9898\uff0c\u7701\u5e02\u4e24\u4e2a\u65b0\u6218\u7565\uff0c\u90fd\u662f\u8981\u4f9d\u6258\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u624d\u80fd\u5b9e\u884c\u4e0b\u53bb\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\u5468\u747e\u73ba\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u8ba9\u8c22\u6c49\u9756\u4ed6\u4eec\u5148\u6d88\u5316\u8fd9\u4e48\u6d88\u606f\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8fd9\u5012\u662f\u6ca1\u6709\u4ec0\u4e48\uff0c\u5efa\u90ba\u5e02\u59d4\u4e0a\u5348\u7684\u5e38\u59d4\u4f1a\u8bae\u4e0a\uff0c\u7f57\u541b\u70b9\u540d\u6279\u8bc4\u80e1\u5b97\u5e86\uff0c\u4f1a\u8bae\u4e0a\u8fd8\u8ba8\u8bba\u7740\u8981\u5c06\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u5e76\u5165\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5c06\u4e4b\u524d\u7684\u5b66\u5e9c\u5df7\u6539\u9020\u65b9\u6848\u90fd\u63a8\u7ffb\u6389\u4e86\uff0c\u4f60\u8bf4\u9526\u6e56\u7684\u624b\u662f\u4e0d\u662f\u4f38\u592a\u957f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5468\u747e\u73ba\u540e\u9762\u7684\u8bdd\u4e0d\u8fc7\u662f\u8981\u6fc0\u8d77\u6797\u96ea\u7684\u540c\u4ec7\u654c\u5ffe\uff0c\u5b66\u5e9c\u5df7\u6539\u9020\u62d6\u4e86\u5f88\u4e45\uff0c\u5c31\u6ca1\u6709\u786e\u5b9a\u7684\u65b9\u6848\uff0c\u4e5f\u5c31\u6ca1\u6709\u63a8\u7ffb\u4e00\u8bf4\uff1b\u8c22\u6c49\u9756\u53ea\u5173\u5fc3\u4ed6\u7684\u524d\u534a\u6bb5\u8bdd\uff0c\u542c\u5230\u90a3\u91cc\uff0c\u5fc3\u795e\u4e00\u51db\uff0c\u6c57\u6bdb\u90fd\u7ad6\u4e86\u8d77\u6765\uff1a\u4e1c\u6d77\u7701\u3001\u5efa\u90ba\u5e02\u8981\u5b9e\u884c\u4ec0\u4e48\u65b0\u6218\u7565\u8fd8\u5fc5\u987b\u4f9d\u6258\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5c82\u4e0d\u662f\u8bf4\u9526\u6e56\u5728\u4e1c\u6d77\u7701\u5185\u7684\u5730\u4f4d\u5c06\u65e0\u4eba\u80fd\u64bc\u52a8\uff1f\u5f90\u5b66\u5e73\u7684\u5931\u52bf\u6ca1\u6709\u6253\u538b\u4f4f\u9526\u6e56\u7684\u56a3\u5f20\u6c14\u7130\uff0c\u53cd\u800c\u8ba9\u4ed6\u4eec\u7684\u6c14\u7130\u8d8a\u70e7\u8d8a\u65fa\u4e86\u3002</p><p>\u3000\u3000\u90a3\u8fb9\uff0c\u8c22\u5251\u5357\u6b63\u601d\u5fd6\u7740\u8981\u600e\u4e48\u773c\u524d\u8fd9\u4f4d\u86c7\u874e\u5973\u4eba\u7684\u5634\u91cc\u591a\u5957\u4e9b\u6d88\u606f\uff0c\u542c\u4ed6\u8205\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5dee\u70b9\u5c06\u684c\u4e0a\u7684\u8336\u76c5\u78b0\u7ffb\uff0c\u53ee\u94db\u4e71\u54cd\uff0c\u8336\u76c5\u6253\u7740\u98a4\u9669\u4e9b\u4ece\u676f\u6258\u4e0a\u6389\u5230\u5730\u4e0a\u6253\u788e\uff1b\u5413\u4e86\u5927\u5bb6\u4e00\u8df3\u3002</p><p>\u3000\u3000\u6628\u5929\u674e\u8fdc\u6e56\u4e0e\u7f57\u541b\u5728\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u5171\u540c\u63a5\u5f85\u5f20\u606a\u3001\u53f6\u5efa\u658c\uff0c\u96be\u9053\u5c31\u662f\u8ba8\u8bba\u8fd9\u4e2a\uff1f\u8c22\u5251\u5357\u5fc3\u91cc\u60f3\uff0c\u7684\u786e\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u6709\u8fd9\u4e48\u5927\u7684\u683c\u5c40\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\uff1f\u201d\u8c22\u77bb\u6c89\u4e0d\u4f4f\u6c14\uff0c\u95ee\u9053\uff0c\u201c\u96be\u9053\u674e\u7701\u957f\u80fd\u591f\u5fcd\u53d7\u90a3\u4e2a\u6bdb\u90fd\u6ca1\u6709\u957f\u9f50\u7684\u5c0f\u5b50\u5728\u90a3\u4ed6\u9762\u524d\u56a3\u5f20\u8dcb\u6248\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u8bf4\u8bdd\u7684\uff1f\u201d\u8c22\u6c49\u9756\u5acc\u6076\u7684\u77a5\u4e86\u8c22\u77bb\u4e00\u773c\uff0c\u5f53\u521d\u5c31\u662f\u5468\u747e\u73ba\u5229\u7528\u8c22\u77bb\u5c06\u6574\u4e2a\u6b63\u6cf0\u96c6\u56e2\u90fd\u7ed1\u4e0a\u4ed6\u7684\u6218\u8f66\uff0c\u4f7f\u5f97\u6b63\u6cf0\u96c6\u56e2\u5fc5\u987b\u4e00\u6761\u9053\u8d70\u5230\u9ed1\uff0c\u4e8b\u60c5\u65e2\u7136\u5230\u8fd9\u4e00\u6b65\uff0c\u6028\u6068\u5468\u747e\u73ba\u3001\u4e0e\u5468\u5bb6\u95f9\u7ffb\u90fd\u65e0\u76ca\u5904\uff0c\u4f46\u662f\u8fd8\u8f6e\u4e0d\u5230\u8c22\u77bb\u592a\u653e\u8086\uff0c\u8bad\u65a5\u4e86\u4e00\u58f0\uff0c\u8ba9\u4ed6\u6ce8\u610f\u6536\u655b\uff0c\u201c\u674e\u7701\u957f\u90a3\u662f\u4e3a\u5168\u7701\u7ecf\u6d4e\u5927\u5c40\u8003\u8651\uff0c\u4ec0\u4e48\u5fcd\u53d7\u4e0d\u5fcd\u53d7\u7684\uff1f\u201d</p><p>\u3000\u3000\u9ad8\u771f\u54c8\u54c8\u4e00\u7b11\uff0c\u4e5f\u4e0d\u4ecb\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5e02\u91cc\u7684\u60c5\u51b5\uff0c\u8981\u4e0d\u662f\u6797\u603b\u8ddf\u6211\u900f\u9732\u6d88\u606f\uff0c\u6211\u8fd8\u641e\u4e0d\u592a\u6e05\u695a\u2026\u2026\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5f88\u6295\u7f57\u541b\u7684\u80c3\u53e3\uff0c\u4e0a\u5348\u7684\u4f1a\u8bae\u4e0a\uff0c\u80e1\u5b97\u5e86\u7ed9\u62ff\u51fa\u6765\u70b9\u540d\u6279\u8bc4\u4e86\uff0c\u8096\u660e\u5efa\u989c\u9762\u4e5f\u65e0\u5149\uff1b\u7f57\u541b\u521d\u6765\u4e4d\u5230\uff0c\u4e00\u76f4\u627e\u4e0d\u5230\u673a\u4f1a\u653e\u6325\u4ed6\u7684\u5a01\u98ce\uff0c\u597d\u4e0d\u5bb9\u6613\u902e\u5230\u673a\u4f1a\uff0c\u504f\u5de7\u738b\u7ef4\u5747\u5728\u65c1\u8fb9\u4e5f\u5e2e\u4ed6\u6447\u65d7\u5450\u558a\u3002\u5176\u4ed6\u5e38\u59d4\u4e0d\u5206\u7ba1\u7ecf\u6d4e\u4e0a\u7684\u4e8b\u60c5\uff0c\u66f4\u4e0d\u4f1a\u8fd9\u65f6\u5019\u9009\u62e9\u8ddf\u7f57\u541b\u5531\u5bf9\u53f0\u620f\uff0c\u52a0\u4e0a\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u539f\u5148\u662f\u80e1\u5b97\u5e86\u76ef\u5f97\u7d27\u7684\uff0c\u6709\u4e9b\u4eba\u6697\u5730\u91cc\u4e0d\u670d\u6c14\uff0c\u5b81\u53ef\u4e00\u8d77\u7ed1\u5230\u6a61\u6811\u56ed\u8ba1\u5212\u91cc\u53bb\u2026\u2026\u201d\u9ad8\u771f\u4e5f\u4e0d\u4ecb\u610f\u5728\u8bdd\u91cc\u6cc4\u9732\u6797\u96ea\u5728\u5efa\u90ba\u5e02\u91cc\u7684\u7ebf\u662f\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\uff0c\u66f4\u662f\u76f4\u63a5\u5c06\u5efa\u90ba\u5e02\u91cc\u7684\u77db\u76fe\u544a\u8bc9\u8c22\u6c49\u9756\u7b49\u4eba\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u542c\u5fc3\u91cc\u6697\u81ea\u601d\u91cf\uff1b\u9648\u9759\u5374\u5728\u7422\u78e8\u5750\u5728\u5979\u53f3\u4e0a\u89d2\u7684\u6797\u96ea\uff1a\u4e8c\u5341\u4e03\u516b\u5c81\u7684\u6f02\u4eae\u5973\u4eba\uff0c\u62e5\u6709\u89c4\u6a21\u9887\u5927\u7684\u5730\u4ea7\u516c\u53f8\uff0c\u5012\u4e0d\u6653\u5f97\u5979\u5982\u6b64\u5728\u7fa4\u9cc4\u73af\u4f3a\u7684\u5546\u573a\u53ae\u6df7\u7684\uff0c\u6216\u8bb8\u5979\u672c\u4eba\u5c31\u662f\u4e00\u5934\u5de8\u9cc4\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u987a\u7740\u9ad8\u771f\u7684\u53e3\u6c14\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u9526\u6e56\u5012\u662f\u4f1a\u5360\u4fbf\u5b9c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u901a\u8fc7\u738b\u7ef4\u5747\u5411\u5efa\u90ba\u5e02\u627f\u8bfa\u5b66\u5e9c\u5df7\u5546\u94fa\u62c6\u8fc1\u91c7\u53d6\u56de\u8fc1\u7684\u8865\u507f\u65b9\u5f0f\uff0c\u5c06\u5176\u4ed6\u4eba\u7684\u5634\u7ed9\u5835\u4e0a\u4e86\u2026\u2026\u201d\u9ad8\u771f\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\uff0c\u9526\u6e56\u4e5f\u786e\u5b9e\u53ef\u6076\uff0c\u4ed6\u60f3\u53d1\u8d22\u53ef\u4ee5\uff0c\u62a2\u522b\u4eba\u8d22\u8def\u7684\u4e8b\u60c5\uff0c\u6574\u5929\u90fd\u6709\u4eba\u5728\u505a\uff0c\u4e5f\u4e0d\u5dee\u4ed6\u4eec\u4e00\u5bb6\uff0c\u4f46\u662f\u4ed6\u4eec\u8fd9\u4e48\u505a\u5c31\u6709\u4e9b\u4e0d\u5730\u9053\u4e86\u2026\u2026\u201d\u9648\u9759\u4e4d\u7136\u6ca1\u6709\u542c\u61c2\u9526\u6e56\u54ea\u91cc\u4e0d\u5730\u9053\uff0c\u773c\u5e18\u62ac\u8d77\uff0c\u770b\u4e86\u4e00\u773c\u5750\u5728\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u8eab\u8fb9\u3001\u4e3a\u90a3\u4e2a\u5973\u4eba\u6253\u62b1\u4e0d\u5e73\u7684\u9ad8\u771f\uff0c\u542c\u4ed6\u7ee7\u7eed\u8bf4\u9053\u4e0b\uff0c\u201c\u6709\u4e86\u8fd9\u4e2a\u5148\u4f8b\uff0c\u5c82\u4e0d\u662f\u8bf4\u4ee5\u540e\u7684\u65e7\u57ce\u6539\u9020\uff0c\u6d89\u53ca\u5230\u5546\u94fa\u62c6\u8fc1\u90fd\u8981\u91c7\u53d6\u56de\u8fc1\u8865\u507f\u7684\u65b9\u5f0f\uff1f\u8981\u90fd\u8fd9\u6837\uff0c\u90a3\u4ee5\u540e\u8fd8\u6709\u5f00\u53d1\u5546\u8c01\u8fd8\u613f\u610f\u5230\u5efa\u90ba\u5e02\u6765\u5f00\u53d1\u5417\uff1f\u5efa\u90ba\u5e02\u7684\u57ce\u5e02\u5efa\u8bbe\u8fd8\u8981\u4e0d\u8981\u7ee7\u7eed\u4e0b\u53bb\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4f3c\u4e4e\u662f\u6307\u8d23\u9526\u6e56\u7834\u574f\u5b98\u4e0e\u5546\u4e4b\u95f4\u7684\u6f5c\u89c4\u5219\uff1f</p><p>\u3000\u3000\u9648\u9759\u626d\u5934\u770b\u4e86\u770b\u8eab\u8fb9\u7684\u8c22\u5251\u5357\uff0c\u4ed6\u8138\u4e0a\u4e5f\u6ca1\u6709\u5f02\u8272\uff0c\u4ee5\u81ea\u5df1\u5bf9\u4ed6\u7684\u4e86\u89e3\uff0c\u4ed6\u82e5\u662f\u5fc3\u91cc\u4e0d\u4ee5\u4e3a\u7136\uff0c\u53cd\u800c\u4f1a\u6709\u9644\u548c\u7684\u8868\u60c5\uff0c\u8fd9\u65f6\u5374\u592a\u5e73\u9759\u4e86\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u9526\u6e56\u6709\u4e9b\u9f83\u9f89\uff0c\u5012\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\uff0c\u4eca\u5929\u4f3c\u4e4e\u5e94\u8be5\u8bf7\u80e1\u5e02\u957f\u4e00\u8d77\u8fc7\u6765\u559d\u9152\u2026\u2026\u201d</p><p>\u3000\u3000\u9ad8\u771f\u7b11\u7740\u8bf4\uff1a\u201c\u4ee5\u540e\u6709\u7684\u662f\u673a\u4f1a\uff0c\u80e1\u5e02\u957f\u4e5f\u662f\u559c\u6b22\u4ea4\u670b\u53cb\u7684\u4eba\uff0c\u6211\u5e2e\u4f60\u4eec\u5f15\u8350\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0b\u5348\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u7684\u60c5\u51b5\u5982\u4f55\u554a\uff1f\u201d\u8c22\u6c49\u9756\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u7701\u91cc\u7684\u60c5\u51b5\u8981\u590d\u6742\u4e00\u4e9b\u2026\u2026\u867d\u7136\u6c11\u8425\u7ecf\u6d4e\u5730\u4f4d\u8ba8\u8bba\u5f97\u70ed\u706b\u671d\u5929\uff0c\u4f46\u662f\u7701\u91cc\u786e\u5b9a\u4ea7\u4e1a\u53d1\u5c55\u7684\u65b0\u6218\u7565\u8ba9\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u7275\u5934\uff0c\u6709\u4e9b\u4eba\u662f\u60f3\u4e0d\u901a\u7684\u2026\u2026\u201d\u9ad8\u771f\u8bf4\u9053\uff0c\u201c\u7f57\u541b\u5728\u5efa\u90ba\u5e02\u91cc\u5927\u5f20\u65d7\u9f13\uff0c\u6240\u8c13\u7684\u6570\u5b57\u8d70\u5eca\u8ba1\u5212\u5df2\u7ecf\u8ba9\u7f57\u541b\u53eb\u5f97\u53ee\u5f53\u7684\u54cd\uff0c\u7701\u91cc\u591a\u534a\u8981\u505a\u51fa\u53cd\u5e94\u2026\u2026\u201d</p><p>\u3000\u3000\u9ad8\u771f\u8fd9\u6761\u7ebf\u662f\u8c22\u6c49\u9756\u3001\u8c22\u610f\u4eb2\u81ea\u6293\u7684\uff0c\u8c22\u77bb\u5728\u8fd9\u6761\u7ebf\u4e0a\u4e0b\u4e86\u591a\u5c11\u672c\u94b1\uff0c\u89c1\u9ad8\u771f\u8fd8\u771f\u662f\u77e5\u65e0\u4e0d\u8a00\u3001\u8a00\u65e0\u4e0d\u5c3d\uff0c\u8fd8\u5c06\u7cbe\u5178\u5730\u4ea7\u7684\u8fd9\u4f4d\u7f8e\u5973\u8001\u603b\u5f15\u8350\u8fc7\u6765\uff0c\u672c\u94b1\u5012\u662f\u6ca1\u6709\u767d\u4e0b\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u6df1\u4eba\u9759\uff0c\u6696\u9999\u5c71\u5e84\u7684\u5ba2\u623f\u5927\u90fd\u7184\u4e86\u706f\uff0c\u4e09\u697c\u4e1c\u5317\u89d2\u7684\u623f\u95f4\u8fd8\u706f\u706b\u901a\u660e\uff0c\u8c22\u6c49\u9756\u3001\u5468\u747e\u73ba\u3001\u8c22\u610f\u3001\u8c22\u5251\u5357\u3001\u8c22\u77bb\u4e94\u4e2a\u4eba\u5750\u5728\u7a97\u524d\u7684\u6c99\u53d1\u4e0a\uff0c\u4e94\u4eba\u4f3c\u4e4e\u90fd\u4fdd\u6301\u7740\u4e00\u6837\u7684\u59ff\u52bf\uff0c\u624b\u90fd\u6401\u5728\u5f27\u5f62\u6c99\u53d1\u7684\u6276\u624b\u4e0a\uff1b\u9648\u9759\u65e9\u65e9\u5c31\u7761\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u8f6c\u8fc7\u5934\uff0c\u4ece\u540e\u9762\u89d2\u53f0\u4e0a\u5c06\u70df\u7070\u7f38\u62ff\u8fc7\u6765\uff0c\u4f38\u624b\u5f80\u91cc\u7684\u53e3\u888b\u91cc\u638f\u4e86\u8bb8\u4e45\uff0c\u8c22\u610f\u5c06\u9999\u70df\u3001\u6253\u706b\u673a\u9012\u7ed9\u4ed6\uff0c\u95ee\u4ed6\uff1a\u201c\u7238\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u53c8\u5f00\u59cb\u62bd\u70df\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8fd8\u6ca1\u6709\u62bd\u5417\uff1f\u201d\u8c22\u6c49\u9756\u53d6\u51fa\u4e00\u652f\u70df\u70b9\u4e0a\uff0c\u5438\u4e86\u4e00\u53e3\uff0c\u8bf4\u9053\uff0c\u201c\u7701\u5e02\u4e24\u7ea7\u9ad8\u5c42\u8ba8\u8bba\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u7684\u601d\u8def\u65e0\u4e00\u4e0d\u662f\u4f9d\u6258\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u9526\u6e56\u8fd8\u771f\u662f\u9510\u4e0d\u53ef\u5f53\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5176\u5b9e\u672a\u5fc5\u554a\uff0c\u201d\u8c22\u5251\u5357\u8bf4\u9053\uff0c\u201c\u5efa\u90ba\u5e02\u91cc\uff0c\u5e94\u8be5\u662f\u6709\u77db\u76fe\u7684\uff0c\u9ad8\u771f\u6709\u4e9b\u8bdd\u6ca1\u6709\u8bf4\u900f\uff0c\u4f46\u4e5f\u4e0d\u96be\u660e\u767d\uff0c\u9ad8\u771f\u5728\u6697\u793a\u674e\u8fdc\u6e56\u662f\u7ed9\u7f57\u541b\u8d76\u9e2d\u5b50\u4e0a\u67b6\u2026\u2026\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u4e0e\u4e1c\u6d77\u7701\u7701\u957f\u80fd\u5dee\u591a\u8fdc\uff0c\u8bf4\u767d\u4e86\u4e5f\u5c31\u4e00\u81c2\u8fdc\u7684\u8ddd\u79bb\uff0c\u674e\u8fdc\u6e56\u662f\u6015\u5efa\u90ba\u5e02\u505a\u51fa\u6210\u7ee9\u800c\u7701\u91cc\u53cd\u5e94\u8fdf\u949d\u4f1a\u8ba9\u4ed6\u9677\u5165\u88ab\u52a8\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u201d\u8c22\u6c49\u9756\u6447\u4e86\u6447\u5934\uff0c\u201c\u674e\u8fdc\u6e56\u662f\u73b0\u5b9e\u7684\u3001\u5b9e\u7528\u4e3b\u4e49\u7684\u653f\u6cbb\u4eba\u7269\uff0c\u8fd9\u4e00\u70b9\u65e9\u5c31\u662f\u786e\u51ff\u65e0\u7591\uff0c\u5373\u4f7f\u5bf9\u9526\u6e56\u6709\u4e9b\u62b1\u6028\uff0c\u4e5f\u5f88\u96be\u53d1\u5c55\u6210\u5bf9\u9526\u6e56\u7684\u654c\u89c6\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u747e\u73ba\u8bf4\u9053\uff1a\u201c\u674e\u8fdc\u6e56\u7684\u8fdf\u7591\uff0c\u53ef\u80fd\u6e90\u4e8e\u7701\u91cc\u4e00\u4e9b\u4eba\u5bf9\u6c11\u8425\u7ecf\u6d4e\u7684\u504f\u89c1\uff0c\u4f46\u662f\u8fd9\u4e00\u70b9\u4e0d\u4f1a\u6210\u4e3a\u969c\u788d\uff0c\u6211\u4eec\u90fd\u786e\u4fe1\u4e0d\u7591\uff0c\u674e\u8fdc\u6e56\u96be\u9053\u4f1a\u6ca1\u6709\u529b\u6392\u4f17\u8bae\u7684\u9b44\u529b\uff1f\u201d</p><p>\u3000\u3000\u8c22\u610f\u8bf4\u9053\uff1a\u201c\u7701\u91cc\u82e5\u771f\u80fd\u5b9e\u884c\u63a8\u52a8\u9ad8\u65b0\u6280\u672f\u53d1\u5c55\u7684\u65b0\u6218\u7565\uff0c\u79d1\u738b\u4e5f\u5c06\u83b7\u76ca\u2026\u2026\u76ee\u524d\u7684\u60c5\u51b5\uff0c\u9526\u6e56\u5728\u65b0\u6218\u7565\u4e2d\u80fd\u53d1\u6325\u7684\u4f5c\u7528\u6ca1\u6709\u5176\u4ed6\u4f01\u4e1a\u80fd\u591f\u66ff\u4ee3\uff0c\u6240\u4ee5\u663e\u5f97\u4ed6\u4eec\u7684\u5730\u4f4d\u91cd\u8981\uff0c\u4f46\u662f\u65e0\u8bba\u662f\u7701\u91cc\u8fd8\u662f\u5efa\u90ba\u5e02\u91cc\uff0c\u90fd\u4e0d\u4f1a\u7518\u5fc3\u8ba9\u4e00\u5bb6\u4f01\u4e1a\u7275\u7740\u9f3b\u5b50\u8d70\uff0c\u8981\u6709\u4f01\u4e1a\u80fd\u7ad9\u51fa\u6765\u8ddf\u9526\u6e56\u7ade\u4e89\uff0c\u4e5f\u662f\u4ed6\u4eec\u4e50\u4e8e\u89c1\u5230\u7684\u2026\u2026\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u671f\u5f85\u674e\u8fdc\u6e56\u5bf9\u9526\u6e56\u90a3\u4e9b\u62b1\u6028\u4f1a\u53d1\u5c55\u6210\u654c\u89c6\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u60f3\u5230\u9526\u6e56\u7684\u9510\u4e0d\u53ef\u5f53\uff0c\u8ddd\u79bb\u4f1a\u8d8a\u6765\u8d8a\u8fdc\u5427\uff0c\u4e0d\u7531\u7684\u6709\u4e9b\u6cae\u4e27\uff0c\u8c22\u610f\u7684\u8bdd\u6839\u672c\u5b89\u6170\u4e0d\u4e86\u4ed6\uff0c\u4e0d\u8fc7\u5c31\u7b97\u5728\u5bb6\u65cf\u4eba\u9762\u524d\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u8ba9\u6cae\u4e27\u7684\u5fc3\u60c5\u6d41\u9732\u51fa\u6765\uff0c\u96be\u9053\u8fd8\u8981\u5766\u7136\u4ed6\u5bf9\u5f20\u606a\u4e2a\u4eba\u7684\u6069\u6028\u5fc3\u5e76\u6ca1\u6709\u51cf\u6de1\uff1f</p><p>\u3000\u3000\u8c22\u610f\u53c8\u8bf4\u9053\uff1a\u201c\u674e\u8fdc\u6e56\uff0c\u6211\u4eec\u662f\u65e0\u6cd5\u5229\u7528\u7684\uff0c\u4e5f\u4e0d\u8981\u8f7b\u6613\u7684\u53bb\u5c1d\u8bd5\uff1b\u4e0d\u8fc7\u5efa\u90ba\u5e02\u91cc\u7684\u77db\u76fe\uff0c\u8fd8\u662f\u6709\u53ef\u4ee5\u5229\u7528\u7684\u5730\u65b9\uff0c\u6211\u60f3\u6211\u4eec\u80fd\u5728\u5efa\u90ba\u5e02\u91cc\u627e\u5230\u4e00\u4e9b\u76df\u53cb\u2026\u2026\u201d</p></article>"}}}]);
