(window.webpackJsonp=window.webpackJsonp||[]).push([[727],{750:function(p){p.exports={chapterTitle:null,title:"\u7b2c726\u7ae0 \u53d8\u5f62",content:"<article><h2>\u7b2c726\u7ae0 \u53d8\u5f62</h2><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u5fc3\u60c5\u7ad9\u5728\u90a3\u91cc\u5077\u542c\u4e24\u4e2a\u6821\u5b66\u751f\u4f1a\u7684\u9752\u74dc\u86cb\u5b50\u5728\u90a3\u91cc\u4ed6\u4eec\u5439\u5618\u5bf9\u6797\u96ea\u8fd9\u4e2a\u86c7\u874e\u7f8e\u5973\u6797\u96ea\u9752\u6625\u95f7\u9a9a\u7684\u6b32\u671b\uff0c\u4ed6\u662f\u8fc7\u6765\u627e\u675c\u98de\u3001\u8499\u4e50\u53bb\u5403\u591c\u5bb5\u7684\u3002</p><p>\u3000\u3000\u5185\u5df7\u91cc\u6709\u5bb6\u80a5\u7f8a\u8089\u9986\uff0c\u5e97\u5802\u65e9\u5c31\u7ed3\u675f\u8425\u4e1a\uff0c\u5e97\u95e8\u53e3\u90a3\u4e2a\u70e4\u7f8a\u8089\u4e32\u7684\u6728\u70ad\u7089\u67b6\u5b50\u8981\u4e00\u76f4\u6446\u5230\u51cc\u6668\u624d\u6536\u56de\u5e97\u91cc\uff0c\u542c\u7740\u7f8a\u6cb9\u6ef4\u5728\u6728\u70ad\u4e0a\u6ecb\u6ecb\u4f5c\u54cd\uff0c\u675c\u98de\u8dd1\u53bb\u9694\u58c1\u7684\u4fbf\u5229\u5e97\u4e70\u5564\u9152\uff0c\u7f8a\u8089\u4e32\u5c31\u5564\u9152\u7684\u591c\u5bb5\u5012\u662f\u4e0d\u9519\u3002</p><p>\u3000\u3000\u6797\u96ea\u4e0e\u80e1\u5b97\u5e86\u540c\u65f6\u51fa\u73b0\u5728\u738b\u6d77\u7c9f\u7ed9\u80e1\u91d1\u661f\u4e3e\u529e\u7684\u751f\u65e5\u5bb4\u4f1a\u4e0a\uff0c\u5f88\u6709\u53ef\u80fd\u8fd9\u4e2a\u86c7\u874e\u5973\u4eba\u4e5f\u60f3\u8fdb\u6765\u63d2\u4e00\u811a\uff0c\u5f20\u606a\u5012\u662f\u6ca1\u6709\u60f3\u5230\u6797\u96ea\u4f1a\u8fd9\u4e48\u5feb\u8fdb\u6765\u63d2\u4e00\u811a\u3002\u60f3\u8d77\u6797\u96ea\uff0c\u5c31\u60f3\u8d77\u9690\u85cf\u6797\u96ea\u80cc\u540e\u7684\u4eba\u7269\u3001\u8eab\u5c45\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u9ad8\u4f4d\u7684\u845b\u5efa\u5fb7\u6765\uff0c\u845b\u5efa\u5fb7\u8fd8\u517c\u4efb\u7740\u56fd\u5bb6\u8ba1\u59d4\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u5c0f\u7ec4\u7ec4\u957f\u8fd9\u4e48\u4e00\u4e2a\u4e3e\u8db3\u8f7b\u91cd\u7684\u804c\u52a1\u3002</p><p>\u3000\u3000\u5750\u5728\u8def\u706f\u4e0b\u7684\u53f0\u9636\u4e0a\uff0c\u56bc\u7740\u6ef4\u6cb9\u7684\u7f8a\u8089\u4e32\uff0c\u559d\u7740\u7f50\u88c5\u7684\u9752\u5c9b\u5564\u9152\uff0c\u5f20\u606a\u7ed9\u8499\u4e50\u3001\u675c\u98de\u4ecb\u7ecd\u4e00\u4e9b\u5f53\u524d\u56fd\u5185\u8f6f\u4ef6\u4ea7\u4e1a\u653f\u7b56\u80cc\u666f\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u67d0\u79cd\u610f\u4e49\u800c\u8a00\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5e94\u8be5\u5f52\u5c5e\u5230\u5546\u4e1a\u5730\u4ea7\u9879\u76ee\u4e2d\u53bb\uff0c\u901a\u8fc7\u4ea7\u4e1a\u57fa\u5730\u7684\u6253\u9020\uff0c\u628a\u4f17\u591a\u9ad8\u79d1\u6280\u4f01\u4e1a\u5438\u5f15\u5230\u4e00\u8d77\uff0c\u4ee5\u5b9e\u73b0\u96c6\u7ea6\u6548\u76ca\uff0c\u4f53\u73b0\u9ad8\u79d1\u6280\u4f01\u4e1a\u7684\u5b75\u5316\u5668\u6548\u5e94\uff0c\u4f5c\u4e3a\u56ed\u533a\u7684\u6295\u8d44\u5546\u4e0e\u7ba1\u7406\u8005\u53ef\u4ee5\u8ffd\u6c42\u957f\u8fdc\u6548\u76ca\u3002\u8fd9\u79cd\u5546\u4e1a\u6a21\u5f0f\u5728\u56fd\u5916\u5df2\u7ecf\u6709\u76f8\u5f53\u666e\u904d\u7684\u6210\u529f\u6848\u4f8b\uff0c\u5c31\u7b97\u56fd\u5185\u7684\u8f6f\u4ef6\u5546\u4e1a\u73af\u5883\u8fd8\u4e0d\u591f\u7406\u60f3\uff0c\u4f46\u662f\u7531\u4e8e\u4e00\u4e9b\u7279\u6b8a\u4ea7\u4e1a\u653f\u7b56\u7684\u4fdd\u62a4\uff0c\u5728\u67d0\u4e9b\u7ec6\u5206\u5e02\u573a\u4e0a\u8fd8\u6709\u7740\u5f88\u660e\u6717\u7684\u524d\u666f\uff0c\u53ea\u8981\u5c06\u5de5\u4f5c\u771f\u6b63\u7684\u505a\u5230\u5b9e\u5904\u53bb\uff0c\u63a7\u5236\u597d\u89c4\u6a21\uff0c\u4e5f\u80fd\u6210\u5c31\u4e00\u756a\u4e8b\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5373\u4f7f\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u7684\u4e8b\u60c5\u5f88\u5fd9\uff0c\u6211\u4e5f\u62bd\u65f6\u95f4\u8ba4\u771f\u7814\u7a76\u8fc7\u90a3\u4efd\u8d44\u6599\u3002\u8bf4\u771f\u7684\uff0c\u6211\u8fd8\u771f\u6709\u4e9b\u62c5\u5fc3\u738b\u6d77\u7c9f\u6839\u636e\u90a3\u4efd\u7b56\u5212\u4e66\u505a\u51fa\u4e00\u756a\u4e8b\u4e1a\u5462\uff0c\u90a3\u6837\u5c82\u4e0d\u662f\u5f88\u96be\u770b\uff1f\u201d\u675c\u98de\u548c\u8863\u8eba\u5f00\u8349\u5730\u4e0a\uff0c\u5f80\u91cc\u5634\u91cc\u704c\u4e86\u4e00\u53e3\u5564\u9152\u3002</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u96be\u770b\u4e0d\u96be\u770b\uff0c\u83ab\u975e\u4e00\u5b9a\u8981\u770b\u5230\u738b\u6d77\u7c9f\u8eab\u8d25\u540d\u88c2\u624d\u9ad8\u5174\uff1f\u6211\u53c8\u8ddf\u4ed6\u6ca1\u6709\u6069\u6028\u3002\u201d\u5f20\u606a\u4e0e\u675c\u98de\u6572\u4e86\u6572\u5564\u9152\u74f6\u5b50\uff0c\u559d\u4e86\u4e00\u53e3\u5564\u9152\uff0c\u8fd8\u77a5\u4e86\u8499\u4e50\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u5efa\u7acb\u8f6f\u4ef6\u5de5\u5382\u3001\u8f6f\u4ef6\u7814\u53d1\u4e0e\u6d4b\u8bd5\u4e2d\u5fc3\u3001\u5bf9\u5916\u5b66\u672f\u4ea4\u6d41\u4e2d\u5fc3\u3001\u6210\u7acb\u8f6f\u4ef6\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u5c06\u4e2d\u79d1\u9662\u8f6f\u4ef6\u7814\u7a76\u6240\u4ee5\u65e0\u5f62\u8d44\u4ea7\u5165\u80a1\u7684\u65b9\u5f0f\u5f15\u5165\uff0c\u4e0e\u9ad8\u7b49\u9662\u6821\u5efa\u7acb\u8054\u5408\u7814\u53d1\u4e0e\u4eba\u624d\u57f9\u517b\u673a\u5236\uff0c\u5c06\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u57fa\u7840\u6846\u67b6\u8fd9\u6837\u5148\u642d\u5efa\u8d77\u6765\uff0c\u7136\u540e\u518d\u5c06\u4e00\u5bb6\u53c8\u4e00\u5bb6\u7684\u8f6f\u4ef6\u4f01\u4e1a\u5f15\u8fdb\u5230\u56ed\u533a\u4e2d\u6765\u2026\u2026\u201d\u8499\u4e50\u8f6c\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u8fd9\u4efd\u7b56\u5212\u4e66\uff0c\u6211\u53cd\u6b63\u662f\u624d\u758f\u5b66\u6d45\u771f\u662f\u770b\u4e0d\u51fa\u4e00\u70b9\u7684\u6f0f\u6d1e\u6765\uff0c\u53ef\u4ee5\u8bf4\u6beb\u65e0\u7455\u75b5\uff0c\u6211\u5c31\u5947\u602a\uff0c\u9526\u6e56\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u81ea\u5df1\u53bb\u505a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8981\u8f7b\u6613\u7684\u5206\u6563\u6709\u9650\u7684\u8d44\u6e90\uff0c\u9526\u6e56\u6700\u7d27\u7f3a\u7684\u8d44\u6e90\u662f\u7ba1\u7406\u4e0e\u4e2d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\uff0c\u9526\u6e56\u4e0d\u4f1a\u4e3a\u4e0e\u4e3b\u4f53\u65b9\u5411\u65e0\u5173\u7684\u9879\u76ee\u5206\u6563\u8fd9\u4e9b\u7d27\u7f3a\u8d44\u6e90\u3002\u8bf4\u53e5\u7528\u5fc3\u9669\u6076\u7684\u8bdd\uff0c\u8981\u662f\u738b\u6d77\u7c9f\u641e\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u4e2d\u9014\u53d7\u632b\uff0c\u56e0\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u800c\u805a\u96c6\u8d77\u6765\u7684\u4e00\u4e9b\u4eba\u529b\u4e0e\u6280\u672f\u8d44\u6e90\uff0c\u9526\u6e56\u5012\u662f\u5f88\u9ad8\u5174\u63a5\u6536\u7684\uff0c\u201d\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u65e0\u8bba\u662f\u529f\u6210\u540d\u5c31\u8fd8\u662f\u8eab\u8d25\u540d\u88c2\uff0c\u90fd\u5728\u738b\u6d77\u7c9f\u81ea\u5df1\u7684\u9009\u62e9\u4e4b\u95f4\u2026\u2026\u201d\u5728\u4ed6\u773c\u91cc\uff0c\u738b\u6d77\u7c9f\u4e0d\u8fc7\u662f\u679a\u68cb\u5b50\u7f62\u4e86\uff0c\u7528\u8fd9\u679a\u68cb\u5b50\uff0c\u5e76\u4e0d\u662f\u8981\u8fbe\u5230\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u76ee\u7684\uff0c\u81f3\u4e8e\u738b\u6d77\u7c9f\u662f\u798f\u662f\u7978\u5168\u770b\u4ed6\u4e2a\u4eba\u9020\u5316\u3002\u5f20\u606a\u5634\u91cc\u8fd9\u4e48\u8bf4\u7740\uff0c\u5fc3\u91cc\u5374\u60f3\uff1a\u8fd9\u4efd\u7acb\u9879\u7b56\u5212\u4e00\u65e6\u5c06\u80e1\u5b97\u5e86\u3001\u6797\u96ea\u3001\u845b\u5efa\u5fb7\u8fd9\u4e9b\u4eba\u90fd\u5377\u8fdb\u6765\uff0c\u751a\u81f3\u8fd8\u53ef\u80fd\u5c06\u8096\u660e\u5efa\u7b49\u884c\u884c\u8272\u8272\u7684\u4eba\u7269\u90fd\u5377\u8fdb\u6765\uff0c\u5c40\u9762\u5230\u5e95\u4f1a\u671d\u4ec0\u4e48\u65b9\u5411\u53d1\u5c55\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u671f\u5f85\u554a\uff01\u4e0d\u8fc7\u8fd9\u6837\u4e5f\u597d\uff0c\u4e22\u4e00\u5757\u9aa8\u5934\u7ed9\u4ed6\u4eec\u5543\u7740\uff0c\u4e5f\u53ef\u4ee5\u4e0d\u7528\u76ef\u7740\u9526\u6e56\u53eb\u5524\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6797\u96ea\u5bf9\u56fd\u5185\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u53d1\u5c55\u73b0\u72b6\u6240\u77e5\u4e0d\u591a\uff0c\u6bd5\u7adf\u4e0d\u662f\u4e13\u4e1a\u4eba\u58eb\uff0c\u4f46\u662f\u5979\u4ece\u542c\u80e1\u5b97\u5e86\u90a3\u91cc\u542c\u5230\u5bf9\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u7684\u7c97\u7565\u63cf\u8ff0\uff0c\u5c31\u770b\u5230\u8fd9\u4e2a\u9879\u76ee\u91cc\u6709\u4ee4\u5979\u6781\u4e3a\u6e34\u671b\u7684\u4e00\u79cd\u8d44\u6e90\uff0c\u90a3\u5c31\u662f\u571f\u5730\u3002\u6839\u636e\u56fd\u5185\u5bf9\u8f6f\u4ef6\u4ea7\u4e1a\u7b49\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u6276\u6301\u653f\u7b56\uff0c\u5efa\u8fd9\u4e48\u4e00\u5ea7\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\uff0c\u53ef\u4ee5\u4ee5\u6700\u4f4e\u5ec9\u7684\u4ef7\u683c\u751a\u81f3\u65e0\u507f\u7684\u4ece\u653f\u5e9c\u624b\u4e2d\u83b7\u5f97\u5927\u91cf\u7684\u56ed\u533a\u5efa\u8bbe\u7528\u5730\u3002</p><p>\u3000\u3000\u9526\u6e56\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u641e\u521b\u4e1a\u56ed\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u5927\u7b14\u4e00\u6325\uff0c\u5c06\u4e3d\u4e30\u5370\u67d3\u5382\u8fd8\u6709\u65c1\u8fb9\u4e00\u5bb6\u6f02\u67d3\u5382\u7684\u5382\u623f\u4e0e\u571f\u5730\u534a\u5356\u534a\u9001\u7684\u7ed9\u4e86\u9526\u6e56\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5bf9\u4e9b\u8bf4\u58f0\u4e0d\u662f\u3002</p><p>\u3000\u3000\u9526\u6e56\u53ef\u4ee5\u8fd9\u4e48\u641e\uff0c\u7cbe\u5178\u4e3a\u4f55\u4e0d\u53ef\u4ee5\u8fd9\u4e48\u641e\uff1f\u5148\u4ee5\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u540d\u4e49\u5c06\u5730\u7ed9\u5708\u8fc7\u6765\uff0c\u7136\u540e\u6162\u6162\u7684\u60f3\u529e\u6cd5\u5c06\u8fd9\u4e9b\u571f\u5730\u5206\u5272\u5f00\u8f6c\u79fb\u5230\u7cbe\u5178\u5730\u4ea7\u624b\u91cc\u7528\u4e8e\u4ed6\u9014\u3002</p><p>\u3000\u3000\u8fd9\u624d\u662f\u6628\u5929\u6797\u96ea\u8feb\u4e0d\u53ca\u5f85\u7684\u4e3b\u52a8\u627e\u738b\u6d77\u7c9f\u7684\u6839\u672c\u539f\u56e0\u3002</p><p>\u3000\u3000\u4e00\u5927\u65e9\uff0c\u738b\u6d77\u7c9f\u5c31\u62ff\u7740\u4e24\u4efd\u8f6c\u8ba9\u6d77\u7c9f\u79d1\u6280\u516c\u53f8\u80a1\u6743\u7684\u7a7a\u767d\u534f\u8bae\u4e0e\u4ed6\u5b8c\u6574\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u7b56\u5212\u4e66\u6765\u5230\u7cbe\u5178\u5730\u4ea7\u627e\u6797\u96ea\u3002</p><p>\u3000\u3000\u867d\u7136\u738b\u6d77\u7c9f\u5f88\u7528\u5fc3\u7684\u5c06\u7acb\u9879\u7b56\u5212\u91cc\u7684\u4e00\u4e9b\u8981\u70b9\u90fd\u8bf4\u7ed9\u80e1\u91d1\u661f\u542c\uff0c\u4f46\u662f\u7ecf\u80e1\u91d1\u661f\u3001\u80e1\u5b97\u5e86\u4e24\u4e2a\u975e\u4e13\u4e1a\u4eba\u58eb\u7684\u8f6c\u8ff0\uff0c\u6797\u96ea\u542c\u5230\u7684\u7acb\u9879\u7b56\u5212\u591a\u5c11\u6709\u4e9b\u9762\u76ee\u5168\u975e\u7684\u611f\u89c9\uff1b\u4eca\u5929\u624d\u662f\u5979\u7b2c\u4e00\u6b21\u770b\u5230\u5b8c\u6574\u7684\u7acb\u9879\u7b56\u5212\u3002</p><p>\u3000\u3000\u6797\u96ea\u8d77\u521d\u662f\u5f88\u968f\u610f\u7684\u7ffb\u770b\u7acb\u9879\u7b56\u5212\u4e66\uff0c\u968f\u610f\u5230\u4e00\u8fb9\u7ffb\u770b\uff0c\u4e00\u8fb9\u62ff\u6307\u7532\u5254\u5507\u7ebf\u6709\u610f\u65e0\u610f\u7684\u53bb\u52fe\u5f15\u738b\u6d77\u7c9f\u2014\u2014\u5979\u5bf9\u535a\u58eb\u3001\u9ad8\u6821\u8bb2\u5e08\u8fd9\u79cd\u9ad8\u77e5\u5206\u5b50\u8fd8\u662f\u6709\u4e9b\u5174\u8da3\u7684\u3002\u5f88\u5feb\uff0c\u5979\u5c31\u7ed9\u67b6\u6784\u6062\u5f18\u3001\u7acb\u610f\u9ad8\u8fdc\u7684\u7b56\u5212\u4e66\u5438\u5f15\u8fdb\u53bb\uff0c\u5373\u4f7f\u4f5c\u4e3a\u4e00\u4e2a\u5916\u884c\u4eba\u90fd\u80fd\u770b\u51fa\u8fd9\u4efd\u7acb\u9879\u7b56\u5212\u4e66\u7684\u4ef7\u503c\u51fa\u6765\uff0c\u770b\u738b\u6d77\u7c9f\u7684\u76ee\u5149\u4e0d\u7531\u7684\u655b\u53bb\u8f7b\u4f7b\u7684\u610f\u5473\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4efd\u7acb\u9879\u7b56\u5212\u7684\u6c34\u5e73\u5982\u4f55\uff0c\u4f1a\u6709\u66f4\u4e13\u4e1a\u7684\u4eba\u58eb\u6765\u5224\u65ad\u2026\u2026\u201d\u6797\u96ea\u4e3e\u8d77\u624b\u91cc\u539a\u539a\u4e00\u53e0\u7acb\u9879\u7b56\u5212\u4e66\u671d\u5750\u5728\u5bf9\u9762\u6c99\u53d1\u4e0a\u7684\u738b\u6d77\u7c9f\u626c\u4e86\u626c\uff0c\u738b\u6d77\u7c9f\u8fd8\u4ee5\u4e3a\u6797\u96ea\u8981\u8ddf\u4ed6\u8c08\u7acb\u9879\u7b56\u5212\u7684\u4e8b\u60c5\uff0c\u5fd9\u7ad9\u8d77\u6765\u8d70\u5230\u529e\u516c\u684c\u524d\uff0c\u6797\u96ea\u57cb\u9996\u5728\u4fbf\u7b7e\u7eb8\u5199\u4e86\u4e00\u4e2a\u4eba\u540d\u3001\u4e00\u4e2a\u5317\u4eac\u7684\u7535\u8bdd\u53f7\u7801\uff0c\u4e5f\u4e0d\u4ecb\u610f\u8ba9\u738b\u6d77\u7c9f\u770b\u5230\uff0c\u6253\u7535\u8bdd\u901a\u77e5\u5979\u7684\u52a9\u7406\u8fdb\u6765\uff0c\u5c06\u7acb\u9879\u7b56\u5212\u4e66\u4e0e\u4fbf\u7b7e\u7eb8\u4ea4\u7ed9\u52a9\u7406\uff0c\u201c\u9a6c\u4e0a\u5c06\u8fd9\u4efd\u8d44\u6599\u4f20\u771f\u5230\u5317\u4eac\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\uff1f\u738b\u6d77\u7c9f\u89c9\u5f97\u4fbf\u7b7e\u7eb8\u4e0a\u7684\u8fd9\u4e2a\u4eba\u540d\u5f88\u719f\u6089\uff0c\u5374\u4e00\u65f6\u60f3\u4e0d\u8d77\u662f\u8c01\u3002</p><p>\u3000\u3000\u6797\u96ea\u7b11\u4e00\u7b11\uff0c\u4e0d\u7406\u4f1a\u738b\u6d77\u7c9f\u7684\u7591\u60d1\uff0c\u62e8\u901a\u845b\u5efa\u5fb7\u7684\u7535\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u5b8c\u6574\u7684\u7acb\u9879\u7b56\u5212\u6211\u7ed9\u4f60\u53d1\u8fc7\u53bb\u4e86\uff0c\u4e4b\u524d\u80e1\u5b97\u5e86\u8bf4\u7684\u5f88\u4e0d\u5b8c\u6574\uff0c\u4f60\u518d\u5e2e\u6211\u770b\u770b\u5b8c\u6574\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4efd\u7acb\u9879\u7b56\u5212\u7684\u4ef7\u503c\uff0c\u6797\u96ea\u8fd8\u8ba4\u8bc6\u4e0d\u5b8c\u5168\uff0c\u8eab\u4e3a\u56fd\u5bb6\u8ba1\u59d4\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u5c0f\u7ec4\u7ec4\u957f\u7684\u845b\u5efa\u5fb7\u5374\u80fd\u770b\u5230\u66f4\u591a\u7684\u4e1c\u897f\uff0c\u4ed6\u5728\u770b\u5230\u6797\u96ea\u4f20\u771f\u7ed9\u4ed6\u7684\u5b8c\u6574\u7acb\u9879\u7b56\u5212\u4e66\u4e4b\u540e\uff0c\u9a6c\u4e0a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u9700\u8981\u505a\u4e24\u4ef6\u4e8b\uff0c\u4e00\u662f\u6309\u7167\u4e4b\u524d\u8bf4\u597d\u7684\uff0c\u7cbe\u5178\u5730\u4ea7\u8981\u5bf9\u6d77\u7c9f\u79d1\u6280\u63a7\u80a1\uff0c\u7b2c\u4e8c\u5c31\u662f\u60f3\u529e\u6cd5\u5c3d\u53ef\u80fd\u7684\u52a0\u5f3a\u6d77\u7c9f\u79d1\u6280\uff0c\u81f3\u5c11\u8981\u8ba9\u6d77\u7c9f\u79d1\u6280\u8868\u9762\u4e0a\u770b\u53bb\u5149\u9c9c\u4e00\u4e9b\u3002\u53e6\u5916\uff0c\u7acb\u9879\u7b56\u5212\u4e66\u8981\u6539\u4e00\u4e0b\uff0c\u6a61\u6811\u56ed\u5360\u5730\u90fd\u8981\u516b\u767e\u4ea9\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5c11\u8bf4\u4e5f\u8981\u4e00\u5343\u4ea9\uff0c\u4e09\u767e\u4ea9\u7684\u683c\u5c40\u592a\u5c0f\uff0c\u53d1\u5c55\u524d\u666f\u91cc\uff0c\u8981\u4fee\u6539\u51e0\u4e2a\u6570\u636e\uff0c\u5efa\u6210\u65f6\u95f4\u4ece\u20186\u52308\u5e74\u2019\u7f29\u77ed\u4e3a\u20184\u52306\u5e74\u2019\uff0c\u2018\u5168\u9762\u5efa\u6210\u540e\u7d2f\u8ba1\u6295\u8d4410\u4ebf\u2019\u6539\u6210\u5230\u2018\u7d2f\u8ba1\u6295\u8d4430\u4ebf\u2019\uff0c\u2018\u5e26\u52a8\u5730\u65b9\u7ecf\u6d4e80\u4ebf\u2019\u6539\u4e3a\u2018\u5e26\u52a8\u5730\u65b9\u7ecf\u6d4e240\u4ebf\u2019\uff0c\u2018\u8f6f\u4ef6\u89c4\u6a21\u6bcf\u5e748\u4ebf\u2019\u6539\u4e3a\u201824\u4ebf\u2019\uff0c\u5176\u4ed6\u4e00\u4e9b\u7ec6\u8282\u5730\u65b9\uff0c\u4f60\u8ba9\u6d77\u7c9f\u79d1\u6280\u7684\u90a3\u4e2a\u5bb6\u4f19\u4fee\u6539\uff0c\u90a3\u5bb6\u4f19\u80fd\u6574\u8fd9\u4efd\u4e1c\u897f\u51fa\u6765\uff0c\u8fd8\u662f\u6709\u4e9b\u771f\u624d\u5b9e\u6599\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u4e00\u8fb9\u8bb0\u5f55\u8981\u4fee\u6539\u7684\u6570\u636e\uff0c\u4e00\u8fb9\u6b6a\u7740\u8111\u888b\u770b\u738b\u6d77\u7c9f\uff0c\u5bf9\u7740\u7535\u8bdd\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u53ef\u662f\u4e1c\u6d77\u5927\u5b66\u7684\u535a\u58eb\u8bb2\u5e08\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u7ed9\u6797\u96ea\u706b\u8fa3\u8fa3\u7684\u76ee\u5149\u770b\u5f97\u5fc3\u5934\u4e71\u649e\uff0c\u4ed6\u8fd8\u6ca1\u6709\u60f3\u8d77\u6765\u201c\u845b\u5efa\u5fb7\u201d\u662f\u8c01\uff0c\u4f46\u662f\u80fd\u731c\u51fa\u662f\u4e2a\u5f88\u91cd\u8981\u7684\u4eba\u7269\uff0c\u4e0d\u7136\u4ed6\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u8fd9\u4e48\u719f\u6089\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5728\u7535\u8bdd\u90a3\u5934\u53ef\u4e0d\u6653\u5f97\u738b\u6d77\u7c9f\u5c31\u5728\u6797\u96ea\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u4ed6\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5efa\u90ba\u5e02\u91cc\u6709\u80e1\u5b97\u5e86\uff0c\u4e1c\u6d77\u7701\u91cc\uff0c\u6211\u5efa\u8bae\u4f60\u53bb\u627e\u526f\u7701\u957f\u9646\u65b0\u534e\uff0c\u4ed6\u8d1f\u8d23\u4e1c\u6d77\u7701\u7684\u7535\u5b50\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u53d1\u5c55\u7684\u5b8f\u89c2\u5927\u5c40\uff0c\u4f60\u5c06\u4fee\u6539\u540e\u4e4b\u597d\u7684\u7acb\u9879\u7b56\u5212\u62ff\u53bb\u7ed9\u4ed6\u770b\uff0c\u5728\u4f60\u4eec\u53bb\u627e\u4ed6\u4e4b\u524d\uff0c\u6211\u4f1a\u5148\u6253\u4e2a\u7535\u8bdd\u7ed9\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u5728\u4fbf\u7b7e\u7eb8\u4e0a\u5199\u4e0b\u201c\u9646\u65b0\u534e\u201d\uff0c\u62ac\u5934\u770b\u4e86\u738b\u6d77\u7c9f\u4e00\u773c\uff0c\u5c06\u4fbf\u7b7e\u7eb8\u63a8\u7ed9\u4ed6\u770b\u3002\u770b\u5230\u9646\u65b0\u534e\u7684\u540d\u5b57\u51fa\u73b0\u5728\u4fbf\u7b7e\u7eb8\u4e0a\uff0c\u738b\u6d77\u7c9f\u9aa4\u7136\u5fc3\u5934\u4e00\u8df3\uff0c\u8fd9\u4e00\u77ac\u95f4\u51e0\u4e4e\u8981\u505c\u6ede\u4e0b\u6765\u3002\u738b\u6d77\u7c9f\u540c\u65f6\u60f3\u8d77\u845b\u5efa\u5fb7\u662f\u8c01\u4e86\uff0c\u77e5\u9053\u81ea\u5df1\u4ece\u6b64\u771f\u7684\u662f\u8df3\u4e0a\u68a7\u6850\u679d\u5934\u6210\u51e4\u51f0\u4e86\u3002\u8981\u77e5\u9053\u7cbe\u5178\u5730\u4ea7\u80cc\u540e\u7684\u4eba\u7269\u662f\u5982\u6b64\u4e4b\u5f3a\uff0c\u4e0d\u9700\u8981\u6797\u96ea\u6765\u63d0\uff0c\u738b\u6d77\u7c9f\u90fd\u4f1a\u4e3b\u52a8\u8ba9\u51fa\u6d77\u7c9f\u516c\u53f8\u56db\u6210\u7684\u80a1\u6743\uff0c\u867d\u7136\u6653\u5f97\u6797\u96ea\u4e0d\u8fc7\u662f\u5229\u7528\u4ed6\uff0c\u5c06\u4ed6\u5f53\u6210\u5b98\u5546\u52fe\u7ed3\u725f\u53d6\u5de8\u989d\u8d22\u5bcc\u7684\u5de5\u5177\uff0c\u4f46\u662f\u4ed6\u5fc3\u7518\u60c5\u613f\uff0c\u6d77\u7c9f\u516c\u53f8\u5c11\u8bf4\u4ed6\u8fd8\u4fdd\u7559\u7740\u4e09\u6210\u7684\u80a1\u6743\u3002</p></article>"}}}]);
