(window.webpackJsonp=window.webpackJsonp||[]).push([[73],{95:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e00\u8282 \u6211\u4eec\u518d\u505a\u4e2a\u4ea4\u6613\u5982\u4f55",content:"<article><h2>\u7b2c\u4e09\u5341\u4e00\u8282 \u6211\u4eec\u518d\u505a\u4e2a\u4ea4\u6613\u5982\u4f55</h2><p>\u6885\u83f2\u65af\u88ab\u6076\u9b54\u5b9a\u4f4f\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u663e\u7136\u662f\u5927\u597d\u673a\u4f1a\uff0c\u53ef\u4ee5\u8086\u610f\u4eab\u7528\u7f8e\u4eba\u3002\u4f46\u95ee\u9898\u5728\u4e8e\uff0c\u518d\u5f3a\u608d\u7684\u5b9a\u8eab\u672f\uff0c\u603b\u4e5f\u6709\u89e3\u9664\u7684\u65f6\u5019\uff0c\u5230\u65f6\u5019\u5979\u82e5\u662f\u7ffb\u8138\uff0c\u743c\u6069\u53ef\u662f\u4e07\u4e07\u654c\u4e0d\u8fc7\u3002\u4ee5\u8482\u5a1c\u82a9\u8fd9\u79cd\u9020\u8be3\u7684\u672f\u58eb\uff0c\u72b9\u81ea\u6321\u4e0d\u4e0b\u5979\u4e00\u5251\uff0c\u743c\u6069\u5e76\u4e0d\u8ba4\u4e3a\u6362\u4e86\u81ea\u5df1\u5c31\u80fd\u505a\u5f97\u66f4\u597d\u3002</p><p>\u5982\u679c\u8981\u7a33\u59a5\u8d77\u89c1\uff0c\u81ea\u7136\u662f\u8d81\u7740\u5979\u8fd8\u6ca1\u6709\u53cd\u6297\u80fd\u529b\uff0c\u5148\u597d\u597d\u5f3a\u5978\u4e00\u987f\uff0c\u4eab\u53d7\u8fd9\u7f8e\u5999\u7389\u4f53\uff0c\u723d\u5b8c\u540e\u5c31\u6740\u4eba\u706d\u53e3\uff0c\u628a\u8d23\u4efb\u63a8\u5012\u4eba\u9762\u72ee\u8eab\u4e0a\uff0c\u81ea\u5df1\u4e00\u8d70\u4e86\u4e4b\uff0c\u4e07\u4e8b\u5927\u5409\u3002\u8fd9\u4e48\u4f18\u8d28\u7684\u7f8e\u4eba\uff0c\u53ea\u80fd\u73a9\u4e00\u6b21\u5b9e\u5728\u53ef\u60dc\uff0c\u4f46\u4e3a\u4e86\u4fdd\u547d\u8d77\u89c1\uff0c\u4e5f\u53ea\u80fd\u5fcd\u75db\u4e0b\u624b\u3002</p><p>\u4e0d\u8fc7\uff0c\u5979\u4f1a\u7ffb\u8138\u4e48\u3002</p><p>\u743c\u6069\u5176\u5b9e\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u6b64\u524d\u4ece\u6ca1\u548c\u5723\u6b66\u58eb\u6253\u8fc7\u4ea4\u9053\uff0c\u9634\u9b42\u57ce\u91cc\u538b\u6839\u6ca1\u6709\u8fd9\u79cd\u5bb6\u4f19\u5b58\u5728\u3002\u53cd\u6b63\u4ece\u4e66\u4e0a\u770b\u6765\u7684\uff0c\u5723\u6b66\u58eb\u90fd\u662f\u8bda\u5b9e\u5b88\u4fe1\u7684\u6b63\u4eba\u541b\u5b50\uff0c\u7edd\u4e0d\u8bf4\u8c0e\uff0c\u7edd\u4e0d\u9a97\u4eba\u7684\u2014\u2014\u4f46\u6885\u83f2\u65af\u660e\u660e\u5c31\u9a97\u4eba\uff0c\u800c\u4e14\u8fd8\u5341\u5206\u5766\u7136\uff0c\u5168\u4e0d\u5f53\u56de\u4e8b\u3002</p><p>\u4f46\u8fd9\u5e76\u4e0d\u80fd\u8bf4\u660e\u8fd9\u4e2a\u4e16\u754c\u91cc\u7684\u5723\u6b66\u58eb\u90fd\u559c\u6b22\u800d\u65e0\u8d56\u2014\u2014\u5426\u5219\u90a3\u4e2a\u53eb\u5361\u7433\u5a1c\u7684\u5973\u4eba\u9762\u72ee\u5c31\u4e0d\u4f1a\u4e0a\u5f53\u4e86\u3002\u5361\u7433\u5a1c\u4e4b\u6240\u4ee5\u8fd9\u4e48\u5bb9\u6613\u4e0a\u6885\u83f2\u65af\u7684\u5f53\uff0c\u6b63\u8bf4\u660e\u8fd9\u4e2a\u4e16\u754c\u4e2d\uff0c\u5723\u6b66\u58eb\u4f9d\u65e7\u662f\u8bda\u5b9e\u5b88\u4fe1\u7684\u4ee3\u540d\u8bcd\uff0c\u81f3\u5c11\u5927\u90e8\u5206\u662f\uff0c\u53ea\u662f\u9762\u524d\u8fd9\u4e2a\u5c11\u5973\u6709\u4e9b\u7279\u6b8a\uff0c\u5979\u4f3c\u4e4e\u4e0d\u90a3\u4e48\u8bda\u5b9e\uff0c\u6216\u8005\u8bf4\u4e0d\u5982\u666e\u904d\u8ba4\u4e3a\u7684\u90a3\u6837\u8bda\u5b9e\u3002</p><p>\u8fd9\u6b21\u5979\u548c\u743c\u6069\u8fbe\u6210\u534f\u8bae\uff0c\u4ee5\u81ea\u5df1\u4e3a\u4ee3\u4ef7\uff0c\u8ba9\u743c\u6069\u7b54\u5e94\u5e2e\u5979\u6765\u593a\u56de\u9ece\u660e\u4e4b\u77f3\uff0c\u5982\u4eca\u6076\u9b54\u6eda\u86cb\u4e86\uff0c\u4eba\u9762\u72ee\u6b7b\u5149\u4e86\uff0c\u77f3\u5934\u5c31\u5728\u8fd9\u91cc\uff0c\u7b97\u662f\u5927\u529f\u544a\u6210\u2026\u2026\u90a3\u4e48\uff0c\u5979\u662f\u4f1a\u8001\u8001\u5b9e\u5b9e\u5151\u73b0\u627f\u8bfa\u5462\uff1f\u8fd8\u662f\u7ffb\u8138\u8d56\u5e10\uff1f</p><p>\u5982\u679c\u6839\u636e\u5361\u7433\u5a1c\u7684\u906d\u9047\uff0c\u90a3\u4e48\u663e\u7136\u4e0d\u8981\u5bf9\u8fd9\u4e2a\u5973\u5723\u6b66\u58eb\u7684\u627f\u8bfa\u62b1\u4efb\u4f55\u4fe1\u4efb\u662f\u6700\u5b89\u5168\u7684\u4f5c\u6cd5\uff1b\u4f46\u662f\uff0c\u8fd9\u5176\u4e2d\u8fd8\u662f\u6709\u6240\u4e0d\u540c\u7684\u5427\u2026\u2026\u5177\u4f53\u4ec0\u4e48\u5730\u65b9\u4e0d\u540c\uff0c\u743c\u6069\u5176\u5b9e\u4e5f\u6ca1\u5341\u5206\u60f3\u660e\u767d\u3002</p><p>\u4e0d\u8fc7\u2026\u2026</p><p>\u5f53\u65f6\u4ed6\u8017\u8d39\u4e00\u4e2a\u4efb\u610f\u95e8\u5377\u8f74\uff0c\u6551\u4e86\u6885\u83f2\u65af\u7684\u6027\u547d\uff0c\u539f\u672c\u4ee5\u4e3a\u5bf9\u65b9\u4f1a\u611f\u6fc0\uff0c\u7ed3\u679c\u6885\u83f2\u65af\u544a\u8bc9\u4ed6\u8bf4\u201c\u4f60\u81ea\u613f\u7684\u201d\u3002\u5f53\u743c\u6069\u542c\u5230\u8fd9\u53e5\u8bdd\u65f6\uff0c\u4ed6\u5c31\u9690\u9690\u660e\u767d\u4e86\u4e00\u4e9b\u4e1c\u897f\u3002</p><p>\u8fd9\u53e5\u8bdd\u80cc\u540e\uff0c\u9690\u85cf\u7740\u4e00\u4e9b\u4e1c\u897f\uff0c\u6697\u542b\u7740\u5979\u7684\u67d0\u4e9b\u60f3\u6cd5\uff0c\u67d0\u4e9b\u7406\u5ff5\uff0c\u6216\u8005\u8bf4\u67d0\u4e9b\u51c6\u5219\u3002\u743c\u6069\u654f\u9510\u5730\u5bdf\u89c9\u5230\u4e86\uff0c\u4f46\u4e00\u65f6\u8fd8\u672a\u80fd\u5b8c\u5168\u60f3\u901a\uff0c\u53ea\u662f\u9690\u9690\u7ea6\u7ea6\u6709\u4e9b\u6a21\u7cca\u7684\u6982\u5ff5\u3002\u4f46\u4ed6\u77e5\u9053\uff1a\u5982\u679c\u80fd\u5b8c\u5168\u660e\u767d\u8fd9\u5176\u4e2d\u7684\u9053\u7406\uff0c\u90a3\u4e48\u5c31\u6709\u5e0c\u671b\u5b8c\u5168\u638c\u63e1\u8fd9\u4e2a\u5973\u5b69\u3002</p><p>\u5979\u4f1a\u9075\u5b88\u534f\u8bae\u3002</p><p>\u8fd9\u6216\u8bb8\u4ec5\u4ec5\u662f\u4e00\u79cd\u76f4\u89c9\uff0c\u6216\u8bb8\u4ec5\u4ec5\u662f\u4e00\u79cd\u81ea\u6211\u5b89\u6170\uff0c\u4f46\u4ed6\u4e0d\u662f\u4e2a\u4fdd\u5b88\u7684\u5bb6\u4f19\uff0c\u8fd9\u4e00\u8def\u90fd\u662f\u62ff\u6027\u547d\u8d4c\u8fc7\u6765\u7684\uff0c\u867d\u7136\u9887\u6709\u6ce2\u6298\uff0c\u5374\u7ec8\u7a76\u8fd8\u662f\u6d3b\u7740\u7ad9\u5728\u8fd9\u91cc\uff0c\u548c\u4ed6\u4f5c\u5bf9\u7684\u654c\u4eba\u5219\u5dee\u4e0d\u591a\u90fd\u53d8\u6210\u4e86\u5c38\u4f53\u3002\u90a3\u4e48\u73b0\u5728\uff0c\u4ed6\u60f3\u6700\u540e\u518d\u8d4c\u4e00\u628a\u8bd5\u8bd5\u3002</p><p>\u743c\u6069\u543b\u7740\u5979\uff0c\u5728\u8033\u8fb9\u8f7b\u58f0\u8bf4\u8bdd\uff0c\u6885\u83f2\u65af\u6ca1\u6709\u56de\u7b54\uff0c\u5f53\u7136\u743c\u6069\u4e5f\u6839\u672c\u4e0d\u9700\u8981\u4ec0\u4e48\u56de\u7b54\u3002\u4ed6\u5c06\u90a3\u628a\u53c8\u5927\u53c8\u5371\u9669\u5f88\u9ec4\u5f88\u66b4\u529b\u7684\u94f6\u5251\u4ece\u5c11\u5973\u624b\u4e2d\u53d6\u4e0b\uff0c\u4e22\u5230\u4e00\u65c1\uff0c\u7136\u540e\u4e00\u624b\u63fd\u4f4f\u5979\u7684\u8170\uff0c\u53e6\u5916\u4e00\u53ea\u624b\u5f00\u59cb\u89e3\u886c\u886b\u7684\u6263\u5b50\u3002</p><p>\u5bf9\u4e8e\u8fd9\u4e2a\u5de5\u4f5c\uff0c\u743c\u6069\u5f88\u719f\u7ec3\u3002\u4ed6\u7d20\u6765\u5c31\u559c\u6b22\u5973\u5b69\u5b50\u7a7f\u886c\u886b\uff0c\u7136\u540e\u81ea\u5df1\u4e00\u7c92\u4e00\u7c92\u5730\u53bb\u89e3\u5f00\u6263\u5b50\uff0c\u4eab\u53d7\u886c\u886b\u91cc\u7684\u7f8e\u5999\u8eab\u4f53\uff0c\u611f\u89c9\u5c31\u50cf\u662f\u5728\u62c6\u5f00\u7cbe\u7f8e\u7684\u5305\u88c5\u76d2\uff0c\u53d6\u51fa\u91cc\u9762\u7684\u793c\u7269\u3002\u8fd9\u611f\u89c9\u975e\u5e38\u68d2\uff0c\u4e0d\u8fc7\u6b64\u65f6\u4e0d\u662f\u5728\u67d4\u8f6f\u7684\u5e8a\u4e0a\uff0c\u800c\u662f\u5728\u8840\u8165\u7684\u796d\u575b\u4e0a\uff0c\u6240\u4ee5\u76f8\u5e94\u7684\uff0c\u9700\u8981\u505a\u4e00\u4e9b\u6539\u53d8\u3002</p><p>\u4ed6\u6ca1\u6709\u5c06\u6263\u5b50\u5b8c\u5168\u89e3\u5f00\uff0c\u800c\u662f\u7559\u4e86\u51e0\u9897\u3002\u4ed6\u7684\u624b\u4ece\u7a7a\u9699\u91cc\u63a2\u8fdb\u53bb\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u5c45\u7136\u6ca1\u6709\u6234\u6587\u80f8\u3002\u201c\u5514\uff0c\u8fd9\u4e2a\u4e60\u60ef\u53ef\u4e0d\u597d\u3002\u201d\u4ed6\u4f4e\u58f0\u8bc4\u4ef7\u7740\uff0c\u4e00\u672c\u6b63\u7ecf\u5730\u5bf9\u5c11\u5973\u8bf4\u3002\u867d\u7136\u4ed6\u77e5\u9053\uff0c\u56e0\u4e3a\u8981\u7a7f\u76d4\u7532\u7684\u5173\u7cfb\uff0c\u6234\u6587\u80f8\u786e\u5b9e\u4e0d\u592a\u65b9\u4fbf\uff0c\u800c\u4e14\u4f5c\u4e3a\u5c11\u5973\uff0c\u5979\u4e5f\u6709\u8db3\u591f\u50b2\u4eba\u7684\u8d44\u672c\uff0c\u5b8c\u5168\u53ef\u4ee5\u65e0\u89c6\u5730\u5fc3\u5f15\u529b\u7684\u62c9\u626f\u3002\u4f46\u957f\u671f\u5982\u6b64\uff0c\u8fd8\u662f\u5f88\u4e0d\u597d\u7684\uff0c\u6709\u4e9b\u4e8b\u60c5\u5e94\u8be5\u9632\u5fae\u675c\u6e10\uff0c\u4ece\u5c0f\u505a\u8d77\u3002</p><p>\u743c\u6069\u7684\u624b\u7ee7\u7eed\u5f80\u91cc\u6478\u7d22\uff0c\u63e1\u4f4f\u4e86\u4e00\u56e2\u9999\u8f6f\u5ae9\u6ed1\u7684\u8089\u4f53\uff0c\u5e76\u4e0d\u7855\u5927\uff0c\u4f46\u6b63\u9002\u5408\u4ed6\u7684\u624b\u638c\u5927\u5c0f\uff0c\u63e1\u8d77\u6765\u611f\u89c9\u975e\u5e38\u7f8e\u5999\u3002\u4ed6\u8086\u610f\u73a9\u5f04\u7740\uff0c\u8ba9\u5b83\u5728\u624b\u6307\u95f4\u53d8\u5e7b\u5404\u79cd\u5f62\u72b6\uff0c\u611f\u53d7\u5b83\u60ca\u4eba\u7684\u67d4\u8f6f\u548c\u5f39\u529b\uff0c\u4e00\u65f6\u6709\u4e9b\u604b\u604b\u4e0d\u820d\u3002</p><p>\u4ed6\u4e00\u8fb9\u73a9\u5f04\u7740\uff0c\u4e00\u8fb9\u4eb2\u543b\u6885\u83f2\u65af\u7684\u8138\u86cb\u548c\u8116\u9888\uff0c\u4eb2\u543b\u7cbe\u5de7\u7684\u9501\u9aa8\u548c\u96ea\u767d\u7684\u80f8\u53e3\u3002\u5c11\u5973\u521a\u624d\u7ecf\u8fc7\u4e00\u573a\u6fc0\u6218\uff0c\u53c8\u88ab\u8fd9\u6837\u73a9\u5f04\uff0c\u8eab\u4f53\u53d1\u70ed\uff0c\u4f53\u9999\u84b8\u817e\uff0c\u4ece\u677e\u5f00\u7684\u886c\u886b\u9886\u53e3\u91cc\u900f\u51fa\u6765\uff0c\u743c\u6069\u6df1\u6df1\u55c5\u7740\uff0c\u201c\u771f\u9999\u3002\u201d\u4ed6\u8bf4\uff0c\u9976\u6709\u5174\u81f4\u5730\u770b\u7740\u6885\u83f2\u65af\u3002\u5973\u5723\u6b66\u58eb\u677f\u7740\u8138\uff0c\u4eff\u4f5b\u5168\u65e0\u6240\u89c9\u3002</p><p>\u743c\u6069\u8f7b\u7b11\u7740\uff0c\u624b\u4ece\u6885\u83f2\u65af\u7684\u6000\u4e2d\u62bd\u51fa\uff0c\u6402\u5728\u5979\u7684\u8170\u4e0a\uff0c\u7136\u540e\u4e00\u70b9\u4e00\u70b9\u63a2\u8fdb\u957f\u88e4\u4e2d\u3002\u4ed6\u7684\u6307\u5c16\u89e6\u6478\u5230\u4e86\u857e\u4e1d\u82b1\u8fb9\uff0c\u53d1\u89c9\u5c11\u5973\u7a7f\u7740\u4e00\u4ef6\u975e\u5e38\u67d4\u8f6f\u7684\u4e1d\u7ec7\u4e09\u89d2\u5185\u88e4\uff0c\u4e8e\u662f\u4ed6\u7684\u624b\u6307\u6cbf\u7740\u82b1\u8fb9\u6162\u6162\u5f80\u4e0b\uff0c\u63a2\u5165\u7d27\u5e76\u7684\u53cc\u817f\u4e2d\uff0c\u611f\u89c9\u5230\u6e7f\u6da6\u7684\u6db2\u4f53\u3002\u4e1d\u7ec7\u5185\u88e4\u975e\u5e38\u8f7b\u8584\uff0c\u5df2\u7ecf\u88ab\u5c11\u5973\u7684\u4f53\u6db2\u5b8c\u5168\u6d78\u900f\uff0c\u7d27\u7d27\u8d34\u7740\u808c\u80a4\uff0c\u743c\u6069\u6478\u7d22\u7740\uff0c\u80fd\u6e05\u695a\u611f\u89c9\u5230\u82b1\u623f\u7684\u8f6e\u5ed3\u3002</p><p>\u6885\u83f2\u65af\u7684\u8138\u4e0a\u6cdb\u8d77\u7f9e\u6012\u7684\u7ea2\u6655\uff0c\u773c\u4e2d\u9690\u9690\u6709\u4e86\u4e9b\u6676\u83b9\u6cea\u5149\u3002\u743c\u6069\u770b\u5728\u773c\u91cc\uff0c\u7565\u7565\u6709\u4e9b\u5f97\u610f\u8d77\u6765\uff0c\u4ed6\u62b1\u7d27\u5c11\u5973\uff0c\u8086\u610f\u8f7b\u8584\u7740\u5979\uff0c\u624b\u6307\u6162\u6162\u62e8\u5f00\u5185\u88e4\uff0c\u4ece\u8fb9\u7f18\u91cc\u63a2\u8fdb\u53bb\uff0c\u76f4\u63a5\u78b0\u89e6\u5230\u5ae9\u6ed1\u65e0\u6bd4\u7684\u808c\u80a4\uff0c\u6162\u6162\u5f80\u4e0b\uff0c\u5148\u4e0d\u6025\u7740\u76f4\u63a5\u6311\u9017\uff0c\u5374\u5728\u5c0f\u8179\u4e0a\u8f7b\u8f7b\u629a\u6478\u7740\uff0c\u611f\u53d7\u5230\u7ec6\u8f6f\u7684\u7ed2\u6bdb\u3002\u201c\u5f88\u7a00\u758f\u5462\u3002\u201d\u4ed6\u8f7b\u58f0\u5728\u5c11\u5973\u8033\u8fb9\u8bf4\uff0c\u731c\u6d4b\u5c11\u5973\u7684\u5e74\u9f84\u5e94\u8be5\u4e0d\u4f1a\u8d85\u8fc7\u5341\u4e03\u516b\u5c81\uff0c\u8bf4\u4e0d\u5b9a\u548c\u81ea\u5df1\u5dee\u4e0d\u591a\u3002</p><p>\u624b\u6307\u7ee7\u7eed\u5f80\u4e0b\uff0c\u611f\u89c9\u5230\u4e86\u90a3\u9897\u7ec6\u5c0f\u7684\u7ea2\u8c46\uff0c\u5df2\u7ecf\u4ece\u82b1\u5507\u4e2d\u9f13\u80c0\u51fa\u6765\uff0c\u5973\u5b69\u5df2\u7ecf\u6709\u4e9b\u52a8\u60c5\u3002\u867d\u7136\u5fc3\u7406\u4e0a\u53ef\u80fd\u5e76\u4e0d\u60c5\u613f\uff0c\u4f46\u5148\u662f\u4eb2\u773c\u76ee\u7779\u5e76\u4e14\u53c2\u4e0e\u4e86\u8fd9\u6837\u6deb\u8361\u90aa\u7cdc\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u770b\u89c1\u7267\u5e08\u548c\u8482\u5a1c\u5c91\u7684\u6fc0\u60c5\u4ea4\u5abe\uff0c\u5982\u4eca\u53c8\u88ab\u743c\u6069\u8fd9\u6837\u6311\u9017\uff0c\u8eab\u4f53\u5f88\u96be\u6ca1\u6709\u8bda\u5b9e\u7684\u53cd\u5e94\u3002</p><p>\u624b\u6307\u5fae\u5fae\u7528\u529b\uff0c\u5206\u5f00\u5c11\u5973\u7684\u9634\u5507\uff0c\u743c\u6069\u6b63\u51c6\u5907\u8fdb\u4e00\u6b65\u6df1\u5165\uff0c\u7a81\u7136\u624b\u6307\u4e0a\u4f20\u6765\u4e00\u9635\u9707\u9ebb\u523a\u75db\uff0c\u4eff\u4f5b\u89e6\u7535\u4e00\u6837\u3002\u4ed6\u672c\u80fd\u5730\u624b\u4e00\u7f29\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u96be\u9053\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u8eab\u4e0a\u8fd8\u8bbe\u4e86\u4ec0\u4e48\u9632\u5fa1\u9b54\u6cd5\uff0c\u9632\u6b62\u88ab\u4eba\u4fb5\u72af\uff1f</p><p>\u4ed6\u7591\u60d1\u7740\uff0c\u770b\u7740\u6885\u83f2\u65af\uff0c\u5c11\u5973\u4e5f\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\u3002\u743c\u6069\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u770b\u6765\u8fd8\u6709\u4e9b\u9ebb\u70e6\uff0c\u4e0d\u8fc7\u597d\u4e0d\u5bb9\u6613\u6709\u8fd9\u6837\u7684\u5929\u8d50\u826f\u673a\uff0c\u96be\u9053\u80fd\u77e5\u96be\u800c\u9000\u4e48\uff1f\u4ed6\u51b7\u7b11\u7740\uff0c\u51c6\u5907\u4f38\u624b\u53bb\u8131\u4e0b\u5973\u5723\u6b66\u58eb\u7684\u957f\u88e4\uff0c\u4ed4\u7ec6\u770b\u770b\u7a76\u7adf\uff0c\u5982\u679c\u771f\u6709\u4ec0\u4e48\u9632\u5fa1\u9b54\u6cd5\uff0c\u90a3\u4ed6\u603b\u4e5f\u80fd\u770b\u51fa\u70b9\u7aef\u502a\u3002</p><p>\u6b63\u5f53\u4ed6\u51c6\u5907\u52a8\u624b\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u7a81\u7136\u8bf4\u8bdd\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u5c11\u5973\u8bf4\u8bdd\u58f0\u5f88\u8f7b\uff0c\u800c\u4e14\u975e\u5e38\u7f13\u6162\uff0c\u548c\u5e73\u65f6\u5b8c\u5168\u4e0d\u540c\u3002\u743c\u6069\u542c\u5230\u5979\u7684\u58f0\u97f3\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u4ee5\u4e3a\u5979\u5df2\u7ecf\u6062\u590d\u4e86\u884c\u52a8\u80fd\u529b\uff0c\u968f\u5373\u53d1\u73b0\u5e76\u975e\u5982\u6b64\u3002\u683c\u62c9\u5179\u7279\u7684\u5b9a\u8eab\u9b54\u6cd5\u5e76\u6ca1\u6709\u5b8c\u5168\u6d88\u9664\uff0c\u5e94\u8be5\u53ea\u662f\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u6548\u529b\u6709\u4e9b\u51cf\u5f31\u3002\u6885\u83f2\u65af\u7684\u8eab\u4f53\u4f9d\u65e7\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u52c9\u5f3a\u53ef\u4ee5\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u6211\u4e0d\u5efa\u8bae\u4f60\u8fd9\u4e48\u505a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u4f1a\u770b\u5230\u4f60\u7edd\u4e0d\u5e0c\u671b\u53d1\u751f\u7684\u7ed3\u679c\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\uff0c\u8fd9\u4f3c\u4e4e\u662f\u4e00\u79cd\u5a01\u80c1\u3002</p><p>\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u53cd\u6094\uff0c\u201d\u4ed6\u8bf4\uff0c\u534a\u662f\u786e\u5b9a\u534a\u662f\u8bd5\u63a2\uff0c\u201c\u6211\u77e5\u9053\u4f60\u8fd9\u6b21\u6ca1\u6709\u6492\u8c0e\uff0c\u6211\u77e5\u9053\u4f60\u7684\u60f3\u6cd5\u3002\u201d</p><p>\u201c\u6211\u4e0d\u4f1a\u53cd\u6094\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u8fd9\u53ea\u662f\u4e2a\u5efa\u8bae\u3002\u201d</p><p>\u743c\u6069\u9690\u9690\u7ea6\u7ea6\u660e\u767d\u4e00\u4e9b\u4e1c\u897f\uff0c\u4f46\u8fd8\u662f\u4e0d\u80fd\u6e05\u6670\u5730\u628a\u63e1\u4f4f\u3002\u201c\u591a\u8c22\uff0c\u201d\u4ed6\u8bf4\uff0c\u624b\u5f00\u59cb\u5f80\u4e0b\u4f38\uff0c\u201c\u6211\u60f3\u6211\u6709\u6700\u540e\u51b3\u5b9a\u7684\u6743\u529b\u3002\u201d</p><p>\u5c11\u5973\u611f\u89c9\u81ea\u5df1\u7684\u8170\u5e26\u5df2\u7ecf\u88ab\u89e3\u5f00\uff0c\u201c\u4e0d\u884c\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u4e0d\u884c\u3002\u201d</p><p>\u743c\u6069\u7684\u7709\u6bdb\u5fae\u5fae\u626c\u8d77\u6765\uff0c\u201c\u4e0d\u884c\uff1f\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u6211\u4eec\u8bf4\u597d\u7684\uff0c\u6211\u5e2e\u4f60\u593a\u56de\u9ece\u660e\u4e4b\u77f3\uff0c\u4f60\u5c31\u662f\u6211\u7684\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u8138\u5fae\u5fae\u6cdb\u8d77\u7ea2\u6655\uff0c\u201c\u6211\u7b54\u5e94\u8fc7\uff0c\u6211\u662f\u4f60\u7684\uff0c\u4f46\u2026\u2026\u90a3\u4e2a\u5730\u65b9\u4e0d\u884c\u3002\u201d</p><p>\u201c\u90a3\u4e2a\u5730\u65b9\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u968f\u5373\u660e\u767d\u8fc7\u6765\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u5c11\u5973\u52aa\u529b\u60f3\u6447\u5934\uff0c\u4f46\u5979\u65e0\u6cd5\u52a8\u4f5c\u3002\u201c\u53cd\u6b63\u2026\u2026\u5c31\u662f\u4e0d\u884c\uff0c\u201d\u5979\u5403\u529b\u5730\u8bf4\uff0c\u201c\u522b\u95ee\u539f\u56e0\uff0c\u6c42\u4f60\u3002\u201d</p><p>\u5979\u7684\u773c\u4e2d\u6cea\u5149\u9690\u9690\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u6eda\u843d\u4e0b\u6765\uff0c\u5728\u8138\u988a\u4e0a\u7559\u4e0b\u4e24\u9053\u6e05\u75d5\u3002\u743c\u6069\u6ca1\u60f3\u5230\u8fd9\u6837\u521a\u5f3a\u7684\u5973\u5b69\u4e5f\u4f1a\u54ed\uff0c\u4e00\u65f6\u5012\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\uff0c\u4f46\u4ed6\u968f\u5373\u9547\u5b9a\u4e0b\u6765\u3002\u201c\u6211\u4e0d\u7ba1\uff0c\u201d\u4ed6\u86ee\u6a2a\u5730\u8bf4\uff0c\u201c\u4f60\u7b54\u5e94\u4e86\u6211\uff0c\u4e0d\u80fd\u53cd\u6094\u3002\u201d</p><p>\u201c\u6211\u6ca1\u6709\u53cd\u6094\uff0c\u201d\u6885\u83f2\u65af\u5f3a\u5fcd\u7740\u773c\u6cea\uff0c\u201c\u6211\u7b54\u5e94\u4e86\u2026\u2026\u53ea\u662f\u90a3\u91cc\u771f\u7684\u4e0d\u884c\u2026\u2026\u201d</p><p>\u743c\u6069\u7684\u5fc3\u5934\uff0c\u7591\u60d1\u8d8a\u6765\u8d8a\u91cd\u3002\u201c\u4f60\u81f3\u5c11\u5f97\u544a\u8bc9\u6211\u4e3a\u4ec0\u4e48\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7ed9\u6211\u7406\u7531\u3002\u201d</p><p>\u201c\u522b\u95ee\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u5fae\u5730\u6447\u7740\u5934\uff0c\u7136\u540e\u5979\u53d1\u73b0\u81ea\u5df1\u7684\u8eab\u4f53\u5df2\u7ecf\u5f00\u59cb\u5728\u6e10\u6e10\u6062\u590d\u81ea\u7531\uff0c\u201c\u522b\u95ee\u4e3a\u4ec0\u4e48\uff0c\u201d\u5979\u6073\u6c42\u7740\uff0c\u201c\u6211\u7b54\u5e94\u4e86\uff0c\u6211\u662f\u4f60\u7684\uff0c\u4f46\u522b\u78b0\u90a3\u91cc\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u4ed6\u62b1\u7740\u6885\u83f2\u65af\uff0c\u8eab\u4f53\u7d27\u7d27\u8d34\u7740\uff0c\u5973\u5b69\u611f\u89c9\u5230\u4ed6\u4e0b\u4f53\u7684\u786c\u633a\uff0c\u9694\u7740\u8863\u670d\u90fd\u80fd\u611f\u89c9\u5230\u6eda\u70eb\u7684\u6c14\u606f\u3002\u201c\u597d\u5427\uff0c\u201d\u5979\u7a81\u7136\u9547\u5b9a\u4e0b\u6765\uff0c\u58f0\u97f3\u53c8\u6062\u590d\u4e86\u51e0\u5206\u5e73\u65f6\u7684\u4ece\u5bb9\u51b7\u9759\uff0c\u201c\u6211\u4eec\u518d\u505a\u4e2a\u4ea4\u6613\u5982\u4f55\uff1f\u201d</p></article>"}}}]);
