(window.webpackJsonp=window.webpackJsonp||[]).push([[732],{755:function(p){p.exports={chapterTitle:null,title:"\u7b2c570\u7ae0 \u70ed\u8840\u51fa\u624b",content:"<article><h2>\u7b2c570\u7ae0 \u70ed\u8840\u51fa\u624b</h2><p>\u3000\u3000\u8f66\u4ece\u97f3\u4e50\u5b66\u9662\u7684\u5927\u95e8\u8fdb\u53bb\uff0c\u5c31\u505c\u5728\u6885\u6797\u5317\u4fa7\u7684\u5e7f\u573a\u4e0a\uff0c\u90a3\u91cc\u79bb\u97f3\u4e50\u5b66\u9662\u7684\u5973\u751f\u5bbf\u820d\u697c\u8fd8\u4e00\u6bb5\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u9648\u6653\u677e\u7a7f\u7740\u5236\u670d\uff0c\u5c06\u8b66\u68cd\u62ff\u5728\u624b\u91cc\u3002\u5f20\u606a\u62c9\u8fc7\u4e00\u540d\u7ecf\u8fc7\u5b66\u751f\u95ee\u6e05\u695a\u60c5\u51b5\uff1b\u4e4b\u524d\u5c31\u6709\u4e00\u8f86\u8b66\u8f66\u5f00\u4e86\u8fc7\u6765\uff0c\u4f46\u662f\u56f4\u5728\u5973\u751f\u5bbf\u820d\u697c\u5916\u7684\u4f53\u6821\u751f\u5728\u8b66\u8f66\u8fc7\u6765\u4e4b\u524d\u5c31\u4e09\u4e09\u4e24\u4e24\u7684\u5206\u6563\u5f00\uff0c\u4e5f\u4e0d\u79bb\u5f00\uff0c\u8d76\u5230\u7684\u6c11\u8b66\u63a8\u8bf4\u8fd9\u8fde\u666e\u901a\u7684\u6ecb\u4e8b\u6270\u4e71\u90fd\u7b97\u4e0d\u4e86\uff0c\u4fbf\u8981\u56de\u53bb\u3002\u8fd8\u662f\u5b66\u6821\u9886\u5bfc\u51fa\u9762\u5c06\u6c11\u8b66\u7559\u4e0b\u6765\u4e00\u8d77\u5f00\u5bfc\u56f4\u5728\u5973\u751f\u5bbf\u820d\u697c\u524d\u7684\u4f53\u6821\u751f\u3002\u805a\u8fc7\u6765\u7684\u4f53\u6821\u751f\u73b0\u5728\u90fd\u6709\u4e03\u516b\u5341\u4eba\uff0c\u53eb\u56a3\u7740\u8ba9\u97f3\u4e50\u5b66\u6821\u5c06\u6253\u4eba\u7684\u51f6\u624b\u4ea4\u51fa\u6765\uff1b\u540e\u6765\u53c8\u6765\u4e86\u4e00\u8f86\u8b66\u8f66\uff0c\u542c\u8bf4\u662f\u4e1c\u534e\u533a\u516c\u5b89\u5c40\u7684\u526f\u5c40\u957f\u8fc7\u6765\u8c03\u89e3\uff0c\u8981\u6c42\u805a\u5728\u97f3\u4e50\u5b66\u9662\u91cc\u7684\u4f53\u6821\u751f\u6563\u5f00\uff0c\u5374\u5e2e\u90a3\u4e9b\u4f53\u6821\u751f\u8981\u6c42\u97f3\u4e50\u5b66\u6821\u5c06\u52a8\u624b\u6253\u4eba\u7684\u5b66\u751f\u5f00\u9664\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u97f3\u4e50\u5b66\u9662\u4e0e\u4f53\u80b2\u5b66\u9662\u7684\u7ea7\u522b\u4e0d\u591f\uff0c\u97f3\u4e50\u5b66\u9662\u5f52\u7701\u6587\u5316\u5385\u4e3b\u7ba1\uff0c\u4f53\u80b2\u5b66\u9662\u5f52\u7701\u4f53\u59d4\u4e3b\u7ba1\uff0c\u6307\u4f7f\u4e0d\u52a8\u4e1c\u534e\u533a\u7684\u6c11\u8b66\uff0c\u6362\u6210\u6559\u80b2\u90e8\u4e3b\u7ba1\u7684\u4e1c\u6d77\u5927\u5b66\u6216\u8005\u5e08\u5927\uff0c\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u4ef6\u65e9\u5c31\u8feb\u4f7f\u4f53\u80b2\u5b66\u9662\u7684\u9886\u5bfc\u51fa\u9762\u5c06\u5b66\u751f\u90fd\u6293\u56de\u53bb\u4e86\uff0c\u54ea\u91cc\u9700\u8981\u8c03\u505c\uff1f</p><p>\u3000\u3000\u8fd9\u4e5f\u9020\u6210\u4f53\u6821\u751f\u4e60\u60ef\u6027\u7684\u5230\u97f3\u4e50\u5b66\u9662\u6765\u6ecb\u4e8b\u751f\u975e\u3002</p><p>\u3000\u3000\u201c\u54fc\uff0c\u201d\u5f20\u606a\u9f3b\u5934\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u5bf9\u9648\u6653\u751f\u8bf4\uff0c\u201c\u6211\u4eec\u5148\u8fc7\u53bb\uff0c\u4ed6\u4eec\u6562\u671d\u6211\u4eec\u52a8\u624b\uff0c\u4f60\u8ddf\u5728\u540e\u9762\u8fc7\u6765\u65f6\u4e0d\u8981\u624b\u8f6f\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u6709\u4e03\u516b\u5341\u4e2a\u4eba\u554a\u2026\u2026\u201d\u5b59\u9759\u6aac\u62c5\u5fe7\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4f60\u8981\u6015\u5c31\u8ddf\u5c0f\u8bd7\u7559\u5728\u8f66\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u6015\uff01\u201d\u5b59\u9759\u6aac\u7709\u773c\u8212\u5c55\u5f00\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u8ddf\u4f60\u4eec\u5148\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u8d70\u5230\u5973\u751f\u5bbf\u820d\u697c\u524d\uff0c\u5dee\u4e0d\u591a\u6709\u4e09\u56db\u767e\u4eba\u7ad9\u5728\u8def\u7259\u4e0a\u770b\u70ed\u95f9\u3002\u4f53\u6821\u751f\u90fd\u7a7f\u7740\u8fd0\u52a8\u670d\uff0c\u5927\u591a\u4eba\u9ad8\u9a6c\u5927\uff0c\u4e0e\u97f3\u4e50\u5b66\u9662\u7684\u5b66\u751f\u8fe5\u7136\u4e0d\u540c\uff0c\u6ca1\u770b\u89c1\u90a3\u4e2a\u68d2\u7403\u5e3d\uff0c\u4e24\u8f86\u8b66\u8f66\u505c\u5728\u5f53\u4e2d\uff0c\u6709\u4e24\u4e2a\u6c11\u8b66\u7ad9\u5728\u8b66\u8f66\u5c3e\u7aef\u804a\u5929\uff0c\u5176\u4ed6\u4eba\u7ed9\u8b66\u8f66\u6321\u4f4f\uff0c\u60f3\u5fc5\u90a3\u4e2a\u68d2\u7403\u5e3d\u4e5f\u5728\u8b66\u8f66\u90a3\u4e00\u4fa7\u3002</p><p>\u3000\u3000\u4e0b\u5348\u4e0e\u68d2\u7403\u5e3d\u4e00\u8d77\u5728\u4e07\u8c61\u6e9c\u51b0\u573a\u7684\u4e00\u4e2a\u4f53\u6821\u751f\u770b\u89c1\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u8d70\u8fc7\u6765\uff0c\u5927\u53eb\u8d77\u6765\uff1a\u201c\u90a3\u4e24\u4e2a\u60f9\u4e8b\u7684\u5c0fB\u4ed6\u5988\u7684\u7adf\u7136\u6562\u8d70\u8fc7\u6765\uff0c\u4ed6\u5a18\u7684\uff0c\u4ed6\u4eec\u6ca1\u6709\u8eb2\u5728\u5bbf\u820d\u697c\u91cc\u2026\u2026\u201d\u6839\u672c\u65e0\u89c6\u5f20\u606a\u8eab\u8fb9\u7684\u9a6c\u6d77\u9f99\u4e0e\u8ddf\u7740\u540e\u9762\u7a7f\u8b66\u670d\u3001\u62ff\u8b66\u68cd\u7684\u9648\u6653\u677e\uff0c\u53eb\u56a3\u7740\uff0c\u201c\u6253\u6b7b\u90a3\u4e24\u5e26\u8e6d\u7684\u2026\u2026\u201d\u4e94\u516d\u4e2a\u4f53\u6821\u751f\u8ddf\u4ed6\u4e00\u8d77\u671d\u8fd9\u8fb9\u8dd1\u8fc7\u6765\uff0c\u4ed6\u4eec\u8fd8\u662f\u6015\u5f20\u606a\u4ed6\u4eec\u770b\u60c5\u5f62\u4e0d\u5bf9\u4f1a\u8dd1\u6389\u3002</p><p>\u3000\u3000\u201c\u4f4f\u624b\uff0c\u4e0d\u8bb8\u6253\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u4e24\u4e2a\u7a7f\u5236\u670d\u7684\u6c11\u8b66\u88c5\u6a21\u4f5c\u6837\u7684\u558a\u7740\uff0c\u5374\u6ca1\u6709\u8981\u8dd1\u8fc7\u6765\u5236\u6b62\u4ed6\u4eec\u7684\u610f\u601d\uff0c\u6487\u5634\u51b7\u7b11\uff0c\u679c\u7136\u5982\u6b64\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u770b\u7740\u516d\u4e2a\u4eba\u9ad8\u9a6c\u5927\u7684\u4f53\u6821\u751f\u8dd1\u8fc7\u6765\uff0c\u5fc3\u91cc\u8fd8\u6709\u4e9b\u53d1\u865a\uff0c\u4e0b\u610f\u8bc6\u7684\u6293\u4f4f\u5f20\u606a\u7684\u624b\u3002\u5f20\u606a\u611f\u89c9\u5230\u5b59\u9759\u6aac\u7684\u624b\u5fc3\u6709\u4e9b\u6f6e\uff0c\u5fc3\u60f3\u8fd9\u59ae\u5b50\u603b\u662f\u6709\u5fc3\u865a\u7684\u65f6\u5019\uff0c\u62cd\u4e86\u62cd\u5979\u7684\u624b\u80cc\uff0c\u5c06\u5979\u7684\u624b\u63b0\u5f00\uff0c\u7b49\u4f1a\u513f\u9a6c\u6d77\u9f99\u5728\u524d\u9762\u6321\u7740\uff0c\u4ed6\u4e5f\u60f3\u6d3b\u52a8\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u68d2\u7403\u5e3d\u7ed9\u4eba\u6276\u8fc7\u6765\u8d70\u5230\u8b66\u8f66\u5c3e\u7aef\uff0c\u770b\u5230\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\uff0c\u6076\u72e0\u72e0\u7684\u5927\u53eb\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u6253\u65ad\u4ed6\u4eec\u7684\u817f\uff0c\u51fa\u4e86\u95ee\u9898\u6211\u8d1f\u8d23\u3002\u201d\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u4e2d\u5e74\u8b66\u5b98\u4e5f\u662f\u53ea\u6309\u4f4f\u68d2\u7403\u5e3d\u4f38\u51fa\u53bb\u7684\u624b\uff0c\u770b\u4ed6\u7684\u610f\u601d\u662f\u8981\u5148\u653e\u7eb5\u8fd9\u4e9b\u4f53\u6821\u751f\u53d1\u6cc4\u4e00\u4e0b\u6012\u706b\u518d\u6536\u62fe\u5c40\u9762\uff0c\u53ea\u662f\u770b\u5230\u7a7f\u7740\u5236\u670d\u7684\u9648\u6653\u677e\uff0c\u6709\u4e9b\u7591\u60d1\uff0c\u53ea\u5f53\u662f\u8fd9\u4e2a\u7247\u533a\u7684\u6c11\u8b66\uff0c\u4e5f\u6ca1\u6709\u5728\u610f\u3002</p><p>\u3000\u3000\u201c\u53f3\u811a\u521a\u9aa8\u88c2\u8fc7\uff0c\u8981\u4e0d\u7136\u7ed9\u4ed6\u4eec\u6253\u65ad\u6211\u7684\u5de6\u817f\uff1f\u201d\u5f20\u606a\u8ddf\u9a6c\u6d77\u9f99\u5f00\u73a9\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u53ef\u65e0\u6687\u56de\u5934\u8ddf\u5f20\u606a\u8bf4\u7b11\uff0c\u4e24\u540d\u8dd1\u5f97\u5feb\u7684\u4f53\u6821\u751f\u5df2\u7ecf\u51b2\u5230\u4ed6\u9762\u524d\uff0c\u4e00\u4e2a\u4eba\u4f38\u624b\u6293\u8981\u4ed6\u7684\u9886\u5b50\u6765\uff0c\u9a6c\u6d77\u9f99\u4f38\u624b\u62d7\u4f4f\u90a3\u4eba\u7684\u864e\u53e3\u4e00\u63b0\uff0c\u4e00\u811a\u98de\u8e22\u53e6\u4e00\u4eba\u7684\u4fa7\u80ef\u3002\u5f20\u606a\u5219\u662f\u902e\u5230\u90a3\u4e2a\u7ed9\u9a6c\u6d77\u9f99\u8e22\u5931\u53bb\u5e73\u8861\u7684\u4f53\u6821\u751f\u515c\u7740\u4ed6\u7684\u8138\u4e0d\u8981\u547d\u7684\u64c2\u4e86\u51e0\u62f3\u3002\u9a6c\u6d77\u9f99\u7684\u4e00\u817f\u529b\u91cf\u5f88\u8db3\uff0c\u8e22\u5f97\u90a3\u4eba\u59cb\u7ec8\u7ad9\u4e0d\u7a33\uff0c\u53ea\u662f\u7ed9\u5f20\u606a\u501f\u8db3\u6253\u5f97\u55f7\u55f7\u76f4\u53eb\uff0c\u8fd9\u65f6\u5019\u5b59\u9759\u6aac\u624d\u5174\u594b\u7684\u5c16\u53eb\u8d77\u6765\uff0c\u62fd\u7740\u90a3\u4e2a\u4f53\u6821\u751f\u7684\u5934\u53d1\u731b\u8e39\u4ed6\u7684\u5c0f\u817f\u3002\u9a6c\u6d77\u9f99\u624b\u91cc\u8fd8\u6709\u4e2a\u7ed9\u4ed6\u62d7\u4f4f\u864e\u53e3\u3001\u8eab\u5b50\u90fd\u75db\u5f97\u77ee\u4e0b\u6765\u7684\u4f53\u6821\u751f\uff0c\u62c9\u4ed6\u5f80\u81ea\u5df1\u6000\u91cc\u4e00\u626f\uff0c\u634f\u7740\u62f3\u624b\u671d\u90a3\u4eba\u80a9\u5934\u4e00\u78d5\uff0c\u5c31\u5c06\u90a3\u4eba\u7684\u80f3\u818a\u6253\u8131\u81fc\uff0c\u53c8\u5c06\u90a3\u4eba\u63a8\u7740\u671d\u524d\u9762\u51b2\u8fc7\u6765\u7684\u4e00\u540d\u4f53\u6821\u751f\u649e\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f4f\u624b\uff0c\u90fd\u7ed9\u6211\u4f4f\u624b\u2026\u2026\u201d\u9648\u6653\u677e\u8fd9\u65f6\u5019\u6325\u821e\u7740\u8b66\u68cd\u4ece\u540e\u9762\u51b2\u4e0a\u6765\uff0c\u902e\u7740\u51b2\u8fc7\u6765\u7684\u7b2c\u56db\u540d\u4f53\u6821\u751f\u624b\u811a\u8b66\u68cd\u731b\u62bd\u4e0b\u53bb\uff0c\u540e\u9762\u8fd8\u6709\u4e24\u4e2a\u4eba\u90fd\u7ed9\u9a6c\u6d77\u9f99\u4e00\u4eba\u63a5\u4e0b\u6765\u2026\u2026</p><p>\u3000\u3000\u8fd9\u4e0d\u8fc7\u662f\u53d1\u751f\u5728\u8f6c\u773c\u95f4\u7684\u4e8b\u60c5\uff0c\u5bbf\u820d\u697c\u524d\u7ad9\u7740\u7684\u97f3\u4e50\u5b66\u9662\u7684\u5b66\u751f\u3001\u4f53\u80b2\u5b66\u9662\u7684\u5b66\u751f\u90fd\u770b\u50bb\u773c\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u7b2c\u4e94\u4e2a\u4f53\u6821\u751f\u7ed9\u4eba\u9a6c\u6d77\u9f99\u8e39\u4e2d\u5927\u817f\u5185\u4fa7\uff0c\u817f\u4e0b\u4e00\u62d0\u8dcc\u51fa\u53bb\u8001\u8fdc\uff0c\u60f3\u7ad9\u8d77\u6765\uff0c\u5927\u817f\u4e00\u8f6f\u6ca1\u80fd\u7ad9\u8d77\u6765\uff0c\u53e6\u4e00\u4e2a\u4eba\u89c1\u673a\u8df3\u5f00\uff0c\u6ca1\u6562\u51b2\u4e0a\u6765\u8ddf\u9a6c\u6d77\u9f99\u4e1c\u52a8\u624b\uff1b\u9a6c\u6d77\u9f99\u811a\u671d\u7740\u7ed9\u649e\u5012\u5728\u5730\u4e0a\u7684\u7b2c\u4e09\u540d\u4f53\u6821\u751f\u8e39\u8fc7\u53bb\uff0c\u4e00\u811a\u8e39\u5728\u90a3\u4eba\u7684\u5c3e\u690e\u9aa8\uff0c\u4e00\u5634\u5543\u5728\u8349\u5730\u91cc\uff0c\u4e5f\u6ca1\u6709\u518d\u7ad9\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u8ba9\u4f60\u4eec\u4f4f\u624b\uff0c\u4e3a\u4ec0\u4e48\u51b2\u4e0a\u6765\u5c31\u6253\u4eba\uff1f\u201d\u9648\u6653\u677e\u6325\u7740\u8b66\u68cd\u51b2\u7740\u5012\u5728\u5730\u4e0a\u7684\u4e94\u540d\u4f53\u6821\u751f\u4e25\u5389\u7684\u559d\u65a5\u3002</p><p>\u3000\u3000\u201c\u662f\u4ed6\u4eec\u2026\u2026\u55f7\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u4e2a\u7ed9\u9a6c\u6d77\u9f99\u6253\u8131\u81fc\u7684\u4f53\u6821\u751f\u4f38\u51fa\u53e6\u4e00\u53ea\u624b\u6307\u7740\u9a6c\u6d77\u9f99\u60f3\u8bf4\u81ea\u5df1\u662f\u88ab\u4ed6\u6253\u7684\uff0c\u6ca1\u6709\u60f3\u5230\u4f38\u51fa\u624b\u5374\u7ed9\u9648\u6653\u677e\u62ff\u8b66\u68cd\u671d\u624b\u80cc\u4e0a\u62bd\u4e86\u4e00\u8bb0\u72e0\u7684\uff0c\u9648\u6653\u677e\u62ff\u8b66\u68cd\u6307\u7740\u90a3\u5c0f\u5b50\u7684\u8138\uff1a\u201c\u7ed9\u6211\u8001\u5b9e\u5750\u597d\u3002\u201d</p><p>\u3000\u3000\u201c\u591f\u4e86\uff0c\u591f\u4e86\uff01\u201d\u5f20\u606a\u4ece\u540e\u9762\u62b1\u4f4f\u5b59\u9759\u6aac\uff0c\u8ba9\u5979\u4ece\u72c2\u8e81\u7684\u60c5\u7eea\u4e2d\u5b89\u9759\u4e0b\u6765\uff0c\u5979\u5728\u637b\u811a\u4e0b\u90a3\u5c0f\u5b50\u7684\u624b\u6307\uff0c\u628a\u4eba\u641e\u6b8b\u5e9f\u4e86\uff0c\u6bd5\u7adf\u4e0d\u597d\u6536\u62fe\u3002</p><p>\u3000\u3000\u4f53\u6821\u751f\u4e89\u5f3a\u6597\u72e0\u7684\u4e0d\u5c11\uff0c\u6709\u4e30\u5bcc\u6253\u67b6\u7ecf\u9a8c\u7684\u4e5f\u4e0d\u5c11\uff0c\u4f46\u662f\u54ea\u91cc\u89c1\u8fc7\u9a6c\u6d77\u9f99\u7684\u96f7\u9706\u624b\u6bb5\uff0c\u5c06\u4e94\u540d\u4f53\u6821\u751f\u6253\u7ffb\u5728\u5730\u4e0d\u8fc7\u8f6c\u773c\u95f4\u7684\u4e8b\u60c5\uff1f\u5f53\u521d\u8d75\u9526\u8363\u56db\u4e2a\u4fdd\u9556\u4e00\u8d77\u4e0a\u4e5f\u4e0d\u8fc7\u8ddf\u9a6c\u6d77\u9f99\u4e24\u8d25\u4ff1\u4f24\uff0c\u9a6c\u6d77\u9f99\u4e4b\u524d\u8fd8\u6253\u7ffb\u4e86\u5341\u591a\u4e2a\u5230\u4ed6\u5bb6\u6ecb\u4e8b\u7684\u5c0f\u6df7\u6df7\u3002</p><p>\u3000\u3000\u51e0\u5341\u540d\u4f53\u6821\u751f\u90fd\u56f4\u8fc7\u6765\uff0c\u4f46\u662f\u4ed6\u4eec\u54ea\u91cc\u518d\u6562\u52a8\u624b\uff0c\u4e5f\u4e0d\u662f\u8bf4\u4ed6\u4eec\u5c31\u5f88\u60e7\u6015\u9a6c\u6d77\u9f99\u2014\u2014\u6bd5\u7adf\u597d\u6c49\u67b6\u4e0d\u4f4f\u4eba\u591a\uff0c\u4f53\u6821\u751f\u6253\u7fa4\u67b6\u8fd8\u662f\u6709\u5f88\u4e30\u5bcc\u7684\u7ecf\u9a8c\u7684\u2014\u2014\u800c\u662f\u62ff\u7740\u8b66\u68cd\u3001\u7a7f\u7740\u5236\u670d\u7684\u9648\u6653\u677e\u8ba9\u4ed6\u4eec\u540c\u6837\u5fc3\u6709\u4f59\u60b8\u3002</p><p>\u3000\u3000\u6709\u591a\u5c11\u6d41\u6c13\u6562\u5f53\u8857\u6253\u8b66\u5bdf\u7684\uff1f\u66f4\u4e0d\u7528\u8bf4\u4e0e\u8fd8\u4e0d\u7b97\u6d41\u6c13\u7684\u4f53\u6821\u751f\u4e86\u3002</p><p>\u3000\u3000\u201c\u90fd\u7ed9\u6211\u4f4f\u624b\u2026\u2026\u201d\u4e2d\u5e74\u8b66\u5b98\u5148\u559d\u6b62\u4e86\u4e00\u58f0\uff0c\u624d\u8d70\u4e0a\u524d\u6765\uff0c\u77a5\u773c\u770b\u7740\u9648\u6653\u677e\u80a9\u4e0a\u7684\u8b66\u8854\uff1a\u4e09\u7ea7\u8b66\u7763\uff0c\u4e09\u7ea7\u8b66\u7763\u6db5\u76d6\u7684\u804c\u52a1\u5f88\u5e7f\uff0c\u4ece\u8d44\u6df1\u79d1\u5458\u5230\u5904\u7ea7\u526f\u804c\u90fd\u53ef\u80fd\u6388\u662f\u4e09\u7ea7\u8b66\u7763\uff0c\u4e2d\u5e74\u8b66\u5b98\u89c1\u9648\u6653\u677e\u5e74\u7eaa\u5f88\u8f7b\uff0c\u76b1\u7740\u7709\u5934\u95ee\uff0c\u201c\u4f60\u662f\u54ea\u4e2a\u533a\u7684\uff1f\u201d\u540e\u9762\u4e94\u4e2a\u6c11\u8b66\u56f4\u8fc7\u6765\uff0c\u5c06\u805a\u5728\u5916\u56f4\u7684\u4f53\u6821\u751f\u9694\u5f00\uff0c\u8fd9\u65f6\u5019\u624d\u8868\u73b0\u51fa\u8981\u5236\u6b62\u4e8b\u4ef6\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u516c\u5b89\u5385\u8b66\u52a1\u7763\u5bdf\u5904\u7684\uff0c\u201d\u9648\u6653\u677e\u5c06\u7763\u5bdf\u8bc1\u4ef6\u4eae\u51fa\u6765\uff0c\u201c\u4f53\u80b2\u5b66\u9662\u5b66\u751f\u805a\u96c6\u97f3\u4e50\u5b66\u9662\u6ecb\u4e8b\u6270\u4e71\u5b66\u6821\u6cbb\u5b89\uff0c\u4e1c\u534e\u533a\u5206\u5c40\u5236\u6b62\u4e0d\u529b\uff0c\u7763\u5bdf\u5904\u63a5\u5230\u4e3e\u62a5\uff0c\u6211\u8fc7\u6765\u770b\u4e00\u4e0b\u2026\u2026\u8bf7\u51fa\u793a\u4f60\u7684\u8bc1\u4ef6\uff0c\u8fd8\u6709\u8bf7\u4f60\u89e3\u91ca\u521a\u624d\u4f60\u4eec\u4e1c\u534e\u5206\u5c40\u7684\u6c11\u8b66\u4e3a\u4ec0\u4e48\u4e0d\u5236\u6b62\u8fd9\u4e9b\u4f53\u6821\u751f\u51b2\u4e0a\u6765\u6253\u4eba\u3002\u201d</p><p>\u3000\u3000\u90a3\u540d\u4e2d\u5e74\u8b66\u5b98\u5fae\u5fae\u4e00\u6123\uff0c\u773c\u775b\u626b\u8fc7\u5b59\u9759\u6aac\u3001\u5f20\u606a\uff0c\u8fd8\u6709\u7ad9\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u9a6c\u6d77\u9f99\uff0c\u53d1\u73b0\u4eca\u5929\u8e22\u5230\u94c1\u677f\u4e0a\u4e86\uff0c\u4eba\u5bb6\u7adf\u7136\u642c\u51fa\u516c\u5b89\u5385\u8b66\u52a1\u7763\u5bdf\u5904\u7684\u724c\u5b50\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u662f\u4e1c\u534e\u533a\u5206\u5c40\u7684\u526f\u5c40\u957f\u5434\u7231\u519b\uff0c\u8bc1\u4ef6\u5c31\u6ca1\u6709\u5fc5\u8981\u4eae\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf7\u914d\u5408\u6211\u4eec\u7684\u7763\u5bdf\u5de5\u4f5c\u51fa\u793a\u4f60\u7684\u8bc1\u4ef6\u3002\u201d\u9648\u6653\u677e\u76ef\u7740\u5434\u7231\u519b\u7684\u8138\uff0c\u516c\u4e8b\u516c\u529e\u7684\u53e3\u6c14\u518d\u6b21\u8bf7\u4ed6\u51fa\u793a\u8bc1\u4ef6\uff0c\u770b\u7740\u4ed6\u8138\u4e0a\u7684\u7b11\u610f\u4e00\u70b9\u70b9\u7ed9\u9634\u6c89\u7684\u795e\u8272\u66ff\u4ee3\u3002\u5c31\u7b97\u5efa\u90ba\u5e02\u516c\u5b89\u5c40\u5c40\u957f\u7ad9\u8fd9\u91cc\uff0c\u9648\u6653\u677e\u4f9d\u7167\u7a0b\u5e8f\u90fd\u53ef\u4ee5\u68c0\u67e5\u5bf9\u65b9\u7684\u8bc1\u4ef6\u2014\u2014\u9648\u6653\u677e\u5f53\u7136\u4e0d\u4f1a\u505a\u8fd9\u4e48\u4e8c\u767e\u4e94\u7684\u4e8b\u60c5\u2014\u2014\u5bf9\u4e00\u4e2a\u533a\u533a\u4e1c\u534e\u533a\u5206\u5c40\u7684\u526f\u5c40\u957f\uff0c\u9648\u6653\u677e\u6ca1\u6709\u5fc5\u8981\u7ed9\u4ed6\u7559\u60c5\u9762\u3002</p><p>\u3000\u3000\u5434\u7231\u519b\u538b\u7740\u4e00\u809a\u5b50\u706b\uff0c\u5c06\u8bc1\u4ef6\u638f\u51fa\u53bb\u66ff\u7ed9\u9648\u6653\u677e\u3002</p><p>\u3000\u3000\u9648\u6653\u677e\u7ffb\u770b\u770b\u5434\u7231\u519b\u7684\u8bc1\u4ef6\uff0c\u8bf4\u9053\uff1a\u201c\u5434\u7231\u519b\u526f\u5c40\u957f\uff0c\u8bf7\u4f60\u89e3\u91ca\u4e00\u4e0b\u521a\u624d\u53d1\u751f\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u6211\u89e3\u91ca\u4ec0\u4e48\uff1f\u201d\u5434\u7231\u519b\u76b1\u7740\u7709\u5934\uff0c\u4ed6\u4e00\u4e2a\u6b63\u5904\u7ea7\u4e00\u7ea7\u8b66\u7763\u7684\u5e02\u5206\u5c40\u5e38\u52a1\u526f\u5c40\u957f\u7ed9\u4e00\u4e2a\u4e09\u7ea7\u8b66\u7763\u7684\u5385\u7763\u5bdf\u5904\u79d1\u957f\u5f53\u7740\u90e8\u5c5e\u7684\u9762\u677f\u7740\u8138\u8d28\u95ee\u3001\u6559\u8bad\uff0c\u4ed6\u7684\u9762\u5b50\u6401\u54ea\u91cc\u53bb\uff1f\u201c\u4eba\u90fd\u7ed9\u4f60\u4eec\u6253\u4e86\uff0c\u8981\u6211\u89e3\u91ca\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u662f\u4f60\u8eab\u8fb9\u4e24\u4e2a\u4eba\u5148\u6ecb\u4e8b\u751f\u975e\uff0c\u600e\u4e48\u4f1a\u5c06\u4e8b\u60c5\u95f9\u8fd9\u4e48\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8981\u6c42\u4f60\u4eec\u5c06\u4ed6\u2026\u2026\u201d\u9648\u6653\u677e\u6307\u7740\u8ba9\u4eba\u6276\u7740\u8d70\u8fc7\u6765\u7684\u68d2\u7403\u5e3d\uff0c\u8d23\u4ee4\u8bf4\u9053\uff0c\u201c\u5efa\u90ba\u4f53\u80b2\u5b66\u9662\u4e5d\u4e94\u7ea7\u4f53\u80b2\u6559\u80b2\u4e13\u4e1a\u5b66\u751f\u5b59\u83ca\u6770\u5e26\u5230\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u63a5\u53d7\u52304\u670810\u65e5\u4e00\u8d77\u6d89\u5acc\u5f3a\u5236\u7325\u4eb5\u6848\u7684\u8c03\u67e5\uff0c\u6211\u7684\u540c\u4e8b\u9a6c\u4e0a\u5c31\u4f1a\u8d76\u5230\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u5bf9\u8be5\u6240\u8b66\u54584\u670810\u65e5\u5f3a\u5236\u7325\u4eb5\u6848\u62a5\u8b66\u540e\u7684\u4fa6\u5bdf\u8fc7\u7a0b\u8fdb\u884c\u7a3d\u67e5\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u7b11\u7740\uff0c\u8ddf\u5b59\u9759\u6aac\u8bf4\uff1a\u201c\u5185\u5730\u5e76\u975e\u6ca1\u6709\u4e25\u8c28\u7684\u6cd5\u5f8b\u6cd5\u89c4\uff0c\u5374\u662f\u8981\u9760\u989d\u5916\u7684\u529b\u91cf\u624d\u80fd\u8ba9\u8fd9\u6cd5\u5f8b\u6cd5\u89c4\u5f97\u5230\u4e25\u683c\u7684\u6267\u884c\u7f62\u4e86\u2026\u2026\u201d</p></article>"}}}]);
