(window.webpackJsonp=window.webpackJsonp||[]).push([[738],{761:function(p){p.exports={chapterTitle:null,title:"\u7b2c576\u7ae0 \u589e\u53d1\u5e76\u8d2d",content:"<article><h2>\u7b2c576\u7ae0 \u589e\u53d1\u5e76\u8d2d</h2><p>\u3000\u3000\u4f4d\u4e8e\u71d5\u5f52\u6e56\u7554\u7684\u56db\u767e\u4ea9\u5730\uff0c\u82e5\u662f\u7528\u4e8e\u4f4f\u5b85\u623f\u4ea7\u7684\u5f00\u53d1\uff0c\u90a3\u7684\u786e\u662f\u4e00\u5757\u6bd4\u5b66\u5e9c\u5df7\u66f4\u80a5\u7684\u80a5\u8089\uff0c\u6797\u96ea\u7f8e\u4e3d\u800c\u9634\u67d4\u7684\u773c\u795e\u655b\u4e86\u655b\uff0c\u77a5\u773c\u770b\u7740\u80e1\u5b97\u5e86\u6b63\u89c2\u5bdf\u81ea\u5df1\u7684\u773c\u795e\uff0c\u98ce\u60c5\u4e07\u79cd\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u80e1\u5e02\u957f\u4f60\u8ddf\u6211\u8bf4\u8fd9\u4e9b\u6709\u7684\u6ca1\u7684\uff0c\u6709\u4ec0\u4e48\u7528\uff1f\u7cbe\u5178\u662f\u505a\u5730\u4ea7\u7684\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u6211\u4eec\u53c8\u505a\u4e0d\u4e86\uff0c\u518d\u80a5\u7684\u80a5\u8089\uff0c\u4e5f\u773c\u998b\u4e0d\u4e86\u2026\u2026\u201d\u6797\u96ea\u5c06\u610f\u89c1\u7a3f\u62ff\u8d77\u6765\u4e22\u529e\u516c\u684c\u65c1\u7684\u5783\u573e\u7b52\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5e02\u91cc\u8fd9\u4e48\u641e\uff0c\u6211\u8fd8\u662f\u7701\u4e0b\u7b7e\u5b57\u7684\u529b\u6c14\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eca\u5e74\u4e0d\u7ba1\u7528\u4ec0\u4e48\u624b\u6bb5\uff0c\u4e5f\u8981\u7b79\u96c6\u8d44\u91d1\u5728\u5165\u79cb\u4e4b\u524d\u4e0a\u9a6c\u5c0f\u6c5f\u4e8c\u6865\u53ca\u71d5\u6d66\u5feb\u901f\u901a\u9053\u5de5\u7a0b\uff0c\u8fd9\u662f\u4eca\u5929\u4e0a\u5348\u5e02\u59d4\u5e38\u59d4\u4f1a\u8bae\u4e0a\u521a\u505a\u51fa\u7684\u51b3\u8bae\uff1b\u7f57\u541b\u5728\u5e02\u59d4\u5e38\u59d4\u4f1a\u8bae\u4e0a\u4fe1\u53e3\u5f00\u6cb3\uff0c\u63d0\u51fa\u2018\u4e94\u5e74\u4e00\u767e\u4ebf\u2019\u8ba1\u5212\uff0c\u4e5f\u5c31\u662f\u8bf4\u5e02\u91cc\u5dee\u4e0d\u591a\u8981\u6bcf\u5e74\u62ff\u51fa\u4e09\u5206\u4e4b\u4e00\u591a\u7684\u8d22\u653f\u6536\u5165\u505a\u4e3a\u65b0\u6218\u7565\u7684\u5e02\u653f\u57fa\u5efa\u4e0e\u7f51\u7edc\u5149\u7ea4\u5efa\u8bbe\u6295\u8d44\uff0c\u4f60\u4e5f\u4e0d\u5fc3\u52a8\uff1f\u201d\u80e1\u5b97\u5e86\u5b89\u9759\u5750\u5728\u529e\u516c\u5ba4\u540e\uff0c\u770b\u7740\u6797\u96ea\u4fee\u957f\u6027\u611f\u7684\u80cc\u5f71\uff0c\u4e0d\u75be\u4e0d\u5f90\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e94\u5e74\u4e00\u767e\u4ebf\uff1f\u201d\u6797\u96ea\u624b\u843d\u5728\u95e8\u628a\u624b\u4e0a\u6b63\u51c6\u5907\u8d70\u51fa\u80e1\u5b97\u5e86\u7684\u529e\u516c\u5ba4\uff0c\u542c\u5230\u80e1\u5b97\u5e86\u8fd9\u4e48\u8bf4\uff0c\u7709\u5934\u8df3\u4e86\u8df3\uff0c\u7cbe\u5178\u5730\u4ea7\u521b\u7acb\u4ee5\u6765\uff0c\u5efa\u90ba\u516d\u533a\u53bf\u5728\u5e02\u653f\u57fa\u5efa\u4e0a\u7684\u603b\u6295\u5165\u90fd\u6ca1\u6709\u4e00\u767e\u4e2a\u4ebf\uff01\u5efa\u90ba\u5e02\u91cc\u63d0\u51fa\u9ad8\u65b0\u6280\u672f\u65b0\u6218\u7565\u7684\u8bbe\u60f3\u5df2\u6709\u5341\u5929\u65f6\u95f4\u4e86\uff0c\u4e4b\u524d\u53ea\u6709\u6a21\u7cca\u7684\u6982\u5ff5\uff0c\u6ca1\u60f3\u5230\u7f57\u541b\u8fd8\u771f\u6709\u51b3\u5fc3\uff0c\u8fd9\u4e48\u5927\u7684\u624b\u7b14\uff0c\u4e94\u5e74\u8981\u4ece\u5e02\u8d22\u653f\u6536\u5165\u6324\u51fa\u4e00\u767e\u4ebf\u6765\u6295\u7ed9\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u5176\u4ed6\u90e8\u95e8\u5c82\u4e0d\u662f\u8981\u52d2\u7d27\u88e4\u8170\u5e26\u8fc7\u65e5\u5b50\uff1f\u201c\u65b0\u6218\u7565\u7684\u89c4\u6a21\u5230\u5e95\u6709\u591a\u5927\uff0c\u7f57\u541b\u662f\u60f3\u8ba9\u8fd9\u5ea7\u57ce\u5e02\u6c38\u8fdc\u90fd\u8bb0\u4f4f\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u6218\u7565\u89c4\u5212\u9762\u79ef\u662f\u65b0\u6d66\u9ad8\u65b0\u533a\u7684\u5341\u500d\uff0c\u6b63\u5f0f\u7684\u540d\u79f0\uff0c\u4eca\u5929\u4e0a\u5348\u7684\u4f1a\u8bae\u4e5f\u8ba8\u8bba\u51fa\u6765\u4e86\uff0c\u53eb\u2018\u6570\u5b57\u957f\u5eca\u2019\u6784\u60f3\u2026\u2026\u201d\u80e1\u5b97\u5e86\u772f\u7740\u773c\u775b\u770b\u7740\u6797\u96ea\u90a3\u53cc\u7f8e\u4e3d\u4f46\u4f1a\u7ed9\u4eba\u9634\u5bd2\u611f\u89c9\u7684\u773c\u775b\u91cc\u7efd\u653e\u51fa\u70bd\u70c8\u7684\u5149\u8292\uff0c\u8fd9\u4e2a\u5973\u4eba\u7231\u8d22\uff0c\u4f5c\u4e3a\u5efa\u90ba\u5e02\u6709\u53f2\u4ee5\u6765\u6700\u5927\u7684\u4e00\u9879\u653f\u5e9c\u7cfb\u7edf\u5de5\u7a0b\uff0c\u5bf9\u4e8e\u7231\u8d22\u7684\u4eba\u6765\u8bf4\uff0c\u5c31\u5982\u6700\u9c9c\u7f8e\u7684\u8702\u871c\uff0c\u54ea\u91cc\u80fd\u62b5\u5f97\u4f4f\u8bf1\u60d1\uff1f\u201c\u82e5\u662f\u8bf4\u9ad8\u65b0\u6280\u672f\u65b0\u6218\u7565\u7684\u5b9e\u65bd\u59cb\u7ec8\u7ed9\u7f57\u541b\u3001\u738b\u7ef4\u5747\u3001\u9ec4\u514b\u7fa4\u7b49\u4eba\u7262\u7262\u638c\u63e1\u5728\u624b\u91cc\uff0c\u4f60\u4eec\u600e\u4e48\u51d1\u8fc7\u53bb\u5206\u4e00\u676f\u7fb9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u4e3a\u6211\u8003\u8651\u5427\uff1f\u201d\u6797\u96ea\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u6709\u7740\u653e\u7eb5\u7684\u610f\u5473\uff0c\u201c\u4f60\u662f\u62c5\u5fc3\u81ea\u5df1\u7ed9\u7529\u4e0b\u8fd9\u73ed\u8f66\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u6325\u4e86\u6325\u624b\uff0c\u6253\u5f00\u95e8\u51fa\u4e86\u80e1\u5b97\u5e86\u7684\u529e\u516c\u5ba4\uff0c\u770b\u89c1\u5f20\u606a\u72ec\u81ea\u4e00\u4eba\u4ece\u8fc7\u9053\u90a3\u5934\u8d70\u8fc7\u6765\uff0c\u8fc7\u9053\u91cc\u5149\u7ebf\u660f\u6697\uff0c\u770b\u4e0d\u6e05\u5f20\u606a\u7684\u8138\uff0c\u5374\u80fd\u4ece\u4ed6\u7279\u6709\u7684\u61d2\u6563\u59ff\u6001\u8ba4\u51fa\u4ed6\u6765\u3002\u4e5f\u4e0d\u6653\u5f97\u4ed6\u521a\u624d\u53bb\u4e86\u54ea\u91cc\uff0c\u6b64\u65f6\u6ca1\u6709\u8ddf\u4ed6\u6253\u4ea4\u9053\u7684\u60f3\u6cd5\uff0c\u6797\u96ea\u626d\u5934\u671d\u53e6\u4e00\u7aef\u8d70\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u6797\u96ea\u505c\u7acb\u7684\u5730\u65b9\uff0c\u4e5f\u7a0d\u505c\u4e86\u4e00\u4e0b\uff0c\u770b\u4e86\u770b\u623f\u95e8\u4e2d\u7684\u91d1\u5c5e\u94ed\u724c\uff0c\u5fc3\u60f3\u8fd9\u4e2a\u5973\u4eba\u8fd8\u771f\u662f\u4ea4\u6e38\u5e7f\u6cdb\uff0c\u674e\u8fdc\u6e56\u7684\u79d8\u4e66\u9ad8\u771f\u3001\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u90fd\u4e0e\u5979\u4ea4\u5f80\u5bc6\u5207\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u4e0d\u6015\u5979\u8fc7\u6765\u6a2a\u63d2\u4e00\u817f\uff0c\u8bbe\u7f6e\u8fd9\u4e48\u9ad8\u7684\u95e8\u69db\uff0c\u8db3\u591f\u5c06\u5efa\u90ba\u5e02\u7684\u5730\u4ea7\u516c\u53f8\u6321\u5728\u95e8\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u521a\u624d\u53bb\u89c1\u59da\u6587\u76db\u4e86\uff0c\u59da\u6587\u76db\u867d\u7136\u62c5\u4efb\u65b0\u6d66\u9ad8\u65b0\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u526f\u5c40\u957f\uff0c\u4f46\u662f\u4ed6\u76ee\u524d\u7684\u5de5\u4f5c\u4e3b\u8981\u662f\u8d1f\u8d23\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u534f\u8c03\u4e0e\u8054\u7edc\uff0c\u53c8\u662f\u65b0\u6d66\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u52a9\u7406\uff0c\u9ec4\u514b\u7fa4\u8fd8\u517c\u4efb\u7740\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u7684\u804c\u8854\uff0c\u59da\u6587\u76db\u66f4\u6709\u7406\u7531\u7559\u5728\u5e02\u653f\u5e9c\u529e\u516c\u3002</p><p>\u3000\u3000\u4ece\u59da\u6587\u76db\u90a3\u91cc\u51fa\u6765\uff0c\u5f20\u606a\u6572\u95e8\u8fdb\u4e86\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u7684\u529e\u516c\u5ba4\uff0c\u90b5\u81f3\u521a\u3001\u9648\u4fe1\u751f\u90fd\u5728\u738b\u7ef4\u5747\u7684\u529e\u516c\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4eca\u5929\u8fc7\u6765\uff0c\u662f\u4e0e\u5e02\u91cc\u6b63\u5f0f\u7b7e\u7f72\u4e3d\u4e30\u5370\u67d3\u5382\u5382\u623f\u53ca\u9644\u5c5e\u571f\u5730\u4f7f\u7528\u6743\u8f6c\u8ba9\u534f\u8bae\u7684\uff0c\u4e00\u540c\u62ff\u4e0b\u7684\u8fd8\u6709\u4e3d\u4e30\u5370\u67d3\u5382\u897f\u90bb\u7684\u4e00\u95f4\u6f02\u67d3\u5382\uff0c\u8fd9\u4e24\u95f4\u5382\u6070\u6070\u662f\u71d5\u5f52\u6e56\u5317\u7aef\u7684\u4e3b\u8981\u6c61\u67d3\u6e90\uff0c\u5728\u622a\u65ad\u8fd9\u4e24\u95f4\u5382\u7ee7\u7eed\u5411\u71d5\u5f52\u6e56\u6392\u67d3\u6c61\u67d3\u7269\u7684\u540c\u65f6\uff0c\u9526\u6e56\u53ef\u4ee5\u6539\u9020\u73b0\u6709\u7684\u5382\u623f\u8fc5\u901f\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u3002</p><p>\u3000\u3000\u5728\u53e6\u4e00\u4e2a\u65f6\u7a7a\uff0c\u4e00\u76f4\u62d6\u5ef6\u5230\u96f6\u4e00\u5e74\u591a\u8fd0\u8425\u4e86\u56db\u5e74\uff0c\u5e02\u653f\u5e9c\u624d\u4e0b\u51b3\u5b9a\u5c06\u4e3d\u4e30\u5370\u67d3\u5382\u4e0e\u71d5\u5f52\u6e56\u5468\u8fb9\u7684\u5176\u4ed6\u4e03\u5bb6\u91cd\u6c61\u67d3\u4f01\u4e1a\u5f7b\u5e95\u5173\u505c\uff0c\u5176\u540e\u516d\u5e74\uff0c\u5e02\u653f\u5e9c\u5171\u6295\u5165\u5341\u4e8c\u4e2a\u4ebf\u6765\u6062\u590d\u71d5\u5f52\u6e56\u7684\u751f\u6001\u73af\u5883\u624d\u4f7f\u5f97\u71d5\u5f52\u6e56\u4ece\u51e0\u4e4e\u5d29\u6e83\u7684\u751f\u6001\u73af\u5883\u8fb9\u7f18\u521d\u6b65\u7684\u6062\u590d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u4e00\u65e6\u5b9e\u65bd\u6570\u5b57\u957f\u5eca\u6784\u60f3\uff0c\u5c31\u53ef\u4ee5\u63d0\u524d\u56db\u5e74\u3001\u5728\u71d5\u5f52\u6e56\u751f\u6001\u73af\u5883\u9677\u5165\u5d29\u6e83\u8fb9\u7f18\u4e4b\u524d\u5c06\u5468\u8fb9\u7684\u91cd\u6c61\u67d3\u4f01\u4e1a\u5f7b\u5e95\u5173\u505c\u8fc1\u51fa\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u81ea\u7136\u662f\u4e3a\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u800c\u6765\uff0c\u5f20\u606a\u62c9\u4e86\u4e00\u628a\u6905\u5b50\u5750\u5728\u738b\u7ef4\u5747\u3001\u9648\u4fe1\u751f\u3001\u90b5\u81f3\u521a\u4e2d\u95f4\uff0c\u95ee\u9053\uff1a\u201c\u5efa\u90ba\u5e02\u6709\u5934\u6709\u8111\u7684\u5730\u4ea7\u516c\u53f8\u8001\u603b\u5dee\u4e0d\u591a\u90fd\u8fc7\u6765\u4e86\uff0c\u6211\u8fd8\u770b\u5230\u7cbe\u5178\u5730\u4ea7\u7684\u8001\u603b\u6797\u96ea\u4ece\u80e1\u526f\u5e02\u957f\u7684\u529e\u516c\u5ba4\u51fa\u6765\uff0c\u610f\u89c1\u7a3f\u7684\u53cd\u9988\u60c5\u51b5\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u7cbe\u5178\u5730\u4ea7\u6ca1\u6709\u4ea4\u4e0a\u6765\uff0c\u4ed6\u4eec\u4ea4\u4e0d\u4ea4\u90fd\u65e0\u5173\u7d27\u8981\uff0c\u5176\u4ed6\u516c\u53f8\u90fd\u660e\u786e\u653e\u5f03\u8be5\u9879\u76ee\uff0c\u201d\u738b\u7ef4\u5747\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u8fd9\u4e0b\u5b50\u603b\u5f52\u80fd\u5835\u4f4f\u4ed6\u4eec\u7684\u5634\u5df4\u4e86\u5427\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e3d\u4e30\u5370\u67d3\u5382\u8bbe\u5907\u62c6\u9664\u4e4b\u540e\u79fb\u4ea4\u5de5\u4f5c\u5728\u4e94\u6708\u521d\u5b8c\u6210\u3002</p><p>\u3000\u30004\u670822\u65e5\uff0c\u4e16\u7eaa\u9526\u6e56\u3001\u7231\u8fbe\u96c6\u56e2\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u7b7e\u7f72\u59d4\u6258\u6539\u9020\u5b66\u5e9c\u5df7\u65e7\u8857\u5de5\u7a0b\u534f\u8bae\u3002\u5b66\u5e9c\u5df7\u6539\u9020\u5de5\u7a0b\u7684\u5f00\u5c55\u9996\u5148\u8981\u4e0e\u5b66\u5e9c\u5df7\u7684\u4e1a\u4e3b\u7b7e\u7f72\u62c6\u8fc1\u8865\u507f\u534f\u8bae\u624d\u53ef\u80fd\u8fdb\u884c\u540e\u671f\u7684\u5de5\u4f5c\uff0c\u5efa\u90ba\u5e02\u653f\u5e9c\u7763\u4fc3\u4e1c\u534e\u533a\u653f\u5e9c\u6210\u7acb\u5b66\u5e9c\u5df7\u62c6\u8fc1\u529e\u516c\u5ba4\u534f\u8c03\u62c6\u8fc1\u8865\u507f\u5de5\u4f5c\uff0c\u4e00\u5207\u90fd\u662f\u4e3a\u4e86\u4fc3\u8fdb\u6a61\u6811\u56ed\u8ba1\u5212\u80fd\u8fc5\u901f\u5168\u9762\u7684\u5c55\u5f00\u3002</p><p>\u3000\u3000\u968f\u7740\u5609\u4fe1\u5b9e\u4e1a\u5b9e\u9645\u7684\u516c\u5f00\u589e\u53d1\u5e76\u8d2d\u5de5\u4f5c\u8fdb\u884c\u6700\u540e\u7684\u6536\u5173\u9636\u6bb5\uff0c\u5f20\u606a22\u65e5\u5c31\u4e0e\u82cf\u6d25\u4e1c\u4e58\u98de\u673a\u524d\u5f80\u9999\u6e2f\u8fdb\u884c\u6700\u540e\u7684\u7763\u6218\u3002</p><p>\u3000\u30004\u670828\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u53ec\u5f00\u4e34\u65f6\u80a1\u4e1c\u5927\u4f1a\u5ba1\u8bae\u4e00\u4e2a\u6708\u4e4b\u524d\u516c\u5e03\u7684\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u3002</p><p>\u3000\u3000\u5e74\u540e\u4ee5\u6765\uff0c\u5df2\u7ecf\u6ca1\u6709\u4efb\u4f55\u52bf\u529b\u80fd\u963b\u6321\u9999\u6e2f\u7684\u56de\u5f52\u4e4b\u9014\uff0c\u4e00\u76f4\u4ee5\u6765\u7b3c\u7f69\u5728\u9999\u6e2f\u5c45\u6c11\u5934\u9876\u7684\u653f\u6cbb\u9634\u4e91\u4e5f\u5728\u56de\u5f52\u4e4b\u524d\u7684\u6700\u540e\u4e00\u523b\u6d88\u5931\u5e72\u51c0\u3002\u9999\u6e2f\u672a\u6765\u7684\u5c40\u52bf\u660e\u6717\u5df2\u7ecf\u6210\u4e3a\u4e24\u5730\u6c11\u4f17\u7684\u5171\u8bc6\uff0c\u4e5f\u5c31\u6210\u4e3a\u4ece\u4e09\u6708\u4e2d\u65ec\u4ee5\u6765\u9999\u6e2f\u80a1\u5e02\u5f3a\u52b2\u6500\u5347\u7684\u6700\u5927\u652f\u6491\uff0c\u7279\u522b\u662f\u5185\u8d44\u53c2\u4e0e\u6216\u63a7\u80a1\u7684\u4e0a\u5e02\u516c\u53f8\u5728\u9999\u6e2f\u80a1\u5e02\u5c24\u5176\u53d7\u5230\u70ed\u68d2\uff0c\u5176\u4e2d\u4e5f\u5305\u62ec\u5609\u4fe1\u5b9e\u4e1a\u3002</p><p>\u3000\u30003\u670828\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u516c\u5e03\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u4e4b\u524d\u7684\u80a1\u4ef7\u4e3a4.6\u6e2f\u5143\uff0c\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u4e8c\u5341\u4e2a\u5de5\u4f5c\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u80a1\u5e02\u7684\u80a1\u7968\u4ef7\u683c\u7d2f\u8ba1\u6da8\u5e45\u8fbe\u523060\uff05\uff0c\u5728\u53ec\u5f00\u4e34\u65f6\u80a1\u4e1c\u5927\u4f1a\u5ba1\u8bae\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u5b9e\u65bd\u505c\u724c\u524d\u7684\u80a1\u4ef7\u8fbe\u52307.4\u6e2f\u5143\u3002</p><p>\u3000\u3000\u539f\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u662f\u589e\u53d18.21\u4ebf\u80a1\u3001\u6bcf\u80a14.6\u6e2f\u5143\uff0c\u603b\u878d\u8d44\u89c4\u6a21\u63a7\u5236\u572838\u4ebf\u6e2f\u5143\u5de6\u53f3\uff0c\u6700\u7ec8\u5ba1\u8bae\u901a\u8fc7\u7684\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u8c03\u6574\u4e4b\u540e\uff0c\u589e\u53d1\u4ef7\u4e0d\u4f4e\u4e8e\u6bcf\u80a16\u6e2f\u5143\uff0c\u89c4\u6a21\u4e5f\u7f29\u51cf\u52306.3\u4ebf\u80a1\uff0c\u94f6\u4ea8\u8bc1\u5238\u4e3a\u6b64\u6b21\u589e\u53d1\u65b0\u80a1\u7684\u4e3b\u5305\u9500\u5546\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6b64\u6b21\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u7684\u89c4\u6a21\u76f8\u5bf9\u4e8e\u5609\u4fe1\u5b9e\u4e1a\u81ea\u8eab\u7684\u5e02\u503c\u8fc7\u4e8e\u5e9e\u5927\uff0c\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u4e34\u65f6\u80a1\u4e1c\u5927\u4f1a\u901a\u8fc7\u4e4b\u540e\u5f53\u65e5\u5c31\u9012\u4ea4\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u5ba1\u6838\u6279\u51c6\u3002</p><p>\u3000\u3000\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u62405\u67084\u65e5\u5ba1\u8bae\u901a\u8fc7\u5609\u4fe1\u5b9e\u4e1a\u589e\u53d1\u5de8\u91cf\u65b0\u80a1\u7684\u7533\u8bf7\uff0c\u5609\u4fe1\u5b9e\u4e1a\u3001\u7231\u8fbe\u7535\u5b50\u4e0e\u94f6\u4ea8\u8bc1\u5238\u5728\u5185\u7684\u53d1\u884c\u5305\u9500\u5546\u4e8e\u5f53\u65e5\u7b7e\u7f72\u589e\u53d1\u65b0\u80a1\u5305\u9500\u534f\u8bae\uff0c6.3\u4ebf\u65b0\u80a1\u7684\u603b\u5305\u9500\u4ef7\u683c\u9ad8\u8fbe44\u4ebf\u6e2f\u5143\uff0c\u8fd9\u7b14\u5de8\u989d\u5c06\u7531\u5305\u9500\u5546\u63d0\u524d\u652f\u4ed8\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u3002\u9664\u4e86\u652f\u4ed8\u7ed9\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u768438\u4ebf\u6e2f\u5143\u80a1\u6743\u6536\u8d2d\u8d44\u91d1\u4e4b\u5916\uff0c\u5609\u4fe1\u5b9e\u4e1a\u8fd8\u5c06\u989d\u5916\u622a\u75596\u4ebf\u6e2f\u5143\u7684\u6d41\u52a8\u8d44\u91d1\u3002</p><p>\u3000\u30005\u67084\u65e5\u591c\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u6e2f\u5c9b\u6700\u9ad8\u7684\u9152\u5e97\u9999\u683c\u91cc\u62c9\u5927\u9152\u5e97\u4e3e\u884c\u7b54\u8c22\u9152\u4f1a\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u5185\u8d44\u6c11\u8425\u4f01\u4e1a\u5728\u6e2f\u5c9b\u7684\u6700\u5927\u4e00\u7b14\u878d\u8d44\uff0c\u800c\u4e14\u7231\u8fbe\u7535\u5b50\u5728\u8fc7\u53bb\u76f8\u5f53\u957f\u7684\u4e00\u4e2a\u65f6\u95f4\u91cc\u98ce\u5934\u751a\u5065\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5e86\u795d\u589e\u53d1\u5e76\u8d2d\u5706\u6ee1\u6210\u529f\u7684\u7b54\u8c22\u9152\u4f1a\u5438\u5f15\u5185\u5730\u3001\u9999\u6e2f\u7b49\u5730\u6570\u5341\u5bb6\u5a92\u4f53\u8ffd\u8e2a\u62a5\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u9999\u683c\u91cc\u62c9\u5927\u9152\u5e97\u7684\u9876\u5c42\u5957\u623f\u95f4\uff0c\u4fef\u89c6\u7740\u9999\u6e2f\u5982\u68a6\u5982\u5e7b\u7684\u591c\u8272\u666f\u81f4\uff0c\u751a\u81f3\u53ef\u4ee5\u770b\u5230\u7ef4\u591a\u5229\u4e9a\u6d77\u6e2f\u8fb9\u7684\u7480\u74a8\u706f\u6cb3\uff0c\u8f6c\u5934\u770b\u7740\u9759\u8c27\u6df1\u6c89\u7684\u51dd\u89c6\u7740\u7a97\u5916\u591c\u7a7a\u7684\u8bb8\u601d\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4e0d\u53bb\u53c2\u52a0\u4e0b\u9762\u7684\u9152\u4f1a\uff0c\u4f60\u4e5f\u5e94\u8be5\u662f\u4e3b\u89d2\u7684\uff1f\u201d</p><p>\u3000\u3000\u540c\u4e00\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u8fd8\u5411\u9999\u6e2f\u8d8a\u79c0\u63a7\u80a1\u96c6\u56e2\u589e\u53d12.86\u4ebf\u80a1\u65b0\u80a1\u7528\u4e8e\u6536\u8d2d\u8d8a\u79c0\u63a7\u80a1\u624b\u4e2d\u6240\u6301\u670928\uff05\u7684\u7231\u8fbe\u7535\u5b50\u80a1\u4efd\uff0c\u8d8a\u79c0\u63a7\u80a1\u5171\u6301\u80a1\u5609\u4fe1\u5b9e\u4e1a30\uff05\u7684\u80a1\u4efd\uff0c\u4f5c\u4e3a\u8d8a\u79c0\u63a7\u80a1\u7684\u7ba1\u7406\u9ad8\u5c42\uff0c\u51fa\u73b0\u5728\u7b54\u8c22\u9152\u4f1a\u4e0a\uff0c\u4e5f\u4f1a\u53d7\u5230\u5a92\u4f53\u7684\u8ffd\u9010\u3002</p><p>\u3000\u3000\u201c\u5149\u9c9c\u8273\u4e3d\u7684\u7ad9\u51fa\u6765\uff0c\u6216\u8bb8\u4f1a\u5931\u53bb\u66f4\u591a\uff0c\u6211\u60f3\u6211\u66f4\u559c\u6b22\u9759\u8c27\u7684\u5728\u5e7d\u8c37\u91cc\u7efd\u653e\uff0c\u4ec5\u4ec5\u7efd\u653e\u7ed9\u4f60\u5c31\u8db3\u591f\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7275\u8fc7\u8bb8\u601d\u7684\u624b\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\u3002</p></article>"}}}]);
