(window.webpackJsonp=window.webpackJsonp||[]).push([[74],{97:function(p){p.exports={chapterTitle:null,title:"\u7b2c1061\u7ae0 \u8089\u98df\u8005",content:"<article><h2>\u7b2c1061\u7ae0 \u8089\u98df\u8005</h2><p>\u3000\u3000\u201c\u4f60\u4ed6\u5988\u773c\u775b\u778e\u4e86\uff0c\u4f60\u5988\u7684\u773c\u91cc\u662f\u4e0d\u662f\u5c31\u4ee5\u4e3a\u53ea\u6709\u5f00\u2018\u522b\u6478\u6211\u2019\u8ddf\u2018\u88e4\u53c9\u2019\u7684\u662f\u4f60\u60f9\u4e0d\u8d77\uff1f\u90a3\u8857\u4e0a\u5f00\u8f86\u5766\u514b\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u8981\u9886\u7740\u4eba\u51b2\u4e0a\u53bb\u7f5a\u4eba\u5bb6\u4e09\u4e07\u5757\uff1f\u201d\u59dc\u5fd7\u660e\u618b\u7740\u4e00\u809a\u5b50\u7684\u706b\uff0c\u4ed6\u867d\u7136\u5bf9\u8054\u5408\u6267\u6cd5\u961f\u5728\u7701\u4eba\u6c11\u533b\u9662\u8e72\u70b9\u7684\u8fd9\u4f19\u4eba\u7834\u53e3\u5927\u9a82\uff0c\u80cc\u810a\u5374\u53d1\u865a\u7684\u76f4\u5192\u51b7\u6c57\uff0c\u624b\u4e5f\u5728\u5fae\u5fae\u98a4\u6296\uff0c\u6108\u662f\u5982\u6b64\uff0c\u6108\u662f\u8981\u53d1\u6cc4\u7684\u9a82\u5f97\u51f6\uff0c\u6068\u4e0d\u5f97\u624b\u811a\u5e76\u7528\u3001\u8fde\u6293\u5e26\u8e39\u7684\u5c06\u8fd9\u4e9b\u4eba\u4eba\u9053\u6bc1\u706d\u6389\uff0c\u7136\u540e\u5c31\u5f53\u4eca\u5929\u7684\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\u56de\u5bb6\u7761\u89c9\u53bb\u3002</p><p>\u3000\u3000\u5e02\u653f\u5e9c\u529e\u526f\u79d8\u4e66\u957f\u3001\u6253\u51fb\u975e\u6cd5\u8fd0\u8425\u8054\u5408\u6267\u6cd5\u5927\u961f\u5927\u961f\u957f\u6c64\u5efa\u950b\u9634\u6c89\u7740\u8138\u7ad9\u5728\u8fdc\u5904\u4ece\u51fa\u73b0\u5230\u73b0\u5728\u4e00\u58f0\u90fd\u6ca1\u6709\u542d\uff0c\u4ed6\u542c\u8bf4\u5728\u4ed6\u4eec\u8d76\u6765\u4e4b\u524d\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u7701\u957f\u674e\u8fdc\u6e56\u5df2\u7ecf\u5148\u4e00\u6b65\u8d76\u4e86\u8fc7\u6765\uff0c\u8f66\u5b50\u5c31\u505c\u5728\u4e0d\u8fdc\u5904\uff0c\u4ed6\u4eec\u8fc7\u6765\u5c31\u76f4\u63a5\u8fdb\u4e86\u6025\u8bca\u697c\uff0c\u8981\u662f\u56e0\u4e3a\u6267\u6cd5\u961f\u6545\u610f\u5201\u96be\u5bfc\u81f4\u75c5\u4eba\u62a2\u6551\u4e0d\u8fc7\u6765\uff0c\u771f\u662f\u65e0\u6cd5\u60f3\u8c61\u8fd9\u540e\u679c\u6709\u591a\u4e25\u91cd\u3002</p><p>\u3000\u3000\u6c64\u5efa\u950b\u9634\u6c89\u7684\u76ef\u7740\u739b\u838e\u62c9\u8482\u8c6a\u534e\u56db\u95e8\u8f7f\u8f66\u524d\u5634\u8138\u4e0a\u7684\u4e09\u53c9\u621f\u6807\u8bc6\uff0c\u771f\u662f\u4e0d\u77e5\u9053\u8981\u5982\u4f55\u53bb\u8bad\u65a5\u8fd9\u4e9b\u6df7\u86cb\uff0c\u6ca1\u6709\u77e5\u8bc6\u771f\u662f\u60b2\u54c0\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u8fd9\u5e74\u5934\uff0c\u56fd\u5185\u53c8\u6709\u591a\u5c11\u4eba\u8ba4\u8bc6\u739b\u838e\u62c9\u8482\u4e09\u53c9\u621f\u6807\u5fd7\u5462\uff1f\u773c\u524d\u8fd9\u8f86\u56db\u95e8\u8f7f\u8f66\u53c8\u4e0d\u662f\u5728\u7535\u89c6\u91cc\u7ecf\u5e38\u770b\u7684\u90a3\u79cd\u8db4\u5728\u5730\u4e0a\u50cf\u9752\u86d9\u7684\u8dd1\u8f66\uff0c\u4e4d\u770b\u4e0a\u53bb\u4e0d\u89c1\u5f97\u6bd4\u5e15\u8428\u7279\u66f4\u9ad8\u6863\uff0c\u4f55\u51b5\u8fd8\u662f\u6302\u4e86\u6d77\u5dde\u8f66\u724c\u3002</p><p>\u3000\u3000\u6c64\u5efa\u950b\u5df2\u7ecf\u6ca1\u6709\u5fc3\u601d\u53bb\u8bad\u65a5\u522b\u4eba\u4e86\uff0c\u4ed6\u73b0\u5728\u53ea\u5173\u5fc3\u81ea\u5df1\u4f1a\u4e0d\u4f1a\u60b2\u54c0\uff0c\u7f57\u541b\u5bf9\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u65e9\u5c31\u6a2a\u4e0d\u662f\u9f3b\u5b50\u3001\u7ad6\u4e0d\u662f\u7709\u6bdb\u7684\u770b\u4e0d\u987a\u773c\u4e86\uff0c\u8fd9\u6b21\u7684\u8054\u5408\u6267\u6cd5\u884c\u52a8\u53c8\u662f\u5e02\u653f\u5e9c\u4e3b\u5bfc\u7684\uff0c\u4e0d\u6653\u5f97\u7f57\u541b\u4f1a\u4e0d\u4f1a\u501f\u9898\u53d1\u4f5c\uff0c\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u518d\u90c1\u95f7\uff0c\u81f3\u5c11\u4ed6\u7684\u5e02\u957f\u3001\u526f\u5e02\u957f\u5934\u8854\u4e0d\u6015\u7f57\u541b\u7ed9\u4ed6\u634b\u4e86\uff0c\u53ef\u60dc\u81ea\u5df1\u53ea\u662f\u5c0f\u5c0f\u7684\u4e00\u6761\u72d7\uff0c\u6307\u4e0d\u5b9a\u5c31\u7ed9\u8fd9\u4e9b\u5927\u4eba\u7269\u4e0d\u5c0f\u5fc3\u8e29\u6b7b\u4e86\u3002</p><p>\u3000\u3000\u201c\u59dc\u961f\u957f\uff0c\u4f60\u8fc7\u6765\u4e00\u4e0b\u2026\u2026\u201d\u6c64\u5efa\u950b\u5c06\u59dc\u5fd7\u660e\u558a\u8fc7\u6765\uff0c\u8fd9\u65f6\u5019\u53ea\u80fd\u4e22\u8f66\u4fdd\u5e05\u4e86\uff0c\u4e00\u5b9a\u8981\u54ac\u6b7b\u7701\u4eba\u6c11\u533b\u9662\u95e8\u53e3\u53d1\u751f\u7684\u53ea\u662f\u6781\u4e2a\u522b\u73b0\u8c61\u3001\u53ea\u662f\u4e2a\u522b\u6267\u6cd5\u4eba\u5458\u6267\u6cd5\u7d20\u8d28\u6709\u5f85\u63d0\u9ad8\uff0c\u5904\u7f5a\u4e00\u5b9a\u8981\u4e25\u5389\uff0c\u4f46\u662f\u53ea\u80fd\u843d\u5230\u4e2a\u4eba\u5934\u4e0a\uff0c\u4e0d\u80fd\u5426\u5b9a\u6574\u4e2a\u6253\u51fb\u79c1\u8f66\u975e\u6cd5\u8fd0\u8425\u8054\u5408\u6267\u6cd5\u8fd0\u52a8\u7684\u610f\u4e49\uff0c\u5982\u6b64\u53ee\u5631\u4e86\u51e0\u53e5\uff0c\u5c31\u4e0e\u59dc\u5fd7\u660e\u5f80\u6025\u8bca\u697c\u8d70\u53bb\uff0c\u5fc3\u60f3\u7740\u8ba9\u59dc\u5fd7\u660e\u5148\u7ad9\u51fa\u6765\u5f53\u70ae\u7070\uff0c\u81ea\u5df1\u8fd8\u80fd\u770b\u5f62\u52bf\u518d\u51b3\u5b9a\u8981\u4e0d\u8981\u8f6c\u8235\u3002</p><p>\u3000\u3000\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u7701\u957f\u674e\u8fdc\u6e56\u7b49\u4eba\u90fd\u5728\u6025\u8bca\u697c\u4e8c\u697c\u7684\u89c2\u5bdf\u5ba4\uff0c\u6c64\u5efa\u950b\u4e0e\u59dc\u5fd7\u660e\u4e58\u7535\u68af\u5230\u4e8c\u697c\uff0c\u8fc7\u9053\u91cc\u7ad9\u7740\u8bb8\u591a\u4eba\uff0c\u90fd\u662f\u7701\u5e02\u968f\u884c\u8d76\u5230\u533b\u9662\u6765\u7684\u5b98\u5458\uff0c\u6c64\u5efa\u950b\u6ca1\u770b\u5230\u7f57\u541b\u4e0e\u674e\u8fdc\u6e56\uff0c\u89c1\u5e02\u59d4\u529e\u526f\u79d8\u4e66\u957f\u4e5f\u5728\uff0c\u8d70\u8fc7\u53bb\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u5927\u4ed6\u4eec\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u91cc\u9762\u2014\u2014\u5927\u8001\u677f\u521a\u521a\u76f4\u63a5\u7ed9\u4e8c\u8001\u677f\u901a\u4e86\u7535\u8bdd\uff0c\u8ba9\u4e8c\u8001\u677f\u7acb\u5373\u8d76\u5230\u533b\u9662\u6765\uff0c\u8bed\u6c14\u6709\u4e9b\u4e0d\u5584\u3002\u201d</p><p>\u3000\u3000\u201c\u4eba\u6ca1\u4e86\uff1f\u201d\u6c64\u5efa\u950b\u5fc3\u91cc\u4e00\u60ca\uff0c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4eba\u8fd8\u5728\uff0c\u62a2\u6551\u8fc7\u6765\u4e86\uff0c\u4e24\u4e2a\u5927\u8001\u677f\u5728\u91cc\u9762\u6170\u95ee\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u5230\u5e95\u662f\u8c01\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u6c64\u5efa\u950b\u95ee\u9053\u3002</p><p>\u3000\u3000\u6ca1\u6709\u803d\u6401\u62a2\u6551\uff0c\u4eba\u65e2\u7136\u90fd\u5df2\u7ecf\u62a2\u6551\u8fc7\u6765\uff0c\u4e00\u822c\u8bf4\u6765\u5c31\u53ef\u4ee5\u5927\u4e8b\u5316\u5c0f\u3001\u5c0f\u4e8b\u5316\u4e86\uff0c\u5927\u8001\u677f\u6709\u5fc5\u8981\u4e3a\u4e9b\u4e0e\u4e8c\u8001\u677f\u76f4\u63a5\u6495\u7834\u8138\u76ae\u5417\uff1f\u8fd8\u662f\u8bf4\u62a2\u6551\u8005\u7684\u8eab\u4efd\u5f02\u5e38\u91cd\u8981\uff0c\u91cd\u8981\u5230\u8ba9\u5927\u8001\u677f\u5fc5\u987b\u8ddf\u4e8c\u8001\u677f\u6495\u7834\u8138\u3001\u6446\u660e\u7acb\u573a\u7684\u6001\u5ea6\uff1f</p><p>\u3000\u3000\u201c\u662f\u4e2a\u4e0b\u5c97\u5973\u804c\u5de5\uff0c\u4e5f\u771f\u662f\u53ef\u601c\uff0c\u4e70\u4e0d\u8d77\u8089\u5403\uff0c\u60f3\u4e0d\u5f00\u7adf\u7136\u81ea\u6740\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6c64\u5efa\u950b\u773c\u775b\u77aa\u5f97\u6e9c\u5706\uff0c\u96be\u4ee5\u7f6e\u4fe1\u7684\u7adf\u7136\u662f\u4e3a\u4e00\u4e2a\u81ea\u6740\u7684\u4e0b\u5c97\u804c\u5de5\u95f9\u8fd9\u4e48\u5927\u52a8\u9759\uff0c\u662f\u8c01\u5403\u9971\u6491\u7740\uff0c\u8981\u60ca\u52a8\u5e02\u59d4\u4e66\u8bb0\u4e0e\u7701\u957f\u540c\u65f6\u8fc7\u6765\uff1f\u8fd9\u65f6\u5019\u7f57\u541b\u8fd8\u76f4\u63a5\u70b9\u540d\u8981\u8096\u5e02\u957f\u8fc7\u6765\uff01</p><p>\u3000\u3000\u201c\u4e70\u4e0d\u8d77\u8089\u5403\uff0c\u4e0d\u53ef\u4ee5\u5fcd\u7740\u70b9\u5417\uff1f\u771f\u60f3\u4e0d\u901a\u7adf\u7136\u4f1a\u6709\u4eba\u56e0\u4e3a\u5403\u4e0d\u8d77\u8089\u53bb\u5bfb\u6b7b\u3002\u8fd9\u5e74\u5934\u8c01\u6d3b\u7740\u5bb9\u6613\uff0c\u7adf\u7136\u4e0d\u8d1f\u8d23\u4efb\u7684\u60f3\u5230\u53bb\u81ea\u6740\uff0c\u8fd9\u79cd\u4eba\u6b7b\u5c31\u6b7b\u4e86\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53ef\u60dc\u7684\uff0c\u6d3b\u7740\u53cd\u800c\u5360\u793e\u4f1a\u8d44\u6e90\u2026\u2026\u201d\u8fb9\u4e0a\u6709\u4e2a\u4eba\u51b7\u5632\u70ed\u8bbd\u7684\u55e4\u7b11\u7740\u3002</p><p>\u3000\u3000\u6c64\u5efa\u950b\u56de\u5934\u770b\u4e86\u773c\uff0c\u8bf4\u8bdd\u7684\u90a3\u4eba\u662f\u5e02\u6c11\u653f\u5c40\u7684\u5b98\u5458\uff0c\u8138\u8272\u9161\u7ea2\uff0c\u60f3\u5fc5\u662f\u5728\u9152\u684c\u4e0a\u559d\u4e86\u4e0d\u5c11\u9152\u7ed9\u62c9\u4e86\u8fc7\u6765\u3002\u4e3a\u4e86\u4e2a\u5bfb\u6b7b\u89c5\u6d3b\u7684\u4e0b\u5c97\u804c\u5de5\u95f9\u8fd9\u4e48\u5927\u52a8\u9759\uff0c\u9152\u4e5f\u559d\u4e0d\u6210\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u6028\u6c14\u7684\uff0c\u6c64\u5efa\u950b\u8fd8\u60f3\u7740\u53bb\u9644\u548c\u4e00\u58f0\uff0c\u53cd\u6b63\u8981\u505a\u653f\u6cbb\u59ff\u6001\u7684\u4eba\u90fd\u5728\u89c2\u5bdf\u5ba4\u91cc\u9762\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u8dd1\u811a\u53d1\u53d1\u7262\u9a9a\u62b1\u6028\u62b1\u6028\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u521a\u8981\u51d1\u8fc7\u53bb\u8bf4\u4e24\u53e5\u7f13\u89e3\u4e00\u4e0b\u5185\u5fc3\u7684\u7d27\u5f20\uff0c\u5374\u89c1\u89c2\u5bdf\u5ba4\u7684\u95e8\u9661\u7136\u7ed9\u6253\u5f00\u3002</p><p>\u3000\u3000\u201c\u95ed\u4e0a\u53bb\u4f60\u7684\u5634\uff01\u201d\u674e\u8fdc\u6e56\u9634\u6c89\u7740\u8138\u5f84\u76f4\u8d70\u51fa\u6765\u6307\u7740\u90a3\u4e2a\u5e02\u6c11\u653f\u5c40\u5b98\u5458\u7684\u9f3b\u5b50\u5b8c\u5168\u4e0d\u987e\u7701\u957f\u4eea\u6001\u7684\u5927\u58f0\u8bad\u65a5\uff0c\u6076\u72e0\u72e0\u7684\u77aa\u7740\u90a3\u4e2a\u5b98\u5458\uff0c\u201c\u4f60\u662f\u54ea\u4e2a\u90e8\u95e8\u7684\uff1f\u6211\u4e0d\u9700\u8981\u4f60\u6709\u591a\u9ad8\u7684\u89c9\u609f\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u8fd8\u6ca1\u6709\u4e00\u70b9\u505a\u4eba\u7684\u826f\u77e5\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7f57\u541b\u7ad9\u5728\u674e\u8fdc\u6e56\u7684\u540e\u9762\uff0c\u51b7\u773c\u770b\u7740\u90a3\u4e2a\u9152\u6c14\u718f\u5929\u8bf4\u602a\u8bdd\u7684\u5b98\u5458\uff0c\u4ed6\u8bf4\u90a3\u756a\u602a\u8bdd\u65f6\uff0c\u4ed6\u4eec\u521a\u597d\u8981\u8d70\u51fa\u6765\uff0c\u5c31\u7ad9\u5728\u95e8\u91cc\u542c\u4ed6\u5c06\u8fd9\u756a\u8bdd\u8bf4\u5b8c\uff0c\u53ea\u6015\u8fc7\u9053\u91cc\u7ad9\u7684\u8bb8\u591a\u4eba\u5fc3\u91cc\u90fd\u662f\u8fd9\u6837\u7684\u60f3\u6cd5\uff0c\u8981\u662f\u8ba9\u4ed6\u4eec\u6574\u4e2a\u5bb6\u5ead\u6bcf\u4e2a\u6708\u90fd\u53ea\u6709\u4e8c\u767e\u591a\u5757\u94b1\u7684\u751f\u6d3b\u8d39\u3001\u5b69\u5b50\u4e09\u4e2a\u6708\u5403\u4e0d\u4e0a\u8089\u8981\u5403\u8089\u3001\u9f13\u8db3\u52c7\u6c14\u62ff\u4e00\u5757\u94b1\u53bb\u4e70\u8089\u5374\u8981\u9971\u53d7\u522b\u4eba\u7684\u7f9e\u8fb1\uff0c\u5c31\u4f1a\u611f\u53d7\u5230\u751f\u6d3b\u6709\u65f6\u5019\u771f\u7684\u4f1a\u5f88\u51c4\u51c9\u3002</p><p>\u3000\u3000\u81ea\u6740\u8005\u4e5f\u8bb8\u4e0d\u591f\u575a\u5f3a\uff0c\u4f46\u662f\u8fc7\u9053\u91cc\u7ad9\u7740\u7684\u8fd9\u4e9b\u597d\u597d\u201c\u6d3b\u7740\u201d\u7684\u4eba\u5374\u662f\u6ca1\u6709\u7acb\u573a\u53bb\u6307\u8d23\u4ed6\u4eec\u7684\u3002</p><p>\u3000\u3000\u8fc7\u9053\u91cc\u4f20\u6765\u201c\u549a\u549a\u549a\u201d\u7684\u811a\u6b65\u58f0\uff0c\u5f20\u606a\u56de\u5934\u770b\u8fc7\u53bb\uff0c\u770b\u5230\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u75be\u6b65\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8096\u660e\u5efa\u8d70\u8fc7\u6765\uff0c\u671d\u674e\u8fdc\u6e56\u8bf4\u9053\uff1a\u201c\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u6211\u5fc3\u91cc\u4e5f\u5f88\u96be\u53d7\uff0c\u662f\u5e02\u653f\u5e9c\u5de5\u4f5c\u6ca1\u6709\u505a\u5230\u4f4d\uff0c\u662f\u6211\u7684\u5de5\u4f5c\u6ca1\u6709\u505a\u5230\u4f4d\uff0c\u6211\u8981\u5c06\u8fd9\u4e2a\u8d23\u4efb\u627f\u62c5\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u660e\u5efa\u4e00\u4e0a\u6765\u5c31\u4e3b\u52a8\u627f\u62c5\u8d23\u4efb\uff0c\u674e\u8fdc\u6e56\u4e5f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4ea7\u4e1a\u7ed3\u6784\u8981\u5927\u8c03\u6574\uff0c\u8fd9\u4e9b\u4e0b\u5c97\u804c\u5de5\u662f\u4ea7\u4e1a\u5927\u8c03\u6574\u7684\u727a\u7272\u8005\uff0c\u793e\u4f1a\u4fdd\u969c\u5236\u5ea6\u8ddf\u4e0d\u4e0a\uff0c\u8bf4\u8981\u8c01\u627f\u62c5\u8d23\u4efb\uff0c\u4ed6\u674e\u8fdc\u6e56\u4e5f\u8131\u4e0d\u8d23\uff0c\u773c\u4e0b\u5e94\u8be5\u5c3d\u53ef\u80fd\u7684\u53bb\u5f25\u8865\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u8c01\u627f\u62c5\u8d23\u4efb\u7684\u95ee\u9898\u2014\u2014\u9996\u5148\uff0c\u5efa\u90ba\u5e02\u91cc\u4e00\u5171\u8fd8\u6709\u591a\u5c11\u53cc\u804c\u5de5\u4e0b\u5c97\u5bb6\u5ead\uff0c\u4e00\u5171\u6709\u591a\u5c11\u53cc\u804c\u5de5\u4e0b\u5c97\u5bb6\u5ead\u6bcf\u4e2a\u6708\u53ea\u80fd\u9760\u4e8c\u767e\u56db\u5341\u5757\u94b1\u7684\u751f\u6d3b\u8d39\u8fc7\u6d3b\uff0c\u6709\u591a\u5c11\u53cc\u804c\u5de5\u4e0b\u5c97\u5bb6\u5ead\u8fde\u7eed\u4e09\u4e2a\u6708\u5403\u4e0d\u8d77\u8089\uff0c\u6709\u591a\u5c11\u53cc\u804c\u5de5\u4e0b\u5c97\u5bb6\u5ead\u5b50\u5973\u4e0a\u5b66\u4ea4\u4e0d\u8d77\u5b66\u8d39\uff0c\u6709\u591a\u5c11\u53cc\u804c\u5de5\u4e0b\u5c97\u5bb6\u5ead\u8fde\u81ea\u6740\u62a2\u6551\u7684\u533b\u7597\u4fdd\u969c\u90fd\u6ca1\u6709\u2014\u2014\u4e5f\u4e0d\u4ec5\u4ec5\u5efa\u90ba\u5e02\u91cc\uff0c\u4e1c\u6d77\u7701\u6bcf\u4e2a\u5730\u533a\uff0c\u90fd\u8981\u5c3d\u5feb\u7684\u6478\u6e05\u695a\u60c5\u51b5\uff0c\u8fd9\u6837\u7684\u60b2\u5267\u53d1\u751f\u4e00\u8d77\uff0c\u5df2\u7ecf\u591f\u8ba9\u4eba\u89e6\u76ee\u5fc3\u60ca\u4e86\uff0c\u4e0d\u80fd\u518d\u53d1\u751f\u7b2c\u4e8c\u8d77\u4e86\u3002\u201d</p><p>\u3000\u3000\u7f57\u541b\u8bf4\u9053\uff1a\u201c\u7edd\u4e0d\u80fd\u518d\u53d1\u751f\u7b2c\u4e8c\u8d77\u8fd9\u6837\u7684\u60b2\u5267\u4e86\uff0c\u5e02\u59d4\u5e02\u653f\u5e9c\u73b0\u5728\u5c31\u53ec\u5f00\u5404\u90e8\u95e8\u5f00\u4f1a\u7814\u7a76\u5bf9\u7b56\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u5c06\u5e02\u59d4\u5e02\u653f\u5e9c\u7684\u51b3\u8bae\u4f20\u8fbe\u5230\u6bcf\u4e2a\u8857\u9053\u3001\u6bcf\u4e2a\u76f8\u5173\u90e8\u95e8\u2014\u2014\u53e6\u5916\uff0c\u6c11\u653f\u5c40\u6768\u534e\u4e1c\u8eab\u4e3a\u515a\u5458\u5e72\u90e8\uff0c\u515a\u6027\u89c9\u609f\u6781\u5176\u4f4e\u4e0b\uff0c\u6211\u5efa\u8bae\u7ec4\u7ec7\u90e8\u95e8\u5373\u523b\u6682\u505c\u6768\u534e\u4e1c\u7684\u4e00\u5207\u804c\u52a1\uff0c\u751a\u81f3\u8981\u8ba8\u8bba\u4e00\u4e0b\u6709\u6ca1\u6709\u5fc5\u8981\u5f00\u9664\u4ed6\u7684\u515a\u7c4d\uff0c\u6211\u770b\u4ed6\u662f\u6ca1\u6709\u8d44\u683c\u518d\u5f53\u515a\u5458\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u4e2a\u6c11\u653f\u5c40\u5b98\u5458\u7ed9\u674e\u8fdc\u6e56\u6307\u7740\u9f3b\u5b50\u7684\u8bad\u65a5\u5df2\u7ecf\u5413\u5f97\u8138\u8272\u82cd\u767d\uff0c\u542c\u7f57\u541b\u8fd9\u4e48\u8bf4\uff0c\u8eab\u5b50\u5c31\u8981\u7ad9\u4e0d\u4f4f\u762b\u8f6f\u4e0b\u6765\uff0c\u4ed6\u8981\u80fd\u60f3\u5230\u56e0\u4e3a\u4e00\u53e5\u7262\u9a9a\u8bdd\u627f\u62c5\u5982\u6b64\u4e25\u91cd\u7684\u540e\u679c\uff0c\u53ea\u6015\u62ff\u7740\u94b3\u5b50\u53bb\u62d4\u4ed6\u7684\u820c\u5934\u90fd\u4e0d\u4f1a\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u7684\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u4e5f\u8868\u4e86\u4e00\u756a\u6001\uff0c\u8981\u627f\u62c5\u8d23\u4efb\uff0c\u4e5f\u4e3b\u52a8\u8981\u4e00\u8d77\u627f\u62c5\u653b\u575a\u89e3\u51b3\u53cc\u4e0b\u5c97\u804c\u5de5\u5bb6\u5ead\u751f\u6d3b\u56f0\u96be\u7684\u96be\u9898\uff0c\u4ed6\u4e0e\u8096\u660e\u5efa\u65e9\u770b\u5230\u5f20\u606a\u5728\u573a\uff0c\u5fc3\u91cc\u96be\u514d\u4f1a\u60f3\u4ec0\u4e48\u7834\u4e8b\u90fd\u7ed9\u4ed6\u6cbe\u4e0a\u8fb9\uff0c\u53ea\u662f\u8fd9\u79cd\u8bdd\u7edd\u5bf9\u4e0d\u4f1a\u8bf4\u51fa\u53e3\u7684\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u53c8\u968f\u540c\u8096\u660e\u5efa\u8d70\u8fdb\u89c2\u5bdf\u5ba4\u770b\u671b\u559d\u519c\u836f\u81ea\u6740\u7684\u4e0b\u5c97\u5973\u804c\u5de5\u592b\u5987\uff0c\u8bf4\u4e86\u4e9b\u5b89\u6170\u7684\u8bdd\uff0c\u4e5f\u90fd\u638f\u51fa\u4e24\u767e\u5143\u94b1\u6765\u4ea4\u7ed9\u4e08\u592b\uff0c\u4e5f\u4e0d\u6307\u671b\u4f1a\u6709\u5a92\u4f53\u5c06\u4ed6\u4eec\u6350\u94b1\u7684\u8fd9\u4e00\u5e55\u7ed9\u62cd\u4e0b\u6765\uff0c\u4e0d\u8fc7\u533b\u9662\u533b\u62a4\u4eba\u5458\u4ee5\u53ca\u968f\u884c\u7684\u5b98\u5458\u90fd\u770b\u7740\uff0c\u4ed6\u4eec\u5fc5\u987b\u8981\u8fd9\u4e48\u505a\u7684\u3002</p><p>\u3000\u3000\u51fa\u4e86\u89c2\u5bdf\u5ba4\uff0c\u5c31\u4e0d\u518d\u533b\u9662\u91cc\u803d\u6401\uff0c\u8fd8\u8981\u8fde\u591c\u5546\u8bae\u5bf9\u7b56\uff0c\u4e0d\u4ec5\u5efa\u90ba\u5e02\u91cc\uff0c\u674e\u8fdc\u6e56\u4e5f\u8ba9\u7701\u6c11\u653f\u90e8\u95e8\u3001\u7701\u536b\u751f\u90e8\u95e8\u7b49\u8d1f\u8d23\u4eba\u8ddf\u4ed6\u56de\u7701\u653f\u5e9c\u8fde\u591c\u5f00\u4f1a\uff0c\u80e1\u5b97\u5e86\u8ba9\u5e02\u653f\u5e9c\u526f\u79d8\u4e66\u957f\u6c64\u5efa\u950b\u4e0a\u4e86\u4ed6\u7684\u8f66\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u5728\u8fd9\u91cc\uff0c\u8c01\u901a\u77e5\u4f60\u8fc7\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u6c64\u5efa\u950b\u54ea\u91cc\u60f3\u5230\u641e\u4e86\u534a\u5929\u538b\u6839\u5c31\u6ca1\u6709\u4eba\u63d0\u8d77\u201c\u9ed1\u8f66\u201d\u7684\u4e8b\u60c5\uff0c\u641e\u4e86\u534a\u5929\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u4e0e\u7701\u957f\u674e\u8fdc\u6e56\u662f\u4e3a\u4e0b\u5c97\u5973\u804c\u5de5\u81ea\u6740\u7684\u4e8b\u60c5\u5927\u53d1\u96f7\u9706\uff0c\u4ed6\u5c06\u8054\u5408\u6267\u6cd5\u961f\u5728\u533b\u9662\u5927\u95e8\u53e3\u6263\u62bc\u739b\u838e\u62c9\u8482\u7f5a\u6b3e\u7684\u4e8b\u60c5\u544a\u8bc9\u80e1\u5b97\u5e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u771f\u662f\u600e\u4e48\u4e0d\u77e5\u9053\u2018\u6b7b\u2019\u5b57\u600e\u4e48\u5199\uff1f\u201d\u80e1\u5b97\u5e86\u5389\u58f0\u9a82\u4e86\u4e00\u53e5\uff0c\u5fc3\u60f3\u96be\u602a\u5f20\u606a\u4e0e\u79d1\u738b\u9ad8\u79d1\u7684\u9648\u9759\u4f1a\u5728\u573a\uff0c\u5f20\u606a\u4e0d\u63d0\u8f66\u7ed9\u65e0\u7406\u6263\u62bc\u7f5a\u6b3e\u7684\u4e8b\u60c5\uff0c\u662f\u8fd9\u6837\u7684\u573a\u5408\u518d\u63d0\u8fd9\u4e2a\u6709\u4e9b\u4e0d\u5408\u9002\uff0c\u4e0d\u662f\u8bf4\u4ed6\u4eec\u5c31\u5c06\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\uff0c\u5429\u5490\u9053\uff0c\u201c\u8c01\u662f\u76f4\u63a5\u8d23\u4efb\u4eba\uff0c\u5904\u7406\u8d77\u6765\u4e0d\u8981\u624b\u8f6f\uff0c\u8be5\u600e\u4e48\u5904\u7406\u5c31\u600e\u4e48\u5904\u7406\uff0c\u8981\u5c3d\u5feb\u5c06\u8fd9\u4e8b\u4e86\u7ed3\u6389\uff0c\u4e0d\u8981\u8ba9\u5bf9\u65b9\u6709\u8bdd\u53ef\u8bf4\u2014\u2014\u8054\u5408\u6267\u6cd5\u961f\u4e5f\u6536\u655b\u4e00\u4e9b\uff0c\u4e0d\u8981\u52a8\u4e0d\u52a8\u5c31\u5c06\u5c4e\u76c6\u5b50\u6263\u5230\u6211\u8ddf\u8096\u5e02\u957f\u5934\u4e0a\u6765\u3002\u201d</p><p>\u3000\u3000\u6c64\u5efa\u950b\u89c1\u80e1\u5b97\u5e86\u4e5f\u754f\u4eba\u5bb6\u5982\u86c7\u874e\uff0c\u5fc3\u60f3\u7740\u4e5f\u53ea\u6709\u8ba9\u59dc\u5fd7\u660e\u60b2\u5267\u4e86\u3002\u4ed6\u8fd8\u6ca1\u6709\u673a\u4f1a\u76f4\u63a5\u770b\u5230\u8fc7\u5f20\u606a\uff0c\u6bd5\u7adf\u9526\u6e56\u4e0e\u5e02\u653f\u5e9c\u7684\u8054\u7cfb\u6781\u5c11\uff0c\u4f46\u662f\u4ed6\u4ece\u521a\u624d\u7684\u4ed7\u52bf\uff0c\u4e5f\u9690\u9690\u7ea6\u7ea6\u7684\u731c\u5230\u90a3\u4e2a\u9752\u5e74\u5c31\u662f\u4f20\u8bf4\u4e2d\u9526\u6e56\u80cc\u540e\u7684\u90a3\u4e2a\u4eba\u3002</p></article>"}}}]);
